INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100601, "この楽譜、\nサックスで吹いて\nみたいなぁ♪", 0, 3, 224, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100602, "このごろ一人でいると\nお兄ちゃんのこと\nばかり考えちゃう…", 0, 1, 226, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100602, "はぁ、はぁ…もっと\nいっぱいしなくちゃ\n…練習", 0, 1, 227, 2, 1, 0);
