INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100602, "双子って、\n好みのタイプも\n似ちゃうのかな？", 0, 1, 230, 5, 1, 0);
