INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 100602, "もう待ちきれない…\nお兄ちゃん、\n早く帰って来て！", 0, 1, 232, 102, 1, 0);
