INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100701, "プリン、プリン、\nプリン、プリン、\nプリン、プリン～♪", 0, 1, 251, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100701, "う～ら～め～し～や～\nなの～♪", 0, 1, 252, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100701, "プリンがないなら\n死んだほうがマシなの\nあ、もう死んでたの～", 0, 1, 253, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100701, "幽霊の正体見たり、\nミヤコなの～", 0, 1, 254, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100701, "ミヤコがいきなり\n消えたら、みんな\n驚くかもなの～♪", 0, 1, 255, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 100701, "アイツがいて、\nプリンもある。\nきっとここが天国なの", 0, 1, 256, 101, 1, 0);
