INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100802, "バラの花びらいっぱい\n浮かべたお風呂に\n入ってから\n寝ようっと♪", 3, 2, 341, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100802, "鏡よ、鏡。この世で\n一番可愛いボクに\nお似合いの人はだれ？", 0, 3, 342, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100802, "ボクが座ったソファは\n「美しいソファ」に\nランクアップした！", 0, 3, 343, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100802, "可愛いものを\n可愛いって言える\nボクって可愛いよね！", 0, 3, 344, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100802, "服を羽織るように\n香りをまとうボク…\n美しい、美しすぎる！", 0, 3, 345, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100802, "冷えは美容の大敵\nなんだから！\nここ、ボクの場所ね！", 0, 3, 346, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100802, "運動は苦手なのに\n運動するボクの姿は\n可愛いなんて…罪♪", 0, 3, 347, 6, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100802, "ボクが食べると\n食べ物まで\n可愛いなあ…", 0, 3, 348, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100802, "ボクが触った瞬間に\nこの本は美を\n携えてしまうんだ", 0, 3, 349, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100901, "「第１０部２０章\n終焉戦争、開幕」\n…これでよし！", 0, 1, 351, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100901, "今度シグルドに\n私の魔法を一つ\n伝授してやるか", 0, 1, 352, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100901, "ここでは風が\n吹いてこないな…\n換気したい！", 0, 1, 353, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100901, "そうだ、ナナカに\nこの間借りた本を\n返さないと…", 0, 1, 354, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100901, "まずは包帯をとって\n手から龍の形の\n炎を出して…", 0, 1, 355, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 100901, "シグルド…\n今頃何処で何を\nしているのだろう…", 0, 1, 356, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 100901, "最近、彼の姿が\nまぶしく見える…\nどうして…？", 0, 1, 357, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100901, "辞書を知らないか？\n…探している\n理由は聞くな", 0, 2, 358, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100901, "そうだ！\n貴様の技にも\n名前をつけてやろう！", 0, 2, 359, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100901, "私に触れても力を\n奪われないのは\n貴様くらいだぞ？", 0, 2, 360, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100901, "何事だ？\nまさか我が瞳に\n魅入られたのか？", 0, 2, 361, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 100901, "貴様がそばにいると\nとても安心できる…\n…不思議だ", 0, 2, 362, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 100901, "い、いきなり目の前に\n現れるな！　ドキドキ\nするだろうがぁ！", 0, 2, 363, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100901, "「覚醒」めたか…\n行くぞ、悪を討つ\n戦いに出発だ！", 1, 2, 364, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100901, "昼間は「ヤツら」も\n襲ってはこない…\n束の間の休息だ", 2, 2, 365, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100901, "どうした？　夜はまだ\nこれから…ふぁ…\nね、眠くなどないぞ！", 3, 2, 366, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100901, "何か足りないな…\n暗黒騎士の紋章を\n作ろうかな", 0, 3, 367, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100901, "くっ…！\n私を眠りの国へ\n誘うつもりか！", 0, 3, 368, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100901, "このようなものに\n懐柔されるものか！\n…されるものか！", 0, 3, 369, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100901, "これを使えば\nもっとそれっぽい\n雰囲気が出そうだ", 0, 3, 370, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100901, "これが我らの求めた\n安寧か…\n悪くない…", 0, 3, 371, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100901, "この程度\n前世での激闘に\n比べれば…！", 0, 3, 372, 6, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100901, "最近金欠でな…\nいやぁありがたい…", 0, 3, 373, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100901, "ここにも\n前世の記憶が\n眠っていたとは…", 0, 3, 374, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100902, "「第１３部１章\n閃光のシグルド」\n…ついに登場だな", 0, 1, 376, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100902, "何故だ…何故\nこうもあっさり金欠に\n逆戻りするのだ！？", 0, 1, 377, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100902, "やはり転生者という\n設定はカッコイイ…\n天才だな、私！", 0, 1, 378, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100902, "そうだ、ナナカに\nこの間借りた本を\n返さないと…", 0, 1, 379, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100902, "そろそろ今日の分の\n魔力を溜めないと\nまた魔力切れが…", 0, 1, 380, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100902, "最近奴がそばに\nいるだけで、鼓動が\n早くなるのは何故だ？", 0, 1, 381, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 100902, "シ、シグルド…！\nだめだ…妄想でも\n上手く言えない…", 0, 1, 382, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 100902, "なんだ？　新たな\n賞金の情報を\n持ってきたのか？", 0, 2, 383, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100902, "名乗りやポーズは\nその強度で相手を\n圧倒する技術だ", 0, 2, 384, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100902, "いいか、魔法の\n詠唱中は絶対に\n声をかけるなよ？", 0, 2, 385, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100902, "おお、ちょうど今\n貴様専用の設定集を\n作成中だぞ！", 0, 2, 386, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100902, "貴様がそばにいると\nとても安心できる…\n…不思議だ", 0, 2, 387, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 100902, "い、いきなり目の前に\n現れるな！　ドキドキ\nするだろうがぁ！", 0, 2, 388, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 100902, "夜更かしのせいか\nまだ眠気が…いや！\nこれは組織の罠だ！", 1, 2, 389, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100902, "平穏な時に\n出来るだけ魔力を\n溜めておかねば…", 2, 2, 390, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100902, "夜になると、力が\n増した気分になるな\n…なりませんか？", 3, 2, 391, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100902, "何か足りないな…\n暗黒騎士の紋章を\n作ろうかな", 0, 3, 392, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100902, "お、おのれ\nヒュプノス…\n私は負けないぞ…", 0, 3, 393, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100902, "くっくっくっ…\n貴様は特別団員に\nしてやろう", 0, 3, 394, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100902, "これを使えば\nもっとそれっぽい\n雰囲気が出そうだ", 0, 3, 395, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100902, "これが我らの求めた\n安寧か…\n悪くない…", 0, 3, 396, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100902, "新たな戦いに備えて\n魔力だけでなく\n体力も鍛えねば！", 0, 3, 397, 6, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100902, "食事は魔力に\n変換する体力の\n元になるのだ！", 0, 3, 398, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100902, "フフフ…\nこれを覚えれば\nもっと強く…！", 0, 3, 399, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101001, "王子はんほんまに\nはんなりしてて、\n王子はんって感じやわ", 0, 1, 401, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101001, "この絵本の王子はん、\n王子はんみたいで\nええなぁ♪", 0, 1, 402, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101001, "うちがガラスの靴を\n落としたら、王子はん\n見つけてくれるやろか", 0, 1, 403, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101001, "マホマホ王国\n次期国王の王子はん\n…見てみたいわ～", 0, 1, 404, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101001, "王子はんのために\n糸を紡いで、\n編み物でもしまひょか", 0, 1, 405, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 101001, "マホ姫と王子はん、\n絵本の世界より\nベストカップルや", 0, 1, 406, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 101001, "王子はんと\n一緒にいると\n胸がせわしのー\nうごいてかなん", 0, 1, 407, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101001, "うちら並んでたら\nお雛様みたいに\n見えると思いまへん？", 0, 2, 408, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101001, "王子はんを\n国に連れて帰ったら\nみんな大歓迎して\nくれるやろなぁ", 0, 2, 409, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101001, "王子はんは、\n絵本から出てきた人\nみたいやね～", 0, 2, 410, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101001, "うちの話し方\nのんびりしてます\nやろか？", 0, 2, 411, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 101001, "マホ姫の手は\nつべたいから、\n王子はんが温めて？", 0, 2, 412, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 101001, "うちがもし悪い魔法に\nかかったら、王子はん\n口づけしてくれはる？", 0, 2, 413, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101001, "朝は小鳥はんも\nおなかがすいてるから\nよう飛び回りはるねえ", 1, 2, 414, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101001, "お茶請けはお大福が\nよろしおすけど\nクッキーもええなあ", 2, 2, 415, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101001, "うち、普段から\n夢見がちやけど…\nほんに眠い時間やわぁ", 3, 2, 416, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101001, "王子はんに\n会いに行くなら、\nおめかしせな\nあかんなぁ", 0, 3, 417, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101001, "はんなりした\nええお椅子やわぁ\nほっこりしますなあ", 0, 3, 418, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101001, "かいらしもんが\nいっぱいで、\nマホマホ王国みたい\nやわぁ", 0, 3, 419, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101001, "心地ええどす。まるで\nお花畑にいるようやね", 0, 3, 420, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101001, "ぽかぽかどす。\n心まであったかく\nなりますわぁ", 0, 3, 421, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101001, "はぁはぁ、お姫はんは\n激しい運動は\nしぃひんもんどす～", 0, 3, 422, 6, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101001, "もぐもぐ…あんまり\n速う食べられへんけど\n美味しいわぁ", 0, 3, 423, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101001, "絵本、絵本～\n幸せやわぁ♪", 0, 3, 424, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101002, "王子はんにとって\n自慢の姫になるえ～", 0, 1, 426, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101002, "カボチャの馬車に\nガラスの靴…\n欲しいなぁ", 0, 1, 427, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101002, "うちがガラスの靴を\n落としたら、王子はん\n見つけてくれるやろか", 0, 1, 428, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101002, "マホマホ王国\n次期国王の王子はん\n…見てみたいわ～", 0, 1, 429, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101002, "ほんまなぁ\nこの絵本が面白いんよ\n王子はんに\n読んであげよ", 0, 1, 430, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 101002, "マホ姫と王子はん\n絵本の世界より\nベストカップルや", 0, 1, 431, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 101002, "王子はんと\n一緒にいると\n胸がせわしのー\nうごいてかなん", 0, 1, 432, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101002, "たまには、王子はんの\n好きな絵本も\n知りたいな～", 0, 2, 433, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101002, "王子はんを\n国に連れて帰ったら\nみんな大歓迎して\nくれるやろなぁ", 0, 2, 434, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101002, "そのうち、王子はんの\nお国にも連れてって\nもらえますやろか？", 0, 2, 435, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101002, "絵本読むんも\nええなぁ…", 0, 2, 436, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 101002, "うちがもし悪い魔法に\nかかったら、王子はん\n口づけしてくれはる？", 0, 2, 437, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 101002, "うちら並んでたら\nお雛様みたいに\n見えると思いまへん？", 0, 2, 438, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101002, "朝は小鳥はんも\nおなかがすいてるから\nよう飛び回りはるねえ", 1, 2, 439, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101002, "マホマホ王国では\nこの時分は\nみんなお昼寝\nしてたんよ", 2, 2, 440, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101002, "夜に飛んだり鳴いたり\nする鳥はんもおるんよ\n昼は寝てはるんかねぇ", 3, 2, 441, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101002, "王子はんに\n会いに行くなら\nおめかしせな\nあかんなぁ", 0, 3, 442, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101002, "王子はんも一緒に\nゆっくりしていっても\nええんよ？", 0, 3, 443, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101002, "かいらしい子やねぇ\nうちとお友だちに\nなってくれるん？", 0, 3, 444, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101002, "癒しアイテムって\nほんま、好っきゃわぁ", 0, 3, 445, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101002, "うちのお城にも\nほしいわぁ。帰ったら\nさっそく準備せな\nあかんなぁ", 0, 3, 446, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101002, "部屋の中で運動\nできるのも、なかなか\n面白いもんやわぁ", 0, 3, 447, 6, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101002, "おいひいわ～\nマホマホ王国には\nない味やわぁ", 0, 3, 448, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101002, "しっかり知識を\n身につけんと\n魔法も使えへん\nからなぁ", 0, 3, 449, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101101, "１、２、３、４…\nあれ？　いま、\n何段目まで\n編んだっけ？", 0, 1, 451, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101101, "おやつのケーキは\n苺かチョコ…\nどっちも食べよう！", 0, 1, 452, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101101, "い、いけない！\n縫い針落としちゃった", 0, 1, 453, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101101, "ケーキをクレープで\n巻いたら…\n大ヒット商品の予感！", 0, 1, 454, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101101, "「どうだ！妹暮らし」\nってことわざ\nなかったっけ？", 0, 1, 455, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 101101, "お兄ちゃんに、\nお洋服でも\n作ろうかな？", 0, 1, 456, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 101101, "ここがお兄ちゃんの\n部屋…\nドキドキしちゃう～", 0, 1, 457, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101101, "ねぇ、おにいちゃん\n一緒に、スイーツでも\n食べにいかない？", 0, 2, 458, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101101, "クリームついてた！？\nさっき食べた、\nケーキかも…！", 0, 2, 459, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101101, "勉強、見てくれるん\nですか？　やった～♪", 0, 2, 460, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101101, "服がほつれてますよ？\n直してあげます！", 0, 2, 461, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 101101, "私をお嫁さんに\nしたいなら、予約は\nお早目に、ですよ♪", 0, 2, 462, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 101101, "ねぇねぇお兄ちゃん！\n…えへへ、\n呼んでみただけ♪", 0, 2, 463, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101101, "今日も１日\n勉強がんばります！\n…少しは遊ぼうかな", 1, 2, 464, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101101, "お昼休憩に\n甘いものが\n食べたいです～", 2, 2, 465, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101101, "ねむいです～\nでももうちょっと\n勉強しないと～…", 3, 2, 466, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101101, "この服、かわいい♪\n今度作って\nみよっと～！", 0, 3, 467, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101101, "勉強で疲れたから\n休憩しよ～っと", 0, 3, 468, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101101, "かわいい♪\n私が服を作って\nあげよっか？", 0, 3, 469, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101101, "リラックスできます～\nお兄ちゃんが隣に\nいるみたい！", 0, 3, 470, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101101, "のんびりしたら、\nまた勉強しよっと！", 0, 3, 471, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101101, "運動すると勉強も\nはかどるよね！", 0, 3, 472, 6, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101101, "おいし～！\nお兄ちゃんにも\nおすそ上げしますね！", 0, 3, 473, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101101, "お兄ちゃんの記憶を\n戻す方法、どこかに\n書いてないかな？", 0, 3, 474, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101102, "ケーキの食べ過ぎで\nお腹が…うぅ、\n因果横暴だよー…", 0, 1, 476, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101102, "１、２、３、４…\nあれ？　いま、\n何段目まで\n編んだっけ？", 0, 1, 477, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101102, "い、いけない！\n縫い針落としちゃった", 0, 1, 478, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101102, "笑う時には服着ます！", 0, 1, 479, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101102, "「どうだ！妹暮らし」\nってことわざ\nなかったっけ？", 0, 1, 480, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 101102, "お兄ちゃんに、\nお洋服でも\n作ろうかな？", 0, 1, 481, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 101102, "お兄ちゃんが帰って\nきたら、何して遊ぼう\nかな～？", 0, 1, 482, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101102, "勉強、見てくれるん\nですか？　やった～♪", 0, 2, 483, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101102, "クリームついてた！？\nさっき食べた、\nケーキかも…！", 0, 2, 484, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101102, "おにいちゃん！\nそろそろ、おやつに\nしませんか？", 0, 2, 485, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101102, "勉強は…\n明日にしようっと", 0, 2, 486, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 101102, "妹だけど、大人な\nレディですからね！", 0, 2, 487, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 101102, "な、ナデナデとか\n恥ずかしいな～", 0, 2, 488, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101102, "今日も１日\n勉強がんばります！\n…少しは遊ぼうかな", 1, 2, 489, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101102, "お昼休憩に\n甘いものが\n食べたいです～", 2, 2, 490, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101102, "お兄ちゃんは\n夜強いんですね\n私は眠くて…", 3, 2, 491, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101102, "服装や髪形が変わると\n気分も変わるよね", 0, 3, 492, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101102, "ここで寝ちゃったら\n受験勉強が～…\nぐーー", 0, 3, 493, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101102, "かわいい♪\n私が服を作って\nあげよっか？", 0, 3, 494, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101102, "いまならどんな試験も\n合格できそうです！", 0, 3, 495, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101102, "のんびりしたら\nまた勉強しよっと！", 0, 3, 496, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101102, "お兄ちゃんを\n守るためには\n毎日、鍛えなくちゃ！", 0, 3, 497, 6, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101102, "おいし～！\nお兄ちゃんにも\nおすそ上げしますね！", 0, 3, 498, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101102, "この本の服\nかわいいなぁ♪\n私に作れるかな？", 0, 3, 499, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101201, "う～ん、今なら\nどこでだって\n眠れちゃいそう…", 0, 1, 501, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101201, "たくさん寝すぎて、\n夢の中か現実か\nわかんなく\nなっちゃうんだよね～", 0, 1, 502, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101201, "…はっ！？\nね、寝てないよ？\nホントだよっ", 0, 1, 503, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101201, "ぐっすり眠って\nパワーを\nフルチャージっ☆", 0, 1, 504, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101201, "もし、超能力のことが\nバレたら…ブルブル", 0, 1, 505, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 101201, "テレパシーで\nキミの心を覗けたら\n…なんてね！", 0, 1, 506, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 101201, "キミを抱き枕に…\nじょ、冗談冗談！", 0, 1, 507, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101201, "ん～、なになに？\n一緒にお昼寝する？", 0, 2, 508, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101201, "忘れ物しちゃった。\nま、いっか！\nえ、ダメ？", 0, 2, 509, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101201, "おおっ、相談ごと？\nなにかな\nなにかな～？", 0, 2, 510, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101201, "う～ん、もしかして\nキミにも仲良しになる\n超能力があるのかも！", 0, 2, 511, 4, 1, 0);
