INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 102301, "ここで、ぷうきち\nフルスイングは、\nまずいかな～？", 0, 1, 854, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 102301, "この部屋、ぷうきちを\n置く場所はないのかな", 0, 1, 855, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 102301, "お兄ちゃんって…\nちょっとぷうきちに\n似てるかも♪", 0, 1, 856, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 102301, "お兄ちゃんにも、\nぷうきちのこと\n好きになって\nもらいたいな～", 0, 1, 857, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 102301, "おやつは、\n私とぷうきちの\n二人分用意してね♪", 0, 2, 858, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 102301, "ぷうきちが、お腹\nペコペコだって～\n何かちょうだい！", 0, 2, 859, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 102301, "『アヤネに手出したら\n…坊ちゃんでも、\n分かってんな？』", 0, 2, 860, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 102301, "ぷうきちとの\n連携を磨いてるの！\nもっと仲良くなるよ！", 0, 2, 861, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 102301, "特別だからね？\nもっと触っていいよ\n…ぷうきちのこと", 0, 2, 862, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 102301, "お兄ちゃんのこと、\n「ぷう兄」って\n呼ぼうかな？", 0, 2, 863, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 102301, "お兄ちゃんって\n朝早いんだね～\n私はまだ眠いよ～", 1, 2, 864, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 102301, "ふぁ～…ぷうきちと\nお昼寝してくるね…", 2, 2, 865, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 102301, "ぷうきちと\nおしゃべりしてたら\nもうこんな時間…", 3, 2, 866, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 102301, "『座り心地がいいな』\nぷうきちは座れない\nでしょ？", 0, 3, 868, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 102301, "かわいい～！\nぷうきちのお友達に\nしちゃおうかな？", 0, 3, 869, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 102301, "いいニオイ～♪\nぷうきちも\n気に入ったみたい！", 0, 3, 870, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 102301, "ぽわぽわする～\nぷうきちも\n気持ちいい？", 0, 3, 871, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 102301, "おいしい～\nぷうきちも\n食べてみる？", 0, 3, 873, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 102301, "ぷうきち！\nこの本、読んで～！", 0, 3, 874, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 102501, "どうして失敗ばっかり\nこうなったら\n腹を切るしか～！", 0, 1, 901, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 102501, "あれ？　あれ？\nお部屋の中で迷子に\nなっちゃいました～", 0, 1, 902, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 102501, "どうしたら転ばなく\nなるんでしょうか？", 0, 1, 903, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 102501, "わわわわ～～っ\nふう…\nきゃ、きゃあっ！", 0, 1, 904, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 102501, "私のドジで、\nこのお部屋を\n壊しちゃったら\nどうしましょう～～", 0, 1, 905, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 102501, "おいしい料理で\nお出迎えしなくちゃ！", 0, 1, 906, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 102501, "一生懸命\nご奉仕しますよ～♪", 0, 1, 907, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 102501, "耳かきしましょうか？\nえ？　こわい？", 0, 2, 908, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 102501, "お疲れでしたら、\nお昼寝しませんか？\n膝枕もしますよ？", 0, 2, 909, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 102501, "おかえりなさいませ\nご主人さま♪", 0, 2, 910, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 102501, "すみません\nすみません\nすみません～～", 0, 2, 911, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 102501, "メイドの私が、\nこんな気持ちを持って\nいいんでしょうか…", 0, 2, 912, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 102501, "私のこと、\n専属メイドと思って\n頼ってくださいね", 0, 2, 913, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 102501, "おはようございます♪\n今日も一日がんば…\nきゃ、きゃあ！", 1, 2, 914, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 102501, "お昼ごはん…\n失敗しちゃいましたぁ\nお、おやつでリベンジします！", 2, 2, 915, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 102501, "明日の目標は\nドジをしないです！\nき、昨日の目標も\nドジをしないでした", 3, 2, 916, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 102501, "あ、メイド服が\nほつれてる\n後でお裁縫しなくちゃ", 0, 3, 917, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 102501, "休憩が終わったら\nお洗濯にお料理に\nお掃除もしないと！", 0, 3, 918, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 102501, "かわいい♪\nあとでお洗濯\nしてあげますね", 0, 3, 919, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 102501, "いい香り…\nお嬢様のお部屋にも\n置いてあげたいです", 0, 3, 920, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 102501, "ここでお洗濯ものを\n乾かしましょうか", 0, 3, 921, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 102501, "あわわわっ…\nこ、転びそうです～！", 0, 3, 922, 6, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 102501, "これくらい\nおいしいものを\n作ってみたいです～", 0, 3, 923, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 102501, "物語のメイドさんって\n御主人さまを好きに\nなるんですよね…", 0, 3, 924, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 102502, "失敗したら、この\nお部屋追い出され\nちゃうかも～？", 0, 1, 926, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 102502, "あれ？　あれ？\nお部屋の中で迷子に\nなっちゃいました～", 0, 1, 927, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 102502, "どうやったら、立派な\nメイドになれるのかな", 0, 1, 928, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 102502, "塩にはＳマーク\n砂糖にはＳマーク\n…あ、あれ！？", 0, 1, 929, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 102502, "ご用があれば\nなんでも言って\nくださいね", 0, 1, 930, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 102502, "迷惑をかけすぎて…\n嫌われちゃったら\nどうしましょう", 0, 1, 931, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 102502, "メイドの私が\nこんな気持ちを持って\nいいんでしょうか…　", 0, 1, 932, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 102502, "おでかけですか？\nいってらっしゃいませ\nご主人さま♪", 0, 2, 933, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 102502, "どうして失敗ばっかり\nこうなったら\n腹を切るしか～！", 0, 2, 934, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 102502, "私、またなにか\nやっちゃいましたか？", 0, 2, 935, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 102502, "おいしいお茶っ葉が\n手に入りました～♪\n一緒に飲みませんか？", 0, 2, 936, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 102502, "転んだふりであの人に\n…って！\nなんでもないですよ！", 0, 2, 937, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 102502, "私、できることなら\nなんでもやりますよ\nあなたのためですから", 0, 2, 938, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 102502, "おはようございます♪\n今日も一日がんば…\nう、うわあ！", 1, 2, 939, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 102502, "お昼ごはん…\n失敗しちゃいましたぁ\nお、おやつでリベンジします！", 2, 2, 940, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 102502, "明日の目標は\nドジをしないです！\nき、昨日の目標も\nドジをしないでした", 3, 2, 941, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 102502, "あ、メイド服が\nほつれてる\n後でお裁縫しなくちゃ", 0, 3, 942, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 102502, "いい座り心地です～\nこれならお嬢さまも\nゆったりできますね♪", 0, 3, 943, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 102502, "かわいい♪\nあとでお洗濯\nしてあげますね", 0, 3, 944, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 102502, "いい香り…\nお嬢様のお部屋にも\n置いてあげたいです", 0, 3, 945, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 102502, "暖かいですけど\n…私が火に近づいて\n大丈夫でしょうか！？", 0, 3, 946, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 102502, "運動しようとすると\nいつも怪我するん\nですよね…", 0, 3, 947, 6, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 102502, "こんなに美味しいもの\nメイドの私には\nもったいないです～！", 0, 3, 948, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 102502, "物語のメイドさんって\n御主人さまを好きに\nなるんですよね…", 0, 3, 949, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 102701, "あの方と私だけの\n部屋にしたいですわ", 0, 1, 951, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 102701, "あの方は私のもの…\n眼も耳も鼻も口も髪も\n首も胸も腰も手も脚も\nぬくもりも心も…！", 0, 1, 952, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 102701, "運命とは決して\n抗えないもの…\nクスクス…クスクス", 0, 1, 953, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 102701, "部屋からあの方の\n香りが…\nああ、至福ですわ", 0, 1, 954, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 102701, "毒にも薬にもならない\n存在なんて嫌ですわ\nどうせならとびきりの\n毒でありたい…", 0, 1, 955, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 102701, "クスクス…\nあの方とデートする\nイメトレ中ですわ\nクスクスクスクス…", 0, 1, 956, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 102701, "記憶を戻す研究より\n記憶をねつ造する研究\nのほうが良いかしら？\n伴侶という設定で…", 0, 1, 957, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 102701, "運命を信じますか？\nもちろん絶対当然\n信じますわよね？", 0, 2, 958, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 102701, "この薬は起床時就寝前\n食前食後食間に、毎日\n服用してください。\n惚れ薬ですわ", 0, 2, 959, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 102701, "お願いがあるのです。\n新しい研究の実験台に\n…", 0, 2, 960, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 102701, "こっそり栽培している\n毒きのこが毒々しく\n育ちましたわ…", 0, 2, 961, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 102701, "あなた様の前では\n普通の女の子…\n少し毒薬づくりが\n得意なだけの…", 0, 2, 962, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 102701, "私の身も心も、全てが\nあなた様のもの。\nあなた様の全ても…", 0, 2, 963, 102, 1, 0);
