INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103002, "むむ、アクダイカンの\n気配がするデース", 0, 1, 1129, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103002, "クニトリ合戦\n楽しみデース！", 0, 1, 1130, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 103002, "最近、ショーグンの\nそばにいると\nドキドキするデス…", 0, 1, 1131, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 103002, "ショーグンの身は\n何があってもワタシが\n守るデス！", 0, 1, 1132, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103002, "あっ、ショーグン\n出陣はいつで\nゴザルデス！？", 0, 2, 1133, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103002, "イクサの前に\nホネ休めも大事デスよ\nゆっくりするデース！", 0, 2, 1134, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103002, "さ、さすがショーグン\nデス…いつの間に\n背後を！？", 0, 2, 1135, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103002, "一緒に忍法考える\nデス！\nショーグン用の\n忍法も必要デスね！", 0, 2, 1136, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 103002, "見つめられると\nドキドキするデス…\n新しい忍法デスか？", 0, 2, 1137, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 103002, "ショーグン！\nえへへ…これからも\nおそばに置いて\nクダサイね？", 0, 2, 1138, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103002, "いい朝デス！\nトーゴク風の朝ゴハン\n作るデス！", 1, 2, 1139, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103002, "もうお昼デス\nお昼寝するデスか？", 2, 2, 1140, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103002, "そろそろおネム\nデース…\n一緒に寝るデス？", 3, 2, 1141, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103002, "服の乱れは心の乱れ\nデス！　きっちり\nしないといけないデス", 0, 3, 1142, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103002, "心地よくて眠く\nなってきマス…\nもしやテキコクの\n罠デスか！？", 0, 3, 1143, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103002, "これを変わり身の\n術に…かわいそう\nだから止めるデス", 0, 3, 1144, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103002, "ワビサビというもの\nデスね！\n素晴らしいデス！", 0, 3, 1145, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103002, "身体を暖めてイクサに\n向かうデス！", 0, 3, 1146, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103002, "ショーグンのお役に\n立つため、修行\nあるのみデス！", 0, 3, 1147, 6, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103002, "美味しいデス…\n酢昆布を和えて\nみたいデス", 0, 3, 1148, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103002, "物事を知って\n来たるべきイクサに\n活かすデス", 0, 3, 1149, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103101, "お父さん、そろそろ\n子離れを…しない？\nそうですか…", 0, 1, 1151, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103101, "父は、いつも男性に\nきびしいですね", 0, 1, 1152, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103101, "占ってほしい人は、\nいつでも言って\nください", 0, 1, 1153, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103101, "あなたの手相は…\nて、手を握るのって\n緊張しますね", 0, 1, 1154, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103101, "この部屋、風水的にも\n運気が上がりそうです", 0, 1, 1155, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 103101, "あの人とのこと占って\nみようかな…\n好き…嫌い…\n好き♪…き、嫌い", 0, 1, 1156, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 103101, "私との相性ですか？\nそれなら占う必要は\nありませんよ…\nふふっ♪", 0, 1, 1157, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103101, "女難の相が\n出ていますね…\n気をつけてください", 0, 2, 1158, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103101, "今日のあなたの運勢は\n大きょ…\n大吉です", 0, 2, 1159, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103101, "父と散歩中です。\n毎日決まったコースを\n散歩させないと、\nストレスが溜まるので", 0, 2, 1160, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103101, "小僧！\nシノブに触るとは\nいい度胸じゃねぇか！", 0, 2, 1161, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 103101, "あなたに触られると、\n動悸が早くなります\nもしかして心霊現象？", 0, 2, 1162, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 103101, "今日もたくさんの幽霊\nに慕われていますね。\n素敵です", 0, 2, 1163, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103101, "おはようございます\n今日の運勢を\n占いましょうか？", 1, 2, 1164, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103101, "今日のラッキー\nアイテムはドクロです\n良ければ父を\nお貸ししましょうか？", 2, 2, 1165, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103101, "今日のあなたの運気は\n最高潮でした。\nもうすぐ今日は\n終わりますが", 3, 2, 1166, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103101, "お父さん。\nいつも、身だしなみに\n気を使ってるけど、\nあまり変わってないよ", 0, 3, 1167, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103101, "一休みしましょう。\n憑かれて疲れました", 0, 3, 1168, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103101, "かわいい…\n付喪神が宿らないかな", 0, 3, 1169, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103101, "お父さん、\nリラックスし過ぎると\n成仏しちゃうよ？", 0, 3, 1170, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103101, "気持ちいいです…\n極楽浄土みたい…", 0, 3, 1171, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103101, "はぁはぁ…\nお、お父さん、\n代わりに運動して…", 0, 3, 1172, 6, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103101, "お父さんも\n食べたいの？\n死んでるのに？", 0, 3, 1173, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103101, "この本…\nもしかして予言書？", 0, 3, 1174, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103102, "ここにいる幽霊は\nみんな幸せそうです", 0, 1, 1176, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103102, "お父さんが\n女の子ばかりに\n声をかけるので\n困っています", 0, 1, 1177, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103102, "成仏はうれしいこと\nです…でも友達が\n減るのは寂しいですね", 0, 1, 1178, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103102, "幽霊に好かれる人は\n素敵な方です\nもちろん、あなたも", 0, 1, 1179, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103102, "お父さん、そろそろ\n子離れを…しない？\nそうですか…", 0, 1, 1180, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 103102, "生きている人に\n好かれるには、\nどうすればいい\nのでしょう？", 0, 1, 1181, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 103102, "お父さん、どうしたら\nあの人のことを\n認めてくれますか？", 0, 1, 1182, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103102, "この部屋、風水的にも\n運気が上がりそうです", 0, 2, 1183, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103102, "シノブには指一本\n触れさせん！\nガブッ！！", 0, 2, 1184, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103102, "あなたの手相は…\nて、手を握るのって\n緊張しますね", 0, 2, 1185, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103102, "ラップ音？\nいえ、父のいびきです", 0, 2, 1186, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 103102, "私との相性ですか？\nそれなら占う必要は\nありませんよ…\nふふっ♪", 0, 2, 1187, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 103102, "前世って信じますか？\n私とあなたは\nもしかすると…", 0, 2, 1188, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103102, "今日のラッキー\nアイテムはドクロです\n良ければ父を\nお貸ししましょうか？", 1, 2, 1189, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103102, "明るい時間は\nあまり得意では\nないんです…", 2, 2, 1190, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103102, "夜のお散歩に\n行きませんか？\n知り合いの幽霊に\n挨拶して回るんです", 3, 2, 1191, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103102, "お父さん\nいつも、身だしなみに\n気を使ってるけど\nあまり変わってないよ", 0, 3, 1192, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103102, "うとうと…\nあれ…\n走馬灯が見える…", 0, 3, 1193, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103102, "かわいい…\nお父さんみたい…", 0, 3, 1194, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103102, "お父さん\nリラックスし過ぎると\n成仏しちゃうよ？", 0, 3, 1195, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103102, "快適です…\n幽霊もどんどん\n集まってきますね…", 0, 3, 1196, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103102, "はぁはぁ…\nお、お父さん、\n代わりに運動して…", 0, 3, 1197, 6, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103102, "お父さんの分も、\n私が食べるね", 0, 3, 1198, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103102, "この本の占い\n私の占いと違う…", 0, 3, 1199, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103201, "私だって、一人で\n買い物ぐらい\nできますわ…きっと", 0, 1, 1201, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103201, "そろそろ新しい\n案件を始めないと\nいけませんわね", 0, 1, 1202, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103201, "一日も早く、\n一人前の商人に\nなりたいものです", 0, 1, 1203, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103201, "平和を買うのには\nどれほどのお金が\n必要なのかしら？", 0, 1, 1204, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103201, "ここももう少し広いと\n落ち着くのですけど。\n改築しませんこと？", 0, 1, 1205, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 103201, "あの方はお金で雇った\nわけではないのに\n私といてくださる…", 0, 1, 1206, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 103201, "たとえ私が間違っても\nきっとあの方が\n正してくれますわ…", 0, 1, 1207, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103201, "私と、庶民向けの\nお店へ一緒に\n参りませんか？", 0, 2, 1208, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103201, "資産運用？\nお幾らほど\n増やしたいのです？", 0, 2, 1209, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103201, "どんぶり勘定だとよく\n言われるのですが…\n例えが小さすぎます！", 0, 2, 1210, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103201, "お金で解決するのは\n悪いことでは\nありませんわよね？", 0, 2, 1211, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 103201, "その、よろしければ\n私とお散歩など\nいかがです？", 0, 2, 1212, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 103201, "もしあなたさまが\n道に迷われるなら、\n私もお供いたします！", 0, 2, 1213, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103201, "早起きは三ルピの徳と\n申しますが…\nもっとお得ですわね", 1, 2, 1214, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103201, "昼食は町の屋台で\n立ったまま食べて\nみたいですわ！", 2, 2, 1215, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103201, "よろしければ\nお休み前のミルクを\n御一緒にいかが？", 3, 2, 1216, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103201, "だらしない身なりでは\n取引相手に信用して\nいただけませんものね", 0, 3, 1217, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103201, "この感触と安らぎ…\n素晴らしい\n費用対効果ですわ…", 0, 3, 1218, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103201, "何の見返りも求めず\nただひたすら愛でて\nいたいのです！", 0, 3, 1219, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103201, "目に見えないものに\nお金をかけるのが\n真の贅沢ですわ！", 0, 3, 1220, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103201, "誰ひとり寒い思いを\nしない町にするために\n私にできる事は…", 0, 3, 1221, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103201, "健康はお金では\n買えませんが\n運動器具は別ですわ！", 0, 3, 1222, 6, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103201, "なかなか\n美味ですわ\nシェフはどちらに？", 0, 3, 1223, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103201, "経済の仕組みを\nしっかりと\n学ばなくては…", 0, 3, 1224, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103202, "そろそろ新しい\n案件を始めないと\nいけませんわね", 0, 1, 1226, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103202, "ええと、これは\nおいくらで買ったん\nでしたっけ？", 0, 1, 1227, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103202, "お金を見せたら\n態度が変わる人は\n信用できませんわ", 0, 1, 1228, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103202, "地図が読めずとも\n道に迷わない方って\nいらっしゃるのですね", 0, 1, 1229, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103202, "この狭さも\n慣れれば\n快適ですわ♪", 0, 1, 1230, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 103202, "あの方はお金で雇った\nわけではないのに\n私といてくださる…", 0, 1, 1231, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 103202, "たとえ私が間違っても\nきっとあの方が\n正してくれますわ…", 0, 1, 1232, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103202, "一人前の商人に\n相応しい居振る舞いを\nしたいものですわ", 0, 2, 1233, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103202, "幼い頃\nお店屋さんごっこを\nなさいましたか？", 0, 2, 1234, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103202, "資産運用？\nお幾らほど\n増やしたいのです？", 0, 2, 1235, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103202, "今度一緒に…\nパーティにでも\n行きませんこと？", 0, 2, 1236, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 103202, "その、よろしければ\n私とお散歩など\nいかがです？", 0, 2, 1237, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 103202, "あなたさまの優しさは\nお金には\n代えられませんわね", 0, 2, 1238, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103202, "なぜだか\n眠り足りないような\n感じがしますわ…", 1, 2, 1239, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103202, "昼食は町の屋台で\n立ったまま食べて\nみたいですわ！", 2, 2, 1240, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103202, "よろしければ\nお休み前のミルクを\n御一緒にいかが？", 3, 2, 1241, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103202, "ドレスは汚れていない\nようですが…やだ\n髪がほつれてますわ！", 0, 3, 1242, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103202, "こういったものに\n腰を沈めると…\nき、気が緩んで…", 0, 3, 1243, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103202, "何の見返りも求めず\nただひたすら愛でて\nいたいのです！", 0, 3, 1244, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103202, "香りは質のいいものと\nそうでないものが\nはっきり分かりますわ", 0, 3, 1245, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103202, "誰ひとり寒い思いを\nしない町にするために\n私にできる事は…", 0, 3, 1246, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103202, "目標は遠くに\n置いた方が励みに\nなりますわ！", 0, 3, 1247, 6, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103202, "なかなか\n美味ですわ\nシェフはどちらに？", 0, 3, 1248, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103202, "商人って、物語では\n悪役にされやすいの\nですね…", 0, 3, 1249, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103301, "家の中のものを使って\n物ボケ、考えるべ！", 0, 1, 1251, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103301, "毎日の日課…それは\n炊事、洗濯、大喜利\nだべ！", 0, 1, 1252, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103301, "うちに塀ができたら、\nへー、って言いたい！", 0, 1, 1253, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103301, "オラが作る料理は\nこの後スタッフで\n美味しくいただくべよ", 0, 1, 1254, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103301, "エリザベスも\n連れてきてぇ～", 0, 1, 1255, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 103301, "あんちゃんも、\nエリザベスと同じ位\nめんこいべ♪", 0, 1, 1256, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 103301, "オラとあんちゃん、\n息がぴったりに\nなってきたべ…！", 0, 1, 1257, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103301, "おぉ！　そんな急に\nツッコまれたら、\nびっくらこくべやー！", 0, 2, 1258, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103301, "ネタはオラが書くけど\nあんちゃんが\n書きたかったら\n書いてもいいんだぞ？", 0, 2, 1259, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103301, "あんちゃん、オラには\n容赦なんかせずに\nツッコんでくれて\nいいから！", 0, 2, 1260, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103301, "押すなよ、\n絶対押すなよ\n…なんて\n言ってないべさ？", 0, 2, 1261, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 103301, "うう…あんちゃんに\nツッコまれたくて\nうずうずしてきたべ～", 0, 2, 1262, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 103301, "将来的には夫婦漫才\nってのも…あ、ひ、\nひとりごとだべさ～！", 0, 2, 1263, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103301, "おはよう！　一緒に\n牛の乳しぼりいくべ？", 1, 2, 1264, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103301, "お昼だ～腹が減っては\n笑いはできぬ～！", 2, 2, 1265, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103301, "今日もよく働いたべ！\nさ、今からは\nお笑いの練習だ～！", 3, 2, 1266, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103301, "舞台に上がる前に\n身なりは整えねぇと\nだめだべ～！", 0, 3, 1267, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103301, "ナイスなイスに\n座りたい、\nなんちゃって！", 0, 3, 1268, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103301, "めんこいのは、\nスベっても許される\nからずるいべな～？", 0, 3, 1269, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103301, "必要以上に\n癒されちまうべや～", 0, 3, 1270, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103301, "ぽかぽかだ…\nウケた会場みてぇだ", 0, 3, 1271, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103301, "お笑い芸人は、\n体が資本だべさ～！", 0, 3, 1272, 6, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103301, "もぐもぐ…\nボケることも\n忘れちまう美味さだ～", 0, 3, 1273, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103301, "ネタ作りのヒントが\n載ってねぇか探すべ", 0, 3, 1274, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103302, "さぁ今日も張り切って\n漫才の練習するべさ！", 0, 1, 1276, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103302, "この部屋でも、商品\n売っていいんだべか？", 0, 1, 1277, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103302, "眼鏡を頭の上に乗せて\n「メガネメガネ～」\nって言う練習した方が\nいいべか？", 0, 1, 1278, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103302, "漫才を持ち時間に\n収めるには、何度も\n練習するしかねぇべ", 0, 1, 1279, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103302, "エリザベスも\n連れてきてぇ～", 0, 1, 1280, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 103302, "うう…あんちゃんに\nツッコまれたくて\nうずうずしてきたべ～", 0, 1, 1281, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 103302, "あんちゃんに\n採れたての食材を\n食わしてやりてぇ～", 0, 1, 1282, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103302, "押すなよ\n絶対押すなよ\n…なんて\n言ってないべさ？", 0, 2, 1283, 1, 1, 0);
