INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103302, "あんちゃん、オラには\n容赦なんかせずに\nツッコんでくれて\nいいから！", 0, 2, 1285, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103302, "「なんでだべ～！」\nって言いながら\nツッコむといいべ！", 0, 2, 1286, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 103302, "あはは～あんちゃんの\nツッコミはやっぱり\n気持ちいいなぁ～", 0, 2, 1287, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 103302, "将来的には夫婦漫才\nってのも…あ、ひ\nひとりごとだべさ～！", 0, 2, 1288, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103302, "おはよう！\n今日もいい\nネタ作り日和だ！", 1, 2, 1289, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103302, "お昼だな\nご飯を食べたら、\nネタの練習するべ！", 2, 2, 1290, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103302, "もうこんな時間だべ\nネタ作りは\n明日にするべ…", 3, 2, 1291, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103302, "うーん…衣装、\nずれてねぇべか？", 0, 3, 1292, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103302, "ナイスなイスに\n座りたい\nなんちゃって！", 0, 3, 1293, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103302, "はう～\nなまらめんこいべや～", 0, 3, 1294, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103302, "必要以上に\n癒されちまうべや～", 0, 3, 1295, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103302, "笑いをとることを\n忘れるほど\nあったけぇべ～", 0, 3, 1296, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103302, "たくさん運動して\n動きでも笑いがとれる\nように頑張るべ～！", 0, 3, 1297, 6, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103302, "もぐもぐ…\nボケることも\n忘れちまう美味さだ～", 0, 3, 1298, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103302, "幅広く教養を\n身につけて\nいいネタ作るべ！", 0, 3, 1299, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103401, "通りにいる\nカップル…爆発\nすればいいのに…", 0, 1, 1301, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103401, "う～ん、仕事なんて\nしないで、\n飲んでたいな～", 0, 1, 1302, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103401, "今日は一日、\n外に出ない！\nもう決めたわ！", 0, 1, 1303, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103401, "お腹空いた…\nけど、外に出るのは\n面倒だなぁ", 0, 1, 1304, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103401, "うぅ…頭がガンガン\nする…昨日、\n飲み過ぎたかな…", 0, 1, 1305, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 103401, "デートとか…\n誘っても嫌がられ\nないかな…？", 0, 1, 1306, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 103401, "もしかして運命の\n相手だったり…\nするのかな", 0, 1, 1307, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103401, "やっぱり一杯目が\n一番おいしいわ～！\n…え、見てた！？", 0, 2, 1308, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103401, "頼れるお姉さんだって\nところ、もっと見せて\nあげるからね", 0, 2, 1309, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103401, "ねえ、一緒に買い物に\n行かない？　その…\nの、飲み物とか…", 0, 2, 1310, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103401, "ひっく、うふふ…\nもっとお姉さんと\nお話しようよ～", 0, 2, 1311, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 103401, "極力人付き合いは\nしない主義なんだけど\nキミは例外かも…", 0, 2, 1312, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 103401, "いつか…\nキミと一緒に飲めたら\n楽しいだろうな", 0, 2, 1313, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103401, "おはよう…朝は\nちょっとつらいな…\n低血圧なのよ…", 1, 2, 1314, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103401, "おっひるー♪\nなに食べよっかな～♪", 2, 2, 1315, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103401, "ふわあ…\n眠る前にちょっと\n一杯…冗談よ", 3, 2, 1316, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103401, "うう…\n髪がボサボサ…\nちゃんとしなきゃ…", 0, 3, 1317, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103401, "自然体でゴロゴロ\nするのって\n幸せだなぁ…", 0, 3, 1318, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103401, "こういうの男の子に\nプレゼントして\nもらいたいな…", 0, 3, 1319, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103401, "うわあ、\nこれ、私の好きな\n香りだー！", 0, 3, 1320, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103401, "ぽっかぽか…\nあとは熱い一杯が\nあれば…！", 0, 3, 1321, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103401, "家でゴロゴロして\n付いちゃったお肉を\n落とさないと…！", 0, 3, 1322, 6, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103401, "もぐもぐ…\nおーいしー！\n…太らないよね？", 0, 3, 1323, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103401, "大人の女らしく\n色んなことを学んで\nおかなくっちゃ", 0, 3, 1324, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103402, "仕事終わりの一杯の\nために、今日も\n頑張らないと", 0, 1, 1326, 1, 1, 0);
