INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103402, "あ…\nお皿、洗って\nなかった…", 0, 1, 1328, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103402, "もう少し、大人の\n魅力が出せるように\nなりたいな", 0, 1, 1329, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103402, "素敵な彼氏…\n欲しいなぁ…", 0, 1, 1330, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 103402, "彼と私\n釣り合うかな…\n大丈夫よね？", 0, 1, 1331, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 103402, "今後のために\nちょっとは花嫁修業\nしてみようかな…？", 0, 1, 1332, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103402, "何事も\nお姉さんと一緒に\n楽しく頑張りましょ？", 0, 2, 1333, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103402, "ね、ねえ！\nキミは年上と年下\nどっちが好きかな？", 0, 2, 1334, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103402, "もう…\n構ってくれないと\n泣いちゃうぞ～？", 0, 2, 1335, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103402, "お姉さんが～\n可愛がって\nあげようか～？", 0, 2, 1336, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 103402, "極力人付き合いは\nしない主義なんだけど\nキミは例外かも…", 0, 2, 1337, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 103402, "お姉さんとチュー\nする？　ふふふ～\n冗談で～す！", 0, 2, 1338, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103402, "さーて、今日も\nいっちょやるぞー！", 1, 2, 1339, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103402, "後でのんびり\nストレッチでも\nしようっと♪", 2, 2, 1340, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103402, "一日よく働いた～！\n自分にごほうび\nあげたいなー♪", 3, 2, 1341, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103402, "仕事の前に、髪とか\n整えないと…", 0, 3, 1342, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103402, "自然体でゴロゴロ\nするのって\n幸せだなぁ…", 0, 3, 1343, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103402, "もう大人だし…\nあんまり可愛いのは\n似合わないかな…？", 0, 3, 1344, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103402, "うわあ\nこれ、私の好きな\n香りだー！", 0, 3, 1345, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103402, "あったかーい…\nだ、だらけ\nちゃいそう…", 0, 3, 1346, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103402, "地道な努力が\nリア充への\n第一歩！", 0, 3, 1347, 6, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103402, "おーいしっ♪\nこのために\n生きてるわねえ", 0, 3, 1348, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103402, "大人の女らしく\n色んなことを学んで\nおかなくっちゃ", 0, 3, 1349, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103801, "大切な人に\n本をすすめるのって\nけっこう難しいなぁ", 0, 1, 1351, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103801, "き、きらーん☆\nお姉ちゃんに\n似てるかな…？", 0, 1, 1352, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103801, "そろそろ好きな\n作家さんの新刊が\n出るから楽しみ…", 0, 1, 1353, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103801, "ちょっと、休憩…\n本でも読もうかな", 0, 1, 1354, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103801, "もっとたくさんの本に\n囲まれて生活したいな", 0, 1, 1355, 5, 1, 0);
