INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103801, "最近調子がいいのは\n牧場の牛乳やチーズの\nおかげでしょうか？", 0, 2, 1361, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 103801, "読みたい本も\nあなたと行きたい所も\n増えるばっかりです", 0, 2, 1362, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 103801, "私の病気、\n悪化してるのかも！？\nすごく胸がドキドキ\nするんです…", 0, 2, 1363, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103801, "えっと、朝食の前には\nこの薬を飲んで…\n食後にはこの薬を…", 1, 2, 1364, 1, 1, 0);
