INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103801, "早く寝ないと\n体に良くないですよね\nでも気になっている\n本があって…", 3, 2, 1366, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103801, "寝てばかりなので、\n髪が乱れてないかな\n…？", 0, 3, 1367, 1, 1, 0);
