INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103802, "最近調子がいいのは\n牧場の牛乳やチーズの\nおかげでしょうか？", 0, 2, 1386, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 103802, "しおりを探している？\nあ、本に挟む方ですね\nドキドキしました…", 0, 2, 1387, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 103802, "私の病気\n悪化してるのかも！？\nすごく胸がドキドキ\nするんです…", 0, 2, 1388, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103802, "えっと、朝食の前には\nこの薬を飲んで…\n食後にはこの薬を…", 1, 2, 1389, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103802, "…お昼の薬？\nあ、飲み忘れてました\nありがとうございます", 2, 2, 1390, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103802, "この薬を飲むと\n眠くなっちゃうんです\nでもまだ、あなたと\nお話したいし～", 3, 2, 1391, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103802, "寝てばかりなので、\n髪が乱れてないかな\n…？　", 0, 3, 1392, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103802, "少し休もう\n…やっぱり\nたくさん休もう", 0, 3, 1393, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103802, "この子がいれば\n病室に一人でも\n寂しくないですね", 0, 3, 1394, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103802, "いろんな効能が？\n健康になる香りも\nあるのでしょうか？", 0, 3, 1395, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103802, "こうして温まって\nいると、病気も\n治る気がします", 0, 3, 1396, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103802, "私も、少しは\n体力をつけないと\nいけませんよね　", 0, 3, 1397, 6, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103802, "いっぱい食べて\n健康な体にならないと", 0, 3, 1398, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103802, "続きが気になって\n読むのを止める\nタイミングが…", 0, 3, 1399, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104001, "友達ができたら、\n山の上でお弁当…\n夢が広がります…", 0, 1, 1401, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104001, "あ、忘れずに\nお花に水を\nあげなきゃ…", 0, 1, 1402, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104001, "お菓子を作って、\nみなさんに振る舞って\n友達作戦！　とか…", 0, 1, 1403, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104001, "そろそろ新しい\n草人形を作って\n友達作りの特訓を…", 0, 1, 1404, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104001, "植物のお世話って、\nぼっちにとっては、\n救い…なんです…", 0, 1, 1405, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 104001, "ＢＢ団の活動日…\nまだかな…\nうふふふっ", 0, 1, 1406, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 104001, "もっともっとあの人の\nそばで、一緒に友達を\n作っていきたいな…", 0, 1, 1407, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104001, "あ、こんにちは！\n今日もＢＢ団で\nお世話になります！", 0, 2, 1408, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104001, "あの…私の友達作りの\n練習方法、間違って\nませんよね…？", 0, 2, 1409, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104001, "そうだ、今度、\n森に一緒に木の苗を\n植えに行きませんか？", 0, 2, 1410, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104001, "人とお話するのって、\n私、大好きです！\n友達ふやすぞ～！", 0, 2, 1411, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 104001, "あなたと話してると、\nまるで私がぼっちじゃ\nないみたいです！", 0, 2, 1412, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 104001, "友達もほしいけど…\nあなたに会えたことが\n私の一番の幸せです", 0, 2, 1413, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104001, "おはようございます！\n今日もＢＢ団、\nがんばるぞー！", 1, 2, 1414, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104001, "なんだか、お腹が\n空いてきました。\nお昼ごはんー♪", 2, 2, 1415, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104001, "もう眠いかもです…\n明日は友達、\nできるといいなあ…", 3, 2, 1416, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104001, "私がもっと\n可愛ければ、\n友達できるかなあ？", 0, 3, 1417, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104001, "ふわあ…のんびり\nしすぎないように\n気を付けなきゃです", 0, 3, 1418, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104001, "かわいいなあ。\nこの子とも\n友達になりたい…", 0, 3, 1419, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104001, "いい香りです…\n精神統一して\n友達のイメトレ…", 0, 3, 1420, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104001, "灯りを見てると、\n心がなごみます～", 0, 3, 1421, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104001, "もぐもぐ…\nおいしいです…！\nみなさんもぜひ！", 0, 3, 1422, 6, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104001, "おいしいです…\n私に友達がいたら、\n分けてあげたいな", 0, 3, 1423, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104001, "友達作りの\n参考になりそうなこと\n書いてないかな～？", 0, 3, 1424, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104002, "友達作りは、焦らず\n慌てず、じっくりと…", 0, 1, 1426, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104002, "ひとつ屋根の下\nって感じ…\nいいですねえ…", 0, 1, 1427, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104002, "煮汁、煮汁が\n噴きこぼれて\nいませんか～", 0, 1, 1428, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104002, "今度、みなさんと\nピクニックとか…\nいってみたいな", 0, 1, 1429, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104002, "ぼっちでもいい\nたくましく育てば\n…なんて", 0, 1, 1430, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 104002, "最近、毎日あの人の\nこと考えちゃいます\nどうしてだろう…", 0, 1, 1431, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 104002, "もっともっとあの人の\nそばで、一緒に友達を\n作っていきたいな…", 0, 1, 1432, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104002, "あ！　ＢＢ団の活動\nですか？　嬉しくて\n飛び跳ねちゃいます！", 0, 2, 1433, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104002, "あなたとのお話で友達\n作りの練習になるなら\n一石二鳥です！", 0, 2, 1434, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104002, "私少しは人と話しても\nテンパらなくなって\nきましたかね…？", 0, 2, 1435, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104002, "あの、友達作りの\n新しい作戦\n考えてみたんですっ！", 0, 2, 1436, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 104002, "あなたとお話する時の\nこの気持ち、私の宝物\nなんです…えへへ", 0, 2, 1437, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 104002, "友達もほしいけど…\nあなたに会えたことが\n私の一番の幸せです", 0, 2, 1438, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104002, "朝ですね！\nこんないい朝は\n友達作りのチャンス！", 1, 2, 1439, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104002, "あの…そろそろお腹\nすきませんか？\nお昼ごはんは…", 2, 2, 1440, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104002, "もう眠いかもです…\n明日は友達\nできるといいなあ…", 3, 2, 1441, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104002, "私…見た目\nそこまで変じゃ\nないですよね…？", 0, 3, 1442, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104002, "ほわあ～…\nふかふかって\nいいものですね～", 0, 3, 1443, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104002, "この子みたいな\nあいくるしさを\n目指したいです！", 0, 3, 1444, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104002, "灯りを見てると\n心がなごみます～", 0, 3, 1445, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104002, "ぬくぬくで、なんだか\n眠くなっちゃいますね", 0, 3, 1446, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104002, "身体を鍛えて\nみなさんを\n守ります！", 0, 3, 1447, 6, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104002, "もぐもぐ…\nおいしいです…！\nみなさんもぜひ！", 0, 3, 1448, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104002, "友達作りの\n参考になりそうなこと\n書いてないかな～？", 0, 3, 1449, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104201, "唱喚の歌は、みだりに\n歌っていいものでは\nないんです", 0, 1, 1451, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104201, "ここでアイドルの\n練習は、さすがに\n迷惑ですよね？", 0, 1, 1452, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104201, "練習してもあんまり\n疲れなくなった…\nアイドルのおかげかな", 0, 1, 1453, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104201, "人々の平和を守る旅に\n同行してくれる人を\n探していて…", 0, 1, 1454, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104201, "あとで曲を復習して\nおかないと…", 0, 1, 1455, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 104201, "踊りを見られるのは\nまだ恥ずかしいけど\nあの人になら…", 0, 1, 1456, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 104201, "浜辺で曲を書こうと\n思うんですけど…\n一緒に行きませんか？", 0, 1, 1457, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104201, "いつも応援ありがとう\nございます♪　これは\nあ、握手の練習です！", 0, 2, 1458, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104201, "え、もう練習の時間？\nごめんなさい\nすぐ準備します！", 0, 2, 1459, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104201, "守るべき人たちに\n笑顔でいてもらうのも\n大切なんですね", 0, 2, 1460, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104201, "歌える曲を増やそうと\n思って。どんな曲が\n聞きたいですか？", 0, 2, 1461, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 104201, "～～♪　あれ？\n今私、歌ってました？\n気づきませんでした", 0, 2, 1462, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 104201, "…歌って欲しい？\n喜んで♪\nどの歌にしましょう？", 0, 2, 1463, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104201, "おはようございます♪\n少し歌の練習を\nしてきますね", 1, 2, 1464, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104201, "もうお昼なんですか？\n練習に夢中で\n気づきませんでした", 2, 2, 1465, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104201, "明日の予定は…\nアイドルはやることが\n多いみたいで…", 3, 2, 1466, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104201, "見た目については\n前よりも気をつけてと\nノゾミに言われてます", 0, 3, 1467, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104201, "歌の調子をよくする\nためには休息も\n必要ですよね…", 0, 3, 1468, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104201, "この子たちを\n相手に練習して\nみようかな…♪", 0, 3, 1469, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104201, "…疲れてるのかな\n落ち着けているのが\n自分でもわかります", 0, 3, 1470, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104201, "ここにいると\n穏やかな旋律が\n浮かんできます…", 0, 3, 1471, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104201, "歌うためには\n体力づくりは\n欠かせません", 0, 3, 1472, 6, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104201, "歌った後は\nお腹が空いて\nしまうんです", 0, 3, 1473, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104201, "音楽について\n理論も勉強\nしないと…", 0, 3, 1474, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104202, "歌うことの楽しさ\n分かってきました\nもっと歌いたいな…", 0, 1, 1476, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104202, "練習してもあんまり\n疲れなくなった…\nアイドルのおかげかな", 0, 1, 1477, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104202, "精霊も強くなったし\nもっとたくさんの人を\n守りたいです", 0, 1, 1478, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104202, "最近、よく頭の中に\n歌が浮かんできます\n不思議な感覚…", 0, 1, 1479, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104202, "楽譜を買いました\n思いついた歌を\n書き留めておくために", 0, 1, 1480, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 104202, "浜辺で曲を書こうと\n思うんですけど…\n一緒に行きませんか？", 0, 1, 1481, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 104202, "私の中にある\n私だけの歌…\n作ってみようかな？", 0, 1, 1482, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104202, "守るべき人たちに\n笑顔でいてもらうのも\n大切なんですね", 0, 2, 1483, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104202, "歌える曲を増やそうと\n思って。どんな曲が\n聞きたいですか？", 0, 2, 1484, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104202, "魔物退治ですか！？\n…違います？\n良かった…", 0, 2, 1485, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104202, "いつも応援ありがとう\nございます♪　これは\nあ、握手の練習です！", 0, 2, 1486, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 104202, "…歌って欲しい？\n喜んで♪\nどの歌にしましょう？", 0, 2, 1487, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 104202, "～～♪　あれ？\n今私、歌ってました？\n気づきませんでした", 0, 2, 1488, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104202, "呼吸を深く、大きく…\nこれでちゃんと\n目が覚めますよ", 1, 2, 1489, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104202, "少し休んだら\n午後の練習に\n行ってきますね", 2, 2, 1490, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104202, "曲を作っていたら\nもうこんな時間…\nおやすみなさい", 3, 2, 1491, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104202, "見た目については\n前よりも気をつけてと\nノゾミに言われてます", 0, 3, 1492, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104202, "あ～…♪\nよく体がほぐれます…\nいい歌が歌えそうです", 0, 3, 1493, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104202, "かわいいと思う\n気持ちを、歌にしても\nいいかも知れませんね", 0, 3, 1494, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104202, "…疲れてるのかな\n落ち着けているのが\n自分でもわかります", 0, 3, 1495, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104202, "ここにいると\n穏やかな旋律が\n浮かんできます…", 0, 3, 1496, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104202, "もっと体力をつけて\n最高のライブを\nファンに届けます！", 0, 3, 1497, 6, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104202, "これでも一応\n体形には気をつけて\n食べてるんですよ？", 0, 3, 1498, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104202, "読書は歌に\n新しい風を\n吹き込んでくれます", 0, 3, 1499, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104301, "店の新メニューでも\n考えるかな", 0, 1, 1501, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104301, "あたしの店から、\n出前を届けようか？", 0, 1, 1502, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104301, "パトロール、\nパトロール…\nここは異常なしかな？", 0, 1, 1503, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104301, "腹へったら食う！\n悩んだら寝る！\nそれが一番だろ？", 0, 1, 1504, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104301, "のんびりし過ぎるのも\n…性に合わねぇな", 0, 1, 1505, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 104301, "そろそろあいつ、\n腹すかしてるん\nじゃないかな？", 0, 1, 1506, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 104301, "今日の献立は…\nあいつの好きなものに\nしてやるか", 0, 1, 1507, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104301, "強くなりたいなら、\nあたしがビシバシ\n鍛えてやるよ", 0, 2, 1508, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104301, "天気がいい日は\n外で運動したく\nならねぇか？", 0, 2, 1509, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104301, "腹減ったか？　なんか\n作ってやろうか？", 0, 2, 1510, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104301, "ん？　なんだよ、\n相談事か？\nなんでも言ってくれ", 0, 2, 1511, 4, 1, 0);
