INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 104301, "お前って不思議だよな\n一緒にいるとなんだか\n…なんでもねーよ", 0, 2, 1513, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104301, "気持ちいい朝だな！\n体を動かしたい気分だ\n付き合ってくれるか", 1, 2, 1514, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104301, "昼飯もう食ったか？\nまだなら、あたしが\nなんか作ってやるよ", 2, 2, 1515, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104301, "日中たっぷり動いたら\nそのぶん夜はしっかり\n休んどけよな", 3, 2, 1516, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104301, "た、たまにはその…\nかわいい格好とか\nしても…", 0, 3, 1517, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104301, "ふわぁ、\n一休みすっかなぁ～", 0, 3, 1518, 2, 1, 0);
