INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104301, "へー　いい匂いだな\nあいつもこういうの\n好きなのかな…", 0, 3, 1520, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104301, "ふわぁ〜！\n温かいと、つい\nウトウトしちまうな", 0, 3, 1521, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104301, "オラオラオラーーー！\n燃えるぜーーー！", 0, 3, 1522, 6, 1, 0);
