INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104501, "最近なにも\nしてくれません…\n焦らしてるんですね", 0, 1, 1551, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104501, "ぐふふ、じゅるり…\nい、いけない、また\nいつの間にかよだれが", 0, 1, 1552, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104501, "好きな武器といえば\n剣より槍より\nやっぱり鞭…ぐふふ", 0, 1, 1553, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104501, "「無数の触手が\nクウカの柔肌に\n食い込み」…ぐふふ", 0, 1, 1554, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104501, "もしドＳさんが\nＭに転じたらと思うと\n身の毛がよだちます！", 0, 1, 1555, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 104501, "ドＳさんを登場\nさせるだけで、妄想に\n無限の広がりが！", 0, 1, 1556, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 104501, "あっ…\nジロジロ見てたの\n気づいちゃいました？", 0, 1, 1557, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104501, "視線を感じると\n思ったら…やはり\nドＳさんでしたか", 0, 2, 1558, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104501, "今日は\nどんなヒドイことを\nしてくれるんですか？\nぐふふふ", 0, 2, 1559, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104501, "いつの間にそこに！？\nまさかクウカを監視\nしていたのですか！？", 0, 2, 1560, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104501, "クウカに触るつもり\nですか？　触るつもり\nなのですねっ！？", 0, 2, 1561, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 104501, "クウカにナニをする\nつもりですか…\n期待してしまいます！", 0, 2, 1562, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 104501, "ドＳさんの視界に入る\nだけでドキドキが…\nこれが恋！？", 0, 2, 1563, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104501, "おはようございます。\n朝からお盛んですね？\nぐふふ…", 1, 2, 1564, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104501, "こんなにも日の高い\nうちからドＳさんは…\nじゅるり", 2, 2, 1565, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104501, "夜はこれから、\nですよ…\nぐふ、ぐふふふ…", 3, 2, 1566, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104501, "キレイにおめかし\nしたのに力ずくで…\nぐふふふ", 0, 3, 1567, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104501, "ソファの背にクウカを\n押し付けて…ぐふふ\nいいですね\nソファドン", 0, 3, 1568, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104501, "「見た目とは裏腹に\nそれは激しく攻め\nたてて」…ぐふふふ", 0, 3, 1569, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104501, "香りを吸ったクウカの\n頬は紅潮し目はうるみ\nもはや抗う力も…！", 0, 3, 1570, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104501, "ああ、身体が\n火照って…\nぐふ、ぐふふ…", 0, 3, 1571, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104501, "はぁ、はぁ、こうして\nクウカの体力を\n奪ってから…ぐふふ", 0, 3, 1572, 6, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104501, "食欲が満たされたら\nつ、次はやはり…！\nそうなのですね！？", 0, 3, 1573, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104501, "この本の下に、\nそういう本を\nカムフラージュ？", 0, 3, 1574, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104502, "「ドＳさんと踊り子\n乱舞の一夜」…\n次の日記はこれで…", 0, 1, 1576, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104502, "最近なにも\nしてくれません…\n焦らしてるんですね", 0, 1, 1577, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104502, "も、妄想が\n足りません…\n禁断症状がっ…！", 0, 1, 1578, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104502, "もしドＳさんが\nＭに転じたらと思うと\n身の毛がよだちます！", 0, 1, 1579, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104502, "次はどんなドＳを\nされるのか、想像する\nだけで…ぐふふふ", 0, 1, 1580, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 104502, "じっとしてると\nドＳさんのことばかり\n考えてしまいますね", 0, 1, 1581, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 104502, "妄想が止まらなくて\nクウカどうにかなって\nしまいそうです！", 0, 1, 1582, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104502, "ドＳさんといると体が\n火照って…クウカは\nいやらしい子です！", 0, 2, 1583, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104502, "あっ…\nジロジロ見てたの\n気づいちゃいました？", 0, 2, 1584, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104502, "ドＳさんのおかげで\n新しい扉が開けそう\nです…じゅるり", 0, 2, 1585, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104502, "いやー！　ドＳさんに\n襲われ…襲って\nくれないんですか？", 0, 2, 1586, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 104502, "クウカはドＳさんに\n身も心も堕とされて\nしまったのですね", 0, 2, 1587, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 104502, "ドＳさんがお望みなら\nクウカは\nどんなことだって！", 0, 2, 1588, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104502, "朝だからこそ\nできる妄想が\nあります…ぐふふ", 1, 2, 1589, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104502, "こんな日の高いうち\nからドＳさんは…\nじゅるり", 2, 2, 1590, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104502, "今夜は一晩中…\nなんですね？\nぐふふ…", 3, 2, 1591, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104502, "意外と難しいんですよ\n正しい亀甲縛りと\nいうものは…", 0, 3, 1592, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104502, "くつろいで油断\nしているスキに何を\nされるのでしょう！？", 0, 3, 1593, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104502, "「見た目とは裏腹に\nそれは激しく攻め\nたてて」…ぐふふふ", 0, 3, 1594, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104502, "これは禁断の香り…\nああっ一段階上の\n妄想が開花するうぅ！", 0, 3, 1595, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104502, "ああっ、暖気がまるで\nロウソクのように\nクウカを攻めますっ！", 0, 3, 1596, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104502, "激しい攻めを受けきる\nためには、鍛えないと\nいけないのですね", 0, 3, 1597, 6, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104502, "こんなにいっぱい\n入りません…\nお腹にっ！", 0, 3, 1598, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104502, "どんな本も\nよく見ると\nいやらしいです…！", 0, 3, 1599, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104601, "この前遊んだ猫、\nモッフモフ\nだったにゃあ…♪", 0, 1, 1601, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104601, "ほっぺやお腹が、\n肉球ぐらいぷにぷに…\nこれはまずいにゃ…", 0, 1, 1602, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104601, "大きな仕事が\n決まったにゃ！\n腕が鳴るにゃ～♪", 0, 1, 1603, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104601, "おやつのたい焼き、\nつぶあんとこしあん、\nどっちに\nしようかにゃ？", 0, 1, 1604, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104601, "うにゃー…\nもうちょっとお昼寝\nしたかったにゃ…\nむにゃむにゃ…", 0, 1, 1605, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 104601, "キミに呼ばれると、\n猫まっしぐらな気分に\nなっちゃうにゃ♪", 0, 1, 1606, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 104601, "キミのおかげで\nギルドの仕事も\n大成功だにゃ♪\nありがとにゃ♪", 0, 1, 1607, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104601, "うずうず…その指、\nじゃれついても\nいいかにゃ？", 0, 2, 1608, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104601, "にゃ？　あたしに何か\nご入り用かにゃ？\nお安くしとくにゃ～♪", 0, 2, 1609, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104601, "うーん、なんだか\n退屈にゃ～\n遊んで、遊んで～？", 0, 2, 1610, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104601, "素早い動きのコツ？\nタダじゃ教えて\nあげられないにゃ～♪", 0, 2, 1611, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 104601, "そんなにあたしを\nなでたいにゃ？\nキミならなでさせて\nあげてもいいかにゃー", 0, 2, 1612, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 104601, "ふにゃあ、ねえねえ、\n一緒にゴロゴロ\nしようにゃあ？", 0, 2, 1613, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104601, "いい朝にゃ！\n顔を洗ってくるにゃ！", 1, 2, 1614, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104601, "むーん、そろそろ\nお昼…猫たちにも\n猫缶を…", 2, 2, 1615, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104601, "ふわあ…眠いにゃ…\n猫は布団で\n丸くなるにゃ", 3, 2, 1616, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104601, "客商売では、\n外見のチェックも\n欠かせないのにゃ！", 0, 3, 1617, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104601, "うにゃ～！\nお昼寝に\nぴったりにゃ", 0, 3, 1618, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104601, "これ…量産したら\n子どもにバカ売れ\nしそうだにゃ！", 0, 3, 1619, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104601, "この灯り、まるで\n猫みたいな\n癒し効果にゃ！", 0, 3, 1620, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104601, "ふにゃあ、\nあったかいと猫は丸く\nなっちゃうのにゃあ…", 0, 3, 1621, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104601, "本当は外で\n運動したいん\nだけどにゃ", 0, 3, 1622, 6, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104601, "むーん、\n美味しいにゃー！\nたい焼きに\n匹敵するにゃ！", 0, 3, 1623, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104601, "ふむふむ…猫の\nしつけ方は、と…", 0, 3, 1624, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104602, "かわいいネコの置物を\nゲットしたにゃ！\nどこに飾ろうか\nにゃ～♪", 0, 1, 1626, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104602, "仕事の交渉がうまく\n行ったにゃ～♪\nいい気分だにゃ～♪", 0, 1, 1627, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104602, "ねこじゃらしには\n食いついちゃうにゃ\nびしっ！　びしっ！", 0, 1, 1628, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104602, "ほっぺやお腹が、肉球\nぐらいぷにぷに…\nこれはまずいにゃ…", 0, 1, 1629, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104602, "さっきのたいやき\nおいしかったにゃ♪\nごろごろごろ…", 0, 1, 1630, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 104602, "手作りのたい焼き\nあいつは食べて\nくれるかにゃ…？", 0, 1, 1631, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 104602, "あいつには、いつか\nたっぷり恩返しが\nしたいにゃ…", 0, 1, 1632, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104602, "にゃーに？　あたしの\nことがそんなに気に\nなるのかにゃ？", 0, 2, 1633, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104602, "あたしのこと…\nしっかり捕まえ\nといてにゃ？", 0, 2, 1634, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104602, "あたしの力が\n必要かにゃ？　猫の手\n貸してあげるにゃ♪", 0, 2, 1635, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104602, "たいやきとクレープを\n合わせれば…\n新しい商売のニオイが\nするにゃ…！", 0, 2, 1636, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 104602, "ふにゃあ、ねえねえ\n一緒にゴロゴロ\nしようにゃあ？", 0, 2, 1637, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 104602, "キミと一緒にいると\n楽しくなってつい\nじゃれちゃうにゃ！", 0, 2, 1638, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104602, "いい朝にゃ！\n顔を洗ってくるにゃ！", 1, 2, 1639, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104602, "そろそろおっひる～\nにゃーんか\n食べたい…", 2, 2, 1640, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104602, "うーん、眠くなって\nきたにゃ。あたしは\n夜行性じゃないのにゃ", 3, 2, 1641, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104602, "にゃにゃ…\n髪が乱れてるにゃ", 0, 3, 1642, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104602, "ふっかふかにゃ～\n猫がねころん\nじゃうにゃ", 0, 3, 1643, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104602, "これ、猫と匹敵する\nぐらいかわいいにゃ！", 0, 3, 1644, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104602, "猫でもこわくない\n優しい灯りにゃ！", 0, 3, 1645, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104602, "ぽっかぽか\nしあわせな気分にゃ～", 0, 3, 1646, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104602, "雨の日でも運動\nできるのは、濡れ\nなくて便利だにゃ！", 0, 3, 1647, 6, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104602, "美味しいにゃ～！\nこれ、朝昼晩\n食べたいにゃ！", 0, 3, 1648, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104602, "本を読んで\n猫の気持ちを\n勉強するのにゃ！", 0, 3, 1649, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104801, "内職手伝える人\nいないかしら？", 0, 1, 1651, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104801, "次の依頼は、\nこういう計画で…\nうん、完璧ね！", 0, 1, 1652, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104801, "次の特売セールに\n向けて、しっかり\n準備しないと！", 0, 1, 1653, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104801, "配達の後に売り子\n次に採取のバイト…\nうん、効率的ね", 0, 1, 1654, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104801, "一日に受ける\nバイトの数、もう少し\n増やしたいわね", 0, 1, 1655, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 104801, "師匠として\n頼りにされたいし\nもっと頑張らなきゃ！", 0, 1, 1656, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 104801, "男の子と効率的に\n仲良くなる方法…\nこればっかりは難題ね", 0, 1, 1657, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104801, "あら、\nちょうどよかった\n今から一緒に\nバイトでもどう？", 0, 2, 1658, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104801, "相談事かしら？\nそれくらいなら\n無料でいいわよ", 0, 2, 1659, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104801, "背後の気配なら\nすぐ分かるわ\n試してみる？", 0, 2, 1660, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104801, "道具やお金の管理は\n大丈夫？　手伝うから\nいつでも言って", 0, 2, 1661, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 104801, "暇ができたから\n働きたいけど…\nキミとの時間も大切ね", 0, 2, 1662, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 104801, "ねえ、あの…\n私はキミにとって…\nいや、何でもないわ", 0, 2, 1663, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104801, "おはよう！\n今日も朝から\nバイトよ！", 1, 2, 1664, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104801, "お昼時ね。\n少し休憩したら、\nまた働くわよ！", 2, 2, 1665, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104801, "もうこんな時間！\n明日もバイトで\n朝早いのよ…", 3, 2, 1666, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104801, "接客業の依頼が\nある時は、外見も\nキレイにしないとね", 0, 3, 1667, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104801, "はぁ～バイトで\n疲れたから癒される～", 0, 3, 1668, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104801, "これを内職で\n作ったら\n儲かるかしら？", 0, 3, 1669, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104801, "リフレッシュできたわ\nこれでまた、バリバリ\n働けるわね！", 0, 3, 1670, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104801, "風邪を引いたら効率が\n落ちちゃうから、\n暖めないと！", 0, 3, 1671, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104801, "働き続けるには\n健康第一！　それには\n運動が一番よ", 0, 3, 1672, 6, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104801, "もぐもぐ…\n腹が減っては\n戦ができぬ、よ！", 0, 3, 1673, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104801, "日々絶えず学んで、\n生活に活かすのよ！", 0, 3, 1674, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104802, "家計簿をつけなきゃ！\n覚えている時に\nやっておかないとね", 0, 1, 1676, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104802, "次の依頼は\nこういう計画で…\nうん、完璧ね！", 0, 1, 1677, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104802, "ギルドの経費\nあとちょっとだけ\n削れそうね…", 0, 1, 1678, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104802, "少しでも時給のいい\nバイトがしたいけど\nなかなか\n出会えないのよ", 0, 1, 1679, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104802, "今度のバイトも\nなかなか大変そう\nね…腕が鳴るわ", 0, 1, 1680, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 104802, "男の子と効率的に\n仲良くなる方法…\nこればっかりは難題ね", 0, 1, 1681, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 104802, "私、どうしたんだろ？\n仕事中もずっと\n彼のこと考えてる…", 0, 1, 1682, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104802, "この間はヘルプに\n来てくれてありがとう\n頼んで正解だったわ", 0, 2, 1683, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104802, "見つけた！　ちょうど\n頼みたいことが\nあるんだけど、いい？", 0, 2, 1684, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104802, "今から\n休憩するところなの\n一緒にどう？", 0, 2, 1685, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104802, "ん？　どうしたの？\n私のことが\n気になるの？", 0, 2, 1686, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 104802, "…たまには甘えても\nいい？　私だって\n弱る時はあるのよ", 0, 2, 1687, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 104802, "暇ができたから\n働きたいけど…\nキミとの時間も大切ね", 0, 2, 1688, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104802, "おはよう！\nいい朝ね。一緒に\nランニングでもいく？", 1, 2, 1689, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104802, "お昼時ね\n少し休憩したら\nまた働くわよ！", 2, 2, 1690, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104802, "もうこんな時間！\n明日もバイトで\n朝早いのよ…", 3, 2, 1691, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104802, "女の子らしく…\nどうも忘れちゃう\nのよね", 0, 3, 1692, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104802, "ここで少し\n休んだら\nまたバイトよ！", 0, 3, 1693, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104802, "これを内職で\n作ったら\n儲かるかしら？", 0, 3, 1694, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104802, "仕事から帰ってきて\nこういうのがあると\n素敵ね", 0, 3, 1695, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104802, "しっかり身体を\n温めて、血流を\nよくしなくっちゃ", 0, 3, 1696, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104802, "これで休憩中も\n無駄なく\n鍛えられるわ♪", 0, 3, 1697, 6, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104802, "もぐもぐ…よく\n食べて、よく働く！\nそれが私のモットーよ", 0, 3, 1698, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104802, "日々絶えず学んで\n生活に活かすのよ！", 0, 3, 1699, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104901, "暇だし、お料理と\nお掃除とお洗濯と\n買い物しようかな？", 0, 1, 1701, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104901, "こうして歩いていれば\n弟くんが声をかけて\nくれるはず…", 0, 1, 1702, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104901, "弟くんがどんな生活を\nしてるのか調べるのも\nお姉ちゃんの務め！", 0, 1, 1703, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104901, "くんくん、\n弟くんのにおい…\n近くにいるね？", 0, 1, 1704, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104901, "お姉ちゃんの痕跡を\n部屋中に\n残しちゃおっと♪", 0, 1, 1705, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 104901, "この部屋を、\n私と弟くんの\n愛の巣に…", 0, 1, 1706, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 104901, "はぁ～…弟くん\n禁断症状だよ～", 0, 1, 1707, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104901, "弟くんの部屋は\nお姉ちゃんの\n部屋だよね～", 0, 2, 1708, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104901, "いいこいいこ～♪\nどう、お姉ちゃんの\nナデナデは？", 0, 2, 1709, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104901, "姉と弟の外出は\nデートって\nいうのかな？", 0, 2, 1710, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104901, "もう！\nイタズラする子には\nお姉ちゃんチョップ！", 0, 2, 1711, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 104901, "お姉ちゃんが\nぎゅ～ってして\nあげるね♪", 0, 2, 1712, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 104901, "あ、あのね…\nたまにはお姉ちゃんも\n甘えていい？", 0, 2, 1713, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104901, "着替えは？　荷物は？\nお出かけの準備は\nお姉ちゃんが手伝うね", 1, 2, 1714, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104901, "出かけるなら\n明るいうちに\n帰ってくるんだよ！", 2, 2, 1715, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104901, "まだ起きてるの？\nそれじゃあ、\nお姉ちゃんも\n夜更かししちゃお☆", 3, 2, 1716, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104901, "きれいなお姉ちゃん\n好きだよね♪", 0, 3, 1717, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104901, "ここにお姉ちゃんの\n匂いを、た～っぷり\n残しておこうっと♪", 0, 3, 1718, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104901, "かわいい♪\nお姉ちゃんチョップ！", 0, 3, 1719, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104901, "この香り、\n弟くん好きかな？", 0, 3, 1720, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104901, "これ、弟くんみたいに\nあったかいね～", 0, 3, 1721, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104901, "ダイエットダイエット\nきれいなお姉ちゃんに\nならなくちゃ", 0, 3, 1722, 6, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104901, "あ、これ\n弟くんが好きな\n味付けだね！", 0, 3, 1723, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104901, "弟くんが読んでいいか\nお姉ちゃんが先に\n中を確認しなくちゃ！", 0, 3, 1724, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104902, "暇だし、お料理と\nお掃除とお洗濯と\n買い物しようかな？", 0, 1, 1726, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104902, "こうして歩いていれば\n弟くんが声をかけて\nくれるはず…", 0, 1, 1727, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104902, "弟くんのお友達は\nみんな私の妹だよ♪", 0, 1, 1728, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104902, "弟くんがどんな生活を\nしてるのか調べるのも\nお姉ちゃんの務め！", 0, 1, 1729, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104902, "はっ！\n弟くんが困ってる\n気がする！", 0, 1, 1730, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 104902, "はぁ～…弟くん、\n禁断症状だよ～", 0, 1, 1731, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 104902, "お姉ちゃんの痕跡を\n部屋中に\n残しちゃおっと♪", 0, 1, 1732, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104902, "お姉ちゃん\nキミのお願いなら\nなんでも聞いて\nあげちゃうぞっ", 0, 2, 1733, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104902, "弟くんの部屋は\nお姉ちゃんの\n部屋だよね～", 0, 2, 1734, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104902, "お姉ちゃんの胸に\n飛び込んでおいでっ♪", 0, 2, 1735, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104902, "いいこいいこ～♪\nどう、お姉ちゃんの\nナデナデは？", 0, 2, 1736, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 104902, "あ、あのね…\nたまにはお姉ちゃんも\n甘えていい？", 0, 2, 1737, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 104902, "お風呂にする？\nごはんにする？\nそれとも…うふふ♪", 0, 2, 1738, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104902, "着替えは？　荷物は？\nお出かけの準備は\nお姉ちゃんが手伝うね", 1, 2, 1739, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104902, "お姉ちゃんが\nお昼ごはん\n作ってあげようか？", 2, 2, 1740, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104902, "眠れないの？\nお姉ちゃんが子守歌\n歌ってあげるね！", 3, 2, 1741, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104902, "弟くんを\n誘惑しちゃうぞ～♪", 0, 3, 1742, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104902, "ここにお姉ちゃんの\n匂いを、た～っぷり\n残しておこうっと♪", 0, 3, 1743, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104902, "かわいい♪\nお姉ちゃんチョップ！", 0, 3, 1744, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104902, "お肌に潤いが\nでてきたみたい～♪", 0, 3, 1745, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104902, "これ、弟くんみたいに\nあったかいね～", 0, 3, 1746, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104902, "ダイエットダイエット\nきれいなお姉ちゃんに\nならなくちゃ", 0, 3, 1747, 6, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104902, "弟くんの手作りなら\nもっとおいしくなると\n思うんだけどなぁ～", 0, 3, 1748, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104902, "弟くんをきちんと\n教育するために\nお姉ちゃんは勉強を\nかかさないのだ☆", 0, 3, 1749, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105001, "ん～、エレガントで\n大人なお部屋ね！\nアタシにぴったり！", 0, 1, 1751, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105001, "身長とバスト以外は\n完璧な大人\nなのになぁ～", 0, 1, 1752, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105001, "アタシの大人コーデで\nあいつも夢中ね♪", 0, 1, 1753, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105001, "朝顔の観察したら\n理系女子になれる\nかしら？", 0, 1, 1754, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105001, "レディは、どんな時も\nおしとやかに…って\nアタシも騒ぎたいー！", 0, 1, 1755, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 105001, "大人っぽい子が\n多いわね…アタシも\n負けてられないわ！", 0, 1, 1756, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 105001, "アタシが待ってる\nなんて、アイツは\n幸せものよねー", 0, 1, 1757, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105001, "わーい、アメだー♪\n食べる食べるー♪", 0, 2, 1758, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105001, "熱いハニーラテが\n飲みたーい！\n淹れてー淹れてー！", 0, 2, 1759, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105001, "なによ？　もしかして\n好きな子にイタズラ\nしたくなるってやつ？", 0, 2, 1760, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105001, "アタシに合う服\n雑誌で探そうっと！\nサイズじゃないわ、\nデザインとかの話よ！", 0, 2, 1761, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 105001, "ねーねー！\nアタシ、かわいい？\nかわいいわよね？", 0, 2, 1762, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 105001, "これから出かけるの？\nしっかりアタシを\nエスコートしてね！", 0, 2, 1763, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105001, "朝のスキンケアは\n大事なの！", 1, 2, 1764, 1, 1, 0);
