INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105101, "仕事も片付いたし\n部屋で\n実験実験…♪", 0, 1, 1802, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105101, "たまった治療代の\n請求書を\nまとめないと…", 0, 1, 1803, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105101, "新薬が出来たし\nすぐに試したいけど…\n実験台がいないわ", 0, 1, 1804, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105101, "部屋にこもりすぎると\n曜日の感覚が\nなくなるわね", 0, 1, 1805, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 105101, "あの子の記憶を\n取り戻せる薬…\n作れないものかしら", 0, 1, 1806, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 105101, "好意に気づいて貰える\n薬って、どうすれば\n作れるのかしら", 0, 1, 1807, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105101, "あら、ヒマなら\n薬草の整理を\n手伝って", 0, 2, 1808, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105101, "もしかして実験に\n付き合ってくれるの？\nえ？　違う？", 0, 2, 1809, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105101, "具合が悪い？\nなら診てあげる\nまずは触診から…", 0, 2, 1810, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105101, "植物に興味あるの？\nなら教えてあげるわ\n色々とね…", 0, 2, 1811, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 105101, "キミの傍にいるだけで\n高揚しちゃうのは\n何故かしら…？", 0, 2, 1812, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 105101, "私はおしおきする\n側のことが多いけど…\nキミにはされたいかも", 0, 2, 1813, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105101, "お昼までに調合用の\n花を集めたいの\n手伝ってくれる？", 1, 2, 1814, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105101, "あら、もうお昼？\nそろそろ一息\n入れましょうか", 2, 2, 1815, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105101, "夜も更けて来たわね\n私は朝まで実験だけど\n見学していく？", 3, 2, 1816, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105101, "医者たるもの\nいつだって\n清潔に…♪", 0, 3, 1817, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105101, "いいわねこれ\n仮眠の時に\n使わせてもらうわ", 0, 3, 1818, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105101, "ぬいぐるみなら\n動物も…", 0, 3, 1819, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105101, "いい香り…\n仕事場にも\n置いてみようかしら", 0, 3, 1820, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105101, "ここにいると\nついウトウト\nしちゃうわ…", 0, 3, 1821, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105101, "座ってる時間が多いと\n運動不足になるし\nたまにはいいわね", 0, 3, 1822, 6, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105101, "研究していると\nつい食べるのを\n忘れちゃうのよね", 0, 3, 1823, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105101, "実験もいいけど\nいつか成果を\n本にしないと…", 0, 3, 1824, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105102, "徹夜はツラいけど\n集中すると時間の\n感覚がなくなって…", 0, 1, 1826, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105102, "痛みに悶える顔って\nどうしてあんなに\n興奮するのかしら？", 0, 1, 1827, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105102, "彼に罵られたら\nどんな気分かしら\n意外と快感かも？", 0, 1, 1828, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105102, "毒を薄めると\n薬になるのって\n今でも不思議だわ", 0, 1, 1829, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105102, "綺麗なバラには\nトゲがあるけど\n魅力の一つよね", 0, 1, 1830, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 105102, "カレをさりげなく\n惚れさせる方法を\n研究するわよ…♪", 0, 1, 1831, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 105102, "好意に気づいて貰える\n薬って、どうすれば\n作れるのかしら", 0, 1, 1832, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105102, "構って欲しいの？\nじゃあ、君の体を\n調べさせて？", 0, 2, 1833, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105102, "実験に付き合って\n大丈夫よ、試すのは\nただの栄養剤だから", 0, 2, 1834, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105102, "欲しい薬が\nあったら言ってね\n調合してみるから", 0, 2, 1835, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105102, "おしおきされたいの？\nキミにだったら\nいつでもしてあげる", 0, 2, 1836, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 105102, "キミの傍にいるだけで\n高揚しちゃうのは\n何故かしら…？", 0, 2, 1837, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 105102, "私はおしおきする\n側のことが多いけど…\nキミにはされたいかも", 0, 2, 1838, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105102, "朝しか取れない\n植物があるの\n採取しに行くわよ", 1, 2, 1839, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105102, "お昼時ってやけに\n眠たくなるわよね\n…調べたくなるわ", 2, 2, 1840, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105102, "今日くらいは\nゆっくり眠りたいわ\n…キミと一緒にね", 3, 2, 1841, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105102, "身なりについて\n研究するのも\n楽しそうね", 0, 3, 1842, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105102, "休息に必要な道具は\n妥協せずに選ぶ！\nこれだけは譲れないわ", 0, 3, 1843, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105102, "たまにはこういう\n可愛らしいものに\n触れないとね♪", 0, 3, 1844, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105102, "いい香り…\n仕事場にも\n置いてみようかしら", 0, 3, 1845, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105102, "仕事がなかったら\nこのまますーっと\n眠りたいのに…", 0, 3, 1846, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105102, "運動しないと\n体型の維持が…", 0, 3, 1847, 6, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105102, "適度な食事も\n大切よね", 0, 3, 1848, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105102, "まさか私が\n恋愛読本を読む\n日が来るなんて…", 0, 3, 1849, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105201, "ぺっぺっ！\n…い、いけないっ！", 0, 1, 1851, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105201, "いっけな～い！\nひづめで床に傷が！", 0, 1, 1852, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105201, "太っちゃったかも～！\n伐採でダイエット\nしなくっちゃ！", 0, 1, 1853, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105201, "きちんと生活すると\n毛づやが良くなる\nみたいなのよね～", 0, 1, 1854, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105201, "みんな、体重\nどれくらいなのかな？", 0, 1, 1855, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 105201, "体力なら自信\nあるんだけど…\n恋にはちょっぴり\n臆病になっちゃう", 0, 1, 1856, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 105201, "いつか人間の女の子\nみたいなデート、\nしてみたいな～♪", 0, 1, 1857, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105201, "私ね「どこにでもいる\n普通の女の子」って\n言葉が憧れなんだ～♪", 0, 2, 1858, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105201, "チャームポイント？\n自分では分からない\nけど…まつげかなぁ？", 0, 2, 1859, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105201, "リマリマ～♪\nはっ！？\nわ、私ったら今何を？", 0, 2, 1860, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105201, "力仕事ならまかせて！", 0, 2, 1861, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 105201, "人間になりたいな～\nそれか、あなたが\n私みたいな姿に…♪", 0, 2, 1862, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 105201, "しょうがないなあ、\nこんなにモフらせるの\nあなただけだよ？", 0, 2, 1863, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105201, "今朝は寝癖がひどいわ\nしっぽの辺りとか～", 1, 2, 1864, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105201, "ランチは\nおしゃれなカフェで\nしてみたいな～♪", 2, 2, 1865, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105201, "お肌のケアに\nパックしながら\n寝ようかな～", 3, 2, 1866, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105201, "やだっ、\nまた寝ぐせついてる！\n直さないと！", 0, 3, 1867, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105201, "ミシッっていった！\n私、重すぎるかも！？", 0, 3, 1868, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105201, "あ！　あなたも\n美人さんね～♪", 0, 3, 1869, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105201, "はぁ、まったり…\n四足歩行しちゃい\nそうだわ…", 0, 3, 1870, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105201, "あったか～い♪\n…私、毛皮だから\n暑すぎるかも", 0, 3, 1871, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105201, "な、なんだか野生に\n帰っちゃいそう！", 0, 3, 1872, 6, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105201, "ぱくぱく、もぐもぐ、\nぺっぺっ…\nやだ、はしたない！", 0, 3, 1873, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105201, "きゃーーー！\nこの恋愛小説\nとっても\nドキドキしちゃう～", 0, 3, 1874, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105202, "素敵ね～、居るだけで\n女子力があがりそうな\nお部屋だわ～", 0, 1, 1876, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105202, "私、大丈夫かしら？\n他の子に怖がられたり\nしないかな…？", 0, 1, 1877, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105202, "動物苑…って\n嫌なギルド名ね…", 0, 1, 1878, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105202, "好き、嫌い、好き…\nあっ！　うっかりして\n花占いのお花\n食べちゃった！", 0, 1, 1879, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105202, "ここ、女の子らしい\nものでいっぱいね～！\nときめいちゃうわ～♪", 0, 1, 1880, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 105202, "体力なら自信\nあるんだけど…\n恋にはちょっぴり\n臆病になっちゃう", 0, 1, 1881, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 105202, "あの人が\n来てくれるまで薪割り\nでもしてよっかな？", 0, 1, 1882, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105202, "一緒にお出かけ？\nうれしいけど…\n心の準備が！\nはわわわ！", 0, 2, 1883, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105202, "どうしたの？\n急に抱きついてきて…\n癒されたいの？", 0, 2, 1884, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105202, "ぺっぺっ！\n…い、いけないっ！", 0, 2, 1885, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105202, "メタモルアップルを\nここで食べたら、\nみんな驚くわよね", 0, 2, 1886, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 105202, "しょうがないなあ、\nこんなにモフらせるの\nあなただけだよ？", 0, 2, 1887, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 105202, "人間になりたいな～\nそれか、あなたが\n私みたいな姿に…♪", 0, 2, 1888, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105202, "今朝は寝癖がひどいわ\nしっぽの辺りとか～", 1, 2, 1889, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105202, "ダイエット中だから\nお昼は抜いてるの～", 2, 2, 1890, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105202, "お肌のケアに\nパックしながら\n寝ようかな～", 3, 2, 1891, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105202, "ちゃんとかわいく\nしてないと刈られ…\n狩られちゃうもんね！", 0, 3, 1892, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105202, "ミシッっていった！\n私、重すぎるかも！？", 0, 3, 1893, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105202, "あ！　あなたも\n美人さんね～♪", 0, 3, 1894, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105202, "いい香り～\n森の中にいるみたいに\nとっても落ち着く～", 0, 3, 1895, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105202, "素敵～♪　…\nきゃっ！　私の毛が\nたいへんなことに～！", 0, 3, 1896, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105202, "な、なんだか野生に\n帰っちゃいそう！", 0, 3, 1897, 6, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105202, "何を食べるかより\n誰と食べるかが\n大事なのよね", 0, 3, 1898, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105202, "きゃーーー！\nこの恋愛小説\nとっても\nドキドキしちゃう～", 0, 3, 1899, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105301, "ヴァイスフリューゲル\nを盛り上げる方法、\n共に語らおう！", 0, 1, 1901, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105301, "私は子供ではない…！\nが、お菓子は\nいただこう！", 0, 1, 1902, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105301, "部屋はきちんと\n片づけておかなくては\n部屋の汚れは\n心の汚れだ", 0, 1, 1903, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105301, "むむむ…明日\n目が覚めたら、身長が\n伸びていないものか…", 0, 1, 1904, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105301, "お菓子が常備されて\nいる部屋。そんな\nものが、私の好みだ…", 0, 1, 1905, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 105301, "あいつとの新たな\n鍛錬が\n待ち遠しいな…！", 0, 1, 1906, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 105301, "最近、あいつと\n過ごしていて、毎日が\n充実しているな…", 0, 1, 1907, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105301, "軍人の本分は日々の\n研鑽にあるからな。\n今日も鍛錬に\n行かないか？", 0, 2, 1908, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105301, "毎日牛乳を飲んでいる\nのだが、なかなか背が\n伸びないものだな…", 0, 2, 1909, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105301, "貴公にも、飴をくれて\nやろうか？\nあまいぞ～！\n美味しいぞ～！", 0, 2, 1910, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105301, "私ともっと高く、\n共に飛ぼう！", 0, 2, 1911, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 105301, "貴公は、本当に頼りに\nなる男だな…いや、\nふとそう思っただけだ", 0, 2, 1912, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 105301, "貴公と共にいられる\nことは私の喜びだ。\nこれからも\nよろしく頼むぞ", 0, 2, 1913, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105301, "うむ、いい朝だ。\n本国の夜明けを\n思い出してしまうな！", 1, 2, 1914, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105301, "そろそろ昼食に\nするか。腹が減っては\n戦はできないからな", 2, 2, 1915, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105301, "ふわああ…私は子供\nではないが…眠い時は\n眠いのだ…", 3, 2, 1916, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105301, "身だしなみは、心の\n在りようを映すから\n気をつけなくてはな", 0, 3, 1917, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105301, "ふう…軍人たるもの\n常日頃、油断は\nできないのだが…\nお、落ち着くな…", 0, 3, 1918, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105301, "うむ…このかわいさ…\n本国に持って\n帰りたい…", 0, 3, 1919, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105301, "このともし火、戦いに\n明け暮れる我らの心を\n癒すものだな…", 0, 3, 1920, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105301, "はあ～、ぽかぽか…\nい、いや！\n気を緩めないぞっ！", 0, 3, 1921, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105301, "うおおおおおっ！\n鍛錬、鍛錬、\n鍛錬っ！！", 0, 3, 1922, 6, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105301, "いつ戦いになっても\n困らぬように、\n腹八分目に\nしておくのだ！", 0, 3, 1923, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105301, "これを読んで、新たな\n戦術を編み出すぞっ！", 0, 3, 1924, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105302, "たまには掃除でも\nするか。部屋が\n綺麗になるのは\n喜ばしいことだ", 0, 1, 1926, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105302, "部屋の中では\n剣を振り回せないのが\n玉に瑕だな…", 0, 1, 1927, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105302, "お菓子が常備されて\nいる部屋。そんな\nものが、私の好みだ…", 0, 1, 1928, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105302, "毎日牛乳を飲んでいる\nのだが、なかなか背が\n伸びないものだな…", 0, 1, 1929, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105302, "家庭を守れない者が\n国を守れるはずが\nない。この部屋も\n私が守るぞ！", 0, 1, 1930, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 105302, "あいつは今頃、何を\nしているのだろうな…\nふふっ", 0, 1, 1931, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 105302, "あいつと会える日が\nこんなにも待ち遠しく\nなるとはな…", 0, 1, 1932, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105302, "軍人の本分は日々の\n研鑽にあるからな\n今日も鍛錬に\n行かないか？", 0, 2, 1933, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105302, "おお、今日も鍛錬の\n時間だったか！\nすまないな\nすぐ行く！", 0, 2, 1934, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105302, "私は子供ではない！\nが…たまには童心に\n帰りたい時もある…", 0, 2, 1935, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105302, "貴公にも、飴を\nくれてやろうか？\nあまいぞ～！\n美味しいぞ～！", 0, 2, 1936, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 105302, "私と歩きたいのか…？\nそうだな。二人で\n散歩でも行こうか", 0, 2, 1937, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 105302, "貴公と共にいられる\nことは私の喜びだ\nこれからも\nよろしく頼むぞ", 0, 2, 1938, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105302, "うむ、いい朝だ\n本国の夜明けを\n思い出してしまうな！", 1, 2, 1939, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105302, "そろそろ昼時だな\n鍛錬の手を緩めて\n昼食にするか", 2, 2, 1940, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105302, "寝る前に…あと少し\nだけ、鍛錬を…", 3, 2, 1941, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105302, "ヴァイス\nフリューゲルの一員と\nして恥ずかしくないよう\n身なりを整えなくては", 0, 3, 1942, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105302, "ふう…軍人たるもの\n常日頃、油断は\nできないのだが…\nお、落ち着くな…", 0, 3, 1943, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105302, "うむ…このかわいさ…\n本国に持って\n帰りたい…", 0, 3, 1944, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105302, "このともし火\n戦いに明け暮れる\n我らの心を\n癒すものだな…", 0, 3, 1945, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105302, "はあ～、ぽかぽか…\nい、いや！\n気を緩めないぞっ！", 0, 3, 1946, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105302, "うおおおおおっ！\n鍛錬、鍛錬\n鍛錬っ！！", 0, 3, 1947, 6, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105302, "いつ戦いになっても\n困らぬように\n腹八分目に\nしておくのだ！", 0, 3, 1948, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105302, "うむ…脳細胞が\n活性化してきたぞ\n読書は大事だな", 0, 3, 1949, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105701, "私たちの騎空艇\nどんなデザインに\nしよう…？", 0, 1, 1951, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105701, "団員募集の\nポスター\n作ろうっと♪", 0, 1, 1952, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105701, "父さん…\n今頃どこで\n何してるんだろ？", 0, 1, 1953, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105701, "騎空艇に乗って、\n誰もいったことがない\n場所を見つけたいな", 0, 1, 1954, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105701, "父さん以上の\n世界一の\n騎空士になりたい！", 0, 1, 1955, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 105701, "仲間と行く空の旅…\n何度想像しても\nドキドキするなぁ", 0, 1, 1956, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 105701, "父さん…\n私にもね、大切な\n仲間ができたよ", 0, 1, 1957, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105701, "仕事の依頼？\nすぐに支度\nしてくるね！", 0, 2, 1958, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105701, "ねえねえ！\n団員募集のポスター\n一緒に作ろうよ！", 0, 2, 1959, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105701, "なーにー？\nどうしたの？\n今呼んだよね？", 0, 2, 1960, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105701, "今は休憩中だよ？\nだからあなたも\nゆっくりしてて", 0, 2, 1961, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 105701, "私にとってあなたは\nとっても頼りになる、\n自慢の団員だよ！", 0, 2, 1962, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 105701, "お父さんに会えたら、\n大切な仲間として\nあなたを紹介するね！", 0, 2, 1963, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105701, "おはよう！\n体の調子はどう？\n今日は頑張れそう？", 1, 2, 1964, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105701, "ねえねえ、お昼\n食べに行こうよ！\nお腹空いちゃった", 2, 2, 1965, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105701, "…あれ？\nおしゃべりしてたら\nもうこんな時間？", 3, 2, 1966, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105701, "どうせならオシャレな\n騎空士って\n呼ばれたいな", 0, 3, 1967, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105701, "ふぅ、疲れた～…\nちょっとだけ\n休んじゃおっと", 0, 3, 1968, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105701, "よし！　キミは\n今から団長代理だ！\n…なんてね♪", 0, 3, 1969, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105701, "いい香り～♪\n気持ちが\n落ち着く～…", 0, 3, 1970, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105701, "これって\n騎空艇に\n置けるかな…？", 0, 3, 1971, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105701, "どんな仕事でも\nこなせるような\n体力がないとね…！", 0, 3, 1972, 6, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105701, "あむっ…うん！\n美味しい～！\n今度作ってみよう♪", 0, 3, 1973, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105701, "空を冒険するには\n色んなことを\n知っておかないと…", 0, 3, 1974, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105702, "…戻らなくなったよ\nどうすれば元に\n戻る…？", 0, 1, 1976, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105702, "暇だよ…\nどこかに強い人は\nいないか？", 0, 1, 1977, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105702, "ジョブチェンジ！\n…叫んでも\n戻らないよ", 0, 1, 1978, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105702, "今のうちに、\n魔物退治の\n仕事に行くね！", 0, 1, 1979, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105702, "ハァ～…\nうん、気の巡りは\n問題ないね！", 0, 1, 1980, 5, 1, 0);
