INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 190801, "おはようございます！\nお掃除の方、\nはじめていきますね", 1, 2, 2155, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 190801, "皆さん、お昼ごはんの\nご要望がありましたら\n用意致しますよ♪", 2, 2, 2156, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 190801, "ここは夜でも\nにぎやかですね", 3, 2, 2157, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104701, "…\n少しうとうと\nしてしまった", 0, 1, 2158, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104701, "室内だし兜を脱ぐか\n…いや\nやはりやめておこう", 0, 1, 2159, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104701, "部屋にいると\n鎧が蒸し暑くなるが\n我慢せねば…", 0, 1, 2160, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104701, "仕事が門番だからか\n部屋にいても\nじっとしてしまう…", 0, 1, 2161, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104701, "少年と会話したことを\n思いだすと\n自然と楽しくなるな", 0, 1, 2162, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 104701, "少年のことを考えると\n顔がにやけてしまう", 0, 1, 2163, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 104701, "少年は今頃\n平和に過ごせて\nいるかな…", 0, 1, 2164, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104701, "ん？　悪いが\n兜は脱がないぞ", 0, 2, 2165, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104701, "私と遊びたいのか？\nそう言われても\n何をしていいやら…", 0, 2, 2166, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104701, "鎧が重くないかって？\n大丈夫。けっこう\n鍛えているからな", 0, 2, 2167, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104701, "少年、寒くはないか？\nいや自分がこうなので\n人が薄着に見えてね", 0, 2, 2168, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 104701, "君といると、兜の中が\nいつもより暑いんだ", 0, 2, 2169, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 104701, "私としゃべりたい？\n物好きだな\nでも、嬉しいよ", 0, 2, 2170, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104701, "早いな少年\nえ？　私の仕事に\n合わせてくれたのか", 1, 2, 2171, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104701, "昼食の誘い？\n嬉しいけど\n人前では食べないんだ", 2, 2, 2172, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104701, "王宮騎士団の\n日報を書かなければ…\n『何事もなし』、と", 3, 2, 2173, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104701, "鎧を着ているから\n感触が分かりづらい\nのが残念だ…", 0, 3, 2175, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104701, "か、かわいいな…\n私には似合わないが\nぎゅ…ってしよう", 0, 3, 2176, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104701, "良い香りだ…\n普段はどうしても\n鉄くさくなるからな", 0, 3, 2177, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104701, "わかっていたけど\n鎧を着たまま暖炉に\nあたると暑い…", 0, 3, 2178, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104701, "兜を脱がずに食べる\nうん、段々コツが\n掴めてきた…", 0, 3, 2180, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104701, "兜があっても\n意外と本くらいは\n難なく読めるんだ", 0, 3, 2181, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 102601, "居心地よくて\nゴロゴロしちゃうよ～\n…いつも通りだね", 0, 1, 2183, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 102601, "ドングリの木\n近くに生えてない？", 0, 1, 2184, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 102601, "快適な寝床があれば\nそれだけで幸せだねぇ", 0, 1, 2185, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 102601, "あたしと一緒に\nだらけてくれる人が\nいそうだねぇ", 0, 1, 2186, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 102601, "ここなら牧場よりも\n速く配達が\n届きそうだね～", 0, 1, 2187, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 102601, "あんパン買ったし\nあいつにも\n分けてあげよう", 0, 1, 2188, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 102601, "あいつと一緒に\nだらけられたら\n最高に幸せだね", 0, 1, 2189, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 102601, "ドングリでも\nつまみながら\n一緒にゲームしようよ", 0, 2, 2190, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 102601, "横になりたいから\n枕になっておくれよ～\nあんたあったかいしさ", 0, 2, 2191, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 102601, "ドングリあげるから\nあんパン買ってきて～", 0, 2, 2192, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 102601, "う～\n疲れちゃったよ～\nおんぶして運んで～", 0, 2, 2193, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 102601, "あんたといると\nリア充って\n感じがするよ", 0, 2, 2194, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 102601, "あんたが一生\n世話してくれたら\n人生安泰なんだけどね", 0, 2, 2195, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 102601, "ふわ～っ…\nおはよう…あたしは\nまだ眠いよ", 1, 2, 2196, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 102601, "そろそろお昼寝でも\nしたい気分だねぇ", 2, 2, 2197, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 102601, "徹夜でゲームするけど\nあんたもどうだい？\nあたしは強いよ", 3, 2, 2198, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 102601, "あ…だめだこれ。\n気持ち良くて\n落ちそう…", 0, 3, 2200, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 102601, "かわいいものに\n囲まれて寝るのも\nよさそうだねぇ…", 0, 3, 2201, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 102601, "心身ともに\nリラックスした状態で\nぐうたら出来そうだね", 0, 3, 2202, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 102601, "ふい～…\nごくらくごくらく…\n暖かいねぇ", 0, 3, 2203, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 102601, "食べてすぐ寝ると\n牛になるって\n本当なのかな？", 0, 3, 2205, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 102601, "おー！　この本なら\nあたしも居眠りせずに\n読めそうだよ", 0, 3, 2206, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106301, "ランドソル…\nおっきな街だなぁ…", 0, 1, 2208, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106301, "ロザリア…\nきっと、いるよね？", 0, 1, 2209, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106301, "最近、弓の練習\nサボっちゃってるな…", 0, 1, 2210, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106301, "今日の私は\n森番じゃなくて\n家番だよ！", 0, 1, 2211, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106301, "ちょっとだけ\n慣れてきたかも？", 0, 1, 2212, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 106301, "いつか、\nあの人と一緒に…", 0, 1, 2213, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 106301, "…なんか最近、\nあの人のこと\nばっかり…", 0, 1, 2214, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106301, "もしよかったら\n街に出かけない？", 0, 2, 2215, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106301, "この部屋、\n妖精さんも\n気に入るかも！", 0, 2, 2216, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106301, "今日は\nなににしよっか？", 0, 2, 2217, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106301, "今日はどこ行く？", 0, 2, 2218, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 106301, "たまには二人で…\nな、なんちゃって！", 0, 2, 2219, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 106301, "最近、\nずっと楽しいんだ！\nあなたのおかげかな", 0, 2, 2220, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106301, "おはよう！\n今日も一緒に\n頑張ろうね！", 1, 2, 2221, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106301, "お昼はどうする？\nあなたのオススメに\n連れて行って欲しいな", 2, 2, 2222, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106301, "ランドソルは\n夜でも賑やかだね！", 3, 2, 2223, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106301, "わぁっ！\nすっごく\nふわふわだよ！", 0, 3, 2225, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106301, "かわい～！\nぎゅってしたく\nなっちゃうかも！", 0, 3, 2226, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106301, "くんくん\nなんか…いい匂い？", 0, 3, 2227, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106301, "すっごいなー\nあったかいなー！", 0, 3, 2228, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106301, "わぁっ、おいしい！\nもっと食べたい\nかも…！", 0, 3, 2230, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106301, "も、文字は苦手…\nでも、頑張らないと\nだよね…！", 0, 3, 2231, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103601, "にんじんとピーマンを\n食べないでいい方法\nないかな…", 0, 1, 2233, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103601, "カップケーキが\n食べたいな…", 0, 1, 2234, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103601, "予習が終わりました！\nさて、次は復習を\nしないと…", 0, 1, 2235, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103601, "あの人の部屋にいる\n私って…もしかして\nふしんしゃなんじゃ？", 0, 1, 2236, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103601, "男の人は、みんな\nへんたいだと\n聞きました…", 0, 1, 2237, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 103601, "あの人に、一緒に\n遊んでほしいけど…\nな、なんていえば？", 0, 1, 2238, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 103601, "早く大人になりたいな\n…あの人と同じくらい", 0, 1, 2239, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103601, "ぼ、防犯魔石を\n鳴らしますよ！？", 0, 2, 2240, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103601, "へんたい！？　…\nあ、あなたでしたか～\nやっぱりへんたーい！", 0, 2, 2241, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103601, "これ宿題です\nやっておいて\nくださいね", 0, 2, 2242, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103601, "あ、遅刻ですよ\n待っていたんですから", 0, 2, 2243, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 103601, "にらめっこですか？\nあ、あなたの前で変顔\nするのはちょっと…", 0, 2, 2244, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 103601, "あの…何歳まで\n子供だと思いますか？", 0, 2, 2245, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103601, "顔を洗って、着替えて\n朝ごはんを食べて、\n歯を磨いて…", 1, 2, 2246, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103601, "て、鉄棒の練習！？　\nほ、他のことを\nしませんか？", 2, 2, 2247, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103601, "ね、眠くないです\n子供ではありません\nから…ふわぁ", 3, 2, 2248, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103601, "ひと休みしよっと\n………うん、\nひと休みおしまい♪", 0, 3, 2250, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103601, "つ、つぶらな瞳には\n弱いんです", 0, 3, 2251, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103601, "リラックスできますね\n集中力も\n上がりそうです", 0, 3, 2252, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103601, "あったかいと\n眠くなっちゃうな…\n勉強しなくちゃなのに", 0, 3, 2253, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103601, "ピーマンもこれくらい\nおいしければ\nいいのに…", 0, 3, 2255, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103601, "この本、むずかしい\n…もっと勉強\nがんばらなくちゃ", 0, 3, 2256, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101501, "今日はみんなに\nなにを読んで\nあげようかしら", 0, 1, 2258, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101501, "子どもたちと同じ\n目線で考えるのが\n大事よね", 0, 1, 2259, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101501, "お天気が良い日は\nみんなでお散歩に\n行きたいわね～", 0, 1, 2260, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101501, "鬼ごっこにかくれんぼ\n…みんなは\nどっちが好きかしら", 0, 1, 2261, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101501, "お遊戯会の配役、\n考え出すと\n大変だわ～", 0, 1, 2262, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 101501, "彼は優しいから、\nきっと良い\nお父さんになるわね", 0, 1, 2263, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 101501, "彼には私がいないと\n…っていうのは少し\n過保護すぎるかしら？", 0, 1, 2264, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101501, "保育園の行事を\n考えてるんだけど…\n良い案ないかしら？", 0, 2, 2265, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101501, "今度、子どもたちと\nおやつを作るから、\n食べに来てね", 0, 2, 2266, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101501, "よかったら一緒に\n絵本を作らない？", 0, 2, 2267, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101501, "あなたはいつも元気ね\n先生も見習わないと♪", 0, 2, 2268, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 101501, "先生は\nずーっと、ずーっと、\nあなたの味方だからね", 0, 2, 2269, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 101501, "あなたみたいな\nいい子をお世話できて\n先生は幸せものだわ", 0, 2, 2270, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101501, "おはよう～\nちゃんと朝ごはんは\n食べた？", 1, 2, 2271, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101501, "お昼寝の時間ね♪\nあなたも一緒に\n寝ましょうか", 2, 2, 2272, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101501, "すっかり真っ暗ね。\n今日も一日、\nよくできました♪", 3, 2, 2273, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101501, "ふかふかねぇ♪\n年甲斐もなく、\n飛び跳ねちゃいそう", 0, 3, 2275, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101501, "あらあら、かわいい！\n保育園のみんなに\n見せてあげたいわ", 0, 3, 2276, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101501, "いい香りね～\n保育園にも\n置こうかしら", 0, 3, 2277, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101501, "寒い日は、ここで\n子どもたちに絵本を\n読んであげましょ♪", 0, 3, 2278, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101501, "美味しい～♪\n遠足に持っていったら\nみんな喜びそう", 0, 3, 2280, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101501, "みんなが\n楽しめる読み聞かせの\n方法特集♪　素敵ねぇ", 0, 3, 2281, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104401, "世界の前に、\nこの部屋を征服\nしてやろう♪", 0, 1, 2283, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104401, "拠点とはやはり\n斯くあるべきじゃの", 0, 1, 2284, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104401, "眷属の物はわらわの物\n故に、この部屋は\nわらわの部屋じゃな！", 0, 1, 2285, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104401, "永く眠っている間に\n随分と世界は変わった\nようじゃな…", 0, 1, 2286, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104401, "うるさいのう～\nまったく、近頃の\n若いもんは…", 0, 1, 2287, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 104401, "あやつが来てから\n動悸が治まらぬ…\n如何ような病で\nあろうかの…", 0, 1, 2288, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 104401, "眷属よ、どこに行った\nのじゃ？\nわらわの世話を\n許可するのじゃ", 0, 1, 2289, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104401, "いずれ世界は\n我が手中に収まる\nふふふ、\n楽しみじゃのう", 0, 2, 2290, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104401, "眷属よ、わらわから\n離れてはならんぞ！\n子供に戻ってしまう\nではないか！", 0, 2, 2291, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104401, "トマトジュースが\n欲しいのじゃ！\n野菜ジュースでは\nダメだぞ！", 0, 2, 2292, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104401, "蚊にくわれたぞ！\nわらわの血を吸う\nとは～！", 0, 2, 2293, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 104401, "こら、頭をなでるな\nお主はこの姿でも\n子供扱いするのか？", 0, 2, 2294, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 104401, "お主が望むなら世界の\n半分をくれてやっても\n良いのじゃぞ？", 0, 2, 2295, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104401, "朝早くから感心じゃの\nさすがわらわの\n眷属じゃ", 1, 2, 2296, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104401, "太陽は苦手じゃ…\nこの部屋の中で\n過ごそうぞ", 2, 2, 2297, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104401, "夜も更けてきた！\nわらわ達の時間は\nこれからじゃ～♪", 3, 2, 2298, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104401, "よい座り心地じゃ\nわらわの玉座としよう", 0, 3, 2300, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104401, "可愛いではないか\nわらわの眷属に\nしてやろうかのう", 0, 3, 2301, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104401, "うむ、良い気持ちじゃ\nこれを作った者、\n褒めてつかわすぞ", 0, 3, 2302, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104401, "あたたかい…\n身も心も\n浄化されるようじゃ", 0, 3, 2303, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104401, "吸血鬼のわらわが、\nこのようなもの…\nうまい♪", 0, 3, 2305, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 104401, "このまま大人の姿を\n保つ方法…どこかに\n書いてないかのう", 0, 3, 2306, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105401, "いろんな人がいて\nいろんな服を着てて…\n目の保養になります♪", 0, 1, 2308, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105401, "レイ様への新作\nどんな服にしよう…\n方向性が絞れない～！", 0, 1, 2309, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105401, "忘れないうちに\n隠し糸のメンテナンス\nやっておこうっと！", 0, 1, 2310, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105401, "むっ！　あの人の\nスリーサイズは…\nほほう…なるほど♪", 0, 1, 2311, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105401, "ノゾミさんの\n衣装はああして\nチカさんのは…", 0, 1, 2312, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 105401, "私がアイドル…\n…実感ないけど\n精一杯やってみよう", 0, 1, 2313, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 105401, "まさか私が\nアイドルになるなんて\n思ってなかったです", 0, 1, 2314, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105401, "なんですかぁ？\n衣装作りで疲れて\n眠いんですぅ…", 0, 2, 2315, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105401, "あーっ！　また\n服がほつれてる…\nしょうがないですね", 0, 2, 2316, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105401, "はっ！　ちょうど今\n新しいデザインが\n思い浮かびました！", 0, 2, 2317, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105401, "暇そうですね？\n時間あるなら仕事\n手伝ってください！", 0, 2, 2318, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 105401, "どうしました？\n新しい服が\n欲しくなりましたか？", 0, 2, 2319, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 105401, "騎士さんのおかげで\nカルミナでも\n頑張れてますよ！", 0, 2, 2320, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105401, "おはようございます…\nなんで朝って毎日\nやってくるんでしょう", 1, 2, 2321, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105401, "お腹空きましたね\n騎士さんはちゃんと\n御飯食べましたか？", 2, 2, 2322, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105401, "夜の方が\n作業が捗るのは\n私だけですかね？", 3, 2, 2323, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105401, "このふかふか具合\nいい素材を\n使ってますね", 0, 3, 2325, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105401, "小さくて可愛い♪\nそうだ！　レイ様の\nぬいぐるみを…！", 0, 3, 2326, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105401, "こういう時くらい\n何も考えずに\nリラックスしないと♪", 0, 3, 2327, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105401, "炎を使った服って\nカッコ良いかも…\nでも材料がな～…", 0, 3, 2328, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105401, "何をするにも\n食事は大切！\n体力が資本です！", 0, 3, 2330, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105401, "私もいつか\nデザインを\nまとめて本に…", 0, 3, 2331, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107501, "またお魚を\n捕りにいこうかな♪", 0, 1, 2333, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107501, "そろそろみんなも\nお腹が空くころ\nじゃないですか～？", 0, 1, 2334, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107501, "ここなら、\n日焼けの心配は\nありませんね！", 0, 1, 2335, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107501, "お刺身に焼き貝…\nお腹が魚介類を\n求めてますっ！", 0, 1, 2336, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107501, "泳ぐのも食べるのも\n大得意です！", 0, 1, 2337, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 107501, "あの人と二人で\n浜辺を散歩…\n素敵ですねぇ", 0, 1, 2338, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 107501, "あの人に水着を\n褒めてもらえたら、\nすごく嬉しいなぁ", 0, 1, 2339, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107501, "たまには釣りじゃなく\n一緒に素潜りでも\n行きませんか？", 0, 2, 2340, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107501, "男の子に水着を\n見られるのって\n少し緊張しちゃいます", 0, 2, 2341, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107501, "ギルドハウスの\nみんなとも、\n海に行きたいですね！", 0, 2, 2342, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107501, "あなたの水着、\n私が選んであげます！", 0, 2, 2343, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 107501, "泳ぎの練習なら\n付き合いますよ！\n手取り足取り♪", 0, 2, 2344, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 107501, "水着姿が好きなら\nいくらでも\n着てあげますよぉ♪", 0, 2, 2345, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107501, "朝の海も\n幻想的で\nいいですよねぇ～！", 1, 2, 2346, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107501, "お昼はみんなで、\n海鮮バーベキューとか\nどうでしょう！", 2, 2, 2347, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107501, "夜の海は星が\nきれいですよ！\n見に行きましょう！", 3, 2, 2348, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107501, "泳ぎ疲れたので\nちょっと休憩です～", 0, 3, 2350, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107501, "あなたにも、\n可愛い水着を\n着せてあげたいです", 0, 3, 2351, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107501, "なんだか、\nリゾート気分です…", 0, 3, 2352, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107501, "ふい～、泳ぎで冷えた\n身体を暖めましょう～", 0, 3, 2353, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107501, "おいひ～です～\nこれ、海水浴にも\n持っていきたいですね", 0, 3, 2355, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107501, "南国のフルーツ図鑑\n…じゅるり…", 0, 3, 2356, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107501, "わ～♪　おいしそ…\nか、かわいいですね～", 0, 3, 2358, 10, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107501, "これくらい大きな貝…\n食べたいな～\n食べたいな～～", 0, 3, 2359, 11, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107601, "この格好で\n部屋をうろつくのは\n不思議な感じがします", 0, 1, 2360, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107601, "南国の果実を使って\n主さまにデザートを\nお作りしましょう", 0, 1, 2361, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107601, "せっかくの水着ですし\nこれから定期的に\n海水浴に行きたいです", 0, 1, 2362, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107601, "海の生き物も、\n森の生き物に負けず\n可愛かったです…", 0, 1, 2363, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107601, "クロールの練習なら、\n部屋でもできそうです", 0, 1, 2364, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 107601, "主さまに、水着姿を\n褒めていただけた…\nえへへ♪", 0, 1, 2365, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 107601, "水着で主さまの\nお世話をしたら、\n喜びますでしょうか…", 0, 1, 2366, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107601, "お疲れでしたら\nわたくしの膝を\nお使いくださいね", 0, 2, 2367, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107601, "主さま、海の家で\nつかうおこづかいを\n渡しておきますね", 0, 2, 2368, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107601, "海の美しさは、\n森とはちがう\n魅力がありました", 0, 2, 2369, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107601, "この髪飾りですか？\n造花ですから\n水の中も平気です", 0, 2, 2370, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 107601, "水着でくっついて\n眠ると、いつもより\n密着して暖かいです", 0, 2, 2371, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 107601, "はじめての海が、\n主さまと一緒で\n幸せです♪", 0, 2, 2372, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107601, "おはようございます。\n泳ぎに行く準備は\n万端です", 1, 2, 2373, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107601, "午後は泳ぐのをやめて\nスイカ割りなど\nいかがでしょう？", 2, 2, 2374, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107601, "夜も更けてきました…\n波の音を聞きながら\n少しお話しませんか？", 3, 2, 2375, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107601, "あぅ…この家具には、\n人をダメにする魔法が\nかけられています…", 0, 3, 2377, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107601, "ふふっ♪\nあなたも、海水浴に\n行きますか？", 0, 3, 2378, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107601, "この香り…\nきっと主さまも\n気に入ります", 0, 3, 2379, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107601, "これは暖かいです…\n後ほど主さまと一緒に\nあたりましょう", 0, 3, 2380, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107601, "泳いだ後の食事は\n一段と美味しく\n感じます", 0, 3, 2382, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107601, "深海魚の生態…\nこれは主さまにも\nお伝えしなくては…", 0, 3, 2383, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107601, "顔立ちが、主さまに\n似ております…\nかわいらしい♪", 0, 3, 2385, 10, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107601, "ふかふかですね\n主さまのようです♪", 0, 3, 2386, 11, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107701, "お掃除、お掃除\nふんふふ～ん☆\nあ、聞かれてました？", 0, 1, 2387, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107701, "日焼けには回復魔法が\n効きますよ。どうして\n逃げるんですか～！", 0, 1, 2388, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107701, "今日はドジを\nしませんように！", 0, 1, 2389, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107701, "接客業を極めれば\nメイドとして\n成長できるかも？", 0, 1, 2390, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107701, "水着って\n動きやすいんですけど\n人の目が気になります", 0, 1, 2391, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 107701, "あの人の気持ちを知る\n魔法とかあれば…\nな、なんでもないです", 0, 1, 2392, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 107701, "水着でご奉仕したら、\nあの人は喜ぶかな？", 0, 1, 2393, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107701, "暑いのでしたら、\n私が魔法で涼しく…\nえ？　いらない？", 0, 2, 2394, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107701, "何か御用ですか？\n特にないのでしたら、\nお話ししましょう♪", 0, 2, 2395, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107701, "水着姿でも\nメイドはメイドです\nご奉仕しますよ！", 0, 2, 2396, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107701, "ひゃっ！　あ、危うく\n転ぶところでした…\n…きゃうっ！", 0, 2, 2397, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 107701, "どうかしましたか？\nあなたの御用なら、\n喜んでお受けしますよ", 0, 2, 2398, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 107701, "水着姿をあなたに\n見られると、\nドキドキしちゃいます", 0, 2, 2399, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107701, "朝は暑くてやる気が\n出ない？　でしたら、\nえぇい、元気になぁれ", 1, 2, 2400, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107701, "お腹がぺこぺこなら、\nお昼は海の家で\n食べましょう♪", 2, 2, 2401, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107701, "夜の浜辺でお散歩も\nいいですね。たくさん\n転びそうですけど…", 3, 2, 2402, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107701, "休憩しながら、\nお掃除すれば\n一石二鳥です！", 0, 3, 2404, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107701, "きれいにすれば\nかわいさも倍増\nですよ～♪", 0, 3, 2405, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107701, "素敵な香りです～\n救護院に置けば、\nみんな喜ぶかも～", 0, 3, 2406, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107701, "あったかいですね…\nはっ！　いけません！\nメイドがサボるなんて", 0, 3, 2407, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107701, "料理のできない\nメイドなんて…\nはぐはぐ、おいしい♪", 0, 3, 2409, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107701, "水着姿で接客をすると\n売り上げが倍になる\n…本当ですか、これ", 0, 3, 2410, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107701, "えへへ♪\n南の島まで\n乗せてください！", 0, 3, 2412, 10, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107701, "なんだか、\n人魚姫に\nなった気分です♪", 0, 3, 2413, 11, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107801, "みんなで過ごす\n休日っていいわよね…\nずっと続かないかな", 0, 1, 2414, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107801, "海に浮輪でぷかぷか\n浮かぶのって\n癖になりそう", 0, 1, 2415, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107801, "ビーチにいると\n日焼けが心配だわ", 0, 1, 2416, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107801, "水着姿って楽よね。\n室内だとちょっと\n落ち着かないけど…", 0, 1, 2417, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107801, "お、泳ぎの練習なんて\nしないからね！", 0, 1, 2418, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 107801, "楽しすぎて立場を\n忘れそう…あんたの\nせいなんだから！", 0, 1, 2419, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 107801, "みんなで遊んで\n美味しいもの食べて\nほんっとに楽しいっ♪", 0, 1, 2420, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107801, "マジであっついわね…\nよっし！　海に入って\n涼みましょ！", 0, 2, 2421, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107801, "シャワー浴びたばかり\nなのに…また、遊び\nたくなってきたわ！", 0, 2, 2422, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107801, "遊び足りないって？\nしょうがないわね…\n付き合ってあげるわ♪", 0, 2, 2423, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107801, "一応伝えておくけど\n猫って水が苦手なの！\nわかった！？", 0, 2, 2424, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 107801, "あたしと話したいの？\nふふ。\nまぁ、いいわよ。\nほら隣に座りなさい", 0, 2, 2425, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 107801, "こんなに楽しいの\n初めてかも…\nずっと続けばいいのに", 0, 2, 2426, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107801, "は、早起きしたのは、\n楽しみで眠れなかった\nからじゃないわよ！", 1, 2, 2427, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107801, "昼食後のストレッチ\n終わり！　さあ、\nビーチが待ってるわ！", 2, 2, 2428, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107801, "夜の海に散歩に\n行こうかな～\nあんたも来なさいよ☆", 3, 2, 2429, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107801, "海ではしゃぎすぎて\n疲れたわ…ちょっと\n休もうかしら？", 0, 3, 2431, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107801, "こんな魔物がいたら\n操るのも楽しいかも", 0, 3, 2432, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107801, "にゃう～♪\nまるで南国にいる\n気分ね♪", 0, 3, 2433, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107801, "あったかぁい♪\n身体が冷えてきたから\n丁度いいわ！", 0, 3, 2434, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107801, "運動後のご飯って\nなんでこんなに\n美味しいんだろ♪", 0, 3, 2436, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107801, "どんなカナヅチでも、\n絶対必ず泳げる方法、\n書いてないかしら？", 0, 3, 2437, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107801, "あんたもあたしが\n操ってあげる！\nなんてね～♪", 0, 3, 2439, 10, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107801, "リゾートリゾートっ\nのんびりしましょ♪", 0, 3, 2440, 11, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108001, "ここなら思う存分\nデスクワークに\n励めるわね！", 0, 1, 2441, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108001, "武器のメンテナンスも\nしっかりしないと\n最高効率は望めないわ", 0, 1, 2442, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108001, "準備をしっかり整えて\nもっともっと\n稼がなくちゃね！", 0, 1, 2443, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108001, "少し休んだら\n次のバイトに\n行ってくるわね！", 0, 1, 2444, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108001, "この格好でここに\nいるのって\n気恥ずかしいわね…", 0, 1, 2445, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 108001, "キミが望むなら\n今日のバイト\n休んでもいいわよ？", 0, 1, 2446, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 108001, "キミだけね…\n私のことをここまで\n理解してくれるのは", 0, 1, 2447, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108001, "普段と違う格好\nだからって\nそんなに見ないでよ…", 0, 2, 2448, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108001, "キミを立派な\nバイト戦士にするのが\n師匠としての役目よね", 0, 2, 2449, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108001, "そろそろ次のバイトに\n行かないと…\nキミも一緒に来る？", 0, 2, 2450, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108001, "効率は大事だけど\nゆっくりするのも\n悪くないわね", 0, 2, 2451, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 108001, "一緒の時間が楽しくて\nバイトのことを\n忘れちゃいそうね", 0, 2, 2452, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 108001, "キミと一緒なら\nどんなミッションも\n楽勝ね！", 0, 2, 2453, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108001, "おはよう、いい朝ね！\n今日もめいっぱい\n稼ぐわよ！", 1, 2, 2454, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108001, "もうお昼だったのね…\n内職のバイトに\n集中しすぎちゃったわ", 2, 2, 2455, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108001, "スケジュールは、と…\nこういう管理の徹底が\n効率を生み出すのよ♪", 3, 2, 2456, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108001, "しっかり働いて休む…\n最高効率にまた一歩\n近づいたわね", 0, 3, 2458, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108001, "なごんじゃうわね…\nよぉし、次のバイトも\n頑張らないと！", 0, 3, 2459, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108001, "いい香りね…\n働く意欲が\n湧いてくるわ", 0, 3, 2460, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108001, "身体が冷えたから\n助かるわ…冷えは\n女の天敵だもの", 0, 3, 2461, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108001, "海のバイトは\n体力勝負よ\nエネルギーはしっかり\n摂取しないとね！", 0, 3, 2463, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108001, "座学も大切ね\nもっと知識を\n身につけないと…", 0, 3, 2464, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108001, "海の楽園みたいね…\n今だけは効率を\n忘れちゃいそう…", 0, 3, 2466, 10, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108001, "落ち着くわ…\n海とひとつになった\n気分ね！", 0, 3, 2467, 11, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107901, "ちょっと休憩にゃ～\n潮風は体に\n良くないにゃ～", 0, 1, 2468, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107901, "猫グッズで\n海の家を飾りたいにゃ", 0, 1, 2469, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107901, "海の幸は美味しいけど\n水はイヤだにゃ…", 0, 1, 2470, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107901, "開店にゃ！\nこのたい焼きなら\nきっと大繁盛にゃ！", 0, 1, 2471, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107901, "海と言えば水着にゃ\n似合ってるかにゃ？", 0, 1, 2472, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 107901, "キミが頼むなら\n手伝ってあげても\nいいにゃ", 0, 1, 2473, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 107901, "あたしの宝は\nここにあるにゃ～", 0, 1, 2474, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107901, "にゃ？\nそんなに見つめても\n何も出ないにゃ", 0, 2, 2475, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107901, "今からキミを\nあたしの相棒に\n任命するにゃ！", 0, 2, 2476, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107901, "これから\n開店の準備にゃ\nキミも手伝って\nくれるのかにゃ？", 0, 2, 2477, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107901, "海の家で出す\n新しいメニューを\n考え中なのにゃ～", 0, 2, 2478, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 107901, "キミと一緒なら\n売上げナンバーワンに\nなれるにゃ！", 0, 2, 2479, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 107901, "キミと一緒にいると\n楽しくて、もっと\nいたくなるにゃ", 0, 2, 2480, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107901, "おはようにゃ！\n今日も頑張って\nいくにゃー！", 1, 2, 2481, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107901, "お腹すいたにゃ～…\nお魚、ちょっとだけ、\nつまみ食いするにゃ", 2, 2, 2482, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107901, "明日の準備は万端にゃ\nいつでも\n出発できるにゃ", 3, 2, 2483, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107901, "このふかふか具合\nまるで子猫の\n毛並みのようにゃ～", 0, 3, 2485, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107901, "にゃにゃ～\nなんてかわいさにゃ～", 0, 3, 2486, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107901, "この匂い…\nもう少し香ばしいと\nいいかもしれないにゃ", 0, 3, 2487, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107901, "にゃ～…\nあったかくて\nぬくぬくだにゃ～", 0, 3, 2488, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107901, "働くにはまず体力にゃ\nしっかり食べて\nスタミナアップ\nだにゃ!", 0, 3, 2490, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107901, "ふむふむ…\nここにお宝が\n眠ってるんだにゃ", 0, 3, 2491, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107901, "シードレイクにゃ！\n本物は怖かったけど、\nぬいぐるみなら\nかわいいにゃ～", 0, 3, 2493, 10, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107901, "貝なのにふかふかの\nクッションにゃ！\n屋台の前にも\n置いてみたいにゃあ", 0, 3, 2494, 11, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103701, "人の家って、道場とは\n違った香りがするね", 0, 1, 2495, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103701, "ちょっと退屈だね…\nあの人をからかいに\n行こうかな♪", 0, 1, 2496, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103701, "普通の女の子みたいに\n遊びたいって思う\nこともあるんだよね", 0, 1, 2497, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103701, "剣の手入れを怠らない\nようにしないと。\nいざという時、困るし", 0, 1, 2498, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103701, "もっと\n上手く立ち回って\n出世したいな", 0, 1, 2499, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 103701, "おかえりなさい！\nふふ、なんだか奥さん\nになった気分だよ♪", 0, 1, 2500, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 103701, "恥ずかしくて、あなた\nを見られないよ…\n私らしくないなぁ…", 0, 1, 2501, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103701, "私、お話好きなんだ。\nどうせなら楽しい話を\nしたいな♪", 0, 2, 2502, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103701, "女の子っぽい子と、\n男勝りな子、あなたは\nどっちが好き？", 0, 2, 2503, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103701, "そろそろ見回りの\n時間かな。あなたも\n一緒に行く？", 0, 2, 2504, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103701, "構って欲しいのかな？\nそれじゃ一緒に\nランニングに行こうか", 0, 2, 2505, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 103701, "二人で出かけるなら、\n手を繋がないとね。\nあはは、冗談冗談♪", 0, 2, 2506, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 103701, "ねえ、あなた♪\nおや、顔が真っ赤だ！\n…ふふ、私も、かな？", 0, 2, 2507, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103701, "私は朝から鍛錬だよ。\n慣れてるけど大変だ。\nお互いに頑張ろうね", 1, 2, 2508, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103701, "鍛錬でお腹ぺこぺこ。\nお昼ごはんが\n楽しみだね", 2, 2, 2509, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103701, "今日も一日見回りと\n鍛錬を頑張ったよ。\nあなたもお疲れさま", 3, 2, 2510, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103701, "いざという時のため\nにも、休憩は大事だね", 0, 3, 2512, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103701, "こういうものには\n今まで\n縁がなかったけど。\nうん、可愛いね♪", 0, 3, 2513, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103701, "精神統一に通じる\nものがあるね。\nふぅ、癒されるなぁ", 0, 3, 2514, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103701, "あったかいなぁ。\nたまにはのんびり\nしようか", 0, 3, 2515, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103701, "鍛錬後はすごくお腹が\n空くんだよね。\nう～ん、おいしい♪", 0, 3, 2517, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 103701, "剣術書だけで強くなる\nには飛びぬけた才能と\n努力が必要だよね", 0, 3, 2518, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100501, "正義の刃で\n悪を挫く！\n…キマったッス♪", 0, 1, 2520, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100501, "ヒーローの\n秘密特訓を\n始めるッスよ～！", 0, 1, 2521, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100501, "正義の味方目指して\n今日も明日も\n全力ッスよ♪", 0, 1, 2522, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100501, "一日一善！\nヒーローだったら\n当たり前ッス♪", 0, 1, 2523, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100501, "どうッスか\nこのポーズ！\nイイ感じッスか？", 0, 1, 2524, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 100501, "た、たまには\nよく頑張ったねって\n褒めてほしいッス…", 0, 1, 2525, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 100501, "自分を信じてくれる\nあんたと一緒なら\n何でもやれるッス！", 0, 1, 2526, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100501, "おつかれッス♪\n冒険の準備は\nばっちりッスよ！", 0, 2, 2527, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100501, "どうしたんッスか？\n…はっ！　もしや敵が\n迫ってるんスかっ！？", 0, 2, 2528, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100501, "べ、別に\n頭を撫でてほしいとか\n思ってないッスよ？", 0, 2, 2529, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100501, "スーパーヒーロー\nトラタイガーは\n絶対無敵ッス！", 0, 2, 2530, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 100501, "あんたと自分で\n正義の心を燃やして\n悪を一網打尽ッス！", 0, 2, 2531, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 100501, "あんたと一緒だと\n幸せな気持ちに\nなるんッス…♪", 0, 2, 2532, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100501, "お、早いッスね～♪\n今日も一日一善\nよろしくッス！", 1, 2, 2533, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100501, "一休みしながら\nヒーローについて\n語り合うッスよ～！", 2, 2, 2534, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100501, "夜のパトロールで\n一緒に平和を\n守るッス！", 3, 2, 2535, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100501, "ふぅぅ…\n落ち着くッスね\n戦士の休息ッス♪", 0, 3, 2537, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100501, "くぅぅ、可愛いッス…\nヒーローが守るべき\n愛らしさッスね！", 0, 3, 2538, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100501, "なんスかこの香り？\nすぅぅ…ふぅぅ…\n力がみなぎるッス！", 0, 3, 2539, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100501, "優しい光ッスね…\nヒーローの優しさ\nそのものッス！", 0, 3, 2540, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100501, "あむあむあむ…\nんぅう、元気が溢れて\n止まらないッス！", 0, 3, 2542, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 100501, "トラタイガーのお話は\n笑いあり涙ありで\n超おすすめッス！", 0, 3, 2543, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108101, "ハッピーハロウィン\nお父さん、お菓子は…\n食べられないね", 0, 1, 2545, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108101, "トリックオアトリート\nお菓子をくれないと\n不吉な占いをします", 0, 1, 2546, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108101, "ふふ、あなたの後ろで\n霊のみなさんが\n踊り始めました", 0, 1, 2547, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108101, "霊感がいつも以上に\n冴えているので\n占いの調子も良好です", 0, 1, 2548, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108101, "父はハロウィンが\n大好きだそうです\n大胆な格好をする子が\n多いので…", 0, 1, 2549, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 108101, "あなたは霊たちにも\n大人気ですよね\n私も見てほしいな…", 0, 1, 2550, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 108101, "ハロウィンの賑やかさ\n今は好きです…\nあなたといられるので", 0, 1, 2551, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108101, "霊のみなさんとの\nダンスパーティー…\n楽しみです", 0, 2, 2552, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108101, "お父さん、元気だね\nえ？　ここは色んな\n女の子に会えて幸せ？", 0, 2, 2553, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108101, "ハロウィンのお菓子\n食べすぎちゃったな…\nでも、美味しかった…", 0, 2, 2554, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108101, "あなたの女難の相…\n悪化していますね\nお祓いが必要かも", 0, 2, 2555, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 108101, "あの、霊のみなさんが\nあなたと踊りたいと…\nえっと、私も…", 0, 2, 2556, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 108101, "あなたと手を繋ぐと\n胸が苦しくなります\nでも、繋ぎたいな…", 0, 2, 2557, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108101, "ハッピーハロウィン…\n父が元気なので\n今日の運気は良好です", 1, 2, 2558, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108101, "父はお昼寝中です\n本当に寝ているかは\n私にも分かりませんが", 2, 2, 2559, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108101, "お父さん、\nもう眠っちゃったの？\n…眠れるんだったっけ", 3, 2, 2560, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108101, "落ち着きますね…\n霊のみなさんも\n喜んでいます", 0, 3, 2562, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108101, "つん、つん。ふふっ♪\n癒やされます…", 0, 3, 2563, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108101, "精神が研ぎ澄まされる\n香りですね…\n占いが捗ります", 0, 3, 2564, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108101, "あったかいね\nお父さん…\nえ？　分からない？", 0, 3, 2565, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108101, "霊のみなさんは\n食べられないので…\n私がいただきます", 0, 3, 2567, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108101, "お父さんを元に戻す\n手掛かり…\n書かれていないかな？", 0, 3, 2568, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108101, "わぁ、お菓子が沢山…", 0, 3, 2570, 12, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108201, "無料でプリンが\nもらえるなんて、\nハロウィンはすごいの", 0, 1, 2572, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108201, "生きてるときも、\nミヤコはハロウィンが\n好きだった気がするの", 0, 1, 2573, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108201, "みんなに仮装を\n見せつけて、\nプリンをもらうの～", 0, 1, 2574, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108201, "この仮装で、\nみんなに恐怖を\n教えてやるの！", 0, 1, 2575, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108201, "アオーン、アオーン！\nミヤコ、狼女に\nなりきってるの～", 0, 1, 2576, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 108201, "アイツに秘密で、\nパーティーの\n飾り付けをするの♪", 0, 1, 2577, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 108201, "アイツが狼男の\n仮装をすれば、\nおそろいなの～", 0, 1, 2578, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108201, "プリンをくれなきゃ、\nオマエをカボチャに\nしてやるの～", 0, 2, 2579, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108201, "ハロウィンは幽霊でも\n参加できるから\n楽しいの～！", 0, 2, 2580, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108201, "プリンがないなら\nカボチャプリンを\n食べればいいの！", 0, 2, 2581, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108201, "プリンの可能性は\n無限大なの～", 0, 2, 2582, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 108201, "オマエの隣で\nプリンを食べると、\nいつもより美味しいの", 0, 2, 2583, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 108201, "オマエが死んでも、\nミヤコはずーっと\n一緒にいてやるの～", 0, 2, 2584, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108201, "朝ごはんは\nカボチャプリンなの！\nハロウィンっぽいの～", 1, 2, 2585, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108201, "ランチは豪華に\nプリンアラモードなの", 2, 2, 2586, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108201, "夜は幽霊たちと\nハロウィンするの！\nオマエも来るの！", 3, 2, 2587, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108201, "幽霊にも休息は\n必要なの！\nプリンもってこいなの", 0, 3, 2589, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108201, "オマエもミヤコの\nパーティーに\n招待してあげるの～", 0, 3, 2590, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108201, "癒されるの～\nまるで天国なの～", 0, 3, 2591, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108201, "ここでなら、\n焼きプリンが\n作れるの！", 0, 3, 2592, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108201, "腹が減っては\nハロウィンは\nできぬなの！", 0, 3, 2594, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108201, "丸ごとカボチャ\nプリン特集！？\n美味しそうなの～♪", 0, 3, 2595, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108201, "お菓子がたくさんで、\nミヤコはとっても\nハッピーなの♪", 0, 3, 2597, 12, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108301, "魔女以外にも、\n色々な仮装を\nしてみたいわね", 0, 1, 2599, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108301, "チョコやアメよりも、\nアタシに相応しい\nスイーツがあるはず…", 0, 1, 2600, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108301, "お菓子の食べ過ぎは、\nお母さんに\n怒られるかも…", 0, 1, 2601, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108301, "この格好で学院に\n行ったら、\nみんな驚くかしら", 0, 1, 2602, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108301, "大人のレディは、\nどういうハロウィンを\n過ごすべきかしら…？", 0, 1, 2603, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 108301, "お菓子、アイツの分も\nとっといてあげよ！\n大人の余裕ってやつね", 0, 1, 2604, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 108301, "アイツを魅了する\n魔法とかないかしら？", 0, 1, 2605, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108301, "おしゃれスイーツを\nくれなきゃ\nイタズラするわよ！", 0, 2, 2606, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108301, "アタシの大人センスで\nアンタに似合う仮装を\n選んであげるわ！", 0, 2, 2607, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108301, "不気味よねぇ…\nジャックオーランタン\nこ、怖くないわよ？", 0, 2, 2608, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108301, "ハロウィンが楽しくて\n宿題やり忘れてたわ…\nて、手伝って～！", 0, 2, 2609, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 108301, "お菓子をもらうのも\nアンタと一緒なら\n恥ずかしくないわ！", 0, 2, 2610, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 108301, "アタシのエスコートを\n任せられるのは、\nアンタだけなんだから", 0, 2, 2611, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108301, "朝食はオシャレに\nグリーンスムージー！\n…に、にがぁ", 1, 2, 2612, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108301, "昼下がりはカフェで\n優雅なティータイム\n…憧れるわ～", 2, 2, 2613, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108301, "ハロウィンナイトの\nはじまりよ！\n街に繰り出しましょ！", 3, 2, 2614, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108301, "あう～、お菓子の\n食べすぎで\n動けな～い", 0, 3, 2616, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108301, "アンタも仮装する？\nいまよりもーっと\nかわいくなるわよ！", 0, 3, 2617, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108301, "大人な香りに\n包まれてる～♪", 0, 3, 2618, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108301, "ポカポカね～♪\n…ああー！\nチョコが溶けてる！", 0, 3, 2619, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108301, "おいしい～♪\n…じゃなくて！\nび、美味ですわ～", 0, 3, 2621, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108301, "この服かわい～♪\nって、なにこの値段！\nお小遣い何年分よ！？", 0, 3, 2622, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108301, "お、お菓子なんかで\n喜ぶほど、子供じゃ…", 0, 3, 2624, 12, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107101, "次はどんな策で団長を\nからかってみようか…", 0, 1, 2626, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107101, "む？\n血の臭いがするな\n祭りの時間か☆", 0, 1, 2627, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107101, "平和に飼い殺される\n前に、混沌の愛を\n育みたいね", 0, 1, 2628, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107101, "本気を出した団長と\nいつか手加減なしに\n殺り合いたいな", 0, 1, 2629, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107101, "おばさん呼ばわりする\n新米騎士の小娘を\n懲らしめてやろうか", 0, 1, 2630, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 107101, "胸を触られるまで\n誓約のことなど\nすっかり忘れていたな", 0, 1, 2631, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 107101, "まさかこのワタシが\nプロポーズするとはな\n…笑えるじゃないか", 0, 1, 2632, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107101, "面白い暇つぶしを\n思いついたんだ\n戯れに付き合え☆", 0, 2, 2633, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107101, "退屈で死にそうだ\n愉しい話題を\nワタシに提供してくれ", 0, 2, 2634, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107101, "なんだ、暇なのか？\n心躍ることでも探しに\n出かけようではないか", 0, 2, 2635, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107101, "このワタシを愉快に\nさせてくれる事でも\nあったのか？", 0, 2, 2636, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 107101, "ワタシに求婚した男の\n数だと？\nいちいち覚えてないな", 0, 2, 2637, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 107101, "最強無敵の女と\n結婚できるんだ\n誇りに思えよ☆", 0, 2, 2638, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107101, "なにか愉快なことが\n起こりそうな朝だ☆\nワクワクするね", 1, 2, 2639, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107101, "うーむ、腹がすいたな\n愉快なことで\nこの空腹を満たしたい", 2, 2, 2640, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107101, "これからは大人の\n時間だ。愉しみ方を\n知りたいのかい？", 3, 2, 2641, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107101, "このワタシとした事が\n穏やかな気分に\nなってしまうとは…", 0, 3, 2643, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107101, "可愛い姿をしたものが\n惨たらしい\n醜態を晒すのを\n想像してしまう", 0, 3, 2644, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107101, "悪くはない香りだ\nだが、ワタシは血の\n臭いを嗅ぎたいぞ☆", 0, 3, 2645, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107101, "ぬるい、ぬるすぎる！\n地獄の業火には\nほど遠い熱さだ！", 0, 3, 2646, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107101, "ワタシの腕前に\n勝るとも劣らない\nなかなかの味だ", 0, 3, 2648, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107101, "意中の男性を絶対に\n落とす方法…？\nく、下らんっ！", 0, 3, 2649, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105601, "明日はあの釣り場に\n行くとしようかな\nあの子も誘ってみるか", 0, 1, 2651, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105601, "ぷはーっ！\n体を動かしたあとの\n一杯は格別だね", 0, 1, 2652, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105601, "天気が良いと\n昼寝か釣りかで\n迷っちまうな…", 0, 1, 2653, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105601, "大物が釣れたときの\n快感は、他じゃ\n味わえないね", 0, 1, 2654, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105601, "ふあぁ～…\n昼寝でもするか", 0, 1, 2655, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 105601, "ふふっ…\nあの子、見た目より\n根性あるんだね", 0, 1, 2656, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 105601, "あの子が強くなるには\nどんな魚を食べさせて\nあげればいいのやら…", 0, 1, 2657, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105601, "姉御って呼ぶんじゃ\nないよ。からかうと\nタダじゃおかないよ", 0, 2, 2658, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105601, "釣りのコツ？\n何事も経験が大事だ\n今から行くかい？", 0, 2, 2659, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105601, "どうしたのさ？\n助太刀が必要かい？\nアタシにまかせときな", 0, 2, 2660, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105601, "困ってる人がいたら\nまた力を貸しておくれ\n頼りにしてるよ", 0, 2, 2661, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 105601, "なにか困りごとかい？\nどうやら昼寝は\nまた今度だね", 0, 2, 2662, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 105601, "よしよし！\nお人好しのお前さんを\n労ってあげるよ", 0, 2, 2663, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105601, "おはよう、お前さん\n魚屋は早起きでなきゃ\nつとまらないよ？", 1, 2, 2664, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105601, "昼飯かい？　今朝\n仕入れた魚があるんだ\n食べてみるかい？", 2, 2, 2665, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105601, "夜釣りにぴったりの\n月が出てるよ\nいい釣りが出来そうだ", 3, 2, 2666, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105601, "コイツに座って\nのんびり釣りが\nできりゃいいのにな", 0, 3, 2668, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105601, "よしよし…♪\nおっと、かわいい物も\nつい抱きしめちまうな", 0, 3, 2669, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105601, "これで魚の臭いを\n消せれば、猫も\n寄らなくなるのかね", 0, 3, 2670, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105601, "冬場の釣りも\nこれくらい暖かけりゃ\nいいんだけどね", 0, 3, 2671, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105601, "腹が減っては\n釣りもできぬ、ってな\nまずは腹ごしらえだ", 0, 3, 2673, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105601, "へぇ…こんな魚が\nいたとは…いつか\n釣りたいもんだね", 0, 3, 2674, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101301, "流石に室内で\n魔法ぶっぱしたら\n怒られるよねぇ", 0, 1, 2676, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101301, "可愛い女の子が\nたくさんいる！\nここは楽園か！？", 0, 1, 2677, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101301, "みんなに漫画とか\n勧めてみようかな！\n布教用のがあるし！", 0, 1, 2678, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101301, "わ、私一人の\n部屋じゃないから\n色々自重せねば！", 0, 1, 2679, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101301, "「ぬるぽ」\n…くっ、\n誰も反応なしか！", 0, 1, 2680, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 101301, "次はどの漫画を\n貸そうかなー♪", 0, 1, 2681, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 101301, "次の定期巡回で\nとっておきの店に\n連れてってあげよう！", 0, 1, 2682, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101301, "君に似合いそうな\n衣装を買ったよ！\n君もいざ魔法少女に！", 0, 2, 2683, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101301, "さあ！\n暇なら定期巡回に\nレッツラゴー！", 0, 2, 2684, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101301, "戦闘だけじゃなくて、\n日常にも役立つ\n魔法を習得したいね！", 0, 2, 2685, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101301, "新魔法思いついた！\n私って天才！？", 0, 2, 2686, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 101301, "決め台詞は超大事！\n君の決め台詞も\n私が考えてあげよう！", 0, 2, 2687, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 101301, "私ってもしやリア充？\n…そんなバカな！\n非リア代表のはずが！", 0, 2, 2688, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101301, "おはよー…\n徹夜で本読んでたから\nまだ眠い～", 1, 2, 2689, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101301, "昼飯食べて、\n午後の定期巡回も\n頑張りましょー！", 2, 2, 2690, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101301, "夜からが、\nオタ活本番でしょ！", 3, 2, 2691, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101301, "ふいー！\nくつろいで本を読む！\n最高だねー！", 0, 3, 2693, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101301, "君、可愛いね～♪\n魔法少女の\n使い魔みたい！", 0, 3, 2694, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101301, "良い匂いに\n釣られてしまった…\nまさか、孔明の罠！？", 0, 3, 2695, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101301, "暖かいけど、\nここで本を読むのは\nちょっち危ないかな", 0, 3, 2696, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101301, "ウマ―！\nこれの詳細きぼんぬ！", 0, 3, 2698, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101301, "こ、古代語の本！？\n現代語でおk…", 0, 3, 2699, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 190901, "ふふふっ♪\n次はどんな悪戯を\nしてやろうかしら♪", 0, 1, 2701, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 190901, "幻影湖の霧を吸った\n人間たちの\nリアクションって、\nサイコーよねぇ～♪", 0, 1, 2702, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 190901, "室内だけとはいえ…\n自由に動けるって、\nやっぱりいいわ～♪", 0, 1, 2703, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 190901, "改めて見ると良い\n部屋ね…悪戯とか\nしやすそうだしぃ♪", 0, 1, 2704, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 190901, "私を封じていた部屋\nなのに…急に居心地が\n良くなったわね♪", 0, 1, 2705, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 190901, "あんたの頭、座り心地\n良いわね…私専用の\n椅子にしてあげる♪", 0, 2, 2706, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 190901, "外は大変みたいねぇ…\nまあ、私は好き勝手に\nやらせてもらうわ♪", 0, 2, 2707, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 190901, "顔に落書された？\n誰か知らないけど、\n悪いことする奴も\nいるものねぇ～♪", 0, 2, 2708, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 190901, "フィオのやつ…\nどこをウロチョロ\nしてるのかしら？", 0, 2, 2709, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 190901, "人間の朝は大変ねぇ…\n私はその様を見ながら\nゆっくりするわね♪", 1, 2, 2710, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 190901, "う～私も、お日様を\n浴びながら、お外を\nお散歩したーい！", 2, 2, 2711, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 190901, "この暗さ…閉じ込め\nられていた時を\n思い出すわね…", 3, 2, 2712, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 190901, "悪戯しすぎて\n疲れたから、ちょっと\n休憩しよっと♪", 0, 3, 2714, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 190901, "ガイド妖精たる私が、\nこんな造形物に\n惹かれる…わけが……\nかわいい♪", 0, 3, 2715, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 190901, "この香りを囮にして、\n周りに悪戯を仕込む…\n完璧ね♪", 0, 3, 2716, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 190901, "不思議ねぇ…私は\n温度とか関係ないのに\n離れられないわ", 0, 3, 2717, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 190901, "食事の必要がない体…\nつまり、食べたければ\n食べていいのよね～♪", 0, 3, 2719, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 190901, "へぇ…最近はこんな\n悪戯もあるのねぇ…\nあいつで試さないと♪", 0, 3, 2720, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108401, "メリークリスマス！\n歌で幸せを\n届けます…！", 0, 1, 2722, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108401, "あなたと出会えたのは\n偶然ではなくて…\nクリスマスの奇跡だと\n思うんです", 0, 1, 2723, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108401, "クリスマスは華やかで\nつい子供のように\nはしゃいでしまいます", 0, 1, 2724, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108401, "曲が書けるのは\nあなたが側に\nいてくれるからです…", 0, 1, 2725, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108401, "感動を与えられる歌…\nあなたと一緒になら\n生み出せるはず…", 0, 1, 2726, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 108401, "クリスマスの幸せを\nあなたと\n感じ合えたらな…", 0, 1, 2727, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 108401, "あなたの幸せを願った\nクリスマスソング…\nいつか披露しますね", 0, 1, 2728, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108401, "みなさんの笑顔を\n守りたいから…\n私は歌い続けます", 0, 2, 2729, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108401, "歌を届ける\nサンタさんに\nなりたいな…", 0, 2, 2730, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108401, "お暇でしたら\nクリスマスのお祝いを\nしませんか？", 0, 2, 2731, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108401, "ステージに立つ前に\n喉を整えないと…\n発声練習は念入りに…", 0, 2, 2732, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 108401, "まわりはかわいい子\nばっかりだけど…\nこの服なら\nアピールになるかな？", 0, 2, 2733, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 108401, "歌でならいくらでも\nあの人への気持ちを\n込められるのに…", 0, 2, 2734, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108401, "今日も一日\n幸せな歌で世界が\n包まれますように…", 1, 2, 2735, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108401, "お茶を飲みながら\n好きな音楽を\n語り合いませんか？", 2, 2, 2736, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108401, "ふふ、寝かしつけの\n子守唄を\nお望みですか？", 3, 2, 2737, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108401, "ふかふかですね\n心地良すぎて\n眠ってしまいそう…", 0, 3, 2739, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108401, "素敵…\n優しい歌みたいに\n気持ちが落ち着く…", 0, 3, 2740, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108401, "インスピレーションが\n溢れてくる…\nいい曲が書けそう…", 0, 3, 2741, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108401, "この光みたいに\n灯火になるような\n歌を歌いたい…", 0, 3, 2742, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108401, "喉の健康のためにも\n食事はしっかり\nとるようにしています", 0, 3, 2744, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108401, "物語には\n曲作りのヒントが\n詰まっているんです", 0, 3, 2745, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108401, "絵が飛び出してくる\nなんて…精霊が宿って\nいるのでしょうか？", 0, 3, 2747, 14, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108401, "か、可愛い…♪\nちょっとだけ触っても\nいいですよね…？", 0, 3, 2748, 15, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108501, "クリスマスでも、\n演劇の練習は\nしっかりやらないと！", 0, 1, 2749, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108501, "あうぅ…\nクリスマスの準備が\n終わらないよぅ…", 0, 1, 2750, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108501, "アヤネちゃんが\n摘み食いしないように\n見ておかないと…", 0, 1, 2751, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108501, "サンタさんみたいに、\n皆に笑顔で接する…\nうぅ…私には無理ぃ…", 0, 1, 2752, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108501, "新しいお洋服～♪\nスズメお姉ちゃんに\nお礼をいわないと♪", 0, 1, 2753, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 108501, "お兄ちゃんの為に、\nプレゼントを\n用意しないと♪", 0, 1, 2754, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 108501, "クリスマス…\n恋人の日…ひゃわっ！\nわ、私ってば何を…！", 0, 1, 2755, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108501, "メリー\nクリスマスです、\nお兄ちゃん♪", 0, 2, 2756, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108501, "そ、そんなに\n見られると…\n恥ずかしいよぉ…", 0, 2, 2757, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108501, "ひゃわぁっ！？\n急に触られると、\n驚くからやめてぇ…", 0, 2, 2758, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108501, "あ…クリスマスの\n準備を手伝って\nくれるんですか？", 0, 2, 2759, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 108501, "お兄ちゃんと\n過ごすクリスマス、\n楽しみですぅ…♪", 0, 2, 2760, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 108501, "クリスマスはその…\n二人で…あうぅ…\nなんでもないですぅ…", 0, 2, 2761, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108501, "うう…パーティーが\n楽しみで、あんまり\n眠れなかったよぅ…", 1, 2, 2762, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108501, "ケーキを食べられなく\nなっちゃうから、\nお昼は軽めにしないと", 2, 2, 2763, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108501, "ちょっと眠いけど…\n今日は少しだけ\n夜更かししたいです♪", 3, 2, 2764, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108501, "ふかふかぁ…\nクリスマスの準備中に\n眠くなっちゃいますぅ", 0, 3, 2766, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108501, "この子と一緒に\nおままごとしたいな♪", 0, 3, 2767, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108501, "いい匂い…♪\nみんなで\nアロマパーティー\nしたいなぁ…♪", 0, 3, 2768, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108501, "ぬっくぬく…♪\nもう、ここから\n離れたくないよぉ…♪", 0, 3, 2769, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108501, "はむはむ…♪\nクリスマスのお料理\nより美味しいかも～♪", 0, 3, 2771, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108501, "すごい…\nクリスマスの\n演劇って、\nこんなにあるんだ…", 0, 3, 2772, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108501, "すごい…まるで、\nクリスマスの演劇を\n観てるようですぅ…♪", 0, 3, 2774, 14, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108501, "とっても可愛いです♪\nお芝居の相手役に\nしたいなぁ…♪", 0, 3, 2775, 15, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108601, "甘ぁいケーキも、\nデッカイ七面鳥も、\nぜーんぶ私のだー♪", 0, 1, 2776, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108601, "毎日が\nクリスマスだったら\nいいのにな～♪", 0, 1, 2777, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108601, "プレゼントはあるし、\n料理は美味しいし、\nクリスマス最高ー♪", 0, 1, 2778, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108601, "ここでぷうきちの\nクリスマス一発芸♪\n『無茶ぶりするな！』", 0, 1, 2779, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108601, "ぷうきちがいつもより\n重いかも…太ったの？\n『衣装の重さだよ！』", 0, 1, 2780, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 108601, "お兄ちゃんが\n一緒にいてくれると\nクリスマス楽しいね♪", 0, 1, 2781, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 108601, "お兄ちゃんに抱きつく\nと、なんだかドキドキ\nする…なんでかな？", 0, 1, 2782, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108601, "メリークリスマス♪\nプレゼントは、\nいつでも受付中だよ♪", 0, 2, 2783, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108601, "このクマさん耳\n可愛いでしょ♪\nぷうきちと\nお揃いなんだー♪", 0, 2, 2784, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108601, "お兄ちゃん…私、\n甘ぁいケーキが\n食べたいなぁ…♪", 0, 2, 2785, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108601, "クリスマスぷうきちは\nぬくぬく感が凄いよ！\nお兄ちゃんもさわる？", 0, 2, 2786, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 108601, "えへへ…お兄ちゃんの\n手は、大きくて、\n暖かくて大好き～♪", 0, 2, 2787, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 108601, "来年のクリスマスも、\nこうやって\nお兄ちゃんに\n甘えていたいな～♪", 0, 2, 2788, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108601, "あれぇ…\n私のケーキはぁ…？\n『ちゃんと起きろ』", 1, 2, 2789, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108601, "お昼はデッカイ\n七面鳥が食べたーい♪\n『夜まで我慢しろ』", 2, 2, 2790, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108601, "『もう寝る時間だぞ』\nサンタさんが来る\nまで起きてるぅ…", 3, 2, 2791, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108601, "このふかふか具合…\nぷうきち！\n負けてられないよ！", 0, 3, 2793, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108601, "この子、\nすっごくかわいい～♪\n『ライバル登場か！』", 0, 3, 2794, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108601, "良い匂い…あっ！\nぷうきちにこの\n匂いを移せば…！\n『匂い袋にするな』", 0, 3, 2795, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108601, "あったか～い♪\nえへへ、ぷうきちも\nあっためてあげる♪", 0, 3, 2796, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108601, "ぷうきち！　これ、\nとってもおいしいよ♪\n『食べすぎるなよ』", 0, 3, 2798, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108601, "お、大人の私なら、\nこれぐらい…読め…\n『無理するな…』", 0, 3, 2799, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108601, "絵が飛び出す…\nぷうきちに絵本を\n入れれば、イタズラに\n使えるかも…♪", 0, 3, 2801, 14, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108601, "か、可愛いけど…\nぷうきちサンタの方が\n可愛いんだからぁ…！", 0, 3, 2802, 15, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108901, "着物も慣れると\n動きやすいものさ", 0, 1, 2803, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108901, "鼻緒が緩んでしまった\n下駄で修業は\n無理があったかな…", 0, 1, 2804, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108901, "小腹が空いた…\nユイが作った餅、\nまだ食べられるかな？", 0, 1, 2805, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108901, "服装も変わったし\nいつもと違う戦い方を\n想定しないと…", 0, 1, 2806, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108901, "…ヒヨリは動きすぎて\n着崩れしてないか\n心配だ", 0, 1, 2807, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 108901, "これからもずっと\n仲間と、彼と\n一緒に…", 0, 1, 2808, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 108901, "願いを叶えるために\nもっと自分を\n鍛えていかないと！", 0, 1, 2809, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108901, "頭の飾りがずれてた？\nそうか、直してくれて\nありがとう", 0, 2, 2810, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108901, "着物がそんなに\n珍しい？\nキミも着てみる？\n…冗談だよ", 0, 2, 2811, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108901, "肩掛けの触り心地が\n気に入った？\nならもっと触る？", 0, 2, 2812, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108901, "ああ、この刀？\n釣り仲間に見繕って\nもらったんだ", 0, 2, 2813, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 108901, "キミといると\n気持ちが段々と\n安らいでいくよ", 0, 2, 2814, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 108901, "キミは私の行く先を\n照らしてくれる\n光のような存在だよ", 0, 2, 2815, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108901, "おはよう\n朝の修行を始めるけど\nキミもどう？", 1, 2, 2816, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108901, "この時間に軽く\n休憩をとるといい\n能率がよくなるよ", 2, 2, 2817, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108901, "もう夜か…\n明日もまた頑張ろう\nそれじゃ、おやすみ", 3, 2, 2818, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108901, "…疲れていたのかな\nもう少しで眠る\nところだった…", 0, 3, 2820, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108901, "ふふっ、正月衣装を\n着せてやるのも\nよさそうだ", 0, 3, 2821, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108901, "ふぅ…いい香りだ\n慌ただしい日々から\n心が解放される", 0, 3, 2822, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108901, "この心地よさ…\n寝てしまわないよう\n気をつけないと", 0, 3, 2823, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108901, "おいしいけど\n帯がきつくなる前に\n手を止めよう…", 0, 3, 2825, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108901, "外を出歩くのも\nいいけど、中で\n読書もありだな", 0, 3, 2826, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108801, "…騎士クンって\nおせち料理で\n何が好きなんだろう？", 0, 1, 2828, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108801, "着物でお掃除や\nお洗濯するの\n慣れてきた♪", 0, 1, 2829, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108801, "今年は\nどんな福袋を\n買おうかな…♪", 0, 1, 2830, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108801, "お餅に使う調味料\nみんなで違うから\n色々用意しなきゃ", 0, 1, 2831, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108801, "ほっぺがお餅みたいに\nぷにぷに…\n食べ過ぎちゃった…", 0, 1, 2832, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 108801, "来年も、再来年も\nみんなで一緒に\n新年を迎えたいな", 0, 1, 2833, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 108801, "お願いです…\nいつかこの気持ちが\n伝わりますように…", 0, 1, 2834, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108801, "髪飾りが落ちそう？\nじゃあ…騎士クン\nつけ直してくれる？", 0, 2, 2835, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108801, "着物が着たいの？\nじゃあ今度一緒に\n買いに…行く？", 0, 2, 2836, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108801, "あっ、騎士クン\nお雑煮のお餅\nいくつ入れたい？", 0, 2, 2837, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108801, "外、寒かったよね？\nはいこれ。熱い甘粥\n飲むと温まるよ", 0, 2, 2838, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 108801, "今年は騎士クンと…\nえっ？　騎士クン！？\nな、なんでもないよ！", 0, 2, 2839, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 108801, "騎士クンの願い事\nわたしが\n叶えてあげたいな…", 0, 2, 2840, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108801, "おはよう\n太陽の光に当たると\n元気が出るよ", 1, 2, 2841, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108801, "お昼ごはん\n作らなきゃ…\n騎士クンも食べる？", 2, 2, 2842, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108801, "一日が終わるのは\nあっという間だよね\n一年も同じなのかな？", 3, 2, 2843, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108801, "ふぅ…\n帯も少し\nゆるめちゃおう…", 0, 3, 2845, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108801, "お正月用に\n着物を縫って\nあげたいな", 0, 3, 2846, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108801, "これでお肌が\n保湿出来たら\nいいのになぁ", 0, 3, 2847, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108801, "ん～…\nぬくぬくしてて\n動きたくないよ～", 0, 3, 2848, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108801, "…冬眠しないのに\n熊さんみたいに\n食べちゃいそう", 0, 3, 2850, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108801, "年末年始は\n告白のチャンス…\nむ、無理無理！", 0, 3, 2851, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108701, "みんな忙しそう…\n待っててね、\nすぐ助けるから！", 0, 1, 2853, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108701, "ユイちゃんと\nレイさん…よし！\nどっちも手伝おう！", 0, 1, 2854, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108701, "あっ、着崩れてる！\nわわっ、どうしよ！\n帯が結べないよー！", 0, 1, 2855, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108701, "初詣楽しみー！\n出店はどこから\n行こうかな？", 0, 1, 2856, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108701, "お正月だし\nみんなで楽しく\n遊んじゃおー！", 0, 1, 2857, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 108701, "うーん…\n騎士くんはお正月\n何してるんだろ…", 0, 1, 2858, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 108701, "ううぅー…\n騎士くんを見てると\nドキってなっちゃう…", 0, 1, 2859, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108701, "あたしから\nおいしそうな匂い？\nくんくん…ソースだ！", 0, 2, 2860, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108701, "ヘアピンが…\n拾ってくれたんだ！\nありがとう騎士くん！", 0, 2, 2861, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108701, "髪飾りがかわいい？\n耳の近く触りたい？\n…優しくね？　", 0, 2, 2862, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108701, "グローブで食べるの\n大変そう？　特訓して\n今はお箸も使えるよ！", 0, 2, 2863, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 108701, "えっ、ほっぺが赤い？\nそうかな…？\n気のせい気のせい！", 0, 2, 2864, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 108701, "騎士くんといるとね…\nなんでもない！\nまだ内緒っ！", 0, 2, 2865, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108701, "おはよー！\n今日も明るく元気に\nがんばろー！", 1, 2, 2866, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108701, "お腹すいてない？\n一緒にご飯食べよ！\n何にしようかなー♪", 2, 2, 2867, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108701, "今日もいっぱい\nがんばったー！\nいつも？　…そう？", 3, 2, 2868, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108701, "休憩休憩♪\nちょっと休んで\nガンガン動くぞー！", 0, 3, 2870, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108701, "かわいい♪\nでも、壊しそうで\nちょっと怖い…", 0, 3, 2871, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108701, "はぁー♪\nいい香りー…\n気分スッキリ♪", 0, 3, 2872, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108701, "ほっこりしてて\nぬくぬく…\n丸まりたい…", 0, 3, 2873, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108701, "ど、どうしよう…！\n手が、止まんないよ！", 0, 3, 2875, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 108701, "へー…お年玉って\n昔はお餅だったんだ\n…今のほうがいいね", 0, 3, 2876, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109001, "甘いチョコの香りを\n漂わせて…ふふっ\nこれで準備万端ですわ", 0, 1, 2878, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109001, "ここを私のチョコで\n埋め尽くしますわ", 0, 1, 2879, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109001, "エプロンを着て\nあの方を待つ私…\nまるで…クスクス", 0, 1, 2880, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109001, "キッチンはどこかしら\n今のうちに作って\nおきたいものが…", 0, 1, 2881, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109001, "あのお方を私のチョコ\nなしでは生きられない\n体に…ふふっ\n冗談ですわ…", 0, 1, 2882, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 109001, "このチョコは運命の味\n一口食べれば私の虜…\nそれも運命ですわ…", 0, 1, 2883, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 109001, "私ともあろうものが\nなんだかドキドキして\nきましたわ…", 0, 1, 2884, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109001, "あなた様。私に\n隠しているものなど\nありませんよね…？", 0, 2, 2885, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109001, "私の手作りチョコです\n味の方にも自信\nありますのよ？", 0, 2, 2886, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109001, "少し苦いのは\nチョコがビターなだけ\nお気になさらず…", 0, 2, 2887, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109001, "解毒薬はどこに\nいれたかしら…？", 0, 2, 2888, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 109001, "運命で結ばれている\nあなた様と私…\nチョコも特別でないと\nいけませんわ", 0, 2, 2889, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 109001, "チョコを食べた後は\nメインディッシュ…\nさぁ、私を召し上がれ", 0, 2, 2890, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109001, "目覚めのチョコですわ\nすっきり目が覚めます\nしばらく眠れなく\nなりますけど…", 1, 2, 2891, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109001, "おやつのチョコですわ\nデザートにどうぞ\n空腹を感じなく\nなりますわ…クスクス", 2, 2, 2892, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109001, "お夜食のチョコですわ\nとてもよく眠れます\nさ、溶けてしまう前に", 3, 2, 2893, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109001, "この心地良さ…\n参考になりますわ\n次の薬は…クスクス", 0, 3, 2895, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109001, "この中、何か\n入りそうですわね\n…チョコレート、とか", 0, 3, 2896, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109001, "いい香り…この香りと\n同じものを調合して\n私も持っていたい", 0, 3, 2897, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109001, "この暖かさ…\nチョコが溶けないよう\n気を付けなくては", 0, 3, 2898, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109001, "いい味をしていますわ\n私も腕を磨きましょう\n料理と、調合の腕を…", 0, 3, 2900, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109001, "勉強になりますわ\n知識はいくらあっても\n困りませんもの\n特に、人体の知識は…", 0, 3, 2901, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109101, "弟くんのことを\n考えると\nあっという間に時間が\n過ぎちゃう…", 0, 1, 2903, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109101, "お姉ちゃんの\n元気の源は、弟くんの\n喜ぶ顔なんだから♪", 0, 1, 2904, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109101, "弟くんと会ったら\nまずは抱きしめて\nそれからそれから…", 0, 1, 2905, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109101, "今日のお姉ちゃんは\n昨日のお姉ちゃんより\nいいお姉ちゃんだよ♪", 0, 1, 2906, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109101, "弟くん、もっと\nお姉ちゃんに\n甘えてもいいのに♪", 0, 1, 2907, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 109101, "お姉ちゃんのチョコ\n食べさせてあげたい…", 0, 1, 2908, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 109101, "ドキドキしてきた…\nお姉ちゃんなのに\nどうしてかなぁ…", 0, 1, 2909, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109101, "お姉ちゃんを\n好きなだけ\n触っていいよ♪", 0, 2, 2910, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109101, "そんなにお姉ちゃんの\nチョコが欲しいの？\n食いしん坊さん♪", 0, 2, 2911, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109101, "ナデナデするのは\nお姉ちゃんの仕事！", 0, 2, 2912, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109101, "ありがとうなんて…\nお姉ちゃんとして\n当たり前だよ♪", 0, 2, 2913, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 109101, "どうしたのかなっ？\nお姉ちゃんはいつでも\n弟くんのそばにいるよ", 0, 2, 2914, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 109101, "チョコを作ったよ\n隠し味は、たくさんの\n弟くんへの愛情♪", 0, 2, 2915, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109101, "おはよ。お姉ちゃんが\n顔を洗うの\n手伝ってあげるね♪", 1, 2, 2916, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109101, "ご飯を食べたら\n眠たくなるよね…\nお姉ちゃんと寝る？", 2, 2, 2917, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109101, "そろそろ眠くなった？\nお姉ちゃんが添い寝\nしてあげるね♪", 3, 2, 2918, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109101, "この気持ちよさは\nお姉ちゃんも太鼓判♪", 0, 3, 2920, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109101, "このかわいさは\nお姉ちゃんも大満足♪", 0, 3, 2921, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109101, "この香り、チョコに\n使えるかな…？", 0, 3, 2922, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109101, "弟くんと一緒に\nぬくぬくしたいな…♪", 0, 3, 2923, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109101, "すごくおいしいねっ♪\nあとで運動しなきゃ…", 0, 3, 2925, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109101, "本を読んでる\n知的なお姉ちゃん\n素敵でしょ♪", 0, 3, 2926, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106101, "アタシって…\nここにいても\nいいのか…？", 0, 1, 2928, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106101, "ちゃんとした建物に\nいるのが久しぶりで\nそわそわする…", 0, 1, 2929, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106101, "ここで天楼覇断剣\n出す練習したら\nアイツら怒るかな？", 0, 1, 2930, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106101, "ギルドハウスか…\nこういう隠れ家\n欲しかったんだよな", 0, 1, 2931, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106101, "めちゃくちゃ暇だ…\n…なんかやること\nないのか？", 0, 1, 2932, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 106101, "みんなが早く\nアタシのこと\n思い出さないかな", 0, 1, 2933, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 106101, "アイツはいいヤツだし\n戦う時はアタシも\n協力してやるか！", 0, 1, 2934, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106101, "ちゃんはいらん！\nノウェムでいい！\n分かったか？", 0, 2, 2935, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106101, "どうした？\n戦うのか！？\n…なんだ、違うのか", 0, 2, 2936, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106101, "身長のことは\nあんまり言うな！\n気にしてるからな！", 0, 2, 2937, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106101, "アタシの戦い方は\nなんでもアリだ！\nだから見習うなよ？", 0, 2, 2938, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 106101, "オマエとの思い出か…\n敵として戦ってばっか\nだったな！", 0, 2, 2939, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 106101, "オマエやオクトーと\n一緒に、あちこち\nいってみたいんだ！", 0, 2, 2940, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106101, "よっ！　おはよう！\n朝だぞー！\n起きてるかー？", 1, 2, 2941, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106101, "腹減ったー！\n飯食うぞ、飯！\nオマエも行くよな？", 2, 2, 2942, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106101, "ふわぁ…\nまだ起きてんのか？\n元気だなオマエ…", 3, 2, 2943, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106101, "ふぃ～…\n極楽極楽…", 0, 3, 2945, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106101, "おいオマエ！\n寝技の練習に\n付き合ってもらうぞ", 0, 3, 2946, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106101, "くんくん…\nたまにはこういうのも\n悪くないかもな", 0, 3, 2947, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106101, "あー…\nもっと前にコイツと\n出会いたかった…", 0, 3, 2948, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106101, "VRでも飯はうまい！\n腹が減るのはちゃんと\n生きてる気がするな", 0, 3, 2950, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106101, "勉強は大事だな！\nうん！…だめだ！\n全然わからん！", 0, 3, 2951, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101401, "探偵というのは\n謎を追い求める\n生き物なのさっ！", 0, 1, 2953, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101401, "事件のオチは\n往々にして\n取るに足らないものさ", 0, 1, 2954, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101401, "読書で得る知識が\n探偵の\n血肉になるんだよ", 0, 1, 2955, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101401, "この前の\n捜査資料…\nどこにあったかな？", 0, 1, 2956, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101401, "どんな小さな謎も\n私にとっては\n宝石みたいなものさ！", 0, 1, 2957, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 101401, "助手くんと一緒だと\nよい発想が浮かぶ…\nどうしてだろうね？", 0, 1, 2958, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 101401, "助手くんの好きな\nタイプって…\nす、推理してみるっ！", 0, 1, 2959, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101401, "珈琲、淹れたてだよ\n一杯どうだい？", 0, 2, 2960, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101401, "事件の考察を\nまとめてみたんだけど\n聞いてくれるかな？", 0, 2, 2961, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101401, "小さな違和感を\n見逃さない…\n探偵の心得だよ", 0, 2, 2962, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101401, "機能的な手帳とペン…\nこの仕事には\n欠かせないね！", 0, 2, 2963, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 101401, "助手くんに\n珈琲…\n淹れてほしいなぁ～", 0, 2, 2964, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 101401, "助手くんは\n私にとって\n最高の相棒だよ☆", 0, 2, 2965, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101401, "朝はいいひらめきが\n得られるんだ…\nさあ捜査を始めよう！", 1, 2, 2966, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101401, "一休みしたら\n推理の答え合わせを\nしてみようか？", 2, 2, 2967, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101401, "夜の張りこみかい？\nなら私も\n同行させてほしいな！", 3, 2, 2968, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101401, "脳を酷使した後は\nゆっくり休む…\nこれも仕事だよ？", 0, 3, 2970, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101401, "この愛らしさ…\n要捜査対象だね！", 0, 3, 2971, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101401, "思考が活性化される\n素敵な香りだね…", 0, 3, 2972, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101401, "くぅ、心地よすぎる…\n今だけは捜査も\n一休みだね♪", 0, 3, 2973, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101401, "いっぱい食べて\nいっぱい考える\n探偵の本分だね！", 0, 3, 2975, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 101401, "探偵だからね\nミステリには\n一家言あるんだ", 0, 3, 2976, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105501, "先輩、素敵だなぁ…\nもっと近づければ\nいいのに…", 0, 1, 2978, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105501, "せ、先輩がいた部屋！\n私には刺激が強い…", 0, 1, 2979, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105501, "先輩を観察しやすい\n場所はあるかな…", 0, 1, 2980, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105501, "バ、バイト代出たし、\nせ、先輩をご飯とかに\n誘いたい…な", 0, 1, 2981, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105501, "今頃先輩は\nあの辺かな…？\n見に行ってみよう！", 0, 1, 2982, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 105501, "私と先輩の出会いって\n思い返すと少女漫画\nみたい…えへへ", 0, 1, 2983, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 105501, "せ、先輩になら\n観察されてもいいなぁ", 0, 1, 2984, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105501, "バイト先に新しい子が\n入って来たんです…\nわ、私、先輩です♪", 0, 2, 2985, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105501, "せ、先輩…？\nえっと、あの、その、\nあわわわわ…！", 0, 2, 2986, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105501, "えへへ…えへへ～\nせ、先輩から\n話しかけてくれました", 0, 2, 2987, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105501, "ま、また…\n先輩と一緒に\nバイトしたい…です", 0, 2, 2988, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 105501, "私と先輩が同じ空間に\n…ゆ、夢みたいです", 0, 2, 2989, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 105501, "せ、先輩のそばに\n居られて、私\n今、とっても\n幸せです…", 0, 2, 2990, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105501, "せ、先輩…！\nおひゃっ！\nおはようございます！", 1, 2, 2991, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105501, "先輩ランチに\n行きましょう！\nよし、練習はバッチリ", 2, 2, 2992, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105501, "あ、せ、先輩。\nえと、そ、外…\nもう真っ暗ですねぇ", 3, 2, 2993, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105501, "こ、ここで先輩も\n休んだり…はっ！\nわ、私ったらなにを！", 0, 3, 2995, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105501, "かわいいですねー\n君も一緒に、\n先輩を観察する？", 0, 3, 2996, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105501, "ここから先輩の\n香りがすればなあ", 0, 3, 2997, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105501, "先輩と一緒に\nぬくぬくできたら\n最高なんだけどなぁ", 0, 3, 2998, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105501, "おいし～\nこれは\n先輩も好きな味かな", 0, 3, 3000, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 105501, "す、好きな人に\n近づく方法…", 0, 3, 3001, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109201, "私の力は、大切な\n人たちを護るために\n役立てたいんだ", 0, 1, 3003, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109201, "きれいな景色と\n美味しいご飯！\nこの世界も素敵だね", 0, 1, 3004, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109201, "この世界のみんなとも\nもっと仲良く\nなりたいな！", 0, 1, 3005, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109201, "わー！\nあれもこれも\n気になっちゃうな！", 0, 1, 3006, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109201, "しっかりしなきゃって\n思ってても、\nグレアといるとつい\n甘えちゃうんだよね～", 0, 1, 3007, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 109201, "違う世界でも\n友だちができて、\nすごくうれしいよ！", 0, 1, 3008, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 109201, "みんなとも一緒に\n授業とか受けたいな～\n絶対楽しそうだもん！", 0, 1, 3009, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109201, "歩き方がきれい？\nえへへ、そうかな…？", 0, 2, 3010, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109201, "勉強は好きだよ！\n知識が増えていくのは\nやっぱり楽しいから", 0, 2, 3011, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109201, "私たちの国マナリアは\n人々の間に魔法が\n伝えられた、その\n始まりの地なんだ！", 0, 2, 3012, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109201, "この部屋にも、\nもっとたくさん本棚が\n増えないかな～…？", 0, 2, 3013, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 109201, "あなたも魔法、\n覚えてみる？\n私、教えるよ！", 0, 2, 3014, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 109201, "お茶の時間に\nしたいな～…\n一緒にどう？", 0, 2, 3015, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109201, "おはようっ！\n今日も素敵な\n一日にしようね！", 1, 2, 3016, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109201, "お昼ご飯は\nちゃんと食べた？\n午後も頑張ろうね！", 2, 2, 3017, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109201, "つい本の続きが\n気になって、夜更かし\nしちゃうんだよね～…", 3, 2, 3018, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109201, "はぁ、気持ちいい～…\nちょっとだけ\n眠っちゃおうかな…", 0, 3, 3020, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109201, "わあ、これ可愛い！\nグレアにも見せて\nあげたいな", 0, 3, 3021, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109201, "いい香り～！\nなんだか気持ちが\n落ち着くね", 0, 3, 3022, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109201, "あぁ、あったかくて\n幸せ～♪　身体が\nポカポカしてきたよ！", 0, 3, 3023, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109201, "こ、これ美味し～！\n止められないよー！", 0, 3, 3025, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109201, "ふんふん…\n…はっ！　む、夢中に\nなっちゃってた…", 0, 3, 3026, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109301, "新しい建物に入ると\n必ず一度は迷子に\nなっちゃうですよ…", 0, 1, 3028, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109301, "おやつはどこですか？\nお腹が空いて\nきちゃったですぅ", 0, 1, 3029, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109301, "宿題、７つぐらい\n出しそびれたですけど\nまあ大丈夫ですぅ！", 0, 1, 3030, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109301, "不思議なこと\nばかりですけど…\nまぁ大丈夫です！\nこれもせいしゅんです", 0, 1, 3031, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109301, "なにか面白そうなもの\nないですかね～？", 0, 1, 3032, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 109301, "ここのみんなとも\nもうお友だちですぅ！", 0, 1, 3033, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 109301, "おなかが減ったです…", 0, 1, 3034, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109301, "ごはんも美味しいし\nここはいいところです\nルゥは幸せですぅ！", 0, 2, 3035, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109301, "魔法の話ですか？\nそれはアンせんぱいに\n聞いてほしいですぅ…", 0, 2, 3036, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109301, "あなたもお勉強\n苦手ですか？", 0, 2, 3037, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109301, "ルゥの帽子、\nかわいいです？\nお気に入りですぅ♪", 0, 2, 3038, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 109301, "お友だちいっぱいで、\nルゥはうれしいです♪", 0, 2, 3039, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 109301, "一緒にお勉強…！？\nぐえぇ、イヤですぅ！", 0, 2, 3040, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109301, "朝ですねぇ…ふわわ…\nルゥはまだ夢の中に\nいたいですぅ…", 1, 2, 3041, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109301, "午後もいっぱい\n遊ぶですよ～！", 2, 2, 3042, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109301, "夜ふかしって\nドキドキするです！\nせいしゅんですぅ！", 3, 2, 3043, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109301, "ふかふかですぅ～♪\nおひるねが\nしたくなるですぅ！", 0, 3, 3045, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109301, "これふかふかですぅ！\n机でうとうとする時の\n枕によさそうですぅ", 0, 3, 3046, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109301, "ふぁぁ、いいにおい…\nなんだか気持ちが\nよくなってきたですぅ", 0, 3, 3047, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109301, "ぽっかぽかですぅ～！\nあったまったら\nまた遊びにいくですぅ", 0, 3, 3048, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109301, "お、おいしいですぅ！\n全部ルゥに\nくださいですぅ！", 0, 3, 3050, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109301, "本は…読むと、\nすぐ眠気がぁ…\n…ぐぅ…", 0, 3, 3051, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109401, "ピアノ…\nどこかにないかな？", 0, 1, 3053, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109401, "ふぅ…\n落ち着くな…", 0, 1, 3054, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109401, "マナリアはいつも\n賑やかなんだ…", 0, 1, 3055, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109401, "みんな…\n今頃何してるかな？", 0, 1, 3056, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109401, "まさか違う世界に\n来ちゃうなんて…\nびっくりだよね…", 0, 1, 3057, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 109401, "人と話すのは\n得意じゃないな…", 0, 1, 3058, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 109401, "ここのみんなと一緒に\n学院に通えたら…\nちょっと面白いかも", 0, 1, 3059, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109401, "やっぱり、私の翼や\n尻尾…気になる？", 0, 2, 3060, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109401, "一番得意なのは、\nやっぱり炎魔法かな…\n加減は難しいけど…", 0, 2, 3061, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109401, "私の故郷は竜の国…\n険しい山脈を\n越えた先にあるんだ", 0, 2, 3062, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109401, "アン…？\nあっ、ご、ごめんね！\n間違えちゃった…", 0, 2, 3063, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 109401, "ふん…ふん…♪\n…わっ！\n今の、聞いてた…？\nは、恥ずかしい…", 0, 2, 3064, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 109401, "私に手伝えること、\nなにかある…？\nなんでも言ってね", 0, 2, 3065, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109401, "朝の空気って\nすごく\n静かで透き通ってて…\nなんだか落ち着くよね", 1, 2, 3066, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109401, "学院食堂のランチも\n美味しいけど…\nここのご飯も好きかな", 2, 2, 3067, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109401, "そろそろベッドに\n行かないとだね\n…寝相、今夜こそ\n気を付けよう…！", 3, 2, 3068, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109401, "羽を伸ばしたく\nなっちゃうね…ふふ", 0, 3, 3070, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109401, "わ、私が持つには\nかわいすぎるかな…\nでも、素敵だね", 0, 3, 3071, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109401, "いい香り…\nこれ、\nアンも好きそう…", 0, 3, 3072, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109401, "火を見ていると\n少し落ち着くんだ…\nへ、変かな？", 0, 3, 3073, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109401, "わ、美味しい…！\nこれ、どうやって\n作ったのかな…？", 0, 3, 3075, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109401, "この本…アンにも\n見せてあげたら\n喜ぶかな、ふふっ", 0, 3, 3076, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109501, "着物はきれいですが\n歩きにくいです…\nいっそ脱いで…ぐふふ", 0, 1, 3078, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109501, "あっ、いけません\n着物がはだけて…はっ\nもしやドＳさんが…！", 0, 1, 3079, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109501, "この部屋は妄想する\nのにうってつけです…\nぐふふ…じゅるり", 0, 1, 3080, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109501, "扇をゆっくりと開く\nこの感触…なぜだか\n艶めかしさを感じます", 0, 1, 3081, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109501, "「マッサージをすると\n言いながらドＳさんの\n手は次第に」…ぐふふ", 0, 1, 3082, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 109501, "…ドＳさんの視線を\n思い出しながら踊りの\n練習です…イイ！", 0, 1, 3083, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 109501, "よいではないか\nよいではないか～\nいけませんご堪忍を～", 0, 1, 3084, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109501, "あ～れ～～～！\nドＳさんもお好きな人\nですねぇ…ぐふふ", 0, 2, 3085, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109501, "どこにいてもドＳさん\nの気配を感じ取れる\nようになりました…", 0, 2, 3086, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109501, "こんな場所で\nなんというプレイを！\n…妄想でした", 0, 2, 3087, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109501, "クウカを押し倒して\nそのまま…じゅるり\nああっ、着物が…！", 0, 2, 3088, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 109501, "ドＳさんは優しいです\n優しいのにドＳとは\nさすが人格破綻者…！", 0, 2, 3089, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 109501, "ドＳさんの考えている\nことが伝わってきます\nはい…クウカ脱ぎます", 0, 2, 3090, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109501, "眩しい朝日がクウカの\n体を焦がします\n…それもイイ！", 1, 2, 3091, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109501, "お昼のランチは\nクウカがいいだなんて\n大胆過ぎです…じゅる", 2, 2, 3092, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109501, "…今夜のドＳさんは\n大胆です…ぐふふ…\nはっ、視線が…！？", 3, 2, 3093, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109501, "この心地良さ、\n心が休まりすぎて\n体が疼いてきます…！", 0, 3, 3095, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109501, "すごく愛らしいです…\n妄想も出てこない\nくらい…じゅるり", 0, 3, 3096, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109501, "この香りに包まれて\nいると、いい気持ちに\nなって…いきますぅ…", 0, 3, 3097, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109501, "「熱を帯びた体は\nどうしようもなく\n誰かを求め…」ぐふふ", 0, 3, 3098, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109501, "お腹が膨らんで\nいきます…それは\nまるで…ぐふふ", 0, 3, 3100, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109501, "クウカの探している\nジャンルはどこに\nありますかね？", 0, 3, 3101, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109601, "カチョーフーゲツは\nトーゴクの心を\n表した言葉デスね", 0, 1, 3103, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109601, "オー！　新しい忍法を\n思いついたデス！\nさっそく試すデス！", 0, 1, 3104, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109601, "トーゴクの天気には\n槍が降る日も\nあるそうデス…", 0, 1, 3105, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109601, "我がショーグンは\n永遠にフメツデース\nいざ出陣デース！", 0, 1, 3106, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109601, "壁に耳あり\n障子に…ん？\nメアリーって誰デス？", 0, 1, 3107, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 109601, "ショーグンがいれば\nワタシはどんな\n忍法も使いこなせマス", 0, 1, 3108, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 109601, "不届き者はどこで\nゴザルデス？　忍法で\n退治してくれヨウ！", 0, 1, 3109, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109601, "ショーグン！　新しい\n忍法を覚えたデス！\n見てほしいデース！", 0, 2, 3110, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109601, "レッツ手裏剣道デス！\n投げてソウロウで\nゴザルデース！", 0, 2, 3111, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109601, "ふりかかる火の粉は\nワタシが払うデス！\nバンバン！", 0, 2, 3112, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109601, "とっておきの忍法が\n吹き荒れるデス！\nよそ見はダメデスヨ？", 0, 2, 3113, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 109601, "ショーグンは\n何階建てのお城を\n建てたいデスか？", 0, 2, 3114, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 109601, "テンシュカクから\n平和な城下町を\n眺めてみたいデス…", 0, 2, 3115, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109601, "おはようございマス！\n本日はオヒガラモヨク\n良い日になりそうデス", 1, 2, 3116, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109601, "お腹すいたデース…\nショーグンもご飯に\nしまショウ！", 2, 2, 3117, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109601, "闇討ちに遭わぬよう\n夜は気を付けて\n歩くデスよー？", 3, 2, 3118, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109601, "天下分け目の\n戦いに備えて…\nマッタリデース", 0, 3, 3120, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109601, "口寄せの術で\n召喚したいくらい\nかわいいデース", 0, 3, 3121, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109601, "いい匂いデ～ス\n香り玉の開発の\n参考になりそうデスね", 0, 3, 3122, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109601, "ワタシの懐よりも\nゾーリが暖まりマス…\nライバル出現デス…！", 0, 3, 3123, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109601, "腹が減っては\n戦はできぬデース！\n天下統一に向けて\n腹ごしらえデス！", 0, 3, 3125, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109601, "ムム、暗号文デス！？\n解読には時間が\nかかりそうデース…", 0, 3, 3126, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109701, "掃除も終わりましたし\nそろそろ、食事の\n準備をしなくては", 0, 1, 3128, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109701, "異世界の演劇…\n許されるならば、\n観てみたいです", 0, 1, 3129, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109701, "こちらの世界には、\nマヨネーズが普通に\n存在するのですね…", 0, 1, 3130, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109701, "こちらの世界は食材も\n面白い物が多いですね\nなにかレシピを\n考えてみましょうか", 0, 1, 3131, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109701, "みょんみょんみょん…\n魔法の練習は\nこれぐらいですね", 0, 1, 3132, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 109701, "倒木の撤去で思わぬ\n収入となりました\nサレン様に\nお渡ししておかないと", 0, 1, 3133, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 109701, "料理を美味しく\n召し上がって頂けると\nメイド冥利につきます", 0, 1, 3134, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109701, "お出かけでしたら、\nレムも同行\nさせてくださいね", 0, 2, 3135, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109701, "レムにできること\nでしたら、なんでも\nお申し付けください", 0, 2, 3136, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109701, "じーー。いえ、\nその髪の長さが少し、\nとても少し、かなり\n少し気になったもので", 0, 2, 3137, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109701, "鉄球ですか？\nあれは護身用ですので\n問題はありませんよ？", 0, 2, 3138, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 109701, "計算の勉強は\n進んでおりますか？\nレムでよろしければ、\nいつでもお教えします", 0, 2, 3139, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 109701, "次はもっと、大きな\n魔物料理に挑戦します\nワイバーンの姿焼き\nなんてどうでしょうか", 0, 2, 3140, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109701, "おはようございます\n朝食の準備が\nできておりますので、\n食卓におこしください", 1, 2, 3141, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109701, "昼食の時間ですね\n何か食べたいものは\nありますか？", 2, 2, 3142, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109701, "寝ないのか、ですか？\nメイドの就寝は、\n屋敷で最後と\n決まっておりますので", 3, 2, 3143, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109701, "ふんわり…\nこのふんわりさは、\n鬼をも黙らせます…", 0, 3, 3145, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109701, "ふふっ…異世界でも\nかわいいものは\n共通なのですね", 0, 3, 3146, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109701, "よい香りですね\n魔女教の悪臭とは\n大違いです", 0, 3, 3147, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109701, "ぬくぬくです…はっ！\nこれはいけません！\n人を…いえ、\n鬼をだめにします！", 0, 3, 3148, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109701, "はむはむ…美味しい！\nこの味を再現できたら\n褒めてもらえる\nかもしれません…！", 0, 3, 3150, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109701, "絵がいっぱい…\n異世界の本は\nとても面白いですね", 0, 3, 3151, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109801, "異世界であろうと、\nラムのやるべきことは\n変わらないわ\nまずは休憩するわよ", 0, 1, 3153, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109801, "マヨネーズがある\nということは、\nバルスの故郷が近い\nのかもしれないわね", 0, 1, 3154, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109801, "この世界では、バルス\nの言う言葉が使われる\nときがあるのね\n忌々しいかぎりだわ", 0, 1, 3155, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109801, "ラムたち以外にも\nメイドが居るのね\nラムが…いえ、\n仕事を押しつけるわ", 0, 1, 3156, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109801, "…千里眼で視るかぎり\n周囲に異変はないわね\n危険がないなら、\nあとはレムに任せるわ", 0, 1, 3157, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 109801, "…ラムもまた、\n「お姉ちゃん」って\n呼ばれる日が\nくるのかしらね", 0, 1, 3158, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 109801, "ハッ！　ラムが作る\n蒸かし芋は完璧ね\n…気が向いたら、また\n作ってやろうかしら", 0, 1, 3159, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109801, "ラムに構う暇が\nあるなら働きなさい\nそうすれば、\nラムが楽をできるわ", 0, 2, 3160, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109801, "ラムに何かを\n期待しても無駄よ\nラムに命令できるのは\nロズワール様だけだわ", 0, 2, 3161, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109801, "暇なら買い物に\n付き合いなさい\n荷物を\n持たせてあげるわ", 0, 2, 3162, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109801, "ラムになんの用？\nふざけた内容なら\nねじ切るわよ", 0, 2, 3163, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 109801, "そういえば、あなたは\n記憶喪失だったわね…\n思い出す努力をなさい\n…家族が悲しむわよ", 0, 2, 3164, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 109801, "蒸かし芋が欲しい？\n随分と厚かましいわね\n…まだ残りがあるから\n好きに食べなさい", 0, 2, 3165, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109801, "だらしない顔ね\n顔を洗って\nしゃんとなさい", 1, 2, 3166, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109801, "お腹が空く時間ね\nラムの昼食を\n用意させてあげるわ", 2, 2, 3167, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109801, "メイドが寝るのは\n屋敷で最後…\nラムが寝るために、\nさっさと寝なさい", 3, 2, 3168, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109801, "…ここから動かない\nという、誓いを\n立てたくなるわね", 0, 3, 3170, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109801, "これは…かわいいわね\nもちろん、\nラムほどではないけど", 0, 3, 3171, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109801, "…香炉かしら？\nこちらの世界にも\n質のよい物があるのね", 0, 3, 3172, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109801, "見つけてしまったの\nかもしれないわ…\nラムの楽園を！", 0, 3, 3173, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109801, "…はむっ\nラムの蒸かし芋ほど\nではないけど、\nなかなかいけるわね", 0, 3, 3175, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109801, "異世界ならではの\n面白い内容もあるわね\nとても興味深いわ", 0, 3, 3176, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109901, "ねえパック、\nにゃんにゃか\nにゃかにゃんしよっか", 0, 1, 3178, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109901, "時間もあるし、\nこっちの世界の石で\n石積みしようかな", 0, 1, 3179, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109901, "私もお姉さんだし、\nサレンさんを\n見習わないと…うん！\n私、すごーく頑張る！", 0, 1, 3180, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109901, "この世界は、銀髪で\nハーフエルフでも受け\n入れてくれるのね…\nなんだか嬉しいな", 0, 1, 3181, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109901, "今日のご飯…\nピーマルは\n入ってないわよね…？", 0, 1, 3182, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 109901, "また、みんなで\n一緒に買い物に\n行きたいな", 0, 1, 3183, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 109901, "パック以外から\n家族って呼ばれたの\n初めて…ふふっ♪\nちょっと嬉しいかも！", 0, 1, 3184, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109901, "今からパックの\n毛繕いをするけど…\n一緒にする？", 0, 2, 3185, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109901, "私も何かお手伝いを…\nあ、そうだ！\n料理とかどうかな！", 0, 2, 3186, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109901, "ねえ、ここで\n歌の練習って、\nしてもいいかな？", 0, 2, 3187, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109901, "…あ、ごめんなさい！\n話しかけられるのって\nあまり慣れてなくて…", 0, 2, 3188, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 109901, "すごーく頑張るから、\nこれからも勉強を\n手伝ってね", 0, 2, 3189, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 109901, "せーの、びくとりー！\nふふっ…また、一緒に\nラジーオタイソー\nしようね♪", 0, 2, 3190, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109901, "ちゃんと起きてる？\nふふっ…よかった。\nあ、べ、別に心配した\nわけじゃないからね？", 1, 2, 3191, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109901, "お昼ご飯が楽しみね♪\nあ、こっちの世界って\nまよねーずはあるの？", 2, 2, 3192, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109901, "そろそろ寝る時間よ。\n夜更かししたら、\nめっ！　だからね！", 3, 2, 3193, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109901, "勉強も終わったし…\nちょっ、ちょっとだけ\n休んでもいいよね…？", 0, 3, 3195, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109901, "か、かわいい…！\nパックのかわいさに\n匹敵するかも…！", 0, 3, 3196, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109901, "良い香り…\nロズワールの屋敷にも\nこういうのって\nあるのかな？", 0, 3, 3197, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109901, "あったかい…\nずっとここに…はっ！\nダメダメ！　王選の\nために勉強しないと！", 0, 3, 3198, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109901, "あ…これ美味しい！\nこっちの世界は色んな\n食べ物があるのね。", 0, 3, 3200, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 109901, "知らないことが\nいっぱい書いてある…\n異世界の本って、\n面白いのね", 0, 3, 3201, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110001, "海ってマジ最高！\nビーチの撮影も\n超楽しいしー♪", 0, 1, 3203, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110001, "20パーって何パ？\n今度ヒデサイに\n聞いてみよっ！", 0, 1, 3204, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110001, "夏の思い出を\nヒデサイと一緒に\n作りたいな～", 0, 1, 3205, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110001, "テンションあがる\n水着ゲットして\n超ラッキーだよ～♪", 0, 1, 3206, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110001, "休憩が終わったら\n撮影にもどらないと！\n気合入れてこ♪", 0, 1, 3207, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 110001, "ヒデサイとビーチで\nパーティーしたいな～\n超楽しそうだよね！", 0, 1, 3208, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 110001, "ヒデサイといると\n楽しくてうれしくて…\nカレシってこんな感じ\nなのかなぁ？", 0, 1, 3209, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110001, "見て見て～！\n夏らしいネイルに\nしてみたんだ～♪", 0, 2, 3210, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110001, "ヒデサイ～\nこの水着どうかな？\nイケてるっしょ♪", 0, 2, 3211, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110001, "水着姿の写真？\nもちいいよ！\nかわいく撮ってね！", 0, 2, 3212, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110001, "うちだって\n夏の海みたいに\nキラキラしちゃうよ！", 0, 2, 3213, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 110001, "ヒデサイとうちの\nフィーリングって\nめっちゃエモいよね♪", 0, 2, 3214, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 110001, "ねーねーヒデサイ！\n一緒に泳ぎにいこっ！\nほら、手繋いで♪", 0, 2, 3215, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110001, "水着オッケー！\nネイルもばっちり！\n朝から遊んじゃおー♪", 1, 2, 3216, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110001, "お昼食べたら\n昼寝して\nリフレッシュしよー♪", 2, 2, 3217, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110001, "水着ってマジで\n最強かも…このまま\nお風呂にも入れるし♪", 3, 2, 3218, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110001, "マジ気持ちいいー♪\nおに癒やされるん\nですけど～！", 0, 3, 3220, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110001, "やばばばばー！\nかわいすぎだってば！\nマジらぶだよ～♪", 0, 3, 3221, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110001, "あ、この香り超好き！\nモデル仲間に\n教えたげよ～♪", 0, 3, 3222, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110001, "あったか～い♪\n夏の太陽みたい！", 0, 3, 3223, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110001, "マジ美味しいねこれ♪\nビーチで\n流行っちゃうかも！", 0, 3, 3225, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110001, "夏のために\n最新ファッションを\n勉強ちう！", 0, 3, 3226, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110001, "なにコレもにカワ～♪\nなんて生き物？\nヒデサイ知ってる？", 0, 3, 3228, 10, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110001, "うわっ超フカフカ～\nなんかうち眠く\nなってきちゃった…", 0, 3, 3229, 11, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110101, "海の家でのんびり\n過ごしましょう\n熱中症には注意してね", 0, 1, 3230, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110101, "はぁ、どうやったら\n泳げるように\nなれるのかしら～", 0, 1, 3231, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110101, "海で日焼け止めを\n塗らないと、あとで\n大変なことになるわよ", 0, 1, 3232, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110101, "久しぶりに水着を\n着たんだけど\n変じゃなかったかな…", 0, 1, 3233, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110101, "波の音って落ち着くわ\n授業中に聞いたら\nみんな寝ちゃうかも？", 0, 1, 3234, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 110101, "海が楽しいって\n思えたのも\nあの子のおかげね", 0, 1, 3235, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 110101, "あの子と二人で海に\n来られてよかったわ\n一日があっという間ね", 0, 1, 3236, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110101, "男の子と海だなんて\n少女漫画みたいで\n憧れてたの～", 0, 2, 3237, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110101, "夏休みの宿題は\nちゃんとやった？\n遊んでばかりではダメ", 0, 2, 3238, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110101, "昼の海と夜の海\nどっちが好き？　私は\n君といる海が一番よ", 0, 2, 3239, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110101, "ふう、疲れたわ～\n冷たいジュースで\n元気回復ね！", 0, 2, 3240, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 110101, "海で見た君の背中\nすごく大きく見えたわ\nやっぱり男の子なのね", 0, 2, 3241, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 110101, "君と一緒に見た\n夜の海…\n忘れられそうにないわ", 0, 2, 3242, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110101, "準備ができたら\n海に出かけようよ\n今日も楽しみましょう", 1, 2, 3243, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110101, "あら、もうお昼？\n海の家でランチに\nしましょ♪", 2, 2, 3244, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110101, "夜の海岸についてきて\nくれる？　暗がりは\n一人じゃ怖くて…", 3, 2, 3245, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110101, "泳ぐ練習をしたら\n疲れちゃったわ～\nちょっと一休み…", 0, 3, 3247, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110101, "かわいいわね～\n浮き輪に乗せて\n海に浮かべたいわ～", 0, 3, 3248, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110101, "この匂い…\nなんだか夏の海を\n思い出すわ～", 0, 3, 3249, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110101, "夜の海風で冷えない\nように、しっかりと\n身体を暖めましょうね", 0, 3, 3250, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110101, "海で遊ぶ前に\nしっかり食べて\n体力つけましょう", 0, 3, 3252, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110101, "夏休み中でも\nしっかりお勉強\nしないと！", 0, 3, 3253, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110101, "この子に乗れば\n海の上でも\nすいすい進めるかしら", 0, 3, 3255, 10, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110101, "ビーナスの誕生！\n…なんちゃって\nはしゃぎすぎかしら？", 0, 3, 3256, 11, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110301, "夏の海には商機が\nあるはず…\nのがす手はないわね♪", 0, 1, 3257, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110301, "あたしが作った\n日焼け止め…\nこれは売れそうね！", 0, 1, 3258, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110301, "子どもたちのために\n浮き輪を用意して\nおかないと…", 0, 1, 3259, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110301, "海っていいわよね！\nみんなで羽を伸ばしに\n行きたいわ！", 0, 1, 3260, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110301, "波の音を聞いて\nのんびりするのも\n悪くないわね…", 0, 1, 3261, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 110301, "ただの砂遊びも\nあんたと一緒なら\n楽しいから不思議ね…", 0, 1, 3262, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 110301, "あんたがあたしの\nなんであれ、大切な\n存在ではあるのよね…", 0, 1, 3263, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110301, "夏の海って\nすごい人よね…\n迷子にならないでよ？", 0, 2, 3264, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110301, "ぼんやり海に浮かんで\n波に揺られるのも\n悪くないわよ？", 0, 2, 3265, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110301, "ビーチで子どもたちと\n一緒に砂のお城を\n作りたいわ♪", 0, 2, 3266, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110301, "海で食べる\n焼きそばパン…\nいいかもしれないわね", 0, 2, 3267, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 110301, "あたしの水着\n似合ってる？\nそう、ありがとう♪", 0, 2, 3268, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 110301, "あんたが居ると\nうれしい…って、\n子どもたちが\n言ってたわ！", 0, 2, 3269, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110301, "朝の浜辺って\nいいわよね\n散歩しに行きたいわ", 1, 2, 3270, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110301, "海の家のご飯って\nなんであんなに\nおいしいのかしら♪", 2, 2, 3271, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110301, "夜の海って、\nロマンチックよね。\nずっと眺めてたいわ…", 3, 2, 3272, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110301, "やわらかくて\n気持ちいい…\nこのまま寝ちゃいそう", 0, 3, 3274, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110301, "海に一緒に入れる\nぬいぐるみとか\nないかしら？", 0, 3, 3275, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110301, "夏らしい\nいい香りね…\nなんの香りかしら？", 0, 3, 3276, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110301, "あったかい…\n濡れた体を乾かすには\nちょうどいいわね♪", 0, 3, 3277, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110301, "泳いだ後の食事は\n普段の何倍も\nおいしく感じるわね！", 0, 3, 3279, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110301, "海に行く前にいろいろ\n調べなきゃね。備え\nあれば憂いなしよ！", 0, 3, 3280, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110301, "海の守り神？\nずいぶんかわいらしい\n神様ね♪", 0, 3, 3282, 10, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110301, "このぐらい\n大きな貝があったら\n食費も浮くかしら？", 0, 3, 3283, 11, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110401, "浜辺で食うものって\nなんであんなに\nうまいのかな～", 0, 1, 3284, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110401, "いてて…\nちょっと日焼け\nしすぎたかな", 0, 1, 3285, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110401, "砂浜で走ると\nいいトレーニングに\nなるんだぜ", 0, 1, 3286, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110401, "夏ならではの\n料理…か\n何がいいかな…", 0, 1, 3287, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110401, "やっぱりここは\n涼しくて快適だな", 0, 1, 3288, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 110401, "泳ぎの練習だったら\nいつでも付き合って\nやるからな！", 0, 1, 3289, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 110401, "水着で部屋の中に\nいるのって\n変な感じだな…", 0, 1, 3290, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110401, "うおっ！\nきゅ、急に\n触るなって…\nびっくりした", 0, 2, 3291, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110401, "え、砂がついてた？\n払ってくれたのか\nありがとよ", 0, 2, 3292, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110401, "夏バテしてないか？\nここで休んでけよ", 0, 2, 3293, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110401, "…そう見られると\n照れるっていうか…", 0, 2, 3294, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 110401, "カキ氷とか\n食いたくないか？\n氷、削ってやるよ", 0, 2, 3295, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 110401, "おまえと\n夏の思い出ができて\nよかったよ", 0, 2, 3296, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110401, "ふう…\n朝一番で泳ぐの\nやっぱ気持ちいいな", 1, 2, 3297, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110401, "一番あちぃ時間だ\n水分補給は\nこまめにな！", 2, 2, 3298, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110401, "今日もお疲れ\n明日のパトロールも\nよろしくな", 3, 2, 3299, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110401, "泳ぎ疲れたからな\n少し休憩だ…", 0, 3, 3301, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110401, "か、かわいい…\nいや、なんでもねぇ！", 0, 3, 3302, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110401, "この香り落ち着くな\nこういうのも\n悪くねーかも…", 0, 3, 3303, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110401, "あったけえ…！\n泳ぎ終わった後って\nけっこう体冷えてよ…", 0, 3, 3304, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110401, "海で食べると\nまた一味違うよな", 0, 3, 3306, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110401, "夏のサバイバル特集…\nちょっと読んでみるか", 0, 3, 3307, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110401, "へー！　こいつ、\nよくできてんな！\nいいなあ、実物も\n乗れたらいいのに", 0, 3, 3309, 10, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110401, "か、かわ…！\nいや、じゃなくて\nあ、あたしがここに\n寝ていいのか…？", 0, 3, 3310, 11, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110501, "故郷の花を飾っても\nいいかな～？\n部屋が楽しくなるよ～", 0, 1, 3311, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110501, "水着で歩いてると\n泳ぎたくなるさ～\n我慢できないさ～！", 0, 1, 3312, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110501, "夏が眩しいのは\nてぃーだがにこにこ\n笑ってるからさ～！", 0, 1, 3313, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110501, "泳ぎ疲れたら\n涼しい木陰で\nお昼寝するさ～", 0, 1, 3314, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110501, "クロールも\n犬かきも\n大得意さ～！", 0, 1, 3315, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 110501, "なんだか歌いたい\n気分さ～\nキミもどう～？", 0, 1, 3316, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 110501, "海の近くに住んで\nキミと歌って踊って\nそれが理想さ～！", 0, 1, 3317, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110501, "私とキミ\nどっちがたくさん\n泳げるか\n競争するさ～！", 0, 2, 3318, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110501, "キミも三線に興味が\nあるのかな～？\n私が教えるさ～！", 0, 2, 3319, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110501, "嫌なことがあっても\n私と歌って踊れば\nなんくるないさー！", 0, 2, 3320, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110501, "たまには時間を忘れて\n私とのーんびり\n海を眺めるさ～", 0, 2, 3321, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 110501, "ぶるぶるぶるっ！\n毛が濡れちゃっても\nへっちゃらさ～！", 0, 2, 3322, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 110501, "泳ぐ前は準備体操～！\n背中押してあげるから\nしっかりやるさ～！", 0, 2, 3323, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110501, "朝の浜辺は\nちゅらさんよ～\n一緒に歩こうよー！", 1, 2, 3324, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110501, "お昼ご飯は\n決まったかな～？\n私はタコライスさ～！", 2, 2, 3325, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110501, "星と波音の中で\n三線鳴らすのも\nオツなものさ～", 3, 2, 3326, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110501, "ちょうどよかったさー\nゆっくり休んだら\nまた泳ぎに行くさ～！", 0, 3, 3328, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110501, "砂浜に飾って\nおきたいさ～\nきっとかわいいさ～！", 0, 3, 3329, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110501, "くんくん…はあ\n癒されるさ…\n故郷の夏の匂いさ～", 0, 3, 3330, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110501, "おおー…！\n濡れた髪が\nみるみる乾いてくさ～", 0, 3, 3331, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110501, "ん～！　これ、\n海で食べたら\nもっとおいしいさ～", 0, 3, 3333, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110501, "海の生物図鑑ね～\nこの前みかけた魚も\n載ってるかな～？", 0, 3, 3334, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110501, "わぁ～！　みてみて！\n背中に乗れるさー！\nキミと一緒に\n乗りたいさ～！", 0, 3, 3336, 10, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110501, "ふかふかさ～\n海の底の\n人魚になった気分さ～", 0, 3, 3337, 11, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 105801, "あの人が\nわたしの騎士だったら\nいいのにな…", 0, 1, 3338, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 105801, "あなたのおかげで\nわたしの心はいつも\nお腹いっぱいです♪", 0, 2, 3339, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 105901, "許されるのならば、\n今すぐにでも、\n主さまのおそばに\n行きたいです…", 0, 1, 3340, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 105901, "主さま…\nお慕いしております…\nいつまでも、おそばに\n居させてください…", 0, 2, 3341, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 106001, "なんか最近、\nあいつのことしか\n考えてない…\nあたし…どうしよう…", 0, 1, 3342, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 106001, "そ、外に行くなら、\n連れていきなさいよね\nその…あたしが\n守ってあげるから…", 0, 2, 3343, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110601, "部屋の中は涼しくて\n気持ちええどすなぁ\n天国やわぁ", 0, 1, 3344, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110601, "クラゲはんって\nフリルいっぱいの\nドレスみたいやねぇ", 0, 1, 3345, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110601, "外でセミはんが\n元気に鳴いてはるねぇ\n今日もええ天気やわぁ", 0, 1, 3346, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110601, "こないに暑いと\n海に入って\n涼みたくなるわぁ", 0, 1, 3347, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110601, "いつか\nマホマホ王国にも\n海の家作りまひょ♪", 0, 1, 3348, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 110601, "うちも\n付き合いますえ\n泳ぎの練習しまひょ♪", 0, 1, 3349, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 110601, "うちの里帰り\n王子はんも一緒に\n来まへんか？", 0, 1, 3350, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110601, "王子はんは日焼けより\n色白美人の方が\n好きでっしゃろか？", 0, 2, 3351, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110601, "うちと一緒に\n浮き輪で海、\nプカプカしまへん？", 0, 2, 3352, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110601, "日が落ちたら\n線香花火\nやりたいわぁ♪", 0, 2, 3353, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110601, "木陰でのんびり\nお昼寝…\n最高やね～", 0, 2, 3354, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 110601, "お祭ではぐれても\n王子はんなら見つけて\nくれはります？", 0, 2, 3355, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 110601, "楽しい夏やわぁ\nずっとこんな日が\nつづくとええね", 0, 2, 3356, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110601, "うちにしては早起き？\n暑くて目が早う\n覚めてもうたんどす", 1, 2, 3357, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110601, "お昼は冷たい\nおそうめんとか\nどうでっしゃろ", 2, 2, 3358, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110601, "夜は星がきれいやね\n二人で浜辺でも\n行きまへんか？", 3, 2, 3359, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110601, "うーん、遊び疲れた\n身体にちょうどええ\nほっこり具合やわぁ", 0, 3, 3361, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110601, "かいらしい子は\nみーんな\nマホマホ王国の\n住人どすえ♪", 0, 3, 3362, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110601, "この香り…\n南国にいる気分やねぇ\n癒されるわぁ～", 0, 3, 3363, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110601, "これで\n冷えた身体も\nポカポカやねぇ", 0, 3, 3364, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110601, "外はまだ暑いやろうし\nバテんよう\nしっかり食べんとね", 0, 3, 3366, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110601, "海の中にもお城が\nあるんやねぇ\n行ってみたいわぁ", 0, 3, 3367, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110601, "この子、ふかふかやね\nこのままマホマホ王国\n乗せてってくれはる？", 0, 3, 3369, 10, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110601, "人魚姫になっても\n最後は王子はんと\nハッピーエンド\nどすえ～", 0, 3, 3370, 11, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107001, "真似したくなる\n人がここには\n大勢いますね", 0, 1, 3371, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107001, "偽物を一人\n紛れこませました\n…冗談です", 0, 1, 3372, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107001, "動物に変身して\nその辺りを\n散歩してきます", 0, 1, 3373, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107001, "失った記憶は\nどの程度回復するの\nでしょうね", 0, 1, 3374, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107001, "人や物だけでなく\nいずれは世界すら\n真似てみせます", 0, 1, 3375, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 107001, "次は彼に\nどんなことを\n教えましょうか…", 0, 1, 3376, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 107001, "知識はともかく\n私の力はまだ\n必要なようですね", 0, 1, 3377, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107001, "どうしました？\n…何でもない？\n…そうですか", 0, 2, 3378, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107001, "退屈しています\n芸でもして\n楽しませなさい", 0, 2, 3379, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107001, "研究のためです\n少し話し相手に\nなりなさい", 0, 2, 3380, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107001, "何か真似して欲しい？\n私の能力を何だと\n思っているのですか", 0, 2, 3381, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 107001, "貴方にはまだ色々と\n教えるべきことが\nありますね", 0, 2, 3382, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 107001, "随分多くの女性と\n知り合いなのですね\n驚きました", 0, 2, 3383, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107001, "…\nおはようございます\n…まだ朝ですよ", 1, 2, 3384, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107001, "食事にします\n…どうしました？\n早く持ってきなさい", 2, 2, 3385, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107001, "早く寝なさい\n夜更かしすると\n背が伸びませんよ？", 3, 2, 3386, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107001, "マサキより\n座り心地は\nいいですね", 0, 3, 3388, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107001, "意外に複雑な\n構造ですね", 0, 3, 3389, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107001, "真似るなら\n香りまで\n再現しなくては", 0, 3, 3390, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107001, "発火現象の\n模倣…なかなか\n難しいものです", 0, 3, 3391, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107001, "偽物や分身を作るには\nエネルギーが\n必要不可欠なのです", 0, 3, 3393, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 107001, "暇つぶしには\nちょうどよさそうです\n…", 0, 3, 3394, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110701, "学院かぁ…\n友達の作り方の\n授業はないかな？", 0, 1, 3396, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110701, "学院でぼっちに\nなったときのために、\n避難場所を\n探しておかないと…", 0, 1, 3397, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110701, "ぼっちの私が学院…\nな、なんだか\n目まいがしてきました", 0, 1, 3398, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110701, "クラスメイトに\n話しかける練習…\nむ、難しすぎます…！", 0, 1, 3399, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110701, "目指せ友達100人…\n無理かな…10人…\nや、やっぱり一人！", 0, 1, 3400, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 110701, "あなたと学院生活が\n送れるなんて、\nとっても\nうれしいです！", 0, 1, 3401, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 110701, "えへへ…\n学院でも、\nあなたと一緒に\n居られてうれしいな♪", 0, 1, 3402, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110701, "が、学院ってあんなに\n人が居るんですね…\n友達作りのイメトレを\nやり直さないと！", 0, 2, 3403, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110701, "私にとって\n部活動とはＢＢ団！\n今日もたくさん\n活動しましょうね！", 0, 2, 3404, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110701, "休み時間は寝たふり…\nご飯はおトイレ…\nぼっち対策は完璧です", 0, 2, 3405, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110701, "学院で友達を作る\n勉強は完璧ですよ！\n購買部でパンを買って\n渡すんですよね！", 0, 2, 3406, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 110701, "学院でも、\nＢＢ団の活動を\nしていきましょうね♪", 0, 2, 3407, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 110701, "ぼっちに学院生活は\n厳しいですね…でも、\nあなたと一緒なら\n乗りこえられそうです", 0, 2, 3408, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110701, "もうこんな時間…\nあの…一緒に学院に\n行ってくれませんか？", 1, 2, 3409, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110701, "お昼ですね！\n言ってくれれば、\nパンでもお握りでも\n買ってきますよ！", 2, 2, 3410, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110701, "今日もぼっち…\nいつか友達と一緒に、\nパジャマパーティー\nしたいなぁ…", 3, 2, 3411, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110701, "森とは違う\n心地良さが…このまま\n眠っちゃいそうです…", 0, 3, 3413, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110701, "かわいい…この子を、\n話す練習の相手役に\nしてみようかな？", 0, 3, 3414, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110701, "いい香り…\nいつ友達ができても\nいいように、私の\n部屋にも置こうかな", 0, 3, 3415, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110701, "こ、これは…\nぼっちで\n日陰者の私には\n暖かすぎます！", 0, 3, 3416, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110701, "おいしい…！\nお弁当にして、学院に\n持っていけないかな？", 0, 3, 3418, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110701, "学院の授業に\nついていくためにも、\n勉強しておこうかな…", 0, 3, 3419, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110801, "あんた、何してんの。\nいいけど、なんでも", 0, 1, 3421, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110801, "邪魔。どいて。\nいや怒ってないし。\nコレがフツーだから", 0, 1, 3422, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110801, "いいけど、\n散らかしめの部屋だと\n片付けたくなるよね…", 0, 1, 3423, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110801, "暇なときは明日の\nコーデ考えてるよ。\n考えるのはタダだし", 0, 1, 3424, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110801, "昔よくシオリン\n構いすぎてハツネに\n文句言われたなァ…", 0, 1, 3425, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 110801, "なんかさ。あんたって\n謎に女子トモ多いよね\n…いいけど、べつに", 0, 1, 3426, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 110801, "ねえ。あのさあ。\nあんた弁当で好きな\n具とかってあんの", 0, 1, 3427, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110801, "あ？　何。なんか用。\nや、ないならないで\nいいけど、べつに", 0, 2, 3428, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110801, "ごめ、ちょっと\n黙っててくんない。\n明日の試験ヤバだから", 0, 2, 3429, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110801, "歩き方が静か？\n癖になってるんだよね\n足音を殺すの", 0, 2, 3430, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110801, "うちの血液型？\n大雑把のОだけど。\nなんか文句あんの", 0, 2, 3431, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 110801, "ひゃんっ！\nや、だからさ…急に\n話しかけんなっての", 0, 2, 3432, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 110801, "な、何…あんまジッと\n見ないでくんない…\nなんか、その、ハズい", 0, 2, 3433, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110801, "早起きは得意な方だと\n思うけど。昔から\nそれがデフォだったし", 1, 2, 3434, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110801, "こんな時間か…\n昼ごはん\nサクッと作っちゃお", 2, 2, 3435, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110801, "あ？　夜遊び？\nはッ…そんなん\n５歳で卒業したっての", 3, 2, 3436, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110801, "あーヤバじゃんコレ…\nふかついてる場合\nじゃないっつーのに…", 0, 3, 3438, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110801, "なんだこれ\nかわいいかよ", 0, 3, 3439, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110801, "ふーん。\nこの匂い…\nなんかいいじゃん", 0, 3, 3440, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110801, "暖炉はアドじゃん。\n煮物、焼き物、蒸し物\nいろいろ作れそう", 0, 3, 3441, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110801, "何コレ\n食べ物困んないじゃん\nうちにも一台くんない", 0, 3, 3443, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110801, "本かァー…んー\n学校の勉強だけで\nいいかなァ…", 0, 3, 3444, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111101, "イタズラってなにを\nしたらいいのかな？", 0, 1, 3446, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111101, "カップケーキ\n上手に作れました\n食べてくれるかな…", 0, 1, 3447, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111101, "あの人、\nお菓子くれるかな？\nくれなかったら…ふふ", 0, 1, 3448, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111101, "オバケ…いないよね？\n怖くなんてないけど！", 0, 1, 3449, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111101, "ハロウィンは\n楽しいけどオバケの\nお祭りなんだよね…", 0, 1, 3450, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 111101, "にゃ～、にゃお～！\nちゃんと怖いかな？\n早く帰ってこないかな", 0, 1, 3451, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 111101, "あの人のことだから\nお菓子欲しがるよね…\n準備はバッチリです♪", 0, 1, 3452, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111101, "えへへ。猫さんのお耳\nよく出来てるでしょ？", 0, 2, 3453, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111101, "触りましたね？\nイタズラしましたね？\nへんた～い！", 0, 2, 3454, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111101, "フー！　頭を撫でたら\n怒りますよ！　あれ？\n猫みたいでした？", 0, 2, 3455, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111101, "きゃっ！　も、もう、\n脅かさないでください\n怖くありませんけど！", 0, 2, 3456, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 111101, "お菓子ですか？\nありがとうございます\nハッピーハロウィン♪", 0, 2, 3457, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 111101, "ゴロゴロ…\nね、猫さんの真似です\n…似てましたか？", 0, 2, 3458, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111101, "さぁ、お菓子作りを\n始めますよ。下準備は\n大事ですからね！", 1, 2, 3459, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111101, "今、つまみ食い\nしましたね！？　あっ\nおやつの時間でしたか", 2, 2, 3460, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111101, "さぁ、ハロウィンの\n始まりです…ふぁ…\nね、眠くありません！", 3, 2, 3461, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111101, "ちょっと疲れたかも…\nパーティの時間まで\nひと休みしなきゃ", 0, 3, 3463, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111101, "私も今、こんな感じ\nなのかな？　こんなに\nかわいいといいなぁ", 0, 3, 3464, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111101, "甘い匂いがいっぱい…\nお菓子の香りかな？", 0, 3, 3465, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111101, "温かくなると、\n丸まりたくなって…\n…猫になっちゃう！？", 0, 3, 3466, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111101, "ニンジンもこれくらい\nおいしければ\nいいのに…", 0, 3, 3468, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111101, "ハロウィンの本、\nオバケがたくさん\n載ってて怖いかも…", 0, 3, 3469, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111101, "美味しそうなお菓子が\nいっぱい…これなら\nイタズラされませんね", 0, 3, 3471, 12, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111201, "たくさんいたずら\nできるから\nハロウィン大好き！", 0, 1, 3473, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111201, "ハロウィンだから\nいつもよりすごい\nいたずらしたいな～！", 0, 1, 3474, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111201, "カボチャを綺麗に\nくり抜いてすごく怖い\nランタン作っちゃお！", 0, 1, 3475, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111201, "お菓子がたくさんで\nどれから食べるか\n迷っちゃうな～！", 0, 1, 3476, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111201, "どうすればみんなを\nびっくりさせられる\nかな～？", 0, 1, 3477, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 111201, "カボチャ見てたら\nお腹空いて来たな～\nお菓子食べちゃお♪", 0, 1, 3478, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 111201, "にいちゃんには\n特別ないたずらを\n用意してあげるね！", 0, 1, 3479, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111201, "カボチャにクラッカー\nを仕掛けて…わっ！？\nみ、見ちゃダメだよ！", 0, 2, 3480, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111201, "ん？　なぁに？\nお菓子ほしいの？\nもう、しかたないな～", 0, 2, 3481, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111201, "にいちゃんの\n面白い仮装が\n見てみたいな～！", 0, 2, 3482, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111201, "お菓子くれたお礼に\nとびっきりの\nいたずらしちゃうね！", 0, 2, 3483, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 111201, "にいちゃんは\nハロウィンでも最高の\nいたずら相手だよ！", 0, 2, 3484, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 111201, "にいちゃんと一緒に\nハロウィンパレード\n歩きたいな～！", 0, 2, 3485, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111201, "おはようにいちゃん！\n仮装して出かける\n準備しなきゃ！", 1, 2, 3486, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111201, "お腹すいたな～\nもらったお菓子\n食べちゃだめかな？", 2, 2, 3487, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111201, "たくさんいたずらして\nもうクタクタ～\nミソギもう寝るね…", 3, 2, 3488, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111201, "やわらか～い…\nここでひと休みして\nまたいたずらしよう！", 0, 3, 3490, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111201, "かわいいもので\n油断させてわっ！\nってするのどうかな？", 0, 3, 3491, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111201, "いいにお～い！\nいたずらのアイデアが\nわいてくるよ！", 0, 3, 3492, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111201, "もらったお菓子\nあったかくしたら\nおいしいかな～？", 0, 3, 3493, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111201, "お菓子ばっかりじゃ\n飽きちゃうもんね！\nご飯も食べよう！", 0, 3, 3495, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111201, "本のなかに\nいたずらのヒント\nないかな～？", 0, 3, 3496, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111201, "お菓子がたくさん！\n一人で食べていいの？\nやっぱりだめか～！", 0, 3, 3498, 12, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 101101, "お勉強は順調ですよ\nやっぱりお兄ちゃんの\nおかげなんでしょうか", 0, 1, 3500, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 101101, "お兄ちゃんとの\n思い出の場所\nまた行きましょうね", 0, 2, 3501, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111301, "えへへ～♪\nハロウィンの\nミイラさんうさぎさん\nぴょんぴょ～ん！", 0, 1, 3502, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111301, "るんたった～\nハロウィンハロウィン\n楽しいな～♪", 0, 1, 3503, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111301, "ハロウィンって\nどんなお菓子を\nもらえるのかな～？", 0, 1, 3504, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111301, "お菓子をもらう\n練習をしなくちゃ！\nトリックオア\nトリート～♪", 0, 1, 3505, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111301, "おにいちゃんと\nハロウィンするの\nミミ楽しみだな～♪", 0, 1, 3506, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 111301, "おにいちゃんと\nいっしょにお菓子を\n食べたいな～", 0, 1, 3507, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 111301, "おにいちゃんと\n毎日ハロウィン\nできたらいいな～♪", 0, 1, 3508, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111301, "えへへ～\nおにいちゃん！\nハッピーハロウィン♪", 0, 2, 3509, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111301, "おにいちゃんも\nいっしょに\nハロウィンパレード\nしようね～！", 0, 2, 3510, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111301, "ミイラさんうさぎさん\nだぞ～…えへへ\nミミこわかったかな？", 0, 2, 3511, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111301, "おにいちゃん！\nハロウィンのお菓子\nおいしいね～♪", 0, 2, 3512, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 111301, "おにいちゃんと\nいっしょなら\nおばけさんも\nこわくないよ！", 0, 2, 3513, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 111301, "ミミね\nハロウィンも\nおにいちゃんも\nだ～いすき♪", 0, 2, 3514, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111301, "おひさまさんも\nハロウィンの\nおようふく\n着たいかな～？", 1, 2, 3515, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111301, "ねえねえおにいちゃん\nお昼ごはんはもらった\nお菓子でもい～い？", 2, 2, 3516, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111301, "まだハロウィン…？\nうぅん…眠くないよぅ\nでもちょっと…ふぁ～", 3, 2, 3517, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111301, "ふかふか～♪\nおばけさんが\n中にいるのかな～？", 0, 3, 3519, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111301, "わぁ、かわいい～♪\nミミといっしょに\nあそぼ～！", 0, 3, 3520, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111301, "ふわぁ～\nミミ、このにおい\nだ～いすき♪", 0, 3, 3521, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111301, "あったかいね～♪\nおばけさんも\nポカポカしてるかな？", 0, 3, 3522, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111301, "おいしい～♪\nおにいちゃんも\n食べようよー！", 0, 3, 3524, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111301, "この本\nおにいちゃんと\nいっしょに\n読みたいな～", 0, 3, 3525, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111301, "ハッピーハロウィン♪\nえへへ、お菓子が\nいっぱいだね～！", 0, 3, 3527, 12, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111401, "ここ、好きだよ。\nみんな優しいから…", 0, 1, 3529, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111401, "なんかさみしいな", 0, 1, 3530, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111401, "知らない人怖いな", 0, 1, 3531, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111401, "お部屋おっきいねー", 0, 1, 3532, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111401, "ルナね…\nお昼寝したいかも", 0, 1, 3533, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 111401, "おにいさん！\n今日は何して遊ぶの？", 0, 1, 3534, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 111401, "あのね、\nお料理教えてほしい！", 0, 1, 3535, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111401, "あっ、おにいさんだ！", 0, 2, 3536, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111401, "ルナは元気だよ！\nいいこだもん！", 0, 2, 3537, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111401, "今日は何しようかな", 0, 2, 3538, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111401, "ルナと遊んでよ！", 0, 2, 3539, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 111401, "ねーねー、\nルナとお出かけして！", 0, 2, 3540, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 111401, "おにいさんと、\n遊びたいなー！", 0, 2, 3541, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111401, "ふふっ！\nなんかまぶしいね！", 1, 2, 3542, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111401, "ルナ、お腹空いたな", 2, 2, 3543, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111401, "どこに行こうかな？", 3, 2, 3544, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111401, "ルナ、これ好きだな", 0, 3, 3546, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111401, "かわいい！　好き！", 0, 3, 3547, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111401, "なんかいい匂い！", 0, 3, 3548, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111401, "あったかいねー", 0, 3, 3549, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111401, "ふふっ\nおいしいね", 0, 3, 3551, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111401, "ルナ、\n眠くなっちゃう…", 0, 3, 3552, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 105201, "えへへ。あの人と\nランドソルでデート\nできたらうれしいな♪", 0, 1, 3554, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 105201, "あなたとなら\nどんなことだって\nできる気がするわ♪", 0, 2, 3555, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106501, "あーあ、退屈だな\nカレでも探しに\nいこうかな", 0, 1, 3556, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106501, "この前のケンカは\n最高に燃えたなぁ\n…拳が疼いてきたぜ", 0, 1, 3557, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106501, "一体、この世界は\nどうなっちまったって\nいうんだ…？", 0, 1, 3558, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106501, "もう仕事の時間か\nボスも人使いが荒いぜ\n…ま、いいけどさ！", 0, 1, 3559, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106501, "オレたち以外に\nドラゴン族はあと何人\nいるんだろうな…", 0, 1, 3560, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 106501, "カレの真剣な顔\n面白かったな\nまた遊びたいぜ", 0, 1, 3561, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 106501, "どうしてカレがそばに\nいると、腹の底から\n力が湧いてくるんだ？", 0, 1, 3562, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106501, "よっす！\nなんかオイシイ話でも\nあったりしない？", 0, 2, 3563, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106501, "なあ、オレと遊ぼうぜ\n心配すんなよ。危ない\nことはよしとくからさ", 0, 2, 3564, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106501, "どうした。オレに\nケンカ売ろうってか？\n…ははは、冗談だよ！", 0, 2, 3565, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106501, "ちょうど退屈してた\nところだ、特訓に\n付き合ってくれよ", 0, 2, 3566, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 106501, "ったくよぉ…\nオレがかわいいなんて\n冗談はもうよせよ？", 0, 2, 3567, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 106501, "次の仕事も一緒に\n来るだろ？　断っても\n連れて行くけどな！", 0, 2, 3568, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106501, "おはよう！　今日の\n予定はまずケンカして\nで、その後もケンカだ", 1, 2, 3569, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106501, "よっす！\n腹ごしらえが済んだら\nオレと遊ぼうぜ！", 2, 2, 3570, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106501, "夜は危ないから\nあんま出歩くな？\n…だから面白えんだろ", 3, 2, 3571, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106501, "次のケンカに備えて\n今は休んでおくか…\n全力で暴れたいからな", 0, 3, 3573, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106501, "ボスの飼ってる\nラーゴが、中に\n入ってるんじゃ？", 0, 3, 3574, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106501, "ああ、たぎる匂いが\nする…ひと暴れ\nしたくなってきた！", 0, 3, 3575, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106501, "おおぉ…\nオレの拳もなんだか\n熱くなってきたぜ…！", 0, 3, 3576, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106501, "いつでも全力で\nケンカするために\n力を蓄えないとな", 0, 3, 3578, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106501, "やっぱりな…オレが\n知ってる地図とは\nまるで違ってやがる", 0, 3, 3579, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111501, "さぁプレゼントで\n埋め尽くしてやろう！\n大いに喜ぶがいいよ！", 0, 1, 3581, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111501, "メリークリスマス☆\n聖夜の宴だ\n派手に楽しむとしよう", 0, 1, 3582, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111501, "煙突から入る？\nいいや、ワタシは\nいつでも正面からさ♪", 0, 1, 3583, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111501, "サンタクロースの\nお出ましだ！\n歓迎するがいいよ♪", 0, 1, 3584, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111501, "飾り付けが足りん！\nワタシが演出して\nやろうじゃないか！", 0, 1, 3585, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 111501, "人の喜ぶ顔を見るのも\n悪くはないな…おっと\nワタシらしくないな☆", 0, 1, 3586, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 111501, "プレゼントはまだか？\nワタシを喜ばせたら\nお返しは弾むぞ♪", 0, 1, 3587, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111501, "ワタシを呼んだか？\n退屈な要件だったら\n覚悟しておくことだ", 0, 2, 3588, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111501, "なんだ？\nワタシへの挑戦か？\nよし、受けた♪", 0, 2, 3589, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111501, "おっと、当たらんよ\nワタシの乱数聖域は\n絶対だからな…♪", 0, 2, 3590, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111501, "なんだ、オマエも\n暇なのか？\n奇遇だな、ワタシもさ", 0, 2, 3591, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 111501, "なにか愉しいことでも\nしようじゃないか\nさぁ、何をする？", 0, 2, 3592, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 111501, "こうもワタシに\n気安く触れるのは\nオマエくらいだよ…♪", 0, 2, 3593, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111501, "朝だろうと関係ない！\nさぁ愉しく苛烈に\n騒ごうじゃないか♪", 1, 2, 3594, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111501, "調子の出てくる時間だ\nしっかりワタシを\n愉しませてくれよ♪", 2, 2, 3595, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111501, "眠いなどと言わせんぞ\nまだまだ一日は\n終わらんからな！", 3, 2, 3596, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111501, "いい具合じゃないか\nこのまま持って帰って\nしまおうか…♪", 0, 3, 3598, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111501, "なかなか愛らしい\n造形をしているな？\nワタシには負けるがな", 0, 3, 3599, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111501, "ほう、悪くない香りだ\n戦いの匂いをうまく\n隠してくれそうだ…♪", 0, 3, 3600, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111501, "体が温まってくると\nひと暴れしたくなるな", 0, 3, 3601, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111501, "ワタシの舌は厳しいぞ\n…ふーむ、なるほど\n認めざるをえんな", 0, 3, 3603, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111501, "クリスティーナさんは\n知識の収集も\n欠かさないんだよ", 0, 3, 3604, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111501, "これは驚いたな\nだがもっと、もっとだ\nワタシを退屈させるな", 0, 3, 3606, 14, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111501, "こんな戯れだって\n軽くこなしてしまう\nクリスティーナさんさ", 0, 3, 3607, 15, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111601, "メリークリスマス！\nさあ、パーティーを\n盛り上げるよ！", 0, 1, 3608, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111601, "衣装一つで全然\n気分が違うね！\n最高のクリスマスに\nなりそうだよ！", 0, 1, 3609, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111601, "ツリーのてっぺんの\n星って、なんだか\n欲しくなるんだよね", 0, 1, 3610, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111601, "めいっぱい\nクリスマスを楽しむよ\n遊びだって本気\nなんだからね！", 0, 1, 3611, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111601, "赤、白、緑、金\nクリスマスカラー\nってお洒落だよね", 0, 1, 3612, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 111601, "雪、降らないかな？\nホワイトクリスマス\nってちょっと\n憧れるなぁ", 0, 1, 3613, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 111601, "キミと過ごすこの\n楽しい時間がずーっと\n続けばいいのにな", 0, 1, 3614, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111601, "クリスマスライブ\nチケット用意するから\n絶対来てよね！", 0, 2, 3615, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111601, "カンパーイ！\nジュースだけど\nちょっと大人の\n気分だね", 0, 2, 3616, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111601, "クリスマス衣装どう？\nかわいいでしょ\nあっ、どこ見てるの！", 0, 2, 3617, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111601, "ねえ、後で街に\n行こうよ\n大きなツリーを\n一緒に見たいんだ♪", 0, 2, 3618, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 111601, "ねえ、ダンスの練習に\n付き合ってくれない？\n大丈夫、手取り足取り\n教えてあげるから♪", 0, 2, 3619, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 111601, "来年のクリスマスも\n隣にキミが\nいてくれたら\nうれしいのにな", 0, 2, 3620, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111601, "クリスマスって朝から\nドキドキしちゃうね\nなんだか子どもみたい", 1, 2, 3621, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111601, "パーティーの準備も\n大分進んだね\n一息ついて\nランチにしよっか", 2, 2, 3622, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111601, "静かな夜だね\n心臓の音まで\n聞こえちゃいそう…", 3, 2, 3623, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111601, "あ～、この絶妙な\n柔らかさ…\nいい仕事してるなぁ…", 0, 3, 3625, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111601, "アイドル顔負けの\nかわいさ、ズルい！\nでもかわいい！", 0, 3, 3626, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111601, "ん～、落ち着くなぁ\n今日はゆっくり\n眠れそうだよ", 0, 3, 3627, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111601, "ぬくぬく～\nどうしよう、ここから\n動きたくないなぁ", 0, 3, 3628, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111601, "ダンスの練習もしたし\nちょっとくらいなら\nいいよね！", 0, 3, 3630, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111601, "アイドルといえど\nかわいいだけじゃダメ\n知識も必要だよね", 0, 3, 3631, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111601, "面白ーい！\nこの演出、ライブでも\nできないかな？", 0, 3, 3633, 14, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111601, "もふもふ～♪\n寒い日には最高の\n抱き心地だね！", 0, 3, 3634, 15, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 101801, "彼ともっとお話し\nしたいな…もう少し\n補習を増やそうかしら", 0, 1, 3635, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 101801, "君のこともっともっと\n教えて欲しいな。私に\n補習授業してくれる？", 0, 2, 3636, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111701, "この姿ならあやつも\n子ども扱いできまい！\n…じゃがなぜか、少し\n物足りないような…", 0, 1, 3637, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111701, "時間もあるし、\n聖夜の宴の\n準備をするかのう♪", 0, 1, 3638, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111701, "…わらわの知る聖夜と\n随分違うが、宴は宴\n楽しませて\nもらおうかのう♪", 0, 1, 3639, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111701, "大人の姿で\nできること…ふむ\n少しあやつを\nからかってやるか♪", 0, 1, 3640, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111701, "…クリスマスは\n恋人たちの夜！？\nち、近頃の若いもんは\n破廉恥なのじゃ！", 0, 1, 3641, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 111701, "…あやつがおらんと、\n宴も物足りぬな\n…馬鹿者が\n早く帰ってこい…", 0, 1, 3642, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 111701, "誰も見ておらぬな…？\n…よし、あやつへの\nプレゼントを\n準備するとしようか！", 0, 1, 3643, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111701, "クリスマスの\nチキンにケーキ…\nべ、別に楽しみになど\nしておらぬぞ！", 0, 2, 3644, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111701, "この姿では、わらわの\n頭をなでるのは\n難しかろう…べ、\n別に残念ではないわ！", 0, 2, 3645, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111701, "メリー\nクリスマスじゃ！\nさあ、わらわのために\n宴の準備をするのじゃ", 0, 2, 3646, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111701, "そんなにこの衣装が\n気になるのか？\n…だったら、直に\n触ってみるがよい♪", 0, 2, 3647, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 111701, "いつもと逆に、お主を\n子ども扱いしてやるか\nほれ、膝枕をしてやる\nからそばに来い♪", 0, 2, 3648, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 111701, "…せ、聖夜の予定は\n空けておくのじゃぞ？\nなんでって…\nな、なんでもじゃ！", 0, 2, 3649, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111701, "もう朝か…聖夜の宴が\n楽しみで…ではなく！\n準備のせいで、\nあまり眠れんかったぞ", 1, 2, 3650, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111701, "…ずっと雪が\n降っておれば、\n陽光が強いこの時間も\n怯えずにすむのにのう", 2, 2, 3651, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111701, "時は来た…\nさあ眷属よ！\nわらわと共に、\n聖夜の宴を楽しもうぞ", 3, 2, 3652, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111701, "良い椅子ではないか\nここから聖夜の宴を\n眺めるのもよいかもな", 0, 3, 3654, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111701, "か、かわい…こほん\nなかなか良い\nデザインをしておるな", 0, 3, 3655, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111701, "ふむ、良い香りじゃ\n聖夜の宴でも\nこれを使うとしよう", 0, 3, 3656, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111701, "これが癒し…はっ！\nあやうく浄化される\nところであった…", 0, 3, 3657, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111701, "ニ、ニンニクは\n入っておらぬよな？\n…うむ、美味じゃ♪", 0, 3, 3659, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111701, "聖夜は恋人たちの夜…\n…き、気になるわけ\nではないが、\n少し調べてみるか", 0, 3, 3660, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111701, "ほう…これは面白い\n魔法の使い方じゃな\nまるで本物の\n演劇のようじゃ", 0, 3, 3662, 14, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111701, "こ、この程度の\nかわいさに誘惑される\nわらわでは…\nふわもこなのじゃ～♪", 0, 3, 3663, 15, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112001, "おせち料理も\n魔物尽くしだったり\nしないわよね！", 0, 1, 3664, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112001, "初詣で何をお祈り\nしようかしら…\n定番でいいわよね？", 0, 1, 3665, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112001, "初日の出って\n特別な感じがして\nいいわよね…", 0, 1, 3666, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112001, "寒い中並んで飲む\n甘酒って不思議なほど\nおいしいわよね", 0, 1, 3667, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112001, "羽根つきで勝負…\nあいつにだったら\n勝てるかしら？", 0, 1, 3668, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 112001, "年賀状になんて書こう\n…いろいろと\n恥ずかしくなるのよね", 0, 1, 3669, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 112001, "今年もあいつと\n居られること…\n結構うれしいわ", 0, 1, 3670, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112001, "何じっと\n見てんのよ？\nお年玉はあげないわよ", 0, 2, 3671, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112001, "おせち料理一緒に\n食べる？\n結構おいしいわよ", 0, 2, 3672, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112001, "羽根つきで負けたら\n顔に墨塗られるのよ\n覚悟しなさい！", 0, 2, 3673, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112001, "門松飾るから\nちょっと手伝って\nくれる？", 0, 2, 3674, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 112001, "あんたと一緒に\n初詣行くのも悪くない\n…かも", 0, 2, 3675, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 112001, "初詣で何をお祈り\nしたか？　うっさい！\n内緒よ！内緒！", 0, 2, 3676, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112001, "もう朝なの…\n正月くらいゆっくり\n寝ていたいわよね…", 1, 2, 3677, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112001, "おせちに飽きたら\n次は何を食べよう\nかしら…", 2, 2, 3678, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112001, "そろそろ寝ましょ\nいい初夢が見られると\nいいわね", 3, 2, 3679, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112001, "この格好でいるのも\nなかなか疲れるわね\n休憩～", 0, 3, 3681, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112001, "か、かわいい…う～ん\n鏡餅も顔を描けば\nかわいくなるかしら？", 0, 3, 3682, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112001, "いい匂いね\n落ち着いて\n眠れそう…", 0, 3, 3683, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112001, "ここでお餅焼いたら\n一段とおいしく\nなりそうね", 0, 3, 3684, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112001, "正月に食べるものは\nなんでもおいしく\n感じちゃうわね！", 0, 3, 3686, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112001, "魔物を使わない\nおせちを\n調べておかなきゃ…", 0, 3, 3687, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112101, "新年を気持ちよく\n過ごせるように\nお掃除がんばります！", 0, 1, 3689, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112101, "神殿に通っていたら\nいつの間にか足腰が\n鍛えられたような…", 0, 1, 3690, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112101, "神殿付近は屋台も\nあるので、良い香りが\n漂ってくるんですよ～", 0, 1, 3691, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112101, "おせちを作れるように\nなれば、子どもたちも\n喜んでくれるかな…？", 0, 1, 3692, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112101, "コホォォォォォ…！\nふぅ、深呼吸すると\n落ち着きますね♪", 0, 1, 3693, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 112101, "お仕事以外の時も\nあなたと…い、いえ！\nなんでもないです！", 0, 1, 3694, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 112101, "あなたと一緒だと\nなんだかポカポカ\nしてくるんです…", 0, 1, 3695, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112101, "この格好をしていると\nなんだか神殿にいる\n気分になりますね", 0, 2, 3696, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112101, "そういえば神殿で\n甘酒をもらったんです\nあなたも飲みますか？", 0, 2, 3697, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112101, "あっ、髪飾りが\nズレちゃってました！\nいけないいけない…", 0, 2, 3698, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112101, "ひゃわっ！？\nく、くすぐったり\nしないでください～", 0, 2, 3699, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 112101, "そ、そんなに\n見つめられると\nて、照れちゃいます…", 0, 2, 3700, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 112101, "今度、私が考えた\n運気上昇のおまじない\nかけてあげますね♪", 0, 2, 3701, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112101, "おはようございます\n神殿のお手伝いに行く\n準備もしなきゃですね", 1, 2, 3702, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112101, "お昼になりましたし\nご飯は私が作…\nえっ、ダメですか！？", 2, 2, 3703, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112101, "明日も早いですし\nそろそろ寝る支度を…\nふわぁ…", 3, 2, 3704, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112101, "お掃除も終わったので\n少し休憩を…ふわぁ…\n急に眠気が…", 0, 3, 3706, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112101, "新年を気持ちよく\n過ごせるようこの子も\nきれいにしましょう♪", 0, 3, 3707, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112101, "神殿のお香のような\n香りだと落ち着いて\n過ごせそうですよね", 0, 3, 3708, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112101, "ぽかぽかですね～…\n甘酒も飲んで体の\n芯から温めましょう", 0, 3, 3709, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112101, "今年こそ\n料理も上手に…\nもぐもぐもぐもぐ…", 0, 3, 3711, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112101, "失敗続きだと\n逆に運が良くなる…\nほ、本当ですかぁ…？", 0, 3, 3712, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 103401, "キミにならもう\nどんな私を見られても\n大丈夫かな…♪", 0, 1, 3714, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 103401, "なになにどしたのぉ？\nお姉さんが撫でたげる\nこっちにきなさぁい♪", 0, 2, 3715, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111901, "わたくしの舞を\n主さまは喜んで\nくださるでしょうか…", 0, 1, 3716, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111901, "年を越したあとに\n食べるおそばも\nおいしいです", 0, 1, 3717, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111901, "甘酒を飲むと\n心まで温まる\n気がいたします", 0, 1, 3718, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111901, "主さまのために\n真心を込めたおせちを\nご用意しなくては", 0, 1, 3719, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111901, "おみくじを引くのは\nドキドキしつつも\n楽しいものです", 0, 1, 3720, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 111901, "主さまの温もりは\n心まで温めてくれる\nようです", 0, 1, 3721, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 111901, "うまくできなくとも\n心を込めて精いっぱい\n舞いましょう", 0, 1, 3722, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111901, "主さま、この晴れ着は\n似合っている\nでしょうか？", 0, 2, 3723, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111901, "お餅はゆっくりと\n召し上がって\nくださいまし", 0, 2, 3724, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111901, "主さまのお顔の\n福笑いなら、完璧に\nできる気がいたします", 0, 2, 3725, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111901, "主さまはどのような\n初夢をご覧になった\nのでしょう？", 0, 2, 3726, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 111901, "甘酒の飲み過ぎは\nいけませんよ？\nめっ、でございます", 0, 2, 3727, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 111901, "二人羽織とは\nあんなにも暖かい\nものなのですね…", 0, 2, 3728, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111901, "おはようございます\n寒い季節の朝日は\nとても美しいですね", 1, 2, 3729, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111901, "おいしくとも\n食べ過ぎは正月太りの\nもとでございます", 2, 2, 3730, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111901, "いい夢が見られるよう\n暖かくして\n眠るといたしましょう", 3, 2, 3731, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111901, "ふかふか…\nこのまま\n寝てしまいそうです", 0, 3, 3733, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111901, "あなたと一緒に\nお正月遊びを\n楽しんでみたいです", 0, 3, 3734, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111901, "神殿の独特な香りを\nおうちに持ち込むのは\nどうでしょうか", 0, 3, 3735, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111901, "風邪をひきませんよう\n暖かくいたしましょう", 0, 3, 3736, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111901, "食べ過ぎは\n正月太りの\nもとだというのに…", 0, 3, 3738, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111901, "主さまのために\n参拝の作法を\n予習しておきましょう", 0, 3, 3739, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112201, "…この杖、変身前には\n無いパーツがあるけど\nこれはどこから…うん\n考えないようにしよう", 0, 1, 3741, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112201, "フ、フリフリの\nかわいい衣装…はっ！\n素に戻ってはダメだ！", 0, 1, 3742, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112201, "魔法探偵\nミスティ★カスミ！\n魔法の力で捜査開始！\n…練習終わりっと", 0, 1, 3743, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112201, "怪しい視線を感じる…\nまさか、近くに\n闇の力を持つ人が！？", 0, 1, 3744, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112201, "時間もあるし、\n魔法少女の修行を…\nじゃ、じゃなくて！\n探偵の仕事をしよう！", 0, 1, 3745, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 112201, "助手くんとずっと\n一緒に居られるし、\n魔法少女も\n案外悪くないかも♪", 0, 1, 3746, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 112201, "魔法少女の訓練のため\n助手くんの所に行こう\nかな…あ、あくまで\n訓練のためにね！", 0, 1, 3747, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112201, "…髪型が急に\n変わって驚いた？\nき、気になるなら…\n少し触ってみるかい？", 0, 2, 3748, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112201, "助手くんは魔法少女に\nなりたいのかい？\nその場合は魔法騎士…\nいや、魔法助手とか？", 0, 2, 3749, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112201, "頭に浮かぶ呪文…\n強制変身…助手くん\n魔法少女とは\n呪いか何かなのかな？", 0, 2, 3750, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112201, "あ、あまり触らないで\nくれ…魔法少女になる\nと感覚が鋭くなって、\n耳も敏感に…ひゃう！", 0, 2, 3751, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 112201, "や、やっぱりこの\n格好は変かな…え、\nかわいい？　じゃあ…\nいや、でも、う～ん", 0, 2, 3752, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 112201, "…この変身は助手くん\nのせいでもあるんだよ\nだから…せ、責任を\n取ってもらうからね！", 0, 2, 3753, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112201, "おはよう助手くん\nさあ、そろそろ\n街のパトロールに\n行こうじゃないか！", 1, 2, 3754, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112201, "もうお昼…助手くん、\nそろそろ本業に戻ろう\n…その顔は何かな？\n私の本業は探偵だぞ！", 2, 2, 3755, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112201, "夜だけど、私は\nもう少し仕事をするよ\n魔法少女の強い体は\nこんなときに便利だね", 3, 2, 3756, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112201, "気持ちいい…\nこの癒しを、魔法と\n論理の二つの観点から\n解明したいね…", 0, 3, 3758, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112201, "ダ、ダメ！！\nここで感情のままに\n飛びついたら、私の\nイメージがぁ～！", 0, 3, 3759, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112201, "心が安らぐ香りだね…\n…今なら、魔法少女も\nすんなりと受け入れ…\nてはダメだっ！", 0, 3, 3760, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112201, "あったかい…\n魔法少女だろうと\n休息は必要だし、少し\n休んでいこうかな", 0, 3, 3761, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112201, "魔法少女も探偵も、\nやっぱり体が資本！\n食べられるときに\n食べておかないとね♪", 0, 3, 3763, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112201, "…魔法少女\nセリイちゃんの本は\nどこにあるのかな？", 0, 3, 3764, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112301, "魔法少女かぁ…\nせっかくだし思う存分\n体を動かしてみたいな", 0, 1, 3766, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112301, "ニャットちゃん…\nこの格好を見て\n私って気づくかな？", 0, 1, 3767, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112301, "ふふ…\n今日はなんだか\n調子がいいかも", 0, 1, 3768, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112301, "魔法は便利だけど\nあまり頼りすぎちゃ\nダメだよね", 0, 1, 3769, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112301, "さっき街で小さな子に\n声かけられちゃった\n照れるけどうれしいな", 0, 1, 3770, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 112301, "あの人がいると\nなんだかホッとする\n不思議…", 0, 1, 3771, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 112301, "魔法少女になっても\nあの人への気持ちは\nずっと同じ…", 0, 1, 3772, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112301, "今日の私…ですか？\nはいとっても元気です\n…あなたのおかげかも", 0, 2, 3773, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112301, "よかったら魔法の\n練習に付き合って\nくれませんか？", 0, 2, 3774, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112301, "あの…\nこの格好どうですか？\n似合ってると…いいな", 0, 2, 3775, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112301, "…な、なんですか？\nあまり見られると\n恥ずかしいです…", 0, 2, 3776, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 112301, "私、あなたのそばに\nいられたら、それで…", 0, 2, 3777, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 112301, "想い人と結ばれる\n魔法は…ひゃっ！？\n今の聞いてましたか？", 0, 2, 3778, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112301, "おはようございます\n今日も街の平和の\nために頑張らなきゃ", 1, 2, 3779, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112301, "お腹すきましたね。\nあの…一緒にランチは\nいかがですか？", 2, 2, 3780, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112301, "あ、もうこんな時間…\nふふ、まだこんなに\n体力が余ってるのに", 3, 2, 3781, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112301, "気持ちいい…\nこの気持ち良さは\nどんな魔法も勝てない", 0, 3, 3783, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112301, "何これ、かわいい…\nお姉ちゃんにも\n見せてあげたいな", 0, 3, 3784, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112301, "ん～、いい香り\n…これ、魔法で\n出せたりしないかな？", 0, 3, 3785, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112301, "あったか～い\nえへへ…なんだか\nお姉ちゃんみたい", 0, 3, 3786, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112301, "おいしいなぁ、これ\n魔法少女になってから\n食欲旺盛かも…", 0, 3, 3788, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112301, "ふふ…\n魔法少女になっても\n読書はやめられないな", 0, 3, 3789, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 101001, "みらくるまほりん\nくるりんぱ～♪\n王子はんのために\n花嫁修業や～♪", 0, 1, 3791, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 101001, "あ、あのな王子はん\nできたらその…\nお姫さまだっこを\nお願いしてもええ…？", 0, 2, 3792, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180401, "やっぱりわたしたちの\nギルドハウスの\n居心地は最高ですね☆", 0, 1, 3793, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180401, "王宮じゃできないこと\nやっちゃいましょう♪\n例えば、えっと～…", 0, 1, 3794, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180401, "そうだ、隠しておいた\nおやつがありました！\nまだ食べられますね☆", 0, 1, 3795, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180401, "ごはんはみんなで\n楽しく食べましょう！\nそれが一番の\n調味料です♪", 0, 1, 3796, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180401, "やっぱりここが、\nわたしの帰る場所です\n…ただいま☆", 0, 1, 3797, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 180401, "なんだか物足りません\nきっと、あの人が隣に\nいないからですね…", 0, 1, 3798, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 180401, "どこを見ても、大切な\n思い出がたくさん\n詰まっていますね♪", 0, 1, 3799, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180401, "無礼者～、なんて\n冗談です♪　どんどん\n触っちゃってください", 0, 2, 3800, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180401, "ぷにぷにでしょう～？\nい～っぱい食べるのが\nぷにぷにの秘訣です！", 0, 2, 3801, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180401, "握手したいんですか？\nはい、にぎにぎ～！\nもう離しませんよ♪", 0, 2, 3802, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180401, "どうかしましたか？\n退屈してるんでしたら\nお散歩に行きましょう", 0, 2, 3803, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 180401, "ごはん王子♪\nわたしは王女ですから\nこれでピッタリですね", 0, 2, 3804, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 180401, "大好きです～\n愛してます～\n…ほんと、ですよ♪", 0, 2, 3805, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180401, "朝ごはんは大事です！\n山盛りごはんを食べて\n元気いっぱいですよ☆", 1, 2, 3806, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180401, "お腹ぺこぺこになる\n時間ですね～！\nごはん特盛、一つ！", 2, 2, 3807, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180401, "お腹いっぱい、今日は\nよく眠れそうですね♪\n明日もおいしい一日を", 3, 2, 3808, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180401, "休憩休憩～♪\nお腹を休めて、次の\nごはんに挑みます…☆", 0, 3, 3810, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180401, "かわいらしい\nぬいぐるみですね～\nキャルちゃんみたい☆", 0, 3, 3811, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180401, "すんすん…この匂い\n食欲増進の効果が\nありそうですね…？", 0, 3, 3812, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180401, "体温が上がってくると\n眠くなっちゃいます…\nほわぁ～", 0, 3, 3813, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180401, "もぐもぐもぐもぐもぐ\nむぐむぐむぐむぐむぐ\nはぐはぐはぐはぐはぐ", 0, 3, 3815, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180401, "教養を身に着けるには\n読書が一番です！\nえっと次のレシピは～", 0, 3, 3816, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112401, "アイドルらしくなる\nコツって、何か\nあるんでしょうか？", 0, 1, 3818, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112401, "色んな人と話すのが\n好きなんです。つい\n長くなっちゃって…", 0, 1, 3819, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112401, "アイドルを目指すって\n決めてから、毎日が\nワクワクなんです♪", 0, 1, 3820, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112401, "ちゃんとストレッチ\nしておかなくっちゃ\nよいしょ、よいしょ…", 0, 1, 3821, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112401, "リンちゃんミオちゃん\n二人に負けないように\n私、頑張ります！", 0, 1, 3822, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 112401, "私の笑顔は素敵だって\n誰かに褒められた…\nそんな気がするんです", 0, 1, 3823, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 112401, "私たちを支えてくれる\n誰かが、ずっと一緒に\nいてくれたような…", 0, 1, 3824, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112401, "どうかしましたか？\n何か困ってるのなら\nお手伝いしますよ！", 0, 2, 3825, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112401, "みなさん個性的で\n普通じゃない人ばかり\nいるような気が…", 0, 2, 3826, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112401, "今日はポカポカした\nいい天気ですね～\nお散歩日和です！", 0, 2, 3827, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112401, "今、ミルクティーを\n淹れてたんです♪\nあなたも飲みますか？", 0, 2, 3828, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 112401, "私の得意なこと？\nうーんなんでしょう…\nあっ、肩たたきです！", 0, 2, 3829, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 112401, "生・ハム・メローン！\nあ、ライブの掛け声を\n練習してたんですよ♪", 0, 2, 3830, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112401, "おはようございます！\n今日も一日\n頑張りましょうね！", 1, 2, 3831, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112401, "しっかりお昼を食べて\n午後からも張り切って\nいきましょうね♪", 2, 2, 3832, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112401, "ぐっすり眠れるよう\n早めに用事を済ませて\nお休みしましょうね", 3, 2, 3833, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112401, "ちょっと休んだら\nレッスンの続きも\n頑張ります！", 0, 3, 3835, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112401, "えへへ～\nふわふわもこもこで\nかわいいです～♪", 0, 3, 3836, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112401, "リラックスできる\nいい香りですね～♪", 0, 3, 3837, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112401, "わぁ、ポカポカです♪\n温かい飲み物も\nほしくなりますね", 0, 3, 3838, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112401, "レッスンしたら\nお腹が空いちゃって…\nぱくぱくもぐもぐ…", 0, 3, 3840, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112401, "レッスンに役立つ\n運動の情報とか\n載ってるでしょうか…", 0, 3, 3841, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112601, "きらきらーん☆\nよし、次のステージは\nこれで行けるかも！", 0, 1, 3843, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112601, "私の元気でみんなも\n元気になってくれたら\nうれしいな～", 0, 1, 3844, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112601, "ちょっとレッスンで\n疲れちゃったかも…\nでも頑張るぞっ！", 0, 1, 3845, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112601, "晩ごはん何にしよう？\nそーだ、しぶりんにも\n聞いてみよっと！", 0, 1, 3846, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112601, "元気だから笑顔になる\nじゃなくて\n笑顔だから\n元気になるんだよね～", 0, 1, 3847, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 112601, "声援を聞いてる時ね\nすごく懐かしいって\n思うことがあるんだ", 0, 1, 3848, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 112601, "私を応援してくれる\n一番大事な人が\n隣にいた気がする…", 0, 1, 3849, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112601, "なになにー？\nミオちゃんに\n何か用かな～？", 0, 2, 3850, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112601, "おっ、やっほー！\n元気そうだね～、キミ\n私？\nもちろん元気～！", 0, 2, 3851, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112601, "ねぇねぇ、ちょっと\n遊びに行かない？\nたまには息抜きで！", 0, 2, 3852, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112601, "ね、この魔物料理さ\nプリンって言い張れば\n食べてもらえそうかな", 0, 2, 3853, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 112601, "えへへ\nいつもありがとねっ！\nパワーもらってるよ～", 0, 2, 3854, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 112601, "どうしたどうした～\nミオちゃんの魅力に\nメロメロなのかい？", 0, 2, 3855, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112601, "おっはよ～！\n一日の始まりだし\nテンション上げてこ！", 1, 2, 3856, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112601, "お腹空いた～…\nねぇねぇ、一緒に\nお昼食べようよ～", 2, 2, 3857, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112601, "ふわぁ～…\n眠くなってきちゃった\nそろそろ休もっかなぁ", 3, 2, 3858, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112601, "わぁ、ふっかふか～！\nレッスンの疲れも\n吹っ飛んじゃうね☆", 0, 3, 3860, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112601, "何これ…かわいーっ！\n早くしまむーに\n教えてあげなきゃ！", 0, 3, 3861, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112601, "すぅ～…\nうんっ、これ私の\n好きな香りだな～♪", 0, 3, 3862, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112601, "ポッカポカだね～♪\n…あ～ん、ここから\n動きたくないーっ！", 0, 3, 3863, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112601, "わっ、おーいしー！\nこれはテンション\n上がっちゃうね♪", 0, 3, 3865, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112601, "ふむふむ…\nふむふむふむ…！\n勉強になった、ブイ☆", 0, 3, 3866, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 100101, "なんでドキドキが\n止まらないの…？\n騎士くんのせいだー！", 0, 1, 3868, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 100101, "ドキドキしてるの\nあたしだけ、かな…\n騎士くんはどう…？", 0, 2, 3869, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 100201, "騎士クンの前で\n好きって言えるように\n練習しないと…", 0, 1, 3870, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 100201, "騎士クン…大好き\n…き、騎士クン！？\nなんでここに！？", 0, 2, 3871, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 100301, "困ったな…\n彼が居ないだけで、\nここまで\n寂しいと思うなんて…", 0, 1, 3872, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 100301, "えっと…\n今日は訓練じゃなくて\nその…二人でゆっくり\n過ごしたいな…", 0, 2, 3873, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112501, "アイドルの心構え…\nどういう風に考えると\nしっくりくるのかな", 0, 1, 3874, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112501, "何か始めるのって\nドキドキするよね\nだから最近楽しいんだ", 0, 1, 3875, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112501, "冒険者の時って毎日\nバタバタしてたけど…\n目標ができると違うね", 0, 1, 3876, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112501, "少しお腹が空いた…\nチョコレート一粒だけ\n食べようかな", 0, 1, 3877, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112501, "ウヅキもミオも\n頑張ってるし\n私も負けられないね", 0, 1, 3878, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 112501, "レッスンしてる時さ\n何か、誰かが足りない\nそう思うことがあるの", 0, 1, 3879, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 112501, "大事なのは笑顔…\n誰か、大切な人から\n言われた気がするんだ", 0, 1, 3880, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112501, "どうしたの、何か用？\n手伝えることがあれば\nすぐに言ってね", 0, 2, 3881, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112501, "姿勢がいい？\n普段から鍛えてるし\n自然とそうなるのかも", 0, 2, 3882, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112501, "さっき散歩してたら\n犬を見かけたんだ\n人懐っこい子だったよ", 0, 2, 3883, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112501, "今から冒険？\nなら私も一緒に行くよ\n前衛は任せておいて", 0, 2, 3884, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 112501, "今からレッスンに\n行くつもりだったんだ\n少し手伝ってくれる？", 0, 2, 3885, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 112501, "蒼の剣を受けよ！\nえ、何してるのって？\nただの発声練習だけど", 0, 2, 3886, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112501, "おはよう、早いね\n今日は依頼もないし\nゆっくりしようかな", 1, 2, 3887, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112501, "そろそろお昼だね\nうーん、今日は\n何を食べよう…", 2, 2, 3888, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112501, "もうこんな時間…\n明日も早いし\nそろそろ寝なくちゃ", 3, 2, 3889, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112501, "レッスンで疲れたし\nちょっとだけ\n休んでおこうかな", 0, 3, 3891, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112501, "ふふっ、かわいい\nふわふわしたものって\n撫でたくなるんだよね", 0, 3, 3892, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112501, "うん、いい香り…\nこういうものがあると\n落ち着くからいいよね", 0, 3, 3893, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112501, "温かい場所って\n居心地がいいから\nのんびりしちゃうよ", 0, 3, 3894, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112501, "レッスン頑張ったら\nすぐお腹が空くし\n食べておかないと…！", 0, 3, 3896, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112501, "アイドルのことを\nもっと勉強して\n成長しなくちゃ", 0, 3, 3897, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111001, "ああ、知りたい…\nなんでもかんでも\nとにかく知りたい…", 0, 1, 3899, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111001, "ああ、眠い…\nいつでもどこでも\nとにかく眠い…", 0, 1, 3900, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111001, "君、気を付けたまえ\nぼーっと歩いていると\nほわっ！　転んだ…", 0, 1, 3901, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111001, "ミツキ師にいただいた\n新作サプリ…丁度よい\n被験体はいないものか", 0, 1, 3902, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111001, "サレンディア救護院に\nユニちゃん８歳として\n潜入済みだが何か？", 0, 1, 3903, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 111001, "君がユニちゃんズの\n同志であること、\nぼくは誇りに思うぞ", 0, 1, 3904, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 111001, "ぼくは青春がしたい\nおい、君\nぼくと青春しないか", 0, 1, 3905, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111001, "ああ、そうだな。\nぼくもそう思う。\n……えっ、何が？", 0, 2, 3906, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111001, "待て、話しかけるな。\n今の閃きをメモに…\n…ああっ、忘れた！", 0, 2, 3907, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111001, "歩き方が綺麗？\n何を今更なこと\n言っとるんだ君は", 0, 2, 3908, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111001, "ぼくの血液型？\n大らかのОだ。\n何か異論あるかね", 0, 2, 3909, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 111001, "君の声は精神に\n高揚をもたらすな…\n波形を分析したいぞ", 0, 2, 3910, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 111001, "妙だな…最近\n君に話しかけられると\n心拍に乱れを覚える", 0, 2, 3911, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111001, "おっと、朝だな。\nそろそろ寝るか、\n朝更かしは体に悪い", 1, 2, 3912, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111001, "ふむ、昼餉時か…\n今日のランチは\nサプリ定食にしよう", 2, 2, 3913, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111001, "もう夜だな、\nそろそろ起きるか。\n最高の一日にするぞ！", 3, 2, 3914, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111001, "ほわぁ～\n副交感神経が…\n優位に…ふふっひ", 0, 3, 3916, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111001, "かわいすぎるな…\nセクシーユニには\nミスマッチか…", 0, 3, 3917, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111001, "ほぁあアあァぁー…\nいい香りー…\nあアァあ…イイ…", 0, 3, 3918, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111001, "炎はいいぞ\nそこには人類発展の\nドラマがある", 0, 3, 3919, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111001, "フオオー！\n次から次へと固形食物\n原理気になるー！", 0, 3, 3921, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111001, "良書は時間を忘れる\n…ふむ、今度\n布団を持ってこよう", 0, 3, 3922, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 101201, "きらーん☆\nハツネちゃんに\n不可能はないのだ☆", 0, 1, 3924, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 101201, "キミと一緒だと\n寝ちゃうのが\nもったいないよ～", 0, 2, 3925, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 192101, "ちぇるーん☆", 0, 2, 3933, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110901, "ちぇる～ん☆\n今日もチエルは\nかわいいですよね～？", 0, 1, 3951, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110901, "どこかに楽しいもの\n落ちてないかにゃー", 0, 1, 3952, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110901, "チエルのオキニは\n甘々カフェラテ！\nシクヨロでぇ～す☆", 0, 1, 3953, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110901, "先輩、ひまですよね？\nチエル遊んであげても\nいいですけど？", 0, 1, 3954, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110901, "はぁー…しゅき…\nカルミナ尊い…\n一生すこれる…", 0, 1, 3955, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 110901, "先輩、ひまー\nちょっとは構えよーう\nデュクシ！デュクシ！", 0, 1, 3956, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 110901, "先輩なんか\nおごって～！\nてか…おごらなくても\nいいから遊んでよ…", 0, 1, 3957, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110901, "え、なんですか？\n告白だったら\n間に合ってますけど", 0, 2, 3958, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110901, "ちょっと静かに！\n今ヤバかわポーズの\nイメトレ中なんです！", 0, 2, 3959, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110901, "足取りが軽やか？\nあー…ダンスとか\n習ってるからですかね", 0, 2, 3960, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110901, "チエルの血液型？\n大型新人のОです\n何か問題でも？", 0, 2, 3961, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 110901, "先輩って何気に\nいい声してますよね\n言うことは変だけど", 0, 2, 3962, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 110901, "え、なんですか？\nたまにだったら\n告白してもいいですよ", 0, 2, 3963, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110901, "おはちぇる～ん☆\nハイ、ご一緒に～？", 1, 2, 3964, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110901, "今日のランチは\nどこで\n映えよっかにゃー", 2, 2, 3965, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110901, "こんばんちぇるるん☆\nハイ、ご一緒に～？", 3, 2, 3966, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110901, "ふかふかチエルン\n略して、ふかルン♪", 0, 3, 3968, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110901, "かわいいこの子と\nかわいいチエル！\n映ぁ～え～る～！", 0, 3, 3969, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110901, "かわいい香り～！\n持って帰ろっと！\nすぅーっ…", 0, 3, 3970, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110901, "マシュマロは\n焼かない方のが\nかわいくないです？", 0, 3, 3971, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110901, "チエル食べても\n太らない体質\nなんでぇ～！", 0, 3, 3973, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 110901, "読書は好きですよ\nシンプルに楽しいし\nギャップ萌えでしょ？", 0, 3, 3974, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112701, "みんなのあたしを\n見る目が違うね\nま、気にしないけどね", 0, 1, 3976, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112701, "今日も疲れた～\nやる気ゼロだし\nもう寝ちゃおうかな", 0, 1, 3977, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112701, "あたしの代わりに誰か\n働いてくれないかね", 0, 1, 3978, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112701, "労働の後に食べる\nあんパン最高～", 0, 1, 3979, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112701, "仕事の合間に\nちょっと一息いれよ\nあ～ドングリ美味し～", 0, 1, 3980, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 112701, "報酬も入ったことだし\nあいつに何か\n買ってあげようかな", 0, 1, 3981, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 112701, "次の休みはあいつと\n一日中だらだら\nしたいな～", 0, 1, 3982, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112701, "あたしは褒められて\n伸びるタイプなんだ\nだから褒めて褒めて", 0, 2, 3983, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112701, "お、差し入れ？\n気が利くねぇ\nあんがと", 0, 2, 3984, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112701, "新しい依頼？\n今日はもう無理～\n閉店で～す", 0, 2, 3985, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112701, "サ、サボってないよ！\nちょっと休憩してた\nだけだから！", 0, 2, 3986, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 112701, "ねえ、一緒に\nサボろうよ？\n働き過ぎはよくないよ", 0, 2, 3987, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 112701, "あんたが近くにいると\n仕事の疲れも\n和らぐんだよね～", 0, 2, 3988, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112701, "はぁ…もう起きなきゃ\nつらい労働の\nはじまりだ～", 1, 2, 3989, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112701, "うぅ、お昼に\nあんパン食べ過ぎた…\n苦しい…眠い…", 2, 2, 3990, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112701, "明日は仕事ないし\nガッツリ夜ふかし\nしちゃうよ～", 3, 2, 3991, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112701, "猫みたいに\n丸くなっちゃうよ～\nあたしリスだけどね", 0, 3, 3993, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112701, "ふかふか～\nあたしの尻尾と\n互角とはやるね～", 0, 3, 3994, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112701, "あんパンの香りの\nアロマとか\nあればいいのにな～", 0, 3, 3995, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112701, "これは危険な\nあたたかさだね～\n労働意欲が\nなくなるよ～", 0, 3, 3996, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112701, "いっぱい働いたから\nいっぱい食べても\nカロリー０だよ", 0, 3, 3998, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112701, "あたしだってたまには\n本くらい読むって\n全然似合わないけどね", 0, 3, 3999, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 104601, "あたしの作った\nオリジナルたい焼き…\nあいつ、\n喜んでくれるかにゃ？", 0, 1, 4001, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 104601, "あ、お帰りにゃー♪\nえへへ…キミに\nお帰りって言えるのが\nなんかうれしいにゃ♪", 0, 2, 4002, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112801, "フロンティア\nスピリッツを\nお見せするべ！", 0, 1, 4003, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112801, "この鎧を着ると\n気が引き締まる\n感じがするべ", 0, 1, 4004, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112801, "搾りたての牛乳飲んで\n今日も一日\nがんばるべ！", 0, 1, 4005, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112801, "みんなに牧場の\n乳製品を\n味わってもらいてぇべ", 0, 1, 4006, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112801, "ここはいいとこだべ\nうちの動物たちも\n連れてきてやりてぇな", 0, 1, 4007, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 112801, "みんなオラより\n若ぇのに働いてて\n立派だべな", 0, 1, 4008, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 112801, "牧場もギルドの\nみんなもオラが\nまとめて面倒見るべ！", 0, 1, 4009, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112801, "さあ、今日も\n忙しくなるべよ\n気合入れてくべ！", 0, 2, 4010, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112801, "お、あんちゃん\n丁度いいとこに！\n一緒に街に\n行商しに行くべ！", 0, 2, 4011, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112801, "牧場仕事で培った力が\n戦いでも役立つなら\n一石二鳥だべさ！", 0, 2, 4012, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112801, "疲れた時は牧場産の\nミルク菓子で\n糖分補給だべ", 0, 2, 4013, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 112801, "これ、牧場の\n新製品だべ\n一番最初に味見\nしてほしいべさ", 0, 2, 4014, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 112801, "働きづめはよくねぇベ\n膝枕してやっから\nこっち来るべさ", 0, 2, 4015, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112801, "ふぁあ…\n酪農家の朝は\n早ぇんだべ…", 1, 2, 4016, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112801, "ごはんの時間だべ！\n腹が減っては酪農も\n戦いもできねえべ！", 2, 2, 4017, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112801, "そろそろ寝るべ！\n健全なお笑いは\n健全な肉体に宿るべ！", 3, 2, 4018, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112801, "牧草みたいに\nふっかふかだべ～", 0, 3, 4020, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112801, "この子、目の感じが\nエリザベスに似てるべ", 0, 3, 4021, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112801, "いい匂いだべ～\n疲れた体に\nしみるべな～", 0, 3, 4022, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112801, "あったけぇな～\n牧場にも\n置いてみっかな～", 0, 3, 4023, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112801, "うまいべ！\n生産者のこだわりを\n感じるべさ", 0, 3, 4025, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112801, "酪農も戦いも\nまずは知識だべ！", 0, 3, 4026, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113001, "ぴょん、ぴょん♪\nこの服のおかげで、\n体が軽いなぁ…♪", 0, 1, 4028, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113001, "今の内に部屋の\n掃除をしておこっと…\nみんな喜んで\nくれるといいなぁ", 0, 1, 4029, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113001, "耳と尻尾…この服、\n本当にウサギさん\nみたいだなぁ", 0, 1, 4030, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113001, "なんだか先輩の\n気配がするような…\n気のせいかな？", 0, 1, 4031, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113001, "ウサギさんの真似を\nしたら、存在感が薄い\nとか言われないかな？\nア、アユミだぴょん♪", 0, 1, 4032, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 113001, "えへへ…\nいつもとは違う先輩が\nたくさん見られて、\nうれしいな♪", 0, 1, 4033, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 113001, "本の中の世界で先輩と\n旅をするなんて…\n幸せすぎて、罰が\n当たりそうだよぉ…", 0, 1, 4034, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113001, "じ～…はっ！？\nす、すみません！\n話しかけてもらえる\nなんて思ってなくて！", 0, 2, 4035, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113001, "先輩がすぐそばに…\nし、幸せすぎて\n倒れそうですぅ！", 0, 2, 4036, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113001, "え、えっと…あの…！\nうう…いざ先輩と\n向き合うと\n緊張しちゃうよぉ…", 0, 2, 4037, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113001, "はうっ！？\nしょ、正面から見る\n先輩は、私にとって\n神々しすぎますぅ…！", 0, 2, 4038, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 113001, "先輩…その…\nい、一緒にご飯でも\nどうでひゅか！\nうう…またかんだぁ…", 0, 2, 4039, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 113001, "せ、先輩…\nハーブティーを\n入れたんですが、\n一緒にどうですか…？", 0, 2, 4040, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113001, "せ、先輩！\nおはようございます！\nえへへ、練習通りに\nバッチリ言えたぁ♪", 1, 2, 4041, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113001, "あ、もうお昼ですね\n時計を持ってて\n良かったです", 2, 2, 4042, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113001, "もうこんな時間…\nお休みする\n準備をしちゃいますね", 3, 2, 4043, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113001, "あ…ちょっとだけ\n先輩の匂いがする…\nえへへ、幸せだなぁ♪", 0, 3, 4045, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113001, "…私もウサギさんの\nぬいぐるみとして、\n先輩のそばに…はっ！\n私ったら何を…！", 0, 3, 4046, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113001, "あ、いい香り…\n先輩もこういうのが\n好きなのかな？", 0, 3, 4047, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113001, "先輩が隣に\n居たら、もっと\n暖かいのになぁ…♪", 0, 3, 4048, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113001, "おいしい…でも、\n先輩と一緒に\n食べたら、もっと\nおいしいだろうな…", 0, 3, 4050, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113001, "ウサギさんって、穴を\n掘ったりするんだ…\n私も、隠れたいときに\n掘ってみようかな？", 0, 3, 4051, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180501, "ネビアさまのように\n普段からパタパタと\n飛んでみるのも…", 0, 1, 4053, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180501, "ご立派になられた\n主さまと並べるように\nもっと精進しなくては", 0, 1, 4054, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180501, "月々の食費が\n増えてしまいました\n申し訳ございません…", 0, 1, 4055, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180501, "ワンワンのほうの\n主さまは、この姿に\n驚くでしょうか…？", 0, 1, 4056, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180501, "主さまと離れていても\n主さまとの絆は\n胸に感じております…", 0, 1, 4057, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 180501, "このギルドハウスは\nみなさまの帰る場所…\nわたくしが守ります", 0, 1, 4058, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 180501, "どんな未来が\n待ち受けようとも\n主さまと添い遂げます", 0, 1, 4059, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180501, "おでかけですか？\nではわたくしも\nお供いたします", 0, 2, 4060, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180501, "もう二度と主さまと\n離れたくはありません\nいつ何時も…", 0, 2, 4061, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180501, "なにかお困りごと\nでございますか？\nわたくしに何なりと…", 0, 2, 4062, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180501, "どうぞゆっくりと\n心ゆくまでくつろいで\nくださいまし、主さま", 0, 2, 4063, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 180501, "この羽で主さまを\nどこまでも遠く…\n高くへと導きます", 0, 2, 4064, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 180501, "主さま、主さま…♪\nおそばにいられるのが\nうれしくて、つい…", 0, 2, 4065, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180501, "おはようございます\n今日も一日、主さまの\nサポートをいたします", 1, 2, 4066, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180501, "今日も頑張っている\n主さまに、スタンプを\nもう一つ…ポン！", 2, 2, 4067, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180501, "寝付けないのでしたら\nわたくしが子守唄を\n歌ってさしあげます", 3, 2, 4068, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180501, "ふかふか…\nまるで主さまのような\n柔らかさでございます", 0, 3, 4070, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180501, "あなたも一緒に\n主さまと遊びますか？\n至福の時間ですよ♪", 0, 3, 4071, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180501, "主さまの優しくて\nくすぐったい匂いが\nたまらなく恋しいです", 0, 3, 4072, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180501, "主さまの心と\n繋がったときのような\n温もりを感じます", 0, 3, 4073, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180501, "いけませんっ…！\n元の姿に戻ると、また\n食べてしまうのに…！", 0, 3, 4075, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180501, "主さまに読み聞かせる\n絵本は…妖精が主役の\n物語にいたしましょう", 0, 3, 4076, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 104801, "…彼のこと弟みたいに\n思ってきたのに…私、\nどうしたらいいの…？", 0, 1, 4078, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 104801, "よ、予定がなかったら\nでいいんだけど…\nその、ふ、二人で\n訓練とか…どうかな？", 0, 2, 4079, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112901, "おめかしもしたし\nあとはお兄ちゃんを\n待つだけですね～♪", 0, 1, 4080, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112901, "お兄ちゃんの身の回り\nをお片付け～、これぞ\nデキる妹アピール！", 0, 1, 4081, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112901, "あ、衣装がほつれてる\nこれくらいなら自分で\n繕えますね…！", 0, 1, 4082, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112901, "この格好で歩いてると\n見慣れた景色も\n違うものに見えますね", 0, 1, 4083, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112901, "勉強もちゃんとします\n…でも、一人だと\n集中力がもちません～", 0, 1, 4084, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 112901, "お兄ちゃんを大好きな\n妹が待ってますよ～\n…まだですかね～", 0, 1, 4085, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 112901, "お兄ちゃんのために\n掃除洗濯をする私って\nお嫁さんみたい…\n…えへへ～", 0, 1, 4086, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112901, "おに～いちゃん！\n縫い物、一緒に\nやりましょうよ！", 0, 2, 4087, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112901, "かわいい妹を\n愛でたくなりました？\n思う存分、どうぞ♪", 0, 2, 4088, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112901, "わっ、ビックリした～\nもう、お兄ちゃんてば\nイタズラっ子ですね～", 0, 2, 4089, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112901, "一緒にお買い物\n行きませんか？\n妹とデートですよっ♪", 0, 2, 4090, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 112901, "お兄ちゃん…頭を\n撫でてくれませんか？\nえへへ、これが一番♪", 0, 2, 4091, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 112901, "お兄ちゃん大好き♪\n何度でも言いますよ！\nお兄ちゃん大好きっ♪", 0, 2, 4092, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112901, "おはよ～お兄ちゃん！\n妹モーニングコールの\n時間ですよっ♪", 1, 2, 4093, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112901, "そろそろ休憩ですね！\n甘いお菓子で\n元気回復しましょ～！", 2, 2, 4094, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112901, "夜更かし厳禁！\nでも、もう少しだけ\nお話ししたいなぁ～", 3, 2, 4095, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112901, "ふっかふかですよ！\nお兄ちゃんと一緒に\n座りたいなぁ…♪", 0, 3, 4097, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112901, "このぬいぐるみの\nかわいさ…う～ん\n負けられません…！", 0, 3, 4098, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112901, "香りにも気をつかって\nパーフェクトな妹に\nなっちゃいますから！", 0, 3, 4099, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112901, "はぁ～ぬくぬくです～\nお兄ちゃんと一緒に\n温まりたいなぁ…♪", 0, 3, 4100, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112901, "これは止まりませんね\nもぐもぐ…いくらでも\n食べられちゃいます", 0, 3, 4102, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 112901, "これ、デザインの\n参考になりそう！\n私にも作れるかなぁ", 0, 3, 4103, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106601, "退屈です…\nなにか楽しいことは\nないですか？", 0, 1, 4105, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106601, "ギャングの仕事は\n今日はお休みです\nのんびりするですよ", 0, 1, 4106, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106601, "ひうっ！　あたしの\n尻尾なんて食べても\nおいしくないですよ？", 0, 1, 4107, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106601, "ふう…どうやら\n例の件はまだボスに\nバレてないですね…", 0, 1, 4108, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106601, "下克上…いい言葉です\nって、ボスに聞かれて\nないですよね…？", 0, 1, 4109, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 106601, "いつか絶対ビッグに\nなってやるです！\nあたしを信じるです！", 0, 1, 4110, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 106601, "あたしが覚醒したら\nきっと誰よりも\n強くなるはずです！", 0, 1, 4111, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106601, "なんですか？\n貢ぎ物なら\nいつでも歓迎ですよ！", 0, 2, 4112, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106601, "あたしだって\nやる時はやるですよ！\nその時がこないだけ！", 0, 2, 4113, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106601, "しりとり勝負ですか？\nいい度胸ですね、\nかかってくるです！", 0, 2, 4114, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106601, "お兄さんは\nヒマそうですね…\nうらやましいです", 0, 2, 4115, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 106601, "かまってほしいです？\nまったく…いいですよ\nしょうがないですね", 0, 2, 4116, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 106601, "お兄さんのことは\nこのあたしが\n守ってやるですよ！", 0, 2, 4117, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106601, "デキるギャングの\n朝は早いのです…\nふぁあ…", 1, 2, 4118, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106601, "お腹がすいたですね\nなにか食べにいくです\nお兄さんのおごりで！", 2, 2, 4119, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106601, "ふぁあ…むにゅう…\nまだ起きてるですよぉ", 3, 2, 4120, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106601, "はぁ～…\nもうここから一歩も\n動きたくないです", 0, 3, 4122, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106601, "なかなかいいモフりを\nしてやがるです…\n舎弟にしてやるですよ", 0, 3, 4123, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106601, "なんかおしゃれな\n匂いがするですね…", 0, 3, 4124, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106601, "ふふーん！\nあたしのブレスの方が\n火力は上ですね！", 0, 3, 4125, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106601, "あたしに貢ぎ物とは\nよく分かってるですね\n食べてやるですよ", 0, 3, 4127, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106601, "ふーむ…\nもっと楽しい本は\nないですかね？", 0, 3, 4128, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113201, "海に眠る秘宝…\n心躍る響きだ！\n早く見つけたい…！", 0, 1, 4130, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113201, "うう～、暑いぃ…\nかき氷を食べて\nすっきりしたいぃ…", 0, 1, 4131, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113201, "アターック！\nいや、もっとこう…\nアタァーック！", 0, 1, 4132, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113201, "スイカ割りで\n目を回さない\nコツはなんだろうか…", 0, 1, 4133, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113201, "せっかく海に来たのだ\n泳ぎも上手に\nなりたいものだな", 0, 1, 4134, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 113201, "ふっふっふ…\n疼く…疼くぞ！\n日焼けの痕が…", 0, 1, 4135, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 113201, "シグルドと私は\n来世でも共にある…\nつまり我らはずっと…\nはわわ…！", 0, 1, 4136, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113201, "シグルド、私と共に\n日光浴に行かないか？\n光の力を集めよう！", 0, 2, 4137, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113201, "この格好だからできる\n必殺技を考えている\n何か案はないか？", 0, 2, 4138, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113201, "シグルド～！\nスイカを貰ったから\n一緒に食べよう！", 0, 2, 4139, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113201, "こう日差しが強いと\n日焼けしそうだな\n跡になってないか…？", 0, 2, 4140, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 113201, "我らの夏の黙示録\nしっかりと記録\nしておかねばな", 0, 2, 4141, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 113201, "ちゃんと日焼けどめを\n塗ってもらうべき\nだったかな…？\nシ、シグルドに…！", 0, 2, 4142, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113201, "朝だぞシグルド！\nさぁ、日光を浴びて\n光の加護を得るのだ！", 1, 2, 4143, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113201, "もう昼食の時間か…\n私の魔力も減ったし\n早く補給しなければ", 2, 2, 4144, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113201, "夜でも我らの活動は\n終わることはない！\nさぁ準備を…ふわぁ…", 3, 2, 4145, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113201, "海で遊んでいると\n疲れてしまうからな\n少し休まるとしよう", 0, 3, 4147, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113201, "えへへ～、君の名前は\n何にしまちょうかね♪\n…はっ、見たのか！？", 0, 3, 4148, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113201, "心地よい香りで\n我が精神も安らぐ…", 0, 3, 4149, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113201, "別に寒くなどないぞ！\n寒くないが、ちょっと\n暖まりたくなってな！", 0, 3, 4150, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113201, "もぐ…食べ過ぎた分は\n魔力に変換されるのだ\n…たぶん", 0, 3, 4152, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113201, "…なな、なんだ！？\nシグルドはこういう\n趣味なのか…！？", 0, 3, 4153, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113201, "むむ、この乗り心地…\nいざゆかん、\nシードラゴンよ！", 0, 3, 4155, 10, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113201, "疾風の冥姫の名に\nふさわしき\n深海の玉座よ…\nふああ…柔らかい～", 0, 3, 4156, 11, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180201, "この姿でいると\nわたしもお姫様に\nなれた気がする…♪", 0, 1, 4157, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180201, "お腹空いてきちゃった\nまずは買い出しに…\n…変身解かなくちゃ！", 0, 1, 4158, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180201, "胸のところが空いてて\nスースーするの\nまだ慣れないな…", 0, 1, 4159, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180201, "治癒魔法に使ってた\n魔力を攻撃魔法に…\n頭の中でも練習練習", 0, 1, 4160, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180201, "何もできなかった\nわたしから、少しは\n変われたのかな…？", 0, 1, 4161, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 180201, "騎士クン…\n今どこにいるの…？\n…会いたいな", 0, 1, 4162, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 180201, "離れてても分かるよ…\n温かい力で騎士クンに\n守られてるの", 0, 1, 4163, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180201, "わたしが戦えてるのは\n騎士クンのおかげだよ\nいつもありがとう！", 0, 2, 4164, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180201, "お腹空いてそう…？\n…内緒で作ったおやつ\n騎士クンも食べる？", 0, 2, 4165, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180201, "ひゃうっ！　え！？\nせ、背中はダメ～！\n言ってから触って～！", 0, 2, 4166, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180201, "この格好、どうかな？\nえ？　似合ってる…？\n本当？　…うれしい！", 0, 2, 4167, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 180201, "かわいい？　そうだね\nひらひらが…わたし？\nええっ、そ、そんな！", 0, 2, 4168, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 180201, "騎士クン、これからも\nずっと…わたしの\nそばにいてください", 0, 2, 4169, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180201, "朝からこんなに\n食べる日が来るなんて\n思ってなかったよ…", 1, 2, 4170, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180201, "お昼を作らなくちゃ\n騎士クン、一緒に\nお料理してみる？", 2, 2, 4171, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180201, "騎士クン、もう眠い？\n眠るまで、わたしが\nずっとそばにいるね", 3, 2, 4172, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180201, "攻撃参加するからこそ\n適度に体を休めるのは\n大切だよね！", 0, 3, 4174, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180201, "かわいい…騎士クンと\nシェフィちゃんにも\nプレゼントしたいな", 0, 3, 4175, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180201, "いい香り～…\nあれ？　お腹が空いて\nきちゃった…！？", 0, 3, 4176, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180201, "温かい…\n騎士クンがくれる\n力みたい…", 0, 3, 4177, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180201, "ぱくっ、\nあむ、はむっ…\nううぅ～…やっぱり\n手が止まらないよぉ～", 0, 3, 4179, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180201, "新しく覚えたいな…\nみんなで食べられる\n大皿向けの料理♪", 0, 3, 4180, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 104901, "お姉ちゃんの愛\nこのままじゃ\nあふれちゃうよ…♪", 0, 1, 4182, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 104901, "弟くん♪\n弟くんっ♪\nおっとうっとくんっ♪", 0, 2, 4183, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113101, "泳ぐのもいいけど\n今日は砂浜で\nのんびりしようかね", 0, 1, 4184, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113101, "さてと\nちょっくらひと泳ぎ\n行ってくるかね", 0, 1, 4185, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113101, "あっついねぇ…\nうちわがないと\nしんどいったらないよ", 0, 1, 4186, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113101, "冷たすぎる飲み物で\n体を冷やさないよう\n気をつけないと…", 0, 1, 4187, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113101, "夏はあっという間だし\n悔いがないように\n楽しまないとね", 0, 1, 4188, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 113101, "きっとこの夏は\nアタシにとって大事な\n想い出になるね…", 0, 1, 4189, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 113101, "…前に比べたら\nアタシたちの距離も\n近づいたみたいだね", 0, 1, 4190, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113101, "もし暇なら\n今日も笹舟釣り\n一緒にどうだい？", 0, 2, 4191, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113101, "お前さん、釣りに\n興味が湧いたなら今度\n道具を見に行こうよ", 0, 2, 4192, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113101, "今から砂浜に行くんだ\nお前さんも一緒に\n散歩するかい？", 0, 2, 4193, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113101, "え、日焼け跡？\nおや…本当だ\nすぐ焼けちまうねえ", 0, 2, 4194, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 113101, "ひゃっ！？\nちょっと！\n日焼け跡をいきなり\n触らないでおくれ！", 0, 2, 4195, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 113101, "お前さんは、その…\nアタシと一緒にいて\n楽しかったかい？", 0, 2, 4196, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113101, "おはようさん\n今日も砂浜に行って\nゆっくりしようか", 1, 2, 4197, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113101, "お昼か…\nそうだ、よかったら\n刺身定食、作ろうか？", 2, 2, 4198, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113101, "夜の空を見てるとさ\nなつかしい気持ちに\nなるんだよね", 3, 2, 4199, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113101, "適度に休憩して\n夏バテしないよう\n気をつけないとね", 0, 3, 4201, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113101, "あんたたちも\n海を楽しみたいって\n思っているのかね", 0, 3, 4202, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113101, "ん～…香りでも\nバカンスらしさを\n楽しめるもんだね", 0, 3, 4203, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113101, "こういう格好だと\n少し冷えちまうんだよ", 0, 3, 4204, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113101, "もぐもぐもぐ…\n遊んだ分、お腹が\n空いちまってねぇ", 0, 3, 4206, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113101, "…ん、なんだいこの本\n催眠…うーん読めない\nナナカに聞いてみるか", 0, 3, 4207, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113101, "へえ…\nなかなかいい\n乗り心地じゃないか", 0, 3, 4209, 10, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113101, "そうそうハマグリって\n竿でも釣れるんだよ\n知ってたかい？", 0, 3, 4210, 11, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113301, "海を満喫しなきゃね\nちょっとひと泳ぎ\n行ってこようかな？", 0, 1, 4211, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113301, "う～ん…♪\nいつもと違った\n開放感～\nなんちゃって！", 0, 1, 4212, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113301, "今のナナカちゃんは\nたぶん水属性！", 0, 1, 4213, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113301, "水着もバッチリ！\nサイカワサイツヨ\nナナカちゃ～ん☆", 0, 1, 4214, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113301, "んっふっふ\nドキ！　水着だらけの\nギルドハウス！\nとかどうかな？", 0, 1, 4215, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 113301, "そんなに見られると\nちょ～っと\n恥ずかしいかも…\nなんてねなんてね！", 0, 1, 4216, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 113301, "ちょっとくらい\nドキッとしてくれても\nいいのにな～、もう…", 0, 1, 4217, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113301, "水着魔法少女の\n相方がほしいなぁ～…\nね、君やってみない？", 0, 2, 4218, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113301, "いつもの格好と同じ？\nも～、わかってない！\nセクスィー度が違う！", 0, 2, 4219, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113301, "どうかなどうかな？\nこのひらひらのとこが\nかわいーかなー\nとか思うんだけど！", 0, 2, 4220, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113301, "せっかくだから\nギャルゲーイベント\nごっことか\nやってみる？", 0, 2, 4221, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 113301, "ナナカちゃんの\n好感度を上げたい？\nならおでかけだー！", 0, 2, 4222, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 113301, "おうちで水着って\nなんだかコスプレ\nみたいで楽しいかも♪", 0, 2, 4223, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113301, "おっはよーう！\n今日はバカンス日和！\n海へゴーですぞ～♪", 1, 2, 4224, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113301, "お昼ご飯どうしよ～…\nあ、ナナカちゃんと\nどっか食べに行く？", 2, 2, 4225, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113301, "夜は本を読むのが\n捗りまくりですな～！\n今日はなに読もっかな", 3, 2, 4226, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113301, "次のイベントもあるし\nちょっと休んで体力を\n回復しなきゃね～", 0, 3, 4228, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113301, "こういうマスコット\n私にもいたらなぁ\nいっそ作っちゃう…？", 0, 3, 4229, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113301, "水着魔法少女に\n似合う香りってのも\n考えてみよっかな♪", 0, 3, 4230, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113301, "夜になると急に冷えて\n肌寒いんだよね～…", 0, 3, 4231, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113301, "あんまり食べると\nお腹が出て…でも…\nうう、止まらない～！", 0, 3, 4233, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113301, "この催眠魔法の本\n誰にもばれないように\n保管しとかなきゃね…", 0, 3, 4234, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113301, "ゴーゴーゴーゴーゴー\nナナカちゃーん♪", 0, 3, 4236, 10, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113301, "ナナカちゃんの\n誕生～\nなんちゃって", 0, 3, 4237, 11, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113401, "ファイトファイト☆\n一生懸命\n応援するね！", 0, 1, 4238, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113401, "みんながんばってる！\n私も応援でみんなを\n盛り上げなきゃ！", 0, 1, 4239, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113401, "もし元気のない人が\nいたら…応援で少し\nでも元気にしたいな！", 0, 1, 4240, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113401, "がんばれ～☆\nよし、発声練習OK！\n応援は、声も大事！", 0, 1, 4241, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113401, "せっかく水着なんだし\n泳いだり潜ったりして\n遊びたいな～！", 0, 1, 4242, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 113401, "チアリーダーとして\nキミを精一杯\n応援したいな…！", 0, 1, 4243, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 113401, "キミへの応援\nちゃんと\n届いてるかな…", 0, 1, 4244, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113401, "えへへ…今ね、\nキミへの応援歌を\n考えてたのだ～♪", 0, 2, 4245, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113401, "どうしたの？\n元気がないなら\n私が応援するよ☆", 0, 2, 4246, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113401, "がんばったあとは休憩\nしなきゃね！　私と\n一緒にお昼寝する？", 0, 2, 4247, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113401, "シオリンとおそろいの\n水着で応援…\nしてみたいなぁ～", 0, 2, 4248, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 113401, "次はなにして遊ぶ？\nキミがしたいことを\n教えてよ！", 0, 2, 4249, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 113401, "キミを応援\nすることができて…\n私、すごく幸せ…", 0, 2, 4250, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113401, "キミって早起き\nなんだね！　私も\n見習わなきゃ！", 1, 2, 4251, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113401, "ちょっとお疲れかな？\nそれじゃあ\n応援してあげるね♪", 2, 2, 4252, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113401, "遅い時間まで\nがんばってえらいね！\nでも無理はダメだよ♪", 3, 2, 4253, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113401, "ちょっとだけ休憩…\n…あう、座り心地が\n良すぎて…眠気が…", 0, 3, 4255, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113401, "ここにいるみんなを\n私の応援でもっと\n元気にするのだ～！", 0, 3, 4256, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113401, "元気の出る香り～♪\nこれもうまく応援に\n活かせないかな～？", 0, 3, 4257, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113401, "ぽかぽかあったまって\n元気をチャージ\nするのだ～☆", 0, 3, 4258, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113401, "いっぱい食べて、\nみんなを応援する\nパワーをつけなきゃ！", 0, 3, 4260, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113401, "みんなを元気にする\n方法は…どこかに\n書いてないかなぁ…？", 0, 3, 4261, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113401, "わぁ、この子\n怪獣さんみたいで\nかわいいね～♪", 0, 3, 4263, 10, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113401, "水着でこれに\n乗ってると海にいる\n気分になれるね！", 0, 3, 4264, 11, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113501, "すごく忙しいけど\nとても充実した気分\nこれが青春なのかしら", 0, 1, 4265, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113501, "森の静かな雰囲気も\n海の開放的な空気も\nどちらも素敵ね～", 0, 1, 4266, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113501, "汚れたお洗濯物は\nみんなが頑張った証\nなんだか愛おしいわ", 0, 1, 4267, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113501, "みんなを見てると\n何だか私も\n走りたくなっちゃうわ", 0, 1, 4268, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113501, "せっかくの水着だし\n泳ぐ練習でも\nしてみようかしら", 0, 1, 4269, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 113501, "みんな～ファイト～！\n応援するのも大事よね\n練習しておかなきゃ♪", 0, 1, 4270, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 113501, "先生に教えられること\nまだまだい～っぱいね\n次は何がいいかしら♪", 0, 1, 4271, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113501, "今日も暑くなりそうね\nこまめな水分補給を\n忘れないでね", 0, 2, 4272, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113501, "手伝ってくれるの？\nありがとう♪\n先生、頼りにしてるわ", 0, 2, 4273, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113501, "休憩はこまめに\nとりましょうね\n無理は厳禁よ", 0, 2, 4274, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113501, "海の遊びって\nどんなのがあるの？\n先生に教えてね", 0, 2, 4275, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 113501, "水着は初めてだけど\nどう？　変じゃない？\nふふ、ありがとう", 0, 2, 4276, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 113501, "あなたと過ごした\nこの夏は、きっと…\n忘れられない思い出ね", 0, 2, 4277, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113501, "おはよう～\n朝練前にちゃんと\nごはん食べましょうね", 1, 2, 4278, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113501, "お弁当作ってきたの\n栄養いっぱい摂って\n午後も頑張ろうね", 2, 2, 4279, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113501, "もうお休みしなきゃね\n先生が子守唄を歌って\nあげましょうか？", 3, 2, 4280, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113501, "ふふ、いい気持ちねぇ\n先生、少しだけ\n休憩しちゃおうかしら", 0, 3, 4282, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113501, "あらかわいい♪\nチームにもこういう\nマスコットが欲しいわ", 0, 3, 4283, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113501, "あら、海の香り？\n波の音まで聞こえて\nきちゃいそうだわ", 0, 3, 4284, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113501, "うふふ\nこれなら濡れた水着を\n乾かすのも簡単ね♪", 0, 3, 4285, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113501, "これ、美味しいわね♪\nでも、食べ過ぎは\n禁物よ", 0, 3, 4287, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113501, "先生だって日々勉強よ\n新しいことを知るのは\nとても楽しいわ♪", 0, 3, 4288, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113501, "あらあら、楽しいわね\nみんなもきっと\nおおはしゃぎね", 0, 3, 4290, 10, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113501, "ふふ、これで\n夏らしい気分を\n満喫するわよ♪", 0, 3, 4291, 11, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 102301, "サンドイッチも\n作ったし、\nお兄ちゃんのお出迎え\n準備は完璧だね♪", 0, 1, 4292, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 102301, "えへへ、お兄ちゃんと\nぷうきちが居てくれる\nだけで、なんだか\nうれしくなっちゃう♪", 0, 2, 4293, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113601, "少し赤くなっているな\n日焼けなんてしたのは\nいつ以来だろう", 0, 1, 4294, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113601, "この格好で\n過ごすのは\n少し勇気がいるな…", 0, 1, 4295, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113601, "暑いのは平気だが\n肌が外気に触れるの\nにはまだ慣れないな…", 0, 1, 4296, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113601, "この格好でも\n堂々としていれば\n恥ずかしくはない…\nのかな？", 0, 1, 4297, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113601, "おっと…鎧姿の時と\n重心が違うせいか\nちょっと不安定だな", 0, 1, 4298, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 113601, "この水着…\n私に似合って\nいるのだろうか…？\n少年には聞きづらいな", 0, 1, 4299, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 113601, "少年との\n海のバカンス…\nまた行きたい\nものだな", 0, 1, 4300, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113601, "あまり見るのは\nよしてくれ…\n恥ずかしいじゃ\nないか…", 0, 2, 4301, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113601, "どうかしたのかい？\n私でよければ\n話し相手くらいには\nなろうじゃないか", 0, 2, 4302, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113601, "ひゃっ！\nす、すまない\nちょっと過敏に\nなっているようだ", 0, 2, 4303, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113601, "…っくしゅん！\nすまない\n薄着には慣れて\nないものでね", 0, 2, 4304, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 113601, "まあ、少年になら\n見られても…\nい、いや！\nなんでもない！", 0, 2, 4305, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 113601, "私がこうして\nゆっくりできるのは\n少年がそばにいて\nくれるからだよ", 0, 2, 4306, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113601, "この時間なら\nビーチにも人は\n少ないだろうね…\n遊びに行くかい？", 1, 2, 4307, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113601, "昼食のお誘いかい？\nいいよ、付き合おう", 2, 2, 4308, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113601, "もう夜か…\n一日が経つのは\nはやいものだね", 3, 2, 4309, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113601, "これは楽でいいな…\nいつもは鎧姿で\n家具を傷つけないか\n気を使っているからね", 0, 3, 4311, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113601, "うん…ふわふわの\n感触が\nよくわかるな…", 0, 3, 4312, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113601, "いつもは\n鎧に匂いが\n移ってしまうのが\n悩みなんだよ…", 0, 3, 4313, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113601, "うーむ…\n涼しいのに暖かい\nなんだか奇妙な気分だ", 0, 3, 4314, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113601, "兜を気にせず\n食べられるのは\n楽でいいな…", 0, 3, 4316, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113601, "ふむ…鎧がないと\nページがめくりやすい", 0, 3, 4317, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113601, "身軽でいいな…\n鎧を着ていると\n騎乗訓練の時\n馬に申し訳がなくてね", 0, 3, 4319, 10, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113601, "貝をうまく使えば\n水中でも\n守りを固められそうだ", 0, 3, 4320, 11, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106801, "どこをアジトと\nつなげようかな～…\nこのへんにしよっと♪", 0, 1, 4321, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106801, "ここってちょっと\n変わった建物だね\n妖精がいるでしょ？", 0, 1, 4322, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106801, "色んな人を観察できて\n眼福眼福…♪", 0, 1, 4323, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106801, "家具の配置変更なら\nアタシにおまかせあれ\n…家の形は変えない？", 0, 1, 4324, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106801, "新メニューどうしよう\n思いついた人がいたら\nアタシに教えてね！", 0, 1, 4325, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 106801, "少年、早く帰って\nこないかな…\nよし、迎えに行こう！", 0, 1, 4326, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 106801, "結局またあちこち\n移動する日々だし…\n少年に会いたいな～", 0, 1, 4327, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106801, "どしたの少年？\nお姉さんに何でも\n相談してみな～？", 0, 2, 4328, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106801, "あれをああして…\nおや少年！　ごめんね\nすぐに気づけなくて！", 0, 2, 4329, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106801, "何？　クリスが\nアタシを探してる？\n…いないって言って！", 0, 2, 4330, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106801, "よければリノちゃんと\nシズルちゃんにも\n顔を見せてあげて", 0, 2, 4331, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 106801, "少年～！\nもうヘトヘトだよ～！\nお願い癒して～！", 0, 2, 4332, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 106801, "…アタシが素直に\n助けてって言えるのは\n少年だけだよ", 0, 2, 4333, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106801, "朝だよ少年！\n爽やかな朝の\nクレープはいかが？", 1, 2, 4334, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106801, "お腹空いたな～\nお昼のクレープ\n少年は食べる？", 2, 2, 4335, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106801, "夜までクレープは\nやめとけ？　ごめん\nそれだけは無理…！", 3, 2, 4336, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106801, "アタシこんなに\nゆっくりしちゃって\nいいんだっけ～？", 0, 3, 4338, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106801, "…これ\nネネカが変身\nしてないよね？", 0, 3, 4339, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106801, "いい香りだ…\n解析完了\n今度生成しようっと♪", 0, 3, 4340, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106801, "安心するな～…\nアジトに置けないから\n羨ましいよ", 0, 3, 4341, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106801, "食べ物を消化するって\n仕組み、どんなものか\n気になる…", 0, 3, 4343, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106801, "速読できるけど\n本を読む時くらい\n文字にじっくり\n浸かりたいんだよね…", 0, 3, 4344, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113701, "エンジェルとして\nみんなを元気に…！\nアカリ、頑張ります♪", 0, 1, 4346, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113701, "お姉ちゃんと一緒に\nお兄ちゃんを…えへへ\nまだかな、お兄ちゃん", 0, 1, 4347, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113701, "エンジェルの音色を\n目指して、今日も\nサックスの練習です♪", 0, 1, 4348, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113701, "お兄ちゃんとの特訓も\n頑張らないと…\n厳しくしてくださいね", 0, 1, 4349, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113701, "かわいいエンジェルの\n格好、お兄ちゃんなら\n喜んでくれますよね♪", 0, 1, 4350, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 113701, "エンジェルの力で\nお兄ちゃんのハートも\nイチコロですよ♪", 0, 1, 4351, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 113701, "アカリが一番\n元気にしたいのは\nお兄ちゃんですよ♪", 0, 1, 4352, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113701, "お兄ちゃん？\n触りたいなら…\nこことかどうですか♪", 0, 2, 4353, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113701, "特訓の時間ですか？\nアカリ、楽しみに\nしてたんですよ♪", 0, 2, 4354, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113701, "あ、お兄ちゃん！\nそろそろアカリと\n休憩…しませんか♪", 0, 2, 4355, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113701, "この羽、触り心地\nいいですか？　あんっ\nくすぐったいですよぉ", 0, 2, 4356, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 113701, "お兄ちゃんも元気に\nなりたいんですか？\nアカリにお任せです♪", 0, 2, 4357, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 113701, "やんっ♪\nお兄ちゃんったら\nダイタンですね♪", 0, 2, 4358, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113701, "お兄ちゃん、朝ですよ\nすっごく気持ちのいい\nお天気ですよ…♪", 1, 2, 4359, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113701, "お昼寝しませんか\nお兄ちゃん？\nアカリの膝枕で…♪", 2, 2, 4360, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113701, "夜ですね、お兄ちゃん\nアカリ、なんだか胸が\nドキドキしちゃいます", 3, 2, 4361, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113701, "フカフカですね…\n隣にお兄ちゃんが\nいてくれたらなぁ…♪", 0, 3, 4363, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113701, "えへへ、アカリも\nこんな風にかわいく\nなりたいな～", 0, 3, 4364, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113701, "お兄ちゃんが好きな\n匂いなのかな？\n参考にしますね！", 0, 3, 4365, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113701, "体が火照ってきて\nなんだかアカリ…\nクラクラしちゃう…", 0, 3, 4366, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113701, "はむ、んぐ…あっ\nほっぺについちゃった\nベトベトですよぉ～", 0, 3, 4368, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113701, "エンジェルって色んな\n物語に出てきますよね\nもっと調べなくちゃ！", 0, 3, 4369, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 103201, "あの方の前では\n常に気高く\nありたいですわね", 0, 1, 4371, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 103201, "どんなに道に迷っても\nあなたさまの元には\n絶対に辿り着く\n自信がありますわ", 0, 2, 4372, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 102801, "あんたと一緒に\nいるときが一番楽しい\n一時かもしれないわ", 0, 1, 4373, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 102801, "いつかあんたと一緒に\nサレンディア救護院を\n切り盛り…なんてね♪", 0, 2, 4374, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113801, "今日も\nエンジェルとして、\nみんなの幸せの\nために頑張らないと", 0, 1, 4375, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113801, "せっかく羽が\nついてるのに\n飛べないのが残念だわ", 0, 1, 4376, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113801, "この格好、子供たちに\n妙にウケがいいわね\nま、悪くないけど", 0, 1, 4377, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113801, "や、やっぱりみんな\n注目してくるわね…\nエンジェルって珍しい\nからしかたないか", 0, 1, 4378, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113801, "エンジェルになると\n人見知りがなおる！\n…わけないか", 0, 1, 4379, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 113801, "エンジェルの活動は\n大変だけど、自分が\n成長してるって\n実感がうれしいわね", 0, 1, 4380, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 113801, "もし誰かの願いを\nかなえられるなら\n私はアイツの…\nって何考えてるのよ！", 0, 1, 4381, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113801, "何ボサっとしてるの！\nさあ、街に出て\n人助けするわよ！", 0, 2, 4382, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113801, "エンジェリック\nアロー！\nなんてね…え、アンタ\nいつから見てたのよ！", 0, 2, 4383, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113801, "コラ！\n羽は触らないで！\n壊れたらどうするのよ", 0, 2, 4384, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113801, "エンジェルじゃなく\nても、アンタはいつも\n人助けしてるわよね\n…ちょっと尊敬するわ", 0, 2, 4385, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 113801, "この服なら他の子より\n私を見てくれるかも…\nって、な、何\n見てるのよ！", 0, 2, 4386, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 113801, "ね、ねえ、もし私が\n困ってたら、一番に\n駆けつけて…やっぱり\n何でもないわ！", 0, 2, 4387, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113801, "おはよう…あ、あの、\nエンジェルとして\n町に行くんだけど…\n一緒に行かない？", 1, 2, 4388, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113801, "ふぅ、いいことした\n後のランチは\nおいしいわね", 2, 2, 4389, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113801, "もうこんな時間…\n夜更かししてないで\n早く寝なさいよね", 3, 2, 4390, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113801, "いい座り心地ね～\nまさに天にも昇る\n心地よさだわ～", 0, 3, 4392, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113801, "ふふ、かわいい…\nあ、羽付けたら\nもっとかわいく\nなるんじゃないかな？", 0, 3, 4393, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113801, "はぁ～落ち着くわ～\n回復アイテムみたいで\n癒やされちゃうわ～", 0, 3, 4394, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113801, "わっ、とと…\n危ない、羽が\nこげちゃうところ\nだったわ", 0, 3, 4395, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113801, "人の役に立つのって\nすごく大変だから、\nこれでライフ回復よ！", 0, 3, 4397, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113801, "エンジェルに関する\n本はあるかしら…あ！\nこれ探してた攻略本！", 0, 3, 4398, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113901, "ふっふっふ\n吸血鬼らしく\n驚かせてあげましょう", 0, 1, 4400, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113901, "獲物はどこですか～！\nう～ん、もうちょっと\n迫力が欲しいですね", 0, 1, 4401, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113901, "トリックオアトリート\n…返事がないので\nイタズラしておきます", 0, 1, 4402, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113901, "ハロウィンですし\nたまには騎士さんでも\n誘ってあげますかね", 0, 1, 4403, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113901, "マントは動きに\nメリハリがついて\nいいですね", 0, 1, 4404, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 113901, "色んな仮装が見られて\nやっぱりハロウィンは\n最高ですね", 0, 1, 4405, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 113901, "吸血鬼らしい行動って\n噛みついたり…いや\nそれは大胆過ぎです！", 0, 1, 4406, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113901, "呼びましたか？\n手短にお願いしますよ\n忙しいんですから！", 0, 2, 4407, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113901, "え？　衣装に穴が？\nありがとうございます\n…よく見てるんですね", 0, 2, 4408, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113901, "ふわあ…吸血鬼の\n姿でも、眠い時は\n眠いんですよね…", 0, 2, 4409, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113901, "ハロウィンってライブ\nとは違った賑やかさが\nあってワクワクします", 0, 2, 4410, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 113901, "私を驚かそうとしても\n無駄ですよ！\nすぐ分かるんですから", 0, 2, 4411, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 113901, "騎士さん、一緒に\nハロウィンを…いいえ\nなんでもないですっ！", 0, 2, 4412, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113901, "仕事で寝不足だから\n太陽がまぶしいです…\n私、吸血鬼っぽいかも", 1, 2, 4413, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113901, "お昼ご飯は真っ赤な…\nトマト尽くしのお弁当\nにしてみました", 2, 2, 4414, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113901, "夜はハロウィンが\n盛り上がる時間…\n行きますよ騎士さん！", 3, 2, 4415, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113901, "このフィット感\nどうにか服作りにも\n活かせれば…！", 0, 3, 4417, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113901, "よく出来てますね…！\nぬいぐるみの服を作る\nのも楽しそうですね！", 0, 3, 4418, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113901, "いい感じですね…\n服に合わせた香りって\nいうのも、いいかも…", 0, 3, 4419, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113901, "はぁ、温まりますね…\nおっと、マントに火が\n点いちゃいそうです！", 0, 3, 4420, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113901, "あぁ、美味しい～\n食べた分は、働いて\n燃焼しますからね！", 0, 3, 4422, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113901, "伝説の吸血鬼…\n夜を統べる者…\nほうほう…", 0, 3, 4423, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 113901, "やっぱりハロウィンと\nいえばお菓子です！\nさぁ配りましょうか！", 0, 3, 4425, 12, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114101, "この格好…\nカッコイイのに\n動きやすくて\n着心地も最高ッス！", 0, 1, 4427, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114101, "キメポーズはこう…\nいやこっちのほうが…\nなやましいッス…！", 0, 1, 4428, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114101, "トリックオア\nジャスティス！\nって言ったら何か\nもらえるッスかね？", 0, 1, 4429, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114101, "新しい必殺技の\n特訓もしなくちゃ\nいけないッスね…\n忙しいッス！", 0, 1, 4430, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114101, "自分のこの姿\nトモね～ちゃんにも\nみせてあげるッス！", 0, 1, 4431, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 114101, "ホワイトタイガー\n参上ッス！\n今のポーズは\nキマったッス！", 0, 1, 4432, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 114101, "あの人と一緒だと\nほんとにヒーローに\nなれる気がするッス！", 0, 1, 4433, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114101, "どうッスか？\nこの色、自分に\n似合ってるッスかね？", 0, 2, 4434, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114101, "ヒマだったら\n必殺技の特訓に\n付き合ってもらって\nいいッスか？", 0, 2, 4435, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114101, "ちょうどいいところに\n来たッスね！\nこのキメポーズ\nどうッスか？", 0, 2, 4436, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114101, "お！　この格好、\n気になるッスか？\nじっくり見ても\nいいッスよ♪", 0, 2, 4437, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 114101, "あんたも一緒に\n新しい技の練習\nどうッスか？", 0, 2, 4438, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 114101, "今日もあんたと一緒に\nランドソルの平和を\n守るッスよ！", 0, 2, 4439, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114101, "朝ッス！\n一緒に早朝特訓\nやるッスよ！", 1, 2, 4440, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114101, "お昼休憩ッス！\nヒーローにも\n休息は必要ッス！", 2, 2, 4441, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114101, "闇夜に映える\n白い衣装で\n平和を守るッスよ！", 3, 2, 4442, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114101, "トラも狩りの時以外は\nごろごろしてるって\n本で読んだッスよー", 0, 3, 4444, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114101, "もこもこッスね♪\nこのかわいらしさを\n守ってあげるのも\nヒーローの役目ッス！", 0, 3, 4445, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114101, "いい匂いッスね…\nリラックスして\n瞑想するッス！", 0, 3, 4446, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114101, "燃える炎は\nヒーローの心意気ッス\nいつまでも\n見てられるッスよ", 0, 3, 4447, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114101, "もぐもぐ…\nいっぱい食べて\n強いヒーローに\nなるッスよ！", 0, 3, 4449, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114101, "ちゃんと勉強の本も\n読んでるッスよ…！\nヒーローには賢さも\n必要らしいッス！", 0, 3, 4450, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114101, "わわっ、お菓子が\nいっぱいッス！\nみんなで\n食べるッスよ！", 0, 3, 4452, 12, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 103001, "ワタシがチューシンと\nなった今、天下統一は\n目の前デス！", 0, 1, 4454, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 103001, "もうショーグンってば\nそれはセイシツに\nすることデ～ス…", 0, 2, 4455, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114001, "女幽霊…うらめしや…\nうーん、これは\nちょっと違う気が\nするな…", 0, 1, 4456, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114001, "何だか寒気がする…\nま、まさか\nこの部屋にも幽霊が？", 0, 1, 4457, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114001, "ハロウィンは楽しいね\n童心に帰ったような\n気分だよ", 0, 1, 4458, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114001, "今度のハロウィンは\nユイとヒヨリを\n誘ってみようかな", 0, 1, 4459, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114001, "ハロウィンのお菓子は\nどれもかわいいね\n…一つくらいなら\nいいかな？", 0, 1, 4460, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 114001, "次は彼と\nどんなハロウィンを\n過ごそうかな…", 0, 1, 4461, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 114001, "こんな賑やかさも\nいいものだね\n彼といられるのなら\nなおさら…", 0, 1, 4462, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114001, "…いたずらしようと\nしてない、キミ？\nふふ、お見通しだよ", 0, 2, 4463, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114001, "え、私にもお菓子を？\nありがとう…ふふ\nうれしいものだね\nハッピーハロウィン！", 0, 2, 4464, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114001, "どうしたの？\n出かけるなら\n私も同行しようか", 0, 2, 4465, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114001, "この格好、舞踏会にも\n出られそうだね\n一曲、踊ってみる？", 0, 2, 4466, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 114001, "やるからには本気で\nやらせてもらおう\nトリックオア\nトリート！", 0, 2, 4467, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 114001, "他の子に目移りする\nのも分かるが、その…\nせっかくの特別な衣装\n見てほしいな", 0, 2, 4468, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114001, "寝起きの体を\n慣らしておこうか\nほら、剣を取って", 1, 2, 4469, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114001, "お菓子もいいけど\nちゃんと昼食も\nとるんだよ", 2, 2, 4470, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114001, "さあ、行こうか！\nハロウィンは\nこれからが本番だよ", 3, 2, 4471, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114001, "ハロウィンはまだ\nこれからだ\n一休みしようか", 0, 3, 4473, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114001, "キミたちも\n仮装に興味は\nあるかな？　…ふふ", 0, 3, 4474, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114001, "いい香りだけど\n幽霊が集まったり\nしないだろうか…？", 0, 3, 4475, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114001, "暖かい…一息ついたら\nハロウィンの\nパレードを\n見に行こうかな", 0, 3, 4476, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114001, "ハロウィンを本気で\n楽しむなら\n食事もしっかり\nとっておかないとね", 0, 3, 4478, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114001, "ハロウィンの\nホラー小説か…\nけ、結構\n怖いものだね…？", 0, 3, 4479, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114001, "お菓子がいっぱいだ…\nこういうのって\n見ているだけで心が\n躍ってしまうね", 0, 3, 4481, 12, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114201, "こんなにかわいい\n武器で戦えるとは…\nすごいものだな\n魔法少女は", 0, 1, 4483, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114201, "決めポーズの訓練も\nしっかりやっておくか\n…誰も見ていないな？", 0, 1, 4484, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114201, "こういう格好は\nユキあたりがすごく\n似合いそうだな", 0, 1, 4485, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114201, "子どもたちにサインを\n求められてしまった…\nこれもまた私の使命！", 0, 1, 4486, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114201, "この格好は目立つな…\nいや、提督とは人々の\n視線を集めるものだ！", 0, 1, 4487, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 114201, "あいつと作戦会議を\nしておくか。あれで\nなかなか頼れるからな", 0, 1, 4488, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 114201, "あいつには私の勇姿を\nちゃんと見ていて\nほしいものだな", 0, 1, 4489, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114201, "一仕事終えたことだし\n一緒に甘いものでも\n食べに行くか？", 0, 2, 4490, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114201, "怪しげな気配がするな\n貴公も警戒を\n怠るなよ！", 0, 2, 4491, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114201, "シオリに魔法少女の\n本を借りたんだ\n一緒に読まないか？", 0, 2, 4492, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114201, "巡回の時間まで\nお茶にでもするか\n貴公も付き合うがいい", 0, 2, 4493, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 114201, "貴公がサポートして\nくれるのなら心強いな\n頼りにしているぞ", 0, 2, 4494, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 114201, "頭を撫でるな～！\n…まったく、貴公だけ\nだぞ、こんな風に私を\n撫でてくるのは…", 0, 2, 4495, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114201, "起きろ貴公！\n魔法少女の朝は\n早いぞ！", 1, 2, 4496, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114201, "ほら、食後の\nデザートだ\nて、適度な糖分は\n体に必要なんだ！", 2, 2, 4497, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114201, "ふわぁ…魔法少女も…\n眠気、には…い、いや\n寝てないぞ！？", 3, 2, 4498, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114201, "任務や訓練で\n疲れた体に\nこれは効くなぁ…", 0, 3, 4500, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114201, "うむ、かわいいな\nモーラのやつを隣に\n並べようか", 0, 3, 4501, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114201, "ああ、身も心も\n安らぐ…\nまるで魔法だな…", 0, 3, 4502, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114201, "この温かさ…\n愛と希望に\n通じるものがある…", 0, 3, 4503, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114201, "食事の大切さは\n魔法少女も軍人も\n変わりない\nしっかり食べるんだ！", 0, 3, 4505, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114201, "魔法少女の本を\n読んでるのだが\nわ、私もこんななのか", 0, 3, 4506, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 103301, "オラとあんちゃんは\nボケもツッコミも\nあうんの呼吸だべ♪", 0, 1, 4508, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 103301, "あんちゃんと二人で\nネタ合わせ…初めての\n共同作業だべ♪", 0, 2, 4509, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 192501, "ぴっぷる！", 0, 1, 4510, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 192501, "ぴっぷるぴっぷる♪", 0, 1, 4511, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 192501, "ぴっ！", 0, 1, 4512, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 192501, "ぷっぷー", 0, 1, 4513, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 192501, "ぴぃ？", 0, 1, 4514, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 192501, "ぴっぷるんるん♪", 0, 2, 4515, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 192501, "ぷぅ！", 0, 2, 4516, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 192501, "ぴぴっ♪", 0, 2, 4517, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 192501, "ぷぅー…", 0, 2, 4518, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 192501, "ちゅ！", 1, 2, 4519, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 192501, "ぷはー", 2, 2, 4520, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 192501, "ぴぇー", 3, 2, 4521, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114301, "魔法少女になったら\n髪型も変わるんだね\n色々試してみようかな", 0, 1, 4531, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114301, "…煌く剣技で、\n悪を断つ！\nうん、今日も\n完璧なポーズだね♪", 0, 1, 4532, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114301, "ミクマ流剣術で、\n魔法少女の技に使え\nそうなものは…よし、\n早速試してみよっと♪", 0, 1, 4533, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114301, "魔法少女になったから\nには、サインの練習も\nしないと…\nよし、頑張るぞ！", 0, 1, 4534, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114301, "魔法少女の姿はすごく\nかわいいけど…この\n姿はジュンさんたちに\n見せられないなぁ…", 0, 1, 4535, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 114301, "あの人は魔法少女\nのこと好きなのかな？\n…好きだったら\nうれしいな♪", 0, 1, 4536, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 114301, "…やっぱり、\nあの人がいないと\n特訓に身が入らない…\n早く帰ってこないかな", 0, 1, 4537, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114301, "もし時間があるなら、\n私と街の見回りに\n行かない？", 0, 2, 4538, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114301, "あ、いいところに！\n新しい魔法剣術を\n編み出したから、\n見てくれないかな？", 0, 2, 4539, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114301, "魔法少女の姿が気に\nなるのは分かるけど…\nず、ずっと見られる\nのは照れるからやめて", 0, 2, 4540, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114301, "変身ポーズ中の動き、\n少し変えた方が\nいいかな…\nあなたはどう思う？", 0, 2, 4541, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 114301, "よければ、\n魔法少女の特訓に\n付き合ってくれない？\nその…二人きりでさ…", 0, 2, 4542, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 114301, "私…あなただけの\n魔法少女になっても…\nふふっ、な～んてね♪", 0, 2, 4543, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114301, "今日もいい朝だね\nさあ、一緒に\n街の見回りに行こうか", 1, 2, 4544, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114301, "もう昼か…なにか\n食べたいものある？\n簡単なのでいいなら、\n私が作ってあげるよ", 2, 2, 4545, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114301, "そろそろ休もうか\n…なんなら、添い寝\nしてあげようか？\nふふっ、冗談だよ♪", 3, 2, 4546, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114301, "これは…\n心身ともに安らぐね\nなんだか\n眠くなっちゃいそう", 0, 3, 4548, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114301, "か、かわいい…これ、\n魔法で動かせたり\nしないかな？", 0, 3, 4549, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114301, "いい香りだね\nなんだか頭が冴えて\nきたし、新しい技でも\n考えようかな", 0, 3, 4550, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114301, "あったかいな…\nこの温もりは、\n魔法少女の魔法でも\n再現不可能だね…", 0, 3, 4551, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114301, "こ、これは…\nおいしすぎて手が\n止まらないかも", 0, 3, 4553, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114301, "この本の\nキャラの動き、\n変身ポーズの\n参考にできるかも！", 0, 3, 4554, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114401, "メリークリスマス！\nさあ、素敵な\nパーティーを始めると\nいたしましょう！", 0, 1, 4556, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114401, "ふふっ、\nいくつになっても、\nやはりクリスマスは、\nワクワクしますわね♪", 0, 1, 4557, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114401, "自分で料理をしてみて\nシェフたちのすごさが\n改めて分かりましたわ", 0, 1, 4558, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114401, "何事も自分で\n体験してみると\n新しい発見が\nあるものですわね♪", 0, 1, 4559, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114401, "やはり、クリスマスは\n街が活発になり\nますわね…これは、\n絶好の商機ですわ♪", 0, 1, 4560, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 114401, "他のクリスマス料理も\n作れるようになったら\nあのお方は食べて\nくださるかしら…？", 0, 1, 4561, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 114401, "あのお方に贈る\nプレゼント…\nこれはかなりの\n難題ですわね…", 0, 1, 4562, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114401, "メリークリスマス！\nよろしければ、一緒に\nクリスマスの準備を\nいたしませんか？", 0, 2, 4563, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114401, "まあ、クリスマスの\n飾りつけを手伝って\nいただけるのですか？\nありがとうございます", 0, 2, 4564, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114401, "クリスマスに合わせて\nあつらえた自慢の衣装\nなのですが…その、\nいかがでしょうか？", 0, 2, 4565, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114401, "幼い頃はサンタさんが\nいらっしゃるのを\n待ちわびたものです\n今は…内緒ですわ♪", 0, 2, 4566, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 114401, "も、もしお時間が\nあるようでしたら、\nクリスマスの街を\n一緒に歩きませんか？", 0, 2, 4567, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 114401, "来年のクリスマスも\nあのお方と一緒に\n迎えられたら…", 0, 2, 4568, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114401, "時は金なり…\nせっかくの朝ですもの\n時間を有効活用\nいたしましょう！", 1, 2, 4569, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114401, "お昼になりましたわね\nさあ、\nパーティーの準備を\n進めますわよ！", 2, 2, 4570, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114401, "静かな夜ですわね…\nもうちょっとだけ、\nあなたさまとお話を\nしていたい気分です", 3, 2, 4571, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114401, "適度な休息は\n成功の秘訣ですわ！", 0, 3, 4573, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114401, "このかわいらしさ…\nいつまでも見て\nいられそうですわ♪", 0, 3, 4574, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114401, "人をもてなすには\nこうしたところにも\n気を配らないと\nいけませんわね", 0, 3, 4575, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114401, "暖かい…\nこういう憩いの\nひと時こそ、大事に\nしたいものですわね", 0, 3, 4576, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114401, "この奥深い味わい…\n何か隠し味など\n使われていまして？", 0, 3, 4578, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114401, "クリスマス料理の\nレシピも、\nこれだけあると\n迷ってしまいますわね", 0, 3, 4579, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114401, "この絵本、とっても\n面白いつくりですわね\nうちの商品に\nしたいぐらいですわ", 0, 3, 4581, 14, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114401, "これだけ大きな\n着ぐるみ…\n職人の技術が\n光りますわね", 0, 3, 4582, 15, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114601, "クリスマスといえば\nパーティーよね！\nいっぱい飲むわよ～♪", 0, 1, 4583, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114601, "私を麦しゅわだけの\n女と思ったら\n大間違いなんだから♪", 0, 1, 4584, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114601, "クリスマスらしい\n飲み物も結構いいわね\n私も今度\n作ってみようかしら", 0, 1, 4585, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114601, "暖かい部屋で飲む\n麦しゅわも\nまた格別なのよね…", 0, 1, 4586, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114601, "今日はオフの日だし\n朝から飲んでも\n怒られないわよね？", 0, 1, 4587, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 114601, "クリスマスの\nデートスポットで\n彼と一緒に…ってなに\n考えてるのユカリ！？", 0, 1, 4588, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 114601, "背伸びしなくても\nありのままの自分を\n受け入れてくれるって\nすごく…幸せ…", 0, 1, 4589, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114601, "大丈夫よ～\n飲んでない\n飲んでないひゃら～", 0, 2, 4590, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114601, "クリスマスは\n特別だもの\n特別な一杯を\n飲まないとね♪", 0, 2, 4591, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114601, "メリークリスマース！\nほらほらぁ\n乾杯しましょ♪", 0, 2, 4592, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114601, "お姉さんの\n頼れるところ\n見せてあげるわね", 0, 2, 4593, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 114601, "何かあったら\n頼ってくれても\nいいんだからね？", 0, 2, 4594, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 114601, "ふふ…これからも\nキミのこと…\n頼りにしちゃうからね", 0, 2, 4595, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114601, "さわやかな朝ね\n…二日酔いがないと\n朝ってこんなに\n気持ちいいのね", 1, 2, 4596, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114601, "昼間から飲むのって\nまた格別よね…って\nガマン、ガマンよ…", 2, 2, 4597, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114601, "もうこんな時間かあ…\nあとは寝るだけだし\nちょっとくらいは…", 3, 2, 4598, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114601, "ゴロゴロするのって\n最高ね～\nあとは麦しゅわが\nあればなぁ", 0, 3, 4600, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114601, "私だって…\nかわいいものは\n好きでいいわよね？", 0, 3, 4601, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114601, "んっ～…いい香りね\n日頃の疲れが\n取れる気がするわ", 0, 3, 4602, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114601, "ポカポカする～\n…なんだかのどが\nかわいてきたわね", 0, 3, 4603, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114601, "もぐもぐ…労働の\n後のご飯はやっぱり\nおいしいわぁ…", 0, 3, 4605, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114601, "クリスマスデート\n完全攻略マニュアル？\n…読んでおかなくちゃ", 0, 3, 4606, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114601, "あら、絵が飛び出して\n見えるけど…\n酔っ払ってるせいじゃ\nないわよね…？", 0, 3, 4608, 14, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114601, "いい手触りね～\nウチにもほしいけど\n麦しゅわをこぼしたら\n大変そうだわ…", 0, 3, 4609, 15, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 101601, "ヒデサイの視線\nずっと独り占め\nしてたいな～", 0, 1, 4610, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 101601, "ヒデサイといるだけで\nうち、おに楽しいし！\nヒデサイもっしょ～？", 0, 2, 4611, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114501, "メリークリスマス！\nふふ、口にするだけで\n何だか楽しくなるわね", 0, 1, 4612, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114501, "いつもは子どもたちを\n見守る方だけど\n今ははしゃいでも\nいいわよね？", 0, 1, 4613, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114501, "ツリーや料理\nそしてプレゼント！\nいくつになっても\nやっぱり心躍るわね♪", 0, 1, 4614, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114501, "飾り付けも料理も\nいいセンスしてるわね\n救護院のパーティーの\n参考にさせてもらうわ", 0, 1, 4615, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114501, "焼きそばパンを\nクリスマスの定番に\nするにはどうすれば…", 0, 1, 4616, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 114501, "これ、あいつの\n好きそうな料理だわ\n取っといて食べさせて\nあげようかしら♪", 0, 1, 4617, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 114501, "あいつのプレゼント\nちょっとだけ奮発\nしたのよね…\n喜んでくれるかしら", 0, 1, 4618, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114501, "このドレス\n似合ってるかしら？\n今だけは見とれても\n許してあげるわ♪", 0, 2, 4619, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114501, "見て、このケーキ！\nおいしそうでしょ\nたまにはこんな贅沢も\nいいわよね♪", 0, 2, 4620, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114501, "料理も飲み物も\nおいしいわね！\nいっぱい食べて\n元を取らないと♪", 0, 2, 4621, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114501, "子どもたちと一緒に\n雪遊び、楽しそうね\nあんたもどう？", 0, 2, 4622, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 114501, "せっかくの\nクリスマスなんだもの\nエスコート\nよろしくね♪", 0, 2, 4623, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 114501, "寒い？　そう…\nなら、もっとこっち\n来ていいわよ…？", 0, 2, 4624, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114501, "ほら、早く起きて？\nパーティー用の\n買い出しに\n付き合ってちょうだい", 1, 2, 4625, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114501, "パーティーの料理の\n準備をしながらお昼も\n作っておいたわ\n我ながらいい手際ね", 2, 2, 4626, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114501, "にぎやかなパーティー\nもいいけど、聖夜の\n余韻を感じるのも\n結構素敵ね", 3, 2, 4627, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114501, "いい座り心地だわ～\n腕のいい職人の仕事ね\nうちでも扱いたいわ", 0, 3, 4629, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114501, "かわいい！\n子どもたちに\nプレゼントしたら\n喜んでくれるかも", 0, 3, 4630, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114501, "華やかな香りね\nクリスマスに\nピッタリだわ", 0, 3, 4631, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114501, "寒い日にはやっぱり\nこれよね…\nはぁ、極楽極楽♪", 0, 3, 4632, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114501, "んー、おいしいわ！\n焼きそばパン味の\nものも、作れない\nかしら？", 0, 3, 4634, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114501, "たまにはのんびり\n読書でも…なんだか\nラブロマンスばかりね\nクリスマスだから？", 0, 3, 4635, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114501, "これ、面白いわね！\n子どもたちの\nプレゼントに\nピッタリだわ", 0, 3, 4637, 14, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114501, "こんないい毛並み\n初めて見るわ…\n一体どこで手に\n入るのかしら？", 0, 3, 4638, 15, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114701, "アタシもみんなと\n一緒に正月を\n楽しんでいいのか？", 0, 1, 4639, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114701, "今年こそ、もう少し\n身長が伸びます\nように…", 0, 1, 4640, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114701, "げっ！　お年玉が\n全然残ってないぞ！？\nそんな、嘘だろ…？", 0, 1, 4641, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114701, "大勢で過ごす\n正月か…初めてだけど\n悪くないもんだな", 0, 1, 4642, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114701, "んー…ここじゃ\n天楼覇断剣の\n出番はなさそうだな", 0, 1, 4643, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 114701, "アイツらと作る\n戦い以外の思い出も\nもっと増やしたいな", 0, 1, 4644, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 114701, "こんなに賑やかな\n正月は初めてだ\n…温かくて、いいな", 0, 1, 4645, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114701, "記憶がなくったって\n楽しい思い出は\n新しく作れるよな？", 0, 2, 4646, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114701, "早くでかけるぞ！\nアタシはもっと\n正月を楽しみたい！", 0, 2, 4647, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114701, "なあ、オマエはもう\nノウェムって名前\n覚えてくれたよな？", 0, 2, 4648, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114701, "この服、アタシに\n似合ってるのか？\n…そっか。\nありがとな！", 0, 2, 4649, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 114701, "オマエらと見た朝日は\nきっと忘れないぞ！\n思い出ができたな！", 0, 2, 4650, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 114701, "いつかオクトーも\n一緒に、みんなで\n正月を過ごせたらな…", 0, 2, 4651, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114701, "朝から元気だな！\nアタシもいっちょ\n見習ってみるか！", 1, 2, 4652, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114701, "昼飯は何にする？\n正月はうまいものが\n多くて迷うよな！", 2, 2, 4653, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114701, "いい夜だな！\nアタシと一緒に\n夜更かしを楽しむか？", 3, 2, 4654, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114701, "正月らしく、たまには\nゆっくりするのも\nいいかもな…", 0, 3, 4656, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114701, "なんだ？\nオマエも初詣に\n行きたいのか？", 0, 3, 4657, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114701, "すーはー…\n神殿のニオイがするな\nいい事ありそうだ！", 0, 3, 4658, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114701, "コイツがあれば\nうまい魚が\n焼けそうだな…", 0, 3, 4659, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114701, "ううー\n食べ過ぎて帯が\nきつくなってきたぞ…", 0, 3, 4661, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 114701, "おみくじって\n書いてあることが\nむずかしいんだよな…", 0, 3, 4662, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180101, "困ってる人は\nいないみたいだし…\nちょっとここで、\n特訓しようかな♪", 0, 1, 4664, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180101, "空が飛べるんだから\nもっとたくさんの人を\n助けにいけちゃう！\n…ここじゃ飛べないや", 0, 1, 4665, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180101, "炎の翼\nかっこいいなー！\nこれ、もっと大きく\nできないのかな？", 0, 1, 4666, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180101, "やっぱり変身すると\nお腹すくんだね…\n…今ならペコさんの\n食欲の理由が分かるよ", 0, 1, 4667, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180101, "色なんかは違うけど…\nマントがあるから\n騎士くんとお揃いに\nなったみたい♪", 0, 1, 4668, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 180101, "…騎士くんがいないと\nなんか調子でない…\n早く会いたいよぉ…", 0, 1, 4669, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 180101, "この姿になると、\n騎士くんを近くに\n感じる…なんだろ\nドキドキしちゃう…", 0, 1, 4670, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180101, "騎士くん騎士くん！\n今大丈夫？\nトレーニングメニュー\n考えるの手伝って！", 0, 2, 4671, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180101, "…うにゃっ！？\nこ、この姿になっても\n尻尾は触っちゃ\nダメー！", 0, 2, 4672, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180101, "もしかして、この服が\n気になってるの？\nだったら、もっと\n近くで見ていいよ♪", 0, 2, 4673, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180101, "あ、騎士くん！\n困ってる人がいないか\n見回りに行くんだけど\nついてきて欲しいな！", 0, 2, 4674, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 180101, "耳に触りたいの？\nだったら頭もなでて！\nそしたら、好きなだけ\n触っていいよ♪", 0, 2, 4675, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 180101, "…あたし、強くなって\n騎士くんを守るよ！\nだから…これからも\n一緒にいていい？", 0, 2, 4676, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180101, "おっはよー！\nさあさあ騎士くん！\n今日も一緒に\nはりきっていこー♪", 1, 2, 4677, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180101, "お昼、お昼～♪\n今日のご飯は何かな～\nあ、騎士くん！\n早くお昼食べ行こ！", 2, 2, 4678, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180101, "今日もお疲れ様ー！\n騎士くん、あたしが\nマッサージしよっか？\n結構評判なんだよ！", 3, 2, 4679, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180101, "うにゃぁ…\nこの気持ちよさ…\n溶けちゃいそう…\nにゃぁ…", 0, 3, 4681, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180101, "か、かわいい～\nこれ、なでなで\nしていいんだよね…？\nよしよし…♪", 0, 3, 4682, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180101, "とってもいい香り！\n気分も落ち着くし、\n特訓の後なんかに\nいいかもー！", 0, 3, 4683, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180101, "ぬくぬくだぁ…\nなんだか、\nふにゃふにゃに\nなっちゃうよぉ…", 0, 3, 4684, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180101, "お、おいひぃ…！\nこの姿になると\nお腹すくし、すっごく\n助かっちゃった！", 0, 3, 4686, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180101, "この本で勉強して、\nぶんぶりょーどーの\n超ウルトラヒヨリに\nなっちゃうぞー！", 0, 3, 4687, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111801, "今年もみんなで\nおいしいものを\nたくさん食べたいです", 0, 1, 4689, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111801, "挑戦は大切ですよね☆\nというわけで\nおせちに魔物を…", 0, 1, 4690, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111801, "おせちは縁起物です！\n食べれば食べるほど\n幸せになるはずです！", 0, 1, 4691, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111801, "食べ物がたくさん\n入った福袋は\nないんでしょうか…？", 0, 1, 4692, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111801, "初詣で何を\nお願いしましょう…\n五穀豊穣？", 0, 1, 4693, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 111801, "ずっとこうして\n年の始まりを\n過ごしたいですね", 0, 1, 4694, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 111801, "いつか二人きりで\n初日の出を\n見たいです", 0, 1, 4695, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111801, "お餅おかわりです！\nいくらでも\n食べられますよ！", 0, 2, 4696, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111801, "おせちの食材を\n取りに行きませんか？\nいい運動です☆", 0, 2, 4697, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111801, "凧揚げって\n難しいですね～\n糸が絡まっちゃいます", 0, 2, 4698, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111801, "一年の計は元旦にあり\n今年一年分の\n献立を作りましょう！", 0, 2, 4699, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 111801, "ほらほら！\nもっと近くで着物を\n見ていいんですよ☆", 0, 2, 4700, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 111801, "あなたはどんな\n初夢を見ましたか？\nわたしは…内緒です☆", 0, 2, 4701, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111801, "年明けの朝は\n心なしか\n気持ちいいですね☆", 1, 2, 4702, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111801, "お雑煮のあとは\nおしるこで\nおやつタイムです！", 2, 2, 4703, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111801, "あたたかくして\n幸せな初夢を\n見ましょうね", 3, 2, 4704, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111801, "はぁ～屋台巡りを\n満喫しました☆\n一休み一休み", 0, 3, 4706, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111801, "縁起のいい\nぬいぐるみを並べて\n福を呼びましょう！", 0, 3, 4707, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111801, "おしるこのアロマって\nないんでしょうか？\nそれなら作るしか…", 0, 3, 4708, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111801, "ここでお餅を\n焼いたらおいしく\nなりそうですね～♪", 0, 3, 4709, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111801, "おいしすぎて\n食べる手が\n止まりません～", 0, 3, 4711, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 111801, "新しいおせち料理の\nヒントは…なるほど…\nふむふむ…", 0, 3, 4712, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 100601, "お兄ちゃんとあんな\n特訓やこんな特訓が\nしたいなぁ、えへへ♪", 0, 1, 4714, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 100601, "お兄ちゃんに褒めて\nもらうと、身体が\nじぃーんと\n熱くなっちゃいます♪", 0, 2, 4715, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115001, "見目麗しい衣装に\n身を包むのも\n悪い気分では\nありませんね", 0, 1, 4716, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115001, "縁起物で\n験を担ぐというのも\nたまにはよいでしょう", 0, 1, 4717, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115001, "非効率なので\nあまり行いませんが\n私も手料理くらいは\n作れるのですよ", 0, 1, 4718, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115001, "研究のためなら\n時に遠路を超え\n実地へ赴くことも\n必要なのです", 0, 1, 4719, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115001, "トゥンヌスの\n突然変異…\n大いに興味を\nそそられますね", 0, 1, 4720, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 115001, "漁師の技法も\n再現できたことで\nまた一つ、仮説が\n実証できました", 0, 1, 4721, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 115001, "正月くらいは\n私ものんびりしたい\n気持ちなのですよ\n意外ですか？", 0, 1, 4722, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115001, "模倣に頼らずとも\n衣装に合わせた\n所作程度は\nできて当然です", 0, 2, 4723, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115001, "お年玉の使い道は\n思いつきましたか\nゆっくり考えても\nいいのですよ", 0, 2, 4724, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115001, "トランプでも花札でも\n相手をしましょう\n手心が必要なら\nいつでも言いなさい", 0, 2, 4725, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115001, "初詣でお祈りする\n内容ですか\n…ヒミツ、として\nおきましょう", 0, 2, 4726, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 115001, "私たちでオーマ中の\nトゥンヌスを\n釣り上げましょうか\n…冗談ですよ", 0, 2, 4727, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 115001, "見返りを求めない願い\nそれは祈りに通じるの\nかもしれませんね", 0, 2, 4728, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115001, "…貴方がその眠たげな\n顔を洗ってくる間に、\n温かいお茶でも\n淹れておきましょう", 1, 2, 4729, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115001, "さて、本格的に\n活動を再開します\n貴方も同行しなさい\n昼食は済みましたか？", 2, 2, 4730, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115001, "私はもう少し\n作業を片付けてから\n寝ることにします\n早く眠りなさい", 3, 2, 4731, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115001, "少し休みます…\n時間が来たら\n起こすように", 0, 3, 4733, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115001, "贈り物を模倣して\n潜入…試してみる\n価値はありそうですね", 0, 3, 4734, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115001, "嗅覚から得られる\n情報量は侮れません\n動物の模倣において\n注意すべき点ですよ", 0, 3, 4735, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115001, "穏やかな炎による\nリラックス効果は\n本能によるものかも\nしれませんね", 0, 3, 4736, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115001, "いかなる強者でも\n兵站、補給は\n必要なものです", 0, 3, 4738, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115001, "あらゆる学問は\n完璧な模倣のための\n大きな礎となります", 0, 3, 4739, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115501, "新たに得たこの力で\n仲間と目指す未来を\n切り拓いてみせます", 0, 1, 4741, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115501, "ここは、みなさまと…\nわたくしの\n帰る場所でございます", 0, 1, 4742, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115501, "主さまも精霊の召喚を\n試してみますか？", 0, 1, 4743, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115501, "主さま、おこづかいは\n足りていますか？\n遠慮は無用ですよ", 0, 1, 4744, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115501, "この装束をまとうと\n自然と背筋が伸びる\n思いがいたします", 0, 1, 4745, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 115501, "進化した精霊に\n負けぬよう\n修行に励みます", 0, 1, 4746, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 115501, "仲間たちといられる\nこの幸せが、末永く\n続きますように…", 0, 1, 4747, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115501, "主さまのお手手は\n大きくて温かいですね\nぎゅっぎゅっ…ふふ♪", 0, 2, 4748, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115501, "今度、主さまにも\n里に伝わる昔話を\nお話しいたしましょう", 0, 2, 4749, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115501, "里で採れた果物を\n持って参りました\n召し上がってください", 0, 2, 4750, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115501, "里のお土産を\nここのみなさまに\nお配りいたしましょう", 0, 2, 4751, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 115501, "仲間と囲む食卓…この\n温もりは、みなさまの\n心の温かさなのですね", 0, 2, 4752, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 115501, "主さまに名前を\n呼んでいただくと\n心が温かくなります♪", 0, 2, 4753, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115501, "ふふっ…主さま\n寝癖がついて\nおりますよ？", 1, 2, 4754, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115501, "今日の昼食は\n里の伝統料理です\nお口に合うでしょうか", 2, 2, 4755, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115501, "主さまが眠れるよう\n安らぎの風を…\nおやすみなさいませ", 3, 2, 4756, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115501, "この癒し…\n主さまにも味わって\nいただきたい…", 0, 3, 4758, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115501, "この愛らしさ…\n精霊たちと似たものを\n感じます…♪", 0, 3, 4759, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115501, "精霊よ…この安らぐ\n香りを、隅々まで\n届けてくださいまし", 0, 3, 4760, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115501, "瞑想するのに\nちょうどよい暖かさで\nございます", 0, 3, 4761, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115501, "進化した精霊を\n使役するためにも\nよく食べて体力を…！", 0, 3, 4763, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115501, "風の精霊について\nもっと詳しく知って\nおかなければ…", 0, 3, 4764, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115601, "儀式の時を思い出して\n精霊に呼びかける…\n出ておいで～♪", 0, 1, 4766, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115601, "ひゃうっ！　もう…\n急に背中を触るなんて\nいたずら好きな精霊に\n育てた覚えはないのに", 0, 1, 4767, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115601, "儀式用の衣装…\nあんまり激しい動きは\nできないよね…でも\n戦う時は気にしない！", 0, 1, 4768, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115601, "そうだ…精霊に\n騎士クンのそばで\n見守っていてって\n頼んでみようかな…", 0, 1, 4769, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115601, "ふふっ、こうやって、\n楽しく精霊とお話し\nしていられる、平穏な\n日々が続くといいな", 0, 1, 4770, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 115601, "き、きき、騎士クン！\nわたしと、あの…！\nうう…練習ですら\n上手く誘えないよぉ…", 0, 1, 4771, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 115601, "あっ、精霊が…\nふふ、応援ありがとう\nわたしの想い…いつか\n必ず伝えるね", 0, 1, 4772, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115601, "新しく覚えた料理\n作ってみたんだけど\n騎士クンも食べない？", 0, 2, 4773, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115601, "騎士クン、\n疲れてない？\nわたしと精霊で\n治療するよ？", 0, 2, 4774, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115601, "もう、いたずらしない\n…騎士クン！？\nご、ごめんなさい！\n精霊だと思って！", 0, 2, 4775, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115601, "騎士クン！\n時間が合ったら、\n精霊と一緒に\nお話しない？", 0, 2, 4776, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 115601, "…こういう服着るの\n初めてなんだけど…\nえっ、かわいい…？\nあ、ありがとう…！", 0, 2, 4777, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 115601, "困ったことがあったら\n相談してね。わたし、\n騎士クンのためなら…\n色々できるよ、色々！", 0, 2, 4778, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115601, "おはよう騎士クン！\n今からお外で\n瞑想するんだけど…\n騎士クンもどうかな？", 1, 2, 4779, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115601, "お昼の時間だ\nご飯ならわたしが\n用意するから\nゆっくりしてて", 2, 2, 4780, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115601, "もうこんな時間…\n騎士クン、はいどうぞ\n寝る前の温かいミルク\nぐっすり眠れるよ♪", 3, 2, 4781, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115601, "…うん、落ち着く\n精霊との魔法で\nこんな風に誰かを\n安心させたいな", 0, 3, 4783, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115601, "かわいい…\n精霊も気に入ってる…\nそうだ、同じ物\n作ってあげようかな？", 0, 3, 4784, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115601, "いい香り…これ、\nうちに置いたら、\nお母さん喜ぶかも！", 0, 3, 4785, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115601, "あったかい…\nまるで誰かが\n抱きしめてくれた\nみたい…", 0, 3, 4786, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115601, "自然の恵みに\n感謝して…\nいただきます♪", 0, 3, 4788, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115601, "もっと精霊について\n勉強して、みんなを\n守れるように\nなりたいな…", 0, 3, 4789, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 102201, "アイツといると胸が\nぎゅーっとなる…\nこれってもしかして…", 0, 1, 4791, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 102201, "アンタとならきっと\nどんなゲームでも…\nそ、その、たた、\n楽しいって思えるわ！", 0, 2, 4792, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106401, "色んな人と話したら\n記憶探しの手がかりが\n見つかるかも…？", 0, 1, 4793, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106401, "アイスクリームでも\n作ろうかしら？\nバニラにミントに…", 0, 1, 4794, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106401, "ネビアに話しかけると\n逃げられるのよね…\nもう、襲わないのに…", 0, 1, 4795, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106401, "「コッコロさん」、\n「コッコロさん」…\n…間違えないように…", 0, 1, 4796, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106401, "最近少し太ったかも？\nきっとおいしいもの、\n食べ過ぎね…", 0, 1, 4797, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 106401, "ここにいると、\nとても落ち着くわ…\n自分の家、だものね", 0, 1, 4798, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 106401, "みんなと一緒の時間、\nずっと記憶に残したい\n…そう思っているのよ", 0, 1, 4799, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106401, "ドラゴン族って、\nそんなに珍しいの？\nあまり実感ないけど…", 0, 2, 4800, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106401, "料理、練習してるのよ\n…味見してもらえる？\n期待はしないでね", 0, 2, 4801, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106401, "頭を叩いたら、\n記憶が戻ったり\nしないかしら…？\nやらなくていいわよ？", 0, 2, 4802, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106401, "赤ちゃんみたいだった\n時のことは、忘れて…\nお願いだから…", 0, 2, 4803, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 106401, "ひゃんっ！？\nし、しっぽを掴むのは\nダメよ？", 0, 2, 4804, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 106401, "記憶が戻っても…\n私は、あなたたちと\n一緒にいたいわ", 0, 2, 4805, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106401, "おはよう。\n寝ぐせついてるわよ？\n…ふふっ、これでよし", 1, 2, 4806, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106401, "そろそろお昼かしら？\nおいしいごはんを、\nみんなで一緒に…ね", 2, 2, 4807, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106401, "もうこんな時間ね。\nそろそろ休みましょ？\n…ふわぁ…", 3, 2, 4808, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106401, "ふかふか、ぎゅう～…\n…あっ、これはその…\nストレッチしててっ…", 0, 3, 4810, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106401, "あまり、興味ないわ。\nけど…けど…\n…か、かわいい…", 0, 3, 4811, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106401, "いい香りね。\nとっても落ち着くわ…\n…っくしゅっ", 0, 3, 4812, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106401, "冷やす方が得意だけど\nあったかいのも、\nキライじゃないのよ", 0, 3, 4813, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106401, "別に、食いしん坊\nじゃないのよ？\nでも、もう一口だけ…", 0, 3, 4815, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106401, "ん…なるほどね。\n勉強になるわ…", 0, 3, 4816, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115701, "水着の探偵か…\nちょっと恥ずかしい\n気がしてきたよ…", 0, 1, 4818, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115701, "依頼があれば\n海でも山でも\n赴くさ！", 0, 1, 4819, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115701, "今のうちに\n助手くんの秘密を…\nわ…私はなにをっ！？", 0, 1, 4820, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115701, "休暇も時には必要さ\n海でゆっくりと…\nむっ？　事件の気配！", 0, 1, 4821, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115701, "探偵の秘密道具…\n水着だと隠す\n場所がない…！", 0, 1, 4822, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 115701, "そろそろ助手くんが\n現れる時間かな？\nふふっ、楽しみだね", 0, 1, 4823, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 115701, "真実は私と助手くん\n二人だからこそ\n解き明かせるのさ", 0, 1, 4824, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115701, "ひゃああっ！？\nわ、私の不意を突く\nとは…やるね助手くん", 0, 2, 4825, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115701, "さて、なにか気づいた\nことはあるかな？\n言ってごらん♪", 0, 2, 4826, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115701, "この感触…分かった！\n助手くんだね！\nふふっ、やっぱり♪", 0, 2, 4827, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115701, "水着…どうかな？\n探偵らしさは\n残してるつもりだけど", 0, 2, 4828, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 115701, "ん？　依頼かな？\n助手くんの頼みなら\nなんでもお安い御用さ", 0, 2, 4829, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 115701, "助手くん、私と海に\n行かないかな\n…事件じゃないよ？", 0, 2, 4830, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115701, "珈琲でも淹れて\n目を覚ますとしよう\n一緒に飲むかい？", 1, 2, 4831, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115701, "昼食は済ませた？\nなにを食べたか\n当ててあげよう！", 2, 2, 4832, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115701, "夜は謎が深まる時間…\n闇に覆い隠された真実\n必ず暴いてみせる！", 3, 2, 4833, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115701, "さて、推理の時間だ\n…サボってないよ？\n考えてるんだからね？", 0, 3, 4835, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115701, "これはもしや、中に\n秘密のメッセージが？\n…かわいいだけだね", 0, 3, 4836, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115701, "ふむ、なんだか\n頭がスッキリするね\n…閃いた！", 0, 3, 4837, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115701, "ポカポカするね…\n助手くん\n君もおいでよ", 0, 3, 4838, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115701, "はむ、はむ…んぐっ\nた、食べてるところを\n見つめられると～…", 0, 3, 4840, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115701, "秘密の依頼とか\nこういう場所に\n隠したりするよね…♪", 0, 3, 4841, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115701, "これはいい乗り心地だ\n事件現場にこれで登場\n…は、やめておこう", 0, 3, 4843, 10, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115701, "不思議な感触だね…\nでも、いい発想が\n生まれてきそうだ…♪", 0, 3, 4844, 11, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180601, "これはあたしが\n前へ進むために\nあいつがくれた\n大切な力…！", 0, 1, 4845, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180601, "チェンジ・\nプリンセスフォーム！\n練習練習、っと", 0, 1, 4846, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180601, "もうあいつに助けて\nもらいっぱなしの\nあたしは…\n卒業しなきゃね", 0, 1, 4847, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180601, "気ままに、お気楽に\nあいつと冒険する…\nそれって幸せよね", 0, 1, 4848, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180601, "プリンセスフォーム…\nお姫様みたいね\nあたしがお姫様か…\nそっかぁ…ふふ♪", 0, 1, 4849, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 180601, "あいつと繋がって\n生まれたこの絆は\nあたしの宝物だわ", 0, 1, 4850, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 180601, "あたしの命の使い方…\n全て受け止めてくれた\nあいつにだったら…", 0, 1, 4851, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180601, "ふふ～ん♪\nプリンセスフォームの\n天才魔法使い\nキャル様に用かしら？", 0, 2, 4852, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180601, "これからは\nあたしの力で\nなんでもパパっと\n解決してあげるわね！", 0, 2, 4853, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180601, "魔力がみなぎってる…\nねえ、もしかして\n今のあたしって本気で\nすごいんじゃない？", 0, 2, 4854, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180601, "変身の反動で\nお腹が空いても\n魔物料理だけは\nいやだからね！？", 0, 2, 4855, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 180601, "ねぇ、あんたになら\nあたしの心…\n全部預けていいかも", 0, 2, 4856, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 180601, "あいつと一緒に\nいられたら、他には\n何も…って、あああ\nあんたいつの間に！？", 0, 2, 4857, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180601, "早起きも悪くないわね\n読みかけの魔導書\n今のうちに\n読んじゃおうかしら", 1, 2, 4858, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180601, "ねぇ～お腹すいたー\nあんたなんか\n作りなさいよ\n魔物料理以外でね！", 2, 2, 4859, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180601, "ふぁぁ…眠すぎて\n今なら変身しても\nお腹が減らない\n自信があるわ…", 3, 2, 4860, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180601, "あー♪　最っ高！\n休んで魔力を高めて…\nあいつの役に\n立ってあげなきゃね", 0, 3, 4862, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180601, "なにこれかわいい…！\nコレクションにしたく\nなっちゃうじゃない", 0, 3, 4863, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180601, "この香り…すごく\nリラックスできるわ…\nあ、いい感じの魔法\n思いついちゃったかも", 0, 3, 4864, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180601, "あったかい…\n落ち着くわ…\nあいつの手の\nぬくもりみたいね", 0, 3, 4865, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180601, "プリンセスフォーム…\nこ、こんなに\nお腹が減るのね\n甘く見てたわ…！", 0, 3, 4867, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180601, "ま、魔物料理の本！？\n誰よこんな本\n買ったの！　って…\n一人しかいないか", 0, 3, 4868, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115801, "憧れの舞踏会…\n私、きちんと\n踊れるかしら", 0, 1, 4870, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115801, "アン・ドゥ・トロワ\nアン・ドゥ・トロワ\n練習はばっちりね♪", 0, 1, 4871, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115801, "こんなにすてきな\nドレスを着られて\n本当に夢みたいだわ♪", 0, 1, 4872, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115801, "魔法はいつか解ける…\nでも、その時間は心に\n残り続けるわ♪", 0, 1, 4873, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115801, "ガラスの靴は夢へ\n踏み出す魔法の道具…\n私、がんばっちゃう！", 0, 1, 4874, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 115801, "舞踏会であなたと\n一緒に踊れたら\nいいのにな", 0, 1, 4875, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 115801, "舞踏会に行く前に\n運命の王子様には\n出会ってるのよね", 0, 1, 4876, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115801, "どうかしら？\n私、きちんとドレス\n着られてる？", 0, 2, 4877, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115801, "あなたも一緒に\n踊ってみる？\nしゃなりしゃなり♪", 0, 2, 4878, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115801, "あなたにエスコート\nされてきれいなお城を\n歩いてみたいわ♪", 0, 2, 4879, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115801, "どう？　レッスンの\n成果はきちんと\n出てるかしら？", 0, 2, 4880, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 115801, "あなたが隣にいれば\n私も立派なお姫様に\n見えるかしら♪", 0, 2, 4881, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 115801, "私にとって\n一番の魔法はあなたが\nくれる勇気よ♪", 0, 2, 4882, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115801, "さぁ！　今日も一日\n立派な淑女を目指して\nいきましょう♪", 1, 2, 4883, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115801, "お昼ごはん代わりに\nメタモルアップルを\n食べておきましょう", 2, 2, 4884, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115801, "そろそろ魔法が解ける\n時間…でも淑女の\n心得は忘れないわ♪", 3, 2, 4885, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115801, "淑女にも休息は\n必要よね…少しだけ\n休ませてもらおっと", 0, 3, 4887, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115801, "かわいいものに\n囲まれた部屋も\n女の子の夢よね…", 0, 3, 4888, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115801, "すてきな香り…\nお花かしら？\n森で探してみようかな", 0, 3, 4889, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115801, "暖かい…暖炉のそばで\nおとぎ話を聞くのも\nいいわよね♪", 0, 3, 4890, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115801, "レッスンのために\n栄養補給して\nおかないと！", 0, 3, 4892, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115801, "教養を身に着ける\nことも大切よね！\nなになに…", 0, 3, 4893, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115901, "や、やっぱスカートは\n落ち着かねえな…\nなんか、\nスースーするしよ…", 0, 1, 4895, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115901, "せっかくこんな格好\nしてるし、いつもとは\n違う剣術の訓練を\nしてみるかな", 0, 1, 4896, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115901, "さて、他の連中が\n帰ってくる前に、\nなんか料理でも\n作っといてやるか", 0, 1, 4897, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115901, "こ、こんな服着てる\nわけだし、紅茶とか\n楽しんじゃっても\n変じゃないよな！", 0, 1, 4898, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115901, "暇だし運動でも…\nあ、あぶねえ！\nスカートなの忘れてた\n…誰も見てねえよな？", 0, 1, 4899, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 115901, "紅茶が切れてるな…\nよし、あいつと一緒に\n買い物にでも行くか♪", 0, 1, 4900, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 115901, "…あいつがいねえと、\nつまんねえな…ったく\n飯用意してやるから、\n早く帰ってこいよな…", 0, 1, 4901, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115901, "あ、あんまり\nジロジロ見んなよ\n…結構、\n恥ずかしいんだからな", 0, 2, 4902, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115901, "ダンスの相手は\n無理だが、\n剣の訓練なら\nいつでも付き合うぜ！", 0, 2, 4903, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115901, "この服に\n触りたいのか？\nすごい作りだし、\n気持ちは分かるぜ", 0, 2, 4904, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115901, "お、いいところに！\nクッキーを焼く\nところだったんだが、\nおまえも食うか？", 0, 2, 4905, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 115901, "おまえにもこういう服\n似合うんじゃねえか？\n今度あたしが\n選んでやるよ♪", 0, 2, 4906, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 115901, "…おまえの前でなら、\nこういう格好を\nすんのも…\nな、なんでもねえよ！", 0, 2, 4907, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115901, "朝の訓練を始めるか\n時間あるなら、\nおまえも付き合えよな", 1, 2, 4908, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115901, "もうこんな時間か…\n昼飯作るけど、\nなんか\n食いたいものあるか？", 2, 2, 4909, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115901, "眠れないなら、\nホットミルク飲むか？\n体あったまって、\n眠くなると思うぜ", 3, 2, 4910, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115901, "ちょっと休憩して…\nおっと、服にしわが\nできないように\nしねえとな", 0, 3, 4912, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115901, "だ、誰も\n見てねえよな？\n…よ～しよしよし、\nかわいいなおまえー♪", 0, 3, 4913, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115901, "すげぇいい香りだな\nうちの居酒屋にも…\nいや、合わねえか", 0, 3, 4914, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115901, "あったけぇ…\nちょっとだけなら、\n寝ちまっても\nいいよな…？", 0, 3, 4915, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115901, "これうめえな！\nたぶん材料は…よし！\nあたしも作ってみるか", 0, 3, 4917, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 115901, "この本は…\n誰でも分かる作法…か\n最低限このぐらいは\n勉強しとくか…", 0, 3, 4918, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 102001, "ミミがおとなに\nなっても\nおにいちゃんとは\nずーっといっしょ♪", 0, 1, 4920, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 102001, "ミミとくべつなだっこ\nしてほしいな…うん！\nおひめさまだっこ～♪", 0, 2, 4921, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 100401, "次はにいちゃんに\nどんなイタズラしよう\n早く会いたいな～", 0, 1, 4922, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 100401, "ミソギの特別な\nイタズラ見てくれる？\nいいよねっ♪", 0, 2, 4923, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 103601, "大人になったら、\nあの人と何しようかな\n…楽しみだなぁ", 0, 1, 4924, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 103601, "頭を撫でないで\nください！　もう、\n少しだけ、ですからね", 0, 2, 4925, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116001, "くるくる〜♪\nうちのドレス\nかわええなぁ", 0, 1, 4926, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116001, "日差しは苦手やけど\nたまにはひなたの下で\nぽかぽかしたいわぁ", 0, 1, 4927, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116001, "外はお花が咲いてる\n久しぶりにお花を\n活けたいなぁ", 0, 1, 4928, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116001, "お花をたくさん集めて\nギルドハウスに飾れば\n喜んでくれはるかな", 0, 1, 4929, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116001, "あたたかい日は\n紅茶を飲みながら\nゆっくりしたいなぁ", 0, 1, 4930, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 116001, "お稽古事を始めるなら\nうちに言ってな\nどんなことも教えたる", 0, 1, 4931, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 116001, "うちにいろんな夢を\n見せてくれはって\nほんま、おおきにな♪", 0, 1, 4932, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116001, "王子はん\n一緒におとぎの国へ\nお散歩にいきまひょ♪", 0, 2, 4933, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116001, "ふんふんふ〜ん♪\n今な、王子はんに\n花冠を作ってるんよ", 0, 2, 4934, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116001, "王子はんは\nお菓子好きどす？\nうち、作りますわ", 0, 2, 4935, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116001, "うーん。ドレスの柄は\nお花とお星さま\nどっちにしまひょか", 0, 2, 4936, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 116001, "王子はんとの\n思い出が増えるたび\nうちの宝物も増えます", 0, 2, 4937, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 116001, "王子はんと一緒なら\nどんな場所でも\n夢の国になりますわ", 0, 2, 4938, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116001, "夢の中におりたいけど\n王子はんに会いたくて\n起きてもうたわ♪", 1, 2, 4939, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116001, "王子はん一緒に\nアフタヌーンティー\nいかがやろか？", 2, 2, 4940, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116001, "夜の舞踏会…\n夢が広がるわぁ\nもちろんお相手は…♪", 3, 2, 4941, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116001, "ふわふわや〜\nなんや心地よぉて\n眠ってしまいそうや…", 0, 3, 4943, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116001, "かいらしいなぁ\n新しいお洋服でも\n作ってあげまひょか♪", 0, 3, 4944, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116001, "お花やろうか…\nリラックスできて\nええ香りやねぇ", 0, 3, 4945, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116001, "こういう場所で\n絵本を読むと\n気分が出てええなぁ♪", 0, 3, 4946, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116001, "舞踏会を楽しむなら\nたくさん食べて\nたくさん踊りまひょ", 0, 3, 4948, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116001, "うち、この本好きや\n王子さまと結ばれる\nお姫さまのお話なんよ", 0, 3, 4949, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116301, "はぁ、宿題めんどい…\nこれは楽しくない…\nこんな青春いらない…", 0, 1, 4951, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116301, "奨学金の道は遠いなぁ\nメルクリちゃんたちが\nお金くれればいいのに", 0, 1, 4952, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116301, "アオイちゃん元気かな\nチエルに会いたくて\n泣いてるんだろうな…", 0, 1, 4953, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116301, "暇だし鏡でも見るか…\nうわ、顔良っ！\nえ誰この超絶美少女！", 0, 1, 4954, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116301, "ワンツーワンツー♪\nうーん…カルミナの\n振りコピ難しいにゃー", 0, 1, 4955, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 116301, "先輩も運動してます？\nチエルが一緒に走って\nあげてもいいですよ？", 0, 1, 4956, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 116301, "来年のテレフェスも\n今から楽しみだなあ…\nまた一緒にお化け屋敷\n入ってくださいね", 0, 1, 4957, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116301, "ちぇる～ん☆\nフェス仕様でも\nかわいいちえるん！", 0, 2, 4958, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116301, "いま話しかけないで！\n来年の聖学祭ライブの\nイメトレ中なんです！", 0, 2, 4959, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116301, "クロエ先輩のウェア姿\nめっちゃカッコよ！\n思わずおへそ触ったら\nぶたれましたけど", 0, 2, 4960, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116301, "ユニ先輩の体操着姿…\nあれはあれで実に良き\n一生見てられますよね", 0, 2, 4961, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 116301, "先輩って体操着持って\nないんですか？　ハ？\nチエルのを貸せと…？\nえっ…変態かな？", 0, 2, 4962, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 116301, "テレフェスでは先輩も\n地味に頑張ってました\nね。まあ格好よかった\nんじゃないですか…", 0, 2, 4963, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116301, "おはちぇる～ん☆\nさあ今日も楽しいを\n探しに行きますよー！", 1, 2, 4964, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116301, "先輩ビッグニュース！\n今からお昼ごはん\nおごらせてあげます！", 2, 2, 4965, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116301, "こんばんちぇるるん☆\nさあ朝まで楽しいを\n探しに行きますよー！", 3, 2, 4966, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116301, "はぁ…ふかふか～…\n運動の後は…眠たく…\nなり…ま…", 0, 3, 4968, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116301, "ユニ先輩に似合いそ！\nいやクロエ先輩かな？\nチエルは当然すぎて…", 0, 3, 4969, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116301, "やん、いい香り～！\nまるでチエルのために\n存在するみたい！", 0, 3, 4970, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116301, "この火を使って\n聖火リレーしましょう\n先輩、一本抜いて！", 0, 3, 4971, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116301, "あむあむ…ちゃんと\n食べないとユニ先輩\nみたいになっちゃう！", 0, 3, 4973, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116301, "ふむふむ…ちゃんと\n読まないとクロエ先輩\nみたいになっちゃう！", 0, 3, 4974, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 102901, "今なら書ける気がする\n私の本当の\n想いを込めた\nラブソングを！", 0, 1, 4976, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 102901, "輝きを増した私を\nいっぱい見てね！\n…ちょ、ちょっと\n見つめすぎだよ！", 0, 2, 4977, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116201, "テレフェスかぁー…\nあんまいい記憶ないな\nよし、忘れとこ", 0, 1, 4978, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116201, "暇だし鏡でも見るか…\nふふ～ん…いいじゃん\nあ？　何見てんだコラ", 0, 1, 4979, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116201, "ワンツー、ワンツー♪\nああクソ、難しいな…\nあ？　何見てんだコラ", 0, 1, 4980, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116201, "アオイ元気してるかな\nまた絶叫したり白目\n剥いたりしてんのかな", 0, 1, 4981, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116201, "ミフユねえさんが\nバイトリーダーしてる\n工場…うちも行くか…", 0, 1, 4982, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 116201, "うーん…今日の晩御飯\n何にすっかなァ…\nあんたも食べてく？", 0, 1, 4983, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 116201, "来年のテレフェスは\nどーなンだかねえ？\nま、一緒にまた弟の\n相手してやってよ", 0, 1, 4984, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116201, "体操着なんてフツーに\n着てもつまんないし？\nその辺センス出るよね", 0, 2, 4985, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116201, "奨学金の道、遠くね？\nアキノ先輩と仲良く\nなった方が早いかも…", 0, 2, 4986, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116201, "あんた体操着は持って\nないの。ハ？　うちの\n貸せって？　いいけど\n…いや、よくねえよ", 0, 2, 4987, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116201, "にゃんっ！\nや、だからさ…急に\n話しかけんなっての", 0, 2, 4988, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 116201, "え、なに？　この服？\nハ？　似合ってる？\n…え、聞こえなかった\nちょ、もっかい言って", 0, 2, 4989, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 116201, "あんたもテレフェスで\n一応は頑張ってたよね\nま格好よかったんじゃ\nないの。知んないけど", 0, 2, 4990, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116201, "ふぁ～あ…おはーす\n昨日も寝たの遅くて\nまだ目がショボだよ…", 1, 2, 4991, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116201, "なに。あんた昼御飯\nまだなの。じゃ、うち\n適当になんか作ろうか", 2, 2, 4992, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116201, "夜っていいよね。色々\n見えすぎない感じとか\n…この話したっけ？", 3, 2, 4993, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116201, "あ、ヤバ…運動の後に\nこのフカついたのは…\n秒でスヤれる…すやぁ", 0, 3, 4995, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116201, "へーこんなんチエルに\n似合いそうじゃん。\n…いやユニ先輩かな？", 0, 3, 4996, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116201, "お、なんか良さげな\n香りじゃん。これは\n何の…いや分かんね", 0, 3, 4997, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116201, "熱ッ！　この野郎なに\n爆ぜてンだコラァ！\n濡らすぞ木、てめえ！", 0, 3, 4998, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116201, "何コレよく分かんない\nけど、今日モヤシしか\n食ってないから助かる", 0, 3, 5000, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116201, "今日、体操着だしね。\n読書には逆アドかな。\nうん帰ろ。いやあ残念", 0, 3, 5001, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116501, "ビッグボス・イノリ様\nふっふっふ…\nしびれる響きです！", 0, 1, 5003, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116501, "いつかお兄さんを\nあたしの側近に\nしてやるですよ！", 0, 1, 5004, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116501, "ビッグボスになったら\nみんなからたくさん\nお菓子をもらうです！", 0, 1, 5005, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116501, "ふふん！\nこの服はあたしにしか\n着こなせませんよ！", 0, 1, 5006, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116501, "ばーんと立って\nどーんと腕を組む！\nこれがボスのポーズ！", 0, 1, 5007, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 116501, "イノリ様の側近に\n選ばれるなんて\n名誉なことですよ！", 0, 1, 5008, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 116501, "未来の側近として\nあたしをしっかり\n甘やかすですよ！", 0, 1, 5009, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116501, "あたしがお兄さんを\n立派なギャングに\nしてあげますからね！", 0, 2, 5010, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116501, "側近たるもの\nボスを甘やかなさいと\nだめですよ！", 0, 2, 5011, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116501, "なに見てやがるです…\nくぅ、分かりました！\nお菓子は半分こです！", 0, 2, 5012, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116501, "お兄さんも\nあたしがボスなら\nうれしいですよね！", 0, 2, 5013, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 116501, "お兄さんが側近なら\nあたしも安心して\n夜眠れるですよ！", 0, 2, 5014, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 116501, "ビッグボスになったら\nお兄さんにいい思い\nさせてあげますから！", 0, 2, 5015, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116501, "ボスたるもの\n早起きは鉄則ですよ！\nふぁあ…", 1, 2, 5016, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116501, "お腹が空きました…\nご飯食べにいきますよ", 2, 2, 5017, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116501, "ボスたるものぉ〜…\n夜更かしぐらい当然…\nぐー…", 3, 2, 5018, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116501, "は〜これは人をだめに\nしやがるやつですね\nあたし専用です", 0, 3, 5020, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116501, "これはけしからん\nかわいらしさですね\nあたしにぴったりです", 0, 3, 5021, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116501, "高級な香りがするです\nボスになったら\n部屋に置いてやるです", 0, 3, 5022, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116501, "あったかいですねぇ\nちょっとこのまま\n一休憩です…", 0, 3, 5023, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116501, "はむはむ♪\nボスたるもの食べねば\n戦はできませんよ！", 0, 3, 5025, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116501, "ボスなら経済書ぐらい\n…う。難しくて\n読めねーです…", 0, 3, 5026, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 102501, "あなたはドジな私を\n受け入れてくれる…\nだから、頑張れます！", 0, 1, 5028, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 102501, "何でも仰ってください\nちょっぴり大胆な\nことでも…な～んて♪", 0, 2, 5029, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116601, "あーあー、よし\nのどの調子もいいし\nはりきってガイド\nしてくるか！", 0, 1, 5030, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116601, "さあ、そろそろ行くか\nガイドが\n遅刻したんじゃ\n話になんないからな", 0, 1, 5031, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116601, "しれっとドラゴン族が\n旅行に来たり\nしてくれないかな", 0, 1, 5032, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116601, "たぎるガイド先…\n火山、魔物の巣…\nダメだ、そんなとこ\nオレしか行けないや", 0, 1, 5033, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116601, "この前行った絶景\nスポットよかったな\nキレイな景色を見る\nのもいいもんだぜ", 0, 1, 5034, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 116601, "お、今日はカレと\n一緒か。なんだか\nやる気出てきたぜ！", 0, 1, 5035, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 116601, "カレにガイドして\nもらったら\nどこに行っても\n楽しいだろうな", 0, 1, 5036, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116601, "よっしゃあ！\nどっちが上手く\nガイドできるか\n勝負しようぜ！", 0, 2, 5037, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116601, "飯でも食いがてら\nおススメの店の\n下調べでもしようぜ！", 0, 2, 5038, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116601, "なかなかいいだろ？\nこの格好。キミも\n作ってもらったら\nどうだ？", 0, 2, 5039, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116601, "普通は行けない\n場所とか行きたいよな\nキミならどんな\nとこがいい？", 0, 2, 5040, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 116601, "ガイドの仕事は\nキミの方が得意そうだ\n参考にさせてもらうぜ\nよろしくな！", 0, 2, 5041, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 116601, "キミの探してきた店\n結構よさそうじゃん\nそうだ、今度二人で\n下見に行こうぜ！", 0, 2, 5042, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116601, "いい朝だな。絶好の\n仕事日和だぜ！\n今日もよろしくな！", 1, 2, 5043, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116601, "よし、昼飯にしよう\nこの後も激務だし\n飯抜きじゃあ\nぶっ倒れちまうからな", 2, 2, 5044, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116601, "お疲れさん！　さーて\n次はオレと遊んで\nくれよ。まだまだ\nいけるだろ？", 3, 2, 5045, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116601, "ガイドは一日\n歩きっぱなしだからな\nゆっくり休ませて\nもらうぜ", 0, 3, 5047, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116601, "これ、どこで\n売ってるんだろ？\n土産の情報も\n仕入れとかなきゃな", 0, 3, 5048, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116601, "アロマとかよく\n分かんねえけど\n土産にはいいかもな\n覚えておくぜ", 0, 3, 5049, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116601, "体が温まると心も\n熱くなるんだよな\nいい仕事が\nできそうだぜ", 0, 3, 5050, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116601, "食べ放題か\nこれはお客さんも\n喜んでくれそうだな！", 0, 3, 5052, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116601, "へえ、こんなスポット\nあったんだ。やっぱ\nたまには勉強も\nしとくもんだな", 0, 3, 5053, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116701, "バイト先になじめる\nように笑顔の練習を！\nに、にへぇ〜…", 0, 1, 5055, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116701, "働いて働いて働いて\n…いつかその先に\nぼっちの出口が\n見えるのかも…！", 0, 1, 5056, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116701, "バイトのイメトレを\n始めます。ポーズを\n決めて…よし！\n角度を変えて…よし！", 0, 1, 5057, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116701, "バイト仲間に入る\nためには厳しい修業が\n必須と聞きました…！", 0, 1, 5058, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116701, "どんどん技術を磨いて\nバイト仲間として\n認めてもらうんです！\n目指せ職人技…！", 0, 1, 5059, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 116701, "あの人が応援して\nくれる…だから必ず\nバイト仲間の輪に\n入ってみせます…！", 0, 1, 5060, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 116701, "あの人がいてくれると\n勇気が湧いてきます…\nぼっち卒業を夢見ても\nいいんだ、って…♪", 0, 1, 5061, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116701, "緊張するけど\n思い切って声をかけて\nみます…バイト仲間に\n入るために…！", 0, 2, 5062, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116701, "ぼっちの私が\nおもちゃ作りで\n人と共同作業…！\nか、感動です…！", 0, 2, 5063, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116701, "好きなおもちゃって\nありますか？　私は\nもちろんマイフレンド\nくんです！", 0, 2, 5064, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116701, "私が作ったおもちゃも\n誰かの友達に\nなってたら\nいいなぁ…♪", 0, 2, 5065, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 116701, "今日も明日もバイト\nですね！　バイト\n仲間ができる日が\n楽しみです…♪", 0, 2, 5066, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 116701, "脱ぼっちを目指して\nこれからも一緒に\n頑張りましょう！\n目指せ、バイト仲間！", 0, 2, 5067, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116701, "おはようございます\n今日も気合いを入れて\nおもちゃ作りします！", 1, 2, 5068, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116701, "ランチの時間です…♪\nもちろん工場の隅で\nぼっち飯です…！", 2, 2, 5069, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116701, "ふわぁ…今日も\nお疲れさまでした\n明日も働きます～！", 3, 2, 5070, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116701, "バイトで頑張った日は\nふわふわの誘惑に\n勝てません…", 0, 3, 5072, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116701, "こういうおもちゃの\nアイデアも\n素敵かも…♪", 0, 3, 5073, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116701, "いい香りがする\nおもちゃはどうかな…\n森の薬草を使って…", 0, 3, 5074, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116701, "ぽかぽか…\n幸せなひとときですね", 0, 3, 5075, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116701, "もぐもぐ…たくさん\n食べて厳しいバイトを\n乗り切ります…！", 0, 3, 5077, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116701, "ふむふむ…おもちゃの\n設計図を見るのも\n勉強になりますね！", 0, 3, 5078, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116801, "う～ん\n次はどんなたい焼きを\n作ろうかにゃぁ…", 0, 1, 5080, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116801, "あたしが売る\nたい焼きには\n夢が詰まってるにゃ！", 0, 1, 5081, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116801, "いつか絶対に\nたい焼き工場を\n建て直してみせるにゃ", 0, 1, 5082, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116801, "たまにたい焼き以外の\nおやつも食べるにゃ\nたとえば煮干しとか！", 0, 1, 5083, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116801, "悪い商売人がいたら\nファントムキャッツに\n知らせるにゃ！", 0, 1, 5084, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 116801, "キミと一緒にいると\nたい焼きのアイデアが\n次々出てくるにゃ♪", 0, 1, 5085, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 116801, "出来ないことも\nキミと一緒にいれば\n出来る気がするにゃ♪", 0, 1, 5086, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116801, "なんにゃ～？\nお姉さんにかまって\nほしいのかにゃ♪", 0, 2, 5087, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116801, "キミ、いいところに！\nあたしとライバル店へ\n視察にいくにゃ！", 0, 2, 5088, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116801, "くんくん…この匂い！\nたい焼き持ってるなら\nちょうだいにゃ♪", 0, 2, 5089, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116801, "次はどんなたい焼きを\n売ろうかにゃ～\nキミの案も欲しいにゃ", 0, 2, 5090, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 116801, "ファントムキャッツに\n相棒が必要にゃ\nたとえばキミとか…", 0, 2, 5091, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 116801, "相棒！\n…ってキミのこと\n呼んでいいかにゃ？", 0, 2, 5092, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116801, "早起きは三文の徳にゃ\n今日もたい焼き\n売れますよーににゃ！", 1, 2, 5093, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116801, "お腹空いたにゃ…\n賄いのたい焼きを\nいただくにゃ！", 2, 2, 5094, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116801, "猫は夜が本番にゃ\nでもあたしは…\nふわぁ～眠いにゃ…", 3, 2, 5095, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116801, "ふかふかにゃ～\nここで体を丸めて\n一眠りしたいにゃ", 0, 3, 5097, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116801, "これは…！\n猫にも負けない\nかわいさだにゃ…！", 0, 3, 5098, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116801, "いい香りにゃ～\nゴロゴロのどが\n鳴りそうだにゃ～", 0, 3, 5099, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116801, "ふにゃ～…\nこのまま働かずに\nまるまってたいにゃ…", 0, 3, 5100, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116801, "商売は戦いにゃ\nたくさん食べて\nたくさん働くにゃ！", 0, 3, 5102, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116801, "ふむふむ。たい焼きの\nアレンジレシピ集…\nためになるにゃ…", 0, 3, 5103, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 102101, "お芝居の前に\nお兄ちゃんの顔を\n見ると、とっても\n落ち着きますぅ…♪", 0, 1, 5105, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 102101, "お兄ちゃんにもっと\n見てもらいたい…\nだから私…\n強くなりたいです…！", 0, 2, 5106, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116901, "さらなる効率化で\nみんなといい仕事を\nしたいわね！", 0, 1, 5107, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116901, "他のバイトとの\nスケジュールを\n調整しないと…", 0, 1, 5108, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116901, "時給がアップしたわ！\n働きが認められると\nやる気が出るわね", 0, 1, 5109, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116901, "効率的に動ければ\n業務を追加できる…\n燃えてきたわ！", 0, 1, 5110, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116901, "帳簿付けのコツ…\nユカリさんに教わって\nみようかしら", 0, 1, 5111, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 116901, "バイトリーダーとして\nみんなをビシバシ\n鍛えていくわ！", 0, 1, 5112, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 116901, "戦いでもバイトでも\nキミなら安心して\n背中を預けられるわね", 0, 1, 5113, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116901, "どうしたの？\n仕事のことなら\nなんでも相談してね！", 0, 2, 5114, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116901, "さすがは私の弟子ね\nこれなら安心して\n仕事を任せられるわ", 0, 2, 5115, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116901, "みんなのお手本になる\n働きぶりを見せないと\nいけないわね！", 0, 2, 5116, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116901, "キミのこと…もっと\n頼りにしちゃっても\nいいのかしら…？", 0, 2, 5117, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 116901, "ふふ、キミも一人前の\nバイト戦士として\n立派に成長したわね！", 0, 2, 5118, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 116901, "新しいおもちゃの\n企画書を作ってみたの\nちょっと見てくれる？", 0, 2, 5119, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116901, "朝は一番効率がいい\n時間帯よ！\nバリバリ働くわよ！", 1, 2, 5120, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116901, "さ、お昼に行きましょ\n今日の日替わり定食は\n何かしら…楽しみね♪", 2, 2, 5121, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116901, "もうこんな時間…\nちゃんと寝て明日に\n備えなくちゃね", 3, 2, 5122, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116901, "適度な労働と休息の\nサイクルが最大効率を\n生み出すのよ…ふぅ…", 0, 3, 5124, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116901, "はぁ～なんて素敵な\n抱き心地なのかしら…\n最高効率の癒やしね♪", 0, 3, 5125, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116901, "落ち着く香りね…\nなんだか集中力が\nアップした気がするわ", 0, 3, 5126, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116901, "ふぅ…癒されるわね…\n仕事のオンオフの\n切り替えは重要よね", 0, 3, 5127, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116901, "働くためには\nエネルギーをちゃんと\n摂っておかないと", 0, 3, 5129, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116901, "効率を上げるため\n生産管理の勉強も\nしておきたいわね", 0, 3, 5130, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180301, "変身している間に\n色々と試しておこう\nまずは…", 0, 1, 5132, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180301, "飛べるというのは\n便利だな。料理が\n暖かいうちに\n持ち帰れる", 0, 1, 5133, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180301, "プリンセスか…\n柄ではないけれど\n嫌な気はしないな", 0, 1, 5134, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180301, "この力で\n何を成すべきなのか…\n剣に聞いてみよう", 0, 1, 5135, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180301, "変身したとて私は私\n一時的に強くなろうと\n元が弱くてはだめだ", 0, 1, 5136, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 180301, "変身していない時でも\n彼がそばにいると\n力が湧いてくる…", 0, 1, 5137, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 180301, "私の思い…\nいつか伝えてみせるよ\n…きっといつか！", 0, 1, 5138, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180301, "何者かと戦うの？\nそれとも修行？\nどちらだとしても\n付き合うよ", 0, 2, 5139, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180301, "変身し続けたら\nどれだけ空腹に…\n私は試さないからね？", 0, 2, 5140, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180301, "本物のプリンセスに\nなるのは御免だが\nナイトがいて\nくれるのは頼もしいよ", 0, 2, 5141, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180301, "いつかこの力を\n誰かを救うために…\n戦い以外に使える時が\nくることを願うよ", 0, 2, 5142, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 180301, "キミに感じてほしいな\n私に流れてくる\nキミからの力を…\nその…私の、思いも…", 0, 2, 5143, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 180301, "思いを告げる…\nそれがこんなにも\n難しいなんて…\nでも、いつか必ず…！", 0, 2, 5144, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180301, "おはよう！\nいい変身日和だ！\n…ふふっ、冗談だよ", 1, 2, 5145, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180301, "いつもより多く\n昼食を用意しよう\n変身が解けたら大変だ", 2, 2, 5146, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180301, "一日お疲れ様\n私もそろそろ変身を\n解いて休むとするよ", 3, 2, 5147, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180301, "変身してしまうと\n動かずにいても\n空腹になるのかな…？", 0, 3, 5149, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180301, "私もキミたちくらい\n可愛げがある姿に\nなっているかな？", 0, 3, 5150, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180301, "香りに対する感覚も\nいつもより鋭く\nなっているみたいだ…", 0, 3, 5151, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180301, "ありがたいな\n少し足元が\n冷えていたところだ", 0, 3, 5152, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180301, "これは…！\n持ち運べるように\n改良できないかな？", 0, 3, 5154, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 180301, "祖父に貰った\n技法書を読んで\n初心に帰ろう", 0, 3, 5155, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117001, "最強の惚れ薬を作って\nあのおかたの心を\n私の思いのままに…", 0, 1, 5157, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117001, "夏といえば海にスイカ\n花火に水着…\nそして惚れ薬ですわ…", 0, 1, 5158, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117001, "あのおかたの心を\n手に入れるための\n準備を進めましょう\nこの夏こそ確実に…", 0, 1, 5159, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117001, "あのおかたに近づく\n虫どもを、どうやって\n片付けようかしら…\nまったく忌々しいこと", 0, 1, 5160, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117001, "…クスクス\n薬の調合を考えるのは\n至福の時間ですわ…", 0, 1, 5161, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 117001, "あのおかたの全てが\n早く私のものに\nなりますように…", 0, 1, 5162, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 117001, "かき氷も花火も海も…\nあなた様がいてこそ…\nそう、あなた様は\n私の全てなのですわ", 0, 1, 5163, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117001, "二人だけの夏を\n楽しみましょう…？\n…クスクス", 0, 2, 5164, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117001, "あら、あなた様\n私に秘密でどこに\n行こうとして\nいますの…？", 0, 2, 5165, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117001, "海に行きませんか？\nあなた様と一緒に\n過ごすには\n最高の場所ですから…", 0, 2, 5166, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117001, "ちょうどいいところに\nいらっしゃいました\nできたての薬を…\nさぁどうぞ…クスクス", 0, 2, 5167, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 117001, "夏の海のきらめきも\nあなた様の存在が\nなければ色あせて\nしまいますわ…", 0, 2, 5168, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 117001, "私とあなた様は\n結ばれる運命に\nあるのです…\n逃がしませんわ…", 0, 2, 5169, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117001, "起きてすぐに会いに\n来てくださるなんて\nようやく私の伴侶たる\n自覚が…クスクス", 1, 2, 5170, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117001, "お食事はまだですか？\n特製の薬草ランチを\n召し上がれ…クスクス", 2, 2, 5171, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117001, "私の夢を見られる薬を\n差し上げますわ…\nおやすみなさいませ\nあなた様…クスクス", 3, 2, 5172, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117001, "いい肌触りですわ…\n考え事にもぴったり\n眠くなるのが\n玉に瑕ですが…", 0, 3, 5174, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117001, "こういったものに\n揮発性の薬を仕込む\nのも一つの手ですわね", 0, 3, 5175, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117001, "この香り…開発中の\n薬に使えそうな気が\nしますわ…クスクス", 0, 3, 5176, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117001, "あのおかたの\nぬくもりを\n思い出しますわ…", 0, 3, 5177, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117001, "美味しい…この味なら\n薬を混ぜても違和感は\nなさそうですわ…", 0, 3, 5179, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117001, "この薬の調合法…\n今度試してみましょう\nもちろんあのおかたに\n飲んでいただいて…", 0, 3, 5180, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117001, "二人乗りができると\nいいですわね…", 0, 3, 5182, 10, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117001, "ふかふかですわ…\nあのおかたの分も\n欲しくなります…", 0, 3, 5183, 11, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 101701, "今日はキミと\nいつまでも歌って\n踊りたい気分さ～♪", 0, 1, 5184, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 101701, "あは～♪\nキミの隣は、故郷より\n落ち着くかも～…♪", 0, 2, 5185, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117101, "この水着…\n一番に弟くんに\n見せたいな♪", 0, 1, 5186, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117101, "弟くんをこっそり\n海に運んで…\nサプライズしちゃお♪", 0, 1, 5187, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117101, "いつもと違う髪型に\nしてみたけど…\n気に入ってくれるかな", 0, 1, 5188, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117101, "海は楽しみだけど\n日焼けしないように\n気を付けなきゃね", 0, 1, 5189, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117101, "まだかな、弟くん\n水着のお姉ちゃんが\nお出迎えだよっ♪", 0, 1, 5190, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 117101, "海が見えるおうちで\n弟くんと一緒に\n暮らしたいなぁ～", 0, 1, 5191, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 117101, "弟くんの水着…\nどんな感じかなぁ\n…あ、想像してたら\nドキドキして…えへへ", 0, 1, 5192, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117101, "こーら、水着の\nお姉ちゃんは\nおさわり禁止♪\n…なんてね？", 0, 2, 5193, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117101, "なぁに？\nふふっ、何回でも\n呼んでいいんだよ♪", 0, 2, 5194, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117101, "海で迷子に\nならないように\n手を繋ごうね♪", 0, 2, 5195, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117101, "お姉ちゃんが\n海の楽しみ方を\n教えてあげるねっ！", 0, 2, 5196, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 117101, "わっ、弟くん\nどこ触ってるの？\nも～驚いちゃったぞ♪", 0, 2, 5197, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 117101, "お姉ちゃん、海でも\nずっとキミを見てるよ\n…安心してね", 0, 2, 5198, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117101, "まだ眠そうだね？\nほら、海が待ってるよ\n早く起きて起きて♪", 1, 2, 5199, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117101, "大丈夫、疲れてない？\n海で遊び疲れた時は\n素直に休憩しようね", 2, 2, 5200, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117101, "ほら、早く寝ないと\n明日もお姉ちゃんと\n遊べないぞ♪", 3, 2, 5201, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117101, "だらけたお姉ちゃんは\n弟くんには\n見せられないな～", 0, 3, 5203, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117101, "ふふっ、かわいいね♪\n弟くんが持つと\nもっとかわいいよっ♪", 0, 3, 5204, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117101, "いい香りだね…\n一味違うお姉ちゃんに\nなれそう…♪", 0, 3, 5205, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117101, "あったか～い♪\nほら、弟くんも\nおいでよ♪", 0, 3, 5206, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117101, "よく食べて、いつでも\n弟くんの呼び声に\n応える準備を…！", 0, 3, 5208, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117101, "弟くんの水着の写真\nこの辺りに隠して\nおこうかな", 0, 3, 5209, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117101, "この子の背に乗って\nお姉ちゃんと弟くんの\n楽園へ…な～んて♪", 0, 3, 5211, 10, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117101, "海を感じる感触だね\n弟くんとの楽しい夏を\n思い出しちゃうな…♪", 0, 3, 5212, 11, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117201, "この夏、誰よりも\nキラキラしてみせる！\nだって私は\nアイドルだから！", 0, 1, 5213, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117201, "日傘にアームカバー\n帽子にサングラス…\n紫外線対策は完璧！", 0, 1, 5214, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117201, "とっておきの\nサマーソングを\nみんなに届けるよ！", 0, 1, 5215, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117201, "なんにもしないのって\n苦手かも…\n彼、早く来ないかな？", 0, 1, 5216, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117201, "ラムネって\n夏って感じするよね\n後で彼と飲もっと♪", 0, 1, 5217, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 117201, "夏は恋の季節！\n燃えるような恋…\nいつかしたいな", 0, 1, 5218, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 117201, "今年の夏の目標は\n…彼との思い出を\nいっぱい作ること！", 0, 1, 5219, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117201, "私の水着写真集\n見てくれたんだ！\nどう、かわいかった？", 0, 2, 5220, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117201, "スイカ割りしたいの？\nいいね、楽しそう！\nみんなも誘おうよ♪", 0, 2, 5221, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117201, "ねぇ、見て見て！\n水着の映える\nポーズ考えたんだ！", 0, 2, 5222, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117201, "ライブ中に\n花火を打ち上げたら\n絶対盛り上がるよね！", 0, 2, 5223, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 117201, "最近、忙しいけど…\nキミの顔見たら\n疲れなんて\n消えちゃった！", 0, 2, 5224, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 117201, "この夏もいーっぱい\nドキドキさせるから！\n覚悟しててよね♪", 0, 2, 5225, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117201, "外は朝から暑いね\nけど、負けないから！\nお仕事、頑張るぞー！", 1, 2, 5226, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117201, "自分へのご褒美に、\nアイス食べちゃお♪\nキミも一緒に食べよ！", 2, 2, 5227, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117201, "夏の夜って\nワクワクするね♪\nホタル見に行こうよ！", 3, 2, 5228, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117201, "ふわぁ…眠い…\n仕事とかの疲れ、\nたまってるのかな…？", 0, 3, 5230, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117201, "私のぬいぐるみが\nグッズになったら…\n彼にかわいがって\nもらえるかな？", 0, 3, 5231, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117201, "この香り…\n夏限定のやつでしょ？\nいいセンスしてるね♪", 0, 3, 5232, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117201, "暑～い！\nけど…汗かいて\n気持ちいいかも！", 0, 3, 5233, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117201, "レッスンの後のご飯は\n特別おいしい♪\n…もうひとつだけ\n食べちゃおっかな", 0, 3, 5235, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117201, "あ、この前撮影した\n水着グラビアだ\nうんうん、いい感じ！", 0, 3, 5236, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117201, "この子、もふもふだね\n触るのクセになりそう\nもふもふもふもふ…", 0, 3, 5238, 10, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117201, "わ、かわいい♪\n今度のグラビア撮影で\nこれ使ってもいい？", 0, 3, 5239, 11, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117401, "水着っていいですよね\n海に浮かぶ一輪の花\nって感じがして！", 0, 1, 5240, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117401, "自分の水着を作るのも\n誰かの水着を作るのも\n楽しいですね♪", 0, 1, 5241, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117401, "水着の最終チェック！\n伸縮性や動きやすさは\n…いい感じですね！", 0, 1, 5242, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117401, "振り付けや衣装作りに\n歌や踊りの練習…\n…やることがいっぱい", 0, 1, 5243, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117401, "はぁ…レイ様の水着…\n私に作らせて\nもらえないかなぁ～…", 0, 1, 5244, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 117401, "騎士さんのことは\n気に入らないですけど\n別に嫌いという\nわけじゃなくて…", 0, 1, 5245, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 117401, "騎士さん暇そうですね\nに、荷物持ちとして\n誘ってみようかな…", 0, 1, 5246, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117401, "騎士さんいいところに\nこの衣装\nどう思います？", 0, 2, 5247, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117401, "何見てるんですか？\nもしかしてこの水着の\n良さが分かります！？", 0, 2, 5248, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117401, "真夏でも涼しい…\nそれも水着の良さの\n一つですね", 0, 2, 5249, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117401, "仕立て屋も\nアイドルも\n諦めません！", 0, 2, 5250, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 117401, "夏と言えば\nバカンスですけど…\n予定、あるんですか？", 0, 2, 5251, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 117401, "う～ん…騎士さんを\n海へ誘うには…って\nなんでもないですよ！", 0, 2, 5252, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117401, "ふー…夏はやっぱり\n朝が一番過ごしやすい\nですね～！", 1, 2, 5253, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117401, "暑い時こそ海です！\n体力づくりのためにも\n泳ぎにいきますよ！", 2, 2, 5254, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117401, "外で虫が鳴いてる…\n夏って感じがして\n風流ですねぇ…", 3, 2, 5255, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117401, "少し休憩です…\nこのまま寝ないよう\n気をつけないと…", 0, 3, 5257, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117401, "か、かわいい…！\nこの感動を次の\nステージ衣装にも…！", 0, 3, 5258, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117401, "いい香りですね～\n波の音が聞こえて\nきそうです…", 0, 3, 5259, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117401, "泳いだ後は\n少し寒いので\nちょうどいいですね", 0, 3, 5260, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117401, "夏バテしないように\n栄養はしっかり\n取らないと！", 0, 3, 5262, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117401, "次のトレンドは…\nふむふむ、やっぱり\nこう来ましたか…", 0, 3, 5263, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117401, "デザインはもちろん\n触り心地も\n最高ですね♪", 0, 3, 5265, 10, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117401, "夏の撮影スポットに\n活用できそうですね\nさっそく衣装考えて…", 0, 3, 5266, 11, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 104001, "えへへ…♪\n団長さんが初めての\n友達で本当に良かった", 0, 1, 5267, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 104001, "あっ、団長さん！\n今日のＢＢ団の活動\nなんですけど…二人で\n大縄跳びしませんか！", 0, 2, 5268, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117301, "この衣装で\nライブするのは\n恥ずかしいな…", 0, 1, 5269, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117301, "せっかくの夏だし\n肝試しに\n行ってみたいかも…", 0, 1, 5270, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117301, "歌もダンスも\nより上を目指すために\n夏合宿をしよう…！", 0, 1, 5271, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117301, "夏の暑さにも負けない\n熱いライブに\nしてみせる…！", 0, 1, 5272, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117301, "新曲のアイデア\nまとめたいな…\n少し海辺を散歩しよう", 0, 1, 5273, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 117301, "かき氷、食べたいな\n…あの人は何味が\n好きなんだろう？", 0, 1, 5274, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 117301, "水着、似合ってるって\n言ってもらえるかな…", 0, 1, 5275, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117301, "サインして、ですか？\nいいですけど…\n少し照れますね…", 0, 2, 5276, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117301, "少し待ってください\n今、新曲の楽譜を\n書いているので…", 0, 2, 5277, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117301, "なんでしょう？\nもしかして…\nボイトレに興味が？", 0, 2, 5278, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117301, "今度のライブでは\n夏らしい爽やかな歌を\nファンにお届けします", 0, 2, 5279, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 117301, "…水鉄砲で遊ぼう？\nふふ。楽しそうですね\nお手柔らかに…♪", 0, 2, 5280, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 117301, "あなたの声を\n聴いていると、\n何だか落ち着きます…", 0, 2, 5281, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117301, "今日も暑くなりそう…\nお互いしっかりと\n水分補給しましょうね", 1, 2, 5282, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117301, "お腹空きましたよね\n海の家で、\nなにか買ってきます", 2, 2, 5283, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117301, "夜の海で月明かりと\n波の音を楽しもうかと\n…一緒に行きますか？", 3, 2, 5284, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117301, "レッスンだけでなく\nリラックスすることも\n大事、ですよね", 0, 3, 5286, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117301, "ふふ、かわいい\n精霊も負けないくらい\nかわいいですけどね", 0, 3, 5287, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117301, "夏らしい爽やかで\nいい匂い…あなたに\nピッタリな香りですね", 0, 3, 5288, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117301, "海に入った後は、\n体が冷えてしまいます\n一緒に温まりましょう", 0, 3, 5289, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117301, "アイドルとして、\n食べすぎには\n気をつけないと…", 0, 3, 5291, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117301, "夏は恋の季節…\nガンガン攻めるべし…\nなるほど…", 0, 3, 5292, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117301, "この子に乗りながら\nライブをする…\nというのはどうですか", 0, 3, 5294, 10, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117301, "まるで人魚になった\n気分…なんて、ふふ", 0, 3, 5295, 11, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118301, "お姉ちゃんとして\nシオリンにかっこいい\nとこ見せなくっちゃ☆", 0, 1, 5296, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118301, "いつかシオリンと\n一緒に空を飛んで\n空中散歩してみたいな", 0, 1, 5297, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118301, "すこし眠くなってきた\nかも…シオリンと\nお昼寝しようかな", 0, 1, 5298, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118301, "お散歩してこよっと！\nシオリンとお出かけ\nするのにいい場所\n探さなくっちゃ！", 0, 1, 5299, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118301, "シオリンに過保護って\n言われちゃった…♪\nそれでも心配しちゃう\nのが姉心…だよね？", 0, 1, 5300, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 118301, "気持ちいい日差し！\nシオリンと彼と、\nお昼寝したら\n気持ちよさそ～♪", 0, 1, 5301, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 118301, "今度天気がいい日には\nシオリンと彼を誘って\n一緒にお出かけ\nしたいな～♪", 0, 1, 5302, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118301, "今日はご機嫌なんだ♪\nだって、シオリンの\n調子がいいんだもん☆", 0, 2, 5303, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118301, "今からシオリンと\nピクニックに\n行くんだ♪　そうだ！\nキミも一緒にどう？", 0, 2, 5304, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118301, "わっ、びっくりした！\nシオリンから借りた本\n読むのに集中\nしてたから…", 0, 2, 5305, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118301, "うん？　どうしたの？\nあっ、もしかして\nこの服、気になる？", 0, 2, 5306, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 118301, "シオリンもキミも元気\nいっぱいだと、私も\n元気になっちゃうよ☆", 0, 2, 5307, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 118301, "シオリンの前だけ\nじゃなくて、キミにも\nかっこいい私の姿を\n見てほしいな☆", 0, 2, 5308, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118301, "おはよう！　えへへっ\n今日は早起きなんだ♪\nシオリンにかっこ悪い\nとこ見せたくないから", 1, 2, 5309, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118301, "おなかすいたなぁ～…\nシオリンと一緒に\nおいしいもの食べに\n行こうかな！", 2, 2, 5310, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118301, "もうこんな時間！？\nシオリンと一緒だと\n時間が早く\n過ぎていっちゃうね！", 3, 2, 5311, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118301, "この快適な寝心地…！\nシオリンにもおすすめ\nしなきゃ…！", 0, 3, 5313, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118301, "わあ、かわいい！\nそれにさわり心地も\nいいし、抱き枕に\nちょうどいいかも！", 0, 3, 5314, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118301, "いいにおい…♪\nなんだかリラックス\nできて…すやぁ…", 0, 3, 5315, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118301, "ぽかぽか～☆\nシオリンと一緒に\nぬくぬくしたら\n気持ちよさそう♪", 0, 3, 5316, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118301, "仲のいい人が一緒だと\nつい食が進んじゃうね\nえへへ…♪", 0, 3, 5318, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118301, "ふむふむ…この本…\nシオリンが好きかも！\n今度おすすめしよっと", 0, 3, 5319, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118401, "お姉ちゃん、\n私のために無理は\nしてないかな…？", 0, 1, 5322, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118401, "新しく買った絵本…\nこういうのなら\nお姉ちゃんと楽しく\n読めるよね", 0, 1, 5323, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118401, "ニャットちゃんを\nここに連れてきて\nみんなで遊べないかな", 0, 1, 5324, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118401, "本屋さんに行こうかな\nみんなで読める\n面白い本が\nあるといいなぁ", 0, 1, 5325, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118401, "たまには…読むだけ\nじゃなくて、自分で\nお話を書いて\nみようかな…？", 0, 1, 5326, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 118401, "時々…お姉ちゃんと\nあの人が似てるって\n思うことがあります…", 0, 1, 5327, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 118401, "お姉ちゃんとあの人…\n優しくて頼もしい\n二人に囲まれて…\n私、幸せ者だなぁ…", 0, 1, 5328, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118401, "どうかしましたか？\nもしかして…あなたも\n一緒にこの本を\n読みたい…とか？", 0, 2, 5329, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118401, "けほっ…けほっ…\nあっ、心配いりません\n飲み物を飲んで\nむせただけですから", 0, 2, 5330, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118401, "この格好…\n似合ってますか…？\nえへへ、照れますね…", 0, 2, 5331, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118401, "なぁに、お姉ちゃん？\n…あっ、すみません\n読書に集中していて…\n間違えちゃいました…", 0, 2, 5332, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 118401, "お姉ちゃん、時々無理\nしちゃうから…\nあなたも一緒だと\n安心できます", 0, 2, 5333, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 118401, "お姉ちゃんとあなたと\n頼れる人に囲まれて\n私は幸せものですね", 0, 2, 5334, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118401, "なんだか朝から\n調子がいい…今日は\nいい日になりそうです", 1, 2, 5335, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118401, "せっかくだから、\nお姉ちゃんを誘って\nお外でランチも\nいいかも", 2, 2, 5336, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118401, "お姉ちゃんに心配\nかけないように…もう\nちょっと本を読んだら\n寝なくちゃね", 3, 2, 5337, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118401, "ふんわり柔らかくて\n気持ちいい手触り…\nお姉ちゃんが寝るのに\nちょうどよさそう", 0, 3, 5339, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118401, "ふふっ、かわいい♪\n絵本の物語みたいに\nおしゃべりできない\nかな…？", 0, 3, 5340, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118401, "素敵な落ち着く香り♪\nなんだかお姉ちゃん\nみたい、ふふっ", 0, 3, 5341, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118401, "あったかくてほっと\nするなぁ\nここでゆっくり\n読書しようかな", 0, 3, 5342, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118401, "この味…\nお姉ちゃんが好きそう\n少しもらって、\n持って行こうかな…？", 0, 3, 5344, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118401, "この本みたいな、\n心躍る大冒険も\nお姉ちゃんと一緒なら\nできちゃいそう", 0, 3, 5345, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117501, "いい部屋ね\nここを私の分院に\nするのもいいかも\nしれないわね…♪", 0, 1, 5348, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117501, "珍しいカンポーが\n手に入ったから\n色々試してみたいわ\n色々と、ね♪", 0, 1, 5349, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117501, "この服を着ると\n自分が医者だって\nより実感するわね\nまあ、闇医者だけどね", 0, 1, 5350, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117501, "新薬開発のために\n薬草を採取して\nおこうかしら", 0, 1, 5351, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117501, "いつ急患が来ても\n対応できるように\nしておきましょうか", 0, 1, 5352, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 117501, "あの子の研究資料も\nだいぶ増えてきたわね\nちゃんとまとめて\nおかないと♪", 0, 1, 5353, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 117501, "あの子は大事な\n研究対象。だから\nいつまでも健康で\nいてもらわないとね♪", 0, 1, 5354, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117501, "薬に興味があるの？\nなら、今度ゆっくりと\n教えてあげるわね", 0, 2, 5355, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117501, "少し時間があいたから\n一緒に新薬の実験を\nしましょうか♪", 0, 2, 5356, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117501, "どうしたの？\nまさか急患…！？\nあら、違うのね\nならよかったわ", 0, 2, 5357, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117501, "具合が悪い時はすぐに\n言うのよ。君には\nいつでも健康でいて\nほしいから", 0, 2, 5358, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 117501, "少し仮眠したいわね\n肩…いいえ、膝を\n貸してもらえる？", 0, 2, 5359, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 117501, "たまには…君の隣なら\n闇医者も悪い研究者も\nお休みにするのも\n悪くないわね♪", 0, 2, 5360, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117501, "たまには朝の陽ざしを\n浴びるのも…\n悪くないわね", 1, 2, 5361, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117501, "天気がいい日は\nいい薬草が採れるの\n君も集めるの、\n手伝ってくれる？", 2, 2, 5362, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117501, "いけない、もうこんな\n時間なのね\n診察に没頭すると\n時間感覚がなくなるわ", 3, 2, 5363, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117501, "もう少し固い方が\n私好みだけど…\n悪くない寝心地ね", 0, 3, 5365, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117501, "このぬいぐるみ…\n闇医者として\n興味深いわね…", 0, 3, 5366, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117501, "落ち着く香りね\n私も診察室で\n使ってみようかしら", 0, 3, 5367, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117501, "冷えは病につながるわ\nしっかり温めて\n体をいたわりましょ", 0, 3, 5368, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117501, "おいしい♪\n研究のお供に\nもってこいの\n味だわ", 0, 3, 5370, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117501, "医学は日進月歩…\n闇医者とはいえ\n最新の論文には\n目を通しておかないと", 0, 3, 5371, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117601, "白衣の天使かあ\nまさにボクのための\n呼び方だね", 0, 1, 5400, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117601, "美しさとは\n日々の努力が\n作るものなんだよ", 0, 1, 5401, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117601, "大変だ…\nボクは美しく\nなりすぎたかも", 0, 1, 5402, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117601, "ボクのかわいさで\n癒やされて\nみんな健康に\nなっちゃうね", 0, 1, 5403, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117601, "どんな服装をしても\nボクの美しさは\n変わらないね", 0, 1, 5404, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 117601, "これからはボクも\n美の伝道師として\nがんばらないとね♪", 0, 1, 5405, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 117601, "ボクのかわいい笑顔を\n近くで見る以上の\n癒やしはないよね♪", 0, 1, 5406, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117601, "どう？　看護師姿の\nボクもかわいいでしょ\nもっと近くで見る？", 0, 2, 5407, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117601, "ほら！　美しいボクの\n看護師姿を見たら\n元気でちゃうでしょ？", 0, 2, 5408, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117601, "白衣の天使のボクも\n最高に美しいね\n存分に癒されてよ♪", 0, 2, 5409, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117601, "具合が悪いの？\nならボクの美しい顔を\n処方してあげる♪", 0, 2, 5410, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 117601, "さあ今日もボクと\n一緒に美の追求に\n付き合ってもらうよ♪", 0, 2, 5411, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 117601, "かわいい天使のボクが\nいつもそばにいるんだ\nキミって幸せ者だね♪", 0, 2, 5412, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117601, "さすがボク\n目が覚めるような\n美しさでしょ？", 1, 2, 5413, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117601, "美は食で作られる\n美容にいいものを\n食べようよ！", 2, 2, 5414, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117601, "ふああ…早く寝よ？\n夜更かしは美容の\n大敵だからね", 3, 2, 5415, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117601, "ふう～…\nリラックスする\nボクも美しい", 0, 3, 5417, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117601, "ボクが抱きしめると\n普段よりかわいく\n見えちゃうね♪", 0, 3, 5418, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117601, "美しいボクは\n常にいい香りを\nしていなくちゃね", 0, 3, 5419, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117601, "光がボクを照らしてる\nんじゃなくて、ボクが\n光を輝かせてるのさ", 0, 3, 5420, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117601, "バランスのいい食事が\n美しいボクを\n作るのさ", 0, 3, 5422, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117601, "ふむふむ…なるほど\nこうすればより美しく\nなれるかもしれない", 0, 3, 5423, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 105701, "仲間のためなら\nきっと…どこまでも\n強くなれるよね", 0, 1, 5425, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 105701, "プリンセスフォーム？\n私と同じような力を\n持ってる人がいるの？\n会ってみたいな～！", 0, 2, 5426, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118001, "ここの空気は、\nどうもゆるすぎて…\n落ち着きませんわね", 0, 1, 5427, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118001, "陛下のお越しになる\n別宅としては、\n少し警備が\n甘すぎでは？", 0, 1, 5428, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118001, "あらあら？\nサッシにホコリが！\nちょっとネコ助！\n掃除は念入りに！", 0, 1, 5429, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118001, "いつまで入り浸って\nいるのかと、\n秘書に叱られて\nしまいましたわ…", 0, 1, 5430, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118001, "もういっそ、\nここに間借りして\n仕事しようかしら…？", 0, 1, 5431, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 118001, "邪魔している身ですし\n家事くらい\nお手伝いさせなさいな", 0, 1, 5432, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 118001, "本当は、意外と\n居心地が良いのかも…\n陛下の気持ち、\n少し分かりましたわ", 0, 1, 5433, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118001, "ちょっ…わたくしを\n叩いても、ホコリなど\nあまり出ませんわよ！", 0, 2, 5434, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118001, "陛下っ？\nなんだ…貴方でしたの\n用件は手短にお願いね", 0, 2, 5435, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118001, "別に、好き嫌いは\nありませんけれど…\n魔物料理って、意外と\n美味なものですわね", 0, 2, 5436, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118001, "言っておきますけれど\nわたくしは商売人です\n荒事は専門外ですわよ", 0, 2, 5437, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 118001, "プリンセスナイトの\n能力…なにか儲け話に\n使えませんかしらね", 0, 2, 5438, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 118001, "貴方がたのことも、\n少しくらいは\n信用してないことも\nありませんでしてよ", 0, 2, 5439, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118001, "おはようございます\nさ、今日もバリバリ\n働きますわよ！", 1, 2, 5440, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118001, "お昼ですわね\n栄養補給をしつつ\n働きますわよ！", 2, 2, 5441, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118001, "定時ですって？\n関係ありません！\nわたくし、まだまだ\n働きますわよ～っ！", 3, 2, 5442, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118001, "うっ…いけませんわ\n日頃の疲れが…\nふわぁ…", 0, 3, 5444, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118001, "もふもふ…ふふっ♪\n…なんですのっ、\nなにかおかしくて！？", 0, 3, 5445, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118001, "いい香りですわね\nわたくしの執務室にも\n置きたいくらいですわ", 0, 3, 5446, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118001, "いけませんわ…\nこんなに暖かいと\n日頃の疲れが…ふわぁ", 0, 3, 5447, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118001, "もう一つ食べたら\n仕事に戻りますわよ？\nあと一つだけ…", 0, 3, 5449, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118001, "商人にとって\n知識は武器ですわ\n陛下のため、貴方も\nもっと勉強なさい？", 0, 3, 5450, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117701, "故郷のお菓子を\nいろいろ作ったんだ～\nみんなに\n配ってくるさ～♪", 0, 1, 5452, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117701, "彼ってどんな仮装が\n似合うかな～？\nシーサーとか？", 0, 1, 5453, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117701, "カボチャチャンプルー\n作ってみよっと！\nきっとおいしいさ～♪", 0, 1, 5454, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117701, "視線を感じるさ～\nもしかして幽霊？\nおしゃべりするさ～♪", 0, 1, 5455, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117701, "さっき作った\nサーターアンダギー、\n早く彼に\n食べてほしいさ～♪", 0, 1, 5456, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 117701, "彼のために、\n特別なイタズラを\n考えとくさ～♪", 0, 1, 5457, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 117701, "いつか彼と、\n故郷のハロウィンを\n楽しみたいさ～♪", 0, 1, 5458, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117701, "ハロウィンは\n私たちが守るさ～♪\nさ、見回りに行こ！", 0, 2, 5459, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117701, "この制服、\nハロウィンらしくて\n大好きさ～♪", 0, 2, 5460, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117701, "イタズラする気ね～？\nどんなことされるのか\n楽しみさ～♪", 0, 2, 5461, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117701, "カボチャのランタン、\n一緒に作ろ！\n絶対楽しいよ～♪", 0, 2, 5462, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 117701, "お菓子くれるの？\nあは～、ありがとー♪\nもぐもぐ…\n優しい味がするさ～♪", 0, 2, 5463, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 117701, "キミと一緒に過ごす\nハロウィンは、\nドキドキとワクワクが\n止まらないさ～♪", 0, 2, 5464, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117701, "ハロウィンは\n朝から大忙しさ～！\nお菓子作り、\n手伝ってほしいさ～", 1, 2, 5465, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117701, "お化け屋敷に行こ！\n一緒にいっぱい\nドキドキしようね～♪", 2, 2, 5466, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117701, "ここからが\nハロウィン本番さ～！\n幽霊たちと\nいっぱい遊ぶさ～♪", 3, 2, 5467, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117701, "ちょっと休んだら、\nお菓子作りを\n再開するさ～♪", 0, 3, 5469, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117701, "でーじかわいいね～♪\nこの子に仮装させたら\nきっと似合うさ～♪", 0, 3, 5470, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117701, "お菓子みたいに\n甘い匂い…\nハロウィンらしい\nいい香りさ～♪", 0, 3, 5471, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117701, "ランドソルの秋って\n冷えるけど…\nこれがあれば\n体も心もぽかぽかさ～", 0, 3, 5472, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117701, "故郷のご飯も\nおいしいけど、\nこっちのご飯も\nでーじおいしいさ～♪", 0, 3, 5474, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117701, "パンプキンパイの\nレシピは…なるほど～\n勉強になるさ～♪", 0, 3, 5475, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117701, "全部おいしそうで\nどれから食べるか\n迷っちゃうさ～♪", 0, 3, 5477, 12, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117801, "ムっ！　幽霊の気配を\n感じるデス！\nさあ姿を現すデス！", 0, 1, 5479, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117801, "ムムム…\n妖魔の力を忍法に\n使えないでショウか…", 0, 1, 5480, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117801, "怪しい妖魔を\n成敗するデス！\nハロウィンの平和は\nワタシが守りマスよ！", 0, 1, 5481, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117801, "ワタシの祭囃子が\n聞こえマスかー？\nさあ集まるデース！\n陽気に踊りまショウ！", 0, 1, 5482, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117801, "お祭りは\n楽しんだ者勝ちデス！\n踊らにゃ\nソンソンデス！", 0, 1, 5483, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 117801, "幽霊になる前に\n天下統一を\n果たしたいデスね", 0, 1, 5484, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 117801, "あの世に行っても\nショーグンは\nワタシの大事な\nショーグンデス！", 0, 1, 5485, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117801, "ショーグン！\n悪霊退治に\n出かけるデース！", 0, 2, 5486, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117801, "妖魔を家来に\nしてみるのは\nどうデス？", 0, 2, 5487, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117801, "マントを使って\nウツセミの術の精度を\n高めるデス！", 0, 2, 5488, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117801, "ハロウィンにちなんだ\n新しい忍法…\nすなわち妖忍法の\n開発を急ぐデス！", 0, 2, 5489, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 117801, "あの世でも\nショーグンに\nお仕えしマスよー！", 0, 2, 5490, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 117801, "ショーグンもワタシと\nハロウィンパレードを\n踊り明かしまセンか？", 0, 2, 5491, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117801, "おはようございマス！\n天下統一に向けて\n急いで準備するデス！", 1, 2, 5492, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117801, "お腹減ってまセンか？\nハロウィンスイーツで\n休憩するデ〜ス", 2, 2, 5493, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117801, "夜更かしすると幽霊が\nでるかもしれまセン…\nヒュ〜ドロロロ〜…", 3, 2, 5494, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117801, "もふもふなところが\nわたあめちゃんに\n似ていマス。わんっ！", 0, 3, 5496, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117801, "ぬいぐるみや人形には\n魂が宿るという\n言い伝えがありマス…\nマカフシギデ〜ス", 0, 3, 5497, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117801, "ウ〜ム…\nなんとも怪しい\n香りがしマスね！", 0, 3, 5498, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117801, "パレードの熱気に\n負けないくらい\n熱いデス！", 0, 3, 5499, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117801, "ついつい食べすぎて\nしまうのは\n妖魔のせいデスか！？", 0, 3, 5501, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117801, "トーゴク妖怪の\n図鑑デス！　個性的な\n妖怪ばかりデスね！", 0, 3, 5502, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117801, "ハロウィンの甘味を\n集めまシタよ！\nショーグンへの\n貢ぎ物デス！", 0, 3, 5504, 12, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 101301, "彼と二人で\n買い物に…って…\nこれってデートに\nなっちゃうのかな！？", 0, 1, 5531, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 101301, "とうっ！　どうどう？\n今の決めポーズ！\nかっこいいよね♪", 0, 2, 5532, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117901, "ヒデサイとおそろの\nハロウィンコスとか\nしてみたいな～", 0, 1, 5533, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117901, "カボチャ料理かぁ…\n作ったらヒデサイ\n食べてくれるかな～", 0, 1, 5534, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117901, "ハロウィンスイーツ…\nどれもおいしすぎて\n激ヤバなんですけど！", 0, 1, 5535, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117901, "霊界ってどんなとこ\nなんだろ～\nわりと近所だったり？", 0, 1, 5536, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117901, "服をハロウィンっぽく\nアレンジするのも\nたのしーよね♪", 0, 1, 5537, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 117901, "ヒデサイと一緒に\nハロウィンパーティー\n…えへへ、楽しみ～", 0, 1, 5538, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 117901, "ヒデサイといると\nドキドキする…\nなんでだろ～？", 0, 1, 5539, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117901, "ねねね、うちらも\nハロウィンパレード\n見に行こー？", 0, 2, 5540, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117901, "ハロウィンセイバーズ\nしゅつどーう！\nヒデサイも行こ～♪", 0, 2, 5541, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117901, "マントのロゴ\nかっこいいっしょ～？\nカボきちってゆーんだ", 0, 2, 5542, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117901, "トリックオア\nトリート！\nねえねえヒデサイ\nお菓子ちょうだい♪", 0, 2, 5543, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 117901, "ヒデサイも\nメイクしてみる？\nハロウィンぽいやつ♪", 0, 2, 5544, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 117901, "ヒデサイのこと\nうちがいつでも\nあっためたげるね！\n…あ！　手、手の話！", 0, 2, 5545, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117901, "ハロウィンのイベント\n朝からでも\nやってるかな～？", 1, 2, 5546, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117901, "カボチャでおやつ\n作ってみたんだ～\nヒデサイ食べてみて！", 2, 2, 5547, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117901, "ハロウィンの夜って\nやっぱテンション\n上がるよね～♪", 3, 2, 5548, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117901, "超ふかふかしてる～\nちょっと休憩させて\nもらっちゃお", 0, 3, 5550, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117901, "はぁ…\nもにカワすぎて\nつらいんですけど～！", 0, 3, 5551, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117901, "ハロウィン限定の\nフレグランスじゃん！\nいい匂いする～♪", 0, 3, 5552, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117901, "やばばば…\nあったかくて\n眠くなっちゃう～", 0, 3, 5553, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117901, "これ食べて\nもうひとがんばり\nしよーっと！", 0, 3, 5555, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117901, "ハロウィンっぽい\nファッションとか\n載ってないかなぁ？", 0, 3, 5556, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 117901, "お菓子いっぱいだ～！\nあとでヒデサイにも\n持ってってあげよ～", 0, 3, 5558, 12, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118501, "お掃除が終わったら\n次は書類を片付けて…\nその次は…", 0, 1, 5560, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118501, "皆さんが冒険している\n間の留守は私に\nお任せください♪", 0, 1, 5561, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118501, "美食殿の皆さんが\n持ってきてくれる\nお土産が楽しみです♪", 0, 1, 5562, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118501, "ネビアさんとは\nときどき一緒にお茶\nする仲なんです", 0, 1, 5563, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118501, "ギルドハウスの乱れは\nギルドの乱れ\n整理整頓が大切です", 0, 1, 5564, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 118501, "素敵なギルドハウスの\n管理人ができて\nとっても幸せです♪", 0, 1, 5565, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 118501, "皆さんをほんの少し\n特別扱いしちゃう\n私がいます", 0, 1, 5566, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118501, "何かわからないことが\nあるときは私に\n聞いてくださいね", 0, 2, 5567, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118501, "書類を書いていると\n落ち着きます…\n一緒にどうですか？", 0, 2, 5568, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118501, "あなたのまわりは\nいつも賑やかで\n楽しいですね♪", 0, 2, 5569, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118501, "あっ！　お頼みしたい\nことがあって\n探してたんです♪", 0, 2, 5570, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 118501, "ギブアンドテイクは\n大切です。たまには\nお礼させてください♪", 0, 2, 5571, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 118501, "陰に日向にあなたを\n支えていきます♪\n期待していてください", 0, 2, 5572, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118501, "今日も一日\n管理人として\nがんばります♪", 1, 2, 5573, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118501, "お腹がすきました…\nそろそろお昼に\nしませんか？", 2, 2, 5574, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118501, "もうこんな時間…\n皆さんといると一日が\nあっという間です", 3, 2, 5575, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118501, "良質なお仕事は\n良質な休憩から\nです♪", 0, 3, 5577, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118501, "かわいいものに\n囲まれてお仕事できる\nのは幸せですね♪", 0, 3, 5578, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118501, "お仕事に集中できる\nアロマを設置するのも\nよさそうですね♪", 0, 3, 5579, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118501, "ポカポカ温まりながら\n書類仕事をするのも\nいいものですね…", 0, 3, 5580, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118501, "栄養補給しないと\n頭が回りませんからね\nきちんと食べましょう", 0, 3, 5582, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118501, "新条例はこのように\n活用されている、と…\nふむふむ…", 0, 3, 5583, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106701, "会合用の資料と\n連載のネーム…\nどっちを片付けよう？", 0, 1, 5585, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106701, "イノリちゃん、また\nお金くすねてる…\n帰ってきたら\nお尻ペンペンだ♪", 0, 1, 5586, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106701, "カヤちゃんの装備\n傷むの早いなぁ〜\nメンテしてあげないと", 0, 1, 5587, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106701, "そうだ、ラーゴのご飯\n買いに行かないと\n今日は何がいいかな？", 0, 1, 5588, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106701, "ここの人たちは見てて\n飽きない人ばっかり♪\n漫画の参考にしよ〜", 0, 1, 5589, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 106701, "また一緒に買い物にも\n行きたいし、冒険も\nしたいな〜…♪", 0, 1, 5590, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 106701, "…君は見てるだけで\n満足なの？ ちゃんと\n触って欲しいな…♪", 0, 1, 5591, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106701, "私の目が見たいの？\nいいけど…魅了されて\n魂が壊れちゃうよ…？\n…冗談で〜す☆", 0, 2, 5592, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106701, "ラーゴがね、君に\n遊んで欲しいって☆\n仲良くしてあげてね", 0, 2, 5593, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106701, "アシスタントの仕事\n気に入ってくれたなら\nまたお願いしていい？", 0, 2, 5594, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106701, "私が覚醒できるか？\n見せてあげたいけど…\nすごいことになるから\nまた今度で♪", 0, 2, 5595, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 106701, "楽しいトラブルが\nあったら、とりあえず\n声はかけて欲しいな♪", 0, 2, 5596, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 106701, "マッサージお願い♪\n変なところは…\n少しだけなら☆", 0, 2, 5597, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106701, "おはよう〜♪\n今日も一日、一緒に\nがんばっていこうね", 1, 2, 5598, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106701, "お腹空いちゃった…\n一緒にお昼行かない？\nいいお店があるんだ〜", 2, 2, 5599, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106701, "君、眠れないの？\n誰かに見られてる？\n…きっと気のせいだよ", 3, 2, 5600, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106701, "ちょっと休憩〜…\nこのまま原稿すれば\n疲れなくなるのかな？", 0, 3, 5602, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106701, "かわいい〜♪\nラーゴのお友達に\nなってくれる？", 0, 3, 5603, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106701, "いい香り〜♪\nこの目でどう見えるか\n伝えられたらいいのに", 0, 3, 5604, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106701, "ほどほどなのが\nちょうどいい時も\nあるよね…♪", 0, 3, 5605, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106701, "この味付け…\nあっちでも再現\nできるかな？", 0, 3, 5607, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106701, "ふふっ、面白い♪\nイノリちゃんの買う\n漫画にハズレなし☆", 0, 3, 5608, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119001, "私…23歳だもん\n魔法少女だなんて\n名乗れないわ…", 0, 1, 5610, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119001, "あん、どぅ、とろわ〜\nみんなイイ子に\nな〜る♪", 0, 1, 5611, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119001, "あっ！　イケナイ子の\n気配がするわ！\n風紀の乱れは\n先生が許しませんっ！", 0, 1, 5612, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119001, "ここで闇の力が\n発動したら大変だわ！\nやだっ…そう思ったら\n緊張して…ダメぇ〜！", 0, 1, 5613, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119001, "オイタした子はみんな\nどうしてうれしそうに\n私の愛のムチを\n欲しがるのかしら…？", 0, 1, 5614, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 119001, "あの子に次は何を\n教えてあげようかしら\n愛？　希望？\nそれとも…♪", 0, 1, 5615, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 119001, "あの子のことを\n考えると、愛と希望で\n胸がいっぱいになるの", 0, 1, 5616, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119001, "先生なのに闇の力を\n持ってるのは秘密よ？\n他の子には\n知られたくないから…", 0, 2, 5617, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119001, "秘密の授業の内容？\nそれは…あんなことや\nこんなことよ♪\nそれから、それから…", 0, 2, 5618, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119001, "しーっ！　私の正体が\nルーセント学院の\n先生だって、ここでは\n秘密にしててね？", 0, 2, 5619, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119001, "あら？　補習して\nほしいの？　いいわよ\n君のお願いなら\nなんでも聞いちゃう♪", 0, 2, 5620, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 119001, "イイ子にはごほうびを\nあげなくちゃ！\n君はなにが欲しい？\n遠慮しちゃダメよ？", 0, 2, 5621, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 119001, "君との秘密が\nまた一つ\n増えちゃったね♪", 0, 2, 5622, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119001, "今日も一日\n元気に過ごしましょう\n先生が指導してあげる", 1, 2, 5623, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119001, "夜まで頑張れるように\nお昼ご飯はしっかり\n食べましょう〜！", 2, 2, 5624, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119001, "夜更かしはダメよ？\nイケナイ子が\nすることです！", 3, 2, 5625, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119001, "闇の力に振り回されて\nもうヘトヘトよ〜\n心も体も\n癒されたいわ〜", 0, 3, 5627, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119001, "『秘密の学び舎』に\nこっそり連れて\n行っちゃおうかしら♪", 0, 3, 5628, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119001, "イケナイ甘い香り…\nなんだか胸の奥が\n熱くなってきたわ…♪", 0, 3, 5629, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119001, "やんっ♪\n燃え上がる愛みたい♪\n火照ってきちゃった♪", 0, 3, 5630, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119001, "生徒の見本に\nなれるように\n好き嫌いはしないの", 0, 3, 5632, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119001, "『学び舎に咲く禁断の\n愛』？　気になるわ…\nううん、ダメよっ！\nでも…ちょっとだけ…", 0, 3, 5633, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 106301, "次に会う時は、\nまたあそこに一緒に…\nえへへ…！", 0, 1, 5635, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 106301, "横顔見てるの、\nバレてないよね…？\n…ちらっ", 0, 2, 5636, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 105301, "背が少し伸びたか！？\nあ、あいつのところへ\n報告に行かねばっ！", 0, 1, 5637, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 105301, "私も貴公の隣に\n立っていられるよう\n日々精進していくぞ！", 0, 2, 5638, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119101, "ああ～\nドＭミストが\nあふれてしまいます～", 0, 1, 5639, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119101, "魔法少女として\nクウカ頑張ります～！\n愛と希望の妄想を…！", 0, 1, 5640, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119101, "決めポーズの練習より\nやられポーズの練習を\nしておきます…♪", 0, 1, 5641, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119101, "快感を溜めに溜めて\n放つドＭビームは\n最高ですよ～", 0, 1, 5642, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119101, "はっ！\nこれは闇の気配！\n…クウカ自身でした", 0, 1, 5643, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 119101, "ドＳさん…ぐふふ…\nクウカを縛りあげ…\nじゅるりぃ～♪", 0, 1, 5644, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 119101, "ドＳさんに攻められる\nことで、クウカは進化\nしてゆくのです～！", 0, 1, 5645, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119101, "はぅっ！\nもっと激しく…！\n情熱的に～～～！", 0, 2, 5646, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119101, "クウカの変身シーンを\nじっくり眺めたい！？\nは、恥ずかしいです～", 0, 2, 5647, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119101, "「本当に魔法少女か\n確かめてやるよ…！」\nドＳさんはそう言って\nクウカを…ぐふふ…♪", 0, 2, 5648, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119101, "愛と希望の力が\nクウカの中に…\nこの胸の高鳴りが…？", 0, 2, 5649, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 119101, "ひゃあんっ！\nそこです～っ！\nもっと、もっと～♪", 0, 2, 5650, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 119101, "クウカをこんなに\n悦ばせるのは\nドＳさんだけです…♪", 0, 2, 5651, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119101, "明るい時間に出歩くと\n目立ちますね\n魔法少女の衣装…", 1, 2, 5652, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119101, "お昼の見回りです～♪\nああ～うっかりドＭを\n増やしてしまいました", 2, 2, 5653, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119101, "夜！　それはクウカが\n活性化する時間です！\n闇の活動開始です～♪", 3, 2, 5654, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119101, "気の緩んだクウカを\nドＳさんの鋭い牙が\n狙っています～♪", 0, 3, 5656, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119101, "魔法少女のマスコット\nにいいかもしれません\nもっと鎖とか飾りを…", 0, 3, 5657, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119101, "魅惑的な香りです～\nドＭミストの香りに\n似て…いや違いますね", 0, 3, 5658, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119101, "火照る体…紅潮した\nクウカの肢体を熱く\n見つめるドＳさん…", 0, 3, 5659, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119101, "食欲を満たした次は\n何を満たせば\nいいですかねぇ～♪", 0, 3, 5661, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119101, "魔法少女がひどい目に\n遭う物語は…\nなさそうですね…", 0, 3, 5662, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119901, "ケーキも悪くないけど\nプリンが一番なの\nでもせっかくだから\n両方もらってやるの！", 0, 1, 5664, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119901, "クリスマスって聞くと\nなんだかあったかい\n気分になるの～", 0, 1, 5665, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119901, "煙突がなくても\nミヤコはどこからでも\nおうちに入れるの♪", 0, 1, 5666, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119901, "イブもプリン！\nクリスマスもプリン！\nプレゼントもプリン！", 0, 1, 5667, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119901, "クリスマスツリーの\n高いところの\n飾りつけは、ミヤコに\nまかせろなの～！", 0, 1, 5668, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 119901, "アイツのプレゼント\n楽しみなの～\nきっと最っ高に特別な\nプリンなの～♪", 0, 1, 5669, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 119901, "ミヤコがサンタなら\nアイツはトナカイなの\nつまりお似合いって\nことなの～♪", 0, 1, 5670, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119901, "ミヤコ、プレゼントを\n配りにいくの～♪\nオマエも手伝えなの！", 0, 2, 5671, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119901, "オマエにも飾りつけ\nしてやるの！\n頭にプリンを乗せると\nかわいいの♪", 0, 2, 5672, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119901, "幽霊のミヤコが\nいるなら、サンタも\nいて当たり前なの～！", 0, 2, 5673, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119901, "靴下にプリンを\n入れたらつぶれるの\nだからプリンは直接\nミヤコによこすの～！", 0, 2, 5674, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 119901, "今日はプリンだけじゃ\n満足してやらないの！\nミヤコをたくさん\n甘やかせなの！", 0, 2, 5675, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 119901, "ミヤコのプリン\nオマエになら一個…\nうう～、一口だけ\nあげてもいいの！", 0, 2, 5676, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119901, "サンタの朝は早いの～\nミヤコも早起きして\nプレゼント準備したの\nとってもえらいの♪", 1, 2, 5677, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119901, "今日は一日中\nみんなと遊ぶの～！\nオマエも来いなの！", 2, 2, 5678, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119901, "みんなが寝たら\nチャンスなの～\nこっそりプレゼント\n配りにいくの♪", 3, 2, 5679, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119901, "むふー、気持ちいいの\nこういうプレゼントも\nいいかもしれないの", 0, 3, 5681, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119901, "これ気に入ったの～\nプリンとアイツの\n次くらいなの♪", 0, 3, 5682, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119901, "いいにおいなの～\nうっかり成仏\nしちゃいそうなの…", 0, 3, 5683, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119901, "ここに飾っといた\n雪だるま\nなくなっちゃったの\nきっと成仏したの～", 0, 3, 5684, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119901, "これはオマエがくれる\nプリンとは\n別腹なの～", 0, 3, 5686, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119901, "プリンケーキ…\nこれならミヤコにも\n作れそうなの！\nさっそく作るの！", 0, 3, 5687, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119901, "びっくりしたの～！\n幽霊のミヤコを\n驚かすなんて…\nなかなかやるの！", 0, 3, 5689, 14, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119901, "コイツの中…\nあったかいの～♪\n昼寝にぴったりなの～", 0, 3, 5690, 15, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119201, "メリークリスマス！\n聖夜に笑顔を\nお届けするべ♪", 0, 1, 5691, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119201, "クリスマス前は\n乳製品がよく売れて\nありがてぇけんど…\n目が回る忙しさだ～", 0, 1, 5692, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119201, "牧場で売る\nクリスマスリースを\n作っておかねぇと…", 0, 1, 5693, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119201, "うちの動物たちにも\nクリスマスプレゼント\n用意しなきゃなぁ", 0, 1, 5694, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119201, "クリスマスに向けた\n限定商品とか\n作ってみるべか？", 0, 1, 5695, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 119201, "あんちゃんへの\nプレゼント選び…\nここでボケるか…？\nやめておくべか…？", 0, 1, 5696, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 119201, "オラ、あんちゃんから\n本当にたくさんの物を\nもらってんなぁ…", 0, 1, 5697, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119201, "雪遊びがしたいべか？\nへへっ、あんちゃん\nには負けねぇど！", 0, 2, 5698, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119201, "ディナーと一緒に\n聖夜のお笑いライブを\nすんのはどうだべか？\nツッコミは任せたべ！", 0, 2, 5699, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119201, "寒くなってくると\n鍋が食べたくなるべや\nあんちゃんはどうだ？", 0, 2, 5700, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119201, "あんちゃんは\nサンタさんのこと\n信じてるだか？", 0, 2, 5701, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 119201, "どでかいクリスマス\nケーキをこさえるべ\nあんちゃんも作るの\n手伝ってくんろ♪", 0, 2, 5702, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 119201, "あんちゃんが\nオラの旦那だったら…\nって、あんちゃん！\n今の聞いてただか！？", 0, 2, 5703, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119201, "牧場の朝は早いど！\nさぁてと、まずは\n雪かきしに行くべや", 1, 2, 5704, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119201, "あんちゃんも一緒に\nお昼食べてくか？\n採れたての冬野菜は\nうんまいど～♪", 2, 2, 5705, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119201, "よい子は早く寝ねぇと\nサンタさんが\n来てくれねぇべよ？", 3, 2, 5706, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119201, "こういうのリンリンが\n好きそうだな…\nプレゼント候補に\n入れておくべ！", 0, 3, 5708, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119201, "もふもふしてて\nなまらめんこくて…\nあぁ…癒されるべや…", 0, 3, 5709, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119201, "この匂い…\n気持ちが和むべや\n夢心地になっちまうな", 0, 3, 5710, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119201, "あったけぇところは\n落ち着くなぁ…\nつい眠くなっちまう", 0, 3, 5711, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119201, "こ、この味は…！\n…うーん、もっと\nリアクション芸も\n練習しねぇとな", 0, 3, 5713, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119201, "シオシオって\nどんな本読んでんだ？\n…難しい字が\nぎっしりだべや…", 0, 3, 5714, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119201, "この仕掛け…\nオラのコントに\n活かせるかも\nしんねぇな", 0, 3, 5716, 14, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119201, "この着ぐるみで\nリマリマのモノマネを\n練習してみるべか…？", 0, 3, 5717, 15, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 105001, "もっとキレイになって\nアイツにぎゃふんと\n言わせてやるんだから", 0, 1, 5718, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 105001, "アタシのこと\nどう思う？\nかわいいって…そこは\nキレイって言ってよね", 0, 2, 5719, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119301, "ハートマークの刺繍…\nは、やりすぎですかね\nいやちょっとくらい\nアピールしてみても…", 0, 1, 5720, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119301, "雪降らないかな～\nそしたらお兄ちゃんと\n雪だるま作ったり\nできるのに～", 0, 1, 5721, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119301, "プレゼントを入れる\n靴下…いくつ編んでも\n足りません～！", 0, 1, 5722, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119301, "お兄ちゃんとの\nクリスマスデートは\n早い者勝ちですからね\n負けませんよ～！", 0, 1, 5723, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119301, "クリスマスだし…\n受験勉強は今日だけ\nお休みです♪", 0, 1, 5724, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 119301, "お兄ちゃんと\nおそろいのコーデで\nクリスマスデート…\nしたいなぁ～！", 0, 1, 5725, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 119301, "お兄ちゃんに\nプレゼントする編み物\nこれで３着目～と♪\nあれ、作りすぎ？", 0, 1, 5726, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119301, "えへへ、もっと近くで\n見たくありませんか？\n私のとっておきの\nクリスマスコーデ♪", 0, 2, 5727, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119301, "お兄ちゃんが風邪を\n引いたら一大事です！\n私の編み物で暖かく\nしてあげます♪", 0, 2, 5728, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119301, "お兄ちゃん、ほっぺに\nケーキのクリームが\nついちゃってますよ♪\n私がとってあげます！", 0, 2, 5729, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119301, "お兄ちゃん！\nケーキ、あーんって\nしてあげます！\nほら、あーん♪", 0, 2, 5730, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 119301, "クリスマスは大切な\n人と過ごすものっ！\nお兄ちゃん…妹の予定\n空いてますよっ！", 0, 2, 5731, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 119301, "クリスマスはずっと、\nお兄ちゃんと一緒に\nいたい…って言ったら\n欲張り…すぎですか？", 0, 2, 5732, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119301, "早起きは３本お得！\n…クリスマスなら\n追加で10本ぐらい\nもらえちゃうかも！？", 1, 2, 5733, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119301, "お昼はケーキ？\nそれとも七面鳥？\nごちそういっぱいで\n迷っちゃいますね～♪", 2, 2, 5734, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119301, "お兄ちゃんと\nクリスマスに夜更かし\n…えへへ、ちょっぴり\nドキドキしちゃう♪", 3, 2, 5735, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119301, "編み物をするには\n絶好のくつろぎ空間\nですね～♪", 0, 3, 5737, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119301, "なんというかわいさ…\nクリスマス\nプレゼントに\nもってこいですね！", 0, 3, 5738, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119301, "これがお兄ちゃんの\nお部屋の香り…？\nんん～！　ずっと\nかいでいたいです～♪", 0, 3, 5739, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119301, "ふぁ、あったか～い…\n冬場はこういうのが\nありがたいですね～♪", 0, 3, 5740, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119301, "ごちそうだぁ～♪\nほら、お兄ちゃんも\n食べて食べてっ！", 0, 3, 5742, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119301, "クリスマスデートの\n必勝法…こ、これは\n読むしかないです！", 0, 3, 5743, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119301, "わっ、すごいです～！\nこれを飾っておけば\nクリスマスが一層\n賑やかになりますね♪", 0, 3, 5745, 14, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 119301, "さぷらーいずっ！\nリマちゃんじゃなくて\nリノちゃんでした～！", 0, 3, 5746, 15, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120601, "ミミにもミソギにも\nあの人にも頼りに\nしてもらえるように\nしっかりしなきゃ！", 0, 1, 5747, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120601, "ミミはにんじんを\n食べられてすごいな…\n私も見習わなきゃ\nがんばろう…！", 0, 1, 5748, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120601, "ミソギから怖い話\n聞いちゃった…\nうぅ…誰か一緒に\nいてくれないかな", 0, 1, 5749, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120601, "立派な大人に\nなれるように\nいっぱい勉強\nしておかなくっちゃ！", 0, 1, 5750, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120601, "なんだか…あの人の\nあやしい視線を\n感じるような…？", 0, 1, 5751, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 120601, "あの人に勉強を\n教えられるように\nしっかり予習を\nしておこうっと…！", 0, 1, 5752, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 120601, "今日はあの人と\n一緒に冒険できる…\nなんだかワクワク\nしてきちゃった…！", 0, 1, 5753, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120601, "あ、いいとこに来て\nくれましたね！\nあなたに頼みたい\nことがあるんです！", 0, 2, 5754, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120601, "きゃあっ！？\nへ、変なところを\n触らないでください！", 0, 2, 5755, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120601, "どうですかこの格好？\nかわいい、ですか…？\nそ、そうですか…\nありがとうございます", 0, 2, 5756, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120601, "ひゃあっ…！\nすみません、勉強に\n集中してたので\n驚いちゃいました", 0, 2, 5757, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 120601, "一緒に勉強して立派な\n大人になりましょうね\n私がそばで見張ってて\nあげます", 0, 2, 5758, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 120601, "まだ立派ではないかも\nですけど、私よりも\n大人なあなたのことは\n頼りにしてますからね", 0, 2, 5759, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120601, "立派な大人は\n朝の時間の使い方が\n上手だと聞きました\nあなたはどうですか？", 1, 2, 5760, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120601, "ミミとミソギと\nあなたと…大事な人と\n一緒のお昼ごはんは\nやさしい味がしますね", 2, 2, 5761, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120601, "うぅ…怖い話を\n思い出しちゃいました\n寝るまで一緒に\nいてくれますか…？", 3, 2, 5762, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120601, "すごい座り心地…！\nとっても勉強が\nはかどりそう…！", 0, 3, 5764, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120601, "この子…なんだか\nミミに似てるような\n気がする…ふふっ♪", 0, 3, 5765, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120601, "いい香り…♪\n勉強で疲れた\n頭がリフレッシュ\nできそうです", 0, 3, 5766, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120601, "ぽかぽかだ…♪\nミミとミソギと\nあの人と一緒に\nいるときみたい", 0, 3, 5767, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120601, "いっぱい食べたら\n早く大人になれたり\nしないかな…？", 0, 3, 5769, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120601, "あう…\n知らない単語が…\nちゃんと調べて\n覚えなくっちゃ…！", 0, 3, 5770, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120401, "うさぎさんが\nぴょ～んぴょん♪\nみんなえがおで\nるんたった～♪", 0, 1, 5773, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120401, "キョウカちゃんも\nいつかにんじんさんを\n好きになってくれたら\nうれしいな～♪", 0, 1, 5774, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120401, "今日のミソギちゃんの\nイタズラ、びっくり\nしちゃったけど\nおもしろかったな～♪", 0, 1, 5775, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120401, "このおうち、いつも\nいろんな人がいて\nドキドキぴょんぴょん\nって楽しいな～♪", 0, 1, 5776, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120401, "いいお天気～♪\nお外でみんなと\nいっぱい遊びたいな♪", 0, 1, 5777, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 120401, "ミソギちゃんも\nキョウカちゃんも\nおにいちゃんがいると\nずっと笑顔だね～♪", 0, 1, 5778, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 120401, "おにいちゃんにはやく\nあいたいな～♪\nまだかな、まだかな♪", 0, 1, 5779, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120401, "えへへ♪\nおにいちゃんに\nなでなでされると\nぽかぽかするね♪", 0, 2, 5780, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120401, "聞いて、おにいちゃん\nさっきね、かわいい\nちょうちょさんに\n会ったんだよ～♪", 0, 2, 5781, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120401, "ふわっ…！\nおにいちゃんの顔を\nお絵かきしてたから\nびっくりしちゃった♪", 0, 2, 5782, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120401, "おにいちゃんも\n遊びたいの？\nそれじゃあ二人も\n呼んで遊ぼう♪", 0, 2, 5783, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 120401, "おにいちゃんも\nみんなも、優しい人\nばっかりで\nミミ幸せ～♪", 0, 2, 5784, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 120401, "おにいちゃん♪\n高い高いしてほしいな\nすっごくぴょんぴょん\nできて、好きなんだ♪", 0, 2, 5785, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120401, "おはよう～♪\nおにいちゃん、今日は\nおひさまみたいに\nきらきら元気だね～♪", 1, 2, 5786, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120401, "おひるごはんだね～♪\nにんじんさんを\nいっぱい食べて\n元気いっぱい♪", 2, 2, 5787, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120401, "ふわあ…\n眠くなってきちゃった\nおにいちゃん、\n一緒に寝よう～？", 3, 2, 5788, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120401, "ふわぁ、気持ちいい♪\nずっとこうやって\n座ってたくなるね～♪", 0, 3, 5790, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120401, "わあ、かわいい～\nぎゅ～っ、ぎゅ～っ♪\nこれで仲よしだね\nえへへ♪", 0, 3, 5791, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120401, "気持ちいいにおい～♪\nぴょんぴょんしたく\nなってきちゃった♪", 0, 3, 5792, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120401, "あったかぽかぽか♪\nおにいちゃんも\nきてくれたら、もっと\nあったかくなりそう♪", 0, 3, 5793, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120401, "もぐもぐ…ごっくん♪\nおっきくなりたいから\nいっぱい食べるよ～♪", 0, 3, 5795, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120401, "えへへ、おもしろ～い\nこの絵本ね、\nキョウカちゃんから\n教えてもらったんだ♪", 0, 3, 5796, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120501, "この部屋…探索の\nしがいがありそう！\nついでにイタズラも\nしかけちゃお♪", 0, 1, 5799, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120501, "ミミってああ見えて\n実は大胆で度胸も\nあるよね…ちょっと\nかっこいいな…！", 0, 1, 5800, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120501, "怖い話を聞いた時の\nキョウカって\n面白いんだよね～♪", 0, 1, 5801, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120501, "にいちゃんが\n慣れちゃわないように\nまた新しいイタズラを\n考えなきゃ！", 0, 1, 5802, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120501, "イタズラもいいけど\nミミとキョウカと\nにいちゃんと、普通に\n遊ぶのも楽しいなぁ♪", 0, 1, 5803, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 120501, "ミミにキョウカ、\nそれににいちゃん！\nみんなそろえば愉快な\n冒険が始まりそう！", 0, 1, 5804, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 120501, "次はどんなイタズラを\nしたら、にいちゃんは\n喜んでくれるかな？", 0, 1, 5805, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120501, "今ミソギに触ったね？\nヤバいよにいちゃん…\n逃げないとトラップが\n発動しちゃうよ…！", 0, 2, 5806, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120501, "わわっ！？　びっくり\nしたあ…！　新しい\nトラップを作ってて\n気づかなかったよ～", 0, 2, 5807, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120501, "お？　にいちゃん、\nイタズラする側にも\n興味があるの？", 0, 2, 5808, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120501, "にいちゃんなにか用？\nあ、もしかして…\nミソギと遊びたく\nなっちゃった？", 0, 2, 5809, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 120501, "ベストタイミングだよ\nにいちゃん！\n今からみんなで探検に\n行くところなんだ！", 0, 2, 5810, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 120501, "にいちゃんってさ\nホントに優しいよね\nなにしても怒らないし\n…ありがとね♪", 0, 2, 5811, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120501, "にいちゃんおはよー！\n景気づけに本日\n一発目のイタズラ、\nやっちゃうよ～♪", 1, 2, 5812, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120501, "お昼ごはんはロシアン\nサンドイッチだよ！\nさあ、にいちゃん！\n好きなの選んでよ！", 2, 2, 5813, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120501, "夜ってなんだか\nワクワクしてなかなか\n眠れないんだよね\nにいちゃんはどう？", 3, 2, 5814, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120501, "この座り心地のよさ…\nなにかのトラップに\nうまく使えないかな？", 0, 3, 5816, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120501, "こういうのに細工して\nミミやキョウカを\n驚かせると、いい反応\nするんだよね♪", 0, 3, 5817, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120501, "においをうまく使った\nイタズラって…\nなにかないかな？", 0, 3, 5818, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120501, "あったまりながら\n新しいイタズラを\n考えると、いい案が\n出て来たりするんだ♪", 0, 3, 5819, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120501, "おいしくてついつい\n食べ過ぎちゃうなぁ…\nはっ…！？　これって\nまさかトラップ…！？", 0, 3, 5821, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120501, "このキョウカから\n借りた冒険物語\nすっごく\nおもしろいんだよね！", 0, 3, 5822, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120801, "晴れ着を着ると\nなんだか心が躍るね\n足取りも軽く\nなるってもんさ", 0, 1, 5825, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120801, "正月の食べ物ってのは\n美味いもんだね\nいくらでも\n食べれちまうよ", 0, 1, 5826, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120801, "普段は好き勝手してる\nアタシらも\n正月くらいは\n大人しくするさ", 0, 1, 5827, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120801, "年越しそばに雑煮\nこの季節は\n温かいものが\n体に沁みるね", 0, 1, 5828, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120801, "正月だからって\nのんびりしすぎたね\nちょっと釣りにでも\n行こうかね", 0, 1, 5829, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 120801, "いい正月だね\nいつかあの子と\n一杯やりたいもんだよ", 0, 1, 5830, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 120801, "大事な人と過ごす\n正月ってのがこんなに\n楽しいもんだとはね", 0, 1, 5831, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120801, "部屋があったかくて\n顔が火照っちまったよ\nちょいと外に涼みに\n行かないか？", 0, 2, 5832, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120801, "餅ってのは\n何をつけてもうまいね\nお前さんは\nどの味が好きだい？", 0, 2, 5833, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120801, "お祝いの鯛が\n足りないって？\nそれじゃあアタシが\n釣ってきてやるよ", 0, 2, 5834, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120801, "おせちが美味いのは\n分かるけど\n食べ過ぎには\n気を付けるんだよ", 0, 2, 5835, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 120801, "お前さんのおかげで\nいい正月を\n迎えられたよ\nありがとさん！", 0, 2, 5836, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 120801, "慣れない晴れ着も\nお前さんに見て\nもらえたなら\n着たかいがあったね", 0, 2, 5837, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120801, "おはようさん\n寒いのは分かるけど\nいい加減布団から\n出て顔洗ってきな", 1, 2, 5838, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120801, "今日の昼飯は雑煮だ\nアタシの郷土の味\nしっかり味わって\nおくれよ", 2, 2, 5839, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120801, "おや、お前さんも\n夜ふかしかい？\n退屈してたところさ\n付き合っておくれよ", 3, 2, 5840, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120801, "晴れ着だと座り方にも\n気を遣うね\nしわになったら大変さ", 0, 3, 5842, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120801, "せっかくだし\nこの子にも晴れ着を\n着せてあげたいね", 0, 3, 5843, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120801, "これは冬の花の香り…\n風情があって\nいいじゃないか", 0, 3, 5844, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120801, "あったかいねぇ…\nこんな寒い日には\nまさに天国だよ", 0, 3, 5845, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120801, "いつも食べてる\nものでも正月に\n食べると\n新鮮な気がするね", 0, 3, 5847, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120801, "せっかくの正月休みだ\nたまにはゆっくりと\n本でも読もうかね", 0, 3, 5848, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120701, "新年のあいさつ…\n私も街のみんなに\nしてみようかしら…", 0, 1, 5850, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120701, "正月はゆっくり過ごす\nのよね…ゆっくりって\n何したらいいのかしら", 0, 1, 5851, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120701, "この格好も\n慣れてくれば\n動きやすいわ", 0, 1, 5852, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120701, "う～ん、今年の抱負…\nやりたいことが\nたくさんあるのよね", 0, 1, 5853, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120701, "おせち料理\nネビアの分も\n用意しなくちゃね", 0, 1, 5854, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 120701, "ふふっ\n正月っていいわね\nみんな楽しそうだもの", 0, 1, 5855, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 120701, "新年の思い出…\nこれからもっと作るわ\nそれが私の抱負よ", 0, 1, 5856, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120701, "今日は家で過ごすの？\nあなたと一緒なら\nきっと楽しいわね", 0, 2, 5857, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120701, "どう？　この格好？\nコッコロさんは\n褒めてくれたけど…", 0, 2, 5858, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120701, "ちょうど良かったわ\nあなたも大掃除\n手伝ってくれる？", 0, 2, 5859, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120701, "これから料理するけど\nお雑煮と焼き餅\nどっちが好きかしら？", 0, 2, 5860, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 120701, "『今の私』にとって\n初めての正月だもの\nあなたと楽しまないと", 0, 2, 5861, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 120701, "去年よりも今年…\nもっと成長するからね\n…お兄ちゃん♪", 0, 2, 5862, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120701, "おはよう\nせっかくの正月だもの\n朝から楽しまないとね", 1, 2, 5863, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120701, "ねぇ…\n良かったら正月の遊び\n教えてくれるかしら？", 2, 2, 5864, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120701, "ふわぁ～…今日が\n終わっちゃうのが…\nもったいないわね…", 3, 2, 5865, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120701, "ふぅ…お参り後だから\n心地よすぎて眠って\nしまいそうだわ…", 0, 3, 5867, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120701, "ふふっ\nかわいいものって\n癒されるわね…", 0, 3, 5868, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120701, "…ん\nこれが梅の香り？\n…いい匂いね", 0, 3, 5869, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120701, "キャルじゃないけど…\nこのまま丸く\nなりたいところね…", 0, 3, 5870, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120701, "はむはむっ…♪\nせっかくの正月だもの\n堪能しなくちゃね♪", 0, 3, 5872, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120701, "…ふんふん\n帯の結び方って\nこんなにあるのね…", 0, 3, 5873, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 104201, "今の私が作れる\n最高の一曲…\nあの人に届くといいな", 0, 1, 5875, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 104201, "あの、歌を…\n聞いてくれませんか？\nあなたに贈る\n特別な歌を", 0, 2, 5876, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120901, "ひい、ふう…\nむむ、みなに配る\nお年玉のポチ袋が\n残りわずかなのじゃ…", 0, 1, 5877, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120901, "宴の準備はわらわに\n任せておくのじゃ！\n支配者の手腕、\n見せつけてくれる！", 0, 1, 5878, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120901, "新年の願掛けなら\nわらわにすればよい\n支配者に頼むほうが\n確実じゃろう？", 0, 1, 5879, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120901, "みなからもらった\nこの年賀状とやら…\nわらわも\n書いてみようかの♪", 0, 1, 5880, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120901, "正月飾りにもいろいろ\n種類があるんじゃの～\nどれも華々しくて\nわらわ好みじゃ♪", 0, 1, 5881, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 120901, "あやつへのお年玉は\nちと多めにしておくか\n…日ごろの感謝も\n込めてのう", 0, 1, 5882, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 120901, "あやつのことを想うと\nそれだけで行く年\n来る年が楽しみになる\nふふ、愉快なやつじゃ", 0, 1, 5883, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120901, "みなに晴れ着を\n褒められて、とても\nよい気分なのじゃ♪\n次はお主の番じゃぞ？", 0, 2, 5884, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120901, "お主へのお年玉は\nいちばん最後じゃ！\n残り物には福が宿る\nそうじゃからのう", 0, 2, 5885, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120901, "この季節は手が\nかじかむのう…ほれ、\nお主！　わらわの手を\nあたためるのじゃ！", 0, 2, 5886, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120901, "お主のために雑煮を\n手作りしてみたのじゃ\n食べていくがよい♪", 0, 2, 5887, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 120901, "眷属のお主に\n正月休みはまだ早い！\nずっとわらわのそばに\n控えておくのじゃ♪", 0, 2, 5888, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 120901, "ふふ、これほど楽しい\n正月は初めてじゃ…\nお主と過ごす時間は\nいつだって特別じゃな", 0, 2, 5889, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120901, "早起きしてわらわを\n待っておったのか？\nさすがはわらわの眷属\n殊勝な心がけじゃな♪", 1, 2, 5890, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120901, "食事どきじゃな\n正月の宴会料理が\nわらわたちを\n待っておるぞ♪", 2, 2, 5891, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120901, "夜も更けてきたのう\n冬の夜は長い…\n心ゆくまでお主と\n過ごせるわけじゃな♪", 3, 2, 5892, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120901, "実に心地がよい♪\nこのまま寝正月も\n悪くないかも\nしれぬのう", 0, 3, 5894, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120901, "愛いやつじゃのう\nわらわのそばに置いて\nやってもよいぞ♪", 0, 3, 5895, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120901, "品のある香りじゃのう\n寝室に置いておけば\nなにかご利益に\nあずかれそうじゃな", 0, 3, 5896, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120901, "心地よい…\nわらわはしばらく\nここから動かぬぞ\nお主も付き合うがよい", 0, 3, 5897, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120901, "舌鼓が鳴るのう♪\n正月の宴には\n持ってこいの品じゃ！", 0, 3, 5899, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120901, "ほう、新春にはそんな\n習わしが…正月を\n制するには、まだまだ\n勉強が必要じゃのう", 0, 3, 5900, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121001, "久しぶりの我が家って\n感じです！　やっぱり\nここが一番ですね〜！", 0, 1, 5902, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121001, "さあ食べましょう！\nみんなで囲める食卓を\n心から味わいますよ☆", 0, 1, 5903, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121001, "ランドソルに\n帰ってきた途端に\nお腹が空きました〜！", 0, 1, 5904, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121001, "ただいま☆\n幸せ特盛りの\nわたしの日常！", 0, 1, 5905, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121001, "ようやくランドソルに\n帰ってきたことですし\nやっぱり魔物料理を\nいただきましょう☆", 0, 1, 5906, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 121001, "あの人との絆を\n守るために\nもっともっと食べて\n強くならなくちゃ☆", 0, 1, 5907, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 121001, "いつもありがとうって\nあの人を思いっきり\n抱きしめたいです☆", 0, 1, 5908, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121001, "あなたの想いは\nこの身をかけて\n全力で受け止めます！", 0, 2, 5909, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121001, "おかえりなさい☆\nここにいるみんなも\nあなたの帰りをずっと\n待っていましたよ！", 0, 2, 5910, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121001, "このギルドハウスで\n冒険の疲れを\n癒しましょう〜！", 0, 2, 5911, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121001, "あなたの想いが\n全力全開を超える\n力をくれました！", 0, 2, 5912, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 121001, "日常が続く幸せを\nあなたと一緒に\nかみしめたいです☆", 0, 2, 5913, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 121001, "ぎゅ〜っ！\nわたしたちは\nどんなことがあっても\nずっと一緒ですよ？", 0, 2, 5914, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121001, "おはようございます！\nあなたと過ごせる\n幸せを、今日も\n味わいますよ〜！", 1, 2, 5915, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121001, "ご飯の時間ですよー！\nあなたも一緒に\n食べましょうね！", 2, 2, 5916, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121001, "明日もあなたが隣に\nいてくれることを\n心から願ってますよ\nおやすみなさい☆", 3, 2, 5917, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121001, "ふい〜…\n安心できる場所で\nくつろげるって\n幸せですね〜…", 0, 3, 5919, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121001, "じゅるり…\n食べたくなっちゃう\nくらい、かわいい☆", 0, 3, 5920, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121001, "くんくん…おおー！\nステーキの香り！\nやばいですね☆", 0, 3, 5921, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121001, "体がぽかぽかしてきて\nわたしの食欲も\n燃えてきましたよー！", 0, 3, 5922, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121001, "わたしの食欲に\nついてこられますか？\nまだまだ食べますよ！", 0, 3, 5924, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121001, "これがキャルちゃんの\n言っていた、キスで\n目覚める王子様の\nおとぎ話ですか〜☆", 0, 3, 5925, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121201, "自分で自分を\n分析するのも、これは\nこれで面白い…♪", 0, 1, 5927, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121201, "強過ぎる力の扱いには\n注意しないと…\n七冠がいい例だね", 0, 1, 5928, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121201, "少年の交友関係は\n計り知れないな…\nアタシも含むのか！", 0, 1, 5929, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121201, "原理を徹底解明して\n再発を防ぐぞー！\n…誰か手伝って～！", 0, 1, 5930, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121201, "これは祝福にも\n呪いにもなる…\n忘れずにいよう", 0, 1, 5931, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 121201, "少年が\n目を覚ましてくれて\n本当によかった…", 0, 1, 5932, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 121201, "彼以外を選んでいたら\n今回の事態は\n乗り切れなかったな…\nさすがだよ、少年", 0, 1, 5933, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121201, "少年！　調子はどう？\nクレープ食べられる？\nっていうか食べて？", 0, 2, 5934, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121201, "元気そうで何よりだ\nまた店を手伝いに\nきてよ。二人もキミに\n会いたがってたしさ", 0, 2, 5935, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121201, "キミの診察もあるし\n今はなるべくそばに\nいるよ。迷惑じゃ\nなければだけど", 0, 2, 5936, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121201, "やっぱり少年は\nあちこちで人を\n助けてる時が\n一番輝いてるよ", 0, 2, 5937, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 121201, "アタシは大したこと\nしてないけど\nみんなのことは\n褒めてあげてね", 0, 2, 5938, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 121201, "みんなを笑顔に\nできるのは\nやっぱり少年だね", 0, 2, 5939, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121201, "やあ少年\n朝寝坊はしてない？\nそれじゃ、よい１日を", 1, 2, 5940, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121201, "おや、少年！\nまかないのクレープ\n作ったけど、食べる？", 2, 2, 5941, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121201, "ふわぁ…もう夜か…\n研究してたら\nあっという間だね…\nおやすみ、少年", 3, 2, 5942, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121201, "体がなまってたのか\n前線に出ると疲れる…\nまさか…年のせい！？", 0, 3, 5944, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121201, "キミたちも今の\nアタシと同じ感じに\nアレンジしてあげよう", 0, 3, 5945, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121201, "誰が置いたんだろう？\nいい香りだねぇ…\nうん、気に入った♪", 0, 3, 5946, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121201, "何も考えてない時間も\n実は思考にとって\n大事なんだよね～…", 0, 3, 5947, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121201, "食の大切さは\nいつの食事からも\n感じられるね♪", 0, 3, 5949, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121201, "ふむ…面白い！\n今度全巻\n借りにこようっと", 0, 3, 5950, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116401, "聖学祭か…\n良い記憶はないな\nよし忘れよう", 0, 1, 5952, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116401, "健全なる精神は\n健全なる肉体に宿る…\nはっ、知ったことかよ", 0, 1, 5953, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116401, "暇だし鏡でも見るか…\nえへへ、ユニだよ♪\nよし子供料金いけるな", 0, 1, 5954, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116401, "アオイ君は元気に\nしているだろうか…\n彼女は人一倍ぼくに\n懐いていたからな…", 0, 1, 5955, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116401, "そろそろシズル君は\n姉という幻想から\n目覚めただろうか…", 0, 1, 5956, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 116401, "ぼくは来年も聖学祭に\n行くさ…卒業しても\n君らとは永遠だ", 0, 1, 5957, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 116401, "最近同志の登校率が\n減少傾向だな…\nまあ仕方ないのだが…\nないのだが…", 0, 1, 5958, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116401, "ほわあああ！\nおい急に話しかけるな\n学びがこぼれる", 0, 2, 5959, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116401, "この体操着、周囲から\n大層評判が良いのだよ\nたいそうだけにな\nどやっ！", 0, 2, 5960, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116401, "何を着ていようと\nぼくはぼくさ\n外見など所詮は\n魂の識別符に過ぎん", 0, 2, 5961, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116401, "救護院のアヤネ君が\n最近ぼくの自称年齢に\n疑念を持ち始めてな…\nあの熊…殺るか…", 0, 2, 5962, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 116401, "うん？　なんだね？\nなに、用は無い？\n用も無いのに呼ぶとは\nもしや…これが友達！", 0, 2, 5963, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 116401, "この服が似合ってる？\nな、なんだね突然…\nやめたまえよでゅふふ", 0, 2, 5964, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116401, "ふぁ～あ…\n朝も更けてきたな…\nそろそろ寝るか…", 1, 2, 5965, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116401, "ふぁ～あ…もう昼だ…\n人間の起きている\n時間ではないな…\n早く寝なくては…", 2, 2, 5966, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116401, "うーん、よく寝た！\n気持ちのいい夜だな\n今日も一日がんばるぞ", 3, 2, 5967, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116401, "ほわぁ～…\nもしやこれは都で噂の\n人を駄目にするやつ…", 0, 3, 5969, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116401, "ふむ、愛くるしいな\nだが似合い過ぎる…\nぼくが持つと我ながら\n子供にしか見えん", 0, 3, 5970, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116401, "アロマか、興味深いな\nミツキ印ポーションも\n気化させて鼻腔から\n吸入してみるか…", 0, 3, 5971, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116401, "んふ、んふふ…\n火ってイイよね…\nずっと見てられるね…", 0, 3, 5972, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116401, "や、やめろ…\nとめどなく固形食物を\n生産するのはやめろ…", 0, 3, 5974, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 116401, "この可愛げのない筆致\nやはり作者は理系か！\nぐああ目が理系になる", 0, 3, 5975, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121101, "…あいつのためなら\n無理くらいいくらでも\nしてやるわよ", 0, 1, 5977, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121101, "この姿、\n魔力の制御ムズ過ぎ…\nま、あたしは優秀だし\nどうとでもなるけど！", 0, 1, 5978, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121101, "あいつのこと、\n全部受け入れるって\n決めたから！", 0, 1, 5979, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121101, "今なら誰だろうと\n負ける気がしないわ\nドラゴンでもなんでも\nかかってこいっての！", 0, 1, 5980, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121101, "散々助けられた分、\n今度はあたしが\nあいつのこと助けるわ", 0, 1, 5981, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 121101, "あいつとの\nこれまでの冒険は\nどれも、あたしの\n大切な思い出…", 0, 1, 5982, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 121101, "もしまたあいつが\n寝たままになったら\nなんだってするわ\nキ…キスでも…", 0, 1, 5983, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121101, "なに、お腹空いたの？\nなら、おいしいご飯を\n食べに行きましょ♪", 0, 2, 5984, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121101, "やっぱ、\nあんたのアホ面見ると\n安心するわ♪", 0, 2, 5985, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121101, "【美食殿】はあんたと\nぺコリーヌがいなきゃ\n始まんないっての", 0, 2, 5986, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121101, "ひゃあっ！？\n急に声かけないで！？\n魔力が暴発しても\n知らないわよ！", 0, 2, 5987, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 121101, "…あんたとこうして\nぼーっと過ごす時間、\nまあ…嫌いじゃないわ", 0, 2, 5988, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 121101, "あんたが信じて\n見守っててくれれば…\nあたし、\nなんだってできるわ", 0, 2, 5989, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121101, "おはよ\n早く顔洗いなさいよ、\nこのねぼすけ♪", 1, 2, 5990, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121101, "今日のお昼は\nどこで食べる？\nあんたが\n決めていいわよ", 2, 2, 5991, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121101, "もしあんたがまた\n夢に囚われても\nあたしが助けるわ\n安心して寝なさいよ", 3, 2, 5992, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121101, "気持ちいい～…♪\nあいつもたまには\nこうして休ませなきゃ", 0, 3, 5994, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121101, "一目惚れしたかも…！\nこの子、\n部屋に飾りたいわー♪", 0, 3, 5995, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121101, "へぇ、あいつって\nこういう匂いが\n好きなのかしら…？", 0, 3, 5996, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121101, "暖かくて最高…♪\n丸くなっちゃいそう…\nうにゃあ…♪", 0, 3, 5997, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121101, "イケるわね、これ！\nやっぱり食べるって\n大事よねー♪", 0, 3, 5999, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121101, "ファーストキスは\nどんな味、ね…\nふ、ふ～ん…？\nなるほど…", 0, 3, 6000, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118101, "お友だち…\nできるかしら…？\nこんなわたしでも…", 0, 1, 6002, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118101, "あまり、きょろきょろ\nしちゃダメよね…\n変な子だと、\n思われてしまうわ…", 0, 1, 6003, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118101, "広い場所、\n落ち着かない…\nはぅ…どこにいれば…", 0, 1, 6004, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118101, "～♪", 0, 1, 6005, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118101, "【レイジ・レギオン】\n…またいつか、\nみんなと…", 0, 1, 6006, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 118101, "…一度でいいから、\nわたしもママって…\nあっ、その…\n…な、なんでもないわ", 0, 1, 6007, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 118101, "ここが、今のわたしに\nとっての帰る場所…\n…そう思っても、\nいいのかしら…？", 0, 1, 6008, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118101, "ええと…\nお腹、空いてる？\nおねむじゃない？", 0, 2, 6009, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118101, "ここにいると\n邪魔かしら…？\nならもっと隅っこに…", 0, 2, 6010, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118101, "お喋り…？\nわたしでいいの…？\nうれしい…", 0, 2, 6011, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118101, "あなたの好きな歌…\n教えて…？\n練習、するから…", 0, 2, 6012, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 118101, "ここは賑やかだけど…\nうるさいとは感じない\nむしろ、心地いい…", 0, 2, 6013, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 118101, "安心、して…\nわたしの歌で、\nあなたを守るから…", 0, 2, 6014, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118101, "ちょっとだけ…\n朝は、苦手…\n…ふぁ…", 1, 2, 6015, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118101, "お昼は、食べた？\n良かったら、一緒に…\n少し…焦げてるけど…", 2, 2, 6016, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118101, "眠れないのかしら…\nじゃ、じゃあ…\n子守歌…聴く？", 3, 2, 6017, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118101, "こんな特等席…\n恐縮、です…", 0, 3, 6019, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118101, "い、今のうちに、\nこの子でお世話の\n練習を…", 0, 3, 6020, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118101, "いい香り…\n…つらい気持ちも\n癒してくれる", 0, 3, 6021, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118101, "あったかい…\nいつまでも、\nこうしていられそう…", 0, 3, 6022, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118101, "おいしく出来てる…\nすごいわ…\n見習わなくっちゃ…", 0, 3, 6024, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118101, "『赤ちゃんとママの\nスキンシップ入門』…\n…べ、勉強になるわ…", 0, 3, 6025, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121301, "きれいなお部屋…！\n立派な舞台の\nセットみたい…♪", 0, 1, 6027, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121301, "お芝居では私が先輩\nなんだから、しっかり\n私がミサキちゃんを\nエスコートしなきゃ", 0, 1, 6028, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121301, "～～♪\n歌うお芝居も多いから\nもっと上手く\nなりたいな…！", 0, 1, 6029, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121301, "ここにいる人たちと\n仲良くなりたいな…\nそしたらきっと演技の\n幅も広げられるよね", 0, 1, 6030, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121301, "機会があったら\nアヤネちゃんとも\nお芝居してみたいな♪", 0, 1, 6031, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 121301, "お兄ちゃんに褒めて\nもらえるように\nお芝居の練習\nいっぱいしなきゃ…♪", 0, 1, 6032, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 121301, "あう…お兄ちゃんを\n意識したら、台詞が\n飛んじゃったよぅ…！", 0, 1, 6033, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121301, "あっ、お兄ちゃん…♪\nお芝居の練習を\n見てくれませんか…？", 0, 2, 6034, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121301, "いまの発声練習\nどうでしたか？\nひゃん…！？\n頭は撫でないでぇ～", 0, 2, 6035, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121301, "ミサキちゃんも\nお芝居、とっても\n上達してるんですよ\n私も負けられません♪", 0, 2, 6036, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121301, "また物語を聞かせて\nほしい、ですか…？\n私なんかでよければ\nもちろんよろこんで♪", 0, 2, 6037, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 121301, "新しいお話を\n考えてみました\n聞いてくれますか…？\nよかった…♪", 0, 2, 6038, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 121301, "いつか…舞台上じゃ\nなくても、照れずに\nお兄ちゃんに気持ちを\n伝えてみせますね…♪", 0, 2, 6039, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121301, "おはようございます♪\nマヒルさんにもらった\nミルク、一緒に\n飲みませんか？", 1, 2, 6040, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121301, "牧場のみなさんに\nドングリパンを\nいただきました～\nおひとつどうぞ♪", 2, 2, 6041, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121301, "寝る前にまたココアを\n飲みながら、お話を\n聞いてもらえますか？\nえへへ、うれしいな♪", 3, 2, 6042, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121301, "お芝居の練習の\nあとは、ここで\nお兄ちゃんと\nゆっくりしたいな♪", 0, 3, 6044, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121301, "今日も演技の\n練習に付き合ってね♪", 0, 3, 6045, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121301, "素敵な香り…\nこのイメージも\nお芝居に\n活かせないかな…？", 0, 3, 6046, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121301, "私もお芝居で\nみんなの心を\nぽかぽか温められる\nようになりたいな…♪", 0, 3, 6047, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121301, "舞台に立つんだから\n食べ過ぎないように\n気をつけないと。\nでも…おいしいなぁ♪", 0, 3, 6049, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121301, "このお話、面白い…\n脚本を書く\n参考にもなりそう…♪", 0, 3, 6050, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 107501, "えへへ…♪\n二人で見た夕日…\nキレイでしたね…", 0, 1, 6052, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 107501, "これから泳ぎの\n練習ですか？\n付き合いますよ♪", 0, 2, 6053, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 107601, "主さまを導くことで\nわたくしも未知なる\n領域へと辿り着けます", 0, 1, 6054, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 107601, "主さまとなら\n海よりも深く\n空よりも高い場所へも\n目指せそうです", 0, 2, 6055, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 107801, "あいつと二人で\n夏の遠い海に…って\nあたしってば\nなに考えてるのよ！", 0, 1, 6056, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 107801, "あたしが楽しく\n過ごせるのはあんたが\nいてくれたからよ\nか、感謝してるわ", 0, 2, 6057, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121401, "はぁ…アタシってば\nこんなにかわいくって\nキレイなんて罪よね～", 0, 1, 6058, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121401, "舞台の衣装を\nクリーニング\nしとかなくちゃ…", 0, 1, 6059, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121401, "舞台映えするメイク…\nスズナなら\n知ってるかしら？", 0, 1, 6060, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121401, "物産展の会計係と\n山盛りの算数の宿題…\nどっちが簡単かしら…", 0, 1, 6061, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121401, "牧場と温泉の仕事に\nお芝居の練習に…\nちょ、ちょっと休憩～", 0, 1, 6062, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 121401, "お客さんの視線も\nうれしいけど…\nあいつも見てて\nくれてるかしら？", 0, 1, 6063, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 121401, "もっともっと\nセクシーになって\nあいつをビックリ\nさせてやるわ♪", 0, 1, 6064, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121401, "アンタも休憩？\nしっかり働くために\n休むのも大事よね", 0, 2, 6065, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121401, "ふふん♪　アタシも\n料理ができるように\nなったんだから！\n簡単なのだけだけど…", 0, 2, 6066, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121401, "温泉ってお肌に\nいいのよね？\nもっとキレイに\nなったらどうしよう〜", 0, 2, 6067, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121401, "物産展の荷物は\nこれでよし…っと\nそれじゃアンタも\n運ぶの手伝ってよね♪", 0, 2, 6068, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 121401, "いつもと違うアタシに\n見とれちゃった？\n仕方ないわね～♪", 0, 2, 6069, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 121401, "お花見って風流で\n大人って感じ♪\n…でもお団子も\n忘れないでよね！", 0, 2, 6070, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121401, "みんなに朝ごはんを\n作ってみたら\n喜んでくれるかしら？", 1, 2, 6071, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121401, "お客さんがいっぱい\n来る時間帯ね！\n元気出していくわ！", 2, 2, 6072, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121401, "そろそろ寝ないと…\n睡眠不足はお肌の\n大敵なんだから", 3, 2, 6073, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121401, "ふかふかしてて\n気持ちいい～♪\n疲れが癒されるぅ…", 0, 3, 6075, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121401, "この子もアタシと\nおそろいの衣装に\nしてあげようかしら♪", 0, 3, 6076, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121401, "素敵な衣装で\nいい香りを\n身にまとう…ふふ、\n大人のたしなみよね♪", 0, 3, 6077, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121401, "あったか～い♪\nここでお昼寝したら\n気持ちよさそう…", 0, 3, 6078, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121401, "お仕事がんばると\nいつもよりお腹\n空いちゃう～！", 0, 3, 6080, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121401, "最新のトレンド\n載ってないかしら？\n物産展の売り物に\n取り入れなくちゃ！", 0, 3, 6081, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121501, "怪盗のこと…\nお父さんお母さんに\nもし知られたら\nなんて説明しよう…", 0, 1, 6083, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121501, "たとえ怪盗でも、人の\n家にお邪魔するときは\n身だしなみをしっかり\nしないと…", 0, 1, 6084, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121501, "鍵開けって難しい…\nうーん、もっと\n練習しておかなくちゃ", 0, 1, 6085, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121501, "姿が見えない\n怪盗の噂…？\nわ、私のことじゃ\nありませんように…！", 0, 1, 6086, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121501, "予告状って…\n切手はどこに貼れば\nいいんだろう…？", 0, 1, 6087, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 121501, "怪盗活動にも\n慣れてきたけど…\nやっぱり先輩が\nいないと心細いなぁ…", 0, 1, 6088, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 121501, "せ、先輩の心も\nいつか盗めるかな…？\n…わわわ、私ってば\nなに言ってるんだろ…", 0, 1, 6089, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121501, "怪盗が大活躍って\n新聞で読んだ…？\nだっ、誰のこと\nでしょうね…あはは", 0, 2, 6090, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121501, "尾行に潜入…\n存在感がないのって\n意外と役立つことも\nあるんですね", 0, 2, 6091, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121501, "せせせ、先輩っ！\n帽子をぎゅーって\nかぶったら、抜けなく\nなっちゃいました…！", 0, 2, 6092, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121501, "ぬ、盗み聞きなんて\nしてませんよ？\nみんなが私に気づかず\n話し始めるだけで…", 0, 2, 6093, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 121501, "秘密道具が見たい？\nそ、それは内緒です！\n秘密道具ですから…\nで、でも先輩になら…", 0, 2, 6094, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 121501, "えへへ\n見つかっちゃいました\n先輩はいつも私を\n見つけてくれますね…", 0, 2, 6095, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121501, "おはようございまふ…\n秘密道具のお手入れで\n夜更かししちゃって…\nちょっと眠たいです～", 1, 2, 6096, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121501, "怪盗が昼に活動する…\n案外そのほうが\n意外性が\nあるのかも…？", 2, 2, 6097, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121501, "外が暗くなりましたね\n…そろそろ怪盗の活動\n始めましょうか？", 3, 2, 6098, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121501, "ここでくつろぐと\n疲れが取れます～\n…今日も怪盗\nがんばろっと", 0, 3, 6100, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121501, "わぁ、かわいい…！\n心を盗まれるって\nこういうことかな…？", 0, 3, 6101, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121501, "素敵な香り…でも\n服に香りがついたら\n隠れてるときに\n見つかっちゃうかな…", 0, 3, 6102, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121501, "ぽかぽかで\nいい気持ち…\n夜道を歩く前に\n温まっておこうっと…", 0, 3, 6103, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121501, "怪盗活動に備えて\n今のうちにしっかり\n食べておかなきゃ…！", 0, 3, 6105, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121501, "女の子は秘密が\n多いほうが魅力的…？\nそ、そうなんだ…", 0, 3, 6106, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 104501, "ドＳさんはいつも\nクウカの妄想の中に…\nぐふふ、じゅるり～♪", 0, 1, 6108, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 104501, "やさしい触り方も\n嫌いじゃありません…\nは…恥ずかしいですが", 0, 2, 6109, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121601, "この大怪盗さまに\n盗めないものなんて\nないです！　たぶん！", 0, 1, 6110, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121601, "あたしにふさわしい\nビッグなお宝は\nどこにあるですかね～", 0, 1, 6111, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121601, "そろーりそろーり…\n気配を消せばお菓子も\n食べ放題…にやり", 0, 1, 6112, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121601, "またサボってたのが\nバレたです…\nボスの地獄耳～！", 0, 1, 6113, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121601, "ギャングで大怪盗…！\nこの肩書の多さ！\nまさに有能の証です！", 0, 1, 6114, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 121601, "お兄さんだったら…\nお宝を山分けして\nあげてもいいですよ", 0, 1, 6115, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 121601, "お兄さんはアレですよ\n大怪盗イノリさまの\n相棒みたいなものです", 0, 1, 6116, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121601, "何事も用心深さが\n大切ですよ。ビ…\nビビりじゃないです！", 0, 2, 6117, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121601, "ぬ、盗み聞き？\nしてないですよっ？\nぴゅーぴゅーぴゅー", 0, 2, 6118, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121601, "情報が欲しければ\nお菓子と交換ですよ\nさぁ、交換交換っ♪", 0, 2, 6119, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121601, "しーっ！\n今、尾行中なんです\n話はあとで…", 0, 2, 6120, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 121601, "お兄さんと話してると\n時間が速いような…？\nこの時間泥棒！", 0, 2, 6121, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 121601, "ちょうどよかった！\nいい話があるんです\n一口乗りませんか？", 0, 2, 6122, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121601, "おてんとうさまが\n出てやがるですね…\n悪事がやりにくいです", 1, 2, 6123, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121601, "もうこんな時間ですか\nご飯にお昼寝…\nいやー忙しいです♪", 2, 2, 6124, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121601, "ふっふっふ！　夜こそ\n大怪盗の時間です！\n……ふぁあ", 3, 2, 6125, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121601, "うーん…\n警備の目がないと\n気楽でいいですねぇ", 0, 3, 6127, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121601, "くう…！\nこのかわいさは\n盗めないです…♪", 0, 3, 6128, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121601, "犯行現場に残ったのは\n香水の匂いだけ…\nふふ、おしゃれ大怪盗", 0, 3, 6129, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121601, "意外とこういう場所に\n隠し扉があったり…\nしないです？", 0, 3, 6130, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121601, "もぐもぐ…\nこの美味しさ…\nとんでもない罠ですね", 0, 3, 6132, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121601, "むむむ…ちっとも\n分からないです…\nほとんど暗号ですよ", 0, 3, 6133, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121701, "あら、サレンさんは\nどこに行かれた\nのかしら？", 0, 1, 6135, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121701, "ふんふんふーん♪\nなんだか華やいだ\n気分ですわ♪", 0, 1, 6136, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121701, "さて…次の事業は\nどうしましょう\n何か面白いことは…", 0, 1, 6137, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121701, "おーっほっほっほ！\nあら…楽しくて、つい\n笑ってしまいましたわ", 0, 1, 6138, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121701, "センスがあって\nいいお部屋ですわね\n私も見習いたいですわ", 0, 1, 6139, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 121701, "サレンさんとあのお方\n仲がいいですわね\nも、もちろん仲がいい\nのはよいことですわ！", 0, 1, 6140, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 121701, "どんなときでも\n正々堂々！\nそれが私の\n誇りですわ！", 0, 1, 6141, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121701, "ふふ、ごきげんよう\n私、絶好調でしてよ", 0, 2, 6142, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121701, "どうかされましたか？\n何かお困りごと\nでしたら\n是非力になりますわよ", 0, 2, 6143, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121701, "たまにはこうして\nゆっくりと\nお話しするのも\nいいものですわね", 0, 2, 6144, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121701, "次の事業に関して\n考え事をしてましたの\nよければ相談に乗って\nくださるかしら？", 0, 2, 6145, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 121701, "よろしければ\nお話しませんこと？\nサレンさんのお話を\n聞かせてほしいですわ", 0, 2, 6146, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 121701, "ふふっ…\n先ほどサレンさんと\nあなたさまのお話を\nしていたところですわ", 0, 2, 6147, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121701, "さあ！\n今日も一日\n全力で参りますわ！", 1, 2, 6148, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121701, "お昼はどうしましょう\nサレンさんをお誘い\nしてみようかしら", 2, 2, 6149, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121701, "もう少しだけ…\n明日の準備をして\nおかないと…", 3, 2, 6150, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121701, "はぁ～…いいですわね\nはっ、私としたことが\nはしたないですわね", 0, 3, 6152, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121701, "かわいらしいですわ\nいくつになっても\nぎゅっと\nしたくなりますわね", 0, 3, 6153, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121701, "落ち着きますわね\nサレンさんはどんな\n香りが好きなのかしら", 0, 3, 6154, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121701, "ふふっ、燃え盛る\n情熱の炎…\nいいですわね", 0, 3, 6155, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121701, "このおいしさで\n元手がゼロ…\nすばらしい\n商品ですわ！", 0, 3, 6157, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121701, "これは…ふむふむ\n新しい事業のヒントに\nなりそうですわ", 0, 3, 6158, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121801, "さて、と…\n今日は何をしなくちゃ\nいけないんだっけ？", 0, 1, 6161, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121801, "やることは山積み\nだけど、手を動かす\nのは嫌いじゃないわ♪", 0, 1, 6162, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121801, "あら？　アキノさんの\n声が聞こえたような\n気がしたんだけど…", 0, 1, 6163, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121801, "アキノさんといると\nついこっちまで笑顔に\nなっちゃうのよね", 0, 1, 6164, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121801, "弱音なんか\nはいてられないわ\nアキノさんに\n笑われちゃうもの", 0, 1, 6165, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 121801, "気が付けばアキノさん\nのペースだけど…\n不思議と嫌では\nないのよね", 0, 1, 6166, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 121801, "あたしを好敵手って\n言ってくれる\nアキノさんのためにも\nしっかりしなくちゃ", 0, 1, 6167, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121801, "どうしたの？\nアキノさんなら\nさっきそこに\nいなかった？", 0, 2, 6168, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121801, "あたしなら大丈夫\n救護院だって\nみんなが助けて\nくれるからね", 0, 2, 6169, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121801, "さっきアキノさんが\nあんたを探してたわよ\nなにかしたの？", 0, 2, 6170, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121801, "こう見えてヒマじゃ\nないんだけど…\n少しくらいなら\n付き合ってあげるわ", 0, 2, 6171, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 121801, "ねえ、あんたと\nアキノさんって…\nううん、やっぱり\nなんでもないわ…", 0, 2, 6172, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 121801, "アキノさんも誘って\nお茶でもしない？\n話したいことが\nたくさんあるの♪", 0, 2, 6173, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121801, "早起きは三文の得よ\nいい一日を迎えるため\n目を覚まさなきゃ", 1, 2, 6174, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121801, "今日のお昼は…\nアキノさんと同席する\n予定だったわね\nあんたもどう？", 2, 2, 6175, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121801, "ふぁ…っと、だめね\nたるんだところを\nアキノさんに\n見せるわけには", 3, 2, 6176, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121801, "ん～…\n落ち着くわね\nこのまま\n寝ちゃいそう…", 0, 3, 6178, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121801, "救護院の皆も\nぬいぐるみが\n大好きなのよ", 0, 3, 6179, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121801, "いい香りね…\nこれはアキノさんの\n趣味かしら？", 0, 3, 6180, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121801, "こんなに激しく\n燃えているのに\nあったかくて落ち着く\nまるでアキノさんね", 0, 3, 6181, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121801, "どんどん出てくる…\nこれ、救護院にも\nほしいわね…", 0, 3, 6183, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121801, "ふむふむ…\n前にアキノさんが\n言ってたのは\nこのことかしら？", 0, 3, 6184, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122001, "この衣装、私には\nちょっとかわい過ぎて\n恥ずかしいな…", 0, 1, 6187, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122001, "一部の人からは\n海賊より海賊らしいと\n評判の父です", 0, 1, 6188, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122001, "潮風で錆びないよう\n武器の手入れを\nしておきましょうか", 0, 1, 6189, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122001, "宝探しのお手伝いなら\n力になれると思います\n父のやる気次第ですが", 0, 1, 6190, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122001, "除霊は気を遣います\n一歩間違えると父まで\n成仏させてしまうので", 0, 1, 6191, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 122001, "あの人と見た\n海の景色を…ずっと\n忘れずにいたいな", 0, 1, 6192, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 122001, "幽霊になっても\n一目会えるなら…\n私はきっとあの人に…", 0, 1, 6193, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122001, "ボートを漕ぐの、少し\n上手になったんですよ\n今度お見せしますね", 0, 2, 6194, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122001, "航海に向けた\n買い出しが必要ですね\n予定は空いていますか", 0, 2, 6195, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122001, "呪われた財宝の\n解呪依頼が多くて…\nふぅ…えっと次は…", 0, 2, 6196, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122001, "夜の航海では\n父が明かり代わりに\nなってくれるんです", 0, 2, 6197, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 122001, "船の上では食材が\n限られるので料理の\nいい練習になります", 0, 2, 6198, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 122001, "この先もずっと\nあなたと一緒に\nたくさんの思い出を\n作っていきたいです", 0, 2, 6199, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122001, "あの人の寝顔…\nもう少しだけ\n眺めていたいな…", 1, 2, 6200, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122001, "船の上では\n規則正しい生活が\n重要ですから", 2, 2, 6201, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122001, "夜の海は幽霊が\n見分けやすいんです\n青白く光るので", 3, 2, 6202, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122001, "海に揺られるような\n心地よさですね\n気が休まります…", 0, 3, 6204, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122001, "あなたも一緒に\n海を見に行きますか？\n…なんて。ふふっ", 0, 3, 6205, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122001, "爽やかな香りですね…\n一面の青い海が\n目に浮かぶようです", 0, 3, 6206, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122001, "温かい…\n潮風で冷えた体に\n沁みますね", 0, 3, 6207, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122001, "あ、おいしい…\n日持ちするなら船旅に\n持っていこうかな…", 0, 3, 6209, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122001, "アンナさんに教えて\nもらった小説は…\nあ、これですね", 0, 3, 6210, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 102601, "なにもしないを\nしてるんだよ\nあ～いそがしいそがし", 0, 1, 6212, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 102601, "はぁ～あんたといると\nどこまでも気持ちよく\nダラダラできるね～", 0, 2, 6213, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121901, "暗黒騎士にして、\n伝説の海賊でもある\n私に不可能はないっ！", 0, 1, 6214, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121901, "海賊団の船員、\n随時募集中です…と\nこれで新たな盟友が\n増えるはずだ…！", 0, 1, 6215, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121901, "肩に乗せるペットが\nほしいな。今のうちに\nペット用品を買いに\n行かなくては…！", 0, 1, 6216, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121901, "財宝の地図はどこで\n売っているのだろう…\n古本屋？　古物商？", 0, 1, 6217, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121901, "この海賊衣装に\n身を包んでいると\n前世の新たな記憶が\nよみがえってくるな！", 0, 1, 6218, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 121901, "いずれはシグルドと\n海原への冒険に\n繰り出したいな…\nきっと楽しいぞ！", 0, 1, 6219, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 121901, "待ちわびたぞ盟友よ！\n私と貴様あっての\n海賊団…だからその…\n今日はそばにいてくれ", 0, 1, 6220, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121901, "シグルド！　港町で\nお魚のおすそ分けを\nもらったんだ！\n一緒に食べよう！", 0, 2, 6221, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121901, "シグルドもこのコート\n羽織ってみないか？\nふふっ、きっとすごく\n似合うと思うぞ！", 0, 2, 6222, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121901, "海賊船の新たな\n秘密兵器を作ったぞ！\nシグルド、これから\n見に来ないか！？", 0, 2, 6223, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121901, "私は伝説の海賊だから\n泳ぐのだって得意だ！\nふ、深いところは\n少し怖いが…", 0, 2, 6224, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 121901, "ふふ、いつ無人島に\n漂着してもいいように\n非常食は準備済みだ\nシグルドの分もある！", 0, 2, 6225, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 121901, "冥風戦記の新章が\n完成したぞ…！\nシグルド、まずは\n貴様に読んでほしい！", 0, 2, 6226, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121901, "ふっ、天気明朗\n絶好の航海日和だ！\n早速海に繰り出そう！", 1, 2, 6227, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121901, "昼食用に魚を\n釣っておいたぞ！\nふふ、いかにも\n海賊っぽいだろう？", 2, 2, 6228, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121901, "夜の海は真っ黒で\n少しだけ怖いな…\nシグルド、私の\nそばを離れるなよ…！", 3, 2, 6229, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121901, "大海賊がくつろぐのに\n相応しい逸品だ…！\nえっと…これ、どこの\nお店で買えますか？", 0, 3, 6231, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121901, "わわ、かわいい…！\n船室のベッドに\n置いておきたいな…\n連れてってはダメか？", 0, 3, 6232, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121901, "おぉ、この香りは…！\n集中力が上がりそうだ\n海賊船の船長室に\nぜひ置きたいな！", 0, 3, 6233, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121901, "これが船乗りの\n伝承に聞く、海原を\nさまよう神秘の火か！\n…暖まるな！", 0, 3, 6234, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121901, "腹が減っては\n海賊はできぬ！\n…小説も書けぬ！", 0, 3, 6236, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 121901, "女海賊と騎士はその後\n恋に落ち…？　恋愛\nモノではないかっ！？\nき、興味深くはあるが", 0, 3, 6237, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122101, "いつもの山籠もりが\nブームになるなんて…\n人生、なにが起きるか\n分からないなぁ", 0, 1, 6239, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122101, "キャンプ道具…\nいえ、キャンプギア！\nカッコいい響き…♪", 0, 1, 6240, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122101, "レッツ、ＢＢキャン！\nこのフレーズ\n流行るかも…？", 0, 1, 6241, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122101, "キャンプ仲間…\n募集してみようかな？\n友達も兼任で…", 0, 1, 6242, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122101, "ユカリさんと\nジュンさん…元気かな\nまた一緒に\nキャンプしたいなぁ…", 0, 1, 6243, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 122101, "私の取材が載った本…\n【フォレスティエ】に\n送らなくちゃ…♪", 0, 1, 6244, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 122101, "もし、あの人が\n落ち込むことが\nあったら…その時は\n私が力になりたいな", 0, 1, 6245, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122101, "ブッシュクラフトは\nマイフレンドくんの\nカスタムにも\n活かせる技術です！", 0, 2, 6246, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122101, "会話に花が咲く…\nって言いますよね\n私の場合、会話相手が\nお花そのものですけど", 0, 2, 6247, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122101, "この服、実は…\n昔ミサト先生に\nこしらえてもらった\n物なんです！", 0, 2, 6248, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122101, "昔はやまびこで会話の\n練習をしてたんです\nほとんどオウム返しで\n気まずかったなぁ～", 0, 2, 6249, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 122101, "どうもマイスターです\n…あ、すみません\nみなさんの期待に\n応えようと練習中で…", 0, 2, 6250, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 122101, "たとえぼっちを\n卒業しても…\n私は一生ＢＢ団です！", 0, 2, 6251, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122101, "小鳥さんのさえずりで\n目が覚める…\n心地いいものですね", 1, 2, 6252, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122101, "キャンプといえば\nおいしいご飯です！\n腕を振るいますよ！", 2, 2, 6253, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122101, "すっかり夜ですね…\nおやすみの前に\n星でも見ませんか？", 3, 2, 6254, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122101, "都会でくつろぎ…\n自然の中でくつろぐ…\nくつろぎ無限ループ…", 0, 3, 6256, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122101, "しっかり観察すれば\nブッシュクラフトで\n再現できるかも…？", 0, 3, 6257, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122101, "はぁ～…やっぱり\n木の香りを嗅ぐと\n肩の力が抜けますねぇ", 0, 3, 6258, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122101, "ああ…あったまります\nなんだか心に\nゆとりがでますね…", 0, 3, 6259, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122101, "このおいしさ…！\n今度はキャンプに\n持っていこうかな…", 0, 3, 6261, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122101, "ふむふむ…\nいろんな知識があると\nキャンプはもっと\n楽しくなりますよね", 0, 3, 6262, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122201, "部屋がキャンプ用品で\n埋まっていく…\nキャンプ…なんて\n恐ろしい趣味なの…？", 0, 1, 6264, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122201, "仕事でも\nアウトドアでも\n頼れるお姉さんに\nなれたらいいなぁ…", 0, 1, 6265, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122201, "この服、かわいい上に\n本当に快適ね～♪\nおしゃれにキャンプを\n楽しめて最高だわ！", 0, 1, 6266, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122201, "ん〜次はどんな\nキャンプ飯を\n作ろうかしら？\n彼の好物は、っと…", 0, 1, 6267, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122201, "早く週末が\n来ないかしら…\n仕事を忘れてパーっと\nキャンプ飲みを…！", 0, 1, 6268, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 122201, "新しいキャンプ飯の\nレシピを覚えたわ！\n彼も喜んで\nくれるはず…よね♪", 0, 1, 6269, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 122201, "彼の分の食器も\nこっそり買い揃えちゃ\nおうかな…そ、それは\nさすがにまだ早いか…", 0, 1, 6270, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122201, "今度、財団主催で\n大規模なキャンプを\nやってみるのも\nいいかもしれないわね", 0, 2, 6271, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122201, "ソロキャンプも\n悪くはないけど\n私はやっぱりキミと…\nな、なんでもない！", 0, 2, 6272, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122201, "キャンプのことなら\n私に聞いて。なんでも\n教えてあげるから！\n…調べた上でね！", 0, 2, 6273, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122201, "キャンプ場で飲む\n最高の一杯は\n他じゃ味わえないわ！\nさあ、乾杯っ！", 0, 2, 6274, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 122201, "うん、決めた！　次は\n禁しゅわキャンプ！\nだってキミとゆっくり\n話したいんだもん♪", 0, 2, 6275, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 122201, "キミのおかげで\n本当の最高の一杯が\n味わえたわ…\nありがとう♪", 0, 2, 6276, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122201, "んー、気持ちいい！\nキャンプが趣味に\nなってから、寝覚めが\nよくなったわ♪", 1, 2, 6277, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122201, "お昼は私がごちそう\nするわ。キャンプ飯で\n鍛えた料理の腕前を\n披露しちゃうわよ～！", 2, 2, 6278, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122201, "そろそろ寝ましょ？\n明日も朝日を眺める\nためにね。もちろん\nコーヒー付きで♪", 3, 2, 6279, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122201, "くつろぐのに\n持ってこいね～\nテントの中に置いても\nよさそうだわ！", 0, 3, 6281, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122201, "ふふ、かわいいわね～\n…今度キャンプ場に\n連れていこうかしら？", 0, 3, 6282, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122201, "うん、いい匂い…♪\nキャンプ場を思い出す\n自然の香りだわ〜", 0, 3, 6283, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122201, "はぁ、温まるぅ～\n冬のキャンプで\n使ってみたいわね", 0, 3, 6284, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122201, "おいしい～♪\nキャンプ飲みの\nおつまみに\nぴったりね！", 0, 3, 6286, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122201, "へえ…こんなキャンプ\n飯もあるんだ？\n彼、好きだったり\nするかな…", 0, 3, 6287, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 100701, "一生ついててやるの\n幽霊の一生は\nなっが～～～いの！", 0, 1, 6289, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 100701, "む～…あんまり触ると\nすり抜けてやるの！\nウソなの。むふふ♪", 0, 2, 6290, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122301, "んー…\n今日はベスちゃんと\nなにして遊ぼーかなー", 0, 1, 6291, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122301, "ヴァンピィちゃんも\nけんぞくの友だちと\n仲良くなりたいな♪", 0, 1, 6292, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122301, "けんぞくのお部屋は\nヴァンピィちゃんが\n守ってあげなくちゃ！", 0, 1, 6293, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122301, "お掃除するなら\nヴァンピィちゃんも\n手伝ってあげよー♪", 0, 1, 6294, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122301, "ふーん、ふーん♪\nけんぞくってこーゆー\nお部屋が好きなんだー", 0, 1, 6295, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 122301, "ひまだなー\nけんぞくをさそって\nお散歩いこっかな♪", 0, 1, 6296, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 122301, "今日のスープに\nけんぞくの大好物を\n入れてあげよっと♪", 0, 1, 6297, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122301, "けんぞくー！\nヴァンピィちゃんと\nお出かけしよ♪", 0, 2, 6298, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122301, "よしよし！\n今日のけんぞくも\nいい子だね♪", 0, 2, 6299, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122301, "んんー…\nやっぱりけんぞくと\nベスちゃんは似てる？", 0, 2, 6300, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122301, "いーい？\nヴァンピィちゃんの\nいうこと聞くのよ？", 0, 2, 6301, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 122301, "ヴァンピィちゃんの\nけんぞくだもん！\n大切にするからね！", 0, 2, 6302, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 122301, "えへへっ！\nやっぱりけんぞくって\nやさしー匂いがする♪", 0, 2, 6303, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122301, "おはよー…\nヴァンピィちゃんは\nもうちょっと眠るねー", 1, 2, 6304, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122301, "ヴァンピィちゃん\nひなたぼっこ\nだーいすき！", 2, 2, 6305, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122301, "んー！　夜だ♪\nヴァンピィちゃん\n絶好調だよ！", 3, 2, 6306, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122301, "わー！\nふかふかだ！\nきもちぃー♪", 0, 3, 6308, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122301, "かわいー♪\nベスちゃんに負けない\n抱き心地！", 0, 3, 6309, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122301, "この香り…なんだろ？\nヴァンピィちゃん\n気に入っちゃった♪", 0, 3, 6310, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122301, "あったかいねー…\nヴァンピィちゃん\nずっとここにいるー…", 0, 3, 6311, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122301, "はむはむ…\nんー！\nおいしー♪", 0, 3, 6313, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122301, "ヴァンピィちゃん\nこの本\n好きなんだよねー♪", 0, 3, 6314, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122501, "夏といえば\nかき氷だよね\n近くにお店は…", 0, 1, 6316, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122501, "この格好でいるのも\nなじんできたし\nちょっと鍛錬でも…", 0, 1, 6317, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122501, "動きやすいけれど…\nいつもと違う服装は\n少し落ち着かないな", 0, 1, 6318, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122501, "夏の空気に\n当てられると思わず\n気が緩んでしまうね", 0, 1, 6319, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122501, "砂浜で走ると\nいい具合に体幹を\n鍛えられそうだ", 0, 1, 6320, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 122501, "終わらない夏は\nないけれど…\nつい願ってしまうな", 0, 1, 6321, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 122501, "胸を張って彼と\n並び立てるよう\n私も、もっと強く…！", 0, 1, 6322, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122501, "どうしたの？\n行きたい場所が\nあるなら付き合おうか", 0, 2, 6323, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122501, "快晴で風は穏やか…\n絶好の釣り日和だね\nさあ、出かけようか", 0, 2, 6324, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122501, "今日の予定を\n立ててるんだけど…\nキミの希望も\n聞きたいな", 0, 2, 6325, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122501, "ビーチスポーツか…\nキミとなら\nいい勝負ができそうだ", 0, 2, 6326, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 122501, "私たちの夏に\n遠慮は無用だよ\n思いきり楽しもう！", 0, 2, 6327, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 122501, "いつかまた一緒に\n花火を見に行こう\n…約束だよ？", 0, 2, 6328, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122501, "潮騒の音で\n目覚めるのも\nいいものだね", 1, 2, 6329, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122501, "今朝釣ってきた魚が\nあるんだ。これで\nお昼ご飯にしようか", 2, 2, 6330, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122501, "夜にもイベントが\nあるみたいなんだ\n一緒に見に行かない？", 3, 2, 6331, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122501, "気持ちが安らぐね…\nうっかり眠って…\nしまい…そうだ…", 0, 3, 6333, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122501, "肌触りのよさが\nこの格好だと\nよく分かるよ", 0, 3, 6334, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122501, "海をイメージさせる\n香りだね…\n爽やかで落ち着くよ", 0, 3, 6335, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122501, "濡れたままだと\n風邪を引いてしまうよ\nほら、キミもおいで", 0, 3, 6336, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122501, "夏を楽しみきるには\nエネルギーが\n必要だからね", 0, 3, 6338, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122501, "大切な人と過ごせる\n観光スポット紹介…\n…こ、このページは\nもういいかな！", 0, 3, 6339, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122501, "濡らしてもいいなら\n海に持っていって\n乗るのもよさそうだ", 0, 3, 6341, 10, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122501, "こういう名画を\n見た気がするな…\nこんな視点なんだね", 0, 3, 6342, 11, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122401, "水着って\n動きやすくて\nいいよねー♪\nいっちにーさんしー♪", 0, 1, 6343, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122401, "ユイちゃんにもらった\n日焼け止め\n塗っておいて\nよかった～", 0, 1, 6344, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122401, "なんだか暑いなあ…\nせっかくだし\nプールで泳いで\nこようかな？", 0, 1, 6345, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122401, "この格好で\n出かけようとしたら\nレイさんに\n怒られちゃった…", 0, 1, 6346, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122401, "たとえ水着姿でも\n困ってる人を見たら\n助けに行くからね！", 0, 1, 6347, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 122401, "バカンス楽しかった～\nまた騎士くんと\n一緒に行きたいな♪", 0, 1, 6348, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 122401, "この水着、あたしに\n似合ってるかなあ？\n騎士くんはどう\n思ってるんだろ…", 0, 1, 6349, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122401, "騎士くん！\n海に行くなら\n準備運動\n忘れずにね！", 0, 2, 6350, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122401, "泳ぐ練習がしたいの？\nいいよ！　水着だから\nなにかあったらすぐに\n助けてあげるね！", 0, 2, 6351, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122401, "騎士くんの水着姿も\nかっこよかったよ！\nまた遊びに行こ！\n約束ね！", 0, 2, 6352, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122401, "このグローブの向日葵\nかわいいでしょ？\nユイちゃんが\n選んでくれたんだ♪", 0, 2, 6353, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 122401, "実はこの水着\nいつも着てる\n服とおなじなんだ…\nって、冗談だよ？", 0, 2, 6354, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 122401, "変な日焼けしてないか\n見てもらってもいい？\n…なし！　今のなし！\nやっぱりなしー！", 0, 2, 6355, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122401, "夏のお日様に\n負けないよう\n今日も元気いっぱい\nがんばるぞー！", 1, 2, 6356, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122401, "お腹減ってきちゃった\n騎士くんはお腹\n空いてない？\n…聞こえちゃった♪", 2, 2, 6357, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122401, "今日も一日\n楽しかったー！\n明日もまた\n楽しもうね！", 3, 2, 6358, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122401, "ふわあ…\nふわふわなのが\nいつもより\nよくわかるよー…", 0, 3, 6360, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122401, "そうだ！　水着なら\n濡れてもいいし\n今度洗ってあげるね♪", 0, 3, 6361, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122401, "んー…いい匂い♪\nなんだか\n身体がぽかぽか\nしてきちゃった！", 0, 3, 6362, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122401, "水着だから、えっと…\n暖か涼しい…！\n不思議な感じー", 0, 3, 6363, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122401, "思いっきり身体を\n動かした後だから\nいくらでも\n食べちゃえるよー！", 0, 3, 6365, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122401, "ふむふむ…\nなるほどー\n水着の種類って\nそんなにあるんだぁ…", 0, 3, 6366, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122401, "乗り心地バッチリだね\nしゅっぱつしんこー！\nなんちゃって♪", 0, 3, 6368, 10, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122401, "わっ、ぽよんぽよん！\nトランポリンみたいで\nた～のし～！", 0, 3, 6369, 11, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 101501, "彼のことを考えてると\n幸せで胸が温かくなる\n…私ったら、もう", 0, 1, 6370, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 101501, "うふふふふ\nくすぐったいわ～♪\nもう、イタズラっ子ね\n次は先生の番よ～♪", 0, 2, 6371, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122601, "騎士クンの汗\n拭いてあげたいな…\n余計なお世話かな…？", 0, 1, 6372, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122601, "夏バテ気味かも…\nそうだ、今夜は\nかば焼きにしよう♪", 0, 1, 6373, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122601, "青い空、白い雲\n眩しい日差し…！\n夏って好きだな…", 0, 1, 6374, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122601, "ヒリヒリする…？\n日に焼けちゃったかな\nふー…ふー…", 0, 1, 6375, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122601, "花火大会があるんだ…\n夜、浜辺…2人きり…\nむ、無理だよ〜！", 0, 1, 6376, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 122601, "水着って不思議…\n引っ込み思案が少し\nおさまる…気がする", 0, 1, 6377, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 122601, "騎士クンとまた\n海に行きたいな…\nまた、一緒に…", 0, 1, 6378, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122601, "ダ、ダイエット！？\nしてないよ！？\n元々これ位だよ…？", 0, 2, 6379, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122601, "スイカ割りのコツ？\nわたしは、騎士クンに\n教わりたいな…", 0, 2, 6380, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122601, "騎士クン。うちわで\n扇いであげるね\nどこに置いたかな…", 0, 2, 6381, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122601, "うぅ…暑いね…\n騎士クン、温度計は\n見ない方がいいよ～", 0, 2, 6382, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 122601, "じ、じっと見ないで…\nええっと…ちょっと！\nちょっとならいいよ…", 0, 2, 6383, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 122601, "え、肝試し？\nう〜ん、うう〜ん…\n…騎士クンとなら！", 0, 2, 6384, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122601, "おはよう騎士クン\n見て、あっちの空\nもう入道雲が出てる", 1, 2, 6385, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122601, "夕立、来るかなぁ…\nもし雷が鳴ったら\nそばにいてくれる…？", 2, 2, 6386, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122601, "しー。…ほら、虫の声\n…わ、わたしの心臓の\n音は気にしないで…！", 3, 2, 6387, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122601, "ふふ、水着でゴロゴロ\nするのってなんだか…\nいけないことみたい", 0, 3, 6389, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122601, "かわいい～♪\n日光浴させて\nあげたいな♪", 0, 3, 6390, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122601, "ほんのり潮の香りが…\n香ばしくて塩辛くて…\nあ…おなか鳴っちゃう", 0, 3, 6391, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122601, "水着、乾かせるかも…\nそうだ、みんなの分も\n持ってこよう！", 0, 3, 6392, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122601, "夏バテなんてしてたら\nみんなの足を\n引っ張っちゃうよね\nいただきます！", 0, 3, 6394, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122601, "騎士クンの、どうして\n海はしょっぱいの？\nって質問の答えは…\n分かんないよぉ～", 0, 3, 6395, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122601, "顔は怒ってるのに\nなんだかかわいい…\n誰かに似てる…？", 0, 3, 6397, 10, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122601, "これを枕に、ビーチで\n騎士クンと…なんて…\nなんて…なんて…！", 0, 3, 6398, 11, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122901, "夏休み、\n毎日たのしいな～♪\nつぎはなにしよう？", 0, 1, 6399, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122901, "自由研究、\nなににしよっかな～？\nプチグリフォンさんの\nかんさつにっきとか？", 0, 1, 6400, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122901, "夏休みって、\n給食ないから…\nそれはちょっぴり\nさみしいかも…？", 0, 1, 6401, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122901, "砂浜をはだしで歩くの\nきもちいいよね♪\nくすぐったいけど\nミミ、好きなんだ～♪", 0, 1, 6402, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122901, "きれいな貝がら\n見つけちゃった♪\nミミのたからもの！", 0, 1, 6403, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 122901, "ミミ、浴衣を\n買ってもらったんだ♪\n今度、おにいちゃんに\n見せてあげたいな！", 0, 1, 6404, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 122901, "ミミ、おにいちゃんと\nいっぱい夏休みの\n思い出つくりたいな♪", 0, 1, 6405, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122901, "ねえ、おにいちゃん…\n夏休みの宿題、\nわかんないとこ\nおしえてくれる…？", 0, 2, 6406, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122901, "おかあさんにね、\n麦茶つくるの上手って\nほめられたんだ～♪\n飲んでみて♪", 0, 2, 6407, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122901, "夏休みの宿題で\nポスターかくんだ～♪\nおにいちゃんを\nかいてもいい？", 0, 2, 6408, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122901, "線香花火やりたいな♪\nミミ、長持ちさせるの\n上手なんだよ♪", 0, 2, 6409, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 122901, "おにいちゃんの\n好きな本ってなあに？\nその本で読書感想文\n書こうかな♪", 0, 2, 6410, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 122901, "海であそんだ後ね、\nおにいちゃんに\n髪を拭いてもらうの\nすきなんだ～♪", 0, 2, 6411, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122901, "夏休みって、\nワクワクして\n早起きしちゃうよね♪", 1, 2, 6412, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122901, "わぁ♪　お昼は、\n流しそうめんなんだ？\nミミ、流してみたい♪", 2, 2, 6413, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122901, "こわい話聞いて、\nねむれないの…\nねえ、おにいちゃん、\nいっしょに寝ていい？", 3, 2, 6414, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122901, "ちょっぴりやすんだら\nまた、いっしょに\n海であそぼうね♪", 0, 3, 6416, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122901, "すっごくかわいいね♪\nこの子のこと、\n絵日記にかこうっと♪", 0, 3, 6417, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122901, "くんくん…\nスイカのにおいだね♪\nこのにおいだいすき！", 0, 3, 6418, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122901, "およいだあとは、\nちゃんと体を\nあっためなくちゃね！", 0, 3, 6419, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122901, "夏休みのご飯って\nなんだかとくべつ\nおいしい気がするね♪", 0, 3, 6421, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122901, "夏の星座って\nすっごくきれいだね♪\nいつかみんなと\nいっしょに見たいな♪", 0, 3, 6422, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122901, "わぁ♪　この子、\nとってもかわいいね！\nおともだちに\nなりたいな♪", 0, 3, 6424, 10, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122901, "ミミ、人魚姫に\nなったみたい！\nおにいちゃん、\nとなりにすわって♪", 0, 3, 6425, 11, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122801, "水着姿になると\nバカンス気分を\n味わえちゃうね！", 0, 1, 6426, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122801, "海に行くの待ちきれ\nなくて、もう水着に\n着替えちゃった！", 0, 1, 6427, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122801, "海に行ったら\nずぶぬれ系のイタズラ\nたくさんしかけよう！", 0, 1, 6428, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122801, "部屋の中で泳ぎの練習\nしよう！　にいちゃん\nも水着に着替えて！", 0, 1, 6429, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122801, "お日さまとお揃いの\n水着なんだ～♪\n似合ってるかなぁ？", 0, 1, 6430, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 122801, "海といえば冒険！\n海底に眠る財宝を\n見つけちゃおう！", 0, 1, 6431, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 122801, "にいちゃんと一緒だと\n部屋にいても海気分♪\nスイカわりしよっか！", 0, 1, 6432, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122801, "にいちゃん！\n早く海に行く準備\nしなよ～！", 0, 2, 6433, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122801, "ねーにいちゃん\n何秒息止められるか\n勝負しよーよ！", 0, 2, 6434, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122801, "にいちゃんはアイスと\nかき氷どっちが好き？\nミソギは…どっちも！", 0, 2, 6435, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122801, "日焼け止めクリーム\nキョウカに\nもらったんだ～", 0, 2, 6436, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 122801, "水中メガネがあれば\n水鉄砲くらっても\n平気だね！", 0, 2, 6437, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 122801, "水着なら、毎日夏休み\n気分！　にいちゃんも\n着てみなよ～！", 0, 2, 6438, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122801, "にいちゃんおはよー！\n早く顔洗って、\nいっぱい遊んじゃお！", 1, 2, 6439, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122801, "にいちゃんデザートは\nイチゴのかき氷だよ！\n…激辛ってバレた？", 2, 2, 6440, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122801, "今日は楽しかった～！\n明日もイタズラ日和に\nなるといいな！", 3, 2, 6441, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122801, "落とし穴の下に\nやわらかいの置けば\nケガしないよね！", 0, 3, 6443, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122801, "かわいいね～！\nこの子も海に\n連れてっちゃおうよ！", 0, 3, 6444, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122801, "このにおいを利用して\nイタズラするには…\nにいちゃんどう思う？", 0, 3, 6445, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122801, "水着を着て暖炉で\nあったまるのって、\n変でおもしろいね～！", 0, 3, 6446, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122801, "海辺で食べる\nお昼ごはんって最高♪\nってことで海行こう！", 0, 3, 6448, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122801, "いつか海を越えて、\n地図にも載ってない\n場所を見つけたいな！", 0, 3, 6449, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122801, "この口から水が出たら\nみんな驚くよね！\nぷぷ…細工しちゃお！", 0, 3, 6451, 10, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122801, "変な音がするイタズラ\nを仕込んどいて…\n誰か座らないかな～♪", 0, 3, 6452, 11, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 102701, "あなた様と私の愛を\n周りに見せつけて\n差し上げましょう♪", 0, 1, 6453, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 102701, "呼びましたか？\n呼びましたね？\nでは愛を語りましょう\nそうしましょう", 0, 2, 6454, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122701, "水着で歩き回るのは\n恥ずかしいな…\n誰も見てないよね？", 0, 1, 6455, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122701, "夏はやっぱり\nかき氷ですよね\n何色がいいかなぁ…♪", 0, 1, 6456, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122701, "あの人、泳げないって\n聞きました！\n私が教えてあげないと", 0, 1, 6457, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122701, "夏休みの宿題は\n計画的に進めましょう\n当たり前のことです！", 0, 1, 6458, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122701, "防犯魔石、どこに\n着けておこうかなぁ\n…ここにしようかな", 0, 1, 6459, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 122701, "海の楽しみ方は\n予習済みです！\nあとは実践ですね♪", 0, 1, 6460, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 122701, "水着…きっとあの人に\n見られちゃいますよね\n…褒めてくれるかな…", 0, 1, 6461, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122701, "触りたいんですか？\n…いいですよ\n帽子くらいなら", 0, 2, 6462, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122701, "夏は体調管理に\n気をつけましょう\n私が管理してあげます", 0, 2, 6463, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122701, "海に行くのが楽しみで\nたまらないんですか？\n私はそこまで…嘘です", 0, 2, 6464, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122701, "いつもより薄着だから\nなんだか恥ずかしい…\nみ、見ないでください", 0, 2, 6465, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 122701, "へんたいふしんしゃ～\nえっ？　ああ、これは\n海に叫ぶ練習です", 0, 2, 6466, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 122701, "あなたと一緒に\n海に行くなんて…\nなんだか大人…えへへ", 0, 2, 6467, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122701, "おはようございます\nさぁ、海に行って\n私と運動しましょう", 1, 2, 6468, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122701, "あっつ～い…\nはっ！？　ダメですよ\nシャキッとしないと！", 2, 2, 6469, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122701, "明日も海で\n遊ぶために、しっかり\nお休みしましょうね", 3, 2, 6470, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122701, "夏を楽しむときは\n休憩も大事ですよね\n計画的に、計画的に！", 0, 3, 6472, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122701, "かわいいなぁ～\n海のお土産屋さんに\n売ってるかなぁ？", 0, 3, 6473, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122701, "大人な香りがします\n私も違いが分かる\n素敵な女性になります", 0, 3, 6474, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122701, "濡れた体を\n乾かしましょう\nタオル、タオル…", 0, 3, 6475, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122701, "泳いだ後は\n栄養補給…だけど\n…おなか膨らんじゃう", 0, 3, 6477, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122701, "遊ぶだけじゃなくて\nお勉強もしてますよ\nあなたもどうですか？", 0, 3, 6478, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122701, "ミミが喜びそう…♪\nわ、私ははしゃいだり\nしませんけど…っ！", 0, 3, 6480, 10, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 122701, "ミソギがいたずらに\n使いそう…しっかりと\n試しておかないと！", 0, 3, 6481, 11, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123001, "あたしがまさか\nここにいるなんて…\n自分でも不思議だわ", 0, 1, 6482, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123001, "昔みたいにあちこち\n飛び回れないのって\nちょっと不便かも", 0, 1, 6483, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123001, "こうして誰かと一緒に\n暮らせるのもあいつや\nみんなのおかげね", 0, 1, 6484, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123001, "ここで厄介になるなら\nあたしも家事とか\n手伝わないと…", 0, 1, 6485, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123001, "…だめ！　誰かの\n世話をせずには\nいられないわ！", 0, 1, 6486, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 123001, "あいつたちが\n帰ってくるところだし\n綺麗にしないと…", 0, 1, 6487, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 123001, "せっかくあたしが\nいるっていうのに\n少しは構いなさいよ…", 0, 1, 6488, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123001, "おいしい店とか\n知らないの？\n食べに行きましょ", 0, 2, 6489, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123001, "あんた暇でしょ？\nあたしのやること\n手伝いなさい", 0, 2, 6490, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123001, "コッコロたんと\n遊んでくるから\n留守番よろしく♪", 0, 2, 6491, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123001, "あらおかえり…って\nボロボロじゃない！\nまた人助けしてたの？", 0, 2, 6492, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 123001, "やっぱりあたしは\nあんたのそばが\n一番落ち着くみたい", 0, 2, 6493, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 123001, "どこまでだって\nあんたと一緒に\nいてやるんだから", 0, 2, 6494, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123001, "ふわぁ…おはよう\n…大きくなったせいか\n朝は特に体が重いわ", 1, 2, 6495, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123001, "お昼ご飯が食べたい？\nしょうがないわね…\nなんか作って\nあげるから", 2, 2, 6496, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123001, "あんたまだ成長期だし\nさっさと寝なさい\n大きくなれないわよ？", 3, 2, 6497, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123001, "たまにはあたしも\nゆっくりしないと\nもたないのよ…", 0, 3, 6499, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123001, "かわいいじゃな～い♪\nコッコロたんに\nプレゼントしたいわ", 0, 3, 6500, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123001, "いい香りね\n塔にもこういうの\n置いておこうかしら", 0, 3, 6501, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123001, "炎か…前は\n羽が焼けそうで\n怖かったっけ", 0, 3, 6502, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123001, "あむ…妖精に\n体重増減の設定って\nあったっけ…？", 0, 3, 6504, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123001, "記憶のデータと違って\n物語はいいわね\n結末が読めなくて", 0, 3, 6505, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123101, "冒険のお土産\nみんなにおすそわけ\nせなあきまへんな", 0, 1, 6507, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123101, "おとぎ話よりも\n不思議なことが\nあるもんやねぇ", 0, 1, 6508, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123101, "冒険しに出かけるなら\n動きやすい服装に\nせななぁ", 0, 1, 6509, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123101, "地図に水筒に…\n他に忘れもんは\nあらへんやろか？", 0, 1, 6510, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123101, "これまでの冒険のこと\nノートに書いて\nまとめてますんよ", 0, 1, 6511, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 123101, "王子はんとの冒険は\nどれもマホマホ王国の\n大事な歴史や♪", 0, 1, 6512, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 123101, "どこへ冒険するときも\nうちと王子はんは\n一緒どす♪", 0, 1, 6513, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123101, "魔法で火ぃつける\n練習してますんよ～\nよう爆発しますけど…", 0, 2, 6514, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123101, "うちと王子はんで\n冒険道具をお揃いに\nするんもええなぁ♪", 0, 2, 6515, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123101, "テント張りも随分\n上達したんよ\n王子はんのおかげやね", 0, 2, 6516, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123101, "ランスロットたちの\n新しい冒険衣装\n思いついたんよ♪", 0, 2, 6517, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 123101, "迷子になっても\n王子はんと一緒なら\nきっと大丈夫やね♪", 0, 2, 6518, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 123101, "王子はんのこと、\nきっちり守れるように\nなってみせますえ", 0, 2, 6519, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123101, "お寝坊さんは\n布団めくって\n起こしにいきますえ～", 1, 2, 6520, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123101, "支度が終わったら\n今日の冒険に\n出かけまひょか", 2, 2, 6521, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123101, "もうちょっと\nおしゃべりしたいん？\nあと少しだけやで♪", 3, 2, 6522, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123101, "冒険で疲れた体に\nこの癒しぱわーは\nあきまへん…すや…", 0, 3, 6524, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123101, "ふふ、あんさんも\nうちと一緒に\n冒険します？", 0, 3, 6525, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123101, "虫除けのアロマは\n冒険の必需品どす\n忘れんようにせんと", 0, 3, 6526, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123101, "うちも魔法使わんでも\n火ぃ起こせるよう\n練習せんとあかんなぁ", 0, 3, 6527, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123101, "この料理…\n冒険するときに\n作ってみまひょか", 0, 3, 6529, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123101, "王子はんとの\n愛と冒険のおとぎ話…\nはぁ…素敵やわぁ♪", 0, 3, 6530, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 103101, "あの人との\n最良の未来のために\n占いも霊能力ももっと\n精進しなきゃ", 0, 1, 6532, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 103101, "あなたともっと深く\nつながりたいです\n絆も、そして魂までも", 0, 2, 6533, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 104401, "絆の契約…\nあやつが望むのなら\n考えぬでもないがのう", 0, 1, 6534, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 104401, "なんじゃ？\nわらわの姿に\n見惚れたかの？", 0, 2, 6535, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123201, "旅の荷物って多いな…\nねぇぷうきち～\nすこしは持ってよぉ～", 0, 1, 6536, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123201, "傘はなくてもいっか\nぷうきちがいるし♪\n『おいなんだと…？』", 0, 1, 6537, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123201, "旅先で人見知りなんか\nしてちゃだめだよね…\nまずはあいさつだ…！", 0, 1, 6538, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123201, "おみやげはやっぱり\nご当地ぬいぐるみ\nだよね♪　ワクワク…", 0, 1, 6539, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123201, "知らない場所かぁ…\nうぅ…考えただけで\nすでにホームシック…", 0, 1, 6540, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 123201, "野宿するってことは…\nお兄ちゃんのマントで\n寝るチャンスかも…！", 0, 1, 6541, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 123201, "どんなに危険な冒険も\nお兄ちゃんと一緒なら\n大丈夫！　…だよね？", 0, 1, 6542, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123201, "ぷうきちのアイマスク\nも腹巻も持ってくの！\n旅の必需品なの～！", 0, 2, 6543, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123201, "これ全部背負うのって\nけっこうむずかしいの\n順番まちがえると大変", 0, 2, 6544, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123201, "えへへ～\nなでなでして？　帽子\nの上からでもいいよ♪", 0, 2, 6545, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123201, "『悪いが絆創膏の部分\nはアンタッチャブルだ\n綿が飛び出ちまう』", 0, 2, 6546, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 123201, "お兄ちゃんと一緒に\nもっともっといろんな\n景色が見たいな！", 0, 2, 6547, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 123201, "旅って、終わる時は\nきっとさびしいよね…\nお兄ちゃん、帰り道は\n手をつないでもいい？", 0, 2, 6548, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123201, "出発が楽しみすぎて\n寝られなかった…\n『旅行のお約束だな』", 1, 2, 6549, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123201, "わぁ　いいお天気！\n旅の間もずーっと\nぴーかんだといいな♪", 2, 2, 6550, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123201, "知らない場所で寝るの\n怖いな…寝る時だけ\n帰ってこようかな…", 3, 2, 6551, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123201, "旅先にはクッションも\nソファもないよね…\n『俺でいいだろ』", 0, 3, 6553, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123201, "ぬいぐるみのみんな\n旅の無事を\n祈ってて…！", 0, 3, 6554, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123201, "旅先の香りも\n思い出に持って\n帰れたらいいのにな～", 0, 3, 6555, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123201, "旅行用に携帯用暖炉\nがあるといいかも…！\n『まだ荷物増やす？』", 0, 3, 6556, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123201, "『もう出発だぜ？』\nえーまってまって！\n腹ごしらえがまだ…", 0, 3, 6558, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123201, "世界ってこんなに広い\nんだ…！　はやく自分\nの目で見てみたいな～", 0, 3, 6559, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123301, "はちゃめちゃヒマ～\nなーんか楽し～コト\nないかなー", 0, 1, 6561, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123301, "ここ、いートコだねー\nこんどカリザきゅんも\nつれてこよー", 0, 1, 6562, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123301, "かわゆいショタが\n見た～い♪\n触りた～～～い♪", 0, 1, 6563, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123301, "なんか服きっついなー\nちょい太ったかー？\nん～\nま・気のせーっしょ♪", 0, 1, 6564, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123301, "あ～やべ\nおねえちゃんに\nメッセすんの忘れたー\nまいっかー", 0, 1, 6565, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 123301, "パンケーキ食いてー\n騎士きゅん\n誘ってみよーっと♪", 0, 1, 6566, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 123301, "きょーも～\n好きぴに囲まれて～\nシ・ア・ワ・セ♪", 0, 1, 6567, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123301, "アタシと遊ばな～い？\nってなんか\nナンパみスゴー\nウケる～", 0, 2, 6568, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123301, "んー？\n今はだるいからパース\nあとでねー", 0, 2, 6569, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123301, "カリザきゅん\n見なかった～？\nおねえちゃんから\n逃げるとかオシオキー", 0, 2, 6570, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123301, "ネイルしたいけど\n無理なんよねー\n爪の間にめっちゃ\nスライム入っからさー", 0, 2, 6571, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 123301, "おねえちゃんに\nかまってほし～の？\nかわゆ～い♪", 0, 2, 6572, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 123301, "な～に騎士きゅん♪\nここでイチャイチャ～\nし・ちゃ・う～？", 0, 2, 6573, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123301, "ねんむ～\nけど外から\nショタの声したんで～\nつい起きちった～", 1, 2, 6574, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123301, "ランチの後はやっぱ\nデザートっしょー\nベツバラってヤツ～♪", 2, 2, 6575, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123301, "こんな時間まで\n起きてるコは～\n食べちゃうぞ～♪", 3, 2, 6576, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123301, "あーラクー\nいっしょーここで\nキューケーできるわー", 0, 3, 6578, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123301, "なにこれ～\nかわいいじゃ～ん\nショタの次くらいに～", 0, 3, 6579, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123301, "いーニオイする～\nなんのニオイとかー\nよくわかんないけどー", 0, 3, 6580, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123301, "あったか～♪\n溶けてスライム\n戻りそ～", 0, 3, 6581, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123301, "ン・ン・ン～♪\nお・い・し～♪\nも～っと食べるぅ～♪", 0, 3, 6583, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123301, "わ～なんかムズー\n読めなー\nウケるー", 0, 3, 6584, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123501, "あんパンって\nお菓子に入るのかな？\nハロウィンで\nもらえたりしない？", 0, 1, 6586, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123501, "お菓子をくれなきゃ\nぐうたらするぞ～！\nハロウィン以外でも！", 0, 1, 6587, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123501, "お化けになったら\n仕事しないで\nゴロゴロし放題\nなのかな？", 0, 1, 6588, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123501, "おっと…この仮装\n袖がすごく長いからさ\n引っ掛けないように\n気を付けないとね～", 0, 1, 6589, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123501, "イタズラするのも\n体力がいるよね～\n楽にできそうなの\n何かないかな？", 0, 1, 6590, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 123501, "あいつって\n仮装とかするのかな？\n似合いそうなのは\nどんなお化けだろ？", 0, 1, 6591, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 123501, "ずっとハロウィンなら\nあいつと遊び放題\nなんだけどな～", 0, 1, 6592, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123501, "今の街はハロウィン\n一色だからね～\n人混みが大変だよ", 0, 2, 6593, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123501, "この仮装、どうかな？\n気に入ったなら\n来年もしてあげるよ～", 0, 2, 6594, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123501, "実はこの長い袖\nドングリとかを\nため込めるんだよね…", 0, 2, 6595, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123501, "カボチャランタンが\nあるんなら\nドングリランタンも\nあっていいよね～♪", 0, 2, 6596, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 123501, "ハロウィンでも\nやる気があれば\nぐうたらできる…\nうん、いいこと言った", 0, 2, 6597, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 123501, "一緒にお菓子食べよ？\n寝転びながら堪能する\nハロウィンスイーツは\n最高だからね～", 0, 2, 6598, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123501, "お化けは寝る時間…\n今のあたしはお化け…\nだから、おやすみ～…", 1, 2, 6599, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123501, "お昼か～…\n食後のデザートは\nハロウィンのお菓子で\n決まりだね～♪", 2, 2, 6600, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123501, "ふぁ～、寝よ…\nお化けは夜に\n出るものだけど\nあたしはリスだからね", 3, 2, 6601, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123501, "ハロウィンを楽しむ\nためにも、ぐっすり\n寝とかないとね～", 0, 3, 6603, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123501, "…袖から手が\n出せないから\nこのまま撫でよう\nよしよし", 0, 3, 6604, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123501, "リラックスできる\n素敵な香りだね～\n成仏しそうだよ～", 0, 3, 6605, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123501, "あったかいね～\nここで寝てもいい？\n…着替えないとダメか", 0, 3, 6606, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123501, "もぐもぐ…\nお化けもリスも\nお腹は空くんだよ", 0, 3, 6608, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123501, "キョンシーって\nどんなお化けなのか\n本で調べようかな？", 0, 3, 6609, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123501, "より取り見取りだね～\nとりあえず、これと\nこのお菓子はキープで", 0, 3, 6611, 12, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123601, "よし、衣装もばっちり\nハロウィンパレードへ\n出かけよう♪", 0, 1, 6613, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123601, "仮装してても\n剣は手放せないね\nほとんど体の一部だよ", 0, 1, 6614, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123601, "見回りに誘うふりして\n二人でパレードに\n参加しちゃおうかな♪", 0, 1, 6615, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123601, "…あれ？　手錠の鍵\n落としちゃった…？\nやば、出かける前に\n見つけないと…！", 0, 1, 6616, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123601, "お家でコスプレも\nこれはこれで楽しいな\n決めポーズの練習\nたくさんしておこう！", 0, 1, 6617, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 123601, "他にも、お姫様に\nメイドさんに学生服…\nやりたいコスプレが\n多すぎるよ～", 0, 1, 6618, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 123601, "漫画もゲームも好き！\n仮装するのも大好き！\n…好きを言葉にすると\nもっと好きになる♪", 0, 1, 6619, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123601, "ずっと目が合ってるね\n見つめてくれるのは\nいいんだけど、今日は\n衣装を見てほしいな♪", 0, 2, 6620, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123601, "トリックオアトリート\nお菓子をくれなきゃ\n逮捕しちゃうぞ♪", 0, 2, 6621, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123601, "あなたにだけはこの姿\nいつでも見せてあげる\nふふ、なんちゃって♪", 0, 2, 6622, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123601, "どうしたの？\nイタズラするなら\n隙を突かなきゃ\nこんなふうに、ね♪", 0, 2, 6623, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 123601, "コスプレしてても\n剣の稽古は休まないよ\nちょっと相手になって\nくれるかな？", 0, 2, 6624, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 123601, "ひゃっ、びっくりした\n急に触らないでよ～\nハロウィンだからって\nイタズラしないでね？", 0, 2, 6625, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123601, "おはよう！\n今日も朝から\nパトロールに行こう！", 1, 2, 6626, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123601, "カボチャのケーキを\n用意してるよ！\nデザートを食べて\n午後も頑張ろう！", 2, 2, 6627, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123601, "暗くなってきたね\nカボチャのランタンに\n火を灯そうよ♪", 3, 2, 6628, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123601, "ふかふか気持ちいい～\nあっでも衣装がシワに\nなっちゃうかも…？\n…でも気持ちいい～", 0, 3, 6630, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123601, "…かわいい\nこの子もパレードに\n連れて行きたいな", 0, 3, 6631, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123601, "はぁ～癒される♪\nハロウィンの見回りが\n終わった後は、\nまったりくつろごう", 0, 3, 6632, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123601, "ここで二人寄り添って\nカボチャスープ飲もう\n…なんて想像したら\nポカポカしてきた…", 0, 3, 6633, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123601, "パレードに参加すると\n体力を使うから、\nいっぱい食べておこう", 0, 3, 6635, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123601, "この絵本のお姫様\nかわいいね\nこの仮装もいいな♪", 0, 3, 6636, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123601, "わあ、おいしそう！\n見回りに行く前に\n少しつまんじゃおう♪", 0, 3, 6638, 12, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 103701, "今日こそは、あの人に\n気持ちを打ち明けて…\nそのあとは…うう\nど、どうしよう？", 0, 1, 6640, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 103701, "私が剣なら、あなたは\nさしずめ私の鞘…\nふふ、私の帰る\n場所ってことだよ♪", 0, 2, 6641, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123701, "やっぱり\nコスプレしてると\nテンションあがる～♪", 0, 1, 6642, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123701, "キャラコスにも\n興味あるけど…\nマジカル、ラブリー\nどっちにしよっかな～", 0, 1, 6643, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123701, "キャラコスといえば\nミスティーシャイニー\nもいいんだよねえ…\nうはー！　悩むー！", 0, 1, 6644, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123701, "ギルドハウスで\n宅コスしてる人\n実は結構\nいるんじゃないかな～", 0, 1, 6645, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123701, "コスプレだけど普段着\nって言いはれそう…\nって、ダメダメ！\nオタクは節度が大事！", 0, 1, 6646, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 123701, "フェスで買ってきた\n薄い本～♪\nギルドハウスに持って\nきちゃおっかな～", 0, 1, 6647, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 123701, "ようこそ\nコスプレ沼へ～\nなんちゃって♪", 0, 1, 6648, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123701, "どうどう？\nナナカちゃんの\nハロウィンコスプレ\n似合ってる？", 0, 2, 6649, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123701, "ハロウィン魔法少女\n参上カボ～！\n…やっぱ今の語尾\nナシで！", 0, 2, 6650, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123701, "ハロウィンといえば\nトリックオア～…\nコスプレ！\nなんちゃって", 0, 2, 6651, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123701, "ランフェスの\n戦利品～♪\nねえねえ\n見たい？　見たい？", 0, 2, 6652, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 123701, "私にしてほしい\nコスプレがあったら\n教えて♪　君のために\nや～ってやるぜぇ！", 0, 2, 6653, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 123701, "一緒にコスプレしたい\nならナナカちゃんは\nいつでも大歓迎だよ♪", 0, 2, 6654, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123701, "ふぁあ…ねむ\nって、ありゃりゃ　\n昨日コスプレしたまま\n寝ちゃってたかあ…", 1, 2, 6655, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123701, "今日のお昼は\nなんにしよ？\nカボチャ料理に\nしよっかな～", 2, 2, 6656, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123701, "もうこんな時間かあ\nそういえばオタクって\n夜行性の人が多い\n気がするぜぇ…", 3, 2, 6657, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123701, "ふっふっふ\nこのパニエは\n座るときはちゃんと\nふにゃってなるのさ！", 0, 3, 6659, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123701, "いや～\nかわいいものをみると\n癒されますな～", 0, 3, 6660, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123701, "新しい薄い本の匂いの\nアロマとか…ダメ？\n私はあの匂い、なんか\n好きなんだよね～", 0, 3, 6661, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123701, "暖か～い…\nけど近づきすぎて\n衣装が燃えないように\n気を付けなきゃ", 0, 3, 6662, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123701, "コス衣装のために\n体型維持は必須だけど\nついつい\n食べちゃうんだよね～", 0, 3, 6664, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123701, "おっ、次はこの本に\nのってるキャラのコス\nしてみよっかな？", 0, 3, 6665, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123701, "おおっ！\nお菓子がいっぱい！\nベルルちゃんチョコ\n入ってないかな～？", 0, 3, 6667, 12, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123801, "むっ…\nこの耳の手触り…\n驚嘆に値する…！", 0, 1, 6669, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123801, "獲物を狩りまくる…\nそんなウサギがいても\nいいだろう？", 0, 1, 6670, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123801, "ワナなど怖くないぞ？\nむしろ積極的に\n踏んでいく性分だ", 0, 1, 6671, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123801, "一匹狼ならぬ\n一匹ウサギか\nどっちでもかまわん♪", 0, 1, 6672, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123801, "人間とて本を正せば獣\n本能に従えばよいのだ\n遠慮は無用さ☆", 0, 1, 6673, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 123801, "脱兎のごとく…\n逃げるものか\n坊やを追い回すのさ♪", 0, 1, 6674, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 123801, "獣の道は険しいが…\n坊やめ、音を上げずに\nちゃんとついてきたな\n褒めてやるとしよう♪", 0, 1, 6675, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123801, "ニンジンはいらん\n好物は好敵手だ\nさぁどんどんおくれ♪", 0, 2, 6676, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123801, "尻尾に触りたいだと？\nワタシの乱数聖域を\n崩せたらな…☆", 0, 2, 6677, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123801, "頭で考えるな\n体に率直になれ\n己を野放しにするのさ", 0, 2, 6678, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123801, "そうさモコフワさ♪\nなで心地は最高だが\n意外にどう猛だぞ…？", 0, 2, 6679, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 123801, "ワタシにピョンピョン\n跳ねてほしいのか？\nあとでじっくり、な♪", 0, 2, 6680, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 123801, "ワタシと番になるか？\nオマエさえよければ\nいつでもかまわんぞ☆", 0, 2, 6681, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123801, "太陽と共に目覚め\n活動する\nそれが獣の生き方さ☆", 1, 2, 6682, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123801, "昼寝をすればいい\nワタシが周囲を\n警戒していてやる", 2, 2, 6683, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123801, "暗くなれば眠るだけさ\nそうすれば余計な\nストレスなどたまらん", 3, 2, 6684, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123801, "むむっ！？\n尻尾がつっかえて\nうまく座れんな…", 0, 3, 6686, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123801, "愛らしさでワタシに\n張り合おうというのか\n５億年後に出直せ☆", 0, 3, 6687, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123801, "野生の芳香か…\n本能が呼び覚まされる\nいいセンスだ♪", 0, 3, 6688, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123801, "寒さも野生のうちだ\n…とはいえ\n温まっていくか♪", 0, 3, 6689, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123801, "悪くはないが物足りん\nこう見えて肉食なのだ\n肉はないのか肉は☆", 0, 3, 6691, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123801, "ウサギ族は地獄耳？\nワタシとの共通点だな", 0, 3, 6692, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 106401, "ここが私の家…\nそれはこれからも\nずっと変わらないわ…", 0, 1, 6694, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 106401, "ええと、その…今から\nデート…じゃなくって\n散歩でもどうかしら？\n…ふ、二人きりで…", 0, 2, 6695, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123901, "ギルドハウスに\nヒーロー参上！\nみんな仲良くッスよ！", 0, 1, 6696, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123901, "料理もできて\n鎧も作れる！\nお母さんはすごいッス", 0, 1, 6697, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123901, "ううう…\nまたマントをドアに\n挟んじゃったッス…", 0, 1, 6698, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123901, "ずっと鎧を着ていれば\nジュンさんに近づける\nかも？　燃えるッス！", 0, 1, 6699, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123901, "サインの練習…\nもっとしておいた\nほうがいいッスかね～", 0, 1, 6700, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 123901, "半人前でも\n胸を張るッス！\nそのうち似合う日が\nくるッスから！", 0, 1, 6701, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 123901, "種族の違いも\n強い魂があれば\n超えられるッス！\n信じてるッスよ！", 0, 1, 6702, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123901, "困ったときは\n自分を呼ぶッス！\nあと楽しいときにも\n呼んでほしいッス！", 0, 2, 6703, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123901, "特訓でも行くッスか？\n望むところッス！\n目指せ、一人前！", 0, 2, 6704, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123901, "新しいショーの台本を\n読みこんでるッス！\nところで…この言葉の\n意味、分かるッスか？", 0, 2, 6705, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123901, "ヒミツがなくなると\nなんか恥ずかしいッス\nあんたはどうッスか？", 0, 2, 6706, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 123901, "ヒーローは遅れて\nやってくる…\nあんたもそうッスね♪", 0, 2, 6707, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 123901, "誰よりも早く\nあんたに見せたいッス\n一人前になった自分！", 0, 2, 6708, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123901, "うーん、今日も\nよく眠れたッス～！\n絶好調、間違いなし！", 1, 2, 6709, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123901, "おお、もうお昼ッスか\nどうりでお腹が空くと\n思ったッス～えへへ", 2, 2, 6710, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123901, "パトロールのあとは\n歯みがきに宿題に…\n大忙しッスね、夜！", 3, 2, 6711, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123901, "こんなにリラックス\nできるなんて…\n平和な証拠ッスね～", 0, 3, 6713, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123901, "か、かわいい…♪\nさあ、自分と握手…\nううん、抱っこッス♪", 0, 3, 6714, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123901, "優しい匂いッスね…♪\nこれくらいみんなを\n包み込めるように\nなりたいッス～", 0, 3, 6715, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123901, "ひっくちゅ！　うー…\nちょっと冷えるッスね\nぬくぬくするッス～♪", 0, 3, 6716, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123901, "特訓した分、\nいっぱい食べるッス！\nでっかくなるッス！", 0, 3, 6718, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 123901, "いつか自分の\nヒミツ図鑑が出たり\nして…いやいやいや", 0, 3, 6719, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106901, "牢獄よりは\nマシな環境ね\n我慢してあげるわ", 0, 1, 6721, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106901, "退屈ね…\nキャルでもいじって\n暇をつぶそうかしら", 0, 1, 6722, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106901, "ここにいる連中\n私を天気予報士と\n勘違いしてない？", 0, 1, 6723, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106901, "音波情報が多いわね…\nここ、もう少し静かに\nならないの…？", 0, 1, 6724, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106901, "共同生活なんて\n久しぶりだわ\n…懐かしいわね", 0, 1, 6725, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 106901, "あの子たちのギルド\n経営が雑すぎるわ…！\n教育が必要みたいね", 0, 1, 6726, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 106901, "とどめを刺される\n準備はできているのに\nまだなのかしら…？", 0, 1, 6727, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106901, "私と戦う気になった？\nあら、まだなのね…\nその時を待ってるわよ", 0, 2, 6728, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106901, "私というリソースは\n有効活用しなさい\n子守なんてごめんだわ", 0, 2, 6729, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106901, "どうしてそんなに\n頭がいいか？\n生まれつきよ", 0, 2, 6730, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106901, "視力は権能に\n影響しないわよ\n当たり前じゃない", 0, 2, 6731, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 106901, "あなたまで天気を\n教えろですって…？\nぶっ殺すわよ？", 0, 2, 6732, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 106901, "暇なら付き合いなさい\n好きなゲームでいいわ\nどうせ私が勝つもの", 0, 2, 6733, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106901, "人生朝露…\n悔いのない一日を\n過ごしなさい", 1, 2, 6734, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106901, "午睡はいいわよ…\nふわぁ…眠りは思考に\n必要不可欠なの…", 2, 2, 6735, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106901, "夙興夜寝なんて\nやめておきなさい\n休息は大切よ", 3, 2, 6736, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106901, "私を満足させるなんて\n大したものね…晶に\n大量生産させるわ", 0, 3, 6738, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106901, "キャルの代わりに\nズタズタにする物が\n欲しかったの…♪", 0, 3, 6739, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106901, "いい香りじゃない\n見たことのない調合ね\n誰が用意したの？", 0, 3, 6740, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106901, "パワーが足りないわね\nもっとよ、もっと私を\n寛がせなさい", 0, 3, 6741, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106901, "栄養価はともかく\n味のバランスは\n悪くないわね", 0, 3, 6743, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 106901, "つまらない本は\n私が書き直すわ\nこれはどうかしら？", 0, 3, 6744, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124001, "今年はアカリと\nお姉ちゃんでサンタが\n二人…つまり幸せも\n二倍ですね♪", 0, 1, 6746, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124001, "クリスマスに向けての\nサックス練習…\nお兄ちゃんと一緒に\n猛特訓です！", 0, 1, 6747, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124001, "クリスマスツリーの\n飾りつけ、完璧です♪\nお兄ちゃんお姉ちゃん\n喜んでくれるかな？", 0, 1, 6748, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124001, "わあ、お外が真っ白！\n生クリームが\nいーっぱいの\nケーキみたいです♪", 0, 1, 6749, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124001, "煙突から入ると\nお洋服が汚れちゃう…\n玄関からじゃ\nダメなのかなぁ？", 0, 1, 6750, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 124001, "お兄ちゃんが来たら\nサンタのお洋服\nいっぱい褒めて\nもらおっと♪", 0, 1, 6751, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 124001, "聖夜をお兄ちゃんと\n過ごせたら…\nアカリもちょっぴり\n大人になれるかも♪", 0, 1, 6752, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124001, "今のアカリは\nサンタさんですから♪\nなんでもお願いして\nくださいね？", 0, 2, 6753, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124001, "お外は寒いですから\nアカリとくっついて\nお出かけしましょ♪", 0, 2, 6754, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124001, "お兄ちゃんは\nアカリのトナカイさん\nですから、ずーっと\nそばにいてください♪", 0, 2, 6755, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124001, "お兄ちゃん、助けて！\nアカリのカラダで\nラッピング練習したら\n絡まっちゃいました…", 0, 2, 6756, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 124001, "プレゼント、いっぱい\n準備できました♪\nお兄ちゃんの分は…\nまだヒミツでーす♪", 0, 2, 6757, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 124001, "アカリ、プレゼントは\n大人っぽいのがいいな\n早くお兄ちゃんに\n追いつきたいから♪", 0, 2, 6758, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124001, "おはようございます♪\nふふ、アカリサンタは\nいい子のお手本なので\n早寝早起きなんです♪", 1, 2, 6759, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124001, "あれ、もうお昼？\nクリスマスナンバーの\n練習してたら\nもうこんな時間…", 2, 2, 6760, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124001, "冬の夜空は星が\nキレイですね～\nお兄ちゃん、一緒に\n見に行きませんか？", 3, 2, 6761, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124001, "はぁ、快適すぎて\nとろけちゃいそう…\nこういうプレゼントも\nいいかもです…", 0, 3, 6763, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124001, "やん、かわいいです♪\nえへへ、お兄ちゃんは\nどんなプレゼントが\n欲しいですか～？", 0, 3, 6764, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124001, "わわ、大人な香り…\nロマンチックな\nクリスマスが\n過ごせそうです♪", 0, 3, 6765, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124001, "これでカラダを温めて\nアカリの温もり…\nお兄ちゃんに\nおすそ分けしよっと♪", 0, 3, 6766, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124001, "お腹がパンパンです…\nでもこれで\nちょっとサンタさんに\n近づけたかも…？", 0, 3, 6768, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124001, "クリスマスイブの\n恋人との過ごし方…？\nはわわ、アカリには\nまだ早いかもです…！", 0, 3, 6769, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124001, "わっ、すごいすごい！\n不思議な絵本ですね♪\nこの本でお兄ちゃんに\nイタズラしちゃお♪", 0, 3, 6771, 14, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124001, "アカリ、ホカホカに\nなっちゃいました～♪\n…ぎゅってしても\nいいですよ？", 0, 3, 6772, 15, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124101, "またエンジェルになる\nかもとは思ってたけど\n…サンタさん要素の\n追加は想定外だったわ", 0, 1, 6773, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124101, "ラフィたちがいない\nときは私がなんとか\nしないとね\n一応…エンジェルだし", 0, 1, 6774, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124101, "部屋の飾りつけとか\nするのも…あり？\nふふ、浮かれすぎかな", 0, 1, 6775, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124101, "パーティーだったら\nみんなで大人数向けの\nボードゲームもできる\nかも…やったっ", 0, 1, 6776, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124101, "この服のモコモコ…\n気づいたら触っちゃう\n…やるわね、ラフィ", 0, 1, 6777, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 124101, "アイツと一緒にいると\n人見知りとは違う\n緊張があるのよね…\nって今の聞いてたっ？", 0, 1, 6778, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 124101, "ぬいぐるみ…\nいじめてないわよね？\n…大事にしてるって？\nそれはそれで…あうぅ", 0, 1, 6779, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124101, "いい子と悪い子って\nどう見分けるのかしら\n…まさか素直さとか？\nだったら困る！", 0, 2, 6780, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124101, "プレゼントといえば\nやっぱりゲーム！\n年末商戦サイコー♪", 0, 2, 6781, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124101, "サンタさんはお仕事を\n見られちゃ\nいけないのよね？\n…天職？", 0, 2, 6782, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124101, "この服は汚れ対策も\nバッチリなのよね\n煙突に入れるように\n…いや私はNGよ！？", 0, 2, 6783, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 124101, "アンタのお願い…\n今なら一つくらい\n聞いてあげるわよ？\nほら、サンタ衣装だし", 0, 2, 6784, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 124101, "私たちって\nなぜかぬいぐるみと\n縁があるわよね\n…ふふ", 0, 2, 6785, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124101, "お、おはよっ\n…ふぅ、寝ぐせを\n直すのが間に合って\nよかったぁ", 1, 2, 6786, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124101, "え…一緒にごはん？\nそ、そう…うん。OK\nまあ、せっかくだし？", 2, 2, 6787, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124101, "この時間のゲームって\n妙に楽しいのよね\n…ということで、\nちょっと遊ばない？", 3, 2, 6788, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124101, "ああ～ダメになるぅ…\nもうここでずっと\nゲームしてた～い", 0, 3, 6790, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124101, "プレゼントのために\n研究は必要よね、うん\n…はぁ、かわいい～♪", 0, 3, 6791, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124101, "集中力を高める香りも\nあるのよね…\n勝負をするときには\n持ち歩こうかしら", 0, 3, 6792, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124101, "なんだかまったり\nしちゃうわね…\n時間が溶けるわ…", 0, 3, 6793, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124101, "お腹いっぱい\n食べられるのも\n幸せなことよね", 0, 3, 6795, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124101, "ちっちゃいときは\nアカリと一緒に絵本を\n読んだなぁ…懐かしい", 0, 3, 6796, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124101, "飛び出す絵本…\nこれでゲームブックを\n作ったらおもしろく\nなる気がするわ…", 0, 3, 6798, 14, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124101, "あったまる…\nモフモフ…ふわふわ…\nちょっと休憩しても…\nいいよね…？", 0, 3, 6799, 15, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 105101, "あら、どうしたの？\nもしかして、私と\n新しい実験を\n始めたくなった？", 0, 1, 6800, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 105101, "調子が悪いときは\n診療所に来て\n君は特別に、じっくり\n診察してあげるわ♪", 0, 2, 6801, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124201, "特別じゃなくても\nまったり過ごす\nクリスマスも\nいいものだね", 0, 1, 6802, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124201, "ニットの帽子に\nマフラーに耳当て…\n素顔用の防寒対策を\n整えなければ", 0, 1, 6803, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124201, "靴下が\n見当たらなかったから\n枕元にはかわりに\n鎧を置いておこう", 0, 1, 6804, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124201, "聖女なんて言われる程\n素晴らしくはないけど\n心は聖くいたいものだ\n日々精進だね", 0, 1, 6805, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124201, "兜がないから\nイルミネーションが\nいつもよりまぶしく\n感じるよ", 0, 1, 6806, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 124201, "皆の幸福を\n祈願して\nクリスマスリースを\n飾ろうか", 0, 1, 6807, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 124201, "私にはかわいすぎる\n衣装だが…開き直って\nクリスマス気分を\n楽しんでしまおう", 0, 1, 6808, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124201, "ツリーの飾りつけ\n手伝ってくれるかい？\nセンスは少年に\nお任せするよ", 0, 2, 6809, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124201, "パーティーは\n用意してる時も楽しい\nねえ少年、この気持ち\n分かってくれるかい？", 0, 2, 6810, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124201, "なんでトナカイの鼻は\n赤いのかって？\n無灯ソリ運転は違反で\n取り締まられるからだ", 0, 2, 6811, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124201, "トモちゃんや\nマツリちゃんは\nいい子だから、きっと\nサンタさんに会えるね", 0, 2, 6812, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 124201, "ヒイラギの髪飾り\n実は気にいってるんだ\n少年にも\n褒めてもらいたいな", 0, 2, 6813, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 124201, "ねえ少年…そろそろ\n見慣れてほしい\nそんなにまじまじ\n見つめられると照れる", 0, 2, 6814, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124201, "朝日に照らされて\n街がキラキラ白銀に\n輝いてるよ", 1, 2, 6815, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124201, "お昼ご飯を食べて\nちょっと散歩しようか\n祝祭に沸く街は\nとてもきれいだよ", 2, 2, 6816, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124201, "やっぱりクリスマスは\n夜が本番って感じだね\nキャンドルを灯して\n厳かに過ごそうか", 3, 2, 6817, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124201, "鎧姿では無縁だったが\nふかふかしていて\n気持ちいいな…\nふかふか…ふかふか…", 0, 3, 6819, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124201, "ふふ、かわいいね\n抱きしめると\nあの雪の日に産まれた\n赤ちゃんを思い出す", 0, 3, 6820, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124201, "兜を脱いだおかげで\nこの香りに出会えた\n…クリスマスの魔法も\n悪くないかもね", 0, 3, 6821, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124201, "指先から体の芯まで\n暖まるね\nこれなら雪の日でも\n安心だよ", 0, 3, 6822, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124201, "もぐ、もぐ…\nあまり食べるところ\n見ないでほしい", 0, 3, 6824, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124201, "素敵なレストランの\n特集記事が載ってる\n聖夜に予約が取れたら\n最高に幸せだろうね", 0, 3, 6825, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124201, "わっ！\n…………いや\n驚いてなどいない", 0, 3, 6827, 14, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124201, "ふふ…頬に直接\nふかふかを感じられる\n温かくて気持ちいいよ", 0, 3, 6828, 15, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124301, "ピアノに読書…\nグレアと一緒に\nなにをしようかな～♪", 0, 1, 6829, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124301, "元の世界へ戻った時\n授業に遅れないよう\nグレアと勉強しとこう", 0, 1, 6830, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124301, "街に魔物料理屋さんが\nあるんだ！　グレアと\n食べに行ってみよう♪", 0, 1, 6831, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124301, "せっかく新しい魔法を\n覚えたし、グレアにも\n見てもらいたいな！", 0, 1, 6832, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124301, "グレアも誘って\nランドソルをもっと\n探索したい！", 0, 1, 6833, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 124301, "私たちも\nあの人を見習って\nバイトとかしよう！", 0, 1, 6834, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 124301, "いつかあの人を\n私たちの学院に\n招待したいな！", 0, 1, 6835, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124301, "あのね！\nこれからグレアと\n魔法愛好会に\n行くんだ！", 0, 2, 6836, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124301, "これからなにするの？\n私たちもついて行って\nいいかな！？", 0, 2, 6837, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124301, "グレアと一緒に\nあなたも魔法の練習\nしてみない？", 0, 2, 6838, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124301, "グレアとよく話すの\nマナリアの制服\nあなたに着せたいって", 0, 2, 6839, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 124301, "グレアのそばって\n温かいんだ～！\nぬくぬくだよ～♪", 0, 2, 6840, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 124301, "あなたは\nグレアと私の友達！\nこれからもずっと！", 0, 2, 6841, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124301, "おはよう！\n今朝はグレアと一緒に\n外を散歩してきたよ！", 1, 2, 6842, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124301, "お腹空いたな…\nよし、グレアと一緒に\n魔物料理を作ろう！", 2, 2, 6843, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124301, "もうこんな時間だね\n明日のために\nそろそろ寝ようかな！", 3, 2, 6844, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124301, "はぁ～この感触…\nグレアの尻尾みたいで\n安らぐ～", 0, 3, 6846, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124301, "ぬいぐるみって\nいいよね…私もよく\nグレアに抱き付くんだ", 0, 3, 6847, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124301, "いい香り～…\nグレアもこういうの\n好きなんだよ！", 0, 3, 6848, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124301, "あったかいね～…\nグレアにくっついてる\n時を思い出すよ～…", 0, 3, 6849, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124301, "この世界にしかない\nおいしいものが\nたくさんあるよね！", 0, 3, 6851, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124301, "世界を繋ぐ魔法の\nヒント…本に書いて\nないかなぁ…", 0, 3, 6852, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124401, "ピアノに読書…\n今日のアンは\nなにをするのかな…", 0, 1, 6855, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124401, "私はアンほど\n勉強はできないから\nちゃんと自習しないと", 0, 1, 6856, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124401, "一度始めたら\nとことん追求するのが\nアンなんだよね…", 0, 1, 6857, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124401, "ふふ…夢中になってる\nアンを眺めてるだけで\n一日があっという間…", 0, 1, 6858, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124401, "ランドソルは広いね…\nアンと一緒にいろんな\n場所へ行ってみたい", 0, 1, 6859, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 124401, "アンやあの人の笑顔を\n見てると、すごく\n安心するんだ…", 0, 1, 6860, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 124401, "もしあの人が私たちの\n学院に編入したら…\nふふ、楽しそうだね", 0, 1, 6861, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124401, "アンもあなたも\nすごいな…\nみんなとすぐに\n仲良くなっちゃうから", 0, 2, 6862, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124401, "あなたはこれから\nなにをするの？\nその…アンと一緒に\nついていってもいい？", 0, 2, 6863, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124401, "アンから魔法の練習に\n誘われた？　実は私も\n…一緒にどうかな？", 0, 2, 6864, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124401, "あなたがマナリアに\n編入したら\n楽しいのになって\nアンとよく話すんだ…", 0, 2, 6865, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 124401, "こっちの世界にも\nアンのように\n私を受け入れてくれる\n人たちがいるんだね…", 0, 2, 6866, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 124401, "元の世界へ帰る日が\n来たとしても私たちは\n「さよなら」より\n「またね」がいいよね", 0, 2, 6867, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124401, "おはよう。さっき\nランドソルの朝日を\nアンと見てきたんだ", 1, 2, 6868, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124401, "私たちは\nこれからなんだけど\nあなたはお昼ごはん\n何を食べたの？", 2, 2, 6869, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124401, "そろそろ\nアンが眠る時間だ…\nあなたもおやすみ", 3, 2, 6870, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124401, "はぁ、疲れが取れる…\nこの尻尾…\nちょっと重いんだよね", 0, 3, 6872, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124401, "これ…\nアンに見せたら\n喜ぶだろうな…", 0, 3, 6873, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124401, "いい香り…\nアンの感想も\n聞いてみたいな", 0, 3, 6874, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124401, "あったかい…\nアンがここにいたら\n本を読みながら\n寝ちゃうんだろうな", 0, 3, 6875, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124401, "おいしいからたくさん\n食べれちゃうけど\nまた尻尾…\n大きくなっちゃうかな", 0, 3, 6877, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124401, "アンがおススメ\nしてくれた本は\nこれかな…？", 0, 3, 6878, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124701, "凶、凶、凶…大凶？\nなによこのおみくじ\n自宅用だからって\n排出率が適当すぎよ…", 0, 1, 6881, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124701, "今年こそ最高の新薬が\nもはや偶然に近い\n最小限の手間で\n作れますように", 0, 1, 6882, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124701, "喉に詰まる前に\n溶けてなくなるお餅…\n最優先の研究課題だわ", 0, 1, 6883, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124701, "よし、完成♪\nミツキ特製…\n「新解釈おせち料理」\n医学的根拠が満載よ", 0, 1, 6884, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124701, "お年玉を彼に…\nあげるべき…？\n色んな意味で微妙ね…", 0, 1, 6885, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 124701, "ふふ、今年もまた一年\n彼のバイタルを\n記録できるのね♪", 0, 1, 6886, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 124701, "今年こそ\n私の薬で彼を魔改造…\nううん、大人の男に！", 0, 1, 6887, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124701, "コマ回しのコマを\n見てたら目が回った？\nそう…手当は不要よ…", 0, 2, 6888, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124701, "私の顔で\n福笑いを作った？\nあら…隻眼まで\n再現度が高いわね♪", 0, 2, 6889, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124701, "門松よし、しめ縄よし\nこんなギルドでもね\n一応縁起を担ぐのよ", 0, 2, 6890, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124701, "本気の薬草を煮込んだ\n本気の七草粥よ\n色は気にしちゃダメ", 0, 2, 6891, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 124701, "君が今年も健康で\nつつがなく…実験台を\n務められますように", 0, 2, 6892, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 124701, "いくつ新年を迎えても\n俗世の垢にまみれない\nそんな君こそが…\n私には最高のお薬よ", 0, 2, 6893, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124701, "お正月なんだから\nいつまでも寝てないで\nさぁ、実験開始よ♪", 1, 2, 6894, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124701, "カルタも双六もしたし\nあとは実験くらいしか\nやることがないわね", 2, 2, 6895, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124701, "日頃からたいがい\n意味深な夢を見るけど\n初夢はまた別かしら…", 3, 2, 6896, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124701, "さすがにこの格好で\n寝正月を敢行するのは\nためらわれるわね", 0, 3, 6898, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124701, "や、闇医者たる者\nこんな愛くるしい物に\n心奪われるわけには…", 0, 3, 6899, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124701, "お雑煮のいい香り…\n食欲が増進されるわ\nアロマも立派なお薬ね", 0, 3, 6900, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124701, "胸元は閉じないわよ？\nでも時々ここで\nこっそり暖まるの…", 0, 3, 6901, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124701, "無から有が生まれてる\n新年早々物理の法則を\n無視してくるのね…", 0, 3, 6903, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124701, "どれもこれも読んだわ\n新しい婚活本の\n入荷はまだかしら…", 0, 3, 6904, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124601, "彼が一生懸命\n書いてくれた年賀状\n気持ちがこもっていて\nすごくうれしいわ♪", 0, 1, 6906, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124601, "しっかり丁寧に\n大掃除したから\nとってもいい気持ちで\n新年を迎えられたわ♪", 0, 1, 6907, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124601, "彼の着物姿\nきっと凛々しいわよね\n想像しただけで\n笑顔になるわ～♪", 0, 1, 6908, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124601, "お雑煮は地域によって\n違いがあるけれど\n愛情が隠し味なのは\nどこも一緒よね♪", 0, 1, 6909, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124601, "彼と凧揚げして\n遊びたいけど\n森では引っかかって\nできないかしら…？", 0, 1, 6910, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 124601, "今年も彼が素直に\n成長できるように\nたくさんいい子いい子\nしてあげましょう…♪", 0, 1, 6911, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 124601, "絵馬に彼と\n書いた通り\n今年もみんなが笑って\n過ごせますように", 0, 1, 6912, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124601, "先生と初詣に\n行きましょう〜♪\n迷子にならないように\n手を繋いで、ね？", 0, 2, 6913, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124601, "初売りセールいいわね\nでも気を付けないと\nあれこれ欲しくなって\n買い過ぎちゃうかも…", 0, 2, 6914, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124601, "風邪をひかないように\n温かくするんですよ～\nえ？　先生、温めて？\nふふ、甘えん坊さんね", 0, 2, 6915, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124601, "あなたのために\nちょっと豪華なおせち\n用意したのよ\nお口に合うかしら？", 0, 2, 6916, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 124601, "あなたも着物着たい？\nふふ、貫禄出ちゃって\n先生ドキドキ\nしちゃうかも…♪", 0, 2, 6917, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 124601, "あなたの成長は\nとってもうれしいけど\n今年もまだ、あなたの\n先生でいたいな…♪", 0, 2, 6918, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124601, "あら、起きたの？\nお雑煮できてるから\nちゃんとお顔\n洗ってらっしゃいね", 1, 2, 6919, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124601, "お昼ご飯を\nしっかり食べて\nみんなでかるた大会\nしましょう〜♪", 2, 2, 6920, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124601, "は～い、良い子は\nもう寝る時間ですよ\n素敵な初夢が\n見られますように♪", 3, 2, 6921, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124601, "このフカフカちゃんも\n天日干ししたいけど…\nその前にちょっと\n休憩しちゃおうかしら", 0, 3, 6923, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124601, "うふふ、かわいい～\nアオイちゃんや\nハツネちゃんも\nきっと気に入るわね♪", 0, 3, 6924, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124601, "お正月は空気も\n透き通ってるから\nいつも以上に\n素敵な香りに感じるわ", 0, 3, 6925, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124601, "火のぬくもりに\n抱かれていると\n不思議と眠気が…\nふああ…", 0, 3, 6926, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124601, "愛情のこもった料理は\nおいしいだけじゃなく\n心も満たされちゃう♪", 0, 3, 6928, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124601, "お正月のお作法も\nたくさんあるのね～\n先生も日々勉強だわ～", 0, 3, 6929, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 104701, "少年への感謝をどんな\n形で伝えるべきか…\n言葉よりも物の方が\n良いのだろうか？", 0, 1, 6931, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 104701, "話し相手になって\nもらってもいいかな？\n少年と一緒にいると\nとても楽しいんだ", 0, 2, 6932, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124501, "お正月だからって\nギャングのボスは\n油断できないよ～？", 0, 1, 6933, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124501, "イノリちゃんがお年玉\n欲しいんだって♪\n悪い子だったし\n10ルピでいいかな☆", 0, 1, 6934, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124501, "お正月だからお雑煮を\n作ったんだけど\nカヤちゃんには\n大好評だったよ♪", 0, 1, 6935, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124501, "ラーゴがお餅を\n食べたがってるけど…\nこの子にあげるのは\nちょっと心配かな～？", 0, 1, 6936, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124501, "お正月は晴れ着の子が\nやっぱり多いよね～\n今度、私の漫画でも\n出してみようかな？", 0, 1, 6937, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 124501, "君と二人っきりで\n神殿に初詣…\n行ってみたいな～♪", 0, 1, 6938, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 124501, "君とお正月を\n過ごしていると\n面倒なことも\n全部忘れられそう…", 0, 1, 6939, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124501, "この着物、どう思う？\n似合ってたら\nうれしいけどな～♪", 0, 2, 6940, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124501, "袖を引っ張ったり\nしないでね？\n着崩れしちゃうから♪", 0, 2, 6941, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124501, "お正月ならではの\n漫画ネタってある？\n参考にしたいんだよね", 0, 2, 6942, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124501, "昔は凧あげとか\n駒回しとかしてたけど\n大人になるとそういう\n機会も減るよね…", 0, 2, 6943, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 124501, "君にもお年玉を\nあげようか？\n好きな額を言ってね☆", 0, 2, 6944, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 124501, "来年も、再来年も…\nこうしてみんなで\n新年がお祝いできると\n素敵だね～♪", 0, 2, 6945, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124501, "おはよ～♪\n朝ごはんは\n焼いたお餅とか\nどうかな？", 1, 2, 6946, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124501, "お昼だね～\nおせちを用意したから\nみんなで食べよっか", 2, 2, 6947, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124501, "もうこんな時間だね～\n一日中着物だったから\n肩が凝っちゃったよ", 3, 2, 6948, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124501, "このまま寝たら\nしわになるかな？\n着替えた方がいい？", 0, 3, 6950, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124501, "こういうのって\nラーゴのおもちゃに\nちょうどいいかも♪", 0, 3, 6951, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124501, "いい香りだけど\n着物に移らないように\n気をつけないとね", 0, 3, 6952, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124501, "ちょうどいい\n火加減だね♪\nぽかぽかするよ～♪", 0, 3, 6953, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124501, "着物の時は\nほどほどにしないと…\n欲張ったら、お腹が\n苦しくなるからね～", 0, 3, 6955, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124501, "ふーん\nイノリちゃんは最近\nこんな漫画を\n読んでるんだ～♪", 0, 3, 6956, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124801, "楽園の気配は…ナシ！\nみんな前を向いてるね\nそうこなくっちゃ！", 0, 1, 6958, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124801, "レッスンの成果も\nこの格好くらい\n目に見えたら\n分かりやすいのになぁ", 0, 1, 6959, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124801, "次の曲はワイルドで\n悪い感じもいいなぁ…\nカヤとムイミちゃん\nみたいなイメージで！", 0, 1, 6960, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124801, "私…服、着てるよね？\nうんうん、着てる！\nあんまり体が軽いと\n急に焦る瞬間あるな～", 0, 1, 6961, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124801, "最近ファンの年齢層が\n幅広くなってきたかも\nこれって私…国民的\nアイドルに近づいた？", 0, 1, 6962, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 124801, "パティスリーとコラボ\nしたらどんな感じかな\n…ノゾミンマカロン♪\nとか？　…いけるかも", 0, 1, 6963, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 124801, "彼と一緒にいると…\nうれしくって苦しいな\nでも両方諦められない\n…アイドルらしいや", 0, 1, 6964, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124801, "この格好、チカと\nツムギも似合うと\n思うんだけどな～\nキミもそう思うよね？", 0, 2, 6965, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124801, "街を歩いてたらね？\nライブとか写真集の\n告知イベントだって\n思われちゃった！", 0, 2, 6966, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124801, "私が持ってた剣に\n結んであったリボン…\n何か気づいてくれた？\nヒントは…色だよ♪", 0, 2, 6967, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124801, "キミの気配ってすごく\n分かりやすいんだよ\n近くにいたらすぐに\nピンとくるんだ♪", 0, 2, 6968, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 124801, "あ、それ…はじめて\nキミにあげた音響盤！\nね、一緒に聴こうよ", 0, 2, 6969, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 124801, "ぼうっとしてたら\nイタズラしちゃうよ♪\nもっともっと私のこと\n見てないとダーメ！", 0, 2, 6970, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124801, "朝は今日のはじまり！\n昨日よりももっと\nいい一日にしようね♪", 1, 2, 6971, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124801, "レッスンがんばったし\nまだお昼だし…\nランチ大盛りにしても\nセーフだよね！", 2, 2, 6972, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124801, "寝る前に明日の予定を\n確認しておかないと！\nえっと、集合場所は…", 3, 2, 6973, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124801, "一休みのつもりが\nなが～い休憩になるの\nあるあるだよね～", 0, 3, 6975, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124801, "ノゾミンの熱烈ハグ♪\n…ここだけ聞いたら\nスクープになっちゃう\nかも？　なーんて！", 0, 3, 6976, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124801, "香りってなんだか\n記憶に残るよねー\n…こういうの私だけ？\nみんなも同じだよね？", 0, 3, 6977, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124801, "なんだかライブの\n熱気を思い出して\nきちゃった…！\nレッスンしよっと！", 0, 3, 6978, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124801, "お腹が減ってたら\nいいパフォーマンスは\nできないもんね！\n…んー、おいし～♪", 0, 3, 6980, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124801, "いい歌詞を書くには\nしっくりくる言葉を\n知っておかないと！", 0, 3, 6981, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124901, "うだうだ悩んでる\nヤツがいたら\nオレのとこ連れてこい\n背中押してやるからさ", 0, 1, 6983, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124901, "最近どことなく\nファミリーの連中が\n浮き足立ってんだよな\n…なんでだ？", 0, 1, 6984, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124901, "イノリのヤツ\nやたらと脇腹\nつついてきやがって…\nくすぐったいっての", 0, 1, 6985, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124901, "身軽になったのは\nいいが…くそっ\nどこもかしこも\nスースーする…", 0, 1, 6986, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124901, "思い出に浸るのも\n悪くはないが\nやっぱ人間\n未来に生きないとな！", 0, 1, 6987, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 124901, "またムイミやノゾミと\nツルむのもいいな\nそこにカレもいたら\nなおいいぜ", 0, 1, 6988, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 124901, "未来のことを考えると\nなぜかいつもカレが\n頭に浮かんじまう…\nええい、やめだやめ！", 0, 1, 6989, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124901, "ちょっくらオレと\n腕相撲しねえか？\nこの新しい力\n試させてくれ！", 0, 2, 6990, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124901, "なあ、悪いが上着\n貸してくれないか？\nこのカッコ、割と\n肌寒いんだよ…", 0, 2, 6991, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124901, "おっ、生まれ変わった\n喧嘩屋にご用命か？\nいいぜ、キミの\n依頼なら二つ返事だ！", 0, 2, 6992, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124901, "なんか悩みごとが\nあるなら、なんでも\n話してくれよな\nオレとキミの仲だろ？", 0, 2, 6993, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 124901, "このカッコが\nきれいだって？\nや、やめろよ…\n照れくさいだろ…", 0, 2, 6994, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 124901, "キミがいると、なにを\nするにも退屈しないぜ\nだから今日も\n付き合ってくれよな！", 0, 2, 6995, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124901, "おはよう！　新しい\n未来がやってきたぜ！\n一秒だって時間は\n無駄にするなよ？", 1, 2, 6996, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124901, "そろそろ昼飯に\nしないか？\n穴場のうまい店に\n案内してやるからさ", 2, 2, 6997, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124901, "夜更かしは\n未来の前借りだ\n体壊さないように\nほどほどにしとけよ？", 3, 2, 6998, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124901, "お、いいなこれ\nファミリーの\nアジトにも\n置いときたいぜ", 0, 3, 7000, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124901, "べ、別にかわいいもん\nなんて、そんなに\n好きじゃないし…\n嫌いでもないけどさ", 0, 3, 7001, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124901, "嗅いだことない\n香りだな\nなかなか気に入った！", 0, 3, 7002, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124901, "薄着で暖を取るのって\n最高にぜいたくだよな\nへへっ、堪能させて\nもらうとするか", 0, 3, 7003, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124901, "こいつはいいや\nこのカッコだと\n消耗が激しくて\nやたら腹が減るからな", 0, 3, 7005, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 124901, "甘辛コーデ…\nへぇ、そんなのが\nあるのか…\nわ、悪くねえかもな", 0, 3, 7006, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 105501, "はぁ…\nもっともっと長い時間\n先輩と過ごせたらなぁ", 0, 1, 7008, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 105501, "えへへ…♪\n先輩が、すぐそばに\nいてくれるなんて…\n幸せだなぁ…♪", 0, 2, 7009, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125001, "はーっはっは！\n強くてカッコいい\n大悪党、ここに参上！", 0, 1, 7010, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125001, "みんなおしゃれで\nカッコいいけど\n今のアタシも\n負けてないぞ！", 0, 1, 7011, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125001, "ギャングのアジトも\nよかったけど\nここも賑やかで\n悪くないな！", 0, 1, 7012, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125001, "悪党にも休息は必要だ\nここならゆっくり\n休めそうだ！\n邪魔するぞ！", 0, 1, 7013, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125001, "いっぱい人がいるな…\nアタシの悪党っぷりを\n広めるチャンスだ！", 0, 1, 7014, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 125001, "ここにいれば\nいい思い出がたくさん\n作れそうだな！", 0, 1, 7015, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 125001, "いつかアタシも\nオクトーと一緒に\nこんなアジトを手に\n入れてやるからな！", 0, 1, 7016, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125001, "ん？　アタシの格好が\n気になるのか？\nならもっと近くで\n見てもいいぞ！", 0, 2, 7017, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125001, "おい、暇だから\nアタシと遊べ！\nカヤとノゾミも呼んで\n大騒ぎするぞ！", 0, 2, 7018, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125001, "オマエ、前より強く\nなってないか！？\nくっそー！　アタシも\n負けてらんないぞー！", 0, 2, 7019, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125001, "今日は特別にアタシの\n思い出話を聞かせて\nやる！　さーて、どの\n悪事から話そうか…", 0, 2, 7020, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 125001, "オマエとの付き合いも\n結構長くなったよな？\nこれからも\nよろしく頼むぞ！", 0, 2, 7021, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 125001, "オマエまでいなく\nなったらアタシが困る\n勝手にどっか\n行くんじゃないぞ！", 0, 2, 7022, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125001, "起きろ、起きろー！\n大悪党の朝は早いんだ\nさっさと\n顔洗ってこい！", 1, 2, 7023, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125001, "昼飯どうしようか…\nそうだ！　オマエの\nおススメに連れてけ！\nいい店期待してるぞ！", 2, 2, 7024, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125001, "明日を思い切り\n楽しむためにも\n早く寝とけよ！\nまた明日な！", 3, 2, 7025, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125001, "こうやって座ると…\nおお！　すっごく悪党\nっぽいぞ！", 0, 3, 7027, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125001, "オマエ、仲間に\nなりたそうな目を\nしてるな！", 0, 3, 7028, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125001, "うん、いい匂いだ！\n何の匂いかは全然\n分かんないけどな！", 0, 3, 7029, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125001, "炎ってなんだか\nカッコいいよな！\n見てるだけで心が\n燃えてくるぞ！", 0, 3, 7030, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125001, "いっぱい食べれば\nちょっとは\n背が伸びるかな…？", 0, 3, 7032, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125001, "ぐぬぅ…たまには\n本でもと思ったけど\nやっぱりアタシは\nバトル担当だな！", 0, 3, 7033, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118201, "お邪魔しまーす★\nなーんてもう\nすっかりくつろいで\nますけどー♪", 0, 1, 7035, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118201, "騎士さんの\nギルドハウス…★\nなんだかドキドキ\nしちゃいます♪", 0, 1, 7036, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118201, "ぺんぽこりんの\n置き場所はー…\nま、このへんで★", 0, 1, 7037, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118201, "うーん、これは\n壊しがいがありそ…\nウソウソ、冗談です★", 0, 1, 7038, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118201, "オトモダチでも\n作ってみますかー\nくすっ★", 0, 1, 7039, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 118201, "退屈ですねー\n騎士さん、構って\nくれないかなー", 0, 1, 7040, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 118201, "もっともっと早く\n騎士さんに逢えてたら\n…なんて、わたし\nらしくないですね", 0, 1, 7041, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118201, "わたし一人にするの\n不安だったりします？\nちゃーんとイイコに\nしてますってー★", 0, 2, 7042, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118201, "お出かけします？\nそれともおうちで\nまったりしますー？", 0, 2, 7043, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118201, "女の子をすぐ家に\nあげちゃうなんて…\nふふ、やっぱり隅に\n置けないですねー★", 0, 2, 7044, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118201, "騎士さん騎士さーん★\nウザ絡みしても\nいいですかー？\n騎士さんってばー★", 0, 2, 7045, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 118201, "一緒にゴロゴロ\nしませんかー？\nコイビトっぽくて\n楽しそうですし♪", 0, 2, 7046, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 118201, "うふふ★\nこーやってあなたに\n優しくされると…\nときめいちゃいます♪", 0, 2, 7047, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118201, "おはようございます★\nあれれー？\nまだおねむですかー？", 1, 2, 7048, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118201, "騎士さん、お昼は\nもう食べましたー？\nわたし、ラーメンが\n食べたいでーす★", 2, 2, 7049, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118201, "あ、夜更かししてちゃ\nダメですよー？\nほらほら、お布団に\nごーごー★", 3, 2, 7050, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118201, "ふかふかのふわふわー\nついついまーったり\nしちゃいます★", 0, 3, 7052, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118201, "かっわいー★\n名前はー、んー…\nぱんぱかぷんで★", 0, 3, 7053, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118201, "くんくん★\nうーん…\nたぶん素敵な香り♪", 0, 3, 7054, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118201, "あったかーい★\n心も体も\nぽっかぽかですねー★", 0, 3, 7055, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118201, "もぐもぐ★\nんー、おいしくって\n止まらないですねー★", 0, 3, 7057, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118201, "へー★\nほほー★\nふんふーん★", 0, 3, 7058, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125101, "さ～て時間もあるし\nラテアートの練習でも\nするかにゃ～♪", 0, 1, 7060, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125101, "カフェの猫たちも\n元気に働いてくれて\nお客さんからの評判も\n上々にゃ～♪", 0, 1, 7061, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125101, "カフェのカウンターに\nファントムキャッツの\n目安箱を置こうかにゃ", 0, 1, 7062, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125101, "あんこもコーヒーも\n原料は豆にゃ～\nそりゃ相性いいに\n決まってるにゃ♪", 0, 1, 7063, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125101, "あいつの為に\nタマキ特製ブレンドを\n開発したいにゃ♪", 0, 1, 7064, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 125101, "あいつと働いてると\nなぜかあっという間に\n閉店時間が\n来ちゃうのにゃ～", 0, 1, 7065, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 125101, "あいつといると\n新しいアイデアが\n溢れるほど\n出てきちゃうのにゃ♪", 0, 1, 7066, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125101, "キミがコーヒーを\n淹れてくれたのにゃ？\nにゃはは、常温で\n猫舌にも優しいにゃ♪", 0, 2, 7067, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125101, "料理が上手くなったら\nカフェご飯メニューも\n充実させたいにゃ～", 0, 2, 7068, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125101, "お店の経営は大変\nだけど、キミとなら\n乗り越えられるって\n確信してるにゃ！", 0, 2, 7069, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125101, "ん？　この制服\n似合ってるにゃ？\nにゃはは！　褒めても\n１ルピも出ないにゃ♪", 0, 2, 7070, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 125101, "あたしが好きなのは\n猫に好かれる人にゃ♪\n…ん、キミ猫に懐かれ\nまくってるにゃ…", 0, 2, 7071, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 125101, "キミの制服姿\nけっこーイケてるにゃ\nあたしがお客さんなら\n通いつめちゃうにゃ♪", 0, 2, 7072, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125101, "朝は一杯の\nコーヒーから始まる…\nにゃふふん♪\nシャレた朝だにゃ～♪", 1, 2, 7073, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125101, "お昼はたい焼きと\nコーヒーのセットで\nまったり休憩\nしようにゃ～♪", 2, 2, 7074, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125101, "コーヒー淹れたにゃ\n星空を見ながら\n二人で飲もうにゃ…♪", 3, 2, 7075, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125101, "ふにゃ～ん…ゴロゴロ\nコーヒーと同じくらい\n癒されるにゃ～…♪", 0, 3, 7077, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125101, "こいつかわいいにゃ～\nうちの猫たちとも\nいい勝負なのにゃ！", 0, 3, 7078, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125101, "ん～いい香りだにゃ～\nコーヒーもそうだけど\n香りの癒し効果は\n抜群にゃ～♪", 0, 3, 7079, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125101, "猫に暖かいものは\nまるまれと言ってる\nようなもんにゃ…♪\nまるまる…", 0, 3, 7080, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125101, "にゃは～うまいにゃ♪\nカフェの新メニューに\n採用しちゃおう\nかにゃ～♪", 0, 3, 7082, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125101, "にゃるほど…\n調べれば調べるほど\nコーヒーは\n奥が深いのにゃ…", 0, 3, 7083, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 107101, "ワタシの手料理は\n坊やの口に合うらしい\n胃袋は討ち取ったぞ♪", 0, 1, 7085, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 107101, "胸に再度触れると\n誓約は無効になるのか\n…だと？　鋭い質問だ\n安心しろ。ならん☆", 0, 2, 7086, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125201, "ようやくこの服にも\n慣れてきたかな？\nふふっ、たまには\nこういう服も悪くない", 0, 1, 7087, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125201, "今日も店は好調だが、\n私は軍人だからな\n当初の目的も\n忘れないようにしよう", 0, 1, 7088, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125201, "猫が近くに来ると\nついつい見てしまうな\n仕事が終わったら\n遊んでやるかな♪", 0, 1, 7089, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125201, "コーヒーの淹れ方が\nうまくなってきたな♪\nどうせならお菓子も\n研究してみるか！", 0, 1, 7090, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125201, "利益を考えると\n価格をあげるべきか？\n…いや、利益よりも\n皆の笑顔の方が大事だ", 0, 1, 7091, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 125201, "またあいつを\n呼んでしまったな…\n今度、コーヒーでも\n淹れて労ってやるか♪", 0, 1, 7092, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 125201, "次に入荷するお菓子は\nあいつの意見を聞くか\nどんな味が好きか\n気になるしな", 0, 1, 7093, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125201, "なに、コーヒーを\n淹れてほしい？\nふふっ♪\n仕方のない奴だな！", 0, 2, 7094, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125201, "…そうだ。\nコーヒーと乾パンは\n合うのではないか？\n今度試してみよう！", 0, 2, 7095, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125201, "コーヒーもいいが、\nココアも悪くないぞ♪\n貴公も一緒にどうだ？", 0, 2, 7096, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125201, "魔法少女に比べれば\n大人しい服だろう？\nだが、カフェには\nこの服でバッチリだ！", 0, 2, 7097, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 125201, "貴公はコーヒーを\n飲めるのかっ？それは\nうらやましいな…", 0, 2, 7098, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 125201, "貴公の働きぶりには\n目を見張るものがある\nその姿は、その…\n結構かっこいいぞっ！", 0, 2, 7099, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125201, "さぁ、今日も一日、\n頑張るぞっ！\n気合いを入れて\n開店準備だっ！", 1, 2, 7100, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125201, "昼は稼ぎ時だからな！\n荒波を乗り切るため\n貴公も一緒に\n頑張ってくれるか？", 2, 2, 7101, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125201, "ふあぁ…もう夜か\n今日は頑張ったからな\nはやめに休むとしよう", 3, 2, 7102, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125201, "はふぅ～…\nたまにはこうして\nゆったりするのも\n悪くないな…", 0, 3, 7104, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125201, "猫もかわいいが、\nこいつもなかなか…\n見ていると、\n撫でたくなるな♪", 0, 3, 7105, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125201, "これは良い香りだな\nふむ、コーヒーも\nより香り高いものが\n作れるよう試すか！", 0, 3, 7106, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125201, "ふぅ～…\n体を温めなければ、\nすぐに動けないからな", 0, 3, 7107, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125201, "これもうまいが、\n私はお菓子も…\nいや、食べすぎは\n体に良くないな", 0, 3, 7109, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125201, "…書物は参考になるな\n次はこのやり方を\n試してみるかな", 0, 3, 7110, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120001, "愛と希望の魔法少女！\n弟くんの平和は\nお姉ちゃんが守るよ♪", 0, 1, 7112, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120001, "魔法少女の戦い方を\n見せてあげる！\nお姉ちゃんチョップ♪", 0, 1, 7113, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120001, "闇の力が高まってる…\n早く弟くんを\nギュ～ってしないと！", 0, 1, 7114, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120001, "ミニ弟くんを\n部屋中に呼び出して\n弟くんハウスに～♪", 0, 1, 7115, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120001, "決めポーズの練習でも\nしよっかな…\nはい、ポーズ♪", 0, 1, 7116, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 120001, "魔法少女の力で\n弟くんのそばに\n瞬間移動したいなぁ", 0, 1, 7117, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 120001, "禁じられた必殺技\nその名も──\n闇のお姉ちゃん頭突き", 0, 1, 7118, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120001, "魔法少女な\nお姉ちゃんに\n興味津々だねっ？\n全部教えてあげるね♪", 0, 2, 7119, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120001, "闇のお姉ちゃんとして\n世界を弟くんのものに\n…な～んて冗談冗談♪", 0, 2, 7120, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120001, "弟くんって魔法少女に\nなりたがってたよね？\n…うん。応援するね！", 0, 2, 7121, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120001, "サインが欲しいの？\nふふっ、任せて！\n愛する弟くんへ、と♪", 0, 2, 7122, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 120001, "ふんふふ～ん♪\n魔法少女お姉ちゃんの\nテーマソング完成っ♪", 0, 2, 7123, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 120001, "えへへ、弟くんからの\n愛と希望を感じるよ…\n更に変身しちゃうぞ♪", 0, 2, 7124, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120001, "弟くんの朝を魔法で\n守りに来たよ！\n希望の朝だねっ♪", 1, 2, 7125, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120001, "弟くんのお昼を魔法で\n守りに来たよ！\n愛のお弁当をどうぞ♪", 2, 2, 7126, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120001, "弟くんの夜を魔法で\n守りに来たよ！\nここからは闇の時間♪", 3, 2, 7127, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120001, "魔法少女の一休み♪\n愛と希望と…闇の力が\n回復していくね～♪", 0, 3, 7129, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120001, "かわいいかわいい♪\nお姉ちゃんの魔法で\n増やしちゃうぞ…♪", 0, 3, 7130, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120001, "この香り…\n弟くんと同じ…\nくんくん、くんくん…", 0, 3, 7131, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120001, "体を温めておいて…\nこの熱を、ぜ～んぶ\n弟くんに伝えるよ…！", 0, 3, 7132, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120001, "ミニ弟くんを呼ぶには\nいっぱい食べないと…\n魔法ってお腹減るから", 0, 3, 7134, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 120001, "弟くんの平和を守る…\nそのためには\nチェックチェック…♪", 0, 3, 7135, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125301, "この服も、最初は\n慣れなかったのですが\n今ではすっかり\n体に馴染んでおります", 0, 1, 7137, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125301, "自然を守るためにも\nお勉強は欠かせません\n日々精進でございます", 0, 1, 7138, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125301, "採取した薬草を\n調合しておきましょう\nいつ必要になるか\n分かりませんからね", 0, 1, 7139, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125301, "合宿は過酷でしたが\nおかげでちょっと\n力持ちになったような\n気がいたします…♪", 0, 1, 7140, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125301, "わんわんのほうの\n主さまに、ごはんを\nあげる時間ですね…", 0, 1, 7141, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 125301, "もっともっと訓練や\nお勉強を重ねて\n主さまのお役に\n立たなくては…", 0, 1, 7142, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 125301, "おや、そろそろ\nわんわんのほうの\n主さまをお散歩に\n連れていく時間です", 0, 1, 7143, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125301, "主さま、よろしければ\nお散歩に行きませんか\n今日の森は穏やかで\n過ごしやすいかと", 0, 2, 7144, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125301, "わんわんのほうの\n主さまも、今では\n【美食殿】の大切な\n仲間でございます", 0, 2, 7145, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125301, "森には危険が\nいっぱいでございます\nケガをなさらないよう\nお気をつけください", 0, 2, 7146, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125301, "もし森や動物のことで\n気になることがあれば\n気軽にお尋ねください", 0, 2, 7147, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 125301, "森へ行く前には\n必ず装備の点検を\nお願いいたします\n約束ですよ、主さま？", 0, 2, 7148, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 125301, "森の中で主さまたちと\nゆったり過ごす時間は\nわたくしにとって\n大切なひとときです", 0, 2, 7149, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125301, "おはようございます\n朝ごはんをしっかり\n食べて、お勤めに\n備えましょう", 1, 2, 7150, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125301, "わんわんのほうの\n主さまにおやつを\nあげる時間です\nどうぞ、召し上がれ", 2, 2, 7151, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125301, "どれだけ野営に\n慣れたとしても\nベッドで眠れるなら\nそうしたいところです", 3, 2, 7152, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125301, "こちらなのですが\nわんわんのほうの\n主さまもお気に入りの\nようです", 0, 3, 7154, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125301, "わんわんのほうの\n主さまにも、こうした\n玩具を与えるべき\nでしょうか…？", 0, 3, 7155, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125301, "心地よい香りです\n次は、自然由来の\n香料も試しましょう", 0, 3, 7156, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125301, "暖をとるのは\n野営の時などに\nとても大事なことで\nございますね", 0, 3, 7157, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125301, "食べられる時に食べて\nおくのも、大自然では\n重要でございます", 0, 3, 7159, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125301, "より知識を蓄え\n自然の力を活用できる\nようにいたしましょう", 0, 3, 7160, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 100901, "今回の「冥風戦記」も\nなかなかよく書けたぞ\nふふっ、シグルドも\n喜んでくれるかな？", 0, 1, 7162, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 100901, "貴様のためならば\n我が真なる魔力\nいつでも\n解き放とうぞ！", 0, 2, 7163, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125401, "ニャットちゃんに\nブラッシングを\nしてあげようかな", 0, 1, 7164, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125401, "ふふ…\nこれがレンジャーの\n制服ですか", 0, 1, 7165, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125401, "そろそろ\nパトロールの\n時間だ…", 0, 1, 7166, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125401, "魔物について\nもっと知りたいな\n本を探しに行こう", 0, 1, 7167, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125401, "レンジャーになって\n体調を崩す日が\n減った気がします…", 0, 1, 7168, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 125401, "本だけじゃわからない\n世界のことを、\nもっと知りたいです…", 0, 1, 7169, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 125401, "こんな私にも\nできることはある…\nそう信じています", 0, 1, 7170, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125401, "あの…良かったら\n一緒に見回りへ\n行きませんか？", 0, 2, 7171, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125401, "またあなたと一緒に\nレンジャーの仕事が\nできたらうれしいです", 0, 2, 7172, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125401, "けほっこほっ\n…だ、大丈夫です\nたまに出るだけなので", 0, 2, 7173, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125401, "ニャットちゃん\nあなたにも\n懐いてきましたね", 0, 2, 7174, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 125401, "ニャットちゃんと\n散歩に出かけようかな\n…あの人も誘って", 0, 2, 7175, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 125401, "あなたと肩を並べて\nレンジャーの仕事が\nできてうれしいです…", 0, 2, 7176, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125401, "おはようございます\nレンジャー・シオリ\n元気に出勤します", 1, 2, 7177, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125401, "ニャットちゃんにも\nお昼ごはんを\n用意しなくちゃ", 2, 2, 7178, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125401, "明日は仕事だから\n早起きしなくちゃ\nおやすみなさい", 3, 2, 7179, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125401, "ふぅ…仕事で\n走り回ったので\n少し休憩します", 0, 3, 7181, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125401, "仕事が終わった後に\nこうして触れていると\n癒されますね…", 0, 3, 7182, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125401, "森の香りがします…\n今日はよく\n眠れそうです…", 0, 3, 7183, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125401, "ニャットちゃんと\n同じぐらい\n暖かいです…", 0, 3, 7184, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125401, "レンジャーの仕事を\nしていると\nお腹が空きます", 0, 3, 7186, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125401, "どんなに忙しくても\n読書だけは\nやめられませんね", 0, 3, 7187, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125801, "兄様のお留守の間\nわたくしが、兄様の\n御家を守ります！", 0, 1, 7189, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125801, "に、に、兄様の御家…\n招待いただいた以上は\n失礼のないよう\n振る舞わなくては…！", 0, 1, 7190, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125801, "ふふっ…兄様の素敵な\nお人柄が、家中から\nにじみ出ています…♪", 0, 1, 7191, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125801, "靴を脱がずに\n御家に上がるのに\n慣れていなくて…あ！\n飛べばいいのでは…！", 0, 1, 7192, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125801, "ここに花を生けたら\n華やぎそうですね\n兄様はお花…\n好きでしょうか…？", 0, 1, 7193, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 125801, "ずっとここにいたい…\nなどと言ったら\n兄様を困らせて\nしまいますよね…", 0, 1, 7194, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 125801, "お料理などを作って\n兄様を出迎えられたら\nいいのに…料理の腕も\n磨かなくては…！", 0, 1, 7195, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125801, "このような姿で\n失礼しております…！\nネ、ネズミがいたので\nつい本気を…！", 0, 2, 7196, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125801, "おかえりなさいませ\nお忙しい御身をどうぞ\n癒してくださいね", 0, 2, 7197, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125801, "兄様の話し相手…！？\nど、どうしましょう…\nわたくしには面白い\n話なんて…はうぅ…！", 0, 2, 7198, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125801, "なぜか、動物などに\n好かれる体質で…\n迷い猫捜しのバイトの\nお役に立てますか？", 0, 2, 7199, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 125801, "に、兄様の家に来たら\n『添い寝』をするのが\n礼儀とリアさんが…\n本当…でしょうか？", 0, 2, 7200, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 125801, "わたくしはここに\nいて…よいのですね？\n『仲間』として…", 0, 2, 7201, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125801, "朝は得意なんです\nもし兄様が苦手なら\nわたくしが起こして\n差し上げますよ", 1, 2, 7202, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125801, "兄様…昼餉を作って\nみたのですが、失敗\nしてしまいました…", 2, 2, 7203, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125801, "ふあぁ…\nはっ！　お…\nお恥ずかしい所を…！", 3, 2, 7204, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125801, "こ、これは…！\n人様の御家なのに\nつい…駄目になって…\nしまいますぅ…！", 0, 3, 7206, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125801, "と、飛びかかっては\nこない…ですよね…？", 0, 3, 7207, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125801, "すぅ…はぁ…\nこのかぐわしさ\n心が洗われます…", 0, 3, 7208, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125801, "なんて暖かい…\n兄様の優しくお広い\n御心のようです…♪", 0, 3, 7209, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125801, "こんなに食べていたら\nはしたない…\nでしょうか？", 0, 3, 7211, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125801, "兄様の本棚はまるで\n知識の宝庫ですね\n己の世間知らずぶりを\n実感してしまいます", 0, 3, 7212, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125501, "次の冒険に向けて\n騎空士弁当を作ろっと\nまずはおにぎりから！", 0, 1, 7214, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125501, "魔法の修行しなくちゃ\nもっともっと頼れる\n団長になりたいから！", 0, 1, 7215, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125501, "ビカラちゃんを\n遊びに誘いたいけど…\nどういう場所が\n好きなんだろう？", 0, 1, 7216, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125501, "仕事を真面目に\nしっかりこなすことが\n騎空艇への\n一番の近道だよね", 0, 1, 7217, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125501, "ここにいる人の中で\n騎空団に興味ある人\nいたりしないかな…？", 0, 1, 7218, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 125501, "これまでも想い出は\nいっぱいできたけど…\nこれからもたくさん\n想い出を作りたいな！", 0, 1, 7219, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 125501, "仲間がいるから\n騎空士は限界だって\n超えられるんだね", 0, 1, 7220, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125501, "この衣装\n魔法使いらしいでしょ\n服に着られないよう\n頑張らなくちゃね！", 0, 2, 7221, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125501, "あなたとなら\nどんな相手だって\n怖くなんてないよ！", 0, 2, 7222, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125501, "カリンさんから\n新しい依頼が来たよ！\n一緒に頑張ろ！", 0, 2, 7223, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125501, "操舵士になるのは\nずいぶん難しそう…\nいつか頼れる仲間が\n見つかるといいな", 0, 2, 7224, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 125501, "手合わせしたいの？\nいいよ、たまには\n団長らしいところ\n見せてあげるね！", 0, 2, 7225, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 125501, "まだ二人きりだけど…\nこの騎空団は\n私の宝物だよ！", 0, 2, 7226, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125501, "おはよう～…\n昨日は遅くまで\n魔法の研究してて…\nふわぁ…眠いなぁ…", 1, 2, 7227, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125501, "さてと、お昼休み！\n紅茶淹れてくるから\n一緒に飲もうよ！", 2, 2, 7228, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125501, "遅くまでお疲れさま！\n無理しない程度に\nお互い頑張ろう！", 3, 2, 7229, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125501, "魔法の研究は\n一旦、休憩！\n頭を休めなくちゃ", 0, 3, 7231, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125501, "魔法みたいに\nかわいい！\n騎空団に入る？\nなんてね！", 0, 3, 7232, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125501, "いい匂い…\nそれになんだか\n集中力が増したかも", 0, 3, 7233, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125501, "体だけじゃなくて\n心までポカポカする…\nなんだか魔法みたい", 0, 3, 7234, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125501, "いろいろ考え事したら\nお腹が空いちゃった！\nいただきまーす！", 0, 3, 7236, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125501, "『摂理の彼方へ\n至るには…』\n…この魔導書\n勉強になるなあ", 0, 3, 7237, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125701, "こちらの服を着ると\n冒険に出かけたく\nなっちゃいますね", 0, 1, 7239, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125701, "冒険者の心得その一\n準備はできる限り\nしっかりとこなすべし\nなんてどうでしょう？", 0, 1, 7240, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125701, "調合に必要な素材は\nあれとこれとそれと…\n時間があるとついつい\n考えちゃいますね", 0, 1, 7241, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125701, "せっかくですから\nより良い素材を\n自分で集めたいですね", 0, 1, 7242, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125701, "このポーションが\nみなさんの役に立つと\nうれしいですね", 0, 1, 7243, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 125701, "冒険者の心得その二\n危なくなった時は\nまず逃げるべし！\n大事なことですね", 0, 1, 7244, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 125701, "あの人の冒険は\nいつも楽しそうで\n元気がもらえます", 0, 1, 7245, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125701, "ぜひおすすめしたい\n依頼があるんです\nこちらをどうぞ♪", 0, 2, 7246, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125701, "ほしいお薬があれば\n錬金術で作って\nさしあげますよ", 0, 2, 7247, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125701, "依頼で気になる\nところがあれば\n何でもご確認ください", 0, 2, 7248, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125701, "このギルドハウスに\n錬金術の実験設備を…\nえ？　だめですか？", 0, 2, 7249, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 125701, "ポーションをどうぞ\n大丈夫、効果は\n確認済みですので♪", 0, 2, 7250, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 125701, "次はどんな冒険に\n行かれるんですか？\nいつでもお供しますよ", 0, 2, 7251, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125701, "今日もいい一日に\nなりそうですね\n冒険日和といった\nところでしょうか", 1, 2, 7252, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125701, "いい時間ですし\nランチにしませんか？\n次の冒険の\n作戦会議も兼ねて♪", 2, 2, 7253, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125701, "夜も遅いですし\n明日の冒険のため\n今日は早く寝ましょう", 3, 2, 7254, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125701, "ふう…やはり冒険は\n事務仕事と違いますね\n少し休憩を…", 0, 3, 7256, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125701, "冒険は緊張の連続です\nかわいい物で\n心を癒しましょう", 0, 3, 7257, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125701, "ふむ、この香りは…\n素晴らしいバランスで\n調合されていますね", 0, 3, 7258, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125701, "この火力…\n錬金術の調合に\n使えそうですね", 0, 3, 7259, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125701, "冒険の際は食べられる\n時に食べておく！\n…とペコリーヌさんも\nおっしゃってましたね", 0, 3, 7261, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125701, "ふむふむ…なるほど\nこんな調合も\nあるのですね…\n奥が深いです", 0, 3, 7262, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 106101, "アイツどこ行った…？\n戦うならアタシも\n連れてけよなー！", 0, 1, 7264, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 106101, "今日も悪いヤツ退治…\nの前に腹ごしらえだ！\nなんか食べに行くぞ！", 0, 2, 7265, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125601, "みんなー！\nギルドハウスで\nぼくと握手！", 0, 1, 7266, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125601, "種族も年齢も違うけど\nみんな楽しそうだね！\nやっぱりここはぼくの\nゆ～とぴあさ！", 0, 1, 7267, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125601, "ハハッ！　まるで\nパーティー会場だね！\nこれは楽しませがいが\nあるってものだよ！", 0, 1, 7268, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125601, "やぁみんな！\nご利益満点のネズ耳は\nいかがかな？\nひとつ３ルピだよ！", 0, 1, 7269, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125601, "ここにいれば\n遊び相手に困らないね\nかしこみかしこみ～！", 0, 1, 7270, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 125601, "どこか遊びに\n行こうかな♪\nそうだ、あの人にも\n声をかけてみよう！", 0, 1, 7271, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 125601, "あの人といると\nぼくまで楽しくなって\nくるんだよね！\n毎日パレード気分さ！", 0, 1, 7272, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125601, "ネズ耳の在庫が\n切れたんだ！\n作るのを\n手伝ってくれるかい？", 0, 2, 7273, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125601, "ん？　握手かい？\nいいとも！\nいつでも何度でも\nしてあげるよ！", 0, 2, 7274, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125601, "この衣装に興味が\nあるのかい？\nきみ、なかなか\n見る目があるね！", 0, 2, 7275, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125601, "しばらくここに厄介に\nなるつもりなんだ\nあとで街を案内\nしてくれるかい？", 0, 2, 7276, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 125601, "とっておきのチーズ\nきみと半分こだ！\nみんなには内緒だよ？", 0, 2, 7277, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 125601, "きみはいてほしい時に\nいつもそばに\nいてくれるね\n本当にかしこみだよ！", 0, 2, 7278, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125601, "やぁ、おはよう！\n気持ちのいい朝だね\n今日も楽しくネズ耳\n売りにはげもうか！", 1, 2, 7279, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125601, "お昼だね！\nおいしいチーズを\n持ってきたから\nぼくと半分こだ！", 2, 2, 7280, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125601, "今日も一日\n楽しかったね！\nいい夢が見られること\n間違いなしさ！", 3, 2, 7281, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125601, "ぼくにも休息は\n必要さ！\nリラックス〜\nリラックス〜♪", 0, 3, 7283, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125601, "ぼくのぬいぐるみも\n作って売ろうかな…\n修繕費も一気に\n集まるかもね！", 0, 3, 7284, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125601, "いい香りだね！\nトーゴクのお香に\nよく似てるよ", 0, 3, 7285, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125601, "はぁ…温まるねぇ…\nそうだ、この火で\nチーズでも炙って\nみようかな", 0, 3, 7286, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125601, "うーん、この味…\nトーゴクを思い出すよ\nどれ、もう一つ\nいただこうか！", 0, 3, 7288, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 125601, "これはランドソルの\nガイド本だね！\nありがたく参考に\nさせてもらうよ！", 0, 3, 7289, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126201, "まさか、わらわが\nこのような装束を\n纏う日がくるとはな…\n感慨深いものじゃ", 0, 1, 7291, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126201, "わらわの美貌で\nこのギルドハウスも\nより華やかに\nなったようじゃな", 0, 1, 7292, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126201, "つまらぬ戦いで\n晴れ着を汚しとうない\n今日は一日\nゆっくりするかの", 0, 1, 7293, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126201, "今日はわらわが食事の\n準備をして、あやつの\n帰りを待ってやろう\n献立は何がよいかの…", 0, 1, 7294, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126201, "ユキといったか…\n掴みどころのない\nやつじゃが、根性は\n認めてやるかの", 0, 1, 7295, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 126201, "こうして、仲間と\nのんびり過ごす時間も\n悪くないのう♪", 0, 1, 7296, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 126201, "あやつを誘って\n月を見に行こうかの\n夜が楽しみじゃ♪", 0, 1, 7297, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126201, "いかなる衣装で\nあろうとも、わらわは\n完璧に着こなして\nしまうのじゃ♪", 0, 2, 7298, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126201, "なんじゃ？\nわらわの姿に\n見惚れておったとな？\nす、素直でよいのじゃ", 0, 2, 7299, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126201, "吸血鬼であるわらわに\n相応しい装束じゃろう\nあとは真っ赤な血を\n添えたいのう…", 0, 2, 7300, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126201, "お主、疲れておるな？\nわらわが癒してやろう\nほれ、近う寄るのじゃ", 0, 2, 7301, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 126201, "お主がおるだけで\nギルドハウスが\n賑やかになるのう\nある種の才能じゃな♪", 0, 2, 7302, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 126201, "眷属よ、風呂にするか\nご飯にするか\nそれとも、わらわに…\nいや、忘れるのじゃ", 0, 2, 7303, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126201, "朝一番、わらわに\n会いたかったとな…\n眷属よ\n褒めてつかわすぞ♪", 1, 2, 7304, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126201, "眷属よ、わらわが\n食事を用意したぞ\n忌々しい太陽に負けず\n頑張るのじゃ", 2, 2, 7305, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126201, "眠るには惜しい夜じゃ\n眷属よ、もうしばらく\nわらわの傍に\nおるがよい", 3, 2, 7306, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126201, "ふかふかじゃの♪\n今度あやつにも\n座らせてやろう", 0, 3, 7308, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126201, "愛いやつじゃ\n少しばかり、わらわが\n遊んでやろうかの", 0, 3, 7309, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126201, "気品ある香りじゃのう\nわらわの古城にも\n一つ欲しいところじゃ", 0, 3, 7310, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126201, "よいぬくもりじゃ…\n忌々しい太陽とは\n大違いじゃの", 0, 3, 7311, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126201, "味付けの参考に\nあと、もう一つだけ…\n止まらんのじゃ…", 0, 3, 7313, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126201, "吸血鬼の弱点について\n記されておるのか…\nふむ、眠気が\nさめてしもうたぞ", 0, 3, 7314, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 100801, "このままボクが美しく\nなり続けても、彼なら\nきっと隣にいてくれる\nはずだよね！", 0, 1, 7316, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 100801, "もっとそばに来なよ\nボクの隣は\nキミのための\n特等席なんだから", 0, 2, 7317, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126301, "ドレスの影響かな…\n前より黒や赤が\n好きになったかも", 0, 1, 7318, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126301, "ボクがいるだけで\nここがぐんっと\n華やかになったね♪", 0, 1, 7319, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126301, "ボクを描いた絵画\n今度持ってきて\n飾ってあげようかな", 0, 1, 7320, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126301, "ボクはボクに\n魅了されっぱなしさ♪\nまさに魔性の存在だよ", 0, 1, 7321, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126301, "美のアドバイスを\n聞きたい人いるかい？\nボクがもっともっと\n輝かせてあげるよ♪", 0, 1, 7322, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 126301, "あの人、今度はボクを\nどう褒めるだろう？\nもし語彙が尽きても\n許してあげよっと♪", 0, 1, 7323, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 126301, "あの人に似合うコーデ\n考えてあげよっと\n案外、なんでも\n着こなしそうだよね♪", 0, 1, 7324, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126301, "吸血鬼になるなんて\nやっぱりゴメンだね！\n永遠の美よりも\n大事なことがあるのさ", 0, 2, 7325, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126301, "お肌にいいものは\n大体好きだよ\nトマトジュースとかね", 0, 2, 7326, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126301, "銀のアクセサリーが\nボクは大好きでね\nいつでもプレゼント\n待ってるよ♪", 0, 2, 7327, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126301, "イリヤみたいにお城に\n住んでみたいんだ♪\n美を統べる者に\n相応しいでしょ？", 0, 2, 7328, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 126301, "今のボクがカップを\n触ると壊しちゃうんだ\nだからほら、キミが\nボクに紅茶を飲ませて", 0, 2, 7329, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 126301, "キミも美を磨けば\nもっと輝けるはずさ！\nボクがメイクとか\nしてあげようか？", 0, 2, 7330, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126301, "んん、まだ眠い…\n美のためにも、\nもうひと眠りするよ…", 1, 2, 7331, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126301, "ボクにぴったりな\n日傘を買ったんだ♪\n散歩にでも行こうよ！", 2, 2, 7332, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126301, "夜こそボクの時間！\nさぁ、あまねく人々へ\nボクの美を広めよう！", 3, 2, 7333, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126301, "リラックスは大事だよ\n心の余裕こそが\n美の秘訣だからね♪", 0, 3, 7335, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126301, "キミもボクの\n美の虜にしてあげる♪\nうれしいでしょ？", 0, 3, 7336, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126301, "人々を魅了するほどの\n魔性の香り…\nボクにふさわしいね♪", 0, 3, 7337, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126301, "冷えは美の大敵さ\nボクのように\n美しくなりたいなら\n体を温めるべきだよ", 0, 3, 7338, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126301, "食事をするボクの\nなまめかしさ…♪\n吸血鬼すらも\nドキッとするだろうね", 0, 3, 7340, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126301, "吸血鬼って\n美しい存在だよね…\nボクほどではないけど", 0, 3, 7341, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126401, "ここがナイトさんの\nお家なんですねーっ！\n雰囲気そっくりっ！", 0, 1, 7343, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126401, "ドアも壁もお菓子じゃ\nないんですよねっ？\n不思議ですねーっ♪", 0, 1, 7344, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126401, "失礼のないように\n気をつけないとっ！\nライラエルさまとも\n約束してますからっ！", 0, 1, 7345, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126401, "向こうに帰るころには\nお土産話がいっぱい\nできてるといいな～♪", 0, 1, 7346, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126401, "戦闘用のアームの他に\n観光用のアームも\nあったらいいのにな～", 0, 1, 7347, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 126401, "ナイトさんの充填と\nライラエルさまの充填\nどっちも違って\nどっちも大好きです♪", 0, 1, 7348, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 126401, "…ナイトさんが\nマスターになって\nくれたら…えへへっ\nなんでもないですっ！", 0, 1, 7349, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126401, "お手伝いだったら\n任せてくださいっ！\nお家の移転とか\nしちゃいますかっ？", 0, 2, 7350, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126401, "こっちに来るときは\nポポおじいさんが\nしっかりメンテして\nくれるんですよっ♪", 0, 2, 7351, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126401, "お家の中にいるのも\nいいですけど、お外も\n気になります～っ！\nお出かけしましょう♪", 0, 2, 7352, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126401, "ヴォルガーノさんに\nそっくりの子猫さんを\n見かけたんですっ！\nご親戚ですかねっ？", 0, 2, 7353, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 126401, "ここだけの話ですよ？\n…実はケーブルって\n触られると、ちょっと\nくすぐったいんですっ", 0, 2, 7354, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 126401, "このお家の持ち主は\nナイトさんですよね？\nそれじゃここでは\nご主人さまですねっ", 0, 2, 7355, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126401, "おはようございますっ\n今日もフルパワーで\nがんばりましょうっ♪", 1, 2, 7356, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126401, "お昼の時間ですねっ！\nランチの充填、\nお願いしまーすっ♪", 2, 2, 7357, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126401, "そろそろ眠らなくて\n大丈夫ですかっ？\n夜ふかしさんなら\nお付き合いしますよ♪", 3, 2, 7358, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126401, "エネルギーの消費を\n抑えるのも大切\nですよね～っ", 0, 3, 7360, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126401, "わたしが触っても\n平気なんて…！\nかわいくて頑丈っ！\n好きですっ！", 0, 3, 7361, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126401, "センサーによると…\nこの香りはいい香りに\n該当するみたいですっ\nなるほどーっ！", 0, 3, 7362, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126401, "あったかいのって\nいいですよねっ！\nみなさんうれしそう\nですしっ♪", 0, 3, 7363, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126401, "これがこっちの世界の\nごはんなんですねっ\nおいしい気がします！", 0, 3, 7365, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126401, "そーっとそーっと…\n本を破かないように…\n書いてある内容より\n難しいです～っ！", 0, 3, 7366, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126701, "ウィスタリア家の\nプライベートビーチも\nご案内しますわね♪", 0, 1, 7368, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126701, "世界を巡る\nリゾートクルーズも\nご用意できますわよ", 0, 1, 7369, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126701, "避暑地に専用の\n別荘がありますの\n今度ご招待しますわね", 0, 1, 7370, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126701, "日焼けは乙女の\n天敵ですわね\n備えはしっかりと…", 0, 1, 7371, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126701, "線香花火だけでは\n物足りませんわね\nもっとドカーンと…", 0, 1, 7372, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 126701, "ギルド活動も全力で！\nバカンスも全力で！\nそれが私ですわ！", 0, 1, 7373, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 126701, "私のセンスで選んだ\n特注の水着ですもの\n似合って当然ですわ♪", 0, 1, 7374, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126701, "ご希望ならすぐにでも\n夏の飛空艇遊覧ツアー\nへご招待しますわ♪", 0, 2, 7375, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126701, "夏の賑やかな\n雰囲気は好きですが…\n暑さには参りますわね", 0, 2, 7376, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126701, "日の光を浴びるほどに\n輝きを増す夏の私！\n今日も絶好調ですわ♪", 0, 2, 7377, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126701, "夏祭りに協力するなら\n資金と人材、どちらも\n用意しないとですわね", 0, 2, 7378, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 126701, "あなたさま…その…\nそうも見つめられると\n照れてしまいます…", 0, 2, 7379, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 126701, "タマキさんの働く\n海の家も訪れて\nみたいですわね♪", 0, 2, 7380, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126701, "港の市場が\n賑わう頃ですわね！\n私たちも参りますわよ", 1, 2, 7381, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126701, "冷たい飲み物でも\nご一緒にいかが？\nうちのシェフに用意\nさせてありますの", 2, 2, 7382, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126701, "まだもう少し…\nこの夏の夜を\nあなたさまと…", 3, 2, 7383, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126701, "あなたさまも\n良ければこちらへ\nいらっしゃいな♪", 0, 3, 7385, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126701, "夏に合わせた装いに\n替えてみるのも\n楽しそうですわね♪", 0, 3, 7386, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126701, "この夏めいた爽やかな\n香り…期間限定で\n売り出したいですわ", 0, 3, 7387, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126701, "く…我慢です…\nこの暑さに耐えてこそ\n美しさにも磨きが…", 0, 3, 7388, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126701, "暑い季節に働く\nみなさまへ差し入れも\n必要ですわね", 0, 3, 7390, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126701, "夏のトレンドを\n押さえておくのも\n大事ですわね", 0, 3, 7391, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126701, "上手くいけば\nヒット商品になる\nポテンシャルを\n感じますわね…！", 0, 3, 7393, 10, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126701, "この極上の肌触り…\n言い値で買いますわ\n一体おいくらですの？", 0, 3, 7394, 11, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126601, "せっかくのバカンス…\n自由に羽を\n伸ばすとしましょう", 0, 1, 7395, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126601, "たとえどのような\n秘密を持とうとも\n私には筒抜けですよ", 0, 1, 7396, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126601, "子供だましの怪談に\n関心はありませんが…\n霊には興味があります\n観察対象として、ね", 0, 1, 7397, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126601, "彼の絵日記…\nあとでゆっくりと\n読ませてもらいます", 0, 1, 7398, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126601, "セミの鳴き声に入道雲\nそしてクリームソーダ\nこれでこそ夏ですね", 0, 1, 7399, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 126601, "夏休みの宿題でも\n作るとしましょうか\n彼の限界を少し超えた\n負荷を与えるには…", 0, 1, 7400, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 126601, "彼と過ごす夏を\n特別なものにしたい…\nそれが私の望みです", 0, 1, 7401, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126601, "夏だからといって\n浮かれすぎないように\n節度を持って\n楽しみなさい？", 0, 2, 7402, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126601, "ああ見えてもマサキは\n私のプリンセスナイト\nあまり侮られては\n困りますよ？", 0, 2, 7403, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126601, "私の秘密ですか？\nまんじゅうが怖い…\nというのはどうです？", 0, 2, 7404, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126601, "ここは誰かの秘密を\n盗み聞くのに\n最適な場所ですね\n…ジョークですよ", 0, 2, 7405, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 126601, "一緒に風鈴の音を\nのんびり楽しもう…？\n…別に構いませんよ\n貴方とならば", 0, 2, 7406, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 126601, "貴方に約束しましょう\n決して忘れられない\n夏にしてあげますよ", 0, 2, 7407, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126601, "早起きとは感心ですね\nでは一緒に\n体操でもしましょうか", 1, 2, 7408, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126601, "昼食は流しそうめん？\n非効率的ですね\n…食べないとは\n一言も言っていません", 2, 2, 7409, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126601, "…貴方が望むならば\nいくらでも怖い話を\nしてあげますが…\nいかがですか？", 3, 2, 7410, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126601, "七冠にも休息は\n必要なのです\nしばらくここで\n休ませてもらいますよ", 0, 3, 7412, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126601, "なかなか気に入った…\nというのは\n誰にも秘密です", 0, 3, 7413, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126601, "薫風を思わせる\nこのさわやかな香り…\n悪くないセンスですね", 0, 3, 7414, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126601, "夏だからこそ\n冷え対策は\n重要なのです\n…とはいえ暑いですね", 0, 3, 7415, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126601, "私がどのような味が\n好きなのかは…\n当然秘密です", 0, 3, 7417, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126601, "夏を舞台にした\n小説は数多くあります\n彼が好きそうな本を\n何冊か見繕いましょう", 0, 3, 7418, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126601, "見た目も手触りも\n完璧に覚えました\nいつでも模倣できます", 0, 3, 7420, 10, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126601, "ふむ…夏らしい\nクッションですね\nナイトプールに\n映えそうです", 0, 3, 7421, 11, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 105401, "レイ様も格好良いけど\n騎士さんもなかなか…\nって、今の聞こえて\nませんよねっ？", 0, 1, 7422, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 105401, "騎士さんはシンプルな\n服が似合いそうです！\n今度、一緒に\n見に行きましょう♪", 0, 2, 7423, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126801, "最初はどうかな～って\n思ったけど…\n水着姿でも案外\n問題ないのよね…", 0, 1, 7424, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126801, "水着で飲みすぎると\nお腹が冷える、って\n言うけど…麦しゅわ\nなら問題ないわね！", 0, 1, 7425, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126801, "暑ければ暑いほど\nその後の麦しゅわは\nおいしくなるのよ…", 0, 1, 7426, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126801, "しっかりと仕事を\nこなしてから、\nしっかりと麦しゅわ！\nやっぱりこれね！", 0, 1, 7427, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126801, "ふふっ、デザインも\n着心地も最高ね\n奮発して用意して\n正解だったわ♪", 0, 1, 7428, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 126801, "伝説の麦しゅわ、\n飲めるものなら\nまた飲みたいわね…", 0, 1, 7429, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 126801, "二つ名に恥じない\n聖騎士として\n私…飲むわ！", 0, 1, 7430, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126801, "この格好なら水に\n濡れても大丈夫！\nなんだけど、さすがに\n書類は濡らせないわね", 0, 2, 7431, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126801, "そうやってじっと\n見られると…ちょっと\n恥ずかしいわ…", 0, 2, 7432, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126801, "今日もいい天気ね\nふふっ、あとで\nビーチに遊びに\n行きましょ♪", 0, 2, 7433, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126801, "水着姿も慣れると\n案外快適ね\nそうだ、キミも\n水着になっちゃう？", 0, 2, 7434, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 126801, "例え水着姿であろうと\n聖騎士としての誇りは\n忘れてないわ！", 0, 2, 7435, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 126801, "キミとだったら\n麦しゅわなしでも\n最高の夏のバカンスに\nなると思うわ♪", 0, 2, 7436, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126801, "仕事終わりの\n麦しゅわのために\n今日も一日\n頑張るわよ♪", 1, 2, 7437, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126801, "今日のお昼は\nどうしようかしら…\nさ、さすがに昼からは\n自重してるわよ？", 2, 2, 7438, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126801, "さ、一日の疲れを\n洗い流すために…\n軽～く飲むわよ！", 3, 2, 7439, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126801, "こうやって\n直に触れると\n肌触りの良さが\nよく分かるわ♪", 0, 3, 7441, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126801, "今日もかわいいわね～\nどう？　今度\nお姉さんの一人飲みに\n付き合わない？", 0, 3, 7442, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126801, "いい香りね～…はっ！\n麦しゅわの匂いの\nアロマポットなんて\nあったらよくない？", 0, 3, 7443, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126801, "こんな格好で火に\n当たってると焙られる\nスルメの気分が\nちょっと分かるわ…", 0, 3, 7444, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126801, "もぐもぐ…うん、\n結構おつまみにも\nむいてそうね～♪", 0, 3, 7446, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126801, "試験に使った参考書、\nたまには読み返して\nおかないとね", 0, 3, 7447, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126801, "ふふ♪\nいい手触りね～", 0, 3, 7449, 10, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126801, "…はふう～…\nゴロゴロするのって\n最高ね～", 0, 3, 7450, 11, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126901, "ん…やっぱり\nこの水着…\n大胆、なんじゃ…？", 0, 1, 7451, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126901, "海でもお世話って、\nできるのかしら…？\n浮き輪代わりになる、\nとか…？", 0, 1, 7452, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126901, "～♪\nふふ…海に行く\n準備も、楽しい…かも", 0, 1, 7453, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126901, "あうぅ…水着で\n歩いてると…お尻…\nすぐ食い込んじゃう…", 0, 1, 7454, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126901, "海では、はしゃいだ\nほうがいいのよね…\nわ、わー、い…♪\n…こうかしら？", 0, 1, 7455, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 126901, "あなたとなら…\n楽しい夏の思い出、\n作れる…気がするの…", 0, 1, 7456, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 126901, "また来年も…\n再来年も…\nあなたと海へ\n行けますように…", 0, 1, 7457, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126901, "水着の着方を\n練習してるの…\n後ろ、変じゃない…？", 0, 2, 7458, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126901, "海に行く支度…\n進んでるかしら…？\nお手伝い、する…？", 0, 2, 7459, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126901, "ひ、日除けの準備は\n万全よ…\nずっと太陽の下に\nいたら…溶けちゃう…", 0, 2, 7460, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126901, "泳ぐ練習…\nこ、ここでするのは\n…さすがに変？", 0, 2, 7461, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 126901, "人混みに入るのは\nまだ不安で…\nでも、あなたといれば\nきっと…大丈夫…", 0, 2, 7462, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 126901, "あなたのおかげで\n海の楽しいところを\nたくさん知ったわ…\nありがとう…♪", 0, 2, 7463, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126901, "お出かけの日の朝は\n早い、のよね…？\nだから、ずっと\n起きていたわ…", 1, 2, 7464, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126901, "今頃ビーチの日差しは\n強いんでしょうね…\nそ、想像しただけで…\nめまいが…", 2, 2, 7465, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126901, "ビーチの夜といえば\n花火って、聞いたわ…\nバン、って言わない\n花火…ある…？", 3, 2, 7466, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126901, "素肌にふわふわが…\n気持ちいい…♪", 0, 3, 7468, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126901, "うふふ…\n一緒に海…行く？", 0, 3, 7469, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126901, "ふぅ…♪\n気分まで…\n晴れやかになるわ…", 0, 3, 7470, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126901, "暑いような…\n寒いような…\n不思議な気持ちに\nなるわ…ね？", 0, 3, 7471, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126901, "泳いだ後には\nいっぱい食べたほうが\n体によさそう、ね…", 0, 3, 7473, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126901, "ビーチの日陰で\n読書するのも…\n気持ちよさそう…♪", 0, 3, 7474, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126901, "これで合ってる…？\n遊び方わからなくて…", 0, 3, 7476, 10, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126901, "ふわふわ…♪\n海の底にいるみたい…", 0, 3, 7477, 11, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127001, "恥ずかしい格好で\n室内を歩くこの快感\nクセになりそうです…", 0, 1, 7478, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127001, "この水着…\nどうしてかポーズを\n決めたくなりますね…", 0, 1, 7479, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127001, "海にはどんな生き物が\nいるのでしょう～\n胸が高鳴ります～♪", 0, 1, 7480, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127001, "夏は暑いですね…\nクウカの体が熱いのも\n夏のせい…ぐふふ…♪", 0, 1, 7481, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127001, "ドＳさんと無人島に\n取り残されて…という\n妄想はかなりイイです", 0, 1, 7482, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 127001, "ドＳさんの\n欲望に満ちた眼差し…\nクウカ、受け止めます", 0, 1, 7483, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 127001, "海に行くのが\n楽しみです…\nドＳさんと…ぐふふ♪", 0, 1, 7484, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127001, "「もっと過激な水着を\n見せてみろよ」なんて\nいけませんドＳさん…", 0, 2, 7485, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127001, "水着のままで\n踊ればいいのですね…\nドＳさんの望みなら！", 0, 2, 7486, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127001, "ドＳさんの熱い視線を\n感じます…じゅるり…\nもっと見てください…", 0, 2, 7487, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127001, "こんな格好のクウカを\n放っておくなんて…\nさすがですドＳさん！", 0, 2, 7488, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 127001, "海で水着といえば\n何かが起きる予感…！\nドＳさんの新たな攻め\n期待していますね…！", 0, 2, 7489, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 127001, "恥ずかしいですけど\n頑張っちゃいました\nドＳさん…こういう\n水着、好きですか？", 0, 2, 7490, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127001, "こんな朝からクウカを\n水着に着替えさせて…\n本当にドＳさんは\n欲望に正直ですね！", 1, 2, 7491, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127001, "こんな昼間から\nドＳさんがヌルヌルの\n液体をクウカに～！\nえっ…日焼け止め？", 2, 2, 7492, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127001, "ドＳさん…これから\nクウカと夜の海を\n見に行きませんか？\nきっと綺麗ですよ…♪", 3, 2, 7493, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127001, "ここなら妄想に\n集中できます～♪\nじゅるり～♪", 0, 3, 7495, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127001, "こうもかわいいと\n妄想に登場させるのも\n気が引けます…", 0, 3, 7496, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127001, "クウカを昂らせる\nこの香り…！\nは！　ドＳさんの罠！", 0, 3, 7497, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127001, "体を熱くさせて\n水着を脱がそうと！？\nテクニカルです…！", 0, 3, 7498, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127001, "クウカの口の中に\nたくさん…♪\n止まりません…！", 0, 3, 7500, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127001, "読書は心が穏やかに\nなりますね…！\nぐふふ…穏やかに…", 0, 3, 7501, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127001, "いい乗り心地です…♪\nあっ…普通に楽しんで\nしまいました…", 0, 3, 7503, 10, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127001, "これは…\n妄想に役立ちそうな\n感触です…！", 0, 3, 7504, 11, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 100501, "成長した自分に\nあわせて、もっと\nかっこいいポーズを\n研究するッス！", 0, 1, 7505, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 100501, "これからも一緒に\n最高のヒーローを\n目指していくッスよ♪", 0, 2, 7506, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127101, "お父さん、娘の\n水着姿がそんなに\nうれしいの？", 0, 1, 7507, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127101, "怖い話を子どもたちに\n聞かせたら泣かれて\nしまいました…\n加減が難しいです…", 0, 1, 7508, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127101, "海に行くと父が\n水着の女性ばかり\n追いかけて困ります…", 0, 1, 7509, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127101, "拾った貝殻で\n新しい占いでも\nはじめてみようかな", 0, 1, 7510, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127101, "貝殻を耳に当てると\n波の音が…あっ\nこれはダメです\nよくない声がします", 0, 1, 7511, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 127101, "この飲み物、スーッと\n体に沁み込んでくる…\nあの人にも\n教えてあげたいです", 0, 1, 7512, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 127101, "強い日差しも騒がしい\n海も苦手なのに\nあの人となら不思議と\n楽しく思えるんです", 0, 1, 7513, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127101, "夏限定、水と氷を\n使った特別な占いは\nいかがですか？", 0, 2, 7514, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127101, "暑い日々にはやはり\n怪談ですね\n厳選フルコースで\nお聞かせします", 0, 2, 7515, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127101, "寒気がする？　それは\n霊ではなくかき氷の\n食べ過ぎですよ", 0, 2, 7516, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127101, "今日の占い、あなたの\nラッキーパーソンは…\n白い水着の女性です", 0, 2, 7517, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 127101, "あの、よければ\n日焼け止めを塗って\nもらえませんか？\n背中は届かなくて…", 0, 2, 7518, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 127101, "海に行きませんか？\n二人きりで静かな\n砂浜を散歩したいです", 0, 2, 7519, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127101, "潮騒だけが響く朝の海\n心が落ち着きます\nこれから一緒に散歩に\n行ってみませんか？", 1, 2, 7520, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127101, "不思議です。どうして\n普通のご飯でも、海で\n食べると特別おいしく\n感じるのでしょう…", 2, 2, 7521, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127101, "夜の海に行きませんか\n今なら夜光虫が\n見られるかも\nしれませんよ", 3, 2, 7522, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127101, "夏は知らぬ間に体力を\n消耗しています\n適度に休息して\nいきましょう", 0, 3, 7524, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127101, "このぬいぐるみ…\n大丈夫です\n何も憑いては\nいませんよ", 0, 3, 7525, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127101, "ほのかにお線香\nみたいな香り…\nなんだか\n落ち着きます", 0, 3, 7526, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127101, "人魂、鬼火\nセントエルモの火…\n炎を見ていると色々と\n連想してしまいます", 0, 3, 7527, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127101, "お腹を壊さぬよう\n食い合わせには\nご注意ください", 0, 3, 7529, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127101, "これはいい\n怪談集ですね…\nつい読みふけって\nしまいます", 0, 3, 7530, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127101, "ふふ、こういうのに\n乗るとなんだか\n子どもに戻った\nみたいですね", 0, 3, 7532, 10, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127101, "こ、これは…\nちょっとメルヘン\n過ぎて恥ずかしい\nですね…", 0, 3, 7533, 11, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126501, "本当に素敵なお家…♪\n騎士たちの人柄を\n感じられるようで…\n不思議と落ち着きます", 0, 1, 7534, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126501, "調度品をつい間食…\nという習慣がこちらの\n世界にはないのですね\n…気をつけないと", 0, 1, 7535, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126501, "手土産のお菓子は\nどちらに置けば\nよいでしょうか？", 0, 1, 7536, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126501, "まるでおとぎ話の中に\n入ってしまったかの\nよう…別世界とは\nよく言ったものです", 0, 1, 7537, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126501, "お忍び…ふふっ♪\nときめく響きです\nなんだかお茶目で…\n密やかな印象が", 0, 1, 7538, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 126501, "ふわ…\nああ、ついあくびが…\n居心地がよすぎるのも\n困り物ですね…ふふ♪", 0, 1, 7539, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 126501, "騎士には不思議な力が\nあるだけでなく\n何者かの加護が\nあるようですね…", 0, 1, 7540, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126501, "この『王家の装備』は\n少し気難しいのです\nわたし以外が触れると\nその…ビリビリっと", 0, 2, 7541, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126501, "子どもたちに新しい\n遊びを教わりました♪\nにーらめっこしましょ\nあっぷっ…ぷ～っ！", 0, 2, 7542, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126501, "実は憧れていたのです\n友人の家を訪ねると\nいう経験に…ふふっ", 0, 2, 7543, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126501, "ぎるどはうす…\nぎるど活動の拠点だと\n管理協会？　の方に\nうかがいました", 0, 2, 7544, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 126501, "あなたは運命を\n信じますか？\nわたしは…信じます\nよい運命だけですが♪", 0, 2, 7545, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 126501, "もしわたしが本当に\n天使だったとしたら…\nどうされますか？\n…ふふ、冗談です♪", 0, 2, 7546, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126501, "パンケーキの香りで\n目覚める朝は\nとても良いものですよ\nぜひともお試しを♪", 1, 2, 7547, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126501, "ひと休みの時間ですね\n食事にお昼寝…\nすべきことを行って\n英気を養いましょう", 2, 2, 7548, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126501, "そろそろ休まれては？\n眠れないようであれば\n寝物語か子守歌でも\nふふ、遠慮は無用です", 3, 2, 7549, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126501, "すう………はっ！\n眠ってはいませんよ？\nいくら居心地が良い\nからといって…ふわぁ", 0, 3, 7551, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126501, "まあ、あいらしい…♪\nぜひわたしの寝室に\n招待させてください", 0, 3, 7552, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126501, "なんて、かぐわしい…\nオーブンからただよう\n焼き菓子の香りとは\nまた異なる魅力が…", 0, 3, 7553, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126501, "みなで暖をとりながら\n互いに語り笑い合う\n…心休まる時間ですね", 0, 3, 7554, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126501, "食は心と体に\nゆとりを生みます\n…辛い食べ物の場合？\nそれは、その…", 0, 3, 7556, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126501, "こちらの世界の知識を\n蓄えておかなければ\nテーブルマナーに\nついてのページは…", 0, 3, 7557, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127201, "新しい色の蛍光ペン\n買ったのよね\nこれで勉強が\n楽しくなるわ～♪", 0, 1, 7559, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127201, "学食のランチ、結構\nおいしいのよね～\n今度ペコリーヌに\n教えてやろうかしら？", 0, 1, 7560, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127201, "ふふふ…扉に黒板消し\n仕掛けてるの…あいつ\n引っかかるかしら？", 0, 1, 7561, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127201, "部活するなら運動部も\n悪くないかもね～\n夕日に向かって\n突っ走ってやるわ！", 0, 1, 7562, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127201, "毎日窮屈な制服着て\n学生も大変ね～\n…ま、かわいいけど", 0, 1, 7563, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 127201, "あいつに貸したノート\n変な落書きとか\nしてなかったよね…？\n…その、相合傘とか…", 0, 1, 7564, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 127201, "昨日の家庭科で\n作ったクッキー…\nあいつにどうやって\n渡そうかな…？", 0, 1, 7565, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127201, "宿題教えてって？\nしょうがないわね～\nじゃあ一緒に\nやりましょ", 0, 2, 7566, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127201, "あ、明日あたしたち\n日直当番よね？\nめんどい仕事は\nあんたに任せるわ～", 0, 2, 7567, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127201, "寮生活は楽しいけど\n就寝時間が早すぎよね\nいきなり消灯されると\nビックリするわ", 0, 2, 7568, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127201, "校則で禁止だけど、\nにけつでほうき通学\nとか青春っぽくない？", 0, 2, 7569, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 127201, "制服って好きな人とも\n自然にお揃いになれて\nいいわよね…って別に\nあんたとって訳じゃ…", 0, 2, 7570, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 127201, "あんたネクタイ\n曲がってるわよ～？\nほら直したげるから\nこっち向いて？", 0, 2, 7571, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127201, "おはよ～！\n今日も元気に登校よ！\n忘れ物ないようにね！", 1, 2, 7572, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127201, "午前の授業疲れたわ～\nしっかりお昼を食べて\n午後も頑張るわよ！", 2, 2, 7573, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127201, "うう…学校の怪談\n思い出しちゃった…\n…別に怖くないけど！", 3, 2, 7574, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127201, "ふかふか～\n…気持ち良すぎて\n制服のまま\n寝ちゃいそうね…", 0, 3, 7576, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127201, "かわいくて癒されるわ\n寮に持ち込んでも\n大丈夫かしら…？", 0, 3, 7577, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127201, "集中力の高まる香りで\n勉強の効率爆上がりね\n次のテストで\nいい点取るわよ～！", 0, 3, 7578, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127201, "うう、ちょっと冷えて\n来たわね…\nスカートの下に\nジャージ穿こうかしら", 0, 3, 7579, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127201, "もぐっ…もぐっ…\n食べとかないと教室で\nお腹なっちゃうわ～", 0, 3, 7581, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127201, "図書室で借りた本\n明日返却日なのよね…\n今日中に読むわよ！", 0, 3, 7582, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 104301, "あいつに背中を\n預けるのもいい気分だ\nいつかは、全部…", 0, 1, 7584, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 104301, "な、なんだよ？\nじっと見るんじゃねぇ\n尻尾が揺れちまうだろ", 0, 2, 7585, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127301, "魔法の練習！\nむむむ～…\nそれっ…！", 0, 1, 7586, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127301, "そだ！　これ着て\nルー学行こっかな？　\n制服自慢しちゃお♪", 0, 1, 7587, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127301, "赤点回避の魔法とか\nあったら最高じゃね？\n誰かうちに教えて～", 0, 1, 7588, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127301, "制服が一番映える\n場所ってどこだろ？\n教室？　遊園地？", 0, 1, 7589, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127301, "ベンキョーつらたん…\n今度キャルっちに\n助けてもらお", 0, 1, 7590, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 127301, "部活か…\nヒデサイと一緒に\nやったら楽し～かも♪", 0, 1, 7591, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 127301, "男子の制服\nもにカワだった…\n今度ヒデサイに\n借りよっかな？", 0, 1, 7592, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127301, "ちょすー、うちの\nさいきょー魔法で\nテンアゲしてあげる～", 0, 2, 7593, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127301, "九九って呪文ぽくね？\nほら、例えばヒチシ…\nヒチシ、なんだっけ？", 0, 2, 7594, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127301, "ヒデサイセンセー\nうちにベンキョー\n教えて！", 0, 2, 7595, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127301, "学割マジ助かるー\nヒデサイ、一緒に\nどんどん使ってこー♪", 0, 2, 7596, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 127301, "今度ヒデサイに\nお弁当作ったげる\n屋上で一緒に食べよ♪", 0, 2, 7597, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 127301, "ヒデサイのバイブス\n上がる言葉教えてよ\nつらたんな時、うちが\nいっぱい言ったげるよ", 0, 2, 7598, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127301, "ちょすちょす～\n今日も朝から\nバイブス十分♪", 1, 2, 7599, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127301, "お昼どうしよっかな\nたまにはガッツリ\nいっちゃう系？", 2, 2, 7600, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127301, "やべ、宿題まだ\nできてないし…\nヒデサイ助けて～", 3, 2, 7601, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127301, "充電ちう～\n…ガッコーのイスも\nこれにして～", 0, 3, 7603, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127301, "かわいいし、\n癒されるし、キュン死\nしちゃうじゃん…！", 0, 3, 7604, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127301, "リフレッシュの\n魔法発見！\nいい香り～♪", 0, 3, 7605, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127301, "ぽかぽかサイコー♪\n今日はもーずーっと\nここにいたいかも～", 0, 3, 7606, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127301, "超おいし～♪\n学食にあったら\n毎日リピする～", 0, 3, 7608, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127301, "ふむふむ…\nねむみパない…\n続きはまた今度～", 0, 3, 7609, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127501, "今度は子どもたちも\nサラサリアに連れて\n行ってあげたいわ", 0, 1, 7611, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127501, "子どもたちから\nお姫さまみたいって\n言われちゃった♪", 0, 1, 7612, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127501, "この剣…火を点けたり\nするのに便利なのよ\nコントロールも効くし", 0, 1, 7613, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127501, "あのサラサリアの\nカラっとした暑さが\nちょっと恋しいわ…", 0, 1, 7614, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127501, "買ってきたお土産、\nお世話になってる\nみんなに配りましょ♪", 0, 1, 7615, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 127501, "あんたと一緒なら\nどこの国でだって\n暮らしていけそうね♪", 0, 1, 7616, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 127501, "今の事業を大きくして\nあんたと世界中の国を\n飛び回ったり…なんて\nすてきじゃない？", 0, 1, 7617, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127501, "サラサリアで食べた\n料理が恋しい…？\nもう…また今度\n作ってあげるわよ", 0, 2, 7618, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127501, "今度旅行するなら\nどこに行こうかしら？\n希望があれば教えてね", 0, 2, 7619, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127501, "サラサリアの香辛料、\nランドソルでも\n人気が出そうよね\nいいビジネスの予感！", 0, 2, 7620, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127501, "そんなにじっと\n見られるとさすがに\n恥ずかしいってば…", 0, 2, 7621, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 127501, "お嫁さんは憧れるけど\nお婿さんになって\nもらうのもありよね…\nあんたはどう思う？", 0, 2, 7622, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 127501, "やっぱり、あんたには\nいつもあたしの隣に\n居てほしいな…", 0, 2, 7623, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127501, "陽射しが強くなる前に\n雑多な用事は\n片付けちゃいましょ", 1, 2, 7624, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127501, "サラサリアで食べた\n料理を再現してみたの\n味見してくれる？", 2, 2, 7625, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127501, "暑さに負けないように\n睡眠はちゃんと\n取っておかないと", 3, 2, 7626, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127501, "こっちでもあっちでも\n忙しいのは変わらない\nわね…ちょっと休憩～", 0, 3, 7628, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127501, "サラサリアで見た\nぬいぐるみも\nかわいかったわね", 0, 3, 7629, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127501, "お香って地域ごとの\n特色が出るわよね\nこの香りは…\nちょっと神秘的？", 0, 3, 7630, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127501, "砂漠地方も夜は冷える\nのよね。温かいものが\nあると安心するわ…♪", 0, 3, 7631, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127501, "食べ慣れた味って\nどうしてほっと\nするのかしら…", 0, 3, 7633, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127501, "今後の取引相手に\nなるかもしれないし\n向こうの文化も\n勉強しておかないと…", 0, 3, 7634, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 101401, "犯人はこの中にいる！\n探偵として一度は\n言ってみたいセリフさ", 0, 1, 7636, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 101401, "助手くんの情報を\n仕入れておかないと…\n理想の私を、ずっと\n見ていてほしいからね", 0, 2, 7637, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127601, "久々の長旅で\n里心がついちまったね\nしばらくランドソルで\nのんびりしようか", 0, 1, 7638, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127601, "たまには違う服装\n違う得物を使うのも\n悪くないね", 0, 1, 7639, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127601, "サラサリアにいた時は\n砂まみれで大丈夫かと\n思ってたけど、今じゃ\n懐かしく感じるよ", 0, 1, 7640, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127601, "魔法の絨毯、あれは\n楽だし速いし便利\nだったねえ…土産に\n一枚欲しかったな", 0, 1, 7641, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127601, "砂上での戦いは\nなかなかいい鍛錬に\nなったね…今度は\n修行しに行くとしよう", 0, 1, 7642, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 127601, "サラサリアの人たちも\n気のいい連中だったが\n元気にやってるかねえ", 0, 1, 7643, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 127601, "旅の恥はかき捨てって\nいうけど、悪い旅じゃ\nなかったね。次は\nアイツとどこに行こう", 0, 1, 7644, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127601, "この格好を冷やかしに\n来たのかい？\n生憎、もうすっかり\n慣れちまったよ", 0, 2, 7645, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127601, "ギルドの連中に\n土産をわたしに\n行くところなんだ\n付き合っておくれよ", 0, 2, 7646, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127601, "久々に水場で\n釣りがしたいねえ…\nもちろんお前さんも\nくるんだろう？", 0, 2, 7647, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127601, "おっと\nサラサリアの連中から\n便りが来てるよ\nさ、一緒に読もうか", 0, 2, 7648, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 127601, "最近サラサリアの踊り\nを練習してるんだ\nあとで振付けを\n見ておくれよ", 0, 2, 7649, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 127601, "なんだいお前さん？\n…なんだか若奥さん\nっぽいって？　まあ、\nそりゃあ、ねえ…", 0, 2, 7650, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127601, "いい時間だね\n日が昇りきる前に\nやることをすませ\nとかないと", 1, 2, 7651, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127601, "昼はどうしようかね\nサラサリア風の\n食事でもつくろうか", 2, 2, 7652, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127601, "さて、早めに寝ようか\n砂漠の夜は冷えるから\n…っと、まだ旅行中の\n気分でいたよ", 3, 2, 7653, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127601, "良い座り心地だ\nサラサリア宮殿の\n応接間を\n思い出すねえ…", 0, 3, 7655, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127601, "ランプの魔神に\nあげたら喜ぶかねえ？\n次に会う時までに\n見繕ってみようか", 0, 3, 7656, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127601, "この香り…\nサラサリア土産の\n香木かい？", 0, 3, 7657, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127601, "旅先での焚火を\n思い出すよ\nなんだかほっとするね", 0, 3, 7658, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127601, "ふふ、慣れてる味も\nいいけど、むこうの\n味付けが懐かしく\nなっちまうね", 0, 3, 7660, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127601, "ふむふむ…\n千夜一夜物語か\nおもしろいもんだねえ", 0, 3, 7661, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126001, "立派なギルドハウス♪\n帰る場所が素敵だと\n冒険に出るのも\n楽しくなりそうです♪", 0, 1, 7663, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126001, "お掃除でも…と\n思いましたけど、\nさすがお兄さんのお家\nきれいにしてますね♪", 0, 1, 7664, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126001, "わたしたちもいつか\n立派なギルドハウスが\n持てる冒険者に\nなりたいです…♪", 0, 1, 7665, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126001, "わたし冒険者ですけど\n家を守ってお兄さんの\n帰りを待つのも\n悪くない…かも？", 0, 1, 7666, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126001, "お菓子焼こうかな…♪\nちょっと多めに作って\nお兄さんにも\n食べてもらおうっと♪", 0, 1, 7667, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 126001, "このお家、とっても\n大好きになりました♪\nだからその…もう少し\nいてもいいですか…？", 0, 1, 7668, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 126001, "お兄さんに\n感謝の手紙を書いて\n枕元に隠して\nおこうかな…♪", 0, 1, 7669, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126001, "…お兄さん\nお暇なんですか？\nじゃあ一緒に冒険へ\n出かけましょう♪", 0, 2, 7670, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126001, "お兄さんは苦手な食材\nとかありますか？\nあの、もしよければ\n今から手料理を…♪", 0, 2, 7671, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126001, "まったり過ごす\nお家時間もいいですね\n冒険者には羽休めも\n大事ですから♪", 0, 2, 7672, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126001, "いつでもお兄さんを\n守れるように\nこの姿でいることに\n慣れておきますね…！", 0, 2, 7673, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 126001, "お兄さん眠そうです…\nちょっとお昼寝\nしちゃいましょうか♪\nはい、膝枕…♪", 0, 2, 7674, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 126001, "お兄さん怪我を…！？\nわたしが治療を…\nふー…ふー…", 0, 2, 7675, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126001, "おはようございます♪\n朝、元気に挨拶すれば\n幸せな一日が過ごせる\nそうですよ♪", 1, 2, 7676, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126001, "お昼ごはんの時間です\n…手がふさがってる？\nわかりました、じゃあ\nあ～んしますね♪", 2, 2, 7677, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126001, "ふわぁ…眠くなって\nきちゃいました…\nお兄さん…お泊りして\nいいですか…？", 3, 2, 7678, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126001, "ふにゃ～～…♪\nこんな姿、他の人には\n見せられません…♪\nもふもふ～～…♪", 0, 3, 7680, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126001, "お兄さんこういうの\n好きなのかな～…\nふふっ、わたしも♪\nぎゅ～～～っ♪", 0, 3, 7681, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126001, "アロマの癒し効果…\n上手くアレンジして\nみなさんの治療に\n活かせないかな～？", 0, 3, 7682, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126001, "は～気持ちいい～♪\n身体が暖まると\n心もぽかぽかです♪", 0, 3, 7683, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126001, "うふふ、おいしい♪\nプレシアちゃんなら\n無限にぱくぱく～って\nしちゃいそうです…♪", 0, 3, 7685, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126001, "本はわたしを\n知らない世界に\n連れていってくれる…\nまるでお兄さんです♪", 0, 3, 7686, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 103801, "話題の新刊…\nあの人も読んでるかな\n今度会う時、感想\n聞いてみたいな…♪", 0, 1, 7688, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 103801, "うにゃっ…あ、熱を\n測ってくれたんですか\nありがとうございます\nバッチリ元気ですよ♪", 0, 2, 7689, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126101, "お兄の、けはい…\nん、みつけた\nぎゅ", 0, 1, 7690, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126101, "きょろきょろ\nここも\nぱとろる、するよ", 0, 1, 7691, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126101, "おうち\nにぎやか\nさみしく、ない", 0, 1, 7692, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126101, "きょだい、おにぎり\nきょだい、肉まん\nきょだい…お兄？", 0, 1, 7693, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126101, "はっぴーちゃん\nどこ？\nお兄、たべた？", 0, 1, 7694, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 126101, "くんくん…この、へや\nお兄の、におい\nすんでる\nすめる？", 0, 1, 7695, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 126101, "おそうじ、にがて\nでも\nれんしゅう\nきゅっきゅ", 0, 1, 7696, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126101, "みみ、さわり、たい？\nんー…だめ…\nむずむず\nなので", 0, 2, 7697, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126101, "くろぷれしあ\nかっこいい？\nん…あざます", 0, 2, 7698, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126101, "お兄の、おうち\nきょうみ、ましまし\nたんけん\nするね", 0, 2, 7699, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126101, "なでなで、される…と\nねむ…く…\nぶぅ…ぶぅ…ぶぅ…", 0, 2, 7700, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 126101, "かみ、りぼん\nおてつだい\nよろ\nです", 0, 2, 7701, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 126101, "ここ\nすむ\nね？\nまいにち、いっしょ", 0, 2, 7702, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126101, "ぐっもにん\nあさごはん\nあさごはん", 1, 2, 7703, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126101, "お兄\nおつかれ？\nれっつ、まいぺーす", 2, 2, 7704, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126101, "ひとりで、ねれる\nでも\nひとりじゃ、なくても\nねれる、よ…？", 3, 2, 7705, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126101, "ふかふか\nパパの、おなか\nみたい", 0, 3, 7707, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126101, "こんにちは\nぷれしあ、です\nおともだち、なろ？", 0, 3, 7708, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126101, "いい、におい\nでも、お兄\nもっと、いい、におい", 0, 3, 7709, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126101, "とりま\nお肉、やこ", 0, 3, 7710, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126101, "もぐもぐ…\nむげん肉まん、も\nきぼう", 0, 3, 7712, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 126101, "ぜんぶ、よんだ\nもっと、ぶあついほん\nでも、いーよ？", 0, 3, 7713, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127901, "幸せなクリスマスを\n過ごすため\nギルドハウスを\nきれいにしなくちゃ♪", 0, 1, 7715, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127901, "えへへ♪\n彼へのプレゼント…\n枕元にそっと\nおいておきましょう☆", 0, 1, 7716, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127901, "クリスマスといえば\n七面鳥の丸焼き…☆\nよだれを拭いて\nいざ準備です♪", 0, 1, 7717, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127901, "彼に用意した\nプレゼント…\n大きすぎて\nくつ下に入りません…", 0, 1, 7718, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127901, "プレゼントは\n貰う方も、贈る方も\nどちらも幸せを\n感じますね…☆", 0, 1, 7719, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 127901, "夜景が見える\nレストランであの人と\nクリスマスデート…\n憧れちゃいますね☆", 0, 1, 7720, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 127901, "プレゼントはわたし…\n恥ずかしいですけど\nこの贈り物、彼に\n喜んでほしいです…☆", 0, 1, 7721, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127901, "オーナメントを\nたくさん用意したので\n二人でツリーに\n飾りつけましょう☆", 0, 2, 7722, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127901, "あはは、お腹\n空いちゃいました？\nパーティーまで\n我慢してください♪", 0, 2, 7723, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127901, "ジングルベ～ル♪\nあなたも一緒に\nクリスマスソング\n歌っちゃいましょう♪", 0, 2, 7724, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127901, "サプライズプレゼント\nの用意をして…って\nあなた！？　い、今の\n聞いてませんよね…？", 0, 2, 7725, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 127901, "クリスマスの夜は\nパーティーしましょう\nできれば…その…\n二人っきりで…☆", 0, 2, 7726, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 127901, "あなたと過ごした\n最高のクリスマス…\nずっとずっと心の奥に\nしまっておきますね", 0, 2, 7727, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127901, "おはようございます！\n皆の願いが叶うような\n素敵な一日に\nしましょうね☆", 1, 2, 7728, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127901, "お昼ですね！\nディナーの買い出しが\n済んだら、少しお昼寝\nしちゃいましょうか☆", 2, 2, 7729, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127901, "そろそろ就寝の\n時間ですね～\n枕元にくつ下の準備を\nお忘れなく～☆", 3, 2, 7730, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127901, "ふ～気持ちいい…☆\nお家でまったり過ごす\nクリスマスも\nいいですよね…♪", 0, 3, 7732, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127901, "かわいいですね～♪\nサンタさんからの\n贈り物でしょうか～？", 0, 3, 7733, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127901, "すう～…はあ～…\nすごく素敵な香り…\n炊き立てのご飯と\nいい勝負です…♪", 0, 3, 7734, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127901, "暖かいですけど…\nあの日の山小屋を\n思い出して\n恥ずかしいですね…☆", 0, 3, 7735, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127901, "むしゃむしゃもぐもぐ\n止まりませんよ\nクリスマスですから\nむしゃむしゃもぐもぐ", 0, 3, 7737, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127901, "物語に出てくる\nサンタさんみたいに\nわたしも幸せを\n運びたいです…☆", 0, 3, 7738, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127901, "わっ！\nこ…これは素敵な\nサプライズプレゼント\nですね…☆", 0, 3, 7740, 14, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127901, "これを着ればわたしも\n立派なサンタさんに\nなれますかね…？", 0, 3, 7741, 15, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 106601, "ここが今のあたしの\n居場所ってやつです\nふふっ、なかなか\n悪くねー気分ですよ", 0, 1, 7742, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 106601, "これからはボス代行\nとしてブイブイ\nいわせてやるです\nついてくるですよ！", 0, 2, 7743, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128001, "陛下～♪\n貴女のクレジッタが\nおめかしして\nはせ参じましたわ～♪", 0, 1, 7744, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128001, "パーティーとは景気の\n良さを見せつける場…\n倹約などは\nもっての外でしてよ！", 0, 1, 7745, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128001, "この天井の高さなら\n搬入できそうですわね\n陛下用の特注ケーキ♪", 0, 1, 7746, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128001, "…ぶぇっくしゅ！\nふう…少し背中を\n開けすぎたかしら…？", 0, 1, 7747, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128001, "ここは秘書の目も\n届きませんから\nチクりメモの心配も\nありませんのよね～♪", 0, 1, 7748, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 128001, "いくらでも二度見して\nよろしくてよ？\n本当にわたくしだと\n心から納得できるまで", 0, 1, 7749, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 128001, "袖の下以外の贈り物も\nなかなかどうして…\n悪くないものですのね", 0, 1, 7750, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128001, "こう見えてダンスの\nたしなみはありますの\nお相手してあげても\nよろしくってよ", 0, 2, 7751, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128001, "ドレスはもちろん\n特注品ですわ♪\nもしや貸衣装だと\n思いまして？", 0, 2, 7752, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128001, "バッグの中身？\n仕事に必要なものと…\n心を潤わせるための\n宝石がいくつか♪", 0, 2, 7753, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128001, "やる気というものは\n仕事に必要な条件です\nいわばドレスコードの\nようなものですわね", 0, 2, 7754, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 128001, "全ての仕事は自分の\n血となり肉となり\nお金にもなるもの\nなのです。経験談です", 0, 2, 7755, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 128001, "今年の教訓を活かして\n来年は更に素晴らしい\nクリスマスにして\n差し上げますわ！", 0, 2, 7756, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128001, "おはようございます\n今日という稼ぎ時の\nはじまりですわよ！", 1, 2, 7757, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128001, "安くて早くておいしい\nそれがランチに必須の\n栄養素ですわ！", 2, 2, 7758, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128001, "宵越しの仕事は\n安眠の敵！\n熟睡したいのであれば\n今が勝負時ですわよ！", 3, 2, 7759, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128001, "たまにはこうして\n休息を取ることも…\n…はっ！？\nドレスにシワが～っ！", 0, 3, 7761, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128001, "…わたくしの勘が\n言っていますわ\nこの愛らしさ！\n爆売れ確・定！", 0, 3, 7762, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128001, "いい香りですわね…\nお金の匂いには\n敵いませんけれども♪", 0, 3, 7763, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128001, "……ハッ！？\n完全にボーッとして\nしまいましたわ…！\nぬくもり…恐ろしい！", 0, 3, 7764, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128001, "考えませんわ…\n考えませんわよ！\nカロリーのことはっ！", 0, 3, 7766, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128001, "ビジネス本以外の本を\n読むのも、たまには\n悪くありませんわね…", 0, 3, 7767, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128001, "よくできていますわね\nこの絵本！\n子ども向けだと甘く\n見ていましたわ…！", 0, 3, 7769, 14, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128001, "この視界の狭さ…\n思い出しますわね\nティッシュ配りの\nアルバイト時代を…", 0, 3, 7770, 15, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128101, "ふふふ…こっそり\nスイーツを食べる\nリノちゃん見～っけ♪", 0, 1, 7771, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128101, "お姉ちゃんの抜き打ち\nお部屋チェック～♪\n…ケーキの食べこぼし\nこれはリノちゃんだね", 0, 1, 7772, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128101, "この衣装は目立つから\nどこにいても弟くんに\n気付いてもらえるね！", 0, 1, 7773, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128101, "弟くん、協力して！\nリノちゃんにお勉強\nしなさいって圧を\nかけるんだよっ…♪", 0, 1, 7774, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128101, "絆の力を感じるよ\nこれならもっと万全な\n弟くんのお世話が\nできそうだねっ！", 0, 1, 7775, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 128101, "お姉ちゃんもたまには\nリノちゃんみたいに\n甘えてみようかな…", 0, 1, 7776, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 128101, "弟くんとリノちゃん\nどっちも大切な\n私のきょうだいだよ", 0, 1, 7777, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128101, "分かってるよ♪\nお姉ちゃんの膝枕を\nご所望だねっ？　え？\n違う？　違わないよ？", 0, 2, 7778, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128101, "リノちゃんがね\nお姉ちゃんチョップを\n防ぐ妹白刃取りを\n訓練してるんだよね", 0, 2, 7779, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128101, "お姉ちゃん頭突きは\n封印しないとね…\nだって、今使うと\n頭飾りで威力が…ね", 0, 2, 7780, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128101, "ふふっ♪\n目をつぶってても\n弟くんは見えてるよ\n…そこでしょ？", 0, 2, 7781, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 128101, "お姉ちゃんも\nリノちゃんみたいに\nかわいさいっぱいに\n振舞っちゃうぞ♪", 0, 2, 7782, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 128101, "弟くんっ♪\nいつもお姉ちゃんの\n声が届く範囲にいてね\n…絶対だよ", 0, 2, 7783, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128101, "おはよう、弟くん！\nリノちゃんが起こして\nくれたんだね♪\n…先を越されちゃった", 1, 2, 7784, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128101, "お姉ちゃんランチの\n時間だぞ♪\nさ、食べさせてあげる\nお口を開けて…♪", 2, 2, 7785, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128101, "おやすみの時間だよ\n…弟くんは真ん中ね\n左右には私たち姉妹が\nいるから安心だよ♪", 3, 2, 7786, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128101, "お姉ちゃんは全力で\nリラックスしてても\n弟くんの声一つで\nすぐにでも動けるよ♪", 0, 3, 7788, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128101, "リノちゃんこういうの\n好きそうだよね\n今度プレゼントして\nみようかな？", 0, 3, 7789, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128101, "いい匂いがするね～\nあれ？\nリノちゃんのケーキの\n甘い匂いも混ざってる", 0, 3, 7790, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128101, "お姉ちゃんの\n弟くんへの愛も\n熱く燃えあがるね…！", 0, 3, 7791, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128101, "たくさん食べて\n栄養を補給して\nお姉ちゃん力に\n全て変換するよっ！", 0, 3, 7793, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128101, "ことわざ辞典…\nリノちゃんにこれを\n読ませようかな", 0, 3, 7794, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128201, "そろ～り、そろり…\nお姉ちゃんが居ぬ間に\nスイーツを～…♪", 0, 1, 7797, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128201, "いつか\nお姉ちゃんに似合う服\n作ってみたいですね", 0, 1, 7798, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128201, "あいたっ！？\nひらひらしたとこ\n踏んづけちゃいました", 0, 1, 7799, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128201, "お姉ちゃんと\nお兄ちゃんの二人から\n勉強しろって圧力が～", 0, 1, 7800, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128201, "私の中にも秘めた力が\n眠っているはず…\nそう、妹パワーが", 0, 1, 7801, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 128201, "私もお兄ちゃんを\n甘やかしてみたいです\n…お姉ちゃんみたいに", 0, 1, 7802, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 128201, "お兄ちゃんの部屋を\nこっそり拝見～♪\nはっ！？　これは\nお姉ちゃんの視線…！", 0, 1, 7803, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128201, "お姉ちゃん頭突きは\n大地を揺らす威力です\n私とお兄ちゃんは\n挨拶感覚で喰らいます", 0, 2, 7804, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128201, "お姉ちゃんチョップは\n雲を裂く切れ味です\n早く妹白刃取りを\n完成させないと…", 0, 2, 7805, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128201, "お姉ちゃんみたいに\n大人っぽく着こなす\nには一体どうしたら…", 0, 2, 7806, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128201, "弓のデザインも\nかわいいんですよっ♪\n常に持ち歩きたいです\nけど…危ないですよね", 0, 2, 7807, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 128201, "きれいな服を着てると\nお姫さま気分に\nなれますね…あとは\n王子さまがいれば…♪", 0, 2, 7808, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 128201, "お兄ちゃんっ！\n私は褒められて伸びる\nタイプなのでもっと\nナデナデしてください", 0, 2, 7809, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128201, "妹モーニングコール♪\nお姉ちゃんより先に\n起こしに来れました！", 1, 2, 7810, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128201, "ああっ、お姉ちゃん！\nお兄ちゃんにアーン\nするなんてずるいです\n私もしたいですっ！", 2, 2, 7811, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128201, "おやすみの時間ですね\nえへへ、きょうだいで\n並んで寝るのって\n楽しいですよね！", 3, 2, 7812, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128201, "スイーツを食べた後は\nお腹を休めましょう～\n平気平気…ちゃぁんと\n運動もしますからぁ…", 0, 3, 7814, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128201, "お姉ちゃんも\nこういうかわいいもの\n好きなんですかね", 0, 3, 7815, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128201, "くんくん…\nなんの匂いでしょうか\n新しいスイーツ？", 0, 3, 7816, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128201, "きょうだい３人\n肩を寄せて暖まるとか\nよくないですかっ？", 0, 3, 7817, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128201, "いつも元気な妹で\nいるためには\nよく食べるのが大事！", 0, 3, 7819, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128201, "なんか姉に関する本が\nちらほらと混ざって…\nこれお姉ちゃんの\n仕業ですね！？", 0, 3, 7820, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128301, "ふんふんふ～ん\nお正月で世間は\n浮かれてるですねえ\nま、いいですけど♪", 0, 1, 7823, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128301, "は～、お正月は\nだらだらできて\n最高ですね\n…いつも通りですけど", 0, 1, 7824, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128301, "ビッグな幹部は\nビッグなお年玉が\nもらえるはずです\n楽しみですね", 0, 1, 7825, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128301, "今度ドラちゃんに\nお年玉を持っていって\nやりたいですね", 0, 1, 7826, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128301, "そういえばアユアユと\n約束してたですよ…\nそろそろ出かける\nですかね", 0, 1, 7827, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 128301, "お年玉がもらえたら\n何に使うか…\n今のうちに\n考えとくです", 0, 1, 7828, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 128301, "年神の報酬の駄菓子が\nたまってきたですね…\nガキどもにでも\n配っちまうですよ", 0, 1, 7829, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128301, "あたしのレアな\n晴れ着姿、今のうちに\nしっかり目に焼き付け\nとくがいいですよ", 0, 2, 7830, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128301, "お兄さんは今日も\nヒマそうですねぇ…\nしょうがないから\nかまってやるですよ", 0, 2, 7831, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128301, "そういえばカヤぴぃ\n見なかったですか？\n喧嘩屋初売りセール\nとか言ってたですけど", 0, 2, 7832, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128301, "お正月ならではの\n大きなシノギ…\nなにかないですかね？", 0, 2, 7833, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 128301, "いいとこに来たですよ\nさっき走ったら帯が\n曲がっちゃったです\n直すの手伝うです！", 0, 2, 7834, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 128301, "年神の仕事ですか？\nこれからいくとこです\nお兄さんも一緒に\n来やがるですよ！", 0, 2, 7835, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128301, "ふぁあ…\nどうしたですか？\nお正月に早起きなんて\n無粋ってヤツですよ…", 1, 2, 7836, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128301, "小腹が減ったですね\n一緒におせちでも\nつつきにいくですよ！", 2, 2, 7837, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128301, "夜はあたしの時間\nと言いたいとこですが\n初夢のために早く\n寝とくですかね…", 3, 2, 7838, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128301, "正月は食っちゃ寝\nするのが仕事…\nって聞いたこと\nあるですよ", 0, 3, 7840, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128301, "ふふっ、晴れ着の\nあたしの次くらいには\nかわいいですね", 0, 3, 7841, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128301, "なんかいい匂い…？\nよく知らんですけど\n晴れ着からも同じ\n匂いがしてるですね…", 0, 3, 7842, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128301, "ふ～あったまるです…\nお正月の唯一の弱点は\n寒いことですからね…", 0, 3, 7843, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128301, "もぐもぐ…正月太り？\nあたしには関係ない\n言葉ですね", 0, 3, 7845, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128301, "あったあった…\nトーゴクには\nドラゴンの神宮も\nあるんですね", 0, 3, 7846, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128401, "お正月のトラブルも\nハツネちゃんが\nめでたく解決\nしてみせるのだ～！", 0, 1, 7848, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128401, "そうだ、新年会に\nサプライズ大作戦を\nしかけちゃおう！", 0, 1, 7849, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128401, "イノリちゃんも\nカスミンも振袖姿が\nかわいかったな～", 0, 1, 7850, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128401, "シオリンに\nお年玉あげたいし\n彼にバイトを紹介\nしてもらおうかな？", 0, 1, 7851, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128401, "今度はどんな凧を\n飛ばそうかな？\nみんなが驚くような\n凧がいいよね～", 0, 1, 7852, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 128401, "ひらめいた！\n彼への年賀状は\nきらーんっ☆って\n感じにしよう！", 0, 1, 7853, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 128401, "来年も彼の隣で\n初日の出が\n見られるといいな～", 0, 1, 7854, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128401, "今日はキミが\nギルドハウスに来る\n予知夢を見たんだよ～\nふふ、正夢になったね", 0, 2, 7855, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128401, "この振袖どうかな？\nすっごくかわいいよね\nえへへ、ありがとう～", 0, 2, 7856, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128401, "ハツネちゃんおみくじ\n開始！　きらーんっ☆\nキミは大吉なのだ～\nどう元気出たかな？", 0, 2, 7857, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128401, "今度エルフの森にある\n薬草を使って七草粥を\n作ろうと思うんだ～\nぜひ食べてほしいな♪", 0, 2, 7858, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 128401, "お正月っぽい遊びを\n色々と用意したのだ～\nたまにはギルドハウス\nでゆっくり過ごそうよ", 0, 2, 7859, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 128401, "もしかして眠たいの？\n…じゃあ、また膝枕\nしてあげてもいいよ？", 0, 2, 7860, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128401, "いっぱい寝たから\n元気いっぱいだよ～\n今日もがんばろう！", 1, 2, 7861, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128401, "お昼だね～\nおせちがあるから\nキミも食べるでしょ？", 2, 2, 7862, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128401, "そろそろ寝る時間だね\n今日はどんな夢を\n見られるかな～", 3, 2, 7863, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128401, "いい座り心地～\n寝正月で過ごすのも\n最高なのだ～", 0, 3, 7865, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128401, "かわいいな～\n癒されてぐっすり\n眠れそうだよ～", 0, 3, 7866, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128401, "いい香り～\nリラックスすると\n眠たくなっちゃうよ…", 0, 3, 7867, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128401, "ポカポカいい気持ち～\n初日の出の温もりを\n思い出しちゃうな～♪", 0, 3, 7868, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128401, "もぐもぐ、おいし～\nでもいっぱい食べたら\n眠くなっちゃった…", 0, 3, 7870, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128401, "ふむふむ\nお正月料理も\n色々あるんだね～\n今度作ってみようかな", 0, 3, 7871, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 106501, "最近カレを見てたら\n何故かおいしそうに\n思えてくるんだよな…", 0, 1, 7873, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 106501, "手握っててくれるか？\nじゃないとオレ…\n力が抑えきれない…", 0, 2, 7874, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128501, "つかの間の平和を享受\nしながらぬくぬくと\n読書に耽る…\nふふ、最高だね", 0, 1, 7875, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128501, "お正月飾りなんて\n置いたらはしゃいでる\nように見えるかな…？", 0, 1, 7876, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128501, "ま、まずい…\n一人遊びしすぎて\nお正月遊びをほぼ\n極めつつある…", 0, 1, 7877, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128501, "ミステリーは\n時と場所を選ばない\nつまり…新年でも\n探偵は休まないよ！", 0, 1, 7878, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128501, "お世話になった人に\n年賀状でも書こうかな\n助手くん、喜んで\nくれるかな…えへへっ", 0, 1, 7879, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 128501, "絵馬に願い事を書こう\n助手くんと一緒に\nいられますように…♪\n…は、恥ずかしいな", 0, 1, 7880, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 128501, "助手くんはもっと\n派手な着物のほうが\n好みだったかな…？\nき、気になる…", 0, 1, 7881, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128501, "やあ。助手くんも\nお正月休みを\n持て余してるのかい？\nふふ、似た者同士だね", 0, 2, 7882, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128501, "助手くん助手くん！\n凧あげしにいこう！\n…そ、捜査の一環で", 0, 2, 7883, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128501, "着物は転びそうで\n怖いんだよなあ…\nお、押さないでね？\nフリじゃないよ！？", 0, 2, 7884, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128501, "ひ、人目を憚る\n本を読むなら\nみんなが帰省中の今！\nはっ！　助手くん！？", 0, 2, 7885, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 128501, "ね、ねえ…助手くん…\n一緒に初詣いかない？\nあ、いや！　深い\n意味はないんだけど！", 0, 2, 7886, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 128501, "あはは…助手くんと\n一緒だと家でごろごろ\nしてるだけでも\n楽しいや…♪", 0, 2, 7887, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128501, "ふぁ～…\nお正月休みのおかげで\n昼夜逆転中だよ…", 1, 2, 7888, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128501, "お正月も依頼が\nひっきりなしだね\nさあ、街へ出よう！\nもちろん君も一緒にね", 2, 2, 7889, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128501, "も、もっと\n近くにきてもいいよ？\n冬の夜は…寒いからね", 3, 2, 7890, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128501, "ううぅ～…！\nこのままじゃ\n寝正月だぁ…！", 0, 3, 7892, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128501, "みんなが帰省中でも\nこれがあれば\n寂しくない…かな", 0, 3, 7893, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128501, "うーん…♪\nお正月休みの\nリフレッシュに\nちょうどいいや♪", 0, 3, 7894, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128501, "ふぅ…あったまるな\n冬は寒いから\n助かるよ…", 0, 3, 7895, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128501, "うーん、おいしい♪\n読書のお供に\nちょうどいい", 0, 3, 7897, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128501, "時間に余裕があると\n読書が進むよ\nいざ、本棚読破だ♪", 0, 3, 7898, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128801, "この新しい力…\nさらに使いこなせる\nようになるには…", 0, 1, 7900, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128801, "私に眠る属性の力…\n風の力以外には\nどんな力があるのかな", 0, 1, 7901, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128801, "今の私なら\nどこまでいけるのか…\nやれるだけ\nやってみよう", 0, 1, 7902, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128801, "風の力の制御…\n段々とコツが\n掴めてきたな", 0, 1, 7903, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128801, "風の力か…\n応用の幅は広そうだ\n何ができそうか\n色々試してみよう", 0, 1, 7904, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 128801, "今度、私とキミで\n料理対決でもする？\n簡単には負けないよ", 0, 1, 7905, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 128801, "この胸に溢れる\n温かな気持ちは…\nきっと彼への…", 0, 1, 7906, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128801, "一段と剣技が\n冴えてきたような…\nいや…油断禁物だ", 0, 2, 7907, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128801, "かっこいいって？\nありがとう…そんなに\n真っすぐ褒められると\n照れてしまうな…", 0, 2, 7908, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128801, "どれほど厚い壁が\n立ち塞がろうと\n打ち破ってみせる", 0, 2, 7909, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128801, "手が空いてるなら\n剣の稽古に少し\n付き合ってくれるかな", 0, 2, 7910, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 128801, "試練を乗り越えて…\n私の中にあるものが\nはっきりと分かったよ", 0, 2, 7911, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 128801, "キミから流れてくる\n力の温かさ…これが\n私たちの絆なんだ", 0, 2, 7912, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128801, "朝だよ、そろそろ\n起きる時間だ\nそれとも私に起こして\nほしいのかな？", 1, 2, 7913, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128801, "昼食は私が作るよ\nリクエストはある？\nできる範囲で応えるよ", 2, 2, 7914, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128801, "おやすみ。また明日も\n一緒に冒険しよう\n約束だよ？", 3, 2, 7915, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128801, "休める時は休む…\n戦い続けるには\n必要なことだからね", 0, 3, 7917, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128801, "心の安らぎの大切さ…\nそれを知れたのは\nキミたちのおかげだね", 0, 3, 7918, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128801, "気持ちが落ち着くな…\nこの香り、風の力で\n纏えたりしないかな", 0, 3, 7919, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128801, "暖まった空気の流れ…\n以前より風の変化に\n気づくようになったな", 0, 3, 7920, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128801, "この味付け…\n今度試してみようか\n味見は任せたからね", 0, 3, 7922, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128801, "心技体、すべてを\n鍛えるには座学も\n必要だからね", 0, 3, 7923, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128901, "瞑想するのに\nいいところはあるかな\n集中できて…お昼寝は\n我慢できるところ", 0, 1, 7925, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128901, "ここにはあったかい\n空気が流れてるなぁ…\nみんなが受け入れて\nくれるおかげだね", 0, 1, 7926, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128901, "まるで心の中に\n光が満ちてるみたい…\nこの気持ち、みんなに\nおすそ分けしたいな♪", 0, 1, 7927, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128901, "この衣装は属性の力を\n反映しやすいんだよね\nキラキラしてるのも\n納得だよ", 0, 1, 7928, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128901, "エルフの里のみなさん\n元気かなぁ…\nまた会いに行きたいな", 0, 1, 7929, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 128901, "まだ変身できてる…\nわたしと騎士クンの\n修行の成果\n出てるなぁ…ふふっ♪", 0, 1, 7930, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 128901, "この服を脱いだら\nどうなるのかな…\nた、試す勇気は\nないけど…", 0, 1, 7931, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128901, "ふぅ…あ、騎士クン\nちょうど瞑想してたの\n一緒に…する？", 0, 2, 7932, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128901, "不思議だけど\nいつの間にか\n出たり消えたりするの\n…背中の輪っか！", 0, 2, 7933, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128901, "この服って肌触りが\nとってもいいんだよ\nさ、触ってみる？\nスベスベだよ？", 0, 2, 7934, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128901, "アストラルの力…\nすごく持続できてるね\n修行の成果が出てるよ", 0, 2, 7935, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 128901, "この力があれば\n暗いところで\n落とし物をしても\n見つけやすいかも…？", 0, 2, 7936, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 128901, "冒険の準備なら\nできてるよ\nいつでも声をかけてね\n…楽しみにしてる！", 0, 2, 7937, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128901, "おはよう騎士クン\n一緒にお散歩とか…\nどうかな？", 1, 2, 7938, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128901, "お昼ご飯のあとの\n瞑想って難しいんだよ\n眠くなっちゃって…", 2, 2, 7939, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128901, "夜ふかしさんは\nほどほどにね？\n一日頑張った自分を\n休ませてあげないと", 3, 2, 7940, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128901, "のんびりしてたら\nケーキが食べたく\nなっちゃうなぁ…\nでも太る…よね…？", 0, 3, 7942, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128901, "わあ、かわいい…！\n見てるだけで\nにやけちゃう…♪", 0, 3, 7943, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128901, "これは…花の香りかな\n知ってる花かも…？\n当ててみようっ", 0, 3, 7944, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128901, "あったかいと\nどうしてこんなに\n落ち着くんだろう…", 0, 3, 7945, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128901, "食べ過ぎたらお腹が\n目立っちゃうかもって\n分かってるのに…！\n止められないよ～っ！", 0, 3, 7947, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128901, "こ、恋の精霊とか\nいたりするのかな…？\nはっ！　ダメダメっ！\n実力で頑張らないと", 0, 3, 7948, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128701, "お部屋の中でも\n修行、修行っ♪\nでも燃やさないように\n注意しなきゃだね", 0, 1, 7950, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128701, "うーん…この衣装\n厚着か薄着か\nどっちなんだろう…？", 0, 1, 7951, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128701, "いつもの肉球がない分\nお片付けしやすいな♪\n騎士くんのお部屋\nピカピカにしとこー！", 0, 1, 7952, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128701, "お休みは騎士くんと\nＢＢＱに行きたいなー\nあたしと一緒なら\n火の心配はいらないよ", 0, 1, 7953, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128701, "この姿ならもっと\n人を助けられる…！\nえへへっ！\n燃えてきたーーっ！", 0, 1, 7954, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 128701, "騎士くんが帰ったら\nアイラみたいな笑顔で\nお疲れさまさまって\nお出迎えしよっと…♪", 0, 1, 7955, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 128701, "この姿の時、いつも\n胸が熱く燃えてるのは\nずっと騎士くんのこと\n考えてるからかな…？", 0, 1, 7956, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128701, "騎士くん騎士くん！\nあたしに触れると\nやけどしちゃうよ…？\nなんちゃってー♪", 0, 2, 7957, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128701, "おっ、騎士くん\n修行に行きたいの？\nえへへっ、いいねー♪\n二人で燃えちゃおー！", 0, 2, 7958, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128701, "そうだ騎士くん\nじゃんけんしよっか？\nグローブ外してるから\nチョキも出せるよっ♪", 0, 2, 7959, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128701, "この姿でいられる時間\nすごく増えてきた…！\nえへへ！　あたしたち\n修行の成果出てるね！", 0, 2, 7960, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 128701, "じっと見つめないでよ\n顔が真っ赤に…やば！\nほんとに顔から\n火噴いちゃいそう…！", 0, 2, 7961, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 128701, "火には強くなれても\n騎士くんに触られた\nほっぺはずーっと\n熱くてたまんないや…", 0, 2, 7962, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128701, "おはよー騎士くん！\n今日も一日\n燃え滾っていくよー！\nファイヤーッ！！", 1, 2, 7963, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128701, "騎士くんお昼だよ！\nちゃんとご飯食べて\n午後からの修行も\n乗り切ろうねー！", 2, 2, 7964, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128701, "今日もお疲れさま！\nそうだ、寝る前に\nキャンプファイヤー\n囲んじゃおっか♪", 3, 2, 7965, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128701, "ぼふーんっ！\nえへへ、気持ちいい…\n修行の前に\nちょっと休憩ー♪", 0, 3, 7967, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128701, "かわいいな～♪\nなでなでなでなでなで\nなでなでなでなでっ！\nあははっ、摩擦熱！", 0, 3, 7968, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128701, "ん～いい香り♪\n少し熱したほうが\n香りが広がるね…って\n蒸発しちゃった！！！", 0, 3, 7969, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128701, "外でも自分の火で\nあったまれるけど\nお部屋でぬくぬく\nするのが一番だよね♪", 0, 3, 7970, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128701, "もぐもぐっ！\nお腹が減っては\n人助けはできずっ！\nもぐもぐもぐっ！", 0, 3, 7972, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 128701, "ふむふむ…\nなるほどなるほど…\n…これが上手な\nＢＢＱのやり方か…", 0, 3, 7973, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129001, "…壁も扉も柱も\n全て記録で見たまま\n本当に…来ているのね", 0, 1, 7975, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129001, "…騒がしい場所\n騎士クンが受け入れて\nくれるのをいいことに", 0, 1, 7976, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129001, "ここが…騎士クンの\n守りたかった場所\n騎士クンの思い出が\n詰まった場所…ね", 0, 1, 7977, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129001, "視線を感じる…\nこちらの様子を\n探るような…心の動き\n取るに足らないけれど", 0, 1, 7978, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129001, "…そこかしこで…\n言葉にもしたくない\n相手が過ごしていた\n記録を思い出す…", 0, 1, 7979, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 129001, "人は多いけれど…\n雪深い山中よりは\n過ごしやすい…か", 0, 1, 7980, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 129001, "自覚したことは\nあまりなかったけれど\nひどく…諦めの悪い女\nなのかもしれない", 0, 1, 7981, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129001, "お招きありがとう\nそれで…何をして\nいればいい…かな？", 0, 2, 7982, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129001, "ここは…明るいね\nわたしの城と比べると\nなんだか眩しいくらい", 0, 2, 7983, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129001, "仮面はもう必要ないよ\nだって…わたしの顔を\n騎士クンに見て\nもらえなくなるから", 0, 2, 7984, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129001, "騎士クンさえよければ\nわたしの城に専用の\n部屋を用意するよ\n…いつでも", 0, 2, 7985, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 129001, "…騎士クンのマント\nまた貸してほしいな\nきれいに使うから\n…だめ？", 0, 2, 7986, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 129001, "いつか二人の願いが\n重なり合えるって…\n信じているね\n…いつまでだって…", 0, 2, 7987, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129001, "おはよう、騎士クン\n…昨日は夢の中で\nお話ししすぎちゃって\nごめんね。ふふっ", 1, 2, 7988, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129001, "お腹は…空いてない？\n食べ物はないけれど\n空腹を忘れる魔法なら\nかけてあげられるよ", 2, 2, 7989, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129001, "騎士クン…\nまだ、眠らないの？\n…そう。それなら…\nわたしも起きているね", 3, 2, 7990, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129001, "肩の力を抜くには…\nまだ時間がかかりそう\nきっと…騎士クンと\n二人きりなら別なのに", 0, 3, 7992, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129001, "ぺんぽこりん…\nそう、確かあれも\nこんな感じだった…", 0, 3, 7993, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129001, "花の香りは…好き\nつかの間\n心を穏やかに\nしてくれるから", 0, 3, 7994, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129001, "あたたかい…けれど\n騎士クンの優しさに\n比べれば、まだまだ", 0, 3, 7995, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129001, "おい、しい…？\nこの感覚が、それ？\nなぜか…納得できる", 0, 3, 7997, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129001, "情報を入手するには\nわざわざ本を開く\n必要があるだなんて…\nプレイヤーは面倒ね", 0, 3, 7998, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129301, "メルクリゾートか…\n良い記憶はないな\nよし忘れよう", 0, 1, 8000, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129301, "知性の戦士ユニ！\nその恐るべき筋力！\nふおお！　うにゃー！", 0, 1, 8001, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129301, "ぽーん！\n石用の水着ください！\nセクシーロゼッタが、\n渚の視線を独占です！", 0, 1, 8002, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129301, "学院から奨学金を\n勝ち取るより某財団を\nだまくらかした方が\n早い気がしてきたな…", 0, 1, 8003, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129301, "わんつぅ、わんつぅ…\nんぎゃあ足攣ったー！\nおのれカルミナとやら\n絶対に許さん！", 0, 1, 8004, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 129301, "ところで…なぜぼくは\nプールでもないのに\n水着のままなんだ…\nおのれ世界め！", 0, 1, 8005, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 129301, "彼と二人で撮った動画\nうう、凝視に耐えん…\nなんだこのムズ痒さは", 0, 1, 8006, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129301, "なに？　水着姿で\n寒くないのかだと？\n普段からもっと薄着の\n娘が沢山いるだろう", 0, 2, 8007, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129301, "シノブ君にメリゾの\n招待状を送ったのだが\n御尊父の暴走を\n心配していたな…", 0, 2, 8008, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129301, "ミミ君らにメリゾの\n招待状を送ったのだよ\n迎える準備をせねば…\nおしゃぶり持ってる？", 0, 2, 8009, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129301, "本質の賢者…？\nいや元気にしているが\nなぜ君がぼくの身内を\n知っているんだ…？", 0, 2, 8010, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 129301, "水着が似合ってる？\nな、なんだね突然…\nやめたまえよふひひ…", 0, 2, 8011, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 129301, "今度は夏にメリゾへ\n行きたいものだ\nむろん君も一緒だぞ", 0, 2, 8012, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129301, "ぐああっ、朝陽が…！\n朝陽が昇っている…！\n夜行性ユニ死んじゃう", 1, 2, 8013, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129301, "はぁ…もう昼か…\nそろそろ寝ないとな\n規則正しい生活を\nしないと不健康だ…", 2, 2, 8014, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129301, "ひゃっはー、夜だー！\nオレたちの時間だぜ！\nふおおブチ上がり～！", 3, 2, 8015, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129301, "これは…ぼくにここで\n寝ろということか？\n一向に構わんが…", 0, 3, 8017, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129301, "きゃっきゃっ！\nこれかわい～！\n…よし、今日も\n子供の擬態完璧だな", 0, 3, 8018, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129301, "ふむ、良い香りだ\nだがそれだけか…\nとくに精神高揚効果は\nないのか…ちぇー", 0, 3, 8019, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129301, "火を見ていると…\n魔導罐を思いだすな…\nかに座、元気かな…", 0, 3, 8020, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129301, "ええい、\n固形食物はいらん！　\nポーションを出せ！", 0, 3, 8022, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129301, "やはり書は文系に限る\nなんと心地よい筆致…\nぐああ突然理系文が！\nひぃ目が理系になる！", 0, 3, 8023, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129301, "ほああ～！\n知性の海上騎士ユニ！\nこれは新たな可能性！", 0, 3, 8025, 10, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129301, "これは…なんだ？\n何が面白いんだ？\n難しいな…", 0, 3, 8026, 11, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 108901, "大切な人と過ごす\n正月の思い出、か…\n昔の私だったら\n考えられなかったな", 0, 1, 8027, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 108901, "叶えたい願いなら\nたくさんあるよ\nそれでも一番を選ぶ\nなら、やはりキミと…", 0, 2, 8028, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 108801, "神殿にあった\n恋愛おみくじ…\n引いてみようかな…", 0, 1, 8029, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 108801, "騎士クン。来年も\nまた一緒におみくじ\n結ぼうね。約束だよ", 0, 2, 8030, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 108701, "今度は騎士くんと\n一緒にうつった写真、\nおばあちゃんに\n送ろうっと♪", 0, 1, 8031, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 108701, "あ、騎士くん！\n今年も…ううん\nこれからも、よろしく\nおねがいします♪", 0, 2, 8032, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129401, "旧校舎の地下牢…\n真冬のサマー計画…\n等身大のＰＲ…\nうッ、頭が…！", 0, 1, 8033, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129401, "暇だし鏡でも見るか。\nお、いい腹筋育って…\nあ？　何見てんだコラ", 0, 1, 8034, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129401, "オレは金色の疾風！\nんん…なんか違うな。\n我が名は金色の…\nあ？　何見てんだコラ", 0, 1, 8035, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129401, "モニカとユニ先輩って\n口調と身長似てるけど\n子供扱いに対する\n反応は真逆だよね…", 0, 1, 8036, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129401, "シオリン元気かな…\n昔くちゃくちゃに\n撫でくり回して\n嫌がられたっけなァ…", 0, 1, 8037, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 129401, "べつにいんだけどさ…\nあんたの知り合いって\n普段から水着みたいな\nカッコの女多くない？", 0, 1, 8038, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 129401, "そだ、弟らがあんたに\n会いたがってたンだ。\n暇だったらいつでも\nウチ来てやってよ", 0, 1, 8039, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129401, "特別講座は色々あった\nけど…メリゾ自体は\nまあ悪くなかったよね", 0, 2, 8040, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129401, "水着と合わせる小物に\nセンス出るよね、\nって話をミィコ先輩が\nしててマジわかりみ", 0, 2, 8041, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129401, "水着のミフユねえさん\nやっぱ仕上がってたね\nあれが竜殺しの体か…\nバリかっけーな…", 0, 2, 8042, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129401, "はにょんっ！？\nや、だからさ…\n後ろから声かけんな、\nつってンじゃん…", 0, 2, 8043, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 129401, "メリゾの特別講座が\n上手くいったのも\n敏腕カメラマンがいた\nお陰だよ。ありがとね", 0, 2, 8044, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 129401, "あ？　この水着が？\n似合っ…え、なんて？\nごめ、聞こえんくて。\nもっかい言ってみて", 0, 2, 8045, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129401, "もう朝とかマジないわ\n昨日二人に付き合って\nオールでカラオケだよ", 1, 2, 8046, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129401, "はぁ授業行かなきゃ…\n真っ昼間から勉強とか\nマジつらたん過ぎ…", 2, 2, 8047, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129401, "今日これから夜勤で…\nいやバイトちゃーくて\nボランティアの話よ？\n校則違反よくない", 3, 2, 8048, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129401, "うわ、ふっかふか…\nオニほど安らぐわ…\nやば、ユニってくる…", 0, 3, 8050, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129401, "うちも妹いりゃ\nこーゆーのもなァ…\nま自分用でもいいか", 0, 3, 8051, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129401, "ふーん…\nいいじゃんこの香り\n抜群に嗅げるわ", 0, 3, 8052, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129401, "うちン家のルーツの森\n大火災でなくなった\nらしンだよね…\n生まれる前の話だけど", 0, 3, 8053, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129401, "何コレありがてぇー\n食料ほくほく過ぎて草\n米ガチで尊いわァ…", 0, 3, 8055, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129401, "いいけど、なんでうち\n水着で本読んでンだよ\nどーゆープレイだよ", 0, 3, 8056, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129401, "金色の海上騎士クロエ\n推して参る！　覇ァ！\nあ？　何見てんだコラ", 0, 3, 8058, 10, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129401, "何コレ、貝の椅子？\nどーやって遊ぶんだ…\nいや座って終わりか…\nまァそりゃそーか…", 0, 3, 8059, 11, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129801, "さてと…\n明日の訓練メニュー\n考えねえとな", 0, 1, 8060, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129801, "また新入りの女子に\n弁当もらっちまった…\nありがたく食うけどよ", 0, 1, 8061, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129801, "種族なんて関係なく\nみんなが力を合わせて\nランドソルを守る…\nいい時代になったよな", 0, 1, 8062, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129801, "誰にだって長所はある\n新入りのいいところを\n見つけてやるのも\n教官の仕事だ", 0, 1, 8063, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129801, "厳し過ぎても\n甘過ぎてもダメ…\n教官ってのは難しいぜ", 0, 1, 8064, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 129801, "戦いが苦手な新入りを\nどうフォローするか…\n後であいつと\n相談してみるか", 0, 1, 8065, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 129801, "あいつは優し過ぎて\n戦いに向いてねえ\n…そういう甘さ\n嫌いじゃねえけどな", 0, 1, 8066, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129801, "強くなる近道はねえ\n地道な鍛錬あるのみだ\nさ、訓練始めるぞ！", 0, 2, 8067, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129801, "新入りにも\nいつも言ってるが…\n装備の手入れは\n念入りにしろよな？", 0, 2, 8068, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129801, "座学も受けてえのか？\nいい心がけだな！\nなんでも教えてやるよ\n…魔法以外ならな", 0, 2, 8069, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129801, "新入りたちがよく\n恋バナしててな…\nお、おまえは…誰か\n気になるやついるか？", 0, 2, 8070, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 129801, "なあ、ストレッチ\n手伝ってくれねえか？\n訓練前にしっかり\n筋肉ほぐさねえと…", 0, 2, 8071, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 129801, "ずいぶん頑張って\n鍛えたよな、おまえ\nよし！　ご褒美に\nお菓子作ってやるよ♪", 0, 2, 8072, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129801, "訓練日和だな！\nさぁて…まずは\n走り込みといくか！", 1, 2, 8073, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129801, "昼飯はしっかり食えよ\nでなきゃ、\nこの後もたねえぜ？", 2, 2, 8074, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129801, "とっくに消灯時間だ\nさっさと寝な？\n明日も早いんだからよ", 3, 2, 8075, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129801, "休むのも訓練のうちだ\n今はのんびり\nさせてもらうぜ", 0, 3, 8077, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129801, "いい面構えだ\n気に入ったぜ！\nあたしの班に入るか？\n…なんてな♪", 0, 3, 8078, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129801, "…いやされる匂いだな\n新入りたちにも\nすすめてみるか…", 0, 3, 8079, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129801, "あったけえからって\n寝ないようにしねえと\n教官としての\n威厳に関わるからな…", 0, 3, 8080, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129801, "食事も訓練の一環だ\nとはいえ食い過ぎには\n気をつけねえと…", 0, 3, 8082, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129801, "トーゴクの剣術…か\nなにか訓練に\n活かせるかもしれねえ", 0, 3, 8083, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129901, "あのおかたに近付く\n不埒な輩には、特別な\n訓練で地獄を見て\nいただきましょう", 0, 1, 8085, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129901, "これだけ広ければ\n多少派手に\n動き回っても\n問題なさそうですね", 0, 1, 8086, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129901, "…今誰か私を\n「鬼」教官と\n呼びませんでしたか？", 0, 1, 8087, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129901, "新しく開発したこの薬\n誰か試してくれる人は\nいませんか？", 0, 1, 8088, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129901, "厳しくすれば投げ出し\n優しくすればサボる\n人というのはどこまで\n度し難いのでしょう", 0, 1, 8089, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 129901, "はじめましょう\nあのおかたの心を\n射貫くための\nシミュレーションを", 0, 1, 8090, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 129901, "あらゆる苦難と\n忌々しい女から守る\nためにも、私自身も\n鍛えておかねば", 0, 1, 8091, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129901, "あなた様、そんなに\n見つめられると…え？\nエリコはカッコいい？\nええ、当然ですとも", 0, 2, 8092, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129901, "不調があればすぐ\n報告してください\n体のケアも教官の\n大事な役目ですので", 0, 2, 8093, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129901, "新しい訓練器具の\n使い心地はどう…え？\n重すぎる？　これでも\n軽くしたのですが…", 0, 2, 8094, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129901, "運動後には特製の\n栄養ドリンクをどうぞ\nクスクス、何も\n入れたりしてませんわ", 0, 2, 8095, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 129901, "よくがんばりました\nあなた様。あぁ…\nお疲れになったお顔も\n素敵ですわ", 0, 2, 8096, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 129901, "あなた様、二人だけの\n遠征訓練へ\nまいりましょう。\n既に準備万端ですわ", 0, 2, 8097, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129901, "まだ眠いですか？\nならばストレッチで\n体を痛めつ…ではなく\nほぐしていきましょう", 1, 2, 8098, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129901, "お昼はお弁当をご用意\nいたしました。量も\nスタミナも増し増し\n残さず食べてください", 2, 2, 8099, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129901, "よい睡眠こそ健やかな\n体に不可欠。ですので\n私が寝ずの番をさせて\nいただきますわ", 3, 2, 8100, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129901, "休憩用に使おうと思い\nましたが、心地よくて\n立てなくなって\nしまいますわ…", 0, 3, 8102, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129901, "中に重しを詰めれば\n愛でながら運動が\n可能ですわ", 0, 3, 8103, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129901, "香りでリラックスさせ\n同時に暗示を\nかけてみましょうか", 0, 3, 8104, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129901, "…くっ、眠気を誘う\nこの暖かさ…\n油断も隙もありません", 0, 3, 8105, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129901, "主食はこちらで十分\nまかなえますわね\nそれでは私はおかずを\n作りましょう", 0, 3, 8107, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129901, "まあ、実用書の類も\n揃っているのですね\nとても助かりますわ", 0, 3, 8108, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129601, "……（そわそわ）", 0, 1, 8110, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129601, "ここに寄らずの花陣…\n作ったらダメですよね", 0, 1, 8111, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129601, "部屋の隅が\n落ち着きます\nそれか、陽の当たる所", 0, 1, 8112, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129601, "身を隠せる場所が\nあると…\n助かるんですけど", 0, 1, 8113, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129601, "複雑な造りの家ですね\n触って壊れたり─\nしませんか？", 0, 1, 8114, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 129601, "出入りするひと\nみんな、笑顔…\n…変わった家ですね", 0, 1, 8115, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 129601, "この家なら安全─\nかもしれません\n魔物が出ないので", 0, 1, 8116, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129601, "これも、視察の一環\n遊びに来たわけじゃ\nないです", 0, 2, 8117, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129601, "長話─苦手です\n疲れるので…\nふぅ…", 0, 2, 8118, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129601, "ここで友達を作れと\nネアさんが…\nそう、言われても…", 0, 2, 8119, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129601, "世話をするのは\n得意です\n…花の、ですよ？", 0, 2, 8120, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 129601, "媛へのお土産を\n探してるんですけど…\n…貴方のおすすめ、\n聞いておきます", 0, 2, 8121, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 129601, "平和でのんきで…\n貴方らしい家です\n一応、褒めてます", 0, 2, 8122, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129601, "花に、お水─？\n…いりません", 1, 2, 8123, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129601, "日が高いうちは\nむやみに外へ出ません\n面倒事、多いので…", 2, 2, 8124, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129601, "夜番。私がします\nあまり眠らない\n性質なので…", 3, 2, 8125, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129601, "ここなら根を張れる\nかもしれません\n…張りませんけど", 0, 3, 8127, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129601, "こんなにやわな体じゃ\n身を守れませんよ─\n…あ。生き物じゃ\nないんですね", 0, 3, 8128, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129601, "花みたいに─\nいい匂い、です", 0, 3, 8129, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129601, "火を見てると、\nジオ・ゲヘナを\n思い出します…ふぅ", 0, 3, 8130, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129601, "食事も立派な戦支度\n媛が言ってました", 0, 3, 8132, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129601, "知識も武器の一つだと\n媛が…でも、\nこれでどうやって…？", 0, 3, 8133, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130701, "天気もいいしお花畑に\nピクニックでも\n行こうかな♪", 0, 1, 8135, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130701, "あ、どこからか\nお花の匂いが…\nいい香りですね", 0, 1, 8136, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130701, "新しいシャベルとか\nじょうろもほしいなぁ\nお小遣いしっかり\nためないとね", 0, 1, 8137, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130701, "お花飾りたいけど\n花瓶はどこかな…\nあとであの人に\n聞いてみよっと", 0, 1, 8138, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130701, "お花の観察日記\n書かなきゃ\n日々勉強は\n欠かせませんよ", 0, 1, 8139, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 130701, "衣装にお花を足して\nみたけど、あの人\n気付いてくれるかな？", 0, 1, 8140, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 130701, "この花冠、上手に\n作れたかも…\n日頃のお礼にあの人に\nあげようかな…", 0, 1, 8141, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130701, "あなたは何のお花が\n好きですか？　ほら、\n一応知っておいたら\n役に立つかもですし", 0, 2, 8142, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130701, "そのお花、乱暴に\n扱っちゃダメですよ\n花粉が服について\nとれなくなりますから", 0, 2, 8143, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130701, "土いじりしたから\n服が汚れてますよ\nはたいてあげますから\nじっとしててください", 0, 2, 8144, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130701, "そんなにすぐに芽は\n出ませんよ\nまあ、楽しみなのは\n私も同じですけど…", 0, 2, 8145, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 130701, "この衣装、そんなに\n気になります？　なら\nこうしてお花を\n着ければおそろいです", 0, 2, 8146, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 130701, "お花の種を私に？\nありがとうございます\nせっかくなので一緒に\nお世話しませんか？", 0, 2, 8147, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130701, "お花にお水をあげるの\n手伝ってもらえます？\nお水は朝にあげるのが\nいいんですよ", 1, 2, 8148, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130701, "とっておきのお花見\nスポットに\nご案内しますよ\nお弁当も忘れずに！", 2, 2, 8149, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130701, "もうこんな時間\n早く寝ましょう\n草木も眠るなんとか\nって言いますからね", 3, 2, 8150, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130701, "お花のお手入れって\nけっこう疲れますね…\nここでちょっと一息\nしちゃおっと", 0, 3, 8152, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130701, "耳にお花を着ければ…\nえへへ、すっごく\nかわいくなったね！", 0, 3, 8153, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130701, "これはラベンダー\nこっちはミモザ\nフローラル系の香り\nっていいですねぇ", 0, 3, 8154, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130701, "ポカポカしますね～\nまるで春の陽気\nみたいです～", 0, 3, 8155, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130701, "お花を一輪さすだけで\nご飯の時間がとても\n華やかになりますね", 0, 3, 8157, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130701, "お花の本が\nいっぱいある！\n今日はこれで\nお勉強です！", 0, 3, 8158, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130801, "ふんふ～ん♪\n華やかな服を着ると\n心がウキウキします♪", 0, 1, 8160, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130801, "花祭りで出会った\nみなさん…\nお元気でしょうか？\nまたお会いしたいです", 0, 1, 8161, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130801, "はっ！　そうでした\nこの服の時は\nメイドのお仕事は\nお休みでした…", 0, 1, 8162, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130801, "この服は替えが\nありませんから\n大きなドジには\n気を付けないと…", 0, 1, 8163, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130801, "目立ったほつれもなし\n…うん、いい感じに\n補修できました♪", 0, 1, 8164, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 130801, "メイド姿でなくても\n心はいつでも\nお嬢さまの\nメイドです♪", 0, 1, 8165, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 130801, "この服を着ると心が\n浮き立つのはあのかた\nと一緒にいる気持ちに\nなるからでしょうか？", 0, 1, 8166, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130801, "はい、今日は一日\nこの服で過ごそうと\n思います\n変じゃないですよね？", 0, 2, 8167, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130801, "花祭りのおかげで\n少しだけお花に\n詳しくなった…\nような気がします！", 0, 2, 8168, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130801, "花祭り、今度は\nお嬢さまと救護院の\nみんなと一緒に\n行ってみたいですね", 0, 2, 8169, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130801, "今度キョウカちゃんと\n花祭りの服でお花を\n見にお出かけ\nするんです", 0, 2, 8170, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 130801, "メイド服を全部洗濯\nしちゃうなんて…\nドジっちゃいましたぁ", 0, 2, 8171, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 130801, "花祭りの服は私たちの\n思い出の服ですから…\nこれからも\n大切にします♪", 0, 2, 8172, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130801, "はう、そうでした\nお花にお水を\nあげる時間です！", 1, 2, 8173, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130801, "お昼ご飯は\nバスケットを持って\nお花畑に食べ\nに行きませんか？", 2, 2, 8174, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130801, "ふぁあ…繕い物を\nしていたらこんな時間\nになってしまいました\n早く寝ないと…", 3, 2, 8175, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130801, "ふわふわしてて\nきもちいいです～\nどんな生地を\nつかってるんだろ～？", 0, 3, 8177, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130801, "かわいいですね～\nぬいぐるみさんにも\n服をつくって\nあげたいです♪", 0, 3, 8178, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130801, "いい香りです～\nあれ？　もしかして\n私の服についてる花と\n同じ香りが…？", 0, 3, 8179, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130801, "暖かいですけど\nあまり近づいちゃうと\n頭の花飾りが乾燥\nしちゃいそうですね…", 0, 3, 8180, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130801, "もぐもぐ…\nいい花を咲かせるには\nしっかりとした栄養が\n必要だそうです！", 0, 3, 8182, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130801, "なるほどなるほど…\nお花のお手入れは\n奥が深いですね…", 0, 3, 8183, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131001, "マホマホ王国にも\nサーカス団設立や\n夢と希望を届けますえ", 0, 1, 8185, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131001, "せっかくの晴れ舞台や\nかっこいいポーズも\nできるようにしたいわ", 0, 1, 8186, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131001, "むむむむ～っ。うーん\nクルルはんみたいに\n胸から宝石を出すんは\n難しいみたいやわ", 0, 1, 8187, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131001, "あむっ…んん～♪\nやっぱり練習のあとは\n甘いものに限るわぁ", 0, 1, 8188, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131001, "アーサーたちの衣装も\n今のうちに考えんと…\n生地、足りるやろか", 0, 1, 8189, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 131001, "王子はんとなら\nどんなアイデアも\n見つけられます♪", 0, 1, 8190, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 131001, "うちの晴れ姿が\n王子はんの目に一等\n輝いて見えますように", 0, 1, 8191, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131001, "キリっとしたうちも\nかっこいい？\nもう、上手やなぁ", 0, 2, 8192, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131001, "新しい演目考えたんよ\nなぁ王子はん、意見\n聞かせてもらえます？", 0, 2, 8193, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131001, "聞いてや王子はん\n動物さんたちとさらに\n息が合うてきたんよ\nみんな頑張り屋さんや", 0, 2, 8194, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131001, "演技の感想なら\n大歓迎どすえ♪\nたんと聞かせてや～", 0, 2, 8195, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 131001, "王子はん、本番当日は\n最前席で見たってな\nそしたらうち、もっと\n頑張れますさかい♪", 0, 2, 8196, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 131001, "この技をあなたに\n捧げます…なんて♪\n驚いたやろ？\nどっきり作戦成功や♪", 0, 2, 8197, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131001, "素敵なショーのためや\n今日も焦らず一歩ずつ\n成長していきまひょ\nな、王子はん♪", 1, 2, 8198, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131001, "お弁当、王子はんも\n一緒にいかがどす？\nみんなで食べて団結や", 2, 2, 8199, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131001, "夜のサーカスも\n幻想的でとっても\n魅力的なんよ\n一緒にいきまひょか♪", 3, 2, 8200, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131001, "練習が終わったあとは\nの～んびりして\n体力を回復せな…", 0, 3, 8202, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131001, "この子たちとなら\n空中ブランコや\n玉乗りもええなぁ", 0, 3, 8203, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131001, "いい香りやなぁ\n本番前の緊張も\n和らげてくれそうやわ", 0, 3, 8204, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131001, "このあったかい感じ…\nスポットライトの熱を\n思い出すわぁ", 0, 3, 8205, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131001, "ちゃんと食べへんと\nいい演技はできまへん\n団長はんも言うてたわ", 0, 3, 8207, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131001, "動物さんたちのことも\nもっと勉強せな\n大事な仲間やさかい", 0, 3, 8208, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 105601, "器用じゃないが、\n情には厚いよ\nこういう女を「一途」\nってそう言うんだろ？", 0, 1, 8210, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 105601, "斬れないものはないと\n思ってきたさ。だけど\n…この気持ちだけは\n斬れないね、絶対に", 0, 2, 8211, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130901, "んしょ、んしょっ…！\n日頃の感謝を込めて\nヒーローさんのお家を\nきれいにしますです♪", 0, 1, 8212, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130901, "ふむふむ…\n新アトラクションは\nこのギルドハウスを\nモチーフに…", 0, 1, 8213, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130901, "ごそごそ、こそこそ…\nヒーローさんに内緒で\nどこかにお礼の宝石を\n隠しておきましょう…", 0, 1, 8214, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130901, "パークの運営が\n落ち着いたら\nわたしもギルド活動？\nしてみたいですます♪", 0, 1, 8215, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130901, "こんなすてきな\nおうちに住めたら…\nいえ！　うさぎ小屋で\n十分です～っ", 0, 1, 8216, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 130901, "ヒーローさんのおうち\nだって意識したら\nアトラクションよりも\nドキドキしてきます…", 0, 1, 8217, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 130901, "ヒーローさんのことを\n考えると顔が赤く…\nこれってもしかして\nひょっとして…？", 0, 1, 8218, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130901, "あっ、ヒーローさん！\n話しかけてくれた記念\nということで、宝石\n受け取ってください♪", 0, 2, 8219, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130901, "元気がない時は\n語尾にぴょんですっ！\nさあヒーローさんも\nやってみるぴょん♪", 0, 2, 8220, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130901, "ヒーローさんへのご恩\nおばあちゃんうさぎに\nなるまでに、お返し\nできるでしょうか…？", 0, 2, 8221, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130901, "ヒーローさん！\n二人で一緒に\nパレードの練習\nしませんか～♪", 0, 2, 8222, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 130901, "あはは♪\nヒーローさんは\n人を笑顔にする力が\nありますです♪", 0, 2, 8223, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 130901, "ヒーローさんに\n一日一恩返しですっ！\n返し終わるまで\nやめませんですます♪", 0, 2, 8224, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130901, "おはようございます♪\nんしょ、んしょっ…！\n気持ちよく起きられた\n記念に宝石どうぞ♪", 1, 2, 8225, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130901, "お昼にフードコートで\nかばばん君バーガー\n買ってきましたよ！\n一緒に食べますです♪", 2, 2, 8226, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130901, "今日も一日\nお疲れさまですっ♪\nマッサージしますから\n寝そべってください♪", 3, 2, 8227, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130901, "はふぅ～…♪\nお仕事の疲れも\nぴょんぴょ～んって\n飛んでっちゃいます♪", 0, 3, 8229, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130901, "わぁっ、とっても\nかわいいですますっ！\nギルドハウスの\nマスコットですね♪", 0, 3, 8230, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130901, "ヒーローさんの好きな\n香り…これを宝石に\nつければ、受け取って\nもらえるでしょうか？", 0, 3, 8231, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130901, "はあ、気持ちいい♪\nヒーローさんの\n好きな温度に\n調整しときますです♪", 0, 3, 8232, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130901, "支配人は体が資本っ！\nたくさん食べ…すぎて\n動けなくなっちゃい\nましたぁ～…！", 0, 3, 8234, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130901, "パークの\nガイドブックとか\nあったらいいですね♪", 0, 3, 8235, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131101, "外あつすぎ溶けるわ～\nやっぱ部屋ん中で\n水着がラクだねー", 0, 1, 8237, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131101, "ン～\nカリザきゅんどこー？\nもしかして逃げた？", 0, 1, 8238, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131101, "ミソラちゃんと\nリリちゃん誘って\nそろそろイイコト\nしよっかなー？", 0, 1, 8239, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131101, "おねえちゃんに\n頼まれたシゴトは\nまた今度にすっかー\n夏休みだしー♪", 0, 1, 8240, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131101, "騎士きゅんの\nプリンセスフォームも\n見てみたいんだよなー\nンフ♡", 0, 1, 8241, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 131101, "スライムを\nこーしてあーしたら\n騎士きゅんの泳ぐ\n練習に使えるっぽ？", 0, 1, 8242, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 131101, "おかえり～♡\nって水着で迎えたら\n騎士きゅん喜んで\nくれるかなー？", 0, 1, 8243, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131101, "騎士きゅん\nこのサングラス\nかけてみる？\nぜってー似合うよー", 0, 2, 8244, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131101, "日焼け痛かったら\nいつでも言ってねー\nスライムでひんやり\nしてあげるよ～♪", 0, 2, 8245, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131101, "今から外で\n焼いてこっかなー\n騎士きゅんもどー？", 0, 2, 8246, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131101, "スライムマッサージ\nしてあげよっかー\nほーれ、もみもみ～♡", 0, 2, 8247, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 131101, "騎士きゅんが選んだ\n水着も着てみたーい\nネアおねえちゃんに\nどんなの着てほしい？", 0, 2, 8248, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 131101, "騎士きゅ～ん\n尻相撲しよっかー♪\nおねえちゃんに\n勝てたらご褒美だぞ～", 0, 2, 8249, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131101, "暑すぎてムダに\n早起きしちった～\n二度寝すっか～\n…ふぁあ…", 1, 2, 8250, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131101, "夏バテしても\nスイカとかかき氷なら\n全然ウェルカム\nなんだよなー", 2, 2, 8251, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131101, "今日はこのまま\n水着で寝るかー\n夢ん中で泳ごー", 3, 2, 8252, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131101, "いったんキューケイ…\nンー…よし\n充電カンリョ～♪", 0, 3, 8254, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131101, "仲間になりたそうに\nこっち見てんねー\nきゃわいいしおっけ～", 0, 3, 8255, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131101, "ン～いいニオーイ\nお日様あたった\n騎士きゅんの\n次くらいかな～", 0, 3, 8256, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131101, "カラダ冷えてきたから\nたすかるーおかげで\nまだまだ水着イケるわ", 0, 3, 8257, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131101, "うめ～\nとまんねぇ～\n夏バテどこいった～？", 0, 3, 8259, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131101, "ふむふむ…\nこれは意外と\nぬるっと読めるっぽー", 0, 3, 8260, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131101, "ンフフ～\n海の戦士ってカンジ？\nアリよりのアリだね～", 0, 3, 8262, 10, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131101, "あーやべ\n一瞬で寝そー\nヤバだわ～", 0, 3, 8263, 11, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129701, "服や紙が易々と\n燃えぬとは\nランドソルとはまこと\n住み易き国で候", 0, 1, 8264, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129701, "どの世界にあっても\n民は活力に溢れ\n街は賑わうもの…\n善哉、善哉", 0, 1, 8265, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129701, "食べ歩きに世界征服…\nギルドとはかくも\n自由気儘な徒党に\nありんすなぁ", 0, 1, 8266, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129701, "連絡は…今日もなし\n我が妹子は相変わらず\n放埓なことよ喃", 0, 1, 8267, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129701, "モネは相変わらず\n警戒しきりなれど…\nそのようなところも\n愛らしくあり申す", 0, 1, 8268, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 129701, "なんと長閑な世にや\nありけむ…この平和は\n騎士殿たちの努力の\n賜物にありんす喃", 0, 1, 8269, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 129701, "『王家の装備』は\n使わぬのかと？\n…フフ。すでに\n用いており申す", 0, 1, 8270, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129701, "今ばかりは媛という\n立場は置いて\nこの地の者らと\n歓談したく候", 0, 2, 8271, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129701, "強さの秘密？　…フフ\n女子の秘密を暴くのは\n野暮にござんす\nご寛恕いただきたく候", 0, 2, 8272, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129701, "此方の家屋、\n見目以上に\n堅牢であり申す\n何ぞ仕掛けがありや？", 0, 2, 8273, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129701, "心地よい時間とは\n疾く過ぎ去りゆくもの\n此処で過ごすと改めて\n実感できる喃", 0, 2, 8274, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 129701, "視線が気になる？\nこれはすみませぬ\nなにぶん、お前さまは\n見ていて飽きませぬ故", 0, 2, 8275, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 129701, "お前さまの前では\n誰もが心を開く…\nなんとまぁ罪作りな\n色男にありんすな？", 0, 2, 8276, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129701, "よく眠れましたかの？\n寝る子は良く育つ、と\n古くより申します由", 1, 2, 8277, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129701, "美食殿の作法に倣い\n皆で共に昼餉を頂くと\n致しんしょう", 2, 2, 8278, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129701, "逢魔ヶ時は望まぬ客も\n参るもの…お前さまも\n気をつけなんし", 3, 2, 8279, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129701, "わっちの蛇に\n勝るとも劣らぬ\n座り心地で候", 0, 3, 8281, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129701, "魂、万物に宿る\nとの云いあらば\n情愛を注いでやるのが\nよかろうの", 0, 3, 8282, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129701, "いと香しき哉…\n騎士殿は香の道にも\n詳しくあられるか", 0, 3, 8283, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129701, "ジオ・ゲヘナの\n灼熱と比ぶれば\nそよ風と変わり\nござんせん", 0, 3, 8284, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129701, "美味なる哉…この味\nぺコリーヌ姫が好むも\n道理にありんす", 0, 3, 8286, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129701, "トーゴクに、オーマ…\n書で見るだけでも\n心惹かれる土地よの", 0, 3, 8287, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131201, "暑い日には\n冷たいものですよねー\nかき氷とか食べて\nすっきりしたいです★", 0, 1, 8289, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131201, "海への行き来は\nテレポーテーションに\nお任せです★", 0, 1, 8290, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131201, "トロピカルジュースが\n飲みたいでーす♪\n海の家で見たんですよ\nカップル用ジュース★", 0, 1, 8291, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131201, "ご飯どうしようかなー\nあ、海鮮ラーメンとか\n美味しそうかも★", 0, 1, 8292, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131201, "水鉄砲、普通に撃つと\n少し物足りないかも…\nどるるるる～ん★って\n感じが欲しいですよね", 0, 1, 8293, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 131201, "コイビトっぽいこと\nアオハルっぽいこと\nいっぱいしたいです★\n修羅場とかも♪", 0, 1, 8294, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 131201, "騎士さんが溺れたら\n絶対助けに行きますね\n人工呼吸でもなんでも\nしてあげますから★", 0, 1, 8295, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131201, "騎士さーん\n日焼け止めの塗り直し\nお願いします★\n優しくしてくださいね", 0, 2, 8296, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131201, "海で食べたフルーツ？\nはい、まだありますよ\nこっちでもあーんして\n食べさせましょうか★", 0, 2, 8297, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131201, "ぺんぽこりんは\nどうしたのかって？\nどうしたんでしょうね\nどーでもいいです★", 0, 2, 8298, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131201, "今日はどんな\nイイコトしましょうか\n思い出作り？\n人助けもいいですね★", 0, 2, 8299, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 131201, "ヒモを引っ張ったら…\n「セ・キ・ニ・ン」\n取ってもらいますよ？\nなーんてウソですけど", 0, 2, 8300, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 131201, "みんなでイイコトも\n楽しいですけど～\n…二人でイイコト\nしちゃいますか？", 0, 2, 8301, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131201, "朝ですよー！\n今日も一日、\nイイコト日和ですね♪", 1, 2, 8302, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131201, "日差しにご注意ですね\n日焼け止め…誰かに\n塗ってほしいなー？\n…ちらっ★", 2, 2, 8303, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131201, "夜の海って\n惹かれちゃいますよね\n…ロマンチックです★", 3, 2, 8304, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131201, "う～ん、まったり～\nこれぞ新ジャンル\nおうちバカンス★", 0, 3, 8306, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131201, "よーしよーし★\nわたしとイイコト\n一緒にしましょうねー", 0, 3, 8307, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131201, "いい香りですねー\nリラックスタイムに\nちょうどよさそう★", 0, 3, 8308, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131201, "水遊びのあとは\nしっかり体を\n温めないとですよね★", 0, 3, 8309, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131201, "栄養補給もバッチリ★\n遊びもイイコトも\nお腹が減ってたら\nできませんからねー？", 0, 3, 8311, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131201, "今だと海関連の本とか\n興味あるんですよねー\n魚とか海難救助とか★", 0, 3, 8312, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131201, "かっこいー★\n名前はー…\nぷんぷくぽんとか？", 0, 3, 8314, 10, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131201, "映えますねー\n写真撮ったら\n素敵な思い出に\nなりそうです★", 0, 3, 8315, 11, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131601, "海でもないのに\nこの姿でいるのは…\nやはり、場違いでは\nありませんか…？", 0, 1, 8316, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131601, "スイカとは割ることと\n見つけたり！\n…ですが目隠しすれば\n見当たらないもの…", 0, 1, 8317, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131601, "まぁ、膝に砂が…\n気がつきませんでした\nまるでわんぱくな\nお子様のよう…ふふっ", 0, 1, 8318, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131601, "家の中でスイカを割る\nわけにはまいりません\nここは潔く包丁で\n切りましょう", 0, 1, 8319, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131601, "ミソラ様やネア様の\n大胆さを見習って…\nき、騎士きゅーん…★\n難しいですね…", 0, 1, 8320, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 131601, "きゅ、急に暗く…！？\n…失礼しました\n鉢巻きが下がってきた\nだけでした…ううぅ", 0, 1, 8321, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 131601, "わたくしに剣の才は\nあるのでしょうか…\nいえ、何事もまずは\nやってみなくては", 0, 1, 8322, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131601, "なにか羽織った方が\nよろしいですよね…？\nそのままでいい？\nそれはそれで…その…", 0, 2, 8323, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131601, "あの、兄様…？\nスイカの種を飛ばす\n遊びもあるというのは\n…本当なのでしょうか", 0, 2, 8324, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131601, "強くなるための修行は\nまだ始まったばかり\nまずは己の目指すべき\n道を見定めなくては", 0, 2, 8325, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131601, "同じ薄着であっても\n黒い天使の姿より…\n気が楽です", 0, 2, 8326, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 131601, "はい、ただいまー！\n…あっ！\n海の家でのお手伝いの\nくせが、つい…", 0, 2, 8327, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 131601, "兄様さえよろしければ\nまた一緒に海へ…\nあ…申し訳ありません\n浮ついてしまいました", 0, 2, 8328, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131601, "本日もイイコトをして\n清々しい一日を\n過ごしたいものです\nまさに一日一善ですね", 1, 2, 8329, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131601, "お昼時になると\n海の家の料理を\n思い出してしまいます\n焼きそばにかき氷…♪", 2, 2, 8330, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131601, "兄様…よろしければ\n浜辺を歩きませんか？\nきっと、月が\nきれいでしょうから", 3, 2, 8331, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131601, "ふわふわ心地よすぎて\n眠くなってしまいます\n水着姿では\nいけません…ふぁ…", 0, 3, 8333, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131601, "なんてよい手触り…\n癖になってしまいます\nふふ、よしよし…♪", 0, 3, 8334, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131601, "爽やかな香り…♪\n日焼け止めクリームを\n思い出してしまいます", 0, 3, 8335, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131601, "冷たい水で冷えた体が\n温まります…♪\nまるで兄様のようです", 0, 3, 8336, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131601, "お腹が空いては\n修行ができません…！\n動きに障りない程度に\n栄養の補給を…！", 0, 3, 8338, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131601, "剣術とスイカ割りが\n上達する指南書…\n見つかりませんね…", 0, 3, 8339, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131601, "さあ、ともに大海原を\n参りましょう！\nなんて…ふふっ♪", 0, 3, 8341, 10, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131601, "まるでおとぎ話の\n登場人物になった\nような心持ちです…♪", 0, 3, 8342, 11, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131701, "あたしはなんで水着で\n室内をうろついてるの\nかしら…不思議だわ", 0, 1, 8343, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131701, "あ、天井が汚れてる\n仕方ないわね\nひとっ飛びしますか", 0, 1, 8344, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131701, "はい、お疲れさま♪\n出迎えの言葉は\nやっぱこれよね♪", 0, 1, 8345, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131701, "そういえばあたしって\n羽があるけど\n泳げるのかしら…", 0, 1, 8346, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131701, "あいつのために\n浮き輪も用意したし\n抜かりはないわよ", 0, 1, 8347, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 131701, "海までのガイド計画も\nまとまったわね\nふふっ…楽しみだわ", 0, 1, 8348, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 131701, "この水着、あいつが\n選んでくれたのよね\n…大事にしないと", 0, 1, 8349, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131701, "ネビアは最近どう？\n迷惑かけてない？\n…かけてるのね", 0, 2, 8350, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131701, "あんた…部屋の片付け\nくらい自分でしなさい\n散らかってたわよ？\n…片付けておいたけど", 0, 2, 8351, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131701, "もしかして暇なの？\nだったら記憶でも\n見せましょうか？\n凄く恥ずかしいやつ♪", 0, 2, 8352, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131701, "夏ってこんなに\n暑いのね…\n海にばかり行く\n気持ちが分かったわ", 0, 2, 8353, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 131701, "あんたの水着って\n新しいの買わないの？\nなんならあたしが\n選んであげるわよ♪", 0, 2, 8354, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 131701, "気軽に触れ合える\n距離にいる…\nあたしにとって\nとても大事なことよ", 0, 2, 8355, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131701, "あらおはよう\n夏の朝は暑いわね\nシャワーでも浴びて\nさっぱりしなさい", 1, 2, 8356, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131701, "お昼は何を食べるの？\nガイド妖精として\n提案しましょうか？", 2, 2, 8357, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131701, "夜更かしは厳禁よ\n無理やり眠らせて\nあげましょうか？", 3, 2, 8358, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131701, "アメスさまだって\nゴロゴロするときは\nあるのよ～", 0, 3, 8360, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131701, "…ふふっ♪\n何よ、あたしが\nかわいいものを\n愛でちゃだめなの？", 0, 3, 8361, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131701, "こういういい香り…\n塔の中じゃなかなか\n楽しめないのよね", 0, 3, 8362, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131701, "水着を着てるのに\n温まるのって\n矛盾してない？\n気にしすぎかしら", 0, 3, 8363, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131701, "あんたと行動するなら\n沢山食べられるように\nならなきゃね…！", 0, 3, 8365, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131701, "文字を読むのと\nデータを読むのは\nまったく違うわ", 0, 3, 8366, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131701, "へぇ、面白いじゃない\nこういう経験、昔じゃ\nできなかったわ", 0, 3, 8368, 10, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131701, "面白いクッションね\nところであんた…\nあたしで遊んでない？\n気のせいならいいけど", 0, 3, 8369, 11, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118601, "また新しい悪戯\n思いついちゃった♪\n誰に仕掛けようかしら", 0, 1, 8370, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118601, "あ～あ、退屈…\nフィオでもからかって\nこようかしら♪", 0, 1, 8371, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118601, "ミソギの悪戯って\nレベル高いのよね…\n負けてらんないわ！", 0, 1, 8372, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118601, "家事ニガテなのよね～\nコッコロたちには\n感謝しないとね", 0, 1, 8373, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118601, "私も魔物料理に\n慣れてきちゃった…\n毒されてるわね…", 0, 1, 8374, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 118601, "あいつに仕掛ける\n次の悪戯は～…♪\nこれ…ううん…\nやっぱこっちかしら？", 0, 1, 8375, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 118601, "結局ギルドハウスが\n一番落ち着くわね\n…私たちの家だもの", 0, 1, 8376, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118601, "悪戯のコツでも\n教えてあげよっか？\n…え～興味ないの？", 0, 2, 8377, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118601, "これからもずーっと\n退屈しない毎日に\nしてあげる♪\n私の悪戯でね♪", 0, 2, 8378, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118601, "ちょうど退屈してたの\nたーっぷりと\n構ってもらうわよ♪", 0, 2, 8379, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118601, "案内が必要なら\n頼ってくれていいわよ\n一応は私だって\nガイド妖精だし！", 0, 2, 8380, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 118601, "冒険のガイドなら\n私に任せなさい♪\nある意味忘れられない\n旅にしてあげる♪", 0, 2, 8381, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 118601, "妖精がいる家は\n栄えるのよ♪　だから\nずっといてあげる\nあんたのそばにね♪", 0, 2, 8382, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118601, "きゃははっ！\n寝ぐせついてるわよ？\nこのままにしましょ！\nだって面白いし！", 1, 2, 8383, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118601, "自由に外へ行けるって\nすっごく幸せなことよ\nさ、散歩行きましょ♪", 2, 2, 8384, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118601, "安心していいわ\n寝てるあんたに\n悪戯しないから\n…あんたにはね♪", 3, 2, 8385, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118601, "悪くはないけど…\nあいつの頭の方が\n座り心地いいかも", 0, 3, 8387, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118601, "この子を使った\n悪戯は…\n…止めとこっと\nこんなにかわいいし♪", 0, 3, 8388, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118601, "このギルドハウスに\nピッタリな匂いね♪\n一言で言えば…\nカオスって感じ？", 0, 3, 8389, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118601, "なんていうか…\n心があたたかいわ\n…変な感じ！", 0, 3, 8390, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118601, "ガイド妖精にも\n食事の機能があって\nよかったのかも\nしれないわね…もぐ…", 0, 3, 8392, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 118601, "じれったい物語ね…\nこんなの霧を使えば\n一瞬で終わるのに！", 0, 3, 8393, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131801, "わる～い子は\nトゥンヌス漁船に\n乗ってもらおうかな～", 0, 1, 8395, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131801, "たまにはファミリーの\nみんなにもお休みを\nあげないとね～☆", 0, 1, 8396, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131801, "海辺に素敵な\nバーがあるんだ～\n彼が大人になったら\n一緒に行きたいな♪", 0, 1, 8397, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131801, "夏の風を肌で\n感じるの好きかも～\nラーゴはどう？", 0, 1, 8398, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131801, "海岸線を\nのんびり散歩もいいね\n砂や貝殻に紛れた\nお宝探しの旅だよ～☆", 0, 1, 8399, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 131801, "みんなで集まって\nビーチパーティーでも\n開いちゃおっかな～♪", 0, 1, 8400, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 131801, "さて、この水着…\n彼は気に入って\nくれてるかな～？", 0, 1, 8401, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131801, "心ゆくまで夏を\n楽しんじゃお～☆\nほら君も♪", 0, 2, 8402, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131801, "水着似合ってるって？\nふふ♪　うれしいこと\n言ってくれるね～☆", 0, 2, 8403, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131801, "熱中症には\n気を付けないと\nだめだよ～？", 0, 2, 8404, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131801, "海の家ならうちの\nファミリーでやってる\nとこにしよっか☆\n豪遊しちゃお～♪", 0, 2, 8405, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 131801, "君と一緒なら\nどんな夏になるのか\n楽しみだね～♪", 0, 2, 8406, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 131801, "君といると子供心を\nくすぐられちゃうな～\n大人だって思いっきり\n遊んでいいんだよね♪", 0, 2, 8407, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131801, "さ、今日は何をして\n過ごそっかな～…\nねえ、君は何したい？", 1, 2, 8408, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131801, "ふふ♪\n波音が心地良い\n時間だね～☆", 2, 2, 8409, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131801, "今夜は星空の下で\n乾杯しよっか～☆\n飲み物取ってくるね♪", 3, 2, 8410, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131801, "ここ涼しい～♪\nちょっと一休み\nしていこっか☆", 0, 3, 8412, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131801, "こういうのも良いけど\nやっぱりラーゴの方が\nかわいいかな～♪", 0, 3, 8413, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131801, "爽やかな香りだね～\nこういう感覚も絵で\n表現してみたいな～", 0, 3, 8414, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131801, "続きを描く前に\n原稿を乾かさないとね\nインクがにじむと\n直すの大変なんだよ～", 0, 3, 8415, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131801, "今日は水着だし…\n食べるのは控えめに\nしておこっかな", 0, 3, 8417, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131801, "話題の作品は\nチェックしないとね～\nあっ、この作家さん\n新刊出すんだ～", 0, 3, 8418, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131801, "うちの出店の客引きに\nこういう乗り物系を\n使うのもアリかな～☆", 0, 3, 8420, 10, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131801, "これ、背景用の\n参考資料に\n一つ欲しいかも♪", 0, 3, 8421, 11, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 181201, "ここに帰るのも\n久しぶり…あれから\n変わったところは\nあるかしら？", 0, 1, 8422, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 181201, "バイトも復帰しなきゃ\nうちのギルドにとって\n食費は重要だもの…！", 0, 1, 8423, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 181201, "コッコロマ…っ\n…うぅ…\nこの癖は\n直らないのね…", 0, 1, 8424, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 181201, "この世界はまだまだ\n知らないことだらけね\n『裏世界』とか…", 0, 1, 8425, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 181201, "必ずみんなを\n助け出してみせるわ\n…もちろん、兄さんも", 0, 1, 8426, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 181201, "またみんなと\n暮らせるなんて…\n夢じゃ、ないのよね", 0, 1, 8427, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 181201, "たくさんの人が\n私に優しくしてくれる\n…だからこそ、私も\n頼れる人になりたいわ", 0, 1, 8428, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 181201, "次はランドソルの\n街の外にも行きたいわ\nあの…よかったらまた\n一緒に回ってくれる？", 0, 2, 8429, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 181201, "独りぼっちで眠るのは\nやっぱり寂しかったわ\n…みんなで雑魚寝？\nそんなの…絶対やるわ", 0, 2, 8430, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 181201, "ネビアってば大泣きで\n私を出迎えてくれたの\n…すごく嬉しかったわ", 0, 2, 8431, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 181201, "この翼、陽に当たると\n本当に綺麗よね…\n自分のだけれど、つい\nずっと見ちゃうもの", 0, 2, 8432, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 181201, "おにーたんっ♪\n…呼びたくなっただけ\n本当にそれだけよっ？", 0, 2, 8433, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 181201, "どうしたの？\n…ふふっ、安心して\nこれからもずっと\n私はここにいるわ", 0, 2, 8434, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 181201, "みんな起きだす時間ね\n街が活気づいてく感じ\n嫌いじゃないわ", 1, 2, 8435, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 181201, "【美食殿】のお昼は\nいつも美味しいから\nまた太っちゃいそうね", 2, 2, 8436, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 181201, "おやすみなさい\n明日も、その先も\n一緒に冒険しましょう", 3, 2, 8437, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 181201, "休憩も大事…よね\nじゃあ遠慮なく…\nふぅ～…♪", 0, 3, 8439, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 181201, "はぁ…ふわふわ…♪\nこのかわいさには\nもう抗えないわ…", 0, 3, 8440, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 181201, "落ち着く香り…\nこの匂いを嗅いでると\n帰ってきたなって\n思うわね", 0, 3, 8441, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 181201, "みんなで野宿した時も\n焚火の明かりを見て\nすごく安心したのよね", 0, 3, 8442, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 181201, "ペコリーヌさんには\n負けるけど…\n美味しくって手が\n止まらないわ…！", 0, 3, 8444, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 181201, "プリンセスフォーム…\n一体どういう仕組みで\n発動するのか、文献に\n書いてないかしら…", 0, 3, 8445, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131901, "すごーく暑いけど、\nこの暑さをどうやって\n乗り越えるかって\n考えるのも楽しいかも", 0, 1, 8447, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131901, "氷で彫刻を作るの？\nそれは楽しそうだけど\nすぐに溶けちゃわ\nないかしら？", 0, 1, 8448, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131901, "お日様の下で遊ぶのも\n部屋の中で涼むのも\nどっちも夏ならではの\n楽しみ方なのね", 0, 1, 8449, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131901, "生き物の観察日記、\n私も書いてみたいな\nパックのお昼寝日記\nなんてどうかしら？", 0, 1, 8450, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131901, "さまぁばけぇしょんは\n計画を立てるもの\nなんだって\n私も挑戦してみるわね", 0, 1, 8451, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 131901, "この世界のことを\nもっと知って\nアーラム村のみんなに\nお話ししてあげるの", 0, 1, 8452, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 131901, "いつルグニカに帰るか\nわからないからこそ\n毎日頑張らなきゃね", 0, 1, 8453, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131901, "今日はどこに行くの？\n私は、えーと…\nかき氷が食べたいわ！", 0, 2, 8454, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131901, "ここにも精霊が\n住んでたりするの？\n…精霊じゃなくて\nがいど妖精？", 0, 2, 8455, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131901, "パックったら、\n近所の子にいっぱい\nなでなでされてたわ\nすごーく人気者ね！", 0, 2, 8456, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131901, "実は…線香花火の\n特訓をしてたの！\n次は負けないんだから", 0, 2, 8457, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 131901, "昨日、みんなの\n似顔絵も絵日記に\n描いてみたんだけど…\n笑っちゃダメよ？", 0, 2, 8458, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 131901, "何気ない時間だって\n後で思い返せば\nずっと忘れない\n素敵な思い出になるの", 0, 2, 8459, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131901, "気持ちよく起きて、\nいい一日を過ごしたい\nそんなときには\nラジーオタイソーね！", 1, 2, 8460, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131901, "お昼ごはんの時間ね\n暑さに負けないように\nしっかり食べましょう", 2, 2, 8461, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131901, "外は涼しいみたいだし\n夜の散歩に行ってみる\nのはどうかしら？", 3, 2, 8462, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131901, "遊び疲れたら\nここでひとやすみ…\nふわぁ…\n眠くなっちゃった…", 0, 3, 8464, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131901, "細かいところまで\nすごーく丁寧に\n作られてる…", 0, 3, 8465, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131901, "わっ、なんだか\nすーっとする香りね\nちょっぴり涼しく\nなったみたい", 0, 3, 8466, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131901, "海で遊ぶと\n体が冷えるから\nあったかいのも\nすごーく嬉しい…", 0, 3, 8467, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131901, "これも美味しい…！\nペコリーヌが\nどんどん食べちゃう\n気持ちが分かるかも", 0, 3, 8469, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131901, "このお話も面白い…！\nあっという間に時間が\n過ぎちゃったわ…\n…ね、寝てません！", 0, 3, 8470, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131901, "この子と一緒に海に\nいったら楽しそうね！\nどこに行けば\n会えるのかしら？", 0, 3, 8472, 10, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 131901, "ふかふかね！\nこうしてると波の音が\n聞こえてきそう…", 0, 3, 8473, 11, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132101, "こちらでは水着姿で\n過ごすのも\n珍しくないようですね", 0, 1, 8474, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132101, "メイド服ではなく\n姉様と色違いの水着…\nレムは嬉しいです", 0, 1, 8475, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132101, "少し室温が高いかも\nしれませんね…\nいくつか氷を配置して\nおきましょうか", 0, 1, 8476, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132101, "清涼感があるとはいえ\n鉄球をビーチバレーの\nボールに代用するのは\n危ないでしょうか…", 0, 1, 8477, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132101, "ふぅー…ふぅー…\nようやく浮輪が\n膨らみました\n姉様にお渡ししないと", 0, 1, 8478, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 132101, "海のことをもっと\n教えていただけますか\n…スバルくんと海の\n話をしたいんです", 0, 1, 8479, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 132101, "もし海に行く機会が\nあればレムにも…\nお声がけくださいね\nぜひ、お願いします", 0, 1, 8480, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132101, "何を着ていても\nメイドはメイド\n掃除も料理も\nなんでもござれです", 0, 2, 8481, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132101, "スバルくんとあなたは\n兄弟のように\n仲がいいですね\n姉様とレムと一緒です", 0, 2, 8482, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132101, "この姿で子どもたちと\n遊ぶのは…さすがに\nはばかられますね\nやはり着替えてきます", 0, 2, 8483, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132101, "ナンパ…というものが\n海にはあるそうです\nスバルくんがとても\n警戒していました", 0, 2, 8484, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 132101, "魚釣りでしたら\nレムもご一緒します\n氷漬けの魚の備蓄を\n補充したかったので", 0, 2, 8485, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 132101, "子どもたちと砂遊びの\n約束をしているんです\nあなたもご一緒に\nいかがですか？", 0, 2, 8486, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132101, "ラジーオタイソーは\nもうされましたか？\n体がほぐれますよ", 1, 2, 8487, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132101, "お昼ですね。では\n姉様に焼きそばを…\nもちろん皆様の分も\n用意してありますよ", 2, 2, 8488, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132101, "熱帯夜であっても\nお休みの際はお腹を\n冷やさないように\n気をつけてくださいね", 3, 2, 8489, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132101, "とても気持ちよさそう\nです…！\nさっそく姉様に\nお教えしないと", 0, 3, 8491, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132101, "季節に合わせた\n着せ替えができれば\n子どもたちも\n楽しめそうです", 0, 3, 8492, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132101, "ふむふむ…この香りは\n疲れを癒やしてくれ\nますね。エミリア様に\nお持ちします", 0, 3, 8493, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132101, "身も心もほぐれます\n思っていたよりも\n体が冷えていたみたい\nですね…ふう…", 0, 3, 8494, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132101, "もぐもぐ…んうっ！？\nこのすっぱさは…\n梅干し、ですか…\nくせになりますね…！", 0, 3, 8496, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132101, "海に関する詩文なども\nあるのでしょうか…\n気になります", 0, 3, 8497, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132101, "地竜とは違った\n乗り心地です…\n本物にも乗ってみたい\nものですね", 0, 3, 8499, 10, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132101, "この大きさで\n本物の貝であれば…\nどんな料理にするか\n想像が膨らみます", 0, 3, 8500, 11, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132001, "これがこの世界の\n居住環境か…\n面白い構造だね", 0, 1, 8501, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132001, "暇を持て余していてね\n誰か話し相手に\nなってくれないかな", 0, 1, 8502, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132001, "目新しいものが\nたくさんあるね\n胸が躍るよ", 0, 1, 8503, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132001, "頑丈そうな建物だ\nここなら彼女たちが\n暴れても大丈夫かな？", 0, 1, 8504, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132001, "力が制限されていて\n良かった面もある\n君も吐かずにすんだし", 0, 1, 8505, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 132001, "悪さなんてするわけ\nないじゃないか\n傷つくなぁ", 0, 1, 8506, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 132001, "普段と違う格好で\n歩き回るのも\n悪くない経験だ", 0, 1, 8507, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132001, "ボクに聞きたいことが\nあるなら遠慮なく\n質問するといい", 0, 2, 8508, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132001, "気軽に魔女に\n話しかけるなんて\n君は物好きだね", 0, 2, 8509, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132001, "この世界においては\nボクは異物だからね\n大人しくしているさ", 0, 2, 8510, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132001, "ボクの反応を\n見たいのかい？\n君の好奇心には\n恐れ入るよ", 0, 2, 8511, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 132001, "ボクの手を借りたい？\nもちろん構わないさ\n対価はいただくけどね", 0, 2, 8512, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 132001, "ボクと海を見たい？\nふふ、魅力的な\nお誘いだね", 0, 2, 8513, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132001, "お目覚めかな？\nさて、君の見た夢を\n教えてくれないかな？", 1, 2, 8514, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132001, "食後のお茶を\n一緒にどうだろうか？\nさあ、席に座ってよ", 2, 2, 8515, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132001, "おや、お休みかな？\n魔女の前で無防備だね\n…無論、何もしないさ", 3, 2, 8516, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132001, "こういう椅子は\n茶会には向かないね\nつい気が緩んでしまう", 0, 3, 8518, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132001, "こういうものを好きな\n女の子がいいのかな？\n君の嗜好を知りたいな", 0, 3, 8519, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132001, "いい香りだ\nこれにはどんな\n効能があるんだろう？", 0, 3, 8520, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132001, "火の点け方に\nボクのいた世界との\n違いはないのかな？", 0, 3, 8521, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132001, "これもまた未知の味だ\n存分に味わわせて\nもらうとしよう", 0, 3, 8523, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132001, "素晴らしいね\nここには叡智が\n詰まっている", 0, 3, 8524, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132001, "魔物を模倣した\nぬいぐるみか…\n実物を見たくなるね", 0, 3, 8526, 10, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132001, "いい気持ちだ…ボクと\nしたことが、何も考え\nられなくなりそうだ", 0, 3, 8527, 11, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 109701, "願わずにいられません\nどうかこちらの\n世界には…悲劇が\n生まれませんようにと", 0, 1, 8528, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 109701, "食事のご希望があれば\nなんなりと。お腹が\nいっぱいになるまで\n沢山ご用意しますね", 0, 2, 8529, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 109801, "あの男、これからも\n観察が必要ね\nラムの目の届く範囲に\n置く必要があるわ", 0, 1, 8530, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 109801, "素直なところは\nあの男の美点ね\n癪だけど、偶然にも\nラムと同じだわ", 0, 2, 8531, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 109901, "迷惑かけちゃったけど\n雪遊びは楽しかったわ\nまたやりたい…なんて\n言ったら怒られるかも", 0, 1, 8532, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 109901, "数えきれないくらいの\n思い出を、みんなで\n作りましょう！", 0, 2, 8533, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132201, "わわっ、今すごく\nいい香りがしたけど…\n誰の香水かな\n教えてほしいな…♪", 0, 1, 8534, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132201, "錬金術用の釜、\n置くならどこかな？\n自分専用の錬金釜、\n憧れちゃうよね♪", 0, 1, 8535, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132201, "この匂い…誰か\n唐揚げ食べたみたい\nうぅ…お腹空いて\nきちゃうよ", 0, 1, 8536, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132201, "次の新曲のタイトルは\n『恋の錬金術』！\nうーん、ちょっと\nベタ過ぎるかな…", 0, 1, 8537, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132201, "あ、錬金の素材\n切らしてたんだ…\n彼を誘って採集に\n行ってこようかな", 0, 1, 8538, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 132201, "瞬時に着替えができる\n薬とかできないかな？\n舞台で衣装チェンジ！\nなーんて、楽しそう♪", 0, 1, 8539, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 132201, "彼が遊びに来た後は\n部屋の空気がほんのり\n彼の匂いなんだ…\nなんだか幸せだな♪", 0, 1, 8540, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132201, "今日もバイト？\nそれならこれ飲んで\n私のお手製ポーション\nがんばってきてね！", 0, 2, 8541, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132201, "新しくできたカレー屋\n行ってみない？\n時々スパイスの香りが\n流れてくるんだよ", 0, 2, 8542, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132201, "私のフィギュア\nそっくりでしょ！\nルイちゃんが作って\nくれたんだ", 0, 2, 8543, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132201, "あれ、シャンプー\n変えた？　私と同じの\nだよ！　どうしよう\nドキドキしちゃう…", 0, 2, 8544, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 132201, "いらっしゃいませ♪\n何を作りますか？\nなーんてね。錬金堂で\nまた一日店長したいな", 0, 2, 8545, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 132201, "クンクン…彼の\nハンカチ…いい匂い♪\n私だけの秘密のお守り\nにしてるんだ", 0, 2, 8546, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132201, "ねえねえ、天気も\nいいし一緒に錬金の\n素材集めに行こうよ！", 1, 2, 8547, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132201, "お昼ご飯、用意したよ\nあはは、さすがに\nランチは錬成しないよ\n私の手作りだよ♪", 2, 2, 8548, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132201, "今日も一日お疲れさま\nこれ飲んでゆっくり\n休んで。薬じゃないよ\nホットミルクだよ？", 3, 2, 8549, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132201, "昨日、遅くまで\n錬金術の勉強してた\nから眠くって…\nちょっと一休みっと", 0, 3, 8551, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132201, "この子にちょっとだけ\n香水かけて、と…\nんー！　だっこした時\nすっごくいい匂い♪", 0, 3, 8552, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132201, "花みたいな、\nお菓子みたいな\nいい香り。気持ちが\n穏やかになるね♪", 0, 3, 8553, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132201, "外でもこの暖かさを\n感じられる道具、\n作れたりしないかな？", 0, 3, 8554, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132201, "素材集めに行くと\nお腹が空くんだよね\nイロハちゃんも\nこの味好きそう", 0, 3, 8556, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132201, "錬金術にはたくさんの\n知識が必要だからね\nさあ、今日も調合書を\n読まなくちゃ", 0, 3, 8557, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127701, "ここが騎士さんたちの\n住んでる場所…！\n粗相がないように\n気をつけなきゃ", 0, 1, 8559, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127701, "ジオ・テオゴニアの\nみんな、私がいない間\nちゃんとお仕事\nしてればいいけど…", 0, 1, 8560, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127701, "コッコロさんも\n従者として長いって\n聞いたような…\nあの年ですごい…！", 0, 1, 8561, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127701, "自分がもてなされる\n側になるのって\nやっぱりちょっと\n落ち着かないかも…", 0, 1, 8562, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127701, "今度のお休みの日は\n何しよう…？\nあっ、最近できたお店\nヴルムと行こうかな？", 0, 1, 8563, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 127701, "お買い物したら\nかわいいからって\nたくさんおまけして\nもらっちゃった…♪", 0, 1, 8564, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 127701, "ここにいると、つい\nぼんやりしちゃう…\nこれも騎士さんの力…\nなのかな", 0, 1, 8565, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127701, "お手伝いできることは\nありませんか？\nお世話でもなんでも\n任せてくださいっ", 0, 2, 8566, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127701, "ランドソルに初めて\n来たときは驚きました\n砂糖の山と思ったら…\nあんなに寒いなんて！", 0, 2, 8567, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127701, "あの、ヴルムを\n見ませんでしたか？\nもう…あの子ってば\nまた仕事をサボって…", 0, 2, 8568, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127701, "家具や家が壊れても\n食べられないなんて\nなんだかもったいない\nですね", 0, 2, 8569, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 127701, "とっておきの紅茶を\n手に入れたんです\n一杯いかがですか？\nその、日頃のお礼です", 0, 2, 8570, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 127701, "頑張ってて偉いって…\nほ、褒めてもなんにも\n出ませんからね！？\n…えへへ♪", 0, 2, 8571, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127701, "おはようございます！\nモーニングティーは\nいかがですか？\n私、準備しますねっ", 1, 2, 8572, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127701, "ライラエルさまも\nお昼寝はお好きです\nけど…だからって\n寝すぎはダメですよ！", 2, 2, 8573, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127701, "おやすみなさい\nくれぐれも寝不足には\n気をつけてくださいね", 3, 2, 8574, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127701, "しっかりしないと…\nメイドの私が\n気を抜くなんて…\nそんな…ふぅ…", 0, 3, 8576, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127701, "わぁ、かわいい…♪\nってダメダメ！\nメイドなんだから\n落ち着かないと…！", 0, 3, 8577, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127701, "いい匂い…♪\nこの香り、\nライラエルさまも\n好きそうです！", 0, 3, 8578, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127701, "ランドソルの環境に\n触れると、この光の\nありがたみが\n身に沁みますね…", 0, 3, 8579, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127701, "甘くない食べ物が\n一般的なのも\n慣れてきました\nあむ…おいひぃ…♪", 0, 3, 8581, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127701, "ランドソルの文字、\n私たちのところと\nちょっと違いますね…\n覚えられるかなぁ", 0, 3, 8582, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127801, "甘い匂いがしない…\nジオ・テオゴニアとは\n別の世界なんですね", 0, 1, 8584, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127801, "掃除めんど…\nん…このくらいで\nまー十分でしょ", 0, 1, 8585, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127801, "そろそろ食事の\n用意もしなきゃ…\nあーやる事多い…", 0, 1, 8586, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127801, "秘密の隠れ場所、\nまた姉さんにバレた…\n次のを探さないと", 0, 1, 8587, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127801, "知らない間に仕事が\n終わったり…しないか\nしてくれたらいいのに", 0, 1, 8588, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 127801, "きちんともてなすには\n騎士さんの好みも\n覚えてかないと…", 0, 1, 8589, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 127801, "ご主人サマに\nご奉仕します～♪\n…キャラじゃないか\nやめやめ、恥ずかし…", 0, 1, 8590, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127801, "居心地いいっすね…\n今日からここに\n住んでもいいですか", 0, 2, 8591, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127801, "あの、ちょっと姉さん\n怒らせちゃったんで\n匿ってくれません？", 0, 2, 8592, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127801, "羽と尻尾が格好いい？\n意外と邪魔ですよ\n仰向けで寝にくいし", 0, 2, 8593, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127801, "ワークライフバランス\nっていい言葉ですね\nぜひライフ多めで", 0, 2, 8594, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 127801, "騎士さんって\n働き者ですね\nそのままあたしの\n仕事も…いえなんでも", 0, 2, 8595, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 127801, "熱心に見てても\n尻尾は触らせて\nあげませんってば\n角？　角なら…まぁ", 0, 2, 8596, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127801, "寝ても寝ても\n寝足りない…ふぁ…\nあー…眠たい…", 1, 2, 8597, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127801, "アフタヌーンティーの\n準備は得意ですよ\nつまみ食いとか\n…つまみ食いとか", 2, 2, 8598, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127801, "よく休んでください\n布団は干しといたんで\n…姉さんが", 3, 2, 8599, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127801, "ふー…\nこのままずーっと\nここに居たい…", 0, 3, 8601, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127801, "あんたも一緒に\nサボっちゃうか～\n…なんて", 0, 3, 8602, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127801, "落ち着く匂い…\n騎士さんの好み\nなんですかね", 0, 3, 8603, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127801, "こういうとこの\n掃除って面倒だし…\n今度でいっか", 0, 3, 8604, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127801, "ご飯の前に\nこんな食べたら\n姉さんきっと怒るな…", 0, 3, 8606, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 127801, "食に関する本が\nやけに多い…\nお国柄ですかね", 0, 3, 8607, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130201, "すんすん…\nお兄さんの匂いが\nぷんぷんする～♡\nザコくっさぁ♡", 0, 1, 8609, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130201, "ぴこーん！\nいひひっ、新作メカの\n面白いアイデア、\n思いついちった～♡", 0, 1, 8610, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130201, "メカもいじりたいし\n服もアクセもほしー…\nあ～、おこづかい\n足んないよ～！", 0, 1, 8611, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130201, "お勉強でもしよっかな\nザコお兄さんには\n絶対わかんない\nよーなやつ♡", 0, 1, 8612, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130201, "お兄さんの部屋って\nどこどこぉ？\nメカの実験室に\n使えそうだよね～♡", 0, 1, 8613, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 130201, "次のデートコースは\nどんなのかなー？\n…いや、むしろ次は\nぼくが決めちゃうっ？", 0, 1, 8614, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 130201, "強奪計画は念入りに♡\nお兄さんのハートも\n全力でザコザコにする\n計画、立てないとね♡", 0, 1, 8615, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130201, "ねー、ヒマヒマヒマ～\nかまってかまってー♡\nどっか連れてけー♡", 0, 2, 8616, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130201, "今日はこっちで\nご飯食べたいなー♡\n…帰っても５日連続の\nバカナ姉カレーだし…", 0, 2, 8617, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130201, "依頼行くならぼくも\n連れてってよー♡\nお兄さんのザコっぷり\n見ててあげる～♡", 0, 2, 8618, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130201, "お兄さんはぼくより\nオトナだもんね～♡\nじゃあオモテナシ、\n期待してるから～♡", 0, 2, 8619, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 130201, "ヤマ姉になんかお土産\n持って帰ろうかなー？\nザコお兄さん、選ぶの\n付きあって～♡", 0, 2, 8620, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 130201, "遊びにいくときは\nザコお兄さんも\n誘ってあげるよ♡\nどうせヒマでしょ～？", 0, 2, 8621, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130201, "ふぁ～、ねむぅ～…\n夜更かしぶん寝るから\nあとで起こして～♡\nあ、変なこと禁止～♡", 1, 2, 8622, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130201, "おなかすいたぁ～\nなんか食べ行こ♡\n甘いのおごって～♡", 2, 2, 8623, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130201, "えー、もう寝る時間？\nじゃあ…添い寝して\nあげよっか？\nなーんて、ウッソー♡", 3, 2, 8624, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130201, "おお～ふわっふわ～♪\nバンディシャーク号の\n座席に使いたーい！", 0, 3, 8626, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130201, "カワイイ～♡\n改造したらもっと\nカワイくなるかな～？", 0, 3, 8627, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130201, "くんくん…いい匂い♡\nザコお兄さんって\nこーゆー香りが\n好きなのかなぁ～", 0, 3, 8628, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130201, "はー、あったかーい♡\nバンディシャーク号\nにも欲しいかもー♪", 0, 3, 8629, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130201, "食べれるときに\n食べるのが大事って\nヤマ姉言ってたしね～", 0, 3, 8631, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130201, "あ、この本読んだよ♡\n参考文献リストが\nしっかりしてて\n勉強になったなー♪", 0, 3, 8632, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130001, "部屋は多少ゴチャつい\nてる方が落ち着くのさ\nバンディシャーク号\nみたいにね♪", 0, 1, 8634, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130001, "呆けてるのもいいが\nちいと物足りないねぇ\n麦しゅわもブドウ朱も\n無いなんてさ", 0, 1, 8635, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130001, "この家セキュリティが\nなってないねぇ…\n盗賊目線でちょっくら\n指南でもしてやるか", 0, 1, 8636, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130001, "不思議だねぇ\nここで飲むと、酔いが\n回るのが早い気が\nするよ…ヒック", 0, 1, 8637, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130001, "ふむ…\nクルマが壊れた時は\nアジト代わりに\n使えそうだ♪", 0, 1, 8638, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 130001, "カカッ、いい部屋だ！\n妹を迎える準備は\nできてるみたいだねぇ", 0, 1, 8639, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 130001, "…っと、油断したら\n落ち着いちまうね\nボーヤの甘っちょろい\n雰囲気のせいだよ", 0, 1, 8640, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130001, "さすがにこの家から\n盗みはしないさ\nいずれは妹たちの\n共有財産になるからね", 0, 2, 8641, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130001, "ボーヤ、暇なら\n博打でもやるかい？\nアタシが負けたら\n掃除でもしてやるよ", 0, 2, 8642, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130001, "立派な家じゃないか！\n一国一城の主とは\nちょっとボーヤのこと\n見くびってたかもねぇ", 0, 2, 8643, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130001, "かわいい義弟のためだ\nアタシが盗ってきた\nお宝をくれてやるよ\n好きな場所に飾りな！", 0, 2, 8644, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 130001, "妹と結ばれる前に\n他の女との関係は\n清算しとくように\nわかったね、ボーヤ？", 0, 2, 8645, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 130001, "お、ヤシオーリかい？\nフフン、義弟からの\nお酌とあっちゃあ\n断れないねぇ♪", 0, 2, 8646, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130001, "うう…清々しい朝だが\n頭がいてぇ…\nちっと昨日は\n飲みすぎたか…？", 1, 2, 8647, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130001, "ほらこれ、ワカナに\n作らせた弁当だ\nこれで昼からも\nバリバリやりな！", 2, 2, 8648, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130001, "まだまだ夜はこれから\n…と言いたいとこだが\n早寝して立派に育ちな\nそれが妹たちの為だよ", 3, 2, 8649, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130001, "中々いい座り心地だが\n盗賊が腑抜ける訳には\nいかないね…が…\nもう少しだけ座るか…", 0, 3, 8651, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130001, "こういうのの中に\n実はレアなブツが\n隠されてるとか\nありがちだねぇ", 0, 3, 8652, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130001, "ふうん、ボーヤは\nこういう匂いが\n好きなのかい？\n妹たちに教えてやろう", 0, 3, 8653, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130001, "体を温めんにゃ\n酵露をカッとやるのが\n一番だが、これも存外\n悪くないねぇ…♪", 0, 3, 8654, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130001, "ガツガツ…\nん、悪くねェ味だ\nこれにヤシオーリでも\nありゃ文句はないね", 0, 3, 8656, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130001, "本にも載ってない\nレアもんのお宝か…\nフッ、想像しただけで\n血沸き肉躍るねぇ…！", 0, 3, 8657, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130101, "今日の買い物は\nいつものお野菜と…\nあっ、確か特売が\nあったはず…！", 0, 1, 8659, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130101, "お掃除、お掃除…♪\nきゅっきゅっ…\nよいしょ、っと…", 0, 1, 8660, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130101, "洗濯ものも\nたまらないうちに\nやっちゃわないと…", 0, 1, 8661, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130101, "夕飯のメニュー…\n何にしよう？\n期限の早い食材は…", 0, 1, 8662, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130101, "色々お菓子も\n作ってみたいけど…\n予算…足りるかなぁ…", 0, 1, 8663, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 130101, "騎士さん…どんな\n料理が好きなんだろ？\n前に作ったコロッケは\n評判よかったけど…", 0, 1, 8664, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 130101, "騎士さんと\nおしゃべりすると、\n胸があたたかくなる…\nなんでだろ…？", 0, 1, 8665, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130101, "あっ、騎士さん\nどうかしましたか？\n御用ならいつでも…♪", 0, 2, 8666, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130101, "削っても削っても\n家計簿は赤字…\n騎士さ～ん…\n助けてください～！", 0, 2, 8667, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130101, "手が空いてたら\n日用品の買い出し、\n手伝ってもらっても\nいいですか…？", 0, 2, 8668, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130101, "やっと一息つけそう…\nあっ、わたし\nお茶を淹れてきますね", 0, 2, 8669, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 130101, "困りごとがあったら\n言ってくださいね\nわたし…騎士さんの\n力になりたいです", 0, 2, 8670, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 130101, "助けてもらった分は\n必ず返しますから…\nそれまでわたしのこと\n見ててくださいね…♪", 0, 2, 8671, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130101, "忘れ物はないですか？\n今日のお仕事も\n頑張りましょうね…！", 1, 2, 8672, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130101, "お昼ご飯できてますよ\n大したものじゃなくて\n恐縮ですけど…", 2, 2, 8673, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130101, "夜更かしはだめですよ\nわたしも家事を\n終わらせたら寝ないと", 3, 2, 8674, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130101, "これ…割れ物防止の\nクッションに\n使えるかも…？", 0, 3, 8676, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130101, "こういうのいいなぁ…\nけど…フブキちゃんに\n絶対改造されちゃう…", 0, 3, 8677, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130101, "いい匂い…でも\n切れかけてるかも？\nえっと補充するには…", 0, 3, 8678, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130101, "あったかい…\nちょっとだけなら\n休んでも…いいよね", 0, 3, 8679, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130101, "これがあったら\nもうお料理を\n作らなくて済むかも？", 0, 3, 8681, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 130101, "新しい節約レシピは…\nあ、この草って\n食べられるんだ…", 0, 3, 8682, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132801, "愛溢れるランドソルの\nクリスマス…ですか\nいけませんね、つい\n胸が躍ってしまいます", 0, 1, 8684, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132801, "ツリーの飾りと\nわたしの髪…\nくるくるしていて\n似ているかも…くすっ", 0, 1, 8685, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132801, "この姿で傲慢な\nわたしになったら…\nいつもより怖がらせて\nしまうでしょうね…", 0, 1, 8686, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132801, "悪魔として、人を\nドキドキさせなくては\nい、イオから教わった\n…投げキスの練習を…", 0, 1, 8687, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132801, "ジオ・テオゴニアの\nお菓子は、ツリーの\n飾りに向いていそう\nですね…ふふっ", 0, 1, 8688, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 132801, "騎士のギルドハウス…\nクリスマスの時期も\n暖かいのですね…", 0, 1, 8689, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 132801, "城の皆と過ごす宴も\n楽しいですが…\n騎士と二人の宴も\n幸せです…", 0, 1, 8690, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132801, "騎士、あなたの\nもてなしに感謝します\nクリスマスの何たるか\n教えてくださいね", 0, 2, 8691, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132801, "この黒い羽は\nどう手入れすれば？\nイオなら知っている\nでしょうか…", 0, 2, 8692, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132801, "城の皆も連れてきたい\nほど、クリスマスは\n楽しい行事ですね\nお土産も迷います", 0, 2, 8693, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132801, "クリスマスの晩餐に\n招待されたのですが…\nその…辛いものは\nありませんよね…？", 0, 2, 8694, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 132801, "クリスマスと言わず…\nいつでも星に祈って\nいますよ？　騎士達に\n幸せがあるように、と", 0, 2, 8695, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 132801, "悪魔として騎士を\nドキドキさせる…？\nそ、そのようなこと\nわたしには…うぅ…", 0, 2, 8696, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132801, "朝は寒いですね…\n雪…積もっている\nでしょうか？", 1, 2, 8697, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132801, "ふあぁ…いけません\n公務の滞在先で\nお昼寝など…", 2, 2, 8698, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132801, "夜は悪魔として\n夢を見せましょう\n騎士はどんな夢が\n見たいですか？", 3, 2, 8699, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132801, "ふぅ…落ち着きます…\n家でゆっくりと過ごす\nクリスマスも\n良いものですね", 0, 3, 8701, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132801, "リンドのプレゼントに\nぬいぐるみは…？\n…子ども扱いすると\n落ち込むでしょうか…", 0, 3, 8702, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132801, "暖かいお部屋に\nこの香りを漂わせたら\nふふっ…素敵な時間を\n過ごせそうです", 0, 3, 8703, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132801, "これがあれば、\n寒い冬も安心ですね\n暖かくて…このまま\n眠ってしまいそうです", 0, 3, 8704, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132801, "悪魔の力を使ったら\nお腹が空いてきて\nしまって…\n恥ずかしいです…", 0, 3, 8706, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132801, "クリスマスには多くの\n伝承があるのですね…\n天使の伝承まで\nありましたし…", 0, 3, 8707, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132803, "まぁ…！\nよくできていますね\nサプライズプレゼント\n…でしょうか。ふふ♪", 0, 3, 8709, 14, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132804, "ど、どうでしょう…？\n親善交流の催しに\nなりそうでしょうか…", 0, 3, 8710, 15, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132901, "ギルドハウスって\nあったかぁ～い♪\n心がぽかぽかするの！", 0, 1, 8711, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132901, "クリスマスの悪魔の\nお仕事はないわね！\nだってここのみんな\nいい子なんですもの♪", 0, 1, 8712, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132901, "みんなちゃんと\n寝られてるかしら？\n夜更かしせずに、\nいい夢見てね♪", 0, 1, 8713, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132901, "毎回、来年が\n楽しみになるような\n素敵なクリスマスを\n過ごしたいわね♪", 0, 1, 8714, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132901, "訳ありケーキを見ると\n刑務作業を思い出すわ\n…あれも貴重な体験\nだったのかしら…？", 0, 1, 8715, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 132901, "ロマンチックな\nクリスマスに備えて\n今のうちに少女漫画で\n予習しておかないと！", 0, 1, 8716, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 132901, "ほっぺにチューなら\nもう一回しても…\nや、やっぱり無理！\n恥ずかしいわ～！", 0, 1, 8717, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132901, "またみんなで\nセッションしたいわね\nカラーベルの練習した\n成果を見せたいの♪", 0, 2, 8718, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132901, "君はサンタさんに\nどんなプレゼントを\n頼んでいたのかしら？\n気になるわ～！", 0, 2, 8719, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132901, "愛が壊れても、\n何度でも治してあげる\n私たちの間に、\n愛の尽きる日はないわ", 0, 2, 8720, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132901, "冬休みって短いようで\n長く感じちゃうわよね\n学校のみんなと\n早く会いたいな～♪", 0, 2, 8721, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 132901, "愛をテーマにした\n冬休みの作文の宿題、\n君の分を読むのが\n今から楽しみね♪", 0, 2, 8722, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 132901, "よかったらまた\n私をハニーって呼んで\nほしいの。ダーリン♪", 0, 2, 8723, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132901, "おはよう！\n冬の朝は冷えるから\n風邪を引かないように\n対策しましょうね", 1, 2, 8724, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132901, "お昼ご飯は一緒に\n食べに行きましょう！\nクリスマス限定の\nランチコースよ♪", 2, 2, 8725, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132901, "ねえねえ、キャンドル\nイルミネーションを\n見に行きましょうよ！\nロマンチックよ～♪", 3, 2, 8726, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132901, "暖かいクリスマスを\n過ごすのに必須よね♪\nふふ、柔らかぁい！", 0, 3, 8728, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132901, "どの子もかわいい～♪\nクリスマスツリーの\n下で、一緒にお祝い\nしてもらいたいな♪", 0, 3, 8729, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132901, "なんだか胸の奥が\n温かくなる匂いね～\nリラックスしちゃう♪", 0, 3, 8730, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132901, "あったかぁい♪\nあ、でも夜になったら\nサンタさんが入れる\nように、火を消すのよ", 0, 3, 8731, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132901, "寒い冬だからこそ\nしっかり食べて\n備えておかなきゃ！", 0, 3, 8733, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132901, "『聖夜の６時間』…？\n気になる小説だけど…\n勝手に読んじゃ悪い\nわよね…ガマンよ…！", 0, 3, 8734, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132901, "きゃっ…！\nこんな不思議な本が\nあったのね～\nちょっと面白いかも♪", 0, 3, 8736, 14, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132901, "もふもふ、ふわふわで\nあったかくて\n気持ちいい～…♪", 0, 3, 8737, 15, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129501, "冬といえばサマー！\n…って言うけど夏は\n何をＰＲするんだろ\nメルクリゾート…", 0, 1, 8738, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129501, "奨学金の道は遠いな…\nあまりに遠すぎて\n最近忘れてたまである", 0, 1, 8739, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129501, "暇だし鏡でも見よっと\nはー、今日も顔が良…\nげ、若干むくんでる？\nはー、今日終わった", 0, 1, 8740, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129501, "水着…お姉ちゃんと\n貸し借りするんだけど\nこれって少数派姉妹？", 0, 1, 8741, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129501, "ちゅむ、ツムギちゃん\n今日も可愛いねデュフ\n…なんで推し語りの声\nキモくなるんだろ私…", 0, 1, 8742, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 129501, "あれ…なんで私\n水着のまま部屋の中\nうろついてるんだっけ\n…こわ！　てか寒っ！", 0, 1, 8743, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 129501, "先輩よく友達と海に\n行くって言ってたけど\n…え、誰とだろ？\nまあ誰でもいいけどさ", 0, 1, 8744, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129501, "始まる…\nチエルの夏が始まる…\nあ冬でしたっけ？\nまどっちでもいいです", 0, 2, 8745, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129501, "こないだトミヱ先輩\n壁に喧嘩売ってました\n相変わらず元気ですね", 0, 2, 8746, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129501, "ユニ先輩にお芝居の\n出来を聞かれたんで\n可愛いって答えました\n嘘は言ってません", 0, 2, 8747, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129501, "クロエ先輩の腹筋\nかっちょいーですよね\nさりげ触りたいけど\nあいつ隙ないんですよ", 0, 2, 8748, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 129501, "先輩もうカメラ係は\nやらないんです？\nチエルはやってますよ\n最高の被写体を常に", 0, 2, 8749, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 129501, "メリゾでカップル割の\n企画あるらしいんです\nちなみに先輩は…\nあ、いえ、何でも…", 0, 2, 8750, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129501, "朝ごはんは抜いても\n朝ちぇるだけは\n抜いちゃ駄目ですよ", 1, 2, 8751, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129501, "お昼なに食べるか\n悩んでます？\nおごってくれるなら\nいい店教えますけど", 2, 2, 8752, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129501, "この時間帯になると\nユニ先輩がごそごそ\n蠢きだすんですよね…", 3, 2, 8753, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129501, "世界の美少女が…\nこんな格好で\nくつろいでたら…\nまずい…事案になる", 0, 3, 8755, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129501, "え、この子って\nウォータープルーフ？\nメリゾにも連れて\nいけちゃうカンジ？", 0, 3, 8756, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129501, "はぁ～、いい香り…\nそういえばメリゾの\nスタッフルームも\n『贅』な匂いしてた…", 0, 3, 8757, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129501, "薄着で暖まるとか…\n冷静になっちゃ\n駄目なやつですよね\nこれ…", 0, 3, 8758, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129501, "おにぎり１個は\nちぇるに換算すると…\n２５ちぇるくらい？", 0, 3, 8760, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129501, "あれー？　この辞書\nまだちぇるの項目\n載ってないんだ？", 0, 3, 8761, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129501, "何これ…地味に楽しい\nあとたぶん\nクロエ先輩が好きそう", 0, 3, 8763, 10, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 129501, "これは良きですねー\n自宅でメリゾ気分とか\nユニ先輩もにっこり", 0, 3, 8764, 11, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133001, "ここが騎士氏の\nお住まいですか\nふむ…地縛霊は\nいなさそうですね", 0, 1, 8765, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133001, "ここは土足厳禁では\nないみたいですね\nま、私には関係ないか\n足、透っけ透けなので", 0, 1, 8766, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133001, "ランドソルの裏路地\nあそこは最高ですね\n暗くてじめじめしてて\n癒されるー", 0, 1, 8767, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133001, "っ！\n今ラップ音が…？\nまさか幽霊？\nやだ、怖い", 0, 1, 8768, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133001, "獣人族にエルフ族…\nなるほど騎士氏が\n幽霊程度では驚かない\n訳です…ちぇ", 0, 1, 8769, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 133001, "今日の深夜\n騎士氏が起きるまで\n枕元でずーっと\n立っててみようかな", 0, 1, 8770, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 133001, "騎士氏の驚く顔を\n見ると胸が高鳴る…\n変ですね、心臓なんて\n動いてないのに", 0, 1, 8771, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133001, "安心して下さい\n勝手にすり抜けて\n騎士氏の部屋は\n覗きませんよ…多分", 0, 2, 8772, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133001, "なんですか騎士氏\n怖い話でも聞きます？\nじゃあいきますね…\n今貴方の前に幽霊がっ", 0, 2, 8773, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133001, "この家…幽霊が\n寄ってくる間取りに\nなってますね\nまあ、嘘ですけど", 0, 2, 8774, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133001, "料理はしませんよ\n指きったら痛いので\nえ…透ければいい？\n……天才ですか？", 0, 2, 8775, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 133001, "お散歩ですか？\n深夜なら付き合います\nあ、やっぱり断ります\n幽霊出たら怖いので", 0, 2, 8776, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 133001, "騎士氏の部屋のセンス\n好きですよ…もっと\nじめじめしていれば\nなおよし、ですが", 0, 2, 8777, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133001, "レイスになってから\nあまり言わなくなった\n挨拶第一位はこちら…\nおはようございます", 1, 2, 8778, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133001, "騎士氏。午後からも\n頑張って下さいね\n私は日陰で休みます\nでは、おつです", 2, 2, 8779, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133001, "夜も更けてきましたね\nここからがレイスの\n本領発揮…では早速\nう　ら　め　し　や～", 3, 2, 8780, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133001, "中々座り心地が\nよろしいですね\nブロッケンに跨る方が\n安心はしますが", 0, 3, 8782, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133001, "知ってますよ\nどうせ貴方も\n夜中になったら\n動き回るんでしょ？", 0, 3, 8783, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133001, "すう…はあ…\nすごく落ち着く香り\n…落ち着き過ぎて\n成仏しそうでした", 0, 3, 8784, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133001, "温度は感じませんが\nなぜかほっこり\n温かい気持ちです\nなんででしょうね？", 0, 3, 8785, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133001, "もぐもぐ…\n食べ物は基本好きです\n消化の方法は…\n企業秘密で", 0, 3, 8787, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133001, "苦手なんですよね\nホラー小説\nだって幽霊とか\n怖いじゃないですか", 0, 3, 8788, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133201, "この得も言われぬ\n高揚感…やはり\n正月はいいものだな！", 0, 1, 8790, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133201, "む、いかんな\n気付くと黒豆と\n栗きんとんばかり\n食べてしまう…", 0, 1, 8791, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133201, "ギルドの連中にも\nお年玉を渡してみるか\n少しは士気が上がる\nかもしれないな！", 0, 1, 8792, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133201, "正月くらいはのんびり\n羽を伸ばすとしよう\n適切な休暇も軍人の\n大事な責務だからな", 0, 1, 8793, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133201, "正月で浮かれすぎた\n者も多いはずだ\n念のため街を見回りに\n行くとしよう", 0, 1, 8794, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 133201, "む、このおせち料理は\n絶品だな！\nあいつの分も\nとっておいてやろう", 0, 1, 8795, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 133201, "今年の抱負は…\nそうだな、あいつと\nさらに高みを目指す\nこれでいこう", 0, 1, 8796, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133201, "えっ、お年玉！\n…い、いや、私は\n子どもではないからな\nえ、遠慮させてもらう", 0, 2, 8797, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133201, "凧あげすごろく\nこま回し、たとえ遊び\nでも手は抜かんぞ\nさあ、勝負だ！", 0, 2, 8798, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133201, "そのきな粉餅も\nうまそうだな…\n私のあんこと交換\nしないか？", 0, 2, 8799, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133201, "貴公、正月料理を\n食べ過ぎてないか？\nじ、実は私も少々、な", 0, 2, 8800, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 133201, "貴公、お参りに行くぞ\n何？　外が寒いだと？\n仕方ない、手ぐらいは\nつないでやろう", 0, 2, 8801, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 133201, "貴公と新たな年を\n迎えられてうれしいぞ\n今年も一つ\nよろしくたのむ！", 0, 2, 8802, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133201, "起きろ貴公。早朝なら\n参拝客も少ないはず\n今のうちにお参りを\n済ませよう", 1, 2, 8803, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133201, "昼食に餅を焼いたぞ\nのどに詰まらせない\nようにゆっくり噛んで\n食べるんだ", 2, 2, 8804, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133201, "夜ふかしはあまり\n褒められたものでは\nないが…正月くらいは\n目をつむろう", 3, 2, 8805, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133201, "これに座って飲む\n甘酒は格別だな", 0, 3, 8807, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133201, "せっかくの正月だし\nこいつにも着物を\n着せてやりたいな", 0, 3, 8808, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133201, "これは梅の香り…？\nふふ、なかなか\n風流ではないか", 0, 3, 8809, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133201, "冬の寒さが堪えるな…\nここでしっかりと\n暖をとるとしよう", 0, 3, 8810, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133201, "正月はのんびり\nしたいからな、勝手に\n食事が出てくるのは\n本当に助かる", 0, 3, 8812, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133201, "ふむ、これが世界の\n新年のお祝いか\nなかなか興味深いな", 0, 3, 8813, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133501, "一年の計は元旦にあり\n計画を立てて、\n有意義な一年を\n過ごしましょ", 0, 1, 8815, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133501, "年末年始はどうしても\n出費が増えちゃうし\nしばらくはきっちり\n節約しなきゃ…！", 0, 1, 8816, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133501, "タタミにフトン、\nトーゴク文化の内装…\nランドソルでももっと\n流行らないかしら", 0, 1, 8817, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133501, "子どもたちが凧を\n手作りして遊んでたの\n偉いわよね、みんな…", 0, 1, 8818, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133501, "お年玉をあげる側に\nなって、分かる苦労…\n大人になるって\nこういうことかしら", 0, 1, 8819, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 133501, "一応実家にも\n顔を出さないと…\nよかったらだけど、\nあんたも来る？", 0, 1, 8820, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 133501, "毎日一緒にいて、\nささやかな幸せを\n同じように感じて…\nそれだけで十分ね", 0, 1, 8821, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133501, "着物姿、もっと\nじっくり見たいの？\nさすがに照れるわよ、\nもう…", 0, 2, 8822, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133501, "整理整頓、できてる？\nもし散らかってるなら\nお正月休みのうちに\n片づけちゃいましょ", 0, 2, 8823, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133501, "今年もみんなが無事に\n過ごせますように…\nほら、一緒に\nお祈りしましょ", 0, 2, 8824, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133501, "みんな、あんたと\n遊ぶのが楽しみだって\n子どもに好かれる\nタイプなのよね", 0, 2, 8825, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 133501, "あんたの運勢は大吉！\nラッキーパーソンは\n金髪の幼なじみよ\n…なんてねっ", 0, 2, 8826, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 133501, "初夢は実現するのよね\nそれじゃ、あたしは\nあんたと…んんっ\nなんでもないわ", 0, 2, 8827, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133501, "外が寒いと、その分\n目も冴えるわよね\nこのまま朝の散歩に\n行こうかしら", 1, 2, 8828, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133501, "お正月休みでも、\n食べてばっかじゃダメ\nしっかり動いて\n体を引き締めましょ", 2, 2, 8829, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133501, "ちゃんとあったかく\nして寝るのよ\nお正月から\n風邪ひかないようにね", 3, 2, 8830, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133501, "着物、しわに\nなっちゃいそう…\nでもそれ以上に、\n心地よくって…はぁ", 0, 3, 8832, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133501, "門松みたいに\nこの子も飾ってみる？\n運勢がよくなるかも♪", 0, 3, 8833, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133501, "この匂い、もしかして\nトーゴクのお香？\nワビサビを感じるわ", 0, 3, 8834, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133501, "ふぅ、あったかい…\n冷えた体に\n染み渡ってくるわ…", 0, 3, 8835, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133501, "んっ、おいしい！\nおせちばっかじゃ\nやっぱり飽きちゃう\nものね…", 0, 3, 8837, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133501, "験担ぎとか縁起物とか\n調べてみると\nいろいろあって\n面白いのよね～", 0, 3, 8838, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133601, "ふーん、いい家ね\nアタシの巣に\nしちゃおうかしら？", 0, 1, 8840, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133601, "お姉ちゃん…\n早く会いたいなぁ…", 0, 1, 8841, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133601, "メドゥシアナも\n気に入るかしら？\n今度連れてこよっと！", 0, 1, 8842, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133601, "お邪魔してるだけ\nなのも悪いわよね…\nお返しはしないとね\n…ネズミ退治とか？", 0, 1, 8843, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133601, "何か平和よね\nランドソルって…\nアタシ的にはもう少し\n荒っぽくてもいいわ", 0, 1, 8844, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 133601, "ランドソルも\n楽しい街よね…\n案内してくれる\nアイツのおかげ？", 0, 1, 8845, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 133601, "アタシ、アイツに\n頼りすぎてるかも…？\nで、でも！　断らない\nアイツも問題だわ！", 0, 1, 8846, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133601, "ねぇ、つまんなーい！\nもっとアタシを\n楽しませなさいよねー", 0, 2, 8847, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133601, "この家悪くないけど…\nもっとムシムシ\nジメジメにできない？", 0, 2, 8848, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133601, "何よ？\n用もないのに声掛けた\n…とか言ったら\n容赦しないわよ？", 0, 2, 8849, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133601, "少し部屋が寂しいわね\nそうだ、アンタを\n石にして家具に…\n冗談よ、ふふんっ♪", 0, 2, 8850, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 133601, "ねぇ、もう少し\n話していかない？\nアンタの話なら…\n特別に聞いてあげるわ", 0, 2, 8851, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 133601, "い、いつも\nあ、あ、ありが…\nうぅ、何でもないー！", 0, 2, 8852, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133601, "やっと起きたわね？\n寝ぼすけなんだから！\n尻尾でひっぱたいて\n起こそうかと思ったわ", 1, 2, 8853, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133601, "人捜しは日の高い\nうちに限るわ！\nさぁ、メドゥシアナ！\n全速力で出発進行よ!", 2, 2, 8854, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133601, "アタシ…\n子どもじゃないもん…\n眠くなんか…\nないんだからぁ…", 3, 2, 8855, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133601, "ふわふわであったかい\nつい、とぐろを\n巻きたくなるわね…", 0, 3, 8857, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133601, "アンタ、こんな\nヤワな体じゃ駄目よ\nウロコくらい\n生やしたらどうなの？", 0, 3, 8858, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133601, "くんくん…\n…ふん\n悪くないじゃない\n…くんくんくんくん…", 0, 3, 8859, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133601, "あったかいの大好き！\nジオ・ゲヘナでも\nよく日向ぼっこ\nしたもんだわ…ふわぁ", 0, 3, 8860, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133601, "お姉ちゃんを捜すなら\n…もぐ\n体力つけないとね\n…もぐもぐ", 0, 3, 8862, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133601, "何よ？　たまには\n勉強くらいするわよ\nお姉ちゃんに\n笑われちゃうもの", 0, 3, 8863, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133701, "この装いと力…\n今の世界との\n整合性を上手いこと\n合わせておかないとだ", 0, 1, 8865, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133701, "この格好…開発してた\n当時のまんまだ\nサイズもそのまま♪\n体型維持も完璧っ！", 0, 1, 8866, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133701, "あとで装備の手入れも\nやっとかないと\n…剣なら研ぐだけで\n終わるんだけどなぁ", 0, 1, 8867, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133701, "自分の原点を\n振り返ってみるのも\nたまには必要だね", 0, 1, 8868, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133701, "完璧じゃなくても\n今できる全力を尽くす\nその事に意味がある…\nそうだよね、少年", 0, 1, 8869, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 133701, "始まりのあの日、\nそして向かうべき道…\nいつも忘れずにいよう", 0, 1, 8870, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 133701, "誰かを救うために\n許容される犠牲なんて\nあっちゃいけない…\n全部、助けてやる", 0, 1, 8871, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133701, "おっと少年、\n何か御用かな？\nキミに関わる事なら\n最優先で対応するよ", 0, 2, 8872, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133701, "困った事があれば\n頼ってもらって\n構わないからね！\nむしろもっと頼って！", 0, 2, 8873, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133701, "そうだ、時間あるなら\n武器のメンテを\n手伝ってくれないかな\n数が多くてさ～", 0, 2, 8874, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133701, "遠距離戦の心得、\n少年にもレクチャー\nしてあげよっか？", 0, 2, 8875, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 133701, "無茶しないで、って\n言ってもするだろうし\nせめてアタシの手が\n届く範囲で頼むよ", 0, 2, 8876, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 133701, "虚実入り混じる\nこの世界でキミが…\n進むべきルートを\n照らしてくれたんだ", 0, 2, 8877, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133701, "グッモーニン！\n朝だよ少年、今日も\n張り切っていこう！", 1, 2, 8878, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133701, "クレープの代わりに\nガレットを焼いたんだ\nこの味で少年を\n唸らせてみせよう！", 2, 2, 8879, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133701, "もうこんな時間だ\n遅くならないうちに\n作業を切り上げようか\n少年も早く寝なよ？", 3, 2, 8880, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133701, "あえて緊張を解くのも\n戦いには必要だし…\nそうそう…大事大事…", 0, 3, 8882, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133701, "愛着を抱かせる事を\n意図したデザインだね\n心理的効果の観点から\n分析してみようか…", 0, 3, 8883, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133701, "香りは記憶と密接に\n結びつく事がある…\n懐かしい匂いだね…", 0, 3, 8884, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133701, "癒されるな…\n何がこの感情変化を\n引き起こすんだろう？", 0, 3, 8885, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133701, "食の偉大さを感じるな\n戦闘糧食ってやっぱり\n味気ないしなぁ…", 0, 3, 8887, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133701, "実用ばかりじゃなく\n詩や物語ってのも\n人には必要だよね", 0, 3, 8888, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133901, "この場所の開発版も\n探せばあるかもな\nさて、どんなデザイン\nだったのやら…", 0, 1, 8890, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133901, "今まで数々の波乱に\n身を投じたが…やはり\n波乱にはこの姿が一番\n似合いだよ☆", 0, 1, 8891, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133901, "むっ…この角に羽と\n尻尾は家具とぶつかり\nやすいな。兎の時は\n気にならなかったが…", 0, 1, 8892, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133901, "いつもより闘争を\n求める気分になるな\n初心に帰るとは\nこのことか…？", 0, 1, 8893, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133901, "未調整上等☆\nそれを上手く使い\nこなしてこその\nワタシだろう？", 0, 1, 8894, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 133901, "坊やとできそうな\n愉しいことは…\nふむ、多すぎて迷うな", 0, 1, 8895, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 133901, "時折、坊やの居心地の\nよさに絆されそうに\nなってしまうな\nワタシらしくもない…", 0, 1, 8896, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133901, "どうだ、おろした\n髪型も艶やかだろう？\nそんじょそこらの\n賞賛では満足せんぞ☆", 0, 2, 8897, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133901, "外に出るぞ！\n共に退屈を殺しに\n行こうじゃないか☆", 0, 2, 8898, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133901, "角と翼も触りたい？\nワタシの虚数魔境に\n挑むというなら\n止めはせんよ☆", 0, 2, 8899, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133901, "なんだ、困りごとか？\nクリスティーナさんが\n何でも聞いてやろう☆\n解決するかは別だがな", 0, 2, 8900, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 133901, "力が欲しいか？\nならば呼ぶがいい！\nワタシの名を☆", 0, 2, 8901, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 133901, "悪魔との契約は絶対\nなのだそうだが…\nどうだ、坊や\n一つ契ってみるか？", 0, 2, 8902, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133901, "まだ眠い？　甘えるな\n寝首を掻かれたく\nなければ飛び起きて\nこい…☆", 1, 2, 8903, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133901, "昼食の準備を\nしてやろう。\n毒見はいらんぞ？\n安心するといい！", 2, 2, 8904, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133901, "坊やは寝る時間だ\n寝ないのならば…\n大人の時間に\n付き合ってもらうか☆", 3, 2, 8905, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133901, "角と翼、尻尾の\nコンディションまで\n完璧だな☆", 0, 3, 8906, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133901, "なかなかのフワモコ\n具合だが…ううむ…\n翼と尻尾が邪魔だな", 0, 3, 8907, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133901, "うーむ、角と尻尾を\n追加して…さらに槍を\n持たせた方が見栄えが\nよさそうだ", 0, 3, 8908, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133901, "いい香りだな\n血と砂塵の臭いで\n上書きするまでの\n間に合わせに最適だ☆", 0, 3, 8909, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133901, "生温いな\nもっと焦がれるほどの\n熱量が欲しいところだ", 0, 3, 8910, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133901, "普段はないものが\n生えているのだから、\n運動でしっかり感覚を\n掴んでおかなければな", 0, 3, 8911, 6, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133901, "腹が減っては戦は…\nと言うからな\nしっかり食べて\nおくとしようか☆", 0, 3, 8912, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133901, "『家庭を築くための\n絶対条件』…？\nふむ…興味がある\nタイトルだ", 0, 3, 8913, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133801, "奇妙な感覚です\nまるであの頃の自分に\n変装しているかのよう", 0, 1, 8915, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133801, "グレゴール？\nおっと、そうでした\n今は昔の姿になって\n離れていましたね", 0, 1, 8916, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133801, "このアバターでいると\n妙に子どもたちからの\n視線を感じますね…", 0, 1, 8917, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133801, "リソースの割り振りが\n変わっただけなのに\n随分と身体が軽く\n感じますね", 0, 1, 8918, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133801, "普段グレゴールに\n任せている分身の管理\n自分でやるには\nいささか煩雑ですね…", 0, 1, 8919, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 133801, "長老に頼まれて描いた\n妖精の絵…いったい\n何に使ったのでしょう", 0, 1, 8920, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 133801, "私自身をモデルにした\n完全なるNPCたち\nそれが私の分身です", 0, 1, 8921, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133801, "このアバターが\n気になるのですか？\n貴方はつくづく\n妖精に縁がありますね", 0, 2, 8922, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133801, "ちょうどよかった\nグレゴールを見かけ\nませんでしたか？", 0, 2, 8923, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133801, "あの翅は飾りでは\nありません。ただ…\nグレゴールに乗って\n飛ぶ方が楽なだけです", 0, 2, 8924, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133801, "妖精とは本来神話に\nおけるトリックスター\n運命をかき回すことが\n存在意義なのです", 0, 2, 8925, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 133801, "変貌大妃の権能との\n相性を考えなければ\nこの姿こそが私たちの\n最強の姿です", 0, 2, 8926, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 133801, "変化とは一種の喪失\nだからこそ得られる物\nがある…貴方のそれも\n大切にするのですよ", 0, 2, 8927, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133801, "マサキからの定時連絡\nが来ませんね…心配は\n不要ですが、様子を\n見に行きましょうか", 1, 2, 8928, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133801, "昼食の時間ですね\nグレゴールのために\nカロリーの高い物を\n食べに行きましょう", 2, 2, 8929, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133801, "夜更かしはダメですよ\n悪いスパイが暗躍して\nいる時間帯ですからね", 3, 2, 8930, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133801, "ふむ、座り心地は中々\nですが…私にはやはり\n玉座の方があいますね", 0, 3, 8932, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133801, "ふふ、いいでしょう\n我が配下に加えます\n存分に働きなさい", 0, 3, 8933, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133801, "時折晶からしていた\nあの甘い匂いは何の\nアロマだったのか…", 0, 3, 8934, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133801, "燃え盛る炎もこうして\n暖炉の中なら安全です\nクリスもあの衝動を\n管理さえできれば…", 0, 3, 8935, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133801, "そういえば真那が\n獄中で手持無沙汰に\nひたすらおにぎりを\n食べていましたね…", 0, 3, 8937, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133801, "これは、妖精学の本…\n長老の蔵書から借りて\n読みふけったものです", 0, 3, 8938, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134301, "どんな詩を\n作ろうかしら…？\n…大体、暗く\nなっちゃうけど…", 0, 1, 8940, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134301, "唱喚術の練習…\nここでやったら\n迷惑よね…", 0, 1, 8941, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134301, "チカちゃんとの\nデュエット…\n足をひっぱってない…\nかしら…？", 0, 1, 8942, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134301, "唱喚士の修行…\n滝に打たれるとか…\n崖を登るとか…\nなくて、よかった…", 0, 1, 8943, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134301, "精霊さん、肉じゃが\n食べる、かしら…？\n魔力だけだと…\n飽きちゃいそうだし…", 0, 1, 8944, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 134301, "精霊さんのお世話に\n慣れたぶん…\nあのひとのお世話も\n上手にできる…はず？", 0, 1, 8945, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 134301, "これからも、歌うわ…\nこの声が、彼の力に\nなる、限り──", 0, 1, 8946, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134301, "この服…踊り子さんに\n間違われちゃって…\nあ…お、お触りしちゃ\nだめよ…？", 0, 2, 8947, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134301, "あなたはどんな\n精霊が…好き…？\n唱喚…してみたいわ…", 0, 2, 8948, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134301, "あんまり白い服って…\n着ないの…その…\nこ、ころんだりして…\n汚すから…", 0, 2, 8949, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134301, "じ、実はね…\nこの体の模様…筆で\n書いてもらうたび…\nくすぐったい…の…", 0, 2, 8950, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 134301, "精霊さん…あなたと\n遊びたいみたい…\nや、やっぱり…\nパパ…だから…？", 0, 2, 8951, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 134301, "あなたを歌にしたら…\n…ふふっ、だめね\n一曲じゃ…あなたを\n表現できない、わ…", 0, 2, 8952, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134301, "朝の修行は…\nうとうと…し…ちゃ…\n……（かくん）\n……あぅっ？", 1, 2, 8953, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134301, "せ、精霊さんと…\nかくれんぼ中なの…\nうぅ…見つからない…\n日が暮れちゃいそう…", 2, 2, 8954, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134301, "精霊さん、寝たから…\nふふっ…ここからは\nパパとふたりの時間…\n…ね？", 3, 2, 8955, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134301, "ふぅ…修行と育児は…\nちょっとだけ…\n休憩…ふぁ…", 0, 3, 8957, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134301, "あなたとも契約…\n…できる、かしら？\nふふっ…", 0, 3, 8958, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134301, "いい香りに包まれたら\n精神統一も\nはかどりそう…♪", 0, 3, 8959, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134301, "あたたかい場所…\nここで作詞したら…\n穏やかな詩が\n書ける、かも…", 0, 3, 8960, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134301, "精霊さんのお世話を\nしていると…\nお腹、空いちゃって…", 0, 3, 8962, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134301, "ふふっ…♪\n色んな楽譜…\n集めるの、楽しい…♪", 0, 3, 8963, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 122301, "けんぞくまだかな？\nはやく会いたいなー\nヴァンピィちゃん\nずっと待ってるからね", 0, 1, 8965, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 122301, "けんぞくのこと\nずっとずっと守るよ！\nだからいつまでも\n一緒にいてね", 0, 2, 8966, 103, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134401, "私の歌や想いを\n誰かに託せる…\nこれほどうれしい\nことはありません", 0, 1, 8967, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134401, "コッコロさんの\n包容力はすごいです\nあんなふうに私も\n人を包み込めたら…", 0, 1, 8968, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134401, "最近のボイトレは\nランファさんと一緒に\nやるんです。お互いに\n切磋琢磨しますよ！", 0, 1, 8969, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134401, "先輩として相応しい\n立ち振る舞いを\n心がけたいですね\nしっかりしなきゃ…！", 0, 1, 8970, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134401, "ランファさんが\n踊ったら映えるかも…\nアイドルにも興味を\n持ってもらえないかな", 0, 1, 8971, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 134401, "支え導くために\n寄り添って考える…\nあの人からたくさん\n教わったことでしたね", 0, 1, 8972, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 134401, "いつだってあなたに\n恥じることのない\n姿をお見せしたいです", 0, 1, 8973, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134401, "あなたの手は温かくて\n触れていると\n落ち着きますね…♪", 0, 2, 8974, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134401, "里のみんなから修行の\n差し入れを頂きました\nよろしければ、一緒に\n食べましょう♪", 0, 2, 8975, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134401, "ノゾミにもお土産を\n用意したいですね\n里のお土産選び、\n付き合ってください♪", 0, 2, 8976, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134401, "精霊たちも、あなたが\n大好きみたいです♪\n優しさが伝わって\nいるんでしょうね", 0, 2, 8977, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 134401, "特訓が終わったら、\n二人でどこかに\n出かけましょうか♪", 0, 2, 8978, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 134401, "ひゃ…す、すみません\n肌の模様を\nなぞられると、\nくすぐったくて…", 0, 2, 8979, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134401, "おはようございます\n精霊たちと一緒に、\n今日も頑張ります\n応援してくださいね♪", 1, 2, 8980, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134401, "お昼の時間です\n今日も精いっぱい\n修行を頑張りましょう", 2, 2, 8981, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134401, "もうすっかり夜ですね\n眠るまで子守唄を\n歌ってあげますよ？\nふふ♪", 3, 2, 8982, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134401, "無理せず休憩するのも\n大切ですよね\nふかふか～…ふふっ♪", 0, 3, 8983, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134401, "今日はこの子に\n歌の特訓を聞いて\nもらいましょう♪", 0, 3, 8984, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134401, "素敵な香り…\nふふ、精霊たちも\n気に入ったみたいです", 0, 3, 8985, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134401, "あったかい…\nこの衣装、やっぱり\n少し冷えやすいものね", 0, 3, 8986, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134401, "特訓の後のご飯は\n美味しいですね♪\nこれでまた頑張れます", 0, 3, 8987, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134401, "唱喚士についての\n書籍はないみたい\nいつか私がここに\n楽譜を入れたいな", 0, 3, 8988, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134601, "ギルドハウスの平和も\n任せてください\n何が来たって\n守ってみせますよ！", 0, 1, 8992, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134601, "このギルドハウスも\nいつの間にか出撃台に\n改造されてたり\nするんでしょうか…？", 0, 1, 8993, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134601, "戦いを経験してこそ\n実感できますね\n平和はいいもの、\n友達は素晴らしいもの", 0, 1, 8994, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134601, "次はランドソルで\n巨大ロボットが\nブームになったり？\n…さすがにないか", 0, 1, 8995, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134601, "改めて見るとこの服…\nか、体の線とか\n結構目立ってるかも…", 0, 1, 8996, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 134601, "団長さんって\n知らない人とも\n話せる系ですよね\nコツはいったい…", 0, 1, 8997, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 134601, "ＢＢ団、世界を救う！\nまだ信じられないけど\n本当にあった出来事\nなんですよね…！", 0, 1, 8998, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134601, "パイロットも\n視力が大事なんです\n弓使いの経験が\n生きました！", 0, 2, 8999, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134601, "ギガンティック\nマイフレンドくん、\n出撃！\nあ、ただの妄想です", 0, 2, 9000, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134601, "コクピットから\n降りたあとに残る\nふわふわ感…ちょっと\n癖になりませんか？", 0, 2, 9001, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134601, "ひえっ、敵襲！？\nあっすみません、\nちょっと\n寝ぼけてたかも…", 0, 2, 9002, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 134601, "共に戦場を駆け抜け\nＢＢ団の絆もより強く\nこれからも結束を\n強めましょうね！", 0, 2, 9003, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 134601, "同じコクピットに\n乗ったのに比べれば、\nスキンシップくらい…\nや、やっぱ緊張します", 0, 2, 9004, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134601, "おはようございます！\n今日もがんばりますっ\n…クローチェさんを\n見習ってみました…", 1, 2, 9005, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134601, "お昼ご飯の時間ですね\nがんばって他の人\n誘ってみようかな…", 2, 2, 9006, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134601, "夜の見張りは\n任せてください\n一人きりの夜も\n慣れてますから！", 3, 2, 9007, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134601, "休めるときに\n休んでおかなくちゃ\nいつ何が起こるか\n分かりませんからね", 0, 3, 9009, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134601, "師匠もこういうの\n好きだったのかな…\nなんだかしんみり\nしちゃいます", 0, 3, 9010, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134601, "香りでリラックス…\n余裕のない時でも\nこういう安らぎは\n大切ですよね", 0, 3, 9011, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134601, "あったかい…\nこれはきっと友情と\n同じぬくもり…", 0, 3, 9012, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134601, "新鮮なご飯、感動…！\n師匠たちの世界だと\nご飯は保存食ばかり\nでしたからね…", 0, 3, 9014, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134601, "操縦マニュアル、\nしっかり読んで\n性能を引き出さなきゃ", 0, 3, 9015, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134501, "屋内で飛べなくても\nここなら平気ですっ\n助けてくれる人が\nたくさんいますからっ", 0, 1, 9017, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134501, "新ボディになってから\n人や物が前より身近に\n感じられて、わたし、\n幸せですっ♪", 0, 1, 9018, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134501, "配達以外にも何か\nお仕事したいですっ！\nハグで遊覧飛行とか、\n喜んでもらえますか？", 0, 1, 9019, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134501, "むむむ～…\n文字を書くのって\n難しいですね～っ\n練習しなくちゃっ！", 0, 1, 9020, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134501, "簡易点検、よしっ！\nポポおじいさんに\n点検の仕方を教わって\n今日も絶好調ですっ", 0, 1, 9021, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 134501, "明日は北の空、\n明後日は南の空っ♪\nえへへっ、見せたい\n景色がいっぱいですっ", 0, 1, 9022, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 134501, "ついナイトさんを\n探しちゃいます…\nわたし、寂しんぼさん\nだったんでしょうかっ", 0, 1, 9023, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134501, "どんなところでも\nひとっ飛びっ♪\nわたしに任せて\nくださいねっ！", 0, 2, 9024, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134501, "まだ知らない機能が\nエアリアルには\n隠されてるかもっ…\nワクワクしますねっ！", 0, 2, 9025, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134501, "ひゃんっ！　…あはは\nエアリアルになっても\nケーブルに触られると\nくすぐったいですねっ", 0, 2, 9026, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134501, "ヴォルガーノさんに\nそっくりの子猫さんに\n最近触れたんですっ！\nかわいかったな～っ♪", 0, 2, 9027, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 134501, "ハグしてくれるん\nですかっ？\nえへへっ、ぎゅーっ♪", 0, 2, 9028, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 134501, "ナイトさんと一緒に\nいるだけで、胸が\nぽかぽか～って\nあったかいですっ♪", 0, 2, 9029, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134501, "おはようございますっ\n目覚まし飛行は\nいかがでしょうかっ？", 1, 2, 9030, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134501, "お昼ですよっ！\nいっぱい補給して\nまたフルパワーで\nがんばりましょうっ♪", 2, 2, 9031, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134501, "眠れない夜はわたしに\n任せてくださいっ！\n充填さえあればずっと\n一緒にいられますよっ", 3, 2, 9032, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134501, "歩きづらいので\nこういう休憩ポイント\nって助かります～っ", 0, 3, 9034, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134501, "スラスターを付けたら\nこの子たちも空を\n自由に飛べたり\nするんでしょうかっ？", 0, 3, 9035, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134501, "あっ、これに似た\n香りを近くのお店で\n検知したことが\nありますっ♪", 0, 3, 9036, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134501, "高熱源反応を検知っ\nわたしのスラスターも\n熱量の高さなら\nかなりものですよっ♪", 0, 3, 9037, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134501, "もぐもぐもぐ…っ\n食べたぶんはあとで\n真空パックにして\nお返ししますねっ", 0, 3, 9039, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134501, "本を読むのって\n楽しいですねっ♪\n前はびりびりーって\nしちゃってたのでっ", 0, 3, 9040, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134701, "なかなかの住処ですね\nこの私の別邸にして\nあげてもいいですよ♪", 0, 1, 9042, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134701, "はぁ…いつになったら\nすべての神力が\n戻るのでしょう？", 0, 1, 9043, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134701, "ここも悪くないですが\n昔私が作った庭園は、\nもっと良い場所でした\n焼けちゃいましたが…", 0, 1, 9044, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134701, "うう…\nおなかが空きました…\nお米が食べたいです…", 0, 1, 9045, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134701, "ここは平和ですねえ…\nジオ・ゲヘナも\nこのくらい穏やかなら\n住みやすいのですが", 0, 1, 9046, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 134701, "騎士さんのおかげで\n神力が戻ってきました\n良い働きですよ♪", 0, 1, 9047, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 134701, "騎士さんのいる場所が\n今の私がいるべき場所\nなのかもしれませんね", 0, 1, 9048, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134701, "頭がたかーい！\n…ですよっ！", 0, 2, 9049, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134701, "ほーちゃんと呼ぶ権利\n安売りはしませんよ？\n特別ですから！", 0, 2, 9050, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134701, "私とおしゃべりを？\nまあ、いいでしょう♪\n何を話しましょうか？\nこの羽の秘密とか？", 0, 2, 9051, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134701, "小鳥扱いは\nやめなさーい！\nこれでも神様ですよ？", 0, 2, 9052, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 134701, "き、気安く触るのは…\n…い、いえ、\n騎士さんなら…まぁ…", 0, 2, 9053, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 134701, "絢爛豪華な絆！\nこの私と結べるなんて\n光栄でしょう？", 0, 2, 9054, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134701, "ふわぁぁ～\nおはようごじゃいます\n…んむむ", 1, 2, 9055, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134701, "お昼ご飯は何を…\nえっ？　私？\nそそんな゛あ゛！", 2, 2, 9056, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134701, "あの…騎士さん？\n添い寝する権利とか…\nほしくないですか？", 3, 2, 9057, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134701, "はぁ～ふかふか…\n…へ？\n鳥の巣にいるみたい？", 0, 3, 9059, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134701, "ふむ…\nそばに置いてあげても\nいいでしょう", 0, 3, 9060, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134701, "ほわぁ～\nいいかおり～", 0, 3, 9061, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134701, "あたたかい…\nなんだか元気が\nでてきますねっ", 0, 3, 9062, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134701, "たくさん食べて力を\n取り戻さなくてはっ\n…んがぐぐっ", 0, 3, 9064, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 134701, "ふむふむ…\n人の子にしては\nよく書けていますね♪", 0, 3, 9065, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133101, "素敵なお家ですね\n温かみがあって\nどこか騎士様に\n似ております。ふふ", 0, 1, 9067, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133101, "賑やかな街ですね\nこれほど活気豊かだと\nつい外に出てみたく\nなってしまいます", 0, 1, 9068, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133101, "別世界の存在は知って\nおりましたが、いざ\n目の当たりにすると\n驚きの連続ですわ", 0, 1, 9069, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133101, "この街は様々な種族が\nなにはばかることなく\n混在しているのですね\n…我々も、いつかは…", 0, 1, 9070, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133101, "ランドソルにも香水の\nお店があるのですね\nどんな香りが揃って\nいるのでしょうか…♪", 0, 1, 9071, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 133101, "昨日、素敵な花園を\n見つけたのです\n今日のお茶会は\nそちらでしましょうか", 0, 1, 9072, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 133101, "気を抜いてはいけない\nとわかっていても、\nここだとつい安心して\nしまいます…ふう…", 0, 1, 9073, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133101, "ごきげんよう\n街で見つけた茶葉を\n試しているんです\n騎士様も一杯どうぞ♪", 0, 2, 9074, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133101, "親切な方ばかりですね\n素敵なお友だちが\nできそうな予感で、\nワクワクしてます…♪", 0, 2, 9075, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133101, "黄泉の食卓の皆様用に\nお土産を用意しないと\n騎士様のお知恵も\n頼りにしておりますわ", 0, 2, 9076, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133101, "ジオ・ニヴルヘルと\n比べると、この世界は\n本当に明るいですね\n少し眩しいくらいです", 0, 2, 9077, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 133101, "あまり、血の巡りが\n良くなくて…\n騎士様の手は\nとても温かいですね", 0, 2, 9078, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 133101, "今度はわたくしに\nジオ・ニヴルヘルを\n案内させてくださいね\n観光案内のお返しです", 0, 2, 9079, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133101, "おはようございます\n朝の眩しい太陽には\nなかなか慣れません…", 1, 2, 9080, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133101, "騎士様、お茶は\nいかがでしょうか？\n一度一休みして、\n英気を養いましょう", 2, 2, 9081, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133101, "眠れないのでしょうか\nまぶたが重くなるまで\nわたくしをお話し\n相手になさって下さい", 3, 2, 9082, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133101, "思わず気が抜けそうに\nなってしまいます…\nふふ、ふわふわ…♪", 0, 3, 9084, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133101, "まあ、かわいらしい\nグレイスもきっと\n気に入りそうな\nデザインですわ", 0, 3, 9085, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133101, "まあ、いい香り…\n心安らぐようですわ\n素敵なセンスを\nお持ちなのですね♪", 0, 3, 9086, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133101, "燃え移りや乾燥がある\nから近づかないほうが\nいいとわかりつつも…\nああ、温かい…♪", 0, 3, 9087, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133101, "食が細いというよりは\n消化があまりうまく\nできなくて…本当は\nまだ頂きたいのですが", 0, 3, 9089, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 133101, "『薔薇の下の契り』？\nまあ、男性同士で…！\nそんな…こ、これ以上\nここで読むわけには！", 0, 3, 9090, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132301, "ダーリンが帰ってくる\n前に部屋の掃除しよ♪\n…ふふ、これってもう\n新婚生活だよね…♪", 0, 1, 9092, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132301, "家具のセンスまで\n私と同じだなんて\n運命って怖いわ…♪", 0, 1, 9093, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132301, "あの人はベッドの下に\n何か隠してるのかな…\nな、なんて全っ然\n気にならないし…っ！", 0, 1, 9094, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132301, "うっかりして\n角引っかけないように\nしないとね…", 0, 1, 9095, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132301, "錬金術を使わなくても\nあなたの心を鷲掴む\n私は恋も錬金術も\n失敗しないんだから！", 0, 1, 9096, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 132301, "あの人って破廉恥なの\n興味あるのかしら…？\n基本破廉恥禁止だけど\nあの人だけは…許す…", 0, 1, 9097, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 132301, "手狭になれば錬金術で\n新しい部屋作るから\nいつ家族が増えても\n安心だよね…♪", 0, 1, 9098, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132301, "錬金術でカレーライス\n作っちゃおっかな♪\n具材を釜でグツグツ…\nま、料理とも言うわね", 0, 2, 9099, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132301, "錬金術に興味がある？\nじゃあさっそく私が\n手取り足取り教えるわ\nって…やだ破廉恥っ！", 0, 2, 9100, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132301, "イロハちゃんとの\n虫捕りは命懸けよ！\n一万匹捕れるまで\n帰れないんだから…", 0, 2, 9101, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132301, "ソノさんって\n体は小さいけど\nとっても頼れる私の\nお姉さん的存在なの♪", 0, 2, 9102, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 132301, "はあ…部屋中に広がる\nダーリンの香り…♪\n…ってノゾミみたいに\nなっちゃってる！？", 0, 2, 9103, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 132301, "鹿の獣人族の特徴は\nこのエッチな角…\n違っ！　叡智よ叡智！\n噛んだだけだからっ！", 0, 2, 9104, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132301, "あ、おはよう！\n寝る前に飲んだ\n目覚めすっきり秘薬の\n効果はバッチリかな？", 1, 2, 9105, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132301, "午後からも頑張る\nあなたのために\n愛妻弁当作ったの♪\n一緒に食べよ？", 2, 2, 9106, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132301, "今日も一日お疲れさま\n眠れないなら隣で…\nじゃなかった早寝して\n明日も頑張ろうね！", 3, 2, 9107, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132301, "いい錬成をするには\n適度な休憩も必要よね\n…気持ち良くてたまに\n寝落ちしちゃうけど…", 0, 3, 9109, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132301, "かわいいぬいぐるみ\n錬成じゃなくて\n手編みで作ったら\n驚いてくれるかも♪", 0, 3, 9110, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132301, "くん、くん…\nん～いい匂い♪\nノゾミの香水と\nいい勝負だわ", 0, 3, 9111, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132301, "ああ、暖かい\nでもソノさんの作った\nミートパイの方が\nもっと温かいわ…♪", 0, 3, 9112, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132301, "もぐもぐ…私も\n食べても減らない秘薬\n錬成して他の食べ物も\n作れないかな？", 0, 3, 9114, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132301, "ふむふむ…\nこのレシピ面白いわね\n今度錬成してみよっと", 0, 3, 9115, 8, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132501, "おー、体が軽い\n腰痛も胃もたれも\nないし、最高かよ", 0, 1, 9117, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132501, "いっつも力入れてたら\nいざって時困るし\nやっぱ省エネだな", 0, 1, 9118, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132501, "やば…なんか急に\nブドウ朱飲みたい…\nルイちゃんに頼むか？\nいや、でもなあ…", 0, 1, 9119, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132501, "あれ、あの材料の在庫\nまだあったっけ…\nルイちゃんに確認しよ", 0, 1, 9120, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132501, "イロハのやつ変な\nイタズラしてない\nといいけど…", 0, 1, 9121, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 132501, "今日のシフトは～…\nお、彼と一緒じゃん\nふふっ…", 0, 1, 9122, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 132501, "イロハのやつ\n不用意に私の体で\n彼に抱きつくなって\nこっちが恥ずかしい…", 0, 1, 9123, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132501, "今日も元気だねえ\n若いってうらやま…\nっていや、今は私の\n方が若いじゃん", 0, 2, 9124, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132501, "ん～？　どしたん\nヒマならうちの店で\nバイトしてく？", 0, 2, 9125, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132501, "イロハ見なかった？\n今日は手伝うって約束\nしてたのにさあ…", 0, 2, 9126, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132501, "ルイちゃん特製\nエナドリ（子ども用）\nこれ飲んで今日も\nお仕事頑張るかー", 0, 2, 9127, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 132501, "元の体なら\n君と目線、合うのにね\n…ってドラマかよ\nごめん、今の忘れて", 0, 2, 9128, 101, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 132501, "なんかヒマそうじゃん\n用事ないなら\nお姉さんがコーヒー\n淹れたげよっか", 0, 2, 9129, 102, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132501, "ふわあ…おはよ\n今日もお仕事\nがんばりますかー", 1, 2, 9130, 1, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132501, "ちょうどいいや\n一緒にお昼どうよ\nお姉さんが\n奢ってあげよう", 2, 2, 9131, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132501, "ふぁぁ…ふぅ\nここからは大人の時間\nなんだけどさぁ\n眠くてかなわん…", 3, 2, 9132, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132501, "おお～なんか前より\nでっかく感じるわ～\nてそりゃそうか", 0, 3, 9134, 2, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132501, "そいえば、イロハって\nあんまこういうの\n欲しがらなかったな～\nクワガタばっかで", 0, 3, 9135, 3, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132501, "はあ…いい匂いだね\nさすがルイちゃん特製\n錬金アロマ\n次の目玉商品だな", 0, 3, 9136, 4, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132501, "ユカリっち焚火の前で\nキメる麦しゅわに\nハマってるらしいけど\n興味あるわぁ…", 0, 3, 9137, 5, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132501, "うわこれ…\n無限にいけちゃうわ\n子どもの胃袋\n恐るべし…", 0, 3, 9139, 7, 1, 0);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 132501, "ふむ、こんな需要が…\n次の新商品として\nルイちゃんに試作\n頼んでみるか～", 0, 3, 9140, 8, 1, 0);
