INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 1, 0, 1, "ここに困ってる人は\nいないよね…？\nよかった♪", 1, 100101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2, 2, 0, 1, "冒険の準備準備…\nうん、忘れ物はなし！\n…あっ！", 1, 100101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3, 3, 0, 1, "リズムを意識して…\nわんつーわんつー！\nうん、調子はバッチリ", 1, 100101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 4, 0, 1, "ここで特訓していい？\n物は壊さないように\n気をつけるね！", 1, 100101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5, 5, 0, 1, "なんか落ち着かないや\nもしかして、\n困ってる人が\n近くにいるのかな…？", 1, 100101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6, 101, 0, 1, "えへへ…いつも\n一緒にいてくれて\nありがとねっ！", 4, 100101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7, 102, 0, 1, "ちゃんと覚えてるよ\n騎士くんの声…\nあの時はありがとね！", 8, 100101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 1, 0, 2, "あははっ！\nやったなー！\nお返しだー！\nこちょこちょこちょ…", 1, 100101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9, 2, 0, 2, "どうしたの？\nもしかして困ってる？\nあたしにまかせて！", 1, 100101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (10, 3, 0, 2, "えっとね…しっぽは\n触っちゃダメだよ？", 1, 100101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (11, 4, 0, 2, "反応しない特訓…\nだったけど無理～！\n悔しいよぉ～！", 1, 100101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 101, 0, 2, "ふぁふぃ？\nやられた～！　ほっぺ\nぷにぷにしないでぇ～", 4, 100101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (13, 102, 0, 2, "えへへ…\n騎士くんの手だ…\nあったかいな…♪", 8, 100101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (14, 1, 0, 2, "おはよー！\n今日も元気に\nいっちゃうよー！", 1, 100101, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (15, 2, 0, 2, "ユイちゃんに\nおやつ作って\nもらおうかな～♪", 1, 100101, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (16, 3, 0, 2, "寝るのも修行だって\nこないだレイさんが\n言ってたんだ…", 1, 100101, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (17, 1, 0, 3, "ちゃんとケアして\n傷が残らないように\nしないと…", 1, 100101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (18, 2, 0, 3, "一回休むと\nこのままずるずる\nいきそう…", 1, 100101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (19, 3, 0, 3, "新しい練習相手に…\nって冗談だよ冗談！\n本気じゃないから！", 1, 100101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (20, 4, 0, 3, "落ち着くなぁ～\n心だけじゃなくて\n体もほぐれそう♪", 1, 100101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (21, 5, 0, 3, "あったかい所に\nいると体が丸く\nなっちゃう…", 1, 100101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (22, 6, 0, 3, "いつ何が起きるか\n分からないし、\nもっと力をつけないと", 1, 100101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (23, 7, 0, 3, "美味しいけど、\n食べ過ぎないように\nしないと…！！", 1, 100101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (24, 8, 0, 3, "文字が多いと\n眠くなっちゃう…\n危ない危ない！", 1, 100101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (26, 1, 0, 1, "リズムを意識して…\nわんつーわんつー！\nうん、調子はバッチリ", 1, 100102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (27, 2, 0, 1, "のんびりするのって\n退屈かも…\n何かして遊ばない？", 1, 100102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (28, 3, 0, 1, "留守番はちょっと\n苦手かも…\nなんか寂しいんだもん", 1, 100102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (29, 4, 0, 1, "あたしのお友達も\n呼んできていい？\nきっと楽しいよ！", 1, 100102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (30, 5, 0, 1, "誰も困ってないよね？\n…何をすれば\nいいのか分かんないや", 1, 100102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (31, 101, 0, 1, "ちゃんと覚えてるよ\n騎士くんの声…\nあの時はありがとね！", 8, 100102, 0, 1);
