INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (33, 1, 0, 2, "反応しない特訓…\nだったけど無理～！\n悔しいよぉ～！", 1, 100102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (34, 2, 0, 2, "触るのって楽しい？\n…そっか\n少しだけだよ…？", 1, 100102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (35, 3, 0, 2, "あたしの元気を\nわけてあげるね！\nフレー！　フレー！", 1, 100102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (36, 4, 0, 2, "何かムズムズする！？\nって騎士くんかぁー\nどしたの？", 1, 100102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (37, 101, 0, 2, "ひゃうっ！　もう…\nしっぽはだめだってば\n次やったらデコピン！", 8, 100102, 0, 1);
