INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (42, 1, 0, 3, "これでよし♪\n騎士くん\n気づくかな…？", 1, 100102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (43, 2, 0, 3, "一回休むと\nこのままずるずる\nいきそう…", 1, 100102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (44, 3, 0, 3, "新しい練習相手に…\nって冗談だよ冗談！\n本気じゃないから！", 1, 100102, 0, 1);
