INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (48, 7, 0, 3, "お腹が減ると\nなんか力が\n出ないんだよね…", 1, 100102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (49, 8, 0, 3, "色んなことを\n知っておくのは\n大事だよね！", 1, 100102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (51, 1, 0, 1, "お片付けも済んだし\n今度は洗濯ものを\nたたもうかな", 1, 100201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (52, 2, 0, 1, "この気持ち\nもっとちゃんと\n伝えなくっちゃ…", 1, 100201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (53, 3, 0, 1, "お茶をいれようと\n思うんだけど\n他に飲みたい人は\nいるかな？", 1, 100201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (54, 4, 0, 1, "戦いは\n得意じゃないけど\nお料理なら…！", 1, 100201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (55, 5, 0, 1, "こういう平和な\n時間が、もっと\n続くといいな", 1, 100201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (56, 101, 0, 1, "わたし…\nもうちょっとだけ勇気\n出してみようかな", 4, 100201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (57, 102, 0, 1, "みんなのことが大好き\nも、もちろん、\n騎士クンのことも…\nな、仲間として！", 8, 100201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (58, 1, 0, 2, "ひゃうっ！\nな、なに？\nびっくりしたぁ…", 1, 100201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (59, 2, 0, 2, "男の子って、\nやっぱりおっきいね。\nその…手とか\n他にもいろいろ…", 1, 100201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (60, 3, 0, 2, "困ったことが\nあったら\nいつでも話してね", 1, 100201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (61, 4, 0, 2, "え！　む、虫っ！？\n取って取って！\nはやく～！", 1, 100201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (62, 101, 0, 2, "あのね…\n一緒におでかけ\nしたいんだけど…\nダメかな？", 4, 100201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (63, 102, 0, 2, "騎士クンに\n伝えたいことがあるの\nとっても大切な…\nこ、今度、話すね！", 8, 100201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (64, 1, 0, 2, "お、おはよう！\nあの…朝ごはんは\nもう食べちゃった？", 1, 100201, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (65, 2, 0, 2, "お昼休みだね\nあの…良かったら\nお弁当…食べない？", 1, 100201, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (66, 3, 0, 2, "遅くまでお疲れ様\n明日も頑張ろうね\nふわぁ…", 1, 100201, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (67, 1, 0, 3, "よし…\nこれなら\n褒めてもらえるかな？", 1, 100201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (68, 2, 0, 3, "がんばることも\n必要だけど、\nお休みも大切だよね", 1, 100201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (69, 3, 0, 3, "かわいいなぁ…\nわたしも今度\n作ってみよう。\nえっと、材料は…", 1, 100201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (70, 4, 0, 3, "戦う前にもこうやって\n落ち着けたら\nいいのに", 1, 100201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (71, 5, 0, 3, "…はへっ？\nも、もしかして今\n寝ちゃってた…？", 1, 100201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (72, 6, 0, 3, "騎士クンと一緒に\n冒険するなら\n体力がないと…！", 1, 100201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (73, 7, 0, 3, "おいしいものって\n食べ過ぎちゃうけど…\nしょうがない…よね？", 1, 100201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (74, 8, 0, 3, "…そっか\nこうすれば魔法が\nもっと強くなるんだ", 1, 100201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (76, 1, 0, 1, "洗濯と掃除\nどっちから\nはじめよう？", 1, 100202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (77, 2, 0, 1, "新しい魔道書を\n買いに行かなくちゃ\nもっとたくさん\n魔法を覚えたいな", 1, 100202, 0, 1);
