INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (82, 102, 0, 1, "騎士クンのそばに\nいられるだけで\n幸せだよ", 8, 100202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (83, 1, 0, 2, "えっと、その…\nこんな所で\nこういうことされると\n恥ずかしいよ…", 1, 100202, 0, 1);
