INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (86, 4, 0, 2, "男の子って\nやっぱりおっきいね\nその…手とか\n他にもいろいろ…", 1, 100202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (87, 101, 0, 2, "騎士クンに\n伝えたいことがあるの\nとっても大切な…\nこ、今度、話すね！", 8, 100202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (88, 102, 0, 2, "ふふふっ…そんなとこ\nツンツンしないで\nくすぐったいよ～", 8, 100202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (89, 1, 0, 2, "おはよう\nよく眠れた？　今日も\n一日、頑張ろうね♪", 1, 100202, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (90, 2, 0, 2, "お腹空いちゃった…\nそうだ。騎士クンも\n一緒にお昼食べない？", 1, 100202, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (91, 3, 0, 2, "もうこんな時間…\nそろそろ寝なくちゃ\nおやすみなさい", 1, 100202, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (92, 1, 0, 3, "よし…\nこれなら\n褒めてもらえるかな？", 1, 100202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (93, 2, 0, 3, "がんばることも\n必要だけど\nお休みも大切だよね", 1, 100202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (94, 3, 0, 3, "かわいい～♪\n騎士クンは\nこういうのって好き？", 1, 100202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (95, 4, 0, 3, "戦う前にもこうやって\n落ち着けたら\nいいのに", 1, 100202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (96, 5, 0, 3, "あの…！\nよ、良かったら\n一緒に…！", 1, 100202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (97, 6, 0, 3, "騎士クンと一緒に\n冒険するなら\n体力がないと…！", 1, 100202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (98, 7, 0, 3, "最近服のサイズが\n合わないんだけど…\n食べ過ぎなのかな？", 1, 100202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (99, 8, 0, 3, "…やっぱり\n月刊恋愛指南は\nためになる…", 1, 100202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (101, 1, 0, 1, "装備の手入れでも\nしようかな…", 1, 100301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (102, 2, 0, 1, "次に鍛えるべきは\n肉体面か精神面か\nどちらにしよう…？", 1, 100301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (103, 3, 0, 1, "ツムギが作ってくれた\nこの服…一体\nいつサイズを…？", 1, 100301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (104, 4, 0, 1, "仲間なんていらない\nそう思ってた頃も\nあったな…", 1, 100301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (105, 5, 0, 1, "考えごとをしていると\nつい部屋中を\n歩いてしまうな", 1, 100301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (106, 101, 0, 1, "彼は\nどこまで強くなるのか\n私も負けられないな", 4, 100301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (107, 102, 0, 1, "私が一本を取られる\nなんてね…願い事は\nもう決まったのかな？", 8, 100301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (108, 1, 0, 2, "ああ、キミだったのか\n人の気配がしたから\n誰かと思ったよ", 1, 100301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (109, 2, 0, 2, "斬りかかるつもりなら\nちゃんとかかってきて\n…呼んだだけ？", 1, 100301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (110, 3, 0, 2, "なに？　私に相談？\n戦いについてなら\n喜んで", 1, 100301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (111, 4, 0, 2, "今のが突き？\n軽い！　もっと鋭く！\nさあ、もう一回！", 1, 100301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (112, 101, 0, 2, "ひゃっ！　ど、どこを\n触ってるの！？\n…油断した", 4, 100301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (113, 102, 0, 2, "キミにはこれからも\n私のそばに…\nいや、なんでもない", 8, 100301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (114, 1, 0, 2, "おはよう\n今日も充実した\n一日にしていこう", 1, 100301, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (115, 2, 0, 2, "そろそろ\nお茶の時間か\nキミも一緒にどう？", 1, 100301, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (116, 3, 0, 2, "遅くまで大変だね\nまだ仕事があるなら\n手伝おうか？", 1, 100301, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (117, 1, 0, 3, "戦いを経てもなお\n美しくあるように…\nこれでよし♪", 1, 100301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (118, 2, 0, 3, "戦ってばかりで\n休まないと、また\nユイに怒られるな", 1, 100301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (119, 3, 0, 3, "ヒヨリみたいに\n練習相手に…\nいや、やめておこう", 1, 100301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (120, 4, 0, 3, "これはいい…\n戦いの前の精神統一に\nもってこいじゃないか", 1, 100301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (121, 5, 0, 3, "時々は落ち着いた\n時間を過ごすのも\n悪くない…", 1, 100301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (122, 6, 0, 3, "彼に負けないよう\n私も更に強くなろう\n共に戦うために！", 1, 100301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (123, 7, 0, 3, "食事は大切だけど\n食べ過ぎには\n注意しておこう…", 1, 100301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (124, 8, 0, 3, "ユイ…！\nこ、こんな本を\n読んでいたのか…！", 1, 100301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (126, 1, 0, 1, "海だけじゃなくて\n川でも釣りがしたいな\n道具も新調しよう", 1, 100302, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (127, 2, 0, 1, "ツムギが作ってくれた\nこの服…一体\nいつサイズを…？", 1, 100302, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (128, 3, 0, 1, "彼は\nどこまで強くなるのか\n私も負けられないな", 1, 100302, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (129, 4, 0, 1, "そろそろ修行の\n内容を新しくしよう\nより厳しいものにね", 1, 100302, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (130, 5, 0, 1, "強くなって…誰にも\n妨げられない自由な\n生き方がしたい", 1, 100302, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (131, 101, 0, 1, "まさか風邪を引くとは\n彼の看病のおかげで\nすっかり良く\nなったけど", 8, 100302, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (132, 102, 0, 1, "私が一本を取られる\nなんてね…願い事は\nもう決まったのかな？", 8, 100302, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (133, 1, 0, 2, "どうしたの？\nなんでもない？\nそれなら良かった", 1, 100302, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (134, 2, 0, 2, "あんまり突っつくと\nお返しするよ？\n覚悟は出来てる？", 1, 100302, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (135, 3, 0, 2, "ねぇ、今度また\n釣りに行かない？\n大きいのを狙おう", 1, 100302, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (136, 4, 0, 2, "修行がしたいの？\nそれを早く言ってよ\nすぐに出発しよう！", 1, 100302, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (137, 101, 0, 2, "ひょっとして\n今のも一本だなんて\n言わないよね？", 8, 100302, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (138, 102, 0, 2, "キミにはこれからも\n私のそばに…\nいや、なんでもない", 8, 100302, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (139, 1, 0, 2, "今日は仕事？\nそれとも修行？\n良ければ付き合うよ", 1, 100302, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (140, 2, 0, 2, "休息も時には\n必要だと、仲間から\n教わったよ", 1, 100302, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (141, 3, 0, 2, "遅くまで大変だね\nまだ仕事があるなら\n手伝おうか？", 1, 100302, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (142, 1, 0, 3, "髪の長さを少し\n変えてみようかな…\nわ、私は何を…！", 1, 100302, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (143, 2, 0, 3, "休息があってこそ\n万全な状態で\n戦えるんだ", 1, 100302, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (144, 3, 0, 3, "ヒヨリみたいに\n練習相手に…\nいや、やめておこう", 1, 100302, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (145, 4, 0, 3, "体と同様に\n心もほぐれるね…\nはぁ…", 1, 100302, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (146, 5, 0, 3, "休む時は休む\nヒヨリもそろそろ\n覚えて欲しいな…", 1, 100302, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (147, 6, 0, 3, "彼に負けないよう\n私も更に強くなろう\n共に戦うために！", 1, 100302, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (148, 7, 0, 3, "修行のせいか\n最近食べる量が\n増えてきたような…", 1, 100302, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (149, 8, 0, 3, "ユイ…！\nこ、こんな本を\n読んでいたのか…！", 1, 100302, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (151, 1, 0, 1, "お宝の予感！\n部屋中を\n探検しちゃうぞ～", 1, 100401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (152, 2, 0, 1, "落とし穴…どこに\n作ったんだっけ？", 1, 100401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (153, 3, 0, 1, "うえ～ん！\n…嘘泣きしたら\n相手は油断するかな", 1, 100401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (154, 4, 0, 1, "ハニートラップって\nすごく甘いお菓子で\nイタズラするのかな？", 1, 100401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (155, 5, 0, 1, "かくれんぼする人\nこの指、と～まれ！", 1, 100401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (156, 101, 0, 1, "ミソギ以外の人に\nイタズラされちゃ\nダメだよ！", 4, 100401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (157, 102, 0, 1, "ミソギと遊ぶ？\nミソギに遊ばれる？", 8, 100401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (158, 1, 0, 2, "いいこと思いついた！\nい、イタズラとかじゃ\nないよ～", 1, 100401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (159, 2, 0, 2, "うわっ！？\n　にいちゃんが、\nミソギにイタズラ？", 1, 100401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (160, 3, 0, 2, "にいちゃんの\n通りそうなところに\nバナナの皮を…", 1, 100401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (161, 4, 0, 2, "にいちゃんのオデコと\nホッペと鼻と目と耳に\n落書きを…", 1, 100401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (162, 101, 0, 2, "ここ、ミソギ探検隊の\n基地にしようよ！", 4, 100401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (163, 102, 0, 2, "だ～れだ…って？\nにいちゃんの声くらい\nわかるよ！\n大好きだもん♪", 8, 100401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (164, 1, 0, 2, "にいちゃん、おはよ！\n目覚まし時計を\n止めておいたのに、\nよく起きられたね～♪", 1, 100401, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (165, 2, 0, 2, "にいちゃんが\n昼寝したら、\nイタズラし放題♪", 1, 100401, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (166, 3, 0, 2, "夜の探検も\nおもしろそう！\nねぇ出かけちゃダメ？", 1, 100401, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (167, 1, 0, 3, "イタズラには\nお色気も必要…\nうん、完璧！", 1, 100401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (168, 2, 0, 3, "座ったら、ブーッって\nいう音が出たら、\nおもしろいな～♪", 1, 100401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (169, 3, 0, 3, "かわいい！\nミソギ探検隊の\nマスコットにしよう♪", 1, 100401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (170, 4, 0, 3, "吸うとくしゃみが\n止まらなくなるお香を\nここに…くしゅっ！", 1, 100401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (171, 5, 0, 3, "にひひ…イタズラの\n証拠、ここで\n燃やしちゃおう～！", 1, 100401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (172, 6, 0, 3, "探検家は\n体力勝負だよー！", 1, 100401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (173, 7, 0, 3, "みんなに分けて\nあげようかな～\n激辛ソースも\n入れちゃうけどね！", 1, 100401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (174, 8, 0, 3, "こういう所に\n宝の地図が\n隠れてるんだよね～", 1, 100401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (176, 1, 0, 1, "お宝の予感！\n部屋中を\n探検しちゃうぞ～", 1, 100402, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (177, 2, 0, 1, "うぅ、探検に出たい！\nじっとしてなんか\nいられないよ～！", 1, 100402, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (178, 3, 0, 1, "うえ～ん！\n…嘘泣きしたら\n相手は油断するかな", 1, 100402, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (179, 4, 0, 1, "ハニートラップって\nすごく甘いお菓子で\nイタズラするのかな？", 1, 100402, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (180, 5, 0, 1, "かくれんぼする人\nこの指、と～まれ！", 1, 100402, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (181, 101, 0, 1, "にいちゃんのオデコと\nホッペと鼻と目と耳に\n落書きを…", 8, 100402, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (182, 102, 0, 1, "にいちゃんが喜ぶ\nイタズラ、何かない\nかな～？", 8, 100402, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (183, 1, 0, 2, "ミソギがしかけた\nイタズラ\n見つけられるかな？", 1, 100402, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (184, 2, 0, 2, "探検する？\n冒険する？\nそれとも…\nイタズラされる？", 1, 100402, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (185, 3, 0, 2, "将来の夢は探検家！\n探したい物はないけど", 1, 100402, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (186, 4, 0, 2, "にいちゃんに宿題！\n落とし穴を10個\n掘ること！", 1, 100402, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (187, 101, 0, 2, "だ～れだ…って？\nにいちゃんの声くらい\nわかるよ！\n大好きだもん♪", 8, 100402, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (188, 102, 0, 2, "ここ、ミソギ探検隊の\n基地にしようよ！", 8, 100402, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (189, 1, 0, 2, "にいちゃん\n探検に行こうよ！\n今からなら遭難しても\n夜には帰れるよ！", 1, 100402, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (190, 2, 0, 2, "にいちゃんが昼寝したら\nイタズラし放題♪", 1, 100402, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (191, 3, 0, 2, "夜の探検も\nおもしろそう！\nねぇ出かけちゃダメ？", 1, 100402, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (192, 1, 0, 3, "イタズラには\nお色気も必要…\nうん、完璧！", 1, 100402, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (193, 2, 0, 3, "ゆっくりまったりして\n次のイタズラを\n考えよ～！", 1, 100402, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (194, 3, 0, 3, "かわいい！\nミソギ探検隊の\nマスコットにしよう♪", 1, 100402, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (195, 4, 0, 3, "吸うとくしゃみが\n止まらなくなるお香を\nここに…くしゅっ！", 1, 100402, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (196, 5, 0, 3, "にひひ…イタズラの\n証拠、ここで\n燃やしちゃおう～！", 1, 100402, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (197, 6, 0, 3, "探検家は\n体力勝負だよー！", 1, 100402, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (198, 7, 0, 3, "探検のおやつに\nぴったりかも～！", 1, 100402, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (199, 8, 0, 3, "こういう所に\n宝の地図が\n隠れてるんだよね～", 1, 100402, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (201, 1, 0, 1, "音楽は全部好きだけど\n一番好きなのは\nやっぱりジャズかな", 1, 100601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (202, 2, 0, 1, "はぁ、はぁ…\n一人でもいっぱい\nしないと…\n特訓…", 1, 100601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (203, 3, 0, 1, "お姉ちゃんには\nもっと自信を\n持ってほしいなぁ", 1, 100601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (204, 4, 0, 1, "お姉ちゃんのマネ\nしちゃおうかな？", 1, 100601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (205, 5, 0, 1, "双子って、\n好みのタイプも\n似ちゃうのかな？", 1, 100601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (206, 101, 0, 1, "お兄ちゃんと私は\n相性抜群です♪", 4, 100601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (207, 102, 0, 1, "もう待ちきれない…\nお兄ちゃん、\n早く帰って来て！", 8, 100601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (208, 1, 0, 2, "あんっ…着替えよう\nと思ったのに\nお兄ちゃんのエッチ！", 1, 100601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (209, 2, 0, 2, "うふふ、お兄ちゃん！\n今日は何を\nするんですか？", 1, 100601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (210, 3, 0, 2, "そんなところ\n触っちゃダメ♪", 1, 100601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (211, 4, 0, 2, "お兄ちゃん、イジワル\nしないでください…", 1, 100601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (212, 101, 0, 2, "お兄ちゃん、もっと\n上級編のテクニック、\n教えてくださいね！", 4, 100601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (213, 102, 0, 2, "コーチ、\n今日も特訓\nお願いします！", 8, 100601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (214, 1, 0, 2, "おはようございます\n昨夜は楽しかった\nですね、うふふ♪", 1, 100601, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (215, 2, 0, 2, "こんな明るい時間から\nす、するんですか…\n二人っきりの特訓…", 1, 100601, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (216, 3, 0, 2, "夜はこれからですよ\nお兄ちゃん♪", 1, 100601, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (217, 1, 0, 3, "世界一、かわいく\nなるんだから♪", 1, 100601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (218, 2, 0, 3, "気持ちいい♪\nもう立てないよ～", 1, 100601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (219, 3, 0, 3, "かわいい♪\nアカリのライバル\nだね", 1, 100601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (220, 4, 0, 3, "はぁ…気持ちいい、\n私、このままダメに\nなっちゃいそう…", 1, 100601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (221, 5, 0, 3, "はぁ…アカリ、\nとろけちゃう…", 1, 100601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (222, 6, 0, 3, "はぁ…はぁはぁ、\n私…もう…ダメ\n限界です…！", 1, 100601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (223, 7, 0, 3, "わぁ、おいしい～\nけど食べ過ぎは美容に\nよくないですよね", 1, 100601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (224, 8, 0, 3, "この楽譜、\nサックスで吹いて\nみたいなぁ♪", 1, 100601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (226, 1, 0, 1, "このごろ一人でいると\nお兄ちゃんのこと\nばかり考えちゃう…", 1, 100602, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (227, 2, 0, 1, "はぁ、はぁ…もっと\nいっぱいしなくちゃ\n…練習", 1, 100602, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (228, 3, 0, 1, "このお部屋、なんだか\n落ち着きます…\nお兄ちゃんが\nいるから？", 1, 100602, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (229, 4, 0, 1, "みんなかわいいな～\n世界一かわいくなる\nのは、難しいな…", 1, 100602, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (230, 5, 0, 1, "双子って、\n好みのタイプも\n似ちゃうのかな？", 1, 100602, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (231, 101, 0, 1, "いつか世界でいちばん\nかわいくなって\nお兄ちゃんと…", 8, 100602, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (232, 102, 0, 1, "もう待ちきれない…\nお兄ちゃん、\n早く帰って来て！", 8, 100602, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (233, 1, 0, 2, "あんっ…着替えよう\nと思ったのに\nお兄ちゃんのエッチ！", 1, 100602, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (234, 2, 0, 2, "二人きりの秘密の特訓\n今度はいつですか？\n待ち遠しいです…", 1, 100602, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (235, 3, 0, 2, "え？　ここでするの？\nみんなの前で恥ずか\nしいな…筋トレ", 1, 100602, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (236, 4, 0, 2, "お兄ちゃん、イジワル\nしないでください…", 1, 100602, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (237, 101, 0, 2, "お兄ちゃんといると\nわ、私…ドキドキ\nしてきちゃう…", 8, 100602, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (238, 102, 0, 2, "コーチ、\n今日も特訓\nお願いします！", 8, 100602, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (239, 1, 0, 2, "おはようございます\n昨夜は楽しかった\nですね、うふふ♪", 1, 100602, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (240, 2, 0, 2, "お兄ちゃんに\n食べてもらいたいなぁ\n手作りのお昼ごはん", 1, 100602, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (241, 3, 0, 2, "お風呂入ってきますね\nそのあとは…\n二人でい～っぱい\n遊びましょうね", 1, 100602, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (242, 1, 0, 3, "お姉ちゃんのマネ\nしちゃおうかな？", 1, 100602, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (243, 2, 0, 3, "気持ちいい♪\nもう立てないよ～", 1, 100602, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (244, 3, 0, 3, "かわいい♪\nアカリのライバル\nだね", 1, 100602, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (245, 4, 0, 3, "はぁ…気持ちいい、\n私、このままダメに\nなっちゃいそう…", 1, 100602, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (246, 5, 0, 3, "あったか～い…\nまるでお兄ちゃんが\n隣にいる時みたい", 1, 100602, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (247, 6, 0, 3, "汗をかくと\n気持ちいいですよね", 1, 100602, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (248, 7, 0, 3, "お兄ちゃんの特訓に\n付き合うために\nたっぷり\n食べておかないと♪", 1, 100602, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (249, 8, 0, 3, "この楽譜\nサックスで吹いて\nみたいなぁ♪", 1, 100602, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (251, 1, 0, 1, "プリン、プリン、\nプリン、プリン、\nプリン、プリン～♪", 1, 100701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (252, 2, 0, 1, "う～ら～め～し～や～\nなの～♪", 1, 100701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (253, 3, 0, 1, "プリンがないなら\n死んだほうがマシなの\nあ、もう死んでたの～", 1, 100701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (254, 4, 0, 1, "幽霊の正体見たり、\nミヤコなの～", 1, 100701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (255, 5, 0, 1, "ミヤコがいきなり\n消えたら、みんな\n驚くかもなの～♪", 1, 100701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (256, 101, 0, 1, "アイツがいて、\nプリンもある。\nきっとここが天国なの", 4, 100701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (257, 102, 0, 1, "アイツはプリンセス\nナイトじゃなくて\nプリンナイトなの～", 8, 100701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (258, 1, 0, 2, "姿を消して、オマエを\n見張ってやるの～\nむふふ～♪", 1, 100701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (259, 2, 0, 2, "もし成仏した先に\nプリンがなかったら\n地獄なの～…", 1, 100701, 0, 1);
