INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (305, 5, 0, 1, "うわっ昨日より可愛く\nなってる。明日は\nどうなっちゃう\nのかな？", 1, 100801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (306, 101, 0, 1, "このボクがいるだけで\nあの人への\nおもてなしだよね", 4, 100801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (307, 102, 0, 1, "ボクの可愛さは、\nふふ…あの人の\nためのものさ", 8, 100801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (308, 1, 0, 2, "ねぇ～、ボクって\n可愛い？\nふふ、知ってる☆", 1, 100801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (309, 2, 0, 2, "呼んだ？\n可愛いボクと話したい\n気持ちはわかるよ", 1, 100801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (310, 3, 0, 2, "ふふ、ボクに触ると\nキミも美しく\nなっちゃうかもよ？", 1, 100801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (311, 4, 0, 2, "どうかな、\n今日のボク。\n美しい？　可愛い？\nねえ、どっち？", 1, 100801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (312, 101, 0, 2, "あの人も今、\n可愛いボクのこと\n考えてるかな…", 4, 100801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (313, 102, 0, 2, "美しい…\nキミの瞳に写るボクは\nより一層きれいだ", 8, 100801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (314, 1, 0, 2, "美しいボクの\n美しい一日が\n始まるよ！", 1, 100801, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (315, 2, 0, 2, "さーて、午後からも\n可愛く美しく\n華麗に優雅に♪", 1, 100801, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (316, 3, 0, 2, "バラの花びら\nいっぱい浮かべた\nお風呂に入ってから\n寝ようっと♪", 1, 100801, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (317, 1, 0, 3, "鏡よ、鏡。この世で\n一番可愛いボクに\nお似合いの人はだれ？", 1, 100801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (318, 2, 0, 3, "ボクが座ったソファは\n「美しいソファ」に\nランクアップした！", 1, 100801, 0, 1);
