INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (321, 5, 0, 3, "気安く触ったら\n火傷する…\n炎はボクに似ているね", 1, 100801, 0, 1);
