INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (341, 3, 0, 2, "バラの花びらいっぱい\n浮かべたお風呂に\n入ってから\n寝ようっと♪", 1, 100802, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (342, 1, 0, 3, "鏡よ、鏡。この世で\n一番可愛いボクに\nお似合いの人はだれ？", 1, 100802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (343, 2, 0, 3, "ボクが座ったソファは\n「美しいソファ」に\nランクアップした！", 1, 100802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (344, 3, 0, 3, "可愛いものを\n可愛いって言える\nボクって可愛いよね！", 1, 100802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (345, 4, 0, 3, "服を羽織るように\n香りをまとうボク…\n美しい、美しすぎる！", 1, 100802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (346, 5, 0, 3, "冷えは美容の大敵\nなんだから！\nここ、ボクの場所ね！", 1, 100802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (347, 6, 0, 3, "運動は苦手なのに\n運動するボクの姿は\n可愛いなんて…罪♪", 1, 100802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (348, 7, 0, 3, "ボクが食べると\n食べ物まで\n可愛いなあ…", 1, 100802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (349, 8, 0, 3, "ボクが触った瞬間に\nこの本は美を\n携えてしまうんだ", 1, 100802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (351, 1, 0, 1, "「第１０部２０章\n終焉戦争、開幕」\n…これでよし！", 1, 100901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (352, 2, 0, 1, "今度シグルドに\n私の魔法を一つ\n伝授してやるか", 1, 100901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (353, 3, 0, 1, "ここでは風が\n吹いてこないな…\n換気したい！", 1, 100901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (354, 4, 0, 1, "そうだ、ナナカに\nこの間借りた本を\n返さないと…", 1, 100901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (355, 5, 0, 1, "まずは包帯をとって\n手から龍の形の\n炎を出して…", 1, 100901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (356, 101, 0, 1, "シグルド…\n今頃何処で何を\nしているのだろう…", 4, 100901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (357, 102, 0, 1, "最近、彼の姿が\nまぶしく見える…\nどうして…？", 8, 100901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (358, 1, 0, 2, "辞書を知らないか？\n…探している\n理由は聞くな", 1, 100901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (359, 2, 0, 2, "そうだ！\n貴様の技にも\n名前をつけてやろう！", 1, 100901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (360, 3, 0, 2, "私に触れても力を\n奪われないのは\n貴様くらいだぞ？", 1, 100901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (361, 4, 0, 2, "何事だ？\nまさか我が瞳に\n魅入られたのか？", 1, 100901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (362, 101, 0, 2, "貴様がそばにいると\nとても安心できる…\n…不思議だ", 4, 100901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (363, 102, 0, 2, "い、いきなり目の前に\n現れるな！　ドキドキ\nするだろうがぁ！", 8, 100901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (364, 1, 0, 2, "「覚醒」めたか…\n行くぞ、悪を討つ\n戦いに出発だ！", 1, 100901, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (365, 2, 0, 2, "昼間は「ヤツら」も\n襲ってはこない…\n束の間の休息だ", 1, 100901, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (366, 3, 0, 2, "どうした？　夜はまだ\nこれから…ふぁ…\nね、眠くなどないぞ！", 1, 100901, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (367, 1, 0, 3, "何か足りないな…\n暗黒騎士の紋章を\n作ろうかな", 1, 100901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (368, 2, 0, 3, "くっ…！\n私を眠りの国へ\n誘うつもりか！", 1, 100901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (369, 3, 0, 3, "このようなものに\n懐柔されるものか！\n…されるものか！", 1, 100901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (370, 4, 0, 3, "これを使えば\nもっとそれっぽい\n雰囲気が出そうだ", 1, 100901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (371, 5, 0, 3, "これが我らの求めた\n安寧か…\n悪くない…", 1, 100901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (372, 6, 0, 3, "この程度\n前世での激闘に\n比べれば…！", 1, 100901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (373, 7, 0, 3, "最近金欠でな…\nいやぁありがたい…", 1, 100901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (374, 8, 0, 3, "ここにも\n前世の記憶が\n眠っていたとは…", 1, 100901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (376, 1, 0, 1, "「第１３部１章\n閃光のシグルド」\n…ついに登場だな", 1, 100902, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (377, 2, 0, 1, "何故だ…何故\nこうもあっさり金欠に\n逆戻りするのだ！？", 1, 100902, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (378, 3, 0, 1, "やはり転生者という\n設定はカッコイイ…\n天才だな、私！", 1, 100902, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (379, 4, 0, 1, "そうだ、ナナカに\nこの間借りた本を\n返さないと…", 1, 100902, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (380, 5, 0, 1, "そろそろ今日の分の\n魔力を溜めないと\nまた魔力切れが…", 1, 100902, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (381, 101, 0, 1, "最近奴がそばに\nいるだけで、鼓動が\n早くなるのは何故だ？", 1, 100902, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (382, 102, 0, 1, "シ、シグルド…！\nだめだ…妄想でも\n上手く言えない…", 8, 100902, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (383, 1, 0, 2, "なんだ？　新たな\n賞金の情報を\n持ってきたのか？", 8, 100902, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (384, 2, 0, 2, "名乗りやポーズは\nその強度で相手を\n圧倒する技術だ", 1, 100902, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (385, 3, 0, 2, "いいか、魔法の\n詠唱中は絶対に\n声をかけるなよ？", 1, 100902, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (386, 4, 0, 2, "おお、ちょうど今\n貴様専用の設定集を\n作成中だぞ！", 1, 100902, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (387, 101, 0, 2, "貴様がそばにいると\nとても安心できる…\n…不思議だ", 1, 100902, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (388, 102, 0, 2, "い、いきなり目の前に\n現れるな！　ドキドキ\nするだろうがぁ！", 8, 100902, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (389, 1, 0, 2, "夜更かしのせいか\nまだ眠気が…いや！\nこれは組織の罠だ！", 8, 100902, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (390, 2, 0, 2, "平穏な時に\n出来るだけ魔力を\n溜めておかねば…", 1, 100902, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (391, 3, 0, 2, "夜になると、力が\n増した気分になるな\n…なりませんか？", 1, 100902, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (392, 1, 0, 3, "何か足りないな…\n暗黒騎士の紋章を\n作ろうかな", 1, 100902, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (393, 2, 0, 3, "お、おのれ\nヒュプノス…\n私は負けないぞ…", 1, 100902, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (394, 3, 0, 3, "くっくっくっ…\n貴様は特別団員に\nしてやろう", 1, 100902, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (395, 4, 0, 3, "これを使えば\nもっとそれっぽい\n雰囲気が出そうだ", 1, 100902, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (396, 5, 0, 3, "これが我らの求めた\n安寧か…\n悪くない…", 1, 100902, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (397, 6, 0, 3, "新たな戦いに備えて\n魔力だけでなく\n体力も鍛えねば！", 1, 100902, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (398, 7, 0, 3, "食事は魔力に\n変換する体力の\n元になるのだ！", 1, 100902, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (399, 8, 0, 3, "フフフ…\nこれを覚えれば\nもっと強く…！", 1, 100902, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (401, 1, 0, 1, "王子はんほんまに\nはんなりしてて、\n王子はんって感じやわ", 1, 101001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (402, 2, 0, 1, "この絵本の王子はん、\n王子はんみたいで\nええなぁ♪", 1, 101001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (403, 3, 0, 1, "うちがガラスの靴を\n落としたら、王子はん\n見つけてくれるやろか", 1, 101001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (404, 4, 0, 1, "マホマホ王国\n次期国王の王子はん\n…見てみたいわ～", 1, 101001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (405, 5, 0, 1, "王子はんのために\n糸を紡いで、\n編み物でもしまひょか", 1, 101001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (406, 101, 0, 1, "マホ姫と王子はん、\n絵本の世界より\nベストカップルや", 4, 101001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (407, 102, 0, 1, "王子はんと\n一緒にいると\n胸がせわしのー\nうごいてかなん", 8, 101001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (408, 1, 0, 2, "うちら並んでたら\nお雛様みたいに\n見えると思いまへん？", 1, 101001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (409, 2, 0, 2, "王子はんを\n国に連れて帰ったら\nみんな大歓迎して\nくれるやろなぁ", 1, 101001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (410, 3, 0, 2, "王子はんは、\n絵本から出てきた人\nみたいやね～", 1, 101001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (411, 4, 0, 2, "うちの話し方\nのんびりしてます\nやろか？", 1, 101001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (412, 101, 0, 2, "マホ姫の手は\nつべたいから、\n王子はんが温めて？", 4, 101001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (413, 102, 0, 2, "うちがもし悪い魔法に\nかかったら、王子はん\n口づけしてくれはる？", 8, 101001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (414, 1, 0, 2, "朝は小鳥はんも\nおなかがすいてるから\nよう飛び回りはるねえ", 1, 101001, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (415, 2, 0, 2, "お茶請けはお大福が\nよろしおすけど\nクッキーもええなあ", 1, 101001, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (416, 3, 0, 2, "うち、普段から\n夢見がちやけど…\nほんに眠い時間やわぁ", 1, 101001, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (417, 1, 0, 3, "王子はんに\n会いに行くなら、\nおめかしせな\nあかんなぁ", 1, 101001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (418, 2, 0, 3, "はんなりした\nええお椅子やわぁ\nほっこりしますなあ", 1, 101001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (419, 3, 0, 3, "かいらしもんが\nいっぱいで、\nマホマホ王国みたい\nやわぁ", 1, 101001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (420, 4, 0, 3, "心地ええどす。まるで\nお花畑にいるようやね", 1, 101001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (421, 5, 0, 3, "ぽかぽかどす。\n心まであったかく\nなりますわぁ", 1, 101001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (422, 6, 0, 3, "はぁはぁ、お姫はんは\n激しい運動は\nしぃひんもんどす～", 1, 101001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (423, 7, 0, 3, "もぐもぐ…あんまり\n速う食べられへんけど\n美味しいわぁ", 1, 101001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (424, 8, 0, 3, "絵本、絵本～\n幸せやわぁ♪", 1, 101001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (426, 1, 0, 1, "王子はんにとって\n自慢の姫になるえ～", 1, 101002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (427, 2, 0, 1, "カボチャの馬車に\nガラスの靴…\n欲しいなぁ", 1, 101002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (428, 3, 0, 1, "うちがガラスの靴を\n落としたら、王子はん\n見つけてくれるやろか", 1, 101002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (429, 4, 0, 1, "マホマホ王国\n次期国王の王子はん\n…見てみたいわ～", 1, 101002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (430, 5, 0, 1, "ほんまなぁ\nこの絵本が面白いんよ\n王子はんに\n読んであげよ", 1, 101002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (431, 101, 0, 1, "マホ姫と王子はん\n絵本の世界より\nベストカップルや", 8, 101002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (432, 102, 0, 1, "王子はんと\n一緒にいると\n胸がせわしのー\nうごいてかなん", 8, 101002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (433, 1, 0, 2, "たまには、王子はんの\n好きな絵本も\n知りたいな～", 1, 101002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (434, 2, 0, 2, "王子はんを\n国に連れて帰ったら\nみんな大歓迎して\nくれるやろなぁ", 1, 101002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (435, 3, 0, 2, "そのうち、王子はんの\nお国にも連れてって\nもらえますやろか？", 1, 101002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (436, 4, 0, 2, "絵本読むんも\nええなぁ…", 1, 101002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (437, 101, 0, 2, "うちがもし悪い魔法に\nかかったら、王子はん\n口づけしてくれはる？", 8, 101002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (438, 102, 0, 2, "うちら並んでたら\nお雛様みたいに\n見えると思いまへん？", 8, 101002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (439, 1, 0, 2, "朝は小鳥はんも\nおなかがすいてるから\nよう飛び回りはるねえ", 1, 101002, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (440, 2, 0, 2, "マホマホ王国では\nこの時分は\nみんなお昼寝\nしてたんよ", 1, 101002, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (441, 3, 0, 2, "夜に飛んだり鳴いたり\nする鳥はんもおるんよ\n昼は寝てはるんかねぇ", 1, 101002, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (442, 1, 0, 3, "王子はんに\n会いに行くなら\nおめかしせな\nあかんなぁ", 1, 101002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (443, 2, 0, 3, "王子はんも一緒に\nゆっくりしていっても\nええんよ？", 1, 101002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (444, 3, 0, 3, "かいらしい子やねぇ\nうちとお友だちに\nなってくれるん？", 1, 101002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (445, 4, 0, 3, "癒しアイテムって\nほんま、好っきゃわぁ", 1, 101002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (446, 5, 0, 3, "うちのお城にも\nほしいわぁ。帰ったら\nさっそく準備せな\nあかんなぁ", 1, 101002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (447, 6, 0, 3, "部屋の中で運動\nできるのも、なかなか\n面白いもんやわぁ", 1, 101002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (448, 7, 0, 3, "おいひいわ～\nマホマホ王国には\nない味やわぁ", 1, 101002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (449, 8, 0, 3, "しっかり知識を\n身につけんと\n魔法も使えへん\nからなぁ", 1, 101002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (451, 1, 0, 1, "１、２、３、４…\nあれ？　いま、\n何段目まで\n編んだっけ？", 1, 101101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (452, 2, 0, 1, "おやつのケーキは\n苺かチョコ…\nどっちも食べよう！", 1, 101101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (453, 3, 0, 1, "い、いけない！\n縫い針落としちゃった", 1, 101101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (454, 4, 0, 1, "ケーキをクレープで\n巻いたら…\n大ヒット商品の予感！", 1, 101101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (455, 5, 0, 1, "「どうだ！妹暮らし」\nってことわざ\nなかったっけ？", 1, 101101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (456, 101, 0, 1, "お兄ちゃんに、\nお洋服でも\n作ろうかな？", 4, 101101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (457, 102, 0, 1, "ここがお兄ちゃんの\n部屋…\nドキドキしちゃう～", 8, 101101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (458, 1, 0, 2, "ねぇ、おにいちゃん\n一緒に、スイーツでも\n食べにいかない？", 1, 101101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (459, 2, 0, 2, "クリームついてた！？\nさっき食べた、\nケーキかも…！", 1, 101101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (460, 3, 0, 2, "勉強、見てくれるん\nですか？　やった～♪", 1, 101101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (461, 4, 0, 2, "服がほつれてますよ？\n直してあげます！", 1, 101101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (462, 101, 0, 2, "私をお嫁さんに\nしたいなら、予約は\nお早目に、ですよ♪", 4, 101101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (463, 102, 0, 2, "ねぇねぇお兄ちゃん！\n…えへへ、\n呼んでみただけ♪", 8, 101101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (464, 1, 0, 2, "今日も１日\n勉強がんばります！\n…少しは遊ぼうかな", 1, 101101, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (465, 2, 0, 2, "お昼休憩に\n甘いものが\n食べたいです～", 1, 101101, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (466, 3, 0, 2, "ねむいです～\nでももうちょっと\n勉強しないと～…", 1, 101101, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (467, 1, 0, 3, "この服、かわいい♪\n今度作って\nみよっと～！", 1, 101101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (468, 2, 0, 3, "勉強で疲れたから\n休憩しよ～っと", 1, 101101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (469, 3, 0, 3, "かわいい♪\n私が服を作って\nあげよっか？", 1, 101101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (470, 4, 0, 3, "リラックスできます～\nお兄ちゃんが隣に\nいるみたい！", 1, 101101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (471, 5, 0, 3, "のんびりしたら、\nまた勉強しよっと！", 1, 101101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (472, 6, 0, 3, "運動すると勉強も\nはかどるよね！", 1, 101101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (473, 7, 0, 3, "おいし～！\nお兄ちゃんにも\nおすそ上げしますね！", 1, 101101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (474, 8, 0, 3, "お兄ちゃんの記憶を\n戻す方法、どこかに\n書いてないかな？", 1, 101101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (476, 1, 0, 1, "ケーキの食べ過ぎで\nお腹が…うぅ、\n因果横暴だよー…", 1, 101102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (477, 2, 0, 1, "１、２、３、４…\nあれ？　いま、\n何段目まで\n編んだっけ？", 1, 101102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (478, 3, 0, 1, "い、いけない！\n縫い針落としちゃった", 1, 101102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (479, 4, 0, 1, "笑う時には服着ます！", 1, 101102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (480, 5, 0, 1, "「どうだ！妹暮らし」\nってことわざ\nなかったっけ？", 1, 101102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (481, 101, 0, 1, "お兄ちゃんに、\nお洋服でも\n作ろうかな？", 8, 101102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (482, 102, 0, 1, "お兄ちゃんが帰って\nきたら、何して遊ぼう\nかな～？", 8, 101102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (483, 1, 0, 2, "勉強、見てくれるん\nですか？　やった～♪", 1, 101102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (484, 2, 0, 2, "クリームついてた！？\nさっき食べた、\nケーキかも…！", 1, 101102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (485, 3, 0, 2, "おにいちゃん！\nそろそろ、おやつに\nしませんか？", 1, 101102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (486, 4, 0, 2, "勉強は…\n明日にしようっと", 1, 101102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (487, 101, 0, 2, "妹だけど、大人な\nレディですからね！", 8, 101102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (488, 102, 0, 2, "な、ナデナデとか\n恥ずかしいな～", 8, 101102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (489, 1, 0, 2, "今日も１日\n勉強がんばります！\n…少しは遊ぼうかな", 1, 101102, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (490, 2, 0, 2, "お昼休憩に\n甘いものが\n食べたいです～", 1, 101102, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (491, 3, 0, 2, "お兄ちゃんは\n夜強いんですね\n私は眠くて…", 1, 101102, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (492, 1, 0, 3, "服装や髪形が変わると\n気分も変わるよね", 1, 101102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (493, 2, 0, 3, "ここで寝ちゃったら\n受験勉強が～…\nぐーー", 1, 101102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (494, 3, 0, 3, "かわいい♪\n私が服を作って\nあげよっか？", 1, 101102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (495, 4, 0, 3, "いまならどんな試験も\n合格できそうです！", 1, 101102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (496, 5, 0, 3, "のんびりしたら\nまた勉強しよっと！", 1, 101102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (497, 6, 0, 3, "お兄ちゃんを\n守るためには\n毎日、鍛えなくちゃ！", 1, 101102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (498, 7, 0, 3, "おいし～！\nお兄ちゃんにも\nおすそ上げしますね！", 1, 101102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (499, 8, 0, 3, "この本の服\nかわいいなぁ♪\n私に作れるかな？", 1, 101102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (501, 1, 0, 1, "う～ん、今なら\nどこでだって\n眠れちゃいそう…", 1, 101201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (502, 2, 0, 1, "たくさん寝すぎて、\n夢の中か現実か\nわかんなく\nなっちゃうんだよね～", 1, 101201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (503, 3, 0, 1, "…はっ！？\nね、寝てないよ？\nホントだよっ", 1, 101201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (504, 4, 0, 1, "ぐっすり眠って\nパワーを\nフルチャージっ☆", 1, 101201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (505, 5, 0, 1, "もし、超能力のことが\nバレたら…ブルブル", 1, 101201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (506, 101, 0, 1, "テレパシーで\nキミの心を覗けたら\n…なんてね！", 4, 101201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (507, 102, 0, 1, "キミを抱き枕に…\nじょ、冗談冗談！", 8, 101201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (508, 1, 0, 2, "ん～、なになに？\n一緒にお昼寝する？", 1, 101201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (509, 2, 0, 2, "忘れ物しちゃった。\nま、いっか！\nえ、ダメ？", 1, 101201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (510, 3, 0, 2, "おおっ、相談ごと？\nなにかな\nなにかな～？", 1, 101201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (511, 4, 0, 2, "う～ん、もしかして\nキミにも仲良しになる\n超能力があるのかも！", 1, 101201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (512, 101, 0, 2, "むむ～…どう？\nテレパシーで、私の\n気持ちを送ったよ！", 4, 101201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (513, 102, 0, 2, "えっ？　私とキミとの\n相性？　むむ～…\nうん、バッチリだよ☆", 8, 101201, 0, 1);
