INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (599, 8, 0, 3, "全然読めない～\nそだ、ヒデサイに\n読んでもらおっと！", 1, 101602, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (601, 1, 0, 1, "やっさー\n踊ってると、どんどん\n楽しくなるさー！", 1, 101701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (602, 2, 0, 1, "このお菓子、でーじ\nおいしいねー！\nキミも食べる？", 1, 101701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (603, 3, 0, 1, "ハイターイ！　元気な\nあいさつが一番さ～！", 1, 101701, 0, 1);
