INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (605, 5, 0, 1, "さぁ、みんなで歌って\n踊るさ～♪", 1, 101701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (606, 101, 0, 1, "毎日、最高に幸せさ～\nキミのおかげだねー", 4, 101701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (607, 102, 0, 1, "キミへの気持ちを\n踊りで表現\nしてみるさ～", 8, 101701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (608, 1, 0, 2, "海でひと泳ぎ\nしたいさ～\nキミもどう？", 1, 101701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (609, 2, 0, 2, "あぎひゃっ！\n急に声かけたら、\nビックリするさ～", 1, 101701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (610, 3, 0, 2, "踊りなら、\n私が手とり足とり\n教えてあげるさ～！", 1, 101701, 0, 1);
