INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (662, 101, 0, 2, "今読んでるのは\nヒロインが年下の子と\n恋に落ちる話…あ、\n深い意味はないのよ～", 4, 101801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (663, 102, 0, 2, "生徒は全員可愛いけど\n君は少し特別…\nみんなには内緒よ？", 8, 101801, 0, 1);
