INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (683, 1, 0, 2, "あん！　見ちゃダメよ\n明日のテストの問題を\n作ってるとこなの！", 1, 101802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (684, 2, 0, 2, "勉強勉強で\n疲れたよね？\n膝枕してあげる！", 1, 101802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (685, 3, 0, 2, "宿題はもうやった？\n苦手なところは\n先生が見てあげる♪", 1, 101802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (686, 4, 0, 2, "んん？　なんだか\n元気がないみたい\n先生がぎゅぅ～って\nしてあげる…　", 1, 101802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (687, 101, 0, 2, "君のお勉強も\nだいぶ進んできたね～\n補習卒業も近いかな？\n少し寂しいけど…", 8, 101802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (688, 102, 0, 2, "テストがんばったね～\nご褒美に先生が\n特別な授業\nしてあげるね？", 8, 101802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (689, 1, 0, 2, "朝の特別授業\nしてあげましょうか？", 1, 101802, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (690, 2, 0, 2, "午後の授業って\n眠いでしょう？\n気持ちは分かるけど\nがんばってね♪", 1, 101802, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (691, 3, 0, 2, "明日の予習はやった？\n先生も手伝うから\n寝る前にやりましょ♪", 1, 101802, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (692, 1, 0, 3, "もしかして…\n少し露出多いかしら？", 1, 101802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (693, 2, 0, 3, "私の膝枕と、どっちが\nフカフカかしら？", 1, 101802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (694, 3, 0, 3, "可愛いわね～\n教室に置いたら\n生徒が喜ぶかしら？　", 1, 101802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (695, 4, 0, 3, "は～落ち着くなぁ～\n教師には\nこういうゆとりも\n必要よね♪　", 1, 101802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (696, 5, 0, 3, "まだ寒いわぁ\n誰か隣に\n来てくれたら…　", 1, 101802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (697, 6, 0, 3, "運動部の顧問も\nできるかしら？　", 1, 101802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (698, 7, 0, 3, "あん！\nお胸にこぼしちゃった", 1, 101802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (699, 8, 0, 3, "あぁーん♪\nこういう物語みたいな\n恋愛あこがれる～　", 1, 101802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (701, 1, 0, 1, "おにいちゃんは、\nにんじん食べられる？", 1, 102001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (702, 2, 0, 1, "えへへ、\nお絵かきしよ～♪", 1, 102001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (703, 3, 0, 1, "あのね、ミミと\nあそんでほしいな～", 1, 102001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (704, 4, 0, 1, "さっきね、きれいな\nお花見つけたよ。\nおにいちゃんにも\n見せてあげたいな～", 1, 102001, 0, 1);
