INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (708, 1, 0, 2, "ふわぁ！\nおにいちゃんの手、\nおっきいね～", 1, 102001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (709, 2, 0, 2, "やった～！\nおにいちゃんが\nほめてくれた～", 1, 102001, 0, 1);
