INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (714, 1, 0, 2, "おひさまさん、\nおはよ～！\n毎朝、早おきだね～", 1, 102001, 1, 1);
