INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (739, 1, 0, 2, "おひさまさん\nおはよ～！\n毎朝、早おきだね～", 1, 102002, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (740, 2, 0, 2, "お昼寝お昼寝～♪\nおにいちゃんと一緒に\nお昼寝～♪", 1, 102002, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (741, 3, 0, 2, "眠くなっちゃった～\nおにいちゃん！\n今日は一緒に寝よ～", 1, 102002, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (742, 1, 0, 3, "ミミも\nうさぎさんみたいに\nかわいくなりたいな", 1, 102002, 0, 1);
