INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (749, 8, 0, 3, "ミミね！\nこのお話だーい好き！", 1, 102002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (751, 1, 0, 1, "はぅぅ…\n部屋のどこにいたら\nいいんだろう…", 1, 102101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (752, 2, 0, 1, "お外より\nお部屋の中の方が\n落ち着くな…", 1, 102101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (753, 3, 0, 1, "クッキー焼いたら\nみんな食べてくれる\nかなぁ…", 1, 102101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (754, 4, 0, 1, "あ、あめんぼあかいな\nあいうえお！", 1, 102101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (755, 5, 0, 1, "お芝居には\n人間観察が大事…\nじーーーーーー…", 1, 102101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (756, 101, 0, 1, "お兄ちゃんを見ると、\n顔が赤くなっちゃうの\n…な、なんでかな？", 4, 102101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (757, 102, 0, 1, "れ、恋愛劇…\nお、お兄ちゃんと\n練習…", 8, 102101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (758, 1, 0, 2, "こ、この本、とっても\nおもしろいんですよ♪", 1, 102101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (759, 2, 0, 2, "い、一緒に遊んで\nくれるんですか？\nや、やったぁ～♪", 1, 102101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (760, 3, 0, 2, "ランドソルには\n慣れましたか？\nわ、私はまだ…", 1, 102101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (761, 4, 0, 2, "お、お兄ちゃん、\nお、お話の練習に\n付き合って欲しいです", 1, 102101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (762, 101, 0, 2, "リボンがかわいい？\nほ、ほぇぇぇ～～\nは、恥ずかしいですぅ", 4, 102101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (763, 102, 0, 2, "お兄ちゃん…♪\nえへへ、呼んでみた\nだけです…♪", 8, 102101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (764, 1, 0, 2, "今日は一日、\n恥ずかしがらずに\n過ごせるかなあ…？", 1, 102101, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (765, 2, 0, 2, "お昼ご飯、どうぞ♪\nわ、私、お料理、\nうまくなったかな？", 1, 102101, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (766, 3, 0, 2, "おにいちゃんが\n帰ってくるまで、\nがんばって\n起きてました", 1, 102101, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (767, 1, 0, 3, "可愛い女の子に変身！\n出来ますように…♪", 1, 102101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (768, 2, 0, 3, "ふかふかで\nあったかくて…\nお兄ちゃんみたい♪", 1, 102101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (769, 3, 0, 3, "この子を相手に、\n演技の練習～", 1, 102101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (770, 4, 0, 3, "リボンや服に\n染み込ませておこう。\nいつでもいい匂い♪", 1, 102101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (771, 5, 0, 3, "えへへ。\nぽっかぽかです…", 1, 102101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (772, 6, 0, 3, "はぁはぁ…\nふひぃ…\nはうぅぅぅ～～", 1, 102101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (773, 7, 0, 3, "もぐもぐ…えへへ、\n美味しいものを\n食べてると幸せです", 1, 102101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (774, 8, 0, 3, "戯曲を読んで\n自分が演じるところを\n想像するの…", 1, 102101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (776, 1, 0, 1, "かわいいものを\nこうして並べて…\nはぁ…落ちつくぅ♪", 1, 102102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (777, 2, 0, 1, "ふぁ～、この部屋は\n時間がゆったりと\n流れてるみたい", 1, 102102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (778, 3, 0, 1, "この台本、感動…\n涙が止まらないよぉ\nいつか演じてみたいな", 1, 102102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (779, 4, 0, 1, "お芝居には\n人間観察が大事…\nじーーーーーー…", 1, 102102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (780, 5, 0, 1, "あえいうえおあお\nかけきくけ、かっ…\nむ、難しいですぅぅ", 1, 102102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (781, 101, 0, 1, "お、お腹が空いた時は\n言ってくださいね\nが、頑張って\n作ります！", 8, 102102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (782, 102, 0, 1, "れ、恋愛劇…\nお、お兄ちゃんと\n練習…", 8, 102102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (783, 1, 0, 2, "こ、この本、とっても\nおもしろいんですよ♪", 1, 102102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (784, 2, 0, 2, "お兄ちゃんと\nいっぱい話せた日は、\n嬉しくなっちゃいます", 1, 102102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (785, 3, 0, 2, "何か私にできる事\n…そうだ！\nお掃除しますね！", 1, 102102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (786, 4, 0, 2, "私、ちょっとずつ、\n自分に自信が\nついてきたかもです…", 1, 102102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (787, 101, 0, 2, "リボンがかわいい？\nほ、ほぇぇぇ～～\nは、恥ずかしいですぅ", 8, 102102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (788, 102, 0, 2, "お兄ちゃん…♪\nえへへ、呼んでみた\nだけです…♪", 8, 102102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (789, 1, 0, 2, "おはようございます♪\n声出しして、お芝居の\n練習しなくちゃ", 1, 102102, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (790, 2, 0, 2, "お茶にしますか？\nクッキー焼いたので、\nよかったら…", 1, 102102, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (791, 3, 0, 2, "寝る前に\n台本を読むと、\n覚えやすいんです", 1, 102102, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (792, 1, 0, 3, "可愛い女の子に変身！\n出来ますように…♪", 1, 102102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (793, 2, 0, 3, "はぅ…\nフッカフカァ…\n癒されちゃいます…", 1, 102102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (794, 3, 0, 3, "この子を相手に\n演技の練習～", 1, 102102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (795, 4, 0, 3, "リボンや服に\n染み込ませておこう\nいつでもいい匂い♪", 1, 102102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (796, 5, 0, 3, "えへへ\nぽっかぽかです…", 1, 102102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (797, 6, 0, 3, "え、演劇には\nた、体力も重要\nだから～", 1, 102102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (798, 7, 0, 3, "残さず\n食べますね…！", 1, 102102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (799, 8, 0, 3, "戯曲を読んで\n自分が演じるところを\n想像するの…", 1, 102102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (801, 1, 0, 1, "ねえ、人見知りを\n治すための練習に\n付き合ってくれない？", 1, 102201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (802, 2, 0, 1, "アカリみたいに\nかわいくなるには、\nどうすればいいのかな", 1, 102201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (803, 3, 0, 1, "レアカードをゲット！\nねえねえ、\nすごいでしょ！？", 1, 102201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (804, 4, 0, 1, "ア、アイツと同じ\n部屋にいるのよね…\nって、だから何よ！？", 1, 102201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (805, 5, 0, 1, "楽しいゲームって\nやめどきが\n見つからないわ", 1, 102201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (806, 101, 0, 1, "もっと女の子らしく\nなったらアイツも\n嬉しいかな…って、\n何考えてるの！？", 4, 102201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (807, 102, 0, 1, "うぅ…最近、\nアイツを見るとなんか\nドキドキするのよね", 8, 102201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (808, 1, 0, 2, "ゲームに負けた罰\nだからって、\nくすぐるのは…\nあはは…ダメだって！", 1, 102201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (809, 2, 0, 2, "アンタと話すのは割と\n緊張しないのよね\n…なんでかな", 1, 102201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (810, 3, 0, 2, "待ちきれない…\n新作ゲームの発売日が\n待ちきれない…", 1, 102201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (811, 4, 0, 2, "アンタは人見知り\nしないタチよね？\nいいなあ～…", 1, 102201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (812, 101, 0, 2, "ア、アンタってさ\n好きな人とかい…\nなな、何でもない！", 4, 102201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (813, 102, 0, 2, "アンタと…そ、その\nこうして話してるのも\nわわ、悪くないわね！", 8, 102201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (814, 1, 0, 2, "おおおおはよう！\n…よし！　ちゃんと\nあいさつできたわ！", 1, 102201, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (815, 2, 0, 2, "一緒にごはんとか…\nううん、やっぱいい！\nなんでもないからっ！", 1, 102201, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (816, 3, 0, 2, "夜はこれからよ！\n朝まで\nゲームするからね！", 1, 102201, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (817, 1, 0, 3, "アカリみたいに\n可愛くなるのは\n難しいかな", 1, 102201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (818, 2, 0, 3, "寝っ転がりながら\nゲームができれば\n気分が良さそう", 1, 102201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (819, 3, 0, 3, "可愛い…けど、\n私には似合わないかな", 1, 102201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (820, 4, 0, 3, "はあ、リラックスして\nゲームができるわ", 1, 102201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (821, 5, 0, 3, "ん…暖かくて、\n寝落ちしちゃいそう", 1, 102201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (822, 6, 0, 3, "ゲーム感覚でやると、\n体動かすのも\n楽しいわね", 1, 102201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (823, 7, 0, 3, "ゲームは頭使うから\nそのぶん\n栄養がいるのよ", 1, 102201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (824, 8, 0, 3, "ふむふむ、なるほど\n友だちを作るには\n笑顔が大事っと…", 1, 102201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (826, 1, 0, 1, "さあ、今日は\n何のゲームで\n遊ぼうかな？", 1, 102202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (827, 2, 0, 1, "一緒にゲームができる\n人がいるっていうのも\n新鮮だな…", 1, 102202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (828, 3, 0, 1, "レアカードをゲット！\nすごいでしょ！？", 1, 102202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (829, 4, 0, 1, "アカリってどうやって\n友達作ってるんだろ？\nコツがあるのかな…", 1, 102202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (830, 5, 0, 1, "楽しいゲームって\nやめどきが\n見つからないわ", 1, 102202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (831, 101, 0, 1, "うぅ…最近\nアイツを見るとなんか\nドキドキするのよね", 8, 102202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (832, 102, 0, 1, "ね、ねえ\nもう少しその…\n一緒にゲームしない？", 8, 102202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (833, 1, 0, 2, "ねえ、人見知りを\n治すための練習に\n付き合ってくれない？", 1, 102202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (834, 2, 0, 2, "欲しいゲームが\n見つからなくて…\n探すの手伝って！", 1, 102202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (835, 3, 0, 2, "ねえ、見て！\n新しいゲームを\n買って来たの！", 1, 102202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (836, 4, 0, 2, "ゲームに負けた罰\nだからって、くすぐる\nのは…あ、あはは！\nだ、ダメだって！", 1, 102202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (837, 101, 0, 2, "ア、アンタってさ\n好きな人とかい…\nなな、何でもない！", 8, 102202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (838, 102, 0, 2, "あ、あのさ…\nもう少し、そこに\nいてくれてもいいわよ", 8, 102202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (839, 1, 0, 2, "おおおおはよう！\n…よし！　ちゃんと\nあいさつできたわ！", 1, 102202, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (840, 2, 0, 2, "一緒にごはんとか…\nううん、やっぱいい！\nなんでもないからっ！", 1, 102202, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (841, 3, 0, 2, "夜はこれからよ！\n朝まで\nゲームするからね！", 1, 102202, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (842, 1, 0, 3, "もっと女の子らしく\nなったらアイツも\n嬉しいかな…って、\n何考えてるの！？", 1, 102202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (843, 2, 0, 3, "ふああ…\n徹夜明けには\n辛い誘惑だな…", 1, 102202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (844, 3, 0, 3, "可愛い…けど、\n私には似合わないかな", 1, 102202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (845, 4, 0, 3, "こういうのに詳しいと\n女の子っぽく\n見えたりするのかな", 1, 102202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (846, 5, 0, 3, "寒い日は\nここでゲームするのが\n最高のぜいたくよね", 1, 102202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (847, 6, 0, 3, "最近ゲームばかりだし\nたまにはいいかな？", 1, 102202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (848, 7, 0, 3, "ゲームは頭使うから\nそのぶん\n栄養がいるのよ", 1, 102202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (849, 8, 0, 3, "ふむふむ、なるほど\n友だちを作るには\n笑顔が大事っと…", 1, 102202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (851, 1, 0, 1, "じゃんけんぽん！\nあっち向いてほい！\nあ～ん、負けたぁ\n　『勝ったぜ！』", 1, 102301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (852, 2, 0, 1, "ぷうきちがいれば、\nなんでも出来ちゃうよ", 1, 102301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (853, 3, 0, 1, "発声練習しよっと\nぷうきち\nフルスイ～ング！", 1, 102301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (854, 4, 0, 1, "ここで、ぷうきち\nフルスイングは、\nまずいかな～？", 1, 102301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (855, 5, 0, 1, "この部屋、ぷうきちを\n置く場所はないのかな", 1, 102301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (856, 101, 0, 1, "お兄ちゃんって…\nちょっとぷうきちに\n似てるかも♪", 4, 102301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (857, 102, 0, 1, "お兄ちゃんにも、\nぷうきちのこと\n好きになって\nもらいたいな～", 8, 102301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (858, 1, 0, 2, "おやつは、\n私とぷうきちの\n二人分用意してね♪", 1, 102301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (859, 2, 0, 2, "ぷうきちが、お腹\nペコペコだって～\n何かちょうだい！", 1, 102301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (860, 3, 0, 2, "『アヤネに手出したら\n…坊ちゃんでも、\n分かってんな？』", 1, 102301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (861, 4, 0, 2, "ぷうきちとの\n連携を磨いてるの！\nもっと仲良くなるよ！", 1, 102301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (862, 101, 0, 2, "特別だからね？\nもっと触っていいよ\n…ぷうきちのこと", 4, 102301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (863, 102, 0, 2, "お兄ちゃんのこと、\n「ぷう兄」って\n呼ぼうかな？", 8, 102301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (864, 1, 0, 2, "お兄ちゃんって\n朝早いんだね～\n私はまだ眠いよ～", 1, 102301, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (865, 2, 0, 2, "ふぁ～…ぷうきちと\nお昼寝してくるね…", 1, 102301, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (866, 3, 0, 2, "ぷうきちと\nおしゃべりしてたら\nもうこんな時間…", 1, 102301, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (868, 2, 0, 3, "『座り心地がいいな』\nぷうきちは座れない\nでしょ？", 1, 102301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (869, 3, 0, 3, "かわいい～！\nぷうきちのお友達に\nしちゃおうかな？", 1, 102301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (870, 4, 0, 3, "いいニオイ～♪\nぷうきちも\n気に入ったみたい！", 1, 102301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (871, 5, 0, 3, "ぽわぽわする～\nぷうきちも\n気持ちいい？", 1, 102301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (873, 7, 0, 3, "おいしい～\nぷうきちも\n食べてみる？", 1, 102301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (874, 8, 0, 3, "ぷうきち！\nこの本、読んで～！", 1, 102301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (901, 1, 0, 1, "どうして失敗ばっかり\nこうなったら\n腹を切るしか～！", 1, 102501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (902, 2, 0, 1, "あれ？　あれ？\nお部屋の中で迷子に\nなっちゃいました～", 1, 102501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (903, 3, 0, 1, "どうしたら転ばなく\nなるんでしょうか？", 1, 102501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (904, 4, 0, 1, "わわわわ～～っ\nふう…\nきゃ、きゃあっ！", 1, 102501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (905, 5, 0, 1, "私のドジで、\nこのお部屋を\n壊しちゃったら\nどうしましょう～～", 1, 102501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (906, 101, 0, 1, "おいしい料理で\nお出迎えしなくちゃ！", 4, 102501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (907, 102, 0, 1, "一生懸命\nご奉仕しますよ～♪", 8, 102501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (908, 1, 0, 2, "耳かきしましょうか？\nえ？　こわい？", 1, 102501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (909, 2, 0, 2, "お疲れでしたら、\nお昼寝しませんか？\n膝枕もしますよ？", 1, 102501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (910, 3, 0, 2, "おかえりなさいませ\nご主人さま♪", 1, 102501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (911, 4, 0, 2, "すみません\nすみません\nすみません～～", 1, 102501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (912, 101, 0, 2, "メイドの私が、\nこんな気持ちを持って\nいいんでしょうか…", 4, 102501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (913, 102, 0, 2, "私のこと、\n専属メイドと思って\n頼ってくださいね", 8, 102501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (914, 1, 0, 2, "おはようございます♪\n今日も一日がんば…\nきゃ、きゃあ！", 1, 102501, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (915, 2, 0, 2, "お昼ごはん…\n失敗しちゃいましたぁ\nお、おやつでリベンジします！", 1, 102501, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (916, 3, 0, 2, "明日の目標は\nドジをしないです！\nき、昨日の目標も\nドジをしないでした", 1, 102501, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (917, 1, 0, 3, "あ、メイド服が\nほつれてる\n後でお裁縫しなくちゃ", 1, 102501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (918, 2, 0, 3, "休憩が終わったら\nお洗濯にお料理に\nお掃除もしないと！", 1, 102501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (919, 3, 0, 3, "かわいい♪\nあとでお洗濯\nしてあげますね", 1, 102501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (920, 4, 0, 3, "いい香り…\nお嬢様のお部屋にも\n置いてあげたいです", 1, 102501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (921, 5, 0, 3, "ここでお洗濯ものを\n乾かしましょうか", 1, 102501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (922, 6, 0, 3, "あわわわっ…\nこ、転びそうです～！", 1, 102501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (923, 7, 0, 3, "これくらい\nおいしいものを\n作ってみたいです～", 1, 102501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (924, 8, 0, 3, "物語のメイドさんって\n御主人さまを好きに\nなるんですよね…", 1, 102501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (926, 1, 0, 1, "失敗したら、この\nお部屋追い出され\nちゃうかも～？", 1, 102502, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (927, 2, 0, 1, "あれ？　あれ？\nお部屋の中で迷子に\nなっちゃいました～", 1, 102502, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (928, 3, 0, 1, "どうやったら、立派な\nメイドになれるのかな", 1, 102502, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (929, 4, 0, 1, "塩にはＳマーク\n砂糖にはＳマーク\n…あ、あれ！？", 1, 102502, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (930, 5, 0, 1, "ご用があれば\nなんでも言って\nくださいね", 1, 102502, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (931, 101, 0, 1, "迷惑をかけすぎて…\n嫌われちゃったら\nどうしましょう", 8, 102502, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (932, 102, 0, 1, "メイドの私が\nこんな気持ちを持って\nいいんでしょうか…　", 8, 102502, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (933, 1, 0, 2, "おでかけですか？\nいってらっしゃいませ\nご主人さま♪", 1, 102502, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (934, 2, 0, 2, "どうして失敗ばっかり\nこうなったら\n腹を切るしか～！", 1, 102502, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (935, 3, 0, 2, "私、またなにか\nやっちゃいましたか？", 1, 102502, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (936, 4, 0, 2, "おいしいお茶っ葉が\n手に入りました～♪\n一緒に飲みませんか？", 1, 102502, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (937, 101, 0, 2, "転んだふりであの人に\n…って！\nなんでもないですよ！", 8, 102502, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (938, 102, 0, 2, "私、できることなら\nなんでもやりますよ\nあなたのためですから", 8, 102502, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (939, 1, 0, 2, "おはようございます♪\n今日も一日がんば…\nう、うわあ！", 1, 102502, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (940, 2, 0, 2, "お昼ごはん…\n失敗しちゃいましたぁ\nお、おやつでリベンジします！", 1, 102502, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (941, 3, 0, 2, "明日の目標は\nドジをしないです！\nき、昨日の目標も\nドジをしないでした", 1, 102502, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (942, 1, 0, 3, "あ、メイド服が\nほつれてる\n後でお裁縫しなくちゃ", 1, 102502, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (943, 2, 0, 3, "いい座り心地です～\nこれならお嬢さまも\nゆったりできますね♪", 1, 102502, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (944, 3, 0, 3, "かわいい♪\nあとでお洗濯\nしてあげますね", 1, 102502, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (945, 4, 0, 3, "いい香り…\nお嬢様のお部屋にも\n置いてあげたいです", 1, 102502, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (946, 5, 0, 3, "暖かいですけど\n…私が火に近づいて\n大丈夫でしょうか！？", 1, 102502, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (947, 6, 0, 3, "運動しようとすると\nいつも怪我するん\nですよね…", 1, 102502, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (948, 7, 0, 3, "こんなに美味しいもの\nメイドの私には\nもったいないです～！", 1, 102502, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (949, 8, 0, 3, "物語のメイドさんって\n御主人さまを好きに\nなるんですよね…", 1, 102502, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (951, 1, 0, 1, "あの方と私だけの\n部屋にしたいですわ", 1, 102701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (952, 2, 0, 1, "あの方は私のもの…\n眼も耳も鼻も口も髪も\n首も胸も腰も手も脚も\nぬくもりも心も…！", 1, 102701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (953, 3, 0, 1, "運命とは決して\n抗えないもの…\nクスクス…クスクス", 1, 102701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (954, 4, 0, 1, "部屋からあの方の\n香りが…\nああ、至福ですわ", 1, 102701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (955, 5, 0, 1, "毒にも薬にもならない\n存在なんて嫌ですわ\nどうせならとびきりの\n毒でありたい…", 1, 102701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (956, 101, 0, 1, "クスクス…\nあの方とデートする\nイメトレ中ですわ\nクスクスクスクス…", 4, 102701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (957, 102, 0, 1, "記憶を戻す研究より\n記憶をねつ造する研究\nのほうが良いかしら？\n伴侶という設定で…", 8, 102701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (958, 1, 0, 2, "運命を信じますか？\nもちろん絶対当然\n信じますわよね？", 1, 102701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (959, 2, 0, 2, "この薬は起床時就寝前\n食前食後食間に、毎日\n服用してください。\n惚れ薬ですわ", 1, 102701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (960, 3, 0, 2, "お願いがあるのです。\n新しい研究の実験台に\n…", 1, 102701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (961, 4, 0, 2, "こっそり栽培している\n毒きのこが毒々しく\n育ちましたわ…", 1, 102701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (962, 101, 0, 2, "あなた様の前では\n普通の女の子…\n少し毒薬づくりが\n得意なだけの…", 4, 102701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (963, 102, 0, 2, "私の身も心も、全てが\nあなた様のもの。\nあなた様の全ても…", 8, 102701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (964, 1, 0, 2, "おはようございます\n一晩中、あなた様の\n寝顔を見ていましたわ", 1, 102701, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (965, 2, 0, 2, "お昼を用意しましたの\n何も入れていませんわ\nクスクス…", 1, 102701, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (966, 3, 0, 2, "あなた様が眠るまで\nお傍で見守りますわ。\n…あの、どうして\n眠らないのですか？", 1, 102701, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (967, 1, 0, 3, "いっそ鏡の中に\n閉じ込めてしまえたら\n私だけのものに…", 1, 102701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (968, 2, 0, 3, "はっ！\nリラックスしたら\n閃きましたわ！", 1, 102701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (969, 3, 0, 3, "あら、キメラの素体に\nぴったりですわ", 1, 102701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (970, 4, 0, 3, "このお部屋は\n良い香りがしますわ。\nはっ、あなた様の匂い\nなのでは…！？", 1, 102701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (971, 5, 0, 3, "あたたかい…\n加熱調合にも\n使えそうですわね", 1, 102701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (972, 6, 0, 3, "この運動エネルギー\n何かにうまく\n利用できないかしら？", 1, 102701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (973, 7, 0, 3, "美味しい。あの方にも\n差し上げましょう…\n薬を入れて…", 1, 102701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (974, 8, 0, 3, "あの方を虜にする\n方法がどこかに\n載ってるはずですわ", 1, 102701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (976, 1, 0, 1, "あの方と私だけの\n部屋にしたいですわ", 1, 102702, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (977, 2, 0, 1, "こっそり栽培している\n毒きのこが毒々しく\n育ちましたわ…", 1, 102702, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (978, 3, 0, 1, "新しい薬\nどうやってあの方に\n飲ませようかしら", 1, 102702, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (979, 4, 0, 1, "部屋からあの方の\n香りが…\nああ、至福ですわ", 1, 102702, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (980, 5, 0, 1, "記憶を戻す研究より\n記憶をねつ造する研究\nのほうが良いかしら？\n伴侶という設定で…", 1, 102702, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (981, 101, 0, 1, "クスクス…\nあの方とデートする\nイメトレ中ですわ\nクスクスクスクス…", 8, 102702, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (982, 102, 0, 1, "魔法も科学も錬金術も\n全て駆使して\nあの方のために…", 8, 102702, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (983, 1, 0, 2, "この部屋には雌犬…\n失礼、女の方がとても\n沢山いますのね", 1, 102702, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (984, 2, 0, 2, "あなた様には私の伴侶\nとして高い教養を…\nあ、逃げられてしまい\nましたわ…！", 1, 102702, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (985, 3, 0, 2, "お願いがあるのです\n新しい研究の\n実験台に…", 1, 102702, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (986, 4, 0, 2, "あなた様が寝ている時\n私は…クスクス\nこれ以上は言えません", 1, 102702, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (987, 101, 0, 2, "想像の中のあなた様は\nすっかり私の虜なのに\n現実は…\nもどかしいですわね", 8, 102702, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (988, 102, 0, 2, "あなた様の前では\n普通の女の子…\n少し毒薬づくりが\n得意なだけの…", 8, 102702, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (989, 1, 0, 2, "おはようございます\n一晩中、あなた様の\n寝顔を見ていましたわ", 1, 102702, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (990, 2, 0, 2, "さあ、お食事ですよ\n味は保証しますわ\n特製ソースで少しだけ\n体が痺れますけど…", 1, 102702, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (991, 3, 0, 2, "あなた様が眠るまで\nお傍で見守りますわ\n…あの、どうして\n眠らないのですか？", 1, 102702, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (992, 1, 0, 3, "私ももう少し\n外見に気を配る\nべきでしょうか…", 1, 102702, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (993, 2, 0, 3, "はっ！\nリラックスしたら\n閃きましたわ！", 1, 102702, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (994, 3, 0, 3, "この私を差し置いて\nあなた様に媚びるなど\n言語道断…っ！", 1, 102702, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (995, 4, 0, 3, "このお部屋は\n良い香りがしますわ\nはっ、あなた様の匂い\nなのでは…！？", 1, 102702, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (996, 5, 0, 3, "素敵なぬくもり…\nまるであの方に\n抱きしめられたような", 1, 102702, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (997, 6, 0, 3, "この運動エネルギー\n何かにうまく\n利用できないかしら？", 1, 102702, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (998, 7, 0, 3, "美味しい。あの方にも\n差し上げましょう…\n薬を入れて…", 1, 102702, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (999, 8, 0, 3, "あの方を虜にする\n方法がどこかに\n載ってるはずですわ", 1, 102702, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1001, 1, 0, 1, "子供たちの遊びって\nあたしが子供の頃と\nあまり変わらないのね", 1, 102801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1002, 2, 0, 1, "使えるものは\n再利用しないと\n…あ、これとか\nまだ使えるわね", 1, 102801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1003, 3, 0, 1, "贅沢は敵！　とはいえ\n気持ちに余裕のない\n暮らしはダメよね", 1, 102801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1004, 4, 0, 1, "お腹空いたわね、\n残り物で\n何か作ろうかしら", 1, 102801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1005, 5, 0, 1, "商売はノンゼロサムが\n理想だなぁ。誰も損を\nしないってことだもの", 1, 102801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1006, 101, 0, 1, "あんたが手伝って\nくれるから、仕事も\n前より楽しいのよね♪", 4, 102801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1007, 102, 0, 1, "焼きそばパンが\n食べたいな～\nあいつと…", 8, 102801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1008, 1, 0, 2, "ねぇあんた、どこか\n安いお店知らない？", 1, 102801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1009, 2, 0, 2, "あたし、エレガントに\n振る舞えてる？\n…そう？　嬉しい♪", 1, 102801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1010, 3, 0, 2, "いっぱい働いて遊んで\nよく休んでまた働く。\nそれが健全な生活よね", 1, 102801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1011, 4, 0, 2, "一緒に部屋の\n整頓でもする？\nハマると楽しいわよ♪", 1, 102801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1012, 101, 0, 2, "あんたがいると\n子供たちも喜ぶわ♪\nもちろんあたしもね！", 4, 102801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1013, 102, 0, 2, "ね、あんた今、幸せ？\n何びっくりしてるのよ\n大事なことじゃないの", 8, 102801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1014, 1, 0, 2, "面倒な用事は午前中に\n片づけてしまうのが、\n時間の使い方のコツよ", 1, 102801, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1015, 2, 0, 2, "午後のティータイム\nとかどう？\n良いカフェがあるの♪", 1, 102801, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1016, 3, 0, 2, "さ、エレガントな夜を\n楽しみましょ。\nモダンな音楽でも\nかけましょうか？", 1, 102801, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1017, 1, 0, 3, "レディたるもの\n身嗜みはしっかりとね\n見本にならないと♪", 1, 102801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1018, 2, 0, 3, "これで仕事は一段落ね\nちょっと休憩…", 1, 102801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1019, 3, 0, 3, "うふふ、\nかわいいものを\n見るのは幸せね～", 1, 102801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1020, 4, 0, 3, "部屋中がいい香り…\nなんだかすごい贅沢\nしてる気分♪", 1, 102801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1021, 5, 0, 3, "風邪を引かないように\n身体を温めなくちゃ", 1, 102801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1022, 6, 0, 3, "さっ！　家の中でも\n効率的に\nトレーニングよ！", 1, 102801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1023, 7, 0, 3, "もぐもぐ…はっ、\n口の周り、\n汚れてないわよね？", 1, 102801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1024, 8, 0, 3, "知識から過去を学んで\n未来に活かすわよ", 1, 102801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1026, 1, 0, 1, "安売りのお店の情報\n整理しないと…", 1, 102802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1027, 2, 0, 1, "どんなに苦労してても\nそう見せないのが真の\nエレガントなレディよ", 1, 102802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1028, 3, 0, 1, "安く済んであいつが\n喜ぶ料理とか…\nないかしら", 1, 102802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1029, 4, 0, 1, "子供たちの遊びって\nあたしが子供の頃と\nあまり変わらないのね", 1, 102802, 0, 1);
