INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1031, 101, 0, 1, "焼きそばパンが\n食べたいな～\nあいつと…", 8, 102802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1032, 102, 0, 1, "あいつと一緒に…\n色々なところに\nいきたいな", 8, 102802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1033, 1, 0, 2, "今度買い物に\n付き合いなさい\nセールがあるのよ", 1, 102802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1034, 2, 0, 2, "あんたがいると子供\nたちも喜ぶわ♪\nもちろんあたしもね♪", 1, 102802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1035, 3, 0, 2, "いっぱい働いて遊んで\nよく休んでまた働く\nそれが健全な生活よね", 1, 102802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1036, 4, 0, 2, "あんたとこうして\nのんびりするのも\nいいものね", 1, 102802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1037, 101, 0, 2, "ねえ、今度遊びに\n行きたいな\nもちろん一緒に、よ", 8, 102802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1038, 102, 0, 2, "あんたが手伝って\nくれるから、仕事も\n前より楽しいのよね♪", 8, 102802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1039, 1, 0, 2, "面倒な用事は午前中に\n片づけてしまうのが\n時間の使い方のコツよ", 1, 102802, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1040, 2, 0, 2, "ランチにして午後の\n仕事に備えましょ！\nやることは一杯よ♪", 1, 102802, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1041, 3, 0, 2, "ふわあ～\nもうこんな時間\n子供たちは\nもう寝たかしら", 1, 102802, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1042, 1, 0, 3, "忙しい時こそ\n身だしなみは隙なく\nぴしっとね！", 1, 102802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1043, 2, 0, 3, "これで仕事は一段落ね\nちょっと休憩…", 1, 102802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1044, 3, 0, 3, "ふふふ\nずっと眺めてたく\nなっちゃうわね", 1, 102802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1045, 4, 0, 3, "部屋中がいい香り…\nなんだかすごい贅沢\nしてる気分♪", 1, 102802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1046, 5, 0, 3, "風邪を引かないように\n身体を温めなくちゃ", 1, 102802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1047, 6, 0, 3, "さっ！　家の中でも\n効率的に\nトレーニングよ！", 1, 102802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1048, 7, 0, 3, "しっかり食べて\n栄養補給、栄養補給！", 1, 102802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1049, 8, 0, 3, "知識から過去を学んで\n未来に活かすわよ", 1, 102802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1051, 1, 0, 1, "可愛い子が\nいっぱいいるなぁ…\n誰か私とユニット\n組んでくれないかな？", 1, 102901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1052, 2, 0, 1, "今度のライブ会場、\nいつもより大きいから\n楽しみなんだ～！", 1, 102901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1053, 3, 0, 1, "新曲の衣装、もうすぐ\n出来あがるんだって！\n楽しみだな～", 1, 102901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1054, 4, 0, 1, "彼とデュエット\nしてみたいな～♪", 1, 102901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1055, 5, 0, 1, "キラキラ無病息災、\nドキドキ一日一善…\n作詞って難しい～", 1, 102901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1056, 101, 0, 1, "ファンはもちろん\n大事だけど…\n彼のことは、もっと\n大切にしたいな…", 4, 102901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1057, 102, 0, 1, "写真集見なくても、\n本物がここに\nいるのにな～", 8, 102901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1058, 1, 0, 2, "握手会の練習、\n付き合ってくれない？", 1, 102901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1059, 2, 0, 2, "写真集のポーズ\n練習中～！\nど、どうかな？", 1, 102901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1060, 3, 0, 2, "何て書いてあるか\n読めないって？\nもー！\nこれは私のサイン！", 1, 102901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1061, 4, 0, 2, "ね、次のライブ\n観に来てくれる？\nいい席とっとくから！", 1, 102901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1062, 101, 0, 2, "ねぇ、台本読むの\nつきあってくれる？\n恋人役に\nなってほしいんだ", 4, 102901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1063, 102, 0, 2, "新曲、聞いてくれた？\nキミとのことを\nテーマにしたんだよ。\nどこかは秘密だけど♪", 8, 102901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1064, 1, 0, 2, "朝の発声練習に\n行くけど、キミも\n一緒に来ない？", 1, 102901, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1065, 2, 0, 2, "この時間は\nお腹すくけど…\n水着撮影があるから\nお昼抜いてるんだ", 1, 102901, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1066, 3, 0, 2, "夜の帳が二人を包み\n…こういう方向性の\n歌詞も悪くないなぁ。", 1, 102901, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1067, 1, 0, 3, "身だしなみオッケー！\nアイドルはいつでも\n完璧でいなくちゃ！", 1, 102901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1068, 2, 0, 3, "ふぅ…\nこんな姿、ファンには\n見せられないな～", 1, 102901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1069, 3, 0, 3, "かわいい～♪\nこれは強力な\nライバル登場かな！", 1, 102901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1070, 4, 0, 3, "ステキな香り～♪\n歌詞のイメージが\n浮かんできそう！", 1, 102901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1071, 5, 0, 3, "体も暖まったし！\nダンスレッスンに\n行こうかな♪", 1, 102901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1072, 6, 0, 3, "ベストタイム更新！\nライブに向けて\n体力つけないと…", 1, 102901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1073, 7, 0, 3, "太っちゃったら\nどうしよう…？\nでも手が止まらない～", 1, 102901, 0, 1);
