INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1076, 1, 0, 1, "このお部屋すてき！\nファンに追われた時の\n隠れ家にピッタリね！", 1, 102902, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1077, 2, 0, 1, "そろそろイメチェン\nしてみようかな…\nそれともこの路線で\nもう少し…？", 1, 102902, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1078, 3, 0, 1, "みんながくれた\nファンレター…\n私の大事な宝物…", 1, 102902, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1079, 4, 0, 1, "彼とデュエット\nしてみたいな～♪", 1, 102902, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1080, 5, 0, 1, "疾風怒濤、威風堂々\n獅子奮迅、唯我独尊…\nラブソングの作詞って\n難しいな～", 1, 102902, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1081, 101, 0, 1, "ファンはもちろん\n大事だけど…\n彼のことは、もっと\n大切にしたいな…", 8, 102902, 0, 1);
