INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1099, 8, 0, 3, "この恋愛小説ステキ！\n私もアイドルじゃ\nなかったら…", 1, 102902, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1101, 1, 0, 1, "手裏剣の数が\n合わないデス！\nどこかに落とした\nデスか…？", 1, 103001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1102, 2, 0, 1, "クセモノ、隠れても\nむだデス！\n…気のせいデシタ", 1, 103001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1103, 3, 0, 1, "金は天下の\nマワリモノ！\nところで、\nどういう意味デス？", 1, 103001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1104, 4, 0, 1, "むむ、アクダイカンの\n気配がするデース", 1, 103001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1105, 5, 0, 1, "クニトリ合戦、\n楽しみデース！", 1, 103001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1106, 101, 0, 1, "ショーグンと\n出会えて、\nワタシは果報者デス！", 4, 103001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1107, 102, 0, 1, "最近、ショーグンの\nそばにいると\nドキドキするデス…", 8, 103001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1108, 1, 0, 2, "なんでも\n命令するデス！\nショーグンの命は\n絶対デス！", 1, 103001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1109, 2, 0, 2, "あっ、ショーグン、\n出陣はいつで\nゴザルデス！？", 1, 103001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1110, 3, 0, 2, "さ、さすが\nショーグンデス…\nいつの間に背後を！？", 1, 103001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1111, 4, 0, 2, "出陣の合図に\n備えるデス！\nほら貝は\nどこデスか！？", 1, 103001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1112, 101, 0, 2, "見つめられると\nドキドキするデス…\n新しい忍法デスか？", 4, 103001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1113, 102, 0, 2, "ショーグン！\nえへへ…これからも\nおそばに置いて\nクダサイね？", 8, 103001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1114, 1, 0, 2, "おはようデス！\n今日も一日\n忍法日和デス！", 1, 103001, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1115, 2, 0, 2, "そろそろお腹\n空いてきたデス。\n昼ごはんにするデス！", 1, 103001, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1116, 3, 0, 2, "明日もきっと\nいい日デスよ、\nショーグン！", 1, 103001, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1117, 1, 0, 3, "ちょんまげに\nしてみるのも\n面白いかもデス！", 1, 103001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1118, 2, 0, 3, "心地よくて\n眠くなってきマス…\nもしやテキコクの\n罠デスか！？", 1, 103001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1119, 3, 0, 3, "これを変わり身の\n術に…かわいそう\nだから止めるデス", 1, 103001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1120, 4, 0, 3, "ワビサビというもの\nデスね！\n素晴らしいデス！", 1, 103001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1121, 5, 0, 3, "身体を暖めて\nイクサに向かうデス！", 1, 103001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1122, 6, 0, 3, "アクロバットな忍法を\n使うには、体を\n鍛えないと危険デス！", 1, 103001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1123, 7, 0, 3, "うーん、\n美味しいデス…\n幸せデス～", 1, 103001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1124, 8, 0, 3, "これは…\nＢＵＳＨＩＤＯ…\nデスか？", 1, 103001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1126, 1, 0, 1, "ふんふんふ～ん\n武器の手入れは\n念入りにしマスよ～", 1, 103002, 0, 1);
