INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1132, 102, 0, 1, "ショーグンの身は\n何があってもワタシが\n守るデス！", 8, 103002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1133, 1, 0, 2, "あっ、ショーグン\n出陣はいつで\nゴザルデス！？", 1, 103002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1134, 2, 0, 2, "イクサの前に\nホネ休めも大事デスよ\nゆっくりするデース！", 1, 103002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1135, 3, 0, 2, "さ、さすがショーグン\nデス…いつの間に\n背後を！？", 1, 103002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1136, 4, 0, 2, "一緒に忍法考える\nデス！\nショーグン用の\n忍法も必要デスね！", 1, 103002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1137, 101, 0, 2, "見つめられると\nドキドキするデス…\n新しい忍法デスか？", 8, 103002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1138, 102, 0, 2, "ショーグン！\nえへへ…これからも\nおそばに置いて\nクダサイね？", 8, 103002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1139, 1, 0, 2, "いい朝デス！\nトーゴク風の朝ゴハン\n作るデス！", 1, 103002, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1140, 2, 0, 2, "もうお昼デス\nお昼寝するデスか？", 1, 103002, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1141, 3, 0, 2, "そろそろおネム\nデース…\n一緒に寝るデス？", 1, 103002, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1142, 1, 0, 3, "服の乱れは心の乱れ\nデス！　きっちり\nしないといけないデス", 1, 103002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1143, 2, 0, 3, "心地よくて眠く\nなってきマス…\nもしやテキコクの\n罠デスか！？", 1, 103002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1144, 3, 0, 3, "これを変わり身の\n術に…かわいそう\nだから止めるデス", 1, 103002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1145, 4, 0, 3, "ワビサビというもの\nデスね！\n素晴らしいデス！", 1, 103002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1146, 5, 0, 3, "身体を暖めてイクサに\n向かうデス！", 1, 103002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1147, 6, 0, 3, "ショーグンのお役に\n立つため、修行\nあるのみデス！", 1, 103002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1148, 7, 0, 3, "美味しいデス…\n酢昆布を和えて\nみたいデス", 1, 103002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1149, 8, 0, 3, "物事を知って\n来たるべきイクサに\n活かすデス", 1, 103002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1151, 1, 0, 1, "お父さん、そろそろ\n子離れを…しない？\nそうですか…", 1, 103101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1152, 2, 0, 1, "父は、いつも男性に\nきびしいですね", 1, 103101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1153, 3, 0, 1, "占ってほしい人は、\nいつでも言って\nください", 1, 103101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1154, 4, 0, 1, "あなたの手相は…\nて、手を握るのって\n緊張しますね", 1, 103101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1155, 5, 0, 1, "この部屋、風水的にも\n運気が上がりそうです", 1, 103101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1156, 101, 0, 1, "あの人とのこと占って\nみようかな…\n好き…嫌い…\n好き♪…き、嫌い", 4, 103101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1157, 102, 0, 1, "私との相性ですか？\nそれなら占う必要は\nありませんよ…\nふふっ♪", 8, 103101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1158, 1, 0, 2, "女難の相が\n出ていますね…\n気をつけてください", 1, 103101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1159, 2, 0, 2, "今日のあなたの運勢は\n大きょ…\n大吉です", 1, 103101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1160, 3, 0, 2, "父と散歩中です。\n毎日決まったコースを\n散歩させないと、\nストレスが溜まるので", 1, 103101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1161, 4, 0, 2, "小僧！\nシノブに触るとは\nいい度胸じゃねぇか！", 1, 103101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1162, 101, 0, 2, "あなたに触られると、\n動悸が早くなります\nもしかして心霊現象？", 4, 103101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1163, 102, 0, 2, "今日もたくさんの幽霊\nに慕われていますね。\n素敵です", 8, 103101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1164, 1, 0, 2, "おはようございます\n今日の運勢を\n占いましょうか？", 1, 103101, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1165, 2, 0, 2, "今日のラッキー\nアイテムはドクロです\n良ければ父を\nお貸ししましょうか？", 1, 103101, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1166, 3, 0, 2, "今日のあなたの運気は\n最高潮でした。\nもうすぐ今日は\n終わりますが", 1, 103101, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1167, 1, 0, 3, "お父さん。\nいつも、身だしなみに\n気を使ってるけど、\nあまり変わってないよ", 1, 103101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1168, 2, 0, 3, "一休みしましょう。\n憑かれて疲れました", 1, 103101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1169, 3, 0, 3, "かわいい…\n付喪神が宿らないかな", 1, 103101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1170, 4, 0, 3, "お父さん、\nリラックスし過ぎると\n成仏しちゃうよ？", 1, 103101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1171, 5, 0, 3, "気持ちいいです…\n極楽浄土みたい…", 1, 103101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1172, 6, 0, 3, "はぁはぁ…\nお、お父さん、\n代わりに運動して…", 1, 103101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1173, 7, 0, 3, "お父さんも\n食べたいの？\n死んでるのに？", 1, 103101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1174, 8, 0, 3, "この本…\nもしかして予言書？", 1, 103101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1176, 1, 0, 1, "ここにいる幽霊は\nみんな幸せそうです", 1, 103102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1177, 2, 0, 1, "お父さんが\n女の子ばかりに\n声をかけるので\n困っています", 1, 103102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1178, 3, 0, 1, "成仏はうれしいこと\nです…でも友達が\n減るのは寂しいですね", 1, 103102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1179, 4, 0, 1, "幽霊に好かれる人は\n素敵な方です\nもちろん、あなたも", 1, 103102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1180, 5, 0, 1, "お父さん、そろそろ\n子離れを…しない？\nそうですか…", 1, 103102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1181, 101, 0, 1, "生きている人に\n好かれるには、\nどうすればいい\nのでしょう？", 8, 103102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1182, 102, 0, 1, "お父さん、どうしたら\nあの人のことを\n認めてくれますか？", 8, 103102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1183, 1, 0, 2, "この部屋、風水的にも\n運気が上がりそうです", 1, 103102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1184, 2, 0, 2, "シノブには指一本\n触れさせん！\nガブッ！！", 1, 103102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1185, 3, 0, 2, "あなたの手相は…\nて、手を握るのって\n緊張しますね", 1, 103102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1186, 4, 0, 2, "ラップ音？\nいえ、父のいびきです", 1, 103102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1187, 101, 0, 2, "私との相性ですか？\nそれなら占う必要は\nありませんよ…\nふふっ♪", 8, 103102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1188, 102, 0, 2, "前世って信じますか？\n私とあなたは\nもしかすると…", 8, 103102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1189, 1, 0, 2, "今日のラッキー\nアイテムはドクロです\n良ければ父を\nお貸ししましょうか？", 1, 103102, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1190, 2, 0, 2, "明るい時間は\nあまり得意では\nないんです…", 1, 103102, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1191, 3, 0, 2, "夜のお散歩に\n行きませんか？\n知り合いの幽霊に\n挨拶して回るんです", 1, 103102, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1192, 1, 0, 3, "お父さん\nいつも、身だしなみに\n気を使ってるけど\nあまり変わってないよ", 1, 103102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1193, 2, 0, 3, "うとうと…\nあれ…\n走馬灯が見える…", 1, 103102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1194, 3, 0, 3, "かわいい…\nお父さんみたい…", 1, 103102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1195, 4, 0, 3, "お父さん\nリラックスし過ぎると\n成仏しちゃうよ？", 1, 103102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1196, 5, 0, 3, "快適です…\n幽霊もどんどん\n集まってきますね…", 1, 103102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1197, 6, 0, 3, "はぁはぁ…\nお、お父さん、\n代わりに運動して…", 1, 103102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1198, 7, 0, 3, "お父さんの分も、\n私が食べるね", 1, 103102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1199, 8, 0, 3, "この本の占い\n私の占いと違う…", 1, 103102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1201, 1, 0, 1, "私だって、一人で\n買い物ぐらい\nできますわ…きっと", 1, 103201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1202, 2, 0, 1, "そろそろ新しい\n案件を始めないと\nいけませんわね", 1, 103201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1203, 3, 0, 1, "一日も早く、\n一人前の商人に\nなりたいものです", 1, 103201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1204, 4, 0, 1, "平和を買うのには\nどれほどのお金が\n必要なのかしら？", 1, 103201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1205, 5, 0, 1, "ここももう少し広いと\n落ち着くのですけど。\n改築しませんこと？", 1, 103201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1206, 101, 0, 1, "あの方はお金で雇った\nわけではないのに\n私といてくださる…", 4, 103201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1207, 102, 0, 1, "たとえ私が間違っても\nきっとあの方が\n正してくれますわ…", 8, 103201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1208, 1, 0, 2, "私と、庶民向けの\nお店へ一緒に\n参りませんか？", 1, 103201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1209, 2, 0, 2, "資産運用？\nお幾らほど\n増やしたいのです？", 1, 103201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1210, 3, 0, 2, "どんぶり勘定だとよく\n言われるのですが…\n例えが小さすぎます！", 1, 103201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1211, 4, 0, 2, "お金で解決するのは\n悪いことでは\nありませんわよね？", 1, 103201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1212, 101, 0, 2, "その、よろしければ\n私とお散歩など\nいかがです？", 4, 103201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1213, 102, 0, 2, "もしあなたさまが\n道に迷われるなら、\n私もお供いたします！", 8, 103201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1214, 1, 0, 2, "早起きは三ルピの徳と\n申しますが…\nもっとお得ですわね", 1, 103201, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1215, 2, 0, 2, "昼食は町の屋台で\n立ったまま食べて\nみたいですわ！", 1, 103201, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1216, 3, 0, 2, "よろしければ\nお休み前のミルクを\n御一緒にいかが？", 1, 103201, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1217, 1, 0, 3, "だらしない身なりでは\n取引相手に信用して\nいただけませんものね", 1, 103201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1218, 2, 0, 3, "この感触と安らぎ…\n素晴らしい\n費用対効果ですわ…", 1, 103201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1219, 3, 0, 3, "何の見返りも求めず\nただひたすら愛でて\nいたいのです！", 1, 103201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1220, 4, 0, 3, "目に見えないものに\nお金をかけるのが\n真の贅沢ですわ！", 1, 103201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1221, 5, 0, 3, "誰ひとり寒い思いを\nしない町にするために\n私にできる事は…", 1, 103201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1222, 6, 0, 3, "健康はお金では\n買えませんが\n運動器具は別ですわ！", 1, 103201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1223, 7, 0, 3, "なかなか\n美味ですわ\nシェフはどちらに？", 1, 103201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1224, 8, 0, 3, "経済の仕組みを\nしっかりと\n学ばなくては…", 1, 103201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1226, 1, 0, 1, "そろそろ新しい\n案件を始めないと\nいけませんわね", 1, 103202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1227, 2, 0, 1, "ええと、これは\nおいくらで買ったん\nでしたっけ？", 1, 103202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1228, 3, 0, 1, "お金を見せたら\n態度が変わる人は\n信用できませんわ", 1, 103202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1229, 4, 0, 1, "地図が読めずとも\n道に迷わない方って\nいらっしゃるのですね", 1, 103202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1230, 5, 0, 1, "この狭さも\n慣れれば\n快適ですわ♪", 1, 103202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1231, 101, 0, 1, "あの方はお金で雇った\nわけではないのに\n私といてくださる…", 8, 103202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1232, 102, 0, 1, "たとえ私が間違っても\nきっとあの方が\n正してくれますわ…", 8, 103202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1233, 1, 0, 2, "一人前の商人に\n相応しい居振る舞いを\nしたいものですわ", 1, 103202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1234, 2, 0, 2, "幼い頃\nお店屋さんごっこを\nなさいましたか？", 1, 103202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1235, 3, 0, 2, "資産運用？\nお幾らほど\n増やしたいのです？", 1, 103202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1236, 4, 0, 2, "今度一緒に…\nパーティにでも\n行きませんこと？", 1, 103202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1237, 101, 0, 2, "その、よろしければ\n私とお散歩など\nいかがです？", 8, 103202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1238, 102, 0, 2, "あなたさまの優しさは\nお金には\n代えられませんわね", 8, 103202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1239, 1, 0, 2, "なぜだか\n眠り足りないような\n感じがしますわ…", 1, 103202, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1240, 2, 0, 2, "昼食は町の屋台で\n立ったまま食べて\nみたいですわ！", 1, 103202, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1241, 3, 0, 2, "よろしければ\nお休み前のミルクを\n御一緒にいかが？", 1, 103202, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1242, 1, 0, 3, "ドレスは汚れていない\nようですが…やだ\n髪がほつれてますわ！", 1, 103202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1243, 2, 0, 3, "こういったものに\n腰を沈めると…\nき、気が緩んで…", 1, 103202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1244, 3, 0, 3, "何の見返りも求めず\nただひたすら愛でて\nいたいのです！", 1, 103202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1245, 4, 0, 3, "香りは質のいいものと\nそうでないものが\nはっきり分かりますわ", 1, 103202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1246, 5, 0, 3, "誰ひとり寒い思いを\nしない町にするために\n私にできる事は…", 1, 103202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1247, 6, 0, 3, "目標は遠くに\n置いた方が励みに\nなりますわ！", 1, 103202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1248, 7, 0, 3, "なかなか\n美味ですわ\nシェフはどちらに？", 1, 103202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1249, 8, 0, 3, "商人って、物語では\n悪役にされやすいの\nですね…", 1, 103202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1251, 1, 0, 1, "家の中のものを使って\n物ボケ、考えるべ！", 1, 103301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1252, 2, 0, 1, "毎日の日課…それは\n炊事、洗濯、大喜利\nだべ！", 1, 103301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1253, 3, 0, 1, "うちに塀ができたら、\nへー、って言いたい！", 1, 103301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1254, 4, 0, 1, "オラが作る料理は\nこの後スタッフで\n美味しくいただくべよ", 1, 103301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1255, 5, 0, 1, "エリザベスも\n連れてきてぇ～", 1, 103301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1256, 101, 0, 1, "あんちゃんも、\nエリザベスと同じ位\nめんこいべ♪", 4, 103301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1257, 102, 0, 1, "オラとあんちゃん、\n息がぴったりに\nなってきたべ…！", 8, 103301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1258, 1, 0, 2, "おぉ！　そんな急に\nツッコまれたら、\nびっくらこくべやー！", 1, 103301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1259, 2, 0, 2, "ネタはオラが書くけど\nあんちゃんが\n書きたかったら\n書いてもいいんだぞ？", 1, 103301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1260, 3, 0, 2, "あんちゃん、オラには\n容赦なんかせずに\nツッコんでくれて\nいいから！", 1, 103301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1261, 4, 0, 2, "押すなよ、\n絶対押すなよ\n…なんて\n言ってないべさ？", 1, 103301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1262, 101, 0, 2, "うう…あんちゃんに\nツッコまれたくて\nうずうずしてきたべ～", 4, 103301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1263, 102, 0, 2, "将来的には夫婦漫才\nってのも…あ、ひ、\nひとりごとだべさ～！", 8, 103301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1264, 1, 0, 2, "おはよう！　一緒に\n牛の乳しぼりいくべ？", 1, 103301, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1265, 2, 0, 2, "お昼だ～腹が減っては\n笑いはできぬ～！", 1, 103301, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1266, 3, 0, 2, "今日もよく働いたべ！\nさ、今からは\nお笑いの練習だ～！", 1, 103301, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1267, 1, 0, 3, "舞台に上がる前に\n身なりは整えねぇと\nだめだべ～！", 1, 103301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1268, 2, 0, 3, "ナイスなイスに\n座りたい、\nなんちゃって！", 1, 103301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1269, 3, 0, 3, "めんこいのは、\nスベっても許される\nからずるいべな～？", 1, 103301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1270, 4, 0, 3, "必要以上に\n癒されちまうべや～", 1, 103301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1271, 5, 0, 3, "ぽかぽかだ…\nウケた会場みてぇだ", 1, 103301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1272, 6, 0, 3, "お笑い芸人は、\n体が資本だべさ～！", 1, 103301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1273, 7, 0, 3, "もぐもぐ…\nボケることも\n忘れちまう美味さだ～", 1, 103301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1274, 8, 0, 3, "ネタ作りのヒントが\n載ってねぇか探すべ", 1, 103301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1276, 1, 0, 1, "さぁ今日も張り切って\n漫才の練習するべさ！", 1, 103302, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1277, 2, 0, 1, "この部屋でも、商品\n売っていいんだべか？", 1, 103302, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1278, 3, 0, 1, "眼鏡を頭の上に乗せて\n「メガネメガネ～」\nって言う練習した方が\nいいべか？", 1, 103302, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1279, 4, 0, 1, "漫才を持ち時間に\n収めるには、何度も\n練習するしかねぇべ", 1, 103302, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1280, 5, 0, 1, "エリザベスも\n連れてきてぇ～", 1, 103302, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1281, 101, 0, 1, "うう…あんちゃんに\nツッコまれたくて\nうずうずしてきたべ～", 8, 103302, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1282, 102, 0, 1, "あんちゃんに\n採れたての食材を\n食わしてやりてぇ～", 8, 103302, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1283, 1, 0, 2, "押すなよ\n絶対押すなよ\n…なんて\n言ってないべさ？", 1, 103302, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1284, 2, 0, 2, "あんちゃん、百連\nツッコミ特訓だ～！", 1, 103302, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1285, 3, 0, 2, "あんちゃん、オラには\n容赦なんかせずに\nツッコんでくれて\nいいから！", 1, 103302, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1286, 4, 0, 2, "「なんでだべ～！」\nって言いながら\nツッコむといいべ！", 1, 103302, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1287, 101, 0, 2, "あはは～あんちゃんの\nツッコミはやっぱり\n気持ちいいなぁ～", 8, 103302, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1288, 102, 0, 2, "将来的には夫婦漫才\nってのも…あ、ひ\nひとりごとだべさ～！", 8, 103302, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1289, 1, 0, 2, "おはよう！\n今日もいい\nネタ作り日和だ！", 1, 103302, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1290, 2, 0, 2, "お昼だな\nご飯を食べたら、\nネタの練習するべ！", 1, 103302, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1291, 3, 0, 2, "もうこんな時間だべ\nネタ作りは\n明日にするべ…", 1, 103302, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1292, 1, 0, 3, "うーん…衣装、\nずれてねぇべか？", 1, 103302, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1293, 2, 0, 3, "ナイスなイスに\n座りたい\nなんちゃって！", 1, 103302, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1294, 3, 0, 3, "はう～\nなまらめんこいべや～", 1, 103302, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1295, 4, 0, 3, "必要以上に\n癒されちまうべや～", 1, 103302, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1296, 5, 0, 3, "笑いをとることを\n忘れるほど\nあったけぇべ～", 1, 103302, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1297, 6, 0, 3, "たくさん運動して\n動きでも笑いがとれる\nように頑張るべ～！", 1, 103302, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1298, 7, 0, 3, "もぐもぐ…\nボケることも\n忘れちまう美味さだ～", 1, 103302, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1299, 8, 0, 3, "幅広く教養を\n身につけて\nいいネタ作るべ！", 1, 103302, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1301, 1, 0, 1, "通りにいる\nカップル…爆発\nすればいいのに…", 1, 103401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1302, 2, 0, 1, "う～ん、仕事なんて\nしないで、\n飲んでたいな～", 1, 103401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1303, 3, 0, 1, "今日は一日、\n外に出ない！\nもう決めたわ！", 1, 103401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1304, 4, 0, 1, "お腹空いた…\nけど、外に出るのは\n面倒だなぁ", 1, 103401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1305, 5, 0, 1, "うぅ…頭がガンガン\nする…昨日、\n飲み過ぎたかな…", 1, 103401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1306, 101, 0, 1, "デートとか…\n誘っても嫌がられ\nないかな…？", 4, 103401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1307, 102, 0, 1, "もしかして運命の\n相手だったり…\nするのかな", 8, 103401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1308, 1, 0, 2, "やっぱり一杯目が\n一番おいしいわ～！\n…え、見てた！？", 1, 103401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1309, 2, 0, 2, "頼れるお姉さんだって\nところ、もっと見せて\nあげるからね", 1, 103401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1310, 3, 0, 2, "ねえ、一緒に買い物に\n行かない？　その…\nの、飲み物とか…", 1, 103401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1311, 4, 0, 2, "ひっく、うふふ…\nもっとお姉さんと\nお話しようよ～", 1, 103401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1312, 101, 0, 2, "極力人付き合いは\nしない主義なんだけど\nキミは例外かも…", 4, 103401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1313, 102, 0, 2, "いつか…\nキミと一緒に飲めたら\n楽しいだろうな", 8, 103401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1314, 1, 0, 2, "おはよう…朝は\nちょっとつらいな…\n低血圧なのよ…", 1, 103401, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1315, 2, 0, 2, "おっひるー♪\nなに食べよっかな～♪", 1, 103401, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1316, 3, 0, 2, "ふわあ…\n眠る前にちょっと\n一杯…冗談よ", 1, 103401, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1317, 1, 0, 3, "うう…\n髪がボサボサ…\nちゃんとしなきゃ…", 1, 103401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1318, 2, 0, 3, "自然体でゴロゴロ\nするのって\n幸せだなぁ…", 1, 103401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1319, 3, 0, 3, "こういうの男の子に\nプレゼントして\nもらいたいな…", 1, 103401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1320, 4, 0, 3, "うわあ、\nこれ、私の好きな\n香りだー！", 1, 103401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1321, 5, 0, 3, "ぽっかぽか…\nあとは熱い一杯が\nあれば…！", 1, 103401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1322, 6, 0, 3, "家でゴロゴロして\n付いちゃったお肉を\n落とさないと…！", 1, 103401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1323, 7, 0, 3, "もぐもぐ…\nおーいしー！\n…太らないよね？", 1, 103401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1324, 8, 0, 3, "大人の女らしく\n色んなことを学んで\nおかなくっちゃ", 1, 103401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1326, 1, 0, 1, "仕事終わりの一杯の\nために、今日も\n頑張らないと", 1, 103402, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1327, 2, 0, 1, "今日は\nのんびり\n過ごそうかな～", 1, 103402, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1328, 3, 0, 1, "あ…\nお皿、洗って\nなかった…", 1, 103402, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1329, 4, 0, 1, "もう少し、大人の\n魅力が出せるように\nなりたいな", 1, 103402, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1330, 5, 0, 1, "素敵な彼氏…\n欲しいなぁ…", 1, 103402, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1331, 101, 0, 1, "彼と私\n釣り合うかな…\n大丈夫よね？", 8, 103402, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1332, 102, 0, 1, "今後のために\nちょっとは花嫁修業\nしてみようかな…？", 8, 103402, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1333, 1, 0, 2, "何事も\nお姉さんと一緒に\n楽しく頑張りましょ？", 1, 103402, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1334, 2, 0, 2, "ね、ねえ！\nキミは年上と年下\nどっちが好きかな？", 1, 103402, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1335, 3, 0, 2, "もう…\n構ってくれないと\n泣いちゃうぞ～？", 1, 103402, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1336, 4, 0, 2, "お姉さんが～\n可愛がって\nあげようか～？", 1, 103402, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1337, 101, 0, 2, "極力人付き合いは\nしない主義なんだけど\nキミは例外かも…", 8, 103402, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1338, 102, 0, 2, "お姉さんとチュー\nする？　ふふふ～\n冗談で～す！", 8, 103402, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1339, 1, 0, 2, "さーて、今日も\nいっちょやるぞー！", 1, 103402, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1340, 2, 0, 2, "後でのんびり\nストレッチでも\nしようっと♪", 1, 103402, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1341, 3, 0, 2, "一日よく働いた～！\n自分にごほうび\nあげたいなー♪", 1, 103402, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1342, 1, 0, 3, "仕事の前に、髪とか\n整えないと…", 1, 103402, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1343, 2, 0, 3, "自然体でゴロゴロ\nするのって\n幸せだなぁ…", 1, 103402, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1344, 3, 0, 3, "もう大人だし…\nあんまり可愛いのは\n似合わないかな…？", 1, 103402, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1345, 4, 0, 3, "うわあ\nこれ、私の好きな\n香りだー！", 1, 103402, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1346, 5, 0, 3, "あったかーい…\nだ、だらけ\nちゃいそう…", 1, 103402, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1347, 6, 0, 3, "地道な努力が\nリア充への\n第一歩！", 1, 103402, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1348, 7, 0, 3, "おーいしっ♪\nこのために\n生きてるわねえ", 1, 103402, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1349, 8, 0, 3, "大人の女らしく\n色んなことを学んで\nおかなくっちゃ", 1, 103402, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1351, 1, 0, 1, "大切な人に\n本をすすめるのって\nけっこう難しいなぁ", 1, 103801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1352, 2, 0, 1, "き、きらーん☆\nお姉ちゃんに\n似てるかな…？", 1, 103801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1353, 3, 0, 1, "そろそろ好きな\n作家さんの新刊が\n出るから楽しみ…", 1, 103801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1354, 4, 0, 1, "ちょっと、休憩…\n本でも読もうかな", 1, 103801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1355, 5, 0, 1, "もっとたくさんの本に\n囲まれて生活したいな", 1, 103801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1356, 101, 0, 1, "むかしむかしある所に\nシオリと\nあの人が…", 4, 103801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1357, 102, 0, 1, "この本の主人公、\nあの人に\n似ているかも…\nかっこいいな", 8, 103801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1358, 1, 0, 2, "ケホケホ…\nコンコン…\nゴホッゴホッ…\nへ、平気ですよ？", 1, 103801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1359, 2, 0, 2, "あ…お姉ちゃんと\n約束があったんだ…", 1, 103801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1360, 3, 0, 2, "しおりを探している？\nあ、本に挟む方ですね\nドキドキしました…", 1, 103801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1361, 4, 0, 2, "最近調子がいいのは\n牧場の牛乳やチーズの\nおかげでしょうか？", 1, 103801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1362, 101, 0, 2, "読みたい本も\nあなたと行きたい所も\n増えるばっかりです", 4, 103801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1363, 102, 0, 2, "私の病気、\n悪化してるのかも！？\nすごく胸がドキドキ\nするんです…", 8, 103801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1364, 1, 0, 2, "えっと、朝食の前には\nこの薬を飲んで…\n食後にはこの薬を…", 1, 103801, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1365, 2, 0, 2, "ふぅ…午前中\n動いたので体力が…\nすこし休みますね", 1, 103801, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1366, 3, 0, 2, "早く寝ないと\n体に良くないですよね\nでも気になっている\n本があって…", 1, 103801, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1367, 1, 0, 3, "寝てばかりなので、\n髪が乱れてないかな\n…？", 1, 103801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1368, 2, 0, 3, "少し休もう\n…やっぱり\nたくさん休もう", 1, 103801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1369, 3, 0, 3, "かわいいものが\n枕元にあると\n安心できるんですよ", 1, 103801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1370, 4, 0, 3, "いい香り…\n病は気からと言うので\n雰囲気も大事ですね", 1, 103801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1371, 5, 0, 3, "こうして温まって\nいると、病気も\n治る気がします", 1, 103801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1372, 6, 0, 3, "運動…イメージ\nトレーニングだけじゃ\nだめでしょうか…", 1, 103801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1373, 7, 0, 3, "少食ですけど\nこれならたくさん\n食べられそうです", 1, 103801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1374, 8, 0, 3, "続きが気になって\n読むのを止める\nタイミングが…", 1, 103801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1376, 1, 0, 1, "しっかり休まないと\n早く回復するように", 1, 103802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1377, 2, 0, 1, "き、きらーん☆\nお姉ちゃんに\n似てるかな…？　", 1, 103802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1378, 3, 0, 1, "病室よりも…\nずっとこの部屋に\nいたいですね…　", 1, 103802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1379, 4, 0, 1, "そろそろ好きな\n作家さんの新刊が\n出るから楽しみ…", 1, 103802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1380, 5, 0, 1, "大切な人に\n本をすすめるのって\nけっこう難しいなぁ", 1, 103802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1381, 101, 0, 1, "むかしむかしある所に\nシオリと\nあの人が…", 8, 103802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1382, 102, 0, 1, "あの人の\nためにも早く\n病気を治さないと…", 8, 103802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1383, 1, 0, 2, "ケホケホ…\nコンコン…\nゴホッゴホッ…\nへ、平気ですよ？", 1, 103802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1384, 2, 0, 2, "新記録です！\n一週間連続、\n平熱を保ちました！", 1, 103802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1385, 3, 0, 2, "あ…お薬の時間…\n苦いけど我慢しなきゃ　", 1, 103802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1386, 4, 0, 2, "最近調子がいいのは\n牧場の牛乳やチーズの\nおかげでしょうか？", 1, 103802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1387, 101, 0, 2, "しおりを探している？\nあ、本に挟む方ですね\nドキドキしました…", 8, 103802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1388, 102, 0, 2, "私の病気\n悪化してるのかも！？\nすごく胸がドキドキ\nするんです…", 8, 103802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1389, 1, 0, 2, "えっと、朝食の前には\nこの薬を飲んで…\n食後にはこの薬を…", 1, 103802, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1390, 2, 0, 2, "…お昼の薬？\nあ、飲み忘れてました\nありがとうございます", 1, 103802, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1391, 3, 0, 2, "この薬を飲むと\n眠くなっちゃうんです\nでもまだ、あなたと\nお話したいし～", 1, 103802, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1392, 1, 0, 3, "寝てばかりなので、\n髪が乱れてないかな\n…？　", 1, 103802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1393, 2, 0, 3, "少し休もう\n…やっぱり\nたくさん休もう", 1, 103802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1394, 3, 0, 3, "この子がいれば\n病室に一人でも\n寂しくないですね", 1, 103802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1395, 4, 0, 3, "いろんな効能が？\n健康になる香りも\nあるのでしょうか？", 1, 103802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1396, 5, 0, 3, "こうして温まって\nいると、病気も\n治る気がします", 1, 103802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1397, 6, 0, 3, "私も、少しは\n体力をつけないと\nいけませんよね　", 1, 103802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1398, 7, 0, 3, "いっぱい食べて\n健康な体にならないと", 1, 103802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1399, 8, 0, 3, "続きが気になって\n読むのを止める\nタイミングが…", 1, 103802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1401, 1, 0, 1, "友達ができたら、\n山の上でお弁当…\n夢が広がります…", 1, 104001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1402, 2, 0, 1, "あ、忘れずに\nお花に水を\nあげなきゃ…", 1, 104001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1403, 3, 0, 1, "お菓子を作って、\nみなさんに振る舞って\n友達作戦！　とか…", 1, 104001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1404, 4, 0, 1, "そろそろ新しい\n草人形を作って\n友達作りの特訓を…", 1, 104001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1405, 5, 0, 1, "植物のお世話って、\nぼっちにとっては、\n救い…なんです…", 1, 104001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1406, 101, 0, 1, "ＢＢ団の活動日…\nまだかな…\nうふふふっ", 4, 104001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1407, 102, 0, 1, "もっともっとあの人の\nそばで、一緒に友達を\n作っていきたいな…", 8, 104001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1408, 1, 0, 2, "あ、こんにちは！\n今日もＢＢ団で\nお世話になります！", 1, 104001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1409, 2, 0, 2, "あの…私の友達作りの\n練習方法、間違って\nませんよね…？", 1, 104001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1410, 3, 0, 2, "そうだ、今度、\n森に一緒に木の苗を\n植えに行きませんか？", 1, 104001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1411, 4, 0, 2, "人とお話するのって、\n私、大好きです！\n友達ふやすぞ～！", 1, 104001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1412, 101, 0, 2, "あなたと話してると、\nまるで私がぼっちじゃ\nないみたいです！", 4, 104001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1413, 102, 0, 2, "友達もほしいけど…\nあなたに会えたことが\n私の一番の幸せです", 8, 104001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1414, 1, 0, 2, "おはようございます！\n今日もＢＢ団、\nがんばるぞー！", 1, 104001, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1415, 2, 0, 2, "なんだか、お腹が\n空いてきました。\nお昼ごはんー♪", 1, 104001, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1416, 3, 0, 2, "もう眠いかもです…\n明日は友達、\nできるといいなあ…", 1, 104001, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1417, 1, 0, 3, "私がもっと\n可愛ければ、\n友達できるかなあ？", 1, 104001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1418, 2, 0, 3, "ふわあ…のんびり\nしすぎないように\n気を付けなきゃです", 1, 104001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1419, 3, 0, 3, "かわいいなあ。\nこの子とも\n友達になりたい…", 1, 104001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1420, 4, 0, 3, "いい香りです…\n精神統一して\n友達のイメトレ…", 1, 104001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1421, 5, 0, 3, "灯りを見てると、\n心がなごみます～", 1, 104001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1422, 6, 0, 3, "もぐもぐ…\nおいしいです…！\nみなさんもぜひ！", 1, 104001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1423, 7, 0, 3, "おいしいです…\n私に友達がいたら、\n分けてあげたいな", 1, 104001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1424, 8, 0, 3, "友達作りの\n参考になりそうなこと\n書いてないかな～？", 1, 104001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1426, 1, 0, 1, "友達作りは、焦らず\n慌てず、じっくりと…", 1, 104002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1427, 2, 0, 1, "ひとつ屋根の下\nって感じ…\nいいですねえ…", 1, 104002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1428, 3, 0, 1, "煮汁、煮汁が\n噴きこぼれて\nいませんか～", 1, 104002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1429, 4, 0, 1, "今度、みなさんと\nピクニックとか…\nいってみたいな", 1, 104002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1430, 5, 0, 1, "ぼっちでもいい\nたくましく育てば\n…なんて", 1, 104002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1431, 101, 0, 1, "最近、毎日あの人の\nこと考えちゃいます\nどうしてだろう…", 8, 104002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1432, 102, 0, 1, "もっともっとあの人の\nそばで、一緒に友達を\n作っていきたいな…", 8, 104002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1433, 1, 0, 2, "あ！　ＢＢ団の活動\nですか？　嬉しくて\n飛び跳ねちゃいます！", 1, 104002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1434, 2, 0, 2, "あなたとのお話で友達\n作りの練習になるなら\n一石二鳥です！", 1, 104002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1435, 3, 0, 2, "私少しは人と話しても\nテンパらなくなって\nきましたかね…？", 1, 104002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1436, 4, 0, 2, "あの、友達作りの\n新しい作戦\n考えてみたんですっ！", 1, 104002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1437, 101, 0, 2, "あなたとお話する時の\nこの気持ち、私の宝物\nなんです…えへへ", 8, 104002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1438, 102, 0, 2, "友達もほしいけど…\nあなたに会えたことが\n私の一番の幸せです", 8, 104002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1439, 1, 0, 2, "朝ですね！\nこんないい朝は\n友達作りのチャンス！", 1, 104002, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1440, 2, 0, 2, "あの…そろそろお腹\nすきませんか？\nお昼ごはんは…", 1, 104002, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1441, 3, 0, 2, "もう眠いかもです…\n明日は友達\nできるといいなあ…", 1, 104002, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1442, 1, 0, 3, "私…見た目\nそこまで変じゃ\nないですよね…？", 1, 104002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1443, 2, 0, 3, "ほわあ～…\nふかふかって\nいいものですね～", 1, 104002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1444, 3, 0, 3, "この子みたいな\nあいくるしさを\n目指したいです！", 1, 104002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1445, 4, 0, 3, "灯りを見てると\n心がなごみます～", 1, 104002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1446, 5, 0, 3, "ぬくぬくで、なんだか\n眠くなっちゃいますね", 1, 104002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1447, 6, 0, 3, "身体を鍛えて\nみなさんを\n守ります！", 1, 104002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1448, 7, 0, 3, "もぐもぐ…\nおいしいです…！\nみなさんもぜひ！", 1, 104002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1449, 8, 0, 3, "友達作りの\n参考になりそうなこと\n書いてないかな～？", 1, 104002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1451, 1, 0, 1, "唱喚の歌は、みだりに\n歌っていいものでは\nないんです", 1, 104201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1452, 2, 0, 1, "ここでアイドルの\n練習は、さすがに\n迷惑ですよね？", 1, 104201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1453, 3, 0, 1, "練習してもあんまり\n疲れなくなった…\nアイドルのおかげかな", 1, 104201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1454, 4, 0, 1, "人々の平和を守る旅に\n同行してくれる人を\n探していて…", 1, 104201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1455, 5, 0, 1, "あとで曲を復習して\nおかないと…", 1, 104201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1456, 101, 0, 1, "踊りを見られるのは\nまだ恥ずかしいけど\nあの人になら…", 4, 104201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1457, 102, 0, 1, "浜辺で曲を書こうと\n思うんですけど…\n一緒に行きませんか？", 8, 104201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1458, 1, 0, 2, "いつも応援ありがとう\nございます♪　これは\nあ、握手の練習です！", 1, 104201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1459, 2, 0, 2, "え、もう練習の時間？\nごめんなさい\nすぐ準備します！", 1, 104201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1460, 3, 0, 2, "守るべき人たちに\n笑顔でいてもらうのも\n大切なんですね", 1, 104201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1461, 4, 0, 2, "歌える曲を増やそうと\n思って。どんな曲が\n聞きたいですか？", 1, 104201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1462, 101, 0, 2, "～～♪　あれ？\n今私、歌ってました？\n気づきませんでした", 4, 104201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1463, 102, 0, 2, "…歌って欲しい？\n喜んで♪\nどの歌にしましょう？", 8, 104201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1464, 1, 0, 2, "おはようございます♪\n少し歌の練習を\nしてきますね", 1, 104201, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1465, 2, 0, 2, "もうお昼なんですか？\n練習に夢中で\n気づきませんでした", 1, 104201, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1466, 3, 0, 2, "明日の予定は…\nアイドルはやることが\n多いみたいで…", 1, 104201, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1467, 1, 0, 3, "見た目については\n前よりも気をつけてと\nノゾミに言われてます", 1, 104201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1468, 2, 0, 3, "歌の調子をよくする\nためには休息も\n必要ですよね…", 1, 104201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1469, 3, 0, 3, "この子たちを\n相手に練習して\nみようかな…♪", 1, 104201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1470, 4, 0, 3, "…疲れてるのかな\n落ち着けているのが\n自分でもわかります", 1, 104201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1471, 5, 0, 3, "ここにいると\n穏やかな旋律が\n浮かんできます…", 1, 104201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1472, 6, 0, 3, "歌うためには\n体力づくりは\n欠かせません", 1, 104201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1473, 7, 0, 3, "歌った後は\nお腹が空いて\nしまうんです", 1, 104201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1474, 8, 0, 3, "音楽について\n理論も勉強\nしないと…", 1, 104201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1476, 1, 0, 1, "歌うことの楽しさ\n分かってきました\nもっと歌いたいな…", 1, 104202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1477, 2, 0, 1, "練習してもあんまり\n疲れなくなった…\nアイドルのおかげかな", 1, 104202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1478, 3, 0, 1, "精霊も強くなったし\nもっとたくさんの人を\n守りたいです", 1, 104202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1479, 4, 0, 1, "最近、よく頭の中に\n歌が浮かんできます\n不思議な感覚…", 1, 104202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1480, 5, 0, 1, "楽譜を買いました\n思いついた歌を\n書き留めておくために", 1, 104202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1481, 101, 0, 1, "浜辺で曲を書こうと\n思うんですけど…\n一緒に行きませんか？", 8, 104202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1482, 102, 0, 1, "私の中にある\n私だけの歌…\n作ってみようかな？", 8, 104202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1483, 1, 0, 2, "守るべき人たちに\n笑顔でいてもらうのも\n大切なんですね", 1, 104202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1484, 2, 0, 2, "歌える曲を増やそうと\n思って。どんな曲が\n聞きたいですか？", 1, 104202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1485, 3, 0, 2, "魔物退治ですか！？\n…違います？\n良かった…", 1, 104202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1486, 4, 0, 2, "いつも応援ありがとう\nございます♪　これは\nあ、握手の練習です！", 1, 104202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1487, 101, 0, 2, "…歌って欲しい？\n喜んで♪\nどの歌にしましょう？", 8, 104202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1488, 102, 0, 2, "～～♪　あれ？\n今私、歌ってました？\n気づきませんでした", 8, 104202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1489, 1, 0, 2, "呼吸を深く、大きく…\nこれでちゃんと\n目が覚めますよ", 1, 104202, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1490, 2, 0, 2, "少し休んだら\n午後の練習に\n行ってきますね", 1, 104202, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1491, 3, 0, 2, "曲を作っていたら\nもうこんな時間…\nおやすみなさい", 1, 104202, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1492, 1, 0, 3, "見た目については\n前よりも気をつけてと\nノゾミに言われてます", 1, 104202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1493, 2, 0, 3, "あ～…♪\nよく体がほぐれます…\nいい歌が歌えそうです", 1, 104202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1494, 3, 0, 3, "かわいいと思う\n気持ちを、歌にしても\nいいかも知れませんね", 1, 104202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1495, 4, 0, 3, "…疲れてるのかな\n落ち着けているのが\n自分でもわかります", 1, 104202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1496, 5, 0, 3, "ここにいると\n穏やかな旋律が\n浮かんできます…", 1, 104202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1497, 6, 0, 3, "もっと体力をつけて\n最高のライブを\nファンに届けます！", 1, 104202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1498, 7, 0, 3, "これでも一応\n体形には気をつけて\n食べてるんですよ？", 1, 104202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1499, 8, 0, 3, "読書は歌に\n新しい風を\n吹き込んでくれます", 1, 104202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1501, 1, 0, 1, "店の新メニューでも\n考えるかな", 1, 104301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1502, 2, 0, 1, "あたしの店から、\n出前を届けようか？", 1, 104301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1503, 3, 0, 1, "パトロール、\nパトロール…\nここは異常なしかな？", 1, 104301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1504, 4, 0, 1, "腹へったら食う！\n悩んだら寝る！\nそれが一番だろ？", 1, 104301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1505, 5, 0, 1, "のんびりし過ぎるのも\n…性に合わねぇな", 1, 104301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1506, 101, 0, 1, "そろそろあいつ、\n腹すかしてるん\nじゃないかな？", 4, 104301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1507, 102, 0, 1, "今日の献立は…\nあいつの好きなものに\nしてやるか", 8, 104301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1508, 1, 0, 2, "強くなりたいなら、\nあたしがビシバシ\n鍛えてやるよ", 1, 104301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1509, 2, 0, 2, "天気がいい日は\n外で運動したく\nならねぇか？", 1, 104301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1510, 3, 0, 2, "腹減ったか？　なんか\n作ってやろうか？", 1, 104301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1511, 4, 0, 2, "ん？　なんだよ、\n相談事か？\nなんでも言ってくれ", 1, 104301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1512, 101, 0, 2, "お前が好きな飯って\nなんだ？\n今度、作ってやるよ", 4, 104301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1513, 102, 0, 2, "お前って不思議だよな\n一緒にいるとなんだか\n…なんでもねーよ", 8, 104301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1514, 1, 0, 2, "気持ちいい朝だな！\n体を動かしたい気分だ\n付き合ってくれるか", 1, 104301, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1515, 2, 0, 2, "昼飯もう食ったか？\nまだなら、あたしが\nなんか作ってやるよ", 1, 104301, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1516, 3, 0, 2, "日中たっぷり動いたら\nそのぶん夜はしっかり\n休んどけよな", 1, 104301, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1517, 1, 0, 3, "た、たまにはその…\nかわいい格好とか\nしても…", 1, 104301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1518, 2, 0, 3, "ふわぁ、\n一休みすっかなぁ～", 1, 104301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1519, 3, 0, 3, "うっ！　か、かわいい\nじゃねぇか", 1, 104301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1520, 4, 0, 3, "へー　いい匂いだな\nあいつもこういうの\n好きなのかな…", 1, 104301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1521, 5, 0, 3, "ふわぁ〜！\n温かいと、つい\nウトウトしちまうな", 1, 104301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1522, 6, 0, 3, "オラオラオラーーー！\n燃えるぜーーー！", 1, 104301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1523, 7, 0, 3, "いい味してるぜ！\nうちの店でも\n出してみたいな", 1, 104301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1524, 8, 0, 3, "文字を読んでると\nどうしても眠く\nなっちまうんだよなぁ", 1, 104301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1526, 1, 0, 1, "パトロール終了～\n本日も異常なしっと！", 1, 104302, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1527, 2, 0, 1, "あたしの店から\n出前を届けようか？", 1, 104302, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1528, 3, 0, 1, "あいつ、早く\n帰ってこないかな", 1, 104302, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1529, 4, 0, 1, "あいつ\n腹すかせてないと\nいいんだけど…", 1, 104302, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1530, 5, 0, 1, "のんびりし過ぎるのも\n…性に合わねぇな", 1, 104302, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1531, 101, 0, 1, "あいつの事を思うと\n胸があったかくなる？\nなんだコレ…？", 8, 104302, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1532, 102, 0, 1, "今日の献立は…\nあいつの好きなものに\nしてやるか", 8, 104302, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1533, 1, 0, 2, "ん？　なんだよ\n相談事か？\nなんでも言ってくれ", 1, 104302, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1534, 2, 0, 2, "天気がいい日は\n外で運動したく\nならねぇか？", 1, 104302, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1535, 3, 0, 2, "腹減ったか？　なんか\n作ってやろうか？", 1, 104302, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1536, 4, 0, 2, "腹へったら食う！\n悩んだら寝る！\nそれが一番だろ？", 1, 104302, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1537, 101, 0, 2, "あのさ…できれば\nもっとそばに…いや\nなんでもねぇ忘れろ！", 8, 104302, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1538, 102, 0, 2, "お前って不思議だよな\n一緒にいるとなんだか\n…なんでもねーよ", 8, 104302, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1539, 1, 0, 2, "気持ちいい朝だな！\n体を動かしたい気分だ\n付き合ってくれるか？", 1, 104302, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1540, 2, 0, 2, "昼飯もう食ったか？\nまだなら、あたしが\nなんか作ってやるよ", 1, 104302, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1541, 3, 0, 2, "あんま夜更しすんなよ\n明日に響いたら\n大変だろ？", 1, 104302, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1542, 1, 0, 3, "た、たまにはその…\nかわいい格好とか\nしても…", 1, 104302, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1543, 2, 0, 3, "ふぅ～♪\n疲れがとれるな～", 1, 104302, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1544, 3, 0, 3, "うっ！　か、かわいい\nじゃねぇか", 1, 104302, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1545, 4, 0, 3, "へー　いい匂いだな\nあいつもこういうの\n好きなのかな…", 1, 104302, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1546, 5, 0, 3, "ふわぁ～！\n温かいと、つい\nウトウトしちまうな", 1, 104302, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1547, 6, 0, 3, "オラオラオラーーー！\n燃えるぜーーー！", 1, 104302, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1548, 7, 0, 3, "いい味してるぜ！\nうちの店でも\n出してみたいな", 1, 104302, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1549, 8, 0, 3, "料理だけじゃなく\n経営術とかも\n学んどかねぇと…", 1, 104302, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1551, 1, 0, 1, "最近なにも\nしてくれません…\n焦らしてるんですね", 1, 104501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1552, 2, 0, 1, "ぐふふ、じゅるり…\nい、いけない、また\nいつの間にかよだれが", 1, 104501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1553, 3, 0, 1, "好きな武器といえば\n剣より槍より\nやっぱり鞭…ぐふふ", 1, 104501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1554, 4, 0, 1, "「無数の触手が\nクウカの柔肌に\n食い込み」…ぐふふ", 1, 104501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1555, 5, 0, 1, "もしドＳさんが\nＭに転じたらと思うと\n身の毛がよだちます！", 1, 104501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1556, 101, 0, 1, "ドＳさんを登場\nさせるだけで、妄想に\n無限の広がりが！", 4, 104501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1557, 102, 0, 1, "あっ…\nジロジロ見てたの\n気づいちゃいました？", 8, 104501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1558, 1, 0, 2, "視線を感じると\n思ったら…やはり\nドＳさんでしたか", 1, 104501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1559, 2, 0, 2, "今日は\nどんなヒドイことを\nしてくれるんですか？\nぐふふふ", 1, 104501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1560, 3, 0, 2, "いつの間にそこに！？\nまさかクウカを監視\nしていたのですか！？", 1, 104501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1561, 4, 0, 2, "クウカに触るつもり\nですか？　触るつもり\nなのですねっ！？", 1, 104501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1562, 101, 0, 2, "クウカにナニをする\nつもりですか…\n期待してしまいます！", 4, 104501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1563, 102, 0, 2, "ドＳさんの視界に入る\nだけでドキドキが…\nこれが恋！？", 8, 104501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1564, 1, 0, 2, "おはようございます。\n朝からお盛んですね？\nぐふふ…", 1, 104501, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1565, 2, 0, 2, "こんなにも日の高い\nうちからドＳさんは…\nじゅるり", 1, 104501, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1566, 3, 0, 2, "夜はこれから、\nですよ…\nぐふ、ぐふふふ…", 1, 104501, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1567, 1, 0, 3, "キレイにおめかし\nしたのに力ずくで…\nぐふふふ", 1, 104501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1568, 2, 0, 3, "ソファの背にクウカを\n押し付けて…ぐふふ\nいいですね\nソファドン", 1, 104501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1569, 3, 0, 3, "「見た目とは裏腹に\nそれは激しく攻め\nたてて」…ぐふふふ", 1, 104501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1570, 4, 0, 3, "香りを吸ったクウカの\n頬は紅潮し目はうるみ\nもはや抗う力も…！", 1, 104501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1571, 5, 0, 3, "ああ、身体が\n火照って…\nぐふ、ぐふふ…", 1, 104501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1572, 6, 0, 3, "はぁ、はぁ、こうして\nクウカの体力を\n奪ってから…ぐふふ", 1, 104501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1573, 7, 0, 3, "食欲が満たされたら\nつ、次はやはり…！\nそうなのですね！？", 1, 104501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1574, 8, 0, 3, "この本の下に、\nそういう本を\nカムフラージュ？", 1, 104501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1576, 1, 0, 1, "「ドＳさんと踊り子\n乱舞の一夜」…\n次の日記はこれで…", 1, 104502, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1577, 2, 0, 1, "最近なにも\nしてくれません…\n焦らしてるんですね", 1, 104502, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1578, 3, 0, 1, "も、妄想が\n足りません…\n禁断症状がっ…！", 1, 104502, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1579, 4, 0, 1, "もしドＳさんが\nＭに転じたらと思うと\n身の毛がよだちます！", 1, 104502, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1580, 5, 0, 1, "次はどんなドＳを\nされるのか、想像する\nだけで…ぐふふふ", 1, 104502, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1581, 101, 0, 1, "じっとしてると\nドＳさんのことばかり\n考えてしまいますね", 8, 104502, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1582, 102, 0, 1, "妄想が止まらなくて\nクウカどうにかなって\nしまいそうです！", 8, 104502, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1583, 1, 0, 2, "ドＳさんといると体が\n火照って…クウカは\nいやらしい子です！", 1, 104502, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1584, 2, 0, 2, "あっ…\nジロジロ見てたの\n気づいちゃいました？", 1, 104502, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1585, 3, 0, 2, "ドＳさんのおかげで\n新しい扉が開けそう\nです…じゅるり", 1, 104502, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1586, 4, 0, 2, "いやー！　ドＳさんに\n襲われ…襲って\nくれないんですか？", 1, 104502, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1587, 101, 0, 2, "クウカはドＳさんに\n身も心も堕とされて\nしまったのですね", 8, 104502, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1588, 102, 0, 2, "ドＳさんがお望みなら\nクウカは\nどんなことだって！", 8, 104502, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1589, 1, 0, 2, "朝だからこそ\nできる妄想が\nあります…ぐふふ", 1, 104502, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1590, 2, 0, 2, "こんな日の高いうち\nからドＳさんは…\nじゅるり", 1, 104502, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1591, 3, 0, 2, "今夜は一晩中…\nなんですね？\nぐふふ…", 1, 104502, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1592, 1, 0, 3, "意外と難しいんですよ\n正しい亀甲縛りと\nいうものは…", 1, 104502, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1593, 2, 0, 3, "くつろいで油断\nしているスキに何を\nされるのでしょう！？", 1, 104502, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1594, 3, 0, 3, "「見た目とは裏腹に\nそれは激しく攻め\nたてて」…ぐふふふ", 1, 104502, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1595, 4, 0, 3, "これは禁断の香り…\nああっ一段階上の\n妄想が開花するうぅ！", 1, 104502, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1596, 5, 0, 3, "ああっ、暖気がまるで\nロウソクのように\nクウカを攻めますっ！", 1, 104502, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1597, 6, 0, 3, "激しい攻めを受けきる\nためには、鍛えないと\nいけないのですね", 1, 104502, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1598, 7, 0, 3, "こんなにいっぱい\n入りません…\nお腹にっ！", 1, 104502, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1599, 8, 0, 3, "どんな本も\nよく見ると\nいやらしいです…！", 1, 104502, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1601, 1, 0, 1, "この前遊んだ猫、\nモッフモフ\nだったにゃあ…♪", 1, 104601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1602, 2, 0, 1, "ほっぺやお腹が、\n肉球ぐらいぷにぷに…\nこれはまずいにゃ…", 1, 104601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1603, 3, 0, 1, "大きな仕事が\n決まったにゃ！\n腕が鳴るにゃ～♪", 1, 104601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1604, 4, 0, 1, "おやつのたい焼き、\nつぶあんとこしあん、\nどっちに\nしようかにゃ？", 1, 104601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1605, 5, 0, 1, "うにゃー…\nもうちょっとお昼寝\nしたかったにゃ…\nむにゃむにゃ…", 1, 104601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1606, 101, 0, 1, "キミに呼ばれると、\n猫まっしぐらな気分に\nなっちゃうにゃ♪", 4, 104601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1607, 102, 0, 1, "キミのおかげで\nギルドの仕事も\n大成功だにゃ♪\nありがとにゃ♪", 8, 104601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1608, 1, 0, 2, "うずうず…その指、\nじゃれついても\nいいかにゃ？", 1, 104601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1609, 2, 0, 2, "にゃ？　あたしに何か\nご入り用かにゃ？\nお安くしとくにゃ～♪", 1, 104601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1610, 3, 0, 2, "うーん、なんだか\n退屈にゃ～\n遊んで、遊んで～？", 1, 104601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1611, 4, 0, 2, "素早い動きのコツ？\nタダじゃ教えて\nあげられないにゃ～♪", 1, 104601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1612, 101, 0, 2, "そんなにあたしを\nなでたいにゃ？\nキミならなでさせて\nあげてもいいかにゃー", 4, 104601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1613, 102, 0, 2, "ふにゃあ、ねえねえ、\n一緒にゴロゴロ\nしようにゃあ？", 8, 104601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1614, 1, 0, 2, "いい朝にゃ！\n顔を洗ってくるにゃ！", 1, 104601, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1615, 2, 0, 2, "むーん、そろそろ\nお昼…猫たちにも\n猫缶を…", 1, 104601, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1616, 3, 0, 2, "ふわあ…眠いにゃ…\n猫は布団で\n丸くなるにゃ", 1, 104601, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1617, 1, 0, 3, "客商売では、\n外見のチェックも\n欠かせないのにゃ！", 1, 104601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1618, 2, 0, 3, "うにゃ～！\nお昼寝に\nぴったりにゃ", 1, 104601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1619, 3, 0, 3, "これ…量産したら\n子どもにバカ売れ\nしそうだにゃ！", 1, 104601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1620, 4, 0, 3, "この灯り、まるで\n猫みたいな\n癒し効果にゃ！", 1, 104601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1621, 5, 0, 3, "ふにゃあ、\nあったかいと猫は丸く\nなっちゃうのにゃあ…", 1, 104601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1622, 6, 0, 3, "本当は外で\n運動したいん\nだけどにゃ", 1, 104601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1623, 7, 0, 3, "むーん、\n美味しいにゃー！\nたい焼きに\n匹敵するにゃ！", 1, 104601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1624, 8, 0, 3, "ふむふむ…猫の\nしつけ方は、と…", 1, 104601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1626, 1, 0, 1, "かわいいネコの置物を\nゲットしたにゃ！\nどこに飾ろうか\nにゃ～♪", 1, 104602, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1627, 2, 0, 1, "仕事の交渉がうまく\n行ったにゃ～♪\nいい気分だにゃ～♪", 1, 104602, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1628, 3, 0, 1, "ねこじゃらしには\n食いついちゃうにゃ\nびしっ！　びしっ！", 1, 104602, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1629, 4, 0, 1, "ほっぺやお腹が、肉球\nぐらいぷにぷに…\nこれはまずいにゃ…", 1, 104602, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1630, 5, 0, 1, "さっきのたいやき\nおいしかったにゃ♪\nごろごろごろ…", 1, 104602, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1631, 101, 0, 1, "手作りのたい焼き\nあいつは食べて\nくれるかにゃ…？", 8, 104602, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1632, 102, 0, 1, "あいつには、いつか\nたっぷり恩返しが\nしたいにゃ…", 8, 104602, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1633, 1, 0, 2, "にゃーに？　あたしの\nことがそんなに気に\nなるのかにゃ？", 1, 104602, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1634, 2, 0, 2, "あたしのこと…\nしっかり捕まえ\nといてにゃ？", 1, 104602, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1635, 3, 0, 2, "あたしの力が\n必要かにゃ？　猫の手\n貸してあげるにゃ♪", 1, 104602, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1636, 4, 0, 2, "たいやきとクレープを\n合わせれば…\n新しい商売のニオイが\nするにゃ…！", 1, 104602, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1637, 101, 0, 2, "ふにゃあ、ねえねえ\n一緒にゴロゴロ\nしようにゃあ？", 8, 104602, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1638, 102, 0, 2, "キミと一緒にいると\n楽しくなってつい\nじゃれちゃうにゃ！", 8, 104602, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1639, 1, 0, 2, "いい朝にゃ！\n顔を洗ってくるにゃ！", 1, 104602, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1640, 2, 0, 2, "そろそろおっひる～\nにゃーんか\n食べたい…", 1, 104602, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1641, 3, 0, 2, "うーん、眠くなって\nきたにゃ。あたしは\n夜行性じゃないのにゃ", 1, 104602, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1642, 1, 0, 3, "にゃにゃ…\n髪が乱れてるにゃ", 1, 104602, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1643, 2, 0, 3, "ふっかふかにゃ～\n猫がねころん\nじゃうにゃ", 1, 104602, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1644, 3, 0, 3, "これ、猫と匹敵する\nぐらいかわいいにゃ！", 1, 104602, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1645, 4, 0, 3, "猫でもこわくない\n優しい灯りにゃ！", 1, 104602, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1646, 5, 0, 3, "ぽっかぽか\nしあわせな気分にゃ～", 1, 104602, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1647, 6, 0, 3, "雨の日でも運動\nできるのは、濡れ\nなくて便利だにゃ！", 1, 104602, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1648, 7, 0, 3, "美味しいにゃ～！\nこれ、朝昼晩\n食べたいにゃ！", 1, 104602, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1649, 8, 0, 3, "本を読んで\n猫の気持ちを\n勉強するのにゃ！", 1, 104602, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1651, 1, 0, 1, "内職手伝える人\nいないかしら？", 1, 104801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1652, 2, 0, 1, "次の依頼は、\nこういう計画で…\nうん、完璧ね！", 1, 104801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1653, 3, 0, 1, "次の特売セールに\n向けて、しっかり\n準備しないと！", 1, 104801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1654, 4, 0, 1, "配達の後に売り子\n次に採取のバイト…\nうん、効率的ね", 1, 104801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1655, 5, 0, 1, "一日に受ける\nバイトの数、もう少し\n増やしたいわね", 1, 104801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1656, 101, 0, 1, "師匠として\n頼りにされたいし\nもっと頑張らなきゃ！", 4, 104801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1657, 102, 0, 1, "男の子と効率的に\n仲良くなる方法…\nこればっかりは難題ね", 8, 104801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1658, 1, 0, 2, "あら、\nちょうどよかった\n今から一緒に\nバイトでもどう？", 1, 104801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1659, 2, 0, 2, "相談事かしら？\nそれくらいなら\n無料でいいわよ", 1, 104801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1660, 3, 0, 2, "背後の気配なら\nすぐ分かるわ\n試してみる？", 1, 104801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1661, 4, 0, 2, "道具やお金の管理は\n大丈夫？　手伝うから\nいつでも言って", 1, 104801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1662, 101, 0, 2, "暇ができたから\n働きたいけど…\nキミとの時間も大切ね", 4, 104801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1663, 102, 0, 2, "ねえ、あの…\n私はキミにとって…\nいや、何でもないわ", 8, 104801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1664, 1, 0, 2, "おはよう！\n今日も朝から\nバイトよ！", 1, 104801, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1665, 2, 0, 2, "お昼時ね。\n少し休憩したら、\nまた働くわよ！", 1, 104801, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1666, 3, 0, 2, "もうこんな時間！\n明日もバイトで\n朝早いのよ…", 1, 104801, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1667, 1, 0, 3, "接客業の依頼が\nある時は、外見も\nキレイにしないとね", 1, 104801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1668, 2, 0, 3, "はぁ～バイトで\n疲れたから癒される～", 1, 104801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1669, 3, 0, 3, "これを内職で\n作ったら\n儲かるかしら？", 1, 104801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1670, 4, 0, 3, "リフレッシュできたわ\nこれでまた、バリバリ\n働けるわね！", 1, 104801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1671, 5, 0, 3, "風邪を引いたら効率が\n落ちちゃうから、\n暖めないと！", 1, 104801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1672, 6, 0, 3, "働き続けるには\n健康第一！　それには\n運動が一番よ", 1, 104801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1673, 7, 0, 3, "もぐもぐ…\n腹が減っては\n戦ができぬ、よ！", 1, 104801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1674, 8, 0, 3, "日々絶えず学んで、\n生活に活かすのよ！", 1, 104801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1676, 1, 0, 1, "家計簿をつけなきゃ！\n覚えている時に\nやっておかないとね", 1, 104802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1677, 2, 0, 1, "次の依頼は\nこういう計画で…\nうん、完璧ね！", 1, 104802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1678, 3, 0, 1, "ギルドの経費\nあとちょっとだけ\n削れそうね…", 1, 104802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1679, 4, 0, 1, "少しでも時給のいい\nバイトがしたいけど\nなかなか\n出会えないのよ", 1, 104802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1680, 5, 0, 1, "今度のバイトも\nなかなか大変そう\nね…腕が鳴るわ", 1, 104802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1681, 101, 0, 1, "男の子と効率的に\n仲良くなる方法…\nこればっかりは難題ね", 8, 104802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1682, 102, 0, 1, "私、どうしたんだろ？\n仕事中もずっと\n彼のこと考えてる…", 8, 104802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1683, 1, 0, 2, "この間はヘルプに\n来てくれてありがとう\n頼んで正解だったわ", 1, 104802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1684, 2, 0, 2, "見つけた！　ちょうど\n頼みたいことが\nあるんだけど、いい？", 1, 104802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1685, 3, 0, 2, "今から\n休憩するところなの\n一緒にどう？", 1, 104802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1686, 4, 0, 2, "ん？　どうしたの？\n私のことが\n気になるの？", 1, 104802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1687, 101, 0, 2, "…たまには甘えても\nいい？　私だって\n弱る時はあるのよ", 8, 104802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1688, 102, 0, 2, "暇ができたから\n働きたいけど…\nキミとの時間も大切ね", 8, 104802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1689, 1, 0, 2, "おはよう！\nいい朝ね。一緒に\nランニングでもいく？", 1, 104802, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1690, 2, 0, 2, "お昼時ね\n少し休憩したら\nまた働くわよ！", 1, 104802, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1691, 3, 0, 2, "もうこんな時間！\n明日もバイトで\n朝早いのよ…", 1, 104802, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1692, 1, 0, 3, "女の子らしく…\nどうも忘れちゃう\nのよね", 1, 104802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1693, 2, 0, 3, "ここで少し\n休んだら\nまたバイトよ！", 1, 104802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1694, 3, 0, 3, "これを内職で\n作ったら\n儲かるかしら？", 1, 104802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1695, 4, 0, 3, "仕事から帰ってきて\nこういうのがあると\n素敵ね", 1, 104802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1696, 5, 0, 3, "しっかり身体を\n温めて、血流を\nよくしなくっちゃ", 1, 104802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1697, 6, 0, 3, "これで休憩中も\n無駄なく\n鍛えられるわ♪", 1, 104802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1698, 7, 0, 3, "もぐもぐ…よく\n食べて、よく働く！\nそれが私のモットーよ", 1, 104802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1699, 8, 0, 3, "日々絶えず学んで\n生活に活かすのよ！", 1, 104802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1701, 1, 0, 1, "暇だし、お料理と\nお掃除とお洗濯と\n買い物しようかな？", 1, 104901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1702, 2, 0, 1, "こうして歩いていれば\n弟くんが声をかけて\nくれるはず…", 1, 104901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1703, 3, 0, 1, "弟くんがどんな生活を\nしてるのか調べるのも\nお姉ちゃんの務め！", 1, 104901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1704, 4, 0, 1, "くんくん、\n弟くんのにおい…\n近くにいるね？", 1, 104901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1705, 5, 0, 1, "お姉ちゃんの痕跡を\n部屋中に\n残しちゃおっと♪", 1, 104901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1706, 101, 0, 1, "この部屋を、\n私と弟くんの\n愛の巣に…", 4, 104901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1707, 102, 0, 1, "はぁ～…弟くん\n禁断症状だよ～", 8, 104901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1708, 1, 0, 2, "弟くんの部屋は\nお姉ちゃんの\n部屋だよね～", 1, 104901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1709, 2, 0, 2, "いいこいいこ～♪\nどう、お姉ちゃんの\nナデナデは？", 1, 104901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1710, 3, 0, 2, "姉と弟の外出は\nデートって\nいうのかな？", 1, 104901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1711, 4, 0, 2, "もう！\nイタズラする子には\nお姉ちゃんチョップ！", 1, 104901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1712, 101, 0, 2, "お姉ちゃんが\nぎゅ～ってして\nあげるね♪", 4, 104901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1713, 102, 0, 2, "あ、あのね…\nたまにはお姉ちゃんも\n甘えていい？", 8, 104901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1714, 1, 0, 2, "着替えは？　荷物は？\nお出かけの準備は\nお姉ちゃんが手伝うね", 1, 104901, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1715, 2, 0, 2, "出かけるなら\n明るいうちに\n帰ってくるんだよ！", 1, 104901, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1716, 3, 0, 2, "まだ起きてるの？\nそれじゃあ、\nお姉ちゃんも\n夜更かししちゃお☆", 1, 104901, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1717, 1, 0, 3, "きれいなお姉ちゃん\n好きだよね♪", 1, 104901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1718, 2, 0, 3, "ここにお姉ちゃんの\n匂いを、た～っぷり\n残しておこうっと♪", 1, 104901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1719, 3, 0, 3, "かわいい♪\nお姉ちゃんチョップ！", 1, 104901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1720, 4, 0, 3, "この香り、\n弟くん好きかな？", 1, 104901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1721, 5, 0, 3, "これ、弟くんみたいに\nあったかいね～", 1, 104901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1722, 6, 0, 3, "ダイエットダイエット\nきれいなお姉ちゃんに\nならなくちゃ", 1, 104901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1723, 7, 0, 3, "あ、これ\n弟くんが好きな\n味付けだね！", 1, 104901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1724, 8, 0, 3, "弟くんが読んでいいか\nお姉ちゃんが先に\n中を確認しなくちゃ！", 1, 104901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1726, 1, 0, 1, "暇だし、お料理と\nお掃除とお洗濯と\n買い物しようかな？", 1, 104902, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1727, 2, 0, 1, "こうして歩いていれば\n弟くんが声をかけて\nくれるはず…", 1, 104902, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1728, 3, 0, 1, "弟くんのお友達は\nみんな私の妹だよ♪", 1, 104902, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1729, 4, 0, 1, "弟くんがどんな生活を\nしてるのか調べるのも\nお姉ちゃんの務め！", 1, 104902, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1730, 5, 0, 1, "はっ！\n弟くんが困ってる\n気がする！", 1, 104902, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1731, 101, 0, 1, "はぁ～…弟くん、\n禁断症状だよ～", 8, 104902, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1732, 102, 0, 1, "お姉ちゃんの痕跡を\n部屋中に\n残しちゃおっと♪", 8, 104902, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1733, 1, 0, 2, "お姉ちゃん\nキミのお願いなら\nなんでも聞いて\nあげちゃうぞっ", 1, 104902, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1734, 2, 0, 2, "弟くんの部屋は\nお姉ちゃんの\n部屋だよね～", 1, 104902, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1735, 3, 0, 2, "お姉ちゃんの胸に\n飛び込んでおいでっ♪", 1, 104902, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1736, 4, 0, 2, "いいこいいこ～♪\nどう、お姉ちゃんの\nナデナデは？", 1, 104902, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1737, 101, 0, 2, "あ、あのね…\nたまにはお姉ちゃんも\n甘えていい？", 8, 104902, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1738, 102, 0, 2, "お風呂にする？\nごはんにする？\nそれとも…うふふ♪", 8, 104902, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1739, 1, 0, 2, "着替えは？　荷物は？\nお出かけの準備は\nお姉ちゃんが手伝うね", 1, 104902, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1740, 2, 0, 2, "お姉ちゃんが\nお昼ごはん\n作ってあげようか？", 1, 104902, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1741, 3, 0, 2, "眠れないの？\nお姉ちゃんが子守歌\n歌ってあげるね！", 1, 104902, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1742, 1, 0, 3, "弟くんを\n誘惑しちゃうぞ～♪", 1, 104902, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1743, 2, 0, 3, "ここにお姉ちゃんの\n匂いを、た～っぷり\n残しておこうっと♪", 1, 104902, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1744, 3, 0, 3, "かわいい♪\nお姉ちゃんチョップ！", 1, 104902, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1745, 4, 0, 3, "お肌に潤いが\nでてきたみたい～♪", 1, 104902, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1746, 5, 0, 3, "これ、弟くんみたいに\nあったかいね～", 1, 104902, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1747, 6, 0, 3, "ダイエットダイエット\nきれいなお姉ちゃんに\nならなくちゃ", 1, 104902, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1748, 7, 0, 3, "弟くんの手作りなら\nもっとおいしくなると\n思うんだけどなぁ～", 1, 104902, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1749, 8, 0, 3, "弟くんをきちんと\n教育するために\nお姉ちゃんは勉強を\nかかさないのだ☆", 1, 104902, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1751, 1, 0, 1, "ん～、エレガントで\n大人なお部屋ね！\nアタシにぴったり！", 1, 105001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1752, 2, 0, 1, "身長とバスト以外は\n完璧な大人\nなのになぁ～", 1, 105001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1753, 3, 0, 1, "アタシの大人コーデで\nあいつも夢中ね♪", 1, 105001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1754, 4, 0, 1, "朝顔の観察したら\n理系女子になれる\nかしら？", 1, 105001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1755, 5, 0, 1, "レディは、どんな時も\nおしとやかに…って\nアタシも騒ぎたいー！", 1, 105001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1756, 101, 0, 1, "大人っぽい子が\n多いわね…アタシも\n負けてられないわ！", 4, 105001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1757, 102, 0, 1, "アタシが待ってる\nなんて、アイツは\n幸せものよねー", 8, 105001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1758, 1, 0, 2, "わーい、アメだー♪\n食べる食べるー♪", 1, 105001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1759, 2, 0, 2, "熱いハニーラテが\n飲みたーい！\n淹れてー淹れてー！", 1, 105001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1760, 3, 0, 2, "なによ？　もしかして\n好きな子にイタズラ\nしたくなるってやつ？", 1, 105001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1761, 4, 0, 2, "アタシに合う服\n雑誌で探そうっと！\nサイズじゃないわ、\nデザインとかの話よ！", 1, 105001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1762, 101, 0, 2, "ねーねー！\nアタシ、かわいい？\nかわいいわよね？", 4, 105001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1763, 102, 0, 2, "これから出かけるの？\nしっかりアタシを\nエスコートしてね！", 8, 105001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1764, 1, 0, 2, "朝のスキンケアは\n大事なの！", 1, 105001, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1765, 2, 0, 2, "お腹すいたー\nダイエット\nやめちゃおうかなー", 1, 105001, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1766, 3, 0, 2, "エイジングケアを\nしないとねー", 1, 105001, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1767, 1, 0, 3, "この角度のほうが\n大人っぽく見えるかな\nそれともこっちかしら", 1, 105001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1768, 2, 0, 3, "あ！　これ雑誌で\n見たことあるわ～\nだからきっと\nオシャレなのよ！", 1, 105001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1769, 3, 0, 3, "か、かわいい♪　けど\n大人はこんなもの…\nでも、かわいいし～！", 1, 105001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1770, 4, 0, 3, "オシャレ～♪\n大人なレディって\n感じよね♪", 1, 105001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1771, 5, 0, 3, "あったかーい♪\nここで宿題\nしようかしら？", 1, 105001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1772, 6, 0, 3, "運動したら成長\nしないかしら？\n背とか胸とか…", 1, 105001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1773, 7, 0, 3, "食べすぎると\n太っちゃうな…\nでも、おいしいし～っ", 1, 105001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1774, 8, 0, 3, "おもしろーい！\n…でも絵本って\n子供っぽいかしら？", 1, 105001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1776, 1, 0, 1, "レディは、どんな時も\nおしとやかに…って\nアタシも騒ぎたいー！", 1, 105002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1777, 2, 0, 1, "恋人にするなら\nイケメンか\nただイケよね～", 1, 105002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1778, 3, 0, 1, "もう少し背が欲しいな\n…もっと牛乳を\n飲まなくちゃ！", 1, 105002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1779, 4, 0, 1, "朝顔の観察したら\n理系女子になれる\nかしら？", 1, 105002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1780, 5, 0, 1, "こ、これは補習の\nお知らせプリント！\nアイツに見つかる前に\n隠さなくちゃ！", 1, 105002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1781, 101, 0, 1, "アイツおっそいなー\n別に会いたいわけじゃ\nないけど…\n来ないと気になる…", 8, 105002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1782, 102, 0, 1, "アタシが待ってる\nなんて、アイツは\n幸せものよねー ", 8, 105002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1783, 1, 0, 2, "なーに？\nアタシに甘えたいの？", 1, 105002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1784, 2, 0, 2, "アタシに合う服\n雑誌で探そうっと！\nサイズじゃないわ\nデザインとかの話よ！", 1, 105002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1785, 3, 0, 2, "頭撫でないで！\nお子様みたいに\nあつかわないでって\n言ってるのに～！", 1, 105002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1786, 4, 0, 2, "壁ドンの練習…\nあれ？　壁ドンって\n男がするんだっけ？", 1, 105002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1787, 101, 0, 2, "ねーねー！\nアタシ、かわいい？\nかわいいわよね？", 8, 105002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1788, 102, 0, 2, "これから出かけるの？\nしっかりアタシを\nエスコートしてね！", 8, 105002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1789, 1, 0, 2, "レディの朝は\n忙しいのよ", 1, 105002, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1790, 2, 0, 2, "お腹すいたー\nダイエット\nやめちゃおうかなー", 1, 105002, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1791, 3, 0, 2, "エイジングケアを\nしないとねー", 1, 105002, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1792, 1, 0, 3, "身だしなみチェック\nオッケー！　これで\nいつアイツが来ても\n大丈夫ね！", 1, 105002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1793, 2, 0, 3, "あ！　これ雑誌で\n見たことあるわ～\nだからきっと\nオシャレなのよ！", 1, 105002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1794, 3, 0, 3, "あたしがもっと\nオシャレにかわいく\nしてあげようか？", 1, 105002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1795, 4, 0, 3, "オシャレ～♪\n大人なレディって\n感じよね♪", 1, 105002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1796, 5, 0, 3, "あったかーい♪\nここで宿題\nしようかしら？", 1, 105002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1797, 6, 0, 3, "運動したら成長\nしないかしら？\n背とか胸とか…", 1, 105002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1798, 7, 0, 3, "レディは、食べ物にも\n気を使うのよ", 1, 105002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1799, 8, 0, 3, "おもしろーい！\n…でも絵本って\n子供っぽいかしら？", 1, 105002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1801, 1, 0, 1, "魔法と科学\nどっちも極めて\nみせるわ", 1, 105101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1802, 2, 0, 1, "仕事も片付いたし\n部屋で\n実験実験…♪", 1, 105101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1803, 3, 0, 1, "たまった治療代の\n請求書を\nまとめないと…", 1, 105101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1804, 4, 0, 1, "新薬が出来たし\nすぐに試したいけど…\n実験台がいないわ", 1, 105101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1805, 5, 0, 1, "部屋にこもりすぎると\n曜日の感覚が\nなくなるわね", 1, 105101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1806, 101, 0, 1, "あの子の記憶を\n取り戻せる薬…\n作れないものかしら", 4, 105101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1807, 102, 0, 1, "好意に気づいて貰える\n薬って、どうすれば\n作れるのかしら", 8, 105101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1808, 1, 0, 2, "あら、ヒマなら\n薬草の整理を\n手伝って", 1, 105101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1809, 2, 0, 2, "もしかして実験に\n付き合ってくれるの？\nえ？　違う？", 1, 105101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1810, 3, 0, 2, "具合が悪い？\nなら診てあげる\nまずは触診から…", 1, 105101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1811, 4, 0, 2, "植物に興味あるの？\nなら教えてあげるわ\n色々とね…", 1, 105101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1812, 101, 0, 2, "キミの傍にいるだけで\n高揚しちゃうのは\n何故かしら…？", 4, 105101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1813, 102, 0, 2, "私はおしおきする\n側のことが多いけど…\nキミにはされたいかも", 8, 105101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1814, 1, 0, 2, "お昼までに調合用の\n花を集めたいの\n手伝ってくれる？", 1, 105101, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1815, 2, 0, 2, "あら、もうお昼？\nそろそろ一息\n入れましょうか", 1, 105101, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1816, 3, 0, 2, "夜も更けて来たわね\n私は朝まで実験だけど\n見学していく？", 1, 105101, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1817, 1, 0, 3, "医者たるもの\nいつだって\n清潔に…♪", 1, 105101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1818, 2, 0, 3, "いいわねこれ\n仮眠の時に\n使わせてもらうわ", 1, 105101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1819, 3, 0, 3, "ぬいぐるみなら\n動物も…", 1, 105101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1820, 4, 0, 3, "いい香り…\n仕事場にも\n置いてみようかしら", 1, 105101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1821, 5, 0, 3, "ここにいると\nついウトウト\nしちゃうわ…", 1, 105101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1822, 6, 0, 3, "座ってる時間が多いと\n運動不足になるし\nたまにはいいわね", 1, 105101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1823, 7, 0, 3, "研究していると\nつい食べるのを\n忘れちゃうのよね", 1, 105101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1824, 8, 0, 3, "実験もいいけど\nいつか成果を\n本にしないと…", 1, 105101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1826, 1, 0, 1, "徹夜はツラいけど\n集中すると時間の\n感覚がなくなって…", 1, 105102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1827, 2, 0, 1, "痛みに悶える顔って\nどうしてあんなに\n興奮するのかしら？", 1, 105102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1828, 3, 0, 1, "彼に罵られたら\nどんな気分かしら\n意外と快感かも？", 1, 105102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1829, 4, 0, 1, "毒を薄めると\n薬になるのって\n今でも不思議だわ", 1, 105102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1830, 5, 0, 1, "綺麗なバラには\nトゲがあるけど\n魅力の一つよね", 1, 105102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1831, 101, 0, 1, "カレをさりげなく\n惚れさせる方法を\n研究するわよ…♪", 8, 105102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1832, 102, 0, 1, "好意に気づいて貰える\n薬って、どうすれば\n作れるのかしら", 8, 105102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1833, 1, 0, 2, "構って欲しいの？\nじゃあ、君の体を\n調べさせて？", 1, 105102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1834, 2, 0, 2, "実験に付き合って\n大丈夫よ、試すのは\nただの栄養剤だから", 1, 105102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1835, 3, 0, 2, "欲しい薬が\nあったら言ってね\n調合してみるから", 1, 105102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1836, 4, 0, 2, "おしおきされたいの？\nキミにだったら\nいつでもしてあげる", 1, 105102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1837, 101, 0, 2, "キミの傍にいるだけで\n高揚しちゃうのは\n何故かしら…？", 8, 105102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1838, 102, 0, 2, "私はおしおきする\n側のことが多いけど…\nキミにはされたいかも", 8, 105102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1839, 1, 0, 2, "朝しか取れない\n植物があるの\n採取しに行くわよ", 1, 105102, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1840, 2, 0, 2, "お昼時ってやけに\n眠たくなるわよね\n…調べたくなるわ", 1, 105102, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1841, 3, 0, 2, "今日くらいは\nゆっくり眠りたいわ\n…キミと一緒にね", 1, 105102, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1842, 1, 0, 3, "身なりについて\n研究するのも\n楽しそうね", 1, 105102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1843, 2, 0, 3, "休息に必要な道具は\n妥協せずに選ぶ！\nこれだけは譲れないわ", 1, 105102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1844, 3, 0, 3, "たまにはこういう\n可愛らしいものに\n触れないとね♪", 1, 105102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1845, 4, 0, 3, "いい香り…\n仕事場にも\n置いてみようかしら", 1, 105102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1846, 5, 0, 3, "仕事がなかったら\nこのまますーっと\n眠りたいのに…", 1, 105102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1847, 6, 0, 3, "運動しないと\n体型の維持が…", 1, 105102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1848, 7, 0, 3, "適度な食事も\n大切よね", 1, 105102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1849, 8, 0, 3, "まさか私が\n恋愛読本を読む\n日が来るなんて…", 1, 105102, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1851, 1, 0, 1, "ぺっぺっ！\n…い、いけないっ！", 1, 105201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1852, 2, 0, 1, "いっけな～い！\nひづめで床に傷が！", 1, 105201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1853, 3, 0, 1, "太っちゃったかも～！\n伐採でダイエット\nしなくっちゃ！", 1, 105201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1854, 4, 0, 1, "きちんと生活すると\n毛づやが良くなる\nみたいなのよね～", 1, 105201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1855, 5, 0, 1, "みんな、体重\nどれくらいなのかな？", 1, 105201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1856, 101, 0, 1, "体力なら自信\nあるんだけど…\n恋にはちょっぴり\n臆病になっちゃう", 4, 105201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1857, 102, 0, 1, "いつか人間の女の子\nみたいなデート、\nしてみたいな～♪", 8, 105201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1858, 1, 0, 2, "私ね「どこにでもいる\n普通の女の子」って\n言葉が憧れなんだ～♪", 1, 105201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1859, 2, 0, 2, "チャームポイント？\n自分では分からない\nけど…まつげかなぁ？", 1, 105201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1860, 3, 0, 2, "リマリマ～♪\nはっ！？\nわ、私ったら今何を？", 1, 105201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1861, 4, 0, 2, "力仕事ならまかせて！", 1, 105201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1862, 101, 0, 2, "人間になりたいな～\nそれか、あなたが\n私みたいな姿に…♪", 4, 105201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1863, 102, 0, 2, "しょうがないなあ、\nこんなにモフらせるの\nあなただけだよ？", 8, 105201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1864, 1, 0, 2, "今朝は寝癖がひどいわ\nしっぽの辺りとか～", 1, 105201, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1865, 2, 0, 2, "ランチは\nおしゃれなカフェで\nしてみたいな～♪", 1, 105201, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1866, 3, 0, 2, "お肌のケアに\nパックしながら\n寝ようかな～", 1, 105201, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1867, 1, 0, 3, "やだっ、\nまた寝ぐせついてる！\n直さないと！", 1, 105201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1868, 2, 0, 3, "ミシッっていった！\n私、重すぎるかも！？", 1, 105201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1869, 3, 0, 3, "あ！　あなたも\n美人さんね～♪", 1, 105201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1870, 4, 0, 3, "はぁ、まったり…\n四足歩行しちゃい\nそうだわ…", 1, 105201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1871, 5, 0, 3, "あったか～い♪\n…私、毛皮だから\n暑すぎるかも", 1, 105201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1872, 6, 0, 3, "な、なんだか野生に\n帰っちゃいそう！", 1, 105201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1873, 7, 0, 3, "ぱくぱく、もぐもぐ、\nぺっぺっ…\nやだ、はしたない！", 1, 105201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1874, 8, 0, 3, "きゃーーー！\nこの恋愛小説\nとっても\nドキドキしちゃう～", 1, 105201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1876, 1, 0, 1, "素敵ね～、居るだけで\n女子力があがりそうな\nお部屋だわ～", 1, 105202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1877, 2, 0, 1, "私、大丈夫かしら？\n他の子に怖がられたり\nしないかな…？", 1, 105202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1878, 3, 0, 1, "動物苑…って\n嫌なギルド名ね…", 1, 105202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1879, 4, 0, 1, "好き、嫌い、好き…\nあっ！　うっかりして\n花占いのお花\n食べちゃった！", 1, 105202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1880, 5, 0, 1, "ここ、女の子らしい\nものでいっぱいね～！\nときめいちゃうわ～♪", 1, 105202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1881, 101, 0, 1, "体力なら自信\nあるんだけど…\n恋にはちょっぴり\n臆病になっちゃう", 8, 105202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1882, 102, 0, 1, "あの人が\n来てくれるまで薪割り\nでもしてよっかな？", 8, 105202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1883, 1, 0, 2, "一緒にお出かけ？\nうれしいけど…\n心の準備が！\nはわわわ！", 1, 105202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1884, 2, 0, 2, "どうしたの？\n急に抱きついてきて…\n癒されたいの？", 1, 105202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1885, 3, 0, 2, "ぺっぺっ！\n…い、いけないっ！", 1, 105202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1886, 4, 0, 2, "メタモルアップルを\nここで食べたら、\nみんな驚くわよね", 1, 105202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1887, 101, 0, 2, "しょうがないなあ、\nこんなにモフらせるの\nあなただけだよ？", 8, 105202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1888, 102, 0, 2, "人間になりたいな～\nそれか、あなたが\n私みたいな姿に…♪", 8, 105202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1889, 1, 0, 2, "今朝は寝癖がひどいわ\nしっぽの辺りとか～", 1, 105202, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1890, 2, 0, 2, "ダイエット中だから\nお昼は抜いてるの～", 1, 105202, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1891, 3, 0, 2, "お肌のケアに\nパックしながら\n寝ようかな～", 1, 105202, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1892, 1, 0, 3, "ちゃんとかわいく\nしてないと刈られ…\n狩られちゃうもんね！", 1, 105202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1893, 2, 0, 3, "ミシッっていった！\n私、重すぎるかも！？", 1, 105202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1894, 3, 0, 3, "あ！　あなたも\n美人さんね～♪", 1, 105202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1895, 4, 0, 3, "いい香り～\n森の中にいるみたいに\nとっても落ち着く～", 1, 105202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1896, 5, 0, 3, "素敵～♪　…\nきゃっ！　私の毛が\nたいへんなことに～！", 1, 105202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1897, 6, 0, 3, "な、なんだか野生に\n帰っちゃいそう！", 1, 105202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1898, 7, 0, 3, "何を食べるかより\n誰と食べるかが\n大事なのよね", 1, 105202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1899, 8, 0, 3, "きゃーーー！\nこの恋愛小説\nとっても\nドキドキしちゃう～", 1, 105202, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1901, 1, 0, 1, "ヴァイスフリューゲル\nを盛り上げる方法、\n共に語らおう！", 1, 105301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1902, 2, 0, 1, "私は子供ではない…！\nが、お菓子は\nいただこう！", 1, 105301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1903, 3, 0, 1, "部屋はきちんと\n片づけておかなくては\n部屋の汚れは\n心の汚れだ", 1, 105301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1904, 4, 0, 1, "むむむ…明日\n目が覚めたら、身長が\n伸びていないものか…", 1, 105301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1905, 5, 0, 1, "お菓子が常備されて\nいる部屋。そんな\nものが、私の好みだ…", 1, 105301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1906, 101, 0, 1, "あいつとの新たな\n鍛錬が\n待ち遠しいな…！", 4, 105301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1907, 102, 0, 1, "最近、あいつと\n過ごしていて、毎日が\n充実しているな…", 8, 105301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1908, 1, 0, 2, "軍人の本分は日々の\n研鑽にあるからな。\n今日も鍛錬に\n行かないか？", 1, 105301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1909, 2, 0, 2, "毎日牛乳を飲んでいる\nのだが、なかなか背が\n伸びないものだな…", 1, 105301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1910, 3, 0, 2, "貴公にも、飴をくれて\nやろうか？\nあまいぞ～！\n美味しいぞ～！", 1, 105301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1911, 4, 0, 2, "私ともっと高く、\n共に飛ぼう！", 1, 105301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1912, 101, 0, 2, "貴公は、本当に頼りに\nなる男だな…いや、\nふとそう思っただけだ", 4, 105301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1913, 102, 0, 2, "貴公と共にいられる\nことは私の喜びだ。\nこれからも\nよろしく頼むぞ", 8, 105301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1914, 1, 0, 2, "うむ、いい朝だ。\n本国の夜明けを\n思い出してしまうな！", 1, 105301, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1915, 2, 0, 2, "そろそろ昼食に\nするか。腹が減っては\n戦はできないからな", 1, 105301, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1916, 3, 0, 2, "ふわああ…私は子供\nではないが…眠い時は\n眠いのだ…", 1, 105301, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1917, 1, 0, 3, "身だしなみは、心の\n在りようを映すから\n気をつけなくてはな", 1, 105301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1918, 2, 0, 3, "ふう…軍人たるもの\n常日頃、油断は\nできないのだが…\nお、落ち着くな…", 1, 105301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1919, 3, 0, 3, "うむ…このかわいさ…\n本国に持って\n帰りたい…", 1, 105301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1920, 4, 0, 3, "このともし火、戦いに\n明け暮れる我らの心を\n癒すものだな…", 1, 105301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1921, 5, 0, 3, "はあ～、ぽかぽか…\nい、いや！\n気を緩めないぞっ！", 1, 105301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1922, 6, 0, 3, "うおおおおおっ！\n鍛錬、鍛錬、\n鍛錬っ！！", 1, 105301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1923, 7, 0, 3, "いつ戦いになっても\n困らぬように、\n腹八分目に\nしておくのだ！", 1, 105301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1924, 8, 0, 3, "これを読んで、新たな\n戦術を編み出すぞっ！", 1, 105301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1926, 1, 0, 1, "たまには掃除でも\nするか。部屋が\n綺麗になるのは\n喜ばしいことだ", 1, 105302, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1927, 2, 0, 1, "部屋の中では\n剣を振り回せないのが\n玉に瑕だな…", 1, 105302, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1928, 3, 0, 1, "お菓子が常備されて\nいる部屋。そんな\nものが、私の好みだ…", 1, 105302, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1929, 4, 0, 1, "毎日牛乳を飲んでいる\nのだが、なかなか背が\n伸びないものだな…", 1, 105302, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1930, 5, 0, 1, "家庭を守れない者が\n国を守れるはずが\nない。この部屋も\n私が守るぞ！", 1, 105302, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1931, 101, 0, 1, "あいつは今頃、何を\nしているのだろうな…\nふふっ", 8, 105302, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1932, 102, 0, 1, "あいつと会える日が\nこんなにも待ち遠しく\nなるとはな…", 8, 105302, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1933, 1, 0, 2, "軍人の本分は日々の\n研鑽にあるからな\n今日も鍛錬に\n行かないか？", 1, 105302, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1934, 2, 0, 2, "おお、今日も鍛錬の\n時間だったか！\nすまないな\nすぐ行く！", 1, 105302, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1935, 3, 0, 2, "私は子供ではない！\nが…たまには童心に\n帰りたい時もある…", 1, 105302, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1936, 4, 0, 2, "貴公にも、飴を\nくれてやろうか？\nあまいぞ～！\n美味しいぞ～！", 1, 105302, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1937, 101, 0, 2, "私と歩きたいのか…？\nそうだな。二人で\n散歩でも行こうか", 8, 105302, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1938, 102, 0, 2, "貴公と共にいられる\nことは私の喜びだ\nこれからも\nよろしく頼むぞ", 8, 105302, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1939, 1, 0, 2, "うむ、いい朝だ\n本国の夜明けを\n思い出してしまうな！", 1, 105302, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1940, 2, 0, 2, "そろそろ昼時だな\n鍛錬の手を緩めて\n昼食にするか", 1, 105302, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1941, 3, 0, 2, "寝る前に…あと少し\nだけ、鍛錬を…", 1, 105302, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1942, 1, 0, 3, "ヴァイス\nフリューゲルの一員と\nして恥ずかしくないよう\n身なりを整えなくては", 1, 105302, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1943, 2, 0, 3, "ふう…軍人たるもの\n常日頃、油断は\nできないのだが…\nお、落ち着くな…", 1, 105302, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1944, 3, 0, 3, "うむ…このかわいさ…\n本国に持って\n帰りたい…", 1, 105302, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1945, 4, 0, 3, "このともし火\n戦いに明け暮れる\n我らの心を\n癒すものだな…", 1, 105302, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1946, 5, 0, 3, "はあ～、ぽかぽか…\nい、いや！\n気を緩めないぞっ！", 1, 105302, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1947, 6, 0, 3, "うおおおおおっ！\n鍛錬、鍛錬\n鍛錬っ！！", 1, 105302, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1948, 7, 0, 3, "いつ戦いになっても\n困らぬように\n腹八分目に\nしておくのだ！", 1, 105302, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1949, 8, 0, 3, "うむ…脳細胞が\n活性化してきたぞ\n読書は大事だな", 1, 105302, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1951, 1, 0, 1, "私たちの騎空艇\nどんなデザインに\nしよう…？", 1, 105701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1952, 2, 0, 1, "団員募集の\nポスター\n作ろうっと♪", 1, 105701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1953, 3, 0, 1, "父さん…\n今頃どこで\n何してるんだろ？", 1, 105701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1954, 4, 0, 1, "騎空艇に乗って、\n誰もいったことがない\n場所を見つけたいな", 1, 105701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1955, 5, 0, 1, "父さん以上の\n世界一の\n騎空士になりたい！", 1, 105701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1956, 101, 0, 1, "仲間と行く空の旅…\n何度想像しても\nドキドキするなぁ", 4, 105701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1957, 102, 0, 1, "父さん…\n私にもね、大切な\n仲間ができたよ", 8, 105701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1958, 1, 0, 2, "仕事の依頼？\nすぐに支度\nしてくるね！", 1, 105701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1959, 2, 0, 2, "ねえねえ！\n団員募集のポスター\n一緒に作ろうよ！", 1, 105701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1960, 3, 0, 2, "なーにー？\nどうしたの？\n今呼んだよね？", 1, 105701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1961, 4, 0, 2, "今は休憩中だよ？\nだからあなたも\nゆっくりしてて", 1, 105701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1962, 101, 0, 2, "私にとってあなたは\nとっても頼りになる、\n自慢の団員だよ！", 4, 105701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1963, 102, 0, 2, "お父さんに会えたら、\n大切な仲間として\nあなたを紹介するね！", 8, 105701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1964, 1, 0, 2, "おはよう！\n体の調子はどう？\n今日は頑張れそう？", 1, 105701, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1965, 2, 0, 2, "ねえねえ、お昼\n食べに行こうよ！\nお腹空いちゃった", 1, 105701, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1966, 3, 0, 2, "…あれ？\nおしゃべりしてたら\nもうこんな時間？", 1, 105701, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1967, 1, 0, 3, "どうせならオシャレな\n騎空士って\n呼ばれたいな", 1, 105701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1968, 2, 0, 3, "ふぅ、疲れた～…\nちょっとだけ\n休んじゃおっと", 1, 105701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1969, 3, 0, 3, "よし！　キミは\n今から団長代理だ！\n…なんてね♪", 1, 105701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1970, 4, 0, 3, "いい香り～♪\n気持ちが\n落ち着く～…", 1, 105701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1971, 5, 0, 3, "これって\n騎空艇に\n置けるかな…？", 1, 105701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1972, 6, 0, 3, "どんな仕事でも\nこなせるような\n体力がないとね…！", 1, 105701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1973, 7, 0, 3, "あむっ…うん！\n美味しい～！\n今度作ってみよう♪", 1, 105701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1974, 8, 0, 3, "空を冒険するには\n色んなことを\n知っておかないと…", 1, 105701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1976, 1, 0, 1, "…戻らなくなったよ\nどうすれば元に\n戻る…？", 1, 105702, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1977, 2, 0, 1, "暇だよ…\nどこかに強い人は\nいないか？", 1, 105702, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1978, 3, 0, 1, "ジョブチェンジ！\n…叫んでも\n戻らないよ", 1, 105702, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1979, 4, 0, 1, "今のうちに、\n魔物退治の\n仕事に行くね！", 1, 105702, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1980, 5, 0, 1, "ハァ～…\nうん、気の巡りは\n問題ないね！", 1, 105702, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1981, 101, 0, 1, "私の力の源は\n仲間との\n絆だよ！", 8, 105702, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1982, 102, 0, 1, "いつかこの拳を\n空を越えた、星まで\n届かせたいね", 8, 105702, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1983, 1, 0, 2, "功夫が足りない人は\nどうすればいいか？\nもちろん修行だよ！", 1, 105702, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1984, 2, 0, 2, "私はいつも通りに\n喋ってるつもり\nだよ？", 1, 105702, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1985, 3, 0, 2, "どうした？\nもしかして手合せ\nしたいか？", 1, 105702, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1986, 4, 0, 2, "騎空士としても\n拳法家としても\n目指すは世界一ね！", 1, 105702, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1987, 101, 0, 2, "背中はあなたに\n預けたよ！\nよろしくね！", 8, 105702, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1988, 102, 0, 2, "あなたと一緒なら\nきっと、今よりもっと\n強くなれるよ！", 8, 105702, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1989, 1, 0, 2, "ちゃんと起きてるか？\n寝ぼけてたら\nダメよ？", 1, 105702, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1990, 2, 0, 2, "腹減ったね…\nなにか食べに\n行くよ！", 1, 105702, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1991, 3, 0, 2, "修行のしすぎは\n良くないよ？\n気をつけるね", 1, 105702, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1992, 1, 0, 3, "美しい技は\n美しい体が\nあってこそ！", 1, 105702, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1993, 2, 0, 3, "よいしょっと…\nちょっとのんびり\nさせてもらうよ", 1, 105702, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1994, 3, 0, 3, "…練習に\n使うのはさすがに\n可哀そうね", 1, 105702, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1995, 4, 0, 3, "これは…\n瞑想の時にも\n使ってみたいね", 1, 105702, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1996, 5, 0, 3, "出来ればもっと\n熱いほうが\n嬉しいよ", 1, 105702, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1997, 6, 0, 3, "ここでも\n修行出来るなんて\n最高だよ！", 1, 105702, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1998, 7, 0, 3, "気力の源は体力！\n体力の源は\n健康とご飯！", 1, 105702, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1999, 8, 0, 3, "んー…\n難しいことは\nわかんないよ！", 1, 105702, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2001, 1, 0, 1, "わたしたちの\nギルドハウスへ\nようこそ～！", 1, 105801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2002, 2, 0, 1, "ぐるるる～…\nあ、お腹が\n鳴っちゃいました…", 1, 105801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2003, 3, 0, 1, "ん？\n食べ物のにおい？", 1, 105801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2004, 4, 0, 1, "みんなと仲良く食卓を\n囲みたいですね～", 1, 105801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2005, 5, 0, 1, "この部屋が、わたしの\n帰る場所です♪", 1, 105801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2006, 101, 0, 1, "あの人を見ると\n胸が満たされます～\nお腹は空いてますが！", 4, 105801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2007, 102, 0, 1, "あの人、\n大食いの子って\n嫌いかな？", 8, 105801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2008, 1, 0, 2, "食べもの、くれるん\nですか？　わーい！\n恋しちゃいそう♪", 1, 105801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2009, 2, 0, 2, "つ、つまみ食いなんて\nしてませんよ？\nこれは味見です！", 1, 105801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2010, 3, 0, 2, "おやつは別腹、\nごはんも別腹、\n魔物も別腹～", 1, 105801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2011, 4, 0, 2, "好きなものを、\n好きな人達と食べる！\n最高の贅沢ですね☆", 1, 105801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2012, 101, 0, 2, "あなたは、\nごはん王子ですね～", 4, 105801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2013, 102, 0, 2, "大好きです～\n愛してます～\nえへへ～♪", 8, 105801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2014, 1, 0, 2, "朝ごはんも終わったし\n早速ブランチを\n食べませんか？", 1, 105801, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2015, 2, 0, 2, "お昼ご飯、\nおいしかったですね～\nそれじゃあ、そろそろ\nおやつにしましょう☆", 1, 105801, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2016, 3, 0, 2, "お夕飯も、晩ごはんも\n食べたし…さぁ！\nお夜食の時間ですよ☆", 1, 105801, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2017, 1, 0, 3, "身だしなみ、\n身だしなみ…\nきちんとしないとね♪", 1, 105801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2018, 2, 0, 3, "ふい～\n消化中です～", 1, 105801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2019, 3, 0, 3, "おいしそ…\nか、かわいいです～", 1, 105801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2020, 4, 0, 3, "いい香りですね～\n空気までおいしそう～\nじゅるり♪", 1, 105801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2021, 5, 0, 3, "ほっかほか～♪\n魔物の丸焼きが\n作れちゃいますね！", 1, 105801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2022, 6, 0, 3, "走って食べて！\n走って食べて！\n\この無限ループです！", 1, 105801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2023, 7, 0, 3, "いくつでも\n食べられますよ！\n幸せ～☆", 1, 105801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2024, 8, 0, 3, "料理の絵を\n見ているだけで、\nよ、よだれが～", 1, 105801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2026, 1, 0, 1, "この部屋が、わたしの\n帰る場所です♪", 1, 105802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2027, 2, 0, 1, "ギルドハウス\nどんどん素敵に\nなっていくな～♪", 1, 105802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2028, 3, 0, 1, "「やばい」とか\n「おいっす」とか…\n民の言葉って難しい", 1, 105802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2029, 4, 0, 1, "お菓子の家に\n住みたいな～", 1, 105802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2030, 5, 0, 1, "あ、頼んでた出前が\n届いたかな？", 1, 105802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2031, 101, 0, 1, "大切な人がいて\n大切な場所がある\nすごく幸せです♪", 8, 105802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2032, 102, 0, 1, "あの人\n大食いの子って\n嫌いかな？", 8, 105802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2033, 1, 0, 2, "ごはんに行きますか？\n作りましょうか？\n店屋物もいいですね～", 1, 105802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2034, 2, 0, 2, "モグモグ…\nあげませんよ～", 1, 105802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2035, 3, 0, 2, "おやつは別腹\nごはんも別腹\n魔物も別腹～", 1, 105802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2036, 4, 0, 2, "好きなものを\n好きな人達と食べる！\n最高の贅沢ですね☆", 1, 105802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2037, 101, 0, 2, "大好きです～\n愛してます～\nえへへ～♪", 8, 105802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2038, 102, 0, 2, "わたし食べても\n太らないんです♪\nほらほら～触って\nみてくださいよ～", 8, 105802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2039, 1, 0, 2, "朝ごはんも終わったし\n早速ブランチを\n食べませんか？", 1, 105802, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2040, 2, 0, 2, "お昼ご飯\nおいしかったですね～\nそれじゃあ、そろそろ\nおやつにしましょう☆", 1, 105802, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2041, 3, 0, 2, "お夕飯も、晩ごはんも\n食べたし～。さぁ！\nお夜食の時間ですよ☆", 1, 105802, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2042, 1, 0, 3, "お姫さまみたいに\nきれいに\nならないと…", 1, 105802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2043, 2, 0, 3, "のんびりしていても\nお腹がすいちゃうん\nですよね～", 1, 105802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2044, 3, 0, 3, "おいしそ…\nか、かわいいです～", 1, 105802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2045, 4, 0, 3, "アップル系、ピーチ系\nオレンジ系…\n香りも色々ですね～", 1, 105802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2046, 5, 0, 3, "ここでお魚を\n焼けたら\n最高ですね～♪", 1, 105802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2047, 6, 0, 3, "身体も鍛えなきゃ～！\nお腹がすいちゃいます\nけど…", 1, 105802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2048, 7, 0, 3, "もぐもぐもぐもぐもぐ\nもぐもぐもぐもぐもぐ\nもぐもぐもぐもぐ…", 1, 105802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2049, 8, 0, 3, "流行語発表…\nこんな言葉遣いが！\nやばいですね", 1, 105802, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2051, 1, 0, 1, "いろいろ美味しいもの\n食べられるし…\nけっこう幸せかも", 1, 106001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2052, 2, 0, 1, "んーー、あたし\nなんで、このギルドに\n入っちゃったんだろ？", 1, 106001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2053, 3, 0, 1, "あいつが来たら、\n尾行しなくちゃ♪", 1, 106001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2054, 4, 0, 1, "この部屋のどこかに、\nあいつの秘密が\n隠されてるかも？", 1, 106001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2055, 5, 0, 1, "ま、まずいわ…\nギルド活動、\nけっこう楽しい♪", 1, 106001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2056, 101, 0, 1, "あいつを監視してる\nと…な、なんか\n変な気分", 4, 106001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2057, 102, 0, 1, "主さま…\nだ、誰も聞いてない\nわよね！？", 8, 106001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2058, 1, 0, 2, "気まぐれは\nチャームポイント\nみたいなもんよ", 1, 106001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2059, 2, 0, 2, "うひゃっ！？\nど、どうしてあんたは\nいつも驚かせるのよ！", 1, 106001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2060, 3, 0, 2, "ぎゃー！\n虫もって近づくなー！", 1, 106001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2061, 4, 0, 2, "この部屋って、\n野良猫を拾ってきたら\nだめかな？", 1, 106001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2062, 101, 0, 2, "遊びにいくわよ！\nあんたはあたしの\n言うとおりにすれば\nいいの♪", 4, 106001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2063, 102, 0, 2, "あんた、あたしに\n操られてみない？", 8, 106001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2064, 1, 0, 2, "美食殿としては、\n朝食もこだわらないと\nねぇ～", 1, 106001, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2065, 2, 0, 2, "ギルド活動だもん！\n美味しいランチを\n食べましょ♪", 1, 106001, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2066, 3, 0, 2, "ディナーに行くわよ！\nギルドの目的、\n食べ歩きだもんね♪", 1, 106001, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2067, 1, 0, 3, "今日もしっぽが\nいい感じ♪", 1, 106001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2068, 2, 0, 3, "こういう所だと、\n丸くなっちゃうのよね\nうにゃぁ～～", 1, 106001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2069, 3, 0, 3, "これ、あたしの能力で\n操れないかな？", 1, 106001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2070, 4, 0, 3, "うにゃぁ～♪\nにゃん、にゃお～ん♪", 1, 106001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2071, 5, 0, 3, "はぁ～ゴロゴロ\nしたくなるわね", 1, 106001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2072, 6, 0, 3, "特訓特訓！\nあたしは\nやればできる！", 1, 106001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2073, 7, 0, 3, "おいしいものを\nたくさん食べる…\nギルド活動って\n楽しい～♪", 1, 106001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2074, 8, 0, 3, "魔法を使うにも、\n知識が必要だものね", 1, 106001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2076, 1, 0, 1, "ま、まずいわ…\nギルド活動\nけっこう楽しい♪", 1, 106002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2077, 2, 0, 1, "いろいろ美味しいもの\n食べられるし…\nけっこう幸せかも", 1, 106002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2078, 3, 0, 1, "主さま…\nだ、誰も聞いてない\nわよね！？", 1, 106002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2079, 4, 0, 1, "監視監視…\nうん！　あたしは\nなまけてないわよね", 1, 106002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2080, 5, 0, 1, "へ、陛下に\nみつからないように\nしなくっちゃ…", 1, 106002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2081, 101, 0, 1, "あいつが来たら、\n尾行しなくちゃ♪", 8, 106002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2082, 102, 0, 1, "あいつと会ってから\n調子くるったな…\n楽しいけどさ", 8, 106002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2083, 1, 0, 2, "食わず嫌いって\nあるのよね\n食べ物もだけど\n人も…", 1, 106002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2084, 2, 0, 2, "うひゃっ！？\nど、どうしてあんたは\nいつも驚かせるのよ！", 1, 106002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2085, 3, 0, 2, "話し相手になって\nやるわよ…情報\n聞き出せるかもだし", 1, 106002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2086, 4, 0, 2, "この部屋って\n野良猫を拾ってきたら\nだめかな？", 1, 106002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2087, 101, 0, 2, "遊びにいくわよ！\nあんたはあたしの\n言うとおりにすれば\nいいの♪", 8, 106002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2088, 102, 0, 2, "あんた、あたしに\n操られてみない？", 8, 106002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2089, 1, 0, 2, "美食殿としては\n朝食もこだわらないと\nねぇ～", 1, 106002, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2090, 2, 0, 2, "ギルド活動だもん！\n美味しいランチを\n食べましょ♪", 1, 106002, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2091, 3, 0, 2, "ディナーに行くわよ！\nギルドの目的\n食べ歩きだもんね♪", 1, 106002, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2092, 1, 0, 3, "今日もしっぽが\nいい感じ♪", 1, 106002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2093, 2, 0, 3, "うにゃ～\nどんどん眠く\nなる～", 1, 106002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2094, 3, 0, 3, "これ、あたしの能力で\n操れないかな？", 1, 106002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2095, 4, 0, 3, "あたし、みんなや\nあいつと同じ香りを\n身に着けてるんだ…", 1, 106002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2096, 5, 0, 3, "うっ、のんびり\nしたくなる…\n特訓しなきゃ\nいけないのに～", 1, 106002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2097, 6, 0, 3, "特訓特訓！\nあたしは\nやればできる！", 1, 106002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2098, 7, 0, 3, "おいしいものを\nたくさん食べる…\nギルド活動って\n楽しい～♪", 1, 106002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2099, 8, 0, 3, "魔法を使うにも\n知識が必要だものね", 1, 106002, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2101, 1, 0, 1, "この空間は主さまの\n香りがします\n…ふふふ♪", 1, 105901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2102, 2, 0, 1, "炊事、洗濯、お掃除、\nお買いものもしなくて\nはいけませんね", 1, 105901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2103, 3, 0, 1, "アメスさまへの、\nお祈りができそうな\n場所は…", 1, 105901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2104, 4, 0, 1, "さすが都会…\nおいしいものが\nたくさんあるのですね", 1, 105901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2105, 5, 0, 1, "主さまが不自由なく\n使える部屋に\nしなくては…", 1, 105901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2106, 101, 0, 1, "できるなら…\n主さまと二人だけの\n部屋の方が…", 4, 105901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2107, 102, 0, 1, "主さまの隣には\nわたくしが…\nわたくしの隣には、\n主さまが…幸せです", 8, 105901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2108, 1, 0, 2, "わたくし、背が伸び\nましたでしょうか？\n…そうですか", 1, 105901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2109, 2, 0, 2, "もしお疲れでしたら\n肩でも\nお揉みしましょうか？", 1, 105901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2110, 3, 0, 2, "主さま、なにか\nしてほしいことは\nありませんか？\n欲しいものは？", 1, 105901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2111, 4, 0, 2, "今日のおこづかいです\n何かおいしいものを\n召し上がってください", 1, 105901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2112, 101, 0, 2, "主さまのおてて\n温かいですね…♪", 4, 105901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2113, 102, 0, 2, "主さまのおそばに\nいられて、わたくし、\n嬉しいです…", 8, 105901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2114, 1, 0, 2, "おはようございます。\n洗濯をしなければ…", 1, 105901, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2115, 2, 0, 2, "そろそろお昼ですね。\nおいしいものを\n食べましょう", 1, 105901, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2116, 3, 0, 2, "おやすみになる時は、\nお声をかけてください\nお供致します", 1, 105901, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2117, 1, 0, 3, "もう少し…\nおっきくなりたい\nです", 1, 105901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2118, 2, 0, 3, "このふかふかの優しさ\n主さまのようです", 1, 105901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2119, 3, 0, 3, "かわいい♪\n主さまにそっくり♪", 1, 105901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2120, 4, 0, 3, "故郷の香りとは\n違いますが、これも\n心地よいものです…", 1, 105901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2121, 5, 0, 3, "ここで洗濯物を\n乾かせるでしょうか", 1, 105901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2122, 6, 0, 3, "都会の人々は\nこんなふうに\n運動を…！？", 1, 105901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2123, 7, 0, 3, "これは…主さまにも\n食べさせて\n差し上げたい…\nあーんって♪", 1, 105901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2124, 8, 0, 3, "よく学んで、\n主さまのお役に\n立たなければ", 1, 105901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2126, 1, 0, 1, "主さまがいつ戻って\n来ても平気なよう\n部屋を綺麗に保たねば", 1, 105902, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2127, 2, 0, 1, "節約のためにも\n自家菜園などするのは\nどうでしょう…　", 1, 105902, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2128, 3, 0, 1, "アメスさまへの\nお祈りができそうな\n場所は…", 1, 105902, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2129, 4, 0, 1, "瞑想をすると\n心が安らぎます…", 1, 105902, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2130, 5, 0, 1, "都会の方の身なりを\n勉強した方が\n良いのでしょうか？", 1, 105902, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2131, 101, 0, 1, "わたくしの居場所は\n主さまのお傍です\nずっと、ずっと…♪", 8, 105902, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2132, 102, 0, 1, "主さまの隣には\nわたくしが…\nわたくしの隣には\n主さまが…幸せです", 8, 105902, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2133, 1, 0, 2, "主さま、なにか\nしてほしいことは\nありませんか？\n欲しいものは？", 1, 105902, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2134, 2, 0, 2, "わたくし、背が伸び\nましたでしょうか？\n…そうですか", 1, 105902, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2135, 3, 0, 2, "お出かけですか？\nわたくしもお供\nいたします", 1, 105902, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2136, 4, 0, 2, "ひゃん！\n主さま…\nいじわるです", 1, 105902, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2137, 101, 0, 2, "主さま、なにか御用は\nありませんか？\nわたくし、主さまの\nお役にたちたいのです", 8, 105902, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2138, 102, 0, 2, "主さまの優しさに\nわたくしはいつも\n癒されています…", 8, 105902, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2139, 1, 0, 2, "おはようございます♪\n主さまの可愛らしい\n寝顔、堪能致しました", 1, 105902, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2140, 2, 0, 2, "のんびりした時間帯\nですね…ふぁ～…\nし、失礼致しました", 1, 105902, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2141, 3, 0, 2, "ふむ、都会は夜でも\nこんなにも、にぎやか\nなのですね", 1, 105902, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2142, 1, 0, 3, "主さまに会う前に\n身なりを整えなくては\nいけませんね", 1, 105902, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2143, 2, 0, 3, "このふかふかの優しさ\n主さまのようです", 1, 105902, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2144, 3, 0, 3, "かわいい♪\n主さまにそっくり♪", 1, 105902, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2145, 4, 0, 3, "この心地よさ…\n主さまのようです♪", 1, 105902, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2146, 5, 0, 3, "ここで洗濯物を\n乾かせるでしょうか　", 1, 105902, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2147, 6, 0, 3, "はぁっはぁっはぁっ\n…すべては\n主さまのため…", 1, 105902, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2148, 7, 0, 3, "都会の味…\nとてもおいしいです\n…もぐもぐもぐ", 1, 105902, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2149, 8, 0, 3, "都会のことを\n学ばないと…\nふむふむ", 1, 105902, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2151, 1, 0, 2, "ふぁ～…あっ\n見られちゃい\nましたか？", 1, 190801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2152, 2, 0, 2, "ギルドハウスの\n使い心地は\nどうですか？", 1, 190801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2153, 3, 0, 2, "ご用があれば\nなんでも言って\nくださいね～♪", 1, 190801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2154, 4, 0, 2, "ときどきみなさんと\nクエストに行きたいな\nって思っちゃいます", 1, 190801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2155, 1, 0, 2, "おはようございます！\nお掃除の方、\nはじめていきますね", 1, 190801, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2156, 2, 0, 2, "皆さん、お昼ごはんの\nご要望がありましたら\n用意致しますよ♪", 1, 190801, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2157, 3, 0, 2, "ここは夜でも\nにぎやかですね", 1, 190801, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2158, 1, 0, 1, "…\n少しうとうと\nしてしまった", 1, 104701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2159, 2, 0, 1, "室内だし兜を脱ぐか\n…いや\nやはりやめておこう", 1, 104701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2160, 3, 0, 1, "部屋にいると\n鎧が蒸し暑くなるが\n我慢せねば…", 1, 104701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2161, 4, 0, 1, "仕事が門番だからか\n部屋にいても\nじっとしてしまう…", 1, 104701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2162, 5, 0, 1, "少年と会話したことを\n思いだすと\n自然と楽しくなるな", 1, 104701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2163, 101, 0, 1, "少年のことを考えると\n顔がにやけてしまう", 4, 104701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2164, 102, 0, 1, "少年は今頃\n平和に過ごせて\nいるかな…", 8, 104701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2165, 1, 0, 2, "ん？　悪いが\n兜は脱がないぞ", 1, 104701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2166, 2, 0, 2, "私と遊びたいのか？\nそう言われても\n何をしていいやら…", 1, 104701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2167, 3, 0, 2, "鎧が重くないかって？\n大丈夫。けっこう\n鍛えているからな", 1, 104701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2168, 4, 0, 2, "少年、寒くはないか？\nいや自分がこうなので\n人が薄着に見えてね", 1, 104701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2169, 101, 0, 2, "君といると、兜の中が\nいつもより暑いんだ", 4, 104701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2170, 102, 0, 2, "私としゃべりたい？\n物好きだな\nでも、嬉しいよ", 8, 104701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2171, 1, 0, 2, "早いな少年\nえ？　私の仕事に\n合わせてくれたのか", 1, 104701, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2172, 2, 0, 2, "昼食の誘い？\n嬉しいけど\n人前では食べないんだ", 1, 104701, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2173, 3, 0, 2, "王宮騎士団の\n日報を書かなければ…\n『何事もなし』、と", 1, 104701, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2175, 2, 0, 3, "鎧を着ているから\n感触が分かりづらい\nのが残念だ…", 1, 104701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2176, 3, 0, 3, "か、かわいいな…\n私には似合わないが\nぎゅ…ってしよう", 1, 104701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2177, 4, 0, 3, "良い香りだ…\n普段はどうしても\n鉄くさくなるからな", 1, 104701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2178, 5, 0, 3, "わかっていたけど\n鎧を着たまま暖炉に\nあたると暑い…", 1, 104701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2180, 7, 0, 3, "兜を脱がずに食べる\nうん、段々コツが\n掴めてきた…", 1, 104701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2181, 8, 0, 3, "兜があっても\n意外と本くらいは\n難なく読めるんだ", 1, 104701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2183, 1, 0, 1, "居心地よくて\nゴロゴロしちゃうよ～\n…いつも通りだね", 1, 102601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2184, 2, 0, 1, "ドングリの木\n近くに生えてない？", 1, 102601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2185, 3, 0, 1, "快適な寝床があれば\nそれだけで幸せだねぇ", 1, 102601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2186, 4, 0, 1, "あたしと一緒に\nだらけてくれる人が\nいそうだねぇ", 1, 102601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2187, 5, 0, 1, "ここなら牧場よりも\n速く配達が\n届きそうだね～", 1, 102601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2188, 101, 0, 1, "あんパン買ったし\nあいつにも\n分けてあげよう", 4, 102601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2189, 102, 0, 1, "あいつと一緒に\nだらけられたら\n最高に幸せだね", 8, 102601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2190, 1, 0, 2, "ドングリでも\nつまみながら\n一緒にゲームしようよ", 1, 102601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2191, 2, 0, 2, "横になりたいから\n枕になっておくれよ～\nあんたあったかいしさ", 1, 102601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2192, 3, 0, 2, "ドングリあげるから\nあんパン買ってきて～", 1, 102601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2193, 4, 0, 2, "う～\n疲れちゃったよ～\nおんぶして運んで～", 1, 102601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2194, 101, 0, 2, "あんたといると\nリア充って\n感じがするよ", 4, 102601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2195, 102, 0, 2, "あんたが一生\n世話してくれたら\n人生安泰なんだけどね", 8, 102601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2196, 1, 0, 2, "ふわ～っ…\nおはよう…あたしは\nまだ眠いよ", 1, 102601, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2197, 2, 0, 2, "そろそろお昼寝でも\nしたい気分だねぇ", 1, 102601, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2198, 3, 0, 2, "徹夜でゲームするけど\nあんたもどうだい？\nあたしは強いよ", 1, 102601, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2200, 2, 0, 3, "あ…だめだこれ。\n気持ち良くて\n落ちそう…", 1, 102601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2201, 3, 0, 3, "かわいいものに\n囲まれて寝るのも\nよさそうだねぇ…", 1, 102601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2202, 4, 0, 3, "心身ともに\nリラックスした状態で\nぐうたら出来そうだね", 1, 102601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2203, 5, 0, 3, "ふい～…\nごくらくごくらく…\n暖かいねぇ", 1, 102601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2205, 7, 0, 3, "食べてすぐ寝ると\n牛になるって\n本当なのかな？", 1, 102601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2206, 8, 0, 3, "おー！　この本なら\nあたしも居眠りせずに\n読めそうだよ", 1, 102601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2208, 1, 0, 1, "ランドソル…\nおっきな街だなぁ…", 1, 106301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2209, 2, 0, 1, "ロザリア…\nきっと、いるよね？", 1, 106301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2210, 3, 0, 1, "最近、弓の練習\nサボっちゃってるな…", 1, 106301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2211, 4, 0, 1, "今日の私は\n森番じゃなくて\n家番だよ！", 1, 106301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2212, 5, 0, 1, "ちょっとだけ\n慣れてきたかも？", 1, 106301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2213, 101, 0, 1, "いつか、\nあの人と一緒に…", 4, 106301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2214, 102, 0, 1, "…なんか最近、\nあの人のこと\nばっかり…", 8, 106301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2215, 1, 0, 2, "もしよかったら\n街に出かけない？", 1, 106301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2216, 2, 0, 2, "この部屋、\n妖精さんも\n気に入るかも！", 1, 106301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2217, 3, 0, 2, "今日は\nなににしよっか？", 1, 106301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2218, 4, 0, 2, "今日はどこ行く？", 1, 106301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2219, 101, 0, 2, "たまには二人で…\nな、なんちゃって！", 4, 106301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2220, 102, 0, 2, "最近、\nずっと楽しいんだ！\nあなたのおかげかな", 8, 106301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2221, 1, 0, 2, "おはよう！\n今日も一緒に\n頑張ろうね！", 1, 106301, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2222, 2, 0, 2, "お昼はどうする？\nあなたのオススメに\n連れて行って欲しいな", 1, 106301, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2223, 3, 0, 2, "ランドソルは\n夜でも賑やかだね！", 1, 106301, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2225, 2, 0, 3, "わぁっ！\nすっごく\nふわふわだよ！", 1, 106301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2226, 3, 0, 3, "かわい～！\nぎゅってしたく\nなっちゃうかも！", 1, 106301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2227, 4, 0, 3, "くんくん\nなんか…いい匂い？", 1, 106301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2228, 5, 0, 3, "すっごいなー\nあったかいなー！", 1, 106301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2230, 7, 0, 3, "わぁっ、おいしい！\nもっと食べたい\nかも…！", 1, 106301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2231, 8, 0, 3, "も、文字は苦手…\nでも、頑張らないと\nだよね…！", 1, 106301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2233, 1, 0, 1, "にんじんとピーマンを\n食べないでいい方法\nないかな…", 1, 103601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2234, 2, 0, 1, "カップケーキが\n食べたいな…", 1, 103601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2235, 3, 0, 1, "予習が終わりました！\nさて、次は復習を\nしないと…", 1, 103601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2236, 4, 0, 1, "あの人の部屋にいる\n私って…もしかして\nふしんしゃなんじゃ？", 1, 103601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2237, 5, 0, 1, "男の人は、みんな\nへんたいだと\n聞きました…", 1, 103601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2238, 101, 0, 1, "あの人に、一緒に\n遊んでほしいけど…\nな、なんていえば？", 4, 103601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2239, 102, 0, 1, "早く大人になりたいな\n…あの人と同じくらい", 8, 103601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2240, 1, 0, 2, "ぼ、防犯魔石を\n鳴らしますよ！？", 1, 103601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2241, 2, 0, 2, "へんたい！？　…\nあ、あなたでしたか～\nやっぱりへんたーい！", 1, 103601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2242, 3, 0, 2, "これ宿題です\nやっておいて\nくださいね", 1, 103601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2243, 4, 0, 2, "あ、遅刻ですよ\n待っていたんですから", 1, 103601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2244, 101, 0, 2, "にらめっこですか？\nあ、あなたの前で変顔\nするのはちょっと…", 4, 103601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2245, 102, 0, 2, "あの…何歳まで\n子供だと思いますか？", 8, 103601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2246, 1, 0, 2, "顔を洗って、着替えて\n朝ごはんを食べて、\n歯を磨いて…", 1, 103601, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2247, 2, 0, 2, "て、鉄棒の練習！？　\nほ、他のことを\nしませんか？", 1, 103601, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2248, 3, 0, 2, "ね、眠くないです\n子供ではありません\nから…ふわぁ", 1, 103601, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2250, 2, 0, 3, "ひと休みしよっと\n………うん、\nひと休みおしまい♪", 1, 103601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2251, 3, 0, 3, "つ、つぶらな瞳には\n弱いんです", 1, 103601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2252, 4, 0, 3, "リラックスできますね\n集中力も\n上がりそうです", 1, 103601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2253, 5, 0, 3, "あったかいと\n眠くなっちゃうな…\n勉強しなくちゃなのに", 1, 103601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2255, 7, 0, 3, "ピーマンもこれくらい\nおいしければ\nいいのに…", 1, 103601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2256, 8, 0, 3, "この本、むずかしい\n…もっと勉強\nがんばらなくちゃ", 1, 103601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2258, 1, 0, 1, "今日はみんなに\nなにを読んで\nあげようかしら", 1, 101501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2259, 2, 0, 1, "子どもたちと同じ\n目線で考えるのが\n大事よね", 1, 101501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2260, 3, 0, 1, "お天気が良い日は\nみんなでお散歩に\n行きたいわね～", 1, 101501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2261, 4, 0, 1, "鬼ごっこにかくれんぼ\n…みんなは\nどっちが好きかしら", 1, 101501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2262, 5, 0, 1, "お遊戯会の配役、\n考え出すと\n大変だわ～", 1, 101501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2263, 101, 0, 1, "彼は優しいから、\nきっと良い\nお父さんになるわね", 4, 101501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2264, 102, 0, 1, "彼には私がいないと\n…っていうのは少し\n過保護すぎるかしら？", 8, 101501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2265, 1, 0, 2, "保育園の行事を\n考えてるんだけど…\n良い案ないかしら？", 1, 101501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2266, 2, 0, 2, "今度、子どもたちと\nおやつを作るから、\n食べに来てね", 1, 101501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2267, 3, 0, 2, "よかったら一緒に\n絵本を作らない？", 1, 101501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2268, 4, 0, 2, "あなたはいつも元気ね\n先生も見習わないと♪", 1, 101501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2269, 101, 0, 2, "先生は\nずーっと、ずーっと、\nあなたの味方だからね", 4, 101501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2270, 102, 0, 2, "あなたみたいな\nいい子をお世話できて\n先生は幸せものだわ", 8, 101501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2271, 1, 0, 2, "おはよう～\nちゃんと朝ごはんは\n食べた？", 1, 101501, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2272, 2, 0, 2, "お昼寝の時間ね♪\nあなたも一緒に\n寝ましょうか", 1, 101501, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2273, 3, 0, 2, "すっかり真っ暗ね。\n今日も一日、\nよくできました♪", 1, 101501, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2275, 2, 0, 3, "ふかふかねぇ♪\n年甲斐もなく、\n飛び跳ねちゃいそう", 1, 101501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2276, 3, 0, 3, "あらあら、かわいい！\n保育園のみんなに\n見せてあげたいわ", 1, 101501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2277, 4, 0, 3, "いい香りね～\n保育園にも\n置こうかしら", 1, 101501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2278, 5, 0, 3, "寒い日は、ここで\n子どもたちに絵本を\n読んであげましょ♪", 1, 101501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2280, 7, 0, 3, "美味しい～♪\n遠足に持っていったら\nみんな喜びそう", 1, 101501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2281, 8, 0, 3, "みんなが\n楽しめる読み聞かせの\n方法特集♪　素敵ねぇ", 1, 101501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2283, 1, 0, 1, "世界の前に、\nこの部屋を征服\nしてやろう♪", 1, 104401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2284, 2, 0, 1, "拠点とはやはり\n斯くあるべきじゃの", 1, 104401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2285, 3, 0, 1, "眷属の物はわらわの物\n故に、この部屋は\nわらわの部屋じゃな！", 1, 104401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2286, 4, 0, 1, "永く眠っている間に\n随分と世界は変わった\nようじゃな…", 1, 104401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2287, 5, 0, 1, "うるさいのう～\nまったく、近頃の\n若いもんは…", 1, 104401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2288, 101, 0, 1, "あやつが来てから\n動悸が治まらぬ…\n如何ような病で\nあろうかの…", 4, 104401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2289, 102, 0, 1, "眷属よ、どこに行った\nのじゃ？\nわらわの世話を\n許可するのじゃ", 8, 104401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2290, 1, 0, 2, "いずれ世界は\n我が手中に収まる\nふふふ、\n楽しみじゃのう", 1, 104401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2291, 2, 0, 2, "眷属よ、わらわから\n離れてはならんぞ！\n子供に戻ってしまう\nではないか！", 1, 104401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2292, 3, 0, 2, "トマトジュースが\n欲しいのじゃ！\n野菜ジュースでは\nダメだぞ！", 1, 104401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2293, 4, 0, 2, "蚊にくわれたぞ！\nわらわの血を吸う\nとは～！", 1, 104401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2294, 101, 0, 2, "こら、頭をなでるな\nお主はこの姿でも\n子供扱いするのか？", 4, 104401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2295, 102, 0, 2, "お主が望むなら世界の\n半分をくれてやっても\n良いのじゃぞ？", 8, 104401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2296, 1, 0, 2, "朝早くから感心じゃの\nさすがわらわの\n眷属じゃ", 1, 104401, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2297, 2, 0, 2, "太陽は苦手じゃ…\nこの部屋の中で\n過ごそうぞ", 1, 104401, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2298, 3, 0, 2, "夜も更けてきた！\nわらわ達の時間は\nこれからじゃ～♪", 1, 104401, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2300, 2, 0, 3, "よい座り心地じゃ\nわらわの玉座としよう", 1, 104401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2301, 3, 0, 3, "可愛いではないか\nわらわの眷属に\nしてやろうかのう", 1, 104401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2302, 4, 0, 3, "うむ、良い気持ちじゃ\nこれを作った者、\n褒めてつかわすぞ", 1, 104401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2303, 5, 0, 3, "あたたかい…\n身も心も\n浄化されるようじゃ", 1, 104401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2305, 7, 0, 3, "吸血鬼のわらわが、\nこのようなもの…\nうまい♪", 1, 104401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2306, 8, 0, 3, "このまま大人の姿を\n保つ方法…どこかに\n書いてないかのう", 1, 104401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2308, 1, 0, 1, "いろんな人がいて\nいろんな服を着てて…\n目の保養になります♪", 1, 105401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2309, 2, 0, 1, "レイ様への新作\nどんな服にしよう…\n方向性が絞れない～！", 1, 105401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2310, 3, 0, 1, "忘れないうちに\n隠し糸のメンテナンス\nやっておこうっと！", 1, 105401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2311, 4, 0, 1, "むっ！　あの人の\nスリーサイズは…\nほほう…なるほど♪", 1, 105401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2312, 5, 0, 1, "ノゾミさんの\n衣装はああして\nチカさんのは…", 1, 105401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2313, 101, 0, 1, "私がアイドル…\n…実感ないけど\n精一杯やってみよう", 4, 105401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2314, 102, 0, 1, "まさか私が\nアイドルになるなんて\n思ってなかったです", 8, 105401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2315, 1, 0, 2, "なんですかぁ？\n衣装作りで疲れて\n眠いんですぅ…", 1, 105401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2316, 2, 0, 2, "あーっ！　また\n服がほつれてる…\nしょうがないですね", 1, 105401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2317, 3, 0, 2, "はっ！　ちょうど今\n新しいデザインが\n思い浮かびました！", 1, 105401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2318, 4, 0, 2, "暇そうですね？\n時間あるなら仕事\n手伝ってください！", 1, 105401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2319, 101, 0, 2, "どうしました？\n新しい服が\n欲しくなりましたか？", 4, 105401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2320, 102, 0, 2, "騎士さんのおかげで\nカルミナでも\n頑張れてますよ！", 8, 105401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2321, 1, 0, 2, "おはようございます…\nなんで朝って毎日\nやってくるんでしょう", 1, 105401, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2322, 2, 0, 2, "お腹空きましたね\n騎士さんはちゃんと\n御飯食べましたか？", 1, 105401, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2323, 3, 0, 2, "夜の方が\n作業が捗るのは\n私だけですかね？", 1, 105401, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2325, 2, 0, 3, "このふかふか具合\nいい素材を\n使ってますね", 1, 105401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2326, 3, 0, 3, "小さくて可愛い♪\nそうだ！　レイ様の\nぬいぐるみを…！", 1, 105401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2327, 4, 0, 3, "こういう時くらい\n何も考えずに\nリラックスしないと♪", 1, 105401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2328, 5, 0, 3, "炎を使った服って\nカッコ良いかも…\nでも材料がな～…", 1, 105401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2330, 7, 0, 3, "何をするにも\n食事は大切！\n体力が資本です！", 1, 105401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2331, 8, 0, 3, "私もいつか\nデザインを\nまとめて本に…", 1, 105401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2333, 1, 0, 1, "またお魚を\n捕りにいこうかな♪", 1, 107501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2334, 2, 0, 1, "そろそろみんなも\nお腹が空くころ\nじゃないですか～？", 1, 107501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2335, 3, 0, 1, "ここなら、\n日焼けの心配は\nありませんね！", 1, 107501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2336, 4, 0, 1, "お刺身に焼き貝…\nお腹が魚介類を\n求めてますっ！", 1, 107501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2337, 5, 0, 1, "泳ぐのも食べるのも\n大得意です！", 1, 107501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2338, 101, 0, 1, "あの人と二人で\n浜辺を散歩…\n素敵ですねぇ", 4, 107501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2339, 102, 0, 1, "あの人に水着を\n褒めてもらえたら、\nすごく嬉しいなぁ", 8, 107501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2340, 1, 0, 2, "たまには釣りじゃなく\n一緒に素潜りでも\n行きませんか？", 1, 107501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2341, 2, 0, 2, "男の子に水着を\n見られるのって\n少し緊張しちゃいます", 1, 107501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2342, 3, 0, 2, "ギルドハウスの\nみんなとも、\n海に行きたいですね！", 1, 107501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2343, 4, 0, 2, "あなたの水着、\n私が選んであげます！", 1, 107501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2344, 101, 0, 2, "泳ぎの練習なら\n付き合いますよ！\n手取り足取り♪", 4, 107501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2345, 102, 0, 2, "水着姿が好きなら\nいくらでも\n着てあげますよぉ♪", 8, 107501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2346, 1, 0, 2, "朝の海も\n幻想的で\nいいですよねぇ～！", 1, 107501, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2347, 2, 0, 2, "お昼はみんなで、\n海鮮バーベキューとか\nどうでしょう！", 1, 107501, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2348, 3, 0, 2, "夜の海は星が\nきれいですよ！\n見に行きましょう！", 1, 107501, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2350, 2, 0, 3, "泳ぎ疲れたので\nちょっと休憩です～", 1, 107501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2351, 3, 0, 3, "あなたにも、\n可愛い水着を\n着せてあげたいです", 1, 107501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2352, 4, 0, 3, "なんだか、\nリゾート気分です…", 1, 107501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2353, 5, 0, 3, "ふい～、泳ぎで冷えた\n身体を暖めましょう～", 1, 107501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2355, 7, 0, 3, "おいひ～です～\nこれ、海水浴にも\n持っていきたいですね", 1, 107501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2356, 8, 0, 3, "南国のフルーツ図鑑\n…じゅるり…", 1, 107501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2358, 10, 0, 3, "わ～♪　おいしそ…\nか、かわいいですね～", 1, 107501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2359, 11, 0, 3, "これくらい大きな貝…\n食べたいな～\n食べたいな～～", 1, 107501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2360, 1, 0, 1, "この格好で\n部屋をうろつくのは\n不思議な感じがします", 1, 107601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2361, 2, 0, 1, "南国の果実を使って\n主さまにデザートを\nお作りしましょう", 1, 107601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2362, 3, 0, 1, "せっかくの水着ですし\nこれから定期的に\n海水浴に行きたいです", 1, 107601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2363, 4, 0, 1, "海の生き物も、\n森の生き物に負けず\n可愛かったです…", 1, 107601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2364, 5, 0, 1, "クロールの練習なら、\n部屋でもできそうです", 1, 107601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2365, 101, 0, 1, "主さまに、水着姿を\n褒めていただけた…\nえへへ♪", 4, 107601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2366, 102, 0, 1, "水着で主さまの\nお世話をしたら、\n喜びますでしょうか…", 8, 107601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2367, 1, 0, 2, "お疲れでしたら\nわたくしの膝を\nお使いくださいね", 1, 107601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2368, 2, 0, 2, "主さま、海の家で\nつかうおこづかいを\n渡しておきますね", 1, 107601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2369, 3, 0, 2, "海の美しさは、\n森とはちがう\n魅力がありました", 1, 107601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2370, 4, 0, 2, "この髪飾りですか？\n造花ですから\n水の中も平気です", 1, 107601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2371, 101, 0, 2, "水着でくっついて\n眠ると、いつもより\n密着して暖かいです", 4, 107601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2372, 102, 0, 2, "はじめての海が、\n主さまと一緒で\n幸せです♪", 8, 107601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2373, 1, 0, 2, "おはようございます。\n泳ぎに行く準備は\n万端です", 1, 107601, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2374, 2, 0, 2, "午後は泳ぐのをやめて\nスイカ割りなど\nいかがでしょう？", 1, 107601, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2375, 3, 0, 2, "夜も更けてきました…\n波の音を聞きながら\n少しお話しませんか？", 1, 107601, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2377, 2, 0, 3, "あぅ…この家具には、\n人をダメにする魔法が\nかけられています…", 1, 107601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2378, 3, 0, 3, "ふふっ♪\nあなたも、海水浴に\n行きますか？", 1, 107601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2379, 4, 0, 3, "この香り…\nきっと主さまも\n気に入ります", 1, 107601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2380, 5, 0, 3, "これは暖かいです…\n後ほど主さまと一緒に\nあたりましょう", 1, 107601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2382, 7, 0, 3, "泳いだ後の食事は\n一段と美味しく\n感じます", 1, 107601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2383, 8, 0, 3, "深海魚の生態…\nこれは主さまにも\nお伝えしなくては…", 1, 107601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2385, 10, 0, 3, "顔立ちが、主さまに\n似ております…\nかわいらしい♪", 1, 107601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2386, 11, 0, 3, "ふかふかですね\n主さまのようです♪", 1, 107601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2387, 1, 0, 1, "お掃除、お掃除\nふんふふ～ん☆\nあ、聞かれてました？", 1, 107701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2388, 2, 0, 1, "日焼けには回復魔法が\n効きますよ。どうして\n逃げるんですか～！", 1, 107701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2389, 3, 0, 1, "今日はドジを\nしませんように！", 1, 107701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2390, 4, 0, 1, "接客業を極めれば\nメイドとして\n成長できるかも？", 1, 107701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2391, 5, 0, 1, "水着って\n動きやすいんですけど\n人の目が気になります", 1, 107701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2392, 101, 0, 1, "あの人の気持ちを知る\n魔法とかあれば…\nな、なんでもないです", 4, 107701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2393, 102, 0, 1, "水着でご奉仕したら、\nあの人は喜ぶかな？", 8, 107701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2394, 1, 0, 2, "暑いのでしたら、\n私が魔法で涼しく…\nえ？　いらない？", 1, 107701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2395, 2, 0, 2, "何か御用ですか？\n特にないのでしたら、\nお話ししましょう♪", 1, 107701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2396, 3, 0, 2, "水着姿でも\nメイドはメイドです\nご奉仕しますよ！", 1, 107701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2397, 4, 0, 2, "ひゃっ！　あ、危うく\n転ぶところでした…\n…きゃうっ！", 1, 107701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2398, 101, 0, 2, "どうかしましたか？\nあなたの御用なら、\n喜んでお受けしますよ", 4, 107701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2399, 102, 0, 2, "水着姿をあなたに\n見られると、\nドキドキしちゃいます", 8, 107701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2400, 1, 0, 2, "朝は暑くてやる気が\n出ない？　でしたら、\nえぇい、元気になぁれ", 1, 107701, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2401, 2, 0, 2, "お腹がぺこぺこなら、\nお昼は海の家で\n食べましょう♪", 1, 107701, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2402, 3, 0, 2, "夜の浜辺でお散歩も\nいいですね。たくさん\n転びそうですけど…", 1, 107701, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2404, 2, 0, 3, "休憩しながら、\nお掃除すれば\n一石二鳥です！", 1, 107701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2405, 3, 0, 3, "きれいにすれば\nかわいさも倍増\nですよ～♪", 1, 107701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2406, 4, 0, 3, "素敵な香りです～\n救護院に置けば、\nみんな喜ぶかも～", 1, 107701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2407, 5, 0, 3, "あったかいですね…\nはっ！　いけません！\nメイドがサボるなんて", 1, 107701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2409, 7, 0, 3, "料理のできない\nメイドなんて…\nはぐはぐ、おいしい♪", 1, 107701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2410, 8, 0, 3, "水着姿で接客をすると\n売り上げが倍になる\n…本当ですか、これ", 1, 107701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2412, 10, 0, 3, "えへへ♪\n南の島まで\n乗せてください！", 1, 107701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2413, 11, 0, 3, "なんだか、\n人魚姫に\nなった気分です♪", 1, 107701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2414, 1, 0, 1, "みんなで過ごす\n休日っていいわよね…\nずっと続かないかな", 1, 107801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2415, 2, 0, 1, "海に浮輪でぷかぷか\n浮かぶのって\n癖になりそう", 1, 107801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2416, 3, 0, 1, "ビーチにいると\n日焼けが心配だわ", 1, 107801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2417, 4, 0, 1, "水着姿って楽よね。\n室内だとちょっと\n落ち着かないけど…", 1, 107801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2418, 5, 0, 1, "お、泳ぎの練習なんて\nしないからね！", 1, 107801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2419, 101, 0, 1, "楽しすぎて立場を\n忘れそう…あんたの\nせいなんだから！", 4, 107801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2420, 102, 0, 1, "みんなで遊んで\n美味しいもの食べて\nほんっとに楽しいっ♪", 8, 107801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2421, 1, 0, 2, "マジであっついわね…\nよっし！　海に入って\n涼みましょ！", 1, 107801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2422, 2, 0, 2, "シャワー浴びたばかり\nなのに…また、遊び\nたくなってきたわ！", 1, 107801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2423, 3, 0, 2, "遊び足りないって？\nしょうがないわね…\n付き合ってあげるわ♪", 1, 107801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2424, 4, 0, 2, "一応伝えておくけど\n猫って水が苦手なの！\nわかった！？", 1, 107801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2425, 101, 0, 2, "あたしと話したいの？\nふふ。\nまぁ、いいわよ。\nほら隣に座りなさい", 4, 107801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2426, 102, 0, 2, "こんなに楽しいの\n初めてかも…\nずっと続けばいいのに", 8, 107801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2427, 1, 0, 2, "は、早起きしたのは、\n楽しみで眠れなかった\nからじゃないわよ！", 1, 107801, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2428, 2, 0, 2, "昼食後のストレッチ\n終わり！　さあ、\nビーチが待ってるわ！", 1, 107801, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2429, 3, 0, 2, "夜の海に散歩に\n行こうかな～\nあんたも来なさいよ☆", 1, 107801, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2431, 2, 0, 3, "海ではしゃぎすぎて\n疲れたわ…ちょっと\n休もうかしら？", 1, 107801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2432, 3, 0, 3, "こんな魔物がいたら\n操るのも楽しいかも", 1, 107801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2433, 4, 0, 3, "にゃう～♪\nまるで南国にいる\n気分ね♪", 1, 107801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2434, 5, 0, 3, "あったかぁい♪\n身体が冷えてきたから\n丁度いいわ！", 1, 107801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2436, 7, 0, 3, "運動後のご飯って\nなんでこんなに\n美味しいんだろ♪", 1, 107801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2437, 8, 0, 3, "どんなカナヅチでも、\n絶対必ず泳げる方法、\n書いてないかしら？", 1, 107801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2439, 10, 0, 3, "あんたもあたしが\n操ってあげる！\nなんてね～♪", 1, 107801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2440, 11, 0, 3, "リゾートリゾートっ\nのんびりしましょ♪", 1, 107801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2441, 1, 0, 1, "ここなら思う存分\nデスクワークに\n励めるわね！", 1, 108001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2442, 2, 0, 1, "武器のメンテナンスも\nしっかりしないと\n最高効率は望めないわ", 1, 108001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2443, 3, 0, 1, "準備をしっかり整えて\nもっともっと\n稼がなくちゃね！", 1, 108001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2444, 4, 0, 1, "少し休んだら\n次のバイトに\n行ってくるわね！", 1, 108001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2445, 5, 0, 1, "この格好でここに\nいるのって\n気恥ずかしいわね…", 1, 108001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2446, 101, 0, 1, "キミが望むなら\n今日のバイト\n休んでもいいわよ？", 4, 108001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2447, 102, 0, 1, "キミだけね…\n私のことをここまで\n理解してくれるのは", 8, 108001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2448, 1, 0, 2, "普段と違う格好\nだからって\nそんなに見ないでよ…", 1, 108001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2449, 2, 0, 2, "キミを立派な\nバイト戦士にするのが\n師匠としての役目よね", 1, 108001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2450, 3, 0, 2, "そろそろ次のバイトに\n行かないと…\nキミも一緒に来る？", 1, 108001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2451, 4, 0, 2, "効率は大事だけど\nゆっくりするのも\n悪くないわね", 1, 108001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2452, 101, 0, 2, "一緒の時間が楽しくて\nバイトのことを\n忘れちゃいそうね", 4, 108001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2453, 102, 0, 2, "キミと一緒なら\nどんなミッションも\n楽勝ね！", 8, 108001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2454, 1, 0, 2, "おはよう、いい朝ね！\n今日もめいっぱい\n稼ぐわよ！", 1, 108001, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2455, 2, 0, 2, "もうお昼だったのね…\n内職のバイトに\n集中しすぎちゃったわ", 1, 108001, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2456, 3, 0, 2, "スケジュールは、と…\nこういう管理の徹底が\n効率を生み出すのよ♪", 1, 108001, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2458, 2, 0, 3, "しっかり働いて休む…\n最高効率にまた一歩\n近づいたわね", 1, 108001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2459, 3, 0, 3, "なごんじゃうわね…\nよぉし、次のバイトも\n頑張らないと！", 1, 108001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2460, 4, 0, 3, "いい香りね…\n働く意欲が\n湧いてくるわ", 1, 108001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2461, 5, 0, 3, "身体が冷えたから\n助かるわ…冷えは\n女の天敵だもの", 1, 108001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2463, 7, 0, 3, "海のバイトは\n体力勝負よ\nエネルギーはしっかり\n摂取しないとね！", 1, 108001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2464, 8, 0, 3, "座学も大切ね\nもっと知識を\n身につけないと…", 1, 108001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2466, 10, 0, 3, "海の楽園みたいね…\n今だけは効率を\n忘れちゃいそう…", 1, 108001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2467, 11, 0, 3, "落ち着くわ…\n海とひとつになった\n気分ね！", 1, 108001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2468, 1, 0, 1, "ちょっと休憩にゃ～\n潮風は体に\n良くないにゃ～", 1, 107901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2469, 2, 0, 1, "猫グッズで\n海の家を飾りたいにゃ", 1, 107901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2470, 3, 0, 1, "海の幸は美味しいけど\n水はイヤだにゃ…", 1, 107901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2471, 4, 0, 1, "開店にゃ！\nこのたい焼きなら\nきっと大繁盛にゃ！", 1, 107901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2472, 5, 0, 1, "海と言えば水着にゃ\n似合ってるかにゃ？", 1, 107901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2473, 101, 0, 1, "キミが頼むなら\n手伝ってあげても\nいいにゃ", 4, 107901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2474, 102, 0, 1, "あたしの宝は\nここにあるにゃ～", 8, 107901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2475, 1, 0, 2, "にゃ？\nそんなに見つめても\n何も出ないにゃ", 1, 107901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2476, 2, 0, 2, "今からキミを\nあたしの相棒に\n任命するにゃ！", 1, 107901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2477, 3, 0, 2, "これから\n開店の準備にゃ\nキミも手伝って\nくれるのかにゃ？", 1, 107901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2478, 4, 0, 2, "海の家で出す\n新しいメニューを\n考え中なのにゃ～", 1, 107901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2479, 101, 0, 2, "キミと一緒なら\n売上げナンバーワンに\nなれるにゃ！", 4, 107901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2480, 102, 0, 2, "キミと一緒にいると\n楽しくて、もっと\nいたくなるにゃ", 8, 107901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2481, 1, 0, 2, "おはようにゃ！\n今日も頑張って\nいくにゃー！", 1, 107901, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2482, 2, 0, 2, "お腹すいたにゃ～…\nお魚、ちょっとだけ、\nつまみ食いするにゃ", 1, 107901, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2483, 3, 0, 2, "明日の準備は万端にゃ\nいつでも\n出発できるにゃ", 1, 107901, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2485, 2, 0, 3, "このふかふか具合\nまるで子猫の\n毛並みのようにゃ～", 1, 107901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2486, 3, 0, 3, "にゃにゃ～\nなんてかわいさにゃ～", 1, 107901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2487, 4, 0, 3, "この匂い…\nもう少し香ばしいと\nいいかもしれないにゃ", 1, 107901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2488, 5, 0, 3, "にゃ～…\nあったかくて\nぬくぬくだにゃ～", 1, 107901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2490, 7, 0, 3, "働くにはまず体力にゃ\nしっかり食べて\nスタミナアップ\nだにゃ!", 1, 107901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2491, 8, 0, 3, "ふむふむ…\nここにお宝が\n眠ってるんだにゃ", 1, 107901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2493, 10, 0, 3, "シードレイクにゃ！\n本物は怖かったけど、\nぬいぐるみなら\nかわいいにゃ～", 1, 107901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2494, 11, 0, 3, "貝なのにふかふかの\nクッションにゃ！\n屋台の前にも\n置いてみたいにゃあ", 1, 107901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2495, 1, 0, 1, "人の家って、道場とは\n違った香りがするね", 1, 103701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2496, 2, 0, 1, "ちょっと退屈だね…\nあの人をからかいに\n行こうかな♪", 1, 103701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2497, 3, 0, 1, "普通の女の子みたいに\n遊びたいって思う\nこともあるんだよね", 1, 103701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2498, 4, 0, 1, "剣の手入れを怠らない\nようにしないと。\nいざという時、困るし", 1, 103701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2499, 5, 0, 1, "もっと\n上手く立ち回って\n出世したいな", 1, 103701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2500, 101, 0, 1, "おかえりなさい！\nふふ、なんだか奥さん\nになった気分だよ♪", 4, 103701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2501, 102, 0, 1, "恥ずかしくて、あなた\nを見られないよ…\n私らしくないなぁ…", 8, 103701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2502, 1, 0, 2, "私、お話好きなんだ。\nどうせなら楽しい話を\nしたいな♪", 1, 103701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2503, 2, 0, 2, "女の子っぽい子と、\n男勝りな子、あなたは\nどっちが好き？", 1, 103701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2504, 3, 0, 2, "そろそろ見回りの\n時間かな。あなたも\n一緒に行く？", 1, 103701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2505, 4, 0, 2, "構って欲しいのかな？\nそれじゃ一緒に\nランニングに行こうか", 1, 103701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2506, 101, 0, 2, "二人で出かけるなら、\n手を繋がないとね。\nあはは、冗談冗談♪", 4, 103701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2507, 102, 0, 2, "ねえ、あなた♪\nおや、顔が真っ赤だ！\n…ふふ、私も、かな？", 8, 103701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2508, 1, 0, 2, "私は朝から鍛錬だよ。\n慣れてるけど大変だ。\nお互いに頑張ろうね", 1, 103701, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2509, 2, 0, 2, "鍛錬でお腹ぺこぺこ。\nお昼ごはんが\n楽しみだね", 1, 103701, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2510, 3, 0, 2, "今日も一日見回りと\n鍛錬を頑張ったよ。\nあなたもお疲れさま", 1, 103701, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2512, 2, 0, 3, "いざという時のため\nにも、休憩は大事だね", 1, 103701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2513, 3, 0, 3, "こういうものには\n今まで\n縁がなかったけど。\nうん、可愛いね♪", 1, 103701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2514, 4, 0, 3, "精神統一に通じる\nものがあるね。\nふぅ、癒されるなぁ", 1, 103701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2515, 5, 0, 3, "あったかいなぁ。\nたまにはのんびり\nしようか", 1, 103701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2517, 7, 0, 3, "鍛錬後はすごくお腹が\n空くんだよね。\nう～ん、おいしい♪", 1, 103701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2518, 8, 0, 3, "剣術書だけで強くなる\nには飛びぬけた才能と\n努力が必要だよね", 1, 103701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2520, 1, 0, 1, "正義の刃で\n悪を挫く！\n…キマったッス♪", 1, 100501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2521, 2, 0, 1, "ヒーローの\n秘密特訓を\n始めるッスよ～！", 1, 100501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2522, 3, 0, 1, "正義の味方目指して\n今日も明日も\n全力ッスよ♪", 1, 100501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2523, 4, 0, 1, "一日一善！\nヒーローだったら\n当たり前ッス♪", 1, 100501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2524, 5, 0, 1, "どうッスか\nこのポーズ！\nイイ感じッスか？", 1, 100501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2525, 101, 0, 1, "た、たまには\nよく頑張ったねって\n褒めてほしいッス…", 4, 100501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2526, 102, 0, 1, "自分を信じてくれる\nあんたと一緒なら\n何でもやれるッス！", 8, 100501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2527, 1, 0, 2, "おつかれッス♪\n冒険の準備は\nばっちりッスよ！", 1, 100501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2528, 2, 0, 2, "どうしたんッスか？\n…はっ！　もしや敵が\n迫ってるんスかっ！？", 1, 100501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2529, 3, 0, 2, "べ、別に\n頭を撫でてほしいとか\n思ってないッスよ？", 1, 100501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2530, 4, 0, 2, "スーパーヒーロー\nトラタイガーは\n絶対無敵ッス！", 1, 100501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2531, 101, 0, 2, "あんたと自分で\n正義の心を燃やして\n悪を一網打尽ッス！", 4, 100501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2532, 102, 0, 2, "あんたと一緒だと\n幸せな気持ちに\nなるんッス…♪", 8, 100501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2533, 1, 0, 2, "お、早いッスね～♪\n今日も一日一善\nよろしくッス！", 1, 100501, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2534, 2, 0, 2, "一休みしながら\nヒーローについて\n語り合うッスよ～！", 1, 100501, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2535, 3, 0, 2, "夜のパトロールで\n一緒に平和を\n守るッス！", 1, 100501, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2537, 2, 0, 3, "ふぅぅ…\n落ち着くッスね\n戦士の休息ッス♪", 1, 100501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2538, 3, 0, 3, "くぅぅ、可愛いッス…\nヒーローが守るべき\n愛らしさッスね！", 1, 100501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2539, 4, 0, 3, "なんスかこの香り？\nすぅぅ…ふぅぅ…\n力がみなぎるッス！", 1, 100501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2540, 5, 0, 3, "優しい光ッスね…\nヒーローの優しさ\nそのものッス！", 1, 100501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2542, 7, 0, 3, "あむあむあむ…\nんぅう、元気が溢れて\n止まらないッス！", 1, 100501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2543, 8, 0, 3, "トラタイガーのお話は\n笑いあり涙ありで\n超おすすめッス！", 1, 100501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2545, 1, 0, 1, "ハッピーハロウィン\nお父さん、お菓子は…\n食べられないね", 1, 108101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2546, 2, 0, 1, "トリックオアトリート\nお菓子をくれないと\n不吉な占いをします", 1, 108101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2547, 3, 0, 1, "ふふ、あなたの後ろで\n霊のみなさんが\n踊り始めました", 1, 108101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2548, 4, 0, 1, "霊感がいつも以上に\n冴えているので\n占いの調子も良好です", 1, 108101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2549, 5, 0, 1, "父はハロウィンが\n大好きだそうです\n大胆な格好をする子が\n多いので…", 1, 108101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2550, 101, 0, 1, "あなたは霊たちにも\n大人気ですよね\n私も見てほしいな…", 4, 108101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2551, 102, 0, 1, "ハロウィンの賑やかさ\n今は好きです…\nあなたといられるので", 8, 108101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2552, 1, 0, 2, "霊のみなさんとの\nダンスパーティー…\n楽しみです", 1, 108101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2553, 2, 0, 2, "お父さん、元気だね\nえ？　ここは色んな\n女の子に会えて幸せ？", 1, 108101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2554, 3, 0, 2, "ハロウィンのお菓子\n食べすぎちゃったな…\nでも、美味しかった…", 1, 108101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2555, 4, 0, 2, "あなたの女難の相…\n悪化していますね\nお祓いが必要かも", 1, 108101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2556, 101, 0, 2, "あの、霊のみなさんが\nあなたと踊りたいと…\nえっと、私も…", 4, 108101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2557, 102, 0, 2, "あなたと手を繋ぐと\n胸が苦しくなります\nでも、繋ぎたいな…", 8, 108101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2558, 1, 0, 2, "ハッピーハロウィン…\n父が元気なので\n今日の運気は良好です", 1, 108101, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2559, 2, 0, 2, "父はお昼寝中です\n本当に寝ているかは\n私にも分かりませんが", 1, 108101, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2560, 3, 0, 2, "お父さん、\nもう眠っちゃったの？\n…眠れるんだったっけ", 1, 108101, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2562, 2, 0, 3, "落ち着きますね…\n霊のみなさんも\n喜んでいます", 1, 108101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2563, 3, 0, 3, "つん、つん。ふふっ♪\n癒やされます…", 1, 108101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2564, 4, 0, 3, "精神が研ぎ澄まされる\n香りですね…\n占いが捗ります", 1, 108101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2565, 5, 0, 3, "あったかいね\nお父さん…\nえ？　分からない？", 1, 108101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2567, 7, 0, 3, "霊のみなさんは\n食べられないので…\n私がいただきます", 1, 108101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2568, 8, 0, 3, "お父さんを元に戻す\n手掛かり…\n書かれていないかな？", 1, 108101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2570, 12, 0, 3, "わぁ、お菓子が沢山…", 1, 108101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2572, 1, 0, 1, "無料でプリンが\nもらえるなんて、\nハロウィンはすごいの", 1, 108201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2573, 2, 0, 1, "生きてるときも、\nミヤコはハロウィンが\n好きだった気がするの", 1, 108201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2574, 3, 0, 1, "みんなに仮装を\n見せつけて、\nプリンをもらうの～", 1, 108201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2575, 4, 0, 1, "この仮装で、\nみんなに恐怖を\n教えてやるの！", 1, 108201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2576, 5, 0, 1, "アオーン、アオーン！\nミヤコ、狼女に\nなりきってるの～", 1, 108201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2577, 101, 0, 1, "アイツに秘密で、\nパーティーの\n飾り付けをするの♪", 4, 108201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2578, 102, 0, 1, "アイツが狼男の\n仮装をすれば、\nおそろいなの～", 8, 108201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2579, 1, 0, 2, "プリンをくれなきゃ、\nオマエをカボチャに\nしてやるの～", 1, 108201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2580, 2, 0, 2, "ハロウィンは幽霊でも\n参加できるから\n楽しいの～！", 1, 108201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2581, 3, 0, 2, "プリンがないなら\nカボチャプリンを\n食べればいいの！", 1, 108201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2582, 4, 0, 2, "プリンの可能性は\n無限大なの～", 1, 108201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2583, 101, 0, 2, "オマエの隣で\nプリンを食べると、\nいつもより美味しいの", 4, 108201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2584, 102, 0, 2, "オマエが死んでも、\nミヤコはずーっと\n一緒にいてやるの～", 8, 108201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2585, 1, 0, 2, "朝ごはんは\nカボチャプリンなの！\nハロウィンっぽいの～", 1, 108201, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2586, 2, 0, 2, "ランチは豪華に\nプリンアラモードなの", 1, 108201, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2587, 3, 0, 2, "夜は幽霊たちと\nハロウィンするの！\nオマエも来るの！", 1, 108201, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2589, 2, 0, 3, "幽霊にも休息は\n必要なの！\nプリンもってこいなの", 1, 108201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2590, 3, 0, 3, "オマエもミヤコの\nパーティーに\n招待してあげるの～", 1, 108201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2591, 4, 0, 3, "癒されるの～\nまるで天国なの～", 1, 108201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2592, 5, 0, 3, "ここでなら、\n焼きプリンが\n作れるの！", 1, 108201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2594, 7, 0, 3, "腹が減っては\nハロウィンは\nできぬなの！", 1, 108201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2595, 8, 0, 3, "丸ごとカボチャ\nプリン特集！？\n美味しそうなの～♪", 1, 108201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2597, 12, 0, 3, "お菓子がたくさんで、\nミヤコはとっても\nハッピーなの♪", 1, 108201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2599, 1, 0, 1, "魔女以外にも、\n色々な仮装を\nしてみたいわね", 1, 108301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2600, 2, 0, 1, "チョコやアメよりも、\nアタシに相応しい\nスイーツがあるはず…", 1, 108301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2601, 3, 0, 1, "お菓子の食べ過ぎは、\nお母さんに\n怒られるかも…", 1, 108301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2602, 4, 0, 1, "この格好で学院に\n行ったら、\nみんな驚くかしら", 1, 108301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2603, 5, 0, 1, "大人のレディは、\nどういうハロウィンを\n過ごすべきかしら…？", 1, 108301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2604, 101, 0, 1, "お菓子、アイツの分も\nとっといてあげよ！\n大人の余裕ってやつね", 4, 108301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2605, 102, 0, 1, "アイツを魅了する\n魔法とかないかしら？", 8, 108301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2606, 1, 0, 2, "おしゃれスイーツを\nくれなきゃ\nイタズラするわよ！", 1, 108301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2607, 2, 0, 2, "アタシの大人センスで\nアンタに似合う仮装を\n選んであげるわ！", 1, 108301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2608, 3, 0, 2, "不気味よねぇ…\nジャックオーランタン\nこ、怖くないわよ？", 1, 108301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2609, 4, 0, 2, "ハロウィンが楽しくて\n宿題やり忘れてたわ…\nて、手伝って～！", 1, 108301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2610, 101, 0, 2, "お菓子をもらうのも\nアンタと一緒なら\n恥ずかしくないわ！", 4, 108301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2611, 102, 0, 2, "アタシのエスコートを\n任せられるのは、\nアンタだけなんだから", 8, 108301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2612, 1, 0, 2, "朝食はオシャレに\nグリーンスムージー！\n…に、にがぁ", 1, 108301, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2613, 2, 0, 2, "昼下がりはカフェで\n優雅なティータイム\n…憧れるわ～", 1, 108301, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2614, 3, 0, 2, "ハロウィンナイトの\nはじまりよ！\n街に繰り出しましょ！", 1, 108301, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2616, 2, 0, 3, "あう～、お菓子の\n食べすぎで\n動けな～い", 1, 108301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2617, 3, 0, 3, "アンタも仮装する？\nいまよりもーっと\nかわいくなるわよ！", 1, 108301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2618, 4, 0, 3, "大人な香りに\n包まれてる～♪", 1, 108301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2619, 5, 0, 3, "ポカポカね～♪\n…ああー！\nチョコが溶けてる！", 1, 108301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2621, 7, 0, 3, "おいしい～♪\n…じゃなくて！\nび、美味ですわ～", 1, 108301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2622, 8, 0, 3, "この服かわい～♪\nって、なにこの値段！\nお小遣い何年分よ！？", 1, 108301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2624, 12, 0, 3, "お、お菓子なんかで\n喜ぶほど、子供じゃ…", 1, 108301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2626, 1, 0, 1, "次はどんな策で団長を\nからかってみようか…", 1, 107101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2627, 2, 0, 1, "む？\n血の臭いがするな\n祭りの時間か☆", 1, 107101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2628, 3, 0, 1, "平和に飼い殺される\n前に、混沌の愛を\n育みたいね", 1, 107101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2629, 4, 0, 1, "本気を出した団長と\nいつか手加減なしに\n殺り合いたいな", 1, 107101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2630, 5, 0, 1, "おばさん呼ばわりする\n新米騎士の小娘を\n懲らしめてやろうか", 1, 107101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2631, 101, 0, 1, "胸を触られるまで\n誓約のことなど\nすっかり忘れていたな", 4, 107101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2632, 102, 0, 1, "まさかこのワタシが\nプロポーズするとはな\n…笑えるじゃないか", 8, 107101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2633, 1, 0, 2, "面白い暇つぶしを\n思いついたんだ\n戯れに付き合え☆", 1, 107101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2634, 2, 0, 2, "退屈で死にそうだ\n愉しい話題を\nワタシに提供してくれ", 1, 107101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2635, 3, 0, 2, "なんだ、暇なのか？\n心躍ることでも探しに\n出かけようではないか", 1, 107101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2636, 4, 0, 2, "このワタシを愉快に\nさせてくれる事でも\nあったのか？", 1, 107101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2637, 101, 0, 2, "ワタシに求婚した男の\n数だと？\nいちいち覚えてないな", 4, 107101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2638, 102, 0, 2, "最強無敵の女と\n結婚できるんだ\n誇りに思えよ☆", 8, 107101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2639, 1, 0, 2, "なにか愉快なことが\n起こりそうな朝だ☆\nワクワクするね", 1, 107101, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2640, 2, 0, 2, "うーむ、腹がすいたな\n愉快なことで\nこの空腹を満たしたい", 1, 107101, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2641, 3, 0, 2, "これからは大人の\n時間だ。愉しみ方を\n知りたいのかい？", 1, 107101, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2643, 2, 0, 3, "このワタシとした事が\n穏やかな気分に\nなってしまうとは…", 1, 107101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2644, 3, 0, 3, "可愛い姿をしたものが\n惨たらしい\n醜態を晒すのを\n想像してしまう", 1, 107101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2645, 4, 0, 3, "悪くはない香りだ\nだが、ワタシは血の\n臭いを嗅ぎたいぞ☆", 1, 107101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2646, 5, 0, 3, "ぬるい、ぬるすぎる！\n地獄の業火には\nほど遠い熱さだ！", 1, 107101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2648, 7, 0, 3, "ワタシの腕前に\n勝るとも劣らない\nなかなかの味だ", 1, 107101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2649, 8, 0, 3, "意中の男性を絶対に\n落とす方法…？\nく、下らんっ！", 1, 107101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2651, 1, 0, 1, "明日はあの釣り場に\n行くとしようかな\nあの子も誘ってみるか", 1, 105601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2652, 2, 0, 1, "ぷはーっ！\n体を動かしたあとの\n一杯は格別だね", 1, 105601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2653, 3, 0, 1, "天気が良いと\n昼寝か釣りかで\n迷っちまうな…", 1, 105601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2654, 4, 0, 1, "大物が釣れたときの\n快感は、他じゃ\n味わえないね", 1, 105601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2655, 5, 0, 1, "ふあぁ～…\n昼寝でもするか", 1, 105601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2656, 101, 0, 1, "ふふっ…\nあの子、見た目より\n根性あるんだね", 4, 105601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2657, 102, 0, 1, "あの子が強くなるには\nどんな魚を食べさせて\nあげればいいのやら…", 8, 105601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2658, 1, 0, 2, "姉御って呼ぶんじゃ\nないよ。からかうと\nタダじゃおかないよ", 1, 105601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2659, 2, 0, 2, "釣りのコツ？\n何事も経験が大事だ\n今から行くかい？", 1, 105601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2660, 3, 0, 2, "どうしたのさ？\n助太刀が必要かい？\nアタシにまかせときな", 1, 105601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2661, 4, 0, 2, "困ってる人がいたら\nまた力を貸しておくれ\n頼りにしてるよ", 1, 105601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2662, 101, 0, 2, "なにか困りごとかい？\nどうやら昼寝は\nまた今度だね", 4, 105601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2663, 102, 0, 2, "よしよし！\nお人好しのお前さんを\n労ってあげるよ", 8, 105601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2664, 1, 0, 2, "おはよう、お前さん\n魚屋は早起きでなきゃ\nつとまらないよ？", 1, 105601, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2665, 2, 0, 2, "昼飯かい？　今朝\n仕入れた魚があるんだ\n食べてみるかい？", 1, 105601, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2666, 3, 0, 2, "夜釣りにぴったりの\n月が出てるよ\nいい釣りが出来そうだ", 1, 105601, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2668, 2, 0, 3, "コイツに座って\nのんびり釣りが\nできりゃいいのにな", 1, 105601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2669, 3, 0, 3, "よしよし…♪\nおっと、かわいい物も\nつい抱きしめちまうな", 1, 105601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2670, 4, 0, 3, "これで魚の臭いを\n消せれば、猫も\n寄らなくなるのかね", 1, 105601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2671, 5, 0, 3, "冬場の釣りも\nこれくらい暖かけりゃ\nいいんだけどね", 1, 105601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2673, 7, 0, 3, "腹が減っては\n釣りもできぬ、ってな\nまずは腹ごしらえだ", 1, 105601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2674, 8, 0, 3, "へぇ…こんな魚が\nいたとは…いつか\n釣りたいもんだね", 1, 105601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2676, 1, 0, 1, "流石に室内で\n魔法ぶっぱしたら\n怒られるよねぇ", 1, 101301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2677, 2, 0, 1, "可愛い女の子が\nたくさんいる！\nここは楽園か！？", 1, 101301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2678, 3, 0, 1, "みんなに漫画とか\n勧めてみようかな！\n布教用のがあるし！", 1, 101301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2679, 4, 0, 1, "わ、私一人の\n部屋じゃないから\n色々自重せねば！", 1, 101301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2680, 5, 0, 1, "「ぬるぽ」\n…くっ、\n誰も反応なしか！", 1, 101301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2681, 101, 0, 1, "次はどの漫画を\n貸そうかなー♪", 4, 101301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2682, 102, 0, 1, "次の定期巡回で\nとっておきの店に\n連れてってあげよう！", 8, 101301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2683, 1, 0, 2, "君に似合いそうな\n衣装を買ったよ！\n君もいざ魔法少女に！", 1, 101301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2684, 2, 0, 2, "さあ！\n暇なら定期巡回に\nレッツラゴー！", 1, 101301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2685, 3, 0, 2, "戦闘だけじゃなくて、\n日常にも役立つ\n魔法を習得したいね！", 1, 101301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2686, 4, 0, 2, "新魔法思いついた！\n私って天才！？", 1, 101301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2687, 101, 0, 2, "決め台詞は超大事！\n君の決め台詞も\n私が考えてあげよう！", 4, 101301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2688, 102, 0, 2, "私ってもしやリア充？\n…そんなバカな！\n非リア代表のはずが！", 8, 101301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2689, 1, 0, 2, "おはよー…\n徹夜で本読んでたから\nまだ眠い～", 1, 101301, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2690, 2, 0, 2, "昼飯食べて、\n午後の定期巡回も\n頑張りましょー！", 1, 101301, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2691, 3, 0, 2, "夜からが、\nオタ活本番でしょ！", 1, 101301, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2693, 2, 0, 3, "ふいー！\nくつろいで本を読む！\n最高だねー！", 1, 101301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2694, 3, 0, 3, "君、可愛いね～♪\n魔法少女の\n使い魔みたい！", 1, 101301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2695, 4, 0, 3, "良い匂いに\n釣られてしまった…\nまさか、孔明の罠！？", 1, 101301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2696, 5, 0, 3, "暖かいけど、\nここで本を読むのは\nちょっち危ないかな", 1, 101301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2698, 7, 0, 3, "ウマ―！\nこれの詳細きぼんぬ！", 1, 101301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2699, 8, 0, 3, "こ、古代語の本！？\n現代語でおk…", 1, 101301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2701, 1, 0, 1, "ふふふっ♪\n次はどんな悪戯を\nしてやろうかしら♪", 1, 190901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2702, 2, 0, 1, "幻影湖の霧を吸った\n人間たちの\nリアクションって、\nサイコーよねぇ～♪", 1, 190901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2703, 3, 0, 1, "室内だけとはいえ…\n自由に動けるって、\nやっぱりいいわ～♪", 1, 190901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2704, 4, 0, 1, "改めて見ると良い\n部屋ね…悪戯とか\nしやすそうだしぃ♪", 1, 190901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2705, 5, 0, 1, "私を封じていた部屋\nなのに…急に居心地が\n良くなったわね♪", 1, 190901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2706, 1, 0, 2, "あんたの頭、座り心地\n良いわね…私専用の\n椅子にしてあげる♪", 1, 190901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2707, 2, 0, 2, "外は大変みたいねぇ…\nまあ、私は好き勝手に\nやらせてもらうわ♪", 1, 190901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2708, 3, 0, 2, "顔に落書された？\n誰か知らないけど、\n悪いことする奴も\nいるものねぇ～♪", 1, 190901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2709, 4, 0, 2, "フィオのやつ…\nどこをウロチョロ\nしてるのかしら？", 1, 190901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2710, 1, 0, 2, "人間の朝は大変ねぇ…\n私はその様を見ながら\nゆっくりするわね♪", 1, 190901, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2711, 2, 0, 2, "う～私も、お日様を\n浴びながら、お外を\nお散歩したーい！", 1, 190901, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2712, 3, 0, 2, "この暗さ…閉じ込め\nられていた時を\n思い出すわね…", 1, 190901, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2714, 2, 0, 3, "悪戯しすぎて\n疲れたから、ちょっと\n休憩しよっと♪", 1, 190901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2715, 3, 0, 3, "ガイド妖精たる私が、\nこんな造形物に\n惹かれる…わけが……\nかわいい♪", 1, 190901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2716, 4, 0, 3, "この香りを囮にして、\n周りに悪戯を仕込む…\n完璧ね♪", 1, 190901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2717, 5, 0, 3, "不思議ねぇ…私は\n温度とか関係ないのに\n離れられないわ", 1, 190901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2719, 7, 0, 3, "食事の必要がない体…\nつまり、食べたければ\n食べていいのよね～♪", 1, 190901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2720, 8, 0, 3, "へぇ…最近はこんな\n悪戯もあるのねぇ…\nあいつで試さないと♪", 1, 190901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2722, 1, 0, 1, "メリークリスマス！\n歌で幸せを\n届けます…！", 1, 108401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2723, 2, 0, 1, "あなたと出会えたのは\n偶然ではなくて…\nクリスマスの奇跡だと\n思うんです", 1, 108401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2724, 3, 0, 1, "クリスマスは華やかで\nつい子供のように\nはしゃいでしまいます", 1, 108401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2725, 4, 0, 1, "曲が書けるのは\nあなたが側に\nいてくれるからです…", 1, 108401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2726, 5, 0, 1, "感動を与えられる歌…\nあなたと一緒になら\n生み出せるはず…", 1, 108401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2727, 101, 0, 1, "クリスマスの幸せを\nあなたと\n感じ合えたらな…", 4, 108401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2728, 102, 0, 1, "あなたの幸せを願った\nクリスマスソング…\nいつか披露しますね", 8, 108401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2729, 1, 0, 2, "みなさんの笑顔を\n守りたいから…\n私は歌い続けます", 1, 108401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2730, 2, 0, 2, "歌を届ける\nサンタさんに\nなりたいな…", 1, 108401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2731, 3, 0, 2, "お暇でしたら\nクリスマスのお祝いを\nしませんか？", 1, 108401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2732, 4, 0, 2, "ステージに立つ前に\n喉を整えないと…\n発声練習は念入りに…", 1, 108401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2733, 101, 0, 2, "まわりはかわいい子\nばっかりだけど…\nこの服なら\nアピールになるかな？", 4, 108401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2734, 102, 0, 2, "歌でならいくらでも\nあの人への気持ちを\n込められるのに…", 8, 108401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2735, 1, 0, 2, "今日も一日\n幸せな歌で世界が\n包まれますように…", 1, 108401, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2736, 2, 0, 2, "お茶を飲みながら\n好きな音楽を\n語り合いませんか？", 1, 108401, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2737, 3, 0, 2, "ふふ、寝かしつけの\n子守唄を\nお望みですか？", 1, 108401, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2739, 2, 0, 3, "ふかふかですね\n心地良すぎて\n眠ってしまいそう…", 1, 108401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2740, 3, 0, 3, "素敵…\n優しい歌みたいに\n気持ちが落ち着く…", 1, 108401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2741, 4, 0, 3, "インスピレーションが\n溢れてくる…\nいい曲が書けそう…", 1, 108401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2742, 5, 0, 3, "この光みたいに\n灯火になるような\n歌を歌いたい…", 1, 108401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2744, 7, 0, 3, "喉の健康のためにも\n食事はしっかり\nとるようにしています", 1, 108401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2745, 8, 0, 3, "物語には\n曲作りのヒントが\n詰まっているんです", 1, 108401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2747, 14, 0, 3, "絵が飛び出してくる\nなんて…精霊が宿って\nいるのでしょうか？", 1, 108401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2748, 15, 0, 3, "か、可愛い…♪\nちょっとだけ触っても\nいいですよね…？", 1, 108401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2749, 1, 0, 1, "クリスマスでも、\n演劇の練習は\nしっかりやらないと！", 1, 108501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2750, 2, 0, 1, "あうぅ…\nクリスマスの準備が\n終わらないよぅ…", 1, 108501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2751, 3, 0, 1, "アヤネちゃんが\n摘み食いしないように\n見ておかないと…", 1, 108501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2752, 4, 0, 1, "サンタさんみたいに、\n皆に笑顔で接する…\nうぅ…私には無理ぃ…", 1, 108501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2753, 5, 0, 1, "新しいお洋服～♪\nスズメお姉ちゃんに\nお礼をいわないと♪", 1, 108501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2754, 101, 0, 1, "お兄ちゃんの為に、\nプレゼントを\n用意しないと♪", 4, 108501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2755, 102, 0, 1, "クリスマス…\n恋人の日…ひゃわっ！\nわ、私ってば何を…！", 8, 108501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2756, 1, 0, 2, "メリー\nクリスマスです、\nお兄ちゃん♪", 1, 108501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2757, 2, 0, 2, "そ、そんなに\n見られると…\n恥ずかしいよぉ…", 1, 108501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2758, 3, 0, 2, "ひゃわぁっ！？\n急に触られると、\n驚くからやめてぇ…", 1, 108501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2759, 4, 0, 2, "あ…クリスマスの\n準備を手伝って\nくれるんですか？", 1, 108501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2760, 101, 0, 2, "お兄ちゃんと\n過ごすクリスマス、\n楽しみですぅ…♪", 4, 108501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2761, 102, 0, 2, "クリスマスはその…\n二人で…あうぅ…\nなんでもないですぅ…", 8, 108501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2762, 1, 0, 2, "うう…パーティーが\n楽しみで、あんまり\n眠れなかったよぅ…", 1, 108501, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2763, 2, 0, 2, "ケーキを食べられなく\nなっちゃうから、\nお昼は軽めにしないと", 1, 108501, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2764, 3, 0, 2, "ちょっと眠いけど…\n今日は少しだけ\n夜更かししたいです♪", 1, 108501, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2766, 2, 0, 3, "ふかふかぁ…\nクリスマスの準備中に\n眠くなっちゃいますぅ", 1, 108501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2767, 3, 0, 3, "この子と一緒に\nおままごとしたいな♪", 1, 108501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2768, 4, 0, 3, "いい匂い…♪\nみんなで\nアロマパーティー\nしたいなぁ…♪", 1, 108501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2769, 5, 0, 3, "ぬっくぬく…♪\nもう、ここから\n離れたくないよぉ…♪", 1, 108501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2771, 7, 0, 3, "はむはむ…♪\nクリスマスのお料理\nより美味しいかも～♪", 1, 108501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2772, 8, 0, 3, "すごい…\nクリスマスの\n演劇って、\nこんなにあるんだ…", 1, 108501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2774, 14, 0, 3, "すごい…まるで、\nクリスマスの演劇を\n観てるようですぅ…♪", 1, 108501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2775, 15, 0, 3, "とっても可愛いです♪\nお芝居の相手役に\nしたいなぁ…♪", 1, 108501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2776, 1, 0, 1, "甘ぁいケーキも、\nデッカイ七面鳥も、\nぜーんぶ私のだー♪", 1, 108601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2777, 2, 0, 1, "毎日が\nクリスマスだったら\nいいのにな～♪", 1, 108601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2778, 3, 0, 1, "プレゼントはあるし、\n料理は美味しいし、\nクリスマス最高ー♪", 1, 108601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2779, 4, 0, 1, "ここでぷうきちの\nクリスマス一発芸♪\n『無茶ぶりするな！』", 1, 108601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2780, 5, 0, 1, "ぷうきちがいつもより\n重いかも…太ったの？\n『衣装の重さだよ！』", 1, 108601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2781, 101, 0, 1, "お兄ちゃんが\n一緒にいてくれると\nクリスマス楽しいね♪", 4, 108601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2782, 102, 0, 1, "お兄ちゃんに抱きつく\nと、なんだかドキドキ\nする…なんでかな？", 8, 108601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2783, 1, 0, 2, "メリークリスマス♪\nプレゼントは、\nいつでも受付中だよ♪", 1, 108601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2784, 2, 0, 2, "このクマさん耳\n可愛いでしょ♪\nぷうきちと\nお揃いなんだー♪", 1, 108601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2785, 3, 0, 2, "お兄ちゃん…私、\n甘ぁいケーキが\n食べたいなぁ…♪", 1, 108601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2786, 4, 0, 2, "クリスマスぷうきちは\nぬくぬく感が凄いよ！\nお兄ちゃんもさわる？", 1, 108601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2787, 101, 0, 2, "えへへ…お兄ちゃんの\n手は、大きくて、\n暖かくて大好き～♪", 4, 108601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2788, 102, 0, 2, "来年のクリスマスも、\nこうやって\nお兄ちゃんに\n甘えていたいな～♪", 8, 108601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2789, 1, 0, 2, "あれぇ…\n私のケーキはぁ…？\n『ちゃんと起きろ』", 1, 108601, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2790, 2, 0, 2, "お昼はデッカイ\n七面鳥が食べたーい♪\n『夜まで我慢しろ』", 1, 108601, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2791, 3, 0, 2, "『もう寝る時間だぞ』\nサンタさんが来る\nまで起きてるぅ…", 1, 108601, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2793, 2, 0, 3, "このふかふか具合…\nぷうきち！\n負けてられないよ！", 1, 108601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2794, 3, 0, 3, "この子、\nすっごくかわいい～♪\n『ライバル登場か！』", 1, 108601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2795, 4, 0, 3, "良い匂い…あっ！\nぷうきちにこの\n匂いを移せば…！\n『匂い袋にするな』", 1, 108601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2796, 5, 0, 3, "あったか～い♪\nえへへ、ぷうきちも\nあっためてあげる♪", 1, 108601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2798, 7, 0, 3, "ぷうきち！　これ、\nとってもおいしいよ♪\n『食べすぎるなよ』", 1, 108601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2799, 8, 0, 3, "お、大人の私なら、\nこれぐらい…読め…\n『無理するな…』", 1, 108601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2801, 14, 0, 3, "絵が飛び出す…\nぷうきちに絵本を\n入れれば、イタズラに\n使えるかも…♪", 1, 108601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2802, 15, 0, 3, "か、可愛いけど…\nぷうきちサンタの方が\n可愛いんだからぁ…！", 1, 108601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2803, 1, 0, 1, "着物も慣れると\n動きやすいものさ", 1, 108901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2804, 2, 0, 1, "鼻緒が緩んでしまった\n下駄で修業は\n無理があったかな…", 1, 108901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2805, 3, 0, 1, "小腹が空いた…\nユイが作った餅、\nまだ食べられるかな？", 1, 108901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2806, 4, 0, 1, "服装も変わったし\nいつもと違う戦い方を\n想定しないと…", 1, 108901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2807, 5, 0, 1, "…ヒヨリは動きすぎて\n着崩れしてないか\n心配だ", 1, 108901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2808, 101, 0, 1, "これからもずっと\n仲間と、彼と\n一緒に…", 4, 108901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2809, 102, 0, 1, "願いを叶えるために\nもっと自分を\n鍛えていかないと！", 8, 108901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2810, 1, 0, 2, "頭の飾りがずれてた？\nそうか、直してくれて\nありがとう", 1, 108901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2811, 2, 0, 2, "着物がそんなに\n珍しい？\nキミも着てみる？\n…冗談だよ", 1, 108901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2812, 3, 0, 2, "肩掛けの触り心地が\n気に入った？\nならもっと触る？", 1, 108901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2813, 4, 0, 2, "ああ、この刀？\n釣り仲間に見繕って\nもらったんだ", 1, 108901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2814, 101, 0, 2, "キミといると\n気持ちが段々と\n安らいでいくよ", 4, 108901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2815, 102, 0, 2, "キミは私の行く先を\n照らしてくれる\n光のような存在だよ", 8, 108901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2816, 1, 0, 2, "おはよう\n朝の修行を始めるけど\nキミもどう？", 1, 108901, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2817, 2, 0, 2, "この時間に軽く\n休憩をとるといい\n能率がよくなるよ", 1, 108901, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2818, 3, 0, 2, "もう夜か…\n明日もまた頑張ろう\nそれじゃ、おやすみ", 1, 108901, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2820, 2, 0, 3, "…疲れていたのかな\nもう少しで眠る\nところだった…", 1, 108901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2821, 3, 0, 3, "ふふっ、正月衣装を\n着せてやるのも\nよさそうだ", 1, 108901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2822, 4, 0, 3, "ふぅ…いい香りだ\n慌ただしい日々から\n心が解放される", 1, 108901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2823, 5, 0, 3, "この心地よさ…\n寝てしまわないよう\n気をつけないと", 1, 108901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2825, 7, 0, 3, "おいしいけど\n帯がきつくなる前に\n手を止めよう…", 1, 108901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2826, 8, 0, 3, "外を出歩くのも\nいいけど、中で\n読書もありだな", 1, 108901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2828, 1, 0, 1, "…騎士クンって\nおせち料理で\n何が好きなんだろう？", 1, 108801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2829, 2, 0, 1, "着物でお掃除や\nお洗濯するの\n慣れてきた♪", 1, 108801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2830, 3, 0, 1, "今年は\nどんな福袋を\n買おうかな…♪", 1, 108801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2831, 4, 0, 1, "お餅に使う調味料\nみんなで違うから\n色々用意しなきゃ", 1, 108801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2832, 5, 0, 1, "ほっぺがお餅みたいに\nぷにぷに…\n食べ過ぎちゃった…", 1, 108801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2833, 101, 0, 1, "来年も、再来年も\nみんなで一緒に\n新年を迎えたいな", 4, 108801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2834, 102, 0, 1, "お願いです…\nいつかこの気持ちが\n伝わりますように…", 8, 108801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2835, 1, 0, 2, "髪飾りが落ちそう？\nじゃあ…騎士クン\nつけ直してくれる？", 1, 108801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2836, 2, 0, 2, "着物が着たいの？\nじゃあ今度一緒に\n買いに…行く？", 1, 108801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2837, 3, 0, 2, "あっ、騎士クン\nお雑煮のお餅\nいくつ入れたい？", 1, 108801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2838, 4, 0, 2, "外、寒かったよね？\nはいこれ。熱い甘粥\n飲むと温まるよ", 1, 108801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2839, 101, 0, 2, "今年は騎士クンと…\nえっ？　騎士クン！？\nな、なんでもないよ！", 4, 108801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2840, 102, 0, 2, "騎士クンの願い事\nわたしが\n叶えてあげたいな…", 8, 108801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2841, 1, 0, 2, "おはよう\n太陽の光に当たると\n元気が出るよ", 1, 108801, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2842, 2, 0, 2, "お昼ごはん\n作らなきゃ…\n騎士クンも食べる？", 1, 108801, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2843, 3, 0, 2, "一日が終わるのは\nあっという間だよね\n一年も同じなのかな？", 1, 108801, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2845, 2, 0, 3, "ふぅ…\n帯も少し\nゆるめちゃおう…", 1, 108801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2846, 3, 0, 3, "お正月用に\n着物を縫って\nあげたいな", 1, 108801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2847, 4, 0, 3, "これでお肌が\n保湿出来たら\nいいのになぁ", 1, 108801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2848, 5, 0, 3, "ん～…\nぬくぬくしてて\n動きたくないよ～", 1, 108801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2850, 7, 0, 3, "…冬眠しないのに\n熊さんみたいに\n食べちゃいそう", 1, 108801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2851, 8, 0, 3, "年末年始は\n告白のチャンス…\nむ、無理無理！", 1, 108801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2853, 1, 0, 1, "みんな忙しそう…\n待っててね、\nすぐ助けるから！", 1, 108701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2854, 2, 0, 1, "ユイちゃんと\nレイさん…よし！\nどっちも手伝おう！", 1, 108701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2855, 3, 0, 1, "あっ、着崩れてる！\nわわっ、どうしよ！\n帯が結べないよー！", 1, 108701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2856, 4, 0, 1, "初詣楽しみー！\n出店はどこから\n行こうかな？", 1, 108701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2857, 5, 0, 1, "お正月だし\nみんなで楽しく\n遊んじゃおー！", 1, 108701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2858, 101, 0, 1, "うーん…\n騎士くんはお正月\n何してるんだろ…", 4, 108701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2859, 102, 0, 1, "ううぅー…\n騎士くんを見てると\nドキってなっちゃう…", 8, 108701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2860, 1, 0, 2, "あたしから\nおいしそうな匂い？\nくんくん…ソースだ！", 1, 108701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2861, 2, 0, 2, "ヘアピンが…\n拾ってくれたんだ！\nありがとう騎士くん！", 1, 108701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2862, 3, 0, 2, "髪飾りがかわいい？\n耳の近く触りたい？\n…優しくね？　", 1, 108701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2863, 4, 0, 2, "グローブで食べるの\n大変そう？　特訓して\n今はお箸も使えるよ！", 1, 108701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2864, 101, 0, 2, "えっ、ほっぺが赤い？\nそうかな…？\n気のせい気のせい！", 4, 108701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2865, 102, 0, 2, "騎士くんといるとね…\nなんでもない！\nまだ内緒っ！", 8, 108701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2866, 1, 0, 2, "おはよー！\n今日も明るく元気に\nがんばろー！", 1, 108701, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2867, 2, 0, 2, "お腹すいてない？\n一緒にご飯食べよ！\n何にしようかなー♪", 1, 108701, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2868, 3, 0, 2, "今日もいっぱい\nがんばったー！\nいつも？　…そう？", 1, 108701, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2870, 2, 0, 3, "休憩休憩♪\nちょっと休んで\nガンガン動くぞー！", 1, 108701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2871, 3, 0, 3, "かわいい♪\nでも、壊しそうで\nちょっと怖い…", 1, 108701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2872, 4, 0, 3, "はぁー♪\nいい香りー…\n気分スッキリ♪", 1, 108701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2873, 5, 0, 3, "ほっこりしてて\nぬくぬく…\n丸まりたい…", 1, 108701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2875, 7, 0, 3, "ど、どうしよう…！\n手が、止まんないよ！", 1, 108701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2876, 8, 0, 3, "へー…お年玉って\n昔はお餅だったんだ\n…今のほうがいいね", 1, 108701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2878, 1, 0, 1, "甘いチョコの香りを\n漂わせて…ふふっ\nこれで準備万端ですわ", 1, 109001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2879, 2, 0, 1, "ここを私のチョコで\n埋め尽くしますわ", 1, 109001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2880, 3, 0, 1, "エプロンを着て\nあの方を待つ私…\nまるで…クスクス", 1, 109001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2881, 4, 0, 1, "キッチンはどこかしら\n今のうちに作って\nおきたいものが…", 1, 109001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2882, 5, 0, 1, "あのお方を私のチョコ\nなしでは生きられない\n体に…ふふっ\n冗談ですわ…", 1, 109001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2883, 101, 0, 1, "このチョコは運命の味\n一口食べれば私の虜…\nそれも運命ですわ…", 4, 109001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2884, 102, 0, 1, "私ともあろうものが\nなんだかドキドキして\nきましたわ…", 8, 109001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2885, 1, 0, 2, "あなた様。私に\n隠しているものなど\nありませんよね…？", 1, 109001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2886, 2, 0, 2, "私の手作りチョコです\n味の方にも自信\nありますのよ？", 1, 109001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2887, 3, 0, 2, "少し苦いのは\nチョコがビターなだけ\nお気になさらず…", 1, 109001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2888, 4, 0, 2, "解毒薬はどこに\nいれたかしら…？", 1, 109001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2889, 101, 0, 2, "運命で結ばれている\nあなた様と私…\nチョコも特別でないと\nいけませんわ", 4, 109001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2890, 102, 0, 2, "チョコを食べた後は\nメインディッシュ…\nさぁ、私を召し上がれ", 8, 109001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2891, 1, 0, 2, "目覚めのチョコですわ\nすっきり目が覚めます\nしばらく眠れなく\nなりますけど…", 1, 109001, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2892, 2, 0, 2, "おやつのチョコですわ\nデザートにどうぞ\n空腹を感じなく\nなりますわ…クスクス", 1, 109001, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2893, 3, 0, 2, "お夜食のチョコですわ\nとてもよく眠れます\nさ、溶けてしまう前に", 1, 109001, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2895, 2, 0, 3, "この心地良さ…\n参考になりますわ\n次の薬は…クスクス", 1, 109001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2896, 3, 0, 3, "この中、何か\n入りそうですわね\n…チョコレート、とか", 1, 109001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2897, 4, 0, 3, "いい香り…この香りと\n同じものを調合して\n私も持っていたい", 1, 109001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2898, 5, 0, 3, "この暖かさ…\nチョコが溶けないよう\n気を付けなくては", 1, 109001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2900, 7, 0, 3, "いい味をしていますわ\n私も腕を磨きましょう\n料理と、調合の腕を…", 1, 109001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2901, 8, 0, 3, "勉強になりますわ\n知識はいくらあっても\n困りませんもの\n特に、人体の知識は…", 1, 109001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2903, 1, 0, 1, "弟くんのことを\n考えると\nあっという間に時間が\n過ぎちゃう…", 1, 109101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2904, 2, 0, 1, "お姉ちゃんの\n元気の源は、弟くんの\n喜ぶ顔なんだから♪", 1, 109101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2905, 3, 0, 1, "弟くんと会ったら\nまずは抱きしめて\nそれからそれから…", 1, 109101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2906, 4, 0, 1, "今日のお姉ちゃんは\n昨日のお姉ちゃんより\nいいお姉ちゃんだよ♪", 1, 109101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2907, 5, 0, 1, "弟くん、もっと\nお姉ちゃんに\n甘えてもいいのに♪", 1, 109101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2908, 101, 0, 1, "お姉ちゃんのチョコ\n食べさせてあげたい…", 4, 109101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2909, 102, 0, 1, "ドキドキしてきた…\nお姉ちゃんなのに\nどうしてかなぁ…", 8, 109101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2910, 1, 0, 2, "お姉ちゃんを\n好きなだけ\n触っていいよ♪", 1, 109101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2911, 2, 0, 2, "そんなにお姉ちゃんの\nチョコが欲しいの？\n食いしん坊さん♪", 1, 109101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2912, 3, 0, 2, "ナデナデするのは\nお姉ちゃんの仕事！", 1, 109101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2913, 4, 0, 2, "ありがとうなんて…\nお姉ちゃんとして\n当たり前だよ♪", 1, 109101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2914, 101, 0, 2, "どうしたのかなっ？\nお姉ちゃんはいつでも\n弟くんのそばにいるよ", 4, 109101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2915, 102, 0, 2, "チョコを作ったよ\n隠し味は、たくさんの\n弟くんへの愛情♪", 8, 109101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2916, 1, 0, 2, "おはよ。お姉ちゃんが\n顔を洗うの\n手伝ってあげるね♪", 1, 109101, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2917, 2, 0, 2, "ご飯を食べたら\n眠たくなるよね…\nお姉ちゃんと寝る？", 1, 109101, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2918, 3, 0, 2, "そろそろ眠くなった？\nお姉ちゃんが添い寝\nしてあげるね♪", 1, 109101, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2920, 2, 0, 3, "この気持ちよさは\nお姉ちゃんも太鼓判♪", 1, 109101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2921, 3, 0, 3, "このかわいさは\nお姉ちゃんも大満足♪", 1, 109101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2922, 4, 0, 3, "この香り、チョコに\n使えるかな…？", 1, 109101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2923, 5, 0, 3, "弟くんと一緒に\nぬくぬくしたいな…♪", 1, 109101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2925, 7, 0, 3, "すごくおいしいねっ♪\nあとで運動しなきゃ…", 1, 109101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2926, 8, 0, 3, "本を読んでる\n知的なお姉ちゃん\n素敵でしょ♪", 1, 109101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2928, 1, 0, 1, "アタシって…\nここにいても\nいいのか…？", 1, 106101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2929, 2, 0, 1, "ちゃんとした建物に\nいるのが久しぶりで\nそわそわする…", 1, 106101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2930, 3, 0, 1, "ここで天楼覇断剣\n出す練習したら\nアイツら怒るかな？", 1, 106101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2931, 4, 0, 1, "ギルドハウスか…\nこういう隠れ家\n欲しかったんだよな", 1, 106101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2932, 5, 0, 1, "めちゃくちゃ暇だ…\n…なんかやること\nないのか？", 1, 106101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2933, 101, 0, 1, "みんなが早く\nアタシのこと\n思い出さないかな", 4, 106101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2934, 102, 0, 1, "アイツはいいヤツだし\n戦う時はアタシも\n協力してやるか！", 8, 106101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2935, 1, 0, 2, "ちゃんはいらん！\nノウェムでいい！\n分かったか？", 1, 106101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2936, 2, 0, 2, "どうした？\n戦うのか！？\n…なんだ、違うのか", 1, 106101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2937, 3, 0, 2, "身長のことは\nあんまり言うな！\n気にしてるからな！", 1, 106101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2938, 4, 0, 2, "アタシの戦い方は\nなんでもアリだ！\nだから見習うなよ？", 1, 106101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2939, 101, 0, 2, "オマエとの思い出か…\n敵として戦ってばっか\nだったな！", 4, 106101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2940, 102, 0, 2, "オマエやオクトーと\n一緒に、あちこち\nいってみたいんだ！", 8, 106101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2941, 1, 0, 2, "よっ！　おはよう！\n朝だぞー！\n起きてるかー？", 1, 106101, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2942, 2, 0, 2, "腹減ったー！\n飯食うぞ、飯！\nオマエも行くよな？", 1, 106101, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2943, 3, 0, 2, "ふわぁ…\nまだ起きてんのか？\n元気だなオマエ…", 1, 106101, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2945, 2, 0, 3, "ふぃ～…\n極楽極楽…", 1, 106101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2946, 3, 0, 3, "おいオマエ！\n寝技の練習に\n付き合ってもらうぞ", 1, 106101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2947, 4, 0, 3, "くんくん…\nたまにはこういうのも\n悪くないかもな", 1, 106101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2948, 5, 0, 3, "あー…\nもっと前にコイツと\n出会いたかった…", 1, 106101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2950, 7, 0, 3, "VRでも飯はうまい！\n腹が減るのはちゃんと\n生きてる気がするな", 1, 106101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2951, 8, 0, 3, "勉強は大事だな！\nうん！…だめだ！\n全然わからん！", 1, 106101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2953, 1, 0, 1, "探偵というのは\n謎を追い求める\n生き物なのさっ！", 1, 101401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2954, 2, 0, 1, "事件のオチは\n往々にして\n取るに足らないものさ", 1, 101401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2955, 3, 0, 1, "読書で得る知識が\n探偵の\n血肉になるんだよ", 1, 101401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2956, 4, 0, 1, "この前の\n捜査資料…\nどこにあったかな？", 1, 101401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2957, 5, 0, 1, "どんな小さな謎も\n私にとっては\n宝石みたいなものさ！", 1, 101401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2958, 101, 0, 1, "助手くんと一緒だと\nよい発想が浮かぶ…\nどうしてだろうね？", 4, 101401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2959, 102, 0, 1, "助手くんの好きな\nタイプって…\nす、推理してみるっ！", 8, 101401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2960, 1, 0, 2, "珈琲、淹れたてだよ\n一杯どうだい？", 1, 101401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2961, 2, 0, 2, "事件の考察を\nまとめてみたんだけど\n聞いてくれるかな？", 1, 101401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2962, 3, 0, 2, "小さな違和感を\n見逃さない…\n探偵の心得だよ", 1, 101401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2963, 4, 0, 2, "機能的な手帳とペン…\nこの仕事には\n欠かせないね！", 1, 101401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2964, 101, 0, 2, "助手くんに\n珈琲…\n淹れてほしいなぁ～", 4, 101401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2965, 102, 0, 2, "助手くんは\n私にとって\n最高の相棒だよ☆", 8, 101401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2966, 1, 0, 2, "朝はいいひらめきが\n得られるんだ…\nさあ捜査を始めよう！", 1, 101401, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2967, 2, 0, 2, "一休みしたら\n推理の答え合わせを\nしてみようか？", 1, 101401, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2968, 3, 0, 2, "夜の張りこみかい？\nなら私も\n同行させてほしいな！", 1, 101401, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2970, 2, 0, 3, "脳を酷使した後は\nゆっくり休む…\nこれも仕事だよ？", 1, 101401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2971, 3, 0, 3, "この愛らしさ…\n要捜査対象だね！", 1, 101401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2972, 4, 0, 3, "思考が活性化される\n素敵な香りだね…", 1, 101401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2973, 5, 0, 3, "くぅ、心地よすぎる…\n今だけは捜査も\n一休みだね♪", 1, 101401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2975, 7, 0, 3, "いっぱい食べて\nいっぱい考える\n探偵の本分だね！", 1, 101401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2976, 8, 0, 3, "探偵だからね\nミステリには\n一家言あるんだ", 1, 101401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2978, 1, 0, 1, "先輩、素敵だなぁ…\nもっと近づければ\nいいのに…", 1, 105501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2979, 2, 0, 1, "せ、先輩がいた部屋！\n私には刺激が強い…", 1, 105501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2980, 3, 0, 1, "先輩を観察しやすい\n場所はあるかな…", 1, 105501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2981, 4, 0, 1, "バ、バイト代出たし、\nせ、先輩をご飯とかに\n誘いたい…な", 1, 105501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2982, 5, 0, 1, "今頃先輩は\nあの辺かな…？\n見に行ってみよう！", 1, 105501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2983, 101, 0, 1, "私と先輩の出会いって\n思い返すと少女漫画\nみたい…えへへ", 4, 105501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2984, 102, 0, 1, "せ、先輩になら\n観察されてもいいなぁ", 8, 105501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2985, 1, 0, 2, "バイト先に新しい子が\n入って来たんです…\nわ、私、先輩です♪", 1, 105501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2986, 2, 0, 2, "せ、先輩…？\nえっと、あの、その、\nあわわわわ…！", 1, 105501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2987, 3, 0, 2, "えへへ…えへへ～\nせ、先輩から\n話しかけてくれました", 1, 105501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2988, 4, 0, 2, "ま、また…\n先輩と一緒に\nバイトしたい…です", 1, 105501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2989, 101, 0, 2, "私と先輩が同じ空間に\n…ゆ、夢みたいです", 4, 105501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2990, 102, 0, 2, "せ、先輩のそばに\n居られて、私\n今、とっても\n幸せです…", 8, 105501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2991, 1, 0, 2, "せ、先輩…！\nおひゃっ！\nおはようございます！", 1, 105501, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2992, 2, 0, 2, "先輩ランチに\n行きましょう！\nよし、練習はバッチリ", 1, 105501, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2993, 3, 0, 2, "あ、せ、先輩。\nえと、そ、外…\nもう真っ暗ですねぇ", 1, 105501, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2995, 2, 0, 3, "こ、ここで先輩も\n休んだり…はっ！\nわ、私ったらなにを！", 1, 105501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2996, 3, 0, 3, "かわいいですねー\n君も一緒に、\n先輩を観察する？", 1, 105501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2997, 4, 0, 3, "ここから先輩の\n香りがすればなあ", 1, 105501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2998, 5, 0, 3, "先輩と一緒に\nぬくぬくできたら\n最高なんだけどなぁ", 1, 105501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3000, 7, 0, 3, "おいし～\nこれは\n先輩も好きな味かな", 1, 105501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3001, 8, 0, 3, "す、好きな人に\n近づく方法…", 1, 105501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3003, 1, 0, 1, "私の力は、大切な\n人たちを護るために\n役立てたいんだ", 1, 109201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3004, 2, 0, 1, "きれいな景色と\n美味しいご飯！\nこの世界も素敵だね", 1, 109201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3005, 3, 0, 1, "この世界のみんなとも\nもっと仲良く\nなりたいな！", 1, 109201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3006, 4, 0, 1, "わー！\nあれもこれも\n気になっちゃうな！", 1, 109201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3007, 5, 0, 1, "しっかりしなきゃって\n思ってても、\nグレアといるとつい\n甘えちゃうんだよね～", 1, 109201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3008, 101, 0, 1, "違う世界でも\n友だちができて、\nすごくうれしいよ！", 4, 109201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3009, 102, 0, 1, "みんなとも一緒に\n授業とか受けたいな～\n絶対楽しそうだもん！", 8, 109201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3010, 1, 0, 2, "歩き方がきれい？\nえへへ、そうかな…？", 1, 109201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3011, 2, 0, 2, "勉強は好きだよ！\n知識が増えていくのは\nやっぱり楽しいから", 1, 109201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3012, 3, 0, 2, "私たちの国マナリアは\n人々の間に魔法が\n伝えられた、その\n始まりの地なんだ！", 1, 109201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3013, 4, 0, 2, "この部屋にも、\nもっとたくさん本棚が\n増えないかな～…？", 1, 109201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3014, 101, 0, 2, "あなたも魔法、\n覚えてみる？\n私、教えるよ！", 4, 109201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3015, 102, 0, 2, "お茶の時間に\nしたいな～…\n一緒にどう？", 8, 109201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3016, 1, 0, 2, "おはようっ！\n今日も素敵な\n一日にしようね！", 1, 109201, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3017, 2, 0, 2, "お昼ご飯は\nちゃんと食べた？\n午後も頑張ろうね！", 1, 109201, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3018, 3, 0, 2, "つい本の続きが\n気になって、夜更かし\nしちゃうんだよね～…", 1, 109201, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3020, 2, 0, 3, "はぁ、気持ちいい～…\nちょっとだけ\n眠っちゃおうかな…", 1, 109201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3021, 3, 0, 3, "わあ、これ可愛い！\nグレアにも見せて\nあげたいな", 1, 109201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3022, 4, 0, 3, "いい香り～！\nなんだか気持ちが\n落ち着くね", 1, 109201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3023, 5, 0, 3, "あぁ、あったかくて\n幸せ～♪　身体が\nポカポカしてきたよ！", 1, 109201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3025, 7, 0, 3, "こ、これ美味し～！\n止められないよー！", 1, 109201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3026, 8, 0, 3, "ふんふん…\n…はっ！　む、夢中に\nなっちゃってた…", 1, 109201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3028, 1, 0, 1, "新しい建物に入ると\n必ず一度は迷子に\nなっちゃうですよ…", 1, 109301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3029, 2, 0, 1, "おやつはどこですか？\nお腹が空いて\nきちゃったですぅ", 1, 109301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3030, 3, 0, 1, "宿題、７つぐらい\n出しそびれたですけど\nまあ大丈夫ですぅ！", 1, 109301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3031, 4, 0, 1, "不思議なこと\nばかりですけど…\nまぁ大丈夫です！\nこれもせいしゅんです", 1, 109301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3032, 5, 0, 1, "なにか面白そうなもの\nないですかね～？", 1, 109301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3033, 101, 0, 1, "ここのみんなとも\nもうお友だちですぅ！", 4, 109301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3034, 102, 0, 1, "おなかが減ったです…", 8, 109301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3035, 1, 0, 2, "ごはんも美味しいし\nここはいいところです\nルゥは幸せですぅ！", 1, 109301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3036, 2, 0, 2, "魔法の話ですか？\nそれはアンせんぱいに\n聞いてほしいですぅ…", 1, 109301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3037, 3, 0, 2, "あなたもお勉強\n苦手ですか？", 1, 109301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3038, 4, 0, 2, "ルゥの帽子、\nかわいいです？\nお気に入りですぅ♪", 1, 109301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3039, 101, 0, 2, "お友だちいっぱいで、\nルゥはうれしいです♪", 4, 109301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3040, 102, 0, 2, "一緒にお勉強…！？\nぐえぇ、イヤですぅ！", 8, 109301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3041, 1, 0, 2, "朝ですねぇ…ふわわ…\nルゥはまだ夢の中に\nいたいですぅ…", 1, 109301, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3042, 2, 0, 2, "午後もいっぱい\n遊ぶですよ～！", 1, 109301, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3043, 3, 0, 2, "夜ふかしって\nドキドキするです！\nせいしゅんですぅ！", 1, 109301, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3045, 2, 0, 3, "ふかふかですぅ～♪\nおひるねが\nしたくなるですぅ！", 1, 109301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3046, 3, 0, 3, "これふかふかですぅ！\n机でうとうとする時の\n枕によさそうですぅ", 1, 109301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3047, 4, 0, 3, "ふぁぁ、いいにおい…\nなんだか気持ちが\nよくなってきたですぅ", 1, 109301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3048, 5, 0, 3, "ぽっかぽかですぅ～！\nあったまったら\nまた遊びにいくですぅ", 1, 109301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3050, 7, 0, 3, "お、おいしいですぅ！\n全部ルゥに\nくださいですぅ！", 1, 109301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3051, 8, 0, 3, "本は…読むと、\nすぐ眠気がぁ…\n…ぐぅ…", 1, 109301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3053, 1, 0, 1, "ピアノ…\nどこかにないかな？", 1, 109401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3054, 2, 0, 1, "ふぅ…\n落ち着くな…", 1, 109401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3055, 3, 0, 1, "マナリアはいつも\n賑やかなんだ…", 1, 109401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3056, 4, 0, 1, "みんな…\n今頃何してるかな？", 1, 109401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3057, 5, 0, 1, "まさか違う世界に\n来ちゃうなんて…\nびっくりだよね…", 1, 109401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3058, 101, 0, 1, "人と話すのは\n得意じゃないな…", 4, 109401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3059, 102, 0, 1, "ここのみんなと一緒に\n学院に通えたら…\nちょっと面白いかも", 8, 109401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3060, 1, 0, 2, "やっぱり、私の翼や\n尻尾…気になる？", 1, 109401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3061, 2, 0, 2, "一番得意なのは、\nやっぱり炎魔法かな…\n加減は難しいけど…", 1, 109401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3062, 3, 0, 2, "私の故郷は竜の国…\n険しい山脈を\n越えた先にあるんだ", 1, 109401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3063, 4, 0, 2, "アン…？\nあっ、ご、ごめんね！\n間違えちゃった…", 1, 109401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3064, 101, 0, 2, "ふん…ふん…♪\n…わっ！\n今の、聞いてた…？\nは、恥ずかしい…", 4, 109401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3065, 102, 0, 2, "私に手伝えること、\nなにかある…？\nなんでも言ってね", 8, 109401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3066, 1, 0, 2, "朝の空気って\nすごく\n静かで透き通ってて…\nなんだか落ち着くよね", 1, 109401, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3067, 2, 0, 2, "学院食堂のランチも\n美味しいけど…\nここのご飯も好きかな", 1, 109401, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3068, 3, 0, 2, "そろそろベッドに\n行かないとだね\n…寝相、今夜こそ\n気を付けよう…！", 1, 109401, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3070, 2, 0, 3, "羽を伸ばしたく\nなっちゃうね…ふふ", 1, 109401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3071, 3, 0, 3, "わ、私が持つには\nかわいすぎるかな…\nでも、素敵だね", 1, 109401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3072, 4, 0, 3, "いい香り…\nこれ、\nアンも好きそう…", 1, 109401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3073, 5, 0, 3, "火を見ていると\n少し落ち着くんだ…\nへ、変かな？", 1, 109401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3075, 7, 0, 3, "わ、美味しい…！\nこれ、どうやって\n作ったのかな…？", 1, 109401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3076, 8, 0, 3, "この本…アンにも\n見せてあげたら\n喜ぶかな、ふふっ", 1, 109401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3078, 1, 0, 1, "着物はきれいですが\n歩きにくいです…\nいっそ脱いで…ぐふふ", 1, 109501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3079, 2, 0, 1, "あっ、いけません\n着物がはだけて…はっ\nもしやドＳさんが…！", 1, 109501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3080, 3, 0, 1, "この部屋は妄想する\nのにうってつけです…\nぐふふ…じゅるり", 1, 109501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3081, 4, 0, 1, "扇をゆっくりと開く\nこの感触…なぜだか\n艶めかしさを感じます", 1, 109501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3082, 5, 0, 1, "「マッサージをすると\n言いながらドＳさんの\n手は次第に」…ぐふふ", 1, 109501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3083, 101, 0, 1, "…ドＳさんの視線を\n思い出しながら踊りの\n練習です…イイ！", 4, 109501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3084, 102, 0, 1, "よいではないか\nよいではないか～\nいけませんご堪忍を～", 8, 109501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3085, 1, 0, 2, "あ～れ～～～！\nドＳさんもお好きな人\nですねぇ…ぐふふ", 1, 109501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3086, 2, 0, 2, "どこにいてもドＳさん\nの気配を感じ取れる\nようになりました…", 1, 109501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3087, 3, 0, 2, "こんな場所で\nなんというプレイを！\n…妄想でした", 1, 109501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3088, 4, 0, 2, "クウカを押し倒して\nそのまま…じゅるり\nああっ、着物が…！", 1, 109501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3089, 101, 0, 2, "ドＳさんは優しいです\n優しいのにドＳとは\nさすが人格破綻者…！", 4, 109501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3090, 102, 0, 2, "ドＳさんの考えている\nことが伝わってきます\nはい…クウカ脱ぎます", 8, 109501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3091, 1, 0, 2, "眩しい朝日がクウカの\n体を焦がします\n…それもイイ！", 1, 109501, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3092, 2, 0, 2, "お昼のランチは\nクウカがいいだなんて\n大胆過ぎです…じゅる", 1, 109501, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3093, 3, 0, 2, "…今夜のドＳさんは\n大胆です…ぐふふ…\nはっ、視線が…！？", 1, 109501, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3095, 2, 0, 3, "この心地良さ、\n心が休まりすぎて\n体が疼いてきます…！", 1, 109501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3096, 3, 0, 3, "すごく愛らしいです…\n妄想も出てこない\nくらい…じゅるり", 1, 109501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3097, 4, 0, 3, "この香りに包まれて\nいると、いい気持ちに\nなって…いきますぅ…", 1, 109501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3098, 5, 0, 3, "「熱を帯びた体は\nどうしようもなく\n誰かを求め…」ぐふふ", 1, 109501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3100, 7, 0, 3, "お腹が膨らんで\nいきます…それは\nまるで…ぐふふ", 1, 109501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3101, 8, 0, 3, "クウカの探している\nジャンルはどこに\nありますかね？", 1, 109501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3103, 1, 0, 1, "カチョーフーゲツは\nトーゴクの心を\n表した言葉デスね", 1, 109601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3104, 2, 0, 1, "オー！　新しい忍法を\n思いついたデス！\nさっそく試すデス！", 1, 109601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3105, 3, 0, 1, "トーゴクの天気には\n槍が降る日も\nあるそうデス…", 1, 109601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3106, 4, 0, 1, "我がショーグンは\n永遠にフメツデース\nいざ出陣デース！", 1, 109601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3107, 5, 0, 1, "壁に耳あり\n障子に…ん？\nメアリーって誰デス？", 1, 109601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3108, 101, 0, 1, "ショーグンがいれば\nワタシはどんな\n忍法も使いこなせマス", 4, 109601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3109, 102, 0, 1, "不届き者はどこで\nゴザルデス？　忍法で\n退治してくれヨウ！", 8, 109601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3110, 1, 0, 2, "ショーグン！　新しい\n忍法を覚えたデス！\n見てほしいデース！", 1, 109601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3111, 2, 0, 2, "レッツ手裏剣道デス！\n投げてソウロウで\nゴザルデース！", 1, 109601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3112, 3, 0, 2, "ふりかかる火の粉は\nワタシが払うデス！\nバンバン！", 1, 109601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3113, 4, 0, 2, "とっておきの忍法が\n吹き荒れるデス！\nよそ見はダメデスヨ？", 1, 109601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3114, 101, 0, 2, "ショーグンは\n何階建てのお城を\n建てたいデスか？", 4, 109601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3115, 102, 0, 2, "テンシュカクから\n平和な城下町を\n眺めてみたいデス…", 8, 109601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3116, 1, 0, 2, "おはようございマス！\n本日はオヒガラモヨク\n良い日になりそうデス", 1, 109601, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3117, 2, 0, 2, "お腹すいたデース…\nショーグンもご飯に\nしまショウ！", 1, 109601, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3118, 3, 0, 2, "闇討ちに遭わぬよう\n夜は気を付けて\n歩くデスよー？", 1, 109601, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3120, 2, 0, 3, "天下分け目の\n戦いに備えて…\nマッタリデース", 1, 109601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3121, 3, 0, 3, "口寄せの術で\n召喚したいくらい\nかわいいデース", 1, 109601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3122, 4, 0, 3, "いい匂いデ～ス\n香り玉の開発の\n参考になりそうデスね", 1, 109601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3123, 5, 0, 3, "ワタシの懐よりも\nゾーリが暖まりマス…\nライバル出現デス…！", 1, 109601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3125, 7, 0, 3, "腹が減っては\n戦はできぬデース！\n天下統一に向けて\n腹ごしらえデス！", 1, 109601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3126, 8, 0, 3, "ムム、暗号文デス！？\n解読には時間が\nかかりそうデース…", 1, 109601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3128, 1, 0, 1, "掃除も終わりましたし\nそろそろ、食事の\n準備をしなくては", 1, 109701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3129, 2, 0, 1, "異世界の演劇…\n許されるならば、\n観てみたいです", 1, 109701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3130, 3, 0, 1, "こちらの世界には、\nマヨネーズが普通に\n存在するのですね…", 1, 109701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3131, 4, 0, 1, "こちらの世界は食材も\n面白い物が多いですね\nなにかレシピを\n考えてみましょうか", 1, 109701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3132, 5, 0, 1, "みょんみょんみょん…\n魔法の練習は\nこれぐらいですね", 1, 109701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3133, 101, 0, 1, "倒木の撤去で思わぬ\n収入となりました\nサレン様に\nお渡ししておかないと", 4, 109701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3134, 102, 0, 1, "料理を美味しく\n召し上がって頂けると\nメイド冥利につきます", 8, 109701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3135, 1, 0, 2, "お出かけでしたら、\nレムも同行\nさせてくださいね", 1, 109701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3136, 2, 0, 2, "レムにできること\nでしたら、なんでも\nお申し付けください", 1, 109701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3137, 3, 0, 2, "じーー。いえ、\nその髪の長さが少し、\nとても少し、かなり\n少し気になったもので", 1, 109701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3138, 4, 0, 2, "鉄球ですか？\nあれは護身用ですので\n問題はありませんよ？", 1, 109701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3139, 101, 0, 2, "計算の勉強は\n進んでおりますか？\nレムでよろしければ、\nいつでもお教えします", 4, 109701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3140, 102, 0, 2, "次はもっと、大きな\n魔物料理に挑戦します\nワイバーンの姿焼き\nなんてどうでしょうか", 8, 109701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3141, 1, 0, 2, "おはようございます\n朝食の準備が\nできておりますので、\n食卓におこしください", 1, 109701, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3142, 2, 0, 2, "昼食の時間ですね\n何か食べたいものは\nありますか？", 1, 109701, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3143, 3, 0, 2, "寝ないのか、ですか？\nメイドの就寝は、\n屋敷で最後と\n決まっておりますので", 1, 109701, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3145, 2, 0, 3, "ふんわり…\nこのふんわりさは、\n鬼をも黙らせます…", 1, 109701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3146, 3, 0, 3, "ふふっ…異世界でも\nかわいいものは\n共通なのですね", 1, 109701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3147, 4, 0, 3, "よい香りですね\n魔女教の悪臭とは\n大違いです", 1, 109701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3148, 5, 0, 3, "ぬくぬくです…はっ！\nこれはいけません！\n人を…いえ、\n鬼をだめにします！", 1, 109701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3150, 7, 0, 3, "はむはむ…美味しい！\nこの味を再現できたら\n褒めてもらえる\nかもしれません…！", 1, 109701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3151, 8, 0, 3, "絵がいっぱい…\n異世界の本は\nとても面白いですね", 1, 109701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3153, 1, 0, 1, "異世界であろうと、\nラムのやるべきことは\n変わらないわ\nまずは休憩するわよ", 1, 109801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3154, 2, 0, 1, "マヨネーズがある\nということは、\nバルスの故郷が近い\nのかもしれないわね", 1, 109801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3155, 3, 0, 1, "この世界では、バルス\nの言う言葉が使われる\nときがあるのね\n忌々しいかぎりだわ", 1, 109801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3156, 4, 0, 1, "ラムたち以外にも\nメイドが居るのね\nラムが…いえ、\n仕事を押しつけるわ", 1, 109801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3157, 5, 0, 1, "…千里眼で視るかぎり\n周囲に異変はないわね\n危険がないなら、\nあとはレムに任せるわ", 1, 109801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3158, 101, 0, 1, "…ラムもまた、\n「お姉ちゃん」って\n呼ばれる日が\nくるのかしらね", 4, 109801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3159, 102, 0, 1, "ハッ！　ラムが作る\n蒸かし芋は完璧ね\n…気が向いたら、また\n作ってやろうかしら", 8, 109801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3160, 1, 0, 2, "ラムに構う暇が\nあるなら働きなさい\nそうすれば、\nラムが楽をできるわ", 1, 109801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3161, 2, 0, 2, "ラムに何かを\n期待しても無駄よ\nラムに命令できるのは\nロズワール様だけだわ", 1, 109801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3162, 3, 0, 2, "暇なら買い物に\n付き合いなさい\n荷物を\n持たせてあげるわ", 1, 109801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3163, 4, 0, 2, "ラムになんの用？\nふざけた内容なら\nねじ切るわよ", 1, 109801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3164, 101, 0, 2, "そういえば、あなたは\n記憶喪失だったわね…\n思い出す努力をなさい\n…家族が悲しむわよ", 4, 109801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3165, 102, 0, 2, "蒸かし芋が欲しい？\n随分と厚かましいわね\n…まだ残りがあるから\n好きに食べなさい", 8, 109801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3166, 1, 0, 2, "だらしない顔ね\n顔を洗って\nしゃんとなさい", 1, 109801, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3167, 2, 0, 2, "お腹が空く時間ね\nラムの昼食を\n用意させてあげるわ", 1, 109801, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3168, 3, 0, 2, "メイドが寝るのは\n屋敷で最後…\nラムが寝るために、\nさっさと寝なさい", 1, 109801, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3170, 2, 0, 3, "…ここから動かない\nという、誓いを\n立てたくなるわね", 1, 109801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3171, 3, 0, 3, "これは…かわいいわね\nもちろん、\nラムほどではないけど", 1, 109801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3172, 4, 0, 3, "…香炉かしら？\nこちらの世界にも\n質のよい物があるのね", 1, 109801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3173, 5, 0, 3, "見つけてしまったの\nかもしれないわ…\nラムの楽園を！", 1, 109801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3175, 7, 0, 3, "…はむっ\nラムの蒸かし芋ほど\nではないけど、\nなかなかいけるわね", 1, 109801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3176, 8, 0, 3, "異世界ならではの\n面白い内容もあるわね\nとても興味深いわ", 1, 109801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3178, 1, 0, 1, "ねえパック、\nにゃんにゃか\nにゃかにゃんしよっか", 1, 109901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3179, 2, 0, 1, "時間もあるし、\nこっちの世界の石で\n石積みしようかな", 1, 109901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3180, 3, 0, 1, "私もお姉さんだし、\nサレンさんを\n見習わないと…うん！\n私、すごーく頑張る！", 1, 109901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3181, 4, 0, 1, "この世界は、銀髪で\nハーフエルフでも受け\n入れてくれるのね…\nなんだか嬉しいな", 1, 109901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3182, 5, 0, 1, "今日のご飯…\nピーマルは\n入ってないわよね…？", 1, 109901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3183, 101, 0, 1, "また、みんなで\n一緒に買い物に\n行きたいな", 4, 109901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3184, 102, 0, 1, "パック以外から\n家族って呼ばれたの\n初めて…ふふっ♪\nちょっと嬉しいかも！", 8, 109901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3185, 1, 0, 2, "今からパックの\n毛繕いをするけど…\n一緒にする？", 1, 109901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3186, 2, 0, 2, "私も何かお手伝いを…\nあ、そうだ！\n料理とかどうかな！", 1, 109901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3187, 3, 0, 2, "ねえ、ここで\n歌の練習って、\nしてもいいかな？", 1, 109901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3188, 4, 0, 2, "…あ、ごめんなさい！\n話しかけられるのって\nあまり慣れてなくて…", 1, 109901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3189, 101, 0, 2, "すごーく頑張るから、\nこれからも勉強を\n手伝ってね", 4, 109901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3190, 102, 0, 2, "せーの、びくとりー！\nふふっ…また、一緒に\nラジーオタイソー\nしようね♪", 8, 109901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3191, 1, 0, 2, "ちゃんと起きてる？\nふふっ…よかった。\nあ、べ、別に心配した\nわけじゃないからね？", 1, 109901, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3192, 2, 0, 2, "お昼ご飯が楽しみね♪\nあ、こっちの世界って\nまよねーずはあるの？", 1, 109901, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3193, 3, 0, 2, "そろそろ寝る時間よ。\n夜更かししたら、\nめっ！　だからね！", 1, 109901, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3195, 2, 0, 3, "勉強も終わったし…\nちょっ、ちょっとだけ\n休んでもいいよね…？", 1, 109901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3196, 3, 0, 3, "か、かわいい…！\nパックのかわいさに\n匹敵するかも…！", 1, 109901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3197, 4, 0, 3, "良い香り…\nロズワールの屋敷にも\nこういうのって\nあるのかな？", 1, 109901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3198, 5, 0, 3, "あったかい…\nずっとここに…はっ！\nダメダメ！　王選の\nために勉強しないと！", 1, 109901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3200, 7, 0, 3, "あ…これ美味しい！\nこっちの世界は色んな\n食べ物があるのね。", 1, 109901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3201, 8, 0, 3, "知らないことが\nいっぱい書いてある…\n異世界の本って、\n面白いのね", 1, 109901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3203, 1, 0, 1, "海ってマジ最高！\nビーチの撮影も\n超楽しいしー♪", 1, 110001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3204, 2, 0, 1, "20パーって何パ？\n今度ヒデサイに\n聞いてみよっ！", 1, 110001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3205, 3, 0, 1, "夏の思い出を\nヒデサイと一緒に\n作りたいな～", 1, 110001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3206, 4, 0, 1, "テンションあがる\n水着ゲットして\n超ラッキーだよ～♪", 1, 110001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3207, 5, 0, 1, "休憩が終わったら\n撮影にもどらないと！\n気合入れてこ♪", 1, 110001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3208, 101, 0, 1, "ヒデサイとビーチで\nパーティーしたいな～\n超楽しそうだよね！", 4, 110001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3209, 102, 0, 1, "ヒデサイといると\n楽しくてうれしくて…\nカレシってこんな感じ\nなのかなぁ？", 8, 110001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3210, 1, 0, 2, "見て見て～！\n夏らしいネイルに\nしてみたんだ～♪", 1, 110001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3211, 2, 0, 2, "ヒデサイ～\nこの水着どうかな？\nイケてるっしょ♪", 1, 110001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3212, 3, 0, 2, "水着姿の写真？\nもちいいよ！\nかわいく撮ってね！", 1, 110001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3213, 4, 0, 2, "うちだって\n夏の海みたいに\nキラキラしちゃうよ！", 1, 110001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3214, 101, 0, 2, "ヒデサイとうちの\nフィーリングって\nめっちゃエモいよね♪", 4, 110001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3215, 102, 0, 2, "ねーねーヒデサイ！\n一緒に泳ぎにいこっ！\nほら、手繋いで♪", 8, 110001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3216, 1, 0, 2, "水着オッケー！\nネイルもばっちり！\n朝から遊んじゃおー♪", 1, 110001, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3217, 2, 0, 2, "お昼食べたら\n昼寝して\nリフレッシュしよー♪", 1, 110001, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3218, 3, 0, 2, "水着ってマジで\n最強かも…このまま\nお風呂にも入れるし♪", 1, 110001, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3220, 2, 0, 3, "マジ気持ちいいー♪\nおに癒やされるん\nですけど～！", 1, 110001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3221, 3, 0, 3, "やばばばばー！\nかわいすぎだってば！\nマジらぶだよ～♪", 1, 110001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3222, 4, 0, 3, "あ、この香り超好き！\nモデル仲間に\n教えたげよ～♪", 1, 110001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3223, 5, 0, 3, "あったか～い♪\n夏の太陽みたい！", 1, 110001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3225, 7, 0, 3, "マジ美味しいねこれ♪\nビーチで\n流行っちゃうかも！", 1, 110001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3226, 8, 0, 3, "夏のために\n最新ファッションを\n勉強ちう！", 1, 110001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3228, 10, 0, 3, "なにコレもにカワ～♪\nなんて生き物？\nヒデサイ知ってる？", 1, 110001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3229, 11, 0, 3, "うわっ超フカフカ～\nなんかうち眠く\nなってきちゃった…", 1, 110001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3230, 1, 0, 1, "海の家でのんびり\n過ごしましょう\n熱中症には注意してね", 1, 110101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3231, 2, 0, 1, "はぁ、どうやったら\n泳げるように\nなれるのかしら～", 1, 110101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3232, 3, 0, 1, "海で日焼け止めを\n塗らないと、あとで\n大変なことになるわよ", 1, 110101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3233, 4, 0, 1, "久しぶりに水着を\n着たんだけど\n変じゃなかったかな…", 1, 110101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3234, 5, 0, 1, "波の音って落ち着くわ\n授業中に聞いたら\nみんな寝ちゃうかも？", 1, 110101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3235, 101, 0, 1, "海が楽しいって\n思えたのも\nあの子のおかげね", 4, 110101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3236, 102, 0, 1, "あの子と二人で海に\n来られてよかったわ\n一日があっという間ね", 8, 110101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3237, 1, 0, 2, "男の子と海だなんて\n少女漫画みたいで\n憧れてたの～", 1, 110101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3238, 2, 0, 2, "夏休みの宿題は\nちゃんとやった？\n遊んでばかりではダメ", 1, 110101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3239, 3, 0, 2, "昼の海と夜の海\nどっちが好き？　私は\n君といる海が一番よ", 1, 110101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3240, 4, 0, 2, "ふう、疲れたわ～\n冷たいジュースで\n元気回復ね！", 1, 110101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3241, 101, 0, 2, "海で見た君の背中\nすごく大きく見えたわ\nやっぱり男の子なのね", 4, 110101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3242, 102, 0, 2, "君と一緒に見た\n夜の海…\n忘れられそうにないわ", 8, 110101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3243, 1, 0, 2, "準備ができたら\n海に出かけようよ\n今日も楽しみましょう", 1, 110101, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3244, 2, 0, 2, "あら、もうお昼？\n海の家でランチに\nしましょ♪", 1, 110101, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3245, 3, 0, 2, "夜の海岸についてきて\nくれる？　暗がりは\n一人じゃ怖くて…", 1, 110101, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3247, 2, 0, 3, "泳ぐ練習をしたら\n疲れちゃったわ～\nちょっと一休み…", 1, 110101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3248, 3, 0, 3, "かわいいわね～\n浮き輪に乗せて\n海に浮かべたいわ～", 1, 110101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3249, 4, 0, 3, "この匂い…\nなんだか夏の海を\n思い出すわ～", 1, 110101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3250, 5, 0, 3, "夜の海風で冷えない\nように、しっかりと\n身体を暖めましょうね", 1, 110101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3252, 7, 0, 3, "海で遊ぶ前に\nしっかり食べて\n体力つけましょう", 1, 110101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3253, 8, 0, 3, "夏休み中でも\nしっかりお勉強\nしないと！", 1, 110101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3255, 10, 0, 3, "この子に乗れば\n海の上でも\nすいすい進めるかしら", 1, 110101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3256, 11, 0, 3, "ビーナスの誕生！\n…なんちゃって\nはしゃぎすぎかしら？", 1, 110101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3257, 1, 0, 1, "夏の海には商機が\nあるはず…\nのがす手はないわね♪", 1, 110301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3258, 2, 0, 1, "あたしが作った\n日焼け止め…\nこれは売れそうね！", 1, 110301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3259, 3, 0, 1, "子どもたちのために\n浮き輪を用意して\nおかないと…", 1, 110301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3260, 4, 0, 1, "海っていいわよね！\nみんなで羽を伸ばしに\n行きたいわ！", 1, 110301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3261, 5, 0, 1, "波の音を聞いて\nのんびりするのも\n悪くないわね…", 1, 110301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3262, 101, 0, 1, "ただの砂遊びも\nあんたと一緒なら\n楽しいから不思議ね…", 4, 110301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3263, 102, 0, 1, "あんたがあたしの\nなんであれ、大切な\n存在ではあるのよね…", 8, 110301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3264, 1, 0, 2, "夏の海って\nすごい人よね…\n迷子にならないでよ？", 1, 110301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3265, 2, 0, 2, "ぼんやり海に浮かんで\n波に揺られるのも\n悪くないわよ？", 1, 110301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3266, 3, 0, 2, "ビーチで子どもたちと\n一緒に砂のお城を\n作りたいわ♪", 1, 110301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3267, 4, 0, 2, "海で食べる\n焼きそばパン…\nいいかもしれないわね", 1, 110301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3268, 101, 0, 2, "あたしの水着\n似合ってる？\nそう、ありがとう♪", 4, 110301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3269, 102, 0, 2, "あんたが居ると\nうれしい…って、\n子どもたちが\n言ってたわ！", 8, 110301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3270, 1, 0, 2, "朝の浜辺って\nいいわよね\n散歩しに行きたいわ", 1, 110301, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3271, 2, 0, 2, "海の家のご飯って\nなんであんなに\nおいしいのかしら♪", 1, 110301, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3272, 3, 0, 2, "夜の海って、\nロマンチックよね。\nずっと眺めてたいわ…", 1, 110301, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3274, 2, 0, 3, "やわらかくて\n気持ちいい…\nこのまま寝ちゃいそう", 1, 110301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3275, 3, 0, 3, "海に一緒に入れる\nぬいぐるみとか\nないかしら？", 1, 110301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3276, 4, 0, 3, "夏らしい\nいい香りね…\nなんの香りかしら？", 1, 110301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3277, 5, 0, 3, "あったかい…\n濡れた体を乾かすには\nちょうどいいわね♪", 1, 110301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3279, 7, 0, 3, "泳いだ後の食事は\n普段の何倍も\nおいしく感じるわね！", 1, 110301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3280, 8, 0, 3, "海に行く前にいろいろ\n調べなきゃね。備え\nあれば憂いなしよ！", 1, 110301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3282, 10, 0, 3, "海の守り神？\nずいぶんかわいらしい\n神様ね♪", 1, 110301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3283, 11, 0, 3, "このぐらい\n大きな貝があったら\n食費も浮くかしら？", 1, 110301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3284, 1, 0, 1, "浜辺で食うものって\nなんであんなに\nうまいのかな～", 1, 110401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3285, 2, 0, 1, "いてて…\nちょっと日焼け\nしすぎたかな", 1, 110401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3286, 3, 0, 1, "砂浜で走ると\nいいトレーニングに\nなるんだぜ", 1, 110401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3287, 4, 0, 1, "夏ならではの\n料理…か\n何がいいかな…", 1, 110401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3288, 5, 0, 1, "やっぱりここは\n涼しくて快適だな", 1, 110401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3289, 101, 0, 1, "泳ぎの練習だったら\nいつでも付き合って\nやるからな！", 4, 110401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3290, 102, 0, 1, "水着で部屋の中に\nいるのって\n変な感じだな…", 8, 110401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3291, 1, 0, 2, "うおっ！\nきゅ、急に\n触るなって…\nびっくりした", 1, 110401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3292, 2, 0, 2, "え、砂がついてた？\n払ってくれたのか\nありがとよ", 1, 110401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3293, 3, 0, 2, "夏バテしてないか？\nここで休んでけよ", 1, 110401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3294, 4, 0, 2, "…そう見られると\n照れるっていうか…", 1, 110401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3295, 101, 0, 2, "カキ氷とか\n食いたくないか？\n氷、削ってやるよ", 4, 110401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3296, 102, 0, 2, "おまえと\n夏の思い出ができて\nよかったよ", 8, 110401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3297, 1, 0, 2, "ふう…\n朝一番で泳ぐの\nやっぱ気持ちいいな", 1, 110401, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3298, 2, 0, 2, "一番あちぃ時間だ\n水分補給は\nこまめにな！", 1, 110401, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3299, 3, 0, 2, "今日もお疲れ\n明日のパトロールも\nよろしくな", 1, 110401, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3301, 2, 0, 3, "泳ぎ疲れたからな\n少し休憩だ…", 1, 110401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3302, 3, 0, 3, "か、かわいい…\nいや、なんでもねぇ！", 1, 110401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3303, 4, 0, 3, "この香り落ち着くな\nこういうのも\n悪くねーかも…", 1, 110401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3304, 5, 0, 3, "あったけえ…！\n泳ぎ終わった後って\nけっこう体冷えてよ…", 1, 110401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3306, 7, 0, 3, "海で食べると\nまた一味違うよな", 1, 110401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3307, 8, 0, 3, "夏のサバイバル特集…\nちょっと読んでみるか", 1, 110401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3309, 10, 0, 3, "へー！　こいつ、\nよくできてんな！\nいいなあ、実物も\n乗れたらいいのに", 1, 110401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3310, 11, 0, 3, "か、かわ…！\nいや、じゃなくて\nあ、あたしがここに\n寝ていいのか…？", 1, 110401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3311, 1, 0, 1, "故郷の花を飾っても\nいいかな～？\n部屋が楽しくなるよ～", 1, 110501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3312, 2, 0, 1, "水着で歩いてると\n泳ぎたくなるさ～\n我慢できないさ～！", 1, 110501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3313, 3, 0, 1, "夏が眩しいのは\nてぃーだがにこにこ\n笑ってるからさ～！", 1, 110501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3314, 4, 0, 1, "泳ぎ疲れたら\n涼しい木陰で\nお昼寝するさ～", 1, 110501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3315, 5, 0, 1, "クロールも\n犬かきも\n大得意さ～！", 1, 110501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3316, 101, 0, 1, "なんだか歌いたい\n気分さ～\nキミもどう～？", 4, 110501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3317, 102, 0, 1, "海の近くに住んで\nキミと歌って踊って\nそれが理想さ～！", 8, 110501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3318, 1, 0, 2, "私とキミ\nどっちがたくさん\n泳げるか\n競争するさ～！", 1, 110501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3319, 2, 0, 2, "キミも三線に興味が\nあるのかな～？\n私が教えるさ～！", 1, 110501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3320, 3, 0, 2, "嫌なことがあっても\n私と歌って踊れば\nなんくるないさー！", 1, 110501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3321, 4, 0, 2, "たまには時間を忘れて\n私とのーんびり\n海を眺めるさ～", 1, 110501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3322, 101, 0, 2, "ぶるぶるぶるっ！\n毛が濡れちゃっても\nへっちゃらさ～！", 4, 110501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3323, 102, 0, 2, "泳ぐ前は準備体操～！\n背中押してあげるから\nしっかりやるさ～！", 8, 110501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3324, 1, 0, 2, "朝の浜辺は\nちゅらさんよ～\n一緒に歩こうよー！", 1, 110501, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3325, 2, 0, 2, "お昼ご飯は\n決まったかな～？\n私はタコライスさ～！", 1, 110501, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3326, 3, 0, 2, "星と波音の中で\n三線鳴らすのも\nオツなものさ～", 1, 110501, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3328, 2, 0, 3, "ちょうどよかったさー\nゆっくり休んだら\nまた泳ぎに行くさ～！", 1, 110501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3329, 3, 0, 3, "砂浜に飾って\nおきたいさ～\nきっとかわいいさ～！", 1, 110501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3330, 4, 0, 3, "くんくん…はあ\n癒されるさ…\n故郷の夏の匂いさ～", 1, 110501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3331, 5, 0, 3, "おおー…！\n濡れた髪が\nみるみる乾いてくさ～", 1, 110501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3333, 7, 0, 3, "ん～！　これ、\n海で食べたら\nもっとおいしいさ～", 1, 110501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3334, 8, 0, 3, "海の生物図鑑ね～\nこの前みかけた魚も\n載ってるかな～？", 1, 110501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3336, 10, 0, 3, "わぁ～！　みてみて！\n背中に乗れるさー！\nキミと一緒に\n乗りたいさ～！", 1, 110501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3337, 11, 0, 3, "ふかふかさ～\n海の底の\n人魚になった気分さ～", 1, 110501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3338, 103, 0, 1, "あの人が\nわたしの騎士だったら\nいいのにな…", 12, 105801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3339, 103, 0, 2, "あなたのおかげで\nわたしの心はいつも\nお腹いっぱいです♪", 12, 105801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3340, 103, 0, 1, "許されるのならば、\n今すぐにでも、\n主さまのおそばに\n行きたいです…", 12, 105901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3341, 103, 0, 2, "主さま…\nお慕いしております…\nいつまでも、おそばに\n居させてください…", 12, 105901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3342, 103, 0, 1, "なんか最近、\nあいつのことしか\n考えてない…\nあたし…どうしよう…", 12, 106001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3343, 103, 0, 2, "そ、外に行くなら、\n連れていきなさいよね\nその…あたしが\n守ってあげるから…", 12, 106001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3344, 1, 0, 1, "部屋の中は涼しくて\n気持ちええどすなぁ\n天国やわぁ", 1, 110601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3345, 2, 0, 1, "クラゲはんって\nフリルいっぱいの\nドレスみたいやねぇ", 1, 110601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3346, 3, 0, 1, "外でセミはんが\n元気に鳴いてはるねぇ\n今日もええ天気やわぁ", 1, 110601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3347, 4, 0, 1, "こないに暑いと\n海に入って\n涼みたくなるわぁ", 1, 110601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3348, 5, 0, 1, "いつか\nマホマホ王国にも\n海の家作りまひょ♪", 1, 110601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3349, 101, 0, 1, "うちも\n付き合いますえ\n泳ぎの練習しまひょ♪", 4, 110601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3350, 102, 0, 1, "うちの里帰り\n王子はんも一緒に\n来まへんか？", 8, 110601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3351, 1, 0, 2, "王子はんは日焼けより\n色白美人の方が\n好きでっしゃろか？", 1, 110601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3352, 2, 0, 2, "うちと一緒に\n浮き輪で海、\nプカプカしまへん？", 1, 110601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3353, 3, 0, 2, "日が落ちたら\n線香花火\nやりたいわぁ♪", 1, 110601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3354, 4, 0, 2, "木陰でのんびり\nお昼寝…\n最高やね～", 1, 110601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3355, 101, 0, 2, "お祭ではぐれても\n王子はんなら見つけて\nくれはります？", 4, 110601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3356, 102, 0, 2, "楽しい夏やわぁ\nずっとこんな日が\nつづくとええね", 8, 110601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3357, 1, 0, 2, "うちにしては早起き？\n暑くて目が早う\n覚めてもうたんどす", 1, 110601, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3358, 2, 0, 2, "お昼は冷たい\nおそうめんとか\nどうでっしゃろ", 1, 110601, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3359, 3, 0, 2, "夜は星がきれいやね\n二人で浜辺でも\n行きまへんか？", 1, 110601, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3361, 2, 0, 3, "うーん、遊び疲れた\n身体にちょうどええ\nほっこり具合やわぁ", 1, 110601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3362, 3, 0, 3, "かいらしい子は\nみーんな\nマホマホ王国の\n住人どすえ♪", 1, 110601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3363, 4, 0, 3, "この香り…\n南国にいる気分やねぇ\n癒されるわぁ～", 1, 110601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3364, 5, 0, 3, "これで\n冷えた身体も\nポカポカやねぇ", 1, 110601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3366, 7, 0, 3, "外はまだ暑いやろうし\nバテんよう\nしっかり食べんとね", 1, 110601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3367, 8, 0, 3, "海の中にもお城が\nあるんやねぇ\n行ってみたいわぁ", 1, 110601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3369, 10, 0, 3, "この子、ふかふかやね\nこのままマホマホ王国\n乗せてってくれはる？", 1, 110601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3370, 11, 0, 3, "人魚姫になっても\n最後は王子はんと\nハッピーエンド\nどすえ～", 1, 110601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3371, 1, 0, 1, "真似したくなる\n人がここには\n大勢いますね", 1, 107001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3372, 2, 0, 1, "偽物を一人\n紛れこませました\n…冗談です", 1, 107001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3373, 3, 0, 1, "動物に変身して\nその辺りを\n散歩してきます", 1, 107001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3374, 4, 0, 1, "失った記憶は\nどの程度回復するの\nでしょうね", 1, 107001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3375, 5, 0, 1, "人や物だけでなく\nいずれは世界すら\n真似てみせます", 1, 107001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3376, 101, 0, 1, "次は彼に\nどんなことを\n教えましょうか…", 4, 107001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3377, 102, 0, 1, "知識はともかく\n私の力はまだ\n必要なようですね", 8, 107001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3378, 1, 0, 2, "どうしました？\n…何でもない？\n…そうですか", 1, 107001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3379, 2, 0, 2, "退屈しています\n芸でもして\n楽しませなさい", 1, 107001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3380, 3, 0, 2, "研究のためです\n少し話し相手に\nなりなさい", 1, 107001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3381, 4, 0, 2, "何か真似して欲しい？\n私の能力を何だと\n思っているのですか", 1, 107001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3382, 101, 0, 2, "貴方にはまだ色々と\n教えるべきことが\nありますね", 4, 107001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3383, 102, 0, 2, "随分多くの女性と\n知り合いなのですね\n驚きました", 8, 107001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3384, 1, 0, 2, "…\nおはようございます\n…まだ朝ですよ", 1, 107001, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3385, 2, 0, 2, "食事にします\n…どうしました？\n早く持ってきなさい", 1, 107001, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3386, 3, 0, 2, "早く寝なさい\n夜更かしすると\n背が伸びませんよ？", 1, 107001, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3388, 2, 0, 3, "マサキより\n座り心地は\nいいですね", 1, 107001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3389, 3, 0, 3, "意外に複雑な\n構造ですね", 1, 107001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3390, 4, 0, 3, "真似るなら\n香りまで\n再現しなくては", 1, 107001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3391, 5, 0, 3, "発火現象の\n模倣…なかなか\n難しいものです", 1, 107001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3393, 7, 0, 3, "偽物や分身を作るには\nエネルギーが\n必要不可欠なのです", 1, 107001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3394, 8, 0, 3, "暇つぶしには\nちょうどよさそうです\n…", 1, 107001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3396, 1, 0, 1, "学院かぁ…\n友達の作り方の\n授業はないかな？", 1, 110701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3397, 2, 0, 1, "学院でぼっちに\nなったときのために、\n避難場所を\n探しておかないと…", 1, 110701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3398, 3, 0, 1, "ぼっちの私が学院…\nな、なんだか\n目まいがしてきました", 1, 110701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3399, 4, 0, 1, "クラスメイトに\n話しかける練習…\nむ、難しすぎます…！", 1, 110701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3400, 5, 0, 1, "目指せ友達100人…\n無理かな…10人…\nや、やっぱり一人！", 1, 110701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3401, 101, 0, 1, "あなたと学院生活が\n送れるなんて、\nとっても\nうれしいです！", 4, 110701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3402, 102, 0, 1, "えへへ…\n学院でも、\nあなたと一緒に\n居られてうれしいな♪", 8, 110701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3403, 1, 0, 2, "が、学院ってあんなに\n人が居るんですね…\n友達作りのイメトレを\nやり直さないと！", 1, 110701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3404, 2, 0, 2, "私にとって\n部活動とはＢＢ団！\n今日もたくさん\n活動しましょうね！", 1, 110701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3405, 3, 0, 2, "休み時間は寝たふり…\nご飯はおトイレ…\nぼっち対策は完璧です", 1, 110701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3406, 4, 0, 2, "学院で友達を作る\n勉強は完璧ですよ！\n購買部でパンを買って\n渡すんですよね！", 1, 110701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3407, 101, 0, 2, "学院でも、\nＢＢ団の活動を\nしていきましょうね♪", 4, 110701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3408, 102, 0, 2, "ぼっちに学院生活は\n厳しいですね…でも、\nあなたと一緒なら\n乗りこえられそうです", 8, 110701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3409, 1, 0, 2, "もうこんな時間…\nあの…一緒に学院に\n行ってくれませんか？", 1, 110701, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3410, 2, 0, 2, "お昼ですね！\n言ってくれれば、\nパンでもお握りでも\n買ってきますよ！", 1, 110701, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3411, 3, 0, 2, "今日もぼっち…\nいつか友達と一緒に、\nパジャマパーティー\nしたいなぁ…", 1, 110701, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3413, 2, 0, 3, "森とは違う\n心地良さが…このまま\n眠っちゃいそうです…", 1, 110701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3414, 3, 0, 3, "かわいい…この子を、\n話す練習の相手役に\nしてみようかな？", 1, 110701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3415, 4, 0, 3, "いい香り…\nいつ友達ができても\nいいように、私の\n部屋にも置こうかな", 1, 110701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3416, 5, 0, 3, "こ、これは…\nぼっちで\n日陰者の私には\n暖かすぎます！", 1, 110701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3418, 7, 0, 3, "おいしい…！\nお弁当にして、学院に\n持っていけないかな？", 1, 110701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3419, 8, 0, 3, "学院の授業に\nついていくためにも、\n勉強しておこうかな…", 1, 110701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3421, 1, 0, 1, "あんた、何してんの。\nいいけど、なんでも", 1, 110801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3422, 2, 0, 1, "邪魔。どいて。\nいや怒ってないし。\nコレがフツーだから", 1, 110801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3423, 3, 0, 1, "いいけど、\n散らかしめの部屋だと\n片付けたくなるよね…", 1, 110801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3424, 4, 0, 1, "暇なときは明日の\nコーデ考えてるよ。\n考えるのはタダだし", 1, 110801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3425, 5, 0, 1, "昔よくシオリン\n構いすぎてハツネに\n文句言われたなァ…", 1, 110801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3426, 101, 0, 1, "なんかさ。あんたって\n謎に女子トモ多いよね\n…いいけど、べつに", 4, 110801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3427, 102, 0, 1, "ねえ。あのさあ。\nあんた弁当で好きな\n具とかってあんの", 8, 110801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3428, 1, 0, 2, "あ？　何。なんか用。\nや、ないならないで\nいいけど、べつに", 1, 110801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3429, 2, 0, 2, "ごめ、ちょっと\n黙っててくんない。\n明日の試験ヤバだから", 1, 110801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3430, 3, 0, 2, "歩き方が静か？\n癖になってるんだよね\n足音を殺すの", 1, 110801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3431, 4, 0, 2, "うちの血液型？\n大雑把のОだけど。\nなんか文句あんの", 1, 110801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3432, 101, 0, 2, "ひゃんっ！\nや、だからさ…急に\n話しかけんなっての", 4, 110801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3433, 102, 0, 2, "な、何…あんまジッと\n見ないでくんない…\nなんか、その、ハズい", 8, 110801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3434, 1, 0, 2, "早起きは得意な方だと\n思うけど。昔から\nそれがデフォだったし", 1, 110801, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3435, 2, 0, 2, "こんな時間か…\n昼ごはん\nサクッと作っちゃお", 1, 110801, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3436, 3, 0, 2, "あ？　夜遊び？\nはッ…そんなん\n５歳で卒業したっての", 1, 110801, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3438, 2, 0, 3, "あーヤバじゃんコレ…\nふかついてる場合\nじゃないっつーのに…", 1, 110801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3439, 3, 0, 3, "なんだこれ\nかわいいかよ", 1, 110801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3440, 4, 0, 3, "ふーん。\nこの匂い…\nなんかいいじゃん", 1, 110801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3441, 5, 0, 3, "暖炉はアドじゃん。\n煮物、焼き物、蒸し物\nいろいろ作れそう", 1, 110801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3443, 7, 0, 3, "何コレ\n食べ物困んないじゃん\nうちにも一台くんない", 1, 110801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3444, 8, 0, 3, "本かァー…んー\n学校の勉強だけで\nいいかなァ…", 1, 110801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3446, 1, 0, 1, "イタズラってなにを\nしたらいいのかな？", 1, 111101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3447, 2, 0, 1, "カップケーキ\n上手に作れました\n食べてくれるかな…", 1, 111101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3448, 3, 0, 1, "あの人、\nお菓子くれるかな？\nくれなかったら…ふふ", 1, 111101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3449, 4, 0, 1, "オバケ…いないよね？\n怖くなんてないけど！", 1, 111101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3450, 5, 0, 1, "ハロウィンは\n楽しいけどオバケの\nお祭りなんだよね…", 1, 111101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3451, 101, 0, 1, "にゃ～、にゃお～！\nちゃんと怖いかな？\n早く帰ってこないかな", 4, 111101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3452, 102, 0, 1, "あの人のことだから\nお菓子欲しがるよね…\n準備はバッチリです♪", 8, 111101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3453, 1, 0, 2, "えへへ。猫さんのお耳\nよく出来てるでしょ？", 1, 111101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3454, 2, 0, 2, "触りましたね？\nイタズラしましたね？\nへんた～い！", 1, 111101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3455, 3, 0, 2, "フー！　頭を撫でたら\n怒りますよ！　あれ？\n猫みたいでした？", 1, 111101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3456, 4, 0, 2, "きゃっ！　も、もう、\n脅かさないでください\n怖くありませんけど！", 1, 111101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3457, 101, 0, 2, "お菓子ですか？\nありがとうございます\nハッピーハロウィン♪", 4, 111101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3458, 102, 0, 2, "ゴロゴロ…\nね、猫さんの真似です\n…似てましたか？", 8, 111101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3459, 1, 0, 2, "さぁ、お菓子作りを\n始めますよ。下準備は\n大事ですからね！", 1, 111101, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3460, 2, 0, 2, "今、つまみ食い\nしましたね！？　あっ\nおやつの時間でしたか", 1, 111101, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3461, 3, 0, 2, "さぁ、ハロウィンの\n始まりです…ふぁ…\nね、眠くありません！", 1, 111101, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3463, 2, 0, 3, "ちょっと疲れたかも…\nパーティの時間まで\nひと休みしなきゃ", 1, 111101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3464, 3, 0, 3, "私も今、こんな感じ\nなのかな？　こんなに\nかわいいといいなぁ", 1, 111101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3465, 4, 0, 3, "甘い匂いがいっぱい…\nお菓子の香りかな？", 1, 111101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3466, 5, 0, 3, "温かくなると、\n丸まりたくなって…\n…猫になっちゃう！？", 1, 111101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3468, 7, 0, 3, "ニンジンもこれくらい\nおいしければ\nいいのに…", 1, 111101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3469, 8, 0, 3, "ハロウィンの本、\nオバケがたくさん\n載ってて怖いかも…", 1, 111101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3471, 12, 0, 3, "美味しそうなお菓子が\nいっぱい…これなら\nイタズラされませんね", 1, 111101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3473, 1, 0, 1, "たくさんいたずら\nできるから\nハロウィン大好き！", 1, 111201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3474, 2, 0, 1, "ハロウィンだから\nいつもよりすごい\nいたずらしたいな～！", 1, 111201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3475, 3, 0, 1, "カボチャを綺麗に\nくり抜いてすごく怖い\nランタン作っちゃお！", 1, 111201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3476, 4, 0, 1, "お菓子がたくさんで\nどれから食べるか\n迷っちゃうな～！", 1, 111201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3477, 5, 0, 1, "どうすればみんなを\nびっくりさせられる\nかな～？", 1, 111201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3478, 101, 0, 1, "カボチャ見てたら\nお腹空いて来たな～\nお菓子食べちゃお♪", 4, 111201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3479, 102, 0, 1, "にいちゃんには\n特別ないたずらを\n用意してあげるね！", 8, 111201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3480, 1, 0, 2, "カボチャにクラッカー\nを仕掛けて…わっ！？\nみ、見ちゃダメだよ！", 1, 111201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3481, 2, 0, 2, "ん？　なぁに？\nお菓子ほしいの？\nもう、しかたないな～", 1, 111201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3482, 3, 0, 2, "にいちゃんの\n面白い仮装が\n見てみたいな～！", 1, 111201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3483, 4, 0, 2, "お菓子くれたお礼に\nとびっきりの\nいたずらしちゃうね！", 1, 111201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3484, 101, 0, 2, "にいちゃんは\nハロウィンでも最高の\nいたずら相手だよ！", 4, 111201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3485, 102, 0, 2, "にいちゃんと一緒に\nハロウィンパレード\n歩きたいな～！", 8, 111201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3486, 1, 0, 2, "おはようにいちゃん！\n仮装して出かける\n準備しなきゃ！", 1, 111201, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3487, 2, 0, 2, "お腹すいたな～\nもらったお菓子\n食べちゃだめかな？", 1, 111201, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3488, 3, 0, 2, "たくさんいたずらして\nもうクタクタ～\nミソギもう寝るね…", 1, 111201, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3490, 2, 0, 3, "やわらか～い…\nここでひと休みして\nまたいたずらしよう！", 1, 111201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3491, 3, 0, 3, "かわいいもので\n油断させてわっ！\nってするのどうかな？", 1, 111201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3492, 4, 0, 3, "いいにお～い！\nいたずらのアイデアが\nわいてくるよ！", 1, 111201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3493, 5, 0, 3, "もらったお菓子\nあったかくしたら\nおいしいかな～？", 1, 111201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3495, 7, 0, 3, "お菓子ばっかりじゃ\n飽きちゃうもんね！\nご飯も食べよう！", 1, 111201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3496, 8, 0, 3, "本のなかに\nいたずらのヒント\nないかな～？", 1, 111201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3498, 12, 0, 3, "お菓子がたくさん！\n一人で食べていいの？\nやっぱりだめか～！", 1, 111201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3500, 103, 0, 1, "お勉強は順調ですよ\nやっぱりお兄ちゃんの\nおかげなんでしょうか", 12, 101101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3501, 103, 0, 2, "お兄ちゃんとの\n思い出の場所\nまた行きましょうね", 12, 101101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3502, 1, 0, 1, "えへへ～♪\nハロウィンの\nミイラさんうさぎさん\nぴょんぴょ～ん！", 1, 111301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3503, 2, 0, 1, "るんたった～\nハロウィンハロウィン\n楽しいな～♪", 1, 111301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3504, 3, 0, 1, "ハロウィンって\nどんなお菓子を\nもらえるのかな～？", 1, 111301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3505, 4, 0, 1, "お菓子をもらう\n練習をしなくちゃ！\nトリックオア\nトリート～♪", 1, 111301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3506, 5, 0, 1, "おにいちゃんと\nハロウィンするの\nミミ楽しみだな～♪", 1, 111301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3507, 101, 0, 1, "おにいちゃんと\nいっしょにお菓子を\n食べたいな～", 4, 111301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3508, 102, 0, 1, "おにいちゃんと\n毎日ハロウィン\nできたらいいな～♪", 8, 111301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3509, 1, 0, 2, "えへへ～\nおにいちゃん！\nハッピーハロウィン♪", 1, 111301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3510, 2, 0, 2, "おにいちゃんも\nいっしょに\nハロウィンパレード\nしようね～！", 1, 111301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3511, 3, 0, 2, "ミイラさんうさぎさん\nだぞ～…えへへ\nミミこわかったかな？", 1, 111301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3512, 4, 0, 2, "おにいちゃん！\nハロウィンのお菓子\nおいしいね～♪", 1, 111301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3513, 101, 0, 2, "おにいちゃんと\nいっしょなら\nおばけさんも\nこわくないよ！", 4, 111301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3514, 102, 0, 2, "ミミね\nハロウィンも\nおにいちゃんも\nだ～いすき♪", 8, 111301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3515, 1, 0, 2, "おひさまさんも\nハロウィンの\nおようふく\n着たいかな～？", 1, 111301, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3516, 2, 0, 2, "ねえねえおにいちゃん\nお昼ごはんはもらった\nお菓子でもい～い？", 1, 111301, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3517, 3, 0, 2, "まだハロウィン…？\nうぅん…眠くないよぅ\nでもちょっと…ふぁ～", 1, 111301, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3519, 2, 0, 3, "ふかふか～♪\nおばけさんが\n中にいるのかな～？", 1, 111301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3520, 3, 0, 3, "わぁ、かわいい～♪\nミミといっしょに\nあそぼ～！", 1, 111301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3521, 4, 0, 3, "ふわぁ～\nミミ、このにおい\nだ～いすき♪", 1, 111301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3522, 5, 0, 3, "あったかいね～♪\nおばけさんも\nポカポカしてるかな？", 1, 111301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3524, 7, 0, 3, "おいしい～♪\nおにいちゃんも\n食べようよー！", 1, 111301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3525, 8, 0, 3, "この本\nおにいちゃんと\nいっしょに\n読みたいな～", 1, 111301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3527, 12, 0, 3, "ハッピーハロウィン♪\nえへへ、お菓子が\nいっぱいだね～！", 1, 111301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3529, 1, 0, 1, "ここ、好きだよ。\nみんな優しいから…", 1, 111401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3530, 2, 0, 1, "なんかさみしいな", 1, 111401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3531, 3, 0, 1, "知らない人怖いな", 1, 111401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3532, 4, 0, 1, "お部屋おっきいねー", 1, 111401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3533, 5, 0, 1, "ルナね…\nお昼寝したいかも", 1, 111401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3534, 101, 0, 1, "おにいさん！\n今日は何して遊ぶの？", 4, 111401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3535, 102, 0, 1, "あのね、\nお料理教えてほしい！", 8, 111401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3536, 1, 0, 2, "あっ、おにいさんだ！", 1, 111401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3537, 2, 0, 2, "ルナは元気だよ！\nいいこだもん！", 1, 111401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3538, 3, 0, 2, "今日は何しようかな", 1, 111401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3539, 4, 0, 2, "ルナと遊んでよ！", 1, 111401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3540, 101, 0, 2, "ねーねー、\nルナとお出かけして！", 4, 111401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3541, 102, 0, 2, "おにいさんと、\n遊びたいなー！", 8, 111401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3542, 1, 0, 2, "ふふっ！\nなんかまぶしいね！", 1, 111401, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3543, 2, 0, 2, "ルナ、お腹空いたな", 1, 111401, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3544, 3, 0, 2, "どこに行こうかな？", 1, 111401, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3546, 2, 0, 3, "ルナ、これ好きだな", 1, 111401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3547, 3, 0, 3, "かわいい！　好き！", 1, 111401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3548, 4, 0, 3, "なんかいい匂い！", 1, 111401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3549, 5, 0, 3, "あったかいねー", 1, 111401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3551, 7, 0, 3, "ふふっ\nおいしいね", 1, 111401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3552, 8, 0, 3, "ルナ、\n眠くなっちゃう…", 1, 111401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3554, 103, 0, 1, "えへへ。あの人と\nランドソルでデート\nできたらうれしいな♪", 12, 105201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3555, 103, 0, 2, "あなたとなら\nどんなことだって\nできる気がするわ♪", 12, 105201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3556, 1, 0, 1, "あーあ、退屈だな\nカレでも探しに\nいこうかな", 1, 106501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3557, 2, 0, 1, "この前のケンカは\n最高に燃えたなぁ\n…拳が疼いてきたぜ", 1, 106501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3558, 3, 0, 1, "一体、この世界は\nどうなっちまったって\nいうんだ…？", 1, 106501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3559, 4, 0, 1, "もう仕事の時間か\nボスも人使いが荒いぜ\n…ま、いいけどさ！", 1, 106501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3560, 5, 0, 1, "オレたち以外に\nドラゴン族はあと何人\nいるんだろうな…", 1, 106501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3561, 101, 0, 1, "カレの真剣な顔\n面白かったな\nまた遊びたいぜ", 4, 106501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3562, 102, 0, 1, "どうしてカレがそばに\nいると、腹の底から\n力が湧いてくるんだ？", 8, 106501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3563, 1, 0, 2, "よっす！\nなんかオイシイ話でも\nあったりしない？", 1, 106501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3564, 2, 0, 2, "なあ、オレと遊ぼうぜ\n心配すんなよ。危ない\nことはよしとくからさ", 1, 106501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3565, 3, 0, 2, "どうした。オレに\nケンカ売ろうってか？\n…ははは、冗談だよ！", 1, 106501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3566, 4, 0, 2, "ちょうど退屈してた\nところだ、特訓に\n付き合ってくれよ", 1, 106501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3567, 101, 0, 2, "ったくよぉ…\nオレがかわいいなんて\n冗談はもうよせよ？", 4, 106501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3568, 102, 0, 2, "次の仕事も一緒に\n来るだろ？　断っても\n連れて行くけどな！", 8, 106501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3569, 1, 0, 2, "おはよう！　今日の\n予定はまずケンカして\nで、その後もケンカだ", 1, 106501, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3570, 2, 0, 2, "よっす！\n腹ごしらえが済んだら\nオレと遊ぼうぜ！", 1, 106501, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3571, 3, 0, 2, "夜は危ないから\nあんま出歩くな？\n…だから面白えんだろ", 1, 106501, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3573, 2, 0, 3, "次のケンカに備えて\n今は休んでおくか…\n全力で暴れたいからな", 1, 106501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3574, 3, 0, 3, "ボスの飼ってる\nラーゴが、中に\n入ってるんじゃ？", 1, 106501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3575, 4, 0, 3, "ああ、たぎる匂いが\nする…ひと暴れ\nしたくなってきた！", 1, 106501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3576, 5, 0, 3, "おおぉ…\nオレの拳もなんだか\n熱くなってきたぜ…！", 1, 106501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3578, 7, 0, 3, "いつでも全力で\nケンカするために\n力を蓄えないとな", 1, 106501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3579, 8, 0, 3, "やっぱりな…オレが\n知ってる地図とは\nまるで違ってやがる", 1, 106501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3581, 1, 0, 1, "さぁプレゼントで\n埋め尽くしてやろう！\n大いに喜ぶがいいよ！", 1, 111501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3582, 2, 0, 1, "メリークリスマス☆\n聖夜の宴だ\n派手に楽しむとしよう", 1, 111501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3583, 3, 0, 1, "煙突から入る？\nいいや、ワタシは\nいつでも正面からさ♪", 1, 111501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3584, 4, 0, 1, "サンタクロースの\nお出ましだ！\n歓迎するがいいよ♪", 1, 111501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3585, 5, 0, 1, "飾り付けが足りん！\nワタシが演出して\nやろうじゃないか！", 1, 111501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3586, 101, 0, 1, "人の喜ぶ顔を見るのも\n悪くはないな…おっと\nワタシらしくないな☆", 4, 111501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3587, 102, 0, 1, "プレゼントはまだか？\nワタシを喜ばせたら\nお返しは弾むぞ♪", 8, 111501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3588, 1, 0, 2, "ワタシを呼んだか？\n退屈な要件だったら\n覚悟しておくことだ", 1, 111501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3589, 2, 0, 2, "なんだ？\nワタシへの挑戦か？\nよし、受けた♪", 1, 111501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3590, 3, 0, 2, "おっと、当たらんよ\nワタシの乱数聖域は\n絶対だからな…♪", 1, 111501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3591, 4, 0, 2, "なんだ、オマエも\n暇なのか？\n奇遇だな、ワタシもさ", 1, 111501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3592, 101, 0, 2, "なにか愉しいことでも\nしようじゃないか\nさぁ、何をする？", 4, 111501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3593, 102, 0, 2, "こうもワタシに\n気安く触れるのは\nオマエくらいだよ…♪", 8, 111501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3594, 1, 0, 2, "朝だろうと関係ない！\nさぁ愉しく苛烈に\n騒ごうじゃないか♪", 1, 111501, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3595, 2, 0, 2, "調子の出てくる時間だ\nしっかりワタシを\n愉しませてくれよ♪", 1, 111501, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3596, 3, 0, 2, "眠いなどと言わせんぞ\nまだまだ一日は\n終わらんからな！", 1, 111501, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3598, 2, 0, 3, "いい具合じゃないか\nこのまま持って帰って\nしまおうか…♪", 1, 111501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3599, 3, 0, 3, "なかなか愛らしい\n造形をしているな？\nワタシには負けるがな", 1, 111501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3600, 4, 0, 3, "ほう、悪くない香りだ\n戦いの匂いをうまく\n隠してくれそうだ…♪", 1, 111501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3601, 5, 0, 3, "体が温まってくると\nひと暴れしたくなるな", 1, 111501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3603, 7, 0, 3, "ワタシの舌は厳しいぞ\n…ふーむ、なるほど\n認めざるをえんな", 1, 111501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3604, 8, 0, 3, "クリスティーナさんは\n知識の収集も\n欠かさないんだよ", 1, 111501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3606, 14, 0, 3, "これは驚いたな\nだがもっと、もっとだ\nワタシを退屈させるな", 1, 111501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3607, 15, 0, 3, "こんな戯れだって\n軽くこなしてしまう\nクリスティーナさんさ", 1, 111501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3608, 1, 0, 1, "メリークリスマス！\nさあ、パーティーを\n盛り上げるよ！", 1, 111601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3609, 2, 0, 1, "衣装一つで全然\n気分が違うね！\n最高のクリスマスに\nなりそうだよ！", 1, 111601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3610, 3, 0, 1, "ツリーのてっぺんの\n星って、なんだか\n欲しくなるんだよね", 1, 111601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3611, 4, 0, 1, "めいっぱい\nクリスマスを楽しむよ\n遊びだって本気\nなんだからね！", 1, 111601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3612, 5, 0, 1, "赤、白、緑、金\nクリスマスカラー\nってお洒落だよね", 1, 111601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3613, 101, 0, 1, "雪、降らないかな？\nホワイトクリスマス\nってちょっと\n憧れるなぁ", 4, 111601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3614, 102, 0, 1, "キミと過ごすこの\n楽しい時間がずーっと\n続けばいいのにな", 8, 111601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3615, 1, 0, 2, "クリスマスライブ\nチケット用意するから\n絶対来てよね！", 1, 111601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3616, 2, 0, 2, "カンパーイ！\nジュースだけど\nちょっと大人の\n気分だね", 1, 111601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3617, 3, 0, 2, "クリスマス衣装どう？\nかわいいでしょ\nあっ、どこ見てるの！", 1, 111601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3618, 4, 0, 2, "ねえ、後で街に\n行こうよ\n大きなツリーを\n一緒に見たいんだ♪", 1, 111601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3619, 101, 0, 2, "ねえ、ダンスの練習に\n付き合ってくれない？\n大丈夫、手取り足取り\n教えてあげるから♪", 4, 111601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3620, 102, 0, 2, "来年のクリスマスも\n隣にキミが\nいてくれたら\nうれしいのにな", 8, 111601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3621, 1, 0, 2, "クリスマスって朝から\nドキドキしちゃうね\nなんだか子どもみたい", 1, 111601, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3622, 2, 0, 2, "パーティーの準備も\n大分進んだね\n一息ついて\nランチにしよっか", 1, 111601, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3623, 3, 0, 2, "静かな夜だね\n心臓の音まで\n聞こえちゃいそう…", 1, 111601, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3625, 2, 0, 3, "あ～、この絶妙な\n柔らかさ…\nいい仕事してるなぁ…", 1, 111601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3626, 3, 0, 3, "アイドル顔負けの\nかわいさ、ズルい！\nでもかわいい！", 1, 111601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3627, 4, 0, 3, "ん～、落ち着くなぁ\n今日はゆっくり\n眠れそうだよ", 1, 111601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3628, 5, 0, 3, "ぬくぬく～\nどうしよう、ここから\n動きたくないなぁ", 1, 111601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3630, 7, 0, 3, "ダンスの練習もしたし\nちょっとくらいなら\nいいよね！", 1, 111601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3631, 8, 0, 3, "アイドルといえど\nかわいいだけじゃダメ\n知識も必要だよね", 1, 111601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3633, 14, 0, 3, "面白ーい！\nこの演出、ライブでも\nできないかな？", 1, 111601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3634, 15, 0, 3, "もふもふ～♪\n寒い日には最高の\n抱き心地だね！", 1, 111601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3635, 103, 0, 1, "彼ともっとお話し\nしたいな…もう少し\n補習を増やそうかしら", 12, 101801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3636, 103, 0, 2, "君のこともっともっと\n教えて欲しいな。私に\n補習授業してくれる？", 12, 101801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3637, 1, 0, 1, "この姿ならあやつも\n子ども扱いできまい！\n…じゃがなぜか、少し\n物足りないような…", 1, 111701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3638, 2, 0, 1, "時間もあるし、\n聖夜の宴の\n準備をするかのう♪", 1, 111701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3639, 3, 0, 1, "…わらわの知る聖夜と\n随分違うが、宴は宴\n楽しませて\nもらおうかのう♪", 1, 111701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3640, 4, 0, 1, "大人の姿で\nできること…ふむ\n少しあやつを\nからかってやるか♪", 1, 111701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3641, 5, 0, 1, "…クリスマスは\n恋人たちの夜！？\nち、近頃の若いもんは\n破廉恥なのじゃ！", 1, 111701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3642, 101, 0, 1, "…あやつがおらんと、\n宴も物足りぬな\n…馬鹿者が\n早く帰ってこい…", 4, 111701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3643, 102, 0, 1, "誰も見ておらぬな…？\n…よし、あやつへの\nプレゼントを\n準備するとしようか！", 8, 111701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3644, 1, 0, 2, "クリスマスの\nチキンにケーキ…\nべ、別に楽しみになど\nしておらぬぞ！", 1, 111701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3645, 2, 0, 2, "この姿では、わらわの\n頭をなでるのは\n難しかろう…べ、\n別に残念ではないわ！", 1, 111701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3646, 3, 0, 2, "メリー\nクリスマスじゃ！\nさあ、わらわのために\n宴の準備をするのじゃ", 1, 111701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3647, 4, 0, 2, "そんなにこの衣装が\n気になるのか？\n…だったら、直に\n触ってみるがよい♪", 1, 111701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3648, 101, 0, 2, "いつもと逆に、お主を\n子ども扱いしてやるか\nほれ、膝枕をしてやる\nからそばに来い♪", 4, 111701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3649, 102, 0, 2, "…せ、聖夜の予定は\n空けておくのじゃぞ？\nなんでって…\nな、なんでもじゃ！", 8, 111701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3650, 1, 0, 2, "もう朝か…聖夜の宴が\n楽しみで…ではなく！\n準備のせいで、\nあまり眠れんかったぞ", 1, 111701, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3651, 2, 0, 2, "…ずっと雪が\n降っておれば、\n陽光が強いこの時間も\n怯えずにすむのにのう", 1, 111701, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3652, 3, 0, 2, "時は来た…\nさあ眷属よ！\nわらわと共に、\n聖夜の宴を楽しもうぞ", 1, 111701, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3654, 2, 0, 3, "良い椅子ではないか\nここから聖夜の宴を\n眺めるのもよいかもな", 1, 111701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3655, 3, 0, 3, "か、かわい…こほん\nなかなか良い\nデザインをしておるな", 1, 111701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3656, 4, 0, 3, "ふむ、良い香りじゃ\n聖夜の宴でも\nこれを使うとしよう", 1, 111701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3657, 5, 0, 3, "これが癒し…はっ！\nあやうく浄化される\nところであった…", 1, 111701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3659, 7, 0, 3, "ニ、ニンニクは\n入っておらぬよな？\n…うむ、美味じゃ♪", 1, 111701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3660, 8, 0, 3, "聖夜は恋人たちの夜…\n…き、気になるわけ\nではないが、\n少し調べてみるか", 1, 111701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3662, 14, 0, 3, "ほう…これは面白い\n魔法の使い方じゃな\nまるで本物の\n演劇のようじゃ", 1, 111701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3663, 15, 0, 3, "こ、この程度の\nかわいさに誘惑される\nわらわでは…\nふわもこなのじゃ～♪", 1, 111701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3664, 1, 0, 1, "おせち料理も\n魔物尽くしだったり\nしないわよね！", 1, 112001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3665, 2, 0, 1, "初詣で何をお祈り\nしようかしら…\n定番でいいわよね？", 1, 112001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3666, 3, 0, 1, "初日の出って\n特別な感じがして\nいいわよね…", 1, 112001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3667, 4, 0, 1, "寒い中並んで飲む\n甘酒って不思議なほど\nおいしいわよね", 1, 112001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3668, 5, 0, 1, "羽根つきで勝負…\nあいつにだったら\n勝てるかしら？", 1, 112001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3669, 101, 0, 1, "年賀状になんて書こう\n…いろいろと\n恥ずかしくなるのよね", 4, 112001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3670, 102, 0, 1, "今年もあいつと\n居られること…\n結構うれしいわ", 8, 112001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3671, 1, 0, 2, "何じっと\n見てんのよ？\nお年玉はあげないわよ", 1, 112001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3672, 2, 0, 2, "おせち料理一緒に\n食べる？\n結構おいしいわよ", 1, 112001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3673, 3, 0, 2, "羽根つきで負けたら\n顔に墨塗られるのよ\n覚悟しなさい！", 1, 112001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3674, 4, 0, 2, "門松飾るから\nちょっと手伝って\nくれる？", 1, 112001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3675, 101, 0, 2, "あんたと一緒に\n初詣行くのも悪くない\n…かも", 4, 112001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3676, 102, 0, 2, "初詣で何をお祈り\nしたか？　うっさい！\n内緒よ！内緒！", 8, 112001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3677, 1, 0, 2, "もう朝なの…\n正月くらいゆっくり\n寝ていたいわよね…", 1, 112001, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3678, 2, 0, 2, "おせちに飽きたら\n次は何を食べよう\nかしら…", 1, 112001, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3679, 3, 0, 2, "そろそろ寝ましょ\nいい初夢が見られると\nいいわね", 1, 112001, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3681, 2, 0, 3, "この格好でいるのも\nなかなか疲れるわね\n休憩～", 1, 112001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3682, 3, 0, 3, "か、かわいい…う～ん\n鏡餅も顔を描けば\nかわいくなるかしら？", 1, 112001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3683, 4, 0, 3, "いい匂いね\n落ち着いて\n眠れそう…", 1, 112001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3684, 5, 0, 3, "ここでお餅焼いたら\n一段とおいしく\nなりそうね", 1, 112001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3686, 7, 0, 3, "正月に食べるものは\nなんでもおいしく\n感じちゃうわね！", 1, 112001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3687, 8, 0, 3, "魔物を使わない\nおせちを\n調べておかなきゃ…", 1, 112001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3689, 1, 0, 1, "新年を気持ちよく\n過ごせるように\nお掃除がんばります！", 1, 112101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3690, 2, 0, 1, "神殿に通っていたら\nいつの間にか足腰が\n鍛えられたような…", 1, 112101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3691, 3, 0, 1, "神殿付近は屋台も\nあるので、良い香りが\n漂ってくるんですよ～", 1, 112101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3692, 4, 0, 1, "おせちを作れるように\nなれば、子どもたちも\n喜んでくれるかな…？", 1, 112101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3693, 5, 0, 1, "コホォォォォォ…！\nふぅ、深呼吸すると\n落ち着きますね♪", 1, 112101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3694, 101, 0, 1, "お仕事以外の時も\nあなたと…い、いえ！\nなんでもないです！", 4, 112101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3695, 102, 0, 1, "あなたと一緒だと\nなんだかポカポカ\nしてくるんです…", 8, 112101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3696, 1, 0, 2, "この格好をしていると\nなんだか神殿にいる\n気分になりますね", 1, 112101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3697, 2, 0, 2, "そういえば神殿で\n甘酒をもらったんです\nあなたも飲みますか？", 1, 112101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3698, 3, 0, 2, "あっ、髪飾りが\nズレちゃってました！\nいけないいけない…", 1, 112101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3699, 4, 0, 2, "ひゃわっ！？\nく、くすぐったり\nしないでください～", 1, 112101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3700, 101, 0, 2, "そ、そんなに\n見つめられると\nて、照れちゃいます…", 4, 112101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3701, 102, 0, 2, "今度、私が考えた\n運気上昇のおまじない\nかけてあげますね♪", 8, 112101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3702, 1, 0, 2, "おはようございます\n神殿のお手伝いに行く\n準備もしなきゃですね", 1, 112101, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3703, 2, 0, 2, "お昼になりましたし\nご飯は私が作…\nえっ、ダメですか！？", 1, 112101, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3704, 3, 0, 2, "明日も早いですし\nそろそろ寝る支度を…\nふわぁ…", 1, 112101, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3706, 2, 0, 3, "お掃除も終わったので\n少し休憩を…ふわぁ…\n急に眠気が…", 1, 112101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3707, 3, 0, 3, "新年を気持ちよく\n過ごせるようこの子も\nきれいにしましょう♪", 1, 112101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3708, 4, 0, 3, "神殿のお香のような\n香りだと落ち着いて\n過ごせそうですよね", 1, 112101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3709, 5, 0, 3, "ぽかぽかですね～…\n甘酒も飲んで体の\n芯から温めましょう", 1, 112101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3711, 7, 0, 3, "今年こそ\n料理も上手に…\nもぐもぐもぐもぐ…", 1, 112101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3712, 8, 0, 3, "失敗続きだと\n逆に運が良くなる…\nほ、本当ですかぁ…？", 1, 112101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3714, 103, 0, 1, "キミにならもう\nどんな私を見られても\n大丈夫かな…♪", 12, 103401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3715, 103, 0, 2, "なになにどしたのぉ？\nお姉さんが撫でたげる\nこっちにきなさぁい♪", 12, 103401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3716, 1, 0, 1, "わたくしの舞を\n主さまは喜んで\nくださるでしょうか…", 1, 111901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3717, 2, 0, 1, "年を越したあとに\n食べるおそばも\nおいしいです", 1, 111901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3718, 3, 0, 1, "甘酒を飲むと\n心まで温まる\n気がいたします", 1, 111901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3719, 4, 0, 1, "主さまのために\n真心を込めたおせちを\nご用意しなくては", 1, 111901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3720, 5, 0, 1, "おみくじを引くのは\nドキドキしつつも\n楽しいものです", 1, 111901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3721, 101, 0, 1, "主さまの温もりは\n心まで温めてくれる\nようです", 4, 111901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3722, 102, 0, 1, "うまくできなくとも\n心を込めて精いっぱい\n舞いましょう", 8, 111901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3723, 1, 0, 2, "主さま、この晴れ着は\n似合っている\nでしょうか？", 1, 111901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3724, 2, 0, 2, "お餅はゆっくりと\n召し上がって\nくださいまし", 1, 111901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3725, 3, 0, 2, "主さまのお顔の\n福笑いなら、完璧に\nできる気がいたします", 1, 111901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3726, 4, 0, 2, "主さまはどのような\n初夢をご覧になった\nのでしょう？", 1, 111901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3727, 101, 0, 2, "甘酒の飲み過ぎは\nいけませんよ？\nめっ、でございます", 4, 111901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3728, 102, 0, 2, "二人羽織とは\nあんなにも暖かい\nものなのですね…", 8, 111901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3729, 1, 0, 2, "おはようございます\n寒い季節の朝日は\nとても美しいですね", 1, 111901, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3730, 2, 0, 2, "おいしくとも\n食べ過ぎは正月太りの\nもとでございます", 1, 111901, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3731, 3, 0, 2, "いい夢が見られるよう\n暖かくして\n眠るといたしましょう", 1, 111901, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3733, 2, 0, 3, "ふかふか…\nこのまま\n寝てしまいそうです", 1, 111901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3734, 3, 0, 3, "あなたと一緒に\nお正月遊びを\n楽しんでみたいです", 1, 111901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3735, 4, 0, 3, "神殿の独特な香りを\nおうちに持ち込むのは\nどうでしょうか", 1, 111901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3736, 5, 0, 3, "風邪をひきませんよう\n暖かくいたしましょう", 1, 111901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3738, 7, 0, 3, "食べ過ぎは\n正月太りの\nもとだというのに…", 1, 111901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3739, 8, 0, 3, "主さまのために\n参拝の作法を\n予習しておきましょう", 1, 111901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3741, 1, 0, 1, "…この杖、変身前には\n無いパーツがあるけど\nこれはどこから…うん\n考えないようにしよう", 1, 112201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3742, 2, 0, 1, "フ、フリフリの\nかわいい衣装…はっ！\n素に戻ってはダメだ！", 1, 112201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3743, 3, 0, 1, "魔法探偵\nミスティ★カスミ！\n魔法の力で捜査開始！\n…練習終わりっと", 1, 112201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3744, 4, 0, 1, "怪しい視線を感じる…\nまさか、近くに\n闇の力を持つ人が！？", 1, 112201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3745, 5, 0, 1, "時間もあるし、\n魔法少女の修行を…\nじゃ、じゃなくて！\n探偵の仕事をしよう！", 1, 112201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3746, 101, 0, 1, "助手くんとずっと\n一緒に居られるし、\n魔法少女も\n案外悪くないかも♪", 4, 112201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3747, 102, 0, 1, "魔法少女の訓練のため\n助手くんの所に行こう\nかな…あ、あくまで\n訓練のためにね！", 8, 112201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3748, 1, 0, 2, "…髪型が急に\n変わって驚いた？\nき、気になるなら…\n少し触ってみるかい？", 1, 112201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3749, 2, 0, 2, "助手くんは魔法少女に\nなりたいのかい？\nその場合は魔法騎士…\nいや、魔法助手とか？", 1, 112201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3750, 3, 0, 2, "頭に浮かぶ呪文…\n強制変身…助手くん\n魔法少女とは\n呪いか何かなのかな？", 1, 112201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3751, 4, 0, 2, "あ、あまり触らないで\nくれ…魔法少女になる\nと感覚が鋭くなって、\n耳も敏感に…ひゃう！", 1, 112201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3752, 101, 0, 2, "や、やっぱりこの\n格好は変かな…え、\nかわいい？　じゃあ…\nいや、でも、う～ん", 4, 112201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3753, 102, 0, 2, "…この変身は助手くん\nのせいでもあるんだよ\nだから…せ、責任を\n取ってもらうからね！", 8, 112201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3754, 1, 0, 2, "おはよう助手くん\nさあ、そろそろ\n街のパトロールに\n行こうじゃないか！", 1, 112201, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3755, 2, 0, 2, "もうお昼…助手くん、\nそろそろ本業に戻ろう\n…その顔は何かな？\n私の本業は探偵だぞ！", 1, 112201, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3756, 3, 0, 2, "夜だけど、私は\nもう少し仕事をするよ\n魔法少女の強い体は\nこんなときに便利だね", 1, 112201, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3758, 2, 0, 3, "気持ちいい…\nこの癒しを、魔法と\n論理の二つの観点から\n解明したいね…", 1, 112201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3759, 3, 0, 3, "ダ、ダメ！！\nここで感情のままに\n飛びついたら、私の\nイメージがぁ～！", 1, 112201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3760, 4, 0, 3, "心が安らぐ香りだね…\n…今なら、魔法少女も\nすんなりと受け入れ…\nてはダメだっ！", 1, 112201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3761, 5, 0, 3, "あったかい…\n魔法少女だろうと\n休息は必要だし、少し\n休んでいこうかな", 1, 112201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3763, 7, 0, 3, "魔法少女も探偵も、\nやっぱり体が資本！\n食べられるときに\n食べておかないとね♪", 1, 112201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3764, 8, 0, 3, "…魔法少女\nセリイちゃんの本は\nどこにあるのかな？", 1, 112201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3766, 1, 0, 1, "魔法少女かぁ…\nせっかくだし思う存分\n体を動かしてみたいな", 1, 112301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3767, 2, 0, 1, "ニャットちゃん…\nこの格好を見て\n私って気づくかな？", 1, 112301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3768, 3, 0, 1, "ふふ…\n今日はなんだか\n調子がいいかも", 1, 112301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3769, 4, 0, 1, "魔法は便利だけど\nあまり頼りすぎちゃ\nダメだよね", 1, 112301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3770, 5, 0, 1, "さっき街で小さな子に\n声かけられちゃった\n照れるけどうれしいな", 1, 112301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3771, 101, 0, 1, "あの人がいると\nなんだかホッとする\n不思議…", 4, 112301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3772, 102, 0, 1, "魔法少女になっても\nあの人への気持ちは\nずっと同じ…", 8, 112301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3773, 1, 0, 2, "今日の私…ですか？\nはいとっても元気です\n…あなたのおかげかも", 1, 112301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3774, 2, 0, 2, "よかったら魔法の\n練習に付き合って\nくれませんか？", 1, 112301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3775, 3, 0, 2, "あの…\nこの格好どうですか？\n似合ってると…いいな", 1, 112301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3776, 4, 0, 2, "…な、なんですか？\nあまり見られると\n恥ずかしいです…", 1, 112301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3777, 101, 0, 2, "私、あなたのそばに\nいられたら、それで…", 4, 112301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3778, 102, 0, 2, "想い人と結ばれる\n魔法は…ひゃっ！？\n今の聞いてましたか？", 8, 112301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3779, 1, 0, 2, "おはようございます\n今日も街の平和の\nために頑張らなきゃ", 1, 112301, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3780, 2, 0, 2, "お腹すきましたね。\nあの…一緒にランチは\nいかがですか？", 1, 112301, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3781, 3, 0, 2, "あ、もうこんな時間…\nふふ、まだこんなに\n体力が余ってるのに", 1, 112301, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3783, 2, 0, 3, "気持ちいい…\nこの気持ち良さは\nどんな魔法も勝てない", 1, 112301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3784, 3, 0, 3, "何これ、かわいい…\nお姉ちゃんにも\n見せてあげたいな", 1, 112301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3785, 4, 0, 3, "ん～、いい香り\n…これ、魔法で\n出せたりしないかな？", 1, 112301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3786, 5, 0, 3, "あったか～い\nえへへ…なんだか\nお姉ちゃんみたい", 1, 112301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3788, 7, 0, 3, "おいしいなぁ、これ\n魔法少女になってから\n食欲旺盛かも…", 1, 112301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3789, 8, 0, 3, "ふふ…\n魔法少女になっても\n読書はやめられないな", 1, 112301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3791, 103, 0, 1, "みらくるまほりん\nくるりんぱ～♪\n王子はんのために\n花嫁修業や～♪", 12, 101001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3792, 103, 0, 2, "あ、あのな王子はん\nできたらその…\nお姫さまだっこを\nお願いしてもええ…？", 12, 101001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3793, 1, 0, 1, "やっぱりわたしたちの\nギルドハウスの\n居心地は最高ですね☆", 1, 180401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3794, 2, 0, 1, "王宮じゃできないこと\nやっちゃいましょう♪\n例えば、えっと～…", 1, 180401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3795, 3, 0, 1, "そうだ、隠しておいた\nおやつがありました！\nまだ食べられますね☆", 1, 180401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3796, 4, 0, 1, "ごはんはみんなで\n楽しく食べましょう！\nそれが一番の\n調味料です♪", 1, 180401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3797, 5, 0, 1, "やっぱりここが、\nわたしの帰る場所です\n…ただいま☆", 1, 180401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3798, 101, 0, 1, "なんだか物足りません\nきっと、あの人が隣に\nいないからですね…", 4, 180401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3799, 102, 0, 1, "どこを見ても、大切な\n思い出がたくさん\n詰まっていますね♪", 8, 180401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3800, 1, 0, 2, "無礼者～、なんて\n冗談です♪　どんどん\n触っちゃってください", 1, 180401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3801, 2, 0, 2, "ぷにぷにでしょう～？\nい～っぱい食べるのが\nぷにぷにの秘訣です！", 1, 180401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3802, 3, 0, 2, "握手したいんですか？\nはい、にぎにぎ～！\nもう離しませんよ♪", 1, 180401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3803, 4, 0, 2, "どうかしましたか？\n退屈してるんでしたら\nお散歩に行きましょう", 1, 180401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3804, 101, 0, 2, "ごはん王子♪\nわたしは王女ですから\nこれでピッタリですね", 4, 180401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3805, 102, 0, 2, "大好きです～\n愛してます～\n…ほんと、ですよ♪", 8, 180401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3806, 1, 0, 2, "朝ごはんは大事です！\n山盛りごはんを食べて\n元気いっぱいですよ☆", 1, 180401, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3807, 2, 0, 2, "お腹ぺこぺこになる\n時間ですね～！\nごはん特盛、一つ！", 1, 180401, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3808, 3, 0, 2, "お腹いっぱい、今日は\nよく眠れそうですね♪\n明日もおいしい一日を", 1, 180401, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3810, 2, 0, 3, "休憩休憩～♪\nお腹を休めて、次の\nごはんに挑みます…☆", 1, 180401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3811, 3, 0, 3, "かわいらしい\nぬいぐるみですね～\nキャルちゃんみたい☆", 1, 180401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3812, 4, 0, 3, "すんすん…この匂い\n食欲増進の効果が\nありそうですね…？", 1, 180401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3813, 5, 0, 3, "体温が上がってくると\n眠くなっちゃいます…\nほわぁ～", 1, 180401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3815, 7, 0, 3, "もぐもぐもぐもぐもぐ\nむぐむぐむぐむぐむぐ\nはぐはぐはぐはぐはぐ", 1, 180401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3816, 8, 0, 3, "教養を身に着けるには\n読書が一番です！\nえっと次のレシピは～", 1, 180401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3818, 1, 0, 1, "アイドルらしくなる\nコツって、何か\nあるんでしょうか？", 1, 112401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3819, 2, 0, 1, "色んな人と話すのが\n好きなんです。つい\n長くなっちゃって…", 1, 112401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3820, 3, 0, 1, "アイドルを目指すって\n決めてから、毎日が\nワクワクなんです♪", 1, 112401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3821, 4, 0, 1, "ちゃんとストレッチ\nしておかなくっちゃ\nよいしょ、よいしょ…", 1, 112401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3822, 5, 0, 1, "リンちゃんミオちゃん\n二人に負けないように\n私、頑張ります！", 1, 112401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3823, 101, 0, 1, "私の笑顔は素敵だって\n誰かに褒められた…\nそんな気がするんです", 4, 112401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3824, 102, 0, 1, "私たちを支えてくれる\n誰かが、ずっと一緒に\nいてくれたような…", 8, 112401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3825, 1, 0, 2, "どうかしましたか？\n何か困ってるのなら\nお手伝いしますよ！", 1, 112401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3826, 2, 0, 2, "みなさん個性的で\n普通じゃない人ばかり\nいるような気が…", 1, 112401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3827, 3, 0, 2, "今日はポカポカした\nいい天気ですね～\nお散歩日和です！", 1, 112401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3828, 4, 0, 2, "今、ミルクティーを\n淹れてたんです♪\nあなたも飲みますか？", 1, 112401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3829, 101, 0, 2, "私の得意なこと？\nうーんなんでしょう…\nあっ、肩たたきです！", 4, 112401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3830, 102, 0, 2, "生・ハム・メローン！\nあ、ライブの掛け声を\n練習してたんですよ♪", 8, 112401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3831, 1, 0, 2, "おはようございます！\n今日も一日\n頑張りましょうね！", 1, 112401, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3832, 2, 0, 2, "しっかりお昼を食べて\n午後からも張り切って\nいきましょうね♪", 1, 112401, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3833, 3, 0, 2, "ぐっすり眠れるよう\n早めに用事を済ませて\nお休みしましょうね", 1, 112401, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3835, 2, 0, 3, "ちょっと休んだら\nレッスンの続きも\n頑張ります！", 1, 112401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3836, 3, 0, 3, "えへへ～\nふわふわもこもこで\nかわいいです～♪", 1, 112401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3837, 4, 0, 3, "リラックスできる\nいい香りですね～♪", 1, 112401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3838, 5, 0, 3, "わぁ、ポカポカです♪\n温かい飲み物も\nほしくなりますね", 1, 112401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3840, 7, 0, 3, "レッスンしたら\nお腹が空いちゃって…\nぱくぱくもぐもぐ…", 1, 112401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3841, 8, 0, 3, "レッスンに役立つ\n運動の情報とか\n載ってるでしょうか…", 1, 112401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3843, 1, 0, 1, "きらきらーん☆\nよし、次のステージは\nこれで行けるかも！", 1, 112601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3844, 2, 0, 1, "私の元気でみんなも\n元気になってくれたら\nうれしいな～", 1, 112601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3845, 3, 0, 1, "ちょっとレッスンで\n疲れちゃったかも…\nでも頑張るぞっ！", 1, 112601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3846, 4, 0, 1, "晩ごはん何にしよう？\nそーだ、しぶりんにも\n聞いてみよっと！", 1, 112601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3847, 5, 0, 1, "元気だから笑顔になる\nじゃなくて\n笑顔だから\n元気になるんだよね～", 1, 112601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3848, 101, 0, 1, "声援を聞いてる時ね\nすごく懐かしいって\n思うことがあるんだ", 4, 112601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3849, 102, 0, 1, "私を応援してくれる\n一番大事な人が\n隣にいた気がする…", 8, 112601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3850, 1, 0, 2, "なになにー？\nミオちゃんに\n何か用かな～？", 1, 112601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3851, 2, 0, 2, "おっ、やっほー！\n元気そうだね～、キミ\n私？\nもちろん元気～！", 1, 112601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3852, 3, 0, 2, "ねぇねぇ、ちょっと\n遊びに行かない？\nたまには息抜きで！", 1, 112601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3853, 4, 0, 2, "ね、この魔物料理さ\nプリンって言い張れば\n食べてもらえそうかな", 1, 112601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3854, 101, 0, 2, "えへへ\nいつもありがとねっ！\nパワーもらってるよ～", 4, 112601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3855, 102, 0, 2, "どうしたどうした～\nミオちゃんの魅力に\nメロメロなのかい？", 8, 112601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3856, 1, 0, 2, "おっはよ～！\n一日の始まりだし\nテンション上げてこ！", 1, 112601, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3857, 2, 0, 2, "お腹空いた～…\nねぇねぇ、一緒に\nお昼食べようよ～", 1, 112601, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3858, 3, 0, 2, "ふわぁ～…\n眠くなってきちゃった\nそろそろ休もっかなぁ", 1, 112601, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3860, 2, 0, 3, "わぁ、ふっかふか～！\nレッスンの疲れも\n吹っ飛んじゃうね☆", 1, 112601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3861, 3, 0, 3, "何これ…かわいーっ！\n早くしまむーに\n教えてあげなきゃ！", 1, 112601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3862, 4, 0, 3, "すぅ～…\nうんっ、これ私の\n好きな香りだな～♪", 1, 112601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3863, 5, 0, 3, "ポッカポカだね～♪\n…あ～ん、ここから\n動きたくないーっ！", 1, 112601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3865, 7, 0, 3, "わっ、おーいしー！\nこれはテンション\n上がっちゃうね♪", 1, 112601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3866, 8, 0, 3, "ふむふむ…\nふむふむふむ…！\n勉強になった、ブイ☆", 1, 112601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3868, 103, 0, 1, "なんでドキドキが\n止まらないの…？\n騎士くんのせいだー！", 12, 100101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3869, 103, 0, 2, "ドキドキしてるの\nあたしだけ、かな…\n騎士くんはどう…？", 12, 100101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3870, 103, 0, 1, "騎士クンの前で\n好きって言えるように\n練習しないと…", 12, 100201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3871, 103, 0, 2, "騎士クン…大好き\n…き、騎士クン！？\nなんでここに！？", 12, 100201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3872, 103, 0, 1, "困ったな…\n彼が居ないだけで、\nここまで\n寂しいと思うなんて…", 12, 100301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3873, 103, 0, 2, "えっと…\n今日は訓練じゃなくて\nその…二人でゆっくり\n過ごしたいな…", 12, 100301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3874, 1, 0, 1, "アイドルの心構え…\nどういう風に考えると\nしっくりくるのかな", 1, 112501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3875, 2, 0, 1, "何か始めるのって\nドキドキするよね\nだから最近楽しいんだ", 1, 112501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3876, 3, 0, 1, "冒険者の時って毎日\nバタバタしてたけど…\n目標ができると違うね", 1, 112501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3877, 4, 0, 1, "少しお腹が空いた…\nチョコレート一粒だけ\n食べようかな", 1, 112501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3878, 5, 0, 1, "ウヅキもミオも\n頑張ってるし\n私も負けられないね", 1, 112501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3879, 101, 0, 1, "レッスンしてる時さ\n何か、誰かが足りない\nそう思うことがあるの", 4, 112501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3880, 102, 0, 1, "大事なのは笑顔…\n誰か、大切な人から\n言われた気がするんだ", 8, 112501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3881, 1, 0, 2, "どうしたの、何か用？\n手伝えることがあれば\nすぐに言ってね", 1, 112501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3882, 2, 0, 2, "姿勢がいい？\n普段から鍛えてるし\n自然とそうなるのかも", 1, 112501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3883, 3, 0, 2, "さっき散歩してたら\n犬を見かけたんだ\n人懐っこい子だったよ", 1, 112501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3884, 4, 0, 2, "今から冒険？\nなら私も一緒に行くよ\n前衛は任せておいて", 1, 112501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3885, 101, 0, 2, "今からレッスンに\n行くつもりだったんだ\n少し手伝ってくれる？", 4, 112501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3886, 102, 0, 2, "蒼の剣を受けよ！\nえ、何してるのって？\nただの発声練習だけど", 8, 112501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3887, 1, 0, 2, "おはよう、早いね\n今日は依頼もないし\nゆっくりしようかな", 1, 112501, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3888, 2, 0, 2, "そろそろお昼だね\nうーん、今日は\n何を食べよう…", 1, 112501, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3889, 3, 0, 2, "もうこんな時間…\n明日も早いし\nそろそろ寝なくちゃ", 1, 112501, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3891, 2, 0, 3, "レッスンで疲れたし\nちょっとだけ\n休んでおこうかな", 1, 112501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3892, 3, 0, 3, "ふふっ、かわいい\nふわふわしたものって\n撫でたくなるんだよね", 1, 112501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3893, 4, 0, 3, "うん、いい香り…\nこういうものがあると\n落ち着くからいいよね", 1, 112501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3894, 5, 0, 3, "温かい場所って\n居心地がいいから\nのんびりしちゃうよ", 1, 112501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3896, 7, 0, 3, "レッスン頑張ったら\nすぐお腹が空くし\n食べておかないと…！", 1, 112501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3897, 8, 0, 3, "アイドルのことを\nもっと勉強して\n成長しなくちゃ", 1, 112501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3899, 1, 0, 1, "ああ、知りたい…\nなんでもかんでも\nとにかく知りたい…", 1, 111001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3900, 2, 0, 1, "ああ、眠い…\nいつでもどこでも\nとにかく眠い…", 1, 111001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3901, 3, 0, 1, "君、気を付けたまえ\nぼーっと歩いていると\nほわっ！　転んだ…", 1, 111001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3902, 4, 0, 1, "ミツキ師にいただいた\n新作サプリ…丁度よい\n被験体はいないものか", 1, 111001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3903, 5, 0, 1, "サレンディア救護院に\nユニちゃん８歳として\n潜入済みだが何か？", 1, 111001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3904, 101, 0, 1, "君がユニちゃんズの\n同志であること、\nぼくは誇りに思うぞ", 4, 111001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3905, 102, 0, 1, "ぼくは青春がしたい\nおい、君\nぼくと青春しないか", 8, 111001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3906, 1, 0, 2, "ああ、そうだな。\nぼくもそう思う。\n……えっ、何が？", 1, 111001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3907, 2, 0, 2, "待て、話しかけるな。\n今の閃きをメモに…\n…ああっ、忘れた！", 1, 111001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3908, 3, 0, 2, "歩き方が綺麗？\n何を今更なこと\n言っとるんだ君は", 1, 111001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3909, 4, 0, 2, "ぼくの血液型？\n大らかのОだ。\n何か異論あるかね", 1, 111001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3910, 101, 0, 2, "君の声は精神に\n高揚をもたらすな…\n波形を分析したいぞ", 4, 111001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3911, 102, 0, 2, "妙だな…最近\n君に話しかけられると\n心拍に乱れを覚える", 8, 111001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3912, 1, 0, 2, "おっと、朝だな。\nそろそろ寝るか、\n朝更かしは体に悪い", 1, 111001, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3913, 2, 0, 2, "ふむ、昼餉時か…\n今日のランチは\nサプリ定食にしよう", 1, 111001, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3914, 3, 0, 2, "もう夜だな、\nそろそろ起きるか。\n最高の一日にするぞ！", 1, 111001, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3916, 2, 0, 3, "ほわぁ～\n副交感神経が…\n優位に…ふふっひ", 1, 111001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3917, 3, 0, 3, "かわいすぎるな…\nセクシーユニには\nミスマッチか…", 1, 111001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3918, 4, 0, 3, "ほぁあアあァぁー…\nいい香りー…\nあアァあ…イイ…", 1, 111001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3919, 5, 0, 3, "炎はいいぞ\nそこには人類発展の\nドラマがある", 1, 111001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3921, 7, 0, 3, "フオオー！\n次から次へと固形食物\n原理気になるー！", 1, 111001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3922, 8, 0, 3, "良書は時間を忘れる\n…ふむ、今度\n布団を持ってこよう", 1, 111001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3924, 103, 0, 1, "きらーん☆\nハツネちゃんに\n不可能はないのだ☆", 12, 101201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3925, 103, 0, 2, "キミと一緒だと\n寝ちゃうのが\nもったいないよ～", 12, 101201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3933, 1, 0, 2, "ちぇるーん☆", 1, 192101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3951, 1, 0, 1, "ちぇる～ん☆\n今日もチエルは\nかわいいですよね～？", 1, 110901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3952, 2, 0, 1, "どこかに楽しいもの\n落ちてないかにゃー", 1, 110901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3953, 3, 0, 1, "チエルのオキニは\n甘々カフェラテ！\nシクヨロでぇ～す☆", 1, 110901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3954, 4, 0, 1, "先輩、ひまですよね？\nチエル遊んであげても\nいいですけど？", 1, 110901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3955, 5, 0, 1, "はぁー…しゅき…\nカルミナ尊い…\n一生すこれる…", 1, 110901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3956, 101, 0, 1, "先輩、ひまー\nちょっとは構えよーう\nデュクシ！デュクシ！", 4, 110901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3957, 102, 0, 1, "先輩なんか\nおごって～！\nてか…おごらなくても\nいいから遊んでよ…", 8, 110901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3958, 1, 0, 2, "え、なんですか？\n告白だったら\n間に合ってますけど", 1, 110901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3959, 2, 0, 2, "ちょっと静かに！\n今ヤバかわポーズの\nイメトレ中なんです！", 1, 110901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3960, 3, 0, 2, "足取りが軽やか？\nあー…ダンスとか\n習ってるからですかね", 1, 110901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3961, 4, 0, 2, "チエルの血液型？\n大型新人のОです\n何か問題でも？", 1, 110901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3962, 101, 0, 2, "先輩って何気に\nいい声してますよね\n言うことは変だけど", 4, 110901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3963, 102, 0, 2, "え、なんですか？\nたまにだったら\n告白してもいいですよ", 8, 110901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3964, 1, 0, 2, "おはちぇる～ん☆\nハイ、ご一緒に～？", 1, 110901, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3965, 2, 0, 2, "今日のランチは\nどこで\n映えよっかにゃー", 1, 110901, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3966, 3, 0, 2, "こんばんちぇるるん☆\nハイ、ご一緒に～？", 1, 110901, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3968, 2, 0, 3, "ふかふかチエルン\n略して、ふかルン♪", 1, 110901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3969, 3, 0, 3, "かわいいこの子と\nかわいいチエル！\n映ぁ～え～る～！", 1, 110901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3970, 4, 0, 3, "かわいい香り～！\n持って帰ろっと！\nすぅーっ…", 1, 110901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3971, 5, 0, 3, "マシュマロは\n焼かない方のが\nかわいくないです？", 1, 110901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3973, 7, 0, 3, "チエル食べても\n太らない体質\nなんでぇ～！", 1, 110901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3974, 8, 0, 3, "読書は好きですよ\nシンプルに楽しいし\nギャップ萌えでしょ？", 1, 110901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3976, 1, 0, 1, "みんなのあたしを\n見る目が違うね\nま、気にしないけどね", 1, 112701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3977, 2, 0, 1, "今日も疲れた～\nやる気ゼロだし\nもう寝ちゃおうかな", 1, 112701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3978, 3, 0, 1, "あたしの代わりに誰か\n働いてくれないかね", 1, 112701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3979, 4, 0, 1, "労働の後に食べる\nあんパン最高～", 1, 112701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3980, 5, 0, 1, "仕事の合間に\nちょっと一息いれよ\nあ～ドングリ美味し～", 1, 112701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3981, 101, 0, 1, "報酬も入ったことだし\nあいつに何か\n買ってあげようかな", 4, 112701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3982, 102, 0, 1, "次の休みはあいつと\n一日中だらだら\nしたいな～", 8, 112701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3983, 1, 0, 2, "あたしは褒められて\n伸びるタイプなんだ\nだから褒めて褒めて", 1, 112701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3984, 2, 0, 2, "お、差し入れ？\n気が利くねぇ\nあんがと", 1, 112701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3985, 3, 0, 2, "新しい依頼？\n今日はもう無理～\n閉店で～す", 1, 112701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3986, 4, 0, 2, "サ、サボってないよ！\nちょっと休憩してた\nだけだから！", 1, 112701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3987, 101, 0, 2, "ねえ、一緒に\nサボろうよ？\n働き過ぎはよくないよ", 4, 112701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3988, 102, 0, 2, "あんたが近くにいると\n仕事の疲れも\n和らぐんだよね～", 8, 112701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3989, 1, 0, 2, "はぁ…もう起きなきゃ\nつらい労働の\nはじまりだ～", 1, 112701, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3990, 2, 0, 2, "うぅ、お昼に\nあんパン食べ過ぎた…\n苦しい…眠い…", 1, 112701, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3991, 3, 0, 2, "明日は仕事ないし\nガッツリ夜ふかし\nしちゃうよ～", 1, 112701, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3993, 2, 0, 3, "猫みたいに\n丸くなっちゃうよ～\nあたしリスだけどね", 1, 112701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3994, 3, 0, 3, "ふかふか～\nあたしの尻尾と\n互角とはやるね～", 1, 112701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3995, 4, 0, 3, "あんパンの香りの\nアロマとか\nあればいいのにな～", 1, 112701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3996, 5, 0, 3, "これは危険な\nあたたかさだね～\n労働意欲が\nなくなるよ～", 1, 112701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3998, 7, 0, 3, "いっぱい働いたから\nいっぱい食べても\nカロリー０だよ", 1, 112701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3999, 8, 0, 3, "あたしだってたまには\n本くらい読むって\n全然似合わないけどね", 1, 112701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4001, 103, 0, 1, "あたしの作った\nオリジナルたい焼き…\nあいつ、\n喜んでくれるかにゃ？", 12, 104601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4002, 103, 0, 2, "あ、お帰りにゃー♪\nえへへ…キミに\nお帰りって言えるのが\nなんかうれしいにゃ♪", 12, 104601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4003, 1, 0, 1, "フロンティア\nスピリッツを\nお見せするべ！", 1, 112801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4004, 2, 0, 1, "この鎧を着ると\n気が引き締まる\n感じがするべ", 1, 112801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4005, 3, 0, 1, "搾りたての牛乳飲んで\n今日も一日\nがんばるべ！", 1, 112801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4006, 4, 0, 1, "みんなに牧場の\n乳製品を\n味わってもらいてぇべ", 1, 112801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4007, 5, 0, 1, "ここはいいとこだべ\nうちの動物たちも\n連れてきてやりてぇな", 1, 112801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4008, 101, 0, 1, "みんなオラより\n若ぇのに働いてて\n立派だべな", 4, 112801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4009, 102, 0, 1, "牧場もギルドの\nみんなもオラが\nまとめて面倒見るべ！", 8, 112801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4010, 1, 0, 2, "さあ、今日も\n忙しくなるべよ\n気合入れてくべ！", 1, 112801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4011, 2, 0, 2, "お、あんちゃん\n丁度いいとこに！\n一緒に街に\n行商しに行くべ！", 1, 112801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4012, 3, 0, 2, "牧場仕事で培った力が\n戦いでも役立つなら\n一石二鳥だべさ！", 1, 112801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4013, 4, 0, 2, "疲れた時は牧場産の\nミルク菓子で\n糖分補給だべ", 1, 112801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4014, 101, 0, 2, "これ、牧場の\n新製品だべ\n一番最初に味見\nしてほしいべさ", 4, 112801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4015, 102, 0, 2, "働きづめはよくねぇベ\n膝枕してやっから\nこっち来るべさ", 8, 112801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4016, 1, 0, 2, "ふぁあ…\n酪農家の朝は\n早ぇんだべ…", 1, 112801, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4017, 2, 0, 2, "ごはんの時間だべ！\n腹が減っては酪農も\n戦いもできねえべ！", 1, 112801, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4018, 3, 0, 2, "そろそろ寝るべ！\n健全なお笑いは\n健全な肉体に宿るべ！", 1, 112801, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4020, 2, 0, 3, "牧草みたいに\nふっかふかだべ～", 1, 112801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4021, 3, 0, 3, "この子、目の感じが\nエリザベスに似てるべ", 1, 112801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4022, 4, 0, 3, "いい匂いだべ～\n疲れた体に\nしみるべな～", 1, 112801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4023, 5, 0, 3, "あったけぇな～\n牧場にも\n置いてみっかな～", 1, 112801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4025, 7, 0, 3, "うまいべ！\n生産者のこだわりを\n感じるべさ", 1, 112801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4026, 8, 0, 3, "酪農も戦いも\nまずは知識だべ！", 1, 112801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4028, 1, 0, 1, "ぴょん、ぴょん♪\nこの服のおかげで、\n体が軽いなぁ…♪", 1, 113001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4029, 2, 0, 1, "今の内に部屋の\n掃除をしておこっと…\nみんな喜んで\nくれるといいなぁ", 1, 113001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4030, 3, 0, 1, "耳と尻尾…この服、\n本当にウサギさん\nみたいだなぁ", 1, 113001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4031, 4, 0, 1, "なんだか先輩の\n気配がするような…\n気のせいかな？", 1, 113001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4032, 5, 0, 1, "ウサギさんの真似を\nしたら、存在感が薄い\nとか言われないかな？\nア、アユミだぴょん♪", 1, 113001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4033, 101, 0, 1, "えへへ…\nいつもとは違う先輩が\nたくさん見られて、\nうれしいな♪", 4, 113001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4034, 102, 0, 1, "本の中の世界で先輩と\n旅をするなんて…\n幸せすぎて、罰が\n当たりそうだよぉ…", 8, 113001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4035, 1, 0, 2, "じ～…はっ！？\nす、すみません！\n話しかけてもらえる\nなんて思ってなくて！", 1, 113001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4036, 2, 0, 2, "先輩がすぐそばに…\nし、幸せすぎて\n倒れそうですぅ！", 1, 113001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4037, 3, 0, 2, "え、えっと…あの…！\nうう…いざ先輩と\n向き合うと\n緊張しちゃうよぉ…", 1, 113001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4038, 4, 0, 2, "はうっ！？\nしょ、正面から見る\n先輩は、私にとって\n神々しすぎますぅ…！", 1, 113001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4039, 101, 0, 2, "先輩…その…\nい、一緒にご飯でも\nどうでひゅか！\nうう…またかんだぁ…", 4, 113001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4040, 102, 0, 2, "せ、先輩…\nハーブティーを\n入れたんですが、\n一緒にどうですか…？", 8, 113001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4041, 1, 0, 2, "せ、先輩！\nおはようございます！\nえへへ、練習通りに\nバッチリ言えたぁ♪", 1, 113001, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4042, 2, 0, 2, "あ、もうお昼ですね\n時計を持ってて\n良かったです", 1, 113001, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4043, 3, 0, 2, "もうこんな時間…\nお休みする\n準備をしちゃいますね", 1, 113001, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4045, 2, 0, 3, "あ…ちょっとだけ\n先輩の匂いがする…\nえへへ、幸せだなぁ♪", 1, 113001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4046, 3, 0, 3, "…私もウサギさんの\nぬいぐるみとして、\n先輩のそばに…はっ！\n私ったら何を…！", 1, 113001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4047, 4, 0, 3, "あ、いい香り…\n先輩もこういうのが\n好きなのかな？", 1, 113001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4048, 5, 0, 3, "先輩が隣に\n居たら、もっと\n暖かいのになぁ…♪", 1, 113001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4050, 7, 0, 3, "おいしい…でも、\n先輩と一緒に\n食べたら、もっと\nおいしいだろうな…", 1, 113001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4051, 8, 0, 3, "ウサギさんって、穴を\n掘ったりするんだ…\n私も、隠れたいときに\n掘ってみようかな？", 1, 113001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4053, 1, 0, 1, "ネビアさまのように\n普段からパタパタと\n飛んでみるのも…", 1, 180501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4054, 2, 0, 1, "ご立派になられた\n主さまと並べるように\nもっと精進しなくては", 1, 180501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4055, 3, 0, 1, "月々の食費が\n増えてしまいました\n申し訳ございません…", 1, 180501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4056, 4, 0, 1, "ワンワンのほうの\n主さまは、この姿に\n驚くでしょうか…？", 1, 180501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4057, 5, 0, 1, "主さまと離れていても\n主さまとの絆は\n胸に感じております…", 1, 180501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4058, 101, 0, 1, "このギルドハウスは\nみなさまの帰る場所…\nわたくしが守ります", 4, 180501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4059, 102, 0, 1, "どんな未来が\n待ち受けようとも\n主さまと添い遂げます", 8, 180501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4060, 1, 0, 2, "おでかけですか？\nではわたくしも\nお供いたします", 1, 180501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4061, 2, 0, 2, "もう二度と主さまと\n離れたくはありません\nいつ何時も…", 1, 180501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4062, 3, 0, 2, "なにかお困りごと\nでございますか？\nわたくしに何なりと…", 1, 180501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4063, 4, 0, 2, "どうぞゆっくりと\n心ゆくまでくつろいで\nくださいまし、主さま", 1, 180501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4064, 101, 0, 2, "この羽で主さまを\nどこまでも遠く…\n高くへと導きます", 4, 180501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4065, 102, 0, 2, "主さま、主さま…♪\nおそばにいられるのが\nうれしくて、つい…", 8, 180501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4066, 1, 0, 2, "おはようございます\n今日も一日、主さまの\nサポートをいたします", 1, 180501, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4067, 2, 0, 2, "今日も頑張っている\n主さまに、スタンプを\nもう一つ…ポン！", 1, 180501, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4068, 3, 0, 2, "寝付けないのでしたら\nわたくしが子守唄を\n歌ってさしあげます", 1, 180501, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4070, 2, 0, 3, "ふかふか…\nまるで主さまのような\n柔らかさでございます", 1, 180501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4071, 3, 0, 3, "あなたも一緒に\n主さまと遊びますか？\n至福の時間ですよ♪", 1, 180501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4072, 4, 0, 3, "主さまの優しくて\nくすぐったい匂いが\nたまらなく恋しいです", 1, 180501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4073, 5, 0, 3, "主さまの心と\n繋がったときのような\n温もりを感じます", 1, 180501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4075, 7, 0, 3, "いけませんっ…！\n元の姿に戻ると、また\n食べてしまうのに…！", 1, 180501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4076, 8, 0, 3, "主さまに読み聞かせる\n絵本は…妖精が主役の\n物語にいたしましょう", 1, 180501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4078, 103, 0, 1, "…彼のこと弟みたいに\n思ってきたのに…私、\nどうしたらいいの…？", 12, 104801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4079, 103, 0, 2, "よ、予定がなかったら\nでいいんだけど…\nその、ふ、二人で\n訓練とか…どうかな？", 12, 104801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4080, 1, 0, 1, "おめかしもしたし\nあとはお兄ちゃんを\n待つだけですね～♪", 1, 112901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4081, 2, 0, 1, "お兄ちゃんの身の回り\nをお片付け～、これぞ\nデキる妹アピール！", 1, 112901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4082, 3, 0, 1, "あ、衣装がほつれてる\nこれくらいなら自分で\n繕えますね…！", 1, 112901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4083, 4, 0, 1, "この格好で歩いてると\n見慣れた景色も\n違うものに見えますね", 1, 112901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4084, 5, 0, 1, "勉強もちゃんとします\n…でも、一人だと\n集中力がもちません～", 1, 112901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4085, 101, 0, 1, "お兄ちゃんを大好きな\n妹が待ってますよ～\n…まだですかね～", 4, 112901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4086, 102, 0, 1, "お兄ちゃんのために\n掃除洗濯をする私って\nお嫁さんみたい…\n…えへへ～", 8, 112901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4087, 1, 0, 2, "おに～いちゃん！\n縫い物、一緒に\nやりましょうよ！", 1, 112901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4088, 2, 0, 2, "かわいい妹を\n愛でたくなりました？\n思う存分、どうぞ♪", 1, 112901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4089, 3, 0, 2, "わっ、ビックリした～\nもう、お兄ちゃんてば\nイタズラっ子ですね～", 1, 112901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4090, 4, 0, 2, "一緒にお買い物\n行きませんか？\n妹とデートですよっ♪", 1, 112901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4091, 101, 0, 2, "お兄ちゃん…頭を\n撫でてくれませんか？\nえへへ、これが一番♪", 4, 112901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4092, 102, 0, 2, "お兄ちゃん大好き♪\n何度でも言いますよ！\nお兄ちゃん大好きっ♪", 8, 112901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4093, 1, 0, 2, "おはよ～お兄ちゃん！\n妹モーニングコールの\n時間ですよっ♪", 1, 112901, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4094, 2, 0, 2, "そろそろ休憩ですね！\n甘いお菓子で\n元気回復しましょ～！", 1, 112901, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4095, 3, 0, 2, "夜更かし厳禁！\nでも、もう少しだけ\nお話ししたいなぁ～", 1, 112901, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4097, 2, 0, 3, "ふっかふかですよ！\nお兄ちゃんと一緒に\n座りたいなぁ…♪", 1, 112901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4098, 3, 0, 3, "このぬいぐるみの\nかわいさ…う～ん\n負けられません…！", 1, 112901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4099, 4, 0, 3, "香りにも気をつかって\nパーフェクトな妹に\nなっちゃいますから！", 1, 112901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4100, 5, 0, 3, "はぁ～ぬくぬくです～\nお兄ちゃんと一緒に\n温まりたいなぁ…♪", 1, 112901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4102, 7, 0, 3, "これは止まりませんね\nもぐもぐ…いくらでも\n食べられちゃいます", 1, 112901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4103, 8, 0, 3, "これ、デザインの\n参考になりそう！\n私にも作れるかなぁ", 1, 112901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4105, 1, 0, 1, "退屈です…\nなにか楽しいことは\nないですか？", 1, 106601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4106, 2, 0, 1, "ギャングの仕事は\n今日はお休みです\nのんびりするですよ", 1, 106601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4107, 3, 0, 1, "ひうっ！　あたしの\n尻尾なんて食べても\nおいしくないですよ？", 1, 106601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4108, 4, 0, 1, "ふう…どうやら\n例の件はまだボスに\nバレてないですね…", 1, 106601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4109, 5, 0, 1, "下克上…いい言葉です\nって、ボスに聞かれて\nないですよね…？", 1, 106601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4110, 101, 0, 1, "いつか絶対ビッグに\nなってやるです！\nあたしを信じるです！", 4, 106601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4111, 102, 0, 1, "あたしが覚醒したら\nきっと誰よりも\n強くなるはずです！", 8, 106601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4112, 1, 0, 2, "なんですか？\n貢ぎ物なら\nいつでも歓迎ですよ！", 1, 106601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4113, 2, 0, 2, "あたしだって\nやる時はやるですよ！\nその時がこないだけ！", 1, 106601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4114, 3, 0, 2, "しりとり勝負ですか？\nいい度胸ですね、\nかかってくるです！", 1, 106601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4115, 4, 0, 2, "お兄さんは\nヒマそうですね…\nうらやましいです", 1, 106601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4116, 101, 0, 2, "かまってほしいです？\nまったく…いいですよ\nしょうがないですね", 4, 106601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4117, 102, 0, 2, "お兄さんのことは\nこのあたしが\n守ってやるですよ！", 8, 106601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4118, 1, 0, 2, "デキるギャングの\n朝は早いのです…\nふぁあ…", 1, 106601, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4119, 2, 0, 2, "お腹がすいたですね\nなにか食べにいくです\nお兄さんのおごりで！", 1, 106601, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4120, 3, 0, 2, "ふぁあ…むにゅう…\nまだ起きてるですよぉ", 1, 106601, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4122, 2, 0, 3, "はぁ～…\nもうここから一歩も\n動きたくないです", 1, 106601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4123, 3, 0, 3, "なかなかいいモフりを\nしてやがるです…\n舎弟にしてやるですよ", 1, 106601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4124, 4, 0, 3, "なんかおしゃれな\n匂いがするですね…", 1, 106601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4125, 5, 0, 3, "ふふーん！\nあたしのブレスの方が\n火力は上ですね！", 1, 106601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4127, 7, 0, 3, "あたしに貢ぎ物とは\nよく分かってるですね\n食べてやるですよ", 1, 106601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4128, 8, 0, 3, "ふーむ…\nもっと楽しい本は\nないですかね？", 1, 106601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4130, 1, 0, 1, "海に眠る秘宝…\n心躍る響きだ！\n早く見つけたい…！", 1, 113201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4131, 2, 0, 1, "うう～、暑いぃ…\nかき氷を食べて\nすっきりしたいぃ…", 1, 113201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4132, 3, 0, 1, "アターック！\nいや、もっとこう…\nアタァーック！", 1, 113201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4133, 4, 0, 1, "スイカ割りで\n目を回さない\nコツはなんだろうか…", 1, 113201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4134, 5, 0, 1, "せっかく海に来たのだ\n泳ぎも上手に\nなりたいものだな", 1, 113201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4135, 101, 0, 1, "ふっふっふ…\n疼く…疼くぞ！\n日焼けの痕が…", 4, 113201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4136, 102, 0, 1, "シグルドと私は\n来世でも共にある…\nつまり我らはずっと…\nはわわ…！", 8, 113201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4137, 1, 0, 2, "シグルド、私と共に\n日光浴に行かないか？\n光の力を集めよう！", 1, 113201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4138, 2, 0, 2, "この格好だからできる\n必殺技を考えている\n何か案はないか？", 1, 113201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4139, 3, 0, 2, "シグルド～！\nスイカを貰ったから\n一緒に食べよう！", 1, 113201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4140, 4, 0, 2, "こう日差しが強いと\n日焼けしそうだな\n跡になってないか…？", 1, 113201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4141, 101, 0, 2, "我らの夏の黙示録\nしっかりと記録\nしておかねばな", 4, 113201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4142, 102, 0, 2, "ちゃんと日焼けどめを\n塗ってもらうべき\nだったかな…？\nシ、シグルドに…！", 8, 113201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4143, 1, 0, 2, "朝だぞシグルド！\nさぁ、日光を浴びて\n光の加護を得るのだ！", 1, 113201, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4144, 2, 0, 2, "もう昼食の時間か…\n私の魔力も減ったし\n早く補給しなければ", 1, 113201, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4145, 3, 0, 2, "夜でも我らの活動は\n終わることはない！\nさぁ準備を…ふわぁ…", 1, 113201, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4147, 2, 0, 3, "海で遊んでいると\n疲れてしまうからな\n少し休まるとしよう", 1, 113201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4148, 3, 0, 3, "えへへ～、君の名前は\n何にしまちょうかね♪\n…はっ、見たのか！？", 1, 113201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4149, 4, 0, 3, "心地よい香りで\n我が精神も安らぐ…", 1, 113201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4150, 5, 0, 3, "別に寒くなどないぞ！\n寒くないが、ちょっと\n暖まりたくなってな！", 1, 113201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4152, 7, 0, 3, "もぐ…食べ過ぎた分は\n魔力に変換されるのだ\n…たぶん", 1, 113201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4153, 8, 0, 3, "…なな、なんだ！？\nシグルドはこういう\n趣味なのか…！？", 1, 113201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4155, 10, 0, 3, "むむ、この乗り心地…\nいざゆかん、\nシードラゴンよ！", 1, 113201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4156, 11, 0, 3, "疾風の冥姫の名に\nふさわしき\n深海の玉座よ…\nふああ…柔らかい～", 1, 113201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4157, 1, 0, 1, "この姿でいると\nわたしもお姫様に\nなれた気がする…♪", 1, 180201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4158, 2, 0, 1, "お腹空いてきちゃった\nまずは買い出しに…\n…変身解かなくちゃ！", 1, 180201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4159, 3, 0, 1, "胸のところが空いてて\nスースーするの\nまだ慣れないな…", 1, 180201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4160, 4, 0, 1, "治癒魔法に使ってた\n魔力を攻撃魔法に…\n頭の中でも練習練習", 1, 180201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4161, 5, 0, 1, "何もできなかった\nわたしから、少しは\n変われたのかな…？", 1, 180201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4162, 101, 0, 1, "騎士クン…\n今どこにいるの…？\n…会いたいな", 4, 180201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4163, 102, 0, 1, "離れてても分かるよ…\n温かい力で騎士クンに\n守られてるの", 8, 180201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4164, 1, 0, 2, "わたしが戦えてるのは\n騎士クンのおかげだよ\nいつもありがとう！", 1, 180201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4165, 2, 0, 2, "お腹空いてそう…？\n…内緒で作ったおやつ\n騎士クンも食べる？", 1, 180201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4166, 3, 0, 2, "ひゃうっ！　え！？\nせ、背中はダメ～！\n言ってから触って～！", 1, 180201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4167, 4, 0, 2, "この格好、どうかな？\nえ？　似合ってる…？\n本当？　…うれしい！", 1, 180201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4168, 101, 0, 2, "かわいい？　そうだね\nひらひらが…わたし？\nええっ、そ、そんな！", 4, 180201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4169, 102, 0, 2, "騎士クン、これからも\nずっと…わたしの\nそばにいてください", 8, 180201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4170, 1, 0, 2, "朝からこんなに\n食べる日が来るなんて\n思ってなかったよ…", 1, 180201, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4171, 2, 0, 2, "お昼を作らなくちゃ\n騎士クン、一緒に\nお料理してみる？", 1, 180201, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4172, 3, 0, 2, "騎士クン、もう眠い？\n眠るまで、わたしが\nずっとそばにいるね", 1, 180201, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4174, 2, 0, 3, "攻撃参加するからこそ\n適度に体を休めるのは\n大切だよね！", 1, 180201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4175, 3, 0, 3, "かわいい…騎士クンと\nシェフィちゃんにも\nプレゼントしたいな", 1, 180201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4176, 4, 0, 3, "いい香り～…\nあれ？　お腹が空いて\nきちゃった…！？", 1, 180201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4177, 5, 0, 3, "温かい…\n騎士クンがくれる\n力みたい…", 1, 180201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4179, 7, 0, 3, "ぱくっ、\nあむ、はむっ…\nううぅ～…やっぱり\n手が止まらないよぉ～", 1, 180201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4180, 8, 0, 3, "新しく覚えたいな…\nみんなで食べられる\n大皿向けの料理♪", 1, 180201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4182, 103, 0, 1, "お姉ちゃんの愛\nこのままじゃ\nあふれちゃうよ…♪", 12, 104901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4183, 103, 0, 2, "弟くん♪\n弟くんっ♪\nおっとうっとくんっ♪", 12, 104901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4184, 1, 0, 1, "泳ぐのもいいけど\n今日は砂浜で\nのんびりしようかね", 1, 113101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4185, 2, 0, 1, "さてと\nちょっくらひと泳ぎ\n行ってくるかね", 1, 113101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4186, 3, 0, 1, "あっついねぇ…\nうちわがないと\nしんどいったらないよ", 1, 113101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4187, 4, 0, 1, "冷たすぎる飲み物で\n体を冷やさないよう\n気をつけないと…", 1, 113101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4188, 5, 0, 1, "夏はあっという間だし\n悔いがないように\n楽しまないとね", 1, 113101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4189, 101, 0, 1, "きっとこの夏は\nアタシにとって大事な\n想い出になるね…", 4, 113101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4190, 102, 0, 1, "…前に比べたら\nアタシたちの距離も\n近づいたみたいだね", 8, 113101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4191, 1, 0, 2, "もし暇なら\n今日も笹舟釣り\n一緒にどうだい？", 1, 113101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4192, 2, 0, 2, "お前さん、釣りに\n興味が湧いたなら今度\n道具を見に行こうよ", 1, 113101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4193, 3, 0, 2, "今から砂浜に行くんだ\nお前さんも一緒に\n散歩するかい？", 1, 113101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4194, 4, 0, 2, "え、日焼け跡？\nおや…本当だ\nすぐ焼けちまうねえ", 1, 113101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4195, 101, 0, 2, "ひゃっ！？\nちょっと！\n日焼け跡をいきなり\n触らないでおくれ！", 4, 113101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4196, 102, 0, 2, "お前さんは、その…\nアタシと一緒にいて\n楽しかったかい？", 8, 113101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4197, 1, 0, 2, "おはようさん\n今日も砂浜に行って\nゆっくりしようか", 1, 113101, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4198, 2, 0, 2, "お昼か…\nそうだ、よかったら\n刺身定食、作ろうか？", 1, 113101, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4199, 3, 0, 2, "夜の空を見てるとさ\nなつかしい気持ちに\nなるんだよね", 1, 113101, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4201, 2, 0, 3, "適度に休憩して\n夏バテしないよう\n気をつけないとね", 1, 113101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4202, 3, 0, 3, "あんたたちも\n海を楽しみたいって\n思っているのかね", 1, 113101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4203, 4, 0, 3, "ん～…香りでも\nバカンスらしさを\n楽しめるもんだね", 1, 113101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4204, 5, 0, 3, "こういう格好だと\n少し冷えちまうんだよ", 1, 113101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4206, 7, 0, 3, "もぐもぐもぐ…\n遊んだ分、お腹が\n空いちまってねぇ", 1, 113101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4207, 8, 0, 3, "…ん、なんだいこの本\n催眠…うーん読めない\nナナカに聞いてみるか", 1, 113101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4209, 10, 0, 3, "へえ…\nなかなかいい\n乗り心地じゃないか", 1, 113101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4210, 11, 0, 3, "そうそうハマグリって\n竿でも釣れるんだよ\n知ってたかい？", 1, 113101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4211, 1, 0, 1, "海を満喫しなきゃね\nちょっとひと泳ぎ\n行ってこようかな？", 1, 113301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4212, 2, 0, 1, "う～ん…♪\nいつもと違った\n開放感～\nなんちゃって！", 1, 113301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4213, 3, 0, 1, "今のナナカちゃんは\nたぶん水属性！", 1, 113301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4214, 4, 0, 1, "水着もバッチリ！\nサイカワサイツヨ\nナナカちゃ～ん☆", 1, 113301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4215, 5, 0, 1, "んっふっふ\nドキ！　水着だらけの\nギルドハウス！\nとかどうかな？", 1, 113301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4216, 101, 0, 1, "そんなに見られると\nちょ～っと\n恥ずかしいかも…\nなんてねなんてね！", 4, 113301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4217, 102, 0, 1, "ちょっとくらい\nドキッとしてくれても\nいいのにな～、もう…", 8, 113301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4218, 1, 0, 2, "水着魔法少女の\n相方がほしいなぁ～…\nね、君やってみない？", 1, 113301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4219, 2, 0, 2, "いつもの格好と同じ？\nも～、わかってない！\nセクスィー度が違う！", 1, 113301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4220, 3, 0, 2, "どうかなどうかな？\nこのひらひらのとこが\nかわいーかなー\nとか思うんだけど！", 1, 113301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4221, 4, 0, 2, "せっかくだから\nギャルゲーイベント\nごっことか\nやってみる？", 1, 113301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4222, 101, 0, 2, "ナナカちゃんの\n好感度を上げたい？\nならおでかけだー！", 4, 113301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4223, 102, 0, 2, "おうちで水着って\nなんだかコスプレ\nみたいで楽しいかも♪", 8, 113301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4224, 1, 0, 2, "おっはよーう！\n今日はバカンス日和！\n海へゴーですぞ～♪", 1, 113301, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4225, 2, 0, 2, "お昼ご飯どうしよ～…\nあ、ナナカちゃんと\nどっか食べに行く？", 1, 113301, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4226, 3, 0, 2, "夜は本を読むのが\n捗りまくりですな～！\n今日はなに読もっかな", 1, 113301, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4228, 2, 0, 3, "次のイベントもあるし\nちょっと休んで体力を\n回復しなきゃね～", 1, 113301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4229, 3, 0, 3, "こういうマスコット\n私にもいたらなぁ\nいっそ作っちゃう…？", 1, 113301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4230, 4, 0, 3, "水着魔法少女に\n似合う香りってのも\n考えてみよっかな♪", 1, 113301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4231, 5, 0, 3, "夜になると急に冷えて\n肌寒いんだよね～…", 1, 113301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4233, 7, 0, 3, "あんまり食べると\nお腹が出て…でも…\nうう、止まらない～！", 1, 113301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4234, 8, 0, 3, "この催眠魔法の本\n誰にもばれないように\n保管しとかなきゃね…", 1, 113301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4236, 10, 0, 3, "ゴーゴーゴーゴーゴー\nナナカちゃーん♪", 1, 113301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4237, 11, 0, 3, "ナナカちゃんの\n誕生～\nなんちゃって", 1, 113301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4238, 1, 0, 1, "ファイトファイト☆\n一生懸命\n応援するね！", 1, 113401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4239, 2, 0, 1, "みんながんばってる！\n私も応援でみんなを\n盛り上げなきゃ！", 1, 113401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4240, 3, 0, 1, "もし元気のない人が\nいたら…応援で少し\nでも元気にしたいな！", 1, 113401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4241, 4, 0, 1, "がんばれ～☆\nよし、発声練習OK！\n応援は、声も大事！", 1, 113401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4242, 5, 0, 1, "せっかく水着なんだし\n泳いだり潜ったりして\n遊びたいな～！", 1, 113401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4243, 101, 0, 1, "チアリーダーとして\nキミを精一杯\n応援したいな…！", 4, 113401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4244, 102, 0, 1, "キミへの応援\nちゃんと\n届いてるかな…", 8, 113401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4245, 1, 0, 2, "えへへ…今ね、\nキミへの応援歌を\n考えてたのだ～♪", 1, 113401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4246, 2, 0, 2, "どうしたの？\n元気がないなら\n私が応援するよ☆", 1, 113401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4247, 3, 0, 2, "がんばったあとは休憩\nしなきゃね！　私と\n一緒にお昼寝する？", 1, 113401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4248, 4, 0, 2, "シオリンとおそろいの\n水着で応援…\nしてみたいなぁ～", 1, 113401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4249, 101, 0, 2, "次はなにして遊ぶ？\nキミがしたいことを\n教えてよ！", 4, 113401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4250, 102, 0, 2, "キミを応援\nすることができて…\n私、すごく幸せ…", 8, 113401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4251, 1, 0, 2, "キミって早起き\nなんだね！　私も\n見習わなきゃ！", 1, 113401, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4252, 2, 0, 2, "ちょっとお疲れかな？\nそれじゃあ\n応援してあげるね♪", 1, 113401, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4253, 3, 0, 2, "遅い時間まで\nがんばってえらいね！\nでも無理はダメだよ♪", 1, 113401, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4255, 2, 0, 3, "ちょっとだけ休憩…\n…あう、座り心地が\n良すぎて…眠気が…", 1, 113401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4256, 3, 0, 3, "ここにいるみんなを\n私の応援でもっと\n元気にするのだ～！", 1, 113401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4257, 4, 0, 3, "元気の出る香り～♪\nこれもうまく応援に\n活かせないかな～？", 1, 113401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4258, 5, 0, 3, "ぽかぽかあったまって\n元気をチャージ\nするのだ～☆", 1, 113401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4260, 7, 0, 3, "いっぱい食べて、\nみんなを応援する\nパワーをつけなきゃ！", 1, 113401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4261, 8, 0, 3, "みんなを元気にする\n方法は…どこかに\n書いてないかなぁ…？", 1, 113401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4263, 10, 0, 3, "わぁ、この子\n怪獣さんみたいで\nかわいいね～♪", 1, 113401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4264, 11, 0, 3, "水着でこれに\n乗ってると海にいる\n気分になれるね！", 1, 113401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4265, 1, 0, 1, "すごく忙しいけど\nとても充実した気分\nこれが青春なのかしら", 1, 113501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4266, 2, 0, 1, "森の静かな雰囲気も\n海の開放的な空気も\nどちらも素敵ね～", 1, 113501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4267, 3, 0, 1, "汚れたお洗濯物は\nみんなが頑張った証\nなんだか愛おしいわ", 1, 113501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4268, 4, 0, 1, "みんなを見てると\n何だか私も\n走りたくなっちゃうわ", 1, 113501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4269, 5, 0, 1, "せっかくの水着だし\n泳ぐ練習でも\nしてみようかしら", 1, 113501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4270, 101, 0, 1, "みんな～ファイト～！\n応援するのも大事よね\n練習しておかなきゃ♪", 4, 113501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4271, 102, 0, 1, "先生に教えられること\nまだまだい～っぱいね\n次は何がいいかしら♪", 8, 113501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4272, 1, 0, 2, "今日も暑くなりそうね\nこまめな水分補給を\n忘れないでね", 1, 113501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4273, 2, 0, 2, "手伝ってくれるの？\nありがとう♪\n先生、頼りにしてるわ", 1, 113501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4274, 3, 0, 2, "休憩はこまめに\nとりましょうね\n無理は厳禁よ", 1, 113501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4275, 4, 0, 2, "海の遊びって\nどんなのがあるの？\n先生に教えてね", 1, 113501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4276, 101, 0, 2, "水着は初めてだけど\nどう？　変じゃない？\nふふ、ありがとう", 4, 113501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4277, 102, 0, 2, "あなたと過ごした\nこの夏は、きっと…\n忘れられない思い出ね", 8, 113501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4278, 1, 0, 2, "おはよう～\n朝練前にちゃんと\nごはん食べましょうね", 1, 113501, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4279, 2, 0, 2, "お弁当作ってきたの\n栄養いっぱい摂って\n午後も頑張ろうね", 1, 113501, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4280, 3, 0, 2, "もうお休みしなきゃね\n先生が子守唄を歌って\nあげましょうか？", 1, 113501, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4282, 2, 0, 3, "ふふ、いい気持ちねぇ\n先生、少しだけ\n休憩しちゃおうかしら", 1, 113501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4283, 3, 0, 3, "あらかわいい♪\nチームにもこういう\nマスコットが欲しいわ", 1, 113501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4284, 4, 0, 3, "あら、海の香り？\n波の音まで聞こえて\nきちゃいそうだわ", 1, 113501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4285, 5, 0, 3, "うふふ\nこれなら濡れた水着を\n乾かすのも簡単ね♪", 1, 113501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4287, 7, 0, 3, "これ、美味しいわね♪\nでも、食べ過ぎは\n禁物よ", 1, 113501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4288, 8, 0, 3, "先生だって日々勉強よ\n新しいことを知るのは\nとても楽しいわ♪", 1, 113501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4290, 10, 0, 3, "あらあら、楽しいわね\nみんなもきっと\nおおはしゃぎね", 1, 113501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4291, 11, 0, 3, "ふふ、これで\n夏らしい気分を\n満喫するわよ♪", 1, 113501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4292, 103, 0, 1, "サンドイッチも\n作ったし、\nお兄ちゃんのお出迎え\n準備は完璧だね♪", 12, 102301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4293, 103, 0, 2, "えへへ、お兄ちゃんと\nぷうきちが居てくれる\nだけで、なんだか\nうれしくなっちゃう♪", 12, 102301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4294, 1, 0, 1, "少し赤くなっているな\n日焼けなんてしたのは\nいつ以来だろう", 1, 113601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4295, 2, 0, 1, "この格好で\n過ごすのは\n少し勇気がいるな…", 1, 113601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4296, 3, 0, 1, "暑いのは平気だが\n肌が外気に触れるの\nにはまだ慣れないな…", 1, 113601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4297, 4, 0, 1, "この格好でも\n堂々としていれば\n恥ずかしくはない…\nのかな？", 1, 113601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4298, 5, 0, 1, "おっと…鎧姿の時と\n重心が違うせいか\nちょっと不安定だな", 1, 113601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4299, 101, 0, 1, "この水着…\n私に似合って\nいるのだろうか…？\n少年には聞きづらいな", 4, 113601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4300, 102, 0, 1, "少年との\n海のバカンス…\nまた行きたい\nものだな", 8, 113601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4301, 1, 0, 2, "あまり見るのは\nよしてくれ…\n恥ずかしいじゃ\nないか…", 1, 113601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4302, 2, 0, 2, "どうかしたのかい？\n私でよければ\n話し相手くらいには\nなろうじゃないか", 1, 113601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4303, 3, 0, 2, "ひゃっ！\nす、すまない\nちょっと過敏に\nなっているようだ", 1, 113601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4304, 4, 0, 2, "…っくしゅん！\nすまない\n薄着には慣れて\nないものでね", 1, 113601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4305, 101, 0, 2, "まあ、少年になら\n見られても…\nい、いや！\nなんでもない！", 4, 113601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4306, 102, 0, 2, "私がこうして\nゆっくりできるのは\n少年がそばにいて\nくれるからだよ", 8, 113601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4307, 1, 0, 2, "この時間なら\nビーチにも人は\n少ないだろうね…\n遊びに行くかい？", 1, 113601, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4308, 2, 0, 2, "昼食のお誘いかい？\nいいよ、付き合おう", 1, 113601, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4309, 3, 0, 2, "もう夜か…\n一日が経つのは\nはやいものだね", 1, 113601, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4311, 2, 0, 3, "これは楽でいいな…\nいつもは鎧姿で\n家具を傷つけないか\n気を使っているからね", 1, 113601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4312, 3, 0, 3, "うん…ふわふわの\n感触が\nよくわかるな…", 1, 113601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4313, 4, 0, 3, "いつもは\n鎧に匂いが\n移ってしまうのが\n悩みなんだよ…", 1, 113601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4314, 5, 0, 3, "うーむ…\n涼しいのに暖かい\nなんだか奇妙な気分だ", 1, 113601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4316, 7, 0, 3, "兜を気にせず\n食べられるのは\n楽でいいな…", 1, 113601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4317, 8, 0, 3, "ふむ…鎧がないと\nページがめくりやすい", 1, 113601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4319, 10, 0, 3, "身軽でいいな…\n鎧を着ていると\n騎乗訓練の時\n馬に申し訳がなくてね", 1, 113601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4320, 11, 0, 3, "貝をうまく使えば\n水中でも\n守りを固められそうだ", 1, 113601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4321, 1, 0, 1, "どこをアジトと\nつなげようかな～…\nこのへんにしよっと♪", 1, 106801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4322, 2, 0, 1, "ここってちょっと\n変わった建物だね\n妖精がいるでしょ？", 1, 106801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4323, 3, 0, 1, "色んな人を観察できて\n眼福眼福…♪", 1, 106801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4324, 4, 0, 1, "家具の配置変更なら\nアタシにおまかせあれ\n…家の形は変えない？", 1, 106801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4325, 5, 0, 1, "新メニューどうしよう\n思いついた人がいたら\nアタシに教えてね！", 1, 106801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4326, 101, 0, 1, "少年、早く帰って\nこないかな…\nよし、迎えに行こう！", 4, 106801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4327, 102, 0, 1, "結局またあちこち\n移動する日々だし…\n少年に会いたいな～", 8, 106801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4328, 1, 0, 2, "どしたの少年？\nお姉さんに何でも\n相談してみな～？", 1, 106801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4329, 2, 0, 2, "あれをああして…\nおや少年！　ごめんね\nすぐに気づけなくて！", 1, 106801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4330, 3, 0, 2, "何？　クリスが\nアタシを探してる？\n…いないって言って！", 1, 106801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4331, 4, 0, 2, "よければリノちゃんと\nシズルちゃんにも\n顔を見せてあげて", 1, 106801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4332, 101, 0, 2, "少年～！\nもうヘトヘトだよ～！\nお願い癒して～！", 4, 106801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4333, 102, 0, 2, "…アタシが素直に\n助けてって言えるのは\n少年だけだよ", 8, 106801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4334, 1, 0, 2, "朝だよ少年！\n爽やかな朝の\nクレープはいかが？", 1, 106801, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4335, 2, 0, 2, "お腹空いたな～\nお昼のクレープ\n少年は食べる？", 1, 106801, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4336, 3, 0, 2, "夜までクレープは\nやめとけ？　ごめん\nそれだけは無理…！", 1, 106801, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4338, 2, 0, 3, "アタシこんなに\nゆっくりしちゃって\nいいんだっけ～？", 1, 106801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4339, 3, 0, 3, "…これ\nネネカが変身\nしてないよね？", 1, 106801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4340, 4, 0, 3, "いい香りだ…\n解析完了\n今度生成しようっと♪", 1, 106801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4341, 5, 0, 3, "安心するな～…\nアジトに置けないから\n羨ましいよ", 1, 106801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4343, 7, 0, 3, "食べ物を消化するって\n仕組み、どんなものか\n気になる…", 1, 106801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4344, 8, 0, 3, "速読できるけど\n本を読む時くらい\n文字にじっくり\n浸かりたいんだよね…", 1, 106801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4346, 1, 0, 1, "エンジェルとして\nみんなを元気に…！\nアカリ、頑張ります♪", 1, 113701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4347, 2, 0, 1, "お姉ちゃんと一緒に\nお兄ちゃんを…えへへ\nまだかな、お兄ちゃん", 1, 113701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4348, 3, 0, 1, "エンジェルの音色を\n目指して、今日も\nサックスの練習です♪", 1, 113701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4349, 4, 0, 1, "お兄ちゃんとの特訓も\n頑張らないと…\n厳しくしてくださいね", 1, 113701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4350, 5, 0, 1, "かわいいエンジェルの\n格好、お兄ちゃんなら\n喜んでくれますよね♪", 1, 113701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4351, 101, 0, 1, "エンジェルの力で\nお兄ちゃんのハートも\nイチコロですよ♪", 4, 113701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4352, 102, 0, 1, "アカリが一番\n元気にしたいのは\nお兄ちゃんですよ♪", 8, 113701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4353, 1, 0, 2, "お兄ちゃん？\n触りたいなら…\nこことかどうですか♪", 1, 113701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4354, 2, 0, 2, "特訓の時間ですか？\nアカリ、楽しみに\nしてたんですよ♪", 1, 113701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4355, 3, 0, 2, "あ、お兄ちゃん！\nそろそろアカリと\n休憩…しませんか♪", 1, 113701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4356, 4, 0, 2, "この羽、触り心地\nいいですか？　あんっ\nくすぐったいですよぉ", 1, 113701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4357, 101, 0, 2, "お兄ちゃんも元気に\nなりたいんですか？\nアカリにお任せです♪", 4, 113701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4358, 102, 0, 2, "やんっ♪\nお兄ちゃんったら\nダイタンですね♪", 8, 113701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4359, 1, 0, 2, "お兄ちゃん、朝ですよ\nすっごく気持ちのいい\nお天気ですよ…♪", 1, 113701, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4360, 2, 0, 2, "お昼寝しませんか\nお兄ちゃん？\nアカリの膝枕で…♪", 1, 113701, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4361, 3, 0, 2, "夜ですね、お兄ちゃん\nアカリ、なんだか胸が\nドキドキしちゃいます", 1, 113701, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4363, 2, 0, 3, "フカフカですね…\n隣にお兄ちゃんが\nいてくれたらなぁ…♪", 1, 113701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4364, 3, 0, 3, "えへへ、アカリも\nこんな風にかわいく\nなりたいな～", 1, 113701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4365, 4, 0, 3, "お兄ちゃんが好きな\n匂いなのかな？\n参考にしますね！", 1, 113701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4366, 5, 0, 3, "体が火照ってきて\nなんだかアカリ…\nクラクラしちゃう…", 1, 113701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4368, 7, 0, 3, "はむ、んぐ…あっ\nほっぺについちゃった\nベトベトですよぉ～", 1, 113701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4369, 8, 0, 3, "エンジェルって色んな\n物語に出てきますよね\nもっと調べなくちゃ！", 1, 113701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4371, 103, 0, 1, "あの方の前では\n常に気高く\nありたいですわね", 12, 103201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4372, 103, 0, 2, "どんなに道に迷っても\nあなたさまの元には\n絶対に辿り着く\n自信がありますわ", 12, 103201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4373, 103, 0, 1, "あんたと一緒に\nいるときが一番楽しい\n一時かもしれないわ", 12, 102801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4374, 103, 0, 2, "いつかあんたと一緒に\nサレンディア救護院を\n切り盛り…なんてね♪", 12, 102801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4375, 1, 0, 1, "今日も\nエンジェルとして、\nみんなの幸せの\nために頑張らないと", 1, 113801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4376, 2, 0, 1, "せっかく羽が\nついてるのに\n飛べないのが残念だわ", 1, 113801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4377, 3, 0, 1, "この格好、子供たちに\n妙にウケがいいわね\nま、悪くないけど", 1, 113801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4378, 4, 0, 1, "や、やっぱりみんな\n注目してくるわね…\nエンジェルって珍しい\nからしかたないか", 1, 113801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4379, 5, 0, 1, "エンジェルになると\n人見知りがなおる！\n…わけないか", 1, 113801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4380, 101, 0, 1, "エンジェルの活動は\n大変だけど、自分が\n成長してるって\n実感がうれしいわね", 4, 113801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4381, 102, 0, 1, "もし誰かの願いを\nかなえられるなら\n私はアイツの…\nって何考えてるのよ！", 8, 113801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4382, 1, 0, 2, "何ボサっとしてるの！\nさあ、街に出て\n人助けするわよ！", 1, 113801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4383, 2, 0, 2, "エンジェリック\nアロー！\nなんてね…え、アンタ\nいつから見てたのよ！", 1, 113801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4384, 3, 0, 2, "コラ！\n羽は触らないで！\n壊れたらどうするのよ", 1, 113801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4385, 4, 0, 2, "エンジェルじゃなく\nても、アンタはいつも\n人助けしてるわよね\n…ちょっと尊敬するわ", 1, 113801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4386, 101, 0, 2, "この服なら他の子より\n私を見てくれるかも…\nって、な、何\n見てるのよ！", 4, 113801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4387, 102, 0, 2, "ね、ねえ、もし私が\n困ってたら、一番に\n駆けつけて…やっぱり\n何でもないわ！", 8, 113801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4388, 1, 0, 2, "おはよう…あ、あの、\nエンジェルとして\n町に行くんだけど…\n一緒に行かない？", 1, 113801, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4389, 2, 0, 2, "ふぅ、いいことした\n後のランチは\nおいしいわね", 1, 113801, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4390, 3, 0, 2, "もうこんな時間…\n夜更かししてないで\n早く寝なさいよね", 1, 113801, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4392, 2, 0, 3, "いい座り心地ね～\nまさに天にも昇る\n心地よさだわ～", 1, 113801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4393, 3, 0, 3, "ふふ、かわいい…\nあ、羽付けたら\nもっとかわいく\nなるんじゃないかな？", 1, 113801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4394, 4, 0, 3, "はぁ～落ち着くわ～\n回復アイテムみたいで\n癒やされちゃうわ～", 1, 113801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4395, 5, 0, 3, "わっ、とと…\n危ない、羽が\nこげちゃうところ\nだったわ", 1, 113801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4397, 7, 0, 3, "人の役に立つのって\nすごく大変だから、\nこれでライフ回復よ！", 1, 113801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4398, 8, 0, 3, "エンジェルに関する\n本はあるかしら…あ！\nこれ探してた攻略本！", 1, 113801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4400, 1, 0, 1, "ふっふっふ\n吸血鬼らしく\n驚かせてあげましょう", 1, 113901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4401, 2, 0, 1, "獲物はどこですか～！\nう～ん、もうちょっと\n迫力が欲しいですね", 1, 113901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4402, 3, 0, 1, "トリックオアトリート\n…返事がないので\nイタズラしておきます", 1, 113901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4403, 4, 0, 1, "ハロウィンですし\nたまには騎士さんでも\n誘ってあげますかね", 1, 113901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4404, 5, 0, 1, "マントは動きに\nメリハリがついて\nいいですね", 1, 113901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4405, 101, 0, 1, "色んな仮装が見られて\nやっぱりハロウィンは\n最高ですね", 4, 113901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4406, 102, 0, 1, "吸血鬼らしい行動って\n噛みついたり…いや\nそれは大胆過ぎです！", 8, 113901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4407, 1, 0, 2, "呼びましたか？\n手短にお願いしますよ\n忙しいんですから！", 1, 113901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4408, 2, 0, 2, "え？　衣装に穴が？\nありがとうございます\n…よく見てるんですね", 1, 113901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4409, 3, 0, 2, "ふわあ…吸血鬼の\n姿でも、眠い時は\n眠いんですよね…", 1, 113901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4410, 4, 0, 2, "ハロウィンってライブ\nとは違った賑やかさが\nあってワクワクします", 1, 113901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4411, 101, 0, 2, "私を驚かそうとしても\n無駄ですよ！\nすぐ分かるんですから", 4, 113901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4412, 102, 0, 2, "騎士さん、一緒に\nハロウィンを…いいえ\nなんでもないですっ！", 8, 113901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4413, 1, 0, 2, "仕事で寝不足だから\n太陽がまぶしいです…\n私、吸血鬼っぽいかも", 1, 113901, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4414, 2, 0, 2, "お昼ご飯は真っ赤な…\nトマト尽くしのお弁当\nにしてみました", 1, 113901, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4415, 3, 0, 2, "夜はハロウィンが\n盛り上がる時間…\n行きますよ騎士さん！", 1, 113901, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4417, 2, 0, 3, "このフィット感\nどうにか服作りにも\n活かせれば…！", 1, 113901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4418, 3, 0, 3, "よく出来てますね…！\nぬいぐるみの服を作る\nのも楽しそうですね！", 1, 113901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4419, 4, 0, 3, "いい感じですね…\n服に合わせた香りって\nいうのも、いいかも…", 1, 113901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4420, 5, 0, 3, "はぁ、温まりますね…\nおっと、マントに火が\n点いちゃいそうです！", 1, 113901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4422, 7, 0, 3, "あぁ、美味しい～\n食べた分は、働いて\n燃焼しますからね！", 1, 113901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4423, 8, 0, 3, "伝説の吸血鬼…\n夜を統べる者…\nほうほう…", 1, 113901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4425, 12, 0, 3, "やっぱりハロウィンと\nいえばお菓子です！\nさぁ配りましょうか！", 1, 113901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4427, 1, 0, 1, "この格好…\nカッコイイのに\n動きやすくて\n着心地も最高ッス！", 1, 114101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4428, 2, 0, 1, "キメポーズはこう…\nいやこっちのほうが…\nなやましいッス…！", 1, 114101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4429, 3, 0, 1, "トリックオア\nジャスティス！\nって言ったら何か\nもらえるッスかね？", 1, 114101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4430, 4, 0, 1, "新しい必殺技の\n特訓もしなくちゃ\nいけないッスね…\n忙しいッス！", 1, 114101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4431, 5, 0, 1, "自分のこの姿\nトモね～ちゃんにも\nみせてあげるッス！", 1, 114101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4432, 101, 0, 1, "ホワイトタイガー\n参上ッス！\n今のポーズは\nキマったッス！", 4, 114101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4433, 102, 0, 1, "あの人と一緒だと\nほんとにヒーローに\nなれる気がするッス！", 8, 114101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4434, 1, 0, 2, "どうッスか？\nこの色、自分に\n似合ってるッスかね？", 1, 114101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4435, 2, 0, 2, "ヒマだったら\n必殺技の特訓に\n付き合ってもらって\nいいッスか？", 1, 114101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4436, 3, 0, 2, "ちょうどいいところに\n来たッスね！\nこのキメポーズ\nどうッスか？", 1, 114101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4437, 4, 0, 2, "お！　この格好、\n気になるッスか？\nじっくり見ても\nいいッスよ♪", 1, 114101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4438, 101, 0, 2, "あんたも一緒に\n新しい技の練習\nどうッスか？", 4, 114101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4439, 102, 0, 2, "今日もあんたと一緒に\nランドソルの平和を\n守るッスよ！", 8, 114101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4440, 1, 0, 2, "朝ッス！\n一緒に早朝特訓\nやるッスよ！", 1, 114101, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4441, 2, 0, 2, "お昼休憩ッス！\nヒーローにも\n休息は必要ッス！", 1, 114101, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4442, 3, 0, 2, "闇夜に映える\n白い衣装で\n平和を守るッスよ！", 1, 114101, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4444, 2, 0, 3, "トラも狩りの時以外は\nごろごろしてるって\n本で読んだッスよー", 1, 114101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4445, 3, 0, 3, "もこもこッスね♪\nこのかわいらしさを\n守ってあげるのも\nヒーローの役目ッス！", 1, 114101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4446, 4, 0, 3, "いい匂いッスね…\nリラックスして\n瞑想するッス！", 1, 114101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4447, 5, 0, 3, "燃える炎は\nヒーローの心意気ッス\nいつまでも\n見てられるッスよ", 1, 114101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4449, 7, 0, 3, "もぐもぐ…\nいっぱい食べて\n強いヒーローに\nなるッスよ！", 1, 114101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4450, 8, 0, 3, "ちゃんと勉強の本も\n読んでるッスよ…！\nヒーローには賢さも\n必要らしいッス！", 1, 114101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4452, 12, 0, 3, "わわっ、お菓子が\nいっぱいッス！\nみんなで\n食べるッスよ！", 1, 114101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4454, 103, 0, 1, "ワタシがチューシンと\nなった今、天下統一は\n目の前デス！", 12, 103001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4455, 103, 0, 2, "もうショーグンってば\nそれはセイシツに\nすることデ～ス…", 12, 103001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4456, 1, 0, 1, "女幽霊…うらめしや…\nうーん、これは\nちょっと違う気が\nするな…", 1, 114001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4457, 2, 0, 1, "何だか寒気がする…\nま、まさか\nこの部屋にも幽霊が？", 1, 114001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4458, 3, 0, 1, "ハロウィンは楽しいね\n童心に帰ったような\n気分だよ", 1, 114001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4459, 4, 0, 1, "今度のハロウィンは\nユイとヒヨリを\n誘ってみようかな", 1, 114001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4460, 5, 0, 1, "ハロウィンのお菓子は\nどれもかわいいね\n…一つくらいなら\nいいかな？", 1, 114001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4461, 101, 0, 1, "次は彼と\nどんなハロウィンを\n過ごそうかな…", 4, 114001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4462, 102, 0, 1, "こんな賑やかさも\nいいものだね\n彼といられるのなら\nなおさら…", 8, 114001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4463, 1, 0, 2, "…いたずらしようと\nしてない、キミ？\nふふ、お見通しだよ", 1, 114001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4464, 2, 0, 2, "え、私にもお菓子を？\nありがとう…ふふ\nうれしいものだね\nハッピーハロウィン！", 1, 114001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4465, 3, 0, 2, "どうしたの？\n出かけるなら\n私も同行しようか", 1, 114001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4466, 4, 0, 2, "この格好、舞踏会にも\n出られそうだね\n一曲、踊ってみる？", 1, 114001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4467, 101, 0, 2, "やるからには本気で\nやらせてもらおう\nトリックオア\nトリート！", 4, 114001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4468, 102, 0, 2, "他の子に目移りする\nのも分かるが、その…\nせっかくの特別な衣装\n見てほしいな", 8, 114001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4469, 1, 0, 2, "寝起きの体を\n慣らしておこうか\nほら、剣を取って", 1, 114001, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4470, 2, 0, 2, "お菓子もいいけど\nちゃんと昼食も\nとるんだよ", 1, 114001, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4471, 3, 0, 2, "さあ、行こうか！\nハロウィンは\nこれからが本番だよ", 1, 114001, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4473, 2, 0, 3, "ハロウィンはまだ\nこれからだ\n一休みしようか", 1, 114001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4474, 3, 0, 3, "キミたちも\n仮装に興味は\nあるかな？　…ふふ", 1, 114001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4475, 4, 0, 3, "いい香りだけど\n幽霊が集まったり\nしないだろうか…？", 1, 114001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4476, 5, 0, 3, "暖かい…一息ついたら\nハロウィンの\nパレードを\n見に行こうかな", 1, 114001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4478, 7, 0, 3, "ハロウィンを本気で\n楽しむなら\n食事もしっかり\nとっておかないとね", 1, 114001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4479, 8, 0, 3, "ハロウィンの\nホラー小説か…\nけ、結構\n怖いものだね…？", 1, 114001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4481, 12, 0, 3, "お菓子がいっぱいだ…\nこういうのって\n見ているだけで心が\n躍ってしまうね", 1, 114001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4483, 1, 0, 1, "こんなにかわいい\n武器で戦えるとは…\nすごいものだな\n魔法少女は", 1, 114201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4484, 2, 0, 1, "決めポーズの訓練も\nしっかりやっておくか\n…誰も見ていないな？", 1, 114201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4485, 3, 0, 1, "こういう格好は\nユキあたりがすごく\n似合いそうだな", 1, 114201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4486, 4, 0, 1, "子どもたちにサインを\n求められてしまった…\nこれもまた私の使命！", 1, 114201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4487, 5, 0, 1, "この格好は目立つな…\nいや、提督とは人々の\n視線を集めるものだ！", 1, 114201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4488, 101, 0, 1, "あいつと作戦会議を\nしておくか。あれで\nなかなか頼れるからな", 4, 114201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4489, 102, 0, 1, "あいつには私の勇姿を\nちゃんと見ていて\nほしいものだな", 8, 114201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4490, 1, 0, 2, "一仕事終えたことだし\n一緒に甘いものでも\n食べに行くか？", 1, 114201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4491, 2, 0, 2, "怪しげな気配がするな\n貴公も警戒を\n怠るなよ！", 1, 114201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4492, 3, 0, 2, "シオリに魔法少女の\n本を借りたんだ\n一緒に読まないか？", 1, 114201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4493, 4, 0, 2, "巡回の時間まで\nお茶にでもするか\n貴公も付き合うがいい", 1, 114201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4494, 101, 0, 2, "貴公がサポートして\nくれるのなら心強いな\n頼りにしているぞ", 4, 114201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4495, 102, 0, 2, "頭を撫でるな～！\n…まったく、貴公だけ\nだぞ、こんな風に私を\n撫でてくるのは…", 8, 114201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4496, 1, 0, 2, "起きろ貴公！\n魔法少女の朝は\n早いぞ！", 1, 114201, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4497, 2, 0, 2, "ほら、食後の\nデザートだ\nて、適度な糖分は\n体に必要なんだ！", 1, 114201, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4498, 3, 0, 2, "ふわぁ…魔法少女も…\n眠気、には…い、いや\n寝てないぞ！？", 1, 114201, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4500, 2, 0, 3, "任務や訓練で\n疲れた体に\nこれは効くなぁ…", 1, 114201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4501, 3, 0, 3, "うむ、かわいいな\nモーラのやつを隣に\n並べようか", 1, 114201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4502, 4, 0, 3, "ああ、身も心も\n安らぐ…\nまるで魔法だな…", 1, 114201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4503, 5, 0, 3, "この温かさ…\n愛と希望に\n通じるものがある…", 1, 114201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4505, 7, 0, 3, "食事の大切さは\n魔法少女も軍人も\n変わりない\nしっかり食べるんだ！", 1, 114201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4506, 8, 0, 3, "魔法少女の本を\n読んでるのだが\nわ、私もこんななのか", 1, 114201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4508, 103, 0, 1, "オラとあんちゃんは\nボケもツッコミも\nあうんの呼吸だべ♪", 12, 103301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4509, 103, 0, 2, "あんちゃんと二人で\nネタ合わせ…初めての\n共同作業だべ♪", 12, 103301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4510, 1, 0, 1, "ぴっぷる！", 1, 192501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4511, 2, 0, 1, "ぴっぷるぴっぷる♪", 1, 192501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4512, 3, 0, 1, "ぴっ！", 1, 192501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4513, 4, 0, 1, "ぷっぷー", 1, 192501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4514, 5, 0, 1, "ぴぃ？", 1, 192501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4515, 1, 0, 2, "ぴっぷるんるん♪", 1, 192501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4516, 2, 0, 2, "ぷぅ！", 1, 192501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4517, 3, 0, 2, "ぴぴっ♪", 1, 192501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4518, 4, 0, 2, "ぷぅー…", 1, 192501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4519, 1, 0, 2, "ちゅ！", 1, 192501, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4520, 2, 0, 2, "ぷはー", 1, 192501, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4521, 3, 0, 2, "ぴぇー", 1, 192501, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4531, 1, 0, 1, "魔法少女になったら\n髪型も変わるんだね\n色々試してみようかな", 1, 114301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4532, 2, 0, 1, "…煌く剣技で、\n悪を断つ！\nうん、今日も\n完璧なポーズだね♪", 1, 114301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4533, 3, 0, 1, "ミクマ流剣術で、\n魔法少女の技に使え\nそうなものは…よし、\n早速試してみよっと♪", 1, 114301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4534, 4, 0, 1, "魔法少女になったから\nには、サインの練習も\nしないと…\nよし、頑張るぞ！", 1, 114301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4535, 5, 0, 1, "魔法少女の姿はすごく\nかわいいけど…この\n姿はジュンさんたちに\n見せられないなぁ…", 1, 114301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4536, 101, 0, 1, "あの人は魔法少女\nのこと好きなのかな？\n…好きだったら\nうれしいな♪", 4, 114301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4537, 102, 0, 1, "…やっぱり、\nあの人がいないと\n特訓に身が入らない…\n早く帰ってこないかな", 8, 114301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4538, 1, 0, 2, "もし時間があるなら、\n私と街の見回りに\n行かない？", 1, 114301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4539, 2, 0, 2, "あ、いいところに！\n新しい魔法剣術を\n編み出したから、\n見てくれないかな？", 1, 114301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4540, 3, 0, 2, "魔法少女の姿が気に\nなるのは分かるけど…\nず、ずっと見られる\nのは照れるからやめて", 1, 114301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4541, 4, 0, 2, "変身ポーズ中の動き、\n少し変えた方が\nいいかな…\nあなたはどう思う？", 1, 114301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4542, 101, 0, 2, "よければ、\n魔法少女の特訓に\n付き合ってくれない？\nその…二人きりでさ…", 4, 114301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4543, 102, 0, 2, "私…あなただけの\n魔法少女になっても…\nふふっ、な～んてね♪", 8, 114301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4544, 1, 0, 2, "今日もいい朝だね\nさあ、一緒に\n街の見回りに行こうか", 1, 114301, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4545, 2, 0, 2, "もう昼か…なにか\n食べたいものある？\n簡単なのでいいなら、\n私が作ってあげるよ", 1, 114301, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4546, 3, 0, 2, "そろそろ休もうか\n…なんなら、添い寝\nしてあげようか？\nふふっ、冗談だよ♪", 1, 114301, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4548, 2, 0, 3, "これは…\n心身ともに安らぐね\nなんだか\n眠くなっちゃいそう", 1, 114301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4549, 3, 0, 3, "か、かわいい…これ、\n魔法で動かせたり\nしないかな？", 1, 114301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4550, 4, 0, 3, "いい香りだね\nなんだか頭が冴えて\nきたし、新しい技でも\n考えようかな", 1, 114301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4551, 5, 0, 3, "あったかいな…\nこの温もりは、\n魔法少女の魔法でも\n再現不可能だね…", 1, 114301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4553, 7, 0, 3, "こ、これは…\nおいしすぎて手が\n止まらないかも", 1, 114301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4554, 8, 0, 3, "この本の\nキャラの動き、\n変身ポーズの\n参考にできるかも！", 1, 114301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4556, 1, 0, 1, "メリークリスマス！\nさあ、素敵な\nパーティーを始めると\nいたしましょう！", 1, 114401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4557, 2, 0, 1, "ふふっ、\nいくつになっても、\nやはりクリスマスは、\nワクワクしますわね♪", 1, 114401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4558, 3, 0, 1, "自分で料理をしてみて\nシェフたちのすごさが\n改めて分かりましたわ", 1, 114401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4559, 4, 0, 1, "何事も自分で\n体験してみると\n新しい発見が\nあるものですわね♪", 1, 114401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4560, 5, 0, 1, "やはり、クリスマスは\n街が活発になり\nますわね…これは、\n絶好の商機ですわ♪", 1, 114401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4561, 101, 0, 1, "他のクリスマス料理も\n作れるようになったら\nあのお方は食べて\nくださるかしら…？", 4, 114401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4562, 102, 0, 1, "あのお方に贈る\nプレゼント…\nこれはかなりの\n難題ですわね…", 8, 114401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4563, 1, 0, 2, "メリークリスマス！\nよろしければ、一緒に\nクリスマスの準備を\nいたしませんか？", 1, 114401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4564, 2, 0, 2, "まあ、クリスマスの\n飾りつけを手伝って\nいただけるのですか？\nありがとうございます", 1, 114401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4565, 3, 0, 2, "クリスマスに合わせて\nあつらえた自慢の衣装\nなのですが…その、\nいかがでしょうか？", 1, 114401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4566, 4, 0, 2, "幼い頃はサンタさんが\nいらっしゃるのを\n待ちわびたものです\n今は…内緒ですわ♪", 1, 114401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4567, 101, 0, 2, "も、もしお時間が\nあるようでしたら、\nクリスマスの街を\n一緒に歩きませんか？", 4, 114401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4568, 102, 0, 2, "来年のクリスマスも\nあのお方と一緒に\n迎えられたら…", 8, 114401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4569, 1, 0, 2, "時は金なり…\nせっかくの朝ですもの\n時間を有効活用\nいたしましょう！", 1, 114401, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4570, 2, 0, 2, "お昼になりましたわね\nさあ、\nパーティーの準備を\n進めますわよ！", 1, 114401, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4571, 3, 0, 2, "静かな夜ですわね…\nもうちょっとだけ、\nあなたさまとお話を\nしていたい気分です", 1, 114401, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4573, 2, 0, 3, "適度な休息は\n成功の秘訣ですわ！", 1, 114401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4574, 3, 0, 3, "このかわいらしさ…\nいつまでも見て\nいられそうですわ♪", 1, 114401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4575, 4, 0, 3, "人をもてなすには\nこうしたところにも\n気を配らないと\nいけませんわね", 1, 114401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4576, 5, 0, 3, "暖かい…\nこういう憩いの\nひと時こそ、大事に\nしたいものですわね", 1, 114401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4578, 7, 0, 3, "この奥深い味わい…\n何か隠し味など\n使われていまして？", 1, 114401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4579, 8, 0, 3, "クリスマス料理の\nレシピも、\nこれだけあると\n迷ってしまいますわね", 1, 114401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4581, 14, 0, 3, "この絵本、とっても\n面白いつくりですわね\nうちの商品に\nしたいぐらいですわ", 1, 114401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4582, 15, 0, 3, "これだけ大きな\n着ぐるみ…\n職人の技術が\n光りますわね", 1, 114401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4583, 1, 0, 1, "クリスマスといえば\nパーティーよね！\nいっぱい飲むわよ～♪", 1, 114601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4584, 2, 0, 1, "私を麦しゅわだけの\n女と思ったら\n大間違いなんだから♪", 1, 114601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4585, 3, 0, 1, "クリスマスらしい\n飲み物も結構いいわね\n私も今度\n作ってみようかしら", 1, 114601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4586, 4, 0, 1, "暖かい部屋で飲む\n麦しゅわも\nまた格別なのよね…", 1, 114601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4587, 5, 0, 1, "今日はオフの日だし\n朝から飲んでも\n怒られないわよね？", 1, 114601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4588, 101, 0, 1, "クリスマスの\nデートスポットで\n彼と一緒に…ってなに\n考えてるのユカリ！？", 4, 114601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4589, 102, 0, 1, "背伸びしなくても\nありのままの自分を\n受け入れてくれるって\nすごく…幸せ…", 8, 114601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4590, 1, 0, 2, "大丈夫よ～\n飲んでない\n飲んでないひゃら～", 1, 114601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4591, 2, 0, 2, "クリスマスは\n特別だもの\n特別な一杯を\n飲まないとね♪", 1, 114601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4592, 3, 0, 2, "メリークリスマース！\nほらほらぁ\n乾杯しましょ♪", 1, 114601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4593, 4, 0, 2, "お姉さんの\n頼れるところ\n見せてあげるわね", 1, 114601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4594, 101, 0, 2, "何かあったら\n頼ってくれても\nいいんだからね？", 4, 114601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4595, 102, 0, 2, "ふふ…これからも\nキミのこと…\n頼りにしちゃうからね", 8, 114601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4596, 1, 0, 2, "さわやかな朝ね\n…二日酔いがないと\n朝ってこんなに\n気持ちいいのね", 1, 114601, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4597, 2, 0, 2, "昼間から飲むのって\nまた格別よね…って\nガマン、ガマンよ…", 1, 114601, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4598, 3, 0, 2, "もうこんな時間かあ…\nあとは寝るだけだし\nちょっとくらいは…", 1, 114601, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4600, 2, 0, 3, "ゴロゴロするのって\n最高ね～\nあとは麦しゅわが\nあればなぁ", 1, 114601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4601, 3, 0, 3, "私だって…\nかわいいものは\n好きでいいわよね？", 1, 114601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4602, 4, 0, 3, "んっ～…いい香りね\n日頃の疲れが\n取れる気がするわ", 1, 114601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4603, 5, 0, 3, "ポカポカする～\n…なんだかのどが\nかわいてきたわね", 1, 114601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4605, 7, 0, 3, "もぐもぐ…労働の\n後のご飯はやっぱり\nおいしいわぁ…", 1, 114601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4606, 8, 0, 3, "クリスマスデート\n完全攻略マニュアル？\n…読んでおかなくちゃ", 1, 114601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4608, 14, 0, 3, "あら、絵が飛び出して\n見えるけど…\n酔っ払ってるせいじゃ\nないわよね…？", 1, 114601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4609, 15, 0, 3, "いい手触りね～\nウチにもほしいけど\n麦しゅわをこぼしたら\n大変そうだわ…", 1, 114601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4610, 103, 0, 1, "ヒデサイの視線\nずっと独り占め\nしてたいな～", 12, 101601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4611, 103, 0, 2, "ヒデサイといるだけで\nうち、おに楽しいし！\nヒデサイもっしょ～？", 12, 101601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4612, 1, 0, 1, "メリークリスマス！\nふふ、口にするだけで\n何だか楽しくなるわね", 1, 114501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4613, 2, 0, 1, "いつもは子どもたちを\n見守る方だけど\n今ははしゃいでも\nいいわよね？", 1, 114501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4614, 3, 0, 1, "ツリーや料理\nそしてプレゼント！\nいくつになっても\nやっぱり心躍るわね♪", 1, 114501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4615, 4, 0, 1, "飾り付けも料理も\nいいセンスしてるわね\n救護院のパーティーの\n参考にさせてもらうわ", 1, 114501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4616, 5, 0, 1, "焼きそばパンを\nクリスマスの定番に\nするにはどうすれば…", 1, 114501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4617, 101, 0, 1, "これ、あいつの\n好きそうな料理だわ\n取っといて食べさせて\nあげようかしら♪", 4, 114501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4618, 102, 0, 1, "あいつのプレゼント\nちょっとだけ奮発\nしたのよね…\n喜んでくれるかしら", 8, 114501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4619, 1, 0, 2, "このドレス\n似合ってるかしら？\n今だけは見とれても\n許してあげるわ♪", 1, 114501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4620, 2, 0, 2, "見て、このケーキ！\nおいしそうでしょ\nたまにはこんな贅沢も\nいいわよね♪", 1, 114501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4621, 3, 0, 2, "料理も飲み物も\nおいしいわね！\nいっぱい食べて\n元を取らないと♪", 1, 114501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4622, 4, 0, 2, "子どもたちと一緒に\n雪遊び、楽しそうね\nあんたもどう？", 1, 114501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4623, 101, 0, 2, "せっかくの\nクリスマスなんだもの\nエスコート\nよろしくね♪", 4, 114501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4624, 102, 0, 2, "寒い？　そう…\nなら、もっとこっち\n来ていいわよ…？", 8, 114501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4625, 1, 0, 2, "ほら、早く起きて？\nパーティー用の\n買い出しに\n付き合ってちょうだい", 1, 114501, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4626, 2, 0, 2, "パーティーの料理の\n準備をしながらお昼も\n作っておいたわ\n我ながらいい手際ね", 1, 114501, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4627, 3, 0, 2, "にぎやかなパーティー\nもいいけど、聖夜の\n余韻を感じるのも\n結構素敵ね", 1, 114501, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4629, 2, 0, 3, "いい座り心地だわ～\n腕のいい職人の仕事ね\nうちでも扱いたいわ", 1, 114501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4630, 3, 0, 3, "かわいい！\n子どもたちに\nプレゼントしたら\n喜んでくれるかも", 1, 114501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4631, 4, 0, 3, "華やかな香りね\nクリスマスに\nピッタリだわ", 1, 114501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4632, 5, 0, 3, "寒い日にはやっぱり\nこれよね…\nはぁ、極楽極楽♪", 1, 114501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4634, 7, 0, 3, "んー、おいしいわ！\n焼きそばパン味の\nものも、作れない\nかしら？", 1, 114501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4635, 8, 0, 3, "たまにはのんびり\n読書でも…なんだか\nラブロマンスばかりね\nクリスマスだから？", 1, 114501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4637, 14, 0, 3, "これ、面白いわね！\n子どもたちの\nプレゼントに\nピッタリだわ", 1, 114501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4638, 15, 0, 3, "こんないい毛並み\n初めて見るわ…\n一体どこで手に\n入るのかしら？", 1, 114501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4639, 1, 0, 1, "アタシもみんなと\n一緒に正月を\n楽しんでいいのか？", 1, 114701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4640, 2, 0, 1, "今年こそ、もう少し\n身長が伸びます\nように…", 1, 114701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4641, 3, 0, 1, "げっ！　お年玉が\n全然残ってないぞ！？\nそんな、嘘だろ…？", 1, 114701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4642, 4, 0, 1, "大勢で過ごす\n正月か…初めてだけど\n悪くないもんだな", 1, 114701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4643, 5, 0, 1, "んー…ここじゃ\n天楼覇断剣の\n出番はなさそうだな", 1, 114701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4644, 101, 0, 1, "アイツらと作る\n戦い以外の思い出も\nもっと増やしたいな", 4, 114701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4645, 102, 0, 1, "こんなに賑やかな\n正月は初めてだ\n…温かくて、いいな", 8, 114701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4646, 1, 0, 2, "記憶がなくったって\n楽しい思い出は\n新しく作れるよな？", 1, 114701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4647, 2, 0, 2, "早くでかけるぞ！\nアタシはもっと\n正月を楽しみたい！", 1, 114701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4648, 3, 0, 2, "なあ、オマエはもう\nノウェムって名前\n覚えてくれたよな？", 1, 114701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4649, 4, 0, 2, "この服、アタシに\n似合ってるのか？\n…そっか。\nありがとな！", 1, 114701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4650, 101, 0, 2, "オマエらと見た朝日は\nきっと忘れないぞ！\n思い出ができたな！", 4, 114701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4651, 102, 0, 2, "いつかオクトーも\n一緒に、みんなで\n正月を過ごせたらな…", 8, 114701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4652, 1, 0, 2, "朝から元気だな！\nアタシもいっちょ\n見習ってみるか！", 1, 114701, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4653, 2, 0, 2, "昼飯は何にする？\n正月はうまいものが\n多くて迷うよな！", 1, 114701, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4654, 3, 0, 2, "いい夜だな！\nアタシと一緒に\n夜更かしを楽しむか？", 1, 114701, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4656, 2, 0, 3, "正月らしく、たまには\nゆっくりするのも\nいいかもな…", 1, 114701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4657, 3, 0, 3, "なんだ？\nオマエも初詣に\n行きたいのか？", 1, 114701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4658, 4, 0, 3, "すーはー…\n神殿のニオイがするな\nいい事ありそうだ！", 1, 114701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4659, 5, 0, 3, "コイツがあれば\nうまい魚が\n焼けそうだな…", 1, 114701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4661, 7, 0, 3, "ううー\n食べ過ぎて帯が\nきつくなってきたぞ…", 1, 114701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4662, 8, 0, 3, "おみくじって\n書いてあることが\nむずかしいんだよな…", 1, 114701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4664, 1, 0, 1, "困ってる人は\nいないみたいだし…\nちょっとここで、\n特訓しようかな♪", 1, 180101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4665, 2, 0, 1, "空が飛べるんだから\nもっとたくさんの人を\n助けにいけちゃう！\n…ここじゃ飛べないや", 1, 180101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4666, 3, 0, 1, "炎の翼\nかっこいいなー！\nこれ、もっと大きく\nできないのかな？", 1, 180101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4667, 4, 0, 1, "やっぱり変身すると\nお腹すくんだね…\n…今ならペコさんの\n食欲の理由が分かるよ", 1, 180101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4668, 5, 0, 1, "色なんかは違うけど…\nマントがあるから\n騎士くんとお揃いに\nなったみたい♪", 1, 180101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4669, 101, 0, 1, "…騎士くんがいないと\nなんか調子でない…\n早く会いたいよぉ…", 4, 180101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4670, 102, 0, 1, "この姿になると、\n騎士くんを近くに\n感じる…なんだろ\nドキドキしちゃう…", 8, 180101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4671, 1, 0, 2, "騎士くん騎士くん！\n今大丈夫？\nトレーニングメニュー\n考えるの手伝って！", 1, 180101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4672, 2, 0, 2, "…うにゃっ！？\nこ、この姿になっても\n尻尾は触っちゃ\nダメー！", 1, 180101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4673, 3, 0, 2, "もしかして、この服が\n気になってるの？\nだったら、もっと\n近くで見ていいよ♪", 1, 180101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4674, 4, 0, 2, "あ、騎士くん！\n困ってる人がいないか\n見回りに行くんだけど\nついてきて欲しいな！", 1, 180101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4675, 101, 0, 2, "耳に触りたいの？\nだったら頭もなでて！\nそしたら、好きなだけ\n触っていいよ♪", 4, 180101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4676, 102, 0, 2, "…あたし、強くなって\n騎士くんを守るよ！\nだから…これからも\n一緒にいていい？", 8, 180101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4677, 1, 0, 2, "おっはよー！\nさあさあ騎士くん！\n今日も一緒に\nはりきっていこー♪", 1, 180101, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4678, 2, 0, 2, "お昼、お昼～♪\n今日のご飯は何かな～\nあ、騎士くん！\n早くお昼食べ行こ！", 1, 180101, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4679, 3, 0, 2, "今日もお疲れ様ー！\n騎士くん、あたしが\nマッサージしよっか？\n結構評判なんだよ！", 1, 180101, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4681, 2, 0, 3, "うにゃぁ…\nこの気持ちよさ…\n溶けちゃいそう…\nにゃぁ…", 1, 180101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4682, 3, 0, 3, "か、かわいい～\nこれ、なでなで\nしていいんだよね…？\nよしよし…♪", 1, 180101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4683, 4, 0, 3, "とってもいい香り！\n気分も落ち着くし、\n特訓の後なんかに\nいいかもー！", 1, 180101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4684, 5, 0, 3, "ぬくぬくだぁ…\nなんだか、\nふにゃふにゃに\nなっちゃうよぉ…", 1, 180101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4686, 7, 0, 3, "お、おいひぃ…！\nこの姿になると\nお腹すくし、すっごく\n助かっちゃった！", 1, 180101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4687, 8, 0, 3, "この本で勉強して、\nぶんぶりょーどーの\n超ウルトラヒヨリに\nなっちゃうぞー！", 1, 180101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4689, 1, 0, 1, "今年もみんなで\nおいしいものを\nたくさん食べたいです", 1, 111801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4690, 2, 0, 1, "挑戦は大切ですよね☆\nというわけで\nおせちに魔物を…", 1, 111801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4691, 3, 0, 1, "おせちは縁起物です！\n食べれば食べるほど\n幸せになるはずです！", 1, 111801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4692, 4, 0, 1, "食べ物がたくさん\n入った福袋は\nないんでしょうか…？", 1, 111801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4693, 5, 0, 1, "初詣で何を\nお願いしましょう…\n五穀豊穣？", 1, 111801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4694, 101, 0, 1, "ずっとこうして\n年の始まりを\n過ごしたいですね", 4, 111801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4695, 102, 0, 1, "いつか二人きりで\n初日の出を\n見たいです", 8, 111801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4696, 1, 0, 2, "お餅おかわりです！\nいくらでも\n食べられますよ！", 1, 111801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4697, 2, 0, 2, "おせちの食材を\n取りに行きませんか？\nいい運動です☆", 1, 111801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4698, 3, 0, 2, "凧揚げって\n難しいですね～\n糸が絡まっちゃいます", 1, 111801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4699, 4, 0, 2, "一年の計は元旦にあり\n今年一年分の\n献立を作りましょう！", 1, 111801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4700, 101, 0, 2, "ほらほら！\nもっと近くで着物を\n見ていいんですよ☆", 4, 111801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4701, 102, 0, 2, "あなたはどんな\n初夢を見ましたか？\nわたしは…内緒です☆", 8, 111801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4702, 1, 0, 2, "年明けの朝は\n心なしか\n気持ちいいですね☆", 1, 111801, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4703, 2, 0, 2, "お雑煮のあとは\nおしるこで\nおやつタイムです！", 1, 111801, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4704, 3, 0, 2, "あたたかくして\n幸せな初夢を\n見ましょうね", 1, 111801, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4706, 2, 0, 3, "はぁ～屋台巡りを\n満喫しました☆\n一休み一休み", 1, 111801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4707, 3, 0, 3, "縁起のいい\nぬいぐるみを並べて\n福を呼びましょう！", 1, 111801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4708, 4, 0, 3, "おしるこのアロマって\nないんでしょうか？\nそれなら作るしか…", 1, 111801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4709, 5, 0, 3, "ここでお餅を\n焼いたらおいしく\nなりそうですね～♪", 1, 111801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4711, 7, 0, 3, "おいしすぎて\n食べる手が\n止まりません～", 1, 111801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4712, 8, 0, 3, "新しいおせち料理の\nヒントは…なるほど…\nふむふむ…", 1, 111801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4714, 103, 0, 1, "お兄ちゃんとあんな\n特訓やこんな特訓が\nしたいなぁ、えへへ♪", 12, 100601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4715, 103, 0, 2, "お兄ちゃんに褒めて\nもらうと、身体が\nじぃーんと\n熱くなっちゃいます♪", 12, 100601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4716, 1, 0, 1, "見目麗しい衣装に\n身を包むのも\n悪い気分では\nありませんね", 1, 115001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4717, 2, 0, 1, "縁起物で\n験を担ぐというのも\nたまにはよいでしょう", 1, 115001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4718, 3, 0, 1, "非効率なので\nあまり行いませんが\n私も手料理くらいは\n作れるのですよ", 1, 115001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4719, 4, 0, 1, "研究のためなら\n時に遠路を超え\n実地へ赴くことも\n必要なのです", 1, 115001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4720, 5, 0, 1, "トゥンヌスの\n突然変異…\n大いに興味を\nそそられますね", 1, 115001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4721, 101, 0, 1, "漁師の技法も\n再現できたことで\nまた一つ、仮説が\n実証できました", 4, 115001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4722, 102, 0, 1, "正月くらいは\n私ものんびりしたい\n気持ちなのですよ\n意外ですか？", 8, 115001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4723, 1, 0, 2, "模倣に頼らずとも\n衣装に合わせた\n所作程度は\nできて当然です", 1, 115001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4724, 2, 0, 2, "お年玉の使い道は\n思いつきましたか\nゆっくり考えても\nいいのですよ", 1, 115001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4725, 3, 0, 2, "トランプでも花札でも\n相手をしましょう\n手心が必要なら\nいつでも言いなさい", 1, 115001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4726, 4, 0, 2, "初詣でお祈りする\n内容ですか\n…ヒミツ、として\nおきましょう", 1, 115001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4727, 101, 0, 2, "私たちでオーマ中の\nトゥンヌスを\n釣り上げましょうか\n…冗談ですよ", 4, 115001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4728, 102, 0, 2, "見返りを求めない願い\nそれは祈りに通じるの\nかもしれませんね", 8, 115001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4729, 1, 0, 2, "…貴方がその眠たげな\n顔を洗ってくる間に、\n温かいお茶でも\n淹れておきましょう", 1, 115001, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4730, 2, 0, 2, "さて、本格的に\n活動を再開します\n貴方も同行しなさい\n昼食は済みましたか？", 1, 115001, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4731, 3, 0, 2, "私はもう少し\n作業を片付けてから\n寝ることにします\n早く眠りなさい", 1, 115001, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4733, 2, 0, 3, "少し休みます…\n時間が来たら\n起こすように", 1, 115001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4734, 3, 0, 3, "贈り物を模倣して\n潜入…試してみる\n価値はありそうですね", 1, 115001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4735, 4, 0, 3, "嗅覚から得られる\n情報量は侮れません\n動物の模倣において\n注意すべき点ですよ", 1, 115001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4736, 5, 0, 3, "穏やかな炎による\nリラックス効果は\n本能によるものかも\nしれませんね", 1, 115001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4738, 7, 0, 3, "いかなる強者でも\n兵站、補給は\n必要なものです", 1, 115001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4739, 8, 0, 3, "あらゆる学問は\n完璧な模倣のための\n大きな礎となります", 1, 115001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4741, 1, 0, 1, "新たに得たこの力で\n仲間と目指す未来を\n切り拓いてみせます", 1, 115501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4742, 2, 0, 1, "ここは、みなさまと…\nわたくしの\n帰る場所でございます", 1, 115501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4743, 3, 0, 1, "主さまも精霊の召喚を\n試してみますか？", 1, 115501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4744, 4, 0, 1, "主さま、おこづかいは\n足りていますか？\n遠慮は無用ですよ", 1, 115501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4745, 5, 0, 1, "この装束をまとうと\n自然と背筋が伸びる\n思いがいたします", 1, 115501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4746, 101, 0, 1, "進化した精霊に\n負けぬよう\n修行に励みます", 4, 115501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4747, 102, 0, 1, "仲間たちといられる\nこの幸せが、末永く\n続きますように…", 8, 115501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4748, 1, 0, 2, "主さまのお手手は\n大きくて温かいですね\nぎゅっぎゅっ…ふふ♪", 1, 115501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4749, 2, 0, 2, "今度、主さまにも\n里に伝わる昔話を\nお話しいたしましょう", 1, 115501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4750, 3, 0, 2, "里で採れた果物を\n持って参りました\n召し上がってください", 1, 115501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4751, 4, 0, 2, "里のお土産を\nここのみなさまに\nお配りいたしましょう", 1, 115501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4752, 101, 0, 2, "仲間と囲む食卓…この\n温もりは、みなさまの\n心の温かさなのですね", 4, 115501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4753, 102, 0, 2, "主さまに名前を\n呼んでいただくと\n心が温かくなります♪", 8, 115501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4754, 1, 0, 2, "ふふっ…主さま\n寝癖がついて\nおりますよ？", 1, 115501, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4755, 2, 0, 2, "今日の昼食は\n里の伝統料理です\nお口に合うでしょうか", 1, 115501, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4756, 3, 0, 2, "主さまが眠れるよう\n安らぎの風を…\nおやすみなさいませ", 1, 115501, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4758, 2, 0, 3, "この癒し…\n主さまにも味わって\nいただきたい…", 1, 115501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4759, 3, 0, 3, "この愛らしさ…\n精霊たちと似たものを\n感じます…♪", 1, 115501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4760, 4, 0, 3, "精霊よ…この安らぐ\n香りを、隅々まで\n届けてくださいまし", 1, 115501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4761, 5, 0, 3, "瞑想するのに\nちょうどよい暖かさで\nございます", 1, 115501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4763, 7, 0, 3, "進化した精霊を\n使役するためにも\nよく食べて体力を…！", 1, 115501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4764, 8, 0, 3, "風の精霊について\nもっと詳しく知って\nおかなければ…", 1, 115501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4766, 1, 0, 1, "儀式の時を思い出して\n精霊に呼びかける…\n出ておいで～♪", 1, 115601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4767, 2, 0, 1, "ひゃうっ！　もう…\n急に背中を触るなんて\nいたずら好きな精霊に\n育てた覚えはないのに", 1, 115601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4768, 3, 0, 1, "儀式用の衣装…\nあんまり激しい動きは\nできないよね…でも\n戦う時は気にしない！", 1, 115601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4769, 4, 0, 1, "そうだ…精霊に\n騎士クンのそばで\n見守っていてって\n頼んでみようかな…", 1, 115601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4770, 5, 0, 1, "ふふっ、こうやって、\n楽しく精霊とお話し\nしていられる、平穏な\n日々が続くといいな", 1, 115601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4771, 101, 0, 1, "き、きき、騎士クン！\nわたしと、あの…！\nうう…練習ですら\n上手く誘えないよぉ…", 4, 115601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4772, 102, 0, 1, "あっ、精霊が…\nふふ、応援ありがとう\nわたしの想い…いつか\n必ず伝えるね", 8, 115601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4773, 1, 0, 2, "新しく覚えた料理\n作ってみたんだけど\n騎士クンも食べない？", 1, 115601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4774, 2, 0, 2, "騎士クン、\n疲れてない？\nわたしと精霊で\n治療するよ？", 1, 115601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4775, 3, 0, 2, "もう、いたずらしない\n…騎士クン！？\nご、ごめんなさい！\n精霊だと思って！", 1, 115601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4776, 4, 0, 2, "騎士クン！\n時間が合ったら、\n精霊と一緒に\nお話しない？", 1, 115601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4777, 101, 0, 2, "…こういう服着るの\n初めてなんだけど…\nえっ、かわいい…？\nあ、ありがとう…！", 4, 115601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4778, 102, 0, 2, "困ったことがあったら\n相談してね。わたし、\n騎士クンのためなら…\n色々できるよ、色々！", 8, 115601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4779, 1, 0, 2, "おはよう騎士クン！\n今からお外で\n瞑想するんだけど…\n騎士クンもどうかな？", 1, 115601, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4780, 2, 0, 2, "お昼の時間だ\nご飯ならわたしが\n用意するから\nゆっくりしてて", 1, 115601, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4781, 3, 0, 2, "もうこんな時間…\n騎士クン、はいどうぞ\n寝る前の温かいミルク\nぐっすり眠れるよ♪", 1, 115601, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4783, 2, 0, 3, "…うん、落ち着く\n精霊との魔法で\nこんな風に誰かを\n安心させたいな", 1, 115601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4784, 3, 0, 3, "かわいい…\n精霊も気に入ってる…\nそうだ、同じ物\n作ってあげようかな？", 1, 115601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4785, 4, 0, 3, "いい香り…これ、\nうちに置いたら、\nお母さん喜ぶかも！", 1, 115601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4786, 5, 0, 3, "あったかい…\nまるで誰かが\n抱きしめてくれた\nみたい…", 1, 115601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4788, 7, 0, 3, "自然の恵みに\n感謝して…\nいただきます♪", 1, 115601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4789, 8, 0, 3, "もっと精霊について\n勉強して、みんなを\n守れるように\nなりたいな…", 1, 115601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4791, 103, 0, 1, "アイツといると胸が\nぎゅーっとなる…\nこれってもしかして…", 12, 102201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4792, 103, 0, 2, "アンタとならきっと\nどんなゲームでも…\nそ、その、たた、\n楽しいって思えるわ！", 12, 102201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4793, 1, 0, 1, "色んな人と話したら\n記憶探しの手がかりが\n見つかるかも…？", 1, 106401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4794, 2, 0, 1, "アイスクリームでも\n作ろうかしら？\nバニラにミントに…", 1, 106401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4795, 3, 0, 1, "ネビアに話しかけると\n逃げられるのよね…\nもう、襲わないのに…", 1, 106401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4796, 4, 0, 1, "「コッコロさん」、\n「コッコロさん」…\n…間違えないように…", 1, 106401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4797, 5, 0, 1, "最近少し太ったかも？\nきっとおいしいもの、\n食べ過ぎね…", 1, 106401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4798, 101, 0, 1, "ここにいると、\nとても落ち着くわ…\n自分の家、だものね", 4, 106401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4799, 102, 0, 1, "みんなと一緒の時間、\nずっと記憶に残したい\n…そう思っているのよ", 8, 106401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4800, 1, 0, 2, "ドラゴン族って、\nそんなに珍しいの？\nあまり実感ないけど…", 1, 106401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4801, 2, 0, 2, "料理、練習してるのよ\n…味見してもらえる？\n期待はしないでね", 1, 106401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4802, 3, 0, 2, "頭を叩いたら、\n記憶が戻ったり\nしないかしら…？\nやらなくていいわよ？", 1, 106401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4803, 4, 0, 2, "赤ちゃんみたいだった\n時のことは、忘れて…\nお願いだから…", 1, 106401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4804, 101, 0, 2, "ひゃんっ！？\nし、しっぽを掴むのは\nダメよ？", 4, 106401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4805, 102, 0, 2, "記憶が戻っても…\n私は、あなたたちと\n一緒にいたいわ", 8, 106401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4806, 1, 0, 2, "おはよう。\n寝ぐせついてるわよ？\n…ふふっ、これでよし", 1, 106401, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4807, 2, 0, 2, "そろそろお昼かしら？\nおいしいごはんを、\nみんなで一緒に…ね", 1, 106401, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4808, 3, 0, 2, "もうこんな時間ね。\nそろそろ休みましょ？\n…ふわぁ…", 1, 106401, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4810, 2, 0, 3, "ふかふか、ぎゅう～…\n…あっ、これはその…\nストレッチしててっ…", 1, 106401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4811, 3, 0, 3, "あまり、興味ないわ。\nけど…けど…\n…か、かわいい…", 1, 106401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4812, 4, 0, 3, "いい香りね。\nとっても落ち着くわ…\n…っくしゅっ", 1, 106401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4813, 5, 0, 3, "冷やす方が得意だけど\nあったかいのも、\nキライじゃないのよ", 1, 106401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4815, 7, 0, 3, "別に、食いしん坊\nじゃないのよ？\nでも、もう一口だけ…", 1, 106401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4816, 8, 0, 3, "ん…なるほどね。\n勉強になるわ…", 1, 106401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4818, 1, 0, 1, "水着の探偵か…\nちょっと恥ずかしい\n気がしてきたよ…", 1, 115701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4819, 2, 0, 1, "依頼があれば\n海でも山でも\n赴くさ！", 1, 115701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4820, 3, 0, 1, "今のうちに\n助手くんの秘密を…\nわ…私はなにをっ！？", 1, 115701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4821, 4, 0, 1, "休暇も時には必要さ\n海でゆっくりと…\nむっ？　事件の気配！", 1, 115701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4822, 5, 0, 1, "探偵の秘密道具…\n水着だと隠す\n場所がない…！", 1, 115701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4823, 101, 0, 1, "そろそろ助手くんが\n現れる時間かな？\nふふっ、楽しみだね", 4, 115701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4824, 102, 0, 1, "真実は私と助手くん\n二人だからこそ\n解き明かせるのさ", 8, 115701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4825, 1, 0, 2, "ひゃああっ！？\nわ、私の不意を突く\nとは…やるね助手くん", 1, 115701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4826, 2, 0, 2, "さて、なにか気づいた\nことはあるかな？\n言ってごらん♪", 1, 115701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4827, 3, 0, 2, "この感触…分かった！\n助手くんだね！\nふふっ、やっぱり♪", 1, 115701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4828, 4, 0, 2, "水着…どうかな？\n探偵らしさは\n残してるつもりだけど", 1, 115701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4829, 101, 0, 2, "ん？　依頼かな？\n助手くんの頼みなら\nなんでもお安い御用さ", 4, 115701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4830, 102, 0, 2, "助手くん、私と海に\n行かないかな\n…事件じゃないよ？", 8, 115701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4831, 1, 0, 2, "珈琲でも淹れて\n目を覚ますとしよう\n一緒に飲むかい？", 1, 115701, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4832, 2, 0, 2, "昼食は済ませた？\nなにを食べたか\n当ててあげよう！", 1, 115701, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4833, 3, 0, 2, "夜は謎が深まる時間…\n闇に覆い隠された真実\n必ず暴いてみせる！", 1, 115701, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4835, 2, 0, 3, "さて、推理の時間だ\n…サボってないよ？\n考えてるんだからね？", 1, 115701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4836, 3, 0, 3, "これはもしや、中に\n秘密のメッセージが？\n…かわいいだけだね", 1, 115701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4837, 4, 0, 3, "ふむ、なんだか\n頭がスッキリするね\n…閃いた！", 1, 115701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4838, 5, 0, 3, "ポカポカするね…\n助手くん\n君もおいでよ", 1, 115701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4840, 7, 0, 3, "はむ、はむ…んぐっ\nた、食べてるところを\n見つめられると～…", 1, 115701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4841, 8, 0, 3, "秘密の依頼とか\nこういう場所に\n隠したりするよね…♪", 1, 115701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4843, 10, 0, 3, "これはいい乗り心地だ\n事件現場にこれで登場\n…は、やめておこう", 1, 115701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4844, 11, 0, 3, "不思議な感触だね…\nでも、いい発想が\n生まれてきそうだ…♪", 1, 115701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4845, 1, 0, 1, "これはあたしが\n前へ進むために\nあいつがくれた\n大切な力…！", 1, 180601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4846, 2, 0, 1, "チェンジ・\nプリンセスフォーム！\n練習練習、っと", 1, 180601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4847, 3, 0, 1, "もうあいつに助けて\nもらいっぱなしの\nあたしは…\n卒業しなきゃね", 1, 180601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4848, 4, 0, 1, "気ままに、お気楽に\nあいつと冒険する…\nそれって幸せよね", 1, 180601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4849, 5, 0, 1, "プリンセスフォーム…\nお姫様みたいね\nあたしがお姫様か…\nそっかぁ…ふふ♪", 1, 180601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4850, 101, 0, 1, "あいつと繋がって\n生まれたこの絆は\nあたしの宝物だわ", 4, 180601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4851, 102, 0, 1, "あたしの命の使い方…\n全て受け止めてくれた\nあいつにだったら…", 8, 180601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4852, 1, 0, 2, "ふふ～ん♪\nプリンセスフォームの\n天才魔法使い\nキャル様に用かしら？", 1, 180601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4853, 2, 0, 2, "これからは\nあたしの力で\nなんでもパパっと\n解決してあげるわね！", 1, 180601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4854, 3, 0, 2, "魔力がみなぎってる…\nねえ、もしかして\n今のあたしって本気で\nすごいんじゃない？", 1, 180601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4855, 4, 0, 2, "変身の反動で\nお腹が空いても\n魔物料理だけは\nいやだからね！？", 1, 180601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4856, 101, 0, 2, "ねぇ、あんたになら\nあたしの心…\n全部預けていいかも", 4, 180601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4857, 102, 0, 2, "あいつと一緒に\nいられたら、他には\n何も…って、あああ\nあんたいつの間に！？", 8, 180601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4858, 1, 0, 2, "早起きも悪くないわね\n読みかけの魔導書\n今のうちに\n読んじゃおうかしら", 1, 180601, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4859, 2, 0, 2, "ねぇ～お腹すいたー\nあんたなんか\n作りなさいよ\n魔物料理以外でね！", 1, 180601, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4860, 3, 0, 2, "ふぁぁ…眠すぎて\n今なら変身しても\nお腹が減らない\n自信があるわ…", 1, 180601, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4862, 2, 0, 3, "あー♪　最っ高！\n休んで魔力を高めて…\nあいつの役に\n立ってあげなきゃね", 1, 180601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4863, 3, 0, 3, "なにこれかわいい…！\nコレクションにしたく\nなっちゃうじゃない", 1, 180601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4864, 4, 0, 3, "この香り…すごく\nリラックスできるわ…\nあ、いい感じの魔法\n思いついちゃったかも", 1, 180601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4865, 5, 0, 3, "あったかい…\n落ち着くわ…\nあいつの手の\nぬくもりみたいね", 1, 180601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4867, 7, 0, 3, "プリンセスフォーム…\nこ、こんなに\nお腹が減るのね\n甘く見てたわ…！", 1, 180601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4868, 8, 0, 3, "ま、魔物料理の本！？\n誰よこんな本\n買ったの！　って…\n一人しかいないか", 1, 180601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4870, 1, 0, 1, "憧れの舞踏会…\n私、きちんと\n踊れるかしら", 1, 115801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4871, 2, 0, 1, "アン・ドゥ・トロワ\nアン・ドゥ・トロワ\n練習はばっちりね♪", 1, 115801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4872, 3, 0, 1, "こんなにすてきな\nドレスを着られて\n本当に夢みたいだわ♪", 1, 115801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4873, 4, 0, 1, "魔法はいつか解ける…\nでも、その時間は心に\n残り続けるわ♪", 1, 115801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4874, 5, 0, 1, "ガラスの靴は夢へ\n踏み出す魔法の道具…\n私、がんばっちゃう！", 1, 115801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4875, 101, 0, 1, "舞踏会であなたと\n一緒に踊れたら\nいいのにな", 4, 115801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4876, 102, 0, 1, "舞踏会に行く前に\n運命の王子様には\n出会ってるのよね", 8, 115801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4877, 1, 0, 2, "どうかしら？\n私、きちんとドレス\n着られてる？", 1, 115801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4878, 2, 0, 2, "あなたも一緒に\n踊ってみる？\nしゃなりしゃなり♪", 1, 115801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4879, 3, 0, 2, "あなたにエスコート\nされてきれいなお城を\n歩いてみたいわ♪", 1, 115801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4880, 4, 0, 2, "どう？　レッスンの\n成果はきちんと\n出てるかしら？", 1, 115801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4881, 101, 0, 2, "あなたが隣にいれば\n私も立派なお姫様に\n見えるかしら♪", 4, 115801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4882, 102, 0, 2, "私にとって\n一番の魔法はあなたが\nくれる勇気よ♪", 8, 115801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4883, 1, 0, 2, "さぁ！　今日も一日\n立派な淑女を目指して\nいきましょう♪", 1, 115801, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4884, 2, 0, 2, "お昼ごはん代わりに\nメタモルアップルを\n食べておきましょう", 1, 115801, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4885, 3, 0, 2, "そろそろ魔法が解ける\n時間…でも淑女の\n心得は忘れないわ♪", 1, 115801, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4887, 2, 0, 3, "淑女にも休息は\n必要よね…少しだけ\n休ませてもらおっと", 1, 115801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4888, 3, 0, 3, "かわいいものに\n囲まれた部屋も\n女の子の夢よね…", 1, 115801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4889, 4, 0, 3, "すてきな香り…\nお花かしら？\n森で探してみようかな", 1, 115801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4890, 5, 0, 3, "暖かい…暖炉のそばで\nおとぎ話を聞くのも\nいいわよね♪", 1, 115801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4892, 7, 0, 3, "レッスンのために\n栄養補給して\nおかないと！", 1, 115801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4893, 8, 0, 3, "教養を身に着ける\nことも大切よね！\nなになに…", 1, 115801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4895, 1, 0, 1, "や、やっぱスカートは\n落ち着かねえな…\nなんか、\nスースーするしよ…", 1, 115901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4896, 2, 0, 1, "せっかくこんな格好\nしてるし、いつもとは\n違う剣術の訓練を\nしてみるかな", 1, 115901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4897, 3, 0, 1, "さて、他の連中が\n帰ってくる前に、\nなんか料理でも\n作っといてやるか", 1, 115901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4898, 4, 0, 1, "こ、こんな服着てる\nわけだし、紅茶とか\n楽しんじゃっても\n変じゃないよな！", 1, 115901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4899, 5, 0, 1, "暇だし運動でも…\nあ、あぶねえ！\nスカートなの忘れてた\n…誰も見てねえよな？", 1, 115901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4900, 101, 0, 1, "紅茶が切れてるな…\nよし、あいつと一緒に\n買い物にでも行くか♪", 4, 115901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4901, 102, 0, 1, "…あいつがいねえと、\nつまんねえな…ったく\n飯用意してやるから、\n早く帰ってこいよな…", 8, 115901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4902, 1, 0, 2, "あ、あんまり\nジロジロ見んなよ\n…結構、\n恥ずかしいんだからな", 1, 115901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4903, 2, 0, 2, "ダンスの相手は\n無理だが、\n剣の訓練なら\nいつでも付き合うぜ！", 1, 115901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4904, 3, 0, 2, "この服に\n触りたいのか？\nすごい作りだし、\n気持ちは分かるぜ", 1, 115901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4905, 4, 0, 2, "お、いいところに！\nクッキーを焼く\nところだったんだが、\nおまえも食うか？", 1, 115901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4906, 101, 0, 2, "おまえにもこういう服\n似合うんじゃねえか？\n今度あたしが\n選んでやるよ♪", 4, 115901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4907, 102, 0, 2, "…おまえの前でなら、\nこういう格好を\nすんのも…\nな、なんでもねえよ！", 8, 115901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4908, 1, 0, 2, "朝の訓練を始めるか\n時間あるなら、\nおまえも付き合えよな", 1, 115901, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4909, 2, 0, 2, "もうこんな時間か…\n昼飯作るけど、\nなんか\n食いたいものあるか？", 1, 115901, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4910, 3, 0, 2, "眠れないなら、\nホットミルク飲むか？\n体あったまって、\n眠くなると思うぜ", 1, 115901, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4912, 2, 0, 3, "ちょっと休憩して…\nおっと、服にしわが\nできないように\nしねえとな", 1, 115901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4913, 3, 0, 3, "だ、誰も\n見てねえよな？\n…よ～しよしよし、\nかわいいなおまえー♪", 1, 115901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4914, 4, 0, 3, "すげぇいい香りだな\nうちの居酒屋にも…\nいや、合わねえか", 1, 115901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4915, 5, 0, 3, "あったけぇ…\nちょっとだけなら、\n寝ちまっても\nいいよな…？", 1, 115901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4917, 7, 0, 3, "これうめえな！\nたぶん材料は…よし！\nあたしも作ってみるか", 1, 115901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4918, 8, 0, 3, "この本は…\n誰でも分かる作法…か\n最低限このぐらいは\n勉強しとくか…", 1, 115901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4920, 103, 0, 1, "ミミがおとなに\nなっても\nおにいちゃんとは\nずーっといっしょ♪", 12, 102001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4921, 103, 0, 2, "ミミとくべつなだっこ\nしてほしいな…うん！\nおひめさまだっこ～♪", 12, 102001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4922, 103, 0, 1, "次はにいちゃんに\nどんなイタズラしよう\n早く会いたいな～", 12, 100401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4923, 103, 0, 2, "ミソギの特別な\nイタズラ見てくれる？\nいいよねっ♪", 12, 100401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4924, 103, 0, 1, "大人になったら、\nあの人と何しようかな\n…楽しみだなぁ", 12, 103601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4925, 103, 0, 2, "頭を撫でないで\nください！　もう、\n少しだけ、ですからね", 12, 103601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4926, 1, 0, 1, "くるくる〜♪\nうちのドレス\nかわええなぁ", 1, 116001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4927, 2, 0, 1, "日差しは苦手やけど\nたまにはひなたの下で\nぽかぽかしたいわぁ", 1, 116001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4928, 3, 0, 1, "外はお花が咲いてる\n久しぶりにお花を\n活けたいなぁ", 1, 116001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4929, 4, 0, 1, "お花をたくさん集めて\nギルドハウスに飾れば\n喜んでくれはるかな", 1, 116001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4930, 5, 0, 1, "あたたかい日は\n紅茶を飲みながら\nゆっくりしたいなぁ", 1, 116001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4931, 101, 0, 1, "お稽古事を始めるなら\nうちに言ってな\nどんなことも教えたる", 4, 116001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4932, 102, 0, 1, "うちにいろんな夢を\n見せてくれはって\nほんま、おおきにな♪", 8, 116001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4933, 1, 0, 2, "王子はん\n一緒におとぎの国へ\nお散歩にいきまひょ♪", 1, 116001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4934, 2, 0, 2, "ふんふんふ〜ん♪\n今な、王子はんに\n花冠を作ってるんよ", 1, 116001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4935, 3, 0, 2, "王子はんは\nお菓子好きどす？\nうち、作りますわ", 1, 116001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4936, 4, 0, 2, "うーん。ドレスの柄は\nお花とお星さま\nどっちにしまひょか", 1, 116001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4937, 101, 0, 2, "王子はんとの\n思い出が増えるたび\nうちの宝物も増えます", 4, 116001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4938, 102, 0, 2, "王子はんと一緒なら\nどんな場所でも\n夢の国になりますわ", 8, 116001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4939, 1, 0, 2, "夢の中におりたいけど\n王子はんに会いたくて\n起きてもうたわ♪", 1, 116001, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4940, 2, 0, 2, "王子はん一緒に\nアフタヌーンティー\nいかがやろか？", 1, 116001, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4941, 3, 0, 2, "夜の舞踏会…\n夢が広がるわぁ\nもちろんお相手は…♪", 1, 116001, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4943, 2, 0, 3, "ふわふわや〜\nなんや心地よぉて\n眠ってしまいそうや…", 1, 116001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4944, 3, 0, 3, "かいらしいなぁ\n新しいお洋服でも\n作ってあげまひょか♪", 1, 116001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4945, 4, 0, 3, "お花やろうか…\nリラックスできて\nええ香りやねぇ", 1, 116001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4946, 5, 0, 3, "こういう場所で\n絵本を読むと\n気分が出てええなぁ♪", 1, 116001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4948, 7, 0, 3, "舞踏会を楽しむなら\nたくさん食べて\nたくさん踊りまひょ", 1, 116001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4949, 8, 0, 3, "うち、この本好きや\n王子さまと結ばれる\nお姫さまのお話なんよ", 1, 116001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4951, 1, 0, 1, "はぁ、宿題めんどい…\nこれは楽しくない…\nこんな青春いらない…", 1, 116301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4952, 2, 0, 1, "奨学金の道は遠いなぁ\nメルクリちゃんたちが\nお金くれればいいのに", 1, 116301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4953, 3, 0, 1, "アオイちゃん元気かな\nチエルに会いたくて\n泣いてるんだろうな…", 1, 116301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4954, 4, 0, 1, "暇だし鏡でも見るか…\nうわ、顔良っ！\nえ誰この超絶美少女！", 1, 116301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4955, 5, 0, 1, "ワンツーワンツー♪\nうーん…カルミナの\n振りコピ難しいにゃー", 1, 116301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4956, 101, 0, 1, "先輩も運動してます？\nチエルが一緒に走って\nあげてもいいですよ？", 4, 116301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4957, 102, 0, 1, "来年のテレフェスも\n今から楽しみだなあ…\nまた一緒にお化け屋敷\n入ってくださいね", 8, 116301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4958, 1, 0, 2, "ちぇる～ん☆\nフェス仕様でも\nかわいいちえるん！", 1, 116301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4959, 2, 0, 2, "いま話しかけないで！\n来年の聖学祭ライブの\nイメトレ中なんです！", 1, 116301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4960, 3, 0, 2, "クロエ先輩のウェア姿\nめっちゃカッコよ！\n思わずおへそ触ったら\nぶたれましたけど", 1, 116301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4961, 4, 0, 2, "ユニ先輩の体操着姿…\nあれはあれで実に良き\n一生見てられますよね", 1, 116301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4962, 101, 0, 2, "先輩って体操着持って\nないんですか？　ハ？\nチエルのを貸せと…？\nえっ…変態かな？", 4, 116301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4963, 102, 0, 2, "テレフェスでは先輩も\n地味に頑張ってました\nね。まあ格好よかった\nんじゃないですか…", 8, 116301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4964, 1, 0, 2, "おはちぇる～ん☆\nさあ今日も楽しいを\n探しに行きますよー！", 1, 116301, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4965, 2, 0, 2, "先輩ビッグニュース！\n今からお昼ごはん\nおごらせてあげます！", 1, 116301, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4966, 3, 0, 2, "こんばんちぇるるん☆\nさあ朝まで楽しいを\n探しに行きますよー！", 1, 116301, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4968, 2, 0, 3, "はぁ…ふかふか～…\n運動の後は…眠たく…\nなり…ま…", 1, 116301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4969, 3, 0, 3, "ユニ先輩に似合いそ！\nいやクロエ先輩かな？\nチエルは当然すぎて…", 1, 116301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4970, 4, 0, 3, "やん、いい香り～！\nまるでチエルのために\n存在するみたい！", 1, 116301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4971, 5, 0, 3, "この火を使って\n聖火リレーしましょう\n先輩、一本抜いて！", 1, 116301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4973, 7, 0, 3, "あむあむ…ちゃんと\n食べないとユニ先輩\nみたいになっちゃう！", 1, 116301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4974, 8, 0, 3, "ふむふむ…ちゃんと\n読まないとクロエ先輩\nみたいになっちゃう！", 1, 116301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4976, 103, 0, 1, "今なら書ける気がする\n私の本当の\n想いを込めた\nラブソングを！", 12, 102901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4977, 103, 0, 2, "輝きを増した私を\nいっぱい見てね！\n…ちょ、ちょっと\n見つめすぎだよ！", 12, 102901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4978, 1, 0, 1, "テレフェスかぁー…\nあんまいい記憶ないな\nよし、忘れとこ", 1, 116201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4979, 2, 0, 1, "暇だし鏡でも見るか…\nふふ～ん…いいじゃん\nあ？　何見てんだコラ", 1, 116201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4980, 3, 0, 1, "ワンツー、ワンツー♪\nああクソ、難しいな…\nあ？　何見てんだコラ", 1, 116201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4981, 4, 0, 1, "アオイ元気してるかな\nまた絶叫したり白目\n剥いたりしてんのかな", 1, 116201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4982, 5, 0, 1, "ミフユねえさんが\nバイトリーダーしてる\n工場…うちも行くか…", 1, 116201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4983, 101, 0, 1, "うーん…今日の晩御飯\n何にすっかなァ…\nあんたも食べてく？", 4, 116201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4984, 102, 0, 1, "来年のテレフェスは\nどーなンだかねえ？\nま、一緒にまた弟の\n相手してやってよ", 8, 116201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4985, 1, 0, 2, "体操着なんてフツーに\n着てもつまんないし？\nその辺センス出るよね", 1, 116201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4986, 2, 0, 2, "奨学金の道、遠くね？\nアキノ先輩と仲良く\nなった方が早いかも…", 1, 116201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4987, 3, 0, 2, "あんた体操着は持って\nないの。ハ？　うちの\n貸せって？　いいけど\n…いや、よくねえよ", 1, 116201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4988, 4, 0, 2, "にゃんっ！\nや、だからさ…急に\n話しかけんなっての", 1, 116201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4989, 101, 0, 2, "え、なに？　この服？\nハ？　似合ってる？\n…え、聞こえなかった\nちょ、もっかい言って", 4, 116201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4990, 102, 0, 2, "あんたもテレフェスで\n一応は頑張ってたよね\nま格好よかったんじゃ\nないの。知んないけど", 8, 116201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4991, 1, 0, 2, "ふぁ～あ…おはーす\n昨日も寝たの遅くて\nまだ目がショボだよ…", 1, 116201, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4992, 2, 0, 2, "なに。あんた昼御飯\nまだなの。じゃ、うち\n適当になんか作ろうか", 1, 116201, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4993, 3, 0, 2, "夜っていいよね。色々\n見えすぎない感じとか\n…この話したっけ？", 1, 116201, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4995, 2, 0, 3, "あ、ヤバ…運動の後に\nこのフカついたのは…\n秒でスヤれる…すやぁ", 1, 116201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4996, 3, 0, 3, "へーこんなんチエルに\n似合いそうじゃん。\n…いやユニ先輩かな？", 1, 116201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4997, 4, 0, 3, "お、なんか良さげな\n香りじゃん。これは\n何の…いや分かんね", 1, 116201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4998, 5, 0, 3, "熱ッ！　この野郎なに\n爆ぜてンだコラァ！\n濡らすぞ木、てめえ！", 1, 116201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5000, 7, 0, 3, "何コレよく分かんない\nけど、今日モヤシしか\n食ってないから助かる", 1, 116201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5001, 8, 0, 3, "今日、体操着だしね。\n読書には逆アドかな。\nうん帰ろ。いやあ残念", 1, 116201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5003, 1, 0, 1, "ビッグボス・イノリ様\nふっふっふ…\nしびれる響きです！", 1, 116501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5004, 2, 0, 1, "いつかお兄さんを\nあたしの側近に\nしてやるですよ！", 1, 116501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5005, 3, 0, 1, "ビッグボスになったら\nみんなからたくさん\nお菓子をもらうです！", 1, 116501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5006, 4, 0, 1, "ふふん！\nこの服はあたしにしか\n着こなせませんよ！", 1, 116501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5007, 5, 0, 1, "ばーんと立って\nどーんと腕を組む！\nこれがボスのポーズ！", 1, 116501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5008, 101, 0, 1, "イノリ様の側近に\n選ばれるなんて\n名誉なことですよ！", 4, 116501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5009, 102, 0, 1, "未来の側近として\nあたしをしっかり\n甘やかすですよ！", 8, 116501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5010, 1, 0, 2, "あたしがお兄さんを\n立派なギャングに\nしてあげますからね！", 1, 116501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5011, 2, 0, 2, "側近たるもの\nボスを甘やかなさいと\nだめですよ！", 1, 116501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5012, 3, 0, 2, "なに見てやがるです…\nくぅ、分かりました！\nお菓子は半分こです！", 1, 116501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5013, 4, 0, 2, "お兄さんも\nあたしがボスなら\nうれしいですよね！", 1, 116501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5014, 101, 0, 2, "お兄さんが側近なら\nあたしも安心して\n夜眠れるですよ！", 4, 116501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5015, 102, 0, 2, "ビッグボスになったら\nお兄さんにいい思い\nさせてあげますから！", 8, 116501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5016, 1, 0, 2, "ボスたるもの\n早起きは鉄則ですよ！\nふぁあ…", 1, 116501, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5017, 2, 0, 2, "お腹が空きました…\nご飯食べにいきますよ", 1, 116501, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5018, 3, 0, 2, "ボスたるものぉ〜…\n夜更かしぐらい当然…\nぐー…", 1, 116501, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5020, 2, 0, 3, "は〜これは人をだめに\nしやがるやつですね\nあたし専用です", 1, 116501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5021, 3, 0, 3, "これはけしからん\nかわいらしさですね\nあたしにぴったりです", 1, 116501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5022, 4, 0, 3, "高級な香りがするです\nボスになったら\n部屋に置いてやるです", 1, 116501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5023, 5, 0, 3, "あったかいですねぇ\nちょっとこのまま\n一休憩です…", 1, 116501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5025, 7, 0, 3, "はむはむ♪\nボスたるもの食べねば\n戦はできませんよ！", 1, 116501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5026, 8, 0, 3, "ボスなら経済書ぐらい\n…う。難しくて\n読めねーです…", 1, 116501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5028, 103, 0, 1, "あなたはドジな私を\n受け入れてくれる…\nだから、頑張れます！", 12, 102501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5029, 103, 0, 2, "何でも仰ってください\nちょっぴり大胆な\nことでも…な～んて♪", 12, 102501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5030, 1, 0, 1, "あーあー、よし\nのどの調子もいいし\nはりきってガイド\nしてくるか！", 1, 116601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5031, 2, 0, 1, "さあ、そろそろ行くか\nガイドが\n遅刻したんじゃ\n話になんないからな", 1, 116601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5032, 3, 0, 1, "しれっとドラゴン族が\n旅行に来たり\nしてくれないかな", 1, 116601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5033, 4, 0, 1, "たぎるガイド先…\n火山、魔物の巣…\nダメだ、そんなとこ\nオレしか行けないや", 1, 116601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5034, 5, 0, 1, "この前行った絶景\nスポットよかったな\nキレイな景色を見る\nのもいいもんだぜ", 1, 116601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5035, 101, 0, 1, "お、今日はカレと\n一緒か。なんだか\nやる気出てきたぜ！", 4, 116601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5036, 102, 0, 1, "カレにガイドして\nもらったら\nどこに行っても\n楽しいだろうな", 8, 116601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5037, 1, 0, 2, "よっしゃあ！\nどっちが上手く\nガイドできるか\n勝負しようぜ！", 1, 116601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5038, 2, 0, 2, "飯でも食いがてら\nおススメの店の\n下調べでもしようぜ！", 1, 116601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5039, 3, 0, 2, "なかなかいいだろ？\nこの格好。キミも\n作ってもらったら\nどうだ？", 1, 116601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5040, 4, 0, 2, "普通は行けない\n場所とか行きたいよな\nキミならどんな\nとこがいい？", 1, 116601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5041, 101, 0, 2, "ガイドの仕事は\nキミの方が得意そうだ\n参考にさせてもらうぜ\nよろしくな！", 4, 116601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5042, 102, 0, 2, "キミの探してきた店\n結構よさそうじゃん\nそうだ、今度二人で\n下見に行こうぜ！", 8, 116601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5043, 1, 0, 2, "いい朝だな。絶好の\n仕事日和だぜ！\n今日もよろしくな！", 1, 116601, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5044, 2, 0, 2, "よし、昼飯にしよう\nこの後も激務だし\n飯抜きじゃあ\nぶっ倒れちまうからな", 1, 116601, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5045, 3, 0, 2, "お疲れさん！　さーて\n次はオレと遊んで\nくれよ。まだまだ\nいけるだろ？", 1, 116601, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5047, 2, 0, 3, "ガイドは一日\n歩きっぱなしだからな\nゆっくり休ませて\nもらうぜ", 1, 116601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5048, 3, 0, 3, "これ、どこで\n売ってるんだろ？\n土産の情報も\n仕入れとかなきゃな", 1, 116601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5049, 4, 0, 3, "アロマとかよく\n分かんねえけど\n土産にはいいかもな\n覚えておくぜ", 1, 116601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5050, 5, 0, 3, "体が温まると心も\n熱くなるんだよな\nいい仕事が\nできそうだぜ", 1, 116601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5052, 7, 0, 3, "食べ放題か\nこれはお客さんも\n喜んでくれそうだな！", 1, 116601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5053, 8, 0, 3, "へえ、こんなスポット\nあったんだ。やっぱ\nたまには勉強も\nしとくもんだな", 1, 116601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5055, 1, 0, 1, "バイト先になじめる\nように笑顔の練習を！\nに、にへぇ〜…", 1, 116701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5056, 2, 0, 1, "働いて働いて働いて\n…いつかその先に\nぼっちの出口が\n見えるのかも…！", 1, 116701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5057, 3, 0, 1, "バイトのイメトレを\n始めます。ポーズを\n決めて…よし！\n角度を変えて…よし！", 1, 116701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5058, 4, 0, 1, "バイト仲間に入る\nためには厳しい修業が\n必須と聞きました…！", 1, 116701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5059, 5, 0, 1, "どんどん技術を磨いて\nバイト仲間として\n認めてもらうんです！\n目指せ職人技…！", 1, 116701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5060, 101, 0, 1, "あの人が応援して\nくれる…だから必ず\nバイト仲間の輪に\n入ってみせます…！", 4, 116701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5061, 102, 0, 1, "あの人がいてくれると\n勇気が湧いてきます…\nぼっち卒業を夢見ても\nいいんだ、って…♪", 8, 116701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5062, 1, 0, 2, "緊張するけど\n思い切って声をかけて\nみます…バイト仲間に\n入るために…！", 1, 116701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5063, 2, 0, 2, "ぼっちの私が\nおもちゃ作りで\n人と共同作業…！\nか、感動です…！", 1, 116701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5064, 3, 0, 2, "好きなおもちゃって\nありますか？　私は\nもちろんマイフレンド\nくんです！", 1, 116701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5065, 4, 0, 2, "私が作ったおもちゃも\n誰かの友達に\nなってたら\nいいなぁ…♪", 1, 116701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5066, 101, 0, 2, "今日も明日もバイト\nですね！　バイト\n仲間ができる日が\n楽しみです…♪", 4, 116701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5067, 102, 0, 2, "脱ぼっちを目指して\nこれからも一緒に\n頑張りましょう！\n目指せ、バイト仲間！", 8, 116701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5068, 1, 0, 2, "おはようございます\n今日も気合いを入れて\nおもちゃ作りします！", 1, 116701, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5069, 2, 0, 2, "ランチの時間です…♪\nもちろん工場の隅で\nぼっち飯です…！", 1, 116701, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5070, 3, 0, 2, "ふわぁ…今日も\nお疲れさまでした\n明日も働きます～！", 1, 116701, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5072, 2, 0, 3, "バイトで頑張った日は\nふわふわの誘惑に\n勝てません…", 1, 116701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5073, 3, 0, 3, "こういうおもちゃの\nアイデアも\n素敵かも…♪", 1, 116701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5074, 4, 0, 3, "いい香りがする\nおもちゃはどうかな…\n森の薬草を使って…", 1, 116701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5075, 5, 0, 3, "ぽかぽか…\n幸せなひとときですね", 1, 116701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5077, 7, 0, 3, "もぐもぐ…たくさん\n食べて厳しいバイトを\n乗り切ります…！", 1, 116701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5078, 8, 0, 3, "ふむふむ…おもちゃの\n設計図を見るのも\n勉強になりますね！", 1, 116701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5080, 1, 0, 1, "う～ん\n次はどんなたい焼きを\n作ろうかにゃぁ…", 1, 116801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5081, 2, 0, 1, "あたしが売る\nたい焼きには\n夢が詰まってるにゃ！", 1, 116801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5082, 3, 0, 1, "いつか絶対に\nたい焼き工場を\n建て直してみせるにゃ", 1, 116801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5083, 4, 0, 1, "たまにたい焼き以外の\nおやつも食べるにゃ\nたとえば煮干しとか！", 1, 116801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5084, 5, 0, 1, "悪い商売人がいたら\nファントムキャッツに\n知らせるにゃ！", 1, 116801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5085, 101, 0, 1, "キミと一緒にいると\nたい焼きのアイデアが\n次々出てくるにゃ♪", 4, 116801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5086, 102, 0, 1, "出来ないことも\nキミと一緒にいれば\n出来る気がするにゃ♪", 8, 116801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5087, 1, 0, 2, "なんにゃ～？\nお姉さんにかまって\nほしいのかにゃ♪", 1, 116801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5088, 2, 0, 2, "キミ、いいところに！\nあたしとライバル店へ\n視察にいくにゃ！", 1, 116801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5089, 3, 0, 2, "くんくん…この匂い！\nたい焼き持ってるなら\nちょうだいにゃ♪", 1, 116801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5090, 4, 0, 2, "次はどんなたい焼きを\n売ろうかにゃ～\nキミの案も欲しいにゃ", 1, 116801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5091, 101, 0, 2, "ファントムキャッツに\n相棒が必要にゃ\nたとえばキミとか…", 4, 116801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5092, 102, 0, 2, "相棒！\n…ってキミのこと\n呼んでいいかにゃ？", 8, 116801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5093, 1, 0, 2, "早起きは三文の徳にゃ\n今日もたい焼き\n売れますよーににゃ！", 1, 116801, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5094, 2, 0, 2, "お腹空いたにゃ…\n賄いのたい焼きを\nいただくにゃ！", 1, 116801, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5095, 3, 0, 2, "猫は夜が本番にゃ\nでもあたしは…\nふわぁ～眠いにゃ…", 1, 116801, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5097, 2, 0, 3, "ふかふかにゃ～\nここで体を丸めて\n一眠りしたいにゃ", 1, 116801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5098, 3, 0, 3, "これは…！\n猫にも負けない\nかわいさだにゃ…！", 1, 116801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5099, 4, 0, 3, "いい香りにゃ～\nゴロゴロのどが\n鳴りそうだにゃ～", 1, 116801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5100, 5, 0, 3, "ふにゃ～…\nこのまま働かずに\nまるまってたいにゃ…", 1, 116801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5102, 7, 0, 3, "商売は戦いにゃ\nたくさん食べて\nたくさん働くにゃ！", 1, 116801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5103, 8, 0, 3, "ふむふむ。たい焼きの\nアレンジレシピ集…\nためになるにゃ…", 1, 116801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5105, 103, 0, 1, "お芝居の前に\nお兄ちゃんの顔を\n見ると、とっても\n落ち着きますぅ…♪", 12, 102101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5106, 103, 0, 2, "お兄ちゃんにもっと\n見てもらいたい…\nだから私…\n強くなりたいです…！", 12, 102101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5107, 1, 0, 1, "さらなる効率化で\nみんなといい仕事を\nしたいわね！", 1, 116901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5108, 2, 0, 1, "他のバイトとの\nスケジュールを\n調整しないと…", 1, 116901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5109, 3, 0, 1, "時給がアップしたわ！\n働きが認められると\nやる気が出るわね", 1, 116901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5110, 4, 0, 1, "効率的に動ければ\n業務を追加できる…\n燃えてきたわ！", 1, 116901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5111, 5, 0, 1, "帳簿付けのコツ…\nユカリさんに教わって\nみようかしら", 1, 116901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5112, 101, 0, 1, "バイトリーダーとして\nみんなをビシバシ\n鍛えていくわ！", 4, 116901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5113, 102, 0, 1, "戦いでもバイトでも\nキミなら安心して\n背中を預けられるわね", 8, 116901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5114, 1, 0, 2, "どうしたの？\n仕事のことなら\nなんでも相談してね！", 1, 116901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5115, 2, 0, 2, "さすがは私の弟子ね\nこれなら安心して\n仕事を任せられるわ", 1, 116901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5116, 3, 0, 2, "みんなのお手本になる\n働きぶりを見せないと\nいけないわね！", 1, 116901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5117, 4, 0, 2, "キミのこと…もっと\n頼りにしちゃっても\nいいのかしら…？", 1, 116901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5118, 101, 0, 2, "ふふ、キミも一人前の\nバイト戦士として\n立派に成長したわね！", 4, 116901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5119, 102, 0, 2, "新しいおもちゃの\n企画書を作ってみたの\nちょっと見てくれる？", 8, 116901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5120, 1, 0, 2, "朝は一番効率がいい\n時間帯よ！\nバリバリ働くわよ！", 1, 116901, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5121, 2, 0, 2, "さ、お昼に行きましょ\n今日の日替わり定食は\n何かしら…楽しみね♪", 1, 116901, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5122, 3, 0, 2, "もうこんな時間…\nちゃんと寝て明日に\n備えなくちゃね", 1, 116901, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5124, 2, 0, 3, "適度な労働と休息の\nサイクルが最大効率を\n生み出すのよ…ふぅ…", 1, 116901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5125, 3, 0, 3, "はぁ～なんて素敵な\n抱き心地なのかしら…\n最高効率の癒やしね♪", 1, 116901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5126, 4, 0, 3, "落ち着く香りね…\nなんだか集中力が\nアップした気がするわ", 1, 116901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5127, 5, 0, 3, "ふぅ…癒されるわね…\n仕事のオンオフの\n切り替えは重要よね", 1, 116901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5129, 7, 0, 3, "働くためには\nエネルギーをちゃんと\n摂っておかないと", 1, 116901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5130, 8, 0, 3, "効率を上げるため\n生産管理の勉強も\nしておきたいわね", 1, 116901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5132, 1, 0, 1, "変身している間に\n色々と試しておこう\nまずは…", 1, 180301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5133, 2, 0, 1, "飛べるというのは\n便利だな。料理が\n暖かいうちに\n持ち帰れる", 1, 180301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5134, 3, 0, 1, "プリンセスか…\n柄ではないけれど\n嫌な気はしないな", 1, 180301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5135, 4, 0, 1, "この力で\n何を成すべきなのか…\n剣に聞いてみよう", 1, 180301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5136, 5, 0, 1, "変身したとて私は私\n一時的に強くなろうと\n元が弱くてはだめだ", 1, 180301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5137, 101, 0, 1, "変身していない時でも\n彼がそばにいると\n力が湧いてくる…", 4, 180301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5138, 102, 0, 1, "私の思い…\nいつか伝えてみせるよ\n…きっといつか！", 8, 180301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5139, 1, 0, 2, "何者かと戦うの？\nそれとも修行？\nどちらだとしても\n付き合うよ", 1, 180301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5140, 2, 0, 2, "変身し続けたら\nどれだけ空腹に…\n私は試さないからね？", 1, 180301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5141, 3, 0, 2, "本物のプリンセスに\nなるのは御免だが\nナイトがいて\nくれるのは頼もしいよ", 1, 180301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5142, 4, 0, 2, "いつかこの力を\n誰かを救うために…\n戦い以外に使える時が\nくることを願うよ", 1, 180301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5143, 101, 0, 2, "キミに感じてほしいな\n私に流れてくる\nキミからの力を…\nその…私の、思いも…", 4, 180301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5144, 102, 0, 2, "思いを告げる…\nそれがこんなにも\n難しいなんて…\nでも、いつか必ず…！", 8, 180301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5145, 1, 0, 2, "おはよう！\nいい変身日和だ！\n…ふふっ、冗談だよ", 1, 180301, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5146, 2, 0, 2, "いつもより多く\n昼食を用意しよう\n変身が解けたら大変だ", 1, 180301, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5147, 3, 0, 2, "一日お疲れ様\n私もそろそろ変身を\n解いて休むとするよ", 1, 180301, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5149, 2, 0, 3, "変身してしまうと\n動かずにいても\n空腹になるのかな…？", 1, 180301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5150, 3, 0, 3, "私もキミたちくらい\n可愛げがある姿に\nなっているかな？", 1, 180301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5151, 4, 0, 3, "香りに対する感覚も\nいつもより鋭く\nなっているみたいだ…", 1, 180301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5152, 5, 0, 3, "ありがたいな\n少し足元が\n冷えていたところだ", 1, 180301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5154, 7, 0, 3, "これは…！\n持ち運べるように\n改良できないかな？", 1, 180301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5155, 8, 0, 3, "祖父に貰った\n技法書を読んで\n初心に帰ろう", 1, 180301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5157, 1, 0, 1, "最強の惚れ薬を作って\nあのおかたの心を\n私の思いのままに…", 1, 117001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5158, 2, 0, 1, "夏といえば海にスイカ\n花火に水着…\nそして惚れ薬ですわ…", 1, 117001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5159, 3, 0, 1, "あのおかたの心を\n手に入れるための\n準備を進めましょう\nこの夏こそ確実に…", 1, 117001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5160, 4, 0, 1, "あのおかたに近づく\n虫どもを、どうやって\n片付けようかしら…\nまったく忌々しいこと", 1, 117001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5161, 5, 0, 1, "…クスクス\n薬の調合を考えるのは\n至福の時間ですわ…", 1, 117001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5162, 101, 0, 1, "あのおかたの全てが\n早く私のものに\nなりますように…", 4, 117001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5163, 102, 0, 1, "かき氷も花火も海も…\nあなた様がいてこそ…\nそう、あなた様は\n私の全てなのですわ", 8, 117001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5164, 1, 0, 2, "二人だけの夏を\n楽しみましょう…？\n…クスクス", 1, 117001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5165, 2, 0, 2, "あら、あなた様\n私に秘密でどこに\n行こうとして\nいますの…？", 1, 117001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5166, 3, 0, 2, "海に行きませんか？\nあなた様と一緒に\n過ごすには\n最高の場所ですから…", 1, 117001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5167, 4, 0, 2, "ちょうどいいところに\nいらっしゃいました\nできたての薬を…\nさぁどうぞ…クスクス", 1, 117001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5168, 101, 0, 2, "夏の海のきらめきも\nあなた様の存在が\nなければ色あせて\nしまいますわ…", 4, 117001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5169, 102, 0, 2, "私とあなた様は\n結ばれる運命に\nあるのです…\n逃がしませんわ…", 8, 117001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5170, 1, 0, 2, "起きてすぐに会いに\n来てくださるなんて\nようやく私の伴侶たる\n自覚が…クスクス", 1, 117001, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5171, 2, 0, 2, "お食事はまだですか？\n特製の薬草ランチを\n召し上がれ…クスクス", 1, 117001, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5172, 3, 0, 2, "私の夢を見られる薬を\n差し上げますわ…\nおやすみなさいませ\nあなた様…クスクス", 1, 117001, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5174, 2, 0, 3, "いい肌触りですわ…\n考え事にもぴったり\n眠くなるのが\n玉に瑕ですが…", 1, 117001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5175, 3, 0, 3, "こういったものに\n揮発性の薬を仕込む\nのも一つの手ですわね", 1, 117001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5176, 4, 0, 3, "この香り…開発中の\n薬に使えそうな気が\nしますわ…クスクス", 1, 117001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5177, 5, 0, 3, "あのおかたの\nぬくもりを\n思い出しますわ…", 1, 117001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5179, 7, 0, 3, "美味しい…この味なら\n薬を混ぜても違和感は\nなさそうですわ…", 1, 117001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5180, 8, 0, 3, "この薬の調合法…\n今度試してみましょう\nもちろんあのおかたに\n飲んでいただいて…", 1, 117001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5182, 10, 0, 3, "二人乗りができると\nいいですわね…", 1, 117001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5183, 11, 0, 3, "ふかふかですわ…\nあのおかたの分も\n欲しくなります…", 1, 117001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5184, 103, 0, 1, "今日はキミと\nいつまでも歌って\n踊りたい気分さ～♪", 12, 101701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5185, 103, 0, 2, "あは～♪\nキミの隣は、故郷より\n落ち着くかも～…♪", 12, 101701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5186, 1, 0, 1, "この水着…\n一番に弟くんに\n見せたいな♪", 1, 117101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5187, 2, 0, 1, "弟くんをこっそり\n海に運んで…\nサプライズしちゃお♪", 1, 117101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5188, 3, 0, 1, "いつもと違う髪型に\nしてみたけど…\n気に入ってくれるかな", 1, 117101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5189, 4, 0, 1, "海は楽しみだけど\n日焼けしないように\n気を付けなきゃね", 1, 117101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5190, 5, 0, 1, "まだかな、弟くん\n水着のお姉ちゃんが\nお出迎えだよっ♪", 1, 117101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5191, 101, 0, 1, "海が見えるおうちで\n弟くんと一緒に\n暮らしたいなぁ～", 4, 117101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5192, 102, 0, 1, "弟くんの水着…\nどんな感じかなぁ\n…あ、想像してたら\nドキドキして…えへへ", 8, 117101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5193, 1, 0, 2, "こーら、水着の\nお姉ちゃんは\nおさわり禁止♪\n…なんてね？", 1, 117101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5194, 2, 0, 2, "なぁに？\nふふっ、何回でも\n呼んでいいんだよ♪", 1, 117101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5195, 3, 0, 2, "海で迷子に\nならないように\n手を繋ごうね♪", 1, 117101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5196, 4, 0, 2, "お姉ちゃんが\n海の楽しみ方を\n教えてあげるねっ！", 1, 117101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5197, 101, 0, 2, "わっ、弟くん\nどこ触ってるの？\nも～驚いちゃったぞ♪", 4, 117101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5198, 102, 0, 2, "お姉ちゃん、海でも\nずっとキミを見てるよ\n…安心してね", 8, 117101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5199, 1, 0, 2, "まだ眠そうだね？\nほら、海が待ってるよ\n早く起きて起きて♪", 1, 117101, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5200, 2, 0, 2, "大丈夫、疲れてない？\n海で遊び疲れた時は\n素直に休憩しようね", 1, 117101, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5201, 3, 0, 2, "ほら、早く寝ないと\n明日もお姉ちゃんと\n遊べないぞ♪", 1, 117101, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5203, 2, 0, 3, "だらけたお姉ちゃんは\n弟くんには\n見せられないな～", 1, 117101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5204, 3, 0, 3, "ふふっ、かわいいね♪\n弟くんが持つと\nもっとかわいいよっ♪", 1, 117101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5205, 4, 0, 3, "いい香りだね…\n一味違うお姉ちゃんに\nなれそう…♪", 1, 117101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5206, 5, 0, 3, "あったか～い♪\nほら、弟くんも\nおいでよ♪", 1, 117101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5208, 7, 0, 3, "よく食べて、いつでも\n弟くんの呼び声に\n応える準備を…！", 1, 117101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5209, 8, 0, 3, "弟くんの水着の写真\nこの辺りに隠して\nおこうかな", 1, 117101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5211, 10, 0, 3, "この子の背に乗って\nお姉ちゃんと弟くんの\n楽園へ…な～んて♪", 1, 117101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5212, 11, 0, 3, "海を感じる感触だね\n弟くんとの楽しい夏を\n思い出しちゃうな…♪", 1, 117101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5213, 1, 0, 1, "この夏、誰よりも\nキラキラしてみせる！\nだって私は\nアイドルだから！", 1, 117201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5214, 2, 0, 1, "日傘にアームカバー\n帽子にサングラス…\n紫外線対策は完璧！", 1, 117201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5215, 3, 0, 1, "とっておきの\nサマーソングを\nみんなに届けるよ！", 1, 117201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5216, 4, 0, 1, "なんにもしないのって\n苦手かも…\n彼、早く来ないかな？", 1, 117201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5217, 5, 0, 1, "ラムネって\n夏って感じするよね\n後で彼と飲もっと♪", 1, 117201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5218, 101, 0, 1, "夏は恋の季節！\n燃えるような恋…\nいつかしたいな", 4, 117201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5219, 102, 0, 1, "今年の夏の目標は\n…彼との思い出を\nいっぱい作ること！", 8, 117201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5220, 1, 0, 2, "私の水着写真集\n見てくれたんだ！\nどう、かわいかった？", 1, 117201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5221, 2, 0, 2, "スイカ割りしたいの？\nいいね、楽しそう！\nみんなも誘おうよ♪", 1, 117201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5222, 3, 0, 2, "ねぇ、見て見て！\n水着の映える\nポーズ考えたんだ！", 1, 117201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5223, 4, 0, 2, "ライブ中に\n花火を打ち上げたら\n絶対盛り上がるよね！", 1, 117201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5224, 101, 0, 2, "最近、忙しいけど…\nキミの顔見たら\n疲れなんて\n消えちゃった！", 4, 117201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5225, 102, 0, 2, "この夏もいーっぱい\nドキドキさせるから！\n覚悟しててよね♪", 8, 117201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5226, 1, 0, 2, "外は朝から暑いね\nけど、負けないから！\nお仕事、頑張るぞー！", 1, 117201, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5227, 2, 0, 2, "自分へのご褒美に、\nアイス食べちゃお♪\nキミも一緒に食べよ！", 1, 117201, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5228, 3, 0, 2, "夏の夜って\nワクワクするね♪\nホタル見に行こうよ！", 1, 117201, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5230, 2, 0, 3, "ふわぁ…眠い…\n仕事とかの疲れ、\nたまってるのかな…？", 1, 117201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5231, 3, 0, 3, "私のぬいぐるみが\nグッズになったら…\n彼にかわいがって\nもらえるかな？", 1, 117201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5232, 4, 0, 3, "この香り…\n夏限定のやつでしょ？\nいいセンスしてるね♪", 1, 117201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5233, 5, 0, 3, "暑～い！\nけど…汗かいて\n気持ちいいかも！", 1, 117201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5235, 7, 0, 3, "レッスンの後のご飯は\n特別おいしい♪\n…もうひとつだけ\n食べちゃおっかな", 1, 117201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5236, 8, 0, 3, "あ、この前撮影した\n水着グラビアだ\nうんうん、いい感じ！", 1, 117201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5238, 10, 0, 3, "この子、もふもふだね\n触るのクセになりそう\nもふもふもふもふ…", 1, 117201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5239, 11, 0, 3, "わ、かわいい♪\n今度のグラビア撮影で\nこれ使ってもいい？", 1, 117201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5240, 1, 0, 1, "水着っていいですよね\n海に浮かぶ一輪の花\nって感じがして！", 1, 117401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5241, 2, 0, 1, "自分の水着を作るのも\n誰かの水着を作るのも\n楽しいですね♪", 1, 117401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5242, 3, 0, 1, "水着の最終チェック！\n伸縮性や動きやすさは\n…いい感じですね！", 1, 117401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5243, 4, 0, 1, "振り付けや衣装作りに\n歌や踊りの練習…\n…やることがいっぱい", 1, 117401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5244, 5, 0, 1, "はぁ…レイ様の水着…\n私に作らせて\nもらえないかなぁ～…", 1, 117401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5245, 101, 0, 1, "騎士さんのことは\n気に入らないですけど\n別に嫌いという\nわけじゃなくて…", 4, 117401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5246, 102, 0, 1, "騎士さん暇そうですね\nに、荷物持ちとして\n誘ってみようかな…", 8, 117401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5247, 1, 0, 2, "騎士さんいいところに\nこの衣装\nどう思います？", 1, 117401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5248, 2, 0, 2, "何見てるんですか？\nもしかしてこの水着の\n良さが分かります！？", 1, 117401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5249, 3, 0, 2, "真夏でも涼しい…\nそれも水着の良さの\n一つですね", 1, 117401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5250, 4, 0, 2, "仕立て屋も\nアイドルも\n諦めません！", 1, 117401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5251, 101, 0, 2, "夏と言えば\nバカンスですけど…\n予定、あるんですか？", 4, 117401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5252, 102, 0, 2, "う～ん…騎士さんを\n海へ誘うには…って\nなんでもないですよ！", 8, 117401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5253, 1, 0, 2, "ふー…夏はやっぱり\n朝が一番過ごしやすい\nですね～！", 1, 117401, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5254, 2, 0, 2, "暑い時こそ海です！\n体力づくりのためにも\n泳ぎにいきますよ！", 1, 117401, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5255, 3, 0, 2, "外で虫が鳴いてる…\n夏って感じがして\n風流ですねぇ…", 1, 117401, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5257, 2, 0, 3, "少し休憩です…\nこのまま寝ないよう\n気をつけないと…", 1, 117401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5258, 3, 0, 3, "か、かわいい…！\nこの感動を次の\nステージ衣装にも…！", 1, 117401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5259, 4, 0, 3, "いい香りですね～\n波の音が聞こえて\nきそうです…", 1, 117401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5260, 5, 0, 3, "泳いだ後は\n少し寒いので\nちょうどいいですね", 1, 117401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5262, 7, 0, 3, "夏バテしないように\n栄養はしっかり\n取らないと！", 1, 117401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5263, 8, 0, 3, "次のトレンドは…\nふむふむ、やっぱり\nこう来ましたか…", 1, 117401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5265, 10, 0, 3, "デザインはもちろん\n触り心地も\n最高ですね♪", 1, 117401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5266, 11, 0, 3, "夏の撮影スポットに\n活用できそうですね\nさっそく衣装考えて…", 1, 117401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5267, 103, 0, 1, "えへへ…♪\n団長さんが初めての\n友達で本当に良かった", 12, 104001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5268, 103, 0, 2, "あっ、団長さん！\n今日のＢＢ団の活動\nなんですけど…二人で\n大縄跳びしませんか！", 12, 104001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5269, 1, 0, 1, "この衣装で\nライブするのは\n恥ずかしいな…", 1, 117301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5270, 2, 0, 1, "せっかくの夏だし\n肝試しに\n行ってみたいかも…", 1, 117301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5271, 3, 0, 1, "歌もダンスも\nより上を目指すために\n夏合宿をしよう…！", 1, 117301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5272, 4, 0, 1, "夏の暑さにも負けない\n熱いライブに\nしてみせる…！", 1, 117301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5273, 5, 0, 1, "新曲のアイデア\nまとめたいな…\n少し海辺を散歩しよう", 1, 117301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5274, 101, 0, 1, "かき氷、食べたいな\n…あの人は何味が\n好きなんだろう？", 4, 117301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5275, 102, 0, 1, "水着、似合ってるって\n言ってもらえるかな…", 8, 117301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5276, 1, 0, 2, "サインして、ですか？\nいいですけど…\n少し照れますね…", 1, 117301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5277, 2, 0, 2, "少し待ってください\n今、新曲の楽譜を\n書いているので…", 1, 117301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5278, 3, 0, 2, "なんでしょう？\nもしかして…\nボイトレに興味が？", 1, 117301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5279, 4, 0, 2, "今度のライブでは\n夏らしい爽やかな歌を\nファンにお届けします", 1, 117301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5280, 101, 0, 2, "…水鉄砲で遊ぼう？\nふふ。楽しそうですね\nお手柔らかに…♪", 4, 117301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5281, 102, 0, 2, "あなたの声を\n聴いていると、\n何だか落ち着きます…", 8, 117301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5282, 1, 0, 2, "今日も暑くなりそう…\nお互いしっかりと\n水分補給しましょうね", 1, 117301, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5283, 2, 0, 2, "お腹空きましたよね\n海の家で、\nなにか買ってきます", 1, 117301, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5284, 3, 0, 2, "夜の海で月明かりと\n波の音を楽しもうかと\n…一緒に行きますか？", 1, 117301, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5286, 2, 0, 3, "レッスンだけでなく\nリラックスすることも\n大事、ですよね", 1, 117301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5287, 3, 0, 3, "ふふ、かわいい\n精霊も負けないくらい\nかわいいですけどね", 1, 117301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5288, 4, 0, 3, "夏らしい爽やかで\nいい匂い…あなたに\nピッタリな香りですね", 1, 117301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5289, 5, 0, 3, "海に入った後は、\n体が冷えてしまいます\n一緒に温まりましょう", 1, 117301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5291, 7, 0, 3, "アイドルとして、\n食べすぎには\n気をつけないと…", 1, 117301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5292, 8, 0, 3, "夏は恋の季節…\nガンガン攻めるべし…\nなるほど…", 1, 117301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5294, 10, 0, 3, "この子に乗りながら\nライブをする…\nというのはどうですか", 1, 117301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5295, 11, 0, 3, "まるで人魚になった\n気分…なんて、ふふ", 1, 117301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5296, 1, 0, 1, "お姉ちゃんとして\nシオリンにかっこいい\nとこ見せなくっちゃ☆", 1, 118301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5297, 2, 0, 1, "いつかシオリンと\n一緒に空を飛んで\n空中散歩してみたいな", 1, 118301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5298, 3, 0, 1, "すこし眠くなってきた\nかも…シオリンと\nお昼寝しようかな", 1, 118301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5299, 4, 0, 1, "お散歩してこよっと！\nシオリンとお出かけ\nするのにいい場所\n探さなくっちゃ！", 1, 118301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5300, 5, 0, 1, "シオリンに過保護って\n言われちゃった…♪\nそれでも心配しちゃう\nのが姉心…だよね？", 1, 118301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5301, 101, 0, 1, "気持ちいい日差し！\nシオリンと彼と、\nお昼寝したら\n気持ちよさそ～♪", 4, 118301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5302, 102, 0, 1, "今度天気がいい日には\nシオリンと彼を誘って\n一緒にお出かけ\nしたいな～♪", 8, 118301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5303, 1, 0, 2, "今日はご機嫌なんだ♪\nだって、シオリンの\n調子がいいんだもん☆", 1, 118301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5304, 2, 0, 2, "今からシオリンと\nピクニックに\n行くんだ♪　そうだ！\nキミも一緒にどう？", 1, 118301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5305, 3, 0, 2, "わっ、びっくりした！\nシオリンから借りた本\n読むのに集中\nしてたから…", 1, 118301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5306, 4, 0, 2, "うん？　どうしたの？\nあっ、もしかして\nこの服、気になる？", 1, 118301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5307, 101, 0, 2, "シオリンもキミも元気\nいっぱいだと、私も\n元気になっちゃうよ☆", 4, 118301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5308, 102, 0, 2, "シオリンの前だけ\nじゃなくて、キミにも\nかっこいい私の姿を\n見てほしいな☆", 8, 118301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5309, 1, 0, 2, "おはよう！　えへへっ\n今日は早起きなんだ♪\nシオリンにかっこ悪い\nとこ見せたくないから", 1, 118301, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5310, 2, 0, 2, "おなかすいたなぁ～…\nシオリンと一緒に\nおいしいもの食べに\n行こうかな！", 1, 118301, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5311, 3, 0, 2, "もうこんな時間！？\nシオリンと一緒だと\n時間が早く\n過ぎていっちゃうね！", 1, 118301, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5313, 2, 0, 3, "この快適な寝心地…！\nシオリンにもおすすめ\nしなきゃ…！", 1, 118301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5314, 3, 0, 3, "わあ、かわいい！\nそれにさわり心地も\nいいし、抱き枕に\nちょうどいいかも！", 1, 118301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5315, 4, 0, 3, "いいにおい…♪\nなんだかリラックス\nできて…すやぁ…", 1, 118301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5316, 5, 0, 3, "ぽかぽか～☆\nシオリンと一緒に\nぬくぬくしたら\n気持ちよさそう♪", 1, 118301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5318, 7, 0, 3, "仲のいい人が一緒だと\nつい食が進んじゃうね\nえへへ…♪", 1, 118301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5319, 8, 0, 3, "ふむふむ…この本…\nシオリンが好きかも！\n今度おすすめしよっと", 1, 118301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5322, 1, 0, 1, "お姉ちゃん、\n私のために無理は\nしてないかな…？", 1, 118401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5323, 2, 0, 1, "新しく買った絵本…\nこういうのなら\nお姉ちゃんと楽しく\n読めるよね", 1, 118401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5324, 3, 0, 1, "ニャットちゃんを\nここに連れてきて\nみんなで遊べないかな", 1, 118401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5325, 4, 0, 1, "本屋さんに行こうかな\nみんなで読める\n面白い本が\nあるといいなぁ", 1, 118401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5326, 5, 0, 1, "たまには…読むだけ\nじゃなくて、自分で\nお話を書いて\nみようかな…？", 1, 118401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5327, 101, 0, 1, "時々…お姉ちゃんと\nあの人が似てるって\n思うことがあります…", 4, 118401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5328, 102, 0, 1, "お姉ちゃんとあの人…\n優しくて頼もしい\n二人に囲まれて…\n私、幸せ者だなぁ…", 8, 118401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5329, 1, 0, 2, "どうかしましたか？\nもしかして…あなたも\n一緒にこの本を\n読みたい…とか？", 1, 118401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5330, 2, 0, 2, "けほっ…けほっ…\nあっ、心配いりません\n飲み物を飲んで\nむせただけですから", 1, 118401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5331, 3, 0, 2, "この格好…\n似合ってますか…？\nえへへ、照れますね…", 1, 118401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5332, 4, 0, 2, "なぁに、お姉ちゃん？\n…あっ、すみません\n読書に集中していて…\n間違えちゃいました…", 1, 118401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5333, 101, 0, 2, "お姉ちゃん、時々無理\nしちゃうから…\nあなたも一緒だと\n安心できます", 4, 118401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5334, 102, 0, 2, "お姉ちゃんとあなたと\n頼れる人に囲まれて\n私は幸せものですね", 8, 118401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5335, 1, 0, 2, "なんだか朝から\n調子がいい…今日は\nいい日になりそうです", 1, 118401, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5336, 2, 0, 2, "せっかくだから、\nお姉ちゃんを誘って\nお外でランチも\nいいかも", 1, 118401, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5337, 3, 0, 2, "お姉ちゃんに心配\nかけないように…もう\nちょっと本を読んだら\n寝なくちゃね", 1, 118401, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5339, 2, 0, 3, "ふんわり柔らかくて\n気持ちいい手触り…\nお姉ちゃんが寝るのに\nちょうどよさそう", 1, 118401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5340, 3, 0, 3, "ふふっ、かわいい♪\n絵本の物語みたいに\nおしゃべりできない\nかな…？", 1, 118401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5341, 4, 0, 3, "素敵な落ち着く香り♪\nなんだかお姉ちゃん\nみたい、ふふっ", 1, 118401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5342, 5, 0, 3, "あったかくてほっと\nするなぁ\nここでゆっくり\n読書しようかな", 1, 118401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5344, 7, 0, 3, "この味…\nお姉ちゃんが好きそう\n少しもらって、\n持って行こうかな…？", 1, 118401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5345, 8, 0, 3, "この本みたいな、\n心躍る大冒険も\nお姉ちゃんと一緒なら\nできちゃいそう", 1, 118401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5348, 1, 0, 1, "いい部屋ね\nここを私の分院に\nするのもいいかも\nしれないわね…♪", 1, 117501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5349, 2, 0, 1, "珍しいカンポーが\n手に入ったから\n色々試してみたいわ\n色々と、ね♪", 1, 117501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5350, 3, 0, 1, "この服を着ると\n自分が医者だって\nより実感するわね\nまあ、闇医者だけどね", 1, 117501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5351, 4, 0, 1, "新薬開発のために\n薬草を採取して\nおこうかしら", 1, 117501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5352, 5, 0, 1, "いつ急患が来ても\n対応できるように\nしておきましょうか", 1, 117501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5353, 101, 0, 1, "あの子の研究資料も\nだいぶ増えてきたわね\nちゃんとまとめて\nおかないと♪", 4, 117501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5354, 102, 0, 1, "あの子は大事な\n研究対象。だから\nいつまでも健康で\nいてもらわないとね♪", 8, 117501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5355, 1, 0, 2, "薬に興味があるの？\nなら、今度ゆっくりと\n教えてあげるわね", 1, 117501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5356, 2, 0, 2, "少し時間があいたから\n一緒に新薬の実験を\nしましょうか♪", 1, 117501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5357, 3, 0, 2, "どうしたの？\nまさか急患…！？\nあら、違うのね\nならよかったわ", 1, 117501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5358, 4, 0, 2, "具合が悪い時はすぐに\n言うのよ。君には\nいつでも健康でいて\nほしいから", 1, 117501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5359, 101, 0, 2, "少し仮眠したいわね\n肩…いいえ、膝を\n貸してもらえる？", 4, 117501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5360, 102, 0, 2, "たまには…君の隣なら\n闇医者も悪い研究者も\nお休みにするのも\n悪くないわね♪", 8, 117501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5361, 1, 0, 2, "たまには朝の陽ざしを\n浴びるのも…\n悪くないわね", 1, 117501, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5362, 2, 0, 2, "天気がいい日は\nいい薬草が採れるの\n君も集めるの、\n手伝ってくれる？", 1, 117501, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5363, 3, 0, 2, "いけない、もうこんな\n時間なのね\n診察に没頭すると\n時間感覚がなくなるわ", 1, 117501, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5365, 2, 0, 3, "もう少し固い方が\n私好みだけど…\n悪くない寝心地ね", 1, 117501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5366, 3, 0, 3, "このぬいぐるみ…\n闇医者として\n興味深いわね…", 1, 117501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5367, 4, 0, 3, "落ち着く香りね\n私も診察室で\n使ってみようかしら", 1, 117501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5368, 5, 0, 3, "冷えは病につながるわ\nしっかり温めて\n体をいたわりましょ", 1, 117501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5370, 7, 0, 3, "おいしい♪\n研究のお供に\nもってこいの\n味だわ", 1, 117501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5371, 8, 0, 3, "医学は日進月歩…\n闇医者とはいえ\n最新の論文には\n目を通しておかないと", 1, 117501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5400, 1, 0, 1, "白衣の天使かあ\nまさにボクのための\n呼び方だね", 1, 117601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5401, 2, 0, 1, "美しさとは\n日々の努力が\n作るものなんだよ", 1, 117601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5402, 3, 0, 1, "大変だ…\nボクは美しく\nなりすぎたかも", 1, 117601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5403, 4, 0, 1, "ボクのかわいさで\n癒やされて\nみんな健康に\nなっちゃうね", 1, 117601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5404, 5, 0, 1, "どんな服装をしても\nボクの美しさは\n変わらないね", 1, 117601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5405, 101, 0, 1, "これからはボクも\n美の伝道師として\nがんばらないとね♪", 4, 117601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5406, 102, 0, 1, "ボクのかわいい笑顔を\n近くで見る以上の\n癒やしはないよね♪", 8, 117601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5407, 1, 0, 2, "どう？　看護師姿の\nボクもかわいいでしょ\nもっと近くで見る？", 1, 117601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5408, 2, 0, 2, "ほら！　美しいボクの\n看護師姿を見たら\n元気でちゃうでしょ？", 1, 117601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5409, 3, 0, 2, "白衣の天使のボクも\n最高に美しいね\n存分に癒されてよ♪", 1, 117601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5410, 4, 0, 2, "具合が悪いの？\nならボクの美しい顔を\n処方してあげる♪", 1, 117601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5411, 101, 0, 2, "さあ今日もボクと\n一緒に美の追求に\n付き合ってもらうよ♪", 4, 117601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5412, 102, 0, 2, "かわいい天使のボクが\nいつもそばにいるんだ\nキミって幸せ者だね♪", 8, 117601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5413, 1, 0, 2, "さすがボク\n目が覚めるような\n美しさでしょ？", 1, 117601, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5414, 2, 0, 2, "美は食で作られる\n美容にいいものを\n食べようよ！", 1, 117601, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5415, 3, 0, 2, "ふああ…早く寝よ？\n夜更かしは美容の\n大敵だからね", 1, 117601, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5417, 2, 0, 3, "ふう～…\nリラックスする\nボクも美しい", 1, 117601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5418, 3, 0, 3, "ボクが抱きしめると\n普段よりかわいく\n見えちゃうね♪", 1, 117601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5419, 4, 0, 3, "美しいボクは\n常にいい香りを\nしていなくちゃね", 1, 117601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5420, 5, 0, 3, "光がボクを照らしてる\nんじゃなくて、ボクが\n光を輝かせてるのさ", 1, 117601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5422, 7, 0, 3, "バランスのいい食事が\n美しいボクを\n作るのさ", 1, 117601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5423, 8, 0, 3, "ふむふむ…なるほど\nこうすればより美しく\nなれるかもしれない", 1, 117601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5425, 103, 0, 1, "仲間のためなら\nきっと…どこまでも\n強くなれるよね", 12, 105701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5426, 103, 0, 2, "プリンセスフォーム？\n私と同じような力を\n持ってる人がいるの？\n会ってみたいな～！", 12, 105701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5427, 1, 0, 1, "ここの空気は、\nどうもゆるすぎて…\n落ち着きませんわね", 1, 118001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5428, 2, 0, 1, "陛下のお越しになる\n別宅としては、\n少し警備が\n甘すぎでは？", 1, 118001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5429, 3, 0, 1, "あらあら？\nサッシにホコリが！\nちょっとネコ助！\n掃除は念入りに！", 1, 118001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5430, 4, 0, 1, "いつまで入り浸って\nいるのかと、\n秘書に叱られて\nしまいましたわ…", 1, 118001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5431, 5, 0, 1, "もういっそ、\nここに間借りして\n仕事しようかしら…？", 1, 118001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5432, 101, 0, 1, "邪魔している身ですし\n家事くらい\nお手伝いさせなさいな", 4, 118001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5433, 102, 0, 1, "本当は、意外と\n居心地が良いのかも…\n陛下の気持ち、\n少し分かりましたわ", 8, 118001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5434, 1, 0, 2, "ちょっ…わたくしを\n叩いても、ホコリなど\nあまり出ませんわよ！", 1, 118001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5435, 2, 0, 2, "陛下っ？\nなんだ…貴方でしたの\n用件は手短にお願いね", 1, 118001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5436, 3, 0, 2, "別に、好き嫌いは\nありませんけれど…\n魔物料理って、意外と\n美味なものですわね", 1, 118001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5437, 4, 0, 2, "言っておきますけれど\nわたくしは商売人です\n荒事は専門外ですわよ", 1, 118001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5438, 101, 0, 2, "プリンセスナイトの\n能力…なにか儲け話に\n使えませんかしらね", 4, 118001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5439, 102, 0, 2, "貴方がたのことも、\n少しくらいは\n信用してないことも\nありませんでしてよ", 8, 118001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5440, 1, 0, 2, "おはようございます\nさ、今日もバリバリ\n働きますわよ！", 1, 118001, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5441, 2, 0, 2, "お昼ですわね\n栄養補給をしつつ\n働きますわよ！", 1, 118001, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5442, 3, 0, 2, "定時ですって？\n関係ありません！\nわたくし、まだまだ\n働きますわよ～っ！", 1, 118001, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5444, 2, 0, 3, "うっ…いけませんわ\n日頃の疲れが…\nふわぁ…", 1, 118001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5445, 3, 0, 3, "もふもふ…ふふっ♪\n…なんですのっ、\nなにかおかしくて！？", 1, 118001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5446, 4, 0, 3, "いい香りですわね\nわたくしの執務室にも\n置きたいくらいですわ", 1, 118001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5447, 5, 0, 3, "いけませんわ…\nこんなに暖かいと\n日頃の疲れが…ふわぁ", 1, 118001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5449, 7, 0, 3, "もう一つ食べたら\n仕事に戻りますわよ？\nあと一つだけ…", 1, 118001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5450, 8, 0, 3, "商人にとって\n知識は武器ですわ\n陛下のため、貴方も\nもっと勉強なさい？", 1, 118001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5452, 1, 0, 1, "故郷のお菓子を\nいろいろ作ったんだ～\nみんなに\n配ってくるさ～♪", 1, 117701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5453, 2, 0, 1, "彼ってどんな仮装が\n似合うかな～？\nシーサーとか？", 1, 117701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5454, 3, 0, 1, "カボチャチャンプルー\n作ってみよっと！\nきっとおいしいさ～♪", 1, 117701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5455, 4, 0, 1, "視線を感じるさ～\nもしかして幽霊？\nおしゃべりするさ～♪", 1, 117701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5456, 5, 0, 1, "さっき作った\nサーターアンダギー、\n早く彼に\n食べてほしいさ～♪", 1, 117701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5457, 101, 0, 1, "彼のために、\n特別なイタズラを\n考えとくさ～♪", 4, 117701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5458, 102, 0, 1, "いつか彼と、\n故郷のハロウィンを\n楽しみたいさ～♪", 8, 117701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5459, 1, 0, 2, "ハロウィンは\n私たちが守るさ～♪\nさ、見回りに行こ！", 1, 117701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5460, 2, 0, 2, "この制服、\nハロウィンらしくて\n大好きさ～♪", 1, 117701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5461, 3, 0, 2, "イタズラする気ね～？\nどんなことされるのか\n楽しみさ～♪", 1, 117701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5462, 4, 0, 2, "カボチャのランタン、\n一緒に作ろ！\n絶対楽しいよ～♪", 1, 117701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5463, 101, 0, 2, "お菓子くれるの？\nあは～、ありがとー♪\nもぐもぐ…\n優しい味がするさ～♪", 4, 117701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5464, 102, 0, 2, "キミと一緒に過ごす\nハロウィンは、\nドキドキとワクワクが\n止まらないさ～♪", 8, 117701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5465, 1, 0, 2, "ハロウィンは\n朝から大忙しさ～！\nお菓子作り、\n手伝ってほしいさ～", 1, 117701, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5466, 2, 0, 2, "お化け屋敷に行こ！\n一緒にいっぱい\nドキドキしようね～♪", 1, 117701, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5467, 3, 0, 2, "ここからが\nハロウィン本番さ～！\n幽霊たちと\nいっぱい遊ぶさ～♪", 1, 117701, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5469, 2, 0, 3, "ちょっと休んだら、\nお菓子作りを\n再開するさ～♪", 1, 117701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5470, 3, 0, 3, "でーじかわいいね～♪\nこの子に仮装させたら\nきっと似合うさ～♪", 1, 117701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5471, 4, 0, 3, "お菓子みたいに\n甘い匂い…\nハロウィンらしい\nいい香りさ～♪", 1, 117701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5472, 5, 0, 3, "ランドソルの秋って\n冷えるけど…\nこれがあれば\n体も心もぽかぽかさ～", 1, 117701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5474, 7, 0, 3, "故郷のご飯も\nおいしいけど、\nこっちのご飯も\nでーじおいしいさ～♪", 1, 117701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5475, 8, 0, 3, "パンプキンパイの\nレシピは…なるほど～\n勉強になるさ～♪", 1, 117701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5477, 12, 0, 3, "全部おいしそうで\nどれから食べるか\n迷っちゃうさ～♪", 1, 117701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5479, 1, 0, 1, "ムっ！　幽霊の気配を\n感じるデス！\nさあ姿を現すデス！", 1, 117801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5480, 2, 0, 1, "ムムム…\n妖魔の力を忍法に\n使えないでショウか…", 1, 117801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5481, 3, 0, 1, "怪しい妖魔を\n成敗するデス！\nハロウィンの平和は\nワタシが守りマスよ！", 1, 117801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5482, 4, 0, 1, "ワタシの祭囃子が\n聞こえマスかー？\nさあ集まるデース！\n陽気に踊りまショウ！", 1, 117801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5483, 5, 0, 1, "お祭りは\n楽しんだ者勝ちデス！\n踊らにゃ\nソンソンデス！", 1, 117801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5484, 101, 0, 1, "幽霊になる前に\n天下統一を\n果たしたいデスね", 4, 117801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5485, 102, 0, 1, "あの世に行っても\nショーグンは\nワタシの大事な\nショーグンデス！", 8, 117801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5486, 1, 0, 2, "ショーグン！\n悪霊退治に\n出かけるデース！", 1, 117801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5487, 2, 0, 2, "妖魔を家来に\nしてみるのは\nどうデス？", 1, 117801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5488, 3, 0, 2, "マントを使って\nウツセミの術の精度を\n高めるデス！", 1, 117801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5489, 4, 0, 2, "ハロウィンにちなんだ\n新しい忍法…\nすなわち妖忍法の\n開発を急ぐデス！", 1, 117801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5490, 101, 0, 2, "あの世でも\nショーグンに\nお仕えしマスよー！", 4, 117801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5491, 102, 0, 2, "ショーグンもワタシと\nハロウィンパレードを\n踊り明かしまセンか？", 8, 117801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5492, 1, 0, 2, "おはようございマス！\n天下統一に向けて\n急いで準備するデス！", 1, 117801, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5493, 2, 0, 2, "お腹減ってまセンか？\nハロウィンスイーツで\n休憩するデ〜ス", 1, 117801, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5494, 3, 0, 2, "夜更かしすると幽霊が\nでるかもしれまセン…\nヒュ〜ドロロロ〜…", 1, 117801, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5496, 2, 0, 3, "もふもふなところが\nわたあめちゃんに\n似ていマス。わんっ！", 1, 117801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5497, 3, 0, 3, "ぬいぐるみや人形には\n魂が宿るという\n言い伝えがありマス…\nマカフシギデ〜ス", 1, 117801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5498, 4, 0, 3, "ウ〜ム…\nなんとも怪しい\n香りがしマスね！", 1, 117801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5499, 5, 0, 3, "パレードの熱気に\n負けないくらい\n熱いデス！", 1, 117801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5501, 7, 0, 3, "ついつい食べすぎて\nしまうのは\n妖魔のせいデスか！？", 1, 117801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5502, 8, 0, 3, "トーゴク妖怪の\n図鑑デス！　個性的な\n妖怪ばかりデスね！", 1, 117801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5504, 12, 0, 3, "ハロウィンの甘味を\n集めまシタよ！\nショーグンへの\n貢ぎ物デス！", 1, 117801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5531, 103, 0, 1, "彼と二人で\n買い物に…って…\nこれってデートに\nなっちゃうのかな！？", 12, 101301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5532, 103, 0, 2, "とうっ！　どうどう？\n今の決めポーズ！\nかっこいいよね♪", 12, 101301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5533, 1, 0, 1, "ヒデサイとおそろの\nハロウィンコスとか\nしてみたいな～", 1, 117901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5534, 2, 0, 1, "カボチャ料理かぁ…\n作ったらヒデサイ\n食べてくれるかな～", 1, 117901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5535, 3, 0, 1, "ハロウィンスイーツ…\nどれもおいしすぎて\n激ヤバなんですけど！", 1, 117901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5536, 4, 0, 1, "霊界ってどんなとこ\nなんだろ～\nわりと近所だったり？", 1, 117901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5537, 5, 0, 1, "服をハロウィンっぽく\nアレンジするのも\nたのしーよね♪", 1, 117901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5538, 101, 0, 1, "ヒデサイと一緒に\nハロウィンパーティー\n…えへへ、楽しみ～", 4, 117901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5539, 102, 0, 1, "ヒデサイといると\nドキドキする…\nなんでだろ～？", 8, 117901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5540, 1, 0, 2, "ねねね、うちらも\nハロウィンパレード\n見に行こー？", 1, 117901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5541, 2, 0, 2, "ハロウィンセイバーズ\nしゅつどーう！\nヒデサイも行こ～♪", 1, 117901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5542, 3, 0, 2, "マントのロゴ\nかっこいいっしょ～？\nカボきちってゆーんだ", 1, 117901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5543, 4, 0, 2, "トリックオア\nトリート！\nねえねえヒデサイ\nお菓子ちょうだい♪", 1, 117901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5544, 101, 0, 2, "ヒデサイも\nメイクしてみる？\nハロウィンぽいやつ♪", 4, 117901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5545, 102, 0, 2, "ヒデサイのこと\nうちがいつでも\nあっためたげるね！\n…あ！　手、手の話！", 8, 117901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5546, 1, 0, 2, "ハロウィンのイベント\n朝からでも\nやってるかな～？", 1, 117901, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5547, 2, 0, 2, "カボチャでおやつ\n作ってみたんだ～\nヒデサイ食べてみて！", 1, 117901, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5548, 3, 0, 2, "ハロウィンの夜って\nやっぱテンション\n上がるよね～♪", 1, 117901, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5550, 2, 0, 3, "超ふかふかしてる～\nちょっと休憩させて\nもらっちゃお", 1, 117901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5551, 3, 0, 3, "はぁ…\nもにカワすぎて\nつらいんですけど～！", 1, 117901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5552, 4, 0, 3, "ハロウィン限定の\nフレグランスじゃん！\nいい匂いする～♪", 1, 117901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5553, 5, 0, 3, "やばばば…\nあったかくて\n眠くなっちゃう～", 1, 117901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5555, 7, 0, 3, "これ食べて\nもうひとがんばり\nしよーっと！", 1, 117901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5556, 8, 0, 3, "ハロウィンっぽい\nファッションとか\n載ってないかなぁ？", 1, 117901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5558, 12, 0, 3, "お菓子いっぱいだ～！\nあとでヒデサイにも\n持ってってあげよ～", 1, 117901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5560, 1, 0, 1, "お掃除が終わったら\n次は書類を片付けて…\nその次は…", 1, 118501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5561, 2, 0, 1, "皆さんが冒険している\n間の留守は私に\nお任せください♪", 1, 118501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5562, 3, 0, 1, "美食殿の皆さんが\n持ってきてくれる\nお土産が楽しみです♪", 1, 118501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5563, 4, 0, 1, "ネビアさんとは\nときどき一緒にお茶\nする仲なんです", 1, 118501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5564, 5, 0, 1, "ギルドハウスの乱れは\nギルドの乱れ\n整理整頓が大切です", 1, 118501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5565, 101, 0, 1, "素敵なギルドハウスの\n管理人ができて\nとっても幸せです♪", 4, 118501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5566, 102, 0, 1, "皆さんをほんの少し\n特別扱いしちゃう\n私がいます", 8, 118501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5567, 1, 0, 2, "何かわからないことが\nあるときは私に\n聞いてくださいね", 1, 118501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5568, 2, 0, 2, "書類を書いていると\n落ち着きます…\n一緒にどうですか？", 1, 118501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5569, 3, 0, 2, "あなたのまわりは\nいつも賑やかで\n楽しいですね♪", 1, 118501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5570, 4, 0, 2, "あっ！　お頼みしたい\nことがあって\n探してたんです♪", 1, 118501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5571, 101, 0, 2, "ギブアンドテイクは\n大切です。たまには\nお礼させてください♪", 4, 118501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5572, 102, 0, 2, "陰に日向にあなたを\n支えていきます♪\n期待していてください", 8, 118501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5573, 1, 0, 2, "今日も一日\n管理人として\nがんばります♪", 1, 118501, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5574, 2, 0, 2, "お腹がすきました…\nそろそろお昼に\nしませんか？", 1, 118501, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5575, 3, 0, 2, "もうこんな時間…\n皆さんといると一日が\nあっという間です", 1, 118501, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5577, 2, 0, 3, "良質なお仕事は\n良質な休憩から\nです♪", 1, 118501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5578, 3, 0, 3, "かわいいものに\n囲まれてお仕事できる\nのは幸せですね♪", 1, 118501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5579, 4, 0, 3, "お仕事に集中できる\nアロマを設置するのも\nよさそうですね♪", 1, 118501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5580, 5, 0, 3, "ポカポカ温まりながら\n書類仕事をするのも\nいいものですね…", 1, 118501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5582, 7, 0, 3, "栄養補給しないと\n頭が回りませんからね\nきちんと食べましょう", 1, 118501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5583, 8, 0, 3, "新条例はこのように\n活用されている、と…\nふむふむ…", 1, 118501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5585, 1, 0, 1, "会合用の資料と\n連載のネーム…\nどっちを片付けよう？", 1, 106701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5586, 2, 0, 1, "イノリちゃん、また\nお金くすねてる…\n帰ってきたら\nお尻ペンペンだ♪", 1, 106701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5587, 3, 0, 1, "カヤちゃんの装備\n傷むの早いなぁ〜\nメンテしてあげないと", 1, 106701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5588, 4, 0, 1, "そうだ、ラーゴのご飯\n買いに行かないと\n今日は何がいいかな？", 1, 106701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5589, 5, 0, 1, "ここの人たちは見てて\n飽きない人ばっかり♪\n漫画の参考にしよ〜", 1, 106701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5590, 101, 0, 1, "また一緒に買い物にも\n行きたいし、冒険も\nしたいな〜…♪", 4, 106701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5591, 102, 0, 1, "…君は見てるだけで\n満足なの？ ちゃんと\n触って欲しいな…♪", 8, 106701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5592, 1, 0, 2, "私の目が見たいの？\nいいけど…魅了されて\n魂が壊れちゃうよ…？\n…冗談で〜す☆", 1, 106701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5593, 2, 0, 2, "ラーゴがね、君に\n遊んで欲しいって☆\n仲良くしてあげてね", 1, 106701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5594, 3, 0, 2, "アシスタントの仕事\n気に入ってくれたなら\nまたお願いしていい？", 1, 106701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5595, 4, 0, 2, "私が覚醒できるか？\n見せてあげたいけど…\nすごいことになるから\nまた今度で♪", 1, 106701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5596, 101, 0, 2, "楽しいトラブルが\nあったら、とりあえず\n声はかけて欲しいな♪", 4, 106701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5597, 102, 0, 2, "マッサージお願い♪\n変なところは…\n少しだけなら☆", 8, 106701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5598, 1, 0, 2, "おはよう〜♪\n今日も一日、一緒に\nがんばっていこうね", 1, 106701, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5599, 2, 0, 2, "お腹空いちゃった…\n一緒にお昼行かない？\nいいお店があるんだ〜", 1, 106701, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5600, 3, 0, 2, "君、眠れないの？\n誰かに見られてる？\n…きっと気のせいだよ", 1, 106701, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5602, 2, 0, 3, "ちょっと休憩〜…\nこのまま原稿すれば\n疲れなくなるのかな？", 1, 106701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5603, 3, 0, 3, "かわいい〜♪\nラーゴのお友達に\nなってくれる？", 1, 106701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5604, 4, 0, 3, "いい香り〜♪\nこの目でどう見えるか\n伝えられたらいいのに", 1, 106701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5605, 5, 0, 3, "ほどほどなのが\nちょうどいい時も\nあるよね…♪", 1, 106701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5607, 7, 0, 3, "この味付け…\nあっちでも再現\nできるかな？", 1, 106701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5608, 8, 0, 3, "ふふっ、面白い♪\nイノリちゃんの買う\n漫画にハズレなし☆", 1, 106701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5610, 1, 0, 1, "私…23歳だもん\n魔法少女だなんて\n名乗れないわ…", 1, 119001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5611, 2, 0, 1, "あん、どぅ、とろわ〜\nみんなイイ子に\nな〜る♪", 1, 119001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5612, 3, 0, 1, "あっ！　イケナイ子の\n気配がするわ！\n風紀の乱れは\n先生が許しませんっ！", 1, 119001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5613, 4, 0, 1, "ここで闇の力が\n発動したら大変だわ！\nやだっ…そう思ったら\n緊張して…ダメぇ〜！", 1, 119001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5614, 5, 0, 1, "オイタした子はみんな\nどうしてうれしそうに\n私の愛のムチを\n欲しがるのかしら…？", 1, 119001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5615, 101, 0, 1, "あの子に次は何を\n教えてあげようかしら\n愛？　希望？\nそれとも…♪", 4, 119001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5616, 102, 0, 1, "あの子のことを\n考えると、愛と希望で\n胸がいっぱいになるの", 8, 119001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5617, 1, 0, 2, "先生なのに闇の力を\n持ってるのは秘密よ？\n他の子には\n知られたくないから…", 1, 119001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5618, 2, 0, 2, "秘密の授業の内容？\nそれは…あんなことや\nこんなことよ♪\nそれから、それから…", 1, 119001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5619, 3, 0, 2, "しーっ！　私の正体が\nルーセント学院の\n先生だって、ここでは\n秘密にしててね？", 1, 119001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5620, 4, 0, 2, "あら？　補習して\nほしいの？　いいわよ\n君のお願いなら\nなんでも聞いちゃう♪", 1, 119001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5621, 101, 0, 2, "イイ子にはごほうびを\nあげなくちゃ！\n君はなにが欲しい？\n遠慮しちゃダメよ？", 4, 119001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5622, 102, 0, 2, "君との秘密が\nまた一つ\n増えちゃったね♪", 8, 119001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5623, 1, 0, 2, "今日も一日\n元気に過ごしましょう\n先生が指導してあげる", 1, 119001, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5624, 2, 0, 2, "夜まで頑張れるように\nお昼ご飯はしっかり\n食べましょう〜！", 1, 119001, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5625, 3, 0, 2, "夜更かしはダメよ？\nイケナイ子が\nすることです！", 1, 119001, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5627, 2, 0, 3, "闇の力に振り回されて\nもうヘトヘトよ〜\n心も体も\n癒されたいわ〜", 1, 119001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5628, 3, 0, 3, "『秘密の学び舎』に\nこっそり連れて\n行っちゃおうかしら♪", 1, 119001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5629, 4, 0, 3, "イケナイ甘い香り…\nなんだか胸の奥が\n熱くなってきたわ…♪", 1, 119001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5630, 5, 0, 3, "やんっ♪\n燃え上がる愛みたい♪\n火照ってきちゃった♪", 1, 119001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5632, 7, 0, 3, "生徒の見本に\nなれるように\n好き嫌いはしないの", 1, 119001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5633, 8, 0, 3, "『学び舎に咲く禁断の\n愛』？　気になるわ…\nううん、ダメよっ！\nでも…ちょっとだけ…", 1, 119001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5635, 103, 0, 1, "次に会う時は、\nまたあそこに一緒に…\nえへへ…！", 12, 106301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5636, 103, 0, 2, "横顔見てるの、\nバレてないよね…？\n…ちらっ", 12, 106301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5637, 103, 0, 1, "背が少し伸びたか！？\nあ、あいつのところへ\n報告に行かねばっ！", 12, 105301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5638, 103, 0, 2, "私も貴公の隣に\n立っていられるよう\n日々精進していくぞ！", 12, 105301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5639, 1, 0, 1, "ああ～\nドＭミストが\nあふれてしまいます～", 1, 119101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5640, 2, 0, 1, "魔法少女として\nクウカ頑張ります～！\n愛と希望の妄想を…！", 1, 119101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5641, 3, 0, 1, "決めポーズの練習より\nやられポーズの練習を\nしておきます…♪", 1, 119101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5642, 4, 0, 1, "快感を溜めに溜めて\n放つドＭビームは\n最高ですよ～", 1, 119101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5643, 5, 0, 1, "はっ！\nこれは闇の気配！\n…クウカ自身でした", 1, 119101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5644, 101, 0, 1, "ドＳさん…ぐふふ…\nクウカを縛りあげ…\nじゅるりぃ～♪", 4, 119101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5645, 102, 0, 1, "ドＳさんに攻められる\nことで、クウカは進化\nしてゆくのです～！", 8, 119101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5646, 1, 0, 2, "はぅっ！\nもっと激しく…！\n情熱的に～～～！", 1, 119101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5647, 2, 0, 2, "クウカの変身シーンを\nじっくり眺めたい！？\nは、恥ずかしいです～", 1, 119101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5648, 3, 0, 2, "「本当に魔法少女か\n確かめてやるよ…！」\nドＳさんはそう言って\nクウカを…ぐふふ…♪", 1, 119101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5649, 4, 0, 2, "愛と希望の力が\nクウカの中に…\nこの胸の高鳴りが…？", 1, 119101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5650, 101, 0, 2, "ひゃあんっ！\nそこです～っ！\nもっと、もっと～♪", 4, 119101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5651, 102, 0, 2, "クウカをこんなに\n悦ばせるのは\nドＳさんだけです…♪", 8, 119101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5652, 1, 0, 2, "明るい時間に出歩くと\n目立ちますね\n魔法少女の衣装…", 1, 119101, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5653, 2, 0, 2, "お昼の見回りです～♪\nああ～うっかりドＭを\n増やしてしまいました", 1, 119101, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5654, 3, 0, 2, "夜！　それはクウカが\n活性化する時間です！\n闇の活動開始です～♪", 1, 119101, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5656, 2, 0, 3, "気の緩んだクウカを\nドＳさんの鋭い牙が\n狙っています～♪", 1, 119101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5657, 3, 0, 3, "魔法少女のマスコット\nにいいかもしれません\nもっと鎖とか飾りを…", 1, 119101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5658, 4, 0, 3, "魅惑的な香りです～\nドＭミストの香りに\n似て…いや違いますね", 1, 119101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5659, 5, 0, 3, "火照る体…紅潮した\nクウカの肢体を熱く\n見つめるドＳさん…", 1, 119101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5661, 7, 0, 3, "食欲を満たした次は\n何を満たせば\nいいですかねぇ～♪", 1, 119101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5662, 8, 0, 3, "魔法少女がひどい目に\n遭う物語は…\nなさそうですね…", 1, 119101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5664, 1, 0, 1, "ケーキも悪くないけど\nプリンが一番なの\nでもせっかくだから\n両方もらってやるの！", 1, 119901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5665, 2, 0, 1, "クリスマスって聞くと\nなんだかあったかい\n気分になるの～", 1, 119901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5666, 3, 0, 1, "煙突がなくても\nミヤコはどこからでも\nおうちに入れるの♪", 1, 119901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5667, 4, 0, 1, "イブもプリン！\nクリスマスもプリン！\nプレゼントもプリン！", 1, 119901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5668, 5, 0, 1, "クリスマスツリーの\n高いところの\n飾りつけは、ミヤコに\nまかせろなの～！", 1, 119901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5669, 101, 0, 1, "アイツのプレゼント\n楽しみなの～\nきっと最っ高に特別な\nプリンなの～♪", 4, 119901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5670, 102, 0, 1, "ミヤコがサンタなら\nアイツはトナカイなの\nつまりお似合いって\nことなの～♪", 8, 119901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5671, 1, 0, 2, "ミヤコ、プレゼントを\n配りにいくの～♪\nオマエも手伝えなの！", 1, 119901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5672, 2, 0, 2, "オマエにも飾りつけ\nしてやるの！\n頭にプリンを乗せると\nかわいいの♪", 1, 119901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5673, 3, 0, 2, "幽霊のミヤコが\nいるなら、サンタも\nいて当たり前なの～！", 1, 119901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5674, 4, 0, 2, "靴下にプリンを\n入れたらつぶれるの\nだからプリンは直接\nミヤコによこすの～！", 1, 119901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5675, 101, 0, 2, "今日はプリンだけじゃ\n満足してやらないの！\nミヤコをたくさん\n甘やかせなの！", 4, 119901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5676, 102, 0, 2, "ミヤコのプリン\nオマエになら一個…\nうう～、一口だけ\nあげてもいいの！", 8, 119901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5677, 1, 0, 2, "サンタの朝は早いの～\nミヤコも早起きして\nプレゼント準備したの\nとってもえらいの♪", 1, 119901, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5678, 2, 0, 2, "今日は一日中\nみんなと遊ぶの～！\nオマエも来いなの！", 1, 119901, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5679, 3, 0, 2, "みんなが寝たら\nチャンスなの～\nこっそりプレゼント\n配りにいくの♪", 1, 119901, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5681, 2, 0, 3, "むふー、気持ちいいの\nこういうプレゼントも\nいいかもしれないの", 1, 119901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5682, 3, 0, 3, "これ気に入ったの～\nプリンとアイツの\n次くらいなの♪", 1, 119901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5683, 4, 0, 3, "いいにおいなの～\nうっかり成仏\nしちゃいそうなの…", 1, 119901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5684, 5, 0, 3, "ここに飾っといた\n雪だるま\nなくなっちゃったの\nきっと成仏したの～", 1, 119901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5686, 7, 0, 3, "これはオマエがくれる\nプリンとは\n別腹なの～", 1, 119901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5687, 8, 0, 3, "プリンケーキ…\nこれならミヤコにも\n作れそうなの！\nさっそく作るの！", 1, 119901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5689, 14, 0, 3, "びっくりしたの～！\n幽霊のミヤコを\n驚かすなんて…\nなかなかやるの！", 1, 119901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5690, 15, 0, 3, "コイツの中…\nあったかいの～♪\n昼寝にぴったりなの～", 1, 119901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5691, 1, 0, 1, "メリークリスマス！\n聖夜に笑顔を\nお届けするべ♪", 1, 119201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5692, 2, 0, 1, "クリスマス前は\n乳製品がよく売れて\nありがてぇけんど…\n目が回る忙しさだ～", 1, 119201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5693, 3, 0, 1, "牧場で売る\nクリスマスリースを\n作っておかねぇと…", 1, 119201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5694, 4, 0, 1, "うちの動物たちにも\nクリスマスプレゼント\n用意しなきゃなぁ", 1, 119201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5695, 5, 0, 1, "クリスマスに向けた\n限定商品とか\n作ってみるべか？", 1, 119201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5696, 101, 0, 1, "あんちゃんへの\nプレゼント選び…\nここでボケるか…？\nやめておくべか…？", 4, 119201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5697, 102, 0, 1, "オラ、あんちゃんから\n本当にたくさんの物を\nもらってんなぁ…", 8, 119201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5698, 1, 0, 2, "雪遊びがしたいべか？\nへへっ、あんちゃん\nには負けねぇど！", 1, 119201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5699, 2, 0, 2, "ディナーと一緒に\n聖夜のお笑いライブを\nすんのはどうだべか？\nツッコミは任せたべ！", 1, 119201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5700, 3, 0, 2, "寒くなってくると\n鍋が食べたくなるべや\nあんちゃんはどうだ？", 1, 119201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5701, 4, 0, 2, "あんちゃんは\nサンタさんのこと\n信じてるだか？", 1, 119201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5702, 101, 0, 2, "どでかいクリスマス\nケーキをこさえるべ\nあんちゃんも作るの\n手伝ってくんろ♪", 4, 119201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5703, 102, 0, 2, "あんちゃんが\nオラの旦那だったら…\nって、あんちゃん！\n今の聞いてただか！？", 8, 119201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5704, 1, 0, 2, "牧場の朝は早いど！\nさぁてと、まずは\n雪かきしに行くべや", 1, 119201, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5705, 2, 0, 2, "あんちゃんも一緒に\nお昼食べてくか？\n採れたての冬野菜は\nうんまいど～♪", 1, 119201, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5706, 3, 0, 2, "よい子は早く寝ねぇと\nサンタさんが\n来てくれねぇべよ？", 1, 119201, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5708, 2, 0, 3, "こういうのリンリンが\n好きそうだな…\nプレゼント候補に\n入れておくべ！", 1, 119201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5709, 3, 0, 3, "もふもふしてて\nなまらめんこくて…\nあぁ…癒されるべや…", 1, 119201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5710, 4, 0, 3, "この匂い…\n気持ちが和むべや\n夢心地になっちまうな", 1, 119201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5711, 5, 0, 3, "あったけぇところは\n落ち着くなぁ…\nつい眠くなっちまう", 1, 119201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5713, 7, 0, 3, "こ、この味は…！\n…うーん、もっと\nリアクション芸も\n練習しねぇとな", 1, 119201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5714, 8, 0, 3, "シオシオって\nどんな本読んでんだ？\n…難しい字が\nぎっしりだべや…", 1, 119201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5716, 14, 0, 3, "この仕掛け…\nオラのコントに\n活かせるかも\nしんねぇな", 1, 119201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5717, 15, 0, 3, "この着ぐるみで\nリマリマのモノマネを\n練習してみるべか…？", 1, 119201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5718, 103, 0, 1, "もっとキレイになって\nアイツにぎゃふんと\n言わせてやるんだから", 12, 105001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5719, 103, 0, 2, "アタシのこと\nどう思う？\nかわいいって…そこは\nキレイって言ってよね", 12, 105001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5720, 1, 0, 1, "ハートマークの刺繍…\nは、やりすぎですかね\nいやちょっとくらい\nアピールしてみても…", 1, 119301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5721, 2, 0, 1, "雪降らないかな～\nそしたらお兄ちゃんと\n雪だるま作ったり\nできるのに～", 1, 119301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5722, 3, 0, 1, "プレゼントを入れる\n靴下…いくつ編んでも\n足りません～！", 1, 119301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5723, 4, 0, 1, "お兄ちゃんとの\nクリスマスデートは\n早い者勝ちですからね\n負けませんよ～！", 1, 119301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5724, 5, 0, 1, "クリスマスだし…\n受験勉強は今日だけ\nお休みです♪", 1, 119301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5725, 101, 0, 1, "お兄ちゃんと\nおそろいのコーデで\nクリスマスデート…\nしたいなぁ～！", 4, 119301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5726, 102, 0, 1, "お兄ちゃんに\nプレゼントする編み物\nこれで３着目～と♪\nあれ、作りすぎ？", 8, 119301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5727, 1, 0, 2, "えへへ、もっと近くで\n見たくありませんか？\n私のとっておきの\nクリスマスコーデ♪", 1, 119301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5728, 2, 0, 2, "お兄ちゃんが風邪を\n引いたら一大事です！\n私の編み物で暖かく\nしてあげます♪", 1, 119301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5729, 3, 0, 2, "お兄ちゃん、ほっぺに\nケーキのクリームが\nついちゃってますよ♪\n私がとってあげます！", 1, 119301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5730, 4, 0, 2, "お兄ちゃん！\nケーキ、あーんって\nしてあげます！\nほら、あーん♪", 1, 119301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5731, 101, 0, 2, "クリスマスは大切な\n人と過ごすものっ！\nお兄ちゃん…妹の予定\n空いてますよっ！", 4, 119301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5732, 102, 0, 2, "クリスマスはずっと、\nお兄ちゃんと一緒に\nいたい…って言ったら\n欲張り…すぎですか？", 8, 119301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5733, 1, 0, 2, "早起きは３本お得！\n…クリスマスなら\n追加で10本ぐらい\nもらえちゃうかも！？", 1, 119301, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5734, 2, 0, 2, "お昼はケーキ？\nそれとも七面鳥？\nごちそういっぱいで\n迷っちゃいますね～♪", 1, 119301, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5735, 3, 0, 2, "お兄ちゃんと\nクリスマスに夜更かし\n…えへへ、ちょっぴり\nドキドキしちゃう♪", 1, 119301, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5737, 2, 0, 3, "編み物をするには\n絶好のくつろぎ空間\nですね～♪", 1, 119301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5738, 3, 0, 3, "なんというかわいさ…\nクリスマス\nプレゼントに\nもってこいですね！", 1, 119301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5739, 4, 0, 3, "これがお兄ちゃんの\nお部屋の香り…？\nんん～！　ずっと\nかいでいたいです～♪", 1, 119301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5740, 5, 0, 3, "ふぁ、あったか～い…\n冬場はこういうのが\nありがたいですね～♪", 1, 119301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5742, 7, 0, 3, "ごちそうだぁ～♪\nほら、お兄ちゃんも\n食べて食べてっ！", 1, 119301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5743, 8, 0, 3, "クリスマスデートの\n必勝法…こ、これは\n読むしかないです！", 1, 119301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5745, 14, 0, 3, "わっ、すごいです～！\nこれを飾っておけば\nクリスマスが一層\n賑やかになりますね♪", 1, 119301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5746, 15, 0, 3, "さぷらーいずっ！\nリマちゃんじゃなくて\nリノちゃんでした～！", 1, 119301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5747, 1, 0, 1, "ミミにもミソギにも\nあの人にも頼りに\nしてもらえるように\nしっかりしなきゃ！", 1, 120601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5748, 2, 0, 1, "ミミはにんじんを\n食べられてすごいな…\n私も見習わなきゃ\nがんばろう…！", 1, 120601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5749, 3, 0, 1, "ミソギから怖い話\n聞いちゃった…\nうぅ…誰か一緒に\nいてくれないかな", 1, 120601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5750, 4, 0, 1, "立派な大人に\nなれるように\nいっぱい勉強\nしておかなくっちゃ！", 1, 120601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5751, 5, 0, 1, "なんだか…あの人の\nあやしい視線を\n感じるような…？", 1, 120601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5752, 101, 0, 1, "あの人に勉強を\n教えられるように\nしっかり予習を\nしておこうっと…！", 4, 120601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5753, 102, 0, 1, "今日はあの人と\n一緒に冒険できる…\nなんだかワクワク\nしてきちゃった…！", 8, 120601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5754, 1, 0, 2, "あ、いいとこに来て\nくれましたね！\nあなたに頼みたい\nことがあるんです！", 1, 120601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5755, 2, 0, 2, "きゃあっ！？\nへ、変なところを\n触らないでください！", 1, 120601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5756, 3, 0, 2, "どうですかこの格好？\nかわいい、ですか…？\nそ、そうですか…\nありがとうございます", 1, 120601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5757, 4, 0, 2, "ひゃあっ…！\nすみません、勉強に\n集中してたので\n驚いちゃいました", 1, 120601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5758, 101, 0, 2, "一緒に勉強して立派な\n大人になりましょうね\n私がそばで見張ってて\nあげます", 4, 120601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5759, 102, 0, 2, "まだ立派ではないかも\nですけど、私よりも\n大人なあなたのことは\n頼りにしてますからね", 8, 120601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5760, 1, 0, 2, "立派な大人は\n朝の時間の使い方が\n上手だと聞きました\nあなたはどうですか？", 1, 120601, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5761, 2, 0, 2, "ミミとミソギと\nあなたと…大事な人と\n一緒のお昼ごはんは\nやさしい味がしますね", 1, 120601, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5762, 3, 0, 2, "うぅ…怖い話を\n思い出しちゃいました\n寝るまで一緒に\nいてくれますか…？", 1, 120601, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5764, 2, 0, 3, "すごい座り心地…！\nとっても勉強が\nはかどりそう…！", 1, 120601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5765, 3, 0, 3, "この子…なんだか\nミミに似てるような\n気がする…ふふっ♪", 1, 120601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5766, 4, 0, 3, "いい香り…♪\n勉強で疲れた\n頭がリフレッシュ\nできそうです", 1, 120601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5767, 5, 0, 3, "ぽかぽかだ…♪\nミミとミソギと\nあの人と一緒に\nいるときみたい", 1, 120601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5769, 7, 0, 3, "いっぱい食べたら\n早く大人になれたり\nしないかな…？", 1, 120601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5770, 8, 0, 3, "あう…\n知らない単語が…\nちゃんと調べて\n覚えなくっちゃ…！", 1, 120601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5773, 1, 0, 1, "うさぎさんが\nぴょ～んぴょん♪\nみんなえがおで\nるんたった～♪", 1, 120401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5774, 2, 0, 1, "キョウカちゃんも\nいつかにんじんさんを\n好きになってくれたら\nうれしいな～♪", 1, 120401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5775, 3, 0, 1, "今日のミソギちゃんの\nイタズラ、びっくり\nしちゃったけど\nおもしろかったな～♪", 1, 120401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5776, 4, 0, 1, "このおうち、いつも\nいろんな人がいて\nドキドキぴょんぴょん\nって楽しいな～♪", 1, 120401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5777, 5, 0, 1, "いいお天気～♪\nお外でみんなと\nいっぱい遊びたいな♪", 1, 120401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5778, 101, 0, 1, "ミソギちゃんも\nキョウカちゃんも\nおにいちゃんがいると\nずっと笑顔だね～♪", 4, 120401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5779, 102, 0, 1, "おにいちゃんにはやく\nあいたいな～♪\nまだかな、まだかな♪", 8, 120401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5780, 1, 0, 2, "えへへ♪\nおにいちゃんに\nなでなでされると\nぽかぽかするね♪", 1, 120401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5781, 2, 0, 2, "聞いて、おにいちゃん\nさっきね、かわいい\nちょうちょさんに\n会ったんだよ～♪", 1, 120401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5782, 3, 0, 2, "ふわっ…！\nおにいちゃんの顔を\nお絵かきしてたから\nびっくりしちゃった♪", 1, 120401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5783, 4, 0, 2, "おにいちゃんも\n遊びたいの？\nそれじゃあ二人も\n呼んで遊ぼう♪", 1, 120401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5784, 101, 0, 2, "おにいちゃんも\nみんなも、優しい人\nばっかりで\nミミ幸せ～♪", 4, 120401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5785, 102, 0, 2, "おにいちゃん♪\n高い高いしてほしいな\nすっごくぴょんぴょん\nできて、好きなんだ♪", 8, 120401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5786, 1, 0, 2, "おはよう～♪\nおにいちゃん、今日は\nおひさまみたいに\nきらきら元気だね～♪", 1, 120401, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5787, 2, 0, 2, "おひるごはんだね～♪\nにんじんさんを\nいっぱい食べて\n元気いっぱい♪", 1, 120401, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5788, 3, 0, 2, "ふわあ…\n眠くなってきちゃった\nおにいちゃん、\n一緒に寝よう～？", 1, 120401, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5790, 2, 0, 3, "ふわぁ、気持ちいい♪\nずっとこうやって\n座ってたくなるね～♪", 1, 120401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5791, 3, 0, 3, "わあ、かわいい～\nぎゅ～っ、ぎゅ～っ♪\nこれで仲よしだね\nえへへ♪", 1, 120401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5792, 4, 0, 3, "気持ちいいにおい～♪\nぴょんぴょんしたく\nなってきちゃった♪", 1, 120401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5793, 5, 0, 3, "あったかぽかぽか♪\nおにいちゃんも\nきてくれたら、もっと\nあったかくなりそう♪", 1, 120401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5795, 7, 0, 3, "もぐもぐ…ごっくん♪\nおっきくなりたいから\nいっぱい食べるよ～♪", 1, 120401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5796, 8, 0, 3, "えへへ、おもしろ～い\nこの絵本ね、\nキョウカちゃんから\n教えてもらったんだ♪", 1, 120401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5799, 1, 0, 1, "この部屋…探索の\nしがいがありそう！\nついでにイタズラも\nしかけちゃお♪", 1, 120501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5800, 2, 0, 1, "ミミってああ見えて\n実は大胆で度胸も\nあるよね…ちょっと\nかっこいいな…！", 1, 120501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5801, 3, 0, 1, "怖い話を聞いた時の\nキョウカって\n面白いんだよね～♪", 1, 120501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5802, 4, 0, 1, "にいちゃんが\n慣れちゃわないように\nまた新しいイタズラを\n考えなきゃ！", 1, 120501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5803, 5, 0, 1, "イタズラもいいけど\nミミとキョウカと\nにいちゃんと、普通に\n遊ぶのも楽しいなぁ♪", 1, 120501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5804, 101, 0, 1, "ミミにキョウカ、\nそれににいちゃん！\nみんなそろえば愉快な\n冒険が始まりそう！", 4, 120501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5805, 102, 0, 1, "次はどんなイタズラを\nしたら、にいちゃんは\n喜んでくれるかな？", 8, 120501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5806, 1, 0, 2, "今ミソギに触ったね？\nヤバいよにいちゃん…\n逃げないとトラップが\n発動しちゃうよ…！", 1, 120501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5807, 2, 0, 2, "わわっ！？　びっくり\nしたあ…！　新しい\nトラップを作ってて\n気づかなかったよ～", 1, 120501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5808, 3, 0, 2, "お？　にいちゃん、\nイタズラする側にも\n興味があるの？", 1, 120501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5809, 4, 0, 2, "にいちゃんなにか用？\nあ、もしかして…\nミソギと遊びたく\nなっちゃった？", 1, 120501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5810, 101, 0, 2, "ベストタイミングだよ\nにいちゃん！\n今からみんなで探検に\n行くところなんだ！", 4, 120501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5811, 102, 0, 2, "にいちゃんってさ\nホントに優しいよね\nなにしても怒らないし\n…ありがとね♪", 8, 120501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5812, 1, 0, 2, "にいちゃんおはよー！\n景気づけに本日\n一発目のイタズラ、\nやっちゃうよ～♪", 1, 120501, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5813, 2, 0, 2, "お昼ごはんはロシアン\nサンドイッチだよ！\nさあ、にいちゃん！\n好きなの選んでよ！", 1, 120501, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5814, 3, 0, 2, "夜ってなんだか\nワクワクしてなかなか\n眠れないんだよね\nにいちゃんはどう？", 1, 120501, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5816, 2, 0, 3, "この座り心地のよさ…\nなにかのトラップに\nうまく使えないかな？", 1, 120501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5817, 3, 0, 3, "こういうのに細工して\nミミやキョウカを\n驚かせると、いい反応\nするんだよね♪", 1, 120501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5818, 4, 0, 3, "においをうまく使った\nイタズラって…\nなにかないかな？", 1, 120501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5819, 5, 0, 3, "あったまりながら\n新しいイタズラを\n考えると、いい案が\n出て来たりするんだ♪", 1, 120501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5821, 7, 0, 3, "おいしくてついつい\n食べ過ぎちゃうなぁ…\nはっ…！？　これって\nまさかトラップ…！？", 1, 120501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5822, 8, 0, 3, "このキョウカから\n借りた冒険物語\nすっごく\nおもしろいんだよね！", 1, 120501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5825, 1, 0, 1, "晴れ着を着ると\nなんだか心が躍るね\n足取りも軽く\nなるってもんさ", 1, 120801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5826, 2, 0, 1, "正月の食べ物ってのは\n美味いもんだね\nいくらでも\n食べれちまうよ", 1, 120801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5827, 3, 0, 1, "普段は好き勝手してる\nアタシらも\n正月くらいは\n大人しくするさ", 1, 120801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5828, 4, 0, 1, "年越しそばに雑煮\nこの季節は\n温かいものが\n体に沁みるね", 1, 120801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5829, 5, 0, 1, "正月だからって\nのんびりしすぎたね\nちょっと釣りにでも\n行こうかね", 1, 120801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5830, 101, 0, 1, "いい正月だね\nいつかあの子と\n一杯やりたいもんだよ", 4, 120801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5831, 102, 0, 1, "大事な人と過ごす\n正月ってのがこんなに\n楽しいもんだとはね", 8, 120801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5832, 1, 0, 2, "部屋があったかくて\n顔が火照っちまったよ\nちょいと外に涼みに\n行かないか？", 1, 120801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5833, 2, 0, 2, "餅ってのは\n何をつけてもうまいね\nお前さんは\nどの味が好きだい？", 1, 120801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5834, 3, 0, 2, "お祝いの鯛が\n足りないって？\nそれじゃあアタシが\n釣ってきてやるよ", 1, 120801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5835, 4, 0, 2, "おせちが美味いのは\n分かるけど\n食べ過ぎには\n気を付けるんだよ", 1, 120801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5836, 101, 0, 2, "お前さんのおかげで\nいい正月を\n迎えられたよ\nありがとさん！", 4, 120801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5837, 102, 0, 2, "慣れない晴れ着も\nお前さんに見て\nもらえたなら\n着たかいがあったね", 8, 120801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5838, 1, 0, 2, "おはようさん\n寒いのは分かるけど\nいい加減布団から\n出て顔洗ってきな", 1, 120801, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5839, 2, 0, 2, "今日の昼飯は雑煮だ\nアタシの郷土の味\nしっかり味わって\nおくれよ", 1, 120801, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5840, 3, 0, 2, "おや、お前さんも\n夜ふかしかい？\n退屈してたところさ\n付き合っておくれよ", 1, 120801, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5842, 2, 0, 3, "晴れ着だと座り方にも\n気を遣うね\nしわになったら大変さ", 1, 120801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5843, 3, 0, 3, "せっかくだし\nこの子にも晴れ着を\n着せてあげたいね", 1, 120801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5844, 4, 0, 3, "これは冬の花の香り…\n風情があって\nいいじゃないか", 1, 120801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5845, 5, 0, 3, "あったかいねぇ…\nこんな寒い日には\nまさに天国だよ", 1, 120801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5847, 7, 0, 3, "いつも食べてる\nものでも正月に\n食べると\n新鮮な気がするね", 1, 120801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5848, 8, 0, 3, "せっかくの正月休みだ\nたまにはゆっくりと\n本でも読もうかね", 1, 120801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5850, 1, 0, 1, "新年のあいさつ…\n私も街のみんなに\nしてみようかしら…", 1, 120701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5851, 2, 0, 1, "正月はゆっくり過ごす\nのよね…ゆっくりって\n何したらいいのかしら", 1, 120701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5852, 3, 0, 1, "この格好も\n慣れてくれば\n動きやすいわ", 1, 120701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5853, 4, 0, 1, "う～ん、今年の抱負…\nやりたいことが\nたくさんあるのよね", 1, 120701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5854, 5, 0, 1, "おせち料理\nネビアの分も\n用意しなくちゃね", 1, 120701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5855, 101, 0, 1, "ふふっ\n正月っていいわね\nみんな楽しそうだもの", 4, 120701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5856, 102, 0, 1, "新年の思い出…\nこれからもっと作るわ\nそれが私の抱負よ", 8, 120701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5857, 1, 0, 2, "今日は家で過ごすの？\nあなたと一緒なら\nきっと楽しいわね", 1, 120701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5858, 2, 0, 2, "どう？　この格好？\nコッコロさんは\n褒めてくれたけど…", 1, 120701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5859, 3, 0, 2, "ちょうど良かったわ\nあなたも大掃除\n手伝ってくれる？", 1, 120701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5860, 4, 0, 2, "これから料理するけど\nお雑煮と焼き餅\nどっちが好きかしら？", 1, 120701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5861, 101, 0, 2, "『今の私』にとって\n初めての正月だもの\nあなたと楽しまないと", 4, 120701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5862, 102, 0, 2, "去年よりも今年…\nもっと成長するからね\n…お兄ちゃん♪", 8, 120701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5863, 1, 0, 2, "おはよう\nせっかくの正月だもの\n朝から楽しまないとね", 1, 120701, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5864, 2, 0, 2, "ねぇ…\n良かったら正月の遊び\n教えてくれるかしら？", 1, 120701, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5865, 3, 0, 2, "ふわぁ～…今日が\n終わっちゃうのが…\nもったいないわね…", 1, 120701, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5867, 2, 0, 3, "ふぅ…お参り後だから\n心地よすぎて眠って\nしまいそうだわ…", 1, 120701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5868, 3, 0, 3, "ふふっ\nかわいいものって\n癒されるわね…", 1, 120701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5869, 4, 0, 3, "…ん\nこれが梅の香り？\n…いい匂いね", 1, 120701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5870, 5, 0, 3, "キャルじゃないけど…\nこのまま丸く\nなりたいところね…", 1, 120701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5872, 7, 0, 3, "はむはむっ…♪\nせっかくの正月だもの\n堪能しなくちゃね♪", 1, 120701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5873, 8, 0, 3, "…ふんふん\n帯の結び方って\nこんなにあるのね…", 1, 120701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5875, 103, 0, 1, "今の私が作れる\n最高の一曲…\nあの人に届くといいな", 12, 104201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5876, 103, 0, 2, "あの、歌を…\n聞いてくれませんか？\nあなたに贈る\n特別な歌を", 12, 104201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5877, 1, 0, 1, "ひい、ふう…\nむむ、みなに配る\nお年玉のポチ袋が\n残りわずかなのじゃ…", 1, 120901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5878, 2, 0, 1, "宴の準備はわらわに\n任せておくのじゃ！\n支配者の手腕、\n見せつけてくれる！", 1, 120901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5879, 3, 0, 1, "新年の願掛けなら\nわらわにすればよい\n支配者に頼むほうが\n確実じゃろう？", 1, 120901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5880, 4, 0, 1, "みなからもらった\nこの年賀状とやら…\nわらわも\n書いてみようかの♪", 1, 120901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5881, 5, 0, 1, "正月飾りにもいろいろ\n種類があるんじゃの～\nどれも華々しくて\nわらわ好みじゃ♪", 1, 120901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5882, 101, 0, 1, "あやつへのお年玉は\nちと多めにしておくか\n…日ごろの感謝も\n込めてのう", 4, 120901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5883, 102, 0, 1, "あやつのことを想うと\nそれだけで行く年\n来る年が楽しみになる\nふふ、愉快なやつじゃ", 8, 120901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5884, 1, 0, 2, "みなに晴れ着を\n褒められて、とても\nよい気分なのじゃ♪\n次はお主の番じゃぞ？", 1, 120901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5885, 2, 0, 2, "お主へのお年玉は\nいちばん最後じゃ！\n残り物には福が宿る\nそうじゃからのう", 1, 120901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5886, 3, 0, 2, "この季節は手が\nかじかむのう…ほれ、\nお主！　わらわの手を\nあたためるのじゃ！", 1, 120901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5887, 4, 0, 2, "お主のために雑煮を\n手作りしてみたのじゃ\n食べていくがよい♪", 1, 120901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5888, 101, 0, 2, "眷属のお主に\n正月休みはまだ早い！\nずっとわらわのそばに\n控えておくのじゃ♪", 4, 120901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5889, 102, 0, 2, "ふふ、これほど楽しい\n正月は初めてじゃ…\nお主と過ごす時間は\nいつだって特別じゃな", 8, 120901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5890, 1, 0, 2, "早起きしてわらわを\n待っておったのか？\nさすがはわらわの眷属\n殊勝な心がけじゃな♪", 1, 120901, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5891, 2, 0, 2, "食事どきじゃな\n正月の宴会料理が\nわらわたちを\n待っておるぞ♪", 1, 120901, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5892, 3, 0, 2, "夜も更けてきたのう\n冬の夜は長い…\n心ゆくまでお主と\n過ごせるわけじゃな♪", 1, 120901, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5894, 2, 0, 3, "実に心地がよい♪\nこのまま寝正月も\n悪くないかも\nしれぬのう", 1, 120901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5895, 3, 0, 3, "愛いやつじゃのう\nわらわのそばに置いて\nやってもよいぞ♪", 1, 120901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5896, 4, 0, 3, "品のある香りじゃのう\n寝室に置いておけば\nなにかご利益に\nあずかれそうじゃな", 1, 120901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5897, 5, 0, 3, "心地よい…\nわらわはしばらく\nここから動かぬぞ\nお主も付き合うがよい", 1, 120901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5899, 7, 0, 3, "舌鼓が鳴るのう♪\n正月の宴には\n持ってこいの品じゃ！", 1, 120901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5900, 8, 0, 3, "ほう、新春にはそんな\n習わしが…正月を\n制するには、まだまだ\n勉強が必要じゃのう", 1, 120901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5902, 1, 0, 1, "久しぶりの我が家って\n感じです！　やっぱり\nここが一番ですね〜！", 1, 121001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5903, 2, 0, 1, "さあ食べましょう！\nみんなで囲める食卓を\n心から味わいますよ☆", 1, 121001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5904, 3, 0, 1, "ランドソルに\n帰ってきた途端に\nお腹が空きました〜！", 1, 121001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5905, 4, 0, 1, "ただいま☆\n幸せ特盛りの\nわたしの日常！", 1, 121001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5906, 5, 0, 1, "ようやくランドソルに\n帰ってきたことですし\nやっぱり魔物料理を\nいただきましょう☆", 1, 121001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5907, 101, 0, 1, "あの人との絆を\n守るために\nもっともっと食べて\n強くならなくちゃ☆", 4, 121001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5908, 102, 0, 1, "いつもありがとうって\nあの人を思いっきり\n抱きしめたいです☆", 8, 121001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5909, 1, 0, 2, "あなたの想いは\nこの身をかけて\n全力で受け止めます！", 1, 121001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5910, 2, 0, 2, "おかえりなさい☆\nここにいるみんなも\nあなたの帰りをずっと\n待っていましたよ！", 1, 121001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5911, 3, 0, 2, "このギルドハウスで\n冒険の疲れを\n癒しましょう〜！", 1, 121001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5912, 4, 0, 2, "あなたの想いが\n全力全開を超える\n力をくれました！", 1, 121001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5913, 101, 0, 2, "日常が続く幸せを\nあなたと一緒に\nかみしめたいです☆", 4, 121001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5914, 102, 0, 2, "ぎゅ〜っ！\nわたしたちは\nどんなことがあっても\nずっと一緒ですよ？", 8, 121001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5915, 1, 0, 2, "おはようございます！\nあなたと過ごせる\n幸せを、今日も\n味わいますよ〜！", 1, 121001, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5916, 2, 0, 2, "ご飯の時間ですよー！\nあなたも一緒に\n食べましょうね！", 1, 121001, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5917, 3, 0, 2, "明日もあなたが隣に\nいてくれることを\n心から願ってますよ\nおやすみなさい☆", 1, 121001, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5919, 2, 0, 3, "ふい〜…\n安心できる場所で\nくつろげるって\n幸せですね〜…", 1, 121001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5920, 3, 0, 3, "じゅるり…\n食べたくなっちゃう\nくらい、かわいい☆", 1, 121001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5921, 4, 0, 3, "くんくん…おおー！\nステーキの香り！\nやばいですね☆", 1, 121001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5922, 5, 0, 3, "体がぽかぽかしてきて\nわたしの食欲も\n燃えてきましたよー！", 1, 121001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5924, 7, 0, 3, "わたしの食欲に\nついてこられますか？\nまだまだ食べますよ！", 1, 121001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5925, 8, 0, 3, "これがキャルちゃんの\n言っていた、キスで\n目覚める王子様の\nおとぎ話ですか〜☆", 1, 121001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5927, 1, 0, 1, "自分で自分を\n分析するのも、これは\nこれで面白い…♪", 1, 121201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5928, 2, 0, 1, "強過ぎる力の扱いには\n注意しないと…\n七冠がいい例だね", 1, 121201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5929, 3, 0, 1, "少年の交友関係は\n計り知れないな…\nアタシも含むのか！", 1, 121201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5930, 4, 0, 1, "原理を徹底解明して\n再発を防ぐぞー！\n…誰か手伝って～！", 1, 121201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5931, 5, 0, 1, "これは祝福にも\n呪いにもなる…\n忘れずにいよう", 1, 121201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5932, 101, 0, 1, "少年が\n目を覚ましてくれて\n本当によかった…", 4, 121201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5933, 102, 0, 1, "彼以外を選んでいたら\n今回の事態は\n乗り切れなかったな…\nさすがだよ、少年", 8, 121201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5934, 1, 0, 2, "少年！　調子はどう？\nクレープ食べられる？\nっていうか食べて？", 1, 121201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5935, 2, 0, 2, "元気そうで何よりだ\nまた店を手伝いに\nきてよ。二人もキミに\n会いたがってたしさ", 1, 121201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5936, 3, 0, 2, "キミの診察もあるし\n今はなるべくそばに\nいるよ。迷惑じゃ\nなければだけど", 1, 121201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5937, 4, 0, 2, "やっぱり少年は\nあちこちで人を\n助けてる時が\n一番輝いてるよ", 1, 121201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5938, 101, 0, 2, "アタシは大したこと\nしてないけど\nみんなのことは\n褒めてあげてね", 4, 121201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5939, 102, 0, 2, "みんなを笑顔に\nできるのは\nやっぱり少年だね", 8, 121201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5940, 1, 0, 2, "やあ少年\n朝寝坊はしてない？\nそれじゃ、よい１日を", 1, 121201, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5941, 2, 0, 2, "おや、少年！\nまかないのクレープ\n作ったけど、食べる？", 1, 121201, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5942, 3, 0, 2, "ふわぁ…もう夜か…\n研究してたら\nあっという間だね…\nおやすみ、少年", 1, 121201, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5944, 2, 0, 3, "体がなまってたのか\n前線に出ると疲れる…\nまさか…年のせい！？", 1, 121201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5945, 3, 0, 3, "キミたちも今の\nアタシと同じ感じに\nアレンジしてあげよう", 1, 121201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5946, 4, 0, 3, "誰が置いたんだろう？\nいい香りだねぇ…\nうん、気に入った♪", 1, 121201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5947, 5, 0, 3, "何も考えてない時間も\n実は思考にとって\n大事なんだよね～…", 1, 121201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5949, 7, 0, 3, "食の大切さは\nいつの食事からも\n感じられるね♪", 1, 121201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5950, 8, 0, 3, "ふむ…面白い！\n今度全巻\n借りにこようっと", 1, 121201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5952, 1, 0, 1, "聖学祭か…\n良い記憶はないな\nよし忘れよう", 1, 116401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5953, 2, 0, 1, "健全なる精神は\n健全なる肉体に宿る…\nはっ、知ったことかよ", 1, 116401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5954, 3, 0, 1, "暇だし鏡でも見るか…\nえへへ、ユニだよ♪\nよし子供料金いけるな", 1, 116401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5955, 4, 0, 1, "アオイ君は元気に\nしているだろうか…\n彼女は人一倍ぼくに\n懐いていたからな…", 1, 116401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5956, 5, 0, 1, "そろそろシズル君は\n姉という幻想から\n目覚めただろうか…", 1, 116401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5957, 101, 0, 1, "ぼくは来年も聖学祭に\n行くさ…卒業しても\n君らとは永遠だ", 4, 116401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5958, 102, 0, 1, "最近同志の登校率が\n減少傾向だな…\nまあ仕方ないのだが…\nないのだが…", 8, 116401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5959, 1, 0, 2, "ほわあああ！\nおい急に話しかけるな\n学びがこぼれる", 1, 116401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5960, 2, 0, 2, "この体操着、周囲から\n大層評判が良いのだよ\nたいそうだけにな\nどやっ！", 1, 116401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5961, 3, 0, 2, "何を着ていようと\nぼくはぼくさ\n外見など所詮は\n魂の識別符に過ぎん", 1, 116401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5962, 4, 0, 2, "救護院のアヤネ君が\n最近ぼくの自称年齢に\n疑念を持ち始めてな…\nあの熊…殺るか…", 1, 116401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5963, 101, 0, 2, "うん？　なんだね？\nなに、用は無い？\n用も無いのに呼ぶとは\nもしや…これが友達！", 4, 116401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5964, 102, 0, 2, "この服が似合ってる？\nな、なんだね突然…\nやめたまえよでゅふふ", 8, 116401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5965, 1, 0, 2, "ふぁ～あ…\n朝も更けてきたな…\nそろそろ寝るか…", 1, 116401, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5966, 2, 0, 2, "ふぁ～あ…もう昼だ…\n人間の起きている\n時間ではないな…\n早く寝なくては…", 1, 116401, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5967, 3, 0, 2, "うーん、よく寝た！\n気持ちのいい夜だな\n今日も一日がんばるぞ", 1, 116401, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5969, 2, 0, 3, "ほわぁ～…\nもしやこれは都で噂の\n人を駄目にするやつ…", 1, 116401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5970, 3, 0, 3, "ふむ、愛くるしいな\nだが似合い過ぎる…\nぼくが持つと我ながら\n子供にしか見えん", 1, 116401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5971, 4, 0, 3, "アロマか、興味深いな\nミツキ印ポーションも\n気化させて鼻腔から\n吸入してみるか…", 1, 116401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5972, 5, 0, 3, "んふ、んふふ…\n火ってイイよね…\nずっと見てられるね…", 1, 116401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5974, 7, 0, 3, "や、やめろ…\nとめどなく固形食物を\n生産するのはやめろ…", 1, 116401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5975, 8, 0, 3, "この可愛げのない筆致\nやはり作者は理系か！\nぐああ目が理系になる", 1, 116401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5977, 1, 0, 1, "…あいつのためなら\n無理くらいいくらでも\nしてやるわよ", 1, 121101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5978, 2, 0, 1, "この姿、\n魔力の制御ムズ過ぎ…\nま、あたしは優秀だし\nどうとでもなるけど！", 1, 121101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5979, 3, 0, 1, "あいつのこと、\n全部受け入れるって\n決めたから！", 1, 121101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5980, 4, 0, 1, "今なら誰だろうと\n負ける気がしないわ\nドラゴンでもなんでも\nかかってこいっての！", 1, 121101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5981, 5, 0, 1, "散々助けられた分、\n今度はあたしが\nあいつのこと助けるわ", 1, 121101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5982, 101, 0, 1, "あいつとの\nこれまでの冒険は\nどれも、あたしの\n大切な思い出…", 4, 121101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5983, 102, 0, 1, "もしまたあいつが\n寝たままになったら\nなんだってするわ\nキ…キスでも…", 8, 121101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5984, 1, 0, 2, "なに、お腹空いたの？\nなら、おいしいご飯を\n食べに行きましょ♪", 1, 121101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5985, 2, 0, 2, "やっぱ、\nあんたのアホ面見ると\n安心するわ♪", 1, 121101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5986, 3, 0, 2, "【美食殿】はあんたと\nぺコリーヌがいなきゃ\n始まんないっての", 1, 121101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5987, 4, 0, 2, "ひゃあっ！？\n急に声かけないで！？\n魔力が暴発しても\n知らないわよ！", 1, 121101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5988, 101, 0, 2, "…あんたとこうして\nぼーっと過ごす時間、\nまあ…嫌いじゃないわ", 4, 121101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5989, 102, 0, 2, "あんたが信じて\n見守っててくれれば…\nあたし、\nなんだってできるわ", 8, 121101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5990, 1, 0, 2, "おはよ\n早く顔洗いなさいよ、\nこのねぼすけ♪", 1, 121101, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5991, 2, 0, 2, "今日のお昼は\nどこで食べる？\nあんたが\n決めていいわよ", 1, 121101, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5992, 3, 0, 2, "もしあんたがまた\n夢に囚われても\nあたしが助けるわ\n安心して寝なさいよ", 1, 121101, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5994, 2, 0, 3, "気持ちいい～…♪\nあいつもたまには\nこうして休ませなきゃ", 1, 121101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5995, 3, 0, 3, "一目惚れしたかも…！\nこの子、\n部屋に飾りたいわー♪", 1, 121101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5996, 4, 0, 3, "へぇ、あいつって\nこういう匂いが\n好きなのかしら…？", 1, 121101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5997, 5, 0, 3, "暖かくて最高…♪\n丸くなっちゃいそう…\nうにゃあ…♪", 1, 121101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5999, 7, 0, 3, "イケるわね、これ！\nやっぱり食べるって\n大事よねー♪", 1, 121101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6000, 8, 0, 3, "ファーストキスは\nどんな味、ね…\nふ、ふ～ん…？\nなるほど…", 1, 121101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6002, 1, 0, 1, "お友だち…\nできるかしら…？\nこんなわたしでも…", 1, 118101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6003, 2, 0, 1, "あまり、きょろきょろ\nしちゃダメよね…\n変な子だと、\n思われてしまうわ…", 1, 118101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6004, 3, 0, 1, "広い場所、\n落ち着かない…\nはぅ…どこにいれば…", 1, 118101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6005, 4, 0, 1, "～♪", 1, 118101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6006, 5, 0, 1, "【レイジ・レギオン】\n…またいつか、\nみんなと…", 1, 118101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6007, 101, 0, 1, "…一度でいいから、\nわたしもママって…\nあっ、その…\n…な、なんでもないわ", 4, 118101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6008, 102, 0, 1, "ここが、今のわたしに\nとっての帰る場所…\n…そう思っても、\nいいのかしら…？", 8, 118101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6009, 1, 0, 2, "ええと…\nお腹、空いてる？\nおねむじゃない？", 1, 118101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6010, 2, 0, 2, "ここにいると\n邪魔かしら…？\nならもっと隅っこに…", 1, 118101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6011, 3, 0, 2, "お喋り…？\nわたしでいいの…？\nうれしい…", 1, 118101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6012, 4, 0, 2, "あなたの好きな歌…\n教えて…？\n練習、するから…", 1, 118101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6013, 101, 0, 2, "ここは賑やかだけど…\nうるさいとは感じない\nむしろ、心地いい…", 4, 118101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6014, 102, 0, 2, "安心、して…\nわたしの歌で、\nあなたを守るから…", 8, 118101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6015, 1, 0, 2, "ちょっとだけ…\n朝は、苦手…\n…ふぁ…", 1, 118101, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6016, 2, 0, 2, "お昼は、食べた？\n良かったら、一緒に…\n少し…焦げてるけど…", 1, 118101, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6017, 3, 0, 2, "眠れないのかしら…\nじゃ、じゃあ…\n子守歌…聴く？", 1, 118101, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6019, 2, 0, 3, "こんな特等席…\n恐縮、です…", 1, 118101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6020, 3, 0, 3, "い、今のうちに、\nこの子でお世話の\n練習を…", 1, 118101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6021, 4, 0, 3, "いい香り…\n…つらい気持ちも\n癒してくれる", 1, 118101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6022, 5, 0, 3, "あったかい…\nいつまでも、\nこうしていられそう…", 1, 118101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6024, 7, 0, 3, "おいしく出来てる…\nすごいわ…\n見習わなくっちゃ…", 1, 118101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6025, 8, 0, 3, "『赤ちゃんとママの\nスキンシップ入門』…\n…べ、勉強になるわ…", 1, 118101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6027, 1, 0, 1, "きれいなお部屋…！\n立派な舞台の\nセットみたい…♪", 1, 121301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6028, 2, 0, 1, "お芝居では私が先輩\nなんだから、しっかり\n私がミサキちゃんを\nエスコートしなきゃ", 1, 121301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6029, 3, 0, 1, "～～♪\n歌うお芝居も多いから\nもっと上手く\nなりたいな…！", 1, 121301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6030, 4, 0, 1, "ここにいる人たちと\n仲良くなりたいな…\nそしたらきっと演技の\n幅も広げられるよね", 1, 121301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6031, 5, 0, 1, "機会があったら\nアヤネちゃんとも\nお芝居してみたいな♪", 1, 121301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6032, 101, 0, 1, "お兄ちゃんに褒めて\nもらえるように\nお芝居の練習\nいっぱいしなきゃ…♪", 4, 121301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6033, 102, 0, 1, "あう…お兄ちゃんを\n意識したら、台詞が\n飛んじゃったよぅ…！", 8, 121301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6034, 1, 0, 2, "あっ、お兄ちゃん…♪\nお芝居の練習を\n見てくれませんか…？", 1, 121301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6035, 2, 0, 2, "いまの発声練習\nどうでしたか？\nひゃん…！？\n頭は撫でないでぇ～", 1, 121301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6036, 3, 0, 2, "ミサキちゃんも\nお芝居、とっても\n上達してるんですよ\n私も負けられません♪", 1, 121301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6037, 4, 0, 2, "また物語を聞かせて\nほしい、ですか…？\n私なんかでよければ\nもちろんよろこんで♪", 1, 121301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6038, 101, 0, 2, "新しいお話を\n考えてみました\n聞いてくれますか…？\nよかった…♪", 4, 121301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6039, 102, 0, 2, "いつか…舞台上じゃ\nなくても、照れずに\nお兄ちゃんに気持ちを\n伝えてみせますね…♪", 8, 121301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6040, 1, 0, 2, "おはようございます♪\nマヒルさんにもらった\nミルク、一緒に\n飲みませんか？", 1, 121301, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6041, 2, 0, 2, "牧場のみなさんに\nドングリパンを\nいただきました～\nおひとつどうぞ♪", 1, 121301, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6042, 3, 0, 2, "寝る前にまたココアを\n飲みながら、お話を\n聞いてもらえますか？\nえへへ、うれしいな♪", 1, 121301, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6044, 2, 0, 3, "お芝居の練習の\nあとは、ここで\nお兄ちゃんと\nゆっくりしたいな♪", 1, 121301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6045, 3, 0, 3, "今日も演技の\n練習に付き合ってね♪", 1, 121301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6046, 4, 0, 3, "素敵な香り…\nこのイメージも\nお芝居に\n活かせないかな…？", 1, 121301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6047, 5, 0, 3, "私もお芝居で\nみんなの心を\nぽかぽか温められる\nようになりたいな…♪", 1, 121301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6049, 7, 0, 3, "舞台に立つんだから\n食べ過ぎないように\n気をつけないと。\nでも…おいしいなぁ♪", 1, 121301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6050, 8, 0, 3, "このお話、面白い…\n脚本を書く\n参考にもなりそう…♪", 1, 121301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6052, 103, 0, 1, "えへへ…♪\n二人で見た夕日…\nキレイでしたね…", 12, 107501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6053, 103, 0, 2, "これから泳ぎの\n練習ですか？\n付き合いますよ♪", 12, 107501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6054, 103, 0, 1, "主さまを導くことで\nわたくしも未知なる\n領域へと辿り着けます", 12, 107601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6055, 103, 0, 2, "主さまとなら\n海よりも深く\n空よりも高い場所へも\n目指せそうです", 12, 107601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6056, 103, 0, 1, "あいつと二人で\n夏の遠い海に…って\nあたしってば\nなに考えてるのよ！", 12, 107801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6057, 103, 0, 2, "あたしが楽しく\n過ごせるのはあんたが\nいてくれたからよ\nか、感謝してるわ", 12, 107801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6058, 1, 0, 1, "はぁ…アタシってば\nこんなにかわいくって\nキレイなんて罪よね～", 1, 121401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6059, 2, 0, 1, "舞台の衣装を\nクリーニング\nしとかなくちゃ…", 1, 121401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6060, 3, 0, 1, "舞台映えするメイク…\nスズナなら\n知ってるかしら？", 1, 121401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6061, 4, 0, 1, "物産展の会計係と\n山盛りの算数の宿題…\nどっちが簡単かしら…", 1, 121401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6062, 5, 0, 1, "牧場と温泉の仕事に\nお芝居の練習に…\nちょ、ちょっと休憩～", 1, 121401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6063, 101, 0, 1, "お客さんの視線も\nうれしいけど…\nあいつも見てて\nくれてるかしら？", 4, 121401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6064, 102, 0, 1, "もっともっと\nセクシーになって\nあいつをビックリ\nさせてやるわ♪", 8, 121401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6065, 1, 0, 2, "アンタも休憩？\nしっかり働くために\n休むのも大事よね", 1, 121401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6066, 2, 0, 2, "ふふん♪　アタシも\n料理ができるように\nなったんだから！\n簡単なのだけだけど…", 1, 121401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6067, 3, 0, 2, "温泉ってお肌に\nいいのよね？\nもっとキレイに\nなったらどうしよう〜", 1, 121401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6068, 4, 0, 2, "物産展の荷物は\nこれでよし…っと\nそれじゃアンタも\n運ぶの手伝ってよね♪", 1, 121401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6069, 101, 0, 2, "いつもと違うアタシに\n見とれちゃった？\n仕方ないわね～♪", 4, 121401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6070, 102, 0, 2, "お花見って風流で\n大人って感じ♪\n…でもお団子も\n忘れないでよね！", 8, 121401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6071, 1, 0, 2, "みんなに朝ごはんを\n作ってみたら\n喜んでくれるかしら？", 1, 121401, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6072, 2, 0, 2, "お客さんがいっぱい\n来る時間帯ね！\n元気出していくわ！", 1, 121401, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6073, 3, 0, 2, "そろそろ寝ないと…\n睡眠不足はお肌の\n大敵なんだから", 1, 121401, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6075, 2, 0, 3, "ふかふかしてて\n気持ちいい～♪\n疲れが癒されるぅ…", 1, 121401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6076, 3, 0, 3, "この子もアタシと\nおそろいの衣装に\nしてあげようかしら♪", 1, 121401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6077, 4, 0, 3, "素敵な衣装で\nいい香りを\n身にまとう…ふふ、\n大人のたしなみよね♪", 1, 121401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6078, 5, 0, 3, "あったか～い♪\nここでお昼寝したら\n気持ちよさそう…", 1, 121401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6080, 7, 0, 3, "お仕事がんばると\nいつもよりお腹\n空いちゃう～！", 1, 121401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6081, 8, 0, 3, "最新のトレンド\n載ってないかしら？\n物産展の売り物に\n取り入れなくちゃ！", 1, 121401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6083, 1, 0, 1, "怪盗のこと…\nお父さんお母さんに\nもし知られたら\nなんて説明しよう…", 1, 121501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6084, 2, 0, 1, "たとえ怪盗でも、人の\n家にお邪魔するときは\n身だしなみをしっかり\nしないと…", 1, 121501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6085, 3, 0, 1, "鍵開けって難しい…\nうーん、もっと\n練習しておかなくちゃ", 1, 121501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6086, 4, 0, 1, "姿が見えない\n怪盗の噂…？\nわ、私のことじゃ\nありませんように…！", 1, 121501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6087, 5, 0, 1, "予告状って…\n切手はどこに貼れば\nいいんだろう…？", 1, 121501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6088, 101, 0, 1, "怪盗活動にも\n慣れてきたけど…\nやっぱり先輩が\nいないと心細いなぁ…", 4, 121501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6089, 102, 0, 1, "せ、先輩の心も\nいつか盗めるかな…？\n…わわわ、私ってば\nなに言ってるんだろ…", 8, 121501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6090, 1, 0, 2, "怪盗が大活躍って\n新聞で読んだ…？\nだっ、誰のこと\nでしょうね…あはは", 1, 121501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6091, 2, 0, 2, "尾行に潜入…\n存在感がないのって\n意外と役立つことも\nあるんですね", 1, 121501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6092, 3, 0, 2, "せせせ、先輩っ！\n帽子をぎゅーって\nかぶったら、抜けなく\nなっちゃいました…！", 1, 121501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6093, 4, 0, 2, "ぬ、盗み聞きなんて\nしてませんよ？\nみんなが私に気づかず\n話し始めるだけで…", 1, 121501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6094, 101, 0, 2, "秘密道具が見たい？\nそ、それは内緒です！\n秘密道具ですから…\nで、でも先輩になら…", 4, 121501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6095, 102, 0, 2, "えへへ\n見つかっちゃいました\n先輩はいつも私を\n見つけてくれますね…", 8, 121501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6096, 1, 0, 2, "おはようございまふ…\n秘密道具のお手入れで\n夜更かししちゃって…\nちょっと眠たいです～", 1, 121501, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6097, 2, 0, 2, "怪盗が昼に活動する…\n案外そのほうが\n意外性が\nあるのかも…？", 1, 121501, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6098, 3, 0, 2, "外が暗くなりましたね\n…そろそろ怪盗の活動\n始めましょうか？", 1, 121501, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6100, 2, 0, 3, "ここでくつろぐと\n疲れが取れます～\n…今日も怪盗\nがんばろっと", 1, 121501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6101, 3, 0, 3, "わぁ、かわいい…！\n心を盗まれるって\nこういうことかな…？", 1, 121501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6102, 4, 0, 3, "素敵な香り…でも\n服に香りがついたら\n隠れてるときに\n見つかっちゃうかな…", 1, 121501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6103, 5, 0, 3, "ぽかぽかで\nいい気持ち…\n夜道を歩く前に\n温まっておこうっと…", 1, 121501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6105, 7, 0, 3, "怪盗活動に備えて\n今のうちにしっかり\n食べておかなきゃ…！", 1, 121501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6106, 8, 0, 3, "女の子は秘密が\n多いほうが魅力的…？\nそ、そうなんだ…", 1, 121501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6108, 103, 0, 1, "ドＳさんはいつも\nクウカの妄想の中に…\nぐふふ、じゅるり～♪", 12, 104501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6109, 103, 0, 2, "やさしい触り方も\n嫌いじゃありません…\nは…恥ずかしいですが", 12, 104501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6110, 1, 0, 1, "この大怪盗さまに\n盗めないものなんて\nないです！　たぶん！", 1, 121601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6111, 2, 0, 1, "あたしにふさわしい\nビッグなお宝は\nどこにあるですかね～", 1, 121601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6112, 3, 0, 1, "そろーりそろーり…\n気配を消せばお菓子も\n食べ放題…にやり", 1, 121601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6113, 4, 0, 1, "またサボってたのが\nバレたです…\nボスの地獄耳～！", 1, 121601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6114, 5, 0, 1, "ギャングで大怪盗…！\nこの肩書の多さ！\nまさに有能の証です！", 1, 121601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6115, 101, 0, 1, "お兄さんだったら…\nお宝を山分けして\nあげてもいいですよ", 4, 121601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6116, 102, 0, 1, "お兄さんはアレですよ\n大怪盗イノリさまの\n相棒みたいなものです", 8, 121601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6117, 1, 0, 2, "何事も用心深さが\n大切ですよ。ビ…\nビビりじゃないです！", 1, 121601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6118, 2, 0, 2, "ぬ、盗み聞き？\nしてないですよっ？\nぴゅーぴゅーぴゅー", 1, 121601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6119, 3, 0, 2, "情報が欲しければ\nお菓子と交換ですよ\nさぁ、交換交換っ♪", 1, 121601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6120, 4, 0, 2, "しーっ！\n今、尾行中なんです\n話はあとで…", 1, 121601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6121, 101, 0, 2, "お兄さんと話してると\n時間が速いような…？\nこの時間泥棒！", 4, 121601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6122, 102, 0, 2, "ちょうどよかった！\nいい話があるんです\n一口乗りませんか？", 8, 121601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6123, 1, 0, 2, "おてんとうさまが\n出てやがるですね…\n悪事がやりにくいです", 1, 121601, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6124, 2, 0, 2, "もうこんな時間ですか\nご飯にお昼寝…\nいやー忙しいです♪", 1, 121601, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6125, 3, 0, 2, "ふっふっふ！　夜こそ\n大怪盗の時間です！\n……ふぁあ", 1, 121601, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6127, 2, 0, 3, "うーん…\n警備の目がないと\n気楽でいいですねぇ", 1, 121601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6128, 3, 0, 3, "くう…！\nこのかわいさは\n盗めないです…♪", 1, 121601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6129, 4, 0, 3, "犯行現場に残ったのは\n香水の匂いだけ…\nふふ、おしゃれ大怪盗", 1, 121601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6130, 5, 0, 3, "意外とこういう場所に\n隠し扉があったり…\nしないです？", 1, 121601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6132, 7, 0, 3, "もぐもぐ…\nこの美味しさ…\nとんでもない罠ですね", 1, 121601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6133, 8, 0, 3, "むむむ…ちっとも\n分からないです…\nほとんど暗号ですよ", 1, 121601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6135, 1, 0, 1, "あら、サレンさんは\nどこに行かれた\nのかしら？", 1, 121701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6136, 2, 0, 1, "ふんふんふーん♪\nなんだか華やいだ\n気分ですわ♪", 1, 121701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6137, 3, 0, 1, "さて…次の事業は\nどうしましょう\n何か面白いことは…", 1, 121701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6138, 4, 0, 1, "おーっほっほっほ！\nあら…楽しくて、つい\n笑ってしまいましたわ", 1, 121701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6139, 5, 0, 1, "センスがあって\nいいお部屋ですわね\n私も見習いたいですわ", 1, 121701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6140, 101, 0, 1, "サレンさんとあのお方\n仲がいいですわね\nも、もちろん仲がいい\nのはよいことですわ！", 4, 121701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6141, 102, 0, 1, "どんなときでも\n正々堂々！\nそれが私の\n誇りですわ！", 8, 121701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6142, 1, 0, 2, "ふふ、ごきげんよう\n私、絶好調でしてよ", 1, 121701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6143, 2, 0, 2, "どうかされましたか？\n何かお困りごと\nでしたら\n是非力になりますわよ", 1, 121701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6144, 3, 0, 2, "たまにはこうして\nゆっくりと\nお話しするのも\nいいものですわね", 1, 121701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6145, 4, 0, 2, "次の事業に関して\n考え事をしてましたの\nよければ相談に乗って\nくださるかしら？", 1, 121701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6146, 101, 0, 2, "よろしければ\nお話しませんこと？\nサレンさんのお話を\n聞かせてほしいですわ", 4, 121701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6147, 102, 0, 2, "ふふっ…\n先ほどサレンさんと\nあなたさまのお話を\nしていたところですわ", 8, 121701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6148, 1, 0, 2, "さあ！\n今日も一日\n全力で参りますわ！", 1, 121701, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6149, 2, 0, 2, "お昼はどうしましょう\nサレンさんをお誘い\nしてみようかしら", 1, 121701, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6150, 3, 0, 2, "もう少しだけ…\n明日の準備をして\nおかないと…", 1, 121701, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6152, 2, 0, 3, "はぁ～…いいですわね\nはっ、私としたことが\nはしたないですわね", 1, 121701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6153, 3, 0, 3, "かわいらしいですわ\nいくつになっても\nぎゅっと\nしたくなりますわね", 1, 121701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6154, 4, 0, 3, "落ち着きますわね\nサレンさんはどんな\n香りが好きなのかしら", 1, 121701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6155, 5, 0, 3, "ふふっ、燃え盛る\n情熱の炎…\nいいですわね", 1, 121701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6157, 7, 0, 3, "このおいしさで\n元手がゼロ…\nすばらしい\n商品ですわ！", 1, 121701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6158, 8, 0, 3, "これは…ふむふむ\n新しい事業のヒントに\nなりそうですわ", 1, 121701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6161, 1, 0, 1, "さて、と…\n今日は何をしなくちゃ\nいけないんだっけ？", 1, 121801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6162, 2, 0, 1, "やることは山積み\nだけど、手を動かす\nのは嫌いじゃないわ♪", 1, 121801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6163, 3, 0, 1, "あら？　アキノさんの\n声が聞こえたような\n気がしたんだけど…", 1, 121801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6164, 4, 0, 1, "アキノさんといると\nついこっちまで笑顔に\nなっちゃうのよね", 1, 121801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6165, 5, 0, 1, "弱音なんか\nはいてられないわ\nアキノさんに\n笑われちゃうもの", 1, 121801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6166, 101, 0, 1, "気が付けばアキノさん\nのペースだけど…\n不思議と嫌では\nないのよね", 4, 121801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6167, 102, 0, 1, "あたしを好敵手って\n言ってくれる\nアキノさんのためにも\nしっかりしなくちゃ", 8, 121801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6168, 1, 0, 2, "どうしたの？\nアキノさんなら\nさっきそこに\nいなかった？", 1, 121801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6169, 2, 0, 2, "あたしなら大丈夫\n救護院だって\nみんなが助けて\nくれるからね", 1, 121801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6170, 3, 0, 2, "さっきアキノさんが\nあんたを探してたわよ\nなにかしたの？", 1, 121801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6171, 4, 0, 2, "こう見えてヒマじゃ\nないんだけど…\n少しくらいなら\n付き合ってあげるわ", 1, 121801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6172, 101, 0, 2, "ねえ、あんたと\nアキノさんって…\nううん、やっぱり\nなんでもないわ…", 4, 121801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6173, 102, 0, 2, "アキノさんも誘って\nお茶でもしない？\n話したいことが\nたくさんあるの♪", 8, 121801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6174, 1, 0, 2, "早起きは三文の得よ\nいい一日を迎えるため\n目を覚まさなきゃ", 1, 121801, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6175, 2, 0, 2, "今日のお昼は…\nアキノさんと同席する\n予定だったわね\nあんたもどう？", 1, 121801, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6176, 3, 0, 2, "ふぁ…っと、だめね\nたるんだところを\nアキノさんに\n見せるわけには", 1, 121801, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6178, 2, 0, 3, "ん～…\n落ち着くわね\nこのまま\n寝ちゃいそう…", 1, 121801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6179, 3, 0, 3, "救護院の皆も\nぬいぐるみが\n大好きなのよ", 1, 121801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6180, 4, 0, 3, "いい香りね…\nこれはアキノさんの\n趣味かしら？", 1, 121801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6181, 5, 0, 3, "こんなに激しく\n燃えているのに\nあったかくて落ち着く\nまるでアキノさんね", 1, 121801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6183, 7, 0, 3, "どんどん出てくる…\nこれ、救護院にも\nほしいわね…", 1, 121801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6184, 8, 0, 3, "ふむふむ…\n前にアキノさんが\n言ってたのは\nこのことかしら？", 1, 121801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6187, 1, 0, 1, "この衣装、私には\nちょっとかわい過ぎて\n恥ずかしいな…", 1, 122001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6188, 2, 0, 1, "一部の人からは\n海賊より海賊らしいと\n評判の父です", 1, 122001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6189, 3, 0, 1, "潮風で錆びないよう\n武器の手入れを\nしておきましょうか", 1, 122001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6190, 4, 0, 1, "宝探しのお手伝いなら\n力になれると思います\n父のやる気次第ですが", 1, 122001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6191, 5, 0, 1, "除霊は気を遣います\n一歩間違えると父まで\n成仏させてしまうので", 1, 122001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6192, 101, 0, 1, "あの人と見た\n海の景色を…ずっと\n忘れずにいたいな", 4, 122001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6193, 102, 0, 1, "幽霊になっても\n一目会えるなら…\n私はきっとあの人に…", 8, 122001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6194, 1, 0, 2, "ボートを漕ぐの、少し\n上手になったんですよ\n今度お見せしますね", 1, 122001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6195, 2, 0, 2, "航海に向けた\n買い出しが必要ですね\n予定は空いていますか", 1, 122001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6196, 3, 0, 2, "呪われた財宝の\n解呪依頼が多くて…\nふぅ…えっと次は…", 1, 122001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6197, 4, 0, 2, "夜の航海では\n父が明かり代わりに\nなってくれるんです", 1, 122001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6198, 101, 0, 2, "船の上では食材が\n限られるので料理の\nいい練習になります", 4, 122001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6199, 102, 0, 2, "この先もずっと\nあなたと一緒に\nたくさんの思い出を\n作っていきたいです", 8, 122001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6200, 1, 0, 2, "あの人の寝顔…\nもう少しだけ\n眺めていたいな…", 1, 122001, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6201, 2, 0, 2, "船の上では\n規則正しい生活が\n重要ですから", 1, 122001, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6202, 3, 0, 2, "夜の海は幽霊が\n見分けやすいんです\n青白く光るので", 1, 122001, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6204, 2, 0, 3, "海に揺られるような\n心地よさですね\n気が休まります…", 1, 122001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6205, 3, 0, 3, "あなたも一緒に\n海を見に行きますか？\n…なんて。ふふっ", 1, 122001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6206, 4, 0, 3, "爽やかな香りですね…\n一面の青い海が\n目に浮かぶようです", 1, 122001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6207, 5, 0, 3, "温かい…\n潮風で冷えた体に\n沁みますね", 1, 122001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6209, 7, 0, 3, "あ、おいしい…\n日持ちするなら船旅に\n持っていこうかな…", 1, 122001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6210, 8, 0, 3, "アンナさんに教えて\nもらった小説は…\nあ、これですね", 1, 122001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6212, 103, 0, 1, "なにもしないを\nしてるんだよ\nあ～いそがしいそがし", 12, 102601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6213, 103, 0, 2, "はぁ～あんたといると\nどこまでも気持ちよく\nダラダラできるね～", 12, 102601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6214, 1, 0, 1, "暗黒騎士にして、\n伝説の海賊でもある\n私に不可能はないっ！", 1, 121901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6215, 2, 0, 1, "海賊団の船員、\n随時募集中です…と\nこれで新たな盟友が\n増えるはずだ…！", 1, 121901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6216, 3, 0, 1, "肩に乗せるペットが\nほしいな。今のうちに\nペット用品を買いに\n行かなくては…！", 1, 121901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6217, 4, 0, 1, "財宝の地図はどこで\n売っているのだろう…\n古本屋？　古物商？", 1, 121901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6218, 5, 0, 1, "この海賊衣装に\n身を包んでいると\n前世の新たな記憶が\nよみがえってくるな！", 1, 121901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6219, 101, 0, 1, "いずれはシグルドと\n海原への冒険に\n繰り出したいな…\nきっと楽しいぞ！", 4, 121901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6220, 102, 0, 1, "待ちわびたぞ盟友よ！\n私と貴様あっての\n海賊団…だからその…\n今日はそばにいてくれ", 8, 121901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6221, 1, 0, 2, "シグルド！　港町で\nお魚のおすそ分けを\nもらったんだ！\n一緒に食べよう！", 1, 121901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6222, 2, 0, 2, "シグルドもこのコート\n羽織ってみないか？\nふふっ、きっとすごく\n似合うと思うぞ！", 1, 121901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6223, 3, 0, 2, "海賊船の新たな\n秘密兵器を作ったぞ！\nシグルド、これから\n見に来ないか！？", 1, 121901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6224, 4, 0, 2, "私は伝説の海賊だから\n泳ぐのだって得意だ！\nふ、深いところは\n少し怖いが…", 1, 121901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6225, 101, 0, 2, "ふふ、いつ無人島に\n漂着してもいいように\n非常食は準備済みだ\nシグルドの分もある！", 4, 121901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6226, 102, 0, 2, "冥風戦記の新章が\n完成したぞ…！\nシグルド、まずは\n貴様に読んでほしい！", 8, 121901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6227, 1, 0, 2, "ふっ、天気明朗\n絶好の航海日和だ！\n早速海に繰り出そう！", 1, 121901, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6228, 2, 0, 2, "昼食用に魚を\n釣っておいたぞ！\nふふ、いかにも\n海賊っぽいだろう？", 1, 121901, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6229, 3, 0, 2, "夜の海は真っ黒で\n少しだけ怖いな…\nシグルド、私の\nそばを離れるなよ…！", 1, 121901, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6231, 2, 0, 3, "大海賊がくつろぐのに\n相応しい逸品だ…！\nえっと…これ、どこの\nお店で買えますか？", 1, 121901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6232, 3, 0, 3, "わわ、かわいい…！\n船室のベッドに\n置いておきたいな…\n連れてってはダメか？", 1, 121901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6233, 4, 0, 3, "おぉ、この香りは…！\n集中力が上がりそうだ\n海賊船の船長室に\nぜひ置きたいな！", 1, 121901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6234, 5, 0, 3, "これが船乗りの\n伝承に聞く、海原を\nさまよう神秘の火か！\n…暖まるな！", 1, 121901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6236, 7, 0, 3, "腹が減っては\n海賊はできぬ！\n…小説も書けぬ！", 1, 121901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6237, 8, 0, 3, "女海賊と騎士はその後\n恋に落ち…？　恋愛\nモノではないかっ！？\nき、興味深くはあるが", 1, 121901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6239, 1, 0, 1, "いつもの山籠もりが\nブームになるなんて…\n人生、なにが起きるか\n分からないなぁ", 1, 122101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6240, 2, 0, 1, "キャンプ道具…\nいえ、キャンプギア！\nカッコいい響き…♪", 1, 122101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6241, 3, 0, 1, "レッツ、ＢＢキャン！\nこのフレーズ\n流行るかも…？", 1, 122101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6242, 4, 0, 1, "キャンプ仲間…\n募集してみようかな？\n友達も兼任で…", 1, 122101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6243, 5, 0, 1, "ユカリさんと\nジュンさん…元気かな\nまた一緒に\nキャンプしたいなぁ…", 1, 122101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6244, 101, 0, 1, "私の取材が載った本…\n【フォレスティエ】に\n送らなくちゃ…♪", 4, 122101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6245, 102, 0, 1, "もし、あの人が\n落ち込むことが\nあったら…その時は\n私が力になりたいな", 8, 122101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6246, 1, 0, 2, "ブッシュクラフトは\nマイフレンドくんの\nカスタムにも\n活かせる技術です！", 1, 122101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6247, 2, 0, 2, "会話に花が咲く…\nって言いますよね\n私の場合、会話相手が\nお花そのものですけど", 1, 122101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6248, 3, 0, 2, "この服、実は…\n昔ミサト先生に\nこしらえてもらった\n物なんです！", 1, 122101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6249, 4, 0, 2, "昔はやまびこで会話の\n練習をしてたんです\nほとんどオウム返しで\n気まずかったなぁ～", 1, 122101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6250, 101, 0, 2, "どうもマイスターです\n…あ、すみません\nみなさんの期待に\n応えようと練習中で…", 4, 122101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6251, 102, 0, 2, "たとえぼっちを\n卒業しても…\n私は一生ＢＢ団です！", 8, 122101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6252, 1, 0, 2, "小鳥さんのさえずりで\n目が覚める…\n心地いいものですね", 1, 122101, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6253, 2, 0, 2, "キャンプといえば\nおいしいご飯です！\n腕を振るいますよ！", 1, 122101, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6254, 3, 0, 2, "すっかり夜ですね…\nおやすみの前に\n星でも見ませんか？", 1, 122101, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6256, 2, 0, 3, "都会でくつろぎ…\n自然の中でくつろぐ…\nくつろぎ無限ループ…", 1, 122101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6257, 3, 0, 3, "しっかり観察すれば\nブッシュクラフトで\n再現できるかも…？", 1, 122101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6258, 4, 0, 3, "はぁ～…やっぱり\n木の香りを嗅ぐと\n肩の力が抜けますねぇ", 1, 122101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6259, 5, 0, 3, "ああ…あったまります\nなんだか心に\nゆとりがでますね…", 1, 122101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6261, 7, 0, 3, "このおいしさ…！\n今度はキャンプに\n持っていこうかな…", 1, 122101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6262, 8, 0, 3, "ふむふむ…\nいろんな知識があると\nキャンプはもっと\n楽しくなりますよね", 1, 122101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6264, 1, 0, 1, "部屋がキャンプ用品で\n埋まっていく…\nキャンプ…なんて\n恐ろしい趣味なの…？", 1, 122201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6265, 2, 0, 1, "仕事でも\nアウトドアでも\n頼れるお姉さんに\nなれたらいいなぁ…", 1, 122201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6266, 3, 0, 1, "この服、かわいい上に\n本当に快適ね～♪\nおしゃれにキャンプを\n楽しめて最高だわ！", 1, 122201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6267, 4, 0, 1, "ん〜次はどんな\nキャンプ飯を\n作ろうかしら？\n彼の好物は、っと…", 1, 122201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6268, 5, 0, 1, "早く週末が\n来ないかしら…\n仕事を忘れてパーっと\nキャンプ飲みを…！", 1, 122201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6269, 101, 0, 1, "新しいキャンプ飯の\nレシピを覚えたわ！\n彼も喜んで\nくれるはず…よね♪", 4, 122201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6270, 102, 0, 1, "彼の分の食器も\nこっそり買い揃えちゃ\nおうかな…そ、それは\nさすがにまだ早いか…", 8, 122201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6271, 1, 0, 2, "今度、財団主催で\n大規模なキャンプを\nやってみるのも\nいいかもしれないわね", 1, 122201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6272, 2, 0, 2, "ソロキャンプも\n悪くはないけど\n私はやっぱりキミと…\nな、なんでもない！", 1, 122201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6273, 3, 0, 2, "キャンプのことなら\n私に聞いて。なんでも\n教えてあげるから！\n…調べた上でね！", 1, 122201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6274, 4, 0, 2, "キャンプ場で飲む\n最高の一杯は\n他じゃ味わえないわ！\nさあ、乾杯っ！", 1, 122201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6275, 101, 0, 2, "うん、決めた！　次は\n禁しゅわキャンプ！\nだってキミとゆっくり\n話したいんだもん♪", 4, 122201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6276, 102, 0, 2, "キミのおかげで\n本当の最高の一杯が\n味わえたわ…\nありがとう♪", 8, 122201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6277, 1, 0, 2, "んー、気持ちいい！\nキャンプが趣味に\nなってから、寝覚めが\nよくなったわ♪", 1, 122201, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6278, 2, 0, 2, "お昼は私がごちそう\nするわ。キャンプ飯で\n鍛えた料理の腕前を\n披露しちゃうわよ～！", 1, 122201, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6279, 3, 0, 2, "そろそろ寝ましょ？\n明日も朝日を眺める\nためにね。もちろん\nコーヒー付きで♪", 1, 122201, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6281, 2, 0, 3, "くつろぐのに\n持ってこいね～\nテントの中に置いても\nよさそうだわ！", 1, 122201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6282, 3, 0, 3, "ふふ、かわいいわね～\n…今度キャンプ場に\n連れていこうかしら？", 1, 122201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6283, 4, 0, 3, "うん、いい匂い…♪\nキャンプ場を思い出す\n自然の香りだわ〜", 1, 122201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6284, 5, 0, 3, "はぁ、温まるぅ～\n冬のキャンプで\n使ってみたいわね", 1, 122201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6286, 7, 0, 3, "おいしい～♪\nキャンプ飲みの\nおつまみに\nぴったりね！", 1, 122201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6287, 8, 0, 3, "へえ…こんなキャンプ\n飯もあるんだ？\n彼、好きだったり\nするかな…", 1, 122201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6289, 103, 0, 1, "一生ついててやるの\n幽霊の一生は\nなっが～～～いの！", 12, 100701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6290, 103, 0, 2, "む～…あんまり触ると\nすり抜けてやるの！\nウソなの。むふふ♪", 12, 100701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6291, 1, 0, 1, "んー…\n今日はベスちゃんと\nなにして遊ぼーかなー", 1, 122301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6292, 2, 0, 1, "ヴァンピィちゃんも\nけんぞくの友だちと\n仲良くなりたいな♪", 1, 122301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6293, 3, 0, 1, "けんぞくのお部屋は\nヴァンピィちゃんが\n守ってあげなくちゃ！", 1, 122301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6294, 4, 0, 1, "お掃除するなら\nヴァンピィちゃんも\n手伝ってあげよー♪", 1, 122301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6295, 5, 0, 1, "ふーん、ふーん♪\nけんぞくってこーゆー\nお部屋が好きなんだー", 1, 122301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6296, 101, 0, 1, "ひまだなー\nけんぞくをさそって\nお散歩いこっかな♪", 4, 122301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6297, 102, 0, 1, "今日のスープに\nけんぞくの大好物を\n入れてあげよっと♪", 8, 122301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6298, 1, 0, 2, "けんぞくー！\nヴァンピィちゃんと\nお出かけしよ♪", 1, 122301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6299, 2, 0, 2, "よしよし！\n今日のけんぞくも\nいい子だね♪", 1, 122301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6300, 3, 0, 2, "んんー…\nやっぱりけんぞくと\nベスちゃんは似てる？", 1, 122301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6301, 4, 0, 2, "いーい？\nヴァンピィちゃんの\nいうこと聞くのよ？", 1, 122301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6302, 101, 0, 2, "ヴァンピィちゃんの\nけんぞくだもん！\n大切にするからね！", 4, 122301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6303, 102, 0, 2, "えへへっ！\nやっぱりけんぞくって\nやさしー匂いがする♪", 8, 122301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6304, 1, 0, 2, "おはよー…\nヴァンピィちゃんは\nもうちょっと眠るねー", 1, 122301, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6305, 2, 0, 2, "ヴァンピィちゃん\nひなたぼっこ\nだーいすき！", 1, 122301, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6306, 3, 0, 2, "んー！　夜だ♪\nヴァンピィちゃん\n絶好調だよ！", 1, 122301, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6308, 2, 0, 3, "わー！\nふかふかだ！\nきもちぃー♪", 1, 122301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6309, 3, 0, 3, "かわいー♪\nベスちゃんに負けない\n抱き心地！", 1, 122301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6310, 4, 0, 3, "この香り…なんだろ？\nヴァンピィちゃん\n気に入っちゃった♪", 1, 122301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6311, 5, 0, 3, "あったかいねー…\nヴァンピィちゃん\nずっとここにいるー…", 1, 122301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6313, 7, 0, 3, "はむはむ…\nんー！\nおいしー♪", 1, 122301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6314, 8, 0, 3, "ヴァンピィちゃん\nこの本\n好きなんだよねー♪", 1, 122301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6316, 1, 0, 1, "夏といえば\nかき氷だよね\n近くにお店は…", 1, 122501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6317, 2, 0, 1, "この格好でいるのも\nなじんできたし\nちょっと鍛錬でも…", 1, 122501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6318, 3, 0, 1, "動きやすいけれど…\nいつもと違う服装は\n少し落ち着かないな", 1, 122501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6319, 4, 0, 1, "夏の空気に\n当てられると思わず\n気が緩んでしまうね", 1, 122501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6320, 5, 0, 1, "砂浜で走ると\nいい具合に体幹を\n鍛えられそうだ", 1, 122501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6321, 101, 0, 1, "終わらない夏は\nないけれど…\nつい願ってしまうな", 4, 122501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6322, 102, 0, 1, "胸を張って彼と\n並び立てるよう\n私も、もっと強く…！", 8, 122501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6323, 1, 0, 2, "どうしたの？\n行きたい場所が\nあるなら付き合おうか", 1, 122501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6324, 2, 0, 2, "快晴で風は穏やか…\n絶好の釣り日和だね\nさあ、出かけようか", 1, 122501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6325, 3, 0, 2, "今日の予定を\n立ててるんだけど…\nキミの希望も\n聞きたいな", 1, 122501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6326, 4, 0, 2, "ビーチスポーツか…\nキミとなら\nいい勝負ができそうだ", 1, 122501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6327, 101, 0, 2, "私たちの夏に\n遠慮は無用だよ\n思いきり楽しもう！", 4, 122501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6328, 102, 0, 2, "いつかまた一緒に\n花火を見に行こう\n…約束だよ？", 8, 122501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6329, 1, 0, 2, "潮騒の音で\n目覚めるのも\nいいものだね", 1, 122501, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6330, 2, 0, 2, "今朝釣ってきた魚が\nあるんだ。これで\nお昼ご飯にしようか", 1, 122501, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6331, 3, 0, 2, "夜にもイベントが\nあるみたいなんだ\n一緒に見に行かない？", 1, 122501, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6333, 2, 0, 3, "気持ちが安らぐね…\nうっかり眠って…\nしまい…そうだ…", 1, 122501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6334, 3, 0, 3, "肌触りのよさが\nこの格好だと\nよく分かるよ", 1, 122501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6335, 4, 0, 3, "海をイメージさせる\n香りだね…\n爽やかで落ち着くよ", 1, 122501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6336, 5, 0, 3, "濡れたままだと\n風邪を引いてしまうよ\nほら、キミもおいで", 1, 122501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6338, 7, 0, 3, "夏を楽しみきるには\nエネルギーが\n必要だからね", 1, 122501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6339, 8, 0, 3, "大切な人と過ごせる\n観光スポット紹介…\n…こ、このページは\nもういいかな！", 1, 122501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6341, 10, 0, 3, "濡らしてもいいなら\n海に持っていって\n乗るのもよさそうだ", 1, 122501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6342, 11, 0, 3, "こういう名画を\n見た気がするな…\nこんな視点なんだね", 1, 122501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6343, 1, 0, 1, "水着って\n動きやすくて\nいいよねー♪\nいっちにーさんしー♪", 1, 122401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6344, 2, 0, 1, "ユイちゃんにもらった\n日焼け止め\n塗っておいて\nよかった～", 1, 122401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6345, 3, 0, 1, "なんだか暑いなあ…\nせっかくだし\nプールで泳いで\nこようかな？", 1, 122401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6346, 4, 0, 1, "この格好で\n出かけようとしたら\nレイさんに\n怒られちゃった…", 1, 122401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6347, 5, 0, 1, "たとえ水着姿でも\n困ってる人を見たら\n助けに行くからね！", 1, 122401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6348, 101, 0, 1, "バカンス楽しかった～\nまた騎士くんと\n一緒に行きたいな♪", 4, 122401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6349, 102, 0, 1, "この水着、あたしに\n似合ってるかなあ？\n騎士くんはどう\n思ってるんだろ…", 8, 122401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6350, 1, 0, 2, "騎士くん！\n海に行くなら\n準備運動\n忘れずにね！", 1, 122401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6351, 2, 0, 2, "泳ぐ練習がしたいの？\nいいよ！　水着だから\nなにかあったらすぐに\n助けてあげるね！", 1, 122401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6352, 3, 0, 2, "騎士くんの水着姿も\nかっこよかったよ！\nまた遊びに行こ！\n約束ね！", 1, 122401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6353, 4, 0, 2, "このグローブの向日葵\nかわいいでしょ？\nユイちゃんが\n選んでくれたんだ♪", 1, 122401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6354, 101, 0, 2, "実はこの水着\nいつも着てる\n服とおなじなんだ…\nって、冗談だよ？", 4, 122401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6355, 102, 0, 2, "変な日焼けしてないか\n見てもらってもいい？\n…なし！　今のなし！\nやっぱりなしー！", 8, 122401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6356, 1, 0, 2, "夏のお日様に\n負けないよう\n今日も元気いっぱい\nがんばるぞー！", 1, 122401, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6357, 2, 0, 2, "お腹減ってきちゃった\n騎士くんはお腹\n空いてない？\n…聞こえちゃった♪", 1, 122401, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6358, 3, 0, 2, "今日も一日\n楽しかったー！\n明日もまた\n楽しもうね！", 1, 122401, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6360, 2, 0, 3, "ふわあ…\nふわふわなのが\nいつもより\nよくわかるよー…", 1, 122401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6361, 3, 0, 3, "そうだ！　水着なら\n濡れてもいいし\n今度洗ってあげるね♪", 1, 122401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6362, 4, 0, 3, "んー…いい匂い♪\nなんだか\n身体がぽかぽか\nしてきちゃった！", 1, 122401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6363, 5, 0, 3, "水着だから、えっと…\n暖か涼しい…！\n不思議な感じー", 1, 122401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6365, 7, 0, 3, "思いっきり身体を\n動かした後だから\nいくらでも\n食べちゃえるよー！", 1, 122401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6366, 8, 0, 3, "ふむふむ…\nなるほどー\n水着の種類って\nそんなにあるんだぁ…", 1, 122401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6368, 10, 0, 3, "乗り心地バッチリだね\nしゅっぱつしんこー！\nなんちゃって♪", 1, 122401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6369, 11, 0, 3, "わっ、ぽよんぽよん！\nトランポリンみたいで\nた～のし～！", 1, 122401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6370, 103, 0, 1, "彼のことを考えてると\n幸せで胸が温かくなる\n…私ったら、もう", 12, 101501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6371, 103, 0, 2, "うふふふふ\nくすぐったいわ～♪\nもう、イタズラっ子ね\n次は先生の番よ～♪", 12, 101501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6372, 1, 0, 1, "騎士クンの汗\n拭いてあげたいな…\n余計なお世話かな…？", 1, 122601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6373, 2, 0, 1, "夏バテ気味かも…\nそうだ、今夜は\nかば焼きにしよう♪", 1, 122601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6374, 3, 0, 1, "青い空、白い雲\n眩しい日差し…！\n夏って好きだな…", 1, 122601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6375, 4, 0, 1, "ヒリヒリする…？\n日に焼けちゃったかな\nふー…ふー…", 1, 122601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6376, 5, 0, 1, "花火大会があるんだ…\n夜、浜辺…2人きり…\nむ、無理だよ〜！", 1, 122601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6377, 101, 0, 1, "水着って不思議…\n引っ込み思案が少し\nおさまる…気がする", 4, 122601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6378, 102, 0, 1, "騎士クンとまた\n海に行きたいな…\nまた、一緒に…", 8, 122601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6379, 1, 0, 2, "ダ、ダイエット！？\nしてないよ！？\n元々これ位だよ…？", 1, 122601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6380, 2, 0, 2, "スイカ割りのコツ？\nわたしは、騎士クンに\n教わりたいな…", 1, 122601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6381, 3, 0, 2, "騎士クン。うちわで\n扇いであげるね\nどこに置いたかな…", 1, 122601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6382, 4, 0, 2, "うぅ…暑いね…\n騎士クン、温度計は\n見ない方がいいよ～", 1, 122601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6383, 101, 0, 2, "じ、じっと見ないで…\nええっと…ちょっと！\nちょっとならいいよ…", 4, 122601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6384, 102, 0, 2, "え、肝試し？\nう〜ん、うう〜ん…\n…騎士クンとなら！", 8, 122601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6385, 1, 0, 2, "おはよう騎士クン\n見て、あっちの空\nもう入道雲が出てる", 1, 122601, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6386, 2, 0, 2, "夕立、来るかなぁ…\nもし雷が鳴ったら\nそばにいてくれる…？", 1, 122601, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6387, 3, 0, 2, "しー。…ほら、虫の声\n…わ、わたしの心臓の\n音は気にしないで…！", 1, 122601, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6389, 2, 0, 3, "ふふ、水着でゴロゴロ\nするのってなんだか…\nいけないことみたい", 1, 122601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6390, 3, 0, 3, "かわいい～♪\n日光浴させて\nあげたいな♪", 1, 122601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6391, 4, 0, 3, "ほんのり潮の香りが…\n香ばしくて塩辛くて…\nあ…おなか鳴っちゃう", 1, 122601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6392, 5, 0, 3, "水着、乾かせるかも…\nそうだ、みんなの分も\n持ってこよう！", 1, 122601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6394, 7, 0, 3, "夏バテなんてしてたら\nみんなの足を\n引っ張っちゃうよね\nいただきます！", 1, 122601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6395, 8, 0, 3, "騎士クンの、どうして\n海はしょっぱいの？\nって質問の答えは…\n分かんないよぉ～", 1, 122601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6397, 10, 0, 3, "顔は怒ってるのに\nなんだかかわいい…\n誰かに似てる…？", 1, 122601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6398, 11, 0, 3, "これを枕に、ビーチで\n騎士クンと…なんて…\nなんて…なんて…！", 1, 122601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6399, 1, 0, 1, "夏休み、\n毎日たのしいな～♪\nつぎはなにしよう？", 1, 122901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6400, 2, 0, 1, "自由研究、\nなににしよっかな～？\nプチグリフォンさんの\nかんさつにっきとか？", 1, 122901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6401, 3, 0, 1, "夏休みって、\n給食ないから…\nそれはちょっぴり\nさみしいかも…？", 1, 122901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6402, 4, 0, 1, "砂浜をはだしで歩くの\nきもちいいよね♪\nくすぐったいけど\nミミ、好きなんだ～♪", 1, 122901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6403, 5, 0, 1, "きれいな貝がら\n見つけちゃった♪\nミミのたからもの！", 1, 122901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6404, 101, 0, 1, "ミミ、浴衣を\n買ってもらったんだ♪\n今度、おにいちゃんに\n見せてあげたいな！", 4, 122901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6405, 102, 0, 1, "ミミ、おにいちゃんと\nいっぱい夏休みの\n思い出つくりたいな♪", 8, 122901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6406, 1, 0, 2, "ねえ、おにいちゃん…\n夏休みの宿題、\nわかんないとこ\nおしえてくれる…？", 1, 122901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6407, 2, 0, 2, "おかあさんにね、\n麦茶つくるの上手って\nほめられたんだ～♪\n飲んでみて♪", 1, 122901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6408, 3, 0, 2, "夏休みの宿題で\nポスターかくんだ～♪\nおにいちゃんを\nかいてもいい？", 1, 122901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6409, 4, 0, 2, "線香花火やりたいな♪\nミミ、長持ちさせるの\n上手なんだよ♪", 1, 122901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6410, 101, 0, 2, "おにいちゃんの\n好きな本ってなあに？\nその本で読書感想文\n書こうかな♪", 4, 122901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6411, 102, 0, 2, "海であそんだ後ね、\nおにいちゃんに\n髪を拭いてもらうの\nすきなんだ～♪", 8, 122901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6412, 1, 0, 2, "夏休みって、\nワクワクして\n早起きしちゃうよね♪", 1, 122901, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6413, 2, 0, 2, "わぁ♪　お昼は、\n流しそうめんなんだ？\nミミ、流してみたい♪", 1, 122901, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6414, 3, 0, 2, "こわい話聞いて、\nねむれないの…\nねえ、おにいちゃん、\nいっしょに寝ていい？", 1, 122901, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6416, 2, 0, 3, "ちょっぴりやすんだら\nまた、いっしょに\n海であそぼうね♪", 1, 122901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6417, 3, 0, 3, "すっごくかわいいね♪\nこの子のこと、\n絵日記にかこうっと♪", 1, 122901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6418, 4, 0, 3, "くんくん…\nスイカのにおいだね♪\nこのにおいだいすき！", 1, 122901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6419, 5, 0, 3, "およいだあとは、\nちゃんと体を\nあっためなくちゃね！", 1, 122901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6421, 7, 0, 3, "夏休みのご飯って\nなんだかとくべつ\nおいしい気がするね♪", 1, 122901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6422, 8, 0, 3, "夏の星座って\nすっごくきれいだね♪\nいつかみんなと\nいっしょに見たいな♪", 1, 122901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6424, 10, 0, 3, "わぁ♪　この子、\nとってもかわいいね！\nおともだちに\nなりたいな♪", 1, 122901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6425, 11, 0, 3, "ミミ、人魚姫に\nなったみたい！\nおにいちゃん、\nとなりにすわって♪", 1, 122901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6426, 1, 0, 1, "水着姿になると\nバカンス気分を\n味わえちゃうね！", 1, 122801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6427, 2, 0, 1, "海に行くの待ちきれ\nなくて、もう水着に\n着替えちゃった！", 1, 122801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6428, 3, 0, 1, "海に行ったら\nずぶぬれ系のイタズラ\nたくさんしかけよう！", 1, 122801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6429, 4, 0, 1, "部屋の中で泳ぎの練習\nしよう！　にいちゃん\nも水着に着替えて！", 1, 122801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6430, 5, 0, 1, "お日さまとお揃いの\n水着なんだ～♪\n似合ってるかなぁ？", 1, 122801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6431, 101, 0, 1, "海といえば冒険！\n海底に眠る財宝を\n見つけちゃおう！", 4, 122801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6432, 102, 0, 1, "にいちゃんと一緒だと\n部屋にいても海気分♪\nスイカわりしよっか！", 8, 122801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6433, 1, 0, 2, "にいちゃん！\n早く海に行く準備\nしなよ～！", 1, 122801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6434, 2, 0, 2, "ねーにいちゃん\n何秒息止められるか\n勝負しよーよ！", 1, 122801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6435, 3, 0, 2, "にいちゃんはアイスと\nかき氷どっちが好き？\nミソギは…どっちも！", 1, 122801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6436, 4, 0, 2, "日焼け止めクリーム\nキョウカに\nもらったんだ～", 1, 122801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6437, 101, 0, 2, "水中メガネがあれば\n水鉄砲くらっても\n平気だね！", 4, 122801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6438, 102, 0, 2, "水着なら、毎日夏休み\n気分！　にいちゃんも\n着てみなよ～！", 8, 122801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6439, 1, 0, 2, "にいちゃんおはよー！\n早く顔洗って、\nいっぱい遊んじゃお！", 1, 122801, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6440, 2, 0, 2, "にいちゃんデザートは\nイチゴのかき氷だよ！\n…激辛ってバレた？", 1, 122801, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6441, 3, 0, 2, "今日は楽しかった～！\n明日もイタズラ日和に\nなるといいな！", 1, 122801, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6443, 2, 0, 3, "落とし穴の下に\nやわらかいの置けば\nケガしないよね！", 1, 122801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6444, 3, 0, 3, "かわいいね～！\nこの子も海に\n連れてっちゃおうよ！", 1, 122801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6445, 4, 0, 3, "このにおいを利用して\nイタズラするには…\nにいちゃんどう思う？", 1, 122801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6446, 5, 0, 3, "水着を着て暖炉で\nあったまるのって、\n変でおもしろいね～！", 1, 122801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6448, 7, 0, 3, "海辺で食べる\nお昼ごはんって最高♪\nってことで海行こう！", 1, 122801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6449, 8, 0, 3, "いつか海を越えて、\n地図にも載ってない\n場所を見つけたいな！", 1, 122801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6451, 10, 0, 3, "この口から水が出たら\nみんな驚くよね！\nぷぷ…細工しちゃお！", 1, 122801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6452, 11, 0, 3, "変な音がするイタズラ\nを仕込んどいて…\n誰か座らないかな～♪", 1, 122801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6453, 103, 0, 1, "あなた様と私の愛を\n周りに見せつけて\n差し上げましょう♪", 12, 102701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6454, 103, 0, 2, "呼びましたか？\n呼びましたね？\nでは愛を語りましょう\nそうしましょう", 12, 102701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6455, 1, 0, 1, "水着で歩き回るのは\n恥ずかしいな…\n誰も見てないよね？", 1, 122701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6456, 2, 0, 1, "夏はやっぱり\nかき氷ですよね\n何色がいいかなぁ…♪", 1, 122701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6457, 3, 0, 1, "あの人、泳げないって\n聞きました！\n私が教えてあげないと", 1, 122701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6458, 4, 0, 1, "夏休みの宿題は\n計画的に進めましょう\n当たり前のことです！", 1, 122701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6459, 5, 0, 1, "防犯魔石、どこに\n着けておこうかなぁ\n…ここにしようかな", 1, 122701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6460, 101, 0, 1, "海の楽しみ方は\n予習済みです！\nあとは実践ですね♪", 4, 122701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6461, 102, 0, 1, "水着…きっとあの人に\n見られちゃいますよね\n…褒めてくれるかな…", 8, 122701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6462, 1, 0, 2, "触りたいんですか？\n…いいですよ\n帽子くらいなら", 1, 122701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6463, 2, 0, 2, "夏は体調管理に\n気をつけましょう\n私が管理してあげます", 1, 122701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6464, 3, 0, 2, "海に行くのが楽しみで\nたまらないんですか？\n私はそこまで…嘘です", 1, 122701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6465, 4, 0, 2, "いつもより薄着だから\nなんだか恥ずかしい…\nみ、見ないでください", 1, 122701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6466, 101, 0, 2, "へんたいふしんしゃ～\nえっ？　ああ、これは\n海に叫ぶ練習です", 4, 122701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6467, 102, 0, 2, "あなたと一緒に\n海に行くなんて…\nなんだか大人…えへへ", 8, 122701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6468, 1, 0, 2, "おはようございます\nさぁ、海に行って\n私と運動しましょう", 1, 122701, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6469, 2, 0, 2, "あっつ～い…\nはっ！？　ダメですよ\nシャキッとしないと！", 1, 122701, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6470, 3, 0, 2, "明日も海で\n遊ぶために、しっかり\nお休みしましょうね", 1, 122701, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6472, 2, 0, 3, "夏を楽しむときは\n休憩も大事ですよね\n計画的に、計画的に！", 1, 122701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6473, 3, 0, 3, "かわいいなぁ～\n海のお土産屋さんに\n売ってるかなぁ？", 1, 122701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6474, 4, 0, 3, "大人な香りがします\n私も違いが分かる\n素敵な女性になります", 1, 122701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6475, 5, 0, 3, "濡れた体を\n乾かしましょう\nタオル、タオル…", 1, 122701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6477, 7, 0, 3, "泳いだ後は\n栄養補給…だけど\n…おなか膨らんじゃう", 1, 122701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6478, 8, 0, 3, "遊ぶだけじゃなくて\nお勉強もしてますよ\nあなたもどうですか？", 1, 122701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6480, 10, 0, 3, "ミミが喜びそう…♪\nわ、私ははしゃいだり\nしませんけど…っ！", 1, 122701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6481, 11, 0, 3, "ミソギがいたずらに\n使いそう…しっかりと\n試しておかないと！", 1, 122701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6482, 1, 0, 1, "あたしがまさか\nここにいるなんて…\n自分でも不思議だわ", 1, 123001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6483, 2, 0, 1, "昔みたいにあちこち\n飛び回れないのって\nちょっと不便かも", 1, 123001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6484, 3, 0, 1, "こうして誰かと一緒に\n暮らせるのもあいつや\nみんなのおかげね", 1, 123001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6485, 4, 0, 1, "ここで厄介になるなら\nあたしも家事とか\n手伝わないと…", 1, 123001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6486, 5, 0, 1, "…だめ！　誰かの\n世話をせずには\nいられないわ！", 1, 123001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6487, 101, 0, 1, "あいつたちが\n帰ってくるところだし\n綺麗にしないと…", 4, 123001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6488, 102, 0, 1, "せっかくあたしが\nいるっていうのに\n少しは構いなさいよ…", 8, 123001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6489, 1, 0, 2, "おいしい店とか\n知らないの？\n食べに行きましょ", 1, 123001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6490, 2, 0, 2, "あんた暇でしょ？\nあたしのやること\n手伝いなさい", 1, 123001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6491, 3, 0, 2, "コッコロたんと\n遊んでくるから\n留守番よろしく♪", 1, 123001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6492, 4, 0, 2, "あらおかえり…って\nボロボロじゃない！\nまた人助けしてたの？", 1, 123001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6493, 101, 0, 2, "やっぱりあたしは\nあんたのそばが\n一番落ち着くみたい", 4, 123001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6494, 102, 0, 2, "どこまでだって\nあんたと一緒に\nいてやるんだから", 8, 123001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6495, 1, 0, 2, "ふわぁ…おはよう\n…大きくなったせいか\n朝は特に体が重いわ", 1, 123001, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6496, 2, 0, 2, "お昼ご飯が食べたい？\nしょうがないわね…\nなんか作って\nあげるから", 1, 123001, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6497, 3, 0, 2, "あんたまだ成長期だし\nさっさと寝なさい\n大きくなれないわよ？", 1, 123001, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6499, 2, 0, 3, "たまにはあたしも\nゆっくりしないと\nもたないのよ…", 1, 123001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6500, 3, 0, 3, "かわいいじゃな～い♪\nコッコロたんに\nプレゼントしたいわ", 1, 123001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6501, 4, 0, 3, "いい香りね\n塔にもこういうの\n置いておこうかしら", 1, 123001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6502, 5, 0, 3, "炎か…前は\n羽が焼けそうで\n怖かったっけ", 1, 123001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6504, 7, 0, 3, "あむ…妖精に\n体重増減の設定って\nあったっけ…？", 1, 123001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6505, 8, 0, 3, "記憶のデータと違って\n物語はいいわね\n結末が読めなくて", 1, 123001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6507, 1, 0, 1, "冒険のお土産\nみんなにおすそわけ\nせなあきまへんな", 1, 123101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6508, 2, 0, 1, "おとぎ話よりも\n不思議なことが\nあるもんやねぇ", 1, 123101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6509, 3, 0, 1, "冒険しに出かけるなら\n動きやすい服装に\nせななぁ", 1, 123101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6510, 4, 0, 1, "地図に水筒に…\n他に忘れもんは\nあらへんやろか？", 1, 123101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6511, 5, 0, 1, "これまでの冒険のこと\nノートに書いて\nまとめてますんよ", 1, 123101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6512, 101, 0, 1, "王子はんとの冒険は\nどれもマホマホ王国の\n大事な歴史や♪", 4, 123101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6513, 102, 0, 1, "どこへ冒険するときも\nうちと王子はんは\n一緒どす♪", 8, 123101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6514, 1, 0, 2, "魔法で火ぃつける\n練習してますんよ～\nよう爆発しますけど…", 1, 123101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6515, 2, 0, 2, "うちと王子はんで\n冒険道具をお揃いに\nするんもええなぁ♪", 1, 123101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6516, 3, 0, 2, "テント張りも随分\n上達したんよ\n王子はんのおかげやね", 1, 123101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6517, 4, 0, 2, "ランスロットたちの\n新しい冒険衣装\n思いついたんよ♪", 1, 123101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6518, 101, 0, 2, "迷子になっても\n王子はんと一緒なら\nきっと大丈夫やね♪", 4, 123101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6519, 102, 0, 2, "王子はんのこと、\nきっちり守れるように\nなってみせますえ", 8, 123101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6520, 1, 0, 2, "お寝坊さんは\n布団めくって\n起こしにいきますえ～", 1, 123101, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6521, 2, 0, 2, "支度が終わったら\n今日の冒険に\n出かけまひょか", 1, 123101, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6522, 3, 0, 2, "もうちょっと\nおしゃべりしたいん？\nあと少しだけやで♪", 1, 123101, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6524, 2, 0, 3, "冒険で疲れた体に\nこの癒しぱわーは\nあきまへん…すや…", 1, 123101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6525, 3, 0, 3, "ふふ、あんさんも\nうちと一緒に\n冒険します？", 1, 123101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6526, 4, 0, 3, "虫除けのアロマは\n冒険の必需品どす\n忘れんようにせんと", 1, 123101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6527, 5, 0, 3, "うちも魔法使わんでも\n火ぃ起こせるよう\n練習せんとあかんなぁ", 1, 123101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6529, 7, 0, 3, "この料理…\n冒険するときに\n作ってみまひょか", 1, 123101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6530, 8, 0, 3, "王子はんとの\n愛と冒険のおとぎ話…\nはぁ…素敵やわぁ♪", 1, 123101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6532, 103, 0, 1, "あの人との\n最良の未来のために\n占いも霊能力ももっと\n精進しなきゃ", 12, 103101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6533, 103, 0, 2, "あなたともっと深く\nつながりたいです\n絆も、そして魂までも", 12, 103101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6534, 103, 0, 1, "絆の契約…\nあやつが望むのなら\n考えぬでもないがのう", 12, 104401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6535, 103, 0, 2, "なんじゃ？\nわらわの姿に\n見惚れたかの？", 12, 104401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6536, 1, 0, 1, "旅の荷物って多いな…\nねぇぷうきち～\nすこしは持ってよぉ～", 1, 123201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6537, 2, 0, 1, "傘はなくてもいっか\nぷうきちがいるし♪\n『おいなんだと…？』", 1, 123201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6538, 3, 0, 1, "旅先で人見知りなんか\nしてちゃだめだよね…\nまずはあいさつだ…！", 1, 123201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6539, 4, 0, 1, "おみやげはやっぱり\nご当地ぬいぐるみ\nだよね♪　ワクワク…", 1, 123201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6540, 5, 0, 1, "知らない場所かぁ…\nうぅ…考えただけで\nすでにホームシック…", 1, 123201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6541, 101, 0, 1, "野宿するってことは…\nお兄ちゃんのマントで\n寝るチャンスかも…！", 4, 123201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6542, 102, 0, 1, "どんなに危険な冒険も\nお兄ちゃんと一緒なら\n大丈夫！　…だよね？", 8, 123201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6543, 1, 0, 2, "ぷうきちのアイマスク\nも腹巻も持ってくの！\n旅の必需品なの～！", 1, 123201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6544, 2, 0, 2, "これ全部背負うのって\nけっこうむずかしいの\n順番まちがえると大変", 1, 123201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6545, 3, 0, 2, "えへへ～\nなでなでして？　帽子\nの上からでもいいよ♪", 1, 123201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6546, 4, 0, 2, "『悪いが絆創膏の部分\nはアンタッチャブルだ\n綿が飛び出ちまう』", 1, 123201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6547, 101, 0, 2, "お兄ちゃんと一緒に\nもっともっといろんな\n景色が見たいな！", 4, 123201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6548, 102, 0, 2, "旅って、終わる時は\nきっとさびしいよね…\nお兄ちゃん、帰り道は\n手をつないでもいい？", 8, 123201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6549, 1, 0, 2, "出発が楽しみすぎて\n寝られなかった…\n『旅行のお約束だな』", 1, 123201, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6550, 2, 0, 2, "わぁ　いいお天気！\n旅の間もずーっと\nぴーかんだといいな♪", 1, 123201, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6551, 3, 0, 2, "知らない場所で寝るの\n怖いな…寝る時だけ\n帰ってこようかな…", 1, 123201, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6553, 2, 0, 3, "旅先にはクッションも\nソファもないよね…\n『俺でいいだろ』", 1, 123201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6554, 3, 0, 3, "ぬいぐるみのみんな\n旅の無事を\n祈ってて…！", 1, 123201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6555, 4, 0, 3, "旅先の香りも\n思い出に持って\n帰れたらいいのにな～", 1, 123201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6556, 5, 0, 3, "旅行用に携帯用暖炉\nがあるといいかも…！\n『まだ荷物増やす？』", 1, 123201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6558, 7, 0, 3, "『もう出発だぜ？』\nえーまってまって！\n腹ごしらえがまだ…", 1, 123201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6559, 8, 0, 3, "世界ってこんなに広い\nんだ…！　はやく自分\nの目で見てみたいな～", 1, 123201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6561, 1, 0, 1, "はちゃめちゃヒマ～\nなーんか楽し～コト\nないかなー", 1, 123301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6562, 2, 0, 1, "ここ、いートコだねー\nこんどカリザきゅんも\nつれてこよー", 1, 123301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6563, 3, 0, 1, "かわゆいショタが\n見た～い♪\n触りた～～～い♪", 1, 123301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6564, 4, 0, 1, "なんか服きっついなー\nちょい太ったかー？\nん～\nま・気のせーっしょ♪", 1, 123301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6565, 5, 0, 1, "あ～やべ\nおねえちゃんに\nメッセすんの忘れたー\nまいっかー", 1, 123301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6566, 101, 0, 1, "パンケーキ食いてー\n騎士きゅん\n誘ってみよーっと♪", 4, 123301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6567, 102, 0, 1, "きょーも～\n好きぴに囲まれて～\nシ・ア・ワ・セ♪", 8, 123301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6568, 1, 0, 2, "アタシと遊ばな～い？\nってなんか\nナンパみスゴー\nウケる～", 1, 123301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6569, 2, 0, 2, "んー？\n今はだるいからパース\nあとでねー", 1, 123301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6570, 3, 0, 2, "カリザきゅん\n見なかった～？\nおねえちゃんから\n逃げるとかオシオキー", 1, 123301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6571, 4, 0, 2, "ネイルしたいけど\n無理なんよねー\n爪の間にめっちゃ\nスライム入っからさー", 1, 123301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6572, 101, 0, 2, "おねえちゃんに\nかまってほし～の？\nかわゆ～い♪", 4, 123301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6573, 102, 0, 2, "な～に騎士きゅん♪\nここでイチャイチャ～\nし・ちゃ・う～？", 8, 123301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6574, 1, 0, 2, "ねんむ～\nけど外から\nショタの声したんで～\nつい起きちった～", 1, 123301, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6575, 2, 0, 2, "ランチの後はやっぱ\nデザートっしょー\nベツバラってヤツ～♪", 1, 123301, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6576, 3, 0, 2, "こんな時間まで\n起きてるコは～\n食べちゃうぞ～♪", 1, 123301, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6578, 2, 0, 3, "あーラクー\nいっしょーここで\nキューケーできるわー", 1, 123301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6579, 3, 0, 3, "なにこれ～\nかわいいじゃ～ん\nショタの次くらいに～", 1, 123301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6580, 4, 0, 3, "いーニオイする～\nなんのニオイとかー\nよくわかんないけどー", 1, 123301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6581, 5, 0, 3, "あったか～♪\n溶けてスライム\n戻りそ～", 1, 123301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6583, 7, 0, 3, "ン・ン・ン～♪\nお・い・し～♪\nも～っと食べるぅ～♪", 1, 123301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6584, 8, 0, 3, "わ～なんかムズー\n読めなー\nウケるー", 1, 123301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6586, 1, 0, 1, "あんパンって\nお菓子に入るのかな？\nハロウィンで\nもらえたりしない？", 1, 123501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6587, 2, 0, 1, "お菓子をくれなきゃ\nぐうたらするぞ～！\nハロウィン以外でも！", 1, 123501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6588, 3, 0, 1, "お化けになったら\n仕事しないで\nゴロゴロし放題\nなのかな？", 1, 123501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6589, 4, 0, 1, "おっと…この仮装\n袖がすごく長いからさ\n引っ掛けないように\n気を付けないとね～", 1, 123501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6590, 5, 0, 1, "イタズラするのも\n体力がいるよね～\n楽にできそうなの\n何かないかな？", 1, 123501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6591, 101, 0, 1, "あいつって\n仮装とかするのかな？\n似合いそうなのは\nどんなお化けだろ？", 4, 123501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6592, 102, 0, 1, "ずっとハロウィンなら\nあいつと遊び放題\nなんだけどな～", 8, 123501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6593, 1, 0, 2, "今の街はハロウィン\n一色だからね～\n人混みが大変だよ", 1, 123501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6594, 2, 0, 2, "この仮装、どうかな？\n気に入ったなら\n来年もしてあげるよ～", 1, 123501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6595, 3, 0, 2, "実はこの長い袖\nドングリとかを\nため込めるんだよね…", 1, 123501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6596, 4, 0, 2, "カボチャランタンが\nあるんなら\nドングリランタンも\nあっていいよね～♪", 1, 123501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6597, 101, 0, 2, "ハロウィンでも\nやる気があれば\nぐうたらできる…\nうん、いいこと言った", 4, 123501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6598, 102, 0, 2, "一緒にお菓子食べよ？\n寝転びながら堪能する\nハロウィンスイーツは\n最高だからね～", 8, 123501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6599, 1, 0, 2, "お化けは寝る時間…\n今のあたしはお化け…\nだから、おやすみ～…", 1, 123501, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6600, 2, 0, 2, "お昼か～…\n食後のデザートは\nハロウィンのお菓子で\n決まりだね～♪", 1, 123501, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6601, 3, 0, 2, "ふぁ～、寝よ…\nお化けは夜に\n出るものだけど\nあたしはリスだからね", 1, 123501, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6603, 2, 0, 3, "ハロウィンを楽しむ\nためにも、ぐっすり\n寝とかないとね～", 1, 123501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6604, 3, 0, 3, "…袖から手が\n出せないから\nこのまま撫でよう\nよしよし", 1, 123501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6605, 4, 0, 3, "リラックスできる\n素敵な香りだね～\n成仏しそうだよ～", 1, 123501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6606, 5, 0, 3, "あったかいね～\nここで寝てもいい？\n…着替えないとダメか", 1, 123501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6608, 7, 0, 3, "もぐもぐ…\nお化けもリスも\nお腹は空くんだよ", 1, 123501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6609, 8, 0, 3, "キョンシーって\nどんなお化けなのか\n本で調べようかな？", 1, 123501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6611, 12, 0, 3, "より取り見取りだね～\nとりあえず、これと\nこのお菓子はキープで", 1, 123501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6613, 1, 0, 1, "よし、衣装もばっちり\nハロウィンパレードへ\n出かけよう♪", 1, 123601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6614, 2, 0, 1, "仮装してても\n剣は手放せないね\nほとんど体の一部だよ", 1, 123601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6615, 3, 0, 1, "見回りに誘うふりして\n二人でパレードに\n参加しちゃおうかな♪", 1, 123601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6616, 4, 0, 1, "…あれ？　手錠の鍵\n落としちゃった…？\nやば、出かける前に\n見つけないと…！", 1, 123601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6617, 5, 0, 1, "お家でコスプレも\nこれはこれで楽しいな\n決めポーズの練習\nたくさんしておこう！", 1, 123601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6618, 101, 0, 1, "他にも、お姫様に\nメイドさんに学生服…\nやりたいコスプレが\n多すぎるよ～", 4, 123601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6619, 102, 0, 1, "漫画もゲームも好き！\n仮装するのも大好き！\n…好きを言葉にすると\nもっと好きになる♪", 8, 123601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6620, 1, 0, 2, "ずっと目が合ってるね\n見つめてくれるのは\nいいんだけど、今日は\n衣装を見てほしいな♪", 1, 123601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6621, 2, 0, 2, "トリックオアトリート\nお菓子をくれなきゃ\n逮捕しちゃうぞ♪", 1, 123601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6622, 3, 0, 2, "あなたにだけはこの姿\nいつでも見せてあげる\nふふ、なんちゃって♪", 1, 123601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6623, 4, 0, 2, "どうしたの？\nイタズラするなら\n隙を突かなきゃ\nこんなふうに、ね♪", 1, 123601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6624, 101, 0, 2, "コスプレしてても\n剣の稽古は休まないよ\nちょっと相手になって\nくれるかな？", 4, 123601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6625, 102, 0, 2, "ひゃっ、びっくりした\n急に触らないでよ～\nハロウィンだからって\nイタズラしないでね？", 8, 123601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6626, 1, 0, 2, "おはよう！\n今日も朝から\nパトロールに行こう！", 1, 123601, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6627, 2, 0, 2, "カボチャのケーキを\n用意してるよ！\nデザートを食べて\n午後も頑張ろう！", 1, 123601, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6628, 3, 0, 2, "暗くなってきたね\nカボチャのランタンに\n火を灯そうよ♪", 1, 123601, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6630, 2, 0, 3, "ふかふか気持ちいい～\nあっでも衣装がシワに\nなっちゃうかも…？\n…でも気持ちいい～", 1, 123601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6631, 3, 0, 3, "…かわいい\nこの子もパレードに\n連れて行きたいな", 1, 123601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6632, 4, 0, 3, "はぁ～癒される♪\nハロウィンの見回りが\n終わった後は、\nまったりくつろごう", 1, 123601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6633, 5, 0, 3, "ここで二人寄り添って\nカボチャスープ飲もう\n…なんて想像したら\nポカポカしてきた…", 1, 123601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6635, 7, 0, 3, "パレードに参加すると\n体力を使うから、\nいっぱい食べておこう", 1, 123601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6636, 8, 0, 3, "この絵本のお姫様\nかわいいね\nこの仮装もいいな♪", 1, 123601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6638, 12, 0, 3, "わあ、おいしそう！\n見回りに行く前に\n少しつまんじゃおう♪", 1, 123601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6640, 103, 0, 1, "今日こそは、あの人に\n気持ちを打ち明けて…\nそのあとは…うう\nど、どうしよう？", 12, 103701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6641, 103, 0, 2, "私が剣なら、あなたは\nさしずめ私の鞘…\nふふ、私の帰る\n場所ってことだよ♪", 12, 103701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6642, 1, 0, 1, "やっぱり\nコスプレしてると\nテンションあがる～♪", 1, 123701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6643, 2, 0, 1, "キャラコスにも\n興味あるけど…\nマジカル、ラブリー\nどっちにしよっかな～", 1, 123701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6644, 3, 0, 1, "キャラコスといえば\nミスティーシャイニー\nもいいんだよねえ…\nうはー！　悩むー！", 1, 123701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6645, 4, 0, 1, "ギルドハウスで\n宅コスしてる人\n実は結構\nいるんじゃないかな～", 1, 123701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6646, 5, 0, 1, "コスプレだけど普段着\nって言いはれそう…\nって、ダメダメ！\nオタクは節度が大事！", 1, 123701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6647, 101, 0, 1, "フェスで買ってきた\n薄い本～♪\nギルドハウスに持って\nきちゃおっかな～", 4, 123701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6648, 102, 0, 1, "ようこそ\nコスプレ沼へ～\nなんちゃって♪", 8, 123701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6649, 1, 0, 2, "どうどう？\nナナカちゃんの\nハロウィンコスプレ\n似合ってる？", 1, 123701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6650, 2, 0, 2, "ハロウィン魔法少女\n参上カボ～！\n…やっぱ今の語尾\nナシで！", 1, 123701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6651, 3, 0, 2, "ハロウィンといえば\nトリックオア～…\nコスプレ！\nなんちゃって", 1, 123701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6652, 4, 0, 2, "ランフェスの\n戦利品～♪\nねえねえ\n見たい？　見たい？", 1, 123701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6653, 101, 0, 2, "私にしてほしい\nコスプレがあったら\n教えて♪　君のために\nや～ってやるぜぇ！", 4, 123701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6654, 102, 0, 2, "一緒にコスプレしたい\nならナナカちゃんは\nいつでも大歓迎だよ♪", 8, 123701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6655, 1, 0, 2, "ふぁあ…ねむ\nって、ありゃりゃ　\n昨日コスプレしたまま\n寝ちゃってたかあ…", 1, 123701, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6656, 2, 0, 2, "今日のお昼は\nなんにしよ？\nカボチャ料理に\nしよっかな～", 1, 123701, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6657, 3, 0, 2, "もうこんな時間かあ\nそういえばオタクって\n夜行性の人が多い\n気がするぜぇ…", 1, 123701, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6659, 2, 0, 3, "ふっふっふ\nこのパニエは\n座るときはちゃんと\nふにゃってなるのさ！", 1, 123701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6660, 3, 0, 3, "いや～\nかわいいものをみると\n癒されますな～", 1, 123701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6661, 4, 0, 3, "新しい薄い本の匂いの\nアロマとか…ダメ？\n私はあの匂い、なんか\n好きなんだよね～", 1, 123701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6662, 5, 0, 3, "暖か～い…\nけど近づきすぎて\n衣装が燃えないように\n気を付けなきゃ", 1, 123701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6664, 7, 0, 3, "コス衣装のために\n体型維持は必須だけど\nついつい\n食べちゃうんだよね～", 1, 123701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6665, 8, 0, 3, "おっ、次はこの本に\nのってるキャラのコス\nしてみよっかな？", 1, 123701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6667, 12, 0, 3, "おおっ！\nお菓子がいっぱい！\nベルルちゃんチョコ\n入ってないかな～？", 1, 123701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6669, 1, 0, 1, "むっ…\nこの耳の手触り…\n驚嘆に値する…！", 1, 123801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6670, 2, 0, 1, "獲物を狩りまくる…\nそんなウサギがいても\nいいだろう？", 1, 123801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6671, 3, 0, 1, "ワナなど怖くないぞ？\nむしろ積極的に\n踏んでいく性分だ", 1, 123801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6672, 4, 0, 1, "一匹狼ならぬ\n一匹ウサギか\nどっちでもかまわん♪", 1, 123801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6673, 5, 0, 1, "人間とて本を正せば獣\n本能に従えばよいのだ\n遠慮は無用さ☆", 1, 123801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6674, 101, 0, 1, "脱兎のごとく…\n逃げるものか\n坊やを追い回すのさ♪", 4, 123801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6675, 102, 0, 1, "獣の道は険しいが…\n坊やめ、音を上げずに\nちゃんとついてきたな\n褒めてやるとしよう♪", 8, 123801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6676, 1, 0, 2, "ニンジンはいらん\n好物は好敵手だ\nさぁどんどんおくれ♪", 1, 123801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6677, 2, 0, 2, "尻尾に触りたいだと？\nワタシの乱数聖域を\n崩せたらな…☆", 1, 123801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6678, 3, 0, 2, "頭で考えるな\n体に率直になれ\n己を野放しにするのさ", 1, 123801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6679, 4, 0, 2, "そうさモコフワさ♪\nなで心地は最高だが\n意外にどう猛だぞ…？", 1, 123801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6680, 101, 0, 2, "ワタシにピョンピョン\n跳ねてほしいのか？\nあとでじっくり、な♪", 4, 123801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6681, 102, 0, 2, "ワタシと番になるか？\nオマエさえよければ\nいつでもかまわんぞ☆", 8, 123801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6682, 1, 0, 2, "太陽と共に目覚め\n活動する\nそれが獣の生き方さ☆", 1, 123801, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6683, 2, 0, 2, "昼寝をすればいい\nワタシが周囲を\n警戒していてやる", 1, 123801, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6684, 3, 0, 2, "暗くなれば眠るだけさ\nそうすれば余計な\nストレスなどたまらん", 1, 123801, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6686, 2, 0, 3, "むむっ！？\n尻尾がつっかえて\nうまく座れんな…", 1, 123801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6687, 3, 0, 3, "愛らしさでワタシに\n張り合おうというのか\n５億年後に出直せ☆", 1, 123801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6688, 4, 0, 3, "野生の芳香か…\n本能が呼び覚まされる\nいいセンスだ♪", 1, 123801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6689, 5, 0, 3, "寒さも野生のうちだ\n…とはいえ\n温まっていくか♪", 1, 123801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6691, 7, 0, 3, "悪くはないが物足りん\nこう見えて肉食なのだ\n肉はないのか肉は☆", 1, 123801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6692, 8, 0, 3, "ウサギ族は地獄耳？\nワタシとの共通点だな", 1, 123801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6694, 103, 0, 1, "ここが私の家…\nそれはこれからも\nずっと変わらないわ…", 12, 106401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6695, 103, 0, 2, "ええと、その…今から\nデート…じゃなくって\n散歩でもどうかしら？\n…ふ、二人きりで…", 12, 106401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6696, 1, 0, 1, "ギルドハウスに\nヒーロー参上！\nみんな仲良くッスよ！", 1, 123901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6697, 2, 0, 1, "料理もできて\n鎧も作れる！\nお母さんはすごいッス", 1, 123901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6698, 3, 0, 1, "ううう…\nまたマントをドアに\n挟んじゃったッス…", 1, 123901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6699, 4, 0, 1, "ずっと鎧を着ていれば\nジュンさんに近づける\nかも？　燃えるッス！", 1, 123901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6700, 5, 0, 1, "サインの練習…\nもっとしておいた\nほうがいいッスかね～", 1, 123901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6701, 101, 0, 1, "半人前でも\n胸を張るッス！\nそのうち似合う日が\nくるッスから！", 4, 123901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6702, 102, 0, 1, "種族の違いも\n強い魂があれば\n超えられるッス！\n信じてるッスよ！", 8, 123901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6703, 1, 0, 2, "困ったときは\n自分を呼ぶッス！\nあと楽しいときにも\n呼んでほしいッス！", 1, 123901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6704, 2, 0, 2, "特訓でも行くッスか？\n望むところッス！\n目指せ、一人前！", 1, 123901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6705, 3, 0, 2, "新しいショーの台本を\n読みこんでるッス！\nところで…この言葉の\n意味、分かるッスか？", 1, 123901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6706, 4, 0, 2, "ヒミツがなくなると\nなんか恥ずかしいッス\nあんたはどうッスか？", 1, 123901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6707, 101, 0, 2, "ヒーローは遅れて\nやってくる…\nあんたもそうッスね♪", 4, 123901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6708, 102, 0, 2, "誰よりも早く\nあんたに見せたいッス\n一人前になった自分！", 8, 123901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6709, 1, 0, 2, "うーん、今日も\nよく眠れたッス～！\n絶好調、間違いなし！", 1, 123901, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6710, 2, 0, 2, "おお、もうお昼ッスか\nどうりでお腹が空くと\n思ったッス～えへへ", 1, 123901, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6711, 3, 0, 2, "パトロールのあとは\n歯みがきに宿題に…\n大忙しッスね、夜！", 1, 123901, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6713, 2, 0, 3, "こんなにリラックス\nできるなんて…\n平和な証拠ッスね～", 1, 123901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6714, 3, 0, 3, "か、かわいい…♪\nさあ、自分と握手…\nううん、抱っこッス♪", 1, 123901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6715, 4, 0, 3, "優しい匂いッスね…♪\nこれくらいみんなを\n包み込めるように\nなりたいッス～", 1, 123901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6716, 5, 0, 3, "ひっくちゅ！　うー…\nちょっと冷えるッスね\nぬくぬくするッス～♪", 1, 123901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6718, 7, 0, 3, "特訓した分、\nいっぱい食べるッス！\nでっかくなるッス！", 1, 123901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6719, 8, 0, 3, "いつか自分の\nヒミツ図鑑が出たり\nして…いやいやいや", 1, 123901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6721, 1, 0, 1, "牢獄よりは\nマシな環境ね\n我慢してあげるわ", 1, 106901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6722, 2, 0, 1, "退屈ね…\nキャルでもいじって\n暇をつぶそうかしら", 1, 106901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6723, 3, 0, 1, "ここにいる連中\n私を天気予報士と\n勘違いしてない？", 1, 106901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6724, 4, 0, 1, "音波情報が多いわね…\nここ、もう少し静かに\nならないの…？", 1, 106901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6725, 5, 0, 1, "共同生活なんて\n久しぶりだわ\n…懐かしいわね", 1, 106901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6726, 101, 0, 1, "あの子たちのギルド\n経営が雑すぎるわ…！\n教育が必要みたいね", 4, 106901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6727, 102, 0, 1, "とどめを刺される\n準備はできているのに\nまだなのかしら…？", 8, 106901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6728, 1, 0, 2, "私と戦う気になった？\nあら、まだなのね…\nその時を待ってるわよ", 1, 106901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6729, 2, 0, 2, "私というリソースは\n有効活用しなさい\n子守なんてごめんだわ", 1, 106901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6730, 3, 0, 2, "どうしてそんなに\n頭がいいか？\n生まれつきよ", 1, 106901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6731, 4, 0, 2, "視力は権能に\n影響しないわよ\n当たり前じゃない", 1, 106901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6732, 101, 0, 2, "あなたまで天気を\n教えろですって…？\nぶっ殺すわよ？", 4, 106901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6733, 102, 0, 2, "暇なら付き合いなさい\n好きなゲームでいいわ\nどうせ私が勝つもの", 8, 106901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6734, 1, 0, 2, "人生朝露…\n悔いのない一日を\n過ごしなさい", 1, 106901, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6735, 2, 0, 2, "午睡はいいわよ…\nふわぁ…眠りは思考に\n必要不可欠なの…", 1, 106901, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6736, 3, 0, 2, "夙興夜寝なんて\nやめておきなさい\n休息は大切よ", 1, 106901, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6738, 2, 0, 3, "私を満足させるなんて\n大したものね…晶に\n大量生産させるわ", 1, 106901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6739, 3, 0, 3, "キャルの代わりに\nズタズタにする物が\n欲しかったの…♪", 1, 106901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6740, 4, 0, 3, "いい香りじゃない\n見たことのない調合ね\n誰が用意したの？", 1, 106901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6741, 5, 0, 3, "パワーが足りないわね\nもっとよ、もっと私を\n寛がせなさい", 1, 106901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6743, 7, 0, 3, "栄養価はともかく\n味のバランスは\n悪くないわね", 1, 106901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6744, 8, 0, 3, "つまらない本は\n私が書き直すわ\nこれはどうかしら？", 1, 106901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6746, 1, 0, 1, "今年はアカリと\nお姉ちゃんでサンタが\n二人…つまり幸せも\n二倍ですね♪", 1, 124001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6747, 2, 0, 1, "クリスマスに向けての\nサックス練習…\nお兄ちゃんと一緒に\n猛特訓です！", 1, 124001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6748, 3, 0, 1, "クリスマスツリーの\n飾りつけ、完璧です♪\nお兄ちゃんお姉ちゃん\n喜んでくれるかな？", 1, 124001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6749, 4, 0, 1, "わあ、お外が真っ白！\n生クリームが\nいーっぱいの\nケーキみたいです♪", 1, 124001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6750, 5, 0, 1, "煙突から入ると\nお洋服が汚れちゃう…\n玄関からじゃ\nダメなのかなぁ？", 1, 124001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6751, 101, 0, 1, "お兄ちゃんが来たら\nサンタのお洋服\nいっぱい褒めて\nもらおっと♪", 4, 124001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6752, 102, 0, 1, "聖夜をお兄ちゃんと\n過ごせたら…\nアカリもちょっぴり\n大人になれるかも♪", 8, 124001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6753, 1, 0, 2, "今のアカリは\nサンタさんですから♪\nなんでもお願いして\nくださいね？", 1, 124001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6754, 2, 0, 2, "お外は寒いですから\nアカリとくっついて\nお出かけしましょ♪", 1, 124001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6755, 3, 0, 2, "お兄ちゃんは\nアカリのトナカイさん\nですから、ずーっと\nそばにいてください♪", 1, 124001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6756, 4, 0, 2, "お兄ちゃん、助けて！\nアカリのカラダで\nラッピング練習したら\n絡まっちゃいました…", 1, 124001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6757, 101, 0, 2, "プレゼント、いっぱい\n準備できました♪\nお兄ちゃんの分は…\nまだヒミツでーす♪", 4, 124001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6758, 102, 0, 2, "アカリ、プレゼントは\n大人っぽいのがいいな\n早くお兄ちゃんに\n追いつきたいから♪", 8, 124001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6759, 1, 0, 2, "おはようございます♪\nふふ、アカリサンタは\nいい子のお手本なので\n早寝早起きなんです♪", 1, 124001, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6760, 2, 0, 2, "あれ、もうお昼？\nクリスマスナンバーの\n練習してたら\nもうこんな時間…", 1, 124001, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6761, 3, 0, 2, "冬の夜空は星が\nキレイですね～\nお兄ちゃん、一緒に\n見に行きませんか？", 1, 124001, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6763, 2, 0, 3, "はぁ、快適すぎて\nとろけちゃいそう…\nこういうプレゼントも\nいいかもです…", 1, 124001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6764, 3, 0, 3, "やん、かわいいです♪\nえへへ、お兄ちゃんは\nどんなプレゼントが\n欲しいですか～？", 1, 124001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6765, 4, 0, 3, "わわ、大人な香り…\nロマンチックな\nクリスマスが\n過ごせそうです♪", 1, 124001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6766, 5, 0, 3, "これでカラダを温めて\nアカリの温もり…\nお兄ちゃんに\nおすそ分けしよっと♪", 1, 124001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6768, 7, 0, 3, "お腹がパンパンです…\nでもこれで\nちょっとサンタさんに\n近づけたかも…？", 1, 124001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6769, 8, 0, 3, "クリスマスイブの\n恋人との過ごし方…？\nはわわ、アカリには\nまだ早いかもです…！", 1, 124001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6771, 14, 0, 3, "わっ、すごいすごい！\n不思議な絵本ですね♪\nこの本でお兄ちゃんに\nイタズラしちゃお♪", 1, 124001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6772, 15, 0, 3, "アカリ、ホカホカに\nなっちゃいました～♪\n…ぎゅってしても\nいいですよ？", 1, 124001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6773, 1, 0, 1, "またエンジェルになる\nかもとは思ってたけど\n…サンタさん要素の\n追加は想定外だったわ", 1, 124101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6774, 2, 0, 1, "ラフィたちがいない\nときは私がなんとか\nしないとね\n一応…エンジェルだし", 1, 124101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6775, 3, 0, 1, "部屋の飾りつけとか\nするのも…あり？\nふふ、浮かれすぎかな", 1, 124101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6776, 4, 0, 1, "パーティーだったら\nみんなで大人数向けの\nボードゲームもできる\nかも…やったっ", 1, 124101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6777, 5, 0, 1, "この服のモコモコ…\n気づいたら触っちゃう\n…やるわね、ラフィ", 1, 124101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6778, 101, 0, 1, "アイツと一緒にいると\n人見知りとは違う\n緊張があるのよね…\nって今の聞いてたっ？", 4, 124101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6779, 102, 0, 1, "ぬいぐるみ…\nいじめてないわよね？\n…大事にしてるって？\nそれはそれで…あうぅ", 8, 124101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6780, 1, 0, 2, "いい子と悪い子って\nどう見分けるのかしら\n…まさか素直さとか？\nだったら困る！", 1, 124101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6781, 2, 0, 2, "プレゼントといえば\nやっぱりゲーム！\n年末商戦サイコー♪", 1, 124101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6782, 3, 0, 2, "サンタさんはお仕事を\n見られちゃ\nいけないのよね？\n…天職？", 1, 124101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6783, 4, 0, 2, "この服は汚れ対策も\nバッチリなのよね\n煙突に入れるように\n…いや私はNGよ！？", 1, 124101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6784, 101, 0, 2, "アンタのお願い…\n今なら一つくらい\n聞いてあげるわよ？\nほら、サンタ衣装だし", 4, 124101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6785, 102, 0, 2, "私たちって\nなぜかぬいぐるみと\n縁があるわよね\n…ふふ", 8, 124101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6786, 1, 0, 2, "お、おはよっ\n…ふぅ、寝ぐせを\n直すのが間に合って\nよかったぁ", 1, 124101, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6787, 2, 0, 2, "え…一緒にごはん？\nそ、そう…うん。OK\nまあ、せっかくだし？", 1, 124101, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6788, 3, 0, 2, "この時間のゲームって\n妙に楽しいのよね\n…ということで、\nちょっと遊ばない？", 1, 124101, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6790, 2, 0, 3, "ああ～ダメになるぅ…\nもうここでずっと\nゲームしてた～い", 1, 124101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6791, 3, 0, 3, "プレゼントのために\n研究は必要よね、うん\n…はぁ、かわいい～♪", 1, 124101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6792, 4, 0, 3, "集中力を高める香りも\nあるのよね…\n勝負をするときには\n持ち歩こうかしら", 1, 124101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6793, 5, 0, 3, "なんだかまったり\nしちゃうわね…\n時間が溶けるわ…", 1, 124101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6795, 7, 0, 3, "お腹いっぱい\n食べられるのも\n幸せなことよね", 1, 124101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6796, 8, 0, 3, "ちっちゃいときは\nアカリと一緒に絵本を\n読んだなぁ…懐かしい", 1, 124101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6798, 14, 0, 3, "飛び出す絵本…\nこれでゲームブックを\n作ったらおもしろく\nなる気がするわ…", 1, 124101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6799, 15, 0, 3, "あったまる…\nモフモフ…ふわふわ…\nちょっと休憩しても…\nいいよね…？", 1, 124101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6800, 103, 0, 1, "あら、どうしたの？\nもしかして、私と\n新しい実験を\n始めたくなった？", 12, 105101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6801, 103, 0, 2, "調子が悪いときは\n診療所に来て\n君は特別に、じっくり\n診察してあげるわ♪", 12, 105101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6802, 1, 0, 1, "特別じゃなくても\nまったり過ごす\nクリスマスも\nいいものだね", 1, 124201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6803, 2, 0, 1, "ニットの帽子に\nマフラーに耳当て…\n素顔用の防寒対策を\n整えなければ", 1, 124201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6804, 3, 0, 1, "靴下が\n見当たらなかったから\n枕元にはかわりに\n鎧を置いておこう", 1, 124201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6805, 4, 0, 1, "聖女なんて言われる程\n素晴らしくはないけど\n心は聖くいたいものだ\n日々精進だね", 1, 124201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6806, 5, 0, 1, "兜がないから\nイルミネーションが\nいつもよりまぶしく\n感じるよ", 1, 124201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6807, 101, 0, 1, "皆の幸福を\n祈願して\nクリスマスリースを\n飾ろうか", 4, 124201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6808, 102, 0, 1, "私にはかわいすぎる\n衣装だが…開き直って\nクリスマス気分を\n楽しんでしまおう", 8, 124201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6809, 1, 0, 2, "ツリーの飾りつけ\n手伝ってくれるかい？\nセンスは少年に\nお任せするよ", 1, 124201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6810, 2, 0, 2, "パーティーは\n用意してる時も楽しい\nねえ少年、この気持ち\n分かってくれるかい？", 1, 124201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6811, 3, 0, 2, "なんでトナカイの鼻は\n赤いのかって？\n無灯ソリ運転は違反で\n取り締まられるからだ", 1, 124201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6812, 4, 0, 2, "トモちゃんや\nマツリちゃんは\nいい子だから、きっと\nサンタさんに会えるね", 1, 124201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6813, 101, 0, 2, "ヒイラギの髪飾り\n実は気にいってるんだ\n少年にも\n褒めてもらいたいな", 4, 124201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6814, 102, 0, 2, "ねえ少年…そろそろ\n見慣れてほしい\nそんなにまじまじ\n見つめられると照れる", 8, 124201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6815, 1, 0, 2, "朝日に照らされて\n街がキラキラ白銀に\n輝いてるよ", 1, 124201, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6816, 2, 0, 2, "お昼ご飯を食べて\nちょっと散歩しようか\n祝祭に沸く街は\nとてもきれいだよ", 1, 124201, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6817, 3, 0, 2, "やっぱりクリスマスは\n夜が本番って感じだね\nキャンドルを灯して\n厳かに過ごそうか", 1, 124201, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6819, 2, 0, 3, "鎧姿では無縁だったが\nふかふかしていて\n気持ちいいな…\nふかふか…ふかふか…", 1, 124201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6820, 3, 0, 3, "ふふ、かわいいね\n抱きしめると\nあの雪の日に産まれた\n赤ちゃんを思い出す", 1, 124201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6821, 4, 0, 3, "兜を脱いだおかげで\nこの香りに出会えた\n…クリスマスの魔法も\n悪くないかもね", 1, 124201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6822, 5, 0, 3, "指先から体の芯まで\n暖まるね\nこれなら雪の日でも\n安心だよ", 1, 124201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6824, 7, 0, 3, "もぐ、もぐ…\nあまり食べるところ\n見ないでほしい", 1, 124201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6825, 8, 0, 3, "素敵なレストランの\n特集記事が載ってる\n聖夜に予約が取れたら\n最高に幸せだろうね", 1, 124201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6827, 14, 0, 3, "わっ！\n…………いや\n驚いてなどいない", 1, 124201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6828, 15, 0, 3, "ふふ…頬に直接\nふかふかを感じられる\n温かくて気持ちいいよ", 1, 124201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6829, 1, 0, 1, "ピアノに読書…\nグレアと一緒に\nなにをしようかな～♪", 1, 124301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6830, 2, 0, 1, "元の世界へ戻った時\n授業に遅れないよう\nグレアと勉強しとこう", 1, 124301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6831, 3, 0, 1, "街に魔物料理屋さんが\nあるんだ！　グレアと\n食べに行ってみよう♪", 1, 124301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6832, 4, 0, 1, "せっかく新しい魔法を\n覚えたし、グレアにも\n見てもらいたいな！", 1, 124301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6833, 5, 0, 1, "グレアも誘って\nランドソルをもっと\n探索したい！", 1, 124301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6834, 101, 0, 1, "私たちも\nあの人を見習って\nバイトとかしよう！", 4, 124301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6835, 102, 0, 1, "いつかあの人を\n私たちの学院に\n招待したいな！", 8, 124301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6836, 1, 0, 2, "あのね！\nこれからグレアと\n魔法愛好会に\n行くんだ！", 1, 124301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6837, 2, 0, 2, "これからなにするの？\n私たちもついて行って\nいいかな！？", 1, 124301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6838, 3, 0, 2, "グレアと一緒に\nあなたも魔法の練習\nしてみない？", 1, 124301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6839, 4, 0, 2, "グレアとよく話すの\nマナリアの制服\nあなたに着せたいって", 1, 124301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6840, 101, 0, 2, "グレアのそばって\n温かいんだ～！\nぬくぬくだよ～♪", 4, 124301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6841, 102, 0, 2, "あなたは\nグレアと私の友達！\nこれからもずっと！", 8, 124301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6842, 1, 0, 2, "おはよう！\n今朝はグレアと一緒に\n外を散歩してきたよ！", 1, 124301, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6843, 2, 0, 2, "お腹空いたな…\nよし、グレアと一緒に\n魔物料理を作ろう！", 1, 124301, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6844, 3, 0, 2, "もうこんな時間だね\n明日のために\nそろそろ寝ようかな！", 1, 124301, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6846, 2, 0, 3, "はぁ～この感触…\nグレアの尻尾みたいで\n安らぐ～", 1, 124301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6847, 3, 0, 3, "ぬいぐるみって\nいいよね…私もよく\nグレアに抱き付くんだ", 1, 124301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6848, 4, 0, 3, "いい香り～…\nグレアもこういうの\n好きなんだよ！", 1, 124301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6849, 5, 0, 3, "あったかいね～…\nグレアにくっついてる\n時を思い出すよ～…", 1, 124301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6851, 7, 0, 3, "この世界にしかない\nおいしいものが\nたくさんあるよね！", 1, 124301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6852, 8, 0, 3, "世界を繋ぐ魔法の\nヒント…本に書いて\nないかなぁ…", 1, 124301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6855, 1, 0, 1, "ピアノに読書…\n今日のアンは\nなにをするのかな…", 1, 124401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6856, 2, 0, 1, "私はアンほど\n勉強はできないから\nちゃんと自習しないと", 1, 124401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6857, 3, 0, 1, "一度始めたら\nとことん追求するのが\nアンなんだよね…", 1, 124401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6858, 4, 0, 1, "ふふ…夢中になってる\nアンを眺めてるだけで\n一日があっという間…", 1, 124401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6859, 5, 0, 1, "ランドソルは広いね…\nアンと一緒にいろんな\n場所へ行ってみたい", 1, 124401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6860, 101, 0, 1, "アンやあの人の笑顔を\n見てると、すごく\n安心するんだ…", 4, 124401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6861, 102, 0, 1, "もしあの人が私たちの\n学院に編入したら…\nふふ、楽しそうだね", 8, 124401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6862, 1, 0, 2, "アンもあなたも\nすごいな…\nみんなとすぐに\n仲良くなっちゃうから", 1, 124401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6863, 2, 0, 2, "あなたはこれから\nなにをするの？\nその…アンと一緒に\nついていってもいい？", 1, 124401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6864, 3, 0, 2, "アンから魔法の練習に\n誘われた？　実は私も\n…一緒にどうかな？", 1, 124401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6865, 4, 0, 2, "あなたがマナリアに\n編入したら\n楽しいのになって\nアンとよく話すんだ…", 1, 124401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6866, 101, 0, 2, "こっちの世界にも\nアンのように\n私を受け入れてくれる\n人たちがいるんだね…", 4, 124401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6867, 102, 0, 2, "元の世界へ帰る日が\n来たとしても私たちは\n「さよなら」より\n「またね」がいいよね", 8, 124401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6868, 1, 0, 2, "おはよう。さっき\nランドソルの朝日を\nアンと見てきたんだ", 1, 124401, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6869, 2, 0, 2, "私たちは\nこれからなんだけど\nあなたはお昼ごはん\n何を食べたの？", 1, 124401, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6870, 3, 0, 2, "そろそろ\nアンが眠る時間だ…\nあなたもおやすみ", 1, 124401, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6872, 2, 0, 3, "はぁ、疲れが取れる…\nこの尻尾…\nちょっと重いんだよね", 1, 124401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6873, 3, 0, 3, "これ…\nアンに見せたら\n喜ぶだろうな…", 1, 124401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6874, 4, 0, 3, "いい香り…\nアンの感想も\n聞いてみたいな", 1, 124401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6875, 5, 0, 3, "あったかい…\nアンがここにいたら\n本を読みながら\n寝ちゃうんだろうな", 1, 124401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6877, 7, 0, 3, "おいしいからたくさん\n食べれちゃうけど\nまた尻尾…\n大きくなっちゃうかな", 1, 124401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6878, 8, 0, 3, "アンがおススメ\nしてくれた本は\nこれかな…？", 1, 124401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6881, 1, 0, 1, "凶、凶、凶…大凶？\nなによこのおみくじ\n自宅用だからって\n排出率が適当すぎよ…", 1, 124701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6882, 2, 0, 1, "今年こそ最高の新薬が\nもはや偶然に近い\n最小限の手間で\n作れますように", 1, 124701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6883, 3, 0, 1, "喉に詰まる前に\n溶けてなくなるお餅…\n最優先の研究課題だわ", 1, 124701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6884, 4, 0, 1, "よし、完成♪\nミツキ特製…\n「新解釈おせち料理」\n医学的根拠が満載よ", 1, 124701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6885, 5, 0, 1, "お年玉を彼に…\nあげるべき…？\n色んな意味で微妙ね…", 1, 124701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6886, 101, 0, 1, "ふふ、今年もまた一年\n彼のバイタルを\n記録できるのね♪", 4, 124701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6887, 102, 0, 1, "今年こそ\n私の薬で彼を魔改造…\nううん、大人の男に！", 8, 124701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6888, 1, 0, 2, "コマ回しのコマを\n見てたら目が回った？\nそう…手当は不要よ…", 1, 124701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6889, 2, 0, 2, "私の顔で\n福笑いを作った？\nあら…隻眼まで\n再現度が高いわね♪", 1, 124701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6890, 3, 0, 2, "門松よし、しめ縄よし\nこんなギルドでもね\n一応縁起を担ぐのよ", 1, 124701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6891, 4, 0, 2, "本気の薬草を煮込んだ\n本気の七草粥よ\n色は気にしちゃダメ", 1, 124701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6892, 101, 0, 2, "君が今年も健康で\nつつがなく…実験台を\n務められますように", 4, 124701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6893, 102, 0, 2, "いくつ新年を迎えても\n俗世の垢にまみれない\nそんな君こそが…\n私には最高のお薬よ", 8, 124701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6894, 1, 0, 2, "お正月なんだから\nいつまでも寝てないで\nさぁ、実験開始よ♪", 1, 124701, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6895, 2, 0, 2, "カルタも双六もしたし\nあとは実験くらいしか\nやることがないわね", 1, 124701, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6896, 3, 0, 2, "日頃からたいがい\n意味深な夢を見るけど\n初夢はまた別かしら…", 1, 124701, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6898, 2, 0, 3, "さすがにこの格好で\n寝正月を敢行するのは\nためらわれるわね", 1, 124701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6899, 3, 0, 3, "や、闇医者たる者\nこんな愛くるしい物に\n心奪われるわけには…", 1, 124701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6900, 4, 0, 3, "お雑煮のいい香り…\n食欲が増進されるわ\nアロマも立派なお薬ね", 1, 124701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6901, 5, 0, 3, "胸元は閉じないわよ？\nでも時々ここで\nこっそり暖まるの…", 1, 124701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6903, 7, 0, 3, "無から有が生まれてる\n新年早々物理の法則を\n無視してくるのね…", 1, 124701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6904, 8, 0, 3, "どれもこれも読んだわ\n新しい婚活本の\n入荷はまだかしら…", 1, 124701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6906, 1, 0, 1, "彼が一生懸命\n書いてくれた年賀状\n気持ちがこもっていて\nすごくうれしいわ♪", 1, 124601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6907, 2, 0, 1, "しっかり丁寧に\n大掃除したから\nとってもいい気持ちで\n新年を迎えられたわ♪", 1, 124601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6908, 3, 0, 1, "彼の着物姿\nきっと凛々しいわよね\n想像しただけで\n笑顔になるわ～♪", 1, 124601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6909, 4, 0, 1, "お雑煮は地域によって\n違いがあるけれど\n愛情が隠し味なのは\nどこも一緒よね♪", 1, 124601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6910, 5, 0, 1, "彼と凧揚げして\n遊びたいけど\n森では引っかかって\nできないかしら…？", 1, 124601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6911, 101, 0, 1, "今年も彼が素直に\n成長できるように\nたくさんいい子いい子\nしてあげましょう…♪", 4, 124601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6912, 102, 0, 1, "絵馬に彼と\n書いた通り\n今年もみんなが笑って\n過ごせますように", 8, 124601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6913, 1, 0, 2, "先生と初詣に\n行きましょう〜♪\n迷子にならないように\n手を繋いで、ね？", 1, 124601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6914, 2, 0, 2, "初売りセールいいわね\nでも気を付けないと\nあれこれ欲しくなって\n買い過ぎちゃうかも…", 1, 124601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6915, 3, 0, 2, "風邪をひかないように\n温かくするんですよ～\nえ？　先生、温めて？\nふふ、甘えん坊さんね", 1, 124601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6916, 4, 0, 2, "あなたのために\nちょっと豪華なおせち\n用意したのよ\nお口に合うかしら？", 1, 124601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6917, 101, 0, 2, "あなたも着物着たい？\nふふ、貫禄出ちゃって\n先生ドキドキ\nしちゃうかも…♪", 4, 124601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6918, 102, 0, 2, "あなたの成長は\nとってもうれしいけど\n今年もまだ、あなたの\n先生でいたいな…♪", 8, 124601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6919, 1, 0, 2, "あら、起きたの？\nお雑煮できてるから\nちゃんとお顔\n洗ってらっしゃいね", 1, 124601, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6920, 2, 0, 2, "お昼ご飯を\nしっかり食べて\nみんなでかるた大会\nしましょう〜♪", 1, 124601, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6921, 3, 0, 2, "は～い、良い子は\nもう寝る時間ですよ\n素敵な初夢が\n見られますように♪", 1, 124601, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6923, 2, 0, 3, "このフカフカちゃんも\n天日干ししたいけど…\nその前にちょっと\n休憩しちゃおうかしら", 1, 124601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6924, 3, 0, 3, "うふふ、かわいい～\nアオイちゃんや\nハツネちゃんも\nきっと気に入るわね♪", 1, 124601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6925, 4, 0, 3, "お正月は空気も\n透き通ってるから\nいつも以上に\n素敵な香りに感じるわ", 1, 124601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6926, 5, 0, 3, "火のぬくもりに\n抱かれていると\n不思議と眠気が…\nふああ…", 1, 124601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6928, 7, 0, 3, "愛情のこもった料理は\nおいしいだけじゃなく\n心も満たされちゃう♪", 1, 124601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6929, 8, 0, 3, "お正月のお作法も\nたくさんあるのね～\n先生も日々勉強だわ～", 1, 124601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6931, 103, 0, 1, "少年への感謝をどんな\n形で伝えるべきか…\n言葉よりも物の方が\n良いのだろうか？", 12, 104701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6932, 103, 0, 2, "話し相手になって\nもらってもいいかな？\n少年と一緒にいると\nとても楽しいんだ", 12, 104701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6933, 1, 0, 1, "お正月だからって\nギャングのボスは\n油断できないよ～？", 1, 124501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6934, 2, 0, 1, "イノリちゃんがお年玉\n欲しいんだって♪\n悪い子だったし\n10ルピでいいかな☆", 1, 124501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6935, 3, 0, 1, "お正月だからお雑煮を\n作ったんだけど\nカヤちゃんには\n大好評だったよ♪", 1, 124501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6936, 4, 0, 1, "ラーゴがお餅を\n食べたがってるけど…\nこの子にあげるのは\nちょっと心配かな～？", 1, 124501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6937, 5, 0, 1, "お正月は晴れ着の子が\nやっぱり多いよね～\n今度、私の漫画でも\n出してみようかな？", 1, 124501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6938, 101, 0, 1, "君と二人っきりで\n神殿に初詣…\n行ってみたいな～♪", 4, 124501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6939, 102, 0, 1, "君とお正月を\n過ごしていると\n面倒なことも\n全部忘れられそう…", 8, 124501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6940, 1, 0, 2, "この着物、どう思う？\n似合ってたら\nうれしいけどな～♪", 1, 124501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6941, 2, 0, 2, "袖を引っ張ったり\nしないでね？\n着崩れしちゃうから♪", 1, 124501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6942, 3, 0, 2, "お正月ならではの\n漫画ネタってある？\n参考にしたいんだよね", 1, 124501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6943, 4, 0, 2, "昔は凧あげとか\n駒回しとかしてたけど\n大人になるとそういう\n機会も減るよね…", 1, 124501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6944, 101, 0, 2, "君にもお年玉を\nあげようか？\n好きな額を言ってね☆", 4, 124501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6945, 102, 0, 2, "来年も、再来年も…\nこうしてみんなで\n新年がお祝いできると\n素敵だね～♪", 8, 124501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6946, 1, 0, 2, "おはよ～♪\n朝ごはんは\n焼いたお餅とか\nどうかな？", 1, 124501, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6947, 2, 0, 2, "お昼だね～\nおせちを用意したから\nみんなで食べよっか", 1, 124501, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6948, 3, 0, 2, "もうこんな時間だね～\n一日中着物だったから\n肩が凝っちゃったよ", 1, 124501, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6950, 2, 0, 3, "このまま寝たら\nしわになるかな？\n着替えた方がいい？", 1, 124501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6951, 3, 0, 3, "こういうのって\nラーゴのおもちゃに\nちょうどいいかも♪", 1, 124501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6952, 4, 0, 3, "いい香りだけど\n着物に移らないように\n気をつけないとね", 1, 124501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6953, 5, 0, 3, "ちょうどいい\n火加減だね♪\nぽかぽかするよ～♪", 1, 124501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6955, 7, 0, 3, "着物の時は\nほどほどにしないと…\n欲張ったら、お腹が\n苦しくなるからね～", 1, 124501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6956, 8, 0, 3, "ふーん\nイノリちゃんは最近\nこんな漫画を\n読んでるんだ～♪", 1, 124501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6958, 1, 0, 1, "楽園の気配は…ナシ！\nみんな前を向いてるね\nそうこなくっちゃ！", 1, 124801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6959, 2, 0, 1, "レッスンの成果も\nこの格好くらい\n目に見えたら\n分かりやすいのになぁ", 1, 124801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6960, 3, 0, 1, "次の曲はワイルドで\n悪い感じもいいなぁ…\nカヤとムイミちゃん\nみたいなイメージで！", 1, 124801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6961, 4, 0, 1, "私…服、着てるよね？\nうんうん、着てる！\nあんまり体が軽いと\n急に焦る瞬間あるな～", 1, 124801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6962, 5, 0, 1, "最近ファンの年齢層が\n幅広くなってきたかも\nこれって私…国民的\nアイドルに近づいた？", 1, 124801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6963, 101, 0, 1, "パティスリーとコラボ\nしたらどんな感じかな\n…ノゾミンマカロン♪\nとか？　…いけるかも", 4, 124801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6964, 102, 0, 1, "彼と一緒にいると…\nうれしくって苦しいな\nでも両方諦められない\n…アイドルらしいや", 8, 124801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6965, 1, 0, 2, "この格好、チカと\nツムギも似合うと\n思うんだけどな～\nキミもそう思うよね？", 1, 124801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6966, 2, 0, 2, "街を歩いてたらね？\nライブとか写真集の\n告知イベントだって\n思われちゃった！", 1, 124801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6967, 3, 0, 2, "私が持ってた剣に\n結んであったリボン…\n何か気づいてくれた？\nヒントは…色だよ♪", 1, 124801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6968, 4, 0, 2, "キミの気配ってすごく\n分かりやすいんだよ\n近くにいたらすぐに\nピンとくるんだ♪", 1, 124801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6969, 101, 0, 2, "あ、それ…はじめて\nキミにあげた音響盤！\nね、一緒に聴こうよ", 4, 124801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6970, 102, 0, 2, "ぼうっとしてたら\nイタズラしちゃうよ♪\nもっともっと私のこと\n見てないとダーメ！", 8, 124801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6971, 1, 0, 2, "朝は今日のはじまり！\n昨日よりももっと\nいい一日にしようね♪", 1, 124801, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6972, 2, 0, 2, "レッスンがんばったし\nまだお昼だし…\nランチ大盛りにしても\nセーフだよね！", 1, 124801, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6973, 3, 0, 2, "寝る前に明日の予定を\n確認しておかないと！\nえっと、集合場所は…", 1, 124801, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6975, 2, 0, 3, "一休みのつもりが\nなが～い休憩になるの\nあるあるだよね～", 1, 124801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6976, 3, 0, 3, "ノゾミンの熱烈ハグ♪\n…ここだけ聞いたら\nスクープになっちゃう\nかも？　なーんて！", 1, 124801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6977, 4, 0, 3, "香りってなんだか\n記憶に残るよねー\n…こういうの私だけ？\nみんなも同じだよね？", 1, 124801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6978, 5, 0, 3, "なんだかライブの\n熱気を思い出して\nきちゃった…！\nレッスンしよっと！", 1, 124801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6980, 7, 0, 3, "お腹が減ってたら\nいいパフォーマンスは\nできないもんね！\n…んー、おいし～♪", 1, 124801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6981, 8, 0, 3, "いい歌詞を書くには\nしっくりくる言葉を\n知っておかないと！", 1, 124801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6983, 1, 0, 1, "うだうだ悩んでる\nヤツがいたら\nオレのとこ連れてこい\n背中押してやるからさ", 1, 124901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6984, 2, 0, 1, "最近どことなく\nファミリーの連中が\n浮き足立ってんだよな\n…なんでだ？", 1, 124901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6985, 3, 0, 1, "イノリのヤツ\nやたらと脇腹\nつついてきやがって…\nくすぐったいっての", 1, 124901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6986, 4, 0, 1, "身軽になったのは\nいいが…くそっ\nどこもかしこも\nスースーする…", 1, 124901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6987, 5, 0, 1, "思い出に浸るのも\n悪くはないが\nやっぱ人間\n未来に生きないとな！", 1, 124901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6988, 101, 0, 1, "またムイミやノゾミと\nツルむのもいいな\nそこにカレもいたら\nなおいいぜ", 4, 124901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6989, 102, 0, 1, "未来のことを考えると\nなぜかいつもカレが\n頭に浮かんじまう…\nええい、やめだやめ！", 8, 124901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6990, 1, 0, 2, "ちょっくらオレと\n腕相撲しねえか？\nこの新しい力\n試させてくれ！", 1, 124901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6991, 2, 0, 2, "なあ、悪いが上着\n貸してくれないか？\nこのカッコ、割と\n肌寒いんだよ…", 1, 124901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6992, 3, 0, 2, "おっ、生まれ変わった\n喧嘩屋にご用命か？\nいいぜ、キミの\n依頼なら二つ返事だ！", 1, 124901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6993, 4, 0, 2, "なんか悩みごとが\nあるなら、なんでも\n話してくれよな\nオレとキミの仲だろ？", 1, 124901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6994, 101, 0, 2, "このカッコが\nきれいだって？\nや、やめろよ…\n照れくさいだろ…", 4, 124901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6995, 102, 0, 2, "キミがいると、なにを\nするにも退屈しないぜ\nだから今日も\n付き合ってくれよな！", 8, 124901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6996, 1, 0, 2, "おはよう！　新しい\n未来がやってきたぜ！\n一秒だって時間は\n無駄にするなよ？", 1, 124901, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6997, 2, 0, 2, "そろそろ昼飯に\nしないか？\n穴場のうまい店に\n案内してやるからさ", 1, 124901, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6998, 3, 0, 2, "夜更かしは\n未来の前借りだ\n体壊さないように\nほどほどにしとけよ？", 1, 124901, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7000, 2, 0, 3, "お、いいなこれ\nファミリーの\nアジトにも\n置いときたいぜ", 1, 124901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7001, 3, 0, 3, "べ、別にかわいいもん\nなんて、そんなに\n好きじゃないし…\n嫌いでもないけどさ", 1, 124901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7002, 4, 0, 3, "嗅いだことない\n香りだな\nなかなか気に入った！", 1, 124901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7003, 5, 0, 3, "薄着で暖を取るのって\n最高にぜいたくだよな\nへへっ、堪能させて\nもらうとするか", 1, 124901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7005, 7, 0, 3, "こいつはいいや\nこのカッコだと\n消耗が激しくて\nやたら腹が減るからな", 1, 124901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7006, 8, 0, 3, "甘辛コーデ…\nへぇ、そんなのが\nあるのか…\nわ、悪くねえかもな", 1, 124901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7008, 103, 0, 1, "はぁ…\nもっともっと長い時間\n先輩と過ごせたらなぁ", 12, 105501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7009, 103, 0, 2, "えへへ…♪\n先輩が、すぐそばに\nいてくれるなんて…\n幸せだなぁ…♪", 12, 105501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7010, 1, 0, 1, "はーっはっは！\n強くてカッコいい\n大悪党、ここに参上！", 1, 125001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7011, 2, 0, 1, "みんなおしゃれで\nカッコいいけど\n今のアタシも\n負けてないぞ！", 1, 125001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7012, 3, 0, 1, "ギャングのアジトも\nよかったけど\nここも賑やかで\n悪くないな！", 1, 125001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7013, 4, 0, 1, "悪党にも休息は必要だ\nここならゆっくり\n休めそうだ！\n邪魔するぞ！", 1, 125001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7014, 5, 0, 1, "いっぱい人がいるな…\nアタシの悪党っぷりを\n広めるチャンスだ！", 1, 125001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7015, 101, 0, 1, "ここにいれば\nいい思い出がたくさん\n作れそうだな！", 4, 125001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7016, 102, 0, 1, "いつかアタシも\nオクトーと一緒に\nこんなアジトを手に\n入れてやるからな！", 8, 125001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7017, 1, 0, 2, "ん？　アタシの格好が\n気になるのか？\nならもっと近くで\n見てもいいぞ！", 1, 125001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7018, 2, 0, 2, "おい、暇だから\nアタシと遊べ！\nカヤとノゾミも呼んで\n大騒ぎするぞ！", 1, 125001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7019, 3, 0, 2, "オマエ、前より強く\nなってないか！？\nくっそー！　アタシも\n負けてらんないぞー！", 1, 125001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7020, 4, 0, 2, "今日は特別にアタシの\n思い出話を聞かせて\nやる！　さーて、どの\n悪事から話そうか…", 1, 125001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7021, 101, 0, 2, "オマエとの付き合いも\n結構長くなったよな？\nこれからも\nよろしく頼むぞ！", 4, 125001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7022, 102, 0, 2, "オマエまでいなく\nなったらアタシが困る\n勝手にどっか\n行くんじゃないぞ！", 8, 125001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7023, 1, 0, 2, "起きろ、起きろー！\n大悪党の朝は早いんだ\nさっさと\n顔洗ってこい！", 1, 125001, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7024, 2, 0, 2, "昼飯どうしようか…\nそうだ！　オマエの\nおススメに連れてけ！\nいい店期待してるぞ！", 1, 125001, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7025, 3, 0, 2, "明日を思い切り\n楽しむためにも\n早く寝とけよ！\nまた明日な！", 1, 125001, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7027, 2, 0, 3, "こうやって座ると…\nおお！　すっごく悪党\nっぽいぞ！", 1, 125001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7028, 3, 0, 3, "オマエ、仲間に\nなりたそうな目を\nしてるな！", 1, 125001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7029, 4, 0, 3, "うん、いい匂いだ！\n何の匂いかは全然\n分かんないけどな！", 1, 125001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7030, 5, 0, 3, "炎ってなんだか\nカッコいいよな！\n見てるだけで心が\n燃えてくるぞ！", 1, 125001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7032, 7, 0, 3, "いっぱい食べれば\nちょっとは\n背が伸びるかな…？", 1, 125001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7033, 8, 0, 3, "ぐぬぅ…たまには\n本でもと思ったけど\nやっぱりアタシは\nバトル担当だな！", 1, 125001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7035, 1, 0, 1, "お邪魔しまーす★\nなーんてもう\nすっかりくつろいで\nますけどー♪", 1, 118201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7036, 2, 0, 1, "騎士さんの\nギルドハウス…★\nなんだかドキドキ\nしちゃいます♪", 1, 118201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7037, 3, 0, 1, "ぺんぽこりんの\n置き場所はー…\nま、このへんで★", 1, 118201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7038, 4, 0, 1, "うーん、これは\n壊しがいがありそ…\nウソウソ、冗談です★", 1, 118201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7039, 5, 0, 1, "オトモダチでも\n作ってみますかー\nくすっ★", 1, 118201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7040, 101, 0, 1, "退屈ですねー\n騎士さん、構って\nくれないかなー", 4, 118201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7041, 102, 0, 1, "もっともっと早く\n騎士さんに逢えてたら\n…なんて、わたし\nらしくないですね", 8, 118201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7042, 1, 0, 2, "わたし一人にするの\n不安だったりします？\nちゃーんとイイコに\nしてますってー★", 1, 118201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7043, 2, 0, 2, "お出かけします？\nそれともおうちで\nまったりしますー？", 1, 118201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7044, 3, 0, 2, "女の子をすぐ家に\nあげちゃうなんて…\nふふ、やっぱり隅に\n置けないですねー★", 1, 118201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7045, 4, 0, 2, "騎士さん騎士さーん★\nウザ絡みしても\nいいですかー？\n騎士さんってばー★", 1, 118201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7046, 101, 0, 2, "一緒にゴロゴロ\nしませんかー？\nコイビトっぽくて\n楽しそうですし♪", 4, 118201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7047, 102, 0, 2, "うふふ★\nこーやってあなたに\n優しくされると…\nときめいちゃいます♪", 8, 118201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7048, 1, 0, 2, "おはようございます★\nあれれー？\nまだおねむですかー？", 1, 118201, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7049, 2, 0, 2, "騎士さん、お昼は\nもう食べましたー？\nわたし、ラーメンが\n食べたいでーす★", 1, 118201, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7050, 3, 0, 2, "あ、夜更かししてちゃ\nダメですよー？\nほらほら、お布団に\nごーごー★", 1, 118201, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7052, 2, 0, 3, "ふかふかのふわふわー\nついついまーったり\nしちゃいます★", 1, 118201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7053, 3, 0, 3, "かっわいー★\n名前はー、んー…\nぱんぱかぷんで★", 1, 118201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7054, 4, 0, 3, "くんくん★\nうーん…\nたぶん素敵な香り♪", 1, 118201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7055, 5, 0, 3, "あったかーい★\n心も体も\nぽっかぽかですねー★", 1, 118201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7057, 7, 0, 3, "もぐもぐ★\nんー、おいしくって\n止まらないですねー★", 1, 118201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7058, 8, 0, 3, "へー★\nほほー★\nふんふーん★", 1, 118201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7060, 1, 0, 1, "さ～て時間もあるし\nラテアートの練習でも\nするかにゃ～♪", 1, 125101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7061, 2, 0, 1, "カフェの猫たちも\n元気に働いてくれて\nお客さんからの評判も\n上々にゃ～♪", 1, 125101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7062, 3, 0, 1, "カフェのカウンターに\nファントムキャッツの\n目安箱を置こうかにゃ", 1, 125101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7063, 4, 0, 1, "あんこもコーヒーも\n原料は豆にゃ～\nそりゃ相性いいに\n決まってるにゃ♪", 1, 125101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7064, 5, 0, 1, "あいつの為に\nタマキ特製ブレンドを\n開発したいにゃ♪", 1, 125101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7065, 101, 0, 1, "あいつと働いてると\nなぜかあっという間に\n閉店時間が\n来ちゃうのにゃ～", 4, 125101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7066, 102, 0, 1, "あいつといると\n新しいアイデアが\n溢れるほど\n出てきちゃうのにゃ♪", 8, 125101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7067, 1, 0, 2, "キミがコーヒーを\n淹れてくれたのにゃ？\nにゃはは、常温で\n猫舌にも優しいにゃ♪", 1, 125101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7068, 2, 0, 2, "料理が上手くなったら\nカフェご飯メニューも\n充実させたいにゃ～", 1, 125101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7069, 3, 0, 2, "お店の経営は大変\nだけど、キミとなら\n乗り越えられるって\n確信してるにゃ！", 1, 125101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7070, 4, 0, 2, "ん？　この制服\n似合ってるにゃ？\nにゃはは！　褒めても\n１ルピも出ないにゃ♪", 1, 125101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7071, 101, 0, 2, "あたしが好きなのは\n猫に好かれる人にゃ♪\n…ん、キミ猫に懐かれ\nまくってるにゃ…", 4, 125101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7072, 102, 0, 2, "キミの制服姿\nけっこーイケてるにゃ\nあたしがお客さんなら\n通いつめちゃうにゃ♪", 8, 125101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7073, 1, 0, 2, "朝は一杯の\nコーヒーから始まる…\nにゃふふん♪\nシャレた朝だにゃ～♪", 1, 125101, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7074, 2, 0, 2, "お昼はたい焼きと\nコーヒーのセットで\nまったり休憩\nしようにゃ～♪", 1, 125101, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7075, 3, 0, 2, "コーヒー淹れたにゃ\n星空を見ながら\n二人で飲もうにゃ…♪", 1, 125101, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7077, 2, 0, 3, "ふにゃ～ん…ゴロゴロ\nコーヒーと同じくらい\n癒されるにゃ～…♪", 1, 125101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7078, 3, 0, 3, "こいつかわいいにゃ～\nうちの猫たちとも\nいい勝負なのにゃ！", 1, 125101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7079, 4, 0, 3, "ん～いい香りだにゃ～\nコーヒーもそうだけど\n香りの癒し効果は\n抜群にゃ～♪", 1, 125101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7080, 5, 0, 3, "猫に暖かいものは\nまるまれと言ってる\nようなもんにゃ…♪\nまるまる…", 1, 125101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7082, 7, 0, 3, "にゃは～うまいにゃ♪\nカフェの新メニューに\n採用しちゃおう\nかにゃ～♪", 1, 125101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7083, 8, 0, 3, "にゃるほど…\n調べれば調べるほど\nコーヒーは\n奥が深いのにゃ…", 1, 125101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7085, 103, 0, 1, "ワタシの手料理は\n坊やの口に合うらしい\n胃袋は討ち取ったぞ♪", 12, 107101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7086, 103, 0, 2, "胸に再度触れると\n誓約は無効になるのか\n…だと？　鋭い質問だ\n安心しろ。ならん☆", 12, 107101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7087, 1, 0, 1, "ようやくこの服にも\n慣れてきたかな？\nふふっ、たまには\nこういう服も悪くない", 1, 125201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7088, 2, 0, 1, "今日も店は好調だが、\n私は軍人だからな\n当初の目的も\n忘れないようにしよう", 1, 125201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7089, 3, 0, 1, "猫が近くに来ると\nついつい見てしまうな\n仕事が終わったら\n遊んでやるかな♪", 1, 125201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7090, 4, 0, 1, "コーヒーの淹れ方が\nうまくなってきたな♪\nどうせならお菓子も\n研究してみるか！", 1, 125201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7091, 5, 0, 1, "利益を考えると\n価格をあげるべきか？\n…いや、利益よりも\n皆の笑顔の方が大事だ", 1, 125201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7092, 101, 0, 1, "またあいつを\n呼んでしまったな…\n今度、コーヒーでも\n淹れて労ってやるか♪", 4, 125201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7093, 102, 0, 1, "次に入荷するお菓子は\nあいつの意見を聞くか\nどんな味が好きか\n気になるしな", 8, 125201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7094, 1, 0, 2, "なに、コーヒーを\n淹れてほしい？\nふふっ♪\n仕方のない奴だな！", 1, 125201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7095, 2, 0, 2, "…そうだ。\nコーヒーと乾パンは\n合うのではないか？\n今度試してみよう！", 1, 125201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7096, 3, 0, 2, "コーヒーもいいが、\nココアも悪くないぞ♪\n貴公も一緒にどうだ？", 1, 125201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7097, 4, 0, 2, "魔法少女に比べれば\n大人しい服だろう？\nだが、カフェには\nこの服でバッチリだ！", 1, 125201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7098, 101, 0, 2, "貴公はコーヒーを\n飲めるのかっ？それは\nうらやましいな…", 4, 125201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7099, 102, 0, 2, "貴公の働きぶりには\n目を見張るものがある\nその姿は、その…\n結構かっこいいぞっ！", 8, 125201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7100, 1, 0, 2, "さぁ、今日も一日、\n頑張るぞっ！\n気合いを入れて\n開店準備だっ！", 1, 125201, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7101, 2, 0, 2, "昼は稼ぎ時だからな！\n荒波を乗り切るため\n貴公も一緒に\n頑張ってくれるか？", 1, 125201, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7102, 3, 0, 2, "ふあぁ…もう夜か\n今日は頑張ったからな\nはやめに休むとしよう", 1, 125201, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7104, 2, 0, 3, "はふぅ～…\nたまにはこうして\nゆったりするのも\n悪くないな…", 1, 125201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7105, 3, 0, 3, "猫もかわいいが、\nこいつもなかなか…\n見ていると、\n撫でたくなるな♪", 1, 125201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7106, 4, 0, 3, "これは良い香りだな\nふむ、コーヒーも\nより香り高いものが\n作れるよう試すか！", 1, 125201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7107, 5, 0, 3, "ふぅ～…\n体を温めなければ、\nすぐに動けないからな", 1, 125201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7109, 7, 0, 3, "これもうまいが、\n私はお菓子も…\nいや、食べすぎは\n体に良くないな", 1, 125201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7110, 8, 0, 3, "…書物は参考になるな\n次はこのやり方を\n試してみるかな", 1, 125201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7112, 1, 0, 1, "愛と希望の魔法少女！\n弟くんの平和は\nお姉ちゃんが守るよ♪", 1, 120001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7113, 2, 0, 1, "魔法少女の戦い方を\n見せてあげる！\nお姉ちゃんチョップ♪", 1, 120001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7114, 3, 0, 1, "闇の力が高まってる…\n早く弟くんを\nギュ～ってしないと！", 1, 120001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7115, 4, 0, 1, "ミニ弟くんを\n部屋中に呼び出して\n弟くんハウスに～♪", 1, 120001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7116, 5, 0, 1, "決めポーズの練習でも\nしよっかな…\nはい、ポーズ♪", 1, 120001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7117, 101, 0, 1, "魔法少女の力で\n弟くんのそばに\n瞬間移動したいなぁ", 4, 120001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7118, 102, 0, 1, "禁じられた必殺技\nその名も──\n闇のお姉ちゃん頭突き", 8, 120001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7119, 1, 0, 2, "魔法少女な\nお姉ちゃんに\n興味津々だねっ？\n全部教えてあげるね♪", 1, 120001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7120, 2, 0, 2, "闇のお姉ちゃんとして\n世界を弟くんのものに\n…な～んて冗談冗談♪", 1, 120001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7121, 3, 0, 2, "弟くんって魔法少女に\nなりたがってたよね？\n…うん。応援するね！", 1, 120001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7122, 4, 0, 2, "サインが欲しいの？\nふふっ、任せて！\n愛する弟くんへ、と♪", 1, 120001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7123, 101, 0, 2, "ふんふふ～ん♪\n魔法少女お姉ちゃんの\nテーマソング完成っ♪", 4, 120001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7124, 102, 0, 2, "えへへ、弟くんからの\n愛と希望を感じるよ…\n更に変身しちゃうぞ♪", 8, 120001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7125, 1, 0, 2, "弟くんの朝を魔法で\n守りに来たよ！\n希望の朝だねっ♪", 1, 120001, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7126, 2, 0, 2, "弟くんのお昼を魔法で\n守りに来たよ！\n愛のお弁当をどうぞ♪", 1, 120001, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7127, 3, 0, 2, "弟くんの夜を魔法で\n守りに来たよ！\nここからは闇の時間♪", 1, 120001, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7129, 2, 0, 3, "魔法少女の一休み♪\n愛と希望と…闇の力が\n回復していくね～♪", 1, 120001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7130, 3, 0, 3, "かわいいかわいい♪\nお姉ちゃんの魔法で\n増やしちゃうぞ…♪", 1, 120001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7131, 4, 0, 3, "この香り…\n弟くんと同じ…\nくんくん、くんくん…", 1, 120001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7132, 5, 0, 3, "体を温めておいて…\nこの熱を、ぜ～んぶ\n弟くんに伝えるよ…！", 1, 120001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7134, 7, 0, 3, "ミニ弟くんを呼ぶには\nいっぱい食べないと…\n魔法ってお腹減るから", 1, 120001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7135, 8, 0, 3, "弟くんの平和を守る…\nそのためには\nチェックチェック…♪", 1, 120001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7137, 1, 0, 1, "この服も、最初は\n慣れなかったのですが\n今ではすっかり\n体に馴染んでおります", 1, 125301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7138, 2, 0, 1, "自然を守るためにも\nお勉強は欠かせません\n日々精進でございます", 1, 125301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7139, 3, 0, 1, "採取した薬草を\n調合しておきましょう\nいつ必要になるか\n分かりませんからね", 1, 125301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7140, 4, 0, 1, "合宿は過酷でしたが\nおかげでちょっと\n力持ちになったような\n気がいたします…♪", 1, 125301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7141, 5, 0, 1, "わんわんのほうの\n主さまに、ごはんを\nあげる時間ですね…", 1, 125301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7142, 101, 0, 1, "もっともっと訓練や\nお勉強を重ねて\n主さまのお役に\n立たなくては…", 4, 125301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7143, 102, 0, 1, "おや、そろそろ\nわんわんのほうの\n主さまをお散歩に\n連れていく時間です", 8, 125301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7144, 1, 0, 2, "主さま、よろしければ\nお散歩に行きませんか\n今日の森は穏やかで\n過ごしやすいかと", 1, 125301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7145, 2, 0, 2, "わんわんのほうの\n主さまも、今では\n【美食殿】の大切な\n仲間でございます", 1, 125301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7146, 3, 0, 2, "森には危険が\nいっぱいでございます\nケガをなさらないよう\nお気をつけください", 1, 125301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7147, 4, 0, 2, "もし森や動物のことで\n気になることがあれば\n気軽にお尋ねください", 1, 125301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7148, 101, 0, 2, "森へ行く前には\n必ず装備の点検を\nお願いいたします\n約束ですよ、主さま？", 4, 125301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7149, 102, 0, 2, "森の中で主さまたちと\nゆったり過ごす時間は\nわたくしにとって\n大切なひとときです", 8, 125301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7150, 1, 0, 2, "おはようございます\n朝ごはんをしっかり\n食べて、お勤めに\n備えましょう", 1, 125301, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7151, 2, 0, 2, "わんわんのほうの\n主さまにおやつを\nあげる時間です\nどうぞ、召し上がれ", 1, 125301, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7152, 3, 0, 2, "どれだけ野営に\n慣れたとしても\nベッドで眠れるなら\nそうしたいところです", 1, 125301, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7154, 2, 0, 3, "こちらなのですが\nわんわんのほうの\n主さまもお気に入りの\nようです", 1, 125301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7155, 3, 0, 3, "わんわんのほうの\n主さまにも、こうした\n玩具を与えるべき\nでしょうか…？", 1, 125301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7156, 4, 0, 3, "心地よい香りです\n次は、自然由来の\n香料も試しましょう", 1, 125301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7157, 5, 0, 3, "暖をとるのは\n野営の時などに\nとても大事なことで\nございますね", 1, 125301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7159, 7, 0, 3, "食べられる時に食べて\nおくのも、大自然では\n重要でございます", 1, 125301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7160, 8, 0, 3, "より知識を蓄え\n自然の力を活用できる\nようにいたしましょう", 1, 125301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7162, 103, 0, 1, "今回の「冥風戦記」も\nなかなかよく書けたぞ\nふふっ、シグルドも\n喜んでくれるかな？", 12, 100901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7163, 103, 0, 2, "貴様のためならば\n我が真なる魔力\nいつでも\n解き放とうぞ！", 12, 100901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7164, 1, 0, 1, "ニャットちゃんに\nブラッシングを\nしてあげようかな", 1, 125401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7165, 2, 0, 1, "ふふ…\nこれがレンジャーの\n制服ですか", 1, 125401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7166, 3, 0, 1, "そろそろ\nパトロールの\n時間だ…", 1, 125401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7167, 4, 0, 1, "魔物について\nもっと知りたいな\n本を探しに行こう", 1, 125401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7168, 5, 0, 1, "レンジャーになって\n体調を崩す日が\n減った気がします…", 1, 125401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7169, 101, 0, 1, "本だけじゃわからない\n世界のことを、\nもっと知りたいです…", 4, 125401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7170, 102, 0, 1, "こんな私にも\nできることはある…\nそう信じています", 8, 125401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7171, 1, 0, 2, "あの…良かったら\n一緒に見回りへ\n行きませんか？", 1, 125401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7172, 2, 0, 2, "またあなたと一緒に\nレンジャーの仕事が\nできたらうれしいです", 1, 125401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7173, 3, 0, 2, "けほっこほっ\n…だ、大丈夫です\nたまに出るだけなので", 1, 125401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7174, 4, 0, 2, "ニャットちゃん\nあなたにも\n懐いてきましたね", 1, 125401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7175, 101, 0, 2, "ニャットちゃんと\n散歩に出かけようかな\n…あの人も誘って", 4, 125401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7176, 102, 0, 2, "あなたと肩を並べて\nレンジャーの仕事が\nできてうれしいです…", 8, 125401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7177, 1, 0, 2, "おはようございます\nレンジャー・シオリ\n元気に出勤します", 1, 125401, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7178, 2, 0, 2, "ニャットちゃんにも\nお昼ごはんを\n用意しなくちゃ", 1, 125401, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7179, 3, 0, 2, "明日は仕事だから\n早起きしなくちゃ\nおやすみなさい", 1, 125401, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7181, 2, 0, 3, "ふぅ…仕事で\n走り回ったので\n少し休憩します", 1, 125401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7182, 3, 0, 3, "仕事が終わった後に\nこうして触れていると\n癒されますね…", 1, 125401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7183, 4, 0, 3, "森の香りがします…\n今日はよく\n眠れそうです…", 1, 125401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7184, 5, 0, 3, "ニャットちゃんと\n同じぐらい\n暖かいです…", 1, 125401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7186, 7, 0, 3, "レンジャーの仕事を\nしていると\nお腹が空きます", 1, 125401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7187, 8, 0, 3, "どんなに忙しくても\n読書だけは\nやめられませんね", 1, 125401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7189, 1, 0, 1, "兄様のお留守の間\nわたくしが、兄様の\n御家を守ります！", 1, 125801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7190, 2, 0, 1, "に、に、兄様の御家…\n招待いただいた以上は\n失礼のないよう\n振る舞わなくては…！", 1, 125801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7191, 3, 0, 1, "ふふっ…兄様の素敵な\nお人柄が、家中から\nにじみ出ています…♪", 1, 125801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7192, 4, 0, 1, "靴を脱がずに\n御家に上がるのに\n慣れていなくて…あ！\n飛べばいいのでは…！", 1, 125801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7193, 5, 0, 1, "ここに花を生けたら\n華やぎそうですね\n兄様はお花…\n好きでしょうか…？", 1, 125801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7194, 101, 0, 1, "ずっとここにいたい…\nなどと言ったら\n兄様を困らせて\nしまいますよね…", 4, 125801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7195, 102, 0, 1, "お料理などを作って\n兄様を出迎えられたら\nいいのに…料理の腕も\n磨かなくては…！", 8, 125801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7196, 1, 0, 2, "このような姿で\n失礼しております…！\nネ、ネズミがいたので\nつい本気を…！", 1, 125801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7197, 2, 0, 2, "おかえりなさいませ\nお忙しい御身をどうぞ\n癒してくださいね", 1, 125801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7198, 3, 0, 2, "兄様の話し相手…！？\nど、どうしましょう…\nわたくしには面白い\n話なんて…はうぅ…！", 1, 125801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7199, 4, 0, 2, "なぜか、動物などに\n好かれる体質で…\n迷い猫捜しのバイトの\nお役に立てますか？", 1, 125801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7200, 101, 0, 2, "に、兄様の家に来たら\n『添い寝』をするのが\n礼儀とリアさんが…\n本当…でしょうか？", 4, 125801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7201, 102, 0, 2, "わたくしはここに\nいて…よいのですね？\n『仲間』として…", 8, 125801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7202, 1, 0, 2, "朝は得意なんです\nもし兄様が苦手なら\nわたくしが起こして\n差し上げますよ", 1, 125801, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7203, 2, 0, 2, "兄様…昼餉を作って\nみたのですが、失敗\nしてしまいました…", 1, 125801, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7204, 3, 0, 2, "ふあぁ…\nはっ！　お…\nお恥ずかしい所を…！", 1, 125801, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7206, 2, 0, 3, "こ、これは…！\n人様の御家なのに\nつい…駄目になって…\nしまいますぅ…！", 1, 125801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7207, 3, 0, 3, "と、飛びかかっては\nこない…ですよね…？", 1, 125801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7208, 4, 0, 3, "すぅ…はぁ…\nこのかぐわしさ\n心が洗われます…", 1, 125801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7209, 5, 0, 3, "なんて暖かい…\n兄様の優しくお広い\n御心のようです…♪", 1, 125801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7211, 7, 0, 3, "こんなに食べていたら\nはしたない…\nでしょうか？", 1, 125801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7212, 8, 0, 3, "兄様の本棚はまるで\n知識の宝庫ですね\n己の世間知らずぶりを\n実感してしまいます", 1, 125801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7214, 1, 0, 1, "次の冒険に向けて\n騎空士弁当を作ろっと\nまずはおにぎりから！", 1, 125501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7215, 2, 0, 1, "魔法の修行しなくちゃ\nもっともっと頼れる\n団長になりたいから！", 1, 125501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7216, 3, 0, 1, "ビカラちゃんを\n遊びに誘いたいけど…\nどういう場所が\n好きなんだろう？", 1, 125501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7217, 4, 0, 1, "仕事を真面目に\nしっかりこなすことが\n騎空艇への\n一番の近道だよね", 1, 125501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7218, 5, 0, 1, "ここにいる人の中で\n騎空団に興味ある人\nいたりしないかな…？", 1, 125501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7219, 101, 0, 1, "これまでも想い出は\nいっぱいできたけど…\nこれからもたくさん\n想い出を作りたいな！", 4, 125501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7220, 102, 0, 1, "仲間がいるから\n騎空士は限界だって\n超えられるんだね", 8, 125501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7221, 1, 0, 2, "この衣装\n魔法使いらしいでしょ\n服に着られないよう\n頑張らなくちゃね！", 1, 125501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7222, 2, 0, 2, "あなたとなら\nどんな相手だって\n怖くなんてないよ！", 1, 125501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7223, 3, 0, 2, "カリンさんから\n新しい依頼が来たよ！\n一緒に頑張ろ！", 1, 125501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7224, 4, 0, 2, "操舵士になるのは\nずいぶん難しそう…\nいつか頼れる仲間が\n見つかるといいな", 1, 125501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7225, 101, 0, 2, "手合わせしたいの？\nいいよ、たまには\n団長らしいところ\n見せてあげるね！", 4, 125501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7226, 102, 0, 2, "まだ二人きりだけど…\nこの騎空団は\n私の宝物だよ！", 8, 125501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7227, 1, 0, 2, "おはよう～…\n昨日は遅くまで\n魔法の研究してて…\nふわぁ…眠いなぁ…", 1, 125501, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7228, 2, 0, 2, "さてと、お昼休み！\n紅茶淹れてくるから\n一緒に飲もうよ！", 1, 125501, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7229, 3, 0, 2, "遅くまでお疲れさま！\n無理しない程度に\nお互い頑張ろう！", 1, 125501, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7231, 2, 0, 3, "魔法の研究は\n一旦、休憩！\n頭を休めなくちゃ", 1, 125501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7232, 3, 0, 3, "魔法みたいに\nかわいい！\n騎空団に入る？\nなんてね！", 1, 125501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7233, 4, 0, 3, "いい匂い…\nそれになんだか\n集中力が増したかも", 1, 125501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7234, 5, 0, 3, "体だけじゃなくて\n心までポカポカする…\nなんだか魔法みたい", 1, 125501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7236, 7, 0, 3, "いろいろ考え事したら\nお腹が空いちゃった！\nいただきまーす！", 1, 125501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7237, 8, 0, 3, "『摂理の彼方へ\n至るには…』\n…この魔導書\n勉強になるなあ", 1, 125501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7239, 1, 0, 1, "こちらの服を着ると\n冒険に出かけたく\nなっちゃいますね", 1, 125701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7240, 2, 0, 1, "冒険者の心得その一\n準備はできる限り\nしっかりとこなすべし\nなんてどうでしょう？", 1, 125701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7241, 3, 0, 1, "調合に必要な素材は\nあれとこれとそれと…\n時間があるとついつい\n考えちゃいますね", 1, 125701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7242, 4, 0, 1, "せっかくですから\nより良い素材を\n自分で集めたいですね", 1, 125701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7243, 5, 0, 1, "このポーションが\nみなさんの役に立つと\nうれしいですね", 1, 125701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7244, 101, 0, 1, "冒険者の心得その二\n危なくなった時は\nまず逃げるべし！\n大事なことですね", 4, 125701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7245, 102, 0, 1, "あの人の冒険は\nいつも楽しそうで\n元気がもらえます", 8, 125701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7246, 1, 0, 2, "ぜひおすすめしたい\n依頼があるんです\nこちらをどうぞ♪", 1, 125701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7247, 2, 0, 2, "ほしいお薬があれば\n錬金術で作って\nさしあげますよ", 1, 125701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7248, 3, 0, 2, "依頼で気になる\nところがあれば\n何でもご確認ください", 1, 125701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7249, 4, 0, 2, "このギルドハウスに\n錬金術の実験設備を…\nえ？　だめですか？", 1, 125701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7250, 101, 0, 2, "ポーションをどうぞ\n大丈夫、効果は\n確認済みですので♪", 4, 125701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7251, 102, 0, 2, "次はどんな冒険に\n行かれるんですか？\nいつでもお供しますよ", 8, 125701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7252, 1, 0, 2, "今日もいい一日に\nなりそうですね\n冒険日和といった\nところでしょうか", 1, 125701, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7253, 2, 0, 2, "いい時間ですし\nランチにしませんか？\n次の冒険の\n作戦会議も兼ねて♪", 1, 125701, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7254, 3, 0, 2, "夜も遅いですし\n明日の冒険のため\n今日は早く寝ましょう", 1, 125701, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7256, 2, 0, 3, "ふう…やはり冒険は\n事務仕事と違いますね\n少し休憩を…", 1, 125701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7257, 3, 0, 3, "冒険は緊張の連続です\nかわいい物で\n心を癒しましょう", 1, 125701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7258, 4, 0, 3, "ふむ、この香りは…\n素晴らしいバランスで\n調合されていますね", 1, 125701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7259, 5, 0, 3, "この火力…\n錬金術の調合に\n使えそうですね", 1, 125701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7261, 7, 0, 3, "冒険の際は食べられる\n時に食べておく！\n…とペコリーヌさんも\nおっしゃってましたね", 1, 125701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7262, 8, 0, 3, "ふむふむ…なるほど\nこんな調合も\nあるのですね…\n奥が深いです", 1, 125701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7264, 103, 0, 1, "アイツどこ行った…？\n戦うならアタシも\n連れてけよなー！", 12, 106101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7265, 103, 0, 2, "今日も悪いヤツ退治…\nの前に腹ごしらえだ！\nなんか食べに行くぞ！", 12, 106101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7266, 1, 0, 1, "みんなー！\nギルドハウスで\nぼくと握手！", 1, 125601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7267, 2, 0, 1, "種族も年齢も違うけど\nみんな楽しそうだね！\nやっぱりここはぼくの\nゆ～とぴあさ！", 1, 125601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7268, 3, 0, 1, "ハハッ！　まるで\nパーティー会場だね！\nこれは楽しませがいが\nあるってものだよ！", 1, 125601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7269, 4, 0, 1, "やぁみんな！\nご利益満点のネズ耳は\nいかがかな？\nひとつ３ルピだよ！", 1, 125601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7270, 5, 0, 1, "ここにいれば\n遊び相手に困らないね\nかしこみかしこみ～！", 1, 125601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7271, 101, 0, 1, "どこか遊びに\n行こうかな♪\nそうだ、あの人にも\n声をかけてみよう！", 4, 125601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7272, 102, 0, 1, "あの人といると\nぼくまで楽しくなって\nくるんだよね！\n毎日パレード気分さ！", 8, 125601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7273, 1, 0, 2, "ネズ耳の在庫が\n切れたんだ！\n作るのを\n手伝ってくれるかい？", 1, 125601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7274, 2, 0, 2, "ん？　握手かい？\nいいとも！\nいつでも何度でも\nしてあげるよ！", 1, 125601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7275, 3, 0, 2, "この衣装に興味が\nあるのかい？\nきみ、なかなか\n見る目があるね！", 1, 125601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7276, 4, 0, 2, "しばらくここに厄介に\nなるつもりなんだ\nあとで街を案内\nしてくれるかい？", 1, 125601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7277, 101, 0, 2, "とっておきのチーズ\nきみと半分こだ！\nみんなには内緒だよ？", 4, 125601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7278, 102, 0, 2, "きみはいてほしい時に\nいつもそばに\nいてくれるね\n本当にかしこみだよ！", 8, 125601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7279, 1, 0, 2, "やぁ、おはよう！\n気持ちのいい朝だね\n今日も楽しくネズ耳\n売りにはげもうか！", 1, 125601, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7280, 2, 0, 2, "お昼だね！\nおいしいチーズを\n持ってきたから\nぼくと半分こだ！", 1, 125601, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7281, 3, 0, 2, "今日も一日\n楽しかったね！\nいい夢が見られること\n間違いなしさ！", 1, 125601, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7283, 2, 0, 3, "ぼくにも休息は\n必要さ！\nリラックス〜\nリラックス〜♪", 1, 125601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7284, 3, 0, 3, "ぼくのぬいぐるみも\n作って売ろうかな…\n修繕費も一気に\n集まるかもね！", 1, 125601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7285, 4, 0, 3, "いい香りだね！\nトーゴクのお香に\nよく似てるよ", 1, 125601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7286, 5, 0, 3, "はぁ…温まるねぇ…\nそうだ、この火で\nチーズでも炙って\nみようかな", 1, 125601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7288, 7, 0, 3, "うーん、この味…\nトーゴクを思い出すよ\nどれ、もう一つ\nいただこうか！", 1, 125601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7289, 8, 0, 3, "これはランドソルの\nガイド本だね！\nありがたく参考に\nさせてもらうよ！", 1, 125601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7291, 1, 0, 1, "まさか、わらわが\nこのような装束を\n纏う日がくるとはな…\n感慨深いものじゃ", 1, 126201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7292, 2, 0, 1, "わらわの美貌で\nこのギルドハウスも\nより華やかに\nなったようじゃな", 1, 126201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7293, 3, 0, 1, "つまらぬ戦いで\n晴れ着を汚しとうない\n今日は一日\nゆっくりするかの", 1, 126201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7294, 4, 0, 1, "今日はわらわが食事の\n準備をして、あやつの\n帰りを待ってやろう\n献立は何がよいかの…", 1, 126201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7295, 5, 0, 1, "ユキといったか…\n掴みどころのない\nやつじゃが、根性は\n認めてやるかの", 1, 126201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7296, 101, 0, 1, "こうして、仲間と\nのんびり過ごす時間も\n悪くないのう♪", 4, 126201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7297, 102, 0, 1, "あやつを誘って\n月を見に行こうかの\n夜が楽しみじゃ♪", 8, 126201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7298, 1, 0, 2, "いかなる衣装で\nあろうとも、わらわは\n完璧に着こなして\nしまうのじゃ♪", 1, 126201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7299, 2, 0, 2, "なんじゃ？\nわらわの姿に\n見惚れておったとな？\nす、素直でよいのじゃ", 1, 126201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7300, 3, 0, 2, "吸血鬼であるわらわに\n相応しい装束じゃろう\nあとは真っ赤な血を\n添えたいのう…", 1, 126201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7301, 4, 0, 2, "お主、疲れておるな？\nわらわが癒してやろう\nほれ、近う寄るのじゃ", 1, 126201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7302, 101, 0, 2, "お主がおるだけで\nギルドハウスが\n賑やかになるのう\nある種の才能じゃな♪", 4, 126201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7303, 102, 0, 2, "眷属よ、風呂にするか\nご飯にするか\nそれとも、わらわに…\nいや、忘れるのじゃ", 8, 126201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7304, 1, 0, 2, "朝一番、わらわに\n会いたかったとな…\n眷属よ\n褒めてつかわすぞ♪", 1, 126201, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7305, 2, 0, 2, "眷属よ、わらわが\n食事を用意したぞ\n忌々しい太陽に負けず\n頑張るのじゃ", 1, 126201, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7306, 3, 0, 2, "眠るには惜しい夜じゃ\n眷属よ、もうしばらく\nわらわの傍に\nおるがよい", 1, 126201, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7308, 2, 0, 3, "ふかふかじゃの♪\n今度あやつにも\n座らせてやろう", 1, 126201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7309, 3, 0, 3, "愛いやつじゃ\n少しばかり、わらわが\n遊んでやろうかの", 1, 126201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7310, 4, 0, 3, "気品ある香りじゃのう\nわらわの古城にも\n一つ欲しいところじゃ", 1, 126201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7311, 5, 0, 3, "よいぬくもりじゃ…\n忌々しい太陽とは\n大違いじゃの", 1, 126201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7313, 7, 0, 3, "味付けの参考に\nあと、もう一つだけ…\n止まらんのじゃ…", 1, 126201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7314, 8, 0, 3, "吸血鬼の弱点について\n記されておるのか…\nふむ、眠気が\nさめてしもうたぞ", 1, 126201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7316, 103, 0, 1, "このままボクが美しく\nなり続けても、彼なら\nきっと隣にいてくれる\nはずだよね！", 12, 100801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7317, 103, 0, 2, "もっとそばに来なよ\nボクの隣は\nキミのための\n特等席なんだから", 12, 100801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7318, 1, 0, 1, "ドレスの影響かな…\n前より黒や赤が\n好きになったかも", 1, 126301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7319, 2, 0, 1, "ボクがいるだけで\nここがぐんっと\n華やかになったね♪", 1, 126301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7320, 3, 0, 1, "ボクを描いた絵画\n今度持ってきて\n飾ってあげようかな", 1, 126301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7321, 4, 0, 1, "ボクはボクに\n魅了されっぱなしさ♪\nまさに魔性の存在だよ", 1, 126301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7322, 5, 0, 1, "美のアドバイスを\n聞きたい人いるかい？\nボクがもっともっと\n輝かせてあげるよ♪", 1, 126301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7323, 101, 0, 1, "あの人、今度はボクを\nどう褒めるだろう？\nもし語彙が尽きても\n許してあげよっと♪", 4, 126301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7324, 102, 0, 1, "あの人に似合うコーデ\n考えてあげよっと\n案外、なんでも\n着こなしそうだよね♪", 8, 126301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7325, 1, 0, 2, "吸血鬼になるなんて\nやっぱりゴメンだね！\n永遠の美よりも\n大事なことがあるのさ", 1, 126301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7326, 2, 0, 2, "お肌にいいものは\n大体好きだよ\nトマトジュースとかね", 1, 126301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7327, 3, 0, 2, "銀のアクセサリーが\nボクは大好きでね\nいつでもプレゼント\n待ってるよ♪", 1, 126301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7328, 4, 0, 2, "イリヤみたいにお城に\n住んでみたいんだ♪\n美を統べる者に\n相応しいでしょ？", 1, 126301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7329, 101, 0, 2, "今のボクがカップを\n触ると壊しちゃうんだ\nだからほら、キミが\nボクに紅茶を飲ませて", 4, 126301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7330, 102, 0, 2, "キミも美を磨けば\nもっと輝けるはずさ！\nボクがメイクとか\nしてあげようか？", 8, 126301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7331, 1, 0, 2, "んん、まだ眠い…\n美のためにも、\nもうひと眠りするよ…", 1, 126301, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7332, 2, 0, 2, "ボクにぴったりな\n日傘を買ったんだ♪\n散歩にでも行こうよ！", 1, 126301, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7333, 3, 0, 2, "夜こそボクの時間！\nさぁ、あまねく人々へ\nボクの美を広めよう！", 1, 126301, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7335, 2, 0, 3, "リラックスは大事だよ\n心の余裕こそが\n美の秘訣だからね♪", 1, 126301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7336, 3, 0, 3, "キミもボクの\n美の虜にしてあげる♪\nうれしいでしょ？", 1, 126301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7337, 4, 0, 3, "人々を魅了するほどの\n魔性の香り…\nボクにふさわしいね♪", 1, 126301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7338, 5, 0, 3, "冷えは美の大敵さ\nボクのように\n美しくなりたいなら\n体を温めるべきだよ", 1, 126301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7340, 7, 0, 3, "食事をするボクの\nなまめかしさ…♪\n吸血鬼すらも\nドキッとするだろうね", 1, 126301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7341, 8, 0, 3, "吸血鬼って\n美しい存在だよね…\nボクほどではないけど", 1, 126301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7343, 1, 0, 1, "ここがナイトさんの\nお家なんですねーっ！\n雰囲気そっくりっ！", 1, 126401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7344, 2, 0, 1, "ドアも壁もお菓子じゃ\nないんですよねっ？\n不思議ですねーっ♪", 1, 126401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7345, 3, 0, 1, "失礼のないように\n気をつけないとっ！\nライラエルさまとも\n約束してますからっ！", 1, 126401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7346, 4, 0, 1, "向こうに帰るころには\nお土産話がいっぱい\nできてるといいな～♪", 1, 126401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7347, 5, 0, 1, "戦闘用のアームの他に\n観光用のアームも\nあったらいいのにな～", 1, 126401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7348, 101, 0, 1, "ナイトさんの充填と\nライラエルさまの充填\nどっちも違って\nどっちも大好きです♪", 4, 126401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7349, 102, 0, 1, "…ナイトさんが\nマスターになって\nくれたら…えへへっ\nなんでもないですっ！", 8, 126401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7350, 1, 0, 2, "お手伝いだったら\n任せてくださいっ！\nお家の移転とか\nしちゃいますかっ？", 1, 126401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7351, 2, 0, 2, "こっちに来るときは\nポポおじいさんが\nしっかりメンテして\nくれるんですよっ♪", 1, 126401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7352, 3, 0, 2, "お家の中にいるのも\nいいですけど、お外も\n気になります～っ！\nお出かけしましょう♪", 1, 126401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7353, 4, 0, 2, "ヴォルガーノさんに\nそっくりの子猫さんを\n見かけたんですっ！\nご親戚ですかねっ？", 1, 126401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7354, 101, 0, 2, "ここだけの話ですよ？\n…実はケーブルって\n触られると、ちょっと\nくすぐったいんですっ", 4, 126401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7355, 102, 0, 2, "このお家の持ち主は\nナイトさんですよね？\nそれじゃここでは\nご主人さまですねっ", 8, 126401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7356, 1, 0, 2, "おはようございますっ\n今日もフルパワーで\nがんばりましょうっ♪", 1, 126401, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7357, 2, 0, 2, "お昼の時間ですねっ！\nランチの充填、\nお願いしまーすっ♪", 1, 126401, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7358, 3, 0, 2, "そろそろ眠らなくて\n大丈夫ですかっ？\n夜ふかしさんなら\nお付き合いしますよ♪", 1, 126401, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7360, 2, 0, 3, "エネルギーの消費を\n抑えるのも大切\nですよね～っ", 1, 126401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7361, 3, 0, 3, "わたしが触っても\n平気なんて…！\nかわいくて頑丈っ！\n好きですっ！", 1, 126401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7362, 4, 0, 3, "センサーによると…\nこの香りはいい香りに\n該当するみたいですっ\nなるほどーっ！", 1, 126401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7363, 5, 0, 3, "あったかいのって\nいいですよねっ！\nみなさんうれしそう\nですしっ♪", 1, 126401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7365, 7, 0, 3, "これがこっちの世界の\nごはんなんですねっ\nおいしい気がします！", 1, 126401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7366, 8, 0, 3, "そーっとそーっと…\n本を破かないように…\n書いてある内容より\n難しいです～っ！", 1, 126401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7368, 1, 0, 1, "ウィスタリア家の\nプライベートビーチも\nご案内しますわね♪", 1, 126701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7369, 2, 0, 1, "世界を巡る\nリゾートクルーズも\nご用意できますわよ", 1, 126701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7370, 3, 0, 1, "避暑地に専用の\n別荘がありますの\n今度ご招待しますわね", 1, 126701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7371, 4, 0, 1, "日焼けは乙女の\n天敵ですわね\n備えはしっかりと…", 1, 126701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7372, 5, 0, 1, "線香花火だけでは\n物足りませんわね\nもっとドカーンと…", 1, 126701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7373, 101, 0, 1, "ギルド活動も全力で！\nバカンスも全力で！\nそれが私ですわ！", 4, 126701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7374, 102, 0, 1, "私のセンスで選んだ\n特注の水着ですもの\n似合って当然ですわ♪", 8, 126701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7375, 1, 0, 2, "ご希望ならすぐにでも\n夏の飛空艇遊覧ツアー\nへご招待しますわ♪", 1, 126701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7376, 2, 0, 2, "夏の賑やかな\n雰囲気は好きですが…\n暑さには参りますわね", 1, 126701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7377, 3, 0, 2, "日の光を浴びるほどに\n輝きを増す夏の私！\n今日も絶好調ですわ♪", 1, 126701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7378, 4, 0, 2, "夏祭りに協力するなら\n資金と人材、どちらも\n用意しないとですわね", 1, 126701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7379, 101, 0, 2, "あなたさま…その…\nそうも見つめられると\n照れてしまいます…", 4, 126701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7380, 102, 0, 2, "タマキさんの働く\n海の家も訪れて\nみたいですわね♪", 8, 126701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7381, 1, 0, 2, "港の市場が\n賑わう頃ですわね！\n私たちも参りますわよ", 1, 126701, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7382, 2, 0, 2, "冷たい飲み物でも\nご一緒にいかが？\nうちのシェフに用意\nさせてありますの", 1, 126701, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7383, 3, 0, 2, "まだもう少し…\nこの夏の夜を\nあなたさまと…", 1, 126701, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7385, 2, 0, 3, "あなたさまも\n良ければこちらへ\nいらっしゃいな♪", 1, 126701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7386, 3, 0, 3, "夏に合わせた装いに\n替えてみるのも\n楽しそうですわね♪", 1, 126701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7387, 4, 0, 3, "この夏めいた爽やかな\n香り…期間限定で\n売り出したいですわ", 1, 126701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7388, 5, 0, 3, "く…我慢です…\nこの暑さに耐えてこそ\n美しさにも磨きが…", 1, 126701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7390, 7, 0, 3, "暑い季節に働く\nみなさまへ差し入れも\n必要ですわね", 1, 126701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7391, 8, 0, 3, "夏のトレンドを\n押さえておくのも\n大事ですわね", 1, 126701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7393, 10, 0, 3, "上手くいけば\nヒット商品になる\nポテンシャルを\n感じますわね…！", 1, 126701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7394, 11, 0, 3, "この極上の肌触り…\n言い値で買いますわ\n一体おいくらですの？", 1, 126701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7395, 1, 0, 1, "せっかくのバカンス…\n自由に羽を\n伸ばすとしましょう", 1, 126601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7396, 2, 0, 1, "たとえどのような\n秘密を持とうとも\n私には筒抜けですよ", 1, 126601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7397, 3, 0, 1, "子供だましの怪談に\n関心はありませんが…\n霊には興味があります\n観察対象として、ね", 1, 126601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7398, 4, 0, 1, "彼の絵日記…\nあとでゆっくりと\n読ませてもらいます", 1, 126601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7399, 5, 0, 1, "セミの鳴き声に入道雲\nそしてクリームソーダ\nこれでこそ夏ですね", 1, 126601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7400, 101, 0, 1, "夏休みの宿題でも\n作るとしましょうか\n彼の限界を少し超えた\n負荷を与えるには…", 4, 126601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7401, 102, 0, 1, "彼と過ごす夏を\n特別なものにしたい…\nそれが私の望みです", 8, 126601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7402, 1, 0, 2, "夏だからといって\n浮かれすぎないように\n節度を持って\n楽しみなさい？", 1, 126601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7403, 2, 0, 2, "ああ見えてもマサキは\n私のプリンセスナイト\nあまり侮られては\n困りますよ？", 1, 126601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7404, 3, 0, 2, "私の秘密ですか？\nまんじゅうが怖い…\nというのはどうです？", 1, 126601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7405, 4, 0, 2, "ここは誰かの秘密を\n盗み聞くのに\n最適な場所ですね\n…ジョークですよ", 1, 126601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7406, 101, 0, 2, "一緒に風鈴の音を\nのんびり楽しもう…？\n…別に構いませんよ\n貴方とならば", 4, 126601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7407, 102, 0, 2, "貴方に約束しましょう\n決して忘れられない\n夏にしてあげますよ", 8, 126601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7408, 1, 0, 2, "早起きとは感心ですね\nでは一緒に\n体操でもしましょうか", 1, 126601, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7409, 2, 0, 2, "昼食は流しそうめん？\n非効率的ですね\n…食べないとは\n一言も言っていません", 1, 126601, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7410, 3, 0, 2, "…貴方が望むならば\nいくらでも怖い話を\nしてあげますが…\nいかがですか？", 1, 126601, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7412, 2, 0, 3, "七冠にも休息は\n必要なのです\nしばらくここで\n休ませてもらいますよ", 1, 126601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7413, 3, 0, 3, "なかなか気に入った…\nというのは\n誰にも秘密です", 1, 126601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7414, 4, 0, 3, "薫風を思わせる\nこのさわやかな香り…\n悪くないセンスですね", 1, 126601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7415, 5, 0, 3, "夏だからこそ\n冷え対策は\n重要なのです\n…とはいえ暑いですね", 1, 126601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7417, 7, 0, 3, "私がどのような味が\n好きなのかは…\n当然秘密です", 1, 126601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7418, 8, 0, 3, "夏を舞台にした\n小説は数多くあります\n彼が好きそうな本を\n何冊か見繕いましょう", 1, 126601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7420, 10, 0, 3, "見た目も手触りも\n完璧に覚えました\nいつでも模倣できます", 1, 126601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7421, 11, 0, 3, "ふむ…夏らしい\nクッションですね\nナイトプールに\n映えそうです", 1, 126601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7422, 103, 0, 1, "レイ様も格好良いけど\n騎士さんもなかなか…\nって、今の聞こえて\nませんよねっ？", 12, 105401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7423, 103, 0, 2, "騎士さんはシンプルな\n服が似合いそうです！\n今度、一緒に\n見に行きましょう♪", 12, 105401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7424, 1, 0, 1, "最初はどうかな～って\n思ったけど…\n水着姿でも案外\n問題ないのよね…", 1, 126801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7425, 2, 0, 1, "水着で飲みすぎると\nお腹が冷える、って\n言うけど…麦しゅわ\nなら問題ないわね！", 1, 126801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7426, 3, 0, 1, "暑ければ暑いほど\nその後の麦しゅわは\nおいしくなるのよ…", 1, 126801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7427, 4, 0, 1, "しっかりと仕事を\nこなしてから、\nしっかりと麦しゅわ！\nやっぱりこれね！", 1, 126801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7428, 5, 0, 1, "ふふっ、デザインも\n着心地も最高ね\n奮発して用意して\n正解だったわ♪", 1, 126801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7429, 101, 0, 1, "伝説の麦しゅわ、\n飲めるものなら\nまた飲みたいわね…", 4, 126801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7430, 102, 0, 1, "二つ名に恥じない\n聖騎士として\n私…飲むわ！", 8, 126801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7431, 1, 0, 2, "この格好なら水に\n濡れても大丈夫！\nなんだけど、さすがに\n書類は濡らせないわね", 1, 126801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7432, 2, 0, 2, "そうやってじっと\n見られると…ちょっと\n恥ずかしいわ…", 1, 126801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7433, 3, 0, 2, "今日もいい天気ね\nふふっ、あとで\nビーチに遊びに\n行きましょ♪", 1, 126801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7434, 4, 0, 2, "水着姿も慣れると\n案外快適ね\nそうだ、キミも\n水着になっちゃう？", 1, 126801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7435, 101, 0, 2, "例え水着姿であろうと\n聖騎士としての誇りは\n忘れてないわ！", 4, 126801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7436, 102, 0, 2, "キミとだったら\n麦しゅわなしでも\n最高の夏のバカンスに\nなると思うわ♪", 8, 126801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7437, 1, 0, 2, "仕事終わりの\n麦しゅわのために\n今日も一日\n頑張るわよ♪", 1, 126801, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7438, 2, 0, 2, "今日のお昼は\nどうしようかしら…\nさ、さすがに昼からは\n自重してるわよ？", 1, 126801, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7439, 3, 0, 2, "さ、一日の疲れを\n洗い流すために…\n軽～く飲むわよ！", 1, 126801, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7441, 2, 0, 3, "こうやって\n直に触れると\n肌触りの良さが\nよく分かるわ♪", 1, 126801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7442, 3, 0, 3, "今日もかわいいわね～\nどう？　今度\nお姉さんの一人飲みに\n付き合わない？", 1, 126801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7443, 4, 0, 3, "いい香りね～…はっ！\n麦しゅわの匂いの\nアロマポットなんて\nあったらよくない？", 1, 126801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7444, 5, 0, 3, "こんな格好で火に\n当たってると焙られる\nスルメの気分が\nちょっと分かるわ…", 1, 126801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7446, 7, 0, 3, "もぐもぐ…うん、\n結構おつまみにも\nむいてそうね～♪", 1, 126801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7447, 8, 0, 3, "試験に使った参考書、\nたまには読み返して\nおかないとね", 1, 126801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7449, 10, 0, 3, "ふふ♪\nいい手触りね～", 1, 126801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7450, 11, 0, 3, "…はふう～…\nゴロゴロするのって\n最高ね～", 1, 126801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7451, 1, 0, 1, "ん…やっぱり\nこの水着…\n大胆、なんじゃ…？", 1, 126901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7452, 2, 0, 1, "海でもお世話って、\nできるのかしら…？\n浮き輪代わりになる、\nとか…？", 1, 126901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7453, 3, 0, 1, "～♪\nふふ…海に行く\n準備も、楽しい…かも", 1, 126901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7454, 4, 0, 1, "あうぅ…水着で\n歩いてると…お尻…\nすぐ食い込んじゃう…", 1, 126901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7455, 5, 0, 1, "海では、はしゃいだ\nほうがいいのよね…\nわ、わー、い…♪\n…こうかしら？", 1, 126901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7456, 101, 0, 1, "あなたとなら…\n楽しい夏の思い出、\n作れる…気がするの…", 4, 126901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7457, 102, 0, 1, "また来年も…\n再来年も…\nあなたと海へ\n行けますように…", 8, 126901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7458, 1, 0, 2, "水着の着方を\n練習してるの…\n後ろ、変じゃない…？", 1, 126901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7459, 2, 0, 2, "海に行く支度…\n進んでるかしら…？\nお手伝い、する…？", 1, 126901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7460, 3, 0, 2, "ひ、日除けの準備は\n万全よ…\nずっと太陽の下に\nいたら…溶けちゃう…", 1, 126901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7461, 4, 0, 2, "泳ぐ練習…\nこ、ここでするのは\n…さすがに変？", 1, 126901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7462, 101, 0, 2, "人混みに入るのは\nまだ不安で…\nでも、あなたといれば\nきっと…大丈夫…", 4, 126901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7463, 102, 0, 2, "あなたのおかげで\n海の楽しいところを\nたくさん知ったわ…\nありがとう…♪", 8, 126901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7464, 1, 0, 2, "お出かけの日の朝は\n早い、のよね…？\nだから、ずっと\n起きていたわ…", 1, 126901, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7465, 2, 0, 2, "今頃ビーチの日差しは\n強いんでしょうね…\nそ、想像しただけで…\nめまいが…", 1, 126901, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7466, 3, 0, 2, "ビーチの夜といえば\n花火って、聞いたわ…\nバン、って言わない\n花火…ある…？", 1, 126901, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7468, 2, 0, 3, "素肌にふわふわが…\n気持ちいい…♪", 1, 126901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7469, 3, 0, 3, "うふふ…\n一緒に海…行く？", 1, 126901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7470, 4, 0, 3, "ふぅ…♪\n気分まで…\n晴れやかになるわ…", 1, 126901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7471, 5, 0, 3, "暑いような…\n寒いような…\n不思議な気持ちに\nなるわ…ね？", 1, 126901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7473, 7, 0, 3, "泳いだ後には\nいっぱい食べたほうが\n体によさそう、ね…", 1, 126901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7474, 8, 0, 3, "ビーチの日陰で\n読書するのも…\n気持ちよさそう…♪", 1, 126901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7476, 10, 0, 3, "これで合ってる…？\n遊び方わからなくて…", 1, 126901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7477, 11, 0, 3, "ふわふわ…♪\n海の底にいるみたい…", 1, 126901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7478, 1, 0, 1, "恥ずかしい格好で\n室内を歩くこの快感\nクセになりそうです…", 1, 127001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7479, 2, 0, 1, "この水着…\nどうしてかポーズを\n決めたくなりますね…", 1, 127001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7480, 3, 0, 1, "海にはどんな生き物が\nいるのでしょう～\n胸が高鳴ります～♪", 1, 127001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7481, 4, 0, 1, "夏は暑いですね…\nクウカの体が熱いのも\n夏のせい…ぐふふ…♪", 1, 127001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7482, 5, 0, 1, "ドＳさんと無人島に\n取り残されて…という\n妄想はかなりイイです", 1, 127001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7483, 101, 0, 1, "ドＳさんの\n欲望に満ちた眼差し…\nクウカ、受け止めます", 4, 127001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7484, 102, 0, 1, "海に行くのが\n楽しみです…\nドＳさんと…ぐふふ♪", 8, 127001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7485, 1, 0, 2, "「もっと過激な水着を\n見せてみろよ」なんて\nいけませんドＳさん…", 1, 127001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7486, 2, 0, 2, "水着のままで\n踊ればいいのですね…\nドＳさんの望みなら！", 1, 127001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7487, 3, 0, 2, "ドＳさんの熱い視線を\n感じます…じゅるり…\nもっと見てください…", 1, 127001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7488, 4, 0, 2, "こんな格好のクウカを\n放っておくなんて…\nさすがですドＳさん！", 1, 127001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7489, 101, 0, 2, "海で水着といえば\n何かが起きる予感…！\nドＳさんの新たな攻め\n期待していますね…！", 4, 127001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7490, 102, 0, 2, "恥ずかしいですけど\n頑張っちゃいました\nドＳさん…こういう\n水着、好きですか？", 8, 127001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7491, 1, 0, 2, "こんな朝からクウカを\n水着に着替えさせて…\n本当にドＳさんは\n欲望に正直ですね！", 1, 127001, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7492, 2, 0, 2, "こんな昼間から\nドＳさんがヌルヌルの\n液体をクウカに～！\nえっ…日焼け止め？", 1, 127001, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7493, 3, 0, 2, "ドＳさん…これから\nクウカと夜の海を\n見に行きませんか？\nきっと綺麗ですよ…♪", 1, 127001, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7495, 2, 0, 3, "ここなら妄想に\n集中できます～♪\nじゅるり～♪", 1, 127001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7496, 3, 0, 3, "こうもかわいいと\n妄想に登場させるのも\n気が引けます…", 1, 127001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7497, 4, 0, 3, "クウカを昂らせる\nこの香り…！\nは！　ドＳさんの罠！", 1, 127001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7498, 5, 0, 3, "体を熱くさせて\n水着を脱がそうと！？\nテクニカルです…！", 1, 127001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7500, 7, 0, 3, "クウカの口の中に\nたくさん…♪\n止まりません…！", 1, 127001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7501, 8, 0, 3, "読書は心が穏やかに\nなりますね…！\nぐふふ…穏やかに…", 1, 127001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7503, 10, 0, 3, "いい乗り心地です…♪\nあっ…普通に楽しんで\nしまいました…", 1, 127001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7504, 11, 0, 3, "これは…\n妄想に役立ちそうな\n感触です…！", 1, 127001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7505, 103, 0, 1, "成長した自分に\nあわせて、もっと\nかっこいいポーズを\n研究するッス！", 12, 100501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7506, 103, 0, 2, "これからも一緒に\n最高のヒーローを\n目指していくッスよ♪", 12, 100501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7507, 1, 0, 1, "お父さん、娘の\n水着姿がそんなに\nうれしいの？", 1, 127101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7508, 2, 0, 1, "怖い話を子どもたちに\n聞かせたら泣かれて\nしまいました…\n加減が難しいです…", 1, 127101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7509, 3, 0, 1, "海に行くと父が\n水着の女性ばかり\n追いかけて困ります…", 1, 127101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7510, 4, 0, 1, "拾った貝殻で\n新しい占いでも\nはじめてみようかな", 1, 127101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7511, 5, 0, 1, "貝殻を耳に当てると\n波の音が…あっ\nこれはダメです\nよくない声がします", 1, 127101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7512, 101, 0, 1, "この飲み物、スーッと\n体に沁み込んでくる…\nあの人にも\n教えてあげたいです", 4, 127101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7513, 102, 0, 1, "強い日差しも騒がしい\n海も苦手なのに\nあの人となら不思議と\n楽しく思えるんです", 8, 127101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7514, 1, 0, 2, "夏限定、水と氷を\n使った特別な占いは\nいかがですか？", 1, 127101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7515, 2, 0, 2, "暑い日々にはやはり\n怪談ですね\n厳選フルコースで\nお聞かせします", 1, 127101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7516, 3, 0, 2, "寒気がする？　それは\n霊ではなくかき氷の\n食べ過ぎですよ", 1, 127101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7517, 4, 0, 2, "今日の占い、あなたの\nラッキーパーソンは…\n白い水着の女性です", 1, 127101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7518, 101, 0, 2, "あの、よければ\n日焼け止めを塗って\nもらえませんか？\n背中は届かなくて…", 4, 127101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7519, 102, 0, 2, "海に行きませんか？\n二人きりで静かな\n砂浜を散歩したいです", 8, 127101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7520, 1, 0, 2, "潮騒だけが響く朝の海\n心が落ち着きます\nこれから一緒に散歩に\n行ってみませんか？", 1, 127101, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7521, 2, 0, 2, "不思議です。どうして\n普通のご飯でも、海で\n食べると特別おいしく\n感じるのでしょう…", 1, 127101, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7522, 3, 0, 2, "夜の海に行きませんか\n今なら夜光虫が\n見られるかも\nしれませんよ", 1, 127101, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7524, 2, 0, 3, "夏は知らぬ間に体力を\n消耗しています\n適度に休息して\nいきましょう", 1, 127101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7525, 3, 0, 3, "このぬいぐるみ…\n大丈夫です\n何も憑いては\nいませんよ", 1, 127101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7526, 4, 0, 3, "ほのかにお線香\nみたいな香り…\nなんだか\n落ち着きます", 1, 127101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7527, 5, 0, 3, "人魂、鬼火\nセントエルモの火…\n炎を見ていると色々と\n連想してしまいます", 1, 127101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7529, 7, 0, 3, "お腹を壊さぬよう\n食い合わせには\nご注意ください", 1, 127101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7530, 8, 0, 3, "これはいい\n怪談集ですね…\nつい読みふけって\nしまいます", 1, 127101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7532, 10, 0, 3, "ふふ、こういうのに\n乗るとなんだか\n子どもに戻った\nみたいですね", 1, 127101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7533, 11, 0, 3, "こ、これは…\nちょっとメルヘン\n過ぎて恥ずかしい\nですね…", 1, 127101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7534, 1, 0, 1, "本当に素敵なお家…♪\n騎士たちの人柄を\n感じられるようで…\n不思議と落ち着きます", 1, 126501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7535, 2, 0, 1, "調度品をつい間食…\nという習慣がこちらの\n世界にはないのですね\n…気をつけないと", 1, 126501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7536, 3, 0, 1, "手土産のお菓子は\nどちらに置けば\nよいでしょうか？", 1, 126501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7537, 4, 0, 1, "まるでおとぎ話の中に\n入ってしまったかの\nよう…別世界とは\nよく言ったものです", 1, 126501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7538, 5, 0, 1, "お忍び…ふふっ♪\nときめく響きです\nなんだかお茶目で…\n密やかな印象が", 1, 126501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7539, 101, 0, 1, "ふわ…\nああ、ついあくびが…\n居心地がよすぎるのも\n困り物ですね…ふふ♪", 4, 126501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7540, 102, 0, 1, "騎士には不思議な力が\nあるだけでなく\n何者かの加護が\nあるようですね…", 8, 126501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7541, 1, 0, 2, "この『王家の装備』は\n少し気難しいのです\nわたし以外が触れると\nその…ビリビリっと", 1, 126501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7542, 2, 0, 2, "子どもたちに新しい\n遊びを教わりました♪\nにーらめっこしましょ\nあっぷっ…ぷ～っ！", 1, 126501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7543, 3, 0, 2, "実は憧れていたのです\n友人の家を訪ねると\nいう経験に…ふふっ", 1, 126501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7544, 4, 0, 2, "ぎるどはうす…\nぎるど活動の拠点だと\n管理協会？　の方に\nうかがいました", 1, 126501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7545, 101, 0, 2, "あなたは運命を\n信じますか？\nわたしは…信じます\nよい運命だけですが♪", 4, 126501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7546, 102, 0, 2, "もしわたしが本当に\n天使だったとしたら…\nどうされますか？\n…ふふ、冗談です♪", 8, 126501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7547, 1, 0, 2, "パンケーキの香りで\n目覚める朝は\nとても良いものですよ\nぜひともお試しを♪", 1, 126501, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7548, 2, 0, 2, "ひと休みの時間ですね\n食事にお昼寝…\nすべきことを行って\n英気を養いましょう", 1, 126501, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7549, 3, 0, 2, "そろそろ休まれては？\n眠れないようであれば\n寝物語か子守歌でも\nふふ、遠慮は無用です", 1, 126501, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7551, 2, 0, 3, "すう………はっ！\n眠ってはいませんよ？\nいくら居心地が良い\nからといって…ふわぁ", 1, 126501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7552, 3, 0, 3, "まあ、あいらしい…♪\nぜひわたしの寝室に\n招待させてください", 1, 126501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7553, 4, 0, 3, "なんて、かぐわしい…\nオーブンからただよう\n焼き菓子の香りとは\nまた異なる魅力が…", 1, 126501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7554, 5, 0, 3, "みなで暖をとりながら\n互いに語り笑い合う\n…心休まる時間ですね", 1, 126501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7556, 7, 0, 3, "食は心と体に\nゆとりを生みます\n…辛い食べ物の場合？\nそれは、その…", 1, 126501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7557, 8, 0, 3, "こちらの世界の知識を\n蓄えておかなければ\nテーブルマナーに\nついてのページは…", 1, 126501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7559, 1, 0, 1, "新しい色の蛍光ペン\n買ったのよね\nこれで勉強が\n楽しくなるわ～♪", 1, 127201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7560, 2, 0, 1, "学食のランチ、結構\nおいしいのよね～\n今度ペコリーヌに\n教えてやろうかしら？", 1, 127201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7561, 3, 0, 1, "ふふふ…扉に黒板消し\n仕掛けてるの…あいつ\n引っかかるかしら？", 1, 127201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7562, 4, 0, 1, "部活するなら運動部も\n悪くないかもね～\n夕日に向かって\n突っ走ってやるわ！", 1, 127201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7563, 5, 0, 1, "毎日窮屈な制服着て\n学生も大変ね～\n…ま、かわいいけど", 1, 127201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7564, 101, 0, 1, "あいつに貸したノート\n変な落書きとか\nしてなかったよね…？\n…その、相合傘とか…", 4, 127201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7565, 102, 0, 1, "昨日の家庭科で\n作ったクッキー…\nあいつにどうやって\n渡そうかな…？", 8, 127201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7566, 1, 0, 2, "宿題教えてって？\nしょうがないわね～\nじゃあ一緒に\nやりましょ", 1, 127201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7567, 2, 0, 2, "あ、明日あたしたち\n日直当番よね？\nめんどい仕事は\nあんたに任せるわ～", 1, 127201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7568, 3, 0, 2, "寮生活は楽しいけど\n就寝時間が早すぎよね\nいきなり消灯されると\nビックリするわ", 1, 127201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7569, 4, 0, 2, "校則で禁止だけど、\nにけつでほうき通学\nとか青春っぽくない？", 1, 127201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7570, 101, 0, 2, "制服って好きな人とも\n自然にお揃いになれて\nいいわよね…って別に\nあんたとって訳じゃ…", 4, 127201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7571, 102, 0, 2, "あんたネクタイ\n曲がってるわよ～？\nほら直したげるから\nこっち向いて？", 8, 127201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7572, 1, 0, 2, "おはよ～！\n今日も元気に登校よ！\n忘れ物ないようにね！", 1, 127201, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7573, 2, 0, 2, "午前の授業疲れたわ～\nしっかりお昼を食べて\n午後も頑張るわよ！", 1, 127201, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7574, 3, 0, 2, "うう…学校の怪談\n思い出しちゃった…\n…別に怖くないけど！", 1, 127201, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7576, 2, 0, 3, "ふかふか～\n…気持ち良すぎて\n制服のまま\n寝ちゃいそうね…", 1, 127201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7577, 3, 0, 3, "かわいくて癒されるわ\n寮に持ち込んでも\n大丈夫かしら…？", 1, 127201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7578, 4, 0, 3, "集中力の高まる香りで\n勉強の効率爆上がりね\n次のテストで\nいい点取るわよ～！", 1, 127201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7579, 5, 0, 3, "うう、ちょっと冷えて\n来たわね…\nスカートの下に\nジャージ穿こうかしら", 1, 127201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7581, 7, 0, 3, "もぐっ…もぐっ…\n食べとかないと教室で\nお腹なっちゃうわ～", 1, 127201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7582, 8, 0, 3, "図書室で借りた本\n明日返却日なのよね…\n今日中に読むわよ！", 1, 127201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7584, 103, 0, 1, "あいつに背中を\n預けるのもいい気分だ\nいつかは、全部…", 12, 104301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7585, 103, 0, 2, "な、なんだよ？\nじっと見るんじゃねぇ\n尻尾が揺れちまうだろ", 12, 104301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7586, 1, 0, 1, "魔法の練習！\nむむむ～…\nそれっ…！", 1, 127301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7587, 2, 0, 1, "そだ！　これ着て\nルー学行こっかな？　\n制服自慢しちゃお♪", 1, 127301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7588, 3, 0, 1, "赤点回避の魔法とか\nあったら最高じゃね？\n誰かうちに教えて～", 1, 127301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7589, 4, 0, 1, "制服が一番映える\n場所ってどこだろ？\n教室？　遊園地？", 1, 127301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7590, 5, 0, 1, "ベンキョーつらたん…\n今度キャルっちに\n助けてもらお", 1, 127301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7591, 101, 0, 1, "部活か…\nヒデサイと一緒に\nやったら楽し～かも♪", 4, 127301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7592, 102, 0, 1, "男子の制服\nもにカワだった…\n今度ヒデサイに\n借りよっかな？", 8, 127301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7593, 1, 0, 2, "ちょすー、うちの\nさいきょー魔法で\nテンアゲしてあげる～", 1, 127301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7594, 2, 0, 2, "九九って呪文ぽくね？\nほら、例えばヒチシ…\nヒチシ、なんだっけ？", 1, 127301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7595, 3, 0, 2, "ヒデサイセンセー\nうちにベンキョー\n教えて！", 1, 127301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7596, 4, 0, 2, "学割マジ助かるー\nヒデサイ、一緒に\nどんどん使ってこー♪", 1, 127301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7597, 101, 0, 2, "今度ヒデサイに\nお弁当作ったげる\n屋上で一緒に食べよ♪", 4, 127301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7598, 102, 0, 2, "ヒデサイのバイブス\n上がる言葉教えてよ\nつらたんな時、うちが\nいっぱい言ったげるよ", 8, 127301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7599, 1, 0, 2, "ちょすちょす～\n今日も朝から\nバイブス十分♪", 1, 127301, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7600, 2, 0, 2, "お昼どうしよっかな\nたまにはガッツリ\nいっちゃう系？", 1, 127301, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7601, 3, 0, 2, "やべ、宿題まだ\nできてないし…\nヒデサイ助けて～", 1, 127301, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7603, 2, 0, 3, "充電ちう～\n…ガッコーのイスも\nこれにして～", 1, 127301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7604, 3, 0, 3, "かわいいし、\n癒されるし、キュン死\nしちゃうじゃん…！", 1, 127301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7605, 4, 0, 3, "リフレッシュの\n魔法発見！\nいい香り～♪", 1, 127301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7606, 5, 0, 3, "ぽかぽかサイコー♪\n今日はもーずーっと\nここにいたいかも～", 1, 127301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7608, 7, 0, 3, "超おいし～♪\n学食にあったら\n毎日リピする～", 1, 127301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7609, 8, 0, 3, "ふむふむ…\nねむみパない…\n続きはまた今度～", 1, 127301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7611, 1, 0, 1, "今度は子どもたちも\nサラサリアに連れて\n行ってあげたいわ", 1, 127501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7612, 2, 0, 1, "子どもたちから\nお姫さまみたいって\n言われちゃった♪", 1, 127501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7613, 3, 0, 1, "この剣…火を点けたり\nするのに便利なのよ\nコントロールも効くし", 1, 127501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7614, 4, 0, 1, "あのサラサリアの\nカラっとした暑さが\nちょっと恋しいわ…", 1, 127501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7615, 5, 0, 1, "買ってきたお土産、\nお世話になってる\nみんなに配りましょ♪", 1, 127501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7616, 101, 0, 1, "あんたと一緒なら\nどこの国でだって\n暮らしていけそうね♪", 4, 127501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7617, 102, 0, 1, "今の事業を大きくして\nあんたと世界中の国を\n飛び回ったり…なんて\nすてきじゃない？", 8, 127501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7618, 1, 0, 2, "サラサリアで食べた\n料理が恋しい…？\nもう…また今度\n作ってあげるわよ", 1, 127501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7619, 2, 0, 2, "今度旅行するなら\nどこに行こうかしら？\n希望があれば教えてね", 1, 127501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7620, 3, 0, 2, "サラサリアの香辛料、\nランドソルでも\n人気が出そうよね\nいいビジネスの予感！", 1, 127501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7621, 4, 0, 2, "そんなにじっと\n見られるとさすがに\n恥ずかしいってば…", 1, 127501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7622, 101, 0, 2, "お嫁さんは憧れるけど\nお婿さんになって\nもらうのもありよね…\nあんたはどう思う？", 4, 127501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7623, 102, 0, 2, "やっぱり、あんたには\nいつもあたしの隣に\n居てほしいな…", 8, 127501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7624, 1, 0, 2, "陽射しが強くなる前に\n雑多な用事は\n片付けちゃいましょ", 1, 127501, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7625, 2, 0, 2, "サラサリアで食べた\n料理を再現してみたの\n味見してくれる？", 1, 127501, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7626, 3, 0, 2, "暑さに負けないように\n睡眠はちゃんと\n取っておかないと", 1, 127501, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7628, 2, 0, 3, "こっちでもあっちでも\n忙しいのは変わらない\nわね…ちょっと休憩～", 1, 127501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7629, 3, 0, 3, "サラサリアで見た\nぬいぐるみも\nかわいかったわね", 1, 127501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7630, 4, 0, 3, "お香って地域ごとの\n特色が出るわよね\nこの香りは…\nちょっと神秘的？", 1, 127501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7631, 5, 0, 3, "砂漠地方も夜は冷える\nのよね。温かいものが\nあると安心するわ…♪", 1, 127501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7633, 7, 0, 3, "食べ慣れた味って\nどうしてほっと\nするのかしら…", 1, 127501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7634, 8, 0, 3, "今後の取引相手に\nなるかもしれないし\n向こうの文化も\n勉強しておかないと…", 1, 127501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7636, 103, 0, 1, "犯人はこの中にいる！\n探偵として一度は\n言ってみたいセリフさ", 12, 101401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7637, 103, 0, 2, "助手くんの情報を\n仕入れておかないと…\n理想の私を、ずっと\n見ていてほしいからね", 12, 101401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7638, 1, 0, 1, "久々の長旅で\n里心がついちまったね\nしばらくランドソルで\nのんびりしようか", 1, 127601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7639, 2, 0, 1, "たまには違う服装\n違う得物を使うのも\n悪くないね", 1, 127601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7640, 3, 0, 1, "サラサリアにいた時は\n砂まみれで大丈夫かと\n思ってたけど、今じゃ\n懐かしく感じるよ", 1, 127601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7641, 4, 0, 1, "魔法の絨毯、あれは\n楽だし速いし便利\nだったねえ…土産に\n一枚欲しかったな", 1, 127601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7642, 5, 0, 1, "砂上での戦いは\nなかなかいい鍛錬に\nなったね…今度は\n修行しに行くとしよう", 1, 127601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7643, 101, 0, 1, "サラサリアの人たちも\n気のいい連中だったが\n元気にやってるかねえ", 4, 127601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7644, 102, 0, 1, "旅の恥はかき捨てって\nいうけど、悪い旅じゃ\nなかったね。次は\nアイツとどこに行こう", 8, 127601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7645, 1, 0, 2, "この格好を冷やかしに\n来たのかい？\n生憎、もうすっかり\n慣れちまったよ", 1, 127601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7646, 2, 0, 2, "ギルドの連中に\n土産をわたしに\n行くところなんだ\n付き合っておくれよ", 1, 127601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7647, 3, 0, 2, "久々に水場で\n釣りがしたいねえ…\nもちろんお前さんも\nくるんだろう？", 1, 127601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7648, 4, 0, 2, "おっと\nサラサリアの連中から\n便りが来てるよ\nさ、一緒に読もうか", 1, 127601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7649, 101, 0, 2, "最近サラサリアの踊り\nを練習してるんだ\nあとで振付けを\n見ておくれよ", 4, 127601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7650, 102, 0, 2, "なんだいお前さん？\n…なんだか若奥さん\nっぽいって？　まあ、\nそりゃあ、ねえ…", 8, 127601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7651, 1, 0, 2, "いい時間だね\n日が昇りきる前に\nやることをすませ\nとかないと", 1, 127601, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7652, 2, 0, 2, "昼はどうしようかね\nサラサリア風の\n食事でもつくろうか", 1, 127601, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7653, 3, 0, 2, "さて、早めに寝ようか\n砂漠の夜は冷えるから\n…っと、まだ旅行中の\n気分でいたよ", 1, 127601, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7655, 2, 0, 3, "良い座り心地だ\nサラサリア宮殿の\n応接間を\n思い出すねえ…", 1, 127601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7656, 3, 0, 3, "ランプの魔神に\nあげたら喜ぶかねえ？\n次に会う時までに\n見繕ってみようか", 1, 127601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7657, 4, 0, 3, "この香り…\nサラサリア土産の\n香木かい？", 1, 127601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7658, 5, 0, 3, "旅先での焚火を\n思い出すよ\nなんだかほっとするね", 1, 127601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7660, 7, 0, 3, "ふふ、慣れてる味も\nいいけど、むこうの\n味付けが懐かしく\nなっちまうね", 1, 127601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7661, 8, 0, 3, "ふむふむ…\n千夜一夜物語か\nおもしろいもんだねえ", 1, 127601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7663, 1, 0, 1, "立派なギルドハウス♪\n帰る場所が素敵だと\n冒険に出るのも\n楽しくなりそうです♪", 1, 126001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7664, 2, 0, 1, "お掃除でも…と\n思いましたけど、\nさすがお兄さんのお家\nきれいにしてますね♪", 1, 126001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7665, 3, 0, 1, "わたしたちもいつか\n立派なギルドハウスが\n持てる冒険者に\nなりたいです…♪", 1, 126001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7666, 4, 0, 1, "わたし冒険者ですけど\n家を守ってお兄さんの\n帰りを待つのも\n悪くない…かも？", 1, 126001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7667, 5, 0, 1, "お菓子焼こうかな…♪\nちょっと多めに作って\nお兄さんにも\n食べてもらおうっと♪", 1, 126001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7668, 101, 0, 1, "このお家、とっても\n大好きになりました♪\nだからその…もう少し\nいてもいいですか…？", 4, 126001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7669, 102, 0, 1, "お兄さんに\n感謝の手紙を書いて\n枕元に隠して\nおこうかな…♪", 8, 126001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7670, 1, 0, 2, "…お兄さん\nお暇なんですか？\nじゃあ一緒に冒険へ\n出かけましょう♪", 1, 126001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7671, 2, 0, 2, "お兄さんは苦手な食材\nとかありますか？\nあの、もしよければ\n今から手料理を…♪", 1, 126001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7672, 3, 0, 2, "まったり過ごす\nお家時間もいいですね\n冒険者には羽休めも\n大事ですから♪", 1, 126001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7673, 4, 0, 2, "いつでもお兄さんを\n守れるように\nこの姿でいることに\n慣れておきますね…！", 1, 126001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7674, 101, 0, 2, "お兄さん眠そうです…\nちょっとお昼寝\nしちゃいましょうか♪\nはい、膝枕…♪", 4, 126001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7675, 102, 0, 2, "お兄さん怪我を…！？\nわたしが治療を…\nふー…ふー…", 8, 126001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7676, 1, 0, 2, "おはようございます♪\n朝、元気に挨拶すれば\n幸せな一日が過ごせる\nそうですよ♪", 1, 126001, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7677, 2, 0, 2, "お昼ごはんの時間です\n…手がふさがってる？\nわかりました、じゃあ\nあ～んしますね♪", 1, 126001, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7678, 3, 0, 2, "ふわぁ…眠くなって\nきちゃいました…\nお兄さん…お泊りして\nいいですか…？", 1, 126001, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7680, 2, 0, 3, "ふにゃ～～…♪\nこんな姿、他の人には\n見せられません…♪\nもふもふ～～…♪", 1, 126001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7681, 3, 0, 3, "お兄さんこういうの\n好きなのかな～…\nふふっ、わたしも♪\nぎゅ～～～っ♪", 1, 126001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7682, 4, 0, 3, "アロマの癒し効果…\n上手くアレンジして\nみなさんの治療に\n活かせないかな～？", 1, 126001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7683, 5, 0, 3, "は～気持ちいい～♪\n身体が暖まると\n心もぽかぽかです♪", 1, 126001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7685, 7, 0, 3, "うふふ、おいしい♪\nプレシアちゃんなら\n無限にぱくぱく～って\nしちゃいそうです…♪", 1, 126001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7686, 8, 0, 3, "本はわたしを\n知らない世界に\n連れていってくれる…\nまるでお兄さんです♪", 1, 126001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7688, 103, 0, 1, "話題の新刊…\nあの人も読んでるかな\n今度会う時、感想\n聞いてみたいな…♪", 12, 103801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7689, 103, 0, 2, "うにゃっ…あ、熱を\n測ってくれたんですか\nありがとうございます\nバッチリ元気ですよ♪", 12, 103801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7690, 1, 0, 1, "お兄の、けはい…\nん、みつけた\nぎゅ", 1, 126101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7691, 2, 0, 1, "きょろきょろ\nここも\nぱとろる、するよ", 1, 126101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7692, 3, 0, 1, "おうち\nにぎやか\nさみしく、ない", 1, 126101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7693, 4, 0, 1, "きょだい、おにぎり\nきょだい、肉まん\nきょだい…お兄？", 1, 126101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7694, 5, 0, 1, "はっぴーちゃん\nどこ？\nお兄、たべた？", 1, 126101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7695, 101, 0, 1, "くんくん…この、へや\nお兄の、におい\nすんでる\nすめる？", 4, 126101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7696, 102, 0, 1, "おそうじ、にがて\nでも\nれんしゅう\nきゅっきゅ", 8, 126101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7697, 1, 0, 2, "みみ、さわり、たい？\nんー…だめ…\nむずむず\nなので", 1, 126101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7698, 2, 0, 2, "くろぷれしあ\nかっこいい？\nん…あざます", 1, 126101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7699, 3, 0, 2, "お兄の、おうち\nきょうみ、ましまし\nたんけん\nするね", 1, 126101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7700, 4, 0, 2, "なでなで、される…と\nねむ…く…\nぶぅ…ぶぅ…ぶぅ…", 1, 126101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7701, 101, 0, 2, "かみ、りぼん\nおてつだい\nよろ\nです", 4, 126101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7702, 102, 0, 2, "ここ\nすむ\nね？\nまいにち、いっしょ", 8, 126101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7703, 1, 0, 2, "ぐっもにん\nあさごはん\nあさごはん", 1, 126101, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7704, 2, 0, 2, "お兄\nおつかれ？\nれっつ、まいぺーす", 1, 126101, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7705, 3, 0, 2, "ひとりで、ねれる\nでも\nひとりじゃ、なくても\nねれる、よ…？", 1, 126101, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7707, 2, 0, 3, "ふかふか\nパパの、おなか\nみたい", 1, 126101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7708, 3, 0, 3, "こんにちは\nぷれしあ、です\nおともだち、なろ？", 1, 126101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7709, 4, 0, 3, "いい、におい\nでも、お兄\nもっと、いい、におい", 1, 126101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7710, 5, 0, 3, "とりま\nお肉、やこ", 1, 126101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7712, 7, 0, 3, "もぐもぐ…\nむげん肉まん、も\nきぼう", 1, 126101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7713, 8, 0, 3, "ぜんぶ、よんだ\nもっと、ぶあついほん\nでも、いーよ？", 1, 126101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7715, 1, 0, 1, "幸せなクリスマスを\n過ごすため\nギルドハウスを\nきれいにしなくちゃ♪", 1, 127901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7716, 2, 0, 1, "えへへ♪\n彼へのプレゼント…\n枕元にそっと\nおいておきましょう☆", 1, 127901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7717, 3, 0, 1, "クリスマスといえば\n七面鳥の丸焼き…☆\nよだれを拭いて\nいざ準備です♪", 1, 127901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7718, 4, 0, 1, "彼に用意した\nプレゼント…\n大きすぎて\nくつ下に入りません…", 1, 127901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7719, 5, 0, 1, "プレゼントは\n貰う方も、贈る方も\nどちらも幸せを\n感じますね…☆", 1, 127901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7720, 101, 0, 1, "夜景が見える\nレストランであの人と\nクリスマスデート…\n憧れちゃいますね☆", 4, 127901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7721, 102, 0, 1, "プレゼントはわたし…\n恥ずかしいですけど\nこの贈り物、彼に\n喜んでほしいです…☆", 8, 127901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7722, 1, 0, 2, "オーナメントを\nたくさん用意したので\n二人でツリーに\n飾りつけましょう☆", 1, 127901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7723, 2, 0, 2, "あはは、お腹\n空いちゃいました？\nパーティーまで\n我慢してください♪", 1, 127901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7724, 3, 0, 2, "ジングルベ～ル♪\nあなたも一緒に\nクリスマスソング\n歌っちゃいましょう♪", 1, 127901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7725, 4, 0, 2, "サプライズプレゼント\nの用意をして…って\nあなた！？　い、今の\n聞いてませんよね…？", 1, 127901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7726, 101, 0, 2, "クリスマスの夜は\nパーティーしましょう\nできれば…その…\n二人っきりで…☆", 4, 127901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7727, 102, 0, 2, "あなたと過ごした\n最高のクリスマス…\nずっとずっと心の奥に\nしまっておきますね", 8, 127901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7728, 1, 0, 2, "おはようございます！\n皆の願いが叶うような\n素敵な一日に\nしましょうね☆", 1, 127901, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7729, 2, 0, 2, "お昼ですね！\nディナーの買い出しが\n済んだら、少しお昼寝\nしちゃいましょうか☆", 1, 127901, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7730, 3, 0, 2, "そろそろ就寝の\n時間ですね～\n枕元にくつ下の準備を\nお忘れなく～☆", 1, 127901, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7732, 2, 0, 3, "ふ～気持ちいい…☆\nお家でまったり過ごす\nクリスマスも\nいいですよね…♪", 1, 127901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7733, 3, 0, 3, "かわいいですね～♪\nサンタさんからの\n贈り物でしょうか～？", 1, 127901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7734, 4, 0, 3, "すう～…はあ～…\nすごく素敵な香り…\n炊き立てのご飯と\nいい勝負です…♪", 1, 127901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7735, 5, 0, 3, "暖かいですけど…\nあの日の山小屋を\n思い出して\n恥ずかしいですね…☆", 1, 127901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7737, 7, 0, 3, "むしゃむしゃもぐもぐ\n止まりませんよ\nクリスマスですから\nむしゃむしゃもぐもぐ", 1, 127901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7738, 8, 0, 3, "物語に出てくる\nサンタさんみたいに\nわたしも幸せを\n運びたいです…☆", 1, 127901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7740, 14, 0, 3, "わっ！\nこ…これは素敵な\nサプライズプレゼント\nですね…☆", 1, 127901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7741, 15, 0, 3, "これを着ればわたしも\n立派なサンタさんに\nなれますかね…？", 1, 127901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7742, 103, 0, 1, "ここが今のあたしの\n居場所ってやつです\nふふっ、なかなか\n悪くねー気分ですよ", 12, 106601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7743, 103, 0, 2, "これからはボス代行\nとしてブイブイ\nいわせてやるです\nついてくるですよ！", 12, 106601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7744, 1, 0, 1, "陛下～♪\n貴女のクレジッタが\nおめかしして\nはせ参じましたわ～♪", 1, 128001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7745, 2, 0, 1, "パーティーとは景気の\n良さを見せつける場…\n倹約などは\nもっての外でしてよ！", 1, 128001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7746, 3, 0, 1, "この天井の高さなら\n搬入できそうですわね\n陛下用の特注ケーキ♪", 1, 128001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7747, 4, 0, 1, "…ぶぇっくしゅ！\nふう…少し背中を\n開けすぎたかしら…？", 1, 128001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7748, 5, 0, 1, "ここは秘書の目も\n届きませんから\nチクりメモの心配も\nありませんのよね～♪", 1, 128001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7749, 101, 0, 1, "いくらでも二度見して\nよろしくてよ？\n本当にわたくしだと\n心から納得できるまで", 4, 128001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7750, 102, 0, 1, "袖の下以外の贈り物も\nなかなかどうして…\n悪くないものですのね", 8, 128001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7751, 1, 0, 2, "こう見えてダンスの\nたしなみはありますの\nお相手してあげても\nよろしくってよ", 1, 128001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7752, 2, 0, 2, "ドレスはもちろん\n特注品ですわ♪\nもしや貸衣装だと\n思いまして？", 1, 128001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7753, 3, 0, 2, "バッグの中身？\n仕事に必要なものと…\n心を潤わせるための\n宝石がいくつか♪", 1, 128001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7754, 4, 0, 2, "やる気というものは\n仕事に必要な条件です\nいわばドレスコードの\nようなものですわね", 1, 128001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7755, 101, 0, 2, "全ての仕事は自分の\n血となり肉となり\nお金にもなるもの\nなのです。経験談です", 4, 128001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7756, 102, 0, 2, "今年の教訓を活かして\n来年は更に素晴らしい\nクリスマスにして\n差し上げますわ！", 8, 128001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7757, 1, 0, 2, "おはようございます\n今日という稼ぎ時の\nはじまりですわよ！", 1, 128001, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7758, 2, 0, 2, "安くて早くておいしい\nそれがランチに必須の\n栄養素ですわ！", 1, 128001, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7759, 3, 0, 2, "宵越しの仕事は\n安眠の敵！\n熟睡したいのであれば\n今が勝負時ですわよ！", 1, 128001, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7761, 2, 0, 3, "たまにはこうして\n休息を取ることも…\n…はっ！？\nドレスにシワが～っ！", 1, 128001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7762, 3, 0, 3, "…わたくしの勘が\n言っていますわ\nこの愛らしさ！\n爆売れ確・定！", 1, 128001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7763, 4, 0, 3, "いい香りですわね…\nお金の匂いには\n敵いませんけれども♪", 1, 128001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7764, 5, 0, 3, "……ハッ！？\n完全にボーッとして\nしまいましたわ…！\nぬくもり…恐ろしい！", 1, 128001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7766, 7, 0, 3, "考えませんわ…\n考えませんわよ！\nカロリーのことはっ！", 1, 128001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7767, 8, 0, 3, "ビジネス本以外の本を\n読むのも、たまには\n悪くありませんわね…", 1, 128001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7769, 14, 0, 3, "よくできていますわね\nこの絵本！\n子ども向けだと甘く\n見ていましたわ…！", 1, 128001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7770, 15, 0, 3, "この視界の狭さ…\n思い出しますわね\nティッシュ配りの\nアルバイト時代を…", 1, 128001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7771, 1, 0, 1, "ふふふ…こっそり\nスイーツを食べる\nリノちゃん見～っけ♪", 1, 128101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7772, 2, 0, 1, "お姉ちゃんの抜き打ち\nお部屋チェック～♪\n…ケーキの食べこぼし\nこれはリノちゃんだね", 1, 128101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7773, 3, 0, 1, "この衣装は目立つから\nどこにいても弟くんに\n気付いてもらえるね！", 1, 128101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7774, 4, 0, 1, "弟くん、協力して！\nリノちゃんにお勉強\nしなさいって圧を\nかけるんだよっ…♪", 1, 128101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7775, 5, 0, 1, "絆の力を感じるよ\nこれならもっと万全な\n弟くんのお世話が\nできそうだねっ！", 1, 128101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7776, 101, 0, 1, "お姉ちゃんもたまには\nリノちゃんみたいに\n甘えてみようかな…", 4, 128101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7777, 102, 0, 1, "弟くんとリノちゃん\nどっちも大切な\n私のきょうだいだよ", 8, 128101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7778, 1, 0, 2, "分かってるよ♪\nお姉ちゃんの膝枕を\nご所望だねっ？　え？\n違う？　違わないよ？", 1, 128101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7779, 2, 0, 2, "リノちゃんがね\nお姉ちゃんチョップを\n防ぐ妹白刃取りを\n訓練してるんだよね", 1, 128101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7780, 3, 0, 2, "お姉ちゃん頭突きは\n封印しないとね…\nだって、今使うと\n頭飾りで威力が…ね", 1, 128101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7781, 4, 0, 2, "ふふっ♪\n目をつぶってても\n弟くんは見えてるよ\n…そこでしょ？", 1, 128101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7782, 101, 0, 2, "お姉ちゃんも\nリノちゃんみたいに\nかわいさいっぱいに\n振舞っちゃうぞ♪", 4, 128101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7783, 102, 0, 2, "弟くんっ♪\nいつもお姉ちゃんの\n声が届く範囲にいてね\n…絶対だよ", 8, 128101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7784, 1, 0, 2, "おはよう、弟くん！\nリノちゃんが起こして\nくれたんだね♪\n…先を越されちゃった", 1, 128101, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7785, 2, 0, 2, "お姉ちゃんランチの\n時間だぞ♪\nさ、食べさせてあげる\nお口を開けて…♪", 1, 128101, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7786, 3, 0, 2, "おやすみの時間だよ\n…弟くんは真ん中ね\n左右には私たち姉妹が\nいるから安心だよ♪", 1, 128101, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7788, 2, 0, 3, "お姉ちゃんは全力で\nリラックスしてても\n弟くんの声一つで\nすぐにでも動けるよ♪", 1, 128101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7789, 3, 0, 3, "リノちゃんこういうの\n好きそうだよね\n今度プレゼントして\nみようかな？", 1, 128101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7790, 4, 0, 3, "いい匂いがするね～\nあれ？\nリノちゃんのケーキの\n甘い匂いも混ざってる", 1, 128101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7791, 5, 0, 3, "お姉ちゃんの\n弟くんへの愛も\n熱く燃えあがるね…！", 1, 128101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7793, 7, 0, 3, "たくさん食べて\n栄養を補給して\nお姉ちゃん力に\n全て変換するよっ！", 1, 128101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7794, 8, 0, 3, "ことわざ辞典…\nリノちゃんにこれを\n読ませようかな", 1, 128101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7797, 1, 0, 1, "そろ～り、そろり…\nお姉ちゃんが居ぬ間に\nスイーツを～…♪", 1, 128201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7798, 2, 0, 1, "いつか\nお姉ちゃんに似合う服\n作ってみたいですね", 1, 128201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7799, 3, 0, 1, "あいたっ！？\nひらひらしたとこ\n踏んづけちゃいました", 1, 128201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7800, 4, 0, 1, "お姉ちゃんと\nお兄ちゃんの二人から\n勉強しろって圧力が～", 1, 128201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7801, 5, 0, 1, "私の中にも秘めた力が\n眠っているはず…\nそう、妹パワーが", 1, 128201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7802, 101, 0, 1, "私もお兄ちゃんを\n甘やかしてみたいです\n…お姉ちゃんみたいに", 4, 128201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7803, 102, 0, 1, "お兄ちゃんの部屋を\nこっそり拝見～♪\nはっ！？　これは\nお姉ちゃんの視線…！", 8, 128201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7804, 1, 0, 2, "お姉ちゃん頭突きは\n大地を揺らす威力です\n私とお兄ちゃんは\n挨拶感覚で喰らいます", 1, 128201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7805, 2, 0, 2, "お姉ちゃんチョップは\n雲を裂く切れ味です\n早く妹白刃取りを\n完成させないと…", 1, 128201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7806, 3, 0, 2, "お姉ちゃんみたいに\n大人っぽく着こなす\nには一体どうしたら…", 1, 128201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7807, 4, 0, 2, "弓のデザインも\nかわいいんですよっ♪\n常に持ち歩きたいです\nけど…危ないですよね", 1, 128201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7808, 101, 0, 2, "きれいな服を着てると\nお姫さま気分に\nなれますね…あとは\n王子さまがいれば…♪", 4, 128201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7809, 102, 0, 2, "お兄ちゃんっ！\n私は褒められて伸びる\nタイプなのでもっと\nナデナデしてください", 8, 128201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7810, 1, 0, 2, "妹モーニングコール♪\nお姉ちゃんより先に\n起こしに来れました！", 1, 128201, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7811, 2, 0, 2, "ああっ、お姉ちゃん！\nお兄ちゃんにアーン\nするなんてずるいです\n私もしたいですっ！", 1, 128201, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7812, 3, 0, 2, "おやすみの時間ですね\nえへへ、きょうだいで\n並んで寝るのって\n楽しいですよね！", 1, 128201, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7814, 2, 0, 3, "スイーツを食べた後は\nお腹を休めましょう～\n平気平気…ちゃぁんと\n運動もしますからぁ…", 1, 128201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7815, 3, 0, 3, "お姉ちゃんも\nこういうかわいいもの\n好きなんですかね", 1, 128201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7816, 4, 0, 3, "くんくん…\nなんの匂いでしょうか\n新しいスイーツ？", 1, 128201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7817, 5, 0, 3, "きょうだい３人\n肩を寄せて暖まるとか\nよくないですかっ？", 1, 128201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7819, 7, 0, 3, "いつも元気な妹で\nいるためには\nよく食べるのが大事！", 1, 128201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7820, 8, 0, 3, "なんか姉に関する本が\nちらほらと混ざって…\nこれお姉ちゃんの\n仕業ですね！？", 1, 128201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7823, 1, 0, 1, "ふんふんふ～ん\nお正月で世間は\n浮かれてるですねえ\nま、いいですけど♪", 1, 128301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7824, 2, 0, 1, "は～、お正月は\nだらだらできて\n最高ですね\n…いつも通りですけど", 1, 128301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7825, 3, 0, 1, "ビッグな幹部は\nビッグなお年玉が\nもらえるはずです\n楽しみですね", 1, 128301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7826, 4, 0, 1, "今度ドラちゃんに\nお年玉を持っていって\nやりたいですね", 1, 128301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7827, 5, 0, 1, "そういえばアユアユと\n約束してたですよ…\nそろそろ出かける\nですかね", 1, 128301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7828, 101, 0, 1, "お年玉がもらえたら\n何に使うか…\n今のうちに\n考えとくです", 4, 128301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7829, 102, 0, 1, "年神の報酬の駄菓子が\nたまってきたですね…\nガキどもにでも\n配っちまうですよ", 8, 128301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7830, 1, 0, 2, "あたしのレアな\n晴れ着姿、今のうちに\nしっかり目に焼き付け\nとくがいいですよ", 1, 128301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7831, 2, 0, 2, "お兄さんは今日も\nヒマそうですねぇ…\nしょうがないから\nかまってやるですよ", 1, 128301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7832, 3, 0, 2, "そういえばカヤぴぃ\n見なかったですか？\n喧嘩屋初売りセール\nとか言ってたですけど", 1, 128301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7833, 4, 0, 2, "お正月ならではの\n大きなシノギ…\nなにかないですかね？", 1, 128301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7834, 101, 0, 2, "いいとこに来たですよ\nさっき走ったら帯が\n曲がっちゃったです\n直すの手伝うです！", 4, 128301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7835, 102, 0, 2, "年神の仕事ですか？\nこれからいくとこです\nお兄さんも一緒に\n来やがるですよ！", 8, 128301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7836, 1, 0, 2, "ふぁあ…\nどうしたですか？\nお正月に早起きなんて\n無粋ってヤツですよ…", 1, 128301, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7837, 2, 0, 2, "小腹が減ったですね\n一緒におせちでも\nつつきにいくですよ！", 1, 128301, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7838, 3, 0, 2, "夜はあたしの時間\nと言いたいとこですが\n初夢のために早く\n寝とくですかね…", 1, 128301, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7840, 2, 0, 3, "正月は食っちゃ寝\nするのが仕事…\nって聞いたこと\nあるですよ", 1, 128301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7841, 3, 0, 3, "ふふっ、晴れ着の\nあたしの次くらいには\nかわいいですね", 1, 128301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7842, 4, 0, 3, "なんかいい匂い…？\nよく知らんですけど\n晴れ着からも同じ\n匂いがしてるですね…", 1, 128301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7843, 5, 0, 3, "ふ～あったまるです…\nお正月の唯一の弱点は\n寒いことですからね…", 1, 128301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7845, 7, 0, 3, "もぐもぐ…正月太り？\nあたしには関係ない\n言葉ですね", 1, 128301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7846, 8, 0, 3, "あったあった…\nトーゴクには\nドラゴンの神宮も\nあるんですね", 1, 128301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7848, 1, 0, 1, "お正月のトラブルも\nハツネちゃんが\nめでたく解決\nしてみせるのだ～！", 1, 128401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7849, 2, 0, 1, "そうだ、新年会に\nサプライズ大作戦を\nしかけちゃおう！", 1, 128401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7850, 3, 0, 1, "イノリちゃんも\nカスミンも振袖姿が\nかわいかったな～", 1, 128401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7851, 4, 0, 1, "シオリンに\nお年玉あげたいし\n彼にバイトを紹介\nしてもらおうかな？", 1, 128401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7852, 5, 0, 1, "今度はどんな凧を\n飛ばそうかな？\nみんなが驚くような\n凧がいいよね～", 1, 128401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7853, 101, 0, 1, "ひらめいた！\n彼への年賀状は\nきらーんっ☆って\n感じにしよう！", 4, 128401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7854, 102, 0, 1, "来年も彼の隣で\n初日の出が\n見られるといいな～", 8, 128401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7855, 1, 0, 2, "今日はキミが\nギルドハウスに来る\n予知夢を見たんだよ～\nふふ、正夢になったね", 1, 128401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7856, 2, 0, 2, "この振袖どうかな？\nすっごくかわいいよね\nえへへ、ありがとう～", 1, 128401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7857, 3, 0, 2, "ハツネちゃんおみくじ\n開始！　きらーんっ☆\nキミは大吉なのだ～\nどう元気出たかな？", 1, 128401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7858, 4, 0, 2, "今度エルフの森にある\n薬草を使って七草粥を\n作ろうと思うんだ～\nぜひ食べてほしいな♪", 1, 128401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7859, 101, 0, 2, "お正月っぽい遊びを\n色々と用意したのだ～\nたまにはギルドハウス\nでゆっくり過ごそうよ", 4, 128401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7860, 102, 0, 2, "もしかして眠たいの？\n…じゃあ、また膝枕\nしてあげてもいいよ？", 8, 128401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7861, 1, 0, 2, "いっぱい寝たから\n元気いっぱいだよ～\n今日もがんばろう！", 1, 128401, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7862, 2, 0, 2, "お昼だね～\nおせちがあるから\nキミも食べるでしょ？", 1, 128401, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7863, 3, 0, 2, "そろそろ寝る時間だね\n今日はどんな夢を\n見られるかな～", 1, 128401, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7865, 2, 0, 3, "いい座り心地～\n寝正月で過ごすのも\n最高なのだ～", 1, 128401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7866, 3, 0, 3, "かわいいな～\n癒されてぐっすり\n眠れそうだよ～", 1, 128401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7867, 4, 0, 3, "いい香り～\nリラックスすると\n眠たくなっちゃうよ…", 1, 128401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7868, 5, 0, 3, "ポカポカいい気持ち～\n初日の出の温もりを\n思い出しちゃうな～♪", 1, 128401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7870, 7, 0, 3, "もぐもぐ、おいし～\nでもいっぱい食べたら\n眠くなっちゃった…", 1, 128401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7871, 8, 0, 3, "ふむふむ\nお正月料理も\n色々あるんだね～\n今度作ってみようかな", 1, 128401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7873, 103, 0, 1, "最近カレを見てたら\n何故かおいしそうに\n思えてくるんだよな…", 12, 106501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7874, 103, 0, 2, "手握っててくれるか？\nじゃないとオレ…\n力が抑えきれない…", 12, 106501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7875, 1, 0, 1, "つかの間の平和を享受\nしながらぬくぬくと\n読書に耽る…\nふふ、最高だね", 1, 128501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7876, 2, 0, 1, "お正月飾りなんて\n置いたらはしゃいでる\nように見えるかな…？", 1, 128501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7877, 3, 0, 1, "ま、まずい…\n一人遊びしすぎて\nお正月遊びをほぼ\n極めつつある…", 1, 128501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7878, 4, 0, 1, "ミステリーは\n時と場所を選ばない\nつまり…新年でも\n探偵は休まないよ！", 1, 128501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7879, 5, 0, 1, "お世話になった人に\n年賀状でも書こうかな\n助手くん、喜んで\nくれるかな…えへへっ", 1, 128501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7880, 101, 0, 1, "絵馬に願い事を書こう\n助手くんと一緒に\nいられますように…♪\n…は、恥ずかしいな", 4, 128501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7881, 102, 0, 1, "助手くんはもっと\n派手な着物のほうが\n好みだったかな…？\nき、気になる…", 8, 128501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7882, 1, 0, 2, "やあ。助手くんも\nお正月休みを\n持て余してるのかい？\nふふ、似た者同士だね", 1, 128501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7883, 2, 0, 2, "助手くん助手くん！\n凧あげしにいこう！\n…そ、捜査の一環で", 1, 128501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7884, 3, 0, 2, "着物は転びそうで\n怖いんだよなあ…\nお、押さないでね？\nフリじゃないよ！？", 1, 128501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7885, 4, 0, 2, "ひ、人目を憚る\n本を読むなら\nみんなが帰省中の今！\nはっ！　助手くん！？", 1, 128501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7886, 101, 0, 2, "ね、ねえ…助手くん…\n一緒に初詣いかない？\nあ、いや！　深い\n意味はないんだけど！", 4, 128501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7887, 102, 0, 2, "あはは…助手くんと\n一緒だと家でごろごろ\nしてるだけでも\n楽しいや…♪", 8, 128501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7888, 1, 0, 2, "ふぁ～…\nお正月休みのおかげで\n昼夜逆転中だよ…", 1, 128501, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7889, 2, 0, 2, "お正月も依頼が\nひっきりなしだね\nさあ、街へ出よう！\nもちろん君も一緒にね", 1, 128501, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7890, 3, 0, 2, "も、もっと\n近くにきてもいいよ？\n冬の夜は…寒いからね", 1, 128501, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7892, 2, 0, 3, "ううぅ～…！\nこのままじゃ\n寝正月だぁ…！", 1, 128501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7893, 3, 0, 3, "みんなが帰省中でも\nこれがあれば\n寂しくない…かな", 1, 128501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7894, 4, 0, 3, "うーん…♪\nお正月休みの\nリフレッシュに\nちょうどいいや♪", 1, 128501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7895, 5, 0, 3, "ふぅ…あったまるな\n冬は寒いから\n助かるよ…", 1, 128501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7897, 7, 0, 3, "うーん、おいしい♪\n読書のお供に\nちょうどいい", 1, 128501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7898, 8, 0, 3, "時間に余裕があると\n読書が進むよ\nいざ、本棚読破だ♪", 1, 128501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7900, 1, 0, 1, "この新しい力…\nさらに使いこなせる\nようになるには…", 1, 128801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7901, 2, 0, 1, "私に眠る属性の力…\n風の力以外には\nどんな力があるのかな", 1, 128801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7902, 3, 0, 1, "今の私なら\nどこまでいけるのか…\nやれるだけ\nやってみよう", 1, 128801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7903, 4, 0, 1, "風の力の制御…\n段々とコツが\n掴めてきたな", 1, 128801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7904, 5, 0, 1, "風の力か…\n応用の幅は広そうだ\n何ができそうか\n色々試してみよう", 1, 128801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7905, 101, 0, 1, "今度、私とキミで\n料理対決でもする？\n簡単には負けないよ", 4, 128801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7906, 102, 0, 1, "この胸に溢れる\n温かな気持ちは…\nきっと彼への…", 8, 128801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7907, 1, 0, 2, "一段と剣技が\n冴えてきたような…\nいや…油断禁物だ", 1, 128801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7908, 2, 0, 2, "かっこいいって？\nありがとう…そんなに\n真っすぐ褒められると\n照れてしまうな…", 1, 128801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7909, 3, 0, 2, "どれほど厚い壁が\n立ち塞がろうと\n打ち破ってみせる", 1, 128801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7910, 4, 0, 2, "手が空いてるなら\n剣の稽古に少し\n付き合ってくれるかな", 1, 128801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7911, 101, 0, 2, "試練を乗り越えて…\n私の中にあるものが\nはっきりと分かったよ", 4, 128801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7912, 102, 0, 2, "キミから流れてくる\n力の温かさ…これが\n私たちの絆なんだ", 8, 128801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7913, 1, 0, 2, "朝だよ、そろそろ\n起きる時間だ\nそれとも私に起こして\nほしいのかな？", 1, 128801, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7914, 2, 0, 2, "昼食は私が作るよ\nリクエストはある？\nできる範囲で応えるよ", 1, 128801, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7915, 3, 0, 2, "おやすみ。また明日も\n一緒に冒険しよう\n約束だよ？", 1, 128801, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7917, 2, 0, 3, "休める時は休む…\n戦い続けるには\n必要なことだからね", 1, 128801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7918, 3, 0, 3, "心の安らぎの大切さ…\nそれを知れたのは\nキミたちのおかげだね", 1, 128801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7919, 4, 0, 3, "気持ちが落ち着くな…\nこの香り、風の力で\n纏えたりしないかな", 1, 128801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7920, 5, 0, 3, "暖まった空気の流れ…\n以前より風の変化に\n気づくようになったな", 1, 128801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7922, 7, 0, 3, "この味付け…\n今度試してみようか\n味見は任せたからね", 1, 128801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7923, 8, 0, 3, "心技体、すべてを\n鍛えるには座学も\n必要だからね", 1, 128801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7925, 1, 0, 1, "瞑想するのに\nいいところはあるかな\n集中できて…お昼寝は\n我慢できるところ", 1, 128901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7926, 2, 0, 1, "ここにはあったかい\n空気が流れてるなぁ…\nみんなが受け入れて\nくれるおかげだね", 1, 128901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7927, 3, 0, 1, "まるで心の中に\n光が満ちてるみたい…\nこの気持ち、みんなに\nおすそ分けしたいな♪", 1, 128901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7928, 4, 0, 1, "この衣装は属性の力を\n反映しやすいんだよね\nキラキラしてるのも\n納得だよ", 1, 128901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7929, 5, 0, 1, "エルフの里のみなさん\n元気かなぁ…\nまた会いに行きたいな", 1, 128901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7930, 101, 0, 1, "まだ変身できてる…\nわたしと騎士クンの\n修行の成果\n出てるなぁ…ふふっ♪", 4, 128901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7931, 102, 0, 1, "この服を脱いだら\nどうなるのかな…\nた、試す勇気は\nないけど…", 8, 128901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7932, 1, 0, 2, "ふぅ…あ、騎士クン\nちょうど瞑想してたの\n一緒に…する？", 1, 128901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7933, 2, 0, 2, "不思議だけど\nいつの間にか\n出たり消えたりするの\n…背中の輪っか！", 1, 128901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7934, 3, 0, 2, "この服って肌触りが\nとってもいいんだよ\nさ、触ってみる？\nスベスベだよ？", 1, 128901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7935, 4, 0, 2, "アストラルの力…\nすごく持続できてるね\n修行の成果が出てるよ", 1, 128901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7936, 101, 0, 2, "この力があれば\n暗いところで\n落とし物をしても\n見つけやすいかも…？", 4, 128901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7937, 102, 0, 2, "冒険の準備なら\nできてるよ\nいつでも声をかけてね\n…楽しみにしてる！", 8, 128901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7938, 1, 0, 2, "おはよう騎士クン\n一緒にお散歩とか…\nどうかな？", 1, 128901, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7939, 2, 0, 2, "お昼ご飯のあとの\n瞑想って難しいんだよ\n眠くなっちゃって…", 1, 128901, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7940, 3, 0, 2, "夜ふかしさんは\nほどほどにね？\n一日頑張った自分を\n休ませてあげないと", 1, 128901, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7942, 2, 0, 3, "のんびりしてたら\nケーキが食べたく\nなっちゃうなぁ…\nでも太る…よね…？", 1, 128901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7943, 3, 0, 3, "わあ、かわいい…！\n見てるだけで\nにやけちゃう…♪", 1, 128901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7944, 4, 0, 3, "これは…花の香りかな\n知ってる花かも…？\n当ててみようっ", 1, 128901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7945, 5, 0, 3, "あったかいと\nどうしてこんなに\n落ち着くんだろう…", 1, 128901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7947, 7, 0, 3, "食べ過ぎたらお腹が\n目立っちゃうかもって\n分かってるのに…！\n止められないよ～っ！", 1, 128901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7948, 8, 0, 3, "こ、恋の精霊とか\nいたりするのかな…？\nはっ！　ダメダメっ！\n実力で頑張らないと", 1, 128901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7950, 1, 0, 1, "お部屋の中でも\n修行、修行っ♪\nでも燃やさないように\n注意しなきゃだね", 1, 128701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7951, 2, 0, 1, "うーん…この衣装\n厚着か薄着か\nどっちなんだろう…？", 1, 128701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7952, 3, 0, 1, "いつもの肉球がない分\nお片付けしやすいな♪\n騎士くんのお部屋\nピカピカにしとこー！", 1, 128701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7953, 4, 0, 1, "お休みは騎士くんと\nＢＢＱに行きたいなー\nあたしと一緒なら\n火の心配はいらないよ", 1, 128701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7954, 5, 0, 1, "この姿ならもっと\n人を助けられる…！\nえへへっ！\n燃えてきたーーっ！", 1, 128701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7955, 101, 0, 1, "騎士くんが帰ったら\nアイラみたいな笑顔で\nお疲れさまさまって\nお出迎えしよっと…♪", 4, 128701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7956, 102, 0, 1, "この姿の時、いつも\n胸が熱く燃えてるのは\nずっと騎士くんのこと\n考えてるからかな…？", 8, 128701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7957, 1, 0, 2, "騎士くん騎士くん！\nあたしに触れると\nやけどしちゃうよ…？\nなんちゃってー♪", 1, 128701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7958, 2, 0, 2, "おっ、騎士くん\n修行に行きたいの？\nえへへっ、いいねー♪\n二人で燃えちゃおー！", 1, 128701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7959, 3, 0, 2, "そうだ騎士くん\nじゃんけんしよっか？\nグローブ外してるから\nチョキも出せるよっ♪", 1, 128701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7960, 4, 0, 2, "この姿でいられる時間\nすごく増えてきた…！\nえへへ！　あたしたち\n修行の成果出てるね！", 1, 128701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7961, 101, 0, 2, "じっと見つめないでよ\n顔が真っ赤に…やば！\nほんとに顔から\n火噴いちゃいそう…！", 4, 128701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7962, 102, 0, 2, "火には強くなれても\n騎士くんに触られた\nほっぺはずーっと\n熱くてたまんないや…", 8, 128701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7963, 1, 0, 2, "おはよー騎士くん！\n今日も一日\n燃え滾っていくよー！\nファイヤーッ！！", 1, 128701, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7964, 2, 0, 2, "騎士くんお昼だよ！\nちゃんとご飯食べて\n午後からの修行も\n乗り切ろうねー！", 1, 128701, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7965, 3, 0, 2, "今日もお疲れさま！\nそうだ、寝る前に\nキャンプファイヤー\n囲んじゃおっか♪", 1, 128701, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7967, 2, 0, 3, "ぼふーんっ！\nえへへ、気持ちいい…\n修行の前に\nちょっと休憩ー♪", 1, 128701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7968, 3, 0, 3, "かわいいな～♪\nなでなでなでなでなで\nなでなでなでなでっ！\nあははっ、摩擦熱！", 1, 128701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7969, 4, 0, 3, "ん～いい香り♪\n少し熱したほうが\n香りが広がるね…って\n蒸発しちゃった！！！", 1, 128701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7970, 5, 0, 3, "外でも自分の火で\nあったまれるけど\nお部屋でぬくぬく\nするのが一番だよね♪", 1, 128701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7972, 7, 0, 3, "もぐもぐっ！\nお腹が減っては\n人助けはできずっ！\nもぐもぐもぐっ！", 1, 128701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7973, 8, 0, 3, "ふむふむ…\nなるほどなるほど…\n…これが上手な\nＢＢＱのやり方か…", 1, 128701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7975, 1, 0, 1, "…壁も扉も柱も\n全て記録で見たまま\n本当に…来ているのね", 1, 129001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7976, 2, 0, 1, "…騒がしい場所\n騎士クンが受け入れて\nくれるのをいいことに", 1, 129001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7977, 3, 0, 1, "ここが…騎士クンの\n守りたかった場所\n騎士クンの思い出が\n詰まった場所…ね", 1, 129001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7978, 4, 0, 1, "視線を感じる…\nこちらの様子を\n探るような…心の動き\n取るに足らないけれど", 1, 129001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7979, 5, 0, 1, "…そこかしこで…\n言葉にもしたくない\n相手が過ごしていた\n記録を思い出す…", 1, 129001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7980, 101, 0, 1, "人は多いけれど…\n雪深い山中よりは\n過ごしやすい…か", 4, 129001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7981, 102, 0, 1, "自覚したことは\nあまりなかったけれど\nひどく…諦めの悪い女\nなのかもしれない", 8, 129001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7982, 1, 0, 2, "お招きありがとう\nそれで…何をして\nいればいい…かな？", 1, 129001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7983, 2, 0, 2, "ここは…明るいね\nわたしの城と比べると\nなんだか眩しいくらい", 1, 129001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7984, 3, 0, 2, "仮面はもう必要ないよ\nだって…わたしの顔を\n騎士クンに見て\nもらえなくなるから", 1, 129001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7985, 4, 0, 2, "騎士クンさえよければ\nわたしの城に専用の\n部屋を用意するよ\n…いつでも", 1, 129001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7986, 101, 0, 2, "…騎士クンのマント\nまた貸してほしいな\nきれいに使うから\n…だめ？", 4, 129001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7987, 102, 0, 2, "いつか二人の願いが\n重なり合えるって…\n信じているね\n…いつまでだって…", 8, 129001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7988, 1, 0, 2, "おはよう、騎士クン\n…昨日は夢の中で\nお話ししすぎちゃって\nごめんね。ふふっ", 1, 129001, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7989, 2, 0, 2, "お腹は…空いてない？\n食べ物はないけれど\n空腹を忘れる魔法なら\nかけてあげられるよ", 1, 129001, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7990, 3, 0, 2, "騎士クン…\nまだ、眠らないの？\n…そう。それなら…\nわたしも起きているね", 1, 129001, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7992, 2, 0, 3, "肩の力を抜くには…\nまだ時間がかかりそう\nきっと…騎士クンと\n二人きりなら別なのに", 1, 129001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7993, 3, 0, 3, "ぺんぽこりん…\nそう、確かあれも\nこんな感じだった…", 1, 129001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7994, 4, 0, 3, "花の香りは…好き\nつかの間\n心を穏やかに\nしてくれるから", 1, 129001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7995, 5, 0, 3, "あたたかい…けれど\n騎士クンの優しさに\n比べれば、まだまだ", 1, 129001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7997, 7, 0, 3, "おい、しい…？\nこの感覚が、それ？\nなぜか…納得できる", 1, 129001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7998, 8, 0, 3, "情報を入手するには\nわざわざ本を開く\n必要があるだなんて…\nプレイヤーは面倒ね", 1, 129001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8000, 1, 0, 1, "メルクリゾートか…\n良い記憶はないな\nよし忘れよう", 1, 129301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8001, 2, 0, 1, "知性の戦士ユニ！\nその恐るべき筋力！\nふおお！　うにゃー！", 1, 129301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8002, 3, 0, 1, "ぽーん！\n石用の水着ください！\nセクシーロゼッタが、\n渚の視線を独占です！", 1, 129301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8003, 4, 0, 1, "学院から奨学金を\n勝ち取るより某財団を\nだまくらかした方が\n早い気がしてきたな…", 1, 129301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8004, 5, 0, 1, "わんつぅ、わんつぅ…\nんぎゃあ足攣ったー！\nおのれカルミナとやら\n絶対に許さん！", 1, 129301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8005, 101, 0, 1, "ところで…なぜぼくは\nプールでもないのに\n水着のままなんだ…\nおのれ世界め！", 4, 129301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8006, 102, 0, 1, "彼と二人で撮った動画\nうう、凝視に耐えん…\nなんだこのムズ痒さは", 8, 129301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8007, 1, 0, 2, "なに？　水着姿で\n寒くないのかだと？\n普段からもっと薄着の\n娘が沢山いるだろう", 1, 129301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8008, 2, 0, 2, "シノブ君にメリゾの\n招待状を送ったのだが\n御尊父の暴走を\n心配していたな…", 1, 129301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8009, 3, 0, 2, "ミミ君らにメリゾの\n招待状を送ったのだよ\n迎える準備をせねば…\nおしゃぶり持ってる？", 1, 129301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8010, 4, 0, 2, "本質の賢者…？\nいや元気にしているが\nなぜ君がぼくの身内を\n知っているんだ…？", 1, 129301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8011, 101, 0, 2, "水着が似合ってる？\nな、なんだね突然…\nやめたまえよふひひ…", 4, 129301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8012, 102, 0, 2, "今度は夏にメリゾへ\n行きたいものだ\nむろん君も一緒だぞ", 8, 129301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8013, 1, 0, 2, "ぐああっ、朝陽が…！\n朝陽が昇っている…！\n夜行性ユニ死んじゃう", 1, 129301, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8014, 2, 0, 2, "はぁ…もう昼か…\nそろそろ寝ないとな\n規則正しい生活を\nしないと不健康だ…", 1, 129301, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8015, 3, 0, 2, "ひゃっはー、夜だー！\nオレたちの時間だぜ！\nふおおブチ上がり～！", 1, 129301, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8017, 2, 0, 3, "これは…ぼくにここで\n寝ろということか？\n一向に構わんが…", 1, 129301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8018, 3, 0, 3, "きゃっきゃっ！\nこれかわい～！\n…よし、今日も\n子供の擬態完璧だな", 1, 129301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8019, 4, 0, 3, "ふむ、良い香りだ\nだがそれだけか…\nとくに精神高揚効果は\nないのか…ちぇー", 1, 129301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8020, 5, 0, 3, "火を見ていると…\n魔導罐を思いだすな…\nかに座、元気かな…", 1, 129301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8022, 7, 0, 3, "ええい、\n固形食物はいらん！　\nポーションを出せ！", 1, 129301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8023, 8, 0, 3, "やはり書は文系に限る\nなんと心地よい筆致…\nぐああ突然理系文が！\nひぃ目が理系になる！", 1, 129301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8025, 10, 0, 3, "ほああ～！\n知性の海上騎士ユニ！\nこれは新たな可能性！", 1, 129301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8026, 11, 0, 3, "これは…なんだ？\n何が面白いんだ？\n難しいな…", 1, 129301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8027, 103, 0, 1, "大切な人と過ごす\n正月の思い出、か…\n昔の私だったら\n考えられなかったな", 12, 108901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8028, 103, 0, 2, "叶えたい願いなら\nたくさんあるよ\nそれでも一番を選ぶ\nなら、やはりキミと…", 12, 108901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8029, 103, 0, 1, "神殿にあった\n恋愛おみくじ…\n引いてみようかな…", 12, 108801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8030, 103, 0, 2, "騎士クン。来年も\nまた一緒におみくじ\n結ぼうね。約束だよ", 12, 108801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8031, 103, 0, 1, "今度は騎士くんと\n一緒にうつった写真、\nおばあちゃんに\n送ろうっと♪", 12, 108701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8032, 103, 0, 2, "あ、騎士くん！\n今年も…ううん\nこれからも、よろしく\nおねがいします♪", 12, 108701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8033, 1, 0, 1, "旧校舎の地下牢…\n真冬のサマー計画…\n等身大のＰＲ…\nうッ、頭が…！", 1, 129401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8034, 2, 0, 1, "暇だし鏡でも見るか。\nお、いい腹筋育って…\nあ？　何見てんだコラ", 1, 129401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8035, 3, 0, 1, "オレは金色の疾風！\nんん…なんか違うな。\n我が名は金色の…\nあ？　何見てんだコラ", 1, 129401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8036, 4, 0, 1, "モニカとユニ先輩って\n口調と身長似てるけど\n子供扱いに対する\n反応は真逆だよね…", 1, 129401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8037, 5, 0, 1, "シオリン元気かな…\n昔くちゃくちゃに\n撫でくり回して\n嫌がられたっけなァ…", 1, 129401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8038, 101, 0, 1, "べつにいんだけどさ…\nあんたの知り合いって\n普段から水着みたいな\nカッコの女多くない？", 4, 129401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8039, 102, 0, 1, "そだ、弟らがあんたに\n会いたがってたンだ。\n暇だったらいつでも\nウチ来てやってよ", 8, 129401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8040, 1, 0, 2, "特別講座は色々あった\nけど…メリゾ自体は\nまあ悪くなかったよね", 1, 129401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8041, 2, 0, 2, "水着と合わせる小物に\nセンス出るよね、\nって話をミィコ先輩が\nしててマジわかりみ", 1, 129401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8042, 3, 0, 2, "水着のミフユねえさん\nやっぱ仕上がってたね\nあれが竜殺しの体か…\nバリかっけーな…", 1, 129401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8043, 4, 0, 2, "はにょんっ！？\nや、だからさ…\n後ろから声かけんな、\nつってンじゃん…", 1, 129401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8044, 101, 0, 2, "メリゾの特別講座が\n上手くいったのも\n敏腕カメラマンがいた\nお陰だよ。ありがとね", 4, 129401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8045, 102, 0, 2, "あ？　この水着が？\n似合っ…え、なんて？\nごめ、聞こえんくて。\nもっかい言ってみて", 8, 129401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8046, 1, 0, 2, "もう朝とかマジないわ\n昨日二人に付き合って\nオールでカラオケだよ", 1, 129401, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8047, 2, 0, 2, "はぁ授業行かなきゃ…\n真っ昼間から勉強とか\nマジつらたん過ぎ…", 1, 129401, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8048, 3, 0, 2, "今日これから夜勤で…\nいやバイトちゃーくて\nボランティアの話よ？\n校則違反よくない", 1, 129401, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8050, 2, 0, 3, "うわ、ふっかふか…\nオニほど安らぐわ…\nやば、ユニってくる…", 1, 129401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8051, 3, 0, 3, "うちも妹いりゃ\nこーゆーのもなァ…\nま自分用でもいいか", 1, 129401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8052, 4, 0, 3, "ふーん…\nいいじゃんこの香り\n抜群に嗅げるわ", 1, 129401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8053, 5, 0, 3, "うちン家のルーツの森\n大火災でなくなった\nらしンだよね…\n生まれる前の話だけど", 1, 129401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8055, 7, 0, 3, "何コレありがてぇー\n食料ほくほく過ぎて草\n米ガチで尊いわァ…", 1, 129401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8056, 8, 0, 3, "いいけど、なんでうち\n水着で本読んでンだよ\nどーゆープレイだよ", 1, 129401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8058, 10, 0, 3, "金色の海上騎士クロエ\n推して参る！　覇ァ！\nあ？　何見てんだコラ", 1, 129401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8059, 11, 0, 3, "何コレ、貝の椅子？\nどーやって遊ぶんだ…\nいや座って終わりか…\nまァそりゃそーか…", 1, 129401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8060, 1, 0, 1, "さてと…\n明日の訓練メニュー\n考えねえとな", 1, 129801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8061, 2, 0, 1, "また新入りの女子に\n弁当もらっちまった…\nありがたく食うけどよ", 1, 129801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8062, 3, 0, 1, "種族なんて関係なく\nみんなが力を合わせて\nランドソルを守る…\nいい時代になったよな", 1, 129801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8063, 4, 0, 1, "誰にだって長所はある\n新入りのいいところを\n見つけてやるのも\n教官の仕事だ", 1, 129801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8064, 5, 0, 1, "厳し過ぎても\n甘過ぎてもダメ…\n教官ってのは難しいぜ", 1, 129801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8065, 101, 0, 1, "戦いが苦手な新入りを\nどうフォローするか…\n後であいつと\n相談してみるか", 4, 129801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8066, 102, 0, 1, "あいつは優し過ぎて\n戦いに向いてねえ\n…そういう甘さ\n嫌いじゃねえけどな", 8, 129801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8067, 1, 0, 2, "強くなる近道はねえ\n地道な鍛錬あるのみだ\nさ、訓練始めるぞ！", 1, 129801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8068, 2, 0, 2, "新入りにも\nいつも言ってるが…\n装備の手入れは\n念入りにしろよな？", 1, 129801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8069, 3, 0, 2, "座学も受けてえのか？\nいい心がけだな！\nなんでも教えてやるよ\n…魔法以外ならな", 1, 129801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8070, 4, 0, 2, "新入りたちがよく\n恋バナしててな…\nお、おまえは…誰か\n気になるやついるか？", 1, 129801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8071, 101, 0, 2, "なあ、ストレッチ\n手伝ってくれねえか？\n訓練前にしっかり\n筋肉ほぐさねえと…", 4, 129801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8072, 102, 0, 2, "ずいぶん頑張って\n鍛えたよな、おまえ\nよし！　ご褒美に\nお菓子作ってやるよ♪", 8, 129801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8073, 1, 0, 2, "訓練日和だな！\nさぁて…まずは\n走り込みといくか！", 1, 129801, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8074, 2, 0, 2, "昼飯はしっかり食えよ\nでなきゃ、\nこの後もたねえぜ？", 1, 129801, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8075, 3, 0, 2, "とっくに消灯時間だ\nさっさと寝な？\n明日も早いんだからよ", 1, 129801, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8077, 2, 0, 3, "休むのも訓練のうちだ\n今はのんびり\nさせてもらうぜ", 1, 129801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8078, 3, 0, 3, "いい面構えだ\n気に入ったぜ！\nあたしの班に入るか？\n…なんてな♪", 1, 129801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8079, 4, 0, 3, "…いやされる匂いだな\n新入りたちにも\nすすめてみるか…", 1, 129801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8080, 5, 0, 3, "あったけえからって\n寝ないようにしねえと\n教官としての\n威厳に関わるからな…", 1, 129801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8082, 7, 0, 3, "食事も訓練の一環だ\nとはいえ食い過ぎには\n気をつけねえと…", 1, 129801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8083, 8, 0, 3, "トーゴクの剣術…か\nなにか訓練に\n活かせるかもしれねえ", 1, 129801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8085, 1, 0, 1, "あのおかたに近付く\n不埒な輩には、特別な\n訓練で地獄を見て\nいただきましょう", 1, 129901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8086, 2, 0, 1, "これだけ広ければ\n多少派手に\n動き回っても\n問題なさそうですね", 1, 129901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8087, 3, 0, 1, "…今誰か私を\n「鬼」教官と\n呼びませんでしたか？", 1, 129901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8088, 4, 0, 1, "新しく開発したこの薬\n誰か試してくれる人は\nいませんか？", 1, 129901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8089, 5, 0, 1, "厳しくすれば投げ出し\n優しくすればサボる\n人というのはどこまで\n度し難いのでしょう", 1, 129901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8090, 101, 0, 1, "はじめましょう\nあのおかたの心を\n射貫くための\nシミュレーションを", 4, 129901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8091, 102, 0, 1, "あらゆる苦難と\n忌々しい女から守る\nためにも、私自身も\n鍛えておかねば", 8, 129901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8092, 1, 0, 2, "あなた様、そんなに\n見つめられると…え？\nエリコはカッコいい？\nええ、当然ですとも", 1, 129901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8093, 2, 0, 2, "不調があればすぐ\n報告してください\n体のケアも教官の\n大事な役目ですので", 1, 129901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8094, 3, 0, 2, "新しい訓練器具の\n使い心地はどう…え？\n重すぎる？　これでも\n軽くしたのですが…", 1, 129901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8095, 4, 0, 2, "運動後には特製の\n栄養ドリンクをどうぞ\nクスクス、何も\n入れたりしてませんわ", 1, 129901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8096, 101, 0, 2, "よくがんばりました\nあなた様。あぁ…\nお疲れになったお顔も\n素敵ですわ", 4, 129901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8097, 102, 0, 2, "あなた様、二人だけの\n遠征訓練へ\nまいりましょう。\n既に準備万端ですわ", 8, 129901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8098, 1, 0, 2, "まだ眠いですか？\nならばストレッチで\n体を痛めつ…ではなく\nほぐしていきましょう", 1, 129901, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8099, 2, 0, 2, "お昼はお弁当をご用意\nいたしました。量も\nスタミナも増し増し\n残さず食べてください", 1, 129901, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8100, 3, 0, 2, "よい睡眠こそ健やかな\n体に不可欠。ですので\n私が寝ずの番をさせて\nいただきますわ", 1, 129901, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8102, 2, 0, 3, "休憩用に使おうと思い\nましたが、心地よくて\n立てなくなって\nしまいますわ…", 1, 129901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8103, 3, 0, 3, "中に重しを詰めれば\n愛でながら運動が\n可能ですわ", 1, 129901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8104, 4, 0, 3, "香りでリラックスさせ\n同時に暗示を\nかけてみましょうか", 1, 129901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8105, 5, 0, 3, "…くっ、眠気を誘う\nこの暖かさ…\n油断も隙もありません", 1, 129901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8107, 7, 0, 3, "主食はこちらで十分\nまかなえますわね\nそれでは私はおかずを\n作りましょう", 1, 129901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8108, 8, 0, 3, "まあ、実用書の類も\n揃っているのですね\nとても助かりますわ", 1, 129901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8110, 1, 0, 1, "……（そわそわ）", 1, 129601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8111, 2, 0, 1, "ここに寄らずの花陣…\n作ったらダメですよね", 1, 129601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8112, 3, 0, 1, "部屋の隅が\n落ち着きます\nそれか、陽の当たる所", 1, 129601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8113, 4, 0, 1, "身を隠せる場所が\nあると…\n助かるんですけど", 1, 129601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8114, 5, 0, 1, "複雑な造りの家ですね\n触って壊れたり─\nしませんか？", 1, 129601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8115, 101, 0, 1, "出入りするひと\nみんな、笑顔…\n…変わった家ですね", 4, 129601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8116, 102, 0, 1, "この家なら安全─\nかもしれません\n魔物が出ないので", 8, 129601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8117, 1, 0, 2, "これも、視察の一環\n遊びに来たわけじゃ\nないです", 1, 129601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8118, 2, 0, 2, "長話─苦手です\n疲れるので…\nふぅ…", 1, 129601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8119, 3, 0, 2, "ここで友達を作れと\nネアさんが…\nそう、言われても…", 1, 129601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8120, 4, 0, 2, "世話をするのは\n得意です\n…花の、ですよ？", 1, 129601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8121, 101, 0, 2, "媛へのお土産を\n探してるんですけど…\n…貴方のおすすめ、\n聞いておきます", 4, 129601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8122, 102, 0, 2, "平和でのんきで…\n貴方らしい家です\n一応、褒めてます", 8, 129601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8123, 1, 0, 2, "花に、お水─？\n…いりません", 1, 129601, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8124, 2, 0, 2, "日が高いうちは\nむやみに外へ出ません\n面倒事、多いので…", 1, 129601, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8125, 3, 0, 2, "夜番。私がします\nあまり眠らない\n性質なので…", 1, 129601, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8127, 2, 0, 3, "ここなら根を張れる\nかもしれません\n…張りませんけど", 1, 129601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8128, 3, 0, 3, "こんなにやわな体じゃ\n身を守れませんよ─\n…あ。生き物じゃ\nないんですね", 1, 129601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8129, 4, 0, 3, "花みたいに─\nいい匂い、です", 1, 129601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8130, 5, 0, 3, "火を見てると、\nジオ・ゲヘナを\n思い出します…ふぅ", 1, 129601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8132, 7, 0, 3, "食事も立派な戦支度\n媛が言ってました", 1, 129601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8133, 8, 0, 3, "知識も武器の一つだと\n媛が…でも、\nこれでどうやって…？", 1, 129601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8135, 1, 0, 1, "天気もいいしお花畑に\nピクニックでも\n行こうかな♪", 1, 130701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8136, 2, 0, 1, "あ、どこからか\nお花の匂いが…\nいい香りですね", 1, 130701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8137, 3, 0, 1, "新しいシャベルとか\nじょうろもほしいなぁ\nお小遣いしっかり\nためないとね", 1, 130701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8138, 4, 0, 1, "お花飾りたいけど\n花瓶はどこかな…\nあとであの人に\n聞いてみよっと", 1, 130701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8139, 5, 0, 1, "お花の観察日記\n書かなきゃ\n日々勉強は\n欠かせませんよ", 1, 130701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8140, 101, 0, 1, "衣装にお花を足して\nみたけど、あの人\n気付いてくれるかな？", 4, 130701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8141, 102, 0, 1, "この花冠、上手に\n作れたかも…\n日頃のお礼にあの人に\nあげようかな…", 8, 130701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8142, 1, 0, 2, "あなたは何のお花が\n好きですか？　ほら、\n一応知っておいたら\n役に立つかもですし", 1, 130701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8143, 2, 0, 2, "そのお花、乱暴に\n扱っちゃダメですよ\n花粉が服について\nとれなくなりますから", 1, 130701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8144, 3, 0, 2, "土いじりしたから\n服が汚れてますよ\nはたいてあげますから\nじっとしててください", 1, 130701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8145, 4, 0, 2, "そんなにすぐに芽は\n出ませんよ\nまあ、楽しみなのは\n私も同じですけど…", 1, 130701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8146, 101, 0, 2, "この衣装、そんなに\n気になります？　なら\nこうしてお花を\n着ければおそろいです", 4, 130701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8147, 102, 0, 2, "お花の種を私に？\nありがとうございます\nせっかくなので一緒に\nお世話しませんか？", 8, 130701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8148, 1, 0, 2, "お花にお水をあげるの\n手伝ってもらえます？\nお水は朝にあげるのが\nいいんですよ", 1, 130701, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8149, 2, 0, 2, "とっておきのお花見\nスポットに\nご案内しますよ\nお弁当も忘れずに！", 1, 130701, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8150, 3, 0, 2, "もうこんな時間\n早く寝ましょう\n草木も眠るなんとか\nって言いますからね", 1, 130701, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8152, 2, 0, 3, "お花のお手入れって\nけっこう疲れますね…\nここでちょっと一息\nしちゃおっと", 1, 130701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8153, 3, 0, 3, "耳にお花を着ければ…\nえへへ、すっごく\nかわいくなったね！", 1, 130701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8154, 4, 0, 3, "これはラベンダー\nこっちはミモザ\nフローラル系の香り\nっていいですねぇ", 1, 130701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8155, 5, 0, 3, "ポカポカしますね～\nまるで春の陽気\nみたいです～", 1, 130701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8157, 7, 0, 3, "お花を一輪さすだけで\nご飯の時間がとても\n華やかになりますね", 1, 130701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8158, 8, 0, 3, "お花の本が\nいっぱいある！\n今日はこれで\nお勉強です！", 1, 130701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8160, 1, 0, 1, "ふんふ～ん♪\n華やかな服を着ると\n心がウキウキします♪", 1, 130801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8161, 2, 0, 1, "花祭りで出会った\nみなさん…\nお元気でしょうか？\nまたお会いしたいです", 1, 130801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8162, 3, 0, 1, "はっ！　そうでした\nこの服の時は\nメイドのお仕事は\nお休みでした…", 1, 130801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8163, 4, 0, 1, "この服は替えが\nありませんから\n大きなドジには\n気を付けないと…", 1, 130801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8164, 5, 0, 1, "目立ったほつれもなし\n…うん、いい感じに\n補修できました♪", 1, 130801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8165, 101, 0, 1, "メイド姿でなくても\n心はいつでも\nお嬢さまの\nメイドです♪", 4, 130801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8166, 102, 0, 1, "この服を着ると心が\n浮き立つのはあのかた\nと一緒にいる気持ちに\nなるからでしょうか？", 8, 130801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8167, 1, 0, 2, "はい、今日は一日\nこの服で過ごそうと\n思います\n変じゃないですよね？", 1, 130801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8168, 2, 0, 2, "花祭りのおかげで\n少しだけお花に\n詳しくなった…\nような気がします！", 1, 130801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8169, 3, 0, 2, "花祭り、今度は\nお嬢さまと救護院の\nみんなと一緒に\n行ってみたいですね", 1, 130801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8170, 4, 0, 2, "今度キョウカちゃんと\n花祭りの服でお花を\n見にお出かけ\nするんです", 1, 130801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8171, 101, 0, 2, "メイド服を全部洗濯\nしちゃうなんて…\nドジっちゃいましたぁ", 4, 130801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8172, 102, 0, 2, "花祭りの服は私たちの\n思い出の服ですから…\nこれからも\n大切にします♪", 8, 130801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8173, 1, 0, 2, "はう、そうでした\nお花にお水を\nあげる時間です！", 1, 130801, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8174, 2, 0, 2, "お昼ご飯は\nバスケットを持って\nお花畑に食べ\nに行きませんか？", 1, 130801, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8175, 3, 0, 2, "ふぁあ…繕い物を\nしていたらこんな時間\nになってしまいました\n早く寝ないと…", 1, 130801, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8177, 2, 0, 3, "ふわふわしてて\nきもちいいです～\nどんな生地を\nつかってるんだろ～？", 1, 130801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8178, 3, 0, 3, "かわいいですね～\nぬいぐるみさんにも\n服をつくって\nあげたいです♪", 1, 130801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8179, 4, 0, 3, "いい香りです～\nあれ？　もしかして\n私の服についてる花と\n同じ香りが…？", 1, 130801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8180, 5, 0, 3, "暖かいですけど\nあまり近づいちゃうと\n頭の花飾りが乾燥\nしちゃいそうですね…", 1, 130801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8182, 7, 0, 3, "もぐもぐ…\nいい花を咲かせるには\nしっかりとした栄養が\n必要だそうです！", 1, 130801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8183, 8, 0, 3, "なるほどなるほど…\nお花のお手入れは\n奥が深いですね…", 1, 130801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8185, 1, 0, 1, "マホマホ王国にも\nサーカス団設立や\n夢と希望を届けますえ", 1, 131001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8186, 2, 0, 1, "せっかくの晴れ舞台や\nかっこいいポーズも\nできるようにしたいわ", 1, 131001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8187, 3, 0, 1, "むむむむ～っ。うーん\nクルルはんみたいに\n胸から宝石を出すんは\n難しいみたいやわ", 1, 131001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8188, 4, 0, 1, "あむっ…んん～♪\nやっぱり練習のあとは\n甘いものに限るわぁ", 1, 131001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8189, 5, 0, 1, "アーサーたちの衣装も\n今のうちに考えんと…\n生地、足りるやろか", 1, 131001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8190, 101, 0, 1, "王子はんとなら\nどんなアイデアも\n見つけられます♪", 4, 131001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8191, 102, 0, 1, "うちの晴れ姿が\n王子はんの目に一等\n輝いて見えますように", 8, 131001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8192, 1, 0, 2, "キリっとしたうちも\nかっこいい？\nもう、上手やなぁ", 1, 131001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8193, 2, 0, 2, "新しい演目考えたんよ\nなぁ王子はん、意見\n聞かせてもらえます？", 1, 131001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8194, 3, 0, 2, "聞いてや王子はん\n動物さんたちとさらに\n息が合うてきたんよ\nみんな頑張り屋さんや", 1, 131001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8195, 4, 0, 2, "演技の感想なら\n大歓迎どすえ♪\nたんと聞かせてや～", 1, 131001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8196, 101, 0, 2, "王子はん、本番当日は\n最前席で見たってな\nそしたらうち、もっと\n頑張れますさかい♪", 4, 131001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8197, 102, 0, 2, "この技をあなたに\n捧げます…なんて♪\n驚いたやろ？\nどっきり作戦成功や♪", 8, 131001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8198, 1, 0, 2, "素敵なショーのためや\n今日も焦らず一歩ずつ\n成長していきまひょ\nな、王子はん♪", 1, 131001, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8199, 2, 0, 2, "お弁当、王子はんも\n一緒にいかがどす？\nみんなで食べて団結や", 1, 131001, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8200, 3, 0, 2, "夜のサーカスも\n幻想的でとっても\n魅力的なんよ\n一緒にいきまひょか♪", 1, 131001, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8202, 2, 0, 3, "練習が終わったあとは\nの～んびりして\n体力を回復せな…", 1, 131001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8203, 3, 0, 3, "この子たちとなら\n空中ブランコや\n玉乗りもええなぁ", 1, 131001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8204, 4, 0, 3, "いい香りやなぁ\n本番前の緊張も\n和らげてくれそうやわ", 1, 131001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8205, 5, 0, 3, "このあったかい感じ…\nスポットライトの熱を\n思い出すわぁ", 1, 131001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8207, 7, 0, 3, "ちゃんと食べへんと\nいい演技はできまへん\n団長はんも言うてたわ", 1, 131001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8208, 8, 0, 3, "動物さんたちのことも\nもっと勉強せな\n大事な仲間やさかい", 1, 131001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8210, 103, 0, 1, "器用じゃないが、\n情には厚いよ\nこういう女を「一途」\nってそう言うんだろ？", 12, 105601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8211, 103, 0, 2, "斬れないものはないと\n思ってきたさ。だけど\n…この気持ちだけは\n斬れないね、絶対に", 12, 105601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8212, 1, 0, 1, "んしょ、んしょっ…！\n日頃の感謝を込めて\nヒーローさんのお家を\nきれいにしますです♪", 1, 130901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8213, 2, 0, 1, "ふむふむ…\n新アトラクションは\nこのギルドハウスを\nモチーフに…", 1, 130901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8214, 3, 0, 1, "ごそごそ、こそこそ…\nヒーローさんに内緒で\nどこかにお礼の宝石を\n隠しておきましょう…", 1, 130901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8215, 4, 0, 1, "パークの運営が\n落ち着いたら\nわたしもギルド活動？\nしてみたいですます♪", 1, 130901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8216, 5, 0, 1, "こんなすてきな\nおうちに住めたら…\nいえ！　うさぎ小屋で\n十分です～っ", 1, 130901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8217, 101, 0, 1, "ヒーローさんのおうち\nだって意識したら\nアトラクションよりも\nドキドキしてきます…", 4, 130901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8218, 102, 0, 1, "ヒーローさんのことを\n考えると顔が赤く…\nこれってもしかして\nひょっとして…？", 8, 130901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8219, 1, 0, 2, "あっ、ヒーローさん！\n話しかけてくれた記念\nということで、宝石\n受け取ってください♪", 1, 130901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8220, 2, 0, 2, "元気がない時は\n語尾にぴょんですっ！\nさあヒーローさんも\nやってみるぴょん♪", 1, 130901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8221, 3, 0, 2, "ヒーローさんへのご恩\nおばあちゃんうさぎに\nなるまでに、お返し\nできるでしょうか…？", 1, 130901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8222, 4, 0, 2, "ヒーローさん！\n二人で一緒に\nパレードの練習\nしませんか～♪", 1, 130901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8223, 101, 0, 2, "あはは♪\nヒーローさんは\n人を笑顔にする力が\nありますです♪", 4, 130901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8224, 102, 0, 2, "ヒーローさんに\n一日一恩返しですっ！\n返し終わるまで\nやめませんですます♪", 8, 130901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8225, 1, 0, 2, "おはようございます♪\nんしょ、んしょっ…！\n気持ちよく起きられた\n記念に宝石どうぞ♪", 1, 130901, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8226, 2, 0, 2, "お昼にフードコートで\nかばばん君バーガー\n買ってきましたよ！\n一緒に食べますです♪", 1, 130901, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8227, 3, 0, 2, "今日も一日\nお疲れさまですっ♪\nマッサージしますから\n寝そべってください♪", 1, 130901, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8229, 2, 0, 3, "はふぅ～…♪\nお仕事の疲れも\nぴょんぴょ～んって\n飛んでっちゃいます♪", 1, 130901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8230, 3, 0, 3, "わぁっ、とっても\nかわいいですますっ！\nギルドハウスの\nマスコットですね♪", 1, 130901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8231, 4, 0, 3, "ヒーローさんの好きな\n香り…これを宝石に\nつければ、受け取って\nもらえるでしょうか？", 1, 130901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8232, 5, 0, 3, "はあ、気持ちいい♪\nヒーローさんの\n好きな温度に\n調整しときますです♪", 1, 130901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8234, 7, 0, 3, "支配人は体が資本っ！\nたくさん食べ…すぎて\n動けなくなっちゃい\nましたぁ～…！", 1, 130901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8235, 8, 0, 3, "パークの\nガイドブックとか\nあったらいいですね♪", 1, 130901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8237, 1, 0, 1, "外あつすぎ溶けるわ～\nやっぱ部屋ん中で\n水着がラクだねー", 1, 131101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8238, 2, 0, 1, "ン～\nカリザきゅんどこー？\nもしかして逃げた？", 1, 131101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8239, 3, 0, 1, "ミソラちゃんと\nリリちゃん誘って\nそろそろイイコト\nしよっかなー？", 1, 131101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8240, 4, 0, 1, "おねえちゃんに\n頼まれたシゴトは\nまた今度にすっかー\n夏休みだしー♪", 1, 131101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8241, 5, 0, 1, "騎士きゅんの\nプリンセスフォームも\n見てみたいんだよなー\nンフ♡", 1, 131101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8242, 101, 0, 1, "スライムを\nこーしてあーしたら\n騎士きゅんの泳ぐ\n練習に使えるっぽ？", 4, 131101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8243, 102, 0, 1, "おかえり～♡\nって水着で迎えたら\n騎士きゅん喜んで\nくれるかなー？", 8, 131101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8244, 1, 0, 2, "騎士きゅん\nこのサングラス\nかけてみる？\nぜってー似合うよー", 1, 131101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8245, 2, 0, 2, "日焼け痛かったら\nいつでも言ってねー\nスライムでひんやり\nしてあげるよ～♪", 1, 131101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8246, 3, 0, 2, "今から外で\n焼いてこっかなー\n騎士きゅんもどー？", 1, 131101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8247, 4, 0, 2, "スライムマッサージ\nしてあげよっかー\nほーれ、もみもみ～♡", 1, 131101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8248, 101, 0, 2, "騎士きゅんが選んだ\n水着も着てみたーい\nネアおねえちゃんに\nどんなの着てほしい？", 4, 131101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8249, 102, 0, 2, "騎士きゅ～ん\n尻相撲しよっかー♪\nおねえちゃんに\n勝てたらご褒美だぞ～", 8, 131101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8250, 1, 0, 2, "暑すぎてムダに\n早起きしちった～\n二度寝すっか～\n…ふぁあ…", 1, 131101, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8251, 2, 0, 2, "夏バテしても\nスイカとかかき氷なら\n全然ウェルカム\nなんだよなー", 1, 131101, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8252, 3, 0, 2, "今日はこのまま\n水着で寝るかー\n夢ん中で泳ごー", 1, 131101, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8254, 2, 0, 3, "いったんキューケイ…\nンー…よし\n充電カンリョ～♪", 1, 131101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8255, 3, 0, 3, "仲間になりたそうに\nこっち見てんねー\nきゃわいいしおっけ～", 1, 131101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8256, 4, 0, 3, "ン～いいニオーイ\nお日様あたった\n騎士きゅんの\n次くらいかな～", 1, 131101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8257, 5, 0, 3, "カラダ冷えてきたから\nたすかるーおかげで\nまだまだ水着イケるわ", 1, 131101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8259, 7, 0, 3, "うめ～\nとまんねぇ～\n夏バテどこいった～？", 1, 131101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8260, 8, 0, 3, "ふむふむ…\nこれは意外と\nぬるっと読めるっぽー", 1, 131101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8262, 10, 0, 3, "ンフフ～\n海の戦士ってカンジ？\nアリよりのアリだね～", 1, 131101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8263, 11, 0, 3, "あーやべ\n一瞬で寝そー\nヤバだわ～", 1, 131101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8264, 1, 0, 1, "服や紙が易々と\n燃えぬとは\nランドソルとはまこと\n住み易き国で候", 1, 129701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8265, 2, 0, 1, "どの世界にあっても\n民は活力に溢れ\n街は賑わうもの…\n善哉、善哉", 1, 129701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8266, 3, 0, 1, "食べ歩きに世界征服…\nギルドとはかくも\n自由気儘な徒党に\nありんすなぁ", 1, 129701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8267, 4, 0, 1, "連絡は…今日もなし\n我が妹子は相変わらず\n放埓なことよ喃", 1, 129701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8268, 5, 0, 1, "モネは相変わらず\n警戒しきりなれど…\nそのようなところも\n愛らしくあり申す", 1, 129701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8269, 101, 0, 1, "なんと長閑な世にや\nありけむ…この平和は\n騎士殿たちの努力の\n賜物にありんす喃", 4, 129701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8270, 102, 0, 1, "『王家の装備』は\n使わぬのかと？\n…フフ。すでに\n用いており申す", 8, 129701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8271, 1, 0, 2, "今ばかりは媛という\n立場は置いて\nこの地の者らと\n歓談したく候", 1, 129701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8272, 2, 0, 2, "強さの秘密？　…フフ\n女子の秘密を暴くのは\n野暮にござんす\nご寛恕いただきたく候", 1, 129701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8273, 3, 0, 2, "此方の家屋、\n見目以上に\n堅牢であり申す\n何ぞ仕掛けがありや？", 1, 129701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8274, 4, 0, 2, "心地よい時間とは\n疾く過ぎ去りゆくもの\n此処で過ごすと改めて\n実感できる喃", 1, 129701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8275, 101, 0, 2, "視線が気になる？\nこれはすみませぬ\nなにぶん、お前さまは\n見ていて飽きませぬ故", 4, 129701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8276, 102, 0, 2, "お前さまの前では\n誰もが心を開く…\nなんとまぁ罪作りな\n色男にありんすな？", 8, 129701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8277, 1, 0, 2, "よく眠れましたかの？\n寝る子は良く育つ、と\n古くより申します由", 1, 129701, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8278, 2, 0, 2, "美食殿の作法に倣い\n皆で共に昼餉を頂くと\n致しんしょう", 1, 129701, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8279, 3, 0, 2, "逢魔ヶ時は望まぬ客も\n参るもの…お前さまも\n気をつけなんし", 1, 129701, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8281, 2, 0, 3, "わっちの蛇に\n勝るとも劣らぬ\n座り心地で候", 1, 129701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8282, 3, 0, 3, "魂、万物に宿る\nとの云いあらば\n情愛を注いでやるのが\nよかろうの", 1, 129701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8283, 4, 0, 3, "いと香しき哉…\n騎士殿は香の道にも\n詳しくあられるか", 1, 129701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8284, 5, 0, 3, "ジオ・ゲヘナの\n灼熱と比ぶれば\nそよ風と変わり\nござんせん", 1, 129701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8286, 7, 0, 3, "美味なる哉…この味\nぺコリーヌ姫が好むも\n道理にありんす", 1, 129701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8287, 8, 0, 3, "トーゴクに、オーマ…\n書で見るだけでも\n心惹かれる土地よの", 1, 129701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8289, 1, 0, 1, "暑い日には\n冷たいものですよねー\nかき氷とか食べて\nすっきりしたいです★", 1, 131201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8290, 2, 0, 1, "海への行き来は\nテレポーテーションに\nお任せです★", 1, 131201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8291, 3, 0, 1, "トロピカルジュースが\n飲みたいでーす♪\n海の家で見たんですよ\nカップル用ジュース★", 1, 131201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8292, 4, 0, 1, "ご飯どうしようかなー\nあ、海鮮ラーメンとか\n美味しそうかも★", 1, 131201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8293, 5, 0, 1, "水鉄砲、普通に撃つと\n少し物足りないかも…\nどるるるる～ん★って\n感じが欲しいですよね", 1, 131201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8294, 101, 0, 1, "コイビトっぽいこと\nアオハルっぽいこと\nいっぱいしたいです★\n修羅場とかも♪", 4, 131201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8295, 102, 0, 1, "騎士さんが溺れたら\n絶対助けに行きますね\n人工呼吸でもなんでも\nしてあげますから★", 8, 131201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8296, 1, 0, 2, "騎士さーん\n日焼け止めの塗り直し\nお願いします★\n優しくしてくださいね", 1, 131201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8297, 2, 0, 2, "海で食べたフルーツ？\nはい、まだありますよ\nこっちでもあーんして\n食べさせましょうか★", 1, 131201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8298, 3, 0, 2, "ぺんぽこりんは\nどうしたのかって？\nどうしたんでしょうね\nどーでもいいです★", 1, 131201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8299, 4, 0, 2, "今日はどんな\nイイコトしましょうか\n思い出作り？\n人助けもいいですね★", 1, 131201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8300, 101, 0, 2, "ヒモを引っ張ったら…\n「セ・キ・ニ・ン」\n取ってもらいますよ？\nなーんてウソですけど", 4, 131201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8301, 102, 0, 2, "みんなでイイコトも\n楽しいですけど～\n…二人でイイコト\nしちゃいますか？", 8, 131201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8302, 1, 0, 2, "朝ですよー！\n今日も一日、\nイイコト日和ですね♪", 1, 131201, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8303, 2, 0, 2, "日差しにご注意ですね\n日焼け止め…誰かに\n塗ってほしいなー？\n…ちらっ★", 1, 131201, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8304, 3, 0, 2, "夜の海って\n惹かれちゃいますよね\n…ロマンチックです★", 1, 131201, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8306, 2, 0, 3, "う～ん、まったり～\nこれぞ新ジャンル\nおうちバカンス★", 1, 131201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8307, 3, 0, 3, "よーしよーし★\nわたしとイイコト\n一緒にしましょうねー", 1, 131201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8308, 4, 0, 3, "いい香りですねー\nリラックスタイムに\nちょうどよさそう★", 1, 131201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8309, 5, 0, 3, "水遊びのあとは\nしっかり体を\n温めないとですよね★", 1, 131201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8311, 7, 0, 3, "栄養補給もバッチリ★\n遊びもイイコトも\nお腹が減ってたら\nできませんからねー？", 1, 131201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8312, 8, 0, 3, "今だと海関連の本とか\n興味あるんですよねー\n魚とか海難救助とか★", 1, 131201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8314, 10, 0, 3, "かっこいー★\n名前はー…\nぷんぷくぽんとか？", 1, 131201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8315, 11, 0, 3, "映えますねー\n写真撮ったら\n素敵な思い出に\nなりそうです★", 1, 131201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8316, 1, 0, 1, "海でもないのに\nこの姿でいるのは…\nやはり、場違いでは\nありませんか…？", 1, 131601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8317, 2, 0, 1, "スイカとは割ることと\n見つけたり！\n…ですが目隠しすれば\n見当たらないもの…", 1, 131601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8318, 3, 0, 1, "まぁ、膝に砂が…\n気がつきませんでした\nまるでわんぱくな\nお子様のよう…ふふっ", 1, 131601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8319, 4, 0, 1, "家の中でスイカを割る\nわけにはまいりません\nここは潔く包丁で\n切りましょう", 1, 131601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8320, 5, 0, 1, "ミソラ様やネア様の\n大胆さを見習って…\nき、騎士きゅーん…★\n難しいですね…", 1, 131601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8321, 101, 0, 1, "きゅ、急に暗く…！？\n…失礼しました\n鉢巻きが下がってきた\nだけでした…ううぅ", 4, 131601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8322, 102, 0, 1, "わたくしに剣の才は\nあるのでしょうか…\nいえ、何事もまずは\nやってみなくては", 8, 131601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8323, 1, 0, 2, "なにか羽織った方が\nよろしいですよね…？\nそのままでいい？\nそれはそれで…その…", 1, 131601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8324, 2, 0, 2, "あの、兄様…？\nスイカの種を飛ばす\n遊びもあるというのは\n…本当なのでしょうか", 1, 131601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8325, 3, 0, 2, "強くなるための修行は\nまだ始まったばかり\nまずは己の目指すべき\n道を見定めなくては", 1, 131601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8326, 4, 0, 2, "同じ薄着であっても\n黒い天使の姿より…\n気が楽です", 1, 131601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8327, 101, 0, 2, "はい、ただいまー！\n…あっ！\n海の家でのお手伝いの\nくせが、つい…", 4, 131601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8328, 102, 0, 2, "兄様さえよろしければ\nまた一緒に海へ…\nあ…申し訳ありません\n浮ついてしまいました", 8, 131601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8329, 1, 0, 2, "本日もイイコトをして\n清々しい一日を\n過ごしたいものです\nまさに一日一善ですね", 1, 131601, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8330, 2, 0, 2, "お昼時になると\n海の家の料理を\n思い出してしまいます\n焼きそばにかき氷…♪", 1, 131601, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8331, 3, 0, 2, "兄様…よろしければ\n浜辺を歩きませんか？\nきっと、月が\nきれいでしょうから", 1, 131601, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8333, 2, 0, 3, "ふわふわ心地よすぎて\n眠くなってしまいます\n水着姿では\nいけません…ふぁ…", 1, 131601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8334, 3, 0, 3, "なんてよい手触り…\n癖になってしまいます\nふふ、よしよし…♪", 1, 131601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8335, 4, 0, 3, "爽やかな香り…♪\n日焼け止めクリームを\n思い出してしまいます", 1, 131601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8336, 5, 0, 3, "冷たい水で冷えた体が\n温まります…♪\nまるで兄様のようです", 1, 131601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8338, 7, 0, 3, "お腹が空いては\n修行ができません…！\n動きに障りない程度に\n栄養の補給を…！", 1, 131601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8339, 8, 0, 3, "剣術とスイカ割りが\n上達する指南書…\n見つかりませんね…", 1, 131601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8341, 10, 0, 3, "さあ、ともに大海原を\n参りましょう！\nなんて…ふふっ♪", 1, 131601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8342, 11, 0, 3, "まるでおとぎ話の\n登場人物になった\nような心持ちです…♪", 1, 131601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8343, 1, 0, 1, "あたしはなんで水着で\n室内をうろついてるの\nかしら…不思議だわ", 1, 131701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8344, 2, 0, 1, "あ、天井が汚れてる\n仕方ないわね\nひとっ飛びしますか", 1, 131701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8345, 3, 0, 1, "はい、お疲れさま♪\n出迎えの言葉は\nやっぱこれよね♪", 1, 131701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8346, 4, 0, 1, "そういえばあたしって\n羽があるけど\n泳げるのかしら…", 1, 131701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8347, 5, 0, 1, "あいつのために\n浮き輪も用意したし\n抜かりはないわよ", 1, 131701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8348, 101, 0, 1, "海までのガイド計画も\nまとまったわね\nふふっ…楽しみだわ", 4, 131701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8349, 102, 0, 1, "この水着、あいつが\n選んでくれたのよね\n…大事にしないと", 8, 131701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8350, 1, 0, 2, "ネビアは最近どう？\n迷惑かけてない？\n…かけてるのね", 1, 131701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8351, 2, 0, 2, "あんた…部屋の片付け\nくらい自分でしなさい\n散らかってたわよ？\n…片付けておいたけど", 1, 131701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8352, 3, 0, 2, "もしかして暇なの？\nだったら記憶でも\n見せましょうか？\n凄く恥ずかしいやつ♪", 1, 131701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8353, 4, 0, 2, "夏ってこんなに\n暑いのね…\n海にばかり行く\n気持ちが分かったわ", 1, 131701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8354, 101, 0, 2, "あんたの水着って\n新しいの買わないの？\nなんならあたしが\n選んであげるわよ♪", 4, 131701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8355, 102, 0, 2, "気軽に触れ合える\n距離にいる…\nあたしにとって\nとても大事なことよ", 8, 131701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8356, 1, 0, 2, "あらおはよう\n夏の朝は暑いわね\nシャワーでも浴びて\nさっぱりしなさい", 1, 131701, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8357, 2, 0, 2, "お昼は何を食べるの？\nガイド妖精として\n提案しましょうか？", 1, 131701, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8358, 3, 0, 2, "夜更かしは厳禁よ\n無理やり眠らせて\nあげましょうか？", 1, 131701, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8360, 2, 0, 3, "アメスさまだって\nゴロゴロするときは\nあるのよ～", 1, 131701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8361, 3, 0, 3, "…ふふっ♪\n何よ、あたしが\nかわいいものを\n愛でちゃだめなの？", 1, 131701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8362, 4, 0, 3, "こういういい香り…\n塔の中じゃなかなか\n楽しめないのよね", 1, 131701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8363, 5, 0, 3, "水着を着てるのに\n温まるのって\n矛盾してない？\n気にしすぎかしら", 1, 131701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8365, 7, 0, 3, "あんたと行動するなら\n沢山食べられるように\nならなきゃね…！", 1, 131701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8366, 8, 0, 3, "文字を読むのと\nデータを読むのは\nまったく違うわ", 1, 131701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8368, 10, 0, 3, "へぇ、面白いじゃない\nこういう経験、昔じゃ\nできなかったわ", 1, 131701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8369, 11, 0, 3, "面白いクッションね\nところであんた…\nあたしで遊んでない？\n気のせいならいいけど", 1, 131701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8370, 1, 0, 1, "また新しい悪戯\n思いついちゃった♪\n誰に仕掛けようかしら", 1, 118601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8371, 2, 0, 1, "あ～あ、退屈…\nフィオでもからかって\nこようかしら♪", 1, 118601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8372, 3, 0, 1, "ミソギの悪戯って\nレベル高いのよね…\n負けてらんないわ！", 1, 118601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8373, 4, 0, 1, "家事ニガテなのよね～\nコッコロたちには\n感謝しないとね", 1, 118601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8374, 5, 0, 1, "私も魔物料理に\n慣れてきちゃった…\n毒されてるわね…", 1, 118601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8375, 101, 0, 1, "あいつに仕掛ける\n次の悪戯は～…♪\nこれ…ううん…\nやっぱこっちかしら？", 4, 118601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8376, 102, 0, 1, "結局ギルドハウスが\n一番落ち着くわね\n…私たちの家だもの", 8, 118601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8377, 1, 0, 2, "悪戯のコツでも\n教えてあげよっか？\n…え～興味ないの？", 1, 118601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8378, 2, 0, 2, "これからもずーっと\n退屈しない毎日に\nしてあげる♪\n私の悪戯でね♪", 1, 118601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8379, 3, 0, 2, "ちょうど退屈してたの\nたーっぷりと\n構ってもらうわよ♪", 1, 118601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8380, 4, 0, 2, "案内が必要なら\n頼ってくれていいわよ\n一応は私だって\nガイド妖精だし！", 1, 118601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8381, 101, 0, 2, "冒険のガイドなら\n私に任せなさい♪\nある意味忘れられない\n旅にしてあげる♪", 4, 118601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8382, 102, 0, 2, "妖精がいる家は\n栄えるのよ♪　だから\nずっといてあげる\nあんたのそばにね♪", 8, 118601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8383, 1, 0, 2, "きゃははっ！\n寝ぐせついてるわよ？\nこのままにしましょ！\nだって面白いし！", 1, 118601, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8384, 2, 0, 2, "自由に外へ行けるって\nすっごく幸せなことよ\nさ、散歩行きましょ♪", 1, 118601, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8385, 3, 0, 2, "安心していいわ\n寝てるあんたに\n悪戯しないから\n…あんたにはね♪", 1, 118601, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8387, 2, 0, 3, "悪くはないけど…\nあいつの頭の方が\n座り心地いいかも", 1, 118601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8388, 3, 0, 3, "この子を使った\n悪戯は…\n…止めとこっと\nこんなにかわいいし♪", 1, 118601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8389, 4, 0, 3, "このギルドハウスに\nピッタリな匂いね♪\n一言で言えば…\nカオスって感じ？", 1, 118601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8390, 5, 0, 3, "なんていうか…\n心があたたかいわ\n…変な感じ！", 1, 118601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8392, 7, 0, 3, "ガイド妖精にも\n食事の機能があって\nよかったのかも\nしれないわね…もぐ…", 1, 118601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8393, 8, 0, 3, "じれったい物語ね…\nこんなの霧を使えば\n一瞬で終わるのに！", 1, 118601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8395, 1, 0, 1, "わる～い子は\nトゥンヌス漁船に\n乗ってもらおうかな～", 1, 131801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8396, 2, 0, 1, "たまにはファミリーの\nみんなにもお休みを\nあげないとね～☆", 1, 131801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8397, 3, 0, 1, "海辺に素敵な\nバーがあるんだ～\n彼が大人になったら\n一緒に行きたいな♪", 1, 131801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8398, 4, 0, 1, "夏の風を肌で\n感じるの好きかも～\nラーゴはどう？", 1, 131801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8399, 5, 0, 1, "海岸線を\nのんびり散歩もいいね\n砂や貝殻に紛れた\nお宝探しの旅だよ～☆", 1, 131801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8400, 101, 0, 1, "みんなで集まって\nビーチパーティーでも\n開いちゃおっかな～♪", 4, 131801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8401, 102, 0, 1, "さて、この水着…\n彼は気に入って\nくれてるかな～？", 8, 131801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8402, 1, 0, 2, "心ゆくまで夏を\n楽しんじゃお～☆\nほら君も♪", 1, 131801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8403, 2, 0, 2, "水着似合ってるって？\nふふ♪　うれしいこと\n言ってくれるね～☆", 1, 131801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8404, 3, 0, 2, "熱中症には\n気を付けないと\nだめだよ～？", 1, 131801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8405, 4, 0, 2, "海の家ならうちの\nファミリーでやってる\nとこにしよっか☆\n豪遊しちゃお～♪", 1, 131801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8406, 101, 0, 2, "君と一緒なら\nどんな夏になるのか\n楽しみだね～♪", 4, 131801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8407, 102, 0, 2, "君といると子供心を\nくすぐられちゃうな～\n大人だって思いっきり\n遊んでいいんだよね♪", 8, 131801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8408, 1, 0, 2, "さ、今日は何をして\n過ごそっかな～…\nねえ、君は何したい？", 1, 131801, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8409, 2, 0, 2, "ふふ♪\n波音が心地良い\n時間だね～☆", 1, 131801, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8410, 3, 0, 2, "今夜は星空の下で\n乾杯しよっか～☆\n飲み物取ってくるね♪", 1, 131801, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8412, 2, 0, 3, "ここ涼しい～♪\nちょっと一休み\nしていこっか☆", 1, 131801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8413, 3, 0, 3, "こういうのも良いけど\nやっぱりラーゴの方が\nかわいいかな～♪", 1, 131801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8414, 4, 0, 3, "爽やかな香りだね～\nこういう感覚も絵で\n表現してみたいな～", 1, 131801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8415, 5, 0, 3, "続きを描く前に\n原稿を乾かさないとね\nインクがにじむと\n直すの大変なんだよ～", 1, 131801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8417, 7, 0, 3, "今日は水着だし…\n食べるのは控えめに\nしておこっかな", 1, 131801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8418, 8, 0, 3, "話題の作品は\nチェックしないとね～\nあっ、この作家さん\n新刊出すんだ～", 1, 131801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8420, 10, 0, 3, "うちの出店の客引きに\nこういう乗り物系を\n使うのもアリかな～☆", 1, 131801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8421, 11, 0, 3, "これ、背景用の\n参考資料に\n一つ欲しいかも♪", 1, 131801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8422, 1, 0, 1, "ここに帰るのも\n久しぶり…あれから\n変わったところは\nあるかしら？", 1, 181201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8423, 2, 0, 1, "バイトも復帰しなきゃ\nうちのギルドにとって\n食費は重要だもの…！", 1, 181201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8424, 3, 0, 1, "コッコロマ…っ\n…うぅ…\nこの癖は\n直らないのね…", 1, 181201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8425, 4, 0, 1, "この世界はまだまだ\n知らないことだらけね\n『裏世界』とか…", 1, 181201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8426, 5, 0, 1, "必ずみんなを\n助け出してみせるわ\n…もちろん、兄さんも", 1, 181201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8427, 101, 0, 1, "またみんなと\n暮らせるなんて…\n夢じゃ、ないのよね", 4, 181201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8428, 102, 0, 1, "たくさんの人が\n私に優しくしてくれる\n…だからこそ、私も\n頼れる人になりたいわ", 8, 181201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8429, 1, 0, 2, "次はランドソルの\n街の外にも行きたいわ\nあの…よかったらまた\n一緒に回ってくれる？", 1, 181201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8430, 2, 0, 2, "独りぼっちで眠るのは\nやっぱり寂しかったわ\n…みんなで雑魚寝？\nそんなの…絶対やるわ", 1, 181201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8431, 3, 0, 2, "ネビアってば大泣きで\n私を出迎えてくれたの\n…すごく嬉しかったわ", 1, 181201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8432, 4, 0, 2, "この翼、陽に当たると\n本当に綺麗よね…\n自分のだけれど、つい\nずっと見ちゃうもの", 1, 181201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8433, 101, 0, 2, "おにーたんっ♪\n…呼びたくなっただけ\n本当にそれだけよっ？", 4, 181201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8434, 102, 0, 2, "どうしたの？\n…ふふっ、安心して\nこれからもずっと\n私はここにいるわ", 8, 181201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8435, 1, 0, 2, "みんな起きだす時間ね\n街が活気づいてく感じ\n嫌いじゃないわ", 1, 181201, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8436, 2, 0, 2, "【美食殿】のお昼は\nいつも美味しいから\nまた太っちゃいそうね", 1, 181201, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8437, 3, 0, 2, "おやすみなさい\n明日も、その先も\n一緒に冒険しましょう", 1, 181201, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8439, 2, 0, 3, "休憩も大事…よね\nじゃあ遠慮なく…\nふぅ～…♪", 1, 181201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8440, 3, 0, 3, "はぁ…ふわふわ…♪\nこのかわいさには\nもう抗えないわ…", 1, 181201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8441, 4, 0, 3, "落ち着く香り…\nこの匂いを嗅いでると\n帰ってきたなって\n思うわね", 1, 181201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8442, 5, 0, 3, "みんなで野宿した時も\n焚火の明かりを見て\nすごく安心したのよね", 1, 181201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8444, 7, 0, 3, "ペコリーヌさんには\n負けるけど…\n美味しくって手が\n止まらないわ…！", 1, 181201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8445, 8, 0, 3, "プリンセスフォーム…\n一体どういう仕組みで\n発動するのか、文献に\n書いてないかしら…", 1, 181201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8447, 1, 0, 1, "すごーく暑いけど、\nこの暑さをどうやって\n乗り越えるかって\n考えるのも楽しいかも", 1, 131901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8448, 2, 0, 1, "氷で彫刻を作るの？\nそれは楽しそうだけど\nすぐに溶けちゃわ\nないかしら？", 1, 131901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8449, 3, 0, 1, "お日様の下で遊ぶのも\n部屋の中で涼むのも\nどっちも夏ならではの\n楽しみ方なのね", 1, 131901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8450, 4, 0, 1, "生き物の観察日記、\n私も書いてみたいな\nパックのお昼寝日記\nなんてどうかしら？", 1, 131901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8451, 5, 0, 1, "さまぁばけぇしょんは\n計画を立てるもの\nなんだって\n私も挑戦してみるわね", 1, 131901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8452, 101, 0, 1, "この世界のことを\nもっと知って\nアーラム村のみんなに\nお話ししてあげるの", 4, 131901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8453, 102, 0, 1, "いつルグニカに帰るか\nわからないからこそ\n毎日頑張らなきゃね", 8, 131901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8454, 1, 0, 2, "今日はどこに行くの？\n私は、えーと…\nかき氷が食べたいわ！", 1, 131901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8455, 2, 0, 2, "ここにも精霊が\n住んでたりするの？\n…精霊じゃなくて\nがいど妖精？", 1, 131901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8456, 3, 0, 2, "パックったら、\n近所の子にいっぱい\nなでなでされてたわ\nすごーく人気者ね！", 1, 131901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8457, 4, 0, 2, "実は…線香花火の\n特訓をしてたの！\n次は負けないんだから", 1, 131901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8458, 101, 0, 2, "昨日、みんなの\n似顔絵も絵日記に\n描いてみたんだけど…\n笑っちゃダメよ？", 4, 131901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8459, 102, 0, 2, "何気ない時間だって\n後で思い返せば\nずっと忘れない\n素敵な思い出になるの", 8, 131901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8460, 1, 0, 2, "気持ちよく起きて、\nいい一日を過ごしたい\nそんなときには\nラジーオタイソーね！", 1, 131901, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8461, 2, 0, 2, "お昼ごはんの時間ね\n暑さに負けないように\nしっかり食べましょう", 1, 131901, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8462, 3, 0, 2, "外は涼しいみたいだし\n夜の散歩に行ってみる\nのはどうかしら？", 1, 131901, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8464, 2, 0, 3, "遊び疲れたら\nここでひとやすみ…\nふわぁ…\n眠くなっちゃった…", 1, 131901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8465, 3, 0, 3, "細かいところまで\nすごーく丁寧に\n作られてる…", 1, 131901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8466, 4, 0, 3, "わっ、なんだか\nすーっとする香りね\nちょっぴり涼しく\nなったみたい", 1, 131901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8467, 5, 0, 3, "海で遊ぶと\n体が冷えるから\nあったかいのも\nすごーく嬉しい…", 1, 131901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8469, 7, 0, 3, "これも美味しい…！\nペコリーヌが\nどんどん食べちゃう\n気持ちが分かるかも", 1, 131901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8470, 8, 0, 3, "このお話も面白い…！\nあっという間に時間が\n過ぎちゃったわ…\n…ね、寝てません！", 1, 131901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8472, 10, 0, 3, "この子と一緒に海に\nいったら楽しそうね！\nどこに行けば\n会えるのかしら？", 1, 131901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8473, 11, 0, 3, "ふかふかね！\nこうしてると波の音が\n聞こえてきそう…", 1, 131901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8474, 1, 0, 1, "こちらでは水着姿で\n過ごすのも\n珍しくないようですね", 1, 132101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8475, 2, 0, 1, "メイド服ではなく\n姉様と色違いの水着…\nレムは嬉しいです", 1, 132101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8476, 3, 0, 1, "少し室温が高いかも\nしれませんね…\nいくつか氷を配置して\nおきましょうか", 1, 132101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8477, 4, 0, 1, "清涼感があるとはいえ\n鉄球をビーチバレーの\nボールに代用するのは\n危ないでしょうか…", 1, 132101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8478, 5, 0, 1, "ふぅー…ふぅー…\nようやく浮輪が\n膨らみました\n姉様にお渡ししないと", 1, 132101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8479, 101, 0, 1, "海のことをもっと\n教えていただけますか\n…スバルくんと海の\n話をしたいんです", 4, 132101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8480, 102, 0, 1, "もし海に行く機会が\nあればレムにも…\nお声がけくださいね\nぜひ、お願いします", 8, 132101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8481, 1, 0, 2, "何を着ていても\nメイドはメイド\n掃除も料理も\nなんでもござれです", 1, 132101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8482, 2, 0, 2, "スバルくんとあなたは\n兄弟のように\n仲がいいですね\n姉様とレムと一緒です", 1, 132101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8483, 3, 0, 2, "この姿で子どもたちと\n遊ぶのは…さすがに\nはばかられますね\nやはり着替えてきます", 1, 132101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8484, 4, 0, 2, "ナンパ…というものが\n海にはあるそうです\nスバルくんがとても\n警戒していました", 1, 132101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8485, 101, 0, 2, "魚釣りでしたら\nレムもご一緒します\n氷漬けの魚の備蓄を\n補充したかったので", 4, 132101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8486, 102, 0, 2, "子どもたちと砂遊びの\n約束をしているんです\nあなたもご一緒に\nいかがですか？", 8, 132101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8487, 1, 0, 2, "ラジーオタイソーは\nもうされましたか？\n体がほぐれますよ", 1, 132101, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8488, 2, 0, 2, "お昼ですね。では\n姉様に焼きそばを…\nもちろん皆様の分も\n用意してありますよ", 1, 132101, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8489, 3, 0, 2, "熱帯夜であっても\nお休みの際はお腹を\n冷やさないように\n気をつけてくださいね", 1, 132101, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8491, 2, 0, 3, "とても気持ちよさそう\nです…！\nさっそく姉様に\nお教えしないと", 1, 132101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8492, 3, 0, 3, "季節に合わせた\n着せ替えができれば\n子どもたちも\n楽しめそうです", 1, 132101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8493, 4, 0, 3, "ふむふむ…この香りは\n疲れを癒やしてくれ\nますね。エミリア様に\nお持ちします", 1, 132101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8494, 5, 0, 3, "身も心もほぐれます\n思っていたよりも\n体が冷えていたみたい\nですね…ふう…", 1, 132101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8496, 7, 0, 3, "もぐもぐ…んうっ！？\nこのすっぱさは…\n梅干し、ですか…\nくせになりますね…！", 1, 132101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8497, 8, 0, 3, "海に関する詩文なども\nあるのでしょうか…\n気になります", 1, 132101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8499, 10, 0, 3, "地竜とは違った\n乗り心地です…\n本物にも乗ってみたい\nものですね", 1, 132101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8500, 11, 0, 3, "この大きさで\n本物の貝であれば…\nどんな料理にするか\n想像が膨らみます", 1, 132101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8501, 1, 0, 1, "これがこの世界の\n居住環境か…\n面白い構造だね", 1, 132001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8502, 2, 0, 1, "暇を持て余していてね\n誰か話し相手に\nなってくれないかな", 1, 132001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8503, 3, 0, 1, "目新しいものが\nたくさんあるね\n胸が躍るよ", 1, 132001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8504, 4, 0, 1, "頑丈そうな建物だ\nここなら彼女たちが\n暴れても大丈夫かな？", 1, 132001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8505, 5, 0, 1, "力が制限されていて\n良かった面もある\n君も吐かずにすんだし", 1, 132001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8506, 101, 0, 1, "悪さなんてするわけ\nないじゃないか\n傷つくなぁ", 4, 132001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8507, 102, 0, 1, "普段と違う格好で\n歩き回るのも\n悪くない経験だ", 8, 132001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8508, 1, 0, 2, "ボクに聞きたいことが\nあるなら遠慮なく\n質問するといい", 1, 132001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8509, 2, 0, 2, "気軽に魔女に\n話しかけるなんて\n君は物好きだね", 1, 132001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8510, 3, 0, 2, "この世界においては\nボクは異物だからね\n大人しくしているさ", 1, 132001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8511, 4, 0, 2, "ボクの反応を\n見たいのかい？\n君の好奇心には\n恐れ入るよ", 1, 132001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8512, 101, 0, 2, "ボクの手を借りたい？\nもちろん構わないさ\n対価はいただくけどね", 4, 132001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8513, 102, 0, 2, "ボクと海を見たい？\nふふ、魅力的な\nお誘いだね", 8, 132001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8514, 1, 0, 2, "お目覚めかな？\nさて、君の見た夢を\n教えてくれないかな？", 1, 132001, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8515, 2, 0, 2, "食後のお茶を\n一緒にどうだろうか？\nさあ、席に座ってよ", 1, 132001, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8516, 3, 0, 2, "おや、お休みかな？\n魔女の前で無防備だね\n…無論、何もしないさ", 1, 132001, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8518, 2, 0, 3, "こういう椅子は\n茶会には向かないね\nつい気が緩んでしまう", 1, 132001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8519, 3, 0, 3, "こういうものを好きな\n女の子がいいのかな？\n君の嗜好を知りたいな", 1, 132001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8520, 4, 0, 3, "いい香りだ\nこれにはどんな\n効能があるんだろう？", 1, 132001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8521, 5, 0, 3, "火の点け方に\nボクのいた世界との\n違いはないのかな？", 1, 132001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8523, 7, 0, 3, "これもまた未知の味だ\n存分に味わわせて\nもらうとしよう", 1, 132001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8524, 8, 0, 3, "素晴らしいね\nここには叡智が\n詰まっている", 1, 132001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8526, 10, 0, 3, "魔物を模倣した\nぬいぐるみか…\n実物を見たくなるね", 1, 132001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8527, 11, 0, 3, "いい気持ちだ…ボクと\nしたことが、何も考え\nられなくなりそうだ", 1, 132001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8528, 103, 0, 1, "願わずにいられません\nどうかこちらの\n世界には…悲劇が\n生まれませんようにと", 12, 109701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8529, 103, 0, 2, "食事のご希望があれば\nなんなりと。お腹が\nいっぱいになるまで\n沢山ご用意しますね", 12, 109701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8530, 103, 0, 1, "あの男、これからも\n観察が必要ね\nラムの目の届く範囲に\n置く必要があるわ", 12, 109801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8531, 103, 0, 2, "素直なところは\nあの男の美点ね\n癪だけど、偶然にも\nラムと同じだわ", 12, 109801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8532, 103, 0, 1, "迷惑かけちゃったけど\n雪遊びは楽しかったわ\nまたやりたい…なんて\n言ったら怒られるかも", 12, 109901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8533, 103, 0, 2, "数えきれないくらいの\n思い出を、みんなで\n作りましょう！", 12, 109901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8534, 1, 0, 1, "わわっ、今すごく\nいい香りがしたけど…\n誰の香水かな\n教えてほしいな…♪", 1, 132201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8535, 2, 0, 1, "錬金術用の釜、\n置くならどこかな？\n自分専用の錬金釜、\n憧れちゃうよね♪", 1, 132201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8536, 3, 0, 1, "この匂い…誰か\n唐揚げ食べたみたい\nうぅ…お腹空いて\nきちゃうよ", 1, 132201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8537, 4, 0, 1, "次の新曲のタイトルは\n『恋の錬金術』！\nうーん、ちょっと\nベタ過ぎるかな…", 1, 132201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8538, 5, 0, 1, "あ、錬金の素材\n切らしてたんだ…\n彼を誘って採集に\n行ってこようかな", 1, 132201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8539, 101, 0, 1, "瞬時に着替えができる\n薬とかできないかな？\n舞台で衣装チェンジ！\nなーんて、楽しそう♪", 4, 132201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8540, 102, 0, 1, "彼が遊びに来た後は\n部屋の空気がほんのり\n彼の匂いなんだ…\nなんだか幸せだな♪", 8, 132201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8541, 1, 0, 2, "今日もバイト？\nそれならこれ飲んで\n私のお手製ポーション\nがんばってきてね！", 1, 132201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8542, 2, 0, 2, "新しくできたカレー屋\n行ってみない？\n時々スパイスの香りが\n流れてくるんだよ", 1, 132201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8543, 3, 0, 2, "私のフィギュア\nそっくりでしょ！\nルイちゃんが作って\nくれたんだ", 1, 132201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8544, 4, 0, 2, "あれ、シャンプー\n変えた？　私と同じの\nだよ！　どうしよう\nドキドキしちゃう…", 1, 132201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8545, 101, 0, 2, "いらっしゃいませ♪\n何を作りますか？\nなーんてね。錬金堂で\nまた一日店長したいな", 4, 132201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8546, 102, 0, 2, "クンクン…彼の\nハンカチ…いい匂い♪\n私だけの秘密のお守り\nにしてるんだ", 8, 132201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8547, 1, 0, 2, "ねえねえ、天気も\nいいし一緒に錬金の\n素材集めに行こうよ！", 1, 132201, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8548, 2, 0, 2, "お昼ご飯、用意したよ\nあはは、さすがに\nランチは錬成しないよ\n私の手作りだよ♪", 1, 132201, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8549, 3, 0, 2, "今日も一日お疲れさま\nこれ飲んでゆっくり\n休んで。薬じゃないよ\nホットミルクだよ？", 1, 132201, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8551, 2, 0, 3, "昨日、遅くまで\n錬金術の勉強してた\nから眠くって…\nちょっと一休みっと", 1, 132201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8552, 3, 0, 3, "この子にちょっとだけ\n香水かけて、と…\nんー！　だっこした時\nすっごくいい匂い♪", 1, 132201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8553, 4, 0, 3, "花みたいな、\nお菓子みたいな\nいい香り。気持ちが\n穏やかになるね♪", 1, 132201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8554, 5, 0, 3, "外でもこの暖かさを\n感じられる道具、\n作れたりしないかな？", 1, 132201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8556, 7, 0, 3, "素材集めに行くと\nお腹が空くんだよね\nイロハちゃんも\nこの味好きそう", 1, 132201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8557, 8, 0, 3, "錬金術にはたくさんの\n知識が必要だからね\nさあ、今日も調合書を\n読まなくちゃ", 1, 132201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8559, 1, 0, 1, "ここが騎士さんたちの\n住んでる場所…！\n粗相がないように\n気をつけなきゃ", 1, 127701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8560, 2, 0, 1, "ジオ・テオゴニアの\nみんな、私がいない間\nちゃんとお仕事\nしてればいいけど…", 1, 127701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8561, 3, 0, 1, "コッコロさんも\n従者として長いって\n聞いたような…\nあの年ですごい…！", 1, 127701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8562, 4, 0, 1, "自分がもてなされる\n側になるのって\nやっぱりちょっと\n落ち着かないかも…", 1, 127701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8563, 5, 0, 1, "今度のお休みの日は\n何しよう…？\nあっ、最近できたお店\nヴルムと行こうかな？", 1, 127701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8564, 101, 0, 1, "お買い物したら\nかわいいからって\nたくさんおまけして\nもらっちゃった…♪", 4, 127701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8565, 102, 0, 1, "ここにいると、つい\nぼんやりしちゃう…\nこれも騎士さんの力…\nなのかな", 8, 127701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8566, 1, 0, 2, "お手伝いできることは\nありませんか？\nお世話でもなんでも\n任せてくださいっ", 1, 127701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8567, 2, 0, 2, "ランドソルに初めて\n来たときは驚きました\n砂糖の山と思ったら…\nあんなに寒いなんて！", 1, 127701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8568, 3, 0, 2, "あの、ヴルムを\n見ませんでしたか？\nもう…あの子ってば\nまた仕事をサボって…", 1, 127701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8569, 4, 0, 2, "家具や家が壊れても\n食べられないなんて\nなんだかもったいない\nですね", 1, 127701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8570, 101, 0, 2, "とっておきの紅茶を\n手に入れたんです\n一杯いかがですか？\nその、日頃のお礼です", 4, 127701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8571, 102, 0, 2, "頑張ってて偉いって…\nほ、褒めてもなんにも\n出ませんからね！？\n…えへへ♪", 8, 127701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8572, 1, 0, 2, "おはようございます！\nモーニングティーは\nいかがですか？\n私、準備しますねっ", 1, 127701, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8573, 2, 0, 2, "ライラエルさまも\nお昼寝はお好きです\nけど…だからって\n寝すぎはダメですよ！", 1, 127701, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8574, 3, 0, 2, "おやすみなさい\nくれぐれも寝不足には\n気をつけてくださいね", 1, 127701, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8576, 2, 0, 3, "しっかりしないと…\nメイドの私が\n気を抜くなんて…\nそんな…ふぅ…", 1, 127701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8577, 3, 0, 3, "わぁ、かわいい…♪\nってダメダメ！\nメイドなんだから\n落ち着かないと…！", 1, 127701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8578, 4, 0, 3, "いい匂い…♪\nこの香り、\nライラエルさまも\n好きそうです！", 1, 127701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8579, 5, 0, 3, "ランドソルの環境に\n触れると、この光の\nありがたみが\n身に沁みますね…", 1, 127701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8581, 7, 0, 3, "甘くない食べ物が\n一般的なのも\n慣れてきました\nあむ…おいひぃ…♪", 1, 127701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8582, 8, 0, 3, "ランドソルの文字、\n私たちのところと\nちょっと違いますね…\n覚えられるかなぁ", 1, 127701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8584, 1, 0, 1, "甘い匂いがしない…\nジオ・テオゴニアとは\n別の世界なんですね", 1, 127801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8585, 2, 0, 1, "掃除めんど…\nん…このくらいで\nまー十分でしょ", 1, 127801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8586, 3, 0, 1, "そろそろ食事の\n用意もしなきゃ…\nあーやる事多い…", 1, 127801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8587, 4, 0, 1, "秘密の隠れ場所、\nまた姉さんにバレた…\n次のを探さないと", 1, 127801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8588, 5, 0, 1, "知らない間に仕事が\n終わったり…しないか\nしてくれたらいいのに", 1, 127801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8589, 101, 0, 1, "きちんともてなすには\n騎士さんの好みも\n覚えてかないと…", 4, 127801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8590, 102, 0, 1, "ご主人サマに\nご奉仕します～♪\n…キャラじゃないか\nやめやめ、恥ずかし…", 8, 127801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8591, 1, 0, 2, "居心地いいっすね…\n今日からここに\n住んでもいいですか", 1, 127801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8592, 2, 0, 2, "あの、ちょっと姉さん\n怒らせちゃったんで\n匿ってくれません？", 1, 127801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8593, 3, 0, 2, "羽と尻尾が格好いい？\n意外と邪魔ですよ\n仰向けで寝にくいし", 1, 127801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8594, 4, 0, 2, "ワークライフバランス\nっていい言葉ですね\nぜひライフ多めで", 1, 127801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8595, 101, 0, 2, "騎士さんって\n働き者ですね\nそのままあたしの\n仕事も…いえなんでも", 4, 127801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8596, 102, 0, 2, "熱心に見てても\n尻尾は触らせて\nあげませんってば\n角？　角なら…まぁ", 8, 127801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8597, 1, 0, 2, "寝ても寝ても\n寝足りない…ふぁ…\nあー…眠たい…", 1, 127801, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8598, 2, 0, 2, "アフタヌーンティーの\n準備は得意ですよ\nつまみ食いとか\n…つまみ食いとか", 1, 127801, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8599, 3, 0, 2, "よく休んでください\n布団は干しといたんで\n…姉さんが", 1, 127801, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8601, 2, 0, 3, "ふー…\nこのままずーっと\nここに居たい…", 1, 127801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8602, 3, 0, 3, "あんたも一緒に\nサボっちゃうか～\n…なんて", 1, 127801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8603, 4, 0, 3, "落ち着く匂い…\n騎士さんの好み\nなんですかね", 1, 127801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8604, 5, 0, 3, "こういうとこの\n掃除って面倒だし…\n今度でいっか", 1, 127801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8606, 7, 0, 3, "ご飯の前に\nこんな食べたら\n姉さんきっと怒るな…", 1, 127801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8607, 8, 0, 3, "食に関する本が\nやけに多い…\nお国柄ですかね", 1, 127801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8609, 1, 0, 1, "すんすん…\nお兄さんの匂いが\nぷんぷんする～♡\nザコくっさぁ♡", 1, 130201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8610, 2, 0, 1, "ぴこーん！\nいひひっ、新作メカの\n面白いアイデア、\n思いついちった～♡", 1, 130201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8611, 3, 0, 1, "メカもいじりたいし\n服もアクセもほしー…\nあ～、おこづかい\n足んないよ～！", 1, 130201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8612, 4, 0, 1, "お勉強でもしよっかな\nザコお兄さんには\n絶対わかんない\nよーなやつ♡", 1, 130201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8613, 5, 0, 1, "お兄さんの部屋って\nどこどこぉ？\nメカの実験室に\n使えそうだよね～♡", 1, 130201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8614, 101, 0, 1, "次のデートコースは\nどんなのかなー？\n…いや、むしろ次は\nぼくが決めちゃうっ？", 4, 130201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8615, 102, 0, 1, "強奪計画は念入りに♡\nお兄さんのハートも\n全力でザコザコにする\n計画、立てないとね♡", 8, 130201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8616, 1, 0, 2, "ねー、ヒマヒマヒマ～\nかまってかまってー♡\nどっか連れてけー♡", 1, 130201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8617, 2, 0, 2, "今日はこっちで\nご飯食べたいなー♡\n…帰っても５日連続の\nバカナ姉カレーだし…", 1, 130201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8618, 3, 0, 2, "依頼行くならぼくも\n連れてってよー♡\nお兄さんのザコっぷり\n見ててあげる～♡", 1, 130201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8619, 4, 0, 2, "お兄さんはぼくより\nオトナだもんね～♡\nじゃあオモテナシ、\n期待してるから～♡", 1, 130201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8620, 101, 0, 2, "ヤマ姉になんかお土産\n持って帰ろうかなー？\nザコお兄さん、選ぶの\n付きあって～♡", 4, 130201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8621, 102, 0, 2, "遊びにいくときは\nザコお兄さんも\n誘ってあげるよ♡\nどうせヒマでしょ～？", 8, 130201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8622, 1, 0, 2, "ふぁ～、ねむぅ～…\n夜更かしぶん寝るから\nあとで起こして～♡\nあ、変なこと禁止～♡", 1, 130201, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8623, 2, 0, 2, "おなかすいたぁ～\nなんか食べ行こ♡\n甘いのおごって～♡", 1, 130201, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8624, 3, 0, 2, "えー、もう寝る時間？\nじゃあ…添い寝して\nあげよっか？\nなーんて、ウッソー♡", 1, 130201, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8626, 2, 0, 3, "おお～ふわっふわ～♪\nバンディシャーク号の\n座席に使いたーい！", 1, 130201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8627, 3, 0, 3, "カワイイ～♡\n改造したらもっと\nカワイくなるかな～？", 1, 130201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8628, 4, 0, 3, "くんくん…いい匂い♡\nザコお兄さんって\nこーゆー香りが\n好きなのかなぁ～", 1, 130201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8629, 5, 0, 3, "はー、あったかーい♡\nバンディシャーク号\nにも欲しいかもー♪", 1, 130201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8631, 7, 0, 3, "食べれるときに\n食べるのが大事って\nヤマ姉言ってたしね～", 1, 130201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8632, 8, 0, 3, "あ、この本読んだよ♡\n参考文献リストが\nしっかりしてて\n勉強になったなー♪", 1, 130201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8634, 1, 0, 1, "部屋は多少ゴチャつい\nてる方が落ち着くのさ\nバンディシャーク号\nみたいにね♪", 1, 130001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8635, 2, 0, 1, "呆けてるのもいいが\nちいと物足りないねぇ\n麦しゅわもブドウ朱も\n無いなんてさ", 1, 130001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8636, 3, 0, 1, "この家セキュリティが\nなってないねぇ…\n盗賊目線でちょっくら\n指南でもしてやるか", 1, 130001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8637, 4, 0, 1, "不思議だねぇ\nここで飲むと、酔いが\n回るのが早い気が\nするよ…ヒック", 1, 130001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8638, 5, 0, 1, "ふむ…\nクルマが壊れた時は\nアジト代わりに\n使えそうだ♪", 1, 130001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8639, 101, 0, 1, "カカッ、いい部屋だ！\n妹を迎える準備は\nできてるみたいだねぇ", 4, 130001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8640, 102, 0, 1, "…っと、油断したら\n落ち着いちまうね\nボーヤの甘っちょろい\n雰囲気のせいだよ", 8, 130001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8641, 1, 0, 2, "さすがにこの家から\n盗みはしないさ\nいずれは妹たちの\n共有財産になるからね", 1, 130001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8642, 2, 0, 2, "ボーヤ、暇なら\n博打でもやるかい？\nアタシが負けたら\n掃除でもしてやるよ", 1, 130001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8643, 3, 0, 2, "立派な家じゃないか！\n一国一城の主とは\nちょっとボーヤのこと\n見くびってたかもねぇ", 1, 130001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8644, 4, 0, 2, "かわいい義弟のためだ\nアタシが盗ってきた\nお宝をくれてやるよ\n好きな場所に飾りな！", 1, 130001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8645, 101, 0, 2, "妹と結ばれる前に\n他の女との関係は\n清算しとくように\nわかったね、ボーヤ？", 4, 130001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8646, 102, 0, 2, "お、ヤシオーリかい？\nフフン、義弟からの\nお酌とあっちゃあ\n断れないねぇ♪", 8, 130001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8647, 1, 0, 2, "うう…清々しい朝だが\n頭がいてぇ…\nちっと昨日は\n飲みすぎたか…？", 1, 130001, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8648, 2, 0, 2, "ほらこれ、ワカナに\n作らせた弁当だ\nこれで昼からも\nバリバリやりな！", 1, 130001, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8649, 3, 0, 2, "まだまだ夜はこれから\n…と言いたいとこだが\n早寝して立派に育ちな\nそれが妹たちの為だよ", 1, 130001, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8651, 2, 0, 3, "中々いい座り心地だが\n盗賊が腑抜ける訳には\nいかないね…が…\nもう少しだけ座るか…", 1, 130001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8652, 3, 0, 3, "こういうのの中に\n実はレアなブツが\n隠されてるとか\nありがちだねぇ", 1, 130001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8653, 4, 0, 3, "ふうん、ボーヤは\nこういう匂いが\n好きなのかい？\n妹たちに教えてやろう", 1, 130001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8654, 5, 0, 3, "体を温めんにゃ\n酵露をカッとやるのが\n一番だが、これも存外\n悪くないねぇ…♪", 1, 130001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8656, 7, 0, 3, "ガツガツ…\nん、悪くねェ味だ\nこれにヤシオーリでも\nありゃ文句はないね", 1, 130001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8657, 8, 0, 3, "本にも載ってない\nレアもんのお宝か…\nフッ、想像しただけで\n血沸き肉躍るねぇ…！", 1, 130001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8659, 1, 0, 1, "今日の買い物は\nいつものお野菜と…\nあっ、確か特売が\nあったはず…！", 1, 130101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8660, 2, 0, 1, "お掃除、お掃除…♪\nきゅっきゅっ…\nよいしょ、っと…", 1, 130101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8661, 3, 0, 1, "洗濯ものも\nたまらないうちに\nやっちゃわないと…", 1, 130101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8662, 4, 0, 1, "夕飯のメニュー…\n何にしよう？\n期限の早い食材は…", 1, 130101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8663, 5, 0, 1, "色々お菓子も\n作ってみたいけど…\n予算…足りるかなぁ…", 1, 130101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8664, 101, 0, 1, "騎士さん…どんな\n料理が好きなんだろ？\n前に作ったコロッケは\n評判よかったけど…", 4, 130101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8665, 102, 0, 1, "騎士さんと\nおしゃべりすると、\n胸があたたかくなる…\nなんでだろ…？", 8, 130101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8666, 1, 0, 2, "あっ、騎士さん\nどうかしましたか？\n御用ならいつでも…♪", 1, 130101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8667, 2, 0, 2, "削っても削っても\n家計簿は赤字…\n騎士さ～ん…\n助けてください～！", 1, 130101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8668, 3, 0, 2, "手が空いてたら\n日用品の買い出し、\n手伝ってもらっても\nいいですか…？", 1, 130101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8669, 4, 0, 2, "やっと一息つけそう…\nあっ、わたし\nお茶を淹れてきますね", 1, 130101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8670, 101, 0, 2, "困りごとがあったら\n言ってくださいね\nわたし…騎士さんの\n力になりたいです", 4, 130101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8671, 102, 0, 2, "助けてもらった分は\n必ず返しますから…\nそれまでわたしのこと\n見ててくださいね…♪", 8, 130101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8672, 1, 0, 2, "忘れ物はないですか？\n今日のお仕事も\n頑張りましょうね…！", 1, 130101, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8673, 2, 0, 2, "お昼ご飯できてますよ\n大したものじゃなくて\n恐縮ですけど…", 1, 130101, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8674, 3, 0, 2, "夜更かしはだめですよ\nわたしも家事を\n終わらせたら寝ないと", 1, 130101, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8676, 2, 0, 3, "これ…割れ物防止の\nクッションに\n使えるかも…？", 1, 130101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8677, 3, 0, 3, "こういうのいいなぁ…\nけど…フブキちゃんに\n絶対改造されちゃう…", 1, 130101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8678, 4, 0, 3, "いい匂い…でも\n切れかけてるかも？\nえっと補充するには…", 1, 130101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8679, 5, 0, 3, "あったかい…\nちょっとだけなら\n休んでも…いいよね", 1, 130101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8681, 7, 0, 3, "これがあったら\nもうお料理を\n作らなくて済むかも？", 1, 130101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8682, 8, 0, 3, "新しい節約レシピは…\nあ、この草って\n食べられるんだ…", 1, 130101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8684, 1, 0, 1, "愛溢れるランドソルの\nクリスマス…ですか\nいけませんね、つい\n胸が躍ってしまいます", 1, 132801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8685, 2, 0, 1, "ツリーの飾りと\nわたしの髪…\nくるくるしていて\n似ているかも…くすっ", 1, 132801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8686, 3, 0, 1, "この姿で傲慢な\nわたしになったら…\nいつもより怖がらせて\nしまうでしょうね…", 1, 132801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8687, 4, 0, 1, "悪魔として、人を\nドキドキさせなくては\nい、イオから教わった\n…投げキスの練習を…", 1, 132801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8688, 5, 0, 1, "ジオ・テオゴニアの\nお菓子は、ツリーの\n飾りに向いていそう\nですね…ふふっ", 1, 132801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8689, 101, 0, 1, "騎士のギルドハウス…\nクリスマスの時期も\n暖かいのですね…", 4, 132801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8690, 102, 0, 1, "城の皆と過ごす宴も\n楽しいですが…\n騎士と二人の宴も\n幸せです…", 8, 132801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8691, 1, 0, 2, "騎士、あなたの\nもてなしに感謝します\nクリスマスの何たるか\n教えてくださいね", 1, 132801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8692, 2, 0, 2, "この黒い羽は\nどう手入れすれば？\nイオなら知っている\nでしょうか…", 1, 132801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8693, 3, 0, 2, "城の皆も連れてきたい\nほど、クリスマスは\n楽しい行事ですね\nお土産も迷います", 1, 132801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8694, 4, 0, 2, "クリスマスの晩餐に\n招待されたのですが…\nその…辛いものは\nありませんよね…？", 1, 132801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8695, 101, 0, 2, "クリスマスと言わず…\nいつでも星に祈って\nいますよ？　騎士達に\n幸せがあるように、と", 4, 132801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8696, 102, 0, 2, "悪魔として騎士を\nドキドキさせる…？\nそ、そのようなこと\nわたしには…うぅ…", 8, 132801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8697, 1, 0, 2, "朝は寒いですね…\n雪…積もっている\nでしょうか？", 1, 132801, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8698, 2, 0, 2, "ふあぁ…いけません\n公務の滞在先で\nお昼寝など…", 1, 132801, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8699, 3, 0, 2, "夜は悪魔として\n夢を見せましょう\n騎士はどんな夢が\n見たいですか？", 1, 132801, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8701, 2, 0, 3, "ふぅ…落ち着きます…\n家でゆっくりと過ごす\nクリスマスも\n良いものですね", 1, 132801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8702, 3, 0, 3, "リンドのプレゼントに\nぬいぐるみは…？\n…子ども扱いすると\n落ち込むでしょうか…", 1, 132801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8703, 4, 0, 3, "暖かいお部屋に\nこの香りを漂わせたら\nふふっ…素敵な時間を\n過ごせそうです", 1, 132801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8704, 5, 0, 3, "これがあれば、\n寒い冬も安心ですね\n暖かくて…このまま\n眠ってしまいそうです", 1, 132801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8706, 7, 0, 3, "悪魔の力を使ったら\nお腹が空いてきて\nしまって…\n恥ずかしいです…", 1, 132801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8707, 8, 0, 3, "クリスマスには多くの\n伝承があるのですね…\n天使の伝承まで\nありましたし…", 1, 132801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8709, 14, 0, 3, "まぁ…！\nよくできていますね\nサプライズプレゼント\n…でしょうか。ふふ♪", 1, 132803, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8710, 15, 0, 3, "ど、どうでしょう…？\n親善交流の催しに\nなりそうでしょうか…", 1, 132804, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8711, 1, 0, 1, "ギルドハウスって\nあったかぁ～い♪\n心がぽかぽかするの！", 1, 132901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8712, 2, 0, 1, "クリスマスの悪魔の\nお仕事はないわね！\nだってここのみんな\nいい子なんですもの♪", 1, 132901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8713, 3, 0, 1, "みんなちゃんと\n寝られてるかしら？\n夜更かしせずに、\nいい夢見てね♪", 1, 132901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8714, 4, 0, 1, "毎回、来年が\n楽しみになるような\n素敵なクリスマスを\n過ごしたいわね♪", 1, 132901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8715, 5, 0, 1, "訳ありケーキを見ると\n刑務作業を思い出すわ\n…あれも貴重な体験\nだったのかしら…？", 1, 132901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8716, 101, 0, 1, "ロマンチックな\nクリスマスに備えて\n今のうちに少女漫画で\n予習しておかないと！", 4, 132901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8717, 102, 0, 1, "ほっぺにチューなら\nもう一回しても…\nや、やっぱり無理！\n恥ずかしいわ～！", 8, 132901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8718, 1, 0, 2, "またみんなで\nセッションしたいわね\nカラーベルの練習した\n成果を見せたいの♪", 1, 132901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8719, 2, 0, 2, "君はサンタさんに\nどんなプレゼントを\n頼んでいたのかしら？\n気になるわ～！", 1, 132901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8720, 3, 0, 2, "愛が壊れても、\n何度でも治してあげる\n私たちの間に、\n愛の尽きる日はないわ", 1, 132901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8721, 4, 0, 2, "冬休みって短いようで\n長く感じちゃうわよね\n学校のみんなと\n早く会いたいな～♪", 1, 132901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8722, 101, 0, 2, "愛をテーマにした\n冬休みの作文の宿題、\n君の分を読むのが\n今から楽しみね♪", 4, 132901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8723, 102, 0, 2, "よかったらまた\n私をハニーって呼んで\nほしいの。ダーリン♪", 8, 132901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8724, 1, 0, 2, "おはよう！\n冬の朝は冷えるから\n風邪を引かないように\n対策しましょうね", 1, 132901, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8725, 2, 0, 2, "お昼ご飯は一緒に\n食べに行きましょう！\nクリスマス限定の\nランチコースよ♪", 1, 132901, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8726, 3, 0, 2, "ねえねえ、キャンドル\nイルミネーションを\n見に行きましょうよ！\nロマンチックよ～♪", 1, 132901, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8728, 2, 0, 3, "暖かいクリスマスを\n過ごすのに必須よね♪\nふふ、柔らかぁい！", 1, 132901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8729, 3, 0, 3, "どの子もかわいい～♪\nクリスマスツリーの\n下で、一緒にお祝い\nしてもらいたいな♪", 1, 132901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8730, 4, 0, 3, "なんだか胸の奥が\n温かくなる匂いね～\nリラックスしちゃう♪", 1, 132901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8731, 5, 0, 3, "あったかぁい♪\nあ、でも夜になったら\nサンタさんが入れる\nように、火を消すのよ", 1, 132901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8733, 7, 0, 3, "寒い冬だからこそ\nしっかり食べて\n備えておかなきゃ！", 1, 132901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8734, 8, 0, 3, "『聖夜の６時間』…？\n気になる小説だけど…\n勝手に読んじゃ悪い\nわよね…ガマンよ…！", 1, 132901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8736, 14, 0, 3, "きゃっ…！\nこんな不思議な本が\nあったのね～\nちょっと面白いかも♪", 1, 132901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8737, 15, 0, 3, "もふもふ、ふわふわで\nあったかくて\n気持ちいい～…♪", 1, 132901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8738, 1, 0, 1, "冬といえばサマー！\n…って言うけど夏は\n何をＰＲするんだろ\nメルクリゾート…", 1, 129501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8739, 2, 0, 1, "奨学金の道は遠いな…\nあまりに遠すぎて\n最近忘れてたまである", 1, 129501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8740, 3, 0, 1, "暇だし鏡でも見よっと\nはー、今日も顔が良…\nげ、若干むくんでる？\nはー、今日終わった", 1, 129501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8741, 4, 0, 1, "水着…お姉ちゃんと\n貸し借りするんだけど\nこれって少数派姉妹？", 1, 129501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8742, 5, 0, 1, "ちゅむ、ツムギちゃん\n今日も可愛いねデュフ\n…なんで推し語りの声\nキモくなるんだろ私…", 1, 129501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8743, 101, 0, 1, "あれ…なんで私\n水着のまま部屋の中\nうろついてるんだっけ\n…こわ！　てか寒っ！", 4, 129501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8744, 102, 0, 1, "先輩よく友達と海に\n行くって言ってたけど\n…え、誰とだろ？\nまあ誰でもいいけどさ", 8, 129501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8745, 1, 0, 2, "始まる…\nチエルの夏が始まる…\nあ冬でしたっけ？\nまどっちでもいいです", 1, 129501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8746, 2, 0, 2, "こないだトミヱ先輩\n壁に喧嘩売ってました\n相変わらず元気ですね", 1, 129501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8747, 3, 0, 2, "ユニ先輩にお芝居の\n出来を聞かれたんで\n可愛いって答えました\n嘘は言ってません", 1, 129501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8748, 4, 0, 2, "クロエ先輩の腹筋\nかっちょいーですよね\nさりげ触りたいけど\nあいつ隙ないんですよ", 1, 129501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8749, 101, 0, 2, "先輩もうカメラ係は\nやらないんです？\nチエルはやってますよ\n最高の被写体を常に", 4, 129501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8750, 102, 0, 2, "メリゾでカップル割の\n企画あるらしいんです\nちなみに先輩は…\nあ、いえ、何でも…", 8, 129501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8751, 1, 0, 2, "朝ごはんは抜いても\n朝ちぇるだけは\n抜いちゃ駄目ですよ", 1, 129501, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8752, 2, 0, 2, "お昼なに食べるか\n悩んでます？\nおごってくれるなら\nいい店教えますけど", 1, 129501, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8753, 3, 0, 2, "この時間帯になると\nユニ先輩がごそごそ\n蠢きだすんですよね…", 1, 129501, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8755, 2, 0, 3, "世界の美少女が…\nこんな格好で\nくつろいでたら…\nまずい…事案になる", 1, 129501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8756, 3, 0, 3, "え、この子って\nウォータープルーフ？\nメリゾにも連れて\nいけちゃうカンジ？", 1, 129501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8757, 4, 0, 3, "はぁ～、いい香り…\nそういえばメリゾの\nスタッフルームも\n『贅』な匂いしてた…", 1, 129501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8758, 5, 0, 3, "薄着で暖まるとか…\n冷静になっちゃ\n駄目なやつですよね\nこれ…", 1, 129501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8760, 7, 0, 3, "おにぎり１個は\nちぇるに換算すると…\n２５ちぇるくらい？", 1, 129501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8761, 8, 0, 3, "あれー？　この辞書\nまだちぇるの項目\n載ってないんだ？", 1, 129501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8763, 10, 0, 3, "何これ…地味に楽しい\nあとたぶん\nクロエ先輩が好きそう", 1, 129501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8764, 11, 0, 3, "これは良きですねー\n自宅でメリゾ気分とか\nユニ先輩もにっこり", 1, 129501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8765, 1, 0, 1, "ここが騎士氏の\nお住まいですか\nふむ…地縛霊は\nいなさそうですね", 1, 133001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8766, 2, 0, 1, "ここは土足厳禁では\nないみたいですね\nま、私には関係ないか\n足、透っけ透けなので", 1, 133001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8767, 3, 0, 1, "ランドソルの裏路地\nあそこは最高ですね\n暗くてじめじめしてて\n癒されるー", 1, 133001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8768, 4, 0, 1, "っ！\n今ラップ音が…？\nまさか幽霊？\nやだ、怖い", 1, 133001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8769, 5, 0, 1, "獣人族にエルフ族…\nなるほど騎士氏が\n幽霊程度では驚かない\n訳です…ちぇ", 1, 133001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8770, 101, 0, 1, "今日の深夜\n騎士氏が起きるまで\n枕元でずーっと\n立っててみようかな", 4, 133001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8771, 102, 0, 1, "騎士氏の驚く顔を\n見ると胸が高鳴る…\n変ですね、心臓なんて\n動いてないのに", 8, 133001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8772, 1, 0, 2, "安心して下さい\n勝手にすり抜けて\n騎士氏の部屋は\n覗きませんよ…多分", 1, 133001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8773, 2, 0, 2, "なんですか騎士氏\n怖い話でも聞きます？\nじゃあいきますね…\n今貴方の前に幽霊がっ", 1, 133001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8774, 3, 0, 2, "この家…幽霊が\n寄ってくる間取りに\nなってますね\nまあ、嘘ですけど", 1, 133001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8775, 4, 0, 2, "料理はしませんよ\n指きったら痛いので\nえ…透ければいい？\n……天才ですか？", 1, 133001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8776, 101, 0, 2, "お散歩ですか？\n深夜なら付き合います\nあ、やっぱり断ります\n幽霊出たら怖いので", 4, 133001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8777, 102, 0, 2, "騎士氏の部屋のセンス\n好きですよ…もっと\nじめじめしていれば\nなおよし、ですが", 8, 133001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8778, 1, 0, 2, "レイスになってから\nあまり言わなくなった\n挨拶第一位はこちら…\nおはようございます", 1, 133001, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8779, 2, 0, 2, "騎士氏。午後からも\n頑張って下さいね\n私は日陰で休みます\nでは、おつです", 1, 133001, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8780, 3, 0, 2, "夜も更けてきましたね\nここからがレイスの\n本領発揮…では早速\nう　ら　め　し　や～", 1, 133001, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8782, 2, 0, 3, "中々座り心地が\nよろしいですね\nブロッケンに跨る方が\n安心はしますが", 1, 133001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8783, 3, 0, 3, "知ってますよ\nどうせ貴方も\n夜中になったら\n動き回るんでしょ？", 1, 133001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8784, 4, 0, 3, "すう…はあ…\nすごく落ち着く香り\n…落ち着き過ぎて\n成仏しそうでした", 1, 133001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8785, 5, 0, 3, "温度は感じませんが\nなぜかほっこり\n温かい気持ちです\nなんででしょうね？", 1, 133001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8787, 7, 0, 3, "もぐもぐ…\n食べ物は基本好きです\n消化の方法は…\n企業秘密で", 1, 133001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8788, 8, 0, 3, "苦手なんですよね\nホラー小説\nだって幽霊とか\n怖いじゃないですか", 1, 133001, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8790, 1, 0, 1, "この得も言われぬ\n高揚感…やはり\n正月はいいものだな！", 1, 133201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8791, 2, 0, 1, "む、いかんな\n気付くと黒豆と\n栗きんとんばかり\n食べてしまう…", 1, 133201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8792, 3, 0, 1, "ギルドの連中にも\nお年玉を渡してみるか\n少しは士気が上がる\nかもしれないな！", 1, 133201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8793, 4, 0, 1, "正月くらいはのんびり\n羽を伸ばすとしよう\n適切な休暇も軍人の\n大事な責務だからな", 1, 133201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8794, 5, 0, 1, "正月で浮かれすぎた\n者も多いはずだ\n念のため街を見回りに\n行くとしよう", 1, 133201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8795, 101, 0, 1, "む、このおせち料理は\n絶品だな！\nあいつの分も\nとっておいてやろう", 4, 133201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8796, 102, 0, 1, "今年の抱負は…\nそうだな、あいつと\nさらに高みを目指す\nこれでいこう", 8, 133201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8797, 1, 0, 2, "えっ、お年玉！\n…い、いや、私は\n子どもではないからな\nえ、遠慮させてもらう", 1, 133201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8798, 2, 0, 2, "凧あげすごろく\nこま回し、たとえ遊び\nでも手は抜かんぞ\nさあ、勝負だ！", 1, 133201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8799, 3, 0, 2, "そのきな粉餅も\nうまそうだな…\n私のあんこと交換\nしないか？", 1, 133201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8800, 4, 0, 2, "貴公、正月料理を\n食べ過ぎてないか？\nじ、実は私も少々、な", 1, 133201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8801, 101, 0, 2, "貴公、お参りに行くぞ\n何？　外が寒いだと？\n仕方ない、手ぐらいは\nつないでやろう", 4, 133201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8802, 102, 0, 2, "貴公と新たな年を\n迎えられてうれしいぞ\n今年も一つ\nよろしくたのむ！", 8, 133201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8803, 1, 0, 2, "起きろ貴公。早朝なら\n参拝客も少ないはず\n今のうちにお参りを\n済ませよう", 1, 133201, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8804, 2, 0, 2, "昼食に餅を焼いたぞ\nのどに詰まらせない\nようにゆっくり噛んで\n食べるんだ", 1, 133201, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8805, 3, 0, 2, "夜ふかしはあまり\n褒められたものでは\nないが…正月くらいは\n目をつむろう", 1, 133201, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8807, 2, 0, 3, "これに座って飲む\n甘酒は格別だな", 1, 133201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8808, 3, 0, 3, "せっかくの正月だし\nこいつにも着物を\n着せてやりたいな", 1, 133201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8809, 4, 0, 3, "これは梅の香り…？\nふふ、なかなか\n風流ではないか", 1, 133201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8810, 5, 0, 3, "冬の寒さが堪えるな…\nここでしっかりと\n暖をとるとしよう", 1, 133201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8812, 7, 0, 3, "正月はのんびり\nしたいからな、勝手に\n食事が出てくるのは\n本当に助かる", 1, 133201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8813, 8, 0, 3, "ふむ、これが世界の\n新年のお祝いか\nなかなか興味深いな", 1, 133201, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8815, 1, 0, 1, "一年の計は元旦にあり\n計画を立てて、\n有意義な一年を\n過ごしましょ", 1, 133501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8816, 2, 0, 1, "年末年始はどうしても\n出費が増えちゃうし\nしばらくはきっちり\n節約しなきゃ…！", 1, 133501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8817, 3, 0, 1, "タタミにフトン、\nトーゴク文化の内装…\nランドソルでももっと\n流行らないかしら", 1, 133501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8818, 4, 0, 1, "子どもたちが凧を\n手作りして遊んでたの\n偉いわよね、みんな…", 1, 133501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8819, 5, 0, 1, "お年玉をあげる側に\nなって、分かる苦労…\n大人になるって\nこういうことかしら", 1, 133501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8820, 101, 0, 1, "一応実家にも\n顔を出さないと…\nよかったらだけど、\nあんたも来る？", 4, 133501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8821, 102, 0, 1, "毎日一緒にいて、\nささやかな幸せを\n同じように感じて…\nそれだけで十分ね", 8, 133501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8822, 1, 0, 2, "着物姿、もっと\nじっくり見たいの？\nさすがに照れるわよ、\nもう…", 1, 133501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8823, 2, 0, 2, "整理整頓、できてる？\nもし散らかってるなら\nお正月休みのうちに\n片づけちゃいましょ", 1, 133501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8824, 3, 0, 2, "今年もみんなが無事に\n過ごせますように…\nほら、一緒に\nお祈りしましょ", 1, 133501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8825, 4, 0, 2, "みんな、あんたと\n遊ぶのが楽しみだって\n子どもに好かれる\nタイプなのよね", 1, 133501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8826, 101, 0, 2, "あんたの運勢は大吉！\nラッキーパーソンは\n金髪の幼なじみよ\n…なんてねっ", 4, 133501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8827, 102, 0, 2, "初夢は実現するのよね\nそれじゃ、あたしは\nあんたと…んんっ\nなんでもないわ", 8, 133501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8828, 1, 0, 2, "外が寒いと、その分\n目も冴えるわよね\nこのまま朝の散歩に\n行こうかしら", 1, 133501, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8829, 2, 0, 2, "お正月休みでも、\n食べてばっかじゃダメ\nしっかり動いて\n体を引き締めましょ", 1, 133501, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8830, 3, 0, 2, "ちゃんとあったかく\nして寝るのよ\nお正月から\n風邪ひかないようにね", 1, 133501, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8832, 2, 0, 3, "着物、しわに\nなっちゃいそう…\nでもそれ以上に、\n心地よくって…はぁ", 1, 133501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8833, 3, 0, 3, "門松みたいに\nこの子も飾ってみる？\n運勢がよくなるかも♪", 1, 133501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8834, 4, 0, 3, "この匂い、もしかして\nトーゴクのお香？\nワビサビを感じるわ", 1, 133501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8835, 5, 0, 3, "ふぅ、あったかい…\n冷えた体に\n染み渡ってくるわ…", 1, 133501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8837, 7, 0, 3, "んっ、おいしい！\nおせちばっかじゃ\nやっぱり飽きちゃう\nものね…", 1, 133501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8838, 8, 0, 3, "験担ぎとか縁起物とか\n調べてみると\nいろいろあって\n面白いのよね～", 1, 133501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8840, 1, 0, 1, "ふーん、いい家ね\nアタシの巣に\nしちゃおうかしら？", 1, 133601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8841, 2, 0, 1, "お姉ちゃん…\n早く会いたいなぁ…", 1, 133601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8842, 3, 0, 1, "メドゥシアナも\n気に入るかしら？\n今度連れてこよっと！", 1, 133601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8843, 4, 0, 1, "お邪魔してるだけ\nなのも悪いわよね…\nお返しはしないとね\n…ネズミ退治とか？", 1, 133601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8844, 5, 0, 1, "何か平和よね\nランドソルって…\nアタシ的にはもう少し\n荒っぽくてもいいわ", 1, 133601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8845, 101, 0, 1, "ランドソルも\n楽しい街よね…\n案内してくれる\nアイツのおかげ？", 4, 133601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8846, 102, 0, 1, "アタシ、アイツに\n頼りすぎてるかも…？\nで、でも！　断らない\nアイツも問題だわ！", 8, 133601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8847, 1, 0, 2, "ねぇ、つまんなーい！\nもっとアタシを\n楽しませなさいよねー", 1, 133601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8848, 2, 0, 2, "この家悪くないけど…\nもっとムシムシ\nジメジメにできない？", 1, 133601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8849, 3, 0, 2, "何よ？\n用もないのに声掛けた\n…とか言ったら\n容赦しないわよ？", 1, 133601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8850, 4, 0, 2, "少し部屋が寂しいわね\nそうだ、アンタを\n石にして家具に…\n冗談よ、ふふんっ♪", 1, 133601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8851, 101, 0, 2, "ねぇ、もう少し\n話していかない？\nアンタの話なら…\n特別に聞いてあげるわ", 4, 133601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8852, 102, 0, 2, "い、いつも\nあ、あ、ありが…\nうぅ、何でもないー！", 8, 133601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8853, 1, 0, 2, "やっと起きたわね？\n寝ぼすけなんだから！\n尻尾でひっぱたいて\n起こそうかと思ったわ", 1, 133601, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8854, 2, 0, 2, "人捜しは日の高い\nうちに限るわ！\nさぁ、メドゥシアナ！\n全速力で出発進行よ!", 1, 133601, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8855, 3, 0, 2, "アタシ…\n子どもじゃないもん…\n眠くなんか…\nないんだからぁ…", 1, 133601, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8857, 2, 0, 3, "ふわふわであったかい\nつい、とぐろを\n巻きたくなるわね…", 1, 133601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8858, 3, 0, 3, "アンタ、こんな\nヤワな体じゃ駄目よ\nウロコくらい\n生やしたらどうなの？", 1, 133601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8859, 4, 0, 3, "くんくん…\n…ふん\n悪くないじゃない\n…くんくんくんくん…", 1, 133601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8860, 5, 0, 3, "あったかいの大好き！\nジオ・ゲヘナでも\nよく日向ぼっこ\nしたもんだわ…ふわぁ", 1, 133601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8862, 7, 0, 3, "お姉ちゃんを捜すなら\n…もぐ\n体力つけないとね\n…もぐもぐ", 1, 133601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8863, 8, 0, 3, "何よ？　たまには\n勉強くらいするわよ\nお姉ちゃんに\n笑われちゃうもの", 1, 133601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8865, 1, 0, 1, "この装いと力…\n今の世界との\n整合性を上手いこと\n合わせておかないとだ", 1, 133701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8866, 2, 0, 1, "この格好…開発してた\n当時のまんまだ\nサイズもそのまま♪\n体型維持も完璧っ！", 1, 133701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8867, 3, 0, 1, "あとで装備の手入れも\nやっとかないと\n…剣なら研ぐだけで\n終わるんだけどなぁ", 1, 133701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8868, 4, 0, 1, "自分の原点を\n振り返ってみるのも\nたまには必要だね", 1, 133701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8869, 5, 0, 1, "完璧じゃなくても\n今できる全力を尽くす\nその事に意味がある…\nそうだよね、少年", 1, 133701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8870, 101, 0, 1, "始まりのあの日、\nそして向かうべき道…\nいつも忘れずにいよう", 4, 133701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8871, 102, 0, 1, "誰かを救うために\n許容される犠牲なんて\nあっちゃいけない…\n全部、助けてやる", 8, 133701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8872, 1, 0, 2, "おっと少年、\n何か御用かな？\nキミに関わる事なら\n最優先で対応するよ", 1, 133701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8873, 2, 0, 2, "困った事があれば\n頼ってもらって\n構わないからね！\nむしろもっと頼って！", 1, 133701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8874, 3, 0, 2, "そうだ、時間あるなら\n武器のメンテを\n手伝ってくれないかな\n数が多くてさ～", 1, 133701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8875, 4, 0, 2, "遠距離戦の心得、\n少年にもレクチャー\nしてあげよっか？", 1, 133701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8876, 101, 0, 2, "無茶しないで、って\n言ってもするだろうし\nせめてアタシの手が\n届く範囲で頼むよ", 4, 133701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8877, 102, 0, 2, "虚実入り混じる\nこの世界でキミが…\n進むべきルートを\n照らしてくれたんだ", 8, 133701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8878, 1, 0, 2, "グッモーニン！\n朝だよ少年、今日も\n張り切っていこう！", 1, 133701, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8879, 2, 0, 2, "クレープの代わりに\nガレットを焼いたんだ\nこの味で少年を\n唸らせてみせよう！", 1, 133701, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8880, 3, 0, 2, "もうこんな時間だ\n遅くならないうちに\n作業を切り上げようか\n少年も早く寝なよ？", 1, 133701, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8882, 2, 0, 3, "あえて緊張を解くのも\n戦いには必要だし…\nそうそう…大事大事…", 1, 133701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8883, 3, 0, 3, "愛着を抱かせる事を\n意図したデザインだね\n心理的効果の観点から\n分析してみようか…", 1, 133701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8884, 4, 0, 3, "香りは記憶と密接に\n結びつく事がある…\n懐かしい匂いだね…", 1, 133701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8885, 5, 0, 3, "癒されるな…\n何がこの感情変化を\n引き起こすんだろう？", 1, 133701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8887, 7, 0, 3, "食の偉大さを感じるな\n戦闘糧食ってやっぱり\n味気ないしなぁ…", 1, 133701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8888, 8, 0, 3, "実用ばかりじゃなく\n詩や物語ってのも\n人には必要だよね", 1, 133701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8890, 1, 0, 1, "この場所の開発版も\n探せばあるかもな\nさて、どんなデザイン\nだったのやら…", 1, 133901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8891, 2, 0, 1, "今まで数々の波乱に\n身を投じたが…やはり\n波乱にはこの姿が一番\n似合いだよ☆", 1, 133901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8892, 3, 0, 1, "むっ…この角に羽と\n尻尾は家具とぶつかり\nやすいな。兎の時は\n気にならなかったが…", 1, 133901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8893, 4, 0, 1, "いつもより闘争を\n求める気分になるな\n初心に帰るとは\nこのことか…？", 1, 133901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8894, 5, 0, 1, "未調整上等☆\nそれを上手く使い\nこなしてこその\nワタシだろう？", 1, 133901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8895, 101, 0, 1, "坊やとできそうな\n愉しいことは…\nふむ、多すぎて迷うな", 4, 133901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8896, 102, 0, 1, "時折、坊やの居心地の\nよさに絆されそうに\nなってしまうな\nワタシらしくもない…", 8, 133901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8897, 1, 0, 2, "どうだ、おろした\n髪型も艶やかだろう？\nそんじょそこらの\n賞賛では満足せんぞ☆", 1, 133901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8898, 2, 0, 2, "外に出るぞ！\n共に退屈を殺しに\n行こうじゃないか☆", 1, 133901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8899, 3, 0, 2, "角と翼も触りたい？\nワタシの虚数魔境に\n挑むというなら\n止めはせんよ☆", 1, 133901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8900, 4, 0, 2, "なんだ、困りごとか？\nクリスティーナさんが\n何でも聞いてやろう☆\n解決するかは別だがな", 1, 133901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8901, 101, 0, 2, "力が欲しいか？\nならば呼ぶがいい！\nワタシの名を☆", 4, 133901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8902, 102, 0, 2, "悪魔との契約は絶対\nなのだそうだが…\nどうだ、坊や\n一つ契ってみるか？", 8, 133901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8903, 1, 0, 2, "まだ眠い？　甘えるな\n寝首を掻かれたく\nなければ飛び起きて\nこい…☆", 1, 133901, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8904, 2, 0, 2, "昼食の準備を\nしてやろう。\n毒見はいらんぞ？\n安心するといい！", 1, 133901, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8905, 3, 0, 2, "坊やは寝る時間だ\n寝ないのならば…\n大人の時間に\n付き合ってもらうか☆", 1, 133901, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8906, 1, 0, 3, "角と翼、尻尾の\nコンディションまで\n完璧だな☆", 1, 133901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8907, 2, 0, 3, "なかなかのフワモコ\n具合だが…ううむ…\n翼と尻尾が邪魔だな", 1, 133901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8908, 3, 0, 3, "うーむ、角と尻尾を\n追加して…さらに槍を\n持たせた方が見栄えが\nよさそうだ", 1, 133901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8909, 4, 0, 3, "いい香りだな\n血と砂塵の臭いで\n上書きするまでの\n間に合わせに最適だ☆", 1, 133901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8910, 5, 0, 3, "生温いな\nもっと焦がれるほどの\n熱量が欲しいところだ", 1, 133901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8911, 6, 0, 3, "普段はないものが\n生えているのだから、\n運動でしっかり感覚を\n掴んでおかなければな", 1, 133901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8912, 7, 0, 3, "腹が減っては戦は…\nと言うからな\nしっかり食べて\nおくとしようか☆", 1, 133901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8913, 8, 0, 3, "『家庭を築くための\n絶対条件』…？\nふむ…興味がある\nタイトルだ", 1, 133901, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8915, 1, 0, 1, "奇妙な感覚です\nまるであの頃の自分に\n変装しているかのよう", 1, 133801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8916, 2, 0, 1, "グレゴール？\nおっと、そうでした\n今は昔の姿になって\n離れていましたね", 1, 133801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8917, 3, 0, 1, "このアバターでいると\n妙に子どもたちからの\n視線を感じますね…", 1, 133801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8918, 4, 0, 1, "リソースの割り振りが\n変わっただけなのに\n随分と身体が軽く\n感じますね", 1, 133801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8919, 5, 0, 1, "普段グレゴールに\n任せている分身の管理\n自分でやるには\nいささか煩雑ですね…", 1, 133801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8920, 101, 0, 1, "長老に頼まれて描いた\n妖精の絵…いったい\n何に使ったのでしょう", 4, 133801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8921, 102, 0, 1, "私自身をモデルにした\n完全なるNPCたち\nそれが私の分身です", 8, 133801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8922, 1, 0, 2, "このアバターが\n気になるのですか？\n貴方はつくづく\n妖精に縁がありますね", 1, 133801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8923, 2, 0, 2, "ちょうどよかった\nグレゴールを見かけ\nませんでしたか？", 1, 133801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8924, 3, 0, 2, "あの翅は飾りでは\nありません。ただ…\nグレゴールに乗って\n飛ぶ方が楽なだけです", 1, 133801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8925, 4, 0, 2, "妖精とは本来神話に\nおけるトリックスター\n運命をかき回すことが\n存在意義なのです", 1, 133801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8926, 101, 0, 2, "変貌大妃の権能との\n相性を考えなければ\nこの姿こそが私たちの\n最強の姿です", 4, 133801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8927, 102, 0, 2, "変化とは一種の喪失\nだからこそ得られる物\nがある…貴方のそれも\n大切にするのですよ", 8, 133801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8928, 1, 0, 2, "マサキからの定時連絡\nが来ませんね…心配は\n不要ですが、様子を\n見に行きましょうか", 1, 133801, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8929, 2, 0, 2, "昼食の時間ですね\nグレゴールのために\nカロリーの高い物を\n食べに行きましょう", 1, 133801, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8930, 3, 0, 2, "夜更かしはダメですよ\n悪いスパイが暗躍して\nいる時間帯ですからね", 1, 133801, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8932, 2, 0, 3, "ふむ、座り心地は中々\nですが…私にはやはり\n玉座の方があいますね", 1, 133801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8933, 3, 0, 3, "ふふ、いいでしょう\n我が配下に加えます\n存分に働きなさい", 1, 133801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8934, 4, 0, 3, "時折晶からしていた\nあの甘い匂いは何の\nアロマだったのか…", 1, 133801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8935, 5, 0, 3, "燃え盛る炎もこうして\n暖炉の中なら安全です\nクリスもあの衝動を\n管理さえできれば…", 1, 133801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8937, 7, 0, 3, "そういえば真那が\n獄中で手持無沙汰に\nひたすらおにぎりを\n食べていましたね…", 1, 133801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8938, 8, 0, 3, "これは、妖精学の本…\n長老の蔵書から借りて\n読みふけったものです", 1, 133801, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8940, 1, 0, 1, "どんな詩を\n作ろうかしら…？\n…大体、暗く\nなっちゃうけど…", 1, 134301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8941, 2, 0, 1, "唱喚術の練習…\nここでやったら\n迷惑よね…", 1, 134301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8942, 3, 0, 1, "チカちゃんとの\nデュエット…\n足をひっぱってない…\nかしら…？", 1, 134301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8943, 4, 0, 1, "唱喚士の修行…\n滝に打たれるとか…\n崖を登るとか…\nなくて、よかった…", 1, 134301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8944, 5, 0, 1, "精霊さん、肉じゃが\n食べる、かしら…？\n魔力だけだと…\n飽きちゃいそうだし…", 1, 134301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8945, 101, 0, 1, "精霊さんのお世話に\n慣れたぶん…\nあのひとのお世話も\n上手にできる…はず？", 4, 134301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8946, 102, 0, 1, "これからも、歌うわ…\nこの声が、彼の力に\nなる、限り──", 8, 134301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8947, 1, 0, 2, "この服…踊り子さんに\n間違われちゃって…\nあ…お、お触りしちゃ\nだめよ…？", 1, 134301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8948, 2, 0, 2, "あなたはどんな\n精霊が…好き…？\n唱喚…してみたいわ…", 1, 134301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8949, 3, 0, 2, "あんまり白い服って…\n着ないの…その…\nこ、ころんだりして…\n汚すから…", 1, 134301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8950, 4, 0, 2, "じ、実はね…\nこの体の模様…筆で\n書いてもらうたび…\nくすぐったい…の…", 1, 134301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8951, 101, 0, 2, "精霊さん…あなたと\n遊びたいみたい…\nや、やっぱり…\nパパ…だから…？", 4, 134301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8952, 102, 0, 2, "あなたを歌にしたら…\n…ふふっ、だめね\n一曲じゃ…あなたを\n表現できない、わ…", 8, 134301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8953, 1, 0, 2, "朝の修行は…\nうとうと…し…ちゃ…\n……（かくん）\n……あぅっ？", 1, 134301, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8954, 2, 0, 2, "せ、精霊さんと…\nかくれんぼ中なの…\nうぅ…見つからない…\n日が暮れちゃいそう…", 1, 134301, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8955, 3, 0, 2, "精霊さん、寝たから…\nふふっ…ここからは\nパパとふたりの時間…\n…ね？", 1, 134301, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8957, 2, 0, 3, "ふぅ…修行と育児は…\nちょっとだけ…\n休憩…ふぁ…", 1, 134301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8958, 3, 0, 3, "あなたとも契約…\n…できる、かしら？\nふふっ…", 1, 134301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8959, 4, 0, 3, "いい香りに包まれたら\n精神統一も\nはかどりそう…♪", 1, 134301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8960, 5, 0, 3, "あたたかい場所…\nここで作詞したら…\n穏やかな詩が\n書ける、かも…", 1, 134301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8962, 7, 0, 3, "精霊さんのお世話を\nしていると…\nお腹、空いちゃって…", 1, 134301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8963, 8, 0, 3, "ふふっ…♪\n色んな楽譜…\n集めるの、楽しい…♪", 1, 134301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8965, 103, 0, 1, "けんぞくまだかな？\nはやく会いたいなー\nヴァンピィちゃん\nずっと待ってるからね", 12, 122301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8966, 103, 0, 2, "けんぞくのこと\nずっとずっと守るよ！\nだからいつまでも\n一緒にいてね", 12, 122301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8967, 1, 0, 1, "私の歌や想いを\n誰かに託せる…\nこれほどうれしい\nことはありません", 1, 134401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8968, 2, 0, 1, "コッコロさんの\n包容力はすごいです\nあんなふうに私も\n人を包み込めたら…", 1, 134401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8969, 3, 0, 1, "最近のボイトレは\nランファさんと一緒に\nやるんです。お互いに\n切磋琢磨しますよ！", 1, 134401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8970, 4, 0, 1, "先輩として相応しい\n立ち振る舞いを\n心がけたいですね\nしっかりしなきゃ…！", 1, 134401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8971, 5, 0, 1, "ランファさんが\n踊ったら映えるかも…\nアイドルにも興味を\n持ってもらえないかな", 1, 134401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8972, 101, 0, 1, "支え導くために\n寄り添って考える…\nあの人からたくさん\n教わったことでしたね", 4, 134401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8973, 102, 0, 1, "いつだってあなたに\n恥じることのない\n姿をお見せしたいです", 8, 134401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8974, 1, 0, 2, "あなたの手は温かくて\n触れていると\n落ち着きますね…♪", 1, 134401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8975, 2, 0, 2, "里のみんなから修行の\n差し入れを頂きました\nよろしければ、一緒に\n食べましょう♪", 1, 134401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8976, 3, 0, 2, "ノゾミにもお土産を\n用意したいですね\n里のお土産選び、\n付き合ってください♪", 1, 134401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8977, 4, 0, 2, "精霊たちも、あなたが\n大好きみたいです♪\n優しさが伝わって\nいるんでしょうね", 1, 134401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8978, 101, 0, 2, "特訓が終わったら、\n二人でどこかに\n出かけましょうか♪", 4, 134401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8979, 102, 0, 2, "ひゃ…す、すみません\n肌の模様を\nなぞられると、\nくすぐったくて…", 8, 134401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8980, 1, 0, 2, "おはようございます\n精霊たちと一緒に、\n今日も頑張ります\n応援してくださいね♪", 1, 134401, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8981, 2, 0, 2, "お昼の時間です\n今日も精いっぱい\n修行を頑張りましょう", 1, 134401, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8982, 3, 0, 2, "もうすっかり夜ですね\n眠るまで子守唄を\n歌ってあげますよ？\nふふ♪", 1, 134401, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8983, 2, 0, 3, "無理せず休憩するのも\n大切ですよね\nふかふか～…ふふっ♪", 1, 134401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8984, 3, 0, 3, "今日はこの子に\n歌の特訓を聞いて\nもらいましょう♪", 1, 134401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8985, 4, 0, 3, "素敵な香り…\nふふ、精霊たちも\n気に入ったみたいです", 1, 134401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8986, 5, 0, 3, "あったかい…\nこの衣装、やっぱり\n少し冷えやすいものね", 1, 134401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8987, 7, 0, 3, "特訓の後のご飯は\n美味しいですね♪\nこれでまた頑張れます", 1, 134401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8988, 8, 0, 3, "唱喚士についての\n書籍はないみたい\nいつか私がここに\n楽譜を入れたいな", 1, 134401, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8992, 1, 0, 1, "ギルドハウスの平和も\n任せてください\n何が来たって\n守ってみせますよ！", 1, 134601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8993, 2, 0, 1, "このギルドハウスも\nいつの間にか出撃台に\n改造されてたり\nするんでしょうか…？", 1, 134601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8994, 3, 0, 1, "戦いを経験してこそ\n実感できますね\n平和はいいもの、\n友達は素晴らしいもの", 1, 134601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8995, 4, 0, 1, "次はランドソルで\n巨大ロボットが\nブームになったり？\n…さすがにないか", 1, 134601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8996, 5, 0, 1, "改めて見るとこの服…\nか、体の線とか\n結構目立ってるかも…", 1, 134601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8997, 101, 0, 1, "団長さんって\n知らない人とも\n話せる系ですよね\nコツはいったい…", 4, 134601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8998, 102, 0, 1, "ＢＢ団、世界を救う！\nまだ信じられないけど\n本当にあった出来事\nなんですよね…！", 8, 134601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8999, 1, 0, 2, "パイロットも\n視力が大事なんです\n弓使いの経験が\n生きました！", 1, 134601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9000, 2, 0, 2, "ギガンティック\nマイフレンドくん、\n出撃！\nあ、ただの妄想です", 1, 134601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9001, 3, 0, 2, "コクピットから\n降りたあとに残る\nふわふわ感…ちょっと\n癖になりませんか？", 1, 134601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9002, 4, 0, 2, "ひえっ、敵襲！？\nあっすみません、\nちょっと\n寝ぼけてたかも…", 1, 134601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9003, 101, 0, 2, "共に戦場を駆け抜け\nＢＢ団の絆もより強く\nこれからも結束を\n強めましょうね！", 4, 134601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9004, 102, 0, 2, "同じコクピットに\n乗ったのに比べれば、\nスキンシップくらい…\nや、やっぱ緊張します", 8, 134601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9005, 1, 0, 2, "おはようございます！\n今日もがんばりますっ\n…クローチェさんを\n見習ってみました…", 1, 134601, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9006, 2, 0, 2, "お昼ご飯の時間ですね\nがんばって他の人\n誘ってみようかな…", 1, 134601, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9007, 3, 0, 2, "夜の見張りは\n任せてください\n一人きりの夜も\n慣れてますから！", 1, 134601, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9009, 2, 0, 3, "休めるときに\n休んでおかなくちゃ\nいつ何が起こるか\n分かりませんからね", 1, 134601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9010, 3, 0, 3, "師匠もこういうの\n好きだったのかな…\nなんだかしんみり\nしちゃいます", 1, 134601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9011, 4, 0, 3, "香りでリラックス…\n余裕のない時でも\nこういう安らぎは\n大切ですよね", 1, 134601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9012, 5, 0, 3, "あったかい…\nこれはきっと友情と\n同じぬくもり…", 1, 134601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9014, 7, 0, 3, "新鮮なご飯、感動…！\n師匠たちの世界だと\nご飯は保存食ばかり\nでしたからね…", 1, 134601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9015, 8, 0, 3, "操縦マニュアル、\nしっかり読んで\n性能を引き出さなきゃ", 1, 134601, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9017, 1, 0, 1, "屋内で飛べなくても\nここなら平気ですっ\n助けてくれる人が\nたくさんいますからっ", 1, 134501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9018, 2, 0, 1, "新ボディになってから\n人や物が前より身近に\n感じられて、わたし、\n幸せですっ♪", 1, 134501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9019, 3, 0, 1, "配達以外にも何か\nお仕事したいですっ！\nハグで遊覧飛行とか、\n喜んでもらえますか？", 1, 134501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9020, 4, 0, 1, "むむむ～…\n文字を書くのって\n難しいですね～っ\n練習しなくちゃっ！", 1, 134501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9021, 5, 0, 1, "簡易点検、よしっ！\nポポおじいさんに\n点検の仕方を教わって\n今日も絶好調ですっ", 1, 134501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9022, 101, 0, 1, "明日は北の空、\n明後日は南の空っ♪\nえへへっ、見せたい\n景色がいっぱいですっ", 4, 134501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9023, 102, 0, 1, "ついナイトさんを\n探しちゃいます…\nわたし、寂しんぼさん\nだったんでしょうかっ", 8, 134501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9024, 1, 0, 2, "どんなところでも\nひとっ飛びっ♪\nわたしに任せて\nくださいねっ！", 1, 134501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9025, 2, 0, 2, "まだ知らない機能が\nエアリアルには\n隠されてるかもっ…\nワクワクしますねっ！", 1, 134501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9026, 3, 0, 2, "ひゃんっ！　…あはは\nエアリアルになっても\nケーブルに触られると\nくすぐったいですねっ", 1, 134501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9027, 4, 0, 2, "ヴォルガーノさんに\nそっくりの子猫さんに\n最近触れたんですっ！\nかわいかったな～っ♪", 1, 134501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9028, 101, 0, 2, "ハグしてくれるん\nですかっ？\nえへへっ、ぎゅーっ♪", 4, 134501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9029, 102, 0, 2, "ナイトさんと一緒に\nいるだけで、胸が\nぽかぽか～って\nあったかいですっ♪", 8, 134501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9030, 1, 0, 2, "おはようございますっ\n目覚まし飛行は\nいかがでしょうかっ？", 1, 134501, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9031, 2, 0, 2, "お昼ですよっ！\nいっぱい補給して\nまたフルパワーで\nがんばりましょうっ♪", 1, 134501, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9032, 3, 0, 2, "眠れない夜はわたしに\n任せてくださいっ！\n充填さえあればずっと\n一緒にいられますよっ", 1, 134501, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9034, 2, 0, 3, "歩きづらいので\nこういう休憩ポイント\nって助かります～っ", 1, 134501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9035, 3, 0, 3, "スラスターを付けたら\nこの子たちも空を\n自由に飛べたり\nするんでしょうかっ？", 1, 134501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9036, 4, 0, 3, "あっ、これに似た\n香りを近くのお店で\n検知したことが\nありますっ♪", 1, 134501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9037, 5, 0, 3, "高熱源反応を検知っ\nわたしのスラスターも\n熱量の高さなら\nかなりものですよっ♪", 1, 134501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9039, 7, 0, 3, "もぐもぐもぐ…っ\n食べたぶんはあとで\n真空パックにして\nお返ししますねっ", 1, 134501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9040, 8, 0, 3, "本を読むのって\n楽しいですねっ♪\n前はびりびりーって\nしちゃってたのでっ", 1, 134501, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9042, 1, 0, 1, "なかなかの住処ですね\nこの私の別邸にして\nあげてもいいですよ♪", 1, 134701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9043, 2, 0, 1, "はぁ…いつになったら\nすべての神力が\n戻るのでしょう？", 1, 134701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9044, 3, 0, 1, "ここも悪くないですが\n昔私が作った庭園は、\nもっと良い場所でした\n焼けちゃいましたが…", 1, 134701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9045, 4, 0, 1, "うう…\nおなかが空きました…\nお米が食べたいです…", 1, 134701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9046, 5, 0, 1, "ここは平和ですねえ…\nジオ・ゲヘナも\nこのくらい穏やかなら\n住みやすいのですが", 1, 134701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9047, 101, 0, 1, "騎士さんのおかげで\n神力が戻ってきました\n良い働きですよ♪", 4, 134701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9048, 102, 0, 1, "騎士さんのいる場所が\n今の私がいるべき場所\nなのかもしれませんね", 8, 134701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9049, 1, 0, 2, "頭がたかーい！\n…ですよっ！", 1, 134701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9050, 2, 0, 2, "ほーちゃんと呼ぶ権利\n安売りはしませんよ？\n特別ですから！", 1, 134701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9051, 3, 0, 2, "私とおしゃべりを？\nまあ、いいでしょう♪\n何を話しましょうか？\nこの羽の秘密とか？", 1, 134701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9052, 4, 0, 2, "小鳥扱いは\nやめなさーい！\nこれでも神様ですよ？", 1, 134701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9053, 101, 0, 2, "き、気安く触るのは…\n…い、いえ、\n騎士さんなら…まぁ…", 4, 134701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9054, 102, 0, 2, "絢爛豪華な絆！\nこの私と結べるなんて\n光栄でしょう？", 8, 134701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9055, 1, 0, 2, "ふわぁぁ～\nおはようごじゃいます\n…んむむ", 1, 134701, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9056, 2, 0, 2, "お昼ご飯は何を…\nえっ？　私？\nそそんな゛あ゛！", 1, 134701, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9057, 3, 0, 2, "あの…騎士さん？\n添い寝する権利とか…\nほしくないですか？", 1, 134701, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9059, 2, 0, 3, "はぁ～ふかふか…\n…へ？\n鳥の巣にいるみたい？", 1, 134701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9060, 3, 0, 3, "ふむ…\nそばに置いてあげても\nいいでしょう", 1, 134701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9061, 4, 0, 3, "ほわぁ～\nいいかおり～", 1, 134701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9062, 5, 0, 3, "あたたかい…\nなんだか元気が\nでてきますねっ", 1, 134701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9064, 7, 0, 3, "たくさん食べて力を\n取り戻さなくてはっ\n…んがぐぐっ", 1, 134701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9065, 8, 0, 3, "ふむふむ…\n人の子にしては\nよく書けていますね♪", 1, 134701, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9067, 1, 0, 1, "素敵なお家ですね\n温かみがあって\nどこか騎士様に\n似ております。ふふ", 1, 133101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9068, 2, 0, 1, "賑やかな街ですね\nこれほど活気豊かだと\nつい外に出てみたく\nなってしまいます", 1, 133101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9069, 3, 0, 1, "別世界の存在は知って\nおりましたが、いざ\n目の当たりにすると\n驚きの連続ですわ", 1, 133101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9070, 4, 0, 1, "この街は様々な種族が\nなにはばかることなく\n混在しているのですね\n…我々も、いつかは…", 1, 133101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9071, 5, 0, 1, "ランドソルにも香水の\nお店があるのですね\nどんな香りが揃って\nいるのでしょうか…♪", 1, 133101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9072, 101, 0, 1, "昨日、素敵な花園を\n見つけたのです\n今日のお茶会は\nそちらでしましょうか", 4, 133101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9073, 102, 0, 1, "気を抜いてはいけない\nとわかっていても、\nここだとつい安心して\nしまいます…ふう…", 8, 133101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9074, 1, 0, 2, "ごきげんよう\n街で見つけた茶葉を\n試しているんです\n騎士様も一杯どうぞ♪", 1, 133101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9075, 2, 0, 2, "親切な方ばかりですね\n素敵なお友だちが\nできそうな予感で、\nワクワクしてます…♪", 1, 133101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9076, 3, 0, 2, "黄泉の食卓の皆様用に\nお土産を用意しないと\n騎士様のお知恵も\n頼りにしておりますわ", 1, 133101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9077, 4, 0, 2, "ジオ・ニヴルヘルと\n比べると、この世界は\n本当に明るいですね\n少し眩しいくらいです", 1, 133101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9078, 101, 0, 2, "あまり、血の巡りが\n良くなくて…\n騎士様の手は\nとても温かいですね", 4, 133101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9079, 102, 0, 2, "今度はわたくしに\nジオ・ニヴルヘルを\n案内させてくださいね\n観光案内のお返しです", 8, 133101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9080, 1, 0, 2, "おはようございます\n朝の眩しい太陽には\nなかなか慣れません…", 1, 133101, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9081, 2, 0, 2, "騎士様、お茶は\nいかがでしょうか？\n一度一休みして、\n英気を養いましょう", 1, 133101, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9082, 3, 0, 2, "眠れないのでしょうか\nまぶたが重くなるまで\nわたくしをお話し\n相手になさって下さい", 1, 133101, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9084, 2, 0, 3, "思わず気が抜けそうに\nなってしまいます…\nふふ、ふわふわ…♪", 1, 133101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9085, 3, 0, 3, "まあ、かわいらしい\nグレイスもきっと\n気に入りそうな\nデザインですわ", 1, 133101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9086, 4, 0, 3, "まあ、いい香り…\n心安らぐようですわ\n素敵なセンスを\nお持ちなのですね♪", 1, 133101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9087, 5, 0, 3, "燃え移りや乾燥がある\nから近づかないほうが\nいいとわかりつつも…\nああ、温かい…♪", 1, 133101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9089, 7, 0, 3, "食が細いというよりは\n消化があまりうまく\nできなくて…本当は\nまだ頂きたいのですが", 1, 133101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9090, 8, 0, 3, "『薔薇の下の契り』？\nまあ、男性同士で…！\nそんな…こ、これ以上\nここで読むわけには！", 1, 133101, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9092, 1, 0, 1, "ダーリンが帰ってくる\n前に部屋の掃除しよ♪\n…ふふ、これってもう\n新婚生活だよね…♪", 1, 132301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9093, 2, 0, 1, "家具のセンスまで\n私と同じだなんて\n運命って怖いわ…♪", 1, 132301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9094, 3, 0, 1, "あの人はベッドの下に\n何か隠してるのかな…\nな、なんて全っ然\n気にならないし…っ！", 1, 132301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9095, 4, 0, 1, "うっかりして\n角引っかけないように\nしないとね…", 1, 132301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9096, 5, 0, 1, "錬金術を使わなくても\nあなたの心を鷲掴む\n私は恋も錬金術も\n失敗しないんだから！", 1, 132301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9097, 101, 0, 1, "あの人って破廉恥なの\n興味あるのかしら…？\n基本破廉恥禁止だけど\nあの人だけは…許す…", 4, 132301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9098, 102, 0, 1, "手狭になれば錬金術で\n新しい部屋作るから\nいつ家族が増えても\n安心だよね…♪", 8, 132301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9099, 1, 0, 2, "錬金術でカレーライス\n作っちゃおっかな♪\n具材を釜でグツグツ…\nま、料理とも言うわね", 1, 132301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9100, 2, 0, 2, "錬金術に興味がある？\nじゃあさっそく私が\n手取り足取り教えるわ\nって…やだ破廉恥っ！", 1, 132301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9101, 3, 0, 2, "イロハちゃんとの\n虫捕りは命懸けよ！\n一万匹捕れるまで\n帰れないんだから…", 1, 132301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9102, 4, 0, 2, "ソノさんって\n体は小さいけど\nとっても頼れる私の\nお姉さん的存在なの♪", 1, 132301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9103, 101, 0, 2, "はあ…部屋中に広がる\nダーリンの香り…♪\n…ってノゾミみたいに\nなっちゃってる！？", 4, 132301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9104, 102, 0, 2, "鹿の獣人族の特徴は\nこのエッチな角…\n違っ！　叡智よ叡智！\n噛んだだけだからっ！", 8, 132301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9105, 1, 0, 2, "あ、おはよう！\n寝る前に飲んだ\n目覚めすっきり秘薬の\n効果はバッチリかな？", 1, 132301, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9106, 2, 0, 2, "午後からも頑張る\nあなたのために\n愛妻弁当作ったの♪\n一緒に食べよ？", 1, 132301, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9107, 3, 0, 2, "今日も一日お疲れさま\n眠れないなら隣で…\nじゃなかった早寝して\n明日も頑張ろうね！", 1, 132301, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9109, 2, 0, 3, "いい錬成をするには\n適度な休憩も必要よね\n…気持ち良くてたまに\n寝落ちしちゃうけど…", 1, 132301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9110, 3, 0, 3, "かわいいぬいぐるみ\n錬成じゃなくて\n手編みで作ったら\n驚いてくれるかも♪", 1, 132301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9111, 4, 0, 3, "くん、くん…\nん～いい匂い♪\nノゾミの香水と\nいい勝負だわ", 1, 132301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9112, 5, 0, 3, "ああ、暖かい\nでもソノさんの作った\nミートパイの方が\nもっと温かいわ…♪", 1, 132301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9114, 7, 0, 3, "もぐもぐ…私も\n食べても減らない秘薬\n錬成して他の食べ物も\n作れないかな？", 1, 132301, 0, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9115, 8, 0, 3, "ふむふむ…\nこのレシピ面白いわね\n今度錬成してみよっと", 1, 132301, 0, 1);
