INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1, 1, 0, 1, 0, "ここに困ってる人は\nいないよね…？\nよかった♪", 100101, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2, 1, 0, 1, 0, "冒険の準備準備…\nうん、忘れ物はなし！\n…あっ！", 100101, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3, 1, 0, 1, 0, "リズムを意識して…\nわんつーわんつー！\nうん、調子はバッチリ", 100101, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4, 1, 0, 1, 0, "ここで特訓していい？\n物は壊さないように\n気をつけるね！", 100101, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5, 1, 0, 1, 0, "なんか落ち着かないや\nもしかして、\n困ってる人が\n近くにいるのかな…？", 100101, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6, 4, 0, 1, 0, "えへへ…いつも\n一緒にいてくれて\nありがとねっ！", 100101, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7, 8, 0, 1, 0, "ちゃんと覚えてるよ\n騎士くんの声…\nあの時はありがとね！", 100101, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8, 1, 0, 1, 0, "あははっ！\nやったなー！\nお返しだー！\nこちょこちょこちょ…", 100101, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9, 1, 0, 1, 0, "どうしたの？\nもしかして困ってる？\nあたしにまかせて！", 100101, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (10, 1, 0, 1, 0, "えっとね…しっぽは\n触っちゃダメだよ？", 100101, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (11, 1, 0, 1, 0, "反応しない特訓…\nだったけど無理～！\n悔しいよぉ～！", 100101, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (12, 4, 0, 1, 0, "ふぁふぃ？\nやられた～！　ほっぺ\nぷにぷにしないでぇ～", 100101, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (13, 8, 0, 1, 0, "えへへ…\n騎士くんの手だ…\nあったかいな…♪", 100101, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (14, 1, 1, 1, 0, "おはよー！\n今日も元気に\nいっちゃうよー！", 100101, 1, 2);
