INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (16, 1, 3, 1, 0, "寝るのも修行だって\nこないだレイさんが\n言ってたんだ…", 100101, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (17, 1, 0, 1, 0, "ちゃんとケアして\n傷が残らないように\nしないと…", 100101, 1, 3);
