INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (28, 1, 0, 1, 0, "留守番はちょっと\n苦手かも…\nなんか寂しいんだもん", 100102, 3, 1);
