INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (86, 1, 0, 1, 0, "男の子って\nやっぱりおっきいね\nその…手とか\n他にもいろいろ…", 100202, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (87, 8, 0, 1, 0, "騎士クンに\n伝えたいことがあるの\nとっても大切な…\nこ、今度、話すね！", 100202, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (88, 8, 0, 1, 0, "ふふふっ…そんなとこ\nツンツンしないで\nくすぐったいよ～", 100202, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (89, 1, 1, 1, 0, "おはよう\nよく眠れた？　今日も\n一日、頑張ろうね♪", 100202, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (90, 1, 2, 1, 0, "お腹空いちゃった…\nそうだ。騎士クンも\n一緒にお昼食べない？", 100202, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (91, 1, 3, 1, 0, "もうこんな時間…\nそろそろ寝なくちゃ\nおやすみなさい", 100202, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (92, 1, 0, 1, 0, "よし…\nこれなら\n褒めてもらえるかな？", 100202, 1, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (93, 1, 0, 1, 0, "がんばることも\n必要だけど\nお休みも大切だよね", 100202, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (94, 1, 0, 1, 0, "かわいい～♪\n騎士クンは\nこういうのって好き？", 100202, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (95, 1, 0, 1, 0, "戦う前にもこうやって\n落ち着けたら\nいいのに", 100202, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (96, 1, 0, 1, 0, "あの…！\nよ、良かったら\n一緒に…！", 100202, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (97, 1, 0, 1, 0, "騎士クンと一緒に\n冒険するなら\n体力がないと…！", 100202, 6, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (98, 1, 0, 1, 0, "最近服のサイズが\n合わないんだけど…\n食べ過ぎなのかな？", 100202, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (99, 1, 0, 1, 0, "…やっぱり\n月刊恋愛指南は\nためになる…", 100202, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (101, 1, 0, 1, 0, "装備の手入れでも\nしようかな…", 100301, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (102, 1, 0, 1, 0, "次に鍛えるべきは\n肉体面か精神面か\nどちらにしよう…？", 100301, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (103, 1, 0, 1, 0, "ツムギが作ってくれた\nこの服…一体\nいつサイズを…？", 100301, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (104, 1, 0, 1, 0, "仲間なんていらない\nそう思ってた頃も\nあったな…", 100301, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (105, 1, 0, 1, 0, "考えごとをしていると\nつい部屋中を\n歩いてしまうな", 100301, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (106, 4, 0, 1, 0, "彼は\nどこまで強くなるのか\n私も負けられないな", 100301, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (107, 8, 0, 1, 0, "私が一本を取られる\nなんてね…願い事は\nもう決まったのかな？", 100301, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (108, 1, 0, 1, 0, "ああ、キミだったのか\n人の気配がしたから\n誰かと思ったよ", 100301, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (109, 1, 0, 1, 0, "斬りかかるつもりなら\nちゃんとかかってきて\n…呼んだだけ？", 100301, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (110, 1, 0, 1, 0, "なに？　私に相談？\n戦いについてなら\n喜んで", 100301, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (111, 1, 0, 1, 0, "今のが突き？\n軽い！　もっと鋭く！\nさあ、もう一回！", 100301, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (112, 4, 0, 1, 0, "ひゃっ！　ど、どこを\n触ってるの！？\n…油断した", 100301, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (113, 8, 0, 1, 0, "キミにはこれからも\n私のそばに…\nいや、なんでもない", 100301, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (114, 1, 1, 1, 0, "おはよう\n今日も充実した\n一日にしていこう", 100301, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (115, 1, 2, 1, 0, "そろそろ\nお茶の時間か\nキミも一緒にどう？", 100301, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (116, 1, 3, 1, 0, "遅くまで大変だね\nまだ仕事があるなら\n手伝おうか？", 100301, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (117, 1, 0, 1, 0, "戦いを経てもなお\n美しくあるように…\nこれでよし♪", 100301, 1, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (118, 1, 0, 1, 0, "戦ってばかりで\n休まないと、また\nユイに怒られるな", 100301, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (119, 1, 0, 1, 0, "ヒヨリみたいに\n練習相手に…\nいや、やめておこう", 100301, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (120, 1, 0, 1, 0, "これはいい…\n戦いの前の精神統一に\nもってこいじゃないか", 100301, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (121, 1, 0, 1, 0, "時々は落ち着いた\n時間を過ごすのも\n悪くない…", 100301, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (122, 1, 0, 1, 0, "彼に負けないよう\n私も更に強くなろう\n共に戦うために！", 100301, 6, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (123, 1, 0, 1, 0, "食事は大切だけど\n食べ過ぎには\n注意しておこう…", 100301, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (124, 1, 0, 1, 0, "ユイ…！\nこ、こんな本を\n読んでいたのか…！", 100301, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (126, 1, 0, 1, 0, "海だけじゃなくて\n川でも釣りがしたいな\n道具も新調しよう", 100302, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (127, 1, 0, 1, 0, "ツムギが作ってくれた\nこの服…一体\nいつサイズを…？", 100302, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (128, 1, 0, 1, 0, "彼は\nどこまで強くなるのか\n私も負けられないな", 100302, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (129, 1, 0, 1, 0, "そろそろ修行の\n内容を新しくしよう\nより厳しいものにね", 100302, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (130, 1, 0, 1, 0, "強くなって…誰にも\n妨げられない自由な\n生き方がしたい", 100302, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (131, 8, 0, 1, 0, "まさか風邪を引くとは\n彼の看病のおかげで\nすっかり良く\nなったけど", 100302, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (132, 8, 0, 1, 0, "私が一本を取られる\nなんてね…願い事は\nもう決まったのかな？", 100302, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (133, 1, 0, 1, 0, "どうしたの？\nなんでもない？\nそれなら良かった", 100302, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (134, 1, 0, 1, 0, "あんまり突っつくと\nお返しするよ？\n覚悟は出来てる？", 100302, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (135, 1, 0, 1, 0, "ねぇ、今度また\n釣りに行かない？\n大きいのを狙おう", 100302, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (136, 1, 0, 1, 0, "修行がしたいの？\nそれを早く言ってよ\nすぐに出発しよう！", 100302, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (137, 8, 0, 1, 0, "ひょっとして\n今のも一本だなんて\n言わないよね？", 100302, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (138, 8, 0, 1, 0, "キミにはこれからも\n私のそばに…\nいや、なんでもない", 100302, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (139, 1, 1, 1, 0, "今日は仕事？\nそれとも修行？\n良ければ付き合うよ", 100302, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (140, 1, 2, 1, 0, "休息も時には\n必要だと、仲間から\n教わったよ", 100302, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (141, 1, 3, 1, 0, "遅くまで大変だね\nまだ仕事があるなら\n手伝おうか？", 100302, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (142, 1, 0, 1, 0, "髪の長さを少し\n変えてみようかな…\nわ、私は何を…！", 100302, 1, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (143, 1, 0, 1, 0, "休息があってこそ\n万全な状態で\n戦えるんだ", 100302, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (144, 1, 0, 1, 0, "ヒヨリみたいに\n練習相手に…\nいや、やめておこう", 100302, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (145, 1, 0, 1, 0, "体と同様に\n心もほぐれるね…\nはぁ…", 100302, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (146, 1, 0, 1, 0, "休む時は休む\nヒヨリもそろそろ\n覚えて欲しいな…", 100302, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (147, 1, 0, 1, 0, "彼に負けないよう\n私も更に強くなろう\n共に戦うために！", 100302, 6, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (148, 1, 0, 1, 0, "修行のせいか\n最近食べる量が\n増えてきたような…", 100302, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (149, 1, 0, 1, 0, "ユイ…！\nこ、こんな本を\n読んでいたのか…！", 100302, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (151, 1, 0, 1, 0, "お宝の予感！\n部屋中を\n探検しちゃうぞ～", 100401, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (152, 1, 0, 1, 0, "落とし穴…どこに\n作ったんだっけ？", 100401, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (153, 1, 0, 1, 0, "うえ～ん！\n…嘘泣きしたら\n相手は油断するかな", 100401, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (154, 1, 0, 1, 0, "ハニートラップって\nすごく甘いお菓子で\nイタズラするのかな？", 100401, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (155, 1, 0, 1, 0, "かくれんぼする人\nこの指、と～まれ！", 100401, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (156, 4, 0, 1, 0, "ミソギ以外の人に\nイタズラされちゃ\nダメだよ！", 100401, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (157, 8, 0, 1, 0, "ミソギと遊ぶ？\nミソギに遊ばれる？", 100401, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (158, 1, 0, 1, 0, "いいこと思いついた！\nい、イタズラとかじゃ\nないよ～", 100401, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (159, 1, 0, 1, 0, "うわっ！？\n　にいちゃんが、\nミソギにイタズラ？", 100401, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (160, 1, 0, 1, 0, "にいちゃんの\n通りそうなところに\nバナナの皮を…", 100401, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (161, 1, 0, 1, 0, "にいちゃんのオデコと\nホッペと鼻と目と耳に\n落書きを…", 100401, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (162, 4, 0, 1, 0, "ここ、ミソギ探検隊の\n基地にしようよ！", 100401, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (163, 8, 0, 1, 0, "だ～れだ…って？\nにいちゃんの声くらい\nわかるよ！\n大好きだもん♪", 100401, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (164, 1, 1, 1, 0, "にいちゃん、おはよ！\n目覚まし時計を\n止めておいたのに、\nよく起きられたね～♪", 100401, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (165, 1, 2, 1, 0, "にいちゃんが\n昼寝したら、\nイタズラし放題♪", 100401, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (166, 1, 3, 1, 0, "夜の探検も\nおもしろそう！\nねぇ出かけちゃダメ？", 100401, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (167, 1, 0, 1, 0, "イタズラには\nお色気も必要…\nうん、完璧！", 100401, 1, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (168, 1, 0, 1, 0, "座ったら、ブーッって\nいう音が出たら、\nおもしろいな～♪", 100401, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (169, 1, 0, 1, 0, "かわいい！\nミソギ探検隊の\nマスコットにしよう♪", 100401, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (170, 1, 0, 1, 0, "吸うとくしゃみが\n止まらなくなるお香を\nここに…くしゅっ！", 100401, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (171, 1, 0, 1, 0, "にひひ…イタズラの\n証拠、ここで\n燃やしちゃおう～！", 100401, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (172, 1, 0, 1, 0, "探検家は\n体力勝負だよー！", 100401, 6, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (173, 1, 0, 1, 0, "みんなに分けて\nあげようかな～\n激辛ソースも\n入れちゃうけどね！", 100401, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (174, 1, 0, 1, 0, "こういう所に\n宝の地図が\n隠れてるんだよね～", 100401, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (176, 1, 0, 1, 0, "お宝の予感！\n部屋中を\n探検しちゃうぞ～", 100402, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (177, 1, 0, 1, 0, "うぅ、探検に出たい！\nじっとしてなんか\nいられないよ～！", 100402, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (178, 1, 0, 1, 0, "うえ～ん！\n…嘘泣きしたら\n相手は油断するかな", 100402, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (179, 1, 0, 1, 0, "ハニートラップって\nすごく甘いお菓子で\nイタズラするのかな？", 100402, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (180, 1, 0, 1, 0, "かくれんぼする人\nこの指、と～まれ！", 100402, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (181, 8, 0, 1, 0, "にいちゃんのオデコと\nホッペと鼻と目と耳に\n落書きを…", 100402, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (182, 8, 0, 1, 0, "にいちゃんが喜ぶ\nイタズラ、何かない\nかな～？", 100402, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (183, 1, 0, 1, 0, "ミソギがしかけた\nイタズラ\n見つけられるかな？", 100402, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (184, 1, 0, 1, 0, "探検する？\n冒険する？\nそれとも…\nイタズラされる？", 100402, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (185, 1, 0, 1, 0, "将来の夢は探検家！\n探したい物はないけど", 100402, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (186, 1, 0, 1, 0, "にいちゃんに宿題！\n落とし穴を10個\n掘ること！", 100402, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (187, 8, 0, 1, 0, "だ～れだ…って？\nにいちゃんの声くらい\nわかるよ！\n大好きだもん♪", 100402, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (188, 8, 0, 1, 0, "ここ、ミソギ探検隊の\n基地にしようよ！", 100402, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (189, 1, 1, 1, 0, "にいちゃん\n探検に行こうよ！\n今からなら遭難しても\n夜には帰れるよ！", 100402, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (190, 1, 2, 1, 0, "にいちゃんが昼寝したら\nイタズラし放題♪", 100402, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (191, 1, 3, 1, 0, "夜の探検も\nおもしろそう！\nねぇ出かけちゃダメ？", 100402, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (192, 1, 0, 1, 0, "イタズラには\nお色気も必要…\nうん、完璧！", 100402, 1, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (193, 1, 0, 1, 0, "ゆっくりまったりして\n次のイタズラを\n考えよ～！", 100402, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (194, 1, 0, 1, 0, "かわいい！\nミソギ探検隊の\nマスコットにしよう♪", 100402, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (195, 1, 0, 1, 0, "吸うとくしゃみが\n止まらなくなるお香を\nここに…くしゅっ！", 100402, 4, 3);
