INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (226, 1, 0, 1, 0, "このごろ一人でいると\nお兄ちゃんのこと\nばかり考えちゃう…", 100602, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (227, 1, 0, 1, 0, "はぁ、はぁ…もっと\nいっぱいしなくちゃ\n…練習", 100602, 2, 1);
