INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (251, 1, 0, 1, 0, "プリン、プリン、\nプリン、プリン、\nプリン、プリン～♪", 100701, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (252, 1, 0, 1, 0, "う～ら～め～し～や～\nなの～♪", 100701, 2, 1);
