INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (341, 1, 3, 1, 0, "バラの花びらいっぱい\n浮かべたお風呂に\n入ってから\n寝ようっと♪", 100802, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (342, 1, 0, 1, 0, "鏡よ、鏡。この世で\n一番可愛いボクに\nお似合いの人はだれ？", 100802, 1, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (343, 1, 0, 1, 0, "ボクが座ったソファは\n「美しいソファ」に\nランクアップした！", 100802, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (344, 1, 0, 1, 0, "可愛いものを\n可愛いって言える\nボクって可愛いよね！", 100802, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (345, 1, 0, 1, 0, "服を羽織るように\n香りをまとうボク…\n美しい、美しすぎる！", 100802, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (346, 1, 0, 1, 0, "冷えは美容の大敵\nなんだから！\nここ、ボクの場所ね！", 100802, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (347, 1, 0, 1, 0, "運動は苦手なのに\n運動するボクの姿は\n可愛いなんて…罪♪", 100802, 6, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (348, 1, 0, 1, 0, "ボクが食べると\n食べ物まで\n可愛いなあ…", 100802, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (349, 1, 0, 1, 0, "ボクが触った瞬間に\nこの本は美を\n携えてしまうんだ", 100802, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (351, 1, 0, 1, 0, "「第１０部２０章\n終焉戦争、開幕」\n…これでよし！", 100901, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (352, 1, 0, 1, 0, "今度シグルドに\n私の魔法を一つ\n伝授してやるか", 100901, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (353, 1, 0, 1, 0, "ここでは風が\n吹いてこないな…\n換気したい！", 100901, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (354, 1, 0, 1, 0, "そうだ、ナナカに\nこの間借りた本を\n返さないと…", 100901, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (355, 1, 0, 1, 0, "まずは包帯をとって\n手から龍の形の\n炎を出して…", 100901, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (356, 4, 0, 1, 0, "シグルド…\n今頃何処で何を\nしているのだろう…", 100901, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (357, 8, 0, 1, 0, "最近、彼の姿が\nまぶしく見える…\nどうして…？", 100901, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (358, 1, 0, 1, 0, "辞書を知らないか？\n…探している\n理由は聞くな", 100901, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (359, 1, 0, 1, 0, "そうだ！\n貴様の技にも\n名前をつけてやろう！", 100901, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (360, 1, 0, 1, 0, "私に触れても力を\n奪われないのは\n貴様くらいだぞ？", 100901, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (361, 1, 0, 1, 0, "何事だ？\nまさか我が瞳に\n魅入られたのか？", 100901, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (362, 4, 0, 1, 0, "貴様がそばにいると\nとても安心できる…\n…不思議だ", 100901, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (363, 8, 0, 1, 0, "い、いきなり目の前に\n現れるな！　ドキドキ\nするだろうがぁ！", 100901, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (364, 1, 1, 1, 0, "「覚醒」めたか…\n行くぞ、悪を討つ\n戦いに出発だ！", 100901, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (365, 1, 2, 1, 0, "昼間は「ヤツら」も\n襲ってはこない…\n束の間の休息だ", 100901, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (366, 1, 3, 1, 0, "どうした？　夜はまだ\nこれから…ふぁ…\nね、眠くなどないぞ！", 100901, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (367, 1, 0, 1, 0, "何か足りないな…\n暗黒騎士の紋章を\n作ろうかな", 100901, 1, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (368, 1, 0, 1, 0, "くっ…！\n私を眠りの国へ\n誘うつもりか！", 100901, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (369, 1, 0, 1, 0, "このようなものに\n懐柔されるものか！\n…されるものか！", 100901, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (370, 1, 0, 1, 0, "これを使えば\nもっとそれっぽい\n雰囲気が出そうだ", 100901, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (371, 1, 0, 1, 0, "これが我らの求めた\n安寧か…\n悪くない…", 100901, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (372, 1, 0, 1, 0, "この程度\n前世での激闘に\n比べれば…！", 100901, 6, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (373, 1, 0, 1, 0, "最近金欠でな…\nいやぁありがたい…", 100901, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (374, 1, 0, 1, 0, "ここにも\n前世の記憶が\n眠っていたとは…", 100901, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (376, 1, 0, 1, 0, "「第１３部１章\n閃光のシグルド」\n…ついに登場だな", 100902, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (377, 1, 0, 1, 0, "何故だ…何故\nこうもあっさり金欠に\n逆戻りするのだ！？", 100902, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (378, 1, 0, 1, 0, "やはり転生者という\n設定はカッコイイ…\n天才だな、私！", 100902, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (379, 1, 0, 1, 0, "そうだ、ナナカに\nこの間借りた本を\n返さないと…", 100902, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (380, 1, 0, 1, 0, "そろそろ今日の分の\n魔力を溜めないと\nまた魔力切れが…", 100902, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (381, 1, 0, 1, 0, "最近奴がそばに\nいるだけで、鼓動が\n早くなるのは何故だ？", 100902, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (382, 8, 0, 1, 0, "シ、シグルド…！\nだめだ…妄想でも\n上手く言えない…", 100902, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (383, 8, 0, 1, 0, "なんだ？　新たな\n賞金の情報を\n持ってきたのか？", 100902, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (384, 1, 0, 1, 0, "名乗りやポーズは\nその強度で相手を\n圧倒する技術だ", 100902, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (385, 1, 0, 1, 0, "いいか、魔法の\n詠唱中は絶対に\n声をかけるなよ？", 100902, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (386, 1, 0, 1, 0, "おお、ちょうど今\n貴様専用の設定集を\n作成中だぞ！", 100902, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (387, 1, 0, 1, 0, "貴様がそばにいると\nとても安心できる…\n…不思議だ", 100902, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (388, 8, 0, 1, 0, "い、いきなり目の前に\n現れるな！　ドキドキ\nするだろうがぁ！", 100902, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (389, 8, 1, 1, 0, "夜更かしのせいか\nまだ眠気が…いや！\nこれは組織の罠だ！", 100902, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (390, 1, 2, 1, 0, "平穏な時に\n出来るだけ魔力を\n溜めておかねば…", 100902, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (391, 1, 3, 1, 0, "夜になると、力が\n増した気分になるな\n…なりませんか？", 100902, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (392, 1, 0, 1, 0, "何か足りないな…\n暗黒騎士の紋章を\n作ろうかな", 100902, 1, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (393, 1, 0, 1, 0, "お、おのれ\nヒュプノス…\n私は負けないぞ…", 100902, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (394, 1, 0, 1, 0, "くっくっくっ…\n貴様は特別団員に\nしてやろう", 100902, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (395, 1, 0, 1, 0, "これを使えば\nもっとそれっぽい\n雰囲気が出そうだ", 100902, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (396, 1, 0, 1, 0, "これが我らの求めた\n安寧か…\n悪くない…", 100902, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (397, 1, 0, 1, 0, "新たな戦いに備えて\n魔力だけでなく\n体力も鍛えねば！", 100902, 6, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (398, 1, 0, 1, 0, "食事は魔力に\n変換する体力の\n元になるのだ！", 100902, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (399, 1, 0, 1, 0, "フフフ…\nこれを覚えれば\nもっと強く…！", 100902, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (401, 1, 0, 1, 0, "王子はんほんまに\nはんなりしてて、\n王子はんって感じやわ", 101001, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (402, 1, 0, 1, 0, "この絵本の王子はん、\n王子はんみたいで\nええなぁ♪", 101001, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (403, 1, 0, 1, 0, "うちがガラスの靴を\n落としたら、王子はん\n見つけてくれるやろか", 101001, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (404, 1, 0, 1, 0, "マホマホ王国\n次期国王の王子はん\n…見てみたいわ～", 101001, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (405, 1, 0, 1, 0, "王子はんのために\n糸を紡いで、\n編み物でもしまひょか", 101001, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (406, 4, 0, 1, 0, "マホ姫と王子はん、\n絵本の世界より\nベストカップルや", 101001, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (407, 8, 0, 1, 0, "王子はんと\n一緒にいると\n胸がせわしのー\nうごいてかなん", 101001, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (408, 1, 0, 1, 0, "うちら並んでたら\nお雛様みたいに\n見えると思いまへん？", 101001, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (409, 1, 0, 1, 0, "王子はんを\n国に連れて帰ったら\nみんな大歓迎して\nくれるやろなぁ", 101001, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (410, 1, 0, 1, 0, "王子はんは、\n絵本から出てきた人\nみたいやね～", 101001, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (411, 1, 0, 1, 0, "うちの話し方\nのんびりしてます\nやろか？", 101001, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (412, 4, 0, 1, 0, "マホ姫の手は\nつべたいから、\n王子はんが温めて？", 101001, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (413, 8, 0, 1, 0, "うちがもし悪い魔法に\nかかったら、王子はん\n口づけしてくれはる？", 101001, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (414, 1, 1, 1, 0, "朝は小鳥はんも\nおなかがすいてるから\nよう飛び回りはるねえ", 101001, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (415, 1, 2, 1, 0, "お茶請けはお大福が\nよろしおすけど\nクッキーもええなあ", 101001, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (416, 1, 3, 1, 0, "うち、普段から\n夢見がちやけど…\nほんに眠い時間やわぁ", 101001, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (417, 1, 0, 1, 0, "王子はんに\n会いに行くなら、\nおめかしせな\nあかんなぁ", 101001, 1, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (418, 1, 0, 1, 0, "はんなりした\nええお椅子やわぁ\nほっこりしますなあ", 101001, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (419, 1, 0, 1, 0, "かいらしもんが\nいっぱいで、\nマホマホ王国みたい\nやわぁ", 101001, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (420, 1, 0, 1, 0, "心地ええどす。まるで\nお花畑にいるようやね", 101001, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (421, 1, 0, 1, 0, "ぽかぽかどす。\n心まであったかく\nなりますわぁ", 101001, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (422, 1, 0, 1, 0, "はぁはぁ、お姫はんは\n激しい運動は\nしぃひんもんどす～", 101001, 6, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (423, 1, 0, 1, 0, "もぐもぐ…あんまり\n速う食べられへんけど\n美味しいわぁ", 101001, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (424, 1, 0, 1, 0, "絵本、絵本～\n幸せやわぁ♪", 101001, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (426, 1, 0, 1, 0, "王子はんにとって\n自慢の姫になるえ～", 101002, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (427, 1, 0, 1, 0, "カボチャの馬車に\nガラスの靴…\n欲しいなぁ", 101002, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (428, 1, 0, 1, 0, "うちがガラスの靴を\n落としたら、王子はん\n見つけてくれるやろか", 101002, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (429, 1, 0, 1, 0, "マホマホ王国\n次期国王の王子はん\n…見てみたいわ～", 101002, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (430, 1, 0, 1, 0, "ほんまなぁ\nこの絵本が面白いんよ\n王子はんに\n読んであげよ", 101002, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (431, 8, 0, 1, 0, "マホ姫と王子はん\n絵本の世界より\nベストカップルや", 101002, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (432, 8, 0, 1, 0, "王子はんと\n一緒にいると\n胸がせわしのー\nうごいてかなん", 101002, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (433, 1, 0, 1, 0, "たまには、王子はんの\n好きな絵本も\n知りたいな～", 101002, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (434, 1, 0, 1, 0, "王子はんを\n国に連れて帰ったら\nみんな大歓迎して\nくれるやろなぁ", 101002, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (435, 1, 0, 1, 0, "そのうち、王子はんの\nお国にも連れてって\nもらえますやろか？", 101002, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (436, 1, 0, 1, 0, "絵本読むんも\nええなぁ…", 101002, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (437, 8, 0, 1, 0, "うちがもし悪い魔法に\nかかったら、王子はん\n口づけしてくれはる？", 101002, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (438, 8, 0, 1, 0, "うちら並んでたら\nお雛様みたいに\n見えると思いまへん？", 101002, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (439, 1, 1, 1, 0, "朝は小鳥はんも\nおなかがすいてるから\nよう飛び回りはるねえ", 101002, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (440, 1, 2, 1, 0, "マホマホ王国では\nこの時分は\nみんなお昼寝\nしてたんよ", 101002, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (441, 1, 3, 1, 0, "夜に飛んだり鳴いたり\nする鳥はんもおるんよ\n昼は寝てはるんかねぇ", 101002, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (442, 1, 0, 1, 0, "王子はんに\n会いに行くなら\nおめかしせな\nあかんなぁ", 101002, 1, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (443, 1, 0, 1, 0, "王子はんも一緒に\nゆっくりしていっても\nええんよ？", 101002, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (444, 1, 0, 1, 0, "かいらしい子やねぇ\nうちとお友だちに\nなってくれるん？", 101002, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (445, 1, 0, 1, 0, "癒しアイテムって\nほんま、好っきゃわぁ", 101002, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (446, 1, 0, 1, 0, "うちのお城にも\nほしいわぁ。帰ったら\nさっそく準備せな\nあかんなぁ", 101002, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (447, 1, 0, 1, 0, "部屋の中で運動\nできるのも、なかなか\n面白いもんやわぁ", 101002, 6, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (448, 1, 0, 1, 0, "おいひいわ～\nマホマホ王国には\nない味やわぁ", 101002, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (449, 1, 0, 1, 0, "しっかり知識を\n身につけんと\n魔法も使えへん\nからなぁ", 101002, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (451, 1, 0, 1, 0, "１、２、３、４…\nあれ？　いま、\n何段目まで\n編んだっけ？", 101101, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (452, 1, 0, 1, 0, "おやつのケーキは\n苺かチョコ…\nどっちも食べよう！", 101101, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (453, 1, 0, 1, 0, "い、いけない！\n縫い針落としちゃった", 101101, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (454, 1, 0, 1, 0, "ケーキをクレープで\n巻いたら…\n大ヒット商品の予感！", 101101, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (455, 1, 0, 1, 0, "「どうだ！妹暮らし」\nってことわざ\nなかったっけ？", 101101, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (456, 4, 0, 1, 0, "お兄ちゃんに、\nお洋服でも\n作ろうかな？", 101101, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (457, 8, 0, 1, 0, "ここがお兄ちゃんの\n部屋…\nドキドキしちゃう～", 101101, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (458, 1, 0, 1, 0, "ねぇ、おにいちゃん\n一緒に、スイーツでも\n食べにいかない？", 101101, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (459, 1, 0, 1, 0, "クリームついてた！？\nさっき食べた、\nケーキかも…！", 101101, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (460, 1, 0, 1, 0, "勉強、見てくれるん\nですか？　やった～♪", 101101, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (461, 1, 0, 1, 0, "服がほつれてますよ？\n直してあげます！", 101101, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (462, 4, 0, 1, 0, "私をお嫁さんに\nしたいなら、予約は\nお早目に、ですよ♪", 101101, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (463, 8, 0, 1, 0, "ねぇねぇお兄ちゃん！\n…えへへ、\n呼んでみただけ♪", 101101, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (464, 1, 1, 1, 0, "今日も１日\n勉強がんばります！\n…少しは遊ぼうかな", 101101, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (465, 1, 2, 1, 0, "お昼休憩に\n甘いものが\n食べたいです～", 101101, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (466, 1, 3, 1, 0, "ねむいです～\nでももうちょっと\n勉強しないと～…", 101101, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (467, 1, 0, 1, 0, "この服、かわいい♪\n今度作って\nみよっと～！", 101101, 1, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (468, 1, 0, 1, 0, "勉強で疲れたから\n休憩しよ～っと", 101101, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (469, 1, 0, 1, 0, "かわいい♪\n私が服を作って\nあげよっか？", 101101, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (470, 1, 0, 1, 0, "リラックスできます～\nお兄ちゃんが隣に\nいるみたい！", 101101, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (471, 1, 0, 1, 0, "のんびりしたら、\nまた勉強しよっと！", 101101, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (472, 1, 0, 1, 0, "運動すると勉強も\nはかどるよね！", 101101, 6, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (473, 1, 0, 1, 0, "おいし～！\nお兄ちゃんにも\nおすそ上げしますね！", 101101, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (474, 1, 0, 1, 0, "お兄ちゃんの記憶を\n戻す方法、どこかに\n書いてないかな？", 101101, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (476, 1, 0, 1, 0, "ケーキの食べ過ぎで\nお腹が…うぅ、\n因果横暴だよー…", 101102, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (477, 1, 0, 1, 0, "１、２、３、４…\nあれ？　いま、\n何段目まで\n編んだっけ？", 101102, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (478, 1, 0, 1, 0, "い、いけない！\n縫い針落としちゃった", 101102, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (479, 1, 0, 1, 0, "笑う時には服着ます！", 101102, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (480, 1, 0, 1, 0, "「どうだ！妹暮らし」\nってことわざ\nなかったっけ？", 101102, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (481, 8, 0, 1, 0, "お兄ちゃんに、\nお洋服でも\n作ろうかな？", 101102, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (482, 8, 0, 1, 0, "お兄ちゃんが帰って\nきたら、何して遊ぼう\nかな～？", 101102, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (483, 1, 0, 1, 0, "勉強、見てくれるん\nですか？　やった～♪", 101102, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (484, 1, 0, 1, 0, "クリームついてた！？\nさっき食べた、\nケーキかも…！", 101102, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (485, 1, 0, 1, 0, "おにいちゃん！\nそろそろ、おやつに\nしませんか？", 101102, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (486, 1, 0, 1, 0, "勉強は…\n明日にしようっと", 101102, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (487, 8, 0, 1, 0, "妹だけど、大人な\nレディですからね！", 101102, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (488, 8, 0, 1, 0, "な、ナデナデとか\n恥ずかしいな～", 101102, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (489, 1, 1, 1, 0, "今日も１日\n勉強がんばります！\n…少しは遊ぼうかな", 101102, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (490, 1, 2, 1, 0, "お昼休憩に\n甘いものが\n食べたいです～", 101102, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (491, 1, 3, 1, 0, "お兄ちゃんは\n夜強いんですね\n私は眠くて…", 101102, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (492, 1, 0, 1, 0, "服装や髪形が変わると\n気分も変わるよね", 101102, 1, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (493, 1, 0, 1, 0, "ここで寝ちゃったら\n受験勉強が～…\nぐーー", 101102, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (494, 1, 0, 1, 0, "かわいい♪\n私が服を作って\nあげよっか？", 101102, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (495, 1, 0, 1, 0, "いまならどんな試験も\n合格できそうです！", 101102, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (496, 1, 0, 1, 0, "のんびりしたら\nまた勉強しよっと！", 101102, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (497, 1, 0, 1, 0, "お兄ちゃんを\n守るためには\n毎日、鍛えなくちゃ！", 101102, 6, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (498, 1, 0, 1, 0, "おいし～！\nお兄ちゃんにも\nおすそ上げしますね！", 101102, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (499, 1, 0, 1, 0, "この本の服\nかわいいなぁ♪\n私に作れるかな？", 101102, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (501, 1, 0, 1, 0, "う～ん、今なら\nどこでだって\n眠れちゃいそう…", 101201, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (502, 1, 0, 1, 0, "たくさん寝すぎて、\n夢の中か現実か\nわかんなく\nなっちゃうんだよね～", 101201, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (503, 1, 0, 1, 0, "…はっ！？\nね、寝てないよ？\nホントだよっ", 101201, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (504, 1, 0, 1, 0, "ぐっすり眠って\nパワーを\nフルチャージっ☆", 101201, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (505, 1, 0, 1, 0, "もし、超能力のことが\nバレたら…ブルブル", 101201, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (506, 4, 0, 1, 0, "テレパシーで\nキミの心を覗けたら\n…なんてね！", 101201, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (507, 8, 0, 1, 0, "キミを抱き枕に…\nじょ、冗談冗談！", 101201, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (508, 1, 0, 1, 0, "ん～、なになに？\n一緒にお昼寝する？", 101201, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (509, 1, 0, 1, 0, "忘れ物しちゃった。\nま、いっか！\nえ、ダメ？", 101201, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (510, 1, 0, 1, 0, "おおっ、相談ごと？\nなにかな\nなにかな～？", 101201, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (511, 1, 0, 1, 0, "う～ん、もしかして\nキミにも仲良しになる\n超能力があるのかも！", 101201, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (512, 4, 0, 1, 0, "むむ～…どう？\nテレパシーで、私の\n気持ちを送ったよ！", 101201, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (513, 8, 0, 1, 0, "えっ？　私とキミとの\n相性？　むむ～…\nうん、バッチリだよ☆", 101201, 102, 2);
