INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (599, 1, 0, 1, 0, "全然読めない～\nそだ、ヒデサイに\n読んでもらおっと！", 101602, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (601, 1, 0, 1, 0, "やっさー\n踊ってると、どんどん\n楽しくなるさー！", 101701, 1, 1);
