INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (663, 8, 0, 1, 0, "生徒は全員可愛いけど\n君は少し特別…\nみんなには内緒よ？", 101801, 102, 2);
