INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (685, 1, 0, 1, 0, "宿題はもうやった？\n苦手なところは\n先生が見てあげる♪", 101802, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (686, 1, 0, 1, 0, "んん？　なんだか\n元気がないみたい\n先生がぎゅぅ～って\nしてあげる…　", 101802, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (687, 8, 0, 1, 0, "君のお勉強も\nだいぶ進んできたね～\n補習卒業も近いかな？\n少し寂しいけど…", 101802, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (688, 8, 0, 1, 0, "テストがんばったね～\nご褒美に先生が\n特別な授業\nしてあげるね？", 101802, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (689, 1, 1, 1, 0, "朝の特別授業\nしてあげましょうか？", 101802, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (690, 1, 2, 1, 0, "午後の授業って\n眠いでしょう？\n気持ちは分かるけど\nがんばってね♪", 101802, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (691, 1, 3, 1, 0, "明日の予習はやった？\n先生も手伝うから\n寝る前にやりましょ♪", 101802, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (692, 1, 0, 1, 0, "もしかして…\n少し露出多いかしら？", 101802, 1, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (693, 1, 0, 1, 0, "私の膝枕と、どっちが\nフカフカかしら？", 101802, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (694, 1, 0, 1, 0, "可愛いわね～\n教室に置いたら\n生徒が喜ぶかしら？　", 101802, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (695, 1, 0, 1, 0, "は～落ち着くなぁ～\n教師には\nこういうゆとりも\n必要よね♪　", 101802, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (696, 1, 0, 1, 0, "まだ寒いわぁ\n誰か隣に\n来てくれたら…　", 101802, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (697, 1, 0, 1, 0, "運動部の顧問も\nできるかしら？　", 101802, 6, 3);
