INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (769, 1, 0, 1, 0, "この子を相手に、\n演技の練習～", 102101, 3, 3);
