INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (771, 1, 0, 1, 0, "えへへ。\nぽっかぽかです…", 102101, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (772, 1, 0, 1, 0, "はぁはぁ…\nふひぃ…\nはうぅぅぅ～～", 102101, 6, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (773, 1, 0, 1, 0, "もぐもぐ…えへへ、\n美味しいものを\n食べてると幸せです", 102101, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (774, 1, 0, 1, 0, "戯曲を読んで\n自分が演じるところを\n想像するの…", 102101, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (776, 1, 0, 1, 0, "かわいいものを\nこうして並べて…\nはぁ…落ちつくぅ♪", 102102, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (777, 1, 0, 1, 0, "ふぁ～、この部屋は\n時間がゆったりと\n流れてるみたい", 102102, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (778, 1, 0, 1, 0, "この台本、感動…\n涙が止まらないよぉ\nいつか演じてみたいな", 102102, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (779, 1, 0, 1, 0, "お芝居には\n人間観察が大事…\nじーーーーーー…", 102102, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (780, 1, 0, 1, 0, "あえいうえおあお\nかけきくけ、かっ…\nむ、難しいですぅぅ", 102102, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (781, 8, 0, 1, 0, "お、お腹が空いた時は\n言ってくださいね\nが、頑張って\n作ります！", 102102, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (782, 8, 0, 1, 0, "れ、恋愛劇…\nお、お兄ちゃんと\n練習…", 102102, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (783, 1, 0, 1, 0, "こ、この本、とっても\nおもしろいんですよ♪", 102102, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (784, 1, 0, 1, 0, "お兄ちゃんと\nいっぱい話せた日は、\n嬉しくなっちゃいます", 102102, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (785, 1, 0, 1, 0, "何か私にできる事\n…そうだ！\nお掃除しますね！", 102102, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (786, 1, 0, 1, 0, "私、ちょっとずつ、\n自分に自信が\nついてきたかもです…", 102102, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (787, 8, 0, 1, 0, "リボンがかわいい？\nほ、ほぇぇぇ～～\nは、恥ずかしいですぅ", 102102, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (788, 8, 0, 1, 0, "お兄ちゃん…♪\nえへへ、呼んでみた\nだけです…♪", 102102, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (789, 1, 1, 1, 0, "おはようございます♪\n声出しして、お芝居の\n練習しなくちゃ", 102102, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (790, 1, 2, 1, 0, "お茶にしますか？\nクッキー焼いたので、\nよかったら…", 102102, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (791, 1, 3, 1, 0, "寝る前に\n台本を読むと、\n覚えやすいんです", 102102, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (792, 1, 0, 1, 0, "可愛い女の子に変身！\n出来ますように…♪", 102102, 1, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (793, 1, 0, 1, 0, "はぅ…\nフッカフカァ…\n癒されちゃいます…", 102102, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (794, 1, 0, 1, 0, "この子を相手に\n演技の練習～", 102102, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (795, 1, 0, 1, 0, "リボンや服に\n染み込ませておこう\nいつでもいい匂い♪", 102102, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (796, 1, 0, 1, 0, "えへへ\nぽっかぽかです…", 102102, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (797, 1, 0, 1, 0, "え、演劇には\nた、体力も重要\nだから～", 102102, 6, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (798, 1, 0, 1, 0, "残さず\n食べますね…！", 102102, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (799, 1, 0, 1, 0, "戯曲を読んで\n自分が演じるところを\n想像するの…", 102102, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (801, 1, 0, 1, 0, "ねえ、人見知りを\n治すための練習に\n付き合ってくれない？", 102201, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (802, 1, 0, 1, 0, "アカリみたいに\nかわいくなるには、\nどうすればいいのかな", 102201, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (803, 1, 0, 1, 0, "レアカードをゲット！\nねえねえ、\nすごいでしょ！？", 102201, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (804, 1, 0, 1, 0, "ア、アイツと同じ\n部屋にいるのよね…\nって、だから何よ！？", 102201, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (805, 1, 0, 1, 0, "楽しいゲームって\nやめどきが\n見つからないわ", 102201, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (806, 4, 0, 1, 0, "もっと女の子らしく\nなったらアイツも\n嬉しいかな…って、\n何考えてるの！？", 102201, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (807, 8, 0, 1, 0, "うぅ…最近、\nアイツを見るとなんか\nドキドキするのよね", 102201, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (808, 1, 0, 1, 0, "ゲームに負けた罰\nだからって、\nくすぐるのは…\nあはは…ダメだって！", 102201, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (809, 1, 0, 1, 0, "アンタと話すのは割と\n緊張しないのよね\n…なんでかな", 102201, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (810, 1, 0, 1, 0, "待ちきれない…\n新作ゲームの発売日が\n待ちきれない…", 102201, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (811, 1, 0, 1, 0, "アンタは人見知り\nしないタチよね？\nいいなあ～…", 102201, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (812, 4, 0, 1, 0, "ア、アンタってさ\n好きな人とかい…\nなな、何でもない！", 102201, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (813, 8, 0, 1, 0, "アンタと…そ、その\nこうして話してるのも\nわわ、悪くないわね！", 102201, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (814, 1, 1, 1, 0, "おおおおはよう！\n…よし！　ちゃんと\nあいさつできたわ！", 102201, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (815, 1, 2, 1, 0, "一緒にごはんとか…\nううん、やっぱいい！\nなんでもないからっ！", 102201, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (816, 1, 3, 1, 0, "夜はこれからよ！\n朝まで\nゲームするからね！", 102201, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (817, 1, 0, 1, 0, "アカリみたいに\n可愛くなるのは\n難しいかな", 102201, 1, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (818, 1, 0, 1, 0, "寝っ転がりながら\nゲームができれば\n気分が良さそう", 102201, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (819, 1, 0, 1, 0, "可愛い…けど、\n私には似合わないかな", 102201, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (820, 1, 0, 1, 0, "はあ、リラックスして\nゲームができるわ", 102201, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (821, 1, 0, 1, 0, "ん…暖かくて、\n寝落ちしちゃいそう", 102201, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (822, 1, 0, 1, 0, "ゲーム感覚でやると、\n体動かすのも\n楽しいわね", 102201, 6, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (823, 1, 0, 1, 0, "ゲームは頭使うから\nそのぶん\n栄養がいるのよ", 102201, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (824, 1, 0, 1, 0, "ふむふむ、なるほど\n友だちを作るには\n笑顔が大事っと…", 102201, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (826, 1, 0, 1, 0, "さあ、今日は\n何のゲームで\n遊ぼうかな？", 102202, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (827, 1, 0, 1, 0, "一緒にゲームができる\n人がいるっていうのも\n新鮮だな…", 102202, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (828, 1, 0, 1, 0, "レアカードをゲット！\nすごいでしょ！？", 102202, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (829, 1, 0, 1, 0, "アカリってどうやって\n友達作ってるんだろ？\nコツがあるのかな…", 102202, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (830, 1, 0, 1, 0, "楽しいゲームって\nやめどきが\n見つからないわ", 102202, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (831, 8, 0, 1, 0, "うぅ…最近\nアイツを見るとなんか\nドキドキするのよね", 102202, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (832, 8, 0, 1, 0, "ね、ねえ\nもう少しその…\n一緒にゲームしない？", 102202, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (833, 1, 0, 1, 0, "ねえ、人見知りを\n治すための練習に\n付き合ってくれない？", 102202, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (834, 1, 0, 1, 0, "欲しいゲームが\n見つからなくて…\n探すの手伝って！", 102202, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (835, 1, 0, 1, 0, "ねえ、見て！\n新しいゲームを\n買って来たの！", 102202, 3, 2);
